(window.webpackJsonp=window.webpackJsonp||[]).push([[1e3],{1023:function(p){p.exports={chapterTitle:null,title:"\u7b2c808\u7ae0 \u5507\u67aa\u820c\u5251",content:"<article><h2>\u7b2c808\u7ae0 \u5507\u67aa\u820c\u5251</h2><p>\u3000\u3000\u4ece\u53bb\u5e74\u5341\u5e74\u56fd\u5185\u79fb\u52a8\u901a\u4fe1\u5f00\u6237\u603b\u6570\u7a81\u7834\u5343\u4e07\uff0c\u56fd\u5185\u79fb\u52a8\u901a\u4fe1\u4e1a\u52a1\u5c31\u5448\u7206\u70b8\u6027\u7684\u53d1\u5c55\u8d77\u6765\uff0c\u4ece\u4eca\u5e74\u5e74\u521d\u6708\u5f00\u6237\u91cf\u7a81\u7834\u767e\u4e07\u4e4b\u540e\uff0c\u8fd1\u4e09\u4e2a\u6708\u4ee5\u6765\uff0c\u6bcf\u6708\u7684\u5f00\u6237\u91cf\u90fd\u5728\u767e\u4e07\u4ee5\u4e0a\uff0c\u4e1d\u6beb\u4e0d\u53d7\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u7b2c\u4e00\u4e2a\u4e00\u5343\u4e07\u79fb\u52a8\u901a\u4fe1\u7528\u6237\u79ef\u7d2f\u4e86\u8fd1\u5341\u5e74\u7684\u65f6\u95f4\uff0c\u773c\u770b\u7740\u7b2c\u4e8c\u4e2a\u4e00\u5343\u4e07\u79fb\u52a8\u901a\u4fe1\u7528\u6237\u5c31\u8981\u5728\u77ed\u77ed\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\u5b8c\u6210\u79ef\u7d2f\uff1b\u968f\u4e4b\u800c\u6765\u662f\u6570\u5b57\u624b\u673a\u5e02\u573a\u7684\u7206\u70b8\u6027\u53d1\u5c55\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u6b27\u7f8e\u53d1\u8fbe\u5730\u533a\uff0c\u4e2d\u56fd\u7684\u624b\u673a\u5e02\u573a\u89c4\u6a21\u770b\u4e0a\u53bb\u8fd8\u6709\u4e9b\u5c0f\uff0c\u4f46\u662f\u8c01\u90fd\u65e0\u6cd5\u5ffd\u89c6\u4e2d\u56fd\u5e02\u573a\u7684\u5de8\u5927\u6f5c\u529b\uff0c\u6b63\u662f\u8fd9\u79cd\u6f5c\u529b\uff0c\u8fd1\u4e24\u5e74\u6765\u4f17\u591a\u6d77\u5916\u624b\u673a\u5382\u5546\u90fd\u7eb7\u7eb7\u52a0\u5feb\u8fdb\u5165\u4e2d\u56fd\u5e02\u573a\u7684\u6b65\u4f10\u3002</p><p>\u3000\u3000\u4e5d\u4e03\u5e74\u5e95\uff0c\u7231\u7acb\u4fe1\u4ee5GH398\u7b49\u7cfb\u5217\u7684\u4e2d\u6587\u6570\u5b57\u624b\u673a\u5c06\u6469\u6258\u7f57\u62c9\u8d76\u4e0b\u795e\u575b\uff0c\u767b\u4e0a\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u5360\u6709\u7387\u7b2c\u4e00\u7684\u5b9d\u5ea7\uff0c\u4e5f\u4f7f\u5f97\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u4e2d\u56fd\u624b\u673a\u5e02\u573a\u7ade\u4e89\u5347\u7ea7\u3002\u8d81\u7740\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u56fd\u5185\u5e02\u573a\u72fc\u70df\u56db\u8d77\u4e4b\u65f6\uff0c\u9526\u6e56\u4ee5\u4e00\u6b3e\u7231\u8fbei19\u624b\u673a\u60ca\u8273\u7684\u5de5\u4e1a\u8bbe\u8ba1\u4e0e\u5728\u56fd\u5185\u5e02\u573a\u7684\u54c1\u724c\u3001\u5e02\u573a\u6e20\u9053\u4f18\u52bf\u5f02\u519b\u7a81\u8d77\uff0c\u987a\u5229\u6324\u5165\u6d77\u5916\u624b\u673a\u5382\u5546\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u6218\u573a\uff0c\u5e02\u573a\u4efd\u989d\u8fbe\u5230\u60ca\u4eba\u768414\uff05\uff0c\u6210\u4e3a\u56fd\u5185\u624b\u673a\u5e02\u573a\u9664\u7231\u7acb\u4fe1\u3001\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u4e4b\u5916\u7684\u7b2c\u56db\u54c1\u724c\uff0c\u7531\u4e8e\u8bfa\u57fa\u4e9a\u8fdb\u5165\u56fd\u5185\u5e02\u573a\u7684\u65f6\u95f4\u4e0d\u957f\uff0c\u5e02\u573a\u5360\u6709\u7387\u5e76\u4e0d\u6bd4\u7231\u8fbe\u9ad8\u591a\u5c11\uff0c\u800c\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u52a0\u4e0a\u79d1\u738b\u9ad8\u79d1\u7684\u624b\u673a\u603b\u9500\u91cf\u751a\u81f3\u5360\u4e0d\u52306\uff05\uff0c\u7531\u6b64\u53ef\u89c1\u79d1\u738b\u9ad8\u79d1\u6bcf\u6708\u4e00\u4e07\u4ef6\u7684\u624b\u673a\u9500\u552e\u662f\u4f55\u7b49\u7684\u5f25\u8db3\u73cd\u8d35\u3002\u6bd4\u8d77\u66fe\u7ecf\u53d1\u751f\u8fc7\u7684\u5386\u53f2\u4e2d\u4e5d\u516b\u5e74\u56fd\u4ea7\u54c1\u724c\u624b\u673a\u5728\u56fd\u5185\u4e0d\u8db33\uff05\u7684\u5e02\u573a\u5360\u6709\u7387\uff0c\u6b64\u65f6\u53ef\u4ee5\u8bf4\u56fd\u5185\u6570\u5b57\u624b\u673a\u7684\u5386\u53f2\u5df2\u7ecf\u7ed9\u5f7b\u5e95\u6539\u5199\u4e86\u3002</p><p>\u3000\u3000\u76f8\u53cd\uff0c\u97e9\u56fd\u3001\u65e5\u672c\u624b\u673a\u54c1\u724c\u8fdb\u5165\u56fd\u5185\u7684\u6b65\u4f10\u8fdf\u7f13\uff0c\u4e09\u661f\u76f4\u5230\u4eca\u5e74\u521d\u624d\u5728\u4e2d\u56fd\u63a8\u51fa\u4ed6\u4eec\u7684\u9996\u6b3e\u624b\u673a\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u5f20\u606a\u53ef\u4e0d\u60f3\u655d\u5e1a\u81ea\u73cd\uff0c\u867d\u7136\u8003\u8651\u5230\u534e\u590f\u7535\u5b50\u80fd\u5236\u9020\u4e00\u4e9b\u9ebb\u70e6\uff0c\u4f46\u662f\u9526\u6e56\u7684\u76ee\u5149\u5df2\u7ecf\u76ef\u5728\u6d77\u5916\u7ade\u4e89\u8005\u7684\u8eab\u4e0a\uff0c\u6469\u6258\u7f57\u62c9\u3001\u7231\u7acb\u4fe1\u7b49\u6d77\u5916\u624b\u673a\u5382\u5546\u5373\u5c06\u4ece\u6d77\u5916\u5f15\u8fdb\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002\u5f20\u606a\u7684\u76ee\u5149\u4ece\u6ca1\u6709\u5c40\u9650\u4e8e\u8d34\u7740\u7231\u8fbe\u54c1\u724c\u7684\u624b\u673a\u5360\u591a\u5c11\u5e02\u573a\u4efd\u989d\uff0c\u800c\u662f\u9526\u6e56\u7684\u6280\u672f\u80fd\u666e\u53ca\u5230\u600e\u6837\u7684\u7a0b\u5ea6\u3001\u5360\u591a\u5c11\u5e02\u573a\u4efd\u989d\uff1b\u5373\u4f7f\u6469\u6258\u7f57\u62c9\u80fd\u653e\u4e0b\u8eab\u6bb5\u8dd1\u4e0a\u95e8\u6765\u5e0c\u671b\u5f15\u8fdb\u9526\u6e56\u7684\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u62d2\u7edd\uff0c\u53ea\u662f\u4f1a\u5728\u65f6\u95f4\u4e0a\u62d6\u4e00\u62d6\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u8fd9\u9879\u6280\u672f\u4e0d\u4f1a\u518d\u514d\u8d39\u6388\u6743\u5176\u4ed6\u5382\u5546\u4f7f\u7528\u4e86\u3002</p><p>\u3000\u3000\u770b\u7740\u5c4f\u5e55\u4e0a\u8c22\u5b50\u5609\u8f93\u5165\u7684\u6587\u5b57\uff0c\u5f20\u606a\u53c8\u5728\u4e0b\u9762\u5f55\u5165\u4e00\u884c\u6587\u5b57\uff1a\u201c\u6709\u5174\u8da3\u73b0\u5728\u5c31\u53ef\u4ee5\u8c08\u2026\u2026\u5373\u4f7f\u534e\u590f\u65d7\u4e0b\u7684\u4e09\u5bb6\u516c\u53f8\uff0c\u6211\u4eec\u4e5f\u4e0d\u4f1a\u62d2\u4e4b\u95e8\u5916\u3002\u201d</p><p>\u3000\u3000\u201c\u771f\u7684\uff1f\u201d\u8c22\u5b50\u5609\u4eae\u82e5\u9ed1\u6f06\u7684\u7738\u5b50\u76ef\u7740\u5f20\u606a\uff0c\u4f3c\u4e4e\u6000\u7591\u5f20\u606a\u62ff\u5979\u5728\u5f00\u73a9\u7b11\uff0c\u9526\u6e56\u73b0\u5728\u90fd\u6ca1\u6709\u63a8\u51fa\u79fb\u690d\u8be5\u9879\u6280\u672f\u7684\u65b0\u6b3e\u624b\u673a\uff0c\u79d1\u738b\u9ad8\u79d1\u73b0\u5728\u5c31\u4ece\u9526\u6e56\u5f15\u8fdb\u8fd9\u9879\u6280\u672f\uff0c\u867d\u8bf4\u63a8\u51fa\u65b0\u4ea7\u54c1\u5728\u65f6\u95f4\u4e0a\u4e0d\u4f1a\u6bd4\u9526\u6e56\u8d85\u524d\uff0c\u4f46\u662f\u7edd\u5bf9\u8981\u6bd4\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u7684\u8054\u4fe1\u3001\u8054\u8baf\u3001\u4e1c\u5174\u8981\u9886\u5148\u4e00\u6b65\uff0c\u5173\u952e\u8fd9\u65f6\u5019\u90a3\u4e9b\u6d77\u5916\u624b\u673a\u54c1\u724c\u90fd\u8fd8\u6ca1\u6709\u63a8\u51fa\u4e2d\u6587\u8f93\u5165\u7684\u8ba1\u5212\uff0c\u5bf9\u4e8e\u79d1\u738b\u9ad8\u79d1\u6765\u8bf4\u662f\u4e00\u6b21\u76f8\u5f53\u4e0d\u9519\u7684\u673a\u4f1a\uff0c\u8c22\u5b50\u5609\u60ca\u559c\u4e4b\u4f59\uff0c\u4e0b\u610f\u8bc6\u7684\u63d0\u9ad8\u4e86\u58f0\u8c03\uff0c\u867d\u7136\u4e5f\u63d0\u9ad8\u4e0d\u4e86\u591a\u5c11\uff0c\u4f46\u5728\u4f17\u4eba\u90fd\u5728\u5b89\u9759\u542c\u5468\u747e\u73ba\u505a\u62a5\u544a\u7684\u6559\u5ba4\u91cc\uff0c\u5374\u5c24\u663e\u5f97\u7a81\u5140\u3002</p><p>\u3000\u3000\u6559\u5ba4\u6324\u5f97\u6ee1\u5f53\u5f53\u7684\u4eba\u7fa4\u90fd\u5f80\u8fd9\u8fb9\u671b\u8fc7\u6765\uff0c\u8fd9\u4e00\u770b\u8fd8\u771f\u6c14\u4e0d\u6253\u4e00\u5904\u6765\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u8eab\u6750\u7ea4\u7ec6\uff0c\u8138\u5e9e\u6e05\u79c0\u660e\u4e3d\uff0c\u5728\u4e1c\u5927\u4e5f\u662f\u5c11\u89c1\u7684\u7f8e\u5c11\u5973\uff0c\u7075\u52a8\u7684\u7738\u5b50\u6b63\u201c\u542b\u60c5\u8109\u8109\u201d\u7684\u770b\u7740\u5f20\u606a\uff1b\u9648\u5983\u84c9\u6258\u7740\u4e0b\u5df4\u4e5f\u6b63\u9976\u6709\u5174\u8da3\u7684\u770b\u7740\u5f20\u606a\u5728\u638c\u4e0a\u7535\u8111\u4e0a\u5f55\u5165\u6587\u5b57\uff0c\u4e24\u4eba\u7684\u8eab\u4f53\u672c\u6765\u5c31\u6328\u5f97\u7d27\uff0c\u8fd9\u65f6\u5019\u8eab\u4f53\u66f4\u8d34\u8fc7\u53bb\u4e86\u3002\u8c22\u5b50\u5609\u5012\u4e5f\u7f62\u4e86\uff0c\u9648\u5983\u84c9\u4ee5\u5979\u6e05\u4e3d\u3001\u6e17\u4eba\u5fc3\u813e\u7684\u5bb9\u989c\u65e9\u5c31\u7ed9\u4e1c\u5927\u5b66\u751f\u89c6\u82e5\u7ee7\u6c5f\u9edb\u513f\u4e4b\u540e\u4e1c\u5927\u5f53\u4e4b\u65e0\u6127\u7684\u6821\u82b1\uff0c\u8fd9\u6559\u5ba4\u91cc\u5c11\u8bf4\u6709\u4e00\u534a\u4eba\u90fd\u8ba4\u5f97\u9648\u5983\u84c9\uff0c\u5f53\u7136\u4e5f\u6709\u5c11\u6570\u4eba\u8ba4\u51fa\u5f20\u606a\u5c31\u662f\u90a3\u4e2a\u6574\u5929\u966a\u9648\u5983\u84c9\u5728\u9605\u9274\u5ba4\u51fa\u73b0\u7684\u90a3\u4e2a\u5c0f\u5b50\uff0c\u8fd9\u5bb6\u4f19\u5de6\u62e5\u53f3\u62b1\uff0c\u5728\u6559\u5ba4\u91cc\u542c\u8bb2\u5ea7\u8fd8\u516c\u7136\u8ddf\u4e24\u4e2a\u7f8e\u5c11\u5973\u8c03\u60c5\uff0c\u8c01\u770b\u4e86\u5fc3\u91cc\u4f1a\u597d\u53d7\uff1f</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e0d\u4f1a\u7b80\u5355\u7684\u5c31\u4f1a\u8bef\u4ee5\u4e3a\u8c22\u5b50\u5609\u4e0e\u5f20\u606a\u5728\u6559\u5ba4\u91cc\u516c\u7136\u8c03\u60c5\uff0c\u4ed6\u4e5f\u6ce8\u610f\u5230\u5728\u5f20\u606a\u4e0e\u8c22\u5b50\u5609\u624b\u91cc\u4f20\u9012\u90a3\u53ea\u6bd4\u624b\u638c\u7565\u5c0f\u7684\u638c\u4e0a\u673a\uff0c\u731c\u6d4b\u8fd9\u591a\u534a\u662f\u9526\u6e56\u8981\u63a8\u51fa\u7684\u65b0\u4ea7\u54c1\uff0c\u53ea\u662f\u731c\u4e0d\u51fa\u662f\u4ec0\u4e48\u65b0\u73a9\u827a\u4f1a\u8ba9\u8c22\u5b50\u5609\u559c\u5f62\u4e8e\u8272\uff0c\u4ed6\u5728\u8bb2\u53f0\u4e0a\u7ad9\u7740\uff0c\u7709\u5934\u5fae\u5fae\u8e59\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u7684\u52a9\u6559\u770b\u5230\u4ed6\u8fd9\u795e\u8272\u8fd8\u4ee5\u4e3a\u4ed6\u5bf9\u5f20\u606a\u3001\u8c22\u5b50\u5609\u4ee5\u53ca\u9648\u5983\u84c9\u4e09\u4e2a\u4eba\u5728\u6559\u5ba4\u91cc\u5f71\u54cd\u4ed6\u4eba\u7684\u8868\u73b0\u76f8\u5f53\u4e0d\u6ee1\uff0c\u63a8\u4e86\u63a8\u773c\u955c\u67b6\uff0c\u77ee\u7740\u8eab\u5b50\u8d70\u5230\u540e\u9762\uff0c\u538b\u4f4e\u7740\u58f0\u97f3\u6559\u8bad\u5f20\u606a\uff1a\u201c\u4f60\u8981\u4e0d\u60f3\u542c\u8bb2\u5ea7\uff0c\u53ef\u4ee5\u51fa\u53bb\uff0c\u4e0d\u8981\u5f71\u54cd\u522b\u4eba\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u4e86\u5468\u747e\u73ba\u5173\u4e8e\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u7684\u8bba\u8c03\u6b63\u4e00\u809a\u5b50\u4e0d\u6ee1\uff0c\u751a\u81f3\u6000\u7591\u5468\u747e\u73ba\u7684\u89c2\u70b9\u4f1a\u4e25\u91cd\u5f71\u54cd\u5230\u56fd\u5185\u7684\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8d70\u5165\u6b67\u9014\uff0c\u65e9\u5c31\u4e0d\u8010\u70e6\u542c\u4ed6\u7ee7\u7eed\u80e1\u626f\u4e0b\u53bb\uff0c\u8fd9\u65f6\u5019\u770b\u5230\u5468\u747e\u73ba\u7684\u52a9\u6559\u8ddf\u53ea\u72d7\u4f3c\u7684\u8dd1\u8fc7\u6765\u538b\u4f4e\u55d3\u5b50\u6559\u8bad\u4eba\uff0c\u7709\u5934\u5fae\u5fae\u4e00\u626c\uff0c\u51b7\u51b7\u76ef\u4e86\u90a3\u5c0f\u5b50\u4e00\u773c\uff0c\u4fa7\u8fc7\u5934\u5bf9\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u8fd9\u79cd\u6c34\u5e73\u7684\u8bb2\u5ea7\uff0c\u4e0d\u542c\u4e5f\u7f62\u2026\u2026\u201d\u60f3\u5230\u672a\u6765\u5341\u5e74\u540e\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u4ee4\u4eba\u987f\u8db3\u6376\u80f8\u7684\u5c40\u9762\uff0c\u5f20\u606a\u5fc3\u91cc\u5bf9\u5468\u747e\u73ba\u8fd9\u79cd\u6240\u8c13\u5b66\u8005\u7684\u8154\u8c03\u5c24\u5176\u75db\u6068\u4e86\uff0c\u5c31\u60f3\u7740\u72e0\u72e0\u7684\u523a\u5468\u747e\u73ba\u4e00\u4e0b\uff0c\u8ddf\u9648\u5983\u84c9\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u4e5f\u6ca1\u6709\u538b\u7740\u58f0\u97f3\uff0c\u5728\u6559\u5ba4\u91cc\u5c24\u663e\u5f97\u523a\u8033\u3002\u5468\u747e\u73ba\u518d\u80fd\u81ea\u6b3a\u6b3a\u4eba\uff0c\u4e5f\u65e0\u6cd5\u5047\u88c5\u542c\u4e0d\u89c1\uff0c\u5728\u53f0\u4e0a\u505c\u4e86\u4e0b\u6765\uff0c\u4e00\u8138\u4e0d\u8c6b\u7684\u770b\u5411\u8fd9\u8fb9\u3002</p><p>\u3000\u3000\u5f20\u606a\u624d\u4e0d\u7ba1\u5468\u747e\u73ba\u5fc3\u60c5\u597d\u4e0d\u597d\uff0c\u8d70\u8d77\u6765\uff0c\u8ba9\u8c22\u5b50\u5609\u8ba9\u4e00\u8ba9\uff0c\u7ed9\u4ed6\u4e0e\u9648\u5983\u84c9\u8d70\u51fa\u53bb\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u7684\u5fc3\u601d\u5b8c\u5168\u5728\u6570\u5b57\u952e\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e0a\uff0c\u521a\u89c1\u5f20\u606a\u677e\u4e86\u53e3\uff0c\u542c\u4ed6\u8981\u62c9\u7740\u9648\u5983\u84c9\u79bb\u5f00\uff0c\u4e5f\u6ca1\u6709\u591a\u60f3\uff0c\u66f4\u4e0d\u4f1a\u60f3\u5230\u8981\u53bb\u7167\u987e\u5468\u747e\u73ba\u5728\u53f0\u4e0a\u7684\u989c\u9762\uff0c\u8d77\u8eab\u5148\u8d70\u4e86\u51fa\u53bb\u3002\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4ed6\u4eec\u5f53\u7136\u4ee5\u5f20\u606a\u9a6c\u9996\u662f\u77bb\uff0c\u5728\u6559\u5ba4\u540e\u9762\u5f04\u51fa\u4e9b\u52a8\u9759\u4e5f\u8981\u5f80\u5916\u8d70\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u540c\u5b66\uff0c\u542c\u4f60\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u5bf9\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u7684\u53d1\u5c55\u53e6\u6709\u9ad8\u89c1\uff1f\u201d\u5468\u747e\u73ba\u603b\u4e0d\u80fd\u5fcd\u4f4f\u5fc3\u4e2d\u7684\u4e00\u53e3\u6c14\uff0c\u5728\u5f20\u606a\u4e0e\u4e09\u4e2a\u5973\u5b69\u8d70\u51fa\u6559\u5ba4\u4e4b\u524d\u558a\u4f4f\u4ed6\u3002</p><p>\u3000\u3000\u201c\u95ee\u6211\u7684\u610f\u89c1\uff1f\u201d\u5f20\u606a\u8f6c\u8fc7\u5934\u53bb\uff0c\u770b\u7740\u8138\u8272\u9634\u6c89\u7684\u5468\u747e\u73ba\uff0c\u53c8\u8f6c\u5934\u770b\u89c1\u6559\u5ba4\u91cc\u5750\u7740\u542c\u8bb2\u5ea7\u7684\u5b66\u751f\u773c\u795e\u91cc\u591a\u6709\u4e0d\u5584\uff0c\u773c\u5e18\u5782\u4e0b\u6765\uff0c\u770b\u7740\u811a\u5c16\u4e24\u4e09\u79d2\uff0c\u53c8\u62ac\u8d77\u5934\u6765\u770b\u7740\u5468\u747e\u73ba\uff0c\u201c\u4f60\u4ee5\u4e3a\u6211\u662f\u6545\u610f\u6765\u6363\u86cb\u7684\u662f\u4e0d\u662f\uff1f\u201d\u5f84\u76f4\u8d70\u5230\u8bb2\u53f0\u4e0a\uff0c\u6307\u7740\u53f0\u4e0b\u7684\u4eba\u95ee\u5468\u747e\u73ba\uff0c\u201c\u6211\u6ca1\u6709\u4ec0\u4e48\u9ad8\u89c1\uff0c\u4f46\u662f\u4f60\u6709\u52c7\u6c14\u5f53\u7740\u53f0\u4e0b\u56db\u767e\u540d\u542c\u4f17\u6765\u8bb2\u89e3\u672a\u6765\u4e2d\u56fd\u7a00\u571f\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u60f3\u5fc5\u5bf9\u4e2d\u56fd\u7684\u7a00\u571f\u4ea7\u4e1a\u73b0\u72b6\u6709\u7740\u6781\u6df1\u5165\u7684\u4e86\u89e3\uff0c\u6211\u6709\u51e0\u4e2a\u95ee\u9898\u60f3\u95ee\u95ee\u4f60\uff01\u201d</p><p>\u3000\u3000\u867d\u8bf4\u9636\u68af\u6559\u5ba4\u91cc\u5750\u7740\u7684\u56db\u767e\u591a\u540d\u5b66\u751f\u5bf9\u5f20\u606a\u201c\u5de6\u62e5\u53f3\u62b1\u201d\u7684\u884c\u4e3a\u76f8\u5f53\u5fcc\u5992\uff0c\u4f46\u662f\u542c\u8bb2\u5ea7\u770b\u5230\u6709\u4eba\u7ad9\u51fa\u6765\u6405\u573a\u5b50\u53c8\u662f\u4ed6\u4eec\u5f02\u5e38\u671f\u5f85\u7684\u4e8b\u60c5\uff0c\u675c\u98de\u3001\u65f6\u5b66\u658c\u3001\u8499\u4e50\u4ed6\u4eec\u53c8\u4e0d\u5931\u65f6\u673a\u7684\u5728\u6559\u5ba4\u540e\u9762\u8d77\u54c4\uff0c\u5468\u747e\u73ba\u8138\u8272\u66f4\u52a0\u96be\u770b\uff0c\u6ca1\u60f3\u5230\u4e00\u53e3\u6c14\u6ca1\u6709\u54bd\u4e0b\u8ba9\u81ea\u5df1\u9677\u5165\u66f4\u88ab\u52a8\u7684\u5730\u6b65\uff0c\u5f53\u7136\uff0c\u4ed6\u4e5f\u4e0d\u4fe1\u5f20\u606a\u5bf9\u7a00\u571f\u4ea7\u4e1a\u7684\u73b0\u72b6\u80fd\u6709\u591a\u6df1\u523b\u7684\u89c1\u89e3\uff0c\u8138\u8272\u867d\u7136\u96be\u770b\uff0c\u5374\u4f9d\u7136\u80fd\u4fdd\u6301\u51b7\u9759\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5728\u505a\u8bb2\u5ea7\u65f6\uff0c\u4e5f\u559c\u6b22\u542c\u5230\u6709\u5b66\u751f\u63d0\u95ee\uff0c\u867d\u7136\u8fd8\u6ca1\u6709\u5230\u63d0\u95ee\u65f6\u95f4\uff0c\u4f46\u4e0d\u59a8\u5148\u542c\u4f60\u8bf4\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u5efa\u8bae\u91c7\u77ff\u6743\u5411\u5730\u65b9\u653e\u677e\uff0c\u7a00\u571f\u5f00\u91c7\u4e0e\u51b6\u70bc\u5206\u79bb\u884c\u4e1a\u5141\u8bb8\u79c1\u6709\u8d44\u672c\u8fdb\u5165\uff0c\u5141\u8bb8\u5916\u8d44\u8fdb\u5165\u7a00\u571f\u77ff\u6df1\u52a0\u5de5\u4e0e\u5de5\u4e1a\u5e94\u7528\u5f00\u53d1\u9886\u57df\u3002\u867d\u7136\u76ee\u524d\u7a00\u571f\u5f00\u91c7\u6ca1\u6709\u5411\u79c1\u4f01\u653e\u5f00\uff0c\u4f46\u662f\u56fd\u5185\u6709\u7a00\u571f\u8d44\u6e90\u7684\u5730\u65b9\uff0c\u5730\u65b9\u653f\u5e9c\u4e0e\u79c1\u4eba\u76f8\u52fe\u7ed3\u76d7\u91c7\u7a00\u571f\u77ff\u7684\u73b0\u72b6\u6709\u591a\u4e25\u91cd\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u4ee5\u7ae0\u5dde\u4e3a\u4f8b\uff0c\u7ae0\u5dde\u4ec5\u6709\u56db\u5f20\u7a00\u571f\u5f00\u91c7\u6743\u8bc1\uff0c\u6bcf\u5e74\u9650\u989d\u5f00\u91c7\u7a00\u571f\u77ff1000\u5428\uff0c\u4f46\u662f\u6bcf\u5e74\u4ece\u7ae0\u5dde\u6d41\u5165\u5e02\u573a\u7684\u91cd\u7a00\u571f\u91d1\u5c5e\u7cbe\u77ff\u9ad8\u8fbe8000\u5428\u2026\u2026\u4f60\u7684\u5efa\u8bae\uff0c\u4e0d\u8fc7\u662f\u8ba9\u79c1\u4eba\u4e0e\u5730\u65b9\u653f\u5e9c\u52fe\u7ed3\u8d77\u6765\u7684\u76d7\u91c7\u7531\u975e\u6cd5\u53d8\u5408\u6cd5\uff0c\u82e5\u771f\u662f\u5bf9\u5730\u65b9\u7ecf\u6d4e\u6709\u5229\uff0c\u6211\u4eec\u5f53\u7136\u80fd\u591f\u62e5\u62a4\u4f60\u7684\u5efa\u8bae\uff0c\u6211\u60f3\u95ee\u4e00\u95ee\uff0c\u4f60\u5bf9\u4e2d\u56fd\u5f53\u524d\u7684\u7a00\u571f\u77ff\u8d44\u6e90\u5f00\u91c7\u4e0e\u5206\u79bb\u60c5\u51b5\u4e86\u89e3\u591a\u5c11\uff1f\u4f60\u77e5\u9053\u7ae0\u5dde\u79bb\u5b50\u578b\u7a00\u571f\u77ff\u8981\u5206\u79bb\u4e00\u5428\u7cbe\u77ff\uff0c\u9700\u8981\u591a\u5c11\u5428\u77ff\u571f\u2026\u2026\u201d\u5f20\u606a\u773c\u775b\u76ef\u7740\u5468\u747e\u73ba\uff0c\u770b\u5230\u4ed6\u7684\u773c\u795e\u95ea\u70c1\uff0c\u6beb\u4e0d\u7559\u60c5\u7684\u8bf4\u9053\uff0c\u201c\u6211\u6765\u544a\u8bc9\u4f60\uff0c\u7ae0\u5dde\u79bb\u5b50\u578b\u7a00\u571f\u5206\u79bb\u4e00\u5428\u7cbe\u77ff\uff0c\u9700\u8981\u8d85\u8fc7\u4e00\u5343\u5428\u7684\u77ff\u571f\u2014\u2014\u5176\u4e2d\u8d85\u8fc770\uff05\u7684\u7a00\u571f\u91d1\u5c5e\u56e0\u4e3a\u843d\u540e\u7684\u5f00\u91c7\u5de5\u827a\u800c\u6d6a\u8d39\u6389\u3002\u90a3\u4e9b\u79c1\u6709\u76d7\u91c7\u4f01\u4e1a\u5f53\u4ece\u77ff\u5c71\u91cc\u76d7\u91c7\u8d70\u4e00\u5428\u7cbe\u77ff\uff0c\u6240\u4ed8\u51fa\u7684\u6210\u672c\u6781\u4e3a\u6709\u9650\uff0c\u4e3b\u8981\u662f\u4e9b\u4eba\u5de5\u3001\u673a\u68b0\u78e8\u635f\u4e0e\u5316\u5b66\u836f\u5242\uff0c\u4f46\u662f\u6bcf\u5428\u7cbe\u77ff\u5728\u56fd\u9645\u5e02\u573a\u4e0a\u53ef\u4ee5\u552e\u51fa\u516b\u5230\u5341\u4e07\u5143\u7684\u9ad8\u4ef7\uff0c\u4f46\u662f\u5728\u8fd9\u7b14\u4e00\u76ee\u4e86\u7136\u7684\u7ecf\u6d4e\u8d26\u4e4b\u5916\uff0c\u662f\u4e00\u5343\u5428\u77ff\u571f\u539f\u5148\u8986\u76d6\u7684\u5730\u8868\u690d\u88ab\u7ed9\u5b8c\u5168\u7834\u574f\u2014\u2014\u8fd9\u8fd8\u4e0d\u662f\u6700\u4e25\u91cd\u7684\uff0c\u6700\u4e25\u91cd\u7684\u662f\u4e00\u5343\u5428\u77ff\u571f\u7ed9\u5f3a\u6c61\u67d3\u836f\u5242\u5b8c\u5168\u6c61\u67d3\u540e\u672a\u7ecf\u4efb\u4f55\u5904\u7406\u5f03\u5728\u8352\u91ce\uff0c\u5c06\u6c61\u67d3\u7ee7\u7eed\u6269\u6563\u5230\u5730\u8868\u6c34\u3001\u5730\u4e0b\u6c34\u2026\u2026\u5982\u679c\u6309\u7167\u4f60\u7684\u5efa\u8bae\uff0c\u653e\u5f00\u5e02\u573a\u5316\u7ade\u4e89\uff0c\u4e2d\u56fd\u6ca1\u6709\u8003\u8651\u4efb\u4f55\u73af\u5883\u6210\u672c\u7684\u7a00\u571f\u4ea7\u54c1\u81ea\u7136\u6709\u7740\u66f4\u5f3a\u7684\u7ade\u4e89\u529b\uff0c\u4f1a\u8fdb\u4e00\u6b65\u7684\u4fc3\u8fdb\u7a00\u571f\u4ef7\u683c\u7684\u4e0b\u8dcc\u2026\u2026\u5f53\u7a00\u571f\u6df1\u52a0\u5de5\u9886\u57df\u5f15\u8fdb\u5916\u8d44\uff0c\u4f1a\u8fdb\u4e00\u6b65\u4fc3\u8fdb\u56fd\u5185\u8fd9\u79cd\u7834\u574f\u5f0f\u517c\u6d6a\u8d39\u5f0f\u7684\u5f00\u91c7\u89c4\u6a21\u2026\u2026\u201d</p></article>"}}}]);
