(window.webpackJsonp=window.webpackJsonp||[]).push([[1001],{1024:function(p){p.exports={chapterTitle:null,title:"\u7b2c809\u7ae0 \u6c14\u5410\u8840",content:"<article><h2>\u7b2c809\u7ae0 \u6c14\u5410\u8840</h2><p>\u3000\u3000\u5468\u747e\u73ba\u8fd9\u624d\u77e5\u9053\u558a\u4f4f\u5f20\u606a\u90a3\u662f\u81ea\u53d6\u5176\u8fb1\uff0c\u8138\u4e0a\u9752\u4e00\u9053\u767d\u4e00\u9053\uff0c\u504f\u504f\u4e8f\u5fc3\u5728\u5148\u53c8\u62ff\u4e0d\u8a00\u8bed\u6765\u53cd\u9a73\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ef\u4e0d\u4f1a\u770b\u5230\u5468\u747e\u73ba\u8138\u8272\u96be\u770b\u5c31\u653e\u5f03\u5bf9\u4ed6\u7684\u653b\u51fb\uff0c\u7ee7\u7eed\u8bf4\u9053\uff1a\u201c\u4f60\u662f\u56fd\u5185\u77e5\u540d\u7684\u5b66\u8005\uff0c\u4f60\u80fd\u60f3\u8c61\u5230\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u6309\u7167\u4f60\u5efa\u8bae\u7684\u53bb\u5236\u8ba2\u4ea7\u4e1a\u653f\u7b56\u4f1a\u9020\u6210\u600e\u6837\u7684\u4e25\u91cd\u540e\u679c\u5417\uff1f\u4f60\u60f3\u8c61\u4e0d\u5230\uff0c\u90a3\u6211\u6765\u544a\u8bc9\u4f60\uff1a\u56fd\u5185\u6781\u4e3a\u91cd\u8981\u7684\u6218\u7565\u8d44\u6e90\u7a00\u571f\u77ff\u4ea7\u5c06\u4f1a\u4ee5\u6bd4\u6ce5\u571f\u9ad8\u4e0d\u4e86\u591a\u5c11\u7684\u4ef7\u683c\u7ed9\u6b27\u7f8e\u65e5\u97e9\u7b49\u56fd\u63a0\u593a\uff0c\u56fd\u5185\u9664\u4e86\u80fd\u80a5\u90a3\u4e9b\u79c1\u8425\u77ff\u4e3b\u4e4b\u5916\u2014\u2014\u56e0\u4e3a\u4ed6\u4eec\u5f00\u91c7\u7a00\u571f\u5b8c\u5168\u4e0d\u53bb\u8003\u8651\u4f1a\u4e0d\u4f1a\u6c61\u67d3\u73af\u5883\uff0c\u4ed8\u51fa\u7684\u6210\u672c\u5f88\u4f4e\u5ec9\uff0c\u4f46\u662f\u4e3a\u6b64\u8981\u4ed8\u51fa\u7684\u6781\u4e3a\u60e8\u91cd\u7684\u4ee3\u4ef7\u5374\u662f\u4e00\u76ee\u4e86\u7136\u7684\uff0c\u4e00\u662f\u7a00\u571f\u8d44\u6e90\u5c06\u906d\u5230\u7834\u574f\u5f0f\u7684\u5f00\u53d1\uff0c\u4e00\u662f\u77ff\u533a\u751f\u6001\u73af\u5883\u5c06\u906d\u5230\u96be\u4ee5\u60f3\u8c61\u7684\u6467\u6b8b\u2026\u2026\u4f60\u662f\u6ca1\u8003\u8651\u8fc7\u8fd9\u6837\u7684\u540e\u679c\uff0c\u8fd8\u662f\u8003\u8651\u5230\u8fd9\u6837\u7684\u540e\u679c\u4f46\u662f\u65e0\u8bba\u540e\u679c\u600e\u6837\u90fd\u8ddf\u4f60\u662f\u4e0d\u662f\u56fd\u5185\u77e5\u540d\u5b66\u8005\u6ca1\u6709\u4e1d\u6beb\u7684\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u773c\u795e\u51b7\u5cfb\u7684\u76ef\u7740\u5468\u747e\u73ba\uff0c\u53f0\u4e0b\u542c\u4f17\u9e26\u96c0\u65e0\u58f0\uff0c\u8c01\u90fd\u672a\u66fe\u89c1\u8bc6\u8fc7\u80fd\u6709\u54ea\u4e2a\u5b66\u751f\u4ee5\u7280\u5229\u3001\u6beb\u4e0d\u7559\u60c5\u7684\u8a00\u8bed\u5c06\u56fd\u5185\u77e5\u540d\u7684\u6559\u6388\u5e72\u7ffb\u5728\u8bb2\u53f0\u4e0a\uff1f\u4f5c\u4e3a\u542c\u4f17\uff0c\u6240\u80fd\u63a5\u89e6\u5230\u4fe1\u606f\u91cf\u90fd\u5f88\u6709\u9650\uff0c\u773c\u524d\u7684\u60c5\u5f62\u4e5f\u8ba9\u4ed6\u4eec\u771f\u5047\u83ab\u8fa9\uff0c\u4f46\u662f\u5468\u747e\u73ba\u7ed9\u5f20\u606a\u53cd\u9a73\u5f97\u54d1\u53e3\u65e0\u8a00\u5374\u53c8\u662f\u773c\u4e0b\u65e0\u6cd5\u5ffd\u89c6\u7684\u4e8b\u5b9e\u3002\u6709\u4eba\u542c\u5f20\u606a\u8a00\u8f9e\u950b\u5229\u7684\u8bb2\u8bdd\u53ea\u89c9\u5f97\u70ed\u8840\u6cb8\u817e\uff0c\u6709\u4eba\u5374\u89c9\u5f97\u5f20\u606a\u4e0d\u77e5\u5929\u9ad8\u5730\u539a\uff0c\u675c\u98de\u4ed6\u4eec\u5374\u5728\u6559\u5ba4\u540e\u6392\u5e26\u5934\u9f13\u638c\u8d77\u54c4\u3002</p><p>\u3000\u3000\u201c\u4f60\u2026\u2026\u201d\u5468\u747e\u73ba\u53ea\u89c9\u5f97\u80f8\u53e3\u5835\u7740\u4e00\u53e3\u6c14\uff0c\u5934\u76ae\u53d1\u6da8\uff0c\u6c14\u8840\u76f4\u5f80\u80f8\u53e3\u6d8c\uff0c\u6307\u7740\u5f20\u606a\u7684\u9f3b\u5b50\u5374\u4e00\u53e5\u8bdd\u90fd\u8bf4\u4e0d\u51fa\u53e3\u3002</p><p>\u3000\u3000\u201c\u4f60\u80fd\u4e0d\u80fd\u6478\u7740\u81ea\u5df1\u7684\u80f8\u53e3\u544a\u8bc9\u53f0\u4e0b\u56db\u767e\u591a\u542c\u4f17\uff0c\u4f60\u521a\u624d\u63d0\u51fa\u7684\u89c2\u70b9\u662f\u51fa\u81ea\u4f60\u72ec\u7acb\u7684\u4ea7\u4e1a\u89c2\u5bdf\u4e0e\u601d\u8003\uff0c\u800c\u975e\u6536\u5230\u67d0\u4e9b\u5229\u76ca\u96c6\u56e2\u7684\u91d1\u94b1\u8d5e\u52a9\uff1f\u4f60\u6307\u7740\u6211\u7684\u9f3b\u5b50\u4e5f\u6ca1\u6709\u7528\uff0c\u6211\u4e0d\u662f\u90a3\u79cd\u53ef\u4ee5\u7ed9\u4f60\u8f7b\u6613\u7cca\u5f04\u7684\u4eba\uff0c\u6211\u8fd8\u6709\u4e9b\u826f\u77e5\uff0c\u624d\u7ad9\u5728\u53f0\u4e0a\u6765\u6307\u51fa\u4f60\u7684\u89c2\u70b9\u662f\u591a\u4e48\u7684\u5c45\u5fc3\u53f5\u6d4b\uff01\u201d\u5f20\u606a\u51b7\u7b11\u9053\uff0c\u201c\u5f53\u4f60\u7ad9\u5728\u8bb2\u53f0\u524d\uff0c\u4f60\u5c31\u5e94\u8be5\u8981\u8003\u8651\u81ea\u5df1\u8eab\u4e3a\u56fd\u5185\u77e5\u540d\u7ecf\u6d4e\u5b66\u8005\u7684\u826f\u77e5\u3001\u8d23\u4efb\u4e0e\u7acb\u573a\uff0c\u800c\u4e0d\u662f\u67d0\u4e9b\u5229\u76ca\u9636\u5c42\u7684\u4ee3\u8a00\u4eba\u4e0e\u8d70\u72d7\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u5468\u747e\u73ba\u7684\u8840\u7ebf\u4ece\u8116\u5b50\u6897\u5f80\u4e0a\u6d8c\uff0c\u5f20\u606a\u6536\u4f4f\u5634\u4e0d\u518d\u8bf4\u4ec0\u4e48\uff0c\u8f6c\u8eab\u5f80\u5916\u8d70\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u662f\u54ea\u4e2a\u9662\u7684\uff0c\u8c01\u6307\u4f7f\u4f60\u51fa\u6765\u80e1\u8bf4\u516b\u9053\u7684\uff1f\u201d\u5468\u747e\u73ba\u7684\u52a9\u6559\u8fd9\u624d\u4ece\u9707\u60ca\u4e2d\u56de\u8fc7\u795e\u6765\uff0c\u89c9\u5f97\u8981\u4e3a\u5468\u747e\u73ba\u51fa\u53e3\u6076\u6c14\u3002</p><p>\u3000\u3000\u201c\u6211\u80e1\u8bf4\u516b\u9053\uff1f\u201d\u5f20\u606a\u8f7b\u8511\u7684\u770b\u7740\u5468\u747e\u73ba\u90a3\u4e2a\u59d3\u82cf\u7684\u52a9\u6559\u4e00\u773c\uff0c\u201c\u4f60\u6709\u4ec0\u4e48\u8d44\u683c\u8bf4\u6211\u5728\u80e1\u8bf4\u516b\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f97\u9976\u4eba\u5904\u4e14\u9976\u4eba\uff0c\u8fd9\u4f4d\u5c0f\u5144\u5f1f\uff0c\u4f60\u8fd9\u5c16\u523b\u7684\u4f36\u7259\u5229\u9f7f\uff0c\u53ef\u4e0d\u4f1a\u7ed9\u4f60\u5e26\u53bb\u4ec0\u4e48\u597d\u5904\uff1f\u201d\u90a3\u4e2a\u4e2d\u5348\u5728\u6559\u5de5\u98df\u5802\u5750\u5728\u5468\u747e\u73ba\u5bf9\u9762\u7684\u4e2d\u5e74\u7537\u5b50\u8fd9\u65f6\u5019\u5750\u5728\u79bb\u6559\u5ba4\u524d\u95e8\u6700\u8fd1\u7684\u7b2c\u4e00\u6392\uff0c\u8fd9\u65f6\u5019\u4e5f\u5fcd\u4e0d\u4f4f\u51fa\u58f0\u6559\u8bad\u5f20\u606a\uff0c\u58f0\u97f3\u4f4e\u6d51\uff0c\u5374\u4e0d\u65e0\u5a01\u80c1\u4e4b\u610f\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u7709\u5934\u626c\u4e86\u626c\uff0c\u770b\u7740\u8fd9\u4e2a\u603b\u89c9\u5f97\u6709\u4e9b\u9762\u719f\u7684\u4e2d\u5e74\u7537\u5b50\uff0c\u51b7\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u8981\u600e\u4e48\u8bf4\u8bdd\u624d\u6709\u597d\u5904\uff1f\u96be\u9053\u50cf\u53f0\u4e0a\u90a3\u4f4d\u77e5\u540d\u7684\u7ecf\u6d4e\u5b66\u5468\u6559\u6388\u8bf4\u8bdd\u624d\u4f1a\u6709\u597d\u5904\uff1f\u201d\u56de\u5934\u770b\u4e86\u5468\u747e\u73ba\u4e00\u773c\uff0c\u6070\u597d\u770b\u5230\u5468\u747e\u73ba\u6447\u6447\u6643\u6643\u7684\u4e00\u5934\u5f80\u8bb2\u53f0\u4e0a\u683d\u53bb\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u5728\u6559\u5ba4\u91cc\u660f\u53a5\u8fc7\u53bb\uff0c\u6559\u5ba4\u91cc\u987f\u65f6\u50cf\u70b8\u5f00\u4e86\u7684\u9a6c\u8702\u7a9d\uff0c\u5468\u747e\u73ba\u7684\u52a9\u6559\u4e0e\u56fd\u5546\u9662\u7684\u6559\u6388\u6c88\u6d4e\u4e00\u7bad\u6b65\u8df3\u5230\u8bb2\u53f0\u4e0a\uff0c\u5c06\u989d\u5934\u7ed9\u649e\u7834\u3001\u5012\u5728\u5730\u4e0a\u4eba\u4e8b\u65e0\u77e5\u7684\u5468\u747e\u73ba\u6276\u8d77\uff0c\u966a\u540c\u5468\u747e\u73ba\u5728\u6559\u5de5\u98df\u5802\u51fa\u73b0\u7684\u90a3\u4e09\u540d\u4e2d\u5e74\u7537\u4eba\u4e5f\u90fd\u6025\u5fd9\u7684\u8d70\u5230\u8bb2\u53f0\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u8c8c\u4f3c\u65e0\u8f9c\u7684\u644a\u4e86\u644a\u624b\uff0c\u4ed6\u4e5f\u6ca1\u6709\u60f3\u5230\u5468\u747e\u73ba\u7684\u5fc3\u7406\u627f\u53d7\u529b\u8fd9\u4e48\u5dee\uff0c\u770b\u4ed6\u5634\u89d2\u91cc\u6709\u6e17\u51fa\u8840\u4e1d\uff0c\u5927\u6982\u7ed9\u6c14\u5f97\u5410\u8840\u3001\u8fd9\u8840\u8fd8\u5f97\u5f80\u809a\u5b50\u91cc\u54bd\u3002</p><p>\u3000\u3000\u8d81\u7740\u6df7\u4e71\uff0c\u5f20\u606a\u62c9\u7740\u9648\u5983\u84c9\u5c31\u51fa\u4e86\u6559\u5ba4\uff0c\u675c\u98de\u3001\u8499\u4e50\u3001\u65f6\u5b66\u658c\u4e5f\u4ece\u540e\u95e8\u6e9c\u51fa\u6765\u8ddf\u4ed6\u4eec\u6c47\u5408\uff0c\u675c\u98de\u770b\u7740\u8c22\u5b50\u5609\u7ad9\u5728\u5f20\u606a\u8eab\u540e\uff0c\u5947\u602a\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4e0d\u5f97\u5bf9\u5f20\u606a\u6068\u4e4b\u5165\u9aa8\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e3a\u4ec0\u4e48\u8981\u5bf9\u5f20\u606a\u6068\u4e4b\u5165\u9aa8\uff0c\u5c31\u56e0\u4e3a\u5f20\u606a\u8fd9\u5f20\u5229\u5634\u8bf4\u5f97\u67d0\u4eba\u5410\u8840\u6655\u5012\uff1f\u201d\u8c22\u5b50\u5609\u5149\u6d01\u7684\u989d\u5934\u5fae\u4ef0\u7740\uff0c\u201c\u4ed6\u672c\u6765\u5c31\u6709\u4e8f\u4e8e\u5fc3\uff0c\u624d\u7ed9\u8bf4\u5f97\u54d1\u53e3\u65e0\u8a00\u2026\u2026\u201d\u53c8\u8be7\u5f02\u7684\u76ef\u7740\u5f20\u606a\uff0c\u201c\u4f60\u600e\u4e48\u5c31\u80fd\u8bf4\u5f97\u4ed6\u54d1\u53e3\u65e0\u8a00\uff1f\u201d</p><p>\u3000\u3000\u201c\u5468\u747e\u73ba\u771f\u662f\u9047\u4eba\u4e0d\u6dd1\u554a\uff0c\u65f6\u8fd0\u4e5f\u4e0d\u6d4e\uff0c\u8d76\u5de7\u6211\u8fd9\u51e0\u5929\u5728\u770b\u7a00\u571f\u4ea7\u4e1a\u65b9\u9762\u7684\u8d44\u6599\u2026\u2026\u201d\u5f20\u606a\u54c2\u7136\u4e00\u7b11\uff0c\u4e0e\u4ed6\u4eec\u4e00\u8d77\u5f80\u5916\u8d70\u53bb\uff0c\u201c\u8981\u4e0d\u662f\u770b\u5230\u8bb2\u5ea7\u8ddf\u7a00\u571f\u6750\u6599\u6709\u5173\uff0c\u6211\u4e5f\u4e0d\u4f1a\u8fc7\u6765\u51d1\u8fd9\u4e2a\u70ed\u95f9\uff0c\u6211\u90fd\u8981\u8d70\u51fa\u6765\u4e86\uff0c\u6ca1\u60f3\u5230\u4ed6\u4eec\u786c\u8981\u5f80\u6211\u7684\u67aa\u53e3\u4e0a\u649e\uff0c\u6211\u5f53\u7136\u4e0d\u4f1a\u5ba2\u6c14\u3002\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u6ca1\u5fc3\u6ca1\u80ba\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u53c8\u77a5\u773c\u770b\u7740\u5f20\u606a\u88c5\u638c\u4e0a\u7535\u8111\u7684\u5916\u5957\u53e3\u888b\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u6211\u5c31\u544a\u8bc9\u9648\u9759\u4e86\uff0c\u4f60\u662f\u5e0c\u671b\u9648\u9759\u6765\u627e\u4f60\uff0c\u8fd8\u662f\u53bb\u627e\u4e01\u69d0\uff1f\u201d\u5979\u5ff5\u5ff5\u4e0d\u5fd8\u7684\u8fd8\u662f\u624b\u673a\u4e2d\u6587\u8f93\u5165\u6280\u672f\u3002</p><p>\u3000\u3000\u201c\u968f\u4fbf\u627e\u8c01\u90fd\u6210\u2026\u2026\u627e\u6211\u4e5f\u505a\u4e0d\u4e86\u4ec0\u4e48\u4e8b\u60c5\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5df2\u7ecf\u662f\u9ec4\u660f\u4e86\uff0c\u5468\u672b\u8bb8\u591a\u5230\u6559\u5ba4\u81ea\u4fee\u7684\u5b66\u751f\u4e5f\u90fd\u7eb7\u7eb7\u79bb\u5f00\u6559\u5ba4\u56de\u5bbf\u820d\uff0c\u7ad9\u5728\u5de5\u5b57\u68af\u524d\u7684\u53f0\u9636\u4e0a\uff0c\u4e00\u4f1a\u513f\u770b\u7740\u5468\u747e\u73ba\u7ed9\u4eba\u62ac\u4e86\u51fa\u6765\uff0c\u8fd8\u6ca1\u6709\u82cf\u9192\u8fc7\u6765\uff0c\u90a3\u4f19\u4eba\u6025\u51b2\u51b2\u7684\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u5f20\u606a\u4ed6\u4eec\u8eb2\u5728\u89d2\u843d\u91cc\u770b\u597d\u620f\u3002\u5f20\u606a\u5728\u6559\u5de5\u98df\u5802\u524d\u7a7a\u5730\u770b\u5230\u90a3\u90e8\u5954\u9a70\u3001\u608d\u9a6c\u5f00\u8fc7\u6765\uff0c\u5c06\u660f\u53a5\u8fc7\u53bb\u7684\u5468\u747e\u73ba\u3001\u6c88\u6d4e\u4ee5\u53ca\u90a3\u4e09\u540d\u4e2d\u5e74\u7537\u4eba\u8f7d\u8d70\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u644a\u4e86\u644a\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5148\u8d70\u4e86\uff0c\u8981\u7ed9\u6211\u7238\u77e5\u9053\u6211\u8fd9\u65f6\u5019\u8fd8\u8ddf\u4f60\u6df7\u5728\u4e00\u8d77\uff0c\u4e00\u5b9a\u7ed9\u8bad\u6211\u4e00\u901a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5148\u8d70\u4e86\uff0c\u8ddf\u7740\u675c\u98de\u3001\u8499\u4e50\u8fc7\u6765\u542c\u8bb2\u5ea7\u7684\u65f6\u5b66\u658c\u770b\u7740\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5b70\u662f\u5b70\u975e\uff0c\u6211\u4e00\u65f6\u8fd8\u62ce\u4e0d\u6e05\u695a\uff0c\u4e0d\u8fc7\u6211\u6562\u80af\u5b9a\uff0c\u4f60\u8ddf\u5468\u747e\u73ba\u4e00\u5b9a\u6709\u4e0d\u5c0f\u7684\u6069\u6028\u5462\uff0c\u4e0d\u7136\u4e0d\u4f1a\u8fd9\u6837\u715e\u4ed6\u7684\u9762\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8bf4\u662f\u6df1\u4ec7\u5927\u75db\u4e5f\u4e0d\u8fc7\u5206\u554a\u3002\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u4e00\u7b11\uff0c\u4e0d\u77e5\u9053\u665a\u6674\u542c\u5230\u5468\u747e\u73ba\u5410\u8840\u6655\u5012\u7684\u6d88\u606f\u4f1a\u6709\u4ec0\u4e48\u5fc3\u60c5\uff0c\u603b\u7b97\u662f\u5e2e\u5979\u51fa\u4e86\u4e00\u53e3\u6076\u6c14\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u4ed6\u4eec\u4e0b\u4e86\u53f0\u9636\uff0c\u627e\u5730\u65b9\u5403\u665a\u996d\u53bb\uff0c\u4ed6\u4eec\u6ca1\u6709\u6ce8\u610f\u5230\u5d14\u90c1\u66fc\u8ddf\u540c\u5b66\u5728\u4ed6\u4eec\u540e\u9762\u8d70\u51fa\u6765\u3002</p><p>\u3000\u3000\u201c\u8fd9\u5c31\u662f\u4f60\u8bf4\u7684\u6d41\u6c13\u5b66\u751f\uff1f\u7adf\u7136\u5c06\u6821\u82b1\u9a97\u5230\u624b\u4e86\uff0c\u771f\u662f\u8fc7\u5206\u554a\uff01\u6211\u89c9\u5f97\u4ed6\u4eca\u5929\u8bf4\u7684\u5f88\u5728\u7406\u554a\uff0c\u867d\u7136\u8a00\u8f9e\u523b\u8584\u4e86\u4e9b\uff0c\u201d\u5d14\u90c1\u66fc\u7684\u540c\u5b66\u770b\u7740\u5f20\u606a\u4ed6\u4eec\u79bb\u53bb\u7684\u80cc\u666f\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u4ed6\u597d\u50cf\u6709\u9ebb\u70e6\u4e86\u5462\uff0c\u6709\u80c6\u91cf\u5c06\u5468\u747e\u73ba\u9a82\u5f97\u5410\u8840\uff0c\u544a\u72b6\u544a\u5230\u9662\u91cc\uff0c\u603b\u4e0d\u4f1a\u6709\u597d\u679c\u5b50\u5403\u5427\uff1f\u4f60\u8bf4\u9662\u91cc\u4f1a\u600e\u4e48\u5904\u5206\u4ed6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u600e\u4e48\u77e5\u9053\uff1f\u201d\u521a\u624d\u6559\u5ba4\u91cc\u585e\u4e86\u592a\u591a\u7684\u4eba\uff0c\u5d14\u90c1\u66fc\u8ddf\u540c\u5b66\u5750\u5728\u6559\u5ba4\u7684\u5185\u4fa7\uff0c\u7ed9\u8fc7\u9053\u91cc\u7ad9\u7740\u7684\u4eba\u6321\u4f4f\uff0c\u675c\u98de\u3001\u5f20\u606a\u4ed6\u4eec\u6ca1\u6709\u770b\u89c1\u5979\uff0c\u5979\u8ddf\u540c\u5b66\u4e5f\u662f\u6700\u540e\u624d\u79bb\u5f00\u6559\u5ba4\uff0c\u8fd9\u4f1a\u513f\u8981\u5148\u53bb\u5e74\u7ea7\u529e\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4e8b\u60c5\u8fdc\u6bd4\u8868\u73b0\u51fa\u6765\u7684\u8981\u6765\u5f97\u68d8\u624b\u3002</p><p>\u3000\u3000\u56fd\u5546\u9662\u5b66\u751f\u5728\u6559\u5ba4\u91cc\u6270\u4e71\u79e9\u5e8f\u5c06\u4e3b\u8bb2\u7684\u6559\u6388\u6c14\u5f97\u5410\u8840\u6655\u5012\uff0c\u53ef\u4e0d\u662f\u7529\u7529\u624b\u5c31\u80fd\u8fc7\u53bb\u7684\u5c0f\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u867d\u7136\u6781\u5c11\u5728\u6559\u5ba4\u91cc\u51fa\u73b0\uff0c\u4f46\u4e0d\u662f\u8bf4\u4eca\u5929\u542c\u8bb2\u5ea7\u7684\u5c31\u6ca1\u6709\u5176\u4ed6\u4eba\u8ba4\u8bc6\u4ed6\u4e86\u2014\u2014\u4eca\u5929\u8dd1\u8fc7\u6765\u542c\u8bb2\u5ea7\u7684\uff0c\u8fd8\u662f\u4ee5\u56fd\u5546\u9662\u7684\u5b66\u751f\u4e3a\u4e3b\uff0c\u5f20\u606a\u53ef\u4ee5\u56fd\u5546\u9662\u6709\u540d\u7684\u523a\u5934\uff0c\u53c8\u8ddf\u6821\u82b1\u9648\u5983\u84c9\u6574\u65e5\u8017\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u5728\u56fd\u5546\u9662\u7684\u540d\u6c14\u53ef\u6bd4\u4ed6\u81ea\u5df1\u60f3\u8c61\u7684\u8981\u5927\u5f97\u591a\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5728\u5e74\u7ea7\u529e\u63a5\u5230\u5468\u747e\u73ba\u90a3\u4f4d\u52a9\u6559\u6253\u6765\u7684\u544a\u72b6\u7535\u8bdd\uff1a\u201c\u2026\u2026\u4f60\u4eec\u5e74\u7ea7\u600e\u4e48\u4f1a\u6709\u8fd9\u6837\u7684\u5b66\u751f\uff0c\u4e4b\u524d\u8003\u8bd5\u4f5c\u5f0a\u4e0d\u8bf4\uff0c\u73b0\u5728\u53c8\u8dd1\u5230\u5468\u6559\u6388\u7684\u8bb2\u5802\u4e0a\u641e\u4e71\uff0c\u516c\u7136\u6c61\u8fb1\u5468\u6559\u6388\uff0c\u8fd8\u628a\u5468\u6559\u6388\u5728\u8bb2\u53f0\u4e0a\u90fd\u6c14\u6655\u8fc7\u53bb\u4e86\uff01\u5468\u6559\u6388\u662f\u56fd\u5185\u77e5\u540d\u7684\u7ecf\u6d4e\u5b66\u8005\uff0c\u4f60\u77e5\u9053\u4eca\u5929\u7684\u4e8b\u4ef6\u6709\u591a\u4e25\u91cd\uff1f\u8fd9\u6837\u7684\u5b66\u751f\uff0c\u9662\u91cc\u5c31\u662f\u5f00\u9664\u4e00\u767e\u6b21\u90fd\u4e0d\u8fc7\u5206\uff01\u4f60\u662f\u4e0d\u662f\u8981\u8ba4\u771f\u8003\u8651\u6211\u7684\u5efa\u8bae\uff1f\u201d</p><p>\u3000\u3000\u201c\u968f\u968f\u4fbf\u4fbf\u542c\u4f60\u7684\u5efa\u8bae\u5c31\u8981\u5f00\u9664\u4e00\u540d\u5b66\u751f\uff0c\u8bf7\u95ee\u4f60\u662f\u8c01\uff0c\u5d14\u9662\u957f\u5417\uff1f\u6211\u600e\u4e48\u542c\u4e0d\u51fa\u662f\u5d14\u9662\u957f\u7684\u58f0\u97f3\uff1f\u201d\u5d14\u90c1\u66fc\u8bf4\u8bdd\u4e5f\u4e0d\u4f1a\u548c\u989c\u60a6\u8272\uff0c\u4e00\u53e5\u8bdd\u5c31\u5c06\u5468\u747e\u73ba\u7684\u52a9\u6559\u7ed9\u9876\u4e86\u56de\u53bb\u3002\u4e0d\u8fc7\u5f20\u606a\u5728\u8bb2\u53f0\u597d\u50cf\u662f\u9a82\u5468\u747e\u73ba\u662f\u8d70\u72d7\u5462\uff0c\u8fd9\u4e48\u8bf4\u597d\u50cf\u662f\u6709\u4e9b\u8fc7\u5206\u2026\u2026\u4f46\u603b\u4e0d\u80fd\u5c31\u56e0\u4e3a\u8fd9\u79cd\u539f\u56e0\u5c31\u5c06\u5f20\u606a\u7ed9\u5f00\u9664\u6389\u5427\uff0c\u867d\u7136\u5979\u6bd4\u8c01\u90fd\u60f3\u7740\u5c06\u5f20\u606a\u8e22\u51fa\u4e1c\u5927\uff0c\u4f46\u662f\u516c\u62a5\u79c1\u4ec7\u53c8\u4e0d\u662f\u5979\u7684\u98ce\u683c\uff0c\u5979\u4e5f\u89c9\u5f97\u5f20\u606a\u5728\u8bb2\u53f0\u4e0a\u8bf4\u7684\u8bdd\u5f88\u6709\u9053\u7406\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5fc3\u60f3\u5468\u747e\u73ba\u7684\u52a9\u6559\u4e0d\u4f1a\u5c31\u6b64\u5584\u7f62\u7518\u4f11\u7684\uff0c\u80af\u5b9a\u4f1a\u544a\u72b6\u544a\u5230\u9662\u957f\u5d14\u56fd\u6052\u90a3\u91cc\u53bb\u7684\uff0c\u4e0d\u8fc7\u5d14\u56fd\u6052\u660e\u663e\u5728\u62a4\u7740\u90a3\u4e2a\u7ea8\u7ed4\u5b50\u5f1f\u554a\uff0c\u6307\u4e0d\u5b9a\u5df2\u7ecf\u7ed9\u8150\u5316\u4e86\uff0c\u5d14\u90c1\u66fc\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u53c8\u60f3\u8d77\u5979\u54e5\u54e5\u6765\uff0c\u62ff\u8d77\u7535\u8bdd\u62e8\u5230\u5317\u4eac\u53bb\uff1a\u201c\u54e5\uff0c\u73b0\u5728\u4f60\u5347\u5b98\u53d1\u8d22\u4e86\uff0c\u90fd\u4e0d\u7ba1\u4f60\u59b9\u59b9\u5728\u5efa\u90ba\u7684\u6b7b\u6d3b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8fd9\u662f\u6076\u4eba\u5148\u544a\u72b6\uff0c\u8fc7\u53bb\u4e00\u5e74\u65f6\u95f4\u91cc\uff0c\u6211\u7ed9\u4f60\u6253\u591a\u5c11\u7535\u8bdd\uff0c\u4f60\u7ed9\u6211\u6253\u591a\u5c11\u7535\u8bdd\uff0c\u6211\u90fd\u8bb0\u5f55\u554a\u2026\u2026\u201d\u521a\u521a\u4ece\u65b0\u534e\u793e\u6d77\u5916\u90e8\u95e8\u8c03\u56de\u56fd\u5185\u7684\u5d14\u90c1\u6052\u5728\u7535\u8bdd\u91cc\u8ddf\u4ed6\u59b9\u59b9\u5f00\u73a9\u7b11\uff0c\u201c\u800c\u4e14\u4f60\u80af\u5b9a\u6709\u4e8b\u60c5\u624d\u4e3b\u52a8\u6253\u7535\u8bdd\u7ed9\u6211\uff0c\u8bf4\u5427\uff0c\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u771f\u662f\u7684\uff0c\u6211\u6709\u8fd9\u4e48\u51b7\u8840\u65e0\u60c5\u5417\uff1f\u201d\u5d14\u90c1\u66fc\u5728\u7535\u8bdd\u8ddf\u5979\u54e5\u54e5\u5a07\u7b11\u7740\uff0c\u201c\u60f3\u8d77\u4f60\u4e86\uff0c\u6b63\u597d\u8fd8\u6709\u4e2a\u95ee\u9898\u60f3\u95ee\u95ee\u4f60\u3002\u6211\u4eec\u5b66\u6821\u6709\u4e2a\u5b66\u751f\u8ddf\u8001\u5e08\u4e89\u8bba\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u7684\u95ee\u9898\uff0c\u6211\u4e0d\u662f\u5f88\u6e05\u695a\u8c01\u7684\u89c2\u70b9\u6b63\u786e\uff0c\u6211\u60f3\u4f60\u505a\u8bb0\u8005\u8d70\u5357\u95ef\u5317\u89c1\u8bc6\u591a\uff0c\u60f3\u542c\u542c\u4f60\u6709\u4ec0\u4e48\u770b\u6cd5\uff1f\u201d</p></article>"}}}]);
