(window.webpackJsonp=window.webpackJsonp||[]).push([[1002],{1025:function(p){p.exports={chapterTitle:null,title:"\u7b2c999\u7ae0 \u9ed1\u8393\u6280\u672f",content:"<article><h2>\u7b2c999\u7ae0 \u9ed1\u8393\u6280\u672f</h2><p>\u3000\u3000\u96f6\u4e94\u5e74\uff0c\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u5c06\u9010\u6e10\u6210\u4e3a\u65b0\u4e00\u4ee3\u667a\u80fd\u624b\u673a\u7684\u6807\u51c6\u914d\u5236\uff0c\u4f46\u662f\u6b64\u65f6\u7684\u52a0\u62ff\u5927RIM\u516c\u53f8\u8fd8\u6ca1\u6709\u63a8\u51fa\u4ed6\u4eec\u7684\u7b2c\u4e00\u53ea\u79fb\u690d\u8be5\u6280\u672f\u7684\u9ed1\u8393\u624b\u673a\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\uff0c\u8fd8\u6ca1\u6709\u8c01\u80fd\u6e05\u9192\u7684\u8ba4\u8bc6\u5230\u624b\u6301\u7ec8\u7aef\u53d1\u9001\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u7684\u4e1a\u52a1\u5e02\u573a\u5230\u5e95\u6709\u591a\u5927\uff0c\u4e0d\u8fc7\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u7684\u57fa\u7840\u4e13\u5229\u5dee\u4e0d\u591a\u5df2\u7ecf\u7ed9NTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u62a2\u6ce8\u8fc7\u53bb\uff0c\u524d\u8005\u4e3b\u8981\u5c06PUSH\u6280\u672f\u636e\u4e3a\u5df1\u6709\uff0c\u540e\u8005\u5219\u5784\u65ad\u4e86\u5176\u4ed6\u91cd\u8981\u7684\u8f6f\u786c\u4ef6\u67b6\u6784\uff0c\u7559\u7ed9\u5176\u4ed6\u516c\u53f8\u7684\u4e13\u5229\u7f1d\u9699\u5df2\u7ecf\u4e0d\u5927\uff0c\u5373\u4f7fRIM\u516c\u53f8\u4e5f\u5ffd\u7565\u4e86\u5728\u4e13\u5229\u4e0a\u7684\u90e8\u7f72\u3002</p><p>\u3000\u3000\u5728\u8fd9\u4e00\u6280\u672f\u9886\u57df\uff0cESS\u8054\u7cfb\u7684\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\uff08Geoff Goodfellow\uff09\u5927\u6982\u662f\u6700\u65e9\u7684\u53d1\u660e\u4eba\u3002\u8fd9\u4e2a\u4e16\u754c\u4f3c\u4e4e\u4ece\u4e0d\u7f3a\u4e4f\u5929\u624d\uff0c\u4ece\u9ad8\u4e2d\u8f8d\u5b66\u7684\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u516b\u4e8c\u5e74\u5c31\u840c\u751f\u4e86\u5411\u624b\u6301\u7ec8\u7aef\u53d1\u9001\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u7684\u60f3\u6cd5\uff0c\u6301\u7eed\u8fdb\u884c\u4e86\u5927\u91cf\u7684\u7814\u7a76\u5f00\u53d1\u5de5\u4f5c\uff0c\u5341\u591a\u5e74\u7684\u65f6\u95f4\u4e00\u76f4\u52aa\u529b\u4f7f\u4ed6\u6240\u638c\u63e1\u7684\u6280\u672f\u4ea7\u4e1a\u5316\uff0c\u4f46\u662f\u4e0e\u52a0\u62ff\u5927\u7684RIM\u516c\u53f8\u4e00\u6837\uff0c\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u4e5f\u5ffd\u7565\u4e86\u4e13\u5229\u7684\u7533\u8bf7\uff0c\u5230\u4e5d\u516b\u5e74\u5e95\uff0c\u4ed6\u6210\u7acb\u7684\u52aa\u529b\u4f7f\u8be5\u6280\u672f\u5546\u4e1a\u5316\u7684\u516c\u53f8\u5df2\u7ecf\u9677\u5165\u56f0\u5883\uff0c\u96be\u4ee5\u633d\u6551\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e24\u5929\u5b89\u6392\u5c31\u6211\u8ddf\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u89c1\u4e00\u9762\uff0c\u201d\u5f20\u606a\u8ddf\u6210\u6c49\u748b\u8bf4\u9053\uff0c\u201c\u6211\u5e0c\u671bESS\u80fd\u5c3d\u5feb\u5c06\u4ed6\u7684\u516c\u53f8\u6536\u8d2d\u5230\u65d7\u4e0b\uff0c\u5c3d\u5feb\u5c06\u8fd9\u4e2a\u4e8b\u786e\u5b9a\u4e0b\u6765\uff0c\u8981\u505a\u7684\u4e8b\u60c5\u8fd8\u6709\u5f88\u591a\u2026\u2026\u201d</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u5f20\u606a\u77e5\u9053\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u6b63\u6253\u7b97\u5173\u95ed\u4ed6\u7684\u516c\u53f8\uff0c\u8ba1\u5212\u53bb\u5e03\u62c9\u683c\u505a\u9152\u5427\u751f\u610f\uff0c\u5728\u5176\u4ed6\u7535\u5b50\u5de8\u5934\u8fd8\u6ca1\u6709\u770b\u6e05\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u4e1a\u52a1\u7684\u5e02\u573a\u524d\u666f\u3001\u91c7\u53d6\u884c\u52a8\u4e4b\u524d\uff0c\u6536\u8d2d\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7684\u516c\u53f8\u4e0d\u4f1a\u5b58\u5728\u592a\u5927\u7684\u56f0\u96be\uff0c\u9664\u4e86\u6536\u8d2d\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7684\u516c\u53f8\u4e4b\u5916\uff0c\u5f20\u606a\u8fd8\u5e0c\u671b\u80fd\u5c06\u4ed6\u672c\u4eba\u633d\u7559\u4e0b\u6765\u3002</p><p>\u3000\u3000\u6210\u6c49\u748b\u7b49\u9526\u6e56\u9ad8\u5c42\uff0c\u5373\u4f7f\u5bf9\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u7684\u524d\u666f\u8fd8\u5f88\u96be\u5224\u65ad\uff0c\u4f46\u662f\u4e5f\u4e0d\u96be\u5224\u65ad\u8be5\u9879\u6280\u672f\u80fd\u591f\u96c6\u6210\u5230\u79fb\u52a8\u624b\u673a\u4e0a\u3002\u7231\u8fbe\u96c6\u56e2\u5728\u53bb\u5e74\u79cb\u540e\u5c31\u786e\u5b9a\u4e86\u6570\u5b57\u6280\u672f\u4e0e\u79fb\u52a8\u624b\u673a\u4ea7\u54c1\u76f8\u878d\u5408\u7684\u6838\u5fc3\u6218\u7565\u65b9\u5411\uff0c\u6536\u8d2d\u5e76\u6301\u7eed\u5f00\u53d1\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\uff0c\u662f\u7b26\u5408\u7231\u8fbe\u96c6\u56e2\u6838\u5fc3\u6218\u7565\u65b9\u5411\u7684\u3002</p><p>\u3000\u3000\u9526\u6e56\u5185\u90e8\u5bf9\u5f20\u606a\u7684\u51b3\u5b9a\u4e5f\u662f\u6ca1\u6709\u4ec0\u4e48\u7591\u4e49\u7684\uff0c\u6216\u8bb8\u5f20\u606a\u6709\u90a3\u4e48\u4e00\u70b9\u70b9\u7684\u6027\u6025\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u6210\u679c\u51b3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u6ce8\u91cd\u4e13\u4e1a\u53d1\u5c55\u4e0e\u5206\u5de5\u7684\u5317\u7f8e\u5730\u533a\uff0c\u79d1\u6280\u4f01\u4e1a\u591a\u4e13\u6ce8\u67d0\u4e2a\u6280\u672f\u9886\u57df\uff0c\u5728\u56fd\u5185\u5374\u6781\u5c11\u6709\u79d1\u6280\u4f01\u4e1a\u80fd\u4e0e\u9526\u6e56\u5171\u540c\u627f\u62c5\u67d0\u4e00\u9886\u57df\u7684\u7814\u53d1\u91cd\u4efb\uff0c\u9526\u6e56\u4e5f\u5c31\u88ab\u8feb\u6295\u5165\u5de8\u8d44\u53bb\u5efa\u7acb\u5168\u9762\u7684\u7814\u53d1\u4f53\u7cfb\uff0c\u53bb\u5efa\u7acb\u4ece\u4e0a\u6e38\u5230\u7ec8\u7aef\u7684\u91cd\u76f4\u751f\u4ea7\u4f53\u7cfb\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u6709\u8fd9\u6837\u7684\u9700\u6c42\uff0c\u5373\u4f7f\u5728\u7845\u8c37\u7684ESS\u5b9e\u9a8c\u57fa\u5730\u62e5\u6709\u4e09\u767e\u540d\u79d1\u7814\u4eba\u5458\uff0c\u8fd8\u65e0\u6cd5\u5b8c\u5168\u6ee1\u8db3ESS\u5206\u62c5\u7684\u7814\u53d1\u4efb\u52a1\uff0c\u4f46\u5728\u5317\u7f8e\u5730\u533a\uff0c\u62e5\u6709\u4e09\u767e\u540d\u79d1\u7814\u4eba\u5458\u7684ESS\u7edd\u4e0d\u80fd\u7b97\u5c0f\u4f01\u4e1a\uff0c\u5373\u4f7f\u65af\u9ad8\u67cf\u5728\u5317\u7f8e\u5730\u533a\u79d1\u7814\u90e8\u95e8\u7684\u5458\u5de5\u4e5f\u624d\u4e00\u767e\u591a\u4eba\u3002</p><p>\u3000\u3000\u6240\u4ee5\uff0c\u5f20\u606a\u4e0e\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7684\u4f1a\u9762\u8981\u6c42\u5f88\u5feb\u5c31\u5f97\u5230\u56de\u5e94\uff0c\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7b2c\u4e8c\u5929\u5c31\u4e58\u591c\u95f4\u98de\u673a\u8d76\u5230\u65e7\u91d1\u5c71\u4e0e\u5f20\u606a\u4f1a\u9762\u3002</p><p>\u3000\u3000\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u4ecd\u7136\u5e0c\u671bESS\u80fd\u8d44\u52a9\u4ed6\u7684\u516c\u53f8\uff0c\u800c\u975e\u6536\u8d2d\u4ed6\u7684\u516c\u53f8\u3002</p><p>\u3000\u3000\u5341\u4e07\u7f8e\u5143\u7684\u85aa\u8d44\u5728\u5317\u7f8e\u5730\u533a\u4e5f\u8981\u7b97\u9ad8\u85aa\uff0c\u6bd4\u5317\u7f8e\u5730\u533a\u7684\u4eba\u5747\u85aa\u8d44\u8981\u9ad8\u51fa\u4e00\u500d\uff0c\u9664\u4e86\u6536\u8d2d\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7684\u516c\u53f8\u4e4b\u5916\uff0c\u5f20\u606a\u7ed9\u4ed6\u672c\u4eba\u5f00\u51fa\u4e94\u5341\u4e07\u7f8e\u5143\u7684\u5e74\u85aa\u633d\u7559\u7ee7\u7eed\u7559\u5728ESS\u4ece\u4e8b\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u7684\u540e\u7eed\u7814\u53d1\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u5373\u4f7fNTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u62a2\u6ce8\u5230\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u8fd1\u4e09\u5341\u7bc7\u57fa\u7840\u4e13\u5229\uff0c\u4f46\u662f\u5728\u5927\u591a\u6570\u6280\u672f\u9886\u57df\uff0c\u4e13\u5229\u90e8\u7f72\u603b\u662f\u4ece\u6700\u521d\u7684\u5c11\u6570\u57fa\u7840\u4e13\u5229\u884d\u751f\u51fa\u6570\u5341\u500d\uff0c\u751a\u81f3\u6570\u767e\u500d\u7684\u4ece\u5c5e\u3001\u6539\u8fdb\u3001\u7ec4\u5408\u4e13\u5229\uff0c\u4ece\u800c\u5c06\u57fa\u7840\u4e13\u5229\u5c65\u76d6\u6389\uff0c\u5373\u4f7f\u5728\u57fa\u7840\u4e13\u5229\u7684\u5e03\u5c40\u4e0a\u6ca1\u6709\u5360\u5230\u4ec0\u4e48\u4fbf\u5b9c\uff0c\u5f20\u606a\u4ecd\u5e0c\u671b\u5728\u540e\u7eed\u7684\u884d\u751f\u4e13\u5229\u5e03\u5c40\u4e2d\uff0c\u9526\u6e56\u80fd\u5728\u5360\u6709\u4e00\u5e2d\u4e4b\u5730\u3002</p><p>\u3000\u3000\u5728\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u72b9\u8c6b\u4e00\u5929\u4e4b\u540e\uff0c\u5f20\u606a\u5c06\u5e74\u85aa\u7b79\u7801\u63d0\u9ad8\u516d\u5341\u4e8c\u4e07\u7f8e\u5143\uff0c\u4f7f\u4ed6\u5f53\u573a\u5728\u8058\u7528\u534f\u8bae\u4ee5\u516c\u53f8\u8f6c\u8ba9\u534f\u8bae\u4e0a\u7b7e\u4e0b\u5b57\u3002</p><p>\u3000\u3000\u5f53\u591c\uff0c\u5728\u65e7\u91d1\u5c71\uff0c\u4e3a\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u52a0\u5165ESS\u4e3e\u529e\u4e86\u4e00\u4e2a\u5c0f\u578b\u7684\u5e86\u795d\u9152\u4f1a\uff0c\u6a61\u6811\u56ed\u8d1f\u8d23\u5f00\u53d1linux\u64cd\u4f5c\u7cfb\u7edf\u7684\u6b21\u5e2d\u79d1\u5b66\u5bb6\u97e6\u5143\u771f\u6070\u597d\u4e5f\u5728\u65e7\u91d1\u5c71\uff0c\u81ea\u7136\u4e5f\u51fa\u5e2d\u4e86\u5e86\u795d\u9152\u4f1a\uff0c\u6210\u6c49\u748b\u7b11\u7740\u8ddf\u4ed6\u8bf4\uff1a\u201c\u4f60\u4eec\u7684\u4efb\u52a1\u53c8\u591a\u4e86\u4e00\u5927\u5757\uff01\u201d</p><p>\u3000\u3000\u5c06\u6765\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u7684\u8f6f\u4ef6\u6a21\u5757\u8981\u5d4c\u5165\u64cd\u4f5c\u7cfb\u7edf\uff0c\u8f6f\u4ef6\u6a21\u5757\u7684\u540e\u7eed\u5f00\u53d1\uff0c\u4e5f\u5c06\u7531\u97e6\u5143\u771f\u9886\u5bfc\u7684\u5c0f\u7ec4\u6765\u8d1f\u8d23\uff0c\u4ed6\u7684\u5de5\u4f5c\u7684\u786e\u53c8\u591a\u4e86\u4e00\u5927\u5757\u3002</p><p>\u3000\u3000\u201c\u7231\u8fbe\u7684\u89c4\u6a21\u6b63\u4ee5\u53ef\u89c1\u7684\u901f\u5ea6\u5728\u81a8\u80c0\uff0c\u6211\u4eec\u505a\u79d1\u7814\u7684\u90fd\u6709\u4e00\u79cd\u88ab\u8ffd\u8d76\u7684\u611f\u89c9\u2026\u2026\u201d\u97e6\u5143\u771f\u7b11\u9053\uff0c\u201c\u6216\u8bb8\u4e0d\u7528\u5341\u5e74\uff0c\u7231\u8fbe\u7684\u53d1\u5c55\u89c4\u6a21\u5c31\u4f1a\u8ba9\u6240\u6709\u4eba\u90fd\u5927\u5403\u4e00\u60ca\u7684\u3002\u201d</p><p>\u3000\u3000\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u4e5f\u65e9\u5c31\u610f\u8bc6\u5230\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u7684\u5546\u4e1a\u5316\u5fc5\u987b\u4f9d\u636e\u6709\u5b9e\u529b\u7684\u79d1\u6280\u516c\u53f8\u624d\u80fd\u96c6\u4e2d\u8db3\u591f\u7684\u4eba\u529b\u3001\u7269\u529b\u53bb\u5b8c\u6210\uff0c\u50cf\u4ed6\u7b7e\u4e0b\u5b57\uff0c\u6210\u6c49\u748b\u5c31\u5141\u8bfa\u4eceESS\u8c03\u4e8c\u5341\u540d\u79d1\u7814\u4eba\u5458\u52a0\u5f3a\u539f\u5148\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u79d1\u7814\u5c0f\u7ec4\u53bb\u5f00\u53d1\u540e\u7eed\u79d1\u6280\uff0c\u8fd8\u6709\u76f8\u5f53\u7684\u7814\u53d1\u4efb\u52a1\u53ef\u4ee5\u4ea4\u7ed9\u56fd\u5185\u7684\u7814\u53d1\u90e8\u95e8\u53bb\u5b8c\u6210\uff0c\u800c\u4e4b\u524d\u5ffd\u89c6\u7684\u4e13\u5229\u7533\u8bf7\u5de5\u4f5c\uff0c\u4e5f\u5c06\u7531\u4e13\u4e1a\u7684\u90e8\u95e8\u8d1f\u8d23\uff0c\u5982\u6b64\u4e00\u6765\uff0c\u5f00\u53d1\u624b\u6301\u7ec8\u7aef\u7684\u901f\u5ea6\u8981\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u5feb\u4e86\u6570\u500d\u3002</p><p>\u3000\u3000\u201c\u52a0\u62ff\u5927\u7684RIM\u516c\u53f8\u5df2\u7ecf\u5728\u5f00\u53d1\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u7684\u624b\u6301\u7ec8\u7aef\u4ea7\u54c1\uff0c\u201d\u6210\u6c49\u748b\u95ee\u5f20\u606a\uff0c\u201c\u6211\u4eec\u53ef\u4ee5\u8ddf\u5728\u540e\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u63a5\u4e0b\u6765\uff0c\u8fd8\u662f\u5148\u4e0eNTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u8fdb\u884c\u8c08\u5224\u5427\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5c06\u76f8\u5173\u6280\u672f\u9886\u57df\u7684\u57fa\u7840\u4e13\u5229\u6280\u672f\u8bb8\u53ef\u6388\u6743\u62ff\u5230\u624b\u518d\u8bf4\u2026\u2026\u201d</p><p>\u3000\u3000\u5373\u4f7f\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u7684\u516c\u53f8\u4e0d\u6301\u6709\u76f8\u5173\u4e13\u5229\uff0c\u4f46\u662f\u8be5\u516c\u53f8\u5b9e\u9645\u638c\u63e1\u4e86\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u65b9\u9762\u7684\u6280\u672f\uff0c\u5e76\u6709\u5927\u91cf\u76f8\u5173\u7684\u6280\u672f\u8d44\u6599\u8bc1\u660e\u5728NTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u62a2\u6ce8\u4e13\u5229\u4e4b\u524d\u5c31\u5df2\u7ecf\u638c\u63e1\u76f8\u5173\u6280\u672f\uff0c\u5c31\u80fd\u4f7f\u8fd9\u4e24\u5bb6\u516c\u53f8\u7684\u4e13\u5229\u7a33\u5b9a\u6027\u53d7\u5230\u5a01\u80c1\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u4f5c\u4e3a\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u7684\u6743\u5a01\u6027\u4eba\u7269\uff0c\u5c06\u4ed6\u63fd\u5165\u65d7\u4e0b\uff0c\u751a\u81f3\u53ef\u4ee5\u5f00\u53d1\u5168\u65b0\u7684\u6280\u672f\u65b9\u6848\u6765\u89c4\u907fNTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u7684\u4e13\u5229\u3002</p><p>\u3000\u3000\u5e76\u8d2d\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\uff0c\u5e76\u5c06\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u672c\u4eba\u7b7e\u5230\u9526\u6e56\u65d7\u4e0b\uff0c\u9526\u6e56\u5c31\u6709\u8db3\u591f\u7684\u7b79\u7801\u5728\u4e0eNTP\u3001Visto\u7684\u8c08\u5224\u4e2d\u5360\u636e\u4e3b\u52a8\u5730\u4f4d\u4ee5\u83b7\u5f97\u6240\u6709\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u9886\u57df\u7684\u6240\u6709\u57fa\u7840\u4e13\u5229\u6280\u672f\u6388\u6743\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ba4\u771f\u8003\u8651\u8fc7\uff0c\u5373\u4f7f\u83b7\u5f97\u6240\u6709\u7684\u57fa\u7840\u4e13\u5229\u6388\u6743\uff0c\u5e76\u6210\u529f\u7814\u5236\u51fa\u624b\u6301\u7ec8\u7aef\u6765\uff0c\u9526\u6e56\u590d\u5236RIM\u9ed1\u8393\u624b\u673a\u7684\u6a21\u5f0f\u5728\u5317\u7f8e\u5e02\u573a\u83b7\u5f97\u6210\u529f\u3001\u751a\u81f3\u9876\u66ff\u6389RIM\u7684\u53ef\u80fd\u6027\u5e76\u4e0d\u9ad8\u3002</p><p>\u3000\u3000\u9664\u4e86\u6280\u672f\u4e4b\u5916\uff0c\u83b7\u5f97\u6210\u529f\u7684\u8981\u7d20\u8fd8\u6709\u5f88\u591a\uff0c\u6709\u8bb8\u591a\u662f\u9526\u6e56\u6b64\u65f6\u5e76\u4e0d\u5177\u5907\u7684\uff0c\u50cfRIM\u4e3a\u53d1\u5c55\u9ed1\u8393\u7684\u4f01\u4e1a\u7528\u6237\uff0c\u751a\u81f3\u4e13\u95e8\u5efa\u7acb\u5927\u91cf\u7684\u4f01\u4e1a\u670d\u52a1\u5668\u6765\u9500\u552e\u4ed6\u4eec\u7684\u7ec8\u7aef\u4ea7\u54c1\uff0c\u5c31\u7b97\u9526\u6e56\u6709\u8fd9\u4e2a\u8d44\u672c\u5b9e\u529b\uff0c\u4f46\u662f\u83b7\u5f97\u7f8e\u56fd\u653f\u5e9c\u8bb8\u53ef\u7684\u53ef\u80fd\u6027\u4e5f\u8981\u8fdc\u8fdc\u4f4e\u4e8e\u6765\u81ea\u52a0\u62ff\u5927\u7684RIM\u3002</p><p>\u3000\u3000\u6b64\u65f6\u8f83\u4e3a\u5173\u952e\u662f\u5148\u5b8c\u6210\u4e13\u5229\u4e0a\u7684\u5e03\u5c40\uff0c\u8ba9RIM\u53bb\u5f53\u524d\u950b\u5f00\u53d1\u5e02\u573a\u5427\uff0c\u7b49\u5230\u65f6\u673a\u76f8\u5bf9\u8f83\u6210\u719f\u65f6\uff0c\u4e5f\u7b49\u9526\u6e56\u7684\u624b\u673a\u6280\u672f\u76f8\u5bf9\u8f83\u6210\u719f\u65f6\uff0c\u518d\u8fdb\u5165\u8fd9\u4e2a\u5e02\u573a\u4e0d\u8fdf\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u5230\u65e7\u91d1\u5c71\u6765\uff0c\u8fd8\u6709\u4e00\u4e2a\u5173\u952e\u7684\u4eba\u7269\u8981\u89c1\uff0c\u4ed6\u5c31\u662f\u53bb\u5e74\u4e09\u6708\u4e2d\u65ec\u53c2\u5165ESS\u9886\u5bfcESS\u65d7\u4e0b\u624b\u673a\u82af\u7247\u7814\u53d1\u90e8\u95e8\u7684\u5f90\u80dc\u5fd7\u3002</p><p>\u3000\u3000ESS\u79d8\u5bc6\u5f00\u5c55\u624b\u673a\u57fa\u5e26\u82af\u7247\u7684\u7814\u7a76\u5df2\u7ecf\u5c06\u8fd1\u4e00\u5e74\u7684\u65f6\u95f4\uff0c\u5f90\u80dc\u5fd7\u66fe\u9886\u5bfc\u57fa\u5e26\u82af\u7247\u7684\u7814\u7a76\u5de5\u4f5c\u8fd1\u4e8c\u5341\u5e74\uff0c\u4ed6\u590d\u5236\u51fa\u5b8c\u6574\u7684\u57fa\u5e26\u82af\u7247\u6280\u672f\u5e76\u4e0d\u662f\u4ec0\u4e48\u96be\u4e8b\uff0c\u5173\u952e\u662f\u8981\u89c4\u907f\u5176\u4ed6\u516c\u53f8\u6240\u6301\u7684\u4e13\u5229\uff0c\u8fd9\u80fd\u624d\u7b97\u83b7\u5f97\u72ec\u7acb\u7684\u57fa\u5e26\u82af\u7247\u6280\u672f\uff0c\u90a3\u5c31\u9700\u8981\u5f90\u80dc\u5fd7\u9886\u5bfc\u7684\u5c0f\u7ec4\u8bbe\u8ba1\u5168\u65b0\u7684\u6280\u672f\u65b9\u6848\u5e76\u6210\u529f\u5b9e\u73b0\u3002</p><p>\u3000\u3000\u5728ESS\u7684\u624b\u673a\u82af\u7247\u5b9e\u9a8c\u5ba4\u91cc\uff0c\u5f20\u606a\u770b\u5230\u5fae\u5904\u7406\u5668+\u57fa\u5e26\u82af\u7247\u67b6\u6784\u7684\u624b\u673a\u6837\u673a\uff0c\u5373\u4f7f\u5728\u529f\u8017\u3001\u6027\u80fd\u7a33\u5b9a\u6027\u65b9\u9762\u8fd8\u5f88\u4e0d\u591f\u51fa\u8272\uff0c\u4f46\u662f\u4e5f\u8db3\u4ee5\u8bc1\u636eESS\u5b9e\u9a8c\u57fa\u5730\u662f\u8d70\u5728\u6b63\u786e\u7684\u65b9\u5411\u4e0a\u3002</p><p>\u3000\u3000\u201c\u5927\u7ea6\u8fd8\u9700\u8981\u4e00\u5e74\u5230\u4e24\u5e74\u7684\u65f6\u95f4\uff0c\u624d\u80fd\u62ff\u51fa\u6210\u719f\u7684\u4ea7\u54c1\u51fa\u6765\u3002\u201d\u6210\u6c49\u748b\u4ecb\u7ecd\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e24\u5e74\u65f6\u95f4\u91cc\uff0c\u56fd\u5185\u7684\u624b\u673a\u5e02\u573a\u73af\u5883\u4e0d\u4f1a\u6076\u52a3\uff0c\u7559\u7ed9\u6211\u4eec\u7684\u65f6\u95f4\u8fd8\u662f\u5bbd\u88d5\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4eca\u540e\u4e24\u5e74\uff0c\u56fd\u5185\u7684\u624b\u673a\u5e02\u573a\u5c06\u4f1a\u6210\u500d\u7684\u589e\u6da8\uff0c\u800c\u5bf9\u5916\u8d44\u54c1\u724c\u624b\u673a\u7684\u53e3\u5b50\u8fd8\u6ca1\u6709\u5b8c\u5168\u7684\u6253\u5f00\uff0c\u90a3\u5c31\u7559\u7ed9\u56fd\u4ea7\u624b\u673a\u5382\u5546\u8db3\u591f\u7684\u53d1\u5c55\u7a7a\u95f4\uff0c\u5728\u56fd\u4ea7\u624b\u673a\u5382\u5546\u91cc\uff0c\u5f20\u606a\u5b8c\u5168\u4e0d\u62c5\u5fc3\u8c01\u80fd\u61be\u52a8\u9526\u6e56\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5728\u65e7\u91d1\u5c71\u5446\u4e86\u8fd1\u5341\u5929\uff0c\u5728\u8fd9\u5341\u5929\u65f6\u95f4\u91cc\uff0c\u5148\u662f\u8096\u664b\u6210\u966a\u4ed6\u5728\u65e7\u91d1\u5c71\uff0c\u8096\u664b\u6210\u79bb\u5f00\u4e4b\u540e\uff0c\u9648\u4fe1\u751f\u3001\u4e01\u69d0\u4e5f\u76f8\u7ee7\u5230\u65e7\u91d1\u5c71\u6765\u5904\u7406\u516c\u52a1\uff0c\u5012\u662f\u6700\u540e\u6a61\u6811\u56ed\u7684\u9996\u5e2d\u79d1\u5b66\u5bb6\u8c2d\u4e91\u677e\u4e0e\u4e00\u6279\u8fd4\u56de\u56fd\u5185\u6a61\u6811\u56ed\u4ece\u4e8b\u79d1\u7814\u5de5\u4f5c\u7684\u4e2d\u9ad8\u7ea7\u79d1\u7814\u4eba\u5458\u966a\u540c\u5f20\u606a\u3001\u7fdf\u4e39\u9752\u4ece\u5317\u4eac\u501f\u9053\u8fd4\u56de\u5efa\u90ba\uff0c\u4e5f\u6b63\u597d\u8d76\u4e0a\u65b0\u5b66\u671f\u7684\u5f00\u5b66\u2014\u2014\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u5c31\u76f4\u63a5\u7559\u5728\u65e7\u91d1\u5c71\u3002</p><p>\u3000\u3000\u4e0a\u5b66\u671f\uff0c\u56e0\u4e3a\u5357\u90ba\u533a\u5b98\u5458\u94b1\u6842\u534e\u7b49\u4eba\u5728\u56fd\u5916\u8003\u5bdf\u65f6\u6f5c\u9003\uff0c\u5f20\u606a\u5f53\u65f6\u6025\u4e8e\u53bb\u89c1\u7f57\u541b\uff0c\u5bfc\u81f4\u6700\u540e\u4e00\u95e8\u8003\u8bd5\u6ca1\u6709\u53c2\u52a0\uff0c\u65b0\u5b66\u671f\u5f00\u5b66\uff0c\u4ed6\u8981\u4e0e\u8865\u8003\u7684\u4eba\u4e00\u540c\u53c2\u52a0\u8fd9\u95e8\u8003\u8bd5\uff0c\u6240\u4ee5\u56de\u5230\u5efa\u90ba\u4e4b\u540e\uff0c\u4eba\u8fd8\u4e0d\u80fd\u6b47\u4e00\u6b47\u3002</p></article>"}}}]);
