(window.webpackJsonp=window.webpackJsonp||[]).push([[1003],{1026:function(p){p.exports={chapterTitle:null,title:"\u7b2c1000\u7ae0 \u4e1a\u7ee9\u592a\u597d\u4e5f\u5934\u75bc",content:"<article><h2>\u7b2c1000\u7ae0 \u4e1a\u7ee9\u592a\u597d\u4e5f\u5934\u75bc</h2><p>\u3000\u3000ESS\u6536\u8d2d\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u5e76\u5c06\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u672c\u4eba\u63fd\u81f3\u65d7\u4e0b\uff0c\u5229\u7528\u8fd1\u4e00\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5bf9\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u5fb7\u516c\u53f8\u7684\u73b0\u6709\u6280\u672f\u8d44\u6599\u3001\u7814\u53d1\u8bb0\u5f55\u8fdb\u884c\u6574\u7406\uff0c\u80fd\u786e\u8ba4\u591a\u7bc7\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u7684\u4e13\u5229\u5728NTP\u3001Visto\u516c\u53f8\u6ce8\u518c\u4e4b\u524d\uff0c\u6770\u592b\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u5df2\u7ecf\u5b9e\u9645\u83b7\u5f97\u8be5\u9879\u6280\u672f\u3002</p><p>\u3000\u3000\u9526\u6e56\u53ef\u4ee5\u901a\u8fc7\u5b8c\u5168\u63a7\u80a1\u7684\u53e4\u5fb7\u8d39\u52d2\u5411\u7f8e\u56fd\u4e13\u5229\u5c40\u63d0\u4ea4\u76f8\u5173\u8d44\u6599\uff0c\u8981\u6c42\u7f8e\u56fd\u4e13\u5229\u5c40\u5ba3\u5e03NTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u6240\u62e5\u6709\u7684\u76f8\u5173\u4e13\u5229\u65e0\u6548\uff0c\u4f46\u662f\u8fd9\u79cd\u505a\u6cd5\u5e76\u4e0d\u7b26\u5408\u9526\u6e56\u7684\u5229\u76ca\uff0c\u800c\u662f\u5411NTP\u3001Visto\u516c\u53f8\u63d0\u8bf7\u4ea4\u6d89\uff0c\u8981\u6c42\u5728\u76f8\u5173\u4e13\u5229\u65b9\u9762\uff0c\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u5e94\u65e0\u507f\u83b7\u5f97\u4e0e\u4e24\u5bb6\u516c\u53f8\u540c\u7b49\u6743\u5229\u7684\u6388\u6743\u3002</p><p>\u3000\u3000\u7531\u4e8e\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u80fd\u63d0\u51fa\u8be6\u5b9e\u6709\u6548\u7684\u8bb0\u5f55\u6750\u6599\uff0c\u5e76\u6709\u80fd\u529b\u901a\u8fc7\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u9886\u5bfc\u4e0b\u7684\u65b0\u7814\u53d1\u56e2\u961f\u5f00\u53d1\u65b0\u7684\u6280\u672f\u65b9\u6848\u89c4\u907f\u5df2\u6709\u7684\u57fa\u7840\u4e13\u5229\uff0c\u7ecf\u8fc7\u8c08\u5224\uff0cNTP\u3001Visto\u4e24\u5bb6\u516c\u53f8\u5f88\u5feb\u4e0eESS\u8fbe\u6210\u548c\u89e3\u534f\u8bae\uff0c\u6770\u592b\xb7\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u5411\u4e24\u5bb6\u516c\u53f8\u652f\u4ed8\u4e00\u5343\u4e07\u7f8e\u5143\u83b7\u5f97\u5171\u5341\u4e8c\u7bc7\u57fa\u7840\u4e13\u5229\u7684\u6df1\u5ea6\u6388\u6743\u3002</p><p>\u3000\u3000\u5176\u65f6\uff0c\u7f8e\u56fd\u4e13\u5229\u5c40\u767b\u8bb0\u5728\u518c\u7684\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9886\u57df\u7684\u57fa\u7840\u4e13\u5229\u624d\u4e09\u5341\u4e8c\u7bc7\uff0c\u9526\u6e56\u6301\u6709\u5176\u4e2d\u5341\u4e8c\u7bc7\u4e13\u5229\u7684\u6df1\u5ea6\u6388\u6743\uff0c\u4e5f\u5c31\u610f\u5473\uff0c\u9526\u6e56\u4ece\u8fd9\u5341\u4e8c\u7bc7\u57fa\u7840\u4e13\u5229\u4e0a\u884d\u751f\u51fa\u6765\u7684\u4e13\u5229\uff0c\u9526\u6e56\u90fd\u6301\u6709\u5b8c\u5168\u7684\u6743\u5229\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\uff0c\u5373\u4f7f\u4ed6\u4eec\u5728\u5317\u7f8e\u5e02\u573a\u53d1\u5c55\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u7684\u4e1a\u52a1\u5e76\u4e0d\u987a\u5229\uff0c\u7b49RIM\u7684\u9ed1\u8393\u624b\u673a\u5927\u83b7\u6210\u529f\u4e4b\u540e\uff0c\u4e5f\u53ef\u4ee5\u8ddf\u5728NTP\u3001Visto\u540e\u9762\u54acRIM\u4e00\u5757\u8089\u4e0b\u6765\uff0c\u8fd9\u65f6\u5019\u638f\u51fa\u4e00\u5343\u4e07\u7f8e\u5143\u51fa\u53bb\uff0c\u603b\u4e0d\u4f1a\u5403\u4e8f\u5c31\u662f\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0e\u6b64\u540c\u65f6\uff0c\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\uff08RIAA\uff09\u5728\u8d39\u5409\u5c3c\u4e9a\u5dde\u4e1c\u533a\u7684\u8054\u5e2e\u5730\u533a\u6cd5\u9662\u6b63\u5f0f\u63a7\u544a\u9526\u6e56\u7684iplayer\u4e1a\u52a1\u56e0\u4e3a\u6545\u610f\u4e0d\u4e25\u8c28\u7684\u52a0\u5bc6\u6280\u672f\u4fc3\u4f7f\u7528\u6237\u5728\u4e92\u8054\u7f51\u4e0a\u5927\u91cf\u65e0\u507f\u4e0b\u8f7d\u97f3\u4e50\u4fb5\u72af\u4e86\u5176\u77e5\u8bc6\u7248\u6743\uff0c\u8bf7\u6c42\u7981\u6b62\u9526\u6e56\u5728\u7f8e\u56fd\u9500\u552eiplayer\uff0c\u5e76\u8d54\u507f5370\u4e07\u7f8e\u5143\u3002</p><p>\u3000\u3000\u9526\u6e56\u5219\u4ee5\u7231\u8fbe\u96c6\u56e2\u7684\u540d\u4e49\u901a\u8fc7\u5317\u7f8e\u516c\u53f8\u8981\u6c42\u6cd5\u9662\u9a73\u56de\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7684\u8bc9\u8bbc\uff0c\u5ba3\u544a\u5e76\u4e0d\u80fd\u4ee5\u4fdd\u62a4\u77e5\u8bc6\u4ea7\u6743\u4e3a\u501f\u53e3\u53cd\u5bf9\u6280\u672f\u7684\u8fdb\u6b65\uff0c\u8981\u6c42\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u91cd\u89c6\u5ba1\u89c6DMP-iplayer\u6280\u672f\u5bf9\u4fc3\u8fdb\u97f3\u50cf\u97f3\u54cd\u4f5c\u54c1\u901a\u8fc7\u4e92\u8054\u7f51\u4f20\u64ad\u7684\u79ef\u6781\u610f\u4e49\u3002</p><p>\u3000\u3000\u867d\u8bf4\u4e2d\u56fd\u4f01\u4e1a\u5728\u7f8e\u56fd\u7ecf\u5e38\u53d7\u5230\u4fb5\u72af\u4e13\u5229\u6743\u3001\u77e5\u8bc6\u7248\u6743\u65b9\u9762\u7684\u8d77\u8bc9\uff0c\u4f46\u662f\u7d22\u8d54\u6570\u989d\u5982\u6b64\u4e4b\u5de8\uff0c\u8fd8\u662f\u6781\u4e3a\u7f55\u89c1\u3002\u4ee4\u7f8e\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u66f4\u4e3a\u8be7\u5f02\u7684\uff0c\u4e2d\u56fd\u4f01\u4e1a\u5728\u7f8e\u56fd\u88ab\u8d77\u8bc9\uff0c\u51e0\u4e4e\u6ca1\u6709\u4f01\u4e1a\u4f1a\u7ad9\u51fa\u6765\u5e94\u8bc9\u2014\u2014\u8fd9\u4e3b\u8981\u662f\u56fd\u5185\u5230\u4e5d\u4e5d\u5e74\u65f6\u8fd8\u6ca1\u6709\u53c2\u52a0\u5173\u8d38\u603b\u534f\u5b9a\uff0c\u56fd\u5185\u4f01\u4e1a\u5bf9\u77e5\u8bc6\u4ea7\u6743\u7684\u8ba4\u8bc6\u4e5f\u4e0d\u591f\u6df1\u523b\uff0c\u66f4\u6ca1\u6709\u5230\u56fd\u9645\u4e0a\u6253\u77e5\u8bc6\u4ea7\u6743\u5b98\u53f8\u7684\u7ecf\u9a8c\u2014\u2014\u50cf\u9526\u6e56\u8fd9\u6837\u9ad8\u8c03\u5e94\u8bc9\u3001\u751a\u81f3\u8981\u6c42\u539f\u544a\u53cd\u601d\u7684\u4f01\u4e1a\u7edd\u65e0\u4ec5\u6709\uff0c\u5728\u7f8e\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u4e2d\u987f\u65f6\u5f15\u8d77\u5f88\u5927\u7684\u53cd\u54cd\u3002</p><p>\u3000\u3000\u5728\u5e94\u8bc9\u4e4b\u524d\uff0c\u9526\u6e56\u9ad8\u5c42\u80fd\u610f\u8bc6\u5230\u8fd9\u573a\u5b98\u53f8\u5bf9\u6269\u5927\u7231\u8fbe\u5728\u5317\u7f8e\u5730\u533a\u7684\u54c1\u724c\u5f71\u54cd\u6709\u597d\u5904\uff0c\u4f46\u662f\u80fd\u8fbe\u5230\u4ec0\u4e48\u6837\u7684\u7a0b\u5ea6\uff0c\u5e76\u6ca1\u6709\u51c6\u786e\u7684\u9884\u6d4b\u3002</p><p>\u3000\u3000\u4e4b\u524d\u53ea\u610f\u8bc6\u5230\u65af\u9ad8\u67cf\u3001\u4e1c\u829d\u3001\u4e09\u661f\u4ee5\u53ca\u5176\u4ed6\u5728\u5317\u7f8e\u5730\u533a\u9500\u552eiplayer\u7684\u516c\u53f8\u4ee5\u53ca\u5fb7\u4eea\uff08\u5fb7\u4eea\u4e0e\u9526\u6e56\u7684\u5408\u8d44\u516c\u53f8\u638c\u63e1iplayer\u7684\u6838\u5fc3\u786c\u4ef6\u67b6\u6784\u6280\u672f\uff09\u90fd\u4e0d\u4f1a\u5e0c\u671b\u9526\u6e56\u6253\u8f93\u8fd9\u573a\u5b98\u53f8\u7684\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u5bf9\u7231\u8fbe\u7684\u8d77\u8bc9\uff0c\u6240\u5f15\u8d77\u77e5\u8bc6\u7248\u6743\u7ea0\u7eb7\u6070\u597d\u662f\u4f20\u7edf\u97f3\u50cf\u97f3\u54cd\u5382\u5546\u4e0e\u4e92\u8054\u7f51\u5185\u5bb9\u5546\u4e4b\u95f4\u7684\u77db\u76fe\u7126\u70b9\u3002\u4f20\u7edf\u7684\u97f3\u50cf\u97f3\u54cd\u5382\u5546\u5e0c\u671b\u5bf9\u77e5\u8bc6\u7248\u6743\u91c7\u63aa\u66f4\u4e25\u683c\u7684\u6cd5\u5f8b\u63aa\u65bd\u4ee5\u675c\u7edd\u76d7\u7248\u4fb5\u6743\u884c\u4e3a\uff0c\u800c\u4e92\u8054\u7f51\u5185\u5bb9\u5546\u5219\u5e0c\u671b\u6709\u66f4\u5bbd\u677e\u7684\u6cd5\u5f8b\u73af\u5883\u6765\u4fc3\u4f7f\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u8981\u662f\u6cd5\u9662\u652f\u6301\u4e25\u5389\u9650\u5236\u8bbe\u5907\u63d0\u4f9b\u5546\u6216\u6280\u672f\u4f9b\u7ed9\u5546\u7684\u884c\u4e3a\u65f6\uff0c\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u6765\u8bf4\uff0c\u51e0\u4e4e\u53ef\u4ee5\u8bf4\u662f\u4e00\u573a\u707e\u96be\u4e86\u3002</p><p>\u3000\u3000\u8054\u90a6\u5730\u533a\u6cd5\u9662\u5bf9\u9526\u6e56\u7684\u4fb5\u6743\u5224\u51b3\u6709\u7740\u5178\u578b\u7684\u610f\u4e49\uff0c\u5728\u5317\u7f8e\u5730\u533a\u7684\u4e92\u8054\u7f51\u5185\u5bb9\u7f51\u7ad9\u4e0a\uff0c\u51e0\u4e4e\u503e\u5012\u6027\u7684\u6d8c\u73b0\u51fa\u5927\u6279\u652f\u6301\u9526\u6e56\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u5728\u8bc9\u8bbc\u4e4b\u524d\uff0c\u7f8e\u56fd\u77e5\u9053DMP-iplayer\u4e0e\u7231\u8fbe\u96c6\u56e2\u7684\u4eba\u7fa4\u6781\u4e3a\u6709\u9650\uff0c\u4e0e\u5f20\u606a\u6240\u6599\u65e0\u5dee\uff0c\u5728\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u63d0\u8bf7\u8bc9\u8bbc\u4e4b\u540e\uff0c\u7ecf\u8fc7\u7f8e\u56fd\u5a92\u4f53\u7684\u5927\u8086\u62a5\u9053\uff0c\u9526\u6e56\u5728\u5317\u7f8e\u5730\u533a\u7684iplayer\u9500\u552e\u91cf\u5f53\u6708\u5c31\u6fc0\u589e\u4e86130\uff05\uff0c\u8ffd\u5e73\u65af\u9ad8\u67cf\u7684\u9500\u552e\u989d\uff0c\u4e5f\u4fc3\u4f7fiplayer\u5728\u5317\u7f8e\u5730\u533a\u7684\u51fa\u8d27\u91cf\u57282\u6708\u4efd\u7a81\u783426\u4e07\u4ef6\uff0c\u8ddd\u8d85\u8d8aMD\u64ad\u653e\u5668\u5728\u5317\u7f8e\u5730\u533a\u7684\u6210\u7ee9\u4ec5\u4e00\u6b65\u4e4b\u9065\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u53ea\u8981\u8fd9\u573a\u5b98\u53f8\u4e0d\u8981\u8f93\u592a\u60e8\u5c31\u8db3\u591f\u4e86\u3002</p><p>\u3000\u30002\u6708\u4efd\uff0c\u7531\u4e8e\u9500\u552e\u7684\u6fc0\u589e\uff0c\u5317\u7f8e\u5730\u533a\u4ea7\u751f\u56db\u767e\u4e07\u7f8e\u5143\u7684\u76c8\u5229\uff0c\u8fd9\u4e5f\u662fiplayer\u4ea7\u54c1\u63a8\u5411\u5e02\u573a\u540e\u7684\u9996\u6708\u76c8\u5229\u3002</p><p>\u3000\u3000\u5728\u6b64\u4e4b\u5916\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u7684\u5408\u8d44\u516c\u53f8\u63a7\u5236\u7740DMP-iplayer\u6838\u5fc3\u786c\u4ef6\u67b6\u6784\u6280\u672f\uff0c\u9526\u6e56\u53c8\u5355\u72ec\u6301\u6709\u9664\u95ea\u5b58\u4e4b\u5916\u7684\u5176\u4ed6\u91cd\u8981\u8f6f\u786c\u4ef6\u67b6\u6784\u6280\u672f\uff0c\u5728\u8fd9\u4e9b\u9886\u57df\uff0c\u51e0\u4e4e\u6bcf\u4e2aiplayer\u751f\u4ea7\u5382\u5546\u90fd\u8981\u7ed9\u9526\u6e56\u96c1\u8fc7\u62e8\u6bdb\u2014\u20142\u6708\u4efd\uff0c\u9526\u6e56\u8fd9\u4e9b\u9886\u57df\u65b9\u9762\u7684\u83b7\u5f97\u4e5f\u8fbe\u5230\u56db\u767e\u4e07\u7f8e\u5143\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u6b63\u5f0f\u610f\u5473\u7740\uff0ciplayer\u6210\u4e3a\u7231\u8fbe\u96c6\u56e2\u9664\u5f71\u789f\u673a\u3001\u624b\u673a\u4e4b\u5916\uff0c\u7b2c\u4e09\u5927\u4e3b\u8425\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e09\u6708\u7684\u5efa\u90ba\u8fd8\u662f\u65e9\u6625\u5929\u6c14\uff0c\u4e2d\u5348\u6625\u9633\u548c\u7199\uff0c\u4eba\u5750\u5728\u843d\u5730\u7a97\u524d\u5374\u662f\u8212\u5766\uff0c\u4f46\u662f\u819d\u76d6\u4e0a\u7684\u62a5\u544a\u5374\u8ba9\u5f20\u606a\u611f\u5230\u4e07\u5206\u5934\u75bc\u3002</p><p>\u3000\u3000\u201c\u5df2\u7ecf\u4e0d\u80fd\u518d\u7f29\u51cf\u4e86\uff0c\u201d\u9648\u4fe1\u751f\u5750\u5728\u5f20\u606a\u5bf9\u9762\uff0c\u201c\u4e5d\u516b\u5e74\uff0c\u56fd\u5185\u9500\u552e\u624b\u673a\u4e00\u5343\u4e94\u767e\u4e07\u90e8\uff0c\u6211\u4eec\u7684\u5e02\u573a\u5360\u6709\u7387\u662f14\uff05\uff0c\u4ec5\u56fd\u5185\u5e02\u573a\u4e24\u767e\u4e07\u90e8\u624b\u673a\u7684\u9500\u552e\u989d\u5c31\u63a5\u8fd190\u4ebf\u5143\uff0c\u7231\u8fbe\u7684\u5e74\u9500\u989d\u89c4\u6a21\u4f3c\u4e4e\u5f88\u96be\u63a7\u5236\u5728\u767e\u4ebf\u5143\u4ee5\u4e0b\u2026\u2026\u53e6\u5916\uff0c\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4f1a\u91c7\u96c6\u7a0e\u6536\u7b49\u6570\u636e\uff0c\u8fd9\u4e9b\u6570\u636e\u4e5f\u4e0d\u662f\u6211\u4eec\u80fd\u968f\u4fbf\u5220\u51cf\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u534e\u590f\u7535\u5b50\u7684\u6570\u636e\u80fd\u505a\u5230\u591a\u5c11\uff1f\u201d\u5f20\u606a\u62ac\u5934\u770b\u4e86\u770b\u9648\u4fe1\u751f\uff0c\u56fd\u5bb6\u6bcf\u5e74\u90fd\u8981\u5bf9\u7535\u5b50\u884c\u4e1a\u90fd\u8981\u8fdb\u884c\u767e\u5f3a\u6392\u540d\uff0c\u7231\u8fbe\u96c6\u56e2\u7531\u53bb\u5e74\u7684\u56db\u5341\u591a\u540d\u4e00\u4e0b\u5b50\u7a9c\u8fdb\u4e09\u7532\u4e4b\u5217\uff0c\u4f3c\u4e4e\u6709\u4e9b\u592a\u523a\u773c\u4e86\u3002</p><p>\u3000\u3000\u201c\u534e\u590f\u7535\u5b50\u4e0d\u53ef\u80fd\u5b66\u6211\u4eec\uff0c\u4ed6\u4eec\u4e5d\u516b\u5e74\u7684\u9500\u552e\u989d\u5927\u6982\u80fd\u505a\u51fa\u4e8c\u767e\u4ebf\uff0c\u8fd9\u4e2a\u662f\u8981\u6bd4\u6211\u4eec\u9ad8\u4e00\u622a\uff0c\u201d\u9648\u4fe1\u751f\u8bf4\u9053\uff0c\u201c\u53e6\u5916\uff0c\u6211\u4eec\u5c06\u5229\u6da6\u591a\u5f80\u5143\u5668\u4ef6\u90e8\u95e8\u5212\uff0c\u6570\u636e\u4e5f\u4f1a\u597d\u770b\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u9526\u6e56\u7684\u624b\u673a\u4e1a\u52a1\u6bdb\u5229\u6da6\u8d85\u8fc740\uff05\uff0c\u7531\u4e8e\u65d7\u4e0b\u7684\u4f01\u4e1a\u5927\u591a\u8fd8\u5904\u4e8e\u7a0e\u8d39\u51cf\u514d\u671f\u5185\uff0c\u7a0e\u8d39\u8d1f\u62c5\u8f83\u8f7b\uff0c\u51c0\u5229\u6da6\u4e5f\u8d85\u8fc730\uff05\uff0c\u800c\u534e\u590f\u7535\u5b50\u7684\u624b\u673a\u4e1a\u52a1\u51c0\u5229\u6da6\u53ea\u6709\u9526\u6e56\u4e09\u5206\u4e4b\u4e00\u7684\u6c34\u5e73\uff0c\u8fd9\u91cc\u9762\u6570\u636e\u4e0d\u6539\u4e00\u6539\uff0c\u4f1a\u5f88\u89e6\u52a8\u90e8\u59d4\u67d0\u4e9b\u5b98\u5458\u7684\u795e\u7ecf\u3002</p><p>\u3000\u3000\u5f20\u606a\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u5212\u5230\u5143\u5668\u4ef6\u90e8\u95e8\u5934\u4e0a\u4e5f\u597d\uff0c\u73b0\u5728\u770b\u6765\u4e5f\u4e0d\u662f\u906e\u4e0d\u906e\u63a9\u7684\u95ee\u9898\uff0c\u4eca\u5e74\u80fd\u5c06\u6570\u636e\u505a\u5f97\u597d\u770b\u4e00\u4e9b\uff0c\u660e\u5e74\u8fd8\u662f\u4f1a\u5f88\u5934\u75bc\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u522b\u4eba\u8fd8\u662f\u7fa1\u6155\u4f60\u7684\u5934\u75bc\u554a\uff0c\u201d\u9648\u4fe1\u751f\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4e5d\u516b\u5e74\uff0c\u79fb\u52a8\u7528\u6237\u603b\u6570\u589e\u52a0\u4e86\u4e00\u5343\u4e00\u767e\u4e07\u6237\uff0c\u4eca\u5e74\u6709\u4e86\u8054\u901a\u7684\u7ade\u4e89\uff0c\u52a0\u4e0a\u4e2d\u6587\u77ed\u4fe1\u4e1a\u52a1\u4f1a\u9010\u6e10\u5f00\u5c55\u8d77\u6765\uff0c\u5de8\u989d\u8d2d\u5361\u8d39\u8fd9\u4e2a\u4e0d\u5408\u9002\u7684\u6536\u8d39\u4f1a\u76f4\u63a5\u780d\u6389\uff0c\u901a\u8baf\u8d39\u7528\u4e5f\u4f1a\u5927\u5e45\u4e0b\u964d\uff0c\u6211\u770b\u79fb\u52a8\u7528\u6237\u603b\u6570\u5c11\u8bf4\u4e5f\u8981\u5728\u6b64\u57fa\u7840\u4e0a\u518d\u7ffb\u4e00\u500d\uff0c\u56fd\u5185\u5e02\u573a\u624b\u673a\u9500\u552e\u91cf\u5c06\u7a81\u7834\u4e09\u5343\u4e07\u4ef6\uff0c\u6211\u4eec\u7684\u76ee\u6807\u662f\u4fdd\u7b2c\u56db\u3001\u4e89\u7b2c\u4e09\uff0c\u8fbe\u5230\u8fd9\u4e2a\u76ee\u6807\uff0c\u6211\u4eec\u4e5d\u4e5d\u5e74\u7684\u56fd\u5185\u624b\u673a\u9500\u552e\u5c06\u8fbe\u5230\u56db\u767e\u4e07\u53f0\uff0c\u9500\u552e\u989d\u5dee\u4e0d\u591a\u5728\u4e00\u767e\u4e94\u5341\u4ebf\u5de6\u53f3\u2014\u2014\u4eca\u5e74\u52c9\u5f3a\u80fd\u4e0d\u8fdb\u4e09\u7532\uff0c\u660e\u5e74\u80af\u5b9a\u8981\u8fdb\u4e09\u7532\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7f13\u4e00\u5e74\uff0c\u4e5f\u662f\u597d\u7684\uff0c\u5bf9\u6709\u4e9b\u5b98\u5458\u6765\u8bf4\uff0c\u4e5f\u7b97\u6709\u4e2a\u63a5\u53d7\u7684\u8fc7\u7a0b\uff0c\u81f3\u5c11\u636e\u6211\u6240\u77e5\uff0c\u803f\u91cd\u9633\u662f\u4e2a\u987d\u56fa\u7684\u56fd\u6709\u5784\u65ad\u4e3b\u4e49\u8005\uff0c\u6211\u4eec\u7684\u98ce\u5149\u592a\u62db\u6447\uff0c\u4ed6\u4f1a\u4e0b\u610f\u8bc6\u7684\u7ed9\u6211\u4eec\u4e00\u4e9b\u538b\u529b\u7684\u3002\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u7ad9\u4e86\u8d77\u6765\u5c06\u62a5\u544a\u8fd8\u7ed9\u9648\u4fe1\u751f\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u6b21\u7684\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\u8fd9\u4e24\u5929\u5c31\u8981\u53ec\u5f00\uff0c\u9526\u6e56\u65d7\u4e0b\u5404\u4f01\u4e1a\u7684\u5e74\u5ea6\u8d22\u52a1\u6570\u636e\u4e5f\u9646\u7eed\u51fa\u7089\uff0c\u5728\u9526\u6e56\u5546\u4e8b\u8fde\u521d\u6b65\u7684\u5e03\u5c40\u90fd\u6ca1\u6709\u5b8c\u6210\u4e4b\u65f6\uff0c\u7231\u8fbe\u96c6\u56e2\u4f5c\u4e3a\u9526\u6e56\u4f53\u7cfb\u7684\u4e24\u5927\u6838\u5fc3\u4e4b\u4e00\uff0c\u81ea\u7136\u8981\u6311\u8d77\u5927\u6881\uff0c\u4e5d\u516b\u5e74\u51c0\u76c8\u5229\u63a5\u8fd1\u56db\u5341\u4ebf\u5143\u3002</p><p>\u3000\u3000\u5728\u8fd1\u4e24\u5e74\u524d\uff0c\u9526\u6e56\u501f\u58f3\u4e0a\u5e02\u4ece\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u5377\u6765\u56db\u5341\u4ebf\uff0c\u5df2\u7ecf\u60ca\u7206\u4eba\u7684\u773c\u7403\uff0c\u800c\u6b64\u65f6\u53ea\u662f\u7231\u8fbe\u96c6\u56e2\u4e00\u5e74\u7684\u76c8\u5229\uff0c\u6574\u4e2a\u5e9e\u6742\u9526\u6e56\u4f53\u7cfb\u4ea7\u751f\u7684\u5229\u6da6\u66f4\u662f\u60ca\u4eba\uff0c\u7231\u8fbe\u7535\u5b50\u5df2\u7ecf\u8fdb\u5165\u7a33\u5b9a\u7684\u76c8\u5229\u671f\uff0c\u76db\u946b\u7535\u5668\u3001\u65b0\u5149\u7eb8\u4e1a\u3001\u9999\u96ea\u6d77\u7b49\u4f01\u4e1a\u90fd\u6b65\u5165\u5feb\u901f\u53d1\u5c55\u7684\u8f68\u9053\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u9999\u96ea\u6d77\u6765\uff0c\u5f20\u606a\u8fd1\u671f\u8981\u524d\u5f80\u60e0\u5c71\u4e00\u6b21\uff0c\u9999\u96ea\u6d77\u8fd8\u6709\u7ea624\uff05\u7684\u80a1\u6743\u7531\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u4ee3\u8868\u60e0\u5c71\u5e02\u653f\u5e9c\u6301\u6709\uff0c\u53ea\u6709\u5148\u5c06\u9999\u96ea\u6d77\u7684\u80a1\u6743\u5173\u7cfb\u7406\u987a\u4e86\uff0c\u65e5\u540e\u624d\u80fd\u987a\u5229\u7684\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4ea4\u6613\u4e0a\u5e02\u3002</p><p>\u3000\u3000\u5728\u4e5d\u516d\u5e74\u521d\uff0c\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u6240\u6301\u7684\u80a1\u6743\u4f5c\u4ef73\u4ebf\u5143\uff0c\u4e5d\u4e03\u5e74\u65f6\uff0c\u9526\u6e56\u901a\u8fc7\u8d8a\u79c0\u518d\u4e00\u6b21\u5bf9\u9999\u96ea\u6d77\u8fdb\u884c\u6ce8\u8d44\uff0c\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u624b\u91cc\u6240\u6301\u80a1\u6743\u589e\u503c\u52304\u4ebf\uff0c\u4e5d\u516b\u5e74\uff0c\u9999\u96ea\u6d77\u7684\u4e1a\u7ee9\u7ecf\u8fc7\u592e\u89c6\u6807\u738b\u7684\u50ac\u80a5\uff0c\u5728\u4e5d\u4e03\u5e74\u7684\u57fa\u7840\u4e0a\u589e\u957f\u4e86\u8fd1\u4e24\u500d\uff0c\u60e0\u5c71\u5e02\u8f7b\u5de5\u5c40\u624b\u91cc\u6240\u6301\u7684\u80a1\u6743\u4ef7\u503c\u81ea\u7136\u518d\u5ea6\u5927\u6da8\uff0c\u76ee\u524d\u5df2\u7ecf\u8c08\u59a5\u516b\u4ebf\u5143\u7684\u4ef7\u683c\uff0c\u52a0\u4e0a\u8fc7\u53bb\u4e00\u6bb5\u65f6\u95f4\u7684\u8d62\u5229\u5206\u5229\uff0c\u60e0\u5c71\u5e02\u653f\u5e9c\u5c11\u8bf4\u4e5f\u4ece\u9999\u96ea\u6d77\u5934\u4e0a\u62ff\u8d70\u4e5d\u4e2a\u4ebf\uff0c\u662f\u5f53\u521d\u97e9\u56fd\u4e09\u661f\u96c6\u56e2\u5f00\u4ef7\u7684\u56db\u500d\u2014\u2014\u5728\u6b63\u5f0f\u7b7e\u7ea6\u4e4b\u65f6\uff0c\u5f20\u606a\u8fd8\u662f\u8981\u4eb2\u81ea\u524d\u5f80\u60e0\u5c71\u8d70\u4e00\u8d9f\uff0c\u4e0e\u8d75\u9633\u89c1\u6b21\u9762\u3002</p></article>"}}}]);
