(window.webpackJsonp=window.webpackJsonp||[]).push([[1006],{1029:function(p){p.exports={chapterTitle:null,title:"\u7b2c814\u7ae0 \u65b0\u95fb\u9898\u6750",content:"<article><h2>\u7b2c814\u7ae0 \u65b0\u95fb\u9898\u6750</h2><p>\u3000\u3000\u6bcf\u5929\u4e0a\u5348\u90fd\u6709\u5317\u4eac\u98de\u5efa\u90ba\u7684\u822a\u73ed\uff0c\u4e0b\u5348\u4e00\u70b9\u949f\u5de6\u53f3\u80fd\u62b5\u8fbe\u5efa\u90ba\u5e02\u533a\uff1b\u591c\u91cc\u4e34\u65f6\u60f3\u7740\u8fc7\u6765\uff0c\u8fd8\u662f\u4e58\u591c\u91cc\u7684\u706b\u8f66\u66f4\u5feb\u4e00\u4e9b\uff0c\u80fd\u8d76\u5728\u4e0a\u5348\u62b5\u8fbe\u5efa\u90ba\u3002\u7535\u8bdd\u91cc\u8bf4\u7684\u5306\u5fd9\uff0c\u5d14\u90c1\u66fc\u90fd\u4e0d\u6e05\u695a\u5979\u54e5\u4e3a\u4ec0\u4e48\u8d76\u7740\u5230\u5efa\u90ba\u6765\uff0c\u60f3\u591a\u95ee\u51e0\u53e5\uff0c\u90a3\u8fb9\u6709\u4eba\u558a\u7740\u5979\u54e5\u8fdb\u7ad9\u68c0\u7968\uff0c\u5306\u5fd9\u6536\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u5d14\u90c1\u66fc\u5148\u53bb\u9662\u91cc\u627e\u5d14\u56fd\u6052\uff0c\u60f3\u77e5\u9053\u9662\u91cc\u5bf9\u5f20\u606a\u7684\u5904\u7406\u610f\u89c1\uff0c\u5979\u4ee5\u4e3a\u5f20\u606a\u8fd9\u6b21\u80af\u5b9a\u4f1a\u53d7\u5230\u5904\u5206\uff0c\u4f46\u662f\u5e0c\u671b\u9662\u91cc\u4e0d\u8981\u5c06\u8d23\u4efb\u65e0\u9650\u7684\u6269\u5927\u3002\u51fa\u4e4e\u5979\u7684\u610f\u6599\uff0c\u5d14\u56fd\u6052\u544a\u8bc9\u5979\uff1a\u201c\u90a3\u4e2a\u5b66\u751f\u7684\u8a00\u8f9e\u867d\u7136\u504f\u6fc0\u4e86\u4e9b\uff0c\u8bf4\u7684\u8bdd\u5e76\u975e\u4e0d\u65e0\u9053\u7406\uff0c\u9662\u91cc\u8981\u614e\u91cd\u8003\u8651\u4e00\u4e0b\uff0c\u5982\u679c\u771f\u662f\u65e0\u7406\u53d6\u95f9\u3001\u5728\u8bb2\u5802\u4e0a\u8c29\u9a82\u8001\u5e08\uff0c\u9662\u91cc\u80af\u5b9a\u8981\u4e25\u8083\u5904\u7406\uff0c\u5982\u679c\u53ea\u662f\u5728\u5b66\u672f\u4e0a\u6709\u4e0d\u540c\u610f\u89c1\uff0c\u9662\u91cc\u8fd8\u662f\u8981\u4ee5\u6279\u8bc4\u6559\u80b2\u4e3a\u4e3b\u2026\u2026\u6628\u5929\u8fc7\u53bb\u542c\u8bb2\u5ea7\u7684\uff0c\u9664\u4e86\u5b66\u751f\u4e4b\u5916\uff0c\u8fd8\u6709\u8bb8\u591a\u662f\u9662\u91cc\u7684\u8001\u5e08\uff0c\u9662\u91cc\u4f1a\u6536\u96c6\u4e00\u4e0b\u4ed6\u4eec\u7684\u610f\u89c1\u3002\u5c0f\u5d14\u8001\u5e08\uff0c\u90a3\u4e2a\u5b66\u751f\u662f\u4e5d\u4e03\u7ea7\u7684\uff0c\u6536\u96c6\u610f\u89c1\u7684\u4e8b\u60c5\uff0c\u662f\u4e0d\u662f\u4f60\u6765\u8d1f\u8d23\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u9662\u91cc\u4ec0\u4e48\u65f6\u5019\u53d8\u5f97\u8fd9\u4e48\u6c11\u4e3b\u5f00\u653e\u4e86\uff1f\u5d14\u90c1\u66fc\u6ee1\u5fc3\u7591\u60d1\uff0c\u4e0d\u8fc7\u8fd9\u6837\u7684\u5904\u7406\u6001\u5ea6\u662f\u5979\u6700\u5e0c\u671b\u770b\u5230\u7684\uff0c\u81f3\u5c11\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u7684\u5c06\u5b66\u751f\u4e00\u68cd\u5b50\u6253\u6b7b\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5e94\u627f\u4e0b\u6765\uff0c\u5979\u63a5\u4e0b\u6765\u8fd8\u8981\u8d76\u7740\u53bb\u706b\u8f66\u7ad9\u63a5\u5979\u54e5\uff0c\u771f\u4e0d\u660e\u767d\u5979\u54e5\u4e3a\u4ec0\u4e48\u8981\u7a81\u7136\u8d76\u5230\u5efa\u90ba\u6765\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u4e0e\u4ed6\u7b2c\u4e00\u6b21\u51fa\u573a\u65f6\u6ca1\u6709\u592a\u5927\u7684\u53d8\u5316\uff0c\u5927\u80e1\u5b50\u3001\u4e00\u8138\u6ca7\u6851\uff0c\u660e\u660e\u624d\u4e09\u5341\u4e94\u516d\u5c81\u7684\u5e74\u9f84\uff0c\u770b\u4e0a\u53bb\u5c31\u50cf\u56db\u5341\u5c81\u51fa\u5934\u7684\u4e2d\u5e74\u4eba\uff0c\u5e06\u5e03\u8863\uff0c\u5916\u9762\u8fd8\u7a7f\u7740\u6ee1\u662f\u53e3\u888b\u7684\u6d45\u7eff\u8272\u9a6c\u5939\uff0c\u80cc\u5728\u540e\u80cc\u4e0a\u9ad8\u8fc7\u5934\u9876\u7684\u80cc\u56ca\uff0c\u8fd8\u6709\u968f\u8eab\u88c5\u6444\u5f71\u5668\u6750\u7684\u630e\u5305\u3002</p><p>\u3000\u3000\u54e5\u54e5\u5e38\u5e74\u9a7b\u5728\u6d77\u5916\uff0c\u4e00\u5e74\u90fd\u89c1\u4e0d\u5230\u51e0\u56de\uff0c\u5d14\u90c1\u66fc\u8d70\u4e0a\u524d\uff0c\u5144\u59b9\u60c5\u6df1\uff0c\u70ed\u60c5\u76f8\u62e5\u4e86\u4e00\u4e0b\uff0c\u8ba9\u522b\u4eba\u4ee5\u4e3a\u662f\u7236\u5973\u5728\u8f66\u7ad9\u76f8\u89c1\u5462\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u8eab\u8fb9\u8fd8\u6709\u4e00\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u6d01\u51c0\u9752\u5e74\uff0c\u5d14\u90c1\u6052\u624d\u6402\u7740\u59b9\u59b9\u59b9\u8ddf\u8eab\u8fb9\u7684\u540c\u4e8b\u4ecb\u7ecd\u8bf4\u9053\uff1a\u201c\u600e\u4e48\u6837\uff0c\u770b\u5230\u6211\u59b9\u672c\u4eba\u4e0d\u4f1a\u8bf4\u6211\u5439\u725b\u4e86\u5427\uff1f\u6211\u7238\u5988\u7684\u4f18\u826f\u57fa\u56e0\u6ca1\u6709\u5728\u6211\u8eab\u4e0a\u5f97\u5230\u4f53\u73b0\uff0c\u4f18\u70b9\u5168\u96c6\u4e2d\u5230\u6211\u59b9\u59b9\u8eab\u4e0a\u4e86\u2026\u2026\u201d\u53c8\u8ddf\u4ed6\u59b9\u59b9\u5d14\u90c1\u66fc\u4ecb\u7ecd\u4ed6\u7684\u540c\u4e8b\uff0c\u201c\u8fd9\u4f4d\u662f\u8a79\u6587\u660e\uff0c\u8a79\u5929\u4f51\u7684\u8a79\uff0c\u4f60\u5524\u4ed6\u5927\u8a79\u5c31\u53ef\u4ee5\u4e86\uff0c\u793e\u91cc\u7684\u540c\u4e8b\u2026\u2026\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6709\u4e9b\u5fae\u7f9e\u7684\u8ddf\u4ed6\u54e5\u7684\u540c\u4e8b\u62db\u547c\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4ec0\u4e48\u539f\u56e0\u90fd\u4e0d\u8bf4\uff0c\u6025\u51b2\u51b2\u7684\u8dd1\u5230\u5efa\u90ba\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5d14\u90c1\u66fc\u770b\u5230\u5979\u54e5\u4e0e\u540c\u4e8b\u4e00\u8d77\u8fc7\u6765\uff0c\u5f53\u7136\u4e0d\u662f\u60f3\u5ff5\u5979\u8fd9\u4e2a\u72ec\u8eab\u5728\u5916\u8bfb\u7684\u59b9\u59b9\u3002</p><p>\u3000\u3000\u201c\u4f60\u6628\u5929\u8ddf\u6211\u8bf4\u7684\u4e8b\u60c5\uff0c\u5927\u8a79\u6b63\u597d\u6709\u4e9b\u4e86\u89e3\uff0c\u6211\u4eec\u4fe9\u4e00\u5408\u8ba1\uff0c\u89c9\u5f97\u7ae0\u5dde\u53ef\u80fd\u6709\u6bd4\u8f83\u91cd\u8981\u7684\u65b0\u95fb\u9898\u6750\u53ef\u4ee5\u6316\u6398\uff0c\u8fd9\u65f6\u5019\u53c8\u6b63\u503c\u56fd\u5185\u77ff\u4ea7\u8d44\u6e90\u653f\u7b56\u8c03\u6574\u7684\u5173\u952e\u65f6\u523b\uff0c\u8fd9\u7c7b\u65b0\u95fb\u7684\u4ef7\u503c\u5c06\u66f4\u9ad8\uff0c\u8d76\u7740\u6211\u56de\u56fd\u5185\u8fd8\u8981\u6b63\u800c\u516b\u7ecf\u7684\u505a\u8fc7\u4ec0\u4e48\u4e8b\uff0c\u54ea\u80fd\u4e0d\u5fc3\u7126\uff1f\u5c31\u8fde\u591c\u8d76\u4e86\u8fc7\u6765\u3002\u201d\u5d14\u90c1\u6052\u8fd9\u624d\u6709\u7a7a\u8bf4\u8d77\u4ed6\u8d76\u5230\u5efa\u90ba\u7684\u539f\u56e0\uff0c\u201c\u6628\u5929\u8d76\u7740\u68c0\u7968\u4e0a\u8f66\uff0c\u6ca1\u6765\u5f97\u53ca\u8ddf\u4f60\u7ec6\u8bf4\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u600e\u4e48\u4f1a\u5728\u5efa\u90ba\u4e0b\u8f66\uff0c\u4e0d\u76f4\u63a5\u53bb\u6c5f\u5357\u7ae0\u5dde\uff1f\u201d\u5d14\u90c1\u66fc\u7591\u60d1\u7684\u76ef\u7740\u5979\u54e5\u54e5\uff0c\u201c\u6211\u53ef\u4e0d\u89c9\u5f97\u4f60\u5728\u5efa\u90ba\u4e2d\u9014\u4e0b\u8f66\u662f\u4e13\u7a0b\u4e3a\u4e86\u770b\u6211\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5d14\u90c1\u6052\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u542c\u4f60\u7684\u63cf\u8ff0\uff0c\u4f60\u90a3\u4e2a\u5b66\u751f\u770b\u4e0a\u53bb\u5f88\u6e05\u695a\u7ae0\u5dde\u7684\u60c5\u51b5\uff0c\u6211\u4eec\u624b\u91cc\u638c\u63e1\u7684\u8d44\u6599\u4e5f\u76f8\u5f53\u6709\u9650\uff0c\u6240\u4ee5\u60f3\u7740\u5148\u5728\u5efa\u90ba\u505c\u4e00\u4e0b\uff0c\u627e\u4f60\u90a3\u4e2a\u5b66\u751f\u4e86\u89e3\u4e00\u4e0b\u60c5\u51b5\u518d\u8054\u7cfb\u6c5f\u5357\u5206\u793e\u7684\u540c\u4e8b\u4e00\u8d77\u4e0b\u53bb\u91c7\u8bbf\u66f4\u6709\u628a\u63e1\u4e00\u4e0b\u2026\u2026\u4f60\u90a3\u4e2a\u5b66\u751f\u662f\u7ae0\u5dde\u5f53\u5730\u4eba\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u5443\uff0c\u5c31\u56e0\u4e3a\u8fd9\u4e2a\u539f\u56e0\u8981\u5728\u5efa\u90ba\u505c\u987f\uff1f\u201d\u5d14\u90c1\u66fc\u5476\u4e86\u5476\u5634\uff0c\u201c\u90fd\u602a\u6211\u6ca1\u6709\u5728\u7535\u8bdd\u91cc\u8ddf\u4f60\u8bf4\u6e05\u695a\uff0c\u4f60\u8fd8\u8bb0\u5f97\u5f53\u521d\u6211\u521a\u53bb\u6d77\u5dde\u4e00\u4e2d\uff0c\u6d77\u5dde\u4e00\u4e2d\u8fd8\u6709\u4e24\u540d\u4e0e\u4e07\u5929\u624d\u3001\u8d75\u53f8\u660e\u88ab\u5b66\u751f\u4e00\u8d77\u79f0\u4e3a\u4e00\u4e2d\u56db\u5c11\u7684\u5b66\u751f\uff1f\u201d</p><p>\u3000\u3000\u6d77\u5dde\u4e8b\u4ef6\uff0c\u5d14\u90c1\u6052\u600e\u4e48\u53ef\u80fd\u5fd8\u6389\uff0c\u5b66\u751f\u7ed1\u67b6\u6559\u5e08\u6848\uff0c\u53ef\u80fd\u4e3e\u56fd\u4e0a\u4e0b\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u51e0\u4ef6\uff0c\u504f\u5de7\u8fd9\u4e8b\u60c5\u53c8\u53d1\u751f\u5728\u4ed6\u59b9\u59b9\u5934\u4e0a\uff0c\u8001\u7237\u5b50\u9707\u6012\u5f02\u5e38\uff0c\u90fd\u5904\u4e8e\u66b4\u8d70\u7684\u8fb9\u7f18\u4e86\u3002\u8ba9\u4eba\u5947\u602a\u7684\uff0c\u6d77\u5dde\u5b98\u65b9\u5bf9\u90a3\u4ef6\u4e8b\u60c5\u7684\u5904\u7406\u975e\u5e38\u7684\u53ca\u65f6\u5230\u4f4d\uff0c\u5373\u4f7f\u5fc3\u91cc\u8fd8\u6709\u6028\u6c14\uff0c\u4e5f\u4e0d\u4fbf\u8868\u9732\u51fa\u6765\uff0c\u5c31\u8ba9\u90a3\u4ef6\u4e8b\u60c5\u8fc7\u53bb\u4e86\u3002</p><p>\u3000\u3000\u6d77\u5dde\u4e8b\u4ef6\u6d89\u53ca\u5230\u88ab\u79f0\u4e3a\u4e00\u4e2d\u56db\u5c11\u7684\u4e24\u540d\u7ea8\u7ed4\u5b66\u751f\uff0c\u5d14\u90c1\u6052\u8d76\u5230\u6d77\u5dde\u5904\u7406\u90a3\u4ef6\u4e8b\u60c5\u7684\u5584\u540e\u5de5\u4f5c\uff0c\u5f53\u7136\u5bf9\u53e6\u4e24\u540d\u56db\u5c11\u4e2d\u7684\u7ea8\u7ed4\u5b66\u751f\u6709\u8fc7\u4e00\u5b9a\u7684\u8c03\u67e5\uff0c\u5012\u662f\u6ca1\u6709\u592a\u8fc7\u5206\u7684\u6076\u884c\u3002\u90a3\u65f6\u5019\uff0c\u5f20\u606a\u4e0e\u9526\u6e56\u7684\u8d44\u6599\u8fd8\u6ca1\u6709\u5411\u7701\u4ee5\u53ca\u66f4\u4e0a\u5c42\u7684\u673a\u5173\u62a5\u5907\uff0c\u5c31\u7b97\u5d14\u90c1\u6052\u6709\u901a\u5929\u7684\u80fd\u8010\uff0c\u4e5f\u65e0\u6cd5\u67e5\u5230\u5f20\u606a\u7684\u5e95\u7ec6\u3002\u8fd9\u4ef6\u4e8b\u90fd\u5df2\u7ecf\u8fc7\u53bb\u4e09\u5e74\u4e86\uff0c\u5d14\u90c1\u6052\u4e5f\u4e0d\u53ef\u80fd\u4e00\u76f4\u5173\u6ce8\uff0c\u542c\u59b9\u59b9\u8fd9\u65f6\u5019\u63d0\u53e6\u4e24\u540d\u5b66\u751f\uff0c\u5947\u602a\u7684\u95ee\u9053\uff1a\u201c\u600e\u4e48\u4e86\uff0c\u96be\u9053\u53c8\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u8ddf\u4ed6\u4eec\u6709\u5173\uff1f\u201d</p><p>\u3000\u3000\u201c\u5176\u4e2d\u4e00\u4e2a\u5b66\u751f\u53eb\u675c\u98de\uff0c\u73b0\u5728\u6210\u4e86\u5927\u5b66\u751f\u521b\u4e1a\u7684\u5178\u8303\uff0c\u53bb\u5e74\u521a\u5165\u5b66\u65f6\uff0c\u5c31\u4ece\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u62ff\u5230\u4e00\u5343\u4e07\u7684\u878d\u8d44\uff0c\u521b\u529e\u516c\u53f8\u5df2\u6709\u6570\u5343\u4e07\u7684\u8d44\u4ea7\uff0c\u53f7\u79f0\u4e1c\u5927\u5b66\u751f\u9996\u5bcc\uff1b\u8fd8\u6709\u4e00\u4e2a\u5b66\u751f\u53eb\u5f20\u606a\uff0c\u5c31\u662f\u4ed6\u5728\u8bb2\u5802\u4e0a\u516c\u7136\u53cd\u9a73\u5468\u747e\u73ba\u7684\u89c2\u70b9\uff0c\u4f60\u82e5\u662f\u8bf4\u6709\u4eba\u5bf9\u7ae0\u5dde\u7684\u60c5\u51b5\u6709\u6240\u4e86\u89e3\u7684\u8bdd\uff0c\u53ef\u80fd\u5c31\u662f\u4ed6\u4e86\uff0c\u4f46\u662f\u4ed6\u662f\u6d77\u5dde\u4eba\uff0c\u8ddf\u7ae0\u5dde\u6ca1\u6709\u534a\u70b9\u5173\u7cfb\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5d14\u90c1\u6052\u5f53\u7136\u60ca\u8bb6\uff0c\u4ed6\u7279\u6709\u7684\u654f\u9510\u804c\u4e1a\u89e6\u611f\u731c\u6d4b\u8fd9\u4e24\u540d\u5b66\u751f\u672c\u8eab\u5c31\u8574\u85cf\u7740\u91cd\u5927\u7684\u65b0\u95fb\u9898\u6750\uff0c\u4ed6\u8bb0\u5f97\u90a3\u4e24\u540d\u5b66\u751f\u7684\u5bb6\u957f\u5f53\u65f6\u90fd\u662f\u6d77\u5dde\u5e02\u91cc\u7684\u91cd\u8981\u5b98\u5458\uff0c\u8fd9\u7c7b\u65b0\u95fb\u4e00\u822c\u4e0d\u80fd\u516c\u5f00\u53d1\u8868\uff0c\u771f\u6709\u4ec0\u4e48\u52fe\u5f53\u5728\u80cc\u540e\uff0c\u53ef\u4ee5\u5148\u53d1\u8868\u5728\u5185\u53c2\u4e0a\uff0c\u201c\u90a3\u6709\u673a\u4f1a\u8fd8\u771f\u8981\u89c1\u89c1\u90a3\u4e24\u540d\u5b66\u751f\uff0c\u7279\u522b\u90a3\u4e2a\u53eb\u5f20\u606a\u7684\uff0c\u5373\u4f7f\u4ed6\u4e0d\u662f\u7ae0\u5dde\u4eba\uff0c\u4e5f\u5e94\u8be5\u6709\u91cd\u8981\u7684\u7ebf\u7d22\u6765\u6e90\u5427\uff0c\u4f60\u5e26\u6211\u4eec\u53bb\u627e\u4ed6\u2026\u2026\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6709\u4e9b\u72b9\u8c6b\uff0c\u5979\u53c8\u4e0d\u60f3\u8ba9\u5979\u54e5\u54e5\u77e5\u9053\u81ea\u5df1\u8fd9\u4e2a\u5e74\u7ea7\u4e3b\u4efb\u5f53\u5f97\u8fd9\u4e48\u7a9d\u56ca\uff0c\u4f46\u662f\u5979\u73b0\u5728\u5230\u54ea\u91cc\u53bb\u627e\u5f20\u606a\uff1f\u53ea\u5f97\u80e1\u4e71\u627e\u4e86\u4e2a\u501f\u53e3\uff1a\u201c\u73b0\u5728\u7684\u5927\u5b66\u751f\uff0c\u4f60\u4ee5\u4e3a\u8ddf\u4f60\u4eec\u90a3\u65f6\u5019\u4e00\u6837\uff1f\u867d\u8bf4\u4eca\u5929\u662f\u5468\u4e00\uff0c\u4f46\u662f\u8c01\u80fd\u4fdd\u8bc1\u8fd9\u4e9b\u5b66\u751f\u5728\u4e0d\u5728\u5b66\u6821\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u5148\u627e\u627e\u770b\u2026\u2026\u201d\u5d14\u90c1\u6052\u5fc3\u60f3\u73b0\u5728\u6709\u4e9b\u5927\u5b66\u751f\u603b\u4e0d\u4f1a\u5b89\u5fc3\u5750\u5728\u6559\u5ba4\u91cc\u542c\u8bfe\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u3001\u5d14\u90c1\u6052\u5144\u59b9\u5f53\u7136\u627e\u4e0d\u5230\u5f20\u606a\uff0c\u5f20\u606a\u4e0a\u5348\u5c31\u4e0e\u665a\u6674\u7a9d\u5728\u9752\u5c71\u516c\u5bd3\u3002</p><p>\u3000\u3000\u4e0e\u5f20\u606a\u80fd\u5728\u516c\u5bd3\u91cc\u7a9d\u4e0a\u4e00\u5929\uff0c\u5bf9\u665a\u6674\u6765\u8bf4\u662f\u96be\u5f97\u7684\u5e78\u798f\uff0c\u5979\u7a7f\u7740\u70df\u7070\u8272\u7684\u8d34\u8eab\u68c9\u886b\uff0c\u4e30\u97f5\u6210\u719f\uff0c\u8eab\u6750\u6027\u611f\u52a8\u4eba\u3002\u5979\u8fd9\u6b21\u4e3a\u7b79\u5907\u6d77\u88d5\u56fd\u9645\u79c1\u7acb\u5b66\u6821\u5efa\u90ba\u5206\u6821\u7684\u4e8b\u60c5\uff0c\u5979\u4f1a\u5728\u5efa\u90ba\u4f4f\u4e0a\u4e00\u6bb5\u65f6\u95f4\uff0c\u524d\u5929\u53bb\u83dc\u5e02\u573a\u4e70\u4e86\u8bb8\u591a\u51c0\u83dc\u4e0e\u901f\u98df\u50a8\u5907\u4e0b\u6765\uff0c\u5f20\u606a\u8db3\u4e0d\u51fa\u6237\u4e0e\u5979\u5728\u8fd9\u91cc\u8fc7\u4e86\u51e0\u5929\u4e0e\u4e16\u9694\u79bb\u7684\u751f\u6d3b\u4e5f\u6ca1\u6709\u95ee\u9898\u3002</p><p>\u3000\u3000\u5728\u5d14\u90c1\u6052\u610f\u8bc6\u5230\u7ae0\u5dde\u5b58\u5728\u91cd\u5927\u7684\u65b0\u95fb\u9898\u6750\uff0c\u5f20\u606a\u5f53\u7136\u6e05\u695a\u5f53\u524d\u56fd\u5185\u7684\u7a00\u571f\u77ff\u8d44\u6e90\u76d7\u91c7\u53ca\u73af\u5883\u7834\u574f\u7684\u4e25\u9177\u73b0\u72b6\u5c06\u5f71\u54cd\u56fd\u5185\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u8d70\u5411\uff0c\u867d\u7136\u540e\u4e16\u5a92\u4f53\u5bf9\u6b64\u591a\u6709\u62a5\u9053\uff0c\u4f46\u662f\u6b64\u65f6\u5e76\u6ca1\u6709\u7b2c\u4e00\u624b\u7684\u6750\u6599\uff0c\u5f20\u606a\u9700\u8981\u7b2c\u4e00\u624b\u7684\u6750\u6599\u6765\u589e\u5f3a\u5bf9\u4e2d\u592e\u6709\u5173\u90e8\u95e8\u7684\u8bf4\u670d\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u6e05\u695a\u65b0\u534e\u793e\u7684\u5d14\u90c1\u6052\u4e0e\u540c\u4e8b\u5df2\u7ecf\u8e0f\u4e0a\u5efa\u90ba\u7684\u571f\u5730\u6b63\u5728\u5bfb\u627e\u4ed6\u4e86\u89e3\u60c5\u51b5\uff0c\u6bd4\u8d77\u90c1\u90c1\u6052\u7684\u51c6\u5907\u4e0d\u8db3\uff0c\u5f20\u606a\u8fd9\u8fb9\u7684\u7b79\u5212\u53ef\u5c31\u5468\u5bc6\u591a\u4e86\u3002\u9664\u4e86\u5c06\u5510\u82f1\u57f9\u6263\u4e0b\u6765\u5438\u5f15\u7ae0\u5dde\u5510\u5bb6\u7684\u6ce8\u610f\u529b\u4e4b\u5916\uff0c\u5f20\u606a\u53c8\u63a5\u5230\u9646\u6587\u592b\u7684\u7535\u8bdd\uff0c\u77e5\u9053\u5d14\u56fd\u6052\u5c06\u5728\u56fd\u5546\u9662\u8fdb\u884c\u7684\u5b89\u6392\uff0c\u5c06\u77db\u5934\u6307\u5411\u5468\u747e\u73ba\uff0c\u5bf9\u5468\u747e\u73ba\u5c55\u5f00\u5b66\u672f\u4e0a\u7684\u653b\u51fb\uff0c\u80fd\u8fdb\u884c\u52a8\u6447\u5bf9\u65b9\u7684\u9635\u811a\uff0c\u6709\u8fd9\u4e24\u6837\u4e3e\u6307\uff0c\u5c31\u8db3\u4ee5\u8ba9\u4e25\u5cfb\u7684\u5c40\u52bf\u7f13\u89e3\u4e0b\u6765\uff0c\u5c31\u4e5f\u6709\u5145\u8db3\u7684\u65f6\u95f4\u53bb\u83b7\u53d6\u7b2c\u4e00\u624b\u7684\u6750\u6599\u3002</p><p>\u3000\u3000\u9526\u6e56\u65d7\u4e0b\u65e9\u5c31\u6210\u7acb\u4e86\u5546\u4e1a\u8c03\u67e5\u4e0e\u5e02\u573a\u7814\u7a76\u673a\u6784\uff0c\u867d\u7136\u5de5\u4f5c\u7684\u91cd\u5fc3\u4e0d\u5728\u56fd\u5185\uff0c\u4f46\u662f\u4e34\u65f6\u62bd\u8c03\u4e00\u7ec4\u4eba\u53bb\u7ae0\u5dde\u8c03\u67e5\u7a00\u571f\u77ff\u8d44\u6e90\u76d7\u91c7\u60c5\u51b5\u8fd8\u662f\u7ef0\u7ef0\u6709\u4f59\u7684\u3002\u53e6\u5916\uff0c\u5f20\u606a\u8fd8\u8ba9\u5085\u4fca\u8054\u7cfb\u5efa\u90ba\u6668\u62a5\u7684\u7b80\u5fd7\u5eb7\uff0c\u5e0c\u671b\u5efa\u90ba\u6668\u62a5\u4f5c\u4e3a\u4f20\u7edf\u7684\u65b0\u95fb\u5a92\u4f53\u80fd\u4ecb\u5165\u6b64\u4e8b\uff1b\u53c8\u8054\u7edc\u6606\u817e\u5728\u7ebf\u7684\u9a6c\u5411\u4e1c\uff0c\u8ba9\u9a6c\u5411\u4e1c\u6d3e\u51fa\u6606\u817e\u65d7\u4e0b\u65b0\u95fb\u90e8\u95e8\u7684\u8bb0\u8005\u5206\u4e09\u7ebf\u524d\u5f80\u7ae0\u5dde\u8c03\u67e5\u7ae0\u5dde\u7a00\u571f\u77ff\u8d44\u6e90\u76d7\u91c7\u53ca\u73af\u5883\u6c61\u67d3\u60c5\u51b5\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u7ae0\u5dde\u5b98\u5546\u52fe\u7ed3\u7684\u4e25\u91cd\u7a0b\u5ea6\uff0c\u4ee5\u53ca\u65b0\u95fb\u673a\u6784\u4ecb\u5165\u8c03\u67e5\u4e00\u65e6\u6cc4\u9732\u6d88\u606f\u53ef\u80fd\u4f1a\u5f15\u8d77\u7ae0\u5dde\u76d7\u77ff\u79c1\u5546\u7684\u75af\u72c2\u53cd\u6251\uff0c\u5f20\u606a\u8fd8\u5f97\u610f\u8ba9\u9a6c\u6d77\u9f99\u7387\u9886\u4e8c\u4e2a\u4eba\u53bb\u7ae0\u5dde\u5c45\u4e2d\u7b56\u5e94\uff0c\u786e\u4fdd\u8c03\u67e5\u4eba\u5458\u53ca\u8bb0\u8005\u7684\u4eba\u8eab\u5b89\u5168\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u63a5\u5230\u675c\u98de\u7684\u7535\u8bdd\u8bf4\u5d14\u90c1\u66fc\u627e\u4ed6\uff0c\u5f20\u606a\u54ea\u91cc\u613f\u610f\u53bb\u89c1\u5d14\u90c1\u66fc\uff1f\u518d\u8bf4\u9662\u91cc\u7684\u4e8b\u60c5\u5df2\u7ecf\u4e0d\u9700\u8981\u4ed6\u518d\u62c5\u5fc3\uff0c\u5c31\u8ba9\u675c\u98de\u9a97\u5d14\u90c1\u66fc\u8bf4\u627e\u4e0d\u5230\u81ea\u5df1\u5e94\u4ed8\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u665a\u6674\u7aef\u7740\u4e00\u676f\u5496\u5561\uff0c\u8737\u817f\u5750\u5230\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u7a7f\u7740\u8d34\u8eab\u7684\u68c9\u8d28\u8863\u88e4\uff0c\u81c0\u817f\u7ef7\u5f97\u6d51\u5706\uff0c\u5f39\u529b\u5341\u8db3\uff0c\u542c\u5f20\u606a\u8ddf\u675c\u98de\u5728\u7535\u8bdd\u91cc\u80e1\u626f\uff0c\u7b49\u7740\u4ed6\u6302\u7740\u4e86\u7535\u8bdd\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4f60\u73b0\u5728\u662f\u8d8a\u6765\u8d8a\u4e0d\u5b66\u597d\u4e86\uff0c\u4f60\u4eec\u5e74\u7ea7\u4e3b\u4efb\u627e\u4f60\uff0c\u4f60\u90fd\u80e1\u4e71\u627e\u501f\u53e3\u63a8\u8131\u6389\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4ec0\u4e48\u65f6\u5019\u6807\u699c\u81ea\u5df1\u662f\u597d\u5b66\u751f\u4e86\uff1f\u201d\u5f20\u606a\u987a\u52bf\u5012\u5728\u665a\u6674\u7684\u6000\u91cc\uff0c\u6795\u7740\u5979\u67d4\u8f6f\u7684\u5c0f\u8179\uff0c\u8c03\u6574\u4e86\u4e2a\u59ff\u6001\uff0c\u8ba9\u81ea\u5df1\u8eba\u5f97\u66f4\u8212\u670d\u4e00\u4e9b\uff0c\u8ddf\u665a\u6674\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u5fc3\u7406\u4f1a\u89c9\u5f97\u53ef\u4ee5\u7a0d\u5fae\u7684\u653e\u7eb5\u81ea\u5df1\uff0c\u8981\u8bf4\u5fc3\u7406\u4e0a\u7684\u5e74\u9f84\uff0c\u5012\u662f\u665a\u6674\u8ddf\u81ea\u5df1\u6700\u63a5\u8fd1\uff0c\u4e5f\u6240\u4ee5\u4f1a\u89c9\u5f97\u665a\u6674\u4f1a\u6bd4\u8bb8\u601d\u3001\u5510\u5a67\u5979\u4eec\u66f4\u4f1a\u653e\u7eb5\u81ea\u5df1\u3002</p></article>"}}}]);
