(window.webpackJsonp=window.webpackJsonp||[]).push([[1007],{1030:function(p){p.exports={chapterTitle:null,title:"\u7b2c1004\u7ae0 \u5e94\u5bf9\u4e4b\u7b56",content:"<article><h2>\u7b2c1004\u7ae0 \u5e94\u5bf9\u4e4b\u7b56</h2><p>\u3000\u3000\u5f20\u606a\u77e5\u9053\u4fe1\u606f\u4ea7\u4e1a\u90e8\u8fd9\u6b21\u8981\u653e\u5f00\u53e3\u5b50\uff0c\u662f\u4ece\u4fe1\u606f\u4ea7\u4e1a\u90e8\u526f\u90e8\u957f\u6613\u4e91\u98de\u90a3\u91cc\u5f97\u6765\u7684\u6d88\u606f\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u5c06\u63e3\u6469\u5230\u7684\u4e00\u4e9b\u5e55\u540e\u66f2\u6298\u5728\u7535\u8bdd\u5206\u6790\u7ed9\u5f20\u606a\u542c\uff0c\u4ed6\u6bd5\u7adf\u4e0d\u662f\u5206\u7ba1\u8fd9\u4e00\u5757\u7684\u526f\u90e8\u957f\uff1b\u5374\u662f\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4e0e\u8096\u745e\u6c11\u89c1\u9762\u540e\uff0c\u624d\u4ece\u8096\u745e\u6c11\u53e3\u4e2d\u8bc1\u5b9e\u5f20\u606a\u4e4b\u524d\u7684\u731c\u6d4b\u3002</p><p>\u3000\u3000\u6e56\u7554\u6728\u5c4b\u9876\u5c42\u7684\u9601\u697c\u662f\u56db\u5206\u4e4b\u4e00\u5f27\u5ea6\u4e0e\u659c\u7acb\u7a97\u7ec4\u7ec7\u6210\u7684\u5f00\u9614\u7a7a\u95f4\uff0c\u7ea2\u9ed1\u683c\u5b50\u7684\u4e66\u67b6\u94fa\u6ee1\u4e24\u4fa7\u7684\u4fa7\u5899\uff0c\u4ece\u6276\u68af\u4e0a\u6765\uff0c\u5730\u677f\u4e0a\u9664\u4e86\u4f9d\u5f27\u5899\u800c\u653e\u7684\u4e00\u5f20\u6c99\u53d1\u4e4b\u5916\uff0c\u518d\u6ca1\u6709\u5176\u4ed6\u5bb6\u4ff1\uff0c\u5730\u677f\u4e0a\u94fa\u7740\u6d0b\u7070\u8272\u7684\u7ed2\u6bef\uff0c\u4e09\u53ea\u6676\u83b9\u5254\u900f\u7684\u9ad8\u811a\u73bb\u7483\u676f\u4e0e\u4e00\u74f6\u6253\u5f00\u7ea2\u74f6\u653e\u5728\u5730\u677f\u4e0a\uff0c\u8bb8\u601d\u4e0e\u9648\u9759\u5750\u5728\u7ed2\u6bef\u4e0a\uff0c\u5f20\u606a\u7ad9\u5728\u7a97\u524d\u63a5\u9648\u4fe1\u751f\u7684\u7535\u8bdd\u3002</p><p>\u3000\u3000\u201c\u55ef\uff0c\u9996\u6279\u724c\u7167\u53d1\u653e\u65f6\uff0c\u5f53\u65f6\u7684\u90ae\u7535\u90e8\u8bbe\u4e0b\u4e09\u9053\u95e8\u69db\uff0c\u6ce8\u518c\u8d44\u91d1\u8981\u8fbe\u52302\u4ebf\u4ee5\u4e0a\uff0c\u8fde\u7eed\u7ecf\u8425\u4e24\u5e74\u4ee5\u4e0a\uff0c\u6709\u7814\u53d1\u57fa\u7840\u4ee5\u73af\u4fdd\u3001\u8d28\u91cf\u7b49\u76f8\u5173\u8ba4\u8bc1\u2026\u2026\u8fd9\u4ef6\u4e8b\u60c5\uff0c\u6211\u4eec\u4e0d\u8981\u5c1d\u8bd5\u53bb\u963b\u6b62\uff0c\u4f46\u662f\u6211\u4eec\u4e5f\u4e0d\u80fd\u5750\u89c6\u51c6\u5165\u95e8\u69db\u7ed9\u964d\u4f4e\uff1b\u8fd8\u6709\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\uff0c\u4e5f\u5e94\u5728\u7814\u53d1\u57fa\u7840\u7b49\u6761\u4ef6\u7684\u5ba1\u6838\u4e0a\u53d1\u6325\u4f5c\u7528\u3002\u201d\u5f20\u606a\u5728\u7535\u8bdd\u91cc\u4e0d\u4f5c\u63a9\u9970\u7684\u8981\u6c42\u9648\u4fe1\u751f\u5229\u7528\u9526\u6e56\u7684\u52bf\u529b\u53bb\u5bf9\u4fe1\u4ea7\u90e8\u8fd9\u6b21\u5927\u89c4\u6a21\u53d1\u653e\u724c\u7167\u7684\u4e3e\u52a8\u65bd\u52a0\u5f71\u54cd\uff0c\u8ba9\u6b63\u89c4\u7684\u3001\u6709\u4e00\u5b9a\u5b9e\u529b\u7684\u7535\u5b50\u5382\u5546\u8fdb\u6765\uff0c\u603b\u6bd4\u8ba9\u90a3\u4e9b\u6295\u673a\u8005\u8fdb\u6765\u8981\u5f3a\uff0c\u53c8\u5728\u6700\u540e\u8ddf\u5f3a\u8c03\u4e86\u4e00\u53e5\uff0c\u201c\u81f3\u5c11\u803f\u91cd\u9633\u4ed6\u4e5f\u4e0d\u4f1a\u5e0c\u671b\u56fd\u5185\u7684\u624b\u673a\u4ea7\u4e1a\u7ed9\u6295\u673a\u8005\u641e\u7838\u3001\u641e\u70c2\u3002\u201d</p><p>\u3000\u3000\u9648\u9759\u4e0e\u8bb8\u601d\u5750\u5728\u4e00\u65c1\u9759\u542c\u7740\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u901a\u7535\u8bdd\uff0c\u61d2\u6d0b\u6d0b\u7684\u5750\u5728\u90a3\u91cc\uff0c\u4e5f\u6ca1\u6709\u8981\u56de\u907f\u7684\u610f\u601d\uff0c\u4f3c\u4e4e\u7406\u6240\u5f53\u7136\u7684\u770b\u7740\u5f20\u606a\u5728\u90a3\u91cc\u53d1\u53f7\u53f8\u4ee4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u845b\u5efa\u5fb7\u5728\u534e\u590f\u7535\u5b50\u5185\u90e8\u5938\u4e0b\u6d77\u53e3\uff0c\u8981\u5728\u660e\u5e74\uff0c\u9500\u552e\u989d\u5728\u4e8c\u767e\u4ebf\u7684\u57fa\u7840\u4e0a\u518d\u7ffb\u4e00\u756a\uff0c\u8fbe\u5230\u56db\u767e\u4ebf\uff0c\u96f6\u4e8c\u5e74\uff0c\u8981\u8fbe\u5230\u4e03\u767e\u4ebf\u2026\u2026\u201d\u5f20\u606a\u6302\u4e86\u7535\u8bdd\uff0c\u8ddf\u9648\u9759\u4ecb\u7ecd\u4e00\u4e9b\u4ed6\u521a\u4e86\u89e3\u5230\u7684\u6d88\u606f\uff0c\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u91cc\u8bf4\u7684\u8bdd\uff0c\u9648\u9759\u5728\u4e00\u65c1\u8fd8\u662f\u542c\u4e0d\u5230\u7684\uff0c\u201c\u534e\u590f\u7535\u5b50\u8981\u6210\u4e3a\u6700\u65e9\u4e00\u6279\u8fdb\u5165\u4e16\u754c500\u5f3a\u7684\u4f01\u4e1a\u3002\u201d</p><p>\u3000\u3000\u201c\u4e16\u754c500\u5f3a\u554a\uff0c\u201d\u9648\u9759\u6175\u61d2\u7684\u4f38\u4e86\u61d2\u8170\uff0c\u53c8\u89c9\u5f97\u5728\u5f20\u606a\u3001\u8bb8\u601d\u9762\u524d\u8fd9\u6837\u592a\u968f\u610f\u4e86\u4e00\u4e9b\uff0c\u655b\u59ff\u7b11\u7740\u8bf4\uff0c\u201c\u8981\u662f\u5230\u96f6\u4e8c\u5e74\u7684\u5165\u699c\u6807\u51c6\u8fd8\u505c\u6ede\u5728\u4e5d\u516b\u5e74\u7684\u6c34\u5e73\u4e0a\uff0c\u534e\u590f\u7535\u5b50\u53c8\u80fd\u505a\u5230700\u4ebf\u7684\u9500\u552e\u989d\uff0c\u52c9\u5f3a\u591f\u8d44\u683c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u56fd\u5185\u4e00\u4e9b\u4eba\u7684\u5fc3\u6001\u592a\u6025\u8e81\u4e86\u4e9b\uff0c\u201d\u5f20\u606a\u5c06\u624b\u673a\u968f\u610f\u7684\u4e22\u5730\u677f\uff0c\u6328\u7740\u8bb8\u601d\u800c\u5750\uff0c\u8bf4\u9053\uff0c\u201c\u56fd\u5185\u8fd9\u4e48\u5927\u7684\u5e02\u573a\uff0c\u7535\u4fe1\u3001\u79fb\u52a8\u901a\u8baf\u3001\u77f3\u6cb9\u7b49\u4ea7\u4e1a\u53c8\u7ed9\u51e0\u5bb6\u4f01\u4e1a\u5784\u65ad\u7ecf\u8425\uff0c\u8fdf\u65e9\u4f1a\u6324\u8fdb500\u5f3a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8981\u8bf4500\u5f3a\u7684\uff0c\u9526\u6e56\u52aa\u529b\u4e00\u4e0b\uff0c\u5728\u4e24\u5e74\u4e4b\u524d\uff0c\u5e94\u8be5\u80fd\u8fbe\u5230\u8fd9\u4e2a\u76ee\u6807\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u4ec0\u4e48\u610f\u601d\u2026\u2026\u9526\u6e56\u4e0d\u4e89\u8fd9\u4e2a\u3002\u201d\u5f20\u606a\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u5408\u5e76\u7231\u8fbe\u7535\u5b50\u4ee5\u53ca\u5143\u5668\u4ef6\u90e8\u95e8\u7684\u6570\u636e\uff0c\u7231\u8fbe\u96c6\u56e2\u4e5d\u516b\u5e74\u7684\u5b9e\u9645\u9500\u552e\u989d\u4e0e\u534e\u590f\u7535\u5b50\u76f8\u5f53\uff0c\u6709200\u4ebf\uff0c\u5f71\u789f\u673a\u7684\u5e02\u573a\u867d\u7136\u5f88\u96be\u6709\u5feb\u901f\u7684\u589e\u6da8\uff0c\u4f46\u662f\u624b\u673a\u4e1a\u52a1\u4ee5\u53caiplayer\u53ca\u95ea\u5b58\u76d8\u7684\u4e1a\u52a1\u90fd\u6709\u5de8\u5927\u7684\u53d1\u5c55\u7a7a\u95f4\uff0c\u4e5d\u4e5d\u5e74\u7231\u8fbe\u96c6\u56e2\u7684\u9500\u552e\u989d\u76ee\u6807\u5b9a\u5728340\u4ebf\u5230360\u4ebf\u4e4b\u95f4\uff0c\u65b0\u5149\u7eb8\u4e1a\uff0c\u5728\u62e5\u6709\u6d77\u5dde\u4e0e\u91d1\u5c71\u4e24\u5927\u751f\u4ea7\u57fa\u5730\u4e4b\u540e\uff0c\u4e5d\u4e5d\u5e74\u7684\u9500\u552e\u76ee\u6807\u662f60\u4ebf\uff0c\u76db\u946b\u7535\u5668\u4e5d\u4e5d\u5e74\u9500\u552e\u89c4\u6a21\u4e5f\u8981\u8d85\u51fa\u4e00\u767e\u4ebf\u8bb8\u591a\uff0c\u518d\u5408\u5e76\u9526\u6e56\u5546\u4e8b\u3001\u4e16\u7eaa\u9526\u6e56\u3001\u4e2d\u6676\u5fae\u82af\u7b49\u4f01\u4e1a\u7684\u6570\u636e\uff0c\u4e5d\u4e5d\u5e74\u5c31\u8fbe\u5230\u52c9\u5f3a\u8fdb\u5165\u4e16\u754c500\u5f3a\u7684\u6807\u51c6\uff0c\u4f46\u662f\u8fd9\u4e48\u505a\u6beb\u65e0\u610f\u601d\uff0c\u9664\u4e86\u8868\u9762\u7684\u98ce\u5149\u4e4b\u5916\uff0c\u53ea\u662f\u63d0\u9192\u56fd\u5185\u67d0\u4e9b\u4eba\u5bf9\u9526\u6e56\u4ea7\u751f\u8b66\u60d5\u7f62\u4e86\u3002\u8981\u8fdb500\u5f3a\uff0c\u4e5f\u8981\u7b49\u5230\u65d7\u4e0b\u67d0\u5bb6\u4f01\u4e1a\u6709\u72ec\u7acb\u8fdb\u5165\u6392\u540d\u8d44\u683c\u7684\u65f6\u5019\uff0c\u90a3\u65f6\u624d\u7b97\u771f\u6b63\u7684\u7ad9\u7a33\u811a\u8ddf\uff0c\u800c\u4e14\u9526\u6e56\u4f53\u7cfb\u7684\u6846\u67b6\u4e5f\u4e0d\u7528\u644a\u5230\u5a92\u4f53\u4e0e\u516c\u4f17\u89c6\u91ce\u4e4b\u4e0b\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8ddf\u9648\u9759\u8bf4\u9053\uff1a\u201c\u82cf\u9648\u4e8c\u4eba\uff0c\u5012\u662f\u6709\u4e9b\u60f3\u6cd5\u4e0e\u6211\u4e0d\u8c0b\u800c\u5408\uff0c\u5c31\u662f\u5e0c\u671b\u770b\u5230\u79d1\u738b\u9ad8\u79d1\u80fd\u5927\u653e\u5149\u5f69\uff0c\u5f53\u7136\u4e86\uff0c\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u91cc\u8ddf\u6211\u8bf4\u5f97\u5f88\u8d64\u88f8\u88f8\uff0c\u8981\u662f\u79d1\u738b\u9ad8\u79d1\u80fd\u4f9d\u8d56\u6211\u4eec\u7684\u6280\u672f\uff0c\u6211\u4eec\u53ef\u4ee5\u6700\u5927\u9650\u5ea6\u7684\u63d0\u4f9b\u6280\u672f\u652f\u6301\u2026\u2026\u8fd9\u4e9b\u4e8b\uff0c\u6211\u8ba9\u9648\u4fe1\u751f\u7a0d\u540e\u627e\u4f60\u8054\u7cfb\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u8bdd\u97f3\u8fd8\u6ca1\u6709\u843d\u4e0b\uff0c\u9648\u9759\u624b\u8fb9\u7684\u624b\u673a\u5c31\u9707\u54cd\u8d77\u6765\uff0c\u9648\u9759\u62ff\u8d77\u6765\u770b\u4e86\u770b\uff0c\u8bf4\u9053\uff1a\u201c\u8bf4\u66f9\u64cd\uff0c\u66f9\u64cd\u5c31\u5230\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u8f7b\u7b11\u7740\u633d\u7740\u5f20\u606a\u7684\u80f3\u818a\uff0c\u8111\u888b\u6795\u5728\u4ed6\u7684\u80a9\u8180\u4e0a\uff0c\u5ae3\u7ea2\u5982\u8102\u7684\u5634\u5507\u9644\u5230\u5f20\u606a\u7684\u8033\u7554\u8f7b\u8bed\uff1a\u201c\u4ed6\u4eec\u5012\u662f\u4e0d\u8bc6\u8da3\uff0c\u4f60\u5c31\u6ca1\u6709\u6697\u793a\u4e00\u4e0b\uff0c\u9648\u9759\u8ddf\u4f60\u5728\u4e00\u8d77\uff1f\u201d</p><p>\u3000\u3000\u80fd\u89e3\u91ca\u6e05\u695a\u5417\uff1f\u5f20\u606a\u5f88\u6000\u7591\uff0c\u53ea\u662f\u542c\u7740\u9648\u9759\u5750\u5728\u90a3\u91cc\u88c5\u4f5c\u7b2c\u4e00\u6b21\u542c\u5230\u6b64\u4e8b\u7684\u8ddf\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u8c08\u6280\u672f\u5408\u4f5c\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e00\u4e0b\u5b50\u653e\u5f00\u53e3\u5b50\uff0c\u9488\u5bf9\u9526\u6e56\u7684\u610f\u56fe\u660e\u663e\uff0c\u4f46\u662f\u4eba\u5728\u5c4b\u6a90\u4e0b\uff0c\u4e0d\u5f97\u4e0d\u4f4e\u5934\uff0c\u4e0e\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5bf9\u6297\uff0c\u7ec8\u7a76\u5bf9\u9526\u6e56\u4e0d\u5229\uff0c\u6bd5\u7adf\u4fe1\u606f\u4ea7\u4e1a\u90e8\u662f\u884c\u4e1a\u4e3b\u7ba1\u90e8\u95e8\uff0c\u9526\u6e56\u60f3\u8981\u5728\u4fe1\u606f\u7535\u5b50\u4ea7\u4e1a\u4e0a\u53d1\u5c55\uff0c\u51e0\u4e4e\u6bcf\u4e00\u4e2a\u91cd\u5927\u52a8\u4f5c\uff0c\u90fd\u79bb\u5f00\u4e0d\u4fe1\u606f\u4ea7\u4e1a\u90e8\u7684\u201c\u5173\u7167\u201d\u3002\u5728\u7b2c\u4e8c\u6279\u83b7\u5f97\u624b\u673a\u724c\u7167\u7684\u5382\u5bb6\u4e4b\u4e2d\uff0c\u79d1\u738b\u9ad8\u79d1\u82e5\u80fd\u5927\u653e\u5f02\u5f69\uff0c\u4e00\u65b9\u9762\u80fd\u6269\u5f20\u9526\u6e56\u6280\u672f\u7684\u5e02\u573a\u5360\u6709\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4e5f\u7b97\u662f\u5bf9\u4fe1\u4ea7\u90e8\u72e0\u72e0\u6247\u4e86\u4e00\u5df4\u638c\u3002</p><p>\u3000\u3000\u8fd9\u4e48\u60f3\u6765\uff0c\u5f20\u606a\u5012\u662f\u5f88\u671f\u5f85\u8c22\u5b50\u5609\u5728\u5546\u4e1a\u4e0a\u7684\u8868\u73b0\uff1a\u5b9d\u77f3\u624b\u673a\u6982\u5ff5\u7684\u786e\u662f\u4e2a\u5f88\u60ca\u8273\u7684\u7b56\u5212\uff0c\u4e0d\u8fc7\u4e5f\u8981\u770b\u79d1\u738b\u9ad8\u79d1\u7684\u6267\u884c\u80fd\u529b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7b49\u9648\u9759\u4e0e\u9648\u4fe1\u751f\u901a\u8fc7\u7535\u8bdd\uff0c\u5f20\u606a\u4e5f\u4e0d\u987e\u65f6\u95f4\u6709\u591a\u665a\uff0c\u7ed9\u90b5\u5fc3\u6587\u6253\u4e86\u7535\u8bdd\uff0c\u8be2\u95ee\u4e86\u201c\u767e\u57ce\u767e\u5e97\u201d\u8ba1\u5212\u7684\u6267\u884c\u60c5\u51b5\u3002</p><p>\u3000\u3000\u90b5\u5fc3\u6587\u5728\u7535\u8bdd\u91cc\u8ddf\u5f20\u606a\u82e6\u7b11\uff0c\u63a5\u7740\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7684\u58f0\u97f3\u4ece\u7535\u8bdd\u91cc\u4f20\u6765\uff0c\u90b5\u5fc3\u6587\u5df2\u7ecf\u5148\u7ed9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u62a2\u5148\u4e00\u6b65\u627e\u4e0a\u95e8\u53bb\u4e86\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u5728\u7535\u8bdd\u91cc\u62b1\u6028\uff0c\u5979\u6bcf\u6708\u96be\u5f97\u62bd\u65f6\u95f4\u56de\u4e00\u8d9f\u5efa\u90ba\uff0c\u592b\u59bb\u751f\u6d3b\u5c31\u8fd9\u6837\u7ed9\u786c\u751f\u751f\u7684\u7834\u574f\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u624d\u4e0d\u4fe1\u5979\u7684\u8bdd\uff0c\u60e0\u5c71\u4e0e\u5efa\u90ba\u4e4b\u95f4\u8d70\u9ad8\u901f\u624d\u4e00\u4e2a\u5c0f\u65f6\u591a\u70b9\u65f6\u95f4\uff0c\u5c0f\u4e24\u53e3\u604b\u5978\u60c5\u70ed\uff0c\u54ea\u91cc\u80fd\u71ac\u5f97\u4f4f\u4e00\u4e2a\u6708\u89c1\u6b21\u9762\uff1f\u4e0d\u8fc7\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u5df2\u7ecf\u627e\u4e0a\u90b5\u5fc3\u6587\uff0c\u4ed6\u4fbf\u4e0d\u7528\u5173\u5fc3\u8fd9\u4e8b\uff0c\u7b49\u4ed6\u4eec\u62df\u51fa\u5177\u4f53\u7684\u65b9\u6848\u5c31\u884c\u3002</p><p>\u3000\u3000\u4e3a\u624b\u673a\u4e13\u8425\u800c\u4e13\u95e8\u6ce8\u518c\u4e86\u76db\u946b\u7535\u8baf\uff0c\u9664\u4e86\u5728\u76db\u946b\u7535\u5668\u5356\u573a\u5185\u6269\u5927\u624b\u673a\u4e13\u8425\u67dc\u53f0\u7684\u89c4\u6a21\uff0c\u8fd8\u8981\u5176\u4ed6\u76db\u946b\u7535\u5668\u5bb6\u7535\u5356\u573a\u8fd8\u6ca1\u6709\u8986\u76d6\u5230\u7684\u57ce\u5e02\u5927\u91cf\u7684\u5f00\u8bbe\u624b\u673a\u8fde\u9501\u4e13\u8425\u95e8\u5e97\uff0c\u4e3a\u6b64\uff0c\u76db\u946b\u81ea\u7b79\u4e09\u4ebf\u8d44\u91d1\u3001\u7231\u8fbe\u96c6\u56e2\u989d\u8d44\u518d\u63d0\u4f9b\u4e94\u4ebf\u8d44\u91d1\uff0c\u5171\u52a8\u7528\u516b\u4ebf\u5de8\u8d44\u542f\u52a8\u201c\u767e\u57ce\u767e\u5e97\u201d\u8ba1\u5212\uff0c\u5c06\u624b\u673a\u9500\u552e\u6e20\u9053\u7ec8\u7aef\u5ef6\u4f38\u5230\u56fd\u5185\u4e3b\u8981\u7684\u5730\u5e02\u7ea7\u57ce\u5e02\u548c\u4e00\u4e9b\u91cd\u8981\u7684\u53bf\u7ea7\u57ce\u5e02\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u8f83\u5bb6\u7535\u5356\u573a\u9700\u8981\u5360\u7528\u7684\u573a\u5730\u5e7f\uff0c\u624b\u673a\u8fde\u9501\u4e13\u8425\u95e8\u5e97\u89c4\u6a21\u5219\u8981\u8896\u73cd\u5f97\u591a\uff0c\u4f9d\u6258\u76db\u946b\u7535\u5668\u5df2\u6709\u7684\u7269\u6d41\u4f53\u7cfb\uff0c\u5927\u7ea6\u53ea\u9700\u8981\u4e8c\u767e\u4e07\u5c31\u80fd\u5f00\u6210\u4e00\u5bb6\u95e8\u5e97\uff0c\u4e0d\u50cf\u5bb6\u7535\u5356\u573a\u52a8\u8f84\u9700\u8981\u4e0a\u5343\u4e07\u7684\u8d44\u91d1\u3002\u8fd9\u4e9b\u4e5f\u51b3\u5b9a\u4e86\u53ea\u6709\u8981\u8db3\u591f\u7684\u8d44\u91d1\uff0c\u518d\u52a0\u4e0a\u4e2d\u5fc3\u57ce\u5e02\u7684\u65d7\u8230\u5e97\u7f51\u7edc\u652f\u6491\u4e0e\u521d\u6b65\u5b8c\u5907\u7684\u7269\u6d41\u3001\u552e\u670d\u7b49\u4f53\u7cfb\uff0c\u624b\u673a\u8fde\u9501\u4e13\u8425\u4e1a\u52a1\u66f4\u5bb9\u6613\u8fc5\u901f\u7684\u5728\u534e\u5357\u3001\u534e\u4e1c\u3001\u534e\u4e2d\u3001\u534e\u5317\u4ee5\u53ca\u4e1c\u5317\u5730\u533a\u5f00\u5c55\u8d77\u6765\u3002</p><p>\u3000\u3000\u771f\u6b63\u8282\u5236\u76db\u946b\u7535\u5668\u6269\u5927\u89c4\u6a21\u7684\u56e0\u7d20\u8fd8\u662f\u5728\u4e8e\u4eba\u529b\u8d44\u6e90\u7684\u532e\u4e4f\u4e86\uff0c\u4e0d\u8fc7\u603b\u662f\u8981\u6bd4\u7231\u8fbe\u96c6\u56e2\u8f7b\u677e\u4e00\u4e9b\u3002\u4e3a\u4e86\u6ee1\u8db3\u6a61\u6811\u56ed\u5bf9\u7814\u53d1\u4eba\u5458\u7684\u9700\u6c42\uff0c\u9526\u6e56\u5f80\u4e1c\u6d77\u5927\u5b66\u7b49\u9ad8\u6821\u90fd\u5927\u628a\u7684\u6d12\u94b1\uff0c\u76db\u946b\u7535\u5668\u53ea\u662f\u6295\u5165\u4e00\u7b14\u8d44\u91d1\u5728\u65b0\u829c\u8bbe\u7acb\u4e13\u95e8\u7684\u57f9\u8bad\u5b66\u6821\u76db\u946b\u5b66\u9662\u3002\u62db\u8058\u5230\u5458\u5de5\uff0c\u5148\u4e22\u5230\u57f9\u8bad\u5b66\u6821\u91cc\u6309\u7167\u6761\u4ef6\u7684\u4e0d\u540c\u5f3a\u5316\u57f9\u517b\u4e00\u4e2a\u6708\u5230\u534a\u5e74\u7684\u65f6\u95f4\uff0c\u4ee5\u6b64\u7f13\u89e3\u5bf9\u4eba\u529b\u8d44\u6e90\u7684\u9700\u6c42\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u4f53\u7cfb\u9690\u853d\u7684\u4ea4\u53c9\u6301\u80a1\u5173\u7cfb\uff0c\u81f3\u6b64\uff0c\u76db\u946b\u5df2\u7ecf\u5b8c\u6210\u5411\u9526\u6e56\u4f53\u7cfb\u878d\u5408\u7684\u8fc7\u7a0b\uff0c\u4f46\u662f\u5728\u5916\u754c\u770b\u6765\uff0c\u9526\u6e56\u4e0e\u7231\u8fbe\u96c6\u56e2\u4ecd\u7136\u6218\u7565\u5408\u4f5c\u5173\u7cfb\u7f62\u4e86\u3002</p><p>\u3000\u3000\u201c\u767e\u57ce\u767e\u5e97\u201d\u8ba1\u5212\u80fd\u987a\u5229\u7684\u5b9e\u65bd\uff0c\u5c06\u662f\u7b2c\u4e00\u5bb6\u8986\u76d6\u56fd\u5185\u5e02\u573a\u7684\u624b\u673a\u9500\u552e\u6e20\u9053\u7ec8\u7aef\u7f51\u7edc\uff0c\u81f3\u5c11\u5728\u4fe1\u4ea7\u90e8\u7a81\u5982\u5176\u6765\u7684\u53d8\u6545\u524d\u633d\u56de\u4e9b\u52a3\u52bf\u2014\u2014\u4e5f\u53ef\u4ee5\u901a\u8fc7\u8fd9\u4e2a\u7ec8\u7aef\u7f51\u7edc\u589e\u5f3a\u5728\u624b\u673a\u4ea7\u4e1a\u91cc\u7684\u8bdd\u8bed\u6743\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u5728\u5546\u8a00\u5546\uff0c\u5bf9\u4e30\u539a\u5229\u6da6\u7684\u671f\u5f85\uff0c\u8fd9\u624d\u662f\u542f\u52a8\u201c\u767e\u57ce\u767e\u5e97\u201d\u8ba1\u5212\u7684\u6839\u672c\u610f\u56fe\u3002\u56fd\u5185\u5e02\u573a\u5bf9\u624b\u673a\u5236\u9020\u4f01\u4e1a\u6765\u8bf4\u8fce\u6765\u4e86\u9ec4\u91d1\u65f6\u4ee3\uff0c\u5bf9\u9500\u552e\u4f01\u4e1a\u6765\u8bf4\u4e5f\u662f\u8fce\u6765\u9ec4\u91d1\u65f6\u4ee3\u3002</p><p>\u3000\u3000\u76db\u946b\u7535\u5668\u7684\u624b\u673a\u9500\u552e\u4e1a\u52a1\uff0c\u6bcf\u53ea\u624b\u673a\u9500\u552e\u6bdb\u5229\u6da6\u90fd\u5728\u4e94\u767e\u5143\u4ee5\u4e0a\uff0c\u90fd\u8981\u9ad8\u8fc7\u7a7a\u8c03\u3001\u5f69\u7535\u7b49\u5927\u4ef6\u5bb6\u7535\u4ea7\u54c1\u4e00\u622a\uff0c\u8fd9\u8fd8\u662f\u76db\u946b\u6bd4\u8f83\u81ea\u5f8b\u7684\u60c5\u51b5\uff0c\u5176\u4ed6\u9500\u552e\u5546\u751a\u81f3\u5728\u4e00\u53ea\u624b\u673a\u4e0a\u8981\u6478\u4e0a\u5343\u5143\u7684\u5dee\u4ef7\u3002</p><p>\u3000\u3000\u773c\u524d\u56fd\u5185\u7684\u624b\u673a\u9500\u552e\u4f01\u4e1a\u90fd\u4e0d\u6210\u4ec0\u4e48\u89c4\u6a21\uff0c\u66f4\u6ca1\u6709\u5927\u8d44\u672c\u8ddf\u9526\u6e56\u7ade\u4e89\uff0c\u6709\u9526\u6e56\u5728\u80cc\u540e\u652f\u6491\u7684\u76db\u946b\u7535\u8baf\u81f3\u5c11\u80fd\u6293\u4f4f\u4eca\u540e\u4e09\u4e94\u5e74\u7684\u66b4\u5229\u671f\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u7a81\u7136\u8fce\u6765\u5982\u6b64\u5de8\u5927\u7684\u53d1\u5c55\u826f\u673a\uff0c\u9648\u9759\u81ea\u7136\u6ca1\u6709\u5fc3\u601d\u7761\u89c9\uff0c\u4f46\u4e5f\u4e0d\u80fd\u6525\u7740\u5f20\u606a\u3001\u8bb8\u6c11\u966a\u5979\u8bf4\u8bdd\uff0c\u4fbf\u5148\u56de\u5ba2\u623f\u4f11\u606f\u3002</p><p>\u3000\u3000\u201c\u4eca\u665a\uff0c\u4f60\u8001\u5b9e\u4e00\u70b9\u2026\u2026\u201d\u5f85\u9648\u9759\u4e0b\u697c\u53bb\uff0c\u89c1\u5f20\u606a\u4f38\u7740\u61d2\u8170\u6446\u51fa\u4e00\u5e45\u65e0\u8d56\u76f8\uff0c\u8bb8\u601d\u5634\u91cc\u867d\u662f\u5728\u201c\u8b66\u544a\u201d\u5f20\u606a\uff0c\u8138\u4e0a\u5374\u6d6e\u8d77\u4e00\u5c42\u7f9e\u4e91\uff0c\u4f3c\u4e4e\u4e5f\u6e05\u695a\u5979\u7684\u201c\u8b66\u544a\u201d\u5bf9\u5f20\u606a\u7684\u4f5c\u7528\u5b9e\u5728\u6709\u9650\u3002\u53ea\u662f\u8fd9\u6e56\u7554\u6728\u5c4b\u91c7\u7528\u5927\u91cf\u7684\u6728\u6750\u5efa\u9020\uff0c\u9648\u9759\u4f11\u606f\u7684\u5ba2\u623f\u4e0e\u4e3b\u5367\u5ba4\u6328\u7740\uff0c\u4f3c\u4e4e\u9694\u97f3\u6548\u679c\u4e0d\u662f\u5341\u5206\u7684\u7406\u60f3\uff0c\u8bb8\u601d\u5fc3\u91cc\u81ea\u7136\u4e0d\u60f3\u6709\u4ec0\u4e48\u52a8\u9759\u7ed9\u9648\u9759\u542c\u53bb\u3002</p></article>"}}}]);
