(window.webpackJsonp=window.webpackJsonp||[]).push([[1007],{1030:function(p){p.exports={chapterTitle:null,title:"\u7b2c815\u7ae0 \u5f62\u52bf",content:"<article><h2>\u7b2c815\u7ae0 \u5f62\u52bf</h2><p>\u3000\u3000\u5f20\u606a\u6d88\u5931\u6389\u81ea\u6709\u6d88\u5931\u7684\u501f\u53e3\uff0c\u6216\u8bb8\u966a\u7701\u957f\u3001\u5e02\u957f\u8fd9\u4e9b\u5927\u4f6c\u8eb2\u5728\u54ea\u91cc\u5546\u91cf\u533a\u57df\u7ecf\u6d4e\u53d1\u5c55\u7684\u5927\u8ba1\uff0c\u4e5f\u6216\u8bb8\u542c\u53d6\u56fd\u5185\u5fae\u7535\u5b50\u9886\u57df\u6700\u6743\u5a01\u4e13\u5bb6\u3001\u5b66\u8005\u7684\u6280\u672f\u62a5\u544a\uff0c\u4e5f\u6216\u8bb8\u6b63\u4e0e\u6d77\u5916\u7535\u5b50\u5382\u5546\u9ad8\u5c42\u6d3d\u8c08\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u675c\u98de\u5f53\u7136\u4e0d\u4f1a\u5e2e\u5f20\u606a\u7f16\u9020\u4ec0\u4e48\u7406\u7531\uff0c\u56e0\u4e3a\u6b63\u800c\u516b\u7ecf\u7684\u501f\u53e3\u542c\u4e0a\u53bb\u4f3c\u4e4e\u592a\u4e0d\u53ef\u4fe1\u4e86\uff0c\u53ea\u662f\u7b80\u5355\u7684\u544a\u8bc9\u5d14\u90c1\u66fc\uff1a\u201c\u8054\u7cfb\u4e0d\u4e0a\uff0c\u8fd9\u5c0f\u5b50\u624b\u673a\u7ecf\u5e38\u4e0d\u5e26\u5728\u8eab\u8fb9\uff0c\u8c01\u77e5\u9053\u4ed6\u85cf\u5728\u5efa\u90ba\u57ce\u7684\u54ea\u4e2a\u89d2\u843d\u91cc\uff0c\u79bb\u5f00\u5efa\u90ba\u4e5f\u8bf4\u4e0d\u5b9a\u2026\u2026\u5d14\u4e3b\u4efb\uff0c\u4f60\u6025\u7740\u627e\u5f20\u606a\u6709\u4ec0\u4e48\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u6253\u7b97\u53bb\u7ae0\u5dde\u91c7\u8bbf\u7684\u4e8b\u60c5\u53c8\u4e0d\u80fd\u89c1\u4eba\u5c31\u8bf4\uff0c\u5d14\u90c1\u66fc\u9057\u61be\u7684\u770b\u4e86\u5979\u54e5\u4e00\u773c\uff0c\u5979\u5fc3\u91cc\u4e5f\u4e0d\u8ba4\u4e3a\u627e\u5230\u5f20\u606a\u5bf9\u5979\u54e5\u53bb\u7ae0\u5dde\u6697\u4e2d\u91c7\u8bbf\u4f1a\u6709\u4ec0\u4e48\u5e2e\u52a9\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u6253\u91cf\u7740\u521b\u57df\u516c\u53f8\u5728\u521b\u57df\u7f51\u5427\u697c\u4e0a\u79df\u7528\u7684\u529e\u516c\u5ba4\uff0c\u529e\u516c\u5ba4\u5f88\u7b80\u964b\uff0c\u5c31\u7b97\u662f\u675c\u98de\u672c\u4eba\u7684\u529e\u516c\u5ba4\u4e5f\u5f88\u7b80\u964b\u3002\u4e8b\u5b9e\u4e0a\uff0c\u675c\u98de\u8ddf\u8499\u4e50\u5171\u7528\u4e00\u95f4\u529e\u516c\u5ba4\uff0c\u5171\u7528\u4e00\u4e2a\u4e13\u804c\u79d8\u4e66\u3002\u5f88\u96be\u770b\u51fa\u8fd9\u662f\u4e00\u5bb6\u6709\u7740\u6570\u5343\u4e07\u8d44\u4ea7\u7684\u5927\u516c\u53f8\uff0c\u4e5f\u5f88\u96be\u770b\u51fa\u8fd9\u5bb6\u516c\u53f8\u662f\u7531\u4e00\u7fa4\u5728\u6821\u5927\u5b66\u751f\u4e00\u8d77\u9f13\u6363\u8d77\u6765\u7684\uff0c\u867d\u7136\u4e00\u4e9b\u5173\u952e\u7ba1\u7406\u804c\u52a1\u90fd\u662f\u5728\u6821\u7684\u5927\u5b66\u751f\uff0c\u4e0d\u8fc7\u666e\u901a\u804c\u5458\u90fd\u662f\u5411\u793e\u4f1a\u62db\u8058\u3002\u5c31\u7b97\u4ece\u793e\u4f1a\u62db\u8058\u7684\u804c\u5458\uff0c\u4e5f\u6709\u4e00\u4e2a\u9c9c\u660e\u7684\u7279\u70b9\uff0c\u90fd\u662f\u76f8\u5f53\u7684\u5e74\u8f7b\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u4e0e\u540c\u4e8b\u8a79\u6587\u660e\u5728\u5efa\u90ba\u5403\u8fc7\u4e2d\u5348\u5c31\u4e58\u4e0b\u5348\u7684\u706b\u8f66\u524d\u5f80\u91d1\u5c71\u4e86\uff0c\u6253\u7b97\u5148\u4e0e\u65b0\u534e\u793e\u6c5f\u5357\u5206\u793e\u7684\u4eba\u8054\u7cfb\uff0c\u7136\u540e\u518d\u4ece\u91d1\u5c71\u501f\u9053\u524d\u5f80\u7ae0\u5dde\u6697\u4e2d\u91c7\u8bbf\u7a00\u571f\u77ff\u8d44\u6e90\u88ab\u79c1\u4eba\u4e0e\u653f\u5e9c\u5b98\u5458\u52fe\u7ed3\u8d77\u76d7\u91c7\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u4e0b\u5348\u56de\u5230\u9662\u91cc\uff0c\u8fd9\u65f6\u5019\u9662\u91cc\u6559\u5e08\u7684\u98ce\u5411\u5df2\u7ecf\u6084\u7136\u53d1\u751f\u8f6c\u53d8\u3002</p><p>\u3000\u3000\u6628\u5929\u5728\u542c\u5230\u5468\u747e\u73ba\u5728\u6559\u5ba4\u91cc\u88ab\u5b66\u751f\u516c\u7136\u8fb1\u9a82\u7684\u4e8b\u60c5\u4e4b\u540e\uff0c\u9662\u91cc\u7684\u8001\u5e08\u4e2a\u4e2a\u4e49\u6124\u586b\u8d5d\uff0c\u6068\u4e0d\u5f97\u5c06\u95f9\u4e8b\u7684\u5b66\u751f\u63ea\u51fa\u6765\u6e38\u8857\u53bb\u3002\u5468\u747e\u73ba\u5728\u53f0\u4e0a\u7ed9\u9a82\u5f97\u5410\u8840\u6c14\u6655\u8fc7\u53bb\uff0c\u540c\u4e3a\u6559\u804c\u4eba\u5458\uff0c\u96be\u514d\u6709\u540c\u4ec7\u654c\u5ffe\u4e4b\u611f\uff0c\u5fc3\u7406\u4e0a\u5df2\u7ecf\u504f\u5411\u5468\u747e\u73ba\u8fd9\u8fb9\u4e86\u3002\u5d14\u56fd\u6052\u4e0a\u5348\u63d0\u51fa\u8981\u6452\u5f03\u5f53\u4e8b\u4eba\u7684\u8eab\u4efd\u8f83\u4e3a\u7eaf\u7cb9\u7684\u53bb\u770b\u5f85\u8fd9\u6b21\u53d1\u751f\u5728\u5b66\u751f\u4e0e\u6559\u5e08\u4e4b\u95f4\u7684\u5b66\u672f\u4e89\u8bba\uff0c\u8fd8\u8981\u9662\u91cc\u7684\u8001\u5e08\u5c31\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u7684\u95ee\u9898\u79ef\u6781\u7684\u53d1\u8868\u81ea\u5df1\u7684\u610f\u89c1\uff0c\u540c\u65f6\u8868\u793a\u7701\u91cc\u4e5f\u5f88\u91cd\u89c6\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u7684\u95ee\u9898\uff0c\u7701\u957f\u674e\u8fdc\u6e56\u8fd8\u4eb2\u81ea\u627e\u6750\u6599\u7cfb\u7a00\u571f\u7814\u7a76\u4e2d\u5fc3\u7684\u6559\u6388\u8fc7\u53bb\u4e86\u89e3\u60c5\u51b5\u3002\u8fd9\u8f7b\u8f7b\u4e00\u62e8\uff0c\u5c31\u5c06\u98ce\u5411\u7ed9\u62e8\u504f\u4e86\uff0c\u8fd9\u65f6\u5019\u82e5\u8fd8\u7d27\u7d27\u54ac\u4f4f\u5f20\u606a\u7684\u5b66\u751f\u8eab\u4efd\uff0c\u975e\u4f46\u4e0d\u662f\u6c14\u91cf\u4e0d\u591f\u7684\u95ee\u9898\uff0c\u7b80\u76f4\u5c31\u662f\u770b\u4e0d\u61c2\u4e0d\u901a\u4e00\u70b9\u4eba\u60c5\u554a\u3002</p><p>\u3000\u3000\u5c06\u95ee\u9898\u7684\u7126\u70b9\u8f6c\u79fb\u5230\u5b66\u672f\u4e89\u8bae\u4e0a\uff0c\u5f20\u606a\u4e2a\u4eba\u7684\u8d23\u4efb\u7b97\u662f\u7ed9\u62b9\u5e72\u51c0\u4e86\uff0c\u4f46\u662f\u5728\u8bb2\u5802\u4e0a\uff0c\u5468\u747e\u73ba\u4e0e\u5f20\u606a\u5404\u63d0\u51fa\u7684\u54ea\u4e00\u79cd\u89c2\u70b9\u66f4\u63a5\u8fd1\u4e8e\u4e8b\u5b9e\uff0c\u5374\u662f\u4f17\u8bf4\u7eb7\u7ead\u3001\u5404\u6267\u4e00\u8f9e\u3002\u867d\u7136\u52a0\u901f\u6c11\u8425\u5316\u3001\u79c1\u6709\u5316\u8fdb\u7a0b\uff0c\u662f\u4e5d\u516b\u5e74\u7684\u4e3b\u6d41\u58f0\u97f3\uff0c\u52a0\u5f3a\u56fd\u6709\u8d44\u672c\u5728\u6838\u5fc3\u4ea7\u4e1a\u5185\u7684\u5730\u4f4d\u867d\u7136\u5728\u4e5d\u516b\u5e74\u7684\u58f0\u97f3\u6bd4\u8f83\u5c0f\uff0c\u4f46\u6bd5\u7adf\u8fd9\u662f\u5f53\u524d\u7ecf\u6d4e\u5de5\u4f5c\u7684\u6838\u5fc3\u601d\u60f3\u4e4b\u4e00\u4e5f\u660e\u786e\u65e0\u8bef\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5468\u747e\u73ba\u4e89\u6267\u7684\u4e5f\u5c31\u505c\u7559\u5728\u7a00\u571f\u4ea7\u4e1a\u8981\u4e0d\u8981\u52a0\u901f\u5e02\u573a\u5316\u7ade\u4e89\uff0c\u8981\u4e0d\u8981\u52a0\u901f\u6c11\u8425\u5316\u3001\u5411\u5916\u8d44\u5f00\u653e\u7684\u8fdb\u7a0b\u3001\u8981\u4e0d\u8981\u4fc3\u8fdb\u51fa\u53e3\u3001\u8981\u4e0d\u8981\u53c2\u4e0e\u56fd\u9645\u7a00\u571f\u5e02\u573a\u7ade\u4e89\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u5728\u8c61\u7259\u5854\u91cc\u505a\u5b66\u95ee\u7684\u6559\u6388\u3001\u5b66\u8005\uff0c\u62e5\u62a4\u4e00\u79cd\u89c2\u70b9\u90fd\u8981\u6446\u51fa\u8bb8\u591a\u7684\u7406\u8bba\uff0c\u65e2\u7136\u4e0a\u5347\u5230\u5b66\u672f\u4e89\u8bae\u7684\u7a0b\u5ea6\u4e0a\uff0c\u8fd9\u79cd\u4e89\u8bae\u4e00\u5929\u4e24\u5929\u662f\u89e3\u51b3\u4e0d\u4e86\u4efb\u4f55\u95ee\u9898\u7684\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u8fd8\u8eba\u5728\u75c5\u623f\u91cc\u9759\u517b\uff0c\u4ed6\u59bb\u5b50\u8ddf\u4ed6\u7684\u52a9\u6559\u867d\u7136\u77e5\u9053\u5b66\u6821\u91cc\u7684\u98ce\u58f0\u5439\u504f\u4e86\uff0c\u95f9\u4e8b\u7684\u5b66\u751f\u6839\u672c\u4e0d\u53ef\u80fd\u518d\u53d7\u5230\u4efb\u4f55\u7684\u5904\u5206\uff0c\u5fc3\u91cc\u6c14\u6124\u5f97\u5f88\uff0c\u5374\u4e0d\u6562\u5c06\u8fd9\u4e9b\u4e8b\u60c5\u544a\u8bc9\u8eba\u5728\u75c5\u5e8a\u4e0a\u7684\u5468\u747e\u73ba\uff0c\u6015\u628a\u4ed6\u523a\u6fc0\u5230\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u66f4\u4e0d\u6e05\u695a\u5510\u82f1\u57f9\u5df2\u7ecf\u7ed9\u5efa\u90ba\u8b66\u65b9\u4ee5\u884c\u653f\u62d8\u7559\u7684\u540d\u4e49\u6682\u65f6\u63a7\u5236\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u610f\u901a\u8fc7\u5176\u4ed6\u6e20\u9053\u4e86\u89e3\u5230\u76f8\u5173\u60c5\u51b5\uff0c\u4ed6\u4e5f\u731c\u5230\u4f1a\u51fa\u73b0\u8fd9\u79cd\u5c40\u9762\uff0c\u5468\u747e\u73ba\u7684\u52a9\u6559\u6c14\u6124\u4e0d\u5e73\u5c06\u4e8b\u60c5\u6345\u5230\u9662\u91cc\uff0c\u5374\u662f\u5c06\u5468\u747e\u73ba\u7ad6\u6210\u9776\u5b50\u7ed9\u4eba\u653b\u51fb\u3002\u4ed6\u5bf9\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u7684\u95ee\u9898\u6ca1\u6709\u4ec0\u4e48\u7814\u7a76\uff0c\u4f46\u662f\u4ed6\u8003\u8651\u5230\uff0c\u5982\u679c\u5468\u747e\u73ba\u771f\u5728\u8fd9\u4e2a\u95ee\u9898\u6709\u4ec0\u4e48\u75db\u811a\u7ed9\u5f20\u606a\u6293\u4f4f\uff0c\u53ea\u6015\u4f1a\u7ed9\u75db\u6253\u843d\u6c34\u72d7\uff0c\u4e0d\u4f1a\u6709\u4ec0\u4e48\u597d\u7ed3\u5c40\u3002</p><p>\u3000\u3000\u8c22\u610f\u72b9\u8c6b\u4e86\u518d\u4e09\uff0c\u8fd8\u662f\u51b3\u5b9a\u5c06\u4e8b\u60c5\u7684\u524d\u56e0\u540e\u679c\u6253\u7535\u8bdd\u544a\u8bc9\u5728\u60e0\u5c71\u7684\u7236\u4eb2\uff1b\u5374\u6ca1\u6709\u4eba\u5c06\u6d88\u606f\u544a\u8bc9\u91d1\u5c71\u7684\u5468\u747e\u745c\u3001\u8c22\u6c49\u660e\uff0c\u4e5f\u6ca1\u6709\u5c06\u6d88\u606f\u544a\u8bc9\u4eba\u5728\u6d77\u5dde\u7684\u8c22\u5251\u5357\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u5c06\u81ea\u5df1\u5173\u5728\u529e\u516c\u5ba4\u91cc\u8003\u8651\u4e86\u597d\u4e45\uff0c\u624d\u56de\u590d\u8c22\u610f\u7684\u7535\u8bdd\uff1a\u201c\u65c1\u89c2\u5427\uff0c\u4f60\u4e09\u53d4\u90a3\u91cc\uff0c\u53ea\u6015\u672a\u5fc5\u80fd\u5e2e\u4e0a\u5fd9\uff0c\u5c31\u4e0d\u8981\u7ed9\u4ed6\u4eec\u6dfb\u4e71\u4e86\u3002\u53ea\u5e0c\u671b\u8fd9\u628a\u706b\u4e0d\u8981\u70e7\u5f97\u592a\u51f6\uff0c\u4f60\u6709\u65f6\u95f4\u4e5f\u8981\u53bb\u770b\u770b\u4f60\u59b9\u59b9\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u610f\u77e5\u9053\u7236\u4eb2\u7684\u62c5\u5fe7\u53ea\u662f\u6015\u60f9\u706b\u70e7\u8eab\u554a\uff0c\u751a\u81f3\u60f3\u5411\u4e09\u53d4\u3001\u4e09\u5a76\u4ed6\u4eec\u90a3\u91cc\u80fd\u88c5\u7cca\u6d82\u5219\u88c5\u7cca\u6d82\uff0c\u80fd\u7792\u5219\u7792\uff0c\u5c31\u7b97\u6709\u4ec0\u4e48\u706b\uff0c\u4e5f\u8ba9\u706b\u53ea\u70e7\u5728\u5468\u747e\u73ba\u4e00\u4eba\u8eab\u4e0a\u597d\u4e86\uff0c\u4eca\u5929\u7684\u9526\u6e56\u5df2\u7ecf\u4e0d\u662f\u6b63\u6cf0\u6240\u80fd\u6297\u8861\u7684\u3002\u867d\u7136\u6b63\u6cf0\u96c6\u56e2\u4e0e\u5609\u4fe1\u7535\u5b50\u8054\u5408\u8d77\u6765\u5728\u91d1\u5c71\u641e\u7535\u5b50\u56ed\u5efa\u8bbe\uff0c\u5e76\u5bc4\u5e0c\u671b\u80fd\u501f\u52a9\u91d1\u5c71\u7535\u5b50\u56ed\u6446\u8131\u9526\u6e56\u8fd9\u51e0\u5e74\u538b\u5728\u5934\u9876\u7684\u9634\u4e91\uff0c\u4f46\u662f\u5728\u91d1\u5c71\u7535\u5b50\u56ed\u91cc\u5934\uff0c\u8c22\u6c49\u660e\u3001\u8c22\u5251\u5357\u7236\u5b50\u4fe9\u7684\u5174\u8da3\u66f4\u5927\u4e00\u4e9b\uff0c\u8c22\u610f\u60f3\u5230\u66f4\u591a\u7684\u53ea\u662f\u987a\u52bf\u800c\u4e3a\u7f62\u4e86\u3002</p><p>\u3000\u3000\u8c22\u610f\u77e5\u9053\u7236\u4eb2\u7684\u5fc3\u601d\uff0c\u662f\u5c06\u79d1\u738b\u9ad8\u79d1\u89c6\u4f5c\u6b63\u6cf0\u4e0e\u9526\u6e56\u4e4b\u95f4\u6700\u540e\u7684\u7f13\u51b2\uff0c\u522b\u770b\u5b50\u5609\u901a\u5e38\u4e0d\u4f1a\u6309\u7167\u5e38\u7406\u51fa\u724c\uff0c\u4f46\u662f\u5979\u5bf9\u5546\u4e1a\u7684\u654f\u9510\u4e0e\u5929\u8d4b\uff0c\u662f\u5e38\u4eba\u8fdc\u8fdc\u4e0d\u53ca\u7684\u3002\u53ea\u662f\u60df\u4e00\u8ba9\u4eba\u89c9\u5f97\u53ef\u60dc\u7684\uff0c\u6bd4\u8d77\u5e2e\u5bb6\u65cf\u4f01\u4e1a\u518d\u4e0a\u4e00\u4e2a\u53f0\u9636\uff0c\u5b50\u5609\u4f3c\u4e4e\u4f1a\u60f3\u5c06\u5bb6\u65cf\u91cc\u7684\u7537\u4eba\u4e00\u4e2a\u4e2a\u7684\u8e0f\u5728\u5979\u7684\u811a\u5e95\u4e0b\u3002</p><p>\u3000\u3000\u8c22\u610f\u5bf9\u8fd9\u4e2a\u59b9\u59b9\u4e5f\u5f88\u65e0\u53ef\u5948\u4f55\u3002</p><p>\u3000\u3000\u4e0b\u5348\uff0c\u5d14\u90c1\u66fc\u672a\u627e\u5230\u5f20\u606a\u9887\u4e3a\u5931\u671b\u7684\u4ece\u521b\u57df\u516c\u53f8\u79bb\u5f00\uff0c\u8c22\u610f\u5219\u7b2c\u4e00\u6b21\u8d70\u8fdb\u79d1\u738b\u9ad8\u79d1\u8bbe\u5728\u521b\u57df\u516c\u53f8\u5df7\u5b50\u5bf9\u9762\u7684\u624b\u673a\u8bbe\u8ba1\u5de5\u4f5c\u5ba4\uff0c\u53ea\u9694\u7740\u4e00\u6761\u7a84\u5df7\u5b50\uff0c\u80fd\u4ece\u6253\u5f00\u7684\u7a97\u6237\u91cc\u770b\u5230\u521b\u57df\u516c\u53f8\u529e\u516c\u5ba4\u91cc\u804c\u5458\u529e\u516c\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u8bf4\u4e0d\u662f\u4f60\u95ef\u7684\u7978\uff0c\u201d\u8c22\u610f\u770b\u7740\u4ed6\u59b9\u59b9\u4e0d\u4fee\u8fb9\u5e45\u7684\u6a21\u6837\uff0c\u5fae\u5fae\u7684\u53f9\u6c14\u6447\u5934\uff0c\u624e\u7740\u9a6c\u5c3e\u8fab\uff0c\u53d1\u4e1d\u51cc\u4e71\uff0c\u201c\u4f60\u6628\u5929\u5728\u6559\u5ba4\u91cc\u8ddf\u5f20\u606a\u8bf4\u4ec0\u4e48\u8bdd\u624d\u5f97\u610f\u5fd8\u5f62\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u8c08\u9526\u6e56\u65b0\u5f00\u53d1\u51fa\u6765\u7684\u4e00\u9879\u65b0\u6280\u672f\uff0c\u90a3\u5c0f\u5b50\u7b54\u5e94\u6388\u6743\u79d1\u738b\u9ad8\u79d1\u4f7f\u7528\uff0c\u5e02\u573a\u6f5c\u529b\u4f1a\u6bd4\u8f83\u5927\uff0c\u6240\u4ee5\u4e00\u65f6\u5fd8\u4e86\u4eba\u8fd8\u5728\u6559\u5ba4\u91cc\u3002\u201d\u8c22\u5b50\u5609\u89c1\u4e8b\u60c5\u90fd\u95f9\u4e86\u8fd9\u4e48\u5927\uff0c\u81ea\u5df1\u90fd\u89c9\u5f97\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u3002</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u6280\u672f\u2026\u2026\u96be\u9053\u5bf9\u6211\u4e5f\u8981\u4fdd\u5bc6\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5b50\u5609\u5476\u5476\u5634\uff0c\u90a3\u610f\u601d\u662f\u8bf4\u201c\u4f60\u731c\u7684\u6ca1\u9519\u201d\uff0c\u8c22\u610f\u9887\u4e3a\u65e0\u8da3\u7684\u81ea\u5df1\u5148\u5c94\u5f00\u5176\u4ed6\u8bdd\u9898\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6700\u521d\u51e0\u5929\u7684\u98ce\u58f0\u4e5f\u53ea\u9650\u5728\u4e1c\u5927\u6821\u56ed\u5185\u90e8\uff0c\u5510\u82f1\u57f9\u88ab\u5efa\u90ba\u8b66\u65b9\u884c\u653f\u62d8\u7559\uff0c\u7531\u4e8e\u5510\u82f1\u57f9\u81ea\u5df1\u7f9e\u4e8e\u5c06\u62d8\u7559\u7684\u539f\u56e0\u8bf4\u51fa\u53e3\uff0c\u7ae0\u5dde\u5510\u5bb6\u7684\u4eba\u4e5f\u53ea\u80fd\u5148\u60f3\u529e\u6cd5\u5c06\u4eba\u635e\u51fa\u6765\u518d\u8bf4\uff0c\u6240\u6709\u5510\u5bb6\u901a\u8fc7\u79cd\u79cd\u5173\u7cfb\u7684\u6c42\u60c5\u7535\u8bdd\u6253\u5230\u4f55\u7eaa\u4e91\u90a3\u91cc\uff0c\u90fd\u7ed9\u4f55\u7eaa\u4e91\u6001\u5ea6\u5f3a\u786c\u7684\u56de\u7edd\u6389\u4e86\uff1a\u5510\u82f1\u57f9\u8fd9\u4e2a\u4eba\u5728\u5efa\u90ba\u72af\u4e86\u4e8b\uff0c\u5c31\u8981\u5728\u5efa\u90ba\u8001\u8001\u5b9e\u5b9e\u7684\u53d7\u5230\u5904\u7f5a\u3002</p><p>\u3000\u3000\u7ed9\u5510\u82f1\u57f9\u4e22\u5728\u5b66\u5e9c\u5df7\u7684\u90a3\u8f86\u608d\u9a6c\u4e24\u591c\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u8f66\u7a97\u73bb\u7483\u7ed9\u5168\u90e8\u7838\u788e\u4e4b\u540e\uff0c\u4ea4\u8b66\u90e8\u95e8\u624d\u5c06\u8f66\u5b50\u62d6\u8d70\u3002</p><p>\u3000\u3000\u60f3\u8981\u6478\u6e05\u7ae0\u5dde\u7a00\u571f\u77ff\u8d44\u6e90\u7ed9\u76d7\u91c7\u3001\u7834\u574f\u7684\u7b2c\u4e00\u624b\u6750\u6599\u4e0d\u662f\u4e00\u5929\u4e24\u5929\u4e4b\u95f4\u5c31\u80fd\u5b8c\u6210\u7684\uff0c\u6709\u4e9b\u6587\u7ae0\u53ef\u4ee5\u5148\u5199\uff0c\u56db\u6708\u4e2d\u65ec\u4e1c\u6d77\u7701\u59d4\u5ba3\u4f20\u90e8\u6240\u5c5e\u7684\u6838\u5fc3\u520a\u300a\u6c42\u5b9e\u300b\u6742\u5fd7\u5c31\u520a\u767b\u4e86\u7701\u7ecf\u6d4e\u7814\u7a76\u9662\u4e13\u5bb6\u4ee5\u300a\u77ff\u4ea7\u8d44\u6e90\u5411\u79c1\u6709\u8d44\u672c\u4e0e\u5916\u8d44\u5f00\u653e\u8fdb\u7a0b\u7684\u82e5\u5e72\u8003\u8651\u300b\u4e3a\u6807\u9898\u53d1\u8868\u7684\u6587\u7ae0\u3002\u7701\u7ecf\u6d4e\u7814\u7a76\u9662\u4e00\u5411\u662f\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u7ecf\u6d4e\u653f\u7b56\u667a\u56ca\uff0c\u8fd9\u7bc7\u6587\u7ae0\u5c06\u5c40\u9650\u5728\u4e1c\u5927\u6821\u56ed\u5185\u90e8\u7684\u4e89\u8bae\u7b2c\u4e00\u6b21\u5f15\u53d1\u5230\u5a92\u4f53\u4e0a\uff0c\u4e5f\u7387\u5148\u5728\u5a92\u4f53\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u8868\u660e\u4e1c\u6d77\u7701\u59d4\u3001\u7701\u653f\u5e9c\u5728\u76f8\u5173\u95ee\u9898\u4e0a\u7684\u503e\u5411\u6027\u770b\u6cd5\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u501f\u53f6\u81fb\u6c11\u56db\u6708\u4e2d\u65ec\u8fd4\u56de\u53f6\u5bb6\u8001\u5b85\u7684\u673a\u4f1a\u5728\u5efa\u90ba\u8ddf\u4ed6\u89c1\u9762\uff0c\u63d0\u51fa\u9526\u6e56\u5e0c\u671b\u80fd\u63a7\u5236\u94df\u7b49\u7a00\u6709\u91d1\u5c5e\u7684\u51fa\u53e3\u6e20\u9053\uff0c\u7279\u522b\u662f\u5411\u65e5\u97e9\u7b49\u56fd\u7684\u51fa\u53e3\u6e20\u9053\u3002</p><p>\u3000\u3000\u94df\u952d\u6b63\u5904\u4e8e\u6709\u53f2\u4ee5\u6765\u7684\u4f4e\u8c37\uff0c\u4f5c\u4e3a\u950c\u3001\u9521\u3001\u94dc\u77ff\u7684\u4f34\u751f\u77ff\uff0c\u5728\u56fd\u5185\u7edd\u5927\u591a\u6570\u7684\u77ff\u4f01\u773c\u4e2d\u662f\u6ca1\u6709\u591a\u5c11\u5546\u4e1a\u4ef7\u503c\u7684\uff0c\u7ed9\u5f53\u6210\u5783\u573e\u5728\u56fd\u9645\u5e02\u573a\u629b\u552e\u3002\u552e\u4ef7\u4f4e\u3001\u4ea7\u91cf\u5fae\uff0c\u4f46\u662f\u672a\u6765\u6db2\u6676\u4ea7\u4e1a\u53c8\u79bb\u5f00\u4e0d\u94df\u952d\uff0c\u5728\u5916\u7ecf\u8d38\u90e8\u7b49\u6709\u5173\u90e8\u95e8\u7684\u534f\u52a9\u4e0b\u53ea\u9700\u8981\u52a8\u7528\u5c11\u91cf\u8d44\u91d1\u3001\u8d44\u6e90\u5c31\u80fd\u5c06\u63a7\u5236\u94df\u952d\u7684\u5916\u9500\u6e20\u9053\u3002\u63a7\u5236\u56fd\u5185\u7684\u94df\u952d\u5916\u9500\u6e20\u9053\uff0c\u4ee5\u53ca\u8d81\u4f4e\u4ef7\u65f6\u56e4\u79ef\u5927\u91cf\u7684\u94df\u952d\u8d44\u6e90\u5c06\u662f\u9526\u6e56\u672a\u6765\u4ece\u65e5\u3001\u97e9\u4f01\u4e1a\u83b7\u53d6\u8584\u819c\u6db2\u6676\u6280\u672f\u7684\u91cd\u8981\u624b\u6bb5\u4e4b\u4e00\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u5728\u7a00\u571f\u53ca\u7a00\u6709\u91d1\u5c5e\u77ff\u4ea7\u4ea7\u4e1a\u662f\u5426\u8981\u5f15\u5165\u5916\u8d44\u53ca\u6269\u5927\u5bf9\u5916\u51fa\u53e3\u95ee\u9898\u4e0a\uff0c\u5df2\u7ecf\u5347\u4efb\u5916\u7ecf\u8d38\u90e8\u515a\u7ec4\u526f\u4e66\u8bb0\u3001\u7b2c\u4e00\u526f\u90e8\u957f\u7684\u53f6\u81fb\u6c11\u662f\u80fd\u591f\u5728\u8fdb\u884c\u4ea7\u4e1a\u51b3\u7b56\u65f6\u76f4\u63a5\u8868\u6001\u7684\u3002\u5f53\u7136\uff0c\u53f6\u81fb\u6c11\u8981\u5728\u8fd9\u4e2a\u95ee\u9898\u4e0a\u8868\u6001\uff0c\u4e5f\u9700\u8981\u66f4\u591a\u3001\u66f4\u76f4\u63a5\u7684\u7b2c\u4e00\u624b\u8d44\u6599\u3002\u76f8\u5bf9\u4e8e\u79c1\u4eba\u5728\u5730\u65b9\u653f\u5e9c\u7684\u5305\u5e87\u4e0b\u76d7\u91c7\u7a00\u571f\u77ff\u8d44\u6e90\u3001\u9020\u6210\u751f\u6001\u73af\u5883\u7684\u4e25\u91cd\u7834\u574f\u95ee\u9898\uff0c\u4ece\u53f6\u81fb\u6c11\u81ea\u5df1\u6240\u5904\u7684\u4f4d\u5b50\u6765\u8bf4\uff0c\u4ed6\u66f4\u60f3\u77e5\u9053\u6709\u6ca1\u6709\u5916\u8d44\u80cc\u666f\u7684\u4f01\u4e1a\u6216\u673a\u6784\u5728\u80cc\u540e\u63a8\u52a8\u7a00\u571f\u4ea7\u4e1a\u5411\u6c11\u8425\u3001\u5411\u5916\u8d44\u5f00\u653e\u8fdb\u7a0b\u7684\u732b\u817b\u2014\u2014\u5f53\u7136\uff0c\u8fd9\u4e00\u70b9\u4f1a\u66f4\u96be\u67e5\uff0c\u5f88\u53ef\u80fd\u5916\u7ecf\u8d38\u90e8\u6216\u56fd\u9662\u52a1\u7a00\u571f\u529e\u7684\u6709\u4e9b\u5b98\u5458\u62b5\u4e0d\u4f4f\u8bf1\u60d1\u3001\u5df2\u7ecf\u906d\u5230\u5916\u5546\u7684\u8150\u8680\uff0c\u6ca6\u4e3a\u5916\u5546\u7684\u8d70\u72d7\u2014\u2014\u8150\u8d25\u5e76\u4e0d\u603b\u662f\u53d1\u751f\u5728\u5730\u65b9\u653f\u5e9c\u5b98\u5458\u8eab\u4e0a\u3002</p></article>"}}}]);
