(window.webpackJsonp=window.webpackJsonp||[]).push([[1009],{1032:function(p){p.exports={chapterTitle:null,title:"\u7b2c82\u7ae0 207\u7f8e\u5973",content:"<article><h2>\u7b2c82\u7ae0 207\u7f8e\u5973</h2><p>\u3000\u3000\u5f20\u606a\u63a8\u5f00\u9694\u58c1\u7684\u623f\u95f4\uff0c\u91cc\u9762\u6ca1\u6709\u9501\uff0c\u7a97\u5e18\u8fd8\u90fd\u62c9\u4e0a\uff0c\u91cc\u9762\u7684\u5149\u7ebf\u5f88\u6697\uff0c\u4e00\u4e2a\u5973\u5b69\u5b50\u6b63\u5750\u5728\u4e66\u684c\u524d\uff0c\u62ff\u7709\u526a\u5bf9\u7740\u955c\u5b50\u4fee\u7406\u7709\u6bdb\u3002\u662f\u90a3\u79cd\u4e5d\u56db\u5e74\u8fd8\u5f88\u5c11\u89c1\u7684\u516c\u5bd3\u5f0f\u5bbf\u820d\uff0c\u56db\u5f20\u4e0a\u4e0b\u53e0\u5c42\u7684\u5e8a\u94fa\u90fd\u5728\u5de6\u8fb9\uff0c\u4e24\u4e2a\u5973\u5b69\u51cc\u4e71\u7684\u8eba\u5728\u4e0b\u94fa\uff0c\u4e00\u4e2a\u5973\u5b69\u88f9\u7740\u88ab\u5b50\uff0c\u5934\u90fd\u57cb\u5728\u88ab\u5b50\u91cc\u3002</p><p>\u3000\u3000\u4e00\u4e2a\u5973\u5b69\u88ab\u5b50\u53ea\u76d6\u7740\u809a\u5b50\uff0c\u96ea\u767d\u7ea4\u7ec6\u7684\u5927\u817f\u8086\u65e0\u5fcc\u60ee\u7684\u4f38\u51fa\u88ab\u5b50\uff0c\u7761\u8863\u5411\u4e0a\u64a9\u8d77\uff0c\u9732\u51fa\u7ea2\u8272\u7684\u5361\u901a\u5185\u88e4\uff0c\u7adf\u7136\u662f\u7c73\u8001\u9f20\uff0c\u5f20\u606a\u9f3b\u8840\u5dee\u70b9\u55b7\u51fa\u6765\u3002\u4e0a\u9762\u4e00\u622a\u5c0f\u8170\uff0c\u808c\u80a4\u5ae9\u767d\uff0c\u80fd\u6390\u51fa\u6c34\u6765\uff0c\u773c\u775b\u7565\u8fc7\u538b\u7740\u8179\u90e8\u7684\u88ab\u5b50\uff0c\u770b\u89c1\u51cc\u4e71\u7761\u8863\u9886\u53e3\u655e\u5f00\u7740\uff0c\u9732\u51fa\u534a\u7247\u96ea\u767d\u7684\u80f8\u812f\uff0c\u867d\u7136\u4ef0\u8eba\u7740\uff0c\u4e73\u623f\u8fd8\u662f\u5014\u5f3a\u7684\u633a\u7acb\u7740\u3002\u4e0d\u7ba1\u4ec0\u4e48\u65f6\u4ee3\uff0c\u5973\u5b69\u5b50\u90fd\u5f88\u5c11\u6709\u7a7f\u80f8\u7f69\u7761\u89c9\u7684\u4e60\u60ef\uff0c\u4e00\u5934\u4e30\u76db\u51cc\u4e71\u7684\u79c0\u53d1\u57cb\u4f4f\u8138\uff0c\u5806\u5728\u6795\u5934\u4e0a\uff0c\u53ea\u9732\u51fa\u5ae9\u817b\u767d\u7699\u7684\u4e0b\u9894\u4e0e\u4e00\u89d2\u5a07\u8273\u52a8\u4eba\u7684\u7ea2\u5507\u3002</p><p>\u3000\u3000\u7ad9\u5728\u684c\u524d\u4fee\u526a\u7709\u6bdb\u7684\u5973\u5b69\u770b\u89c1\u5f20\u606a\u624b\u63d0\u7740\u4e1c\u897f\u7ad9\u5728\u95e8\u53e3\uff0c\u6123\u4e86\u6123\uff0c\u5faa\u7740\u4ed6\u7684\u76ee\u5149\uff0c\u770b\u89c1\u7389\u9635\u6a2a\u9635\u7684\u5973\u5b69\uff0c\u6342\u7740\u5634\u53d1\u51fa\u5c16\u9510\u7684\u60ca\u53eb\uff1a\u201c\u6c5f\u9edb\u513f\uff0c\u4f60\u8d70\u5149\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u60f3\u591a\u770b\u51e0\u773c\uff0c\u9887\u4e3a\u4e0d\u820d\u7684\u9000\u51fa\u95e8\u5916\u3002\u8bb8\u601d\u8ba9\u91cc\u9762\u7684\u5c16\u53eb\u641e\u5f97\u83ab\u660e\u5176\u5999\uff0c\u8bb8\u7ef4\u5927\u53eb\u4e0d\u597d\uff1a\u201c\u6c5f\u9edb\u513f\u8ddf\u6797\u51b0\u90fd\u6ca1\u6709\u8d77\u5e8a\uff0c\u8fd9\u4e24\u4eba\u53c8\u559c\u6b22\u88f8\u7761\u2026\u2026\u201d\u7d27\u5fd9\u63a8\u95e8\u8d70\u4e86\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u8bb8\u601d\u72d0\u7591\u7684\u773c\u795e\uff0c\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u610f\u5916\uff0c\u7eaf\u5c5e\u610f\u5916\u3002\u201d</p><p>\u3000\u3000\u8bb8\u601d\u63a2\u5934\u5f80\u91cc\u770b\u4e86\u770b\uff0c\u53c8\u56de\u8fc7\u5934\u6765\uff0c\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u574f\u574f\u7684\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u4fbf\u5b9c\u6b7b\u4f60\u4e86\u3002\u201d\u7709\u773c\u95f4\u59a9\u5a9a\u65e0\u7aef\uff0c\u5634\u91cc\u542b\u7740\u7b11\uff0c\u4e0e\u5f20\u606a\u7ad9\u5728\u5916\u9762\u7b49\u91cc\u9762\u7684\u5973\u5b69\u5b50\u6536\u62fe\u6574\u9f50\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u8bb8\u7ef4\u63a8\u95e8\u5524\u5f20\u606a\u8ddf\u8bb8\u601d\u8fdb\u53bb\uff0c\u91cc\u9762\u4e09\u4e2a\u5973\u5b69\u90fd\u6536\u62fe\u6574\u9f50\uff0c\u7a97\u5e18\u62c9\u8d77\u6765\uff0c\u5ba4\u5185\u5149\u7ebf\u660e\u4eae\u3002\u5f20\u606a\u4e00\u8d70\u8fdb\u6765\uff0c\u521a\u624d\u5750\u5728\u684c\u524d\u4fee\u7709\u3001\u60ca\u53eb\u7684\u5973\u5b69\u77aa\u773c\u770b\u7740\u5f20\u606a\uff0c\u7a81\u7136\u5927\u58f0\u55a7\u56b7\u8d77\u6765\uff1a\u201c\u8bb8\u7ef4\uff0c\u6211\u592a\u7231\u4f60\u4e86\uff0c\u6211\u53ea\u662f\u8ba9\u4f60\u5e2e\u6211\u4eec\u53bb\u4e70\u65e9\u996d\uff0c\u4f60\u5374\u5e26\u4e86\u4e2a\u5927\u5e05\u54e5\u7ed9\u6211\u4eec\u2026\u2026\u201d\u53c8\u6402\u7740\u80cc\u5bf9\u7740\u95e8\u5750\u7684\u5973\u5b69\u8bf4\uff0c\u201c\u6c5f\u9edb\u513f\uff0c\u4f60\u6ca1\u6709\u5403\u4e8f\uff0c\u771f\u662f\u5927\u5e05\u54e5\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u4e00\u60ca\uff0c\u72b9\u8c6b\u7740\u662f\u4e0d\u662f\u9000\u51fa\u53bb\u6bd4\u8f83\u5408\u9002\u3002</p><p>\u3000\u3000\u201c\u4ee4\u5c0f\u71d5\uff0c\u4eba\u5bb6\u5dee\u4f60\u56db\u5206\u4e4b\u4e00\u8f6e\u5462\uff0c\u4f60\u6536\u655b\u70b9\uff0c\u4e0d\u8981\u628a\u5c0f\u5b69\u5b50\u5413\u7740\u4e86\uff0c\u201d\u8bb8\u7ef4\u7b11\u7740\u9a82\u5979\uff0c\u201c\u4ed6\u53eb\u5f20\u606a\uff0c\u6211\u59d0\u505a\u5bb6\u6559\u7684\u5b66\u751f\u2026\u2026\u201d</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u82b1\u75f4\u7684\u76ef\u7740\u5f20\u606a\uff1a\u201c\u771f\u7684\u5f88\u5e05\u554a\uff0c\u5c31\u5c0f\u4e09\u5c81\u800c\u5df2\uff0c\u8001\u725b\u5403\u5ae9\u8349\uff0c\u6211\u80fd\u7167\u987e\u597d\u4ed6\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u7a81\u7136\u53d1\u73b0\u5728\u8fd9\u6837\u7684\u5973\u751f\u9762\u524d\uff0c\u81ea\u5df1\u53d8\u5f97\u4e00\u70b9\u90fd\u6ca1\u6709\u6218\u6597\u529b\uff0c\u5c34\u5c2c\u7684\u7ad9\u5728\u95e8\u53e3\uff0c\u6709\u4e9b\u4e0d\u77e5\u6240\u63aa\u3002</p><p>\u3000\u3000\u201c\u770b\u770b\uff0c\u4ed6\u8fd8\u8138\u7ea2\u4e86\uff0c\u597d\u6e05\u7eaf\u7684\u6837\u5b50\uff0c\u6211\u559c\u6b22\u2026\u2026\u201d</p><p>\u3000\u3000\u770b\u7740\u4ee4\u5c0f\u71d5\u53e3\u6c34\u90fd\u5dee\u70b9\u6d41\u51fa\u6765\u7684\u6837\u5b50\uff0c\u5f20\u606a\u4f38\u624b\u6293\u4f4f\u95e8\u628a\uff0c\u968f\u65f6\u51c6\u5907\u51b2\u51fa\u53bb\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u7a81\u7136\u5927\u7b11\u8d77\u6765\uff1a\u201c\u8bb8\u7ef4\uff0c\u4f60\u7f16\u6545\u4e8b\u5427\uff01\u5c31\u8fd9\u5c0f\u5c41\u5b69\uff0c\u521a\u521a\u8fd8\u6562\u8c03\u620f\u4f60\u3001\u5bf9\u4f60\u8bf4\u2018\u5927\u59d0\u597d\u51b7\u6de1\u2019\uff1f\u201d</p><p>\u3000\u3000\u8bb8\u7ef4\u4e0e\u53e6\u5916\u4e00\u4e2a\u53eb\u6797\u51b0\u7684\u5973\u5b69\u5b50\u4e5f\u5927\u7b11\u8d77\u6765\uff0c\u521a\u624d\u5927\u7247\u8d70\u5149\u7684\u6c5f\u9edb\u513f\u80cc\u7740\u95e8\u5750\u7740\uff0c\u4e0d\u597d\u610f\u601d\u770b\u5916\u9762\uff0c\u80a9\u8180\u4e5f\u5728\u6296\u4e2a\u4e0d\u505c\uff0c\u60f3\u5fc5\u5fcd\u7740\u7b11\u662f\u5f88\u8f9b\u82e6\u7684\u4e8b\u3002</p><p>\u3000\u3000\u60f3\u4e0d\u5230\u7ec8\u65e5\u730e\u96c1\uff0c\u4eca\u65e5\u5374\u7ed9\u96c1\u5544\u4e86\u773c\uff0c\u5f20\u606a\u5c34\u5c2c\u7684\u644a\u4e86\u644a\u624b\uff0c\u8bb8\u601d\u5728\u4e00\u65c1\u6342\u7740\u8170\u773c\u7b11\u7740\u82b1\u679d\u4e71\u98a4\uff0c\u8bb8\u7ef4\u624b\u53c9\u7740\u8170\uff0c\u8086\u65e0\u5fcc\u60ee\u7684\u7b11\u7740\uff0c\u8fd8\u6311\u8845\u7684\u76ef\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f7b\u5e95\u8ba4\u8f93\uff0c\u53ea\u6709\u88c5\u4f5c\u7eaf\u60c5\u5c11\u5e74\uff0c\u8fd8\u80fd\u9003\u8fc7\u5979\u4eec\u8086\u65e0\u5fcc\u60ee\u7684\u803b\u7b11\u3002</p><p>\u3000\u3000\u8bb8\u7ef4\u5fcd\u7740\u7b11\u7ed9\u59d0\u59d0\u8bb8\u601d\u4ecb\u7ecd\u5bbf\u820d\u7684\u5176\u4ed6\u4e09\u4e2a\u5973\u5b69\u5b50\u3002\u6c5f\u9edb\u513f\u8138\u6e17\u8840\u4f3c\u7684\u7ea2\u8273\uff0c\u7c89\u9762\u5a07\u8273\uff0c\u7531\u4e8e\u8d70\u5149\u7684\u7f18\u6545\uff0c\u773c\u795e\u8eb2\u95ea\u4e0d\u6562\u770b\u5f20\u606a\uff0c\u5374\u6108\u53d1\u7684\u52fe\u4eba\u9b42\u9b44\u3002\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u4e00\u95f4\u5bbf\u820d\u80fd\u6709\u8bb8\u7ef4\u4e0e\u6c5f\u9edb\u513f\u4e24\u4e2a\u5927\u7f8e\u5973\uff0c\u5fc3\u60f3\u81ea\u5df1\u8fdb\u4e1c\u6d77\u5927\u5b66\uff0c\u6070\u9022\u5979\u4eec\u6bd5\u4e1a\u79bb\u6821\uff0c\u5931\u4e4b\u4ea4\u81c2\uff0c\u60f3\u4e0d\u5230\u91cd\u56de\u4e5d\u56db\u5e74\uff0c\u8fd8\u6709\u91cd\u65b0\u8ba4\u8bc6\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u6797\u51b0\uff0c\u521a\u521a\u62ff\u88ab\u5b50\u5305\u88f9\u8fc7\u5168\u8eab\u7684\u5973\u5b69\u5b50\uff0c\u7709\u76ee\u6e05\u79c0\uff0c\u76f4\u9f3b\u8584\u5507\uff0c\u8138\u5f62\u7a0d\u957f\uff0c\u7ed9\u4eba\u5f88\u6587\u9759\u7684\u611f\u89c9\u3002\u4ee4\u5c0f\u71d5\u63cf\u7709\u62b9\u816e\uff0c\u7ea2\u5507\u7c89\u9762\uff0c\u6253\u626e\u65f6\u9ae6\uff0c\u4e5f\u6709\u51e0\u5206\u59ff\u8272\uff0c\u7709\u773c\u95f4\u795e\u91c7\u98de\u626c\uff0c\u770b\u4e0a\u53bb\u5341\u5206\u7cbe\u795e\u3002</p><p>\u3000\u3000\u4e00\u65e6\u7ed9\u8bb8\u7ef4\u5979\u4eec\u8ba4\u5b9a\u8fd8\u662f\u5341\u516d\u5c81\u7684\u5c11\u5e74\uff0c\u77ed\u65f6\u95f4\u5c31\u6ca1\u6709\u6539\u53d8\u5979\u4eec\u5370\u8c61\u7684\u673a\u4f1a\u3002\u8bb8\u7ef4\u4e0b\u5348\u9700\u8981\u6253\u96f6\u5de5\uff0c\u6253\u7535\u8bdd\u8bf7\u4e86\u5047\uff0c\u8fd8\u9080\u5bbf\u820d\u7684\u4e09\u4e2a\u5973\u5b69\u5b50\u4e00\u8d77\u966a\u7740\u8bb8\u601d\u3001\u5f20\u606a\u53bb\u901b\u8857\u3002\u5728\u8bb8\u601d\u770b\u6765\uff0c\u5f20\u606a\u867d\u7136\u8fd9\u4e2a\u590f\u5929\u5728\u7701\u57ce\u4f4f\u8fc7\u4e00\u6bb5\u65f6\u95f4\uff0c\u5374\u6ca1\u6709\u673a\u4f1a\u597d\u597d\u7684\u901b\u8fc7\u7701\u57ce\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u65e9\u4e0a\u8d77\u5e8a\u63cf\u7709\u62b9\u816e\u662f\u8981\u8d76\u7740\u51fa\u53bb\u7ea6\u4f1a\uff0c\u8bb8\u7ef4\u8ba9\u5979\u5c06\u7537\u670b\u53cb\u62ce\u8fc7\u6765\u5f53\u82e6\u529b\uff0c\u5176\u4ed6\u4e24\u4e2a\u5973\u5b69\u5468\u65e5\u65e0\u6240\u4e8b\u4e8b\uff0c\u6c5f\u9edb\u513f\u4e00\u76f4\u7ea2\u7740\u8138\uff0c\u773c\u775b\u8eb2\u95ea\u7740\u4e0d\u770b\u5f20\u606a\uff0c\u4e5f\u7ed9\u8bb8\u7ef4\u7f20\u7740\u7b54\u5e94\u4e00\u8d77\u53bb\u901b\u8857\u3002</p><p>\u3000\u3000\u5f20\u606a\u9ed8\u9ed8\u7684\u770b\u7740\uff0c\u8bb8\u7ef4\u5fc3\u91cc\u771f\u6709\u4ec0\u4e48\u7599\u7629\u4e5f\u8bf4\u4e0d\u5b9a\uff0c\u5979\u597d\u50cf\u4e0d\u5927\u613f\u610f\u4e0e\u8bb8\u601d\u5355\u72ec\u76f8\u5904\u3002</p><p>\u3000\u3000\u5750\u5728\u4f20\u8fbe\u5ba4\u91cc\u6b63\u76ee\u4e0d\u8f6c\u775b\u76ef\u7740\u8fc7\u9053\u7684\u7ba1\u7406\u5458\u770b\u89c1\u5f20\u606a\u5728\u4e00\u7fa4\u7f8e\u5973\u7684\u7c07\u62e5\u4e0b\u8d70\u51fa\u6765\uff0c\u77b3\u5b54\u9aa4\u7136\u653e\u5927\uff0c\u201c\u5509\u2026\u2026\u201d\u7626\u5c0f\u7684\u8eab\u4f53\u4eff\u4f5b\u4ece\u85e4\u6905\u4e0a\u5f39\u51fa\u6765\uff0c\u4eba\u7a9c\u51fa\u4f20\u8fbe\u5ba4\u5c31\u8981\u6765\u63ea\u5f20\u606a\u3002</p><p>\u3000\u3000\u201c\u848b\u963f\u59e8\uff0c\u661f\u671f\u5929\u90fd\u4e0d\u4f11\u606f\uff1f\u201d\u5f20\u606a\u5728\u7ba1\u7406\u5458\u7684\u624b\u5c06\u8981\u63ea\u4f4f\u81ea\u5df1\u7684\u8863\u670d\uff0c\u671d\u5979\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u771f\u8f9b\u82e6\uff0c\u521a\u521a\u8fdb\u6765\u65f6\uff0c\u770b\u4f60\u5728\u85e4\u6905\u4e0a\u7761\u7740\u4e86\uff0c\u6ca1\u597d\u610f\u601d\u60ca\u52a8\u4f60\uff0c\u8981\u4e0d\u8981\u8865\u4e00\u4e0b\u767b\u8bb0\uff1f\u201d\u6307\u7740\u8bb8\u7ef4\u8bf4\uff0c\u201c\u8fd9\u662f\u6211\u59d0\u59d0\uff0c207\u7684\u8bb8\u7ef4\u3002\u201d</p><p>\u3000\u3000\u6307\u5c16\u64e6\u7740\u5f20\u606a\u7684\u8863\u6446\uff0c\u624b\u53c8\u6536\u4e86\u56de\u53bb\uff0c\u5973\u7ba1\u7406\u5458\u6ee1\u8138\u56f0\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u817c\u8146\u800c\u7565\u5e26\u7f9e\u6da9\u7684\u7b11\u5bb9\uff0c\u4e0d\u50cf\u90a3\u4e9b\u6cb9\u5934\u6ed1\u8111\u3001\u4e0d\u5b89\u5fc3\u5b66\u4e60\u3001\u4e00\u95e8\u5fc3\u601d\u60f3\u7740\u8ddf\u5973\u751f\u4e71\u641e\u7684\u6ed1\u5934\u5927\u5b66\u751f\uff0c\u53c8\u770b\u4e86\u770b\u8bb8\u7ef4\uff0c\u8138\u4e0a\u7684\u8868\u60c5\u7f13\u4e86\u7f13\uff0c\u677f\u7740\u8138\u8bf4\uff1a\u201c\u8fdb\u53bb\u8981\u767b\u8bb0\u7684\uff0c\u4e0d\u8981\u6015\u9ebb\u70e6\uff0c\u8fd9\u6b21\u5c31\u7b97\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8981\u60f3\u5f97\u5230\u5141\u8bb8\u8fdb\u5165\u5973\u751f\u5bbf\u820d\uff0c\u9664\u975e\u628a\u81ea\u5df1\u9609\u5272\u4e86\uff0c\u5f20\u606a\u5f88\u8bda\u6073\u7684\u70b9\u70b9\u5934\uff0c\u5fd9\u62c9\u7740\u8bb8\u601d\u3001\u8bb8\u7ef4\u5979\u4eec\u51fa\u4e86\u5bbf\u820d\u697c\uff0c\u514d\u5f97\u7ed9\u5979\u4eec\u50f5\u786c\u7684\u8868\u60c5\u6cc4\u4e86\u5929\u673a\uff0c\u8fd9\u4e2a\u59d3\u848b\u7684\u8001\u5973\u4eba\u6b63\u5904\u4e8e\u66f4\u5e74\u671f\uff0c\u6293\u8d77\u72c2\u6765\uff0c\u662f\u76f8\u5f53\u8981\u547d\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u5bf9\u6b64\u6df1\u6709\u4f53\u4f1a\u3002</p><p>\u3000\u3000\u51fa\u4e86\u5bbf\u820d\u697c\uff0c\u8bb8\u7ef4\u773c\u775b\u76ef\u7740\u5f20\u606a\uff0c\u5567\u5567\u5482\u7740\u5634\uff0c\u4e00\u8138\u7684\u4e0d\u53ef\u601d\u5f02\u3002\u76f8\u6bd4\u8bb8\u601d\u7684\u5a07\u8273\u59a9\u5a9a\uff0c\u8bb8\u7ef4\u8eab\u6750\u6709\u4e9b\u7626\u5f31\uff0c\u6e05\u79c0\u660e\u4e3d\uff0c\u76f8\u5f53\u5e72\u51c0\u7684\u611f\u89c9\uff0c\u4e0e\u5979\u540c\u5bbf\u820d\u7684\u6c5f\u9edb\u513f\uff0c\u4e00\u8138\u7684\u67d4\u5f31\u5a07\u5ae9\uff0c\u8ba9\u4eba\u4e0d\u582a\u601c\u60dc\uff0c\u8981\u4e0d\u662f\u8bb8\u601d\u3001\u8bb8\u7ef4\u5728\u573a\uff0c\u8fd9\u6837\u7684\u5973\u5b69\u5b50\u4f1a\u8ba9\u4eba\u5fcd\u4e0d\u4f4f\u51fa\u8a00\u8c03\u620f\u7684\u3002\u4e09\u4e2a\u5927\u7f8e\u5973\uff0c\u52a0\u4e0a\u6797\u51b0\u3001\u4ee4\u5c0f\u71d5\u4e24\u4e2a\u8fd8\u7b97\u4e0d\u9519\u7684\u966a\u886c\uff0c\u7ed9\u56f4\u5728\u5bbf\u820d\u7684\u7537\u751f\u6781\u5927\u7684\u9707\u61be\uff0c\u90fd\u6068\u4e0d\u5f97\u5c06\u773c\u775b\u4e22\u8fc7\u6765\uff0c\u5f20\u606a\u90fd\u80fd\u611f\u89c9\u5230\u90a3\u4e9b\u98d8\u5230\u81ea\u5df1\u8eab\u4e0a\u7684\u773c\u795e\uff0c\u662f\u90a3\u6837\u7684\u5fcc\u6068\u4ea4\u52a0\uff0c\u611f\u89c9\u8fd8\u4e0d\u662f\u4e00\u822c\u7684\u723d\u3002</p><p>\u3000\u3000\u8bb8\u601d\u8dd1\u5230\u8def\u8fb9\u5c06\u8f66\u5f00\u8fc7\u6765\uff0c\u5f20\u606a\u7740\u770b\u4e00\u4e2a\u7537\u5b69\u5b50\u4ece\u4e1c\u8fb9\u8d70\u8fc7\u6765\uff0c\u8fb9\u8d70\u8fb9\u7b11\u7740\u8bf4\uff1a\u201c207\u7f8e\u5973\u96c6\u4f53\u51fa\u5de1\u554a\uff0c\u5c0f\u5fc3\u5f15\u8d77\u4e1c\u5927\u7684\u9a9a\u4e71\u3002\u201d</p><p>\u3000\u3000\u201c\u6b7b\u53bb\uff0c\u600e\u4e48\u73b0\u5728\u624d\u6765\uff1f\u201d\u4ee4\u5c0f\u71d5\u4ecb\u7ecd\u5979\u7537\u670b\u53cb\u6731\u5c0f\u519b\u7ed9\u5f20\u606a\u8ba4\u8bc6\uff0c\u662f\u653f\u6cd5\u7cfb\u5927\u56db\u7684\u5b66\u751f\uff0c\u6bd4\u5f20\u606a\u8fd8\u7565\u77ee\u4e00\u4e9b\uff0c\u76ae\u80a4\u9edd\u9ed1\uff0c\u4f46\u8138\u5f62\u8fd8\u7b97\u8010\u770b\uff0c\u4ed6\u626d\u5934\u770b\u89c1\u8bb8\u601d\u5f00\u7740\u5965\u8fea\u9760\u8fc7\u6765\uff0c\u773c\u775b\u4eae\u4e86\u4eae\uff1a\u201c\u9760\uff0c\u8fd9\u8f66\u725b\u53c9\u554a\u3002\u201d</p><p>\u3000\u3000\u8bb8\u7ef4\u5bf9\u8f66\u6ca1\u6709\u6982\u5ff5\uff0c\u5bf9\u8f66\u724c\u66f4\u6ca1\u6709\u6982\u5ff5\uff0c\u5149\u987e\u7740\u62c9\u4eba\u966a\u7740\u53bb\u901b\u8857\uff0c\u6ca1\u60f3\u5230\u8f66\u5b50\u91cc\u6324\u8fd9\u4e48\u591a\uff0c\u90a3\u7edd\u5bf9\u662f\u8d85\u8f7d\u3002</p><p>\u3000\u3000\u8bb8\u601d\u63a8\u5f00\u95e8\u8f66\u4e0b\u6765\uff0c\u8ba9\u8bb8\u7ef4\u9886\u4e24\u4e2a\u4eba\u5750\u51fa\u79df\u8f66\u5230\u5e02\u4e2d\u5fc3\u6c47\u5408\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6210\u719f\u8273\u4e3d\u7684\u5bb9\u989c\uff0c\u5df2\u7ecf\u76f8\u5f53\u6709\u9707\u61be\u529b\u4e86\uff0c\u52a0\u4e0a\u5979\u4ece\u5965\u8fea\u8f66\u91cc\u51fa\u6765\uff0c\u6731\u5c0f\u519b\u6123\u4e86\u597d\u4e00\u4f1a\u513f\u624d\u56de\u8fc7\u795e\uff0c\u542c\u5230\u8bb8\u601d\u8ba9\u8bb8\u7ef4\u9886\u4eba\u5750\u51fa\u79df\u8f66\uff0c\u8be7\u5f02\u7684\u8bf4\uff1a\u201c\u5965\u8fea\u8f66\u7a7a\u95f4\u5927\uff0c\u5927\u5bb6\u90fd\u6ee1\u7626\u7684\uff0c\u6324\u4e00\u6324\u5750\u5f97\u4e0b\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e0d\u77e5\u9053\u8d85\u8f7d\uff1f\u201d\u4ee4\u5c0f\u71d5\u6a2a\u4ed6\u4e00\u773c\u3002</p><p>\u3000\u3000\u6731\u5c0f\u519b\u9f3b\u8154\u91cc\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5f00\u8fd9\u8f66\u8fd8\u6015\u8d85\u8f7d\uff1f\u6211\u5012\u60f3\u770b\u770b\u54ea\u4e2a\u4ea4\u8b66\u6562\u6321\u8fd9\u90e8\u8f66\uff01\u201d</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u8bb8\u7ef4\u7591\u60d1\u7684\u770b\u4e86\u770b\u5f20\u606a\uff0c\u770b\u5f97\u51fa\uff0c\u6c5f\u9edb\u513f\u3001\u6797\u51bb\u3001\u4ee4\u5c0f\u71d5\u773c\u775b\u91cc\u90fd\u6709\u7591\u95ee\uff0c\u5979\u4eec\u90fd\u6653\u5f97\u8bb8\u7ef4\u7684\u5bb6\u5883\uff0c\u4e5f\u76f4\u63a5\u770b\u7740\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u60f3\u5230\u6731\u5c0f\u519b\u4f1a\u5bf9\u519b\u724c\u719f\u6089\uff0c\u5c31\u7b97\u6709\u7279\u6743\u641e\u5230\u8fd9\u4e00\u7c7b\u519b\u724c\uff0c\u4e5f\u4e0d\u4f1a\u820d\u5f97\u7ed9\u516c\u53f8\u7684\u8f66\u7528\uff0c\u76f4\u63a5\u8bf4\u662f\u516c\u53f8\u7684\u8f66\uff0c\u4e0d\u4e00\u5b9a\u80fd\u8ba9\u4ed6\u4eec\u76f8\u4fe1\uff0c\u8146\u7740\u8138\u8bf4\uff1a\u201c\u5bb6\u91cc\u7684\u8f66\uff0c\u5f00\u51fa\u6765\u73a9\u7684\u3002\u201d</p><p>\u3000\u3000\u6731\u5c0f\u519b\u521a\u521a\u542c\u4ee4\u5c0f\u71d5\u4ecb\u7ecd\u5f20\u606a\uff0c\u4e5f\u76f4\u63a5\u5c06\u4ed6\u5f53\u6210\u521a\u8bfb\u9ad8\u4e2d\u7684\u5c0f\u5b69\uff0c\u6ca1\u5927\u5728\u610f\uff0c\u542c\u5230\u8fd9\u53e5\u8bdd\uff0c\u6a2a\u7740\u624b\u4f38\u8fc7\u6765\u63fd\u8fc7\u5f20\u606a\u7684\u80a9\u8180\uff0c\u4e00\u8138\u7684\u4eb2\u70ed\uff1a\u201c\u5c0f\u5144\u5f1f\uff0c\u8fd9\u8f66\u8ba9\u6211\u8fc7\u4e00\u628a\u624b\u3002\u201d</p><p>\u3000\u3000\u4e5d\u56db\u5e74\u7684\u5728\u6821\u5927\u5b66\u751f\uff0c\u80fd\u62ff\u5230\u9a7e\u8bc1\u7684\u4eba\uff0c\u4e00\u822c\u6765\u8bf4\u90fd\u662f\u5bb6\u5883\u4e0d\u9519\u7684\uff0c\u5fc3\u60f3\u4ed6\u5f00\u8f66\u7684\u8bdd\uff0c\u81ea\u5df1\u5c31\u6709\u673a\u4f1a\u8ddf\u7f8e\u5973\u4eec\u6324\u4e00\u6324\uff0c\u52c9\u4e3a\u5176\u96be\u7684\u70b9\u4e86\u70b9\u5934\uff0c\u770b\u8bb8\u519b\u94bb\u8fdb\u8f66\uff0c\u8fc5\u901f\u6253\u5f00\u8f66\u95e8\uff0c\u7a9c\u5230\u540e\u5ea7\u4e0a\uff0c\u6ca1\u60f3\u5230\u8bb8\u7ef4\u624b\u6401\u5728\u8f66\u95e8\uff0c\u5934\u63a2\u8fdb\u6765\u76ef\u7740\u4ed6\uff1a\u201c\u4f60\u5750\u540e\u9762\uff0c\u6211\u4eec\u51e0\u4e2a\u5973\u5b69\u5b50\u600e\u4e48\u6324\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u6015\u4f60\u4eec\u6709\u4eba\u6655\u8f66\u2026\u2026\u201d\u5f20\u606a\u65e0\u8f9c\u7684\u770b\u7740\u8bb8\u7ef4\u3002</p><p>\u3000\u3000\u8ba9\u5f20\u606a\u8fd9\u4e48\u4e00\u8bf4\uff0c\u4ee4\u5c0f\u71d5\u62a2\u7740\u53bb\u5750\u526f\u9a7e\u9a76\u5e2d\uff0c\u534a\u4e2a\u8eab\u5b50\u94bb\u8fdb\u8f66\u91cc\uff0c\u5934\u8fd8\u5728\u5916\u9762\u5bf9\u8bb8\u7ef4\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u6655\u8f66\uff0c\u8ba9\u4f60\u4eec\u8ddf\u5c0f\u5e05\u54e5\u6324\u4e00\u6324\uff0c\u4e0d\u8981\u4e71\u5403\u4eba\u5bb6\u8c46\u8150\u2026\u2026\u201d</p><p>\u3000\u3000\u8bb8\u7ef4\u94bb\u8fdb\u8eab\u6765\uff0c\u5c06\u5f20\u606a\u5f80\u91cc\u63a8\u4e86\u63a8\uff0c\u77aa\u773c\u770b\u7740\u8bf4\uff1a\u201c\u5c0f\u5c41\u5b69\u6562\u52a8\u4ec0\u4e48\u8272\u5fc3\uff0c\u5c0f\u5fc3\u6211\u6536\u62fe\u4f60\u3002\u201d\u5c06\u4ed6\u7684\u8eab\u5b50\u538b\u8d34\u5728\u53e6\u4e00\u4fa7\u7684\u8f66\u95e8\u4e0a\uff0c\u626d\u56de\u5934\u5bf9\u6c5f\u9edb\u513f\u8bf4\uff1b\u201c\u9edb\u513f\uff0c\u4f60\u5750\u6211\u8eab\u4e0a\u6765\u3002\u201d</p></article>"}}}]);
