(window.webpackJsonp=window.webpackJsonp||[]).push([[101],{124:function(p){p.exports={chapterTitle:null,title:"\u7b2c101\u7ae0 \u4f60\u7b97\u5e2e\u6211\u505a\u4e8b",content:"<article><h2>\u7b2c101\u7ae0 \u4f60\u7b97\u5e2e\u6211\u505a\u4e8b</h2><p>\u3000\u3000\u5f20\u606a\u8ba9\u5468\u6587\u658c\u5148\u9001\u8bb8\u601d\u56de\u5bb6\u4f11\u606f\uff0c\u968f\u540e\u4ed6\u8d70\u8fdb\u848b\u8587\u7684\u529e\u516c\u5ba4\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u5750\u5728\u848b\u8587\u7684\u5bf9\u9762\uff0c\u4ed6\u4eec\u6b64\u65f6\u7684\u773c\u795e\u6e05\u4eae\u800c\u6709\u795e\uff0c\u795e\u60c5\u4e13\u6ce8\uff0c\u60f3\u5fc5\u662f\u7ed9\u848b\u8587\u900f\u9732\u51fa\u6765\u7684\u7ec6\u8282\u6240\u5438\u5f15\u4f4f\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u770b\u89c1\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u7a0d\u7a0d\u72b9\u8c6b\u4e86\u4e00\u4f1a\u513f\uff0c\u4e0d\u7ea6\u800c\u540c\u90fd\u4e0b\u610f\u8bc6\u7684\u6b20\u8d77\u8eab\u5b50\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u7ee7\u7eed\u3002\u201d\u5f20\u606a\u8ba9\u848b\u8587\u7ee7\u7eed\u5411\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u4e24\u4eba\u4ecb\u7ecd\u7b56\u5212\u6848\uff0c\u89c1\u529e\u516c\u5ba4\u91cc\u6ca1\u6709\u591a\u4f59\u7684\u6905\u5b50\uff0c\u4ed6\u4ece\u5916\u9762\u62c9\u4e86\u5f20\u6905\u5b50\u5750\u8fdb\u6765\uff0c\u53cc\u624b\u62b1\u80f8\u770b\u7740\u4ed6\u4eec\u4e09\u4eba\u7ee7\u7eed\u4ea4\u8c08\u996e\u98df\u5e7f\u573a\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u5f20\u606a\u6700\u521d\u53ea\u662f\u5f88\u7c97\u7565\u7684\u5199\u4e86\u4e00\u6279\u8ba1\u5212\u4e66\uff0c\u848b\u8587\u8fd9\u65f6\u5019\u8c08\u7684\u5185\u5bb9\u5177\u4f53\u591a\u4e86\uff0c\u6bd5\u7adf\u5f20\u606a\u5728\u4e13\u4e1a\u4e0a\u7684\u89c1\u8bc6\u6bd4\u4e0d\u4e0a\u4e13\u4e1a\u4eba\u58eb\u3002\u8bf7\u8bbe\u8ba1\u9662\u5728\u4e00\u5468\u65f6\u95f4\u91cc\u5306\u5fd9\u505a\u51fa\u7684\u8bbe\u8ba1\u56fe\u51cc\u4e71\u7684\u90fd\u94fa\u5728\u684c\u4e0a\uff0c\u5bf9\u8bbe\u8ba1\u56fe\u592a\u6280\u672f\u6027\u7684\u7ec6\u8282\uff0c\u5728\u5ea7\u7684\u8c01\u4e5f\u4e0d\u5173\u5fc3\uff0c\u5728\u5ea7\u7684\u56db\u4eba\u66f4\u5173\u5fc3\u65b0\u5149\u9020\u7eb8\u5382\u8001\u5382\u6539\u5efa\u4e4b\u540e\u7684\u6548\u679c\u4ee5\u53ca\u6240\u8981\u82b1\u8d39\u7684\u6539\u5efa\u6210\u672c\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8f6c\u8fc7\u8eab\u6765\u6ce8\u89c6\u7740\u5f20\u606a\uff0c\u76ee\u5149\u5728\u4ed6\u8138\u4e0a\u505c\u7559\u4e86\u5927\u6982\u6709\u4e94\u79d2\u949f\uff0c\u76ee\u5149\u9510\u5229\uff0c\u60f3\u8981\u770b\u5230\u5f20\u606a\u5e74\u8f7b\u5916\u8868\u4e0b\u85cf\u7740\u600e\u4e48\u667a\u6167\u3002</p><p>\u3000\u3000\u201c\u6211\u73b0\u5728\u6653\u5f97\u4f60\u4e3a\u4ec0\u4e48\u4f1a\u8eb2\u7740\u6211\u4eec\uff1f\u201d\u90b5\u81f3\u521a\u521a\u624d\u53ea\u662f\u5c06\u8138\u4e0a\u7684\u51b7\u6de1\u8868\u60c5\u63a9\u9970\u6389\uff0c\u4ed6\u6709\u4ed6\u7684\u9a84\u50b2\uff0c\u5c31\u7b97\u4e3a\u4e86\u5229\u76ca\u5c48\u819d\u4f4e\u5934\uff0c\u4ed6\u4e5f\u5f88\u5c11\u4ece\u5fc3\u91cc\u4f69\u670d\u522b\u4eba\uff0c\u6b64\u65f6\u7684\u4ed6\uff0c\u773c\u775b\u5374\u5b8c\u5168\u6ca1\u6709\u51b7\u6de1\uff0c\u201c\u4e0d\u770b\u8fd9\u51e0\u5929\u7684\u6d77\u5dde\u65e5\u62a5\uff0c\u65e0\u6cd5\u7406\u89e3\u4f60\u8981\u5c06\u8fd9\u996e\u98df\u5e7f\u573a\u8fde\u540c\u56db\u51e4\u6865\u5851\u9020\u6210\u57ce\u5357\u533a\u7279\u5f81\u7684\u7528\u610f\u3002\u7edd\u5bf9\u662f\u795e\u6765\u4e4b\u7b14\uff01\u201d\u90b5\u81f3\u521a\u7528\u5f88\u91cd\u7684\u8bed\u6c14\u8bf4\u51fa\u5bf9\u65b9\u6848\u7684\u8d5e\u53f9\uff0c\u8f6c\u800c\u8bed\u6c14\u4e00\u8f6c\uff0c\u201c\u4f46\u662f\u4f60\u4eec\u65e0\u6cd5\u5426\u8ba4\uff0c\u4f60\u4eec\u5bf9\u9910\u996e\u4e1a\u5e76\u4e0d\u719f\u6089\uff0c\u6240\u4ee5\u6574\u4e2a\u7b56\u5212\u4e66\u7684\u7ec6\u8282\u90e8\u5206\u8fd8\u4e0d\u591f\u5c3d\u5584\u5c3d\u7f8e\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u4ed6\u8ddf\u848b\u8587\u3001\u4ee5\u81f3\u848b\u8587\u627e\u6765\u7684\u8bbe\u8ba1\u9662\uff0c\u5bf9\u9910\u996e\u4e1a\u90fd\u4e0d\u719f\u6089\uff0c\u4f55\u51b5\u7ed9\u848b\u8587\u7684\u65f6\u95f4\u5f88\u7d27\uff0c\u4e5f\u4e0d\u53ef\u80fd\u5728\u51fa\u7a3f\u4e4b\u524d\u4e0e\u4ece\u4e8b\u9910\u996e\u4e1a\u7684\u4eba\u58eb\u8fdb\u884c\u5145\u5206\u7684\u6c9f\u901a\uff0c\u4f46\u662f\u770b\u5230\u90b5\u81f3\u521a\u80f8\u6709\u6210\u7af9\u7684\u6837\u5b50\uff0c\u5fc3\u60f3\u4ed6\u5927\u6982\u5728\u60f3\u81ea\u5df1\u4f1a\u5f00\u53e3\u5411\u4ed6\u8ba8\u6559\u3002</p><p>\u3000\u3000\u201c\u90b5\u7ecf\u7406\u662f\u90a3\u79cd\u4e0d\u8f7b\u6613\u6c14\u59a5\u7684\u4eba\uff0c\u7b56\u5212\u4e66\u7684\u6458\u8981\u4ee5\u53ca\u6548\u679c\u56fe\u6700\u8fdf\u540e\u5929\u4f1a\u5728\u6d77\u5dde\u65e5\u62a5\u4e0a\u53d1\u8868\uff0c\u6211\u8fd8\u6101\u627e\u4e0d\u5230\u5e2e\u6211\u5b8c\u5584\u7ec6\u8282\u7684\u4eba\u5417\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u90b5\u81f3\u521a\u6b64\u65f6\u7684\u4fe1\u5fc3\u53ef\u4e0d\u5229\u4e8e\u81ea\u5df1\u62db\u63fd\u4ed6\uff0c\u201c\u4e0d\u8fc7\u6211\u4f1a\u8ba4\u771f\u8003\u8651\u90b5\u7ecf\u7406\u7684\u5efa\u8bae\u3002\u201d</p><p>\u3000\u3000\u201c\u6574\u4e2a\u7b56\u5212\u4e66\u6700\u6709\u5546\u4e1a\u4ef7\u503c\u90e8\u5206\u5c31\u662f\u996e\u98df\u5e7f\u573a\u4e86\uff0c\u65e0\u8bba\u662f\u9020\u7eb8\u5382\u8fd8\u662f\u9526\u6e56\u516c\u53f8\uff0c\u90fd\u4e0d\u662f\u505a\u9910\u996e\u51fa\u8eab\u7684\uff0c\u53ef\u4e0d\u53ef\u5c06\u9879\u76ee\u7684\u8fd9\u90e8\u4efd\u8f6c\u8ba9\u7ed9\u6211\uff0c\u9664\u4e86\u79df\u91d1\u4e4b\u5916\uff0c\u6211\u53ef\u4ee5\u5411\u9526\u6e56\u516c\u53f8\u989d\u5916\u652f\u4ed8\u4e00\u767e\u4e07\u3002\u201d\u90b5\u81f3\u521a\u773c\u775b\u70af\u70af\u6709\u795e\u7684\u76ef\u7740\u5f20\u606a\uff0c\u8feb\u5207\u7684\u95ee\uff1a\u201c\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u542c\u4e86\u90b5\u81f3\u521a\u7684\u8fd9\u4e9b\u8bdd\uff0c\u848b\u8587\u5fc3\u91cc\u4e00\u8df3\uff0c\u539f\u6765\u94b1\u8fd9\u4e48\u597d\u8d5a\u3002</p><p>\u3000\u3000\u6362\u4f5c\u522b\u4eba\uff0c\u8bf4\u4e0d\u5b9a\u5c31\u4f1a\u5fc3\u52a8\u4e86\u3002\u5f20\u606a\u4e0d\u4f1a\uff0c\u4ed6\u7684\u76ee\u6807\u4e0d\u662f\u996e\u98df\u5e7f\u573a\uff0c\u4ed6\u772f\u7740\u773c\u775b\u7b11\u4e86\u8d77\u6765\uff0c\u5fc3\u60f3\uff1a\u90b5\u81f3\u521a\u8fd9\u4eba\u679c\u7136\u4e0e\u4f20\u95fb\u4e2d\u6beb\u65e0\u4e8c\u81f4\u3002\u5f20\u606a\u6562\u80af\u5b9a\u73b0\u5728\u7684\u90b5\u81f3\u521a\u7838\u9505\u5356\u94c1\u5dee\u4e0d\u591a\u80fd\u51d1\u51fa\u4e00\u767e\u4e07\u7684\u8eab\u5bb6\uff0c\u4ed6\u5c31\u6562\u62ff\u4e00\u767e\u4e07\u6362\u8fd9\u4e2a\u9879\u76ee\uff0c\u9b44\u529b\u7684\u786e\u4e0d\u5c0f\u3002</p><p>\u3000\u3000\u201c\u4f60\u51c6\u5907\u6bcf\u5e74\u652f\u4ed8\u591a\u5c11\u79df\u91d1\u7ed9\u9020\u7eb8\u5382\u2026\u2026\u201d\u5f20\u606a\u8fd8\u662f\u60f3\u63a2\u63a2\u4ed6\u7684\u5e95\u7ec6\u3002</p><p>\u3000\u3000\u201c\u9020\u7eb8\u5382\u9694\u58c1\u6e90\u7fd4\u6e9c\u51b0\u573a\u662f\u6d77\u5dde\u6700\u5927\u7684\u6e9c\u51b0\u573a\uff0c\u6211\u53ef\u4ee5\u5728\u4ed6\u4eec\u79df\u91d1\u6807\u51c6\u4e0a\u518d\u63d0\u9ad8\u4e00\u534a\u2026\u2026\u201d</p><p>\u3000\u3000\u848b\u8587\u6697\u6697\u4e4d\u820c\uff0c\u6574\u4e2a\u8001\u5382\u79df\u51fa\u53bb\uff0c\u6bcf\u5e74\u80fd\u6536\u56de\u4e24\u767e\u4e07\u7684\u79df\u91d1\u3002\u5728\u7b7e\u8ba2\u627f\u5305\u5408\u540c\u4e4b\u524d\uff0c\u5468\u590d\u63d0\u4f9b\u751f\u4ea7\u8ba1\u5212\u4e66\uff0c\u53ea\u8003\u8651\u8001\u5382\u6bcf\u5e74\u80fd\u4ea7\u751f\u4e03\u516b\u5341\u4e07\u7684\u6536\u76ca\uff0c\u4e0d\u6653\u5f97\u5468\u590d\u542c\u5230\u90b5\u81f3\u521a\u7684\u8bdd\u6709\u4ec0\u4e48\u53cd\u5e94\u3002</p><p>\u3000\u3000\u848b\u8587\u6bd4\u8f83\u503e\u5411\u63a5\u53d7\u90b5\u81f3\u521a\u7684\u63d0\u8bae\uff0c\u9526\u6e56\u5b8c\u5168\u662f\u58f3\u516c\u53f8\uff0c\u9020\u7eb8\u5382\u6ca1\u4eba\u6709\u64cd\u4f5c\u8fd9\u4e2a\u9879\u76ee\u7684\u80fd\u529b\uff0c\u5fc3\u60f3\u5f20\u606a\u8fd8\u5728\u8bfb\u4e66\uff0c\u4e0d\u53ef\u80fd\u6709\u8db3\u591f\u7684\u7cbe\u529b\u53bb\u8fd0\u4f5c\u8fd9\u4e2a\u9879\u76ee\u3002</p><p>\u3000\u3000\u201c\u6e90\u7fd4\u6e9c\u51b0\u573a\u662f\u4e0d\u662f\u6d77\u5dde\u6700\u5927\uff0c\u6211\u4e0d\u5927\u6e05\u695a\uff0c\u4f46\u662f\u6e90\u7fd4\u6e9c\u51b0\u573a\u7edd\u5bf9\u4e0d\u662f\u6700\u597d\u7684\uff0c\u9020\u7eb8\u5382\u91cc\u6563\u53d1\u51fa\u6765\u7684\u5473\u9053\u5f88\u523a\u9f3b\uff0c\u5c31\u7b97\u8fdb\u5165\u6e9c\u51b0\u573a\u5185\uff0c\u4e5f\u4e0d\u5927\u597d\u53d7\uff0c\u5728\u8fdb\u884c\u996e\u98df\u5e7f\u573a\u9879\u76ee\u4e4b\u524d\uff0c\u6211\u8fd8\u60f3\u5c06\u7fd4\u6e90\u6e9c\u51b0\u573a\u5403\u4e0b\u6765\u5462\uff01\u201d\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u505c\u4e86\u4e00\u4e0b\uff0c\u89c2\u5bdf\u90b5\u81f3\u521a\u7684\u8138\u8272\u5982\u5e38\uff0c\u6ca1\u6709\u7ed9\u63ed\u7834\u7684\u5c34\u5c2c\uff0c\u5546\u4e1a\u8c08\u5224\u65ad\u4fbf\u662f\u5982\u6b64\uff0c\u5b8c\u5168\u4e0d\u5fc5\u8981\u81ea\u5df1\u5148\u5fc3\u865a\uff0c\u5f20\u606a\u77e5\u9053\u80fd\u4f30\u7b97\u51fa\u90b5\u81f3\u521a\u7684\u8eab\u5bb6\uff0c\u81ea\u7136\u77e5\u9053\u600e\u4e48\u634f\u4f4f\u4ed6\u7684\u4e03\u5bf8\uff0c\u201c\u90b5\u7ecf\u7406\u660e\u5929\u80fd\u5c06\u4e00\u767e\u4e07\u73b0\u91d1\u4e22\u5230\u6211\u7684\u9762\u524d\uff0c\u6211\u53ef\u4ee5\u8ba4\u771f\u8003\u8651\u4f60\u7684\u5efa\u8bae\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u773c\u795e\u95ea\u4e86\u4e00\u4e0b\uff0c\u51e0\u4e4e\u8981\u8eb2\u5f00\u5f20\u606a\u7684\u773c\u795e\uff0c\u5434\u5929\u5b9d\u542c\u4e86\u4e5f\u6697\u6697\u5fc3\u60ca\uff0c\u4ed6\u90fd\u6ca1\u6709\u770b\u5230\u90b5\u81f3\u521a\u4e0e\u4eba\u4ea4\u950b\u6709\u5904\u4e8e\u88ab\u52a8\u7684\u65f6\u5019\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u60f3\u903c\u4ed6\u592a\u6025\uff0c\u8fd9\u79cd\u4eba\u4e0d\u662f\u968f\u968f\u4fbf\u4fbf\u5c31\u80fd\u8bad\u670d\u7684\uff0c\u62ff\u5229\u76ca\u5148\u5c06\u4ed6\u5957\u4f4f\u5c31\u53ef\u4ee5\u4e86\uff0c\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u4f1a\u53e6\u6210\u7acb\u4e00\u5bb6\u516c\u53f8\u638c\u63e1\u8fd9\u4e2a\u9879\u76ee\u7684\u7ecf\u8425\u6743\uff0c\u90b5\u7ecf\u7406\u53ef\u4ee5\u62ff\u56db\u5341\u4e07\u6765\u636240\uff05\u7684\u80a1\u4efd\uff0c\u516c\u53f8\u59d4\u6258\u4f60\u7ba1\u7406\u2026\u2026\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u95ee\u9053\uff1a\u201c\u5730\u79df\u5462\uff1f\u9526\u6e56\u516c\u53f8\u5e0c\u671b\u652f\u4ed8\u9020\u7eb8\u5382\u591a\u5c11\u5730\u79df\u5408\u9002\uff1f\u201d</p><p>\u3000\u3000\u4ed6\u4e0d\u6653\u5f97\u9526\u6e56\u516c\u53f8\u627f\u5305\u9020\u7eb8\u5382\u7684\u5f62\u5f0f\uff0c\u56fd\u5185\u7684\u627f\u5305\u7ecf\u8425\u4e00\u822c\u8bf4\u6765\u90fd\u662f\u6bcf\u5e74\u4ea4\u7f34\u56fa\u5b9a\u7684\u627f\u5305\u8d39\u7528\uff0c\u6240\u4ee5\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u79df\u91d1\u8d8a\u9ad8\u8d8a\u6709\u5229\u3002</p><p>\u3000\u3000\u5c31\u6015\u5f20\u606a\u63d0\u51fa\u4e00\u4e2a\u96be\u4ee5\u63a5\u53d7\u7684\u5730\u79df\uff0c\u9879\u76ee\u516c\u53f8\u5c06\u65e0\u5229\u53ef\u56fe\uff0c\u62ff\u56db\u5341\u4e07\u6362\u9879\u76ee\u516c\u53f840\uff05\u7684\u80a1\u6743\uff0c\u90a3\u624d\u53eb\u6709\u75c5\u5462\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u5e0c\u671b\u4ece\u996e\u98df\u6587\u573a\u7684\u7ecf\u8425\u6743\u652b\u53d6\u591a\u5c11\u5229\u6da6\uff0c\u53ea\u5e0c\u671b\u901a\u8fc7\u8fd9\u79cd\u65b9\u5f0f\uff0c\u53d8\u76f8\u7684\u5c06\u90b5\u81f3\u521a\u7b3c\u7edc\u7684\u9526\u6e56\u540d\u4e0b\uff0c\u5f53\u7136\u4e5f\u8981\u7ed9\u90b5\u81f3\u521a\u4e9b\u751c\u5934\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff1a\u201c\u4e0d\u59a8\u544a\u8bc9\u90b5\u7ecf\u7406\uff0c\u9526\u6e56\u6bcf\u5e74\u53ef\u4ee5\u62ff\u8d70\u9020\u7eb8\u538260\uff05\u7684\u76c8\u6da6\uff0c\u90b5\u7ecf\u7406\u5e0c\u671b\u6211\u5c06\u5730\u79df\u5b9a\u5728\u4ec0\u4e48\u4f4d\u7f6e\u4e0a\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u8fdf\u7591\u4e86\u3002\u7ec4\u5efa\u65b0\u7684\u516c\u53f8\u6700\u7ec8\u638c\u63e1\u996e\u98df\u5e7f\u573a\u7684\u7ecf\u8425\u6743\uff0c\u9526\u6e56\u4e5f\u53ea\u8981\u6c42\u638c\u63e160\uff05\u7684\u80a1\u6743\uff0c\u4e5f\u5c31\u662f\u5411\u9020\u7eb8\u5382\u652f\u4ed8\u79df\u91d1\u65e0\u8bba\u662f\u591a\u662f\u5c11\uff0c\u5bf9\u9526\u6e56\u90fd\u6ca1\u6709\u4ec0\u4e48\u5f71\u54cd\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u90b5\u81f3\u521a\u4e0d\u8bf4\u8bdd\uff0c\u7b11\u4e86\u8d77\u6765\uff1a\u201c\u6bcf\u5e74\u4e09\u767e\u4e07\u79df\u91d1\u662f\u5404\u65b9\u90fd\u53ef\u4ee5\u63a5\u53d7\u7684\uff0c\u9020\u7eb8\u5382\u4f1a\u62ff\u51fa\u4e00\u90e8\u5206\u5730\u79df\uff0c\u5bf9\u8001\u5382\u8fdb\u884c\u6539\u9020\uff0c\u90b5\u7ecf\u7406\u62ff\u51fa\u6765\u7684\u56db\u5341\u4e07\uff0c\u9526\u6e56\u4e5f\u4e0d\u4f1a\u585e\u8fdb\u81ea\u5df1\u7684\u8170\u5305\uff0c\u53ef\u4ee5\u4f5c\u4e3a\u524d\u671f\u7684\u8fd0\u8425\u8d39\u7528\u3002\u90b5\u7ecf\u7406\uff0c\u4f60\u89c9\u5f97\u6211\u7684\u5efa\u8bae\u5982\u4f55\uff1f\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u6ca1\u6709\u8bf4\u8bdd\uff0c\u5f20\u606a\u7684\u6761\u4ef6\u4e0d\u7b97\u82db\u523b\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u6709\u4e9b\u6177\u6168\uff0c\u6b63\u56e0\u4e3a\u8fd9\u6837\uff0c\u90b5\u81f3\u521a\u5012\u6709\u4e9b\u72b9\u8c6b\uff1a\u201c\u6211\u4eec\u4e4b\u524d\u4f3c\u4e4e\u4e0d\u8ba4\u8bc6\uff0c\u4f60\u5c31\u8fd9\u4e48\u80af\u5b9a\u6211\u80fd\u505a\u597d\u8fd9\u4e2a\u9879\u76ee\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6837\u4e00\u6765\uff0c\u4f60\u4e5f\u7b97\u662f\u5e2e\u6211\u505a\u4e8b\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u5c31\u8fd9\u4e00\u70b9\uff0c\u6211\u6bd4\u6211\u5c0f\u53d4\u5f3a\u4e00\u4e9b\uff0c\u6211\u5c0f\u53d4\u544a\u8bc9\u6211\uff0c\u4ed6\u66fe\u5e0c\u671b\u4f60\u5230\u5b8f\u8fdc\u5e2e\u4ed6\u3002\u201d</p><p>\u3000\u3000\u90b5\u81f3\u521a\u4e0e\u5434\u5929\u5b9d\u9762\u9762\u76f8\u89d1\uff0c\u8fd9\u7b97\u54ea\u95e8\u5b50\u7406\u7531\uff0c\u592a\u5b69\u5b50\u6c14\u4e86\u5427\uff1f\u4ed6\u4eec\u4e0d\u77e5\u9053\uff0c\u5f20\u606a\u5bf9\u90b5\u81f3\u521a\u662f\u771f\u7684\u4e86\u89e3\u3002</p><p>\u3000\u3000\u90b5\u81f3\u521a\u63d0\u51fa\u8981\u8003\u8651\u4e00\u4e0b\uff0c\u5434\u5929\u5b9d\u6ca1\u6709\u53c2\u4e0e\u6574\u4e2a\u9879\u76ee\u7684\u9b44\u529b\uff0c\u81ea\u7136\u8981\u5728\u90b5\u81f3\u521a\u4e4b\u540e\u624d\u80fd\u505a\u51b3\uff0c\u4f46\u4ed6\u77e5\u9053\u4ed6\u80fd\u62ff\u5230\u7684\u5730\u79df\u7edd\u4e0d\u4f1a\u4fbf\u5b9c\u3002</p><p>\u3000\u3000\u5c06\u90b5\u81f3\u521a\u3001\u5434\u5929\u5b9d\u9001\u8d70\uff0c\u5f20\u606a\u5429\u5490\u848b\u8587\u5c06\u521a\u624d\u4e0e\u90b5\u81f3\u521a\u7684\u8c08\u8bdd\u505a\u4e00\u4efd\u5907\u5fd8\u5f55\uff0c\u4ea4\u7531\u57ce\u5357\u533a\u653f\u5e9c\u80cc\u4e66\u3002\u65e0\u8bba\u662f\u539f\u6599\u91c7\u8d2d\u3001\u4ea7\u54c1\u59d4\u6258\u9500\u552e\uff0c\u8fd8\u662f\u8981\u80fd\u6d89\u53ca\u5230\u7684\u7ecf\u8425\u6743\u8f6c\u8ba9\uff0c\u9526\u6e56\u3001\u6d77\u88d5\u3001\u6d77\u6cf0\u516c\u53f8\u80a1\u6743\u7ed3\u6784\u9519\u6742\uff0c\u662f\u5229\u76ca\u76f8\u5173\u65b9\uff0c\u6240\u6709\u7684\u4ea4\u6613\u90fd\u9700\u8981\u57ce\u5357\u533a\u653f\u5e9c\u80cc\u4e66\uff0c\u4e0d\u7136\u9526\u6e56\u5b8c\u5168\u53ef\u4ee5\u5c06\u9020\u7eb8\u5382\u7684\u5e94\u5f97\u5229\u6da6\u90fd\u505a\u5230\u76f8\u5173\u65b9\u53bb\uff0c\u8ba9\u57ce\u5357\u533a\u653f\u5e9c\u4e00\u5206\u94b1\u90fd\u62ff\u4e0d\u5230\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u996e\u98df\u5e7f\u573a\u7b56\u5212\u4e66\u6458\u8981\u4ee5\u53ca\u6548\u679c\u56fe\u5c31\u5728\u6d77\u5dde\u65e5\u62a5\u4e0a\u520a\u767b\uff0c\u5b8b\u57f9\u660e\u7684\u5fc3\u5207\u53ef\u89c1\u4e00\u6591\u3002\u7b56\u5212\u4e66\u6458\u8981\u63d0\u51fa\u4ee5\u505c\u4ea7\u8001\u5382\u4e3a\u57fa\u7840\uff0c\u6539\u5efa\u590d\u53e4\u5efa\u7b51\u98ce\u683c\u7684\u996e\u98df\u4f11\u95f2\u5e7f\u573a\uff0c\u5e76\u5728\u8001\u5382\u4e0e\u56db\u51e4\u6865\u4e4b\u95f4\u3001\u6668\u9e23\u5bbe\u9986\u4e0e\u56db\u51e4\u6865\u4e4b\u95f4\u7684\u7a7a\u9614\u5730\u5efa\u9020\u5c0f\u578b\u7684\u5e02\u6c11\u4f11\u95f2\u7eff\u5730\uff0c\u6539\u5584\u56db\u51e4\u6865\u5c40\u90e8\u81ea\u7136\u73af\u5883\uff0c\u4f7f\u5f97\u56db\u51e4\u6865\u4e0e\u996e\u98df\u6587\u573a\u6210\u4e3a\u57ce\u5357\u7279\u5f81\u3002</p><p>\u3000\u3000\u6587\u7ae0\u7531\u57ce\u5357\u533a\u653f\u5e9c\u529e\u4e0e\u65b0\u5149\u9020\u7eb8\u5382\u529e\u8054\u5408\u7f72\u540d\uff0c\u90b5\u81f3\u521a\u770b\u5230\u8fd9\u7bc7\u6587\u7ae0\u5728\u9910\u996e\u540c\u884c\u95f4\u5f15\u8d77\u7684\u53cd\u5e94\u4e4b\u540e\uff0c\u51b3\u5b9a\u63a5\u53d7\u5f20\u606a\u7684\u5efa\u8bae\u3002</p><p>\u3000\u3000\u5b8b\u57f9\u660e\u770b\u5230\u848b\u8587\u9001\u6765\u7684\u5907\u5fd8\u5f55\uff0c\u540c\u6837\u5403\u4e86\u4e00\u60ca\uff0c\u5907\u5fd8\u5f55\u4e0a\u63d0\u53ca\u7684\u5730\u79df\u9ad8\u8fbe\u4e09\u767e\u4e07\uff0c\u662f\u4ed6\u6ca1\u6709\u60f3\u5230\u7684\uff0c\u8981\u77e5\u9053\u65b0\u5149\u9020\u7eb8\u5382\u4e4b\u524d\u6bcf\u5e74\u7684\u5229\u7a0e\u8fd8\u4e0d\u8db3\u767e\u4e07\uff0c\u8fd8\u5bf9\u758f\u6e2f\u6cb3\u9020\u6210\u90a3\u4e48\u4e25\u91cd\u7684\u6c61\u67d3\u3002</p><p>\u3000\u3000\u5c31\u51ed\u8fd9\u4e00\u6761\uff0c\u5c31\u8db3\u4ee5\u8bc1\u660e\u5173\u505c\u8001\u5382\u3001\u62e9\u5730\u5efa\u65b0\u5382\u662f\u9879\u6b63\u786e\u7684\u51b3\u7b56\u3002\u5b8b\u57f9\u660e\u5f53\u5373\u5c31\u53ec\u5f00\u533a\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\uff0c\u5728\u5907\u5fd8\u5f55\u4e0a\u80cc\u4e66\uff0c\u50ac\u4fc3\u9526\u6e56\u65e9\u65e5\u4fc3\u6210\u6b64\u4e8b\uff0c\u8fd9\u53ef\u8981\u7b97\u4ed6\u5b8b\u57f9\u660e\u7684\u653f\u7ee9\uff1b\u5f20\u606a\u5219\u5e0c\u671b\u5b8b\u57f9\u660e\u5c3d\u5feb\u4fc3\u4f7f\u533a\u5e38\u59d4\u4f1a\u8bae\u901a\u8fc7\u56db\u51e4\u6865\u73af\u5883\u6539\u9020\u7684\u8bae\u9898\uff0c\u56db\u51e4\u6865\u5468\u56f4\u7684\u73af\u5883\u6539\u9020\u3001\u5efa\u8bbe\u5c0f\u578b\u7684\u4f11\u95f2\u7eff\u5730\uff0c\u662f\u996e\u98df\u5e7f\u573a\u505a\u6210\u7cbe\u54c1\u9879\u76ee\u7684\u5fc5\u8981\u5916\u90e8\u6761\u4ef6\u3002</p></article>"}}}]);
