(window.webpackJsonp=window.webpackJsonp||[]).push([[1011],{1034:function(p){p.exports={chapterTitle:null,title:"\u7b2c1008\u7ae0 \u8ba9\u5ea7",content:"<article><h2>\u7b2c1008\u7ae0 \u8ba9\u5ea7</h2><p>\u3000\u3000\u5efa\u90ba\u3001\u6d77\u5dde\u65b0\u589e\u4e94\u5bb6\u624b\u673a\u5236\u9020\u57fa\u5730\uff0c\u52a0\u4e0a\u9526\u6e56\u4e0e\u79d1\u738b\u9ad8\u79d1\u65b0\u6295\u5efa\u7684\u751f\u4ea7\u7ebf\uff0c\u4e24\u5730\u624b\u673a\u4ea7\u91cf\u5230\u660e\u5e74\u5c06\u589e\u52a0\u5230\u4e00\u5343\u4e8c\u767e\u4e07\u4ee5\u4e0a\uff0c\u65b0\u589e\u5de5\u4e1a\u4ea7\u503c\u5c06\u8fd1\u4e09\u767e\u4ebf\u3002\u624b\u673a\u4ea7\u80fd\u7684\u6025\u5267\u6269\u5f20\uff0c\u4e5f\u5c06\u5bfc\u81f4\u8f6f\u786c\u4ef6\u914d\u5957\u4ea7\u4e1a\u6295\u8d44\u7684\u6fc0\u589e\u3002</p><p>\u3000\u3000\u867d\u8bf4\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e09\u6708\u4e0b\u65ec\u653e\u5f00\u6280\u672f\u5206\u4eab\u7684\u5730\u57df\u9650\u5236\uff0c\u4f46\u662f\u6d77\u5dde\u3001\u5efa\u90ba\u6bd5\u7adf\u5360\u4e86\u4e00\u5e74\u591a\u7684\u5148\u673a\uff0c\u521d\u6b65\u5f62\u6210\u8f83\u5b8c\u6574\u7684\u624b\u673a\u914d\u5957\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u5efa\u90ba\u3001\u6d77\u5dde\u53c8\u5c06\u662f\u56fd\u5185\u6700\u5927\u7684\u624b\u673a\u5236\u9020\u57fa\u5730\uff0c\u6295\u8d44\u5546\u81ea\u7136\u8fd8\u70ed\u8877\u5728\u5efa\u90ba\u6216\u6d77\u5dde\u5efa\u8bbe\u914d\u4ef6\u5de5\u5382\uff0c\u5c31\u7b97\u62e5\u6709\u5236\u9020\u6280\u672f\u3001\u4e0d\u4f9d\u8d56\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6e2f\u53f0\u8d44\u4f01\u4e1a\u4e5f\u591a\u534a\u9009\u62e9\u5230\u5efa\u90ba\u3001\u6d77\u5dde\u5efa\u5382\u3002</p><p>\u3000\u3000\u7531\u4e8e\u9526\u6e56\u5c06\u4e3b\u8981\u7684\u751f\u4ea7\u57fa\u5730\u5efa\u5728\u6d77\u5dde\uff0c\u6d77\u5dde\u4e0e\u5efa\u90ba\u5728\u624b\u673a\u5236\u9020\u4ea7\u4e1a\u4e0a\u6240\u5360\u7684\u6bd4\u91cd\u5012\u662f\u5e73\u5206\u79cb\u8272\u3002\u8f6f\u786c\u4ef6\u914d\u5957\u4ea7\u4e1a\u4e0a\uff0c\u5efa\u90ba\u5728\u79d1\u7814\u4e0e\u4eba\u529b\u8d44\u6e90\u4e0a\u7684\u4f18\u52bf\u975e\u5e38\u660e\u663e\uff0c\u7814\u53d1\u4e2d\u5fc3\u3001\u624b\u673a\u8bbe\u8ba1\u516c\u53f8\u8fd9\u4e9b\u9ad8\u9644\u52a0\u503c\u7684\u914d\u5957\u4ea7\u4e1a\u51e0\u4e4e\u90fd\u5f80\u5efa\u90ba\u96c6\u4e2d\uff0c\u800c\u96f6\u914d\u4ef6\u5de5\u5382\u6295\u8d44\uff0c\u6d77\u5dde\u5206\u5230\u7684\u6bd4\u91cd\u5012\u4e0d\u6bd4\u5efa\u90ba\u4f4e\uff0c\u6bd5\u7adf\u597d\u4e9b\u96f6\u914d\u4ef6\u5546\u66f4\u770b\u4e2d\u7231\u8fbe\u96c6\u56e2\u8fd9\u4e2a\u8d85\u7ea7\u5ba2\u6237\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u56db\u6708\uff0c\u5df2\u7ecf\u80fd\u60f3\u8c61\u51e0\u5e74\u4e4b\u540e\u6d77\u5dde\u3001\u5efa\u90ba\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u7684\u89c4\u6a21\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e03\u516b\u5e74\u540e\uff0c\u4e5f\u5c31\u662f\u96f6\u516d\u5e74\uff0c\u4f60\u80fd\u9884\u6d4b\u5230\u90a3\u65f6\u56fd\u4ea7\u624b\u673a\u7684\u751f\u4ea7\u89c4\u6a21\uff1f\u201d\u4ece\u65b0\u5e02\u8857\u56de\u5b66\u5e9c\u5df7\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5750\u5728\u516c\u4ea4\u8f66\uff0c\u5915\u9633\u897f\u4e0b\uff0c\u91d1\u7ea2\u8272\u7684\u5149\u8f89\u7167\u8fdb\u8f66\u7a97\uff0c\u80fd\u770b\u89c1\u8bb8\u591a\u6d6e\u5c18\u5728\u5149\u67f1\u91cc\u98de\u821e\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u4e00\u4e2a\u4e3b\u8981\u4ea7\u4e1a\u65b9\u5411\u662f\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u4e0a\uff0c\u5f20\u606a\u8fd8\u662f\u7ecf\u5e38\u7684\u63d0\u9192\u9526\u6e56\u7684\u9ad8\u5c42\u4e0d\u8981\u8eb2\u5230\u89c2\u5bdf\u4e0d\u5230\u5e73\u6c11\u751f\u6d3b\u7684\u4e91\u7aef\uff0c\u4e00\u65e6\u9ad8\u5c42\u5bf9\u5e73\u6c11\u65f6\u5c1a\u90fd\u6ca1\u6709\u6e05\u9192\u7684\u8ba4\u8bc6\uff0c\u4e5f\u5c31\u4e0d\u518d\u8ba9\u4eba\u653e\u5fc3\u4e86\u3002</p><p>\u3000\u3000\u4eca\u5929\u5f20\u606a\u5374\u662f\u8c08\u5b8c\u516c\u52a1\u4e4b\u540e\uff0c\u5728\u65b0\u5e02\u8857\u4e0b\u8f66\u966a\u7fdf\u4e39\u9752\u901b\u4e86\u4e24\u4e2a\u5c0f\u65f6\u7684\u8857\u624d\u5750\u516c\u4ea4\u8f66\u5f80\u56de\u8d76\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u5df4\u78d5\u5728\u524d\u5750\u6905\u7684\u9760\u6905\u4e0a\uff0c\u989d\u5934\u62b5\u7740\u7fdf\u4e39\u9752\u67d4\u8f6f\u7684\u80cc\u810a\uff0c\u5fc3\u60f3\u8ba9\u7fdf\u4e39\u9752\u7ecf\u5e38\u5750\u516c\u4ea4\u8f66\uff0c\u4ee5\u5979\u6027\u611f\u8eab\u6750\u3001\u5a07\u7f8e\u7684\u5bb9\u989c\uff0c\u5e94\u8be5\u4f1a\u7ecf\u5e38\u9047\u5230\u516c\u4ea4\u8272\u72fc\u5427\uff0c\u4e0d\u8fc7\u8981\u662f\u54ea\u4e2a\u516c\u4ea4\u8272\u72fc\u6562\u5bf9\u7fdf\u4e39\u9752\u4e0b\u624b\uff0c\u6015\u4e5f\u662f\u5012\u4e86\u5927\u9709\u3002\u8fd9\u4f1a\u513f\uff0c\u4e2d\u95f4\u8fc7\u9053\u91cc\u7ad9\u7740\u51e0\u4e2a\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\u5c31\u65f6\u4e0d\u65f6\u7684\u62ff\u773c\u5077\u7785\u5979\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff0c\u8fd8\u8981\u8003\u6211\u554a\uff1f\u201d\u7fdf\u4e39\u9752\u56de\u5934\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5a07\u55d4\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u5427\uff01\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e03\u516b\u5e74\u540e\u554a\uff1f\u201d\u7fdf\u4e39\u9752\u624b\u6307\u62b5\u7740\u5ae3\u7ea2\u6027\u611f\u7684\u5634\u5507\uff0c\u8ba9\u5f20\u606a\u770b\u4e86\u60f3\u53bb\u54ac\u4e00\u53e3\uff0c\u201c\u4e24\u4ebf\u5230\u4e09\u4ebf\u90e8\u4e4b\u95f4\u5427\uff0c\u901a\u8baf\u8d44\u8d39\u80fd\u6301\u7eed\u964d\u4f4e\u5230\u5408\u7406\u6c34\u5e73\u7684\u8bdd\uff0c\u56fd\u5185\u79fb\u52a8\u901a\u8baf\u7528\u6237\u5dee\u4e0d\u591a\u4e5f\u80fd\u8fbe\u5230\u6b64\u6570\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u60f3\u4e5f\u5dee\u4e0d\u591a\u6b64\u6570\u5427\u3002\u201d\u5fc3\u60f3\uff1a\u7fdf\u4e39\u9752\u7684\u4f30\u7b97\u6bd4\u8d77\u771f\u5b9e\u7684\u53d1\u5c55\u6709\u4e9b\u4fdd\u5b88\u4e86\uff0c\u4f46\u5dee\u4e0d\u591a\u4e5f\u628a\u63e1\u4f4f\u4eca\u5e74\u51e0\u540e\u7684\u53d1\u5c55\u8d8b\u52bf\uff0c\u5f53\u7136\u4e86\uff0c\u8fd9\u4e5f\u4e0d\u5b8c\u5168\u662f\u7fdf\u4e39\u9752\u4e2a\u4eba\u7684\u667a\u6167\uff0c\u80cc\u540e\u6709\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u7684\u4ea7\u4e1a\u7ecf\u6d4e\u4e13\u5bb6\u5728\u80cc\u540e\u63d0\u4f9b\u667a\u529b\u652f\u6301\uff0c\u4e0d\u7136\u975e\u4e13\u4e1a\u7684\u4e2a\u4eba\u662f\u5f88\u96be\u5efa\u7acb\u8db3\u591f\u7cbe\u786e\u7684\u6570\u5b66\u6a21\u578b\u7684\u3002\u5f20\u606a\u4e5f\u8bb0\u4e0d\u6e05\u96f6\u516d\u5e74\u7684\u56fd\u4ea7\u624b\u673a\u4ea7\u91cf\u5177\u4f53\u662f\u591a\u5c11\uff0c\u53ea\u8bb0\u5f97\u8981\u8d85\u8fc7\u4e09\u4ebf\u90e8\uff0c\u7531\u4e8e\u624b\u673a\u5236\u9020\u4ea7\u4e1a\u7684\u4e0a\u6e38\u90fd\u638c\u63e1\u5728\u5916\u8d44\u4f01\u4e1a\u624b\u91cc\uff0c\u867d\u7136\u6574\u4e2a\u4ea7\u4e1a\u7684\u5de5\u4e1a\u4ea7\u503c\u76f8\u5f53\u53ef\u89c2\uff0c\u4f46\u662f\u5bf9\u56fd\u6c11\u7ecf\u6d4e\u6709\u66f4\u5927\u610f\u4e49\u7684\u5de5\u4e1a\u589e\u52a0\u503c\u5c31\u76f8\u5bf9\u6709\u9650\u3002</p><p>\u3000\u3000\u53ef\u4ee5\u8bf4\u5168\u7403\u624b\u673a\u4ea7\u4e1a\u90fd\u7ed9\u9526\u6e56\u8fd9\u53ea\u5927\u8774\u8776\u6405\u52a8\u4e86\uff0c\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u53d1\u5c55\u9636\u6bb5\u53ea\u4f1a\u63d0\u524d\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5efa\u90ba\u3001\u6d77\u5dde\u4e24\u4e2a\u4ebf\u6570\u91cf\u7ea7\u7684\u4ea7\u4e1a\u96c6\u7fa4\u5c06\u5728\u96f6\u516d\u5e74\u4e4b\u524d\u5f62\u6210\uff0c\u4ea7\u503c\u4e5f\u5c06\u5728\u5343\u4ebf\u5143\u5de6\u53f3\uff0c\u52a0\u4e0a\u5e9e\u5927\u7684\u914d\u5957\u4ea7\u4e1a\uff0c\u5efa\u90ba\u4e14\u4e0d\u53bb\u8bf4\uff0c\u5374\u662f\u8db3\u4ee5\u5c06\u6d77\u5dde\u8fd9\u4e00\u4e2a\u4e2d\u7b49\u89c4\u6a21\u7684\u57ce\u5e02\u7ecf\u6d4e\u652f\u6491\u8d77\u6765\u3002</p><p>\u3000\u3000\u516c\u4ea4\u8f66\u8fdb\u7ad9\uff0c\u524d\u95e8\u4e0a\u8f66\u53c8\u6324\u8fdb\u6765\u4e00\u4e9b\u4eba\uff0c\u7ad9\u5728\u4e2d\u95f4\u8fc7\u9053\u7684\u51e0\u540d\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\u6216\u8bb8\u662f\u65b9\u4fbf\u5077\u7785\u7fdf\u4e39\u9752\u7684\u7f18\u6545\uff0c\u5c31\u6ca1\u6709\u5f80\u540e\u8d70\uff0c\u4f7f\u5f97\u524d\u8f66\u53a2\u8f83\u6324\uff0c\u5175\u8f66\u53a2\u53c8\u975e\u5e38\u7684\u7a7a\u3002\u5f20\u606a\u770b\u4e86\u4e5f\u53ea\u662f\u6de1\u6de1\u7684\u4e00\u7b11\uff0c\u6362\u6210\u81ea\u5df1\u591a\u534a\u4e5f\u4f1a\u8fd9\u6837\u5427\uff0c\u7ad9\u5728\u516c\u4ea4\u8f66\u4e0a\u5df2\u7ecf\u591f\u65e0\u804a\u4e86\uff0c\u80fd\u6709\u7f8e\u5973\u517b\u773c\uff0c\u53c8\u600e\u4e48\u4f1a\u5bb3\u7f9e\u4e0d\u770b\u5462\uff1f\u8fd9\u51e0\u4e2a\u9752\u5e74\u867d\u7136\u90fd\u7a7f\u7740\u6574\u9f50\u7684\u897f\u88c5\uff0c\u4e24\u4e2a\u5973\u5b69\u5b50\u7a7f\u7740\u5012\u662f\u968f\u610f\u4e9b\uff0c\u8138\u4e0a\u90fd\u4e9b\u672a\u767b\u5165\u793e\u4f1a\u7684\u7a1a\u6c14\u3002</p><p>\u3000\u3000\u201c\u8bf7\u8ba9\u4e00\u8ba9\uff0c\u8c22\u8c22\u2026\u2026\u201d\u6709\u7709\u76ee\u6e05\u79c0\u7684\u5c11\u5987\u7275\u7740\u4e00\u540d\u4e09\u56db\u5c81\u3001\u90fd\u6ca1\u6709\u5c11\u5987\u5927\u817f\u9ad8\u7684\u5c0f\u5973\u5b69\u5b50\u8d39\u529b\u7684\u6324\u5230\u540e\u9762\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u51fa\u6765\u5c06\u5ea7\u4f4d\u8ba9\u7ed9\u5979\u4eec\uff0c\u7ad9\u5230\u7fdf\u4e39\u9752\u8eab\u8fb9\uff0c\u6ca1\u60f3\u5230\u8033\u8fb9\u4f20\u6765\u4e00\u58f0\u8ba5\u7b11\uff1a\u201c\u8fd9\u5e74\u5934\u771f\u6709\u7ed9\u4eba\u8ba9\u5ea7\u7684\u50bbB\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4e00\u4e2a\u6234\u7740\u7802\u94f6\u8fb9\u6846\u773c\u955c\u7684\u9752\u5e74\u6b63\u8f6c\u8fc7\u5934\u53bb\u907f\u5f00\u81ea\u5df1\u7684\u773c\u795e\uff0c\u4e0d\u8fc7\u4ed6\u7684\u8138\u4e0a\u8fd8\u6302\u7740\u4e0d\u5c51\u7684\u795e\u8272\uff0c\u65c1\u8fb9\u51e0\u540d\u9752\u5e74\u5927\u6982\u662f\u8ddf\u4ed6\u4e00\u8d77\u7684\uff0c\u8138\u4e0a\u90fd\u5e26\u7740\u7b11\uff0c\u6709\u4e2a\u9752\u5e74\u77a5\u773c\u770b\u7740\u7fdf\u4e39\u9752\uff0c\u8f7b\u8511\u7684\u5e94\u548c\u7740\u521a\u624d\u90a3\u4e2a\u9752\u5e74\u7684\u6311\u7554\uff1a\u201c\u4eca\u5e74\u5934\u8bf4\u4e0d\u5b9a\u8fd8\u771f\u662f\u6709\u5723\u4eba\u5462\u3002\u201d\u9f3b\u8154\u91cc\u8fd8\u51b7\u55e4\u4e86\u4e00\u58f0\uff0c\u773c\u775b\u5374\u4e0d\u770b\u5f20\u606a\uff0c\u8fd8\u6709\u9752\u5e74\u540e\u8111\u52fa\u80cc\u5bf9\u7740\u5f20\u606a\uff0c\u8f7b\u7b11\u7740\u8bf4\uff1a\u201c\u88c5B\u5427\u3002\u201d\u5012\u662f\u4ed6\u4eec\u5f53\u4e2d\u7684\u4e24\u4e2a\u5973\u5b69\u5b50\u7ed9\u5f20\u606a\u7684\u773c\u795e\u626b\u8fc7\uff0c\u6709\u4e9b\u6b49\u610f\uff0c\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f88\u662f\u65e0\u5948\uff1b\u7fdf\u4e39\u9752\u8eab\u5b50\u671d\u91cc\u8ba9\u4e86\u8ba9\uff0c\u8ba9\u5f20\u606a\u8ddf\u5979\u6324\u4e00\u4e2a\u5ea7\u4f4d\uff0c\u9644\u5728\u4ed6\u7684\u8033\u8fb9\u8f7b\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u4f60\u6ca1\u6709\u8fd9\u4e48\u50bb\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u66f4\u662f\u51a4\u6789\u6211\uff0c\u6211\u975e\u8981\u6025\u7740\u5728\u516c\u4ea4\u8f66\u4e0a\u8ddf\u4f60\u6324\u4e00\u5f20\u6905\u5b50\uff1f\u201d\u5f20\u606a\u624b\u6284\u7740\u7fdf\u4e39\u9752\u8f6f\u8170\uff0c\u5728\u5979\u7684\u8170\u8089\u4e0a\u8f7b\u8f7b\u7684\u634f\u7740\uff0c\u5634\u8d34\u5230\u5979\u8033\u6735\u7ec6\u58f0\u8bf4\u8bdd\uff0c\u201c\u8bf4\u8d77\u6765\uff0c\u8fd8\u4e0d\u662f\u4f60\u8fd9\u4e2a\u7ea2\u989c\u7978\u6c34\u6700\u80fd\u7ed9\u4eba\u62db\u60f9\u662f\u975e\uff1f\u5bb3\u6211\u5e38\u7ed9\u4eba\u5fcc\u6068\u3002\u201d</p><p>\u3000\u3000\u201c\u5509\uff0c\u5509\u2026\u2026\u201d\u7fdf\u4e39\u9752\u6015\u5f20\u606a\u7528\u529b\u634f\uff0c\u5509\u58f0\u6c42\u9976\uff0c\u8bf4\u51fa\u6765\u7684\u8bdd\u53ef\u6ca1\u6709\u544a\u8f6f\uff0c\u201c\u4f60\u4e0d\u906d\u4eba\u5fcc\u6068\uff0c\u8c01\u906d\u4eba\u5fcc\u6068\uff1f\u518d\u8bf4\u7ea2\u989c\u7978\u6c34\u4e5f\u8f6e\u4e0d\u5230\u6211\u5934\u4e0a\uff0c\u4f60\u4e0d\u662f\u53c8\u591a\u4e86\u4e2a\u9648\u9759\u59d0\u59d0\uff1f\u201d</p><p>\u3000\u3000\u201c\u5173\u6211\u4ec0\u4e48\u4e8b\uff1f\u8bb8\u601d\u8ddf\u5979\u611f\u60c5\u597d\u3002\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u7fdf\u4e39\u9752\u5a9a\u773c\u77a5\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u4e00\u624b\u538b\u5f20\u606a\u7684\u5927\u817f\uff0c\u4e00\u624b\u53c8\u5c06\u8d2d\u7269\u888b\u76d6\u4e0a\uff0c\u7ea4\u957f\u7684\u624b\u6307\u5728\u5f20\u606a\u5927\u817f\u5185\u4fa7\u8f7b\u6320\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd8\u4ee5\u4e3a\u5979\u8981\u8ddf\u81ea\u5df1\u5728\u516c\u4ea4\u4e0a\u8c03\u60c5\uff0c\u773c\u89c1\u5b66\u5e9c\u5df7\u6ca1\u4e24\u7ad9\u5c31\u5230\u4e86\uff0c\u4e0b\u5df4\u78d5\u5728\u7fdf\u4e39\u9752\u7684\u80a9\u4e0a\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u5750\u5230\u5de5\u5730\u5427\uff0c\u6b63\u597d\u8981\u53bb\u770b\u770b\u5efa\u8bbe\u5de5\u5730\u2026\u2026\u201d\u60f3\u7740\u5230\u6e56\u4e1c\u5546\u5708\u7684\u4e1c\u4fa7\u5de5\u5730\u80fd\u591a\u5750\u4e09\u7ad9\u8f66\u3002</p><p>\u3000\u3000\u201c\u597d\u554a\uff01\u201d\u7fdf\u4e39\u9752\u5a07\u7b11\u7740\uff0c\u5bb9\u5149\u6108\u76db\uff0c\u5206\u5916\u7684\u8bf1\u4eba\uff0c\u624b\u6307\u4e5f\u6ca1\u6709\u505c\uff0c\u6320\u7740\u50cf\u8981\u6320\u5230\u4eba\u5fc3\u91cc\u53bb\uff0c\u8fd9\u65f6\u5019\u89c9\u5f97\u4eca\u5929\u7a7f\u7684\u88e4\u5b50\u592a\u7d27\u4e86\u3002</p><p>\u3000\u3000\u516c\u4ea4\u8f66\u5230\u4e86\u521b\u4e1a\u56ed\u7ad9\u505c\u4e0b\u6765\uff0c\u7fdf\u4e39\u9752\u5374\u7a81\u7136\u62ff\u8d77\u6765\u8d2d\u7269\u888b\u7ad9\u4e86\u8d77\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u6709\u4e8b\u60c5\u53bb\u627e\u8d75\u6893\u7433\uff0c\u4f60\u966a\u6211\u4e0b\u8f66\u53bb\u3002\u201d\u53c8\u7275\u7740\u5f20\u606a\u7684\u624b\u5f80\u540e\u95e8\u8d70\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u77e5\u9053\u4ec0\u4e48\u53eb\u6076\u6bd2\u5987\u4eba\u5fc3\uff0c\u4ed3\u7687\u7684\u62a2\u8fc7\u7fdf\u4e39\u9752\u624b\u91cc\u7684\u8d2d\u7269\u888b\u906e\u7740\u9686\u8d77\u7684\u4e0b\u8eab\uff0c\u8dcc\u8dcc\u649e\u649e\u7684\u8ddf\u5979\u4e0b\u4e86\u8f66\u3002\u4e0b\u8f66\u540e\uff0c\u7fdf\u4e39\u9752\u8fd8\u4e00\u4e2a\u52b2\u7684\u8ddf\u4ed6\u62a2\u8d2d\u7269\u888b\uff0c\u5f20\u606a\u5939\u514b\u4e0b\u6446\u8f83\u77ed\uff0c\u8d2d\u7269\u4f4e\u7ed9\u7fdf\u4e39\u9752\u62a2\u8d70\uff0c\u4ed6\u53ea\u6709\u8e72\u4e0b\u6765\u624d\u4e0d\u51fa\u4e11\uff0c\u7fdf\u4e39\u9752\u5374\u5728\u4e00\u65c1\u7b11\u7684\u82b1\u679d\u4e71\u98a4\u3002</p><p>\u3000\u3000\u521a\u624d\u90a3\u51e0\u4e2a\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\u4e5f\u5728\u521b\u4e1a\u56ed\u7ad9\u4e0b\u4e86\u8f66\uff0c\u4ed6\u4eec\u7a7f\u8fc7\u9a6c\u8def\u5f80\u521b\u4e1a\u56ed\u91cc\u8d70\u53bb\uff0c\u770b\u4ed6\u4eec\u624b\u91cc\u62ff\u7740\u900f\u660e\u7684\u5851\u6599\u6587\u4ef6\u888b\uff0c\u91cc\u9762\u88c5\u7684\u597d\u50cf\u662f\u7b80\u5386\uff0c\u4e5f\u4e0d\u77e5\u9053\u662f\u53bb\u521b\u4e1a\u56ed\u91cc\u54ea\u5bb6\u4f01\u4e1a\u5e94\u8058\u7684\u5b66\u751f\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6015\u5f20\u606a\u201c\u62a5\u590d\u201d\uff0c\u5148\u7a7f\u8fc7\u9a6c\u8def\u5728\u8857\u5bf9\u9762\u8ddf\u4ed6\u6324\u7709\u5f04\u773c\u7684\u5632\u7b11\u4ed6\uff0c\u5f20\u606a\u7b49\u8eab\u4f53\u91cc\u7684\u706b\u7184\u6389\u4e86\uff0c\u624d\u8dd1\u5230\u8857\u5bf9\u9762\u53bb\u6293\u7fdf\u4e39\u9752\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e00\u6b65\u8de8\u8d70\u521b\u4e1a\u56ed\u7684\u5927\u95e8\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u662f\u5de5\u4f5c\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e00\u624b\u5c06\u5979\u62c9\u51fa\u6765\uff0c\u624b\u5361\u7740\u5979\u7684\u9888\u8116\u5b50\uff0c\u95ee\u9053\uff1a\u201c\u73b0\u5728\u662f\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7f29\u9888\u6447\u5934\u7684\u7b11\u7740\u60f3\u6323\u624e\uff0c\u5230\u5e95\u529b\u6c14\u6ca1\u6709\u5f20\u606a\u5927\uff0c\u5374\u662f\u6709\u8f86\u8f66\u4ece\u521b\u4e1a\u56ed\u91cc\u9a76\u51fa\u6765\uff0c\u6015\u662f\u9526\u6e56\u5185\u90e8\u7684\u4eba\uff0c\u624d\u505c\u4e0b\u7b11\u95f9\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6574\u7406\u88d9\u886b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u771f\u8981\u53bb\u8d75\u6893\u7433\u62ff\u4efd\u6750\u6599\u2026\u2026\u201d\u5e73\u65f6\u6750\u6599\u5f53\u7136\u4e0d\u7528\u5979\u4eb2\u81ea\u6765\u62ff\uff0c\u65e2\u7136\u5728\u8fd9\u91cc\u4e0b\u4e86\u8f66\uff0c\u5c31\u671d\u91cc\u8d70\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u521b\u4e1a\u56ed\u897f\u533a\u4e5f\u5c31\u662f\u4e3d\u4e30\u56ed\u533a\uff0c\u539f\u5148\u662f\u5370\u67d3\u5382\uff0c\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u7ba1\u7406\u4e2d\u5fc3\u7684\u529e\u516c\u697c\u5728\u56ed\u533a\u7684\u6700\u91cc\u4fa7\uff0c\u6b63\u503c\u6625\u5b63\u6a61\u6811\u6362\u53f6\u7684\u5b63\u8282\uff0c\u56ed\u533a\u91cc\u666f\u8272\u9887\u7f8e\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u6f2b\u6b65\u800c\u884c\uff0c\u4e5f\u4e0d\u6025\u7740\u53bb\u627e\u8d75\u6893\u7433\u62ff\u6750\u6599\uff0c\u5012\u662f\u82b1\u4e86\u5341\u591a\u5206\u949f\u624d\u5728\u56ed\u533a\u91cc\u8f6c\u4e86\u4e00\u5708\uff0c\u8d70\u5230\u7ba1\u7406\u4e2d\u5fc3\u3002</p><p>\u3000\u3000\u201c\u8fd8\u4ee5\u4e3a\u4f60\u8dd1\u4e22\u4e86\u5462\uff1f\u201d\u8d75\u6893\u7433\u63a5\u5230\u7fdf\u4e39\u9752\u7684\u7535\u8bdd\uff0c\u5c31\u4e0b\u697c\u6765\u7b49\uff0c\u770b\u5230\u5f20\u606a\u966a\u7fdf\u4e39\u9752\u8fc7\u6765\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u606a\u5c11\u600e\u4e48\u4e5f\u8fc7\u6765\uff0c\u8981\u4e0d\u8981\u8fdb\u53bb\u770b\u4e00\u770b\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u8fd9\u4e48\u591a\u4eba\uff1f\u201d\u5f20\u606a\u62ac\u5934\u770b\u89c1\u4e8c\u697c\u7684\u5927\u5385\u91cc\u5750\u7740\u597d\u4e9b\u4eba\u3002</p><p>\u3000\u3000\u201c\u6d77\u5dde\u5de5\u5382\u501f\u6211\u4eec\u7684\u5730\u65b9\u9762\u8bd5\u5462\u3002\u201d\u8d75\u6893\u7433\u89e3\u91ca\u9053\uff0c\u5185\u90e8\u901a\u5e38\u5c06\u7231\u8fbe\u96c6\u56e2\u79f0\u4e3a\u6d77\u5dde\u5de5\u5382\u3002</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f20\u606a\u773c\u775b\u4e00\u4eae\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u4e0a\u53bb\u770b\u770b\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u77e5\u9053\u4ed6\u662f\u60f3\u8d77\u521a\u624d\u8f66\u4e0a\u90a3\u51e0\u4e2a\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u81ea\u4e2a\u513f\u53bb\u73a9\u5427\uff0c\u6211\u6b63\u597d\u8ddf\u8d75\u6893\u7433\u8c08\u8fd9\u4e2a\u2026\u2026\u201d\u4e3e\u8d77\u624b\u91cc\u7684\u6750\u6599\u626c\u4e86\u626c\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u8d75\u6893\u7433\uff1a\u201c\u90a3\u8fb9\u8fc7\u6765\u8d1f\u8d23\u62db\u8058\u7684\u6709\u8c01\uff1f\u201d\u8981\u662f\u7231\u8fbe\u96c6\u56e2\u4eba\u4e8b\u90e8\u95e8\u8fc7\u6765\u8d1f\u8d23\u9762\u8bd5\u7684\u5458\u5de5\u4e0d\u8ba4\u8bc6\u4ed6\uff0c\u90a3\u624d\u4e22\u8138\u4e22\u5927\u4e86\u3002</p><p>\u3000\u3000\u201c\u6709\u5e02\u573a\u90e8\u95e8\u7684\u9762\u8bd5\uff0c\u6768\u4e91\u4ed6\u4eba\u6b63\u597d\u5728\u5efa\u90ba\u3002\u201d</p><p>\u3000\u3000\u6768\u4e91\u5728\u5c31\u884c\uff0c\u5f20\u606a\u201c\u54e6\u201d\u7684\u4e00\u58f0\u5c31\u4e0a\u4e86\u697c\uff0c\u666e\u901a\u5458\u5de5\u7684\u9762\u8bd5\uff0c\u53ea\u6709\u5230\u6700\u540e\u4e00\u8f6e\u624d\u7531\u90e8\u95e8\u603b\u7ecf\u7406\u53c2\u4e0e\u9762\u8bd5\uff0c\u6768\u4e91\u4ed6\u662f\u8d76\u5de7\u5728\u5efa\u90ba\u624d\u53c2\u4e0e\u3002</p><p>\u3000\u3000\u5f20\u606a\u8d70\u5230\u4e8c\u697c\u7684\u5927\u5385\uff0c\u90a3\u51e0\u4e2a\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\u4e5f\u5750\u5728\u90a3\u91cc\u521a\u5c06\u9762\u8bd5\u8868\u683c\u586b\u5b8c\u4ea4\u7ed9\u5de5\u4f5c\u4eba\u5458\uff0c\u5750\u5728\u90a3\u91cc\u7b49\u5f85\u9762\u8bd5\uff0c\u5634\u89d2\u85cf\u7740\u82e5\u6709\u82e5\u65e0\u7684\u6d45\u7b11\uff0c\u5750\u4e86\u8fc7\u53bb\u3002\u90a3\u51e0\u4e2a\u9752\u5e74\u4ee5\u4e3a\u5f20\u606a\u4e5f\u662f\u8ddf\u4ed6\u4eec\u4e00\u9053\u9762\u8bd5\uff0c\u4e3a\u516c\u4ea4\u8f66\u4e0a\u7684\u4e8b\u60c5\uff0c\u90fd\u6709\u4e9b\u5c34\u5c2c\uff0c\u5012\u662f\u90a3\u4e09\u4e2a\u5728\u8f66\u4e0a\u51fa\u8a00\u8ba5\u7b11\u7684\u9752\u5e74\u522b\u8fc7\u8138\u53bb\u3002</p><p>\u3000\u3000\u5728\u5916\u9762\u8d1f\u8d23\u63a5\u5f85\u7684\u5de5\u4f5c\u4eba\u5458\u4e5f\u4ee5\u4e3a\u5f20\u606a\u662f\u8fc7\u6765\u9762\u8bd5\u7684\u5b66\u751f\uff0c\u62ff\u4e86\u4e00\u5f20\u8868\u683c\u8fc7\u6765\u8ba9\u5f20\u606a\u586b\uff0c\u5f20\u606a\u63a5\u8fc7\u8868\u683c\u5750\u5728\u90a3\u91cc\u80e1\u4e71\u7684\u586b\u5199\u7740\uff0c\u4fa7\u8fc7\u5934\u770b\u4e86\u770b\u65c1\u8fb9\u5973\u5b69\u5b50\u653e\u5728\u684c\u4e0a\u7684\u8d44\u6599\u888b\uff0c\u900f\u8fc7\u900f\u660e\u7684\u5851\u6599\u76ae\uff0c\u770b\u5f97\u5230\u7b80\u5386\u5c01\u9762\u4e0a\u7684\u5b66\u6821\uff0c\u539f\u6765\u90fd\u662f\u5efa\u90ba\u5de5\u4e1a\u5b66\u9662\u7684\u6bd5\u4e1a\u751f\u3002</p><p>\u3000\u3000\u516b\u4eba\u4ea4\u53c9\u9762\u8bd5\u8fdb\u884c\u5f88\u5feb\uff0c\u5f88\u5feb\u5c31\u8f6e\u5230\u5efa\u5de5\u7684\u8fd9\u51e0\u4e2a\u4eba\uff0c\u5f20\u606a\u4e5f\u7ad9\u8d77\u6765\u8ddf\u7740\u5f80\u91cc\u8d70\uff0c\u5de5\u4f5c\u4eba\u5458\u8fd8\u558a\u4f4f\u4ed6\uff1a\u201c\u5509\uff0c\u4f60\u7684\u8868\u8fd8\u6ca1\u6709\u586b\u597d\u4ea4\u7ed9\u6211\u5462\uff0c\u4f60\u600e\u4e48\u5c31\u5f80\u91cc\u8d70\uff1f\u201d</p><p>\u3000\u3000\u6768\u4e91\u5728\u9762\u8bd5\u7684\u4f1a\u8bae\u5ba4\u91cc\u5df2\u7ecf\u770b\u5230\u5f20\u606a\u8138\u4e86\uff0c\u90fd\u7ad9\u8d77\u6765\uff1a\u201c\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6325\u4e86\u6325\u624b\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u8fc7\u6765\u770b\u770b\uff0c\u4f60\u4eec\u505a\u4f60\u4eec\u7684\u4e8b\u2026\u2026\u201d\u89c1\u4f1a\u8bae\u5ba4\u9760\u5899\u58c1\u6709\u4e00\u6392\u6905\u5b50\uff0c\u62d6\u4e86\u4e00\u628a\u6905\u5b50\u653e\u5230\u4f1a\u8bae\u684c\u4e0a\u7684\u89d2\u4e0a\u5750\u4e0b\u6765\uff0c\u8fd9\u624d\u6162\u6761\u4e1d\u7406\u7684\u62db\u5934\u770b\u7740\u90a3\u51e0\u4e2a\u4e00\u8138\u60ca\u8c14\u800c\u5750\u7acb\u4e0d\u5b89\u7684\u5efa\u5de5\u5b66\u751f\u3002</p><p>\u3000\u3000\u9664\u4e86\u6768\u4e91\u5916\uff0c\u4eba\u4e8b\u90e8\u95e8\u8d1f\u8d23\u7ec4\u7ec7\u9762\u8bd5\u7684\u5434\u4e3a\u94ed\uff0c\u662f\u526f\u7ecf\u7406\u7ea7\u7684\u4e3b\u7ba1\uff0c\u5f20\u606a\u8ba4\u8bc6\uff0c\u8fd8\u6709\u4e24\u4e2a\u4eba\u9762\u719f\uff0c\u662f\u5e02\u573a\u90e8\u95e8\u7684\uff0c\u70b9\u5934\u7b97\u662f\u62db\u547c\u3002</p><p>\u3000\u3000\u5f20\u606a\u90fd\u8fd9\u4e48\u8bf4\u4e86\uff0c\u5434\u4e3a\u94ed\u53ea\u6709\u51c6\u5907\u5f00\u59cb\u9762\u8bd5\uff0c\u6768\u4e91\u4e5f\u662f\u4e34\u65f6\u8fc7\u6765\u53c2\u4e0e\u9762\u8bd5\u7684\uff0c\u4ed6\u4e0e\u5f20\u606a\u719f\u7edc\uff0c\u632a\u5750\u5230\u4ed6\u8eab\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u6709\u8fd9\u95f2\u60c5\u6765\u5fae\u670d\u79c1\u8bbf\uff1f\u201d</p><p>\u3000\u3000\u201c\u8ba9\u4ed6\u4eec\u6bcf\u4e2a\u4eba\u5728\u7eb8\u4e0a\u7b54\u8fd9\u4e2a\u95ee\u9898\u2026\u2026\u201d\u5f20\u606a\u5728\u5de5\u4f5c\u4eba\u5458\u521a\u624d\u9012\u7ed9\u4ed6\u7684\u8868\u683c\u5199\u4e0b\u4e00\u53e5\u8bdd\u538b\u7740\u6768\u4e91\u7684\u684c\u524d\u3002</p><p>\u3000\u3000\u6768\u4e91\u770b\u7740\u5f20\u606a\u5199\u7684\u90a3\u53e5\u8bdd\uff0c\u5c31\u50bb\u4e86\u773c\uff0c\u4fc4\u800c\u60f3\u5230\u4ec0\u4e48\uff0c\u53c8\u7b11\u4e86\u8d77\u6765\uff0c\u5c06\u8868\u683c\u9012\u7ed9\u65c1\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\uff0c\u5429\u5490\u4ed6\u7167\u529e\u53bb\u51c6\u5907\u7eb8\u7b14\u8ba9\u8fc7\u6765\u9762\u8bd5\u7684\u5b66\u751f\u7b54\u9898\u3002</p><p>\u3000\u3000\u90a3\u51e0\u4e2a\u5efa\u5de5\u5b66\u751f\u5c40\u4fc3\u4e0d\u5b89\u7684\u5750\u5728\u90a3\u91cc\uff0c\u7ed9\u5f20\u606a\u9510\u5229\u7684\u773c\u795e\u76ef\u7740\uff0c\u5f3a\u4f5c\u9547\u5b9a\u4e5f\u4e0d\u884c\uff0c\u5f85\u5de5\u4f5c\u4eba\u5458\u5c06\u7b14\u8ddf\u590d\u5370\u7eb8\u88c1\u6210\u7684\u7b54\u9898\u7eb8\u7247\u9012\u7ed9\u4ed6\u4eec\uff0c\u770b\u7740\u7b54\u9898\u7eb8\u4e0a\u5370\u7740\u7b80\u5355\u7684\u4e00\u4e2a\u95ee\u9898\uff0c\u9762\u7ea2\u8033\u70eb\uff0c\u53d7\u4e0d\u4f4f\u538b\u529b\u7684\uff0c\u989d\u5934\u90fd\u5f00\u59cb\u5192\u6c57\u4e86\u3002</p><p>\u3000\u3000\u201c\u8bf7\u8bc4\u4ef7\u516c\u4ea4\u8f66\u4e0a\u7ed9\u5987\u5973\u3001\u513f\u7ae5\u8ba9\u5ea7\u7684\u884c\u4e3a\uff01\u201d\u7eb8\u7247\u5c31\u8fd9\u4e48\u7b80\u5355\u7684\u4e00\u4e2a\u95ee\u9898\uff0c\u5176\u4ed6\u7684\u90fd\u662f\u7a7a\u767d\uff0c\u767d\u82b1\u82b1\u7684\u8ba9\u4eba\u770b\u4e86\u89c9\u5f97\u53d1\u6655\uff0c\u597d\u51e0\u4e2a\u4eba\u633a\u8170\u76f4\u80cc\u7684\u5f00\u59cb\u5438\u6c14\u4e86\uff0c\u90fd\u6709\u79cd\u8981\u9003\u8d70\u7684\u51b2\u52a8\uff0c\u9762\u9762\u76f8\u89d1\uff0c\u4e0d\u77e5\u9053\u8981\u4e0d\u8981\u786c\u7740\u5934\u76ae\u56de\u7b54\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e73\u9759\u7684\u770b\u7740\u8fd9\u51e0\u4e2a\u5efa\u5de5\u7684\u5b66\u751f\uff0c\u770b\u7740\u4ed6\u4eec\u786c\u7740\u5934\u76ae\u5728\u7eb8\u7247\u4e0a\u56de\u7b54\u95ee\u9898\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u6768\u4e91\u4eb2\u81ea\u8d70\u5230\u524d\u9762\u5c06\u7eb8\u7247\u6536\u8fc7\u6765\uff0c\u8fde\u770b\u8fb9\u7b11\u7740\u9012\u7ed9\u5f20\u606a\uff0c\u5f20\u606a\u7ffb\u4e86\u7ffb\uff0c\u5c06\u51e0\u5f20\u7eb8\u538b\u5728\u624b\u4e0b\uff0c\u770b\u7740\u5750\u5728\u524d\u9762\u7684\u51e0\u4e2a\u5efa\u5de5\u5b66\u751f\uff0c\u8bf4\u9053\uff1a\u201c\u7eb8\u4e0a\u5199\u7684\u90fd\u5f88\u6f02\u4eae\uff0c\u5bf9\u8fd9\u4e2a\u95ee\u9898\u7684\u56de\u7b54\u4e0d\u4f1a\u5f71\u54cd\u5230\u4f60\u4eec\u8fd9\u6b21\u7684\u9762\u8bd5\u6210\u7ee9\uff0c\u201d\u5f20\u606a\u8bed\u6c14\u7a0d\u7a0d\u4e00\u987f\uff0c\u53c8\u5f3a\u8c03\u8bf4\u9053\uff0c\u201c\u4f46\u662f\uff0c\u4f60\u4eec\u82e5\u662f\u80fd\u901a\u8fc7\u6700\u7ec8\u7684\u9762\u8bd5\u53c2\u52a0\u7231\u8fbe\u96c6\u56e2\uff0c\u6210\u4e3a\u7231\u8fbe\u96c6\u56e2\u7684\u4e00\u540d\u5458\u5de5\uff0c\u90a3\u5c31\u8bf7\u4f60\u4eec\u8bb0\u4f4f\uff0c\u4f60\u4eec\u5728\u516c\u4ea4\u8f66\u4e0a\uff0c\u770b\u5230\u5e26\u5c0f\u5b69\u5b50\u7684\u5987\u5973\u3001\u770b\u5230\u8001\u4eba\u3001\u770b\u5230\u6b8b\u75be\u4eba\uff0c\u8bf7\u4e3b\u52a8\u7ad9\u8d77\u6765\u8ba9\u5ea7\u2026\u2026\u201d</p></article>"}}}]);
