(window.webpackJsonp=window.webpackJsonp||[]).push([[1012],{1035:function(p){p.exports={chapterTitle:null,title:"\u7b2c819\u7ae0 \u7f1c\u5bc6",content:"<article><h2>\u7b2c819\u7ae0 \u7f1c\u5bc6</h2><p>\u3000\u3000\u542c\u7740\u7535\u8bdd\u90a3\u5934\u4f20\u6765\u6d51\u539a\u800c\u6c89\u7a33\u7684\u58f0\u97f3\uff0c\u80fd\u60f3\u8c61\u51fa\u5d14\u6587\u6bc5\u662f\u4e2a\u6c14\u5ea6\u6c89\u6bc5\u7684\u7537\u5b50\uff0c\u6709\u7740\u957f\u671f\u5728\u90e8\u961f\u91cc\u57f9\u517b\u51fa\u6765\u7684\u8bf4\u8bdd\u53e3\u543b\uff0c\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u662f\u5d14\u8001\u5e08\u7684\u5b66\u751f\u2026\u2026\u5efa\u90ba\u8fd9\u8fb9\u7684\u5730\u65b9\u62a5\u793e\u4e5f\u6d3e\u8bb0\u8005\u5230\u7ae0\u5dde\u8c03\u67e5\uff0c\u4e5f\u6df1\u5165\u5230\u95ee\u9898\u6700\u4e25\u91cd\u7684\u897f\u5858\u9547\u8c03\u67e5\uff0c\u53ea\u4e0d\u8fc7\u4e8b\u524d\u6ca1\u6709\u8ddf\u5d14\u8bb0\u8005\u8054\u7edc\u3002\u73b0\u5728\u53ea\u77e5\u9053\u5d14\u8bb0\u8005\u4eca\u5929\u8fdb\u5165\u8fc7\u897f\u5858\u9547\uff0c\u5230\u73b0\u5728\u8fd8\u6ca1\u6709\u8054\u7cfb\u4e0a\uff0c\u8fd8\u65e0\u6cd5\u786e\u8ba4\u4e00\u5b9a\u5c31\u662f\u53d1\u751f\u4e86\u4ec0\u4e48\u72b6\u51b5\uff0c\u4f46\u662f\u5efa\u90ba\u8fd9\u8fb9\u7684\u8bb0\u8005\u53d1\u73b0\u5230\u897f\u5858\u9547\u5730\u65b9\u4eca\u5929\u6709\u4e9b\u4e0e\u5f80\u5e73\u5e38\u4e0d\u4e00\u6837\u7684\u5730\u65b9\uff0c\u89c9\u5f97\u5d14\u8bb0\u8005\u5931\u53bb\u8054\u7cfb\u8fd9\u4ef6\u4e8b\u9700\u8981\u614e\u91cd\u5bf9\u5f85\u2026\u2026\u201d\u5f20\u606a\u5e76\u4e0d\u6e05\u695a\u5d14\u5bb6\u7684\u60c5\u51b5\uff0c\u66f4\u4e0d\u77e5\u9053\u5d14\u90c1\u66fc\u7236\u4eb2\u771f\u5b9e\u7684\u6027\u5b50\u5982\u4f55\uff0c\u5f53\u7136\u4e0d\u53ef\u80fd\u4e00\u4e0b\u5b50\u5c31\u638f\u5fc3\u7a9d\u5b50\u7684\u5c06\u8fd9\u8fb9\u7684\u5e95\u7ec6\u90fd\u544a\u8bc9\u5bf9\u65b9\uff0c\u53ea\u5e0c\u671b\u4ed6\u5bf9\u5d14\u90c1\u6052\u5931\u53bb\u8054\u7cfb\u8fd9\u4ef6\u4e8b\u614e\u91cd\u770b\u5f85\uff0c\u4e5f\u4e0d\u8981\u614c\u4e86\u624b\u811a\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5d14\u6587\u6bc5\u6c89\u541f\u4e86\u7247\u523b\uff0c\u6d51\u539a\u7684\u58f0\u97f3\u624d\u91cd\u65b0\u4f20\u6765\uff0c\u201c\u4f60\u4eec\u8ba4\u4e3a\u7ae0\u5dde\u53ef\u80fd\u53d1\u751f\u4e86\u54ea\u65b9\u9762\u95ee\u9898\u624d\u4e0b\u53bb\u8c03\u67e5\uff1f\u201d</p><p>\u3000\u3000\u8fd9\u7247\u523b\u7684\u63a5\u89e6\uff0c\u5f20\u606a\u5012\u662f\u80fd\u786e\u8ba4\u5d14\u6587\u6bc5\u7684\u5fc3\u601d\u7f1c\u5bc6\uff0c\u800c\u4e14\u80fd\u4e00\u4e0b\u5b50\u6293\u4f4f\u95ee\u9898\u7684\u5173\u952e\uff0c\u5012\u4e0d\u6653\u5f97\u5d14\u90c1\u66fc\u8fd9\u70ed\u8840\u3001\u9887\u65e0\u987e\u5fcc\u7684\u6027\u5b50\u662f\u7ee7\u627f\u4e86\u8c01\u3002\u5f20\u606a\u8fd9\u8fb9\u4e5f\u7a0d\u601d\u91cf\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u7ae0\u5dde\u7684\u60c5\u51b5\u6709\u4e9b\u590d\u6742\uff0c\u7a00\u571f\u77ff\u8d44\u6e90\u76d7\u91c7\u7684\u5de8\u5927\u5229\u76ca\u4f7f\u5f97\u5730\u65b9\u653f\u5e9c\u7684\u4e00\u4e9b\u5b98\u5458\u7518\u5f53\u8fd9\u4e9b\u6076\u52bf\u529b\u7684\u4fdd\u62a4\u4f1e\uff0c\u6709\u8bb8\u591a\u653f\u5e9c\u673a\u5173\u4eba\u5458\u751a\u81f3\u76f4\u63a5\u5728\u76d7\u91c7\u77ff\u70b9\u6301\u80a1\uff0c\u80fd\u521d\u6b65\u80af\u5b9a\u7ae0\u5dde\u56e0\u7a00\u571f\u76d7\u91c7\u800c\u7ed3\u6210\u4e00\u5f20\u5bc6\u96c6\u7684\u5229\u76ca\u7f51\uff0c\u5c1a\u65e0\u6cd5\u5224\u65ad\u8fd9\u5f20\u5229\u76ca\u7f51\u6709\u591a\u6df1\u3001\u591a\u5bc6\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u53ea\u80fd\u63d0\u9192\u5230\u8fd9\u4e48\u591a\u4e86\uff0c\u4ed6\u603b\u4e0d\u80fd\u76f4\u63a5\u8bf4\u7ae0\u5dde\u5730\u65b9\u4e0a\u7684\u73ed\u5b50\u6574\u4e2a\u7684\u90fd\u8150\u70c2\u6389\u4e86\u3002\u8bf4\u8fd9\u6837\u7684\u8bdd\uff0c\u8981\u6ca1\u6709\u8db3\u591f\u7684\u8bc1\u636e\uff0c\u662f\u8981\u627f\u62c5\u653f\u6cbb\u8d23\u4efb\u7684\uff1b\u518d\u8bf4\u5c31\u7b97\u4ed6\u628a\u4e00\u5207\u90fd\u660e\u8bf4\u4e86\uff0c\u5d14\u6587\u6bc5\u672a\u5fc5\u5c31\u4f1a\u91c7\u4fe1\u3002</p><p>\u3000\u3000\u201c\u4f60\u7684\u610f\u601d\u662f\u8bf4\uff0c\u8981\u5bfb\u4eba\u4e0d\u5b9c\u901a\u8fc7\u5730\u65b9\uff1f\u201d\u5d14\u6587\u6bc5\u5012\u662f\u8fc5\u901f\u9886\u4f1a\u4e86\u5f20\u606a\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u8986\u5de2\u4e4b\u4e0b\uff0c\u7109\u6709\u5b8c\u5375\uff1f\u5c31\u7b97\u5d14\u5bb6\u7684\u52bf\u529b\u901a\u5929\uff0c\u5c31\u7b97\u5d14\u90c1\u6052\u6709\u7740\u65b0\u534e\u793e\u8bb0\u8005\u7684\u62a4\u8eab\u7b26\uff0c\u4f46\u662f\u5e76\u4e0d\u6392\u9664\u5510\u82f1\u80b2\u5144\u5f1f\u5728\u7389\u77f3\u4ff1\u711a\u4e4b\u524d\u884c\u9669\u5236\u9020\u60ac\u6848\u7684\u53ef\u80fd\u3002\u5d14\u90c1\u6052\u4e0e\u65b0\u534e\u793e\u5176\u4ed6\u4e24\u540d\u8bb0\u8005\u82e5\u771f\u662f\u88ab\u6263\u62bc\u4e0b\u6765\uff0c\u901a\u8fc7\u7ae0\u5dde\u5e02\u8b66\u65b9\u627e\u4eba\uff0c\u4e0d\u8fc7\u662f\u901a\u8fc7\u7ae0\u5dde\u8b66\u65b9\u5c06\u6d88\u606f\u63d0\u524d\u6cc4\u9732\u7ed9\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\u77e5\u9053\u800c\u5df2\uff0c\u53ea\u4f1a\u5c06\u5d14\u90c1\u6052\u4e09\u4e2a\u4eba\u63a8\u52a8\u66f4\u5371\u9669\u7684\u60ac\u5d16\u8fb9\u7f18\uff0c\u80fd\u627e\u5230\u4eba\u624d\u602a\uff01\u8fd9\u5e74\u5934\uff0c\u56fd\u5916\u90fd\u6709\u4e3a\u5229\u76ca\u4e4b\u4e89\u523a\u6740\u603b\u7edf\u7684\u4e8b\u60c5\u53d1\u751f\uff0c\u56fd\u5185\u4e5f\u6709\u7701\u7eaa\u59d4\u4e66\u8bb0\u5dee\u70b9\u906d\u6697\u6740\u3001\u5e02\u59d4\u5b98\u5458\u7ed9\u5168\u5bb6\u706d\u53e3\u7684\u4f8b\u5b50\uff0c\u7ed9\u903c\u5230\u6b7b\u80e1\u540c\u91cc\u7684\u56f0\u517d\u4f1a\u6709\u4ec0\u4e48\u53cd\u5e94\uff0c\u662f\u5e38\u4eba\u96be\u4ee5\u9884\u6d4b\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u542d\u58f0\uff0c\u9ed8\u8ba4\u4e86\u5d14\u6587\u6bc5\u7684\u731c\u6d4b\uff0c\u5f53\u7136\uff0c\u521a\u624d\u7684\u8bdd\u4e5f\u5411\u5d14\u6587\u6bc5\u900f\u9732\u51fa\u4ed6\u8fd9\u8fb9\u7684\u786e\u6e05\u695a\u7ae0\u5dde\u7684\u4e00\u4e9b\u5185\u5e55\u3002\u7535\u8bdd\u90a3\u5934\u4f20\u6765\u5d14\u6587\u6bc5\u8f7b\u5fae\u7684\u5438\u6c14\u58f0\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u65e0\u7591\u662f\u975e\u5e38\u7684\u68d8\u624b\uff0c\u96be\u5f97\u5d14\u6587\u6bc5\u8fd8\u80fd\u5982\u6b64\u51b7\u9759\u7684\u8003\u8651\u95ee\u9898\u3002\u5d14\u90c1\u66fc\u7ad9\u5728\u529e\u516c\u684c\u8fb9\u5fc3\u614c\u610f\u4e71\u7684\u76ef\u7740\u5f20\u606a\u8ddf\u5979\u7236\u4eb2\u901a\u7535\u8bdd\uff0c\u6293\u7740\u529e\u516c\u684c\u8fb9\u7f18\u7684\u624b\u6307\u53d1\u767d\uff0c\u6452\u4f4f\u547c\u5438\u60f3\u542c\u7535\u8bdd\u91cc\u7684\u58f0\u97f3\uff0c\u8fde\u5927\u58f0\u90fd\u4e0d\u6562\u5598\u4e00\u53e3\u3002</p><p>\u3000\u3000\u675c\u98de\u3001\u8499\u4e50\u3001\u5e2d\u82e5\u7433\u4ed6\u4eec\u90fd\u610f\u8bc6\u5230\u4e8b\u6001\u7684\u4e25\u91cd\uff0c\u4ed6\u4eec\u8eab\u5728\u5efa\u90ba\uff0c\u5bf9\u7ae0\u5dde\u53d1\u751f\u7684\u4e8b\u60c5\u90fd\u65e0\u80fd\u4e3a\u529b\uff0c\u53ea\u80fd\u770b\u7740\u5f20\u606a\u5728\u8fd9\u91cc\u8fd0\u7b79\u5e37\u5e44\u7684\u6307\u6325\u8c03\u5ea6\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u7247\u523b\uff0c\u5d14\u6587\u6bc5\u5728\u7535\u8bdd\u90a3\u5934\u53c8\u95ee\u9053\uff1a\u201c\u8bf7\u5730\u65b9\u6b66\u8b66\u5e2e\u5fd9\u4f1a\u4e0d\u4f1a\u7a33\u59a5\u4e00\u4e9b\uff0c\u8fd8\u662f\u8bf4\u9700\u8981\u66f4\u7a33\u59a5\u7684\u65b9\u5f0f\uff1f\u201d</p><p>\u3000\u3000\u8c03\u52a8\u5730\u65b9\u9a7b\u519b\u7684\u5173\u7cfb\u5f53\u7136\u66f4\u7a33\u59a5\u4e00\u4e9b\uff0c\u542c\u5d14\u6587\u6bc5\u7684\u53e3\u6c14\uff0c\u4f3c\u4e4e\u8feb\u4e0d\u5f97\u5df2\u4e5f\u80fd\u8bf7\u52a8\u5730\u65b9\u9a7b\u519b\u5e2e\u5fd9\u5bfb\u4eba\uff0c\u4f46\u662f\u4e3a\u4e00\u4e2a\u672a\u66fe\u8bc1\u5b9e\u7684\u6d88\u606f\uff0c\u65e0\u8bba\u662f\u76f4\u63a5\u8fd8\u662f\u901a\u8fc7\u5173\u7cfb\u8c03\u52a8\u5730\u65b9\u9a7b\u519b\u534f\u52a9\uff0c\u90fd\u662f\u9887\u72af\u5fcc\u8bb3\u7684\u4e8b\u60c5\uff0c\u518d\u5927\u6743\u52bf\u7684\u4eba\u4e5f\u6015\u4e0b\u9762\u4eba\u57a2\u75c5\uff0c\u518d\u8bf4\u519b\u961f\u5728\u627e\u4eba\u8fd9\u4e2a\u7ec6\u817b\u6d3b\u65b9\u9762\u4e5f\u4e0d\u64c5\u957f\u3002\u6b66\u8b66\u662f\u5c5e\u4e8e\u5185\u536b\u6027\u8d28\u7684\u73b0\u5f79\u90e8\u961f\uff0c\u5730\u65b9\u6cbb\u5b89\u51fa\u4e86\u95ee\u9898\uff0c\u6b66\u8b66\u51fa\u52a8\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u3002\u53e6\u5916\uff0c\u7531\u4e8e\u6362\u9632\u4e0e\u5175\u5f79\u5236\u5ea6\u7684\u5173\u7cfb\uff0c\u6b66\u8b66\u90e8\u961f\u53d7\u5730\u65b9\u6076\u52bf\u529b\u8150\u8680\u7684\u53ef\u80fd\u6027\u8f83\u5c11\uff0c\u5f20\u606a\u4e5f\u4e0d\u8bb0\u5f97\u7ae0\u5dde\u5927\u6848\u53c8\u7275\u6d89\u5230\u6b66\u8b66\u4e0e\u5730\u65b9\u9a7b\u519b\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u80fd\u5728\u6b66\u8b66\u90e8\u961f\u627e\u5230\u7a33\u59a5\u7684\u4eba\uff0c\u8fd9\u8fb9\u62a5\u793e\u6d3e\u51fa\u53bb\u7684\u8bb0\u8005\u53ef\u4ee5\u63d0\u4f9b\u534f\u52a9\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90a3\u597d\uff0c\u4f60\u90a3\u8fb9\u7b49\u6211\u7684\u7535\u8bdd\u2026\u2026\u201d\u5d14\u6587\u6bc5\u5728\u7535\u8bdd\u90a3\u5934\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u5c31\u5148\u6302\u65ad\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\uff0c\u5f20\u606a\u6401\u5728\u684c\u8fb9\u7684\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u9a6c\u6d77\u9f99\u4ece\u7ae0\u5dde\u6253\u5f00\u7535\u8bdd\uff1a\u201c\u7ae0\u5dde\u8b66\u65b9\u6709\u5f02\u52a8\uff0c\u8fdb\u51fa\u897f\u5858\u7684\u51e0\u6761\u8def\u90fd\u8bbe\u7f6e\u4e86\u68c0\u67e5\u5c97\uff0c\u5bf9\u5916\u5730\u4eba\u76d8\u67e5\u7279\u522b\u4e25\u683c\uff0c\u8bf4\u662f\u897f\u5858\u90a3\u8fb9\u53d1\u751f\u4e86\u6848\u5b50\uff0c\u5177\u4f53\u7684\u60c5\u51b5\u5374\u8c01\u90fd\u8bf4\u4e0d\u6e05\u695a\uff0c\u597d\u5728\u6211\u4eec\u5728\u897f\u5858\u6709\u4eba\uff0c\u4e0d\u7136\u60f3\u8981\u4e58\u8f66\u8fdb\u897f\u5858\u5f88\u56f0\u96be\u2026\u2026\u6211\u4eec\u521a\u6709\u4eba\u8d76\u5230\u5d14\u90c1\u6052\u5165\u4f4f\u7684\u5bbe\u9986\uff0c\u4e0d\u8fc7\u542c\u8bf4\u8b66\u65b9\u4e0b\u5348\u5df2\u7ecf\u67e5\u8fc7\u90a3\u91cc\uff0c\u6ca1\u6562\u6253\u542c\u592a\u8be6\u7ec6\uff0c\u53ea\u786e\u5b9a\u5d14\u90c1\u6052\u7b49\u4e09\u540d\u65b0\u534e\u793e\u7684\u8bb0\u8005\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u8fd4\u56de\u5bbe\u9986\uff0c\u897f\u5858\u90a3\u8fb9\u98ce\u58f0\u8fd9\u4e48\u7d27\uff0c\u4eca\u5929\u90fd\u672a\u5fc5\u80fd\u67e5\u5230\u4ec0\u4e48\u6709\u7528\u7684\u6d88\u606f\uff0c\u6211\u8fd9\u5c31\u4eb2\u81ea\u53bb\u897f\u5858\u8d70\u4e00\u8d9f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u8fb9\u53ef\u80fd\u4f1a\u8054\u7cfb\u5f53\u5730\u7684\u6b66\u8b66\u90e8\u961f\u9700\u8981\u4f60\u53bb\u914d\u5408\u4eba\u5bb6\u5de5\u4f5c\uff0c\u4f60\u6682\u65f6\u4e0d\u8981\u52a8\u3002\u201d\u5f20\u606a\u5490\u5429\u9053\u3002</p><p>\u3000\u3000\u5927\u6982\u8fc7\u4e86\u4e03\u516b\u5206\u949f\uff0c\u529e\u516c\u684c\u4e0a\u7684\u7535\u8bdd\u54cd\u4e86\u8d77\u6765\uff0c\u5bf9\u5d14\u90c1\u66fc\u8fd9\u4e9b\u6b63\u7126\u6025\u7b49\u7535\u8bdd\u7684\u4eba\u6765\u8bf4\uff0c\u8fd8\u662f\u7ed9\u7a81\u7136\u54cd\u8d77\u7684\u624b\u673a\u94c3\u58f0\u5413\u4e86\u4e00\u8df3\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ff\u8d77\u7535\u8bdd\uff0c\u5d14\u6587\u6bc5\u6d51\u539a\u7684\u58f0\u97f3\u518d\u6b21\u4ece\u7535\u8bdd\u90a3\u5934\u4f20\u8fc7\u6765\uff1a\u201c\u6c5f\u5357\u7701\u6b66\u8b66\u90e8\u961f\u5728\u91d1\u5c71\u4e0e\u7ae0\u5dde\u4e4b\u95f4\u9a7b\u6709\u4e00\u652f\u90e8\u961f\uff0c\u4ece\u90a3\u91cc\u53ef\u4ee5\u8c03\u4eba\u8fc7\u53bb\uff0c\u8fd9\u662f\u9ece\u5efa\u660e\u5927\u6821\u7684\u8054\u7cfb\u65b9\u5f0f\uff0c\u4f60\u8bb0\u4e00\u4e0b\u2026\u2026\u201d</p><p>\u3000\u3000\u867d\u7136\u80fd\u8bb0\u4f4f\uff0c\u4e3a\u4e86\u8868\u793a\u614e\u91cd\uff0c\u5f20\u606a\u8fd8\u662f\u62ff\u51fa\u7eb8\u8ddf\u7b14\u8bb0\u5f55\u9ece\u5efa\u660e\u5927\u6821\u7684\u540d\u5b57\u4e0e\u8054\u7cfb\u65b9\u5f0f\uff0c\u653e\u4e0b\u7535\u8bdd\u65f6\uff0c\u95ee\u4e86\u5d14\u90c1\u66fc\u4e00\u58f0\uff1a\u201c\u4f60\u5bb6\u662f\u519b\u961f\u7cfb\u7edf\u7684\uff1f\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u6b63\u5fe7\u5fc3\u5982\u711a\uff0c\u542c\u5f20\u606a\u51b7\u4e0d\u4e01\u7684\u8fd9\u4e48\u4e00\u95ee\uff0c\u4e0b\u610f\u8bc6\u7684\u53cd\u95ee\u9053\uff1a\u201c\u4f60\u600e\u4e48\u77e5\u9053\u7684\uff1f\u201d</p><p>\u3000\u3000\u6c5f\u5357\u7701\u6b66\u8b66\u603b\u961f\u7684\u5934\u662f\u5c11\u5c06\u8fd8\u662f\u5927\u6821\uff0c\u5f20\u606a\u4e00\u65f6\u8fd8\u641e\u4e0d\u6e05\u695a\uff0c\u624d\u4e03\u516b\u5206\u949f\u7684\u65f6\u95f4\uff0c\u5d14\u6587\u6bc5\u901a\u77e5\u8fd9\u8fb9\u8054\u7cfb\u6c5f\u5357\u7701\u6b66\u8b66\u90e8\u961f\u7684\u4e00\u4e2a\u5927\u6821\uff0c\u8981\u4e0d\u662f\u5d14\u6587\u6bc5\u4e00\u5f00\u59cb\u6ca1\u6709\u5f88\u80af\u5b9a\u7684\u8bf4\u8981\u52a8\u7528\u6b66\u8b66\u90e8\u961f\u7684\u5173\u7cfb\uff0c\u5f20\u606a\u51e0\u4e4e\u6000\u7591\u5d14\u6587\u6bc5\u662f\u6b66\u8b66\u90e8\u961f\u7684\u9ad8\u5c42\u3002</p><p>\u3000\u3000\u6b66\u8b66\u90e8\u961f\u867d\u8bf4\u4e5d\u516d\u5e74\u4e4b\u524d\u4e00\u76f4\u90fd\u5c5e\u4e8e\u516c\u5b89\u90e8\u7ba1\u8f96\uff0c\u4f46\u662f\u7531\u4e8e\u6b66\u8b66\u90e8\u961f\u4ee5\u5185\u536b\u4e3a\u4e3b\u7684\u6027\u8d28\uff0c\u51b3\u5b9a\u5176\u4e0e\u519b\u961f\u7684\u5173\u7cfb\u66f4\u52a0\u5bc6\u5207\uff0c\u6b66\u8b66\u90e8\u961f\u8bf8\u591a\u9ad8\u5c42\u5c06\u9886\u90fd\u662f\u4ece\u73b0\u5f79\u90e8\u961f\u8f6c\u8fc7\u53bb\u7684\uff1b\u4e5d\u516d\u5e74\u6b66\u8b66\u90e8\u961f\u8131\u79bb\u516c\u5b89\u90e8\u7ba1\u8f96\uff0c\u5f52\u5165\u519b\u59d4\u3001\u56fd\u52a1\u9662\u7684\u53cc\u91cd\u9886\u5bfc\uff0c\u4e5d\u516d\u3001\u4e5d\u4e03\u5e74\uff0c\u73b0\u5f79\u90e8\u961f\u88c1\u519b\u65f6\uff0c\u90fd\u6709\u4e00\u90e8\u5206\u4e59\u7ea7\u5e08\u5c31\u76f4\u63a5\u8f6c\u4e3a\u6b66\u8b66\u90e8\u961f\u3002</p><p>\u3000\u3000\u5d14\u6587\u6bc5\u80fd\u5728\u8fd9\u4e48\u77ed\u7684\u65f6\u95f4\u91cc\u8c03\u52a8\u6c5f\u5357\u7701\u6b66\u8b66\u603b\u961f\u7684\u4eba\u624b\u534f\u52a9\u5bfb\u4eba\uff0c\u90a3\u8bf4\u660e\u5d14\u5bb6\u4e0e\u519b\u65b9\u7684\u6e0a\u6e90\u6781\u6df1\uff0c\u5d14\u6587\u6bc5\u5c31\u662f\u73b0\u5f79\u7684\u5c06\u519b\u4e5f\u8bf4\u4e0d\u5b9a\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u8ddf\u5d14\u90c1\u66fc\u8bf4\u4ec0\u4e48\uff0c\u62ff\u8d77\u624b\u673a\u8ddf\u9a6c\u6d77\u9f99\u8054\u7cfb\uff0c\u8ba9\u4ed6\u76f4\u63a5\u8054\u7edc\u6c5f\u5357\u7701\u6b66\u8b66\u603b\u961f\u7684\u9ece\u5efa\u660e\u5927\u6821\uff0c\u8ba9\u4ed6\u534f\u52a9\u6c5f\u5357\u7701\u6b66\u8b66\u603b\u961f\u6d3e\u51fa\u7684\u4eba\u8fc7\u53bb\u5bfb\u627e\u5d14\u90c1\u6052\u7684\u4e0b\u843d\u3002</p><p>\u3000\u3000\u5f20\u606a\u6302\u4e86\u7535\u8bdd\uff0c\u5750\u4e0b\u6765\u770b\u4e86\u5d14\u90c1\u66fc\u4e00\u773c\uff0c\u8bf4\u9053\uff1a\u201c\u73b0\u5728\u4e5f\u53ea\u6709\u7b49\u5f85\u4e86\u3002\u201d</p><p>\u3000\u3000\u90fd\u51fa\u52a8\u6b66\u8b66\u53bb\u627e\u4eba\u4e86\uff0c\u867d\u7136\u5fc3\u91cc\u8fd8\u6709\u7740\u7275\u6302\uff0c\u4f46\u4e0d\u4f1a\u8fd8\u7279\u522b\u7684\u5fc3\u614c\uff0c\u518d\u8bf4\u5fc3\u91cc\u591a\u5c11\u4e5f\u4ee5\u4e3a\u53ef\u80fd\u662f\u56e0\u4e3a\u4ec0\u4e48\u610f\u5916\u7684\u4e8b\u60c5\u803d\u6401\u4e86\uff0c\u5d14\u90c1\u66fc\u53cc\u624b\u73af\u80f8\u5750\u5728\u90a3\u91cc\uff0c\u540e\u80cc\u90fd\u51fa\u4e86\u51b7\u6c57\uff0c\u8fd9\u624d\u7ec6\u60f3\u8d77\u6765\u5f20\u606a\u521a\u624d\u9547\u5b9a\u81ea\u82e5\u7684\u5904\u7f6e\u95ee\u9898\u7edd\u4e0d\u50cf\u5bfb\u5e38\u7684\u5927\u5b66\u4e00\u5e74\u7ea7\u7684\u5b66\u751f\u5e94\u6709\u7684\u8868\u73b0\u3002\u5728\u4ed6\u7684\u51b7\u9759\u4e0e\u6c89\u7a33\u9762\u524d\uff0c\u5d14\u90c1\u66fc\u90fd\u8981\u4e3a\u81ea\u5df1\u521a\u624d\u7684\u624b\u8db3\u65e0\u63aa\u3001\u5fc3\u614c\u610f\u4e71\u800c\u60ed\u6127\u4e86\uff0c\u5fc3\u91cc\u867d\u7136\u6709\u7740\u66f4\u591a\u7684\u7591\u95ee\uff0c\u53ea\u4e0d\u8fc7\u6bd4\u8d77\u54e5\u54e5\u7684\u5b89\u5371\u90fd\u4e0d\u91cd\u8981\uff0c\u5979\u4e5f\u6ca1\u6709\u8868\u9732\u51fa\u6765\u3002</p><p>\u3000\u3000\u529e\u516c\u684c\u4e0a\u7684\u7535\u8bdd\u53c8\u54cd\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u5476\u5476\u5634\uff0c\u793a\u610f\u5d14\u90c1\u66fc\u5979\u81ea\u5df1\u53bb\u63a5\uff0c\u80fd\u4e3a\u5d14\u90c1\u6052\u505a\u7684\u4e8b\u60c5\uff0c\u4e5f\u5df2\u7ecf\u8fd9\u6837\u4e86\uff0c\u63a5\u4e0b\u6765\u8be5\u662f\u5979\u4eec\u5bb6\u4eba\u4e92\u76f8\u5b89\u6170\u4e86\u3002\u5f20\u606a\u5c06\u624b\u673a\u88c5\u8fdb\u53e3\u888b\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u6709\u4e9b\u53e3\u6e34\uff0c\u6211\u4e0b\u53bb\u62ff\u74f6\u6c34\u559d\uff0c\u4f60\u4eec\u8c01\u8fd8\u9700\u8981\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0b\u4e86\u697c\uff0c\u5085\u4fca\u5c31\u5728\u697c\u4e0b\u7684\u7f51\u53f0\u91cc\u7b49\u7740\uff0c\u5c06\u4e8b\u60c5\u8ddf\u4ed6\u7565\u8bf4\u4e86\u4e00\u904d\uff0c\u53c8\u8bf4\u9053\uff1a\u201c\u4f60\u7ed9\u4e39\u9752\u6253\u4e2a\u7535\u8bdd\uff0c\u8ba9\u5979\u8054\u7edc\u6606\u817e\u53ca\u5efa\u90ba\u65e5\u62a5\uff0c\u5c06\u7a00\u571f\u95ee\u9898\u7684\u62a5\u9053\u90fd\u5148\u64a4\u4e0b\u6765\u2026\u2026\u201d\u8fd9\u65f6\u5019\u5c31\u662f\u8981\u907f\u514d\u60ca\u52a8\u5510\u82f1\u57f9\u3001\u5510\u82f1\u80b2\u5144\u5f1f\uff0c\u8ba9\u4ed6\u4eec\u4ee5\u4e3a\u4e8b\u60c5\u90fd\u5728\u4ed6\u4eec\u7684\u638c\u63e1\u4e4b\u4e2d\uff0c\u5c31\u7b97\u5d14\u90c1\u6052\u5728\u4ed6\u4eec\u624b\u91cc\uff0c\u53ea\u8981\u8fd8\u6709\u56de\u65cb\u7684\u4f59\u5730\uff0c\u4ed6\u4eec\u662f\u4e0d\u4f1a\u6025\u7740\u4e0b\u8fa3\u624b\u7684\u3002</p><p>\u3000\u3000\u4ece\u7efc\u5408\u5427\u5408\u90a3\u91cc\u62ff\u4e86\u4e00\u74f6\u6c34\uff0c\u5750\u5230\u4f11\u606f\u533a\u57df\u7684\u89d2\u843d\u91cc\uff0c\u5f20\u606a\u62e8\u901a\u6881\u4f1f\u6cd5\u79d8\u4e66\u7684\u7535\u8bdd\uff0c\u8ba9\u4ed6\u5c06\u624b\u673a\u8f6c\u4ea4\u5230\u6881\u4f1f\u6cd5\u7684\u624b\u4e2d\uff1a\u201c\u6881\u7701\u957f\u8fd8\u5728\u8def\u4e0a\u5427\uff1f\u201d</p><p>\u3000\u3000\u5728\u6885\u56ed\u9152\u5e97\u89c1\u8fc7\u9762\u4e4b\u524d\uff0c\u6881\u4f1f\u6cd5\u3001\u8d75\u6709\u4f26\u4ed6\u4eec\u8981\u79bb\u591c\u8d76\u56de\u91d1\u5c71\u53bb\uff0c\u8fd9\u65f6\u5019\u6b63\u5728\u5efa\u90ba\u901a\u5f80\u91d1\u5c71\u7684\u56fd\u9053\u4e0a\u3002\u6ca1\u6709\u9ad8\u901f\uff0c\u53c8\u662f\u591c\u95f4\u884c\u9a76\uff0c\u867d\u8bf4\u6709\u8b66\u8f66\u5f00\u9053\uff0c\u4e24\u767e\u516c\u91cc\u4e0d\u5230\u7684\u8def\u7a0b\u4e5f\u8981\u56db\u4e94\u4e2a\u5c0f\u65f6\uff0c\u8d76\u56de\u91d1\u5c71\u90fd\u8981\u5230\u51cc\u6668\u4e24\u4e09\u70b9\u949f\u4e86\u3002\u8fd9\u4e48\u611f\u53d7\u7740\uff0c\u90fd\u4f1a\u89c9\u5f97\u5b9e\u5728\u6709\u4fee\u7b51\u9ad8\u901f\u516c\u8def\u7684\u5fc5\u8981\u3002\u6881\u4f1f\u6cd5\u89c1\u5f20\u606a\u5206\u5f00\u4e0d\u4e45\u5c31\u53c8\u6253\u6765\u7535\u8bdd\uff0c\u5947\u602a\u7684\u95ee\uff1a\u201c\u53c8\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u4eca\u5929\u63a5\u8fde\u63a5\u5230\u4f60\u629b\u6765\u7684\u4e24\u4e2a\u96be\u9898\uff0c\u770b\u5230\u4f60\u7684\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u90fd\u6709\u4e9b\u5fc3\u60ca\u8089\u8df3\u7684\u611f\u89c9\u554a\u3002\u201d</p><p>\u3000\u3000\u6551\u5d14\u90c1\u6052\u662f\u8981\u6551\uff0c\u80fd\u6709\u673a\u4f1a\u501f\u52bf\u5c06\u7ae0\u5dde\u7684\u6bd2\u7624\u6316\u6389\uff0c\u5f20\u606a\u4e5f\u4e0d\u4f1a\u9519\u8fc7\u673a\u4f1a\u3002</p></article>"}}}]);
