(window.webpackJsonp=window.webpackJsonp||[]).push([[1015],{1038:function(p){p.exports={chapterTitle:null,title:"\u7b2c1012\u7ae0 \u70ed\u6f6e",content:"<article><h2>\u7b2c1012\u7ae0 \u70ed\u6f6e</h2><p>\u3000\u3000\u5317\u7f8e\u5730\u533a\u7684\u6295\u8d44\u5546\u90fd\u5f88\u96be\u5bf9\u4e5d\u4e5d\u5e74\u65b0\u79d1\u6280\u6d6a\u6f6e\u91cc\u7684\u6ce1\u6cab\u6709\u6e05\u9192\u7684\u8ba4\u8bc6\uff0c\u66f4\u4e0d\u7528\u8bf4\u662f\u56fd\u5185\u7684\u5927\u5b66\u751f\u521b\u4e1a\u8005\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u7535\u8bdd\u7ed9\u7fdf\u4e39\u9752\uff0c\u8ba9\u5979\u6d3e\u4eba\u5c06\u4e00\u4e9b\u5bf9\u65b0\u79d1\u6280\u4ea7\u4e1a\u6d6a\u6f6e\u7684\u7814\u7a76\u62a5\u544a\u6d3e\u4eba\u9001\u8fc7\u6765\uff0c\u6b63\u89c4\u7684\u6709\u7406\u6709\u636e\u7684\u7814\u7a76\u62a5\u544a\uff0c\u603b\u6bd4\u4e00\u5f20\u5634\u6709\u8bf4\u670d\u529b\u3002</p><p>\u3000\u3000\u201c\u5168\u7403\u65b0\u79d1\u6280\u4ea7\u4e1a\u7279\u522b\u662f\u4e92\u8054\u7f51\u4ea7\u4e1a\u957f\u671f\u5feb\u901f\u53d1\u5c55\u662f\u80af\u5b9a\u7684\uff0c\u2014\u2014\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u53d1\u5c55\u524d\u671f\uff0c\u81ea\u8eab\u76c8\u5229\u80fd\u529b\u6781\u4e3a\u6709\u9650\uff0c\u53d1\u5c55\u79bb\u4e0d\u5f00\u8d44\u672c\u5e02\u573a\u7684\u652f\u6491\uff0c\u6240\u4ee5\u98ce\u9669\u6295\u8d44\u8005\u5bf9\u8d44\u672c\u5e02\u573a\u7684\u52bf\u6001\u8d70\u5411\u6781\u4e3a\u5173\u6ce8\uff0c\u201d\u5f20\u606a\u7b11\u7740\u5206\u6790\u8bf4\uff0c\u201c\u4f60\u4eec\u7ed9\u521b\u57df\u8bbe\u8ba1\u7684\u6295\u8d44\u76c8\u5229\u8def\u7ebf\uff0c\u4e5f\u5c31\u662f\u5c06\u5353\u57df\u6253\u9020\u6210\u4e2d\u56fd\u7684\u4e9a\u9a6c\u900a\uff0c\u6700\u5feb\u7684\u5c06\u4e1a\u52a1\u89c4\u6a21\u505a\u5927\uff0c\u4ee5\u671f\u80fd\u83b7\u5f97\u6d77\u5916\u98ce\u6295\u7684\u9752\u7750\u6700\u7ec8\u80fd\u5728\u6d77\u5916\u4e0a\u5e02\uff0c\u521b\u57df\u5c4a\u65f6\u5c31\u80fd\u5957\u73b0\u9000\u51fa\u2014\u2014\u4f46\u662f\u8fd9\u4e2a\u8fc7\u7a0b\u6700\u5feb\u4e5f\u9700\u8981\u4e24\u5230\u4e09\u5e74\u7684\u65f6\u95f4\uff0c\u5f88\u53ef\u60dc\u8d44\u672c\u5e02\u573a\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u6001\u5ea6\u5e76\u4e0d\u603b\u662f\u70ed\u60c5\u4f3c\u706b\u7684\uff0c\u4e00\u65e6\u8d44\u672c\u5e02\u573a\u9677\u5165\u4f4e\u8c37\u671f\uff0c\u4e0a\u5e02\u8ba1\u5212\u5c31\u4f1a\u592d\u6298\u2026\u2026\u5bf9\u8d44\u672c\u5e02\u573a\u7684\u70ed\u60c5\u6709\u8fc7\u9ad8\u7684\u671f\u5f85\uff0c\u8fd9\u662f\u8ba1\u5212\u4e66\u91cc\u6700\u5927\u7684\u7f3a\u9677\uff0c\u4e5f\u662f\u6700\u5927\u7684\u98ce\u9669\u2014\u2014\u4f5c\u4e3a\u6807\u51c6\u7684\u98ce\u9669\u6295\u8d44\u6a21\u5f0f\uff0c\u5bf9\u8fd9\u79cd\u98ce\u9669\u81ea\u7136\u4f1a\u6709\u4e00\u79cd\u7684\u627f\u53d7\u80fd\u529b\uff0c\u4f46\u662f\u521b\u57df\u4e0e\u98ce\u9669\u6295\u8d44\u8fd8\u662f\u6709\u5f88\u5927\u533a\u522b\u7684\u3002\u521b\u57df\u6210\u7acb\u7684\u5b97\u65e8\u662f\u652f\u6301\u5927\u5b66\u751f\u521b\u4e1a\uff0c\u80fd\u83b7\u5f97\u8d44\u672c\u5e02\u573a\u7684\u9752\u7750\u6700\u7ec8\u5728\u6d77\u5916\u4e0a\u5e02\uff0c\u81ea\u7136\u662f\u6700\u7406\u60f3\u7684\u7ed3\u679c\uff0c\u4f46\u662f\u4f5c\u4e3a\u521b\u4e1a\u57fa\u91d1\u7684\u672c\u8d28\uff0c\u8fd8\u662f\u8981\u76f8\u5bf9\u7684\u964d\u4f4e\u5bf9\u8d44\u672c\u5e02\u573a\u70ed\u60c5\u7684\u4f9d\u8d56\uff0c\u66f4\u591a\u7684\u53bb\u8003\u8651\u4ea7\u4e1a\u672c\u8eab\u7684\u53d1\u5c55\u6f5c\u529b\u3002\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u7684\u8bdd\u8ba9\u9a6c\u529b\u7b49\u4eba\u90fd\u62bf\u5634\u4f4e\u5934\u9677\u5165\u6c89\u601d\uff0c\u4ed6\u7684\u8bdd\u91cc\u4e5f\u6709\u7740\u675c\u98de\u5e73\u65f6\u601d\u8003\u4e0d\u5230\u7684\u6df1\u5ea6\uff0c\u521b\u57df\u5185\u90e8\uff0c\u50cf\u65f6\u5b66\u658c\u3001\u65bd\u65b0\u98de\u3001\u8463\u8dc3\u534e\u7b49\u4eba\u5bf9\u98ce\u9669\u6295\u8d44\u4e0e\u521b\u4e1a\u6295\u8d44\u4e4b\u95f4\u7684\u533a\u522b\u4e5f\u6ca1\u6709\u5f88\u6df1\u523b\u7684\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u201c\u6211\u5e76\u6ca1\u6709\u5426\u5b9a\u6389\u8fd9\u4efd\u5546\u4e1a\u8ba1\u5212\u4e66\u7684\u7cbe\u534e\u90e8\u5206\uff0c\u600e\u4e48\u8bf4\u5462\uff0c\u201d\u5f20\u606a\u8f7b\u8f7b\u4e00\u7b11\uff0c\u201c\u5e94\u8be5\u8bf4\u521b\u57df\u9700\u8981\u4e00\u4efd\u66f4\u4fdd\u5b88\u7684\u3001\u5bf9\u4ea7\u4e1a\u672c\u8eab\u6709\u7740\u66f4\u6df1\u523b\u601d\u8003\u7684\u5546\u4e1a\u8ba1\u5212\u4e66\uff0c\u81f3\u4e8e\u878d\u8d44\u989d\u5ea6\uff0c\u4e94\u767e\u4e07\u4e5f\u884c\uff0c\u4e00\u5343\u4e07\u4e5f\u884c\uff0c\u90fd\u4e0d\u6210\u4ec0\u4e48\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0b\u5348\u7684\u521b\u534f\u7406\u4e8b\u6d3b\u52a8\uff0c\u8fd8\u662f\u8ba8\u8bba\u8fd9\u4e2a\uff0c\u81f3\u5c11\u6211\u4eec\u73b0\u5728\u7684\u601d\u8def\u5df2\u7ecf\u660e\u786e\u4e86\u3002\u201d\u675c\u98de\u5c06\u8ba1\u5212\u4e66\u9012\u7ed9\u65f6\u5b66\u658c\uff0c\u4ed6\u6ca1\u6253\u7b97\u5360\u7528\u5f20\u606a\u592a\u591a\u7684\u65f6\u95f4\uff0c\u5982\u4eca\u65f6\u5b66\u658c\u3001\u65bd\u65b0\u98de\u3001\u8463\u8dc3\u534e\u90fd\u662f\u521b\u534f\u7684\u7406\u4e8b\uff0c\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\u53bb\u4e86\u7f8e\u56fd\uff0c\u675c\u98de\u5728\u8fd9\u65b9\u9762\u5c31\u66f4\u591a\u7684\u4f9d\u8d56\u4ed6\u4eec\uff0c\u800c\u79e6\u521a\u3001\u4ee4\u5c0f\u71d5\u66f4\u591a\u7684\u8d1f\u8d23\u5df2\u6709\u4e1a\u52a1\u7684\u65e5\u5e38\u7ba1\u7406\uff0c\u6bd5\u7adf\u5bf9\u5927\u5b66\u751f\u521b\u4e1a\u652f\u6301\u7684\u8d44\u91d1\u4e3b\u8981\u662f\u6765\u6e90\u4e8e\u521b\u57df\u5df2\u6709\u7684\u4e1a\u52a1\u76c8\u5229\u3002</p><p>\u3000\u3000\u65f6\u5b66\u658c\u5bf9\u9a6c\u529b\u4ee5\u53ca\u4ed6\u7684\u4e09\u540d\u5408\u4f5c\u4f19\u4f34\u8bf4\u9053\uff1a\u201c\u4e5f\u4e0d\u7528\u7b49\u5230\u4e0b\u5348\u4e86\uff0c\u6211\u4eec\u627e\u5730\u4f4d\u5403\u4e2d\u996d\uff0c\u8fb9\u5403\u8fb9\u8c08\u5427\u2026\u2026\u201d\u53c8\u95ee\u5f20\u606a\uff0c\u201c\u4f60\u53c2\u4e0d\u53c2\u52a0\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u6ca1\u4ec0\u4e48\u4e8b\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4f60\u4eec\u5148\u627e\u5403\u996d\u7684\u5730\u65b9\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u8ba9\u65f6\u5b66\u658c\u4e0e\u9a6c\u529b\u4ed6\u4eec\u5148\u79bb\u5f00\uff0c\u5f20\u606a\u4e0e\u675c\u98de\u3001\u9648\u5983\u84c9\u5728\u540e\u9762\u8fb9\u8d70\u8fb9\u804a\uff1a\u201c\u8d44\u672c\u5e02\u573a\u4ece\u6765\u90fd\u662f\u76db\u6781\u800c\u8870\u7684\uff0c\u98ce\u9669\u6295\u8d44\u7684\u5468\u671f\u4e00\u822c\u4e0d\u4f1a\u8d85\u8fc7\u4e24\u5e74\uff0c\u5468\u671f\u592a\u957f\uff0c\u5c31\u5f88\u96be\u5bf9\u8d44\u672c\u5e02\u573a\u8d70\u5411\u6709\u51c6\u5907\u7684\u5224\u65ad\u2014\u2014\u521b\u4e1a\u6295\u8d44\uff0c\u9700\u8981\u66f4\u591a\u7684\u8003\u8651\u4ea7\u4e1a\u672c\u8eab\u7684\u53d1\u5c55\u6f5c\u529b\uff0c\u8003\u8651\u6295\u8d44\u5468\u671f\u53ef\u4ee5\u6bd4\u98ce\u6295\u66f4\u957f\uff0c\u51c6\u786e\u7684\u8bf4\uff0c\u98ce\u9669\u66f4\u50cf\u662f\u4ea7\u4e1a\u6295\u673a\u2026\u2026\u201d</p><p>\u3000\u3000\u675c\u98de\u8bf4\u9053\uff1a\u201c\u8fd9\u4efd\u5546\u4e1a\u8ba1\u5212\u4e66\uff0c\u82e5\u662f\u4e0d\u53bb\u8003\u8651\u5f15\u8fdb\u98ce\u6295\uff0c\u521b\u57df\u5dee\u4e0d\u591a\u8981\u6295\u8fdb\u53bb\u4e00\u4e2a\u4ebf\u624d\u591f\u2014\u2014\u5bf9\u521b\u57df\u6765\u8bf4\uff0c\u592a\u5927\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u7535\u5b50\u5546\u52a1\u4e0d\u4f1a\u53ea\u5c40\u9650\u4e8e\u56fe\u4e66\u3001\u97f3\u50cf\u3001\u97f3\u54cd\u7b49\u6587\u5316\u5546\u54c1\u7684\u5728\u7ebf\u9500\u552e\uff0c\u7b49\u4f60\u4eec\u53d1\u5c55\u5230\u6d88\u8d39\u7c7b\u7535\u5b50\u5546\u54c1\u751a\u81f3\u5bb6\u7535\u4ea7\u54c1\u7684\u5728\u7ebf\u9500\u552e\u9636\u6bb5\uff0c\u7231\u8fbe\u3001\u76db\u946b\u90fd\u53ef\u4ee5\u8003\u8651\u6ce8\u8d44\u6301\u80a1\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u4e5f\u662f\uff0c\u201d\u675c\u98de\u7b11\u4e86\u7b11\uff0c\u201c\u8981\u8bf4\u4e92\u8054\u7f51\u4ea7\u4e1a\uff0c\u80cc\u540e\u8fd8\u6709\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0e\u6606\u817e\u5728\u7ebf\u4e24\u5ea7\u5927\u5c71\u5e2e\u521b\u57df\u9876\u7740\u5929\u5462\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4e00\u6b21\u662f\u6606\u817e\u5728\u7ebf\u4e0a\u5e02\u4e4b\u524d\u7684\u6700\u540e\u4e00\u6b21\u878d\u8d44\u4e0e\u80a1\u6743\u8c03\u6574\uff0c\u9664\u4e86\u9526\u6e56\u5de6\u624b\u817e\u53f3\u624b\uff0c\u5c06\u6a61\u6811\u56ed\u521b\u6295\u6240\u6301\u7684\u5927\u90e8\u5206\u80a1\u6743\u8f6c\u79fb\u5230\u6d77\u5916\u516c\u53f8\u540d\u4e0b\uff0c\u53e6\u5916\u5c31\u53ea\u662f\u5411\u9ad8\u76db\u5173\u8054\u7684\u98ce\u6295\u878d\u8d44\u4e24\u5343\u4e07\u7f8e\u5143\u3002\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u7efc\u5408\u6307\u6570\u8fd1\u4e24\u5e74\u6765\u7a33\u6b65\u4e0a\u5347\u5df2\u7ecf\u6709\u52a0\u901f\u7684\u8ff9\u8c61\uff0c\u6606\u817e\u5728\u7ebf\u7ecf\u8fc7\u6b64\u65f6\u8c03\u6574\uff0c\u6700\u5feb\u80fd\u5728\u516d\u6708\u4e2d\u65ec\u6d77\u5916\u4e0a\u5e02\uff0c\u7b79\u63aa\u5de5\u4f5c\u6b63\u6709\u6761\u4e0d\u7d6e\u7684\u8fdb\u884c\u4e2d\u3002</p><p>\u3000\u3000\u9664\u4e86\u6606\u817e\u5728\u7ebf\u4e4b\u5916\uff0c\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u662f\u5f53\u521d\u4e3a\u914d\u5408iplayer\u786c\u4ef6\u9500\u552e\u800c\u6210\u7acb\u7684\u7f51\u4e0a\u6b63\u7248\u97f3\u4e50\u9500\u552e\u5546\u5e97\uff0c\u4e5f\u662f\u9526\u6e56\u540d\u4e0b\u91cd\u8981\u7684\u4e92\u8054\u7f51\u8d44\u6e90\u3002\u5e74\u540e\uff0c\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u8054\u5408\u5411\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u6ce8\u8d44\u516d\u5343\u4e07\u7f8e\u5143\u6362\u53d630\uff05\u7684\u80a1\u6743\uff0c\u4e5f\u5c31\u610f\u8c13\u7740\u9526\u6e56\u624b\u91cc\u5269\u4e0b\u768470\uff05\u80a1\u6743\u5e02\u503c2.1\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u524d\u540e\u624d\u5728\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0a\u6295\u5165\u4e24\u5343\u591a\u4e07\u7f8e\u5143\u3002</p><p>\u3000\u3000\u82e5\u662f\u53ea\u8ba1\u7b97\u7eb8\u4e0a\u8d22\u5bcc\u7684\u8bdd\uff0c\u9526\u6e56\u4f3c\u4e4e\u5728\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0a\u7684\u8d26\u9762\u76c8\u5229\u8981\u6570\u5341\u500d\u4e8e\u786c\u4ef6\u9500\u552e\u2014\u2014\u53ef\u60dc\u53ea\u662f\u8d26\u9762\u76c8\u5229\uff0c\u4eca\u5e74\u51e0\u5e74\u65f6\u95f4\u5185\u9526\u6e56\u53c8\u4e0d\u53ef\u80fd\u5c06\u8d26\u9762\u76c8\u5229\u5151\u73b0\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e9b\u6d89\u53ca\u5230\u5bf9\u4e92\u8054\u7f51\u5e73\u53f0\u8d44\u6e90\u7684\u957f\u671f\u89c4\u5212\uff0c\u672c\u8eab\u4e5f\u662f\u9526\u6e56\u773c\u4e0b\u6b63\u8003\u8651\u7684\u5927\u95ee\u9898\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5c31\u50cf\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u8054\u5408\u5411\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7f51\u7ad9\u6ce8\u8d44\u516d\u5343\u4e07\u7f8e\u5143\u6362\u53d630\uff05\u7684\u80a1\u6743\uff0c\u770b\u4e0a\u53bb\u8fd9\u662f\u4e00\u7b14\u5728\u65b0\u79d1\u6280\u6d6a\u6f6e\u80cc\u666f\u4e0b\u7684\u5927\u80c6\u6295\u8d44\uff0c\u4f46\u5bf9\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u6765\u8bf4\uff0c\u62ff\u51fa\u8fd9\u7b14\u94b1\u6765\uff0c\u66f4\u91cd\u8981\u7684\u662f\u4ea7\u4e1a\u5e03\u5c40\u4e0a\u7684\u8003\u8651\u3002\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u8fd9\u6b21\u5de6\u624b\u817e\u53f3\u624b\uff0c\u6a61\u6811\u56ed\u521b\u6295\u65d7\u4e0b\u4e0d\u662f\u51ed\u7a7a\u591a\u4e86\u4e09\u5343\u591a\u4e07\u7f8e\u5143\u7684\uff1f\u5e94\u8be5\u53ef\u4ee5\u505a\u4e9b\u4e8b\u60c5\u4e86\u3002\u201d\u675c\u98de\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e09\u5343\u4e07\u7f8e\u91d1\u4e5f\u505a\u4e0d\u4e86\u591a\u5c11\u4e8b\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u201c\u8981\u505a\u7684\u4e8b\u60c5\u5f88\u591a\uff0c\u73b0\u5728\u6210\u7acb\u4e86\u7231\u8fbe\u6587\u5316\u57fa\u91d1\uff0c\u770b\u4e0a\u53bb\u5728\u5317\u95e8\u5efa\u4e00\u5ea7\u56fe\u4e66\u9986\u5df2\u7ecf\u5f88\u4e86\u4e0d\u8d77\u4e86\uff0c\u4f46\u662f\u601d\u7ef4\u518d\u5f80\u5916\u62d3\u5c55\u4e00\u4e9b\uff0c\u5728\u4e92\u8054\u7f51\u4e0a\u5efa\u4e00\u5ea7\u6570\u5b57\u56fe\u4e66\u9986\uff0c\u4f1a\u4e0d\u4f1a\u610f\u4e49\u66f4\u5927\u4e00\u4e9b\u2014\u2014\u4f46\u662f\u8fd9\u4e2a\u5de5\u7a0b\u8981\u6bd4\u5efa\u4e00\u5ea7\u5730\u4e0b\u4e24\u5c42\u3001\u5730\u4e0a\u516d\u5c42\u7684\u5b9e\u4f53\u56fe\u4e66\u9986\u8981\u590d\u6742\u5f97\u591a\u2026\u2026\u8fd9\u4e9b\u90fd\u6d89\u53ca\u5230\u672a\u6765\u4e92\u8054\u7f51\u5185\u5bb9\u3001\u5e73\u53f0\u3001\u7528\u6237\u7b49\u8d44\u6e90\u5206\u5272\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u7684\u6570\u5b57\u56fe\u4e66\u9986\uff0c\u8fd8\u771f\u6709\u4eba\u63d0\u51fa\u6765\u8fc7\uff0c\u662f\u5b66\u6821\u79d1\u6280\u60c5\u62a5\u7814\u7a76\u6240\u7684\u4e00\u4e2a\u7814\u7a76\u5458\u63d0\u51fa\u6765\u7684\uff0c\u4e0d\u8fc7\u4ed6\u6240\u5212\u5b9a\u7684\u8303\u56f4\u5f88\u72ed\u7a84\uff0c\u53ea\u9650\u4e8e\u4e13\u4e1a\u9886\u57df\u7684\u8d44\u6599\u6c47\u96c6\uff0c\u4e5f\u5e94\u8be5\u5f52\u4e8e\u6570\u5b57\u56fe\u4e66\u9986\u7684\u4e00\u7c7b\uff0c\u201d\u675c\u98de\u8bf4\u9053\uff0c\u201c\u4eba\u662f\u79e6\u521a\u4ecb\u7ecd\u7684\uff0c\u5c31\u662f\u8003\u8651\u5230\u6d89\u53ca\u5230\u7684\u95ee\u9898\u592a\u590d\u6742\uff0c\u6211\u6ca1\u6709\u8ba9\u521b\u57df\u63a5\u624b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u53ea\u662f\u63d0\u51fa\u6784\u60f3\uff0c\u8fd8\u662f\u8bf4\u4ed6\u4e5f\u6709\u6267\u884c\u7684\u80fd\u529b\uff1f\u201d\u5f20\u606a\u95ee\u9053\uff0c\u8fd9\u5e74\u5934\uff0c\u521b\u610f\u662f\u5f88\u5ec9\u4ef7\u7684\u4e1c\u897f\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u6295\u8d44\u7684\u9677\u9631\uff0c\u6700\u7ec8\u8fd8\u662f\u8981\u770b\u4eba\u7684\u6267\u884c\u80fd\u529b\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e5f\u8981\u5148\u7ed9\u4ed6\u51e0\u5341\u4e07\u8bd5\u4e00\u8bd5\u624d\u77e5\u9053\u3002\u201d\u675c\u98de\u8bf4\u9053\u3002</p><p>\u3000\u3000\u79e6\u521a\u4ecb\u7ecd\u8fc7\u6765\u7684\u4eba\uff0c\u80fd\u529b\u5e94\u8be5\u4e0d\u4f1a\u592a\u5dee\uff0c\u4f46\u662f\u6210\u4e0d\u6210\u4e8b\uff0c\u56e0\u7d20\u592a\u591a\uff0c\u5bf9\u4e8e\u6295\u8d44\u57fa\u91d1\u6240\u8981\u505a\u7684\uff0c\u603b\u4e0d\u4f1a\u5c06\u53d1\u5c55\u6240\u9700\u8981\u7684\u57fa\u91d1\u4e00\u4e0b\u5b50\u90fd\u6295\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u65e2\u7136\u675c\u98de\u5bf9\u4e92\u8054\u7f51\u4ea7\u4e1a\u6709\u7740\u6d53\u539a\u7684\u5174\u8da3\uff0c\u5f20\u606a\u4e5f\u5e0c\u671b\u521b\u57df\u80fd\u811a\u8e0f\u5b9e\u5730\u7684\u53bb\u505a\u4e00\u4e9b\u57fa\u7840\u6027\u7684\u4e1c\u897f\uff0c\u53ea\u8981\u6b65\u4f10\u4e0d\u8fc8\u5f97\u592a\u5927\uff0c\u521b\u57df\u5373\u4f7f\u4f1a\u906d\u9047\u4e92\u8054\u7f51\u5bd2\u6f6e\uff0c\u5bf9\u9526\u6e56\u6765\u8bf4\uff0c\u4e5f\u4e0d\u4f1a\u662f\u591a\u5927\u7684\u9ebb\u70e6\uff0c\u6bd5\u7adf\u57fa\u7840\u6027\u7684\u4e1c\u897f\u4e5f\u5fc5\u987b\u8981\u505a\uff0c\u800c\u4e14\u8fd9\u5757\u8d44\u6e90\u80fd\u5c3d\u91cf\u6293\u5728\u81ea\u5df1\u624b\u91cc\uff0c\u4e5f\u8981\u5c3d\u53ef\u80fd\u7684\u6293\u5230\u81ea\u5df1\u7684\u624b\u91cc\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6bb5\u65f6\u95f4\u5012\u8fd8\u662f\u86ee\u7a7a\u95f2\u7684\uff0c\u65e0\u8bba\u662f\u4e09\u661f\u3001\u8fd8\u662f\u80cc\u5730\u6709\u4e09\u4e95\u3001\u534e\u590f\u7535\u5b50\u652f\u6491\u7684\u79d1\u738b\uff0c\u8fd8\u662f\u4e25\u5bb6\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\uff0c\u90fd\u5728\u5fd9\u7740\u8fdb\u884c\u4ea7\u4e1a\u5e03\u5c40\u3002</p><p>\u3000\u3000\u65f6\u95f4\u8fdb\u884c\u5230\u4e5d\u4e5d\u5e74\u56db\u6708\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u9634\u973e\u7ec8\u4e8e\u7ed9\u5439\u6563\u3002\u65b0\u79d1\u6280\u7ecf\u6d4e\u6b63\u84ec\u52c3\u7684\u6c79\u6d8c\u7740\u6d6a\u6f6e\uff0c\u867d\u8bf4\u5728\u915d\u917f\u7740\u6ce1\u6cab\uff0c\u5374\u5bf9\u6b64\u65f6\u7684\u7ecf\u6d4e\u590d\u82cf\u63d0\u4f9b\u76f8\u5f53\u5927\u7684\u52a9\u529b\u3002\u65b0\u52a0\u5761\u3001\u9a6c\u6765\u897f\u4e9a\u6700\u5148\u653e\u51fa\u7ecf\u6d4e\u590d\u82cf\u7684\u4fe1\u53f7\u3002\u97e9\u56fd\u4e09\u661f\u7684\u8d22\u52a1\u538b\u529b\u4e5f\u5927\u4e3a\u7f13\u89e3\uff0c\u8fde\u7eed\u516c\u5e03\u4e86\u5bf9\u4e2d\u56fd\u5730\u533a\u7684\u6295\u8d44\u8ffd\u52a0\u8ba1\u5212\uff0c\u79d1\u738b\u6709\u4e09\u4e95\u4e0e\u534e\u590f\u7535\u5b50\u7684\u652f\u6301\uff0c\u5728\u91d1\u5c71\u7684\u5236\u9020\u57fa\u5730\u4e5f\u65e5\u8d8b\u6709\u4e00\u79cd\u7e41\u8363\u7684\u666f\u8c61\uff0c\u5bf9\u4e8e\u4e25\u5bb6\u6765\u8bf4\uff0c\u4fe1\u901a\u94f6\u884c\u5c06\u4e1a\u52a1\u62d3\u5c55\u5230\u4e1c\u6d77\u7701\uff0c\u7b97\u662f\u4e3a\u5b8f\u4fe1\u5efa\u8bbe\u5728\u4e1c\u6d77\u7701\u7684\u5730\u4ea7\u4e1a\u52a1\u6253\u901a\u4e86\u4efb\u7763\u4e8c\u8109\u4e2d\u7684\u4e00\u8109\uff0c\u81f3\u5c11\u4e0d\u7528\u62c5\u5fc3\u8d44\u91d1\u4e0a\u7684\u538b\u529b\uff0c\u8fd9\u4e00\u5b9a\u53c8\u5f80\u5f80\u662f\u4e00\u5bb6\u4f01\u4e1a\u6700\u96be\u5f97\u7684\u4fdd\u8bc1\u3002</p><p>\u3000\u3000\u5f20\u606a\u95f2\u6765\u65e0\u4e8b\uff0c\u5468\u672b\u4e24\u5929\u966a\u675c\u98de\u3001\u65f6\u5b66\u658c\u7b49\u4eba\u4ee5\u53ca\u5546\u4e1a\u8ba1\u5212\u4e66\u63d0\u4ea4\u8005\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u90fd\u5173\u5728\u521b\u534f\u7684\u529e\u516c\u5ba4\u91cc\u7814\u7a76\u7814\u7a76\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u7684\u5546\u4e1a\u6a21\u5f0f\u3002</p><p>\u3000\u3000\u4e9a\u9a6c\u900a\u5728\u5317\u7f8e\u5730\u533a\u5927\u83b7\u6210\u529f\uff0c\u4f46\u662f\u4e9a\u9a6c\u900a\u7684\u4e1a\u52a1\u6a21\u5f0f\uff0c\u56fd\u5185\u5374\u4e0d\u80fd\u7b80\u5355\u7684\u6a21\u4eff\uff0c\u56fd\u5185\u7535\u5b50\u5546\u52a1\u7684\u53d1\u5c55\u751a\u81f3\u53ef\u4ee5\u7528\u4efb\u91cd\u800c\u9053\u8fdc\u6765\u5f62\u5bb9\u3002</p><p>\u3000\u3000\u201c\u5728\u5317\u7f8e\u5730\u533a\uff0c\u7f51\u4e0a\u4fe1\u7528\u5361\u652f\u4ed8\u76f8\u5f53\u666e\u53ca\uff0c\u800c\u5728\u4e2d\u56fd\u5219\u662f\u73b0\u91d1\u4ea4\u6613\u7684\u5927\u56fd\uff0c\u4eba\u4eec\u751a\u81f3\u90fd\u4e0d\u4e60\u60ef\u5230\u90ae\u5c40\u6c47\u6b3e\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5317\u7f8e\u5730\u533a\u6709\u8986\u76d6\u7f8e\u56fd\u751a\u81f3\u5168\u7403\u7684UPS\u3001FedEx\u7269\u6d41\u4f01\u4e1a\uff0c\u76f8\u5bf9\u5317\u7f8e\u5730\u533a\u7684\u7269\u4ef7\uff0c\u7269\u6d41\u8d39\u7528\u4f4e\u5ec9\uff0c\u4e2d\u56fd\u7684\u90ae\u653f\u5feb\u9012\u6536\u8d39\u6602\u8d35\uff0c\u670d\u52a1\u4e5f\u4e0d\u751a\u7406\u60f3\uff0c\u57ce\u9645\u5feb\u9012\u3001\u57ce\u5e02\u5feb\u9012\u516c\u53f8\u8986\u76d6\u533a\u57df\u72ed\u5c0f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5317\u7f8e\u5730\u533a\u7684\u6d88\u8d39\u8005\u6709\u90ae\u8d2d\u7684\u4f20\u7edf\uff0c\u65e9\u5c31\u5f62\u6210\u90ae\u8d2d\u5546\u4e1a\u6a21\u5f0f\uff0c\u5bf9\u7f51\u4e0a\u8d2d\u7269\u7684\u8ba4\u540c\u6781\u9ad8\uff0c\u5bf9\u4e2d\u56fd\u6d88\u8d39\u8005\u6765\u8bf4\uff0c\u7f51\u4e0a\u8d2d\u7269\u8ddf\u2018\u9694\u5c71\u4e70\u725b\u2019\u4e00\u6837\u4ee4\u4eba\u96be\u4ee5\u5b89\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5317\u7f8e\u5730\u533a\u6d88\u8d39\u8005\u5bf9\u4ea4\u8d27\u901f\u5ea6\u5bb9\u5fcd\u9ad8\uff0c\u4e9a\u9a6c\u900a\u901a\u5e38\u4f1a\u5728\u4e03\u4e2a\u5de5\u4f5c\u65e5\u4e4b\u540e\u4ea4\u8d27\uff0c\u800c\u4e2d\u56fd\u6d88\u8d39\u8005\u7684\u8010\u5fc3\u5c31\u975e\u5e38\u7684\u6709\u9650\u2026\u2026\u201d</p><p>\u3000\u3000\u5728\u539f\u5546\u4e1a\u8ba1\u5212\u4e66\u4e2d\u6ca1\u6709\u7ed9\u91cd\u89c6\u7684\u7ec6\u5904\uff0c\u4e00\u9879\u9879\u7684\u7ed9\u5217\u5199\u5728\u521b\u534f\u529e\u516c\u5ba4\u7684\u9ed1\u677f\u4e0a\u62ff\u51fa\u6765\u8ba8\u8bba\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u5efa\u90ba\u5c0f\u6709\u540d\u6c14\u7684\u5b66\u751f\u4e66\u5546\uff0c\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u5728\u56fe\u4e66\u5b9e\u4f53\u9500\u552e\u4e0a\u5df2\u7ecf\u6253\u4e0b\u4e86\u4e00\u5b9a\u7684\u57fa\u7840\uff0c\u5728\u5efa\u90ba\u56fe\u4e66\u5546\u57ce\u62e5\u6709\u5360\u5730\u4e24\u767e\u591a\u5e73\u7c73\u7684\u5b9e\u4f53\u4e66\u94fa\u4e3a\u4ed6\u4e4b\u524d\u7684\u9ad8\u6821\u9500\u552e\u7f51\u7edc\u63d0\u4f9b\u4ed3\u50a8\u3001\u7269\u6d41\u914d\u9001\u3001\u8d27\u6e90\u4f9b\u5e94\u7b49\u65b9\u9762\u7684\u652f\u6301\uff0c\u4e4b\u524d\u521b\u7acb\u7684\u56fe\u4e66\u5728\u7ebf\u9500\u552e\u7f51\u7edc\u867d\u7136\u53ea\u9650\u4e8e\u5efa\u90ba\u7684\u51e0\u6240\u9ad8\u6821\uff0c\u4f46\u662f\u5df2\u7ecf\u521d\u6b65\u8d70\u51fa\u5b9e\u4f53\u5546\u52a1\u5411\u865a\u62df\u5546\u52a1\u6a21\u5f0f\u8f6c\u53d8\u7684\u7b2c\u4e00\u6b65\uff0c\u4ed6\u4eec\u7ebf\u4e0a\u9500\u552e\u56fe\u4e66\u4e0e\u97f3\u50cf\u5236\u54c1\u7684\u4e2a\u4eba\u7f51\u7ad9\uff0c\u4e5f\u9500\u552e\u4e00\u4e9b\u9762\u5411\u5b66\u751f\u7684\u7535\u5b50\u4ea7\u54c1\uff0c\u751a\u81f3\u8fd8\u4e0e\u4e00\u5bb6\u82b1\u5e97\u8054\u5408\u8d77\u6765\u5728\u7f51\u4e0a\u63a8\u51fa\u8ba2\u82b1\u4e1a\u52a1\u3002\u4f46\u662f\u4e4b\u524d\u7684\u7f51\u4e0a\u9500\u552e\u4e5f\u53ea\u662f\u9650\u4e8e\u5efa\u90ba\u7684\u9ad8\u6821\u7fa4\uff0c\u7ec4\u7ec7\u52e4\u5de5\u4fed\u5b66\u7684\u5927\u5b66\u751f\u9a91\u4e0a\u81ea\u884c\u8f66\u5c31\u7ec4\u6210\u5b8c\u5584\u7684\u914d\u9001\u961f\u4f0d\uff0c\u800c\u4e14\u9488\u5bf9\u7684\u53c8\u662f\u5927\u5b66\u751f\u7fa4\u4f53\uff0c\u4e00\u65e6\u5c06\u6574\u4e2a\u5546\u4e1a\u6a21\u5f0f\u63a8\u5e7f\u5230\u5168\u56fd\uff0c\u9762\u5bf9\u6574\u4e2a\u4e0a\u7f51\u6d88\u8d39\u8005\u4eba\u7fa4\uff0c\u5176\u4e2d\u589e\u52a0\u7684\u56f0\u96be\u662f\u5f88\u96be\u60f3\u8c61\u7684\u3002</p><p>\u3000\u3000\u76f8\u5bf9\u6765\u8bf4\uff0c\u67b6\u6784\u4e00\u4e2a\u4e92\u8054\u7f51\u9500\u552e\u5e73\u53f0\u5012\u7b80\u5355\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u521b\u610f\u4e0e\u6784\u601d\u6c38\u8fdc\u662f\u5ec9\u4ef7\u7684\uff0c\u771f\u6b63\u6709\u4ef7\u503c\u7684\u662f\u5c06\u521b\u610f\u4e0e\u6784\u601d\u4e00\u6b65\u6b65\u5b9e\u73b0\u51fa\u6765\u7684\u80fd\u529b\uff0c\u6295\u8d44\u5546\u4e1a\u65b9\u6848\uff0c\u8fd8\u4e0d\u5982\u8bf4\u6295\u8d44\u63d0\u51fa\u5546\u4e1a\u65b9\u6848\u7684\u4eba\u3002\u6295\u5bf9\u4e86\u4eba\uff0c\u4e00\u4e2a\u770b\u4e0a\u53bb\u62d9\u52a3\u7684\u5546\u4e1a\u65b9\u6848\u4e5f\u6709\u76c8\u5229\u7684\u53ef\u80fd\uff1b\u6295\u9519\u4e86\u4eba\uff0c\u518d\u5b8c\u7f8e\u7684\u5546\u4e1a\u65b9\u6848\u90fd\u662f\u586b\u4e0d\u5c3d\u7684\u65e0\u5e95\u6d1e\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u675c\u98de\u6700\u7ec8\u51b3\u5b9a\u7531\u521b\u57df\u5411\u9a6c\u9f99\u3001\u6768\u4f1f\u6bc5\u7b49\u4eba\u5408\u4f19\u516c\u53f8\u5efa\u90ba\u79d1\u6587\u516c\u53f8\u6ce8\u8d44\u4e94\u767e\u4e07\u6210\u7acb\u80a1\u4efd\u6709\u9650\u516c\u53f8\uff0c\u5efa\u7acb\u6536\u96c6\u3001\u9500\u552e\u4e2d\u56fd\u53ef\u4f9b\u4e66\u6570\u636e\u5e93\uff0c\u53d1\u5c55\u5353\u57df\u7f51\uff08JOYU.com\uff09\u5728\u7ebf\u56fe\u4e66\u9500\u552e\u4e1a\u52a1\uff0c\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u5efa\u90ba\u79d1\u6587\u7684\u521b\u59cb\u4eba\u5219\u5411\u5b66\u6821\u7533\u8bf7\u4f11\u5b66\uff0c\u5c06\u7cbe\u529b\u90fd\u6295\u5165\u5230\u516c\u53f8\u7684\u53d1\u5c55\u4e0a\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u5927\u5b66\u5728\u9ad8\u6821\u4ea7\u4e1a\u5316\u53d1\u5c55\u4e0a\u4e00\u76f4\u6ca1\u6709\u7279\u522b\u5927\u7684\u6210\u5c31\uff0c\u50cf\u5317\u5927\u6709\u65b9\u6b63\u3001\u9752\u9e1f\uff0c\u6e05\u534e\u6709\u540c\u65b9\u3001\u7d2b\u5149\uff0c\u4e1c\u5927\u79d1\u6280\u5f00\u53d1\u603b\u516c\u53f8\u65d7\u4e0b\u5374\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u6709\u540d\u6c14\u7684\u79d1\u6280\u516c\u53f8\uff0c\u8fd9\u4e5f\u662f\u4e1c\u5927\u5728\u56fd\u5185\u5e95\u6c14\u4e0d\u591f\u5f3a\u786c\u7684\u8f6f\u808b\u3002</p><p>\u3000\u3000\u8fd9\u4e24\u5e74\uff0c\u4e1c\u5927\u5728\u4ea7\u4e1a\u5316\u4e0a\u4e5f\u505a\u51fa\u5f88\u5927\u7684\u52aa\u529b\uff0c\u5c06\u6821\u56ed\u4e1c\u4fa7\u4e24\u767e\u591a\u4ea9\u5730\u62ff\u4e0b\u6765\u6295\u8d44\u5efa\u4e1c\u5927\u79d1\u6280\u56ed\uff0c\u6276\u6301\u79d1\u6280\u521b\u4e1a\u9879\u76ee\uff0c\u8fd8\u4e0e\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u8054\u5408\u6210\u7acb\u79d1\u6280\u56ed\u521b\u4e1a\u57fa\u91d1\uff0c\u81ea\u7136\u4e5f\u539a\u7740\u8138\u76ae\u5c06\u521b\u57df\u516c\u53f8\u5f53\u6210\u4e1c\u5927\u4ea7\u4e1a\u5316\u53d1\u5c55\u7684\u5178\u578b\uff0c\u6bd5\u7adf\u521b\u57df\u7f51\u6821\uff0c\u4e1c\u5927\u4e5f\u6301\u670930\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u521b\u57df\u5411\u79d1\u6587\u516c\u53f8\u6ce8\u8d44\u53d1\u5c55\u5353\u57df\u7f51\u5728\u7ebf\u56fe\u4e66\u9500\u552e\u4e1a\u52a1\uff0c\u4e5f\u5f97\u5230\u5b66\u6821\u7684\u5927\u529b\u652f\u6301\uff0c\u6821\u65b9\u4e0d\u4ec5\u652f\u6301\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u4f11\u5b66\u521b\u4e1a\uff0c\u4e1c\u5927\u79d1\u6280\u5f00\u53d1\u603b\u516c\u53f8\u8fd8\u989d\u5916\u6ce8\u8d44\u4e09\u767e\u4e07\uff0c\u4e0e\u521b\u57df\u5171\u540c\u6301\u5efa\u90ba\u79d1\u658749\uff05\u7684\u80a1\u6743\uff0c\u9a6c\u529b\u3001\u6768\u4f1f\u6bc5\u7b49\u521b\u59cb\u4ee5\u539f\u6709\u8d44\u6e90\u6301\u6709\u79d1\u6587\u516c\u53f851\uff05\u7684\u80a1\u6743\u3002</p><p>\u3000\u3000\u65b0\u7684\u79d1\u6587\u516c\u53f8\u4e5f\u5c06\u8fc1\u5165\u4e1c\u5927\u79d1\u6280\u56ed\u5185\u8fdb\u884c\u5165\u6b63\u5f0f\u5f00\u5c55\u5728\u7ebf\u56fe\u4e66\u9500\u552e\u4e1a\u52a1\u4e4b\u524d\u7684\u7b79\u5907\u3002</p></article>"}}}]);
