(window.webpackJsonp=window.webpackJsonp||[]).push([[1015],{1038:function(p){p.exports={chapterTitle:null,title:"\u7b2c822\u7ae0 \u8c0b\u5212",content:"<article><h2>\u7b2c822\u7ae0 \u8c0b\u5212</h2><p>\u3000\u3000\u5f20\u606a\u75db\u5f97\u5472\u7259\u54a7\u5634\uff0c\u542c\u7fdf\u4e39\u9752\u8fd8\u5728\u8033\u6735\u8bf4\u8fd9\u79cd\u4e0d\u7740\u8fb9\u9645\u7684\u8bdd\uff0c\u53ea\u6709\u4fa7\u8fc7\u8eab\u5b50\u9762\u671d\u7740\u8f66\u7a97\u800c\u5750\uff0c\u53cc\u817f\u4ea4\u53e0\u7740\uff0c\u4e8c\u5341\u5c81\u7684\u5e74\u8f7b\u8eab\u4f53\u7684\u786e\u5f88\u597d\uff0c\u4f46\u662f\u4e24\u817f\u4e4b\u95f4\u9686\u8d77\u4e00\u56e2\u672a\u514d\u592a\u96be\u770b\u4e86\uff1f\u4ece\u8f66\u7a97\u6a21\u7cca\u7684\u5012\u5f71\u91cc\u770b\u5230\u7fdf\u4e39\u9752\u77a5\u773c\u770b\u7740\u90a3\u91cc\u76b1\u7709\u5476\u5634\uff0c\u4f3c\u4e4e\u5728\u770b\u5f88\u4e11\u964b\u7684\u4e1c\u897f\uff0c\u5f20\u606a\u54ed\u7b11\u4e0d\u5f97\uff0c\u8ddf\u8fd9\u5a18\u4eec\uff0c\u8981\u4e48\u81ea\u5df1\u5360\u4e0a\u98ce\uff0c\u8981\u4e48\u5979\u5360\u4e0a\u98ce\uff0c\u5d14\u90c1\u66fc\u8fd8\u5728\u8f66\u91cc\u5462\uff0c\u8fd9\u65f6\u5019\u53ea\u80fd\u4ece\u8f66\u5939\u888b\u91cc\u62ff\u4e86\u62a5\u7eb8\u644a\u5f00\u5728\u819d\u76d6\u4e0a\u906e\u4e11\u3002</p><p>\u3000\u3000\u89c1\u5f20\u606a\u8fd9\u6837\u5b50\uff0c\u7fdf\u4e39\u9752\u5012\u4e0d\u518d\u5fc3\u614c\u4e86\uff0c\u77e5\u9053\u4ed6\u6015\u5728\u5d14\u90c1\u66fc\u9762\u524d\u51fa\u4e11\uff0c\u62bf\u7740\u5634\u5ae3\u7136\u4e00\u7b11\uff0c\u6709\u7740\u8c03\u620f\u7537\u5b69\u5b50\u7684\u4e50\u8da3\uff0c\u521a\u9192\u6765\u65f6\u53d1\u73b0\u4e24\u4eba\u7684\u5927\u817f\u8089\u9694\u7740\u8584\u8584\u7684\u4e24\u5c42\u5e03\u7d27\u8d34\u7740\uff0c\u5979\u63d0\u81c0\u5750\u7740\uff0c\u8ba9\u4e24\u4eba\u4e0d\u8981\u6328\u8fd9\u4e48\u7d27\uff0c\u8fd9\u65f6\u5019\u8212\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u653e\u677e\u7684\u5750\u4e0b\u6765\uff0c\u8fd8\u5f2f\u4e0b\u8170\u53bb\u770b\u5f20\u606a\u819d\u4e0a\u7684\u62a5\u7eb8\uff1a\u201c\u8fd9\u662f\u6628\u5929\u7684\u62a5\u7eb8\u5417\uff1f\u6211\u8fd8\u6ca1\u6709\u770b\u8fc7\u5462\uff1f\u201d\u5979\u8fd9\u4e00\u653e\u677e\uff0c\u4e24\u4eba\u7684\u5927\u817f\u518d\u6b21\u8d34\u7d27\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u60f3\u80fd\u5c3d\u5feb\u5c06\u819d\u76d6\u4e0a\u906e\u4e11\u7684\u62a5\u7eb8\u62ff\u5f00\u4ece\u8840\u8109\u8d32\u5f20\u4e2d\u6062\u590d\u5e73\u9759\uff0c\u53ef\u662f\u9694\u7740\u4e24\u5c42\u8584\u5e03\u6e05\u6670\u65e0\u6bd4\u7684\u4f20\u6765\u7fdf\u4e39\u9752\u5927\u817f\u60ca\u4eba\u7684\u5f39\u6027\u4e0e\u6e29\u70ed\u7684\u4f53\u6e29\uff0c\u771f\u53eb\u4eba\u597d\u53d7\uff0c\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u5979\u5f2f\u8fc7\u8170\u6765\u65f6\u62ff\u90a3\u6839\u5ae9\u82e5\u9752\u8471\u7684\u5c3e\u6307\u64a9\u5f00\u8033\u8fb9\u7684\u5934\u53d1\uff0c\u9732\u51fa\u6676\u83b9\u5254\u900f\u5f97\u80fd\u770b\u89c1\u7ec6\u5fae\u7684\u8840\u7ba1\u7684\u8033\u5ed3\u4e0e\u957f\u7fd8\u7684\u776b\u6bdb\uff0c\u5979\u8170\u5f2f\u4e0b\u6765\uff0c\u80f8\u90e8\u5448\u6c34\u6ef4\u5f62\u5760\u7740\uff0c\u624b\u81c2\u5c31\u5dee\u4e00\u70b9\u8981\u7ed9\u78b0\u4e0a\uff0c\u5f20\u606a\u90fd\u4e0d\u77e5\u9053\u81ea\u5df1\u80fd\u4e0d\u80fd\u5fcd\u4f4f\u4e0d\u5047\u88c5\u65e0\u610f\u7684\u62ac\u4e00\u4e0b\u624b\u3002</p><p>\u3000\u3000\u8f66\u5230\u91d1\u5c71\u65f6\uff0c\u5f20\u606a\u6574\u4e2a\u4eba\u51e0\u4e4e\u80fd\u7b97\u5f97\u4e0a\u662f\u8737\u7f29\u5728\u8f66\u53a2\u91cc\u3002</p><p>\u3000\u3000\u867d\u7136\u8def\u4e0a\u6ca1\u6709\u803d\u6401\uff0c\u8d76\u5230\u91d1\u5c71\u5e02\u91cc\uff0c\u5df2\u7ecf\u662f\u4e0a\u5348\u5341\u70b9\u4e86\u3002\u9a6c\u6d77\u9f99\u4ed6\u4eec\u65e9\u4e24\u4e2a\u5c0f\u65f6\u5230\u91d1\u5c71\uff0c\u5d14\u90c1\u6052\u7b49\u4e09\u540d\u65b0\u534e\u793e\u8bb0\u8005\u5df2\u7ecf\u8fdb\u5165\u533b\u9662\u8fdb\u884c\u89c2\u5bdf\u6cbb\u7597\uff0c\u7ed9\u9a6c\u6d77\u9f99\u63a7\u5236\u4f4f\u7684\u4e09\u4e2a\u4eba\u4e5f\u79fb\u4ea4\u7ed9\u6c5f\u5357\u7701\u516c\u5b89\u5385\u4e86\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5979\u6bcd\u4eb2\u53ca\u5979\u5ac2\u5b50\u8d77\u65e9\u4ece\u5317\u4eac\u4e58\u98de\u673a\u5012\u6bd4\u4ed6\u4eec\u63d0\u524d\u4e00\u817f\u8d76\u5230\u5efa\u90ba\u3002</p><p>\u3000\u3000\u91d1\u5c71\u8fd9\u8fb9\u7684\u5de5\u4f5c\u4eba\u5458\u5df2\u7ecf\u5b89\u6392\u9a6c\u6d77\u9f99\u7b49\u4eba\u5728\u9152\u5e97\u91cc\u4f4f\u4e0b\uff0c\u5f20\u606a\u5148\u966a\u5d14\u90c1\u66fc\u53bb\u5728\u533b\u9662\u770b\u5230\u5979\u54e5\u4ed6\u4eec\uff0c\u542c\u8bf4\u4e09\u4e2a\u4eba\u867d\u7136\u770b\u4e0a\u53bb\u6709\u4e9b\u60e8\u4e0d\u5fcd\u7779\u4f46\u7ecf\u8fc7\u68c0\u67e5\u90fd\u6ca1\u6709\u4ec0\u4e48\u5927\u788d\uff0c\u6881\u4f1f\u6cd5\u3001\u6c5f\u5357\u7701\u516c\u5b89\u5385\u5385\u957f\u8c2d\u4ee5\u91d1\u4ee5\u53ca\u65b0\u534e\u793e\u6c5f\u5357\u5206\u793e\u7684\u8d1f\u8d23\u4eba\u521a\u521a\u90fd\u5230\u533b\u9662\u91cc\u6170\u95ee\u8fc7\uff0c\u79bb\u5f00\u8fd8\u6ca1\u6709\u591a\u5c11\u65f6\u95f4\u3002\u5f20\u606a\u4e0d\u6025\u7740\u53bb\u627e\u6881\u4f1f\u6cd5\uff0c\u8ba9\u4ed6\u5148\u7ed9\u7701\u516c\u5b89\u5385\u65bd\u52a0\u5373\u53ef\uff0c\u4e0d\u8fc7\u600e\u4e48\u8bf4\uff0c\u90fd\u8981\u5047\u6a21\u5047\u6837\u7684\u8ddf\u7740\u5d14\u90c1\u66fc\u5148\u53bb\u95ee\u5019\u5d14\u90c1\u6052\u3002\u597d\u6b79\u4e5f\u8981\u8ba9\u5d14\u90c1\u6052\u77e5\u9053\u662f\u8c01\u8d39\u8fd9\u4e48\u5927\u52b2\u6551\u4ed6\u8131\u56f0\uff0c\u5d14\u90c1\u6052\u77e5\u4e0d\u77e5\u9053\u4e5f\u65e0\u6240\u8c13\uff0c\u5173\u952e\u8981\u8ba9\u5d14\u6587\u6bc5\u77e5\u9053\u8c01\u8d39\u8fd9\u4e48\u5927\u52b2\u6551\u4ed6\u513f\u5b50\u8131\u56f0\uff0c\u8003\u8651\u7740\u662f\u4e0d\u662f\u5c06\u5f53\u5e74\u6d77\u5dde\u53d1\u751f\u7684\u4e8b\u60c5\u8bf4\u51fa\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8ddf\u5f20\u606a\u5750\u5728\u533b\u62a4\u5ba4\u5916\u9762\u7684\u5851\u6599\u6905\u4e0a\uff0c\u8ba9\u5d14\u90c1\u66fc\u5148\u8fdb\u53bb\u5bb6\u4eba\u56e2\u805a\uff0c\u5979\u770b\u7740\u5f20\u606a\u5634\u89d2\u6302\u7740\u5f97\u610f\u7684\u7b11\u5bb9\uff0c\u8f7b\u58f0\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u662f\u90a3\u79cd\u505a\u597d\u4e8b\u4e0d\u7559\u540d\u7684\u597d\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e8b\u524d\u53ef\u6ca1\u6709\u60f3\u5230\u6709\u597d\u5904\uff0c\u4f46\u662f\u770b\u5230\u597d\u5904\u4e0d\u968f\u624b\u62ff\u70b9\uff0c\u5c82\u4e0d\u662f\u592a\u50bb\uff1f\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u62bf\u7740\u5634\u800c\u7b11\uff0c\u8fd9\u65f6\u5019\u5d14\u90c1\u66fc\u7ad9\u5728\u95e8\u524d\u8bf7\u4ed6\u4eec\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u4e00\u8d77\u8fdb\u4e86\u533b\u62a4\u5ba4\uff0c\u5d14\u90c1\u6052\u4e09\u4e2a\u4eba\u770b\u4e0a\u53bb\u662f\u6709\u4e9b\u60e8\u4e0d\u5fcd\u7779\uff0c\u8981\u4e0d\u662f\u5d14\u90c1\u6052\u90a3\u6807\u5fd7\u6027\u7684\u5927\u80e1\u5b50\uff0c\u5f20\u606a\u8fd8\u771f\u7684\u5f88\u96be\u4ece\u5d14\u90c1\u66fc\u7684\u63cf\u8ff0\u4e2d\u8ba4\u51fa\u8c01\u662f\u5d14\u90c1\u6052\u3002</p><p>\u3000\u3000\u201c\u6211\u662f\u5c0f\u5d14\u8001\u5e08\u7684\u5b66\u751f\uff0c\u9ad8\u4e2d\u65f6\u5c31\u662f\u5979\u7684\u5b66\u751f\uff0c\u5230\u4e86\u5927\u5b66\u8fd8\u8131\u79bb\u4e0d\u4e86\u5979\u7684\u624b\u638c\uff0c\u201d\u5f20\u606a\u8ddf\u5d14\u90c1\u66fc\u4e00\u5bb6\u4eba\u81ea\u6211\u4ecb\u7ecd\u9053\uff0c\u201c\u4f60\u4eec\u5524\u6211\u5f20\u606a\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u7684\u6bcd\u4eb2\u7ed9\u4eba\u611f\u89c9\u5c31\u50cf\u4ece\u5b66\u6821\u91cc\u5f53\u4e86\u51e0\u5341\u5e74\u6559\u5e08\u9000\u4f11\u7684\u666e\u901a\u8001\u592a\u592a\uff0c\u5750\u5728\u75c5\u5e8a\u8fb9\uff0c\u5934\u53d1\u82b1\u767d\uff0c\u770b\u7740\u5f20\u606a\u4ed6\u4eec\u8fd8\u5e2e\u7740\u9012\u51f3\u5b50\u51fa\u6765\uff0c\u800c\u4e0d\u662f\u5355\u7eaf\u7684\u7ad9\u5728\u90a3\u91cc\u62db\u547c\u3002\u5d14\u90c1\u66fc\u7684\u5ac2\u5b50\u662f\u4e2a\u4e09\u5341\u5c81\u5de6\u53f3\u7684\u6e05\u4e3d\u5c11\u5987\uff0c\u5750\u5728\u75c5\u5e8a\u91cc\uff0c\u773c\u775b\u90fd\u54ed\u7ea2\u4e86\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5979\u6bcd\u4eb2\u8fd8\u6709\u5979\u5ac2\u5b50\u4ee5\u53ca\u8eba\u5728\u75c5\u5e8a\u4e0a\u8138\u7ed9\u5305\u7684\u50cf\u53ea\u68d5\u5b50\u7684\u5d14\u90c1\u6052\u90fd\u9887\u4e3a\u7591\u60d1\u7684\u770b\u7740\u5f20\u606a\uff0c\u6709\u4e9b\u4e0d\u660e\u6240\u4ee5\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u5c31\u662f\u6211\u8fd9\u4e2a\u5b66\u751f\u53ca\u65f6\u8054\u7cfb\u5efa\u90ba\u6668\u62a5\u7684\u8bb0\u8005\u627e\u4f60\u4eec\u88ab\u5173\u62bc\u7684\u5730\u65b9\u624d\u6551\u51fa\u4f60\u4eec\uff0c\u5efa\u90ba\u6668\u62a5\u4e5f\u6d3e\u51fa\u8bb0\u8005\u5230\u7ae0\u5dde\u8c03\u67e5\u76d7\u77ff\u95ee\u9898\u3002\u201d\u5d14\u90c1\u66fc\u8bf4\u9053\uff0c\u5979\u4e5f\u4e0d\u77e5\u9053\u8be5\u5982\u4f55\u624d\u80fd\u51c6\u786e\u5c06\u5f20\u606a\u53d1\u6325\u7684\u4f5c\u7528\u4ee5\u53ca\u5979\u5fc3\u91cc\u7684\u5de8\u5927\u7591\u60d1\u8bf4\u51fa\u6765\uff0c\u4ed3\u4fc3\u4e4b\u4e0b\u4e5f\u53ea\u80fd\u5148\u8fd9\u4e48\u4ecb\u7ecd\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u8ddf\u8001\u5d14\u901a\u7535\u8bdd\u7684\u90a3\u4e2a\u5b69\u5b50\uff0c\u8fd8\u662f\u8981\u611f\u8c22\u4f60\u5462\uff0c\u8981\u4e0d\u662f\u4f60\u5e2e\u5fd9\u8054\u7cfb\u5efa\u90ba\u6668\u62a5\u7684\u8bb0\u8005\uff0c\u771f\u4e0d\u77e5\u9053\u90c1\u6052\u4ed6\u5728\u7ae0\u5dde\u8981\u906d\u4ec0\u4e48\u7f6a\u2026\u2026\u201d\u5d14\u90c1\u66fc\u7684\u6bcd\u4eb2\u70ed\u5207\u7684\u6293\u4f4f\u5f20\u606a\u7684\u624b\uff0c\u5f20\u606a\u54a7\u4e86\u4e00\u4e0b\u5634\uff0c\u53c8\u4e0d\u80fd\u544a\u8bc9\u5979\u81ea\u5df1\u5176\u5b9e\u505a\u5f97\u66f4\u591a\uff0c\u8fd9\u4e9b\u4e1c\u897f\u4f3c\u4e4e\u8981\u4ed6\u4eec\u81ea\u5df1\u53d1\u73b0\u624d\u4e0d\u81f3\u4e8e\u53d8\u5473\u3002</p><p>\u3000\u3000\u65e2\u7136\u4e0d\u80fd\u9080\u66f4\u591a\u7684\u529f\uff0c\u5f20\u606a\u53ea\u80fd\u5148\u65e0\u8da3\u7684\u544a\u8f9e\u79bb\u5f00\uff0c\u8d76\u7740\u53bb\u627e\u6881\u4f1f\u6cd5\u3002</p><p>\u3000\u3000\u8d70\u51fa\u533b\u9662\u7684\u5927\u5385\uff0c\u7fdf\u4e39\u9752\u5a07\u7b11\u7740\u95ee\u5f20\u606a\uff1a\u201c\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u5c0f\u5931\u843d\uff1f\u90fd\u6ca1\u6709\u70ed\u60c5\u7684\u9080\u4f60\u53bb\u5317\u4eac\u505a\u5ba2\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u5f53\u505a\u597d\u4e8b\u4e0d\u7559\u540d\u5427\u3002\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u518d\u8bf4\u6211\u559c\u6b22\u522b\u4eba\u80cc\u540e\u8bae\u8bba\u6211\uff0c\u5f53\u9762\u4e00\u60ca\u4e00\u4e4d\u7684\u591a\u865a\u4f2a\u554a\uff01\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u4ed6\u4eec\u8fd8\u80fd\u5728\u8d76\u5f80\u91d1\u5c71\u7684\u8f66\u4e0a\u8865\u89c9\uff0c\u6881\u4f1f\u6cd5\u53ef\u662f\u6574\u591c\u90fd\u6ca1\u6709\u7761\u597d\u3002</p><p>\u3000\u3000\u6c81\u56ed\u662f\u996e\u9a6c\u6cb3\u8fb9\u7684\u4eff\u53e4\u5efa\u7b51\u7684\u4f11\u95f2\u5c71\u5e84\u5f0f\u9152\u5e97\uff0c\u4e2d\u5348\u603b\u8981\u627e\u5730\u65b9\u7528\u9910\uff0c\u5f20\u606a\u5c31\u5728\u6c81\u56ed\u5bb4\u8bf7\u6881\u4f1f\u6cd5\u3002</p><p>\u3000\u3000\u8f66\u505c\u5728\u6c81\u56ed\u524d\u7684\u505c\u8f66\u573a\u4e0a\uff0c\u4e0b\u8f66\u5c31\u95fb\u5230\u996e\u9a6c\u6cb3\u90a3\u8fb9\u4f20\u6765\u7684\u523a\u9f3b\u5473\uff0c\u8fd9\u4e5f\u4e0d\u96be\u60f3\u8c61\u4e3a\u4f55\u6c81\u56ed\u5728\u996d\u65f6\u4f1a\u95e8\u5ead\u51b7\u843d\u4e86\u3002\u6881\u4f1f\u6cd5\u4e0b\u4e86\u8f66\uff0c\u6ca1\u6709\u5047\u8165\u8165\u7684\u53bb\u6342\u4ec0\u4e48\u9f3b\u5b50\uff0c\u8fd8\u662f\u8ddf\u8eab\u8fb9\u7684\u79d8\u4e66\u957f\u65e0\u5948\u7684\u7b11\u7740\u8bf4\uff1a\u201c\u5f20\u606a\u603b\u662f\u60f3\u7740\u65b9\u7684\u63d0\u9192\u6211\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u771f\u8be5\u8ba9\u4ed6\u53bb\u770b\u770b\u4e0a\u5348\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u65f6\u7684\u70ed\u95f9\uff1f\u201d\u6c5f\u5357\u7701\u653f\u5e9c\u79d8\u4e66\u957f\u803f\u5efa\u5fe0\u8bf4\u9053\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u9526\u6e56\u662f\u4f01\u4e1a\uff0c\u4ed6\u4eec\u7684\u505a\u6cd5\u5e76\u6ca1\u6709\u9519\u3002\u4f60\u6211\u90fd\u6e05\u695a\uff0c\u9526\u6e56\u7ed9\u51fa\u8fd9\u4e2a\u62a5\u4ef7\uff0c\u53c8\u505a\u51fa\u8fd9\u4e48\u6709\u51b3\u5fc3\u7684\u627f\u8bfa\uff0c\u5df2\u7ecf\u76f8\u5f53\u6709\u8bda\u610f\u4e86\uff1b\u8981\u662f\u8fd8\u6709\u56f0\u96be\u4e0d\u80fd\u514b\u670d\uff0c\u90a3\u53ea\u662f\u6211\u4eec\u8fd9\u8fb9\u8bda\u610f\u4e0d\u8db3\u3002\u201d</p><p>\u3000\u3000\u4e3a\u4f55\u6c11\u8425\u4f01\u4e1a\u5728\u56fd\u5185\u7684\u5730\u4f4d\u8981\u8fdc\u8fdc\u4f4e\u4e8e\u56fd\u4f01\u3001\u6c11\u4f01\uff0c\u5173\u952e\u662f\u80fd\u5e73\u7b49\u770b\u5f85\u6c11\u4f01\u7684\u653f\u5e9c\u592a\u5c11\u4e86\u3002\u5c31\u50cf\u5b98\u5458\u591a\u81ea\u79f0\u662f\u516c\u4ec6\uff0c\u4f46\u662f\u90e8\u5206\u7684\u516c\u4ec6\u4e5f\u592a\u7a77\u51f6\u6781\u6076\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u7684\u8fd9\u79cd\u6c14\u5ea6\uff0c\u662f\u803f\u5efa\u5fe0\u8fd8\u4e00\u65f6\u65e0\u6cd5\u7406\u89e3\u7684\uff0c\u6bd5\u7adf\u803f\u5efa\u5fe0\u624d\u4e3a\u6881\u4f1f\u6cd5\u5de5\u4f5c\u4e0d\u5230\u534a\u5e74\u65f6\u95f4\uff0c\u8fd8\u7422\u78e8\u4e0d\u900f\u6881\u4f1f\u6cd5\u7684\u813e\u6c14\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u4e5f\u6ca1\u6709\u7279\u610f\u8981\u5f20\u606a\u4ed6\u4eec\u51fa\u6765\u8fce\u63a5\uff0c\u800c\u662f\u5f84\u76f4\u8ba9\u670d\u52a1\u5458\u9886\u4ed6\u4eec\u8d70\u5230\u5f20\u606a\u5148\u8d76\u5230\u5305\u4e0b\u6765\u7684\u4e00\u4e2a\u504f\u9662\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u8fd9\u6bb5\u65f6\u95f4\u4e00\u76f4\u7559\u5728\u91d1\u5c71\u7684\u8bb8\u9e3f\u4f2f\u8bf4\u8bdd\uff0c\u8fd8\u6709\u9a6c\u6d77\u9f99\u3001\u7b80\u5fd7\u5eb7\u7b49\u4eba\uff0c\u5f20\u5955\u8fd9\u6b21\u4e5f\u8ddf\u7740\u9a6c\u6d77\u9f99\u4ed6\u4eec\u4e00\u8d77\u53bb\u7ae0\u5dde\u4e86\uff0c\u591c\u91cc\u4e5f\u4e00\u8d77\u64a4\u51fa\u7ae0\u5dde\u3002\u542c\u7740\u5916\u9762\u8bf4\u6881\u7701\u957f\u5230\u4e86\uff0c\u5f20\u606a\u624d\u4e0e\u8bb8\u9e3f\u4f2f\u5306\u5fd9\u8d70\u51fa\u5385\u95e8\u8fce\u63a5\u3002</p><p>\u3000\u3000\u201c\u6211\u539f\u60f3\u5f85\u6668\u66e6\u7eb8\u4e1a\u7684\u6536\u8d2d\u4e8b\u5b9c\u89e3\u51b3\u4e4b\u540e\u624d\u56de\u8fc7\u5934\u53bb\u6574\u6cbb\u7ae0\u5dde\u7684\u76d7\u77ff\u95ee\u9898\uff0c\u4eca\u5929\u4e0a\u5348\u6211\u5728\u7701\u653f\u5e9c\u529e\u516c\u4f1a\u8bae\u4e0a\u5c06\u9526\u6e56\u63d0\u4f9b\u7684\u5168\u8d44\u6536\u8d2d\u65b9\u6848\u62ff\u51fa\u6765\u2026\u2026\u201d\u6881\u4f1f\u6cd5\u6267\u7740\u5f20\u606a\u7684\u624b\uff0c\u8fb9\u8d70\u8fb9\u8bf4\uff0c\u201c\u62ff\u51fa\u8fd9\u4e2a\u65b9\u6848\uff0c\u603b\u8981\u7559\u4e9b\u65f6\u95f4\u7ed9\u4ed6\u4eec\u53bb\u8ba8\u8bba\uff0c\u7ae0\u5dde\u53d1\u751f\u8fd9\u4e48\u5927\u7684\u6848\u5b50\uff0c\u662f\u4e0d\u80fd\u62d6\u5ef6\u4e0b\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd8\u6709\u4e00\u4ef6\u4e8b\u5fd8\u4e86\u63d0\u524d\u8ddf\u6881\u7701\u957f\u8bf4\uff0c\u5728\u65b0\u534e\u793e\u4e09\u540d\u8bb0\u8005\u7ed9\u6551\u51fa\u7684\u540c\u65f6\uff0c\u53ef\u80fd\u4e3b\u4f7f\u975e\u6cd5\u62d8\u7981\u7684\u5e55\u540e\u4eba\u7269\u5510\u82f1\u80b2\u4e0e\u91d1\u5c71\u534e\u7a00\u7684\u8001\u603b\u5510\u82f1\u57f9\u5728\u5efa\u90ba\u8b66\u65b9\u7684\u626b\u9ec4\u6253\u975e\u4e13\u9879\u6574\u6cbb\u8fd0\u52a8\u4e2d\u88ab\u6355\uff0c\u6211\u521a\u521a\u63a5\u5230\u5efa\u90ba\u7684\u7535\u8bdd\uff0c\u8bf4\u662f\u5efa\u90ba\u8b66\u65b9\u5df2\u7ecf\u5bf9\u4ed6\u4eec\u505a\u51fa\u62d8\u7559\u5341\u4e94\u5929\u7684\u51b3\u5b9a\u2026\u2026\u201d\u5f20\u606a\u8fb9\u8bf4\uff0c\u8fb9\u8bf7\u6881\u4f1f\u6cd5\u5750\u4e0b\u8bf4\u8bdd\uff0c\u4eb2\u81ea\u7ed9\u4ed6\u659f\u8336\u3002</p><p>\u3000\u3000\u201c\u5341\u4e94\u5929\u554a\uff0c\u201d\u6881\u4f1f\u6cd5\u7709\u5934\u5fae\u5fae\u76b1\u8d77\u6765\uff0c\u4ed6\u90fd\u4e0d\u80fd\u80af\u5b9a\u6c5f\u5357\u7701\u516c\u5b89\u5385\u80fd\u4e0d\u80fd\u9876\u4f4f\u538b\u529b\u5728\u5341\u4e94\u5929\u5185\u5bf9\u6848\u60c5\u7684\u4fa6\u67e5\u53d6\u5f97\u7a81\u7834\u6027\u7684\u8fdb\u5c55\uff0c\u4ed6\u5728\u6c5f\u5357\u7701\u867d\u662f\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\uff0c\u4f46\u662f\u4ecd\u6709\u4e25\u91cd\u7684\u52bf\u5355\u529b\u52bf\u4e4b\u611f\uff0c\u6881\u4f1f\u6cd5\u6c89\u541f\u7247\u523b\uff0c\u53c8\u6bc5\u7136\u70b9\u4e86\u70b9\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u4e9b\u56f0\u96be\u5fc5\u987b\u8981\u514b\u670d\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6216\u8bb8\u6ca1\u6709\u60f3\u8c61\u4e2d\u56f0\u96be\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u521a\u624d\u8fd8\u63a5\u5230\u6d88\u606f\u8bf4\u65b0\u534e\u793e\u526f\u793e\u957f\u4e0b\u5348\u8981\u8d76\u5230\u91d1\u5c71\u6765\u770b\u671b\u82f1\u52c7\u53d7\u4f24\u7684\u4e09\u540d\u8bb0\u8005\u5462\uff0c\u6c5f\u5357\u7701\u59d4\u7684\u5176\u4ed6\u9886\u5bfc\u5927\u6982\u4e5f\u4e0d\u60f3\u516c\u5b89\u90e8\u76f4\u63a5\u63d2\u624b\u6b64\u6848\u5427\uff1f\u53e6\u5916\uff0c\u7a00\u571f\u95ee\u9898\uff0c\u53ef\u4e0d\u4ec5\u4ec5\u53ea\u5173\u7cfb\u5355\u4e2a\u4ea7\u4e1a\u7684\u95ee\u9898\uff0c\u5d14\u6587\u6bc5\u5c06\u519b\u884c\u4e8b\u4f4e\u8c03\uff0c\u4e0d\u613f\u58f0\u5f20\uff0c\u4f46\u662f\u4ed6\u5728\u7a00\u571f\u95ee\u9898\u4f1a\u4e0d\u4f1a\u4e09\u7f04\u5176\u53e3\uff1f\u201d</p><p>\u3000\u3000\u516c\u5b89\u90e8\u63d2\u4e0d\u63d2\u624b\u5012\u662f\u5c0f\u4e8b\u60c5\uff0c\u82e5\u516c\u5b89\u90e8\u5728\u4fa6\u67e5\u8fc7\u7a0b\u5f53\u4e2d\u53d1\u73b0\u7ae0\u5dde\u5e02\u59d4\u3001\u5e02\u653f\u5e9c\u51fa\u4e86\u5927\u7bd3\u5b50\uff0c\u90a3\u5c31\u53ef\u80fd\u76f4\u63a5\u5bfc\u81f4\u4e2d\u7eaa\u59d4\u4ecb\u5165\u3002\u5c0f\u5fc3\u8c28\u614e\u4e3a\u8ba1\uff0c\u6c5f\u5357\u7701\u66f4\u591a\u4eba\u80af\u5b9a\u662f\u5e0c\u671b\u4e8b\u6001\u8fd8\u662f\u4fdd\u6301\u5728\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u63a7\u5236\u8303\u56f4\u4e4b\u5185\u3002</p><p>\u3000\u3000\u4e5f\u8d8a\u6765\u8d8a\u80fd\u80af\u5b9a\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u3001\u534e\u7a00\u77ff\u4e1a\u4e0e\u5468\u747e\u73ba\u4e4b\u95f4\u5b58\u5728\u67d0\u79cd\u5229\u76ca\u4ea4\u6613\uff0c\u8fd9\u5e76\u4e0d\u662f\u7b80\u5355\u7684\u5546\u4e1a\u72af\u7f6a\uff0c\u82e5\u662f\u8fd8\u6709\u56fd\u52a1\u9662\u7a00\u571f\u529e\u7684\u6210\u5458\u9677\u8fdb\u53bb\uff0c\u53ef\u80fd\u4f1a\u5bfc\u81f4\u56fd\u5b89\u90e8\u95e8\u4ecb\u5165\u6b64\u6848\uff0c\u6bd5\u7adf\u4ed6\u4eec\u60f3\u5f71\u54cd\u3001\u6240\u5f71\u54cd\u7684\u4e5f\u4e0d\u4ec5\u4ec5\u662f\u76ee\u524d\u4ea7\u4e1a\u89c4\u6a21\u8fd8\u4e0d\u7b97\u592a\u5927\u7684\u5355\u4e2a\u7a00\u571f\u4ea7\u4e1a\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u80fd\u660e\u767d\u5f20\u606a\u7684\u610f\u601d\uff0c\u5c31\u662f\u8981\u501f\u529b\u5c06\u8fd9\u4e00\u62f3\u6253\u5f97\u53c8\u72e0\u53c8\u51c6\uff0c\u8ba9\u8fd9\u4e00\u62f3\u6253\u4e0b\u53bb\u8ba9\u5bf9\u624b\u6ca1\u6709\u8fd8\u624b\u7684\u4f59\u5730\u3002\u5bf9\u624b\u642d\u8d77\u7684\u591a\u7c73\u8bfa\u9aa8\u724c\uff0c\u6700\u5173\u952e\u7684\u4e00\u5f20\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e24\u5144\u5f1f\u5df2\u7ecf\u7ed9\u5efa\u90ba\u8b66\u65b9\u6263\u62bc\u4e0b\u6765\uff0c\u773c\u4e0b\u65b0\u534e\u793e\u4e09\u540d\u8bb0\u8005\u88ab\u975e\u6cd5\u62d8\u7981\uff0c\u8981\u63a8\u5012\u8fd9\u5f20\u724c\uff0c\u5df2\u7ecf\u4e0d\u662f\u7ae0\u5dde\u5730\u65b9\u52bf\u529b\u80fd\u963b\u6b62\u7684\u4e86\u3002</p></article>"}}}]);
