(window.webpackJsonp=window.webpackJsonp||[]).push([[1018],{1041:function(p){p.exports={chapterTitle:null,title:"\u7b2c825\u7ae0 \u6240\u8c13\u771f\u76f8",content:"<article><h2>\u7b2c825\u7ae0 \u6240\u8c13\u771f\u76f8</h2><p>\u3000\u3000\u5d14\u90c1\u6052\u5728\u7ae0\u5dde\u7ed9\u6263\u62bc\u65f6\uff0c\u88ab\u6bb4\u6253\u81f4\u9f3b\u6881\u9aa8\u4e0e\u5de6\u988a\u6709\u8f7b\u5fae\u9aa8\u88c2\uff0c\u8eab\u4e0a\u7684\u6de4\u4f24\u5012\u4e0d\u8981\u7d27\uff0c\u7ecf\u8fc7\u5904\u7406\uff0c\u867d\u7136\u8fd8\u8981\u4f4f\u9662\u89c2\u5bdf\uff0c\u5012\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u4e0d\u4fbf\u7684\u5f71\u54cd\uff0c\u4e5f\u4e0d\u5f71\u54cd\u4ed6\u5728\u533b\u9662\u91cc\u5c06\u6750\u6599\u6574\u7406\u51fa\u6765\u3002</p><p>\u3000\u3000\u4ed6\u4eec\u7ed9\u6263\u62bc\u65f6\uff0c\u5927\u91cf\u6697\u8bbf\u5230\u7684\u7b2c\u4e00\u624b\u6750\u6599\u90fd\u7ed9\u6bc1\u6389\uff0c\u4e0d\u60f3\u91cd\u56de\u7ae0\u5dde\u91c7\u8bbf\uff0c\u53ea\u80fd\u4ece\u5efa\u90ba\u6668\u62a5\u501f\u7528\u4ed6\u4eec\u6697\u8bbf\u5f97\u5230\u7684\u6750\u6599\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u4e09\u540d\u65b0\u534e\u793e\u7684\u8bb0\u8005\u5728\u7ae0\u5dde\u7684\u91c7\u8bbf\u5df2\u7ecf\u591f\u7ec6\u81f4\u4e86\uff0c\u770b\u5230\u7b80\u5fd7\u5eb7\u63d0\u4f9b\u7684\u6750\u6599\uff0c\u624d\u77e5\u9053\u5efa\u90ba\u6668\u62a5\u4e3a\u6b64\u505a\u4e86\u66f4\u591a\u7684\u51c6\u5907\uff0c\u57fa\u672c\u5c06\u7ae0\u5dde\u5730\u533a\u4e0a\u767e\u5bb6\u79c1\u76d7\u77ff\u70b9\u90fd\u6e05\u67e5\u51fa\u6765\uff0c\u5c06\u76d7\u91c7\u5230\u6d41\u5411\u56fd\u5185\u5916\u5e02\u573a\u7684\u9ed1\u8272\u6e20\u9053\u4e5f\u6709\u6478\u5e95\uff0c\u751a\u81f3\u5bf9\u7ae0\u5dde\u5168\u5883\u56e0\u65e0\u5e8f\u76d7\u91c7\u7a00\u571f\u77ff\u800c\u5bfc\u81f4\u7684\u751f\u6001\u6c61\u67d3\u72b6\u51b5\uff0c\u4e5f\u6709\u8be6\u7ec6\u7684\u8c03\u67e5\uff0c\u5305\u62ec\u751f\u6001\u6c61\u67d3\u81f4\u4f7f\u5f53\u5730\u65b0\u751f\u513f\u795e\u7ecf\u6027\u7f3a\u9677\u7684\u6bd4\u7387\u9ad8\u8fc7\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u8d85\u8fc7\u5341\u500d\u7684\u6570\u636e\u4e5f\u6709\u8be6\u7ec6\u7684\u63cf\u8ff0\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u5f88\u96be\u60f3\u8c61\u624b\u91cc\u7684\u8fd9\u4efd\u6750\u6599\u662f\u5efa\u90ba\u6668\u62a5\u4e24\u540d\u8bb0\u8005\u5728\u534a\u4e2a\u6708\u7684\u65f6\u95f4\u91cc\u8c03\u67e5\u51fa\u6765\u7684\uff0c\u8981\u4e0d\u662f\u6750\u6599\u91cc\u5f88\u591a\u5185\u5bb9\u90fd\u8ddf\u5d14\u90c1\u6052\u4ed6\u4eec\u7684\u8c03\u67e5\u76f8\u7b26\uff0c\u90fd\u8981\u6000\u7591\u4ed6\u4eec\u662f\u4e0d\u662f\u5728\u9020\u5047\u3002\u4ee4\u5d14\u90c1\u6052\u5947\u602a\u7684\uff0c\u8521\u4e91\u98de\u526f\u793e\u957f\u4ee5\u53ca\u4ee3\u8868\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u6881\u4f1f\u6cd5\u5bf9\u8fd9\u4efd\u6750\u6599\u7684\u771f\u5b9e\u6027\u90fd\u6ca1\u6709\u63d0\u51fa\u8d28\u7591\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u4e5f\u5c31\u7f62\u4e86\uff0c\u8521\u4e91\u98de\u53ef\u4ee5\u5f53\u4e86\u597d\u51e0\u5341\u5e74\u7684\u8bb0\u8005\uff0c\u5e94\u8be5\u77e5\u9053\u4e24\u540d\u8bb0\u8005\u7684\u8c03\u67e5\u80fd\u529b\u5341\u5206\u6709\u9650\uff0c\u80fd\u63ed\u5f00\u51b0\u5c71\u7684\u4e00\u89d2\u5df2\u7ecf\u7b97\u4e0d\u9519\u4e86\uff0c\u600e\u4e48\u53ef\u80fd\u4f1a\u5982\u6b64\u8be6\u5b9e\u800c\u51c6\u786e\u5462\uff1f</p><p>\u3000\u3000\u8521\u4e91\u98de\u591c\u91cc\u843d\u69bb\u7701\u653f\u5e9c\u62db\u5f85\u5bbe\u9986\uff0c\u660e\u5929\u8d76\u65e9\u8fd8\u8981\u56de\u5317\u4eac\uff1b\u5d14\u90c1\u6052\u4ed6\u4eec\u4f4f\u7684\u75c5\u623f\u4e0e\u5176\u8bf4\u75c5\u623f\u8fd8\u4e0d\u5982\u8bf4\u4f11\u517b\u522b\u5885\uff0c\u5d14\u90c1\u66fc\u8ddf\u5979\u5988\u53ca\u5979\u5ac2\u5b50\u8fd8\u6709\u53e6\u4e00\u540d\u8bb0\u8005\u7684\u59bb\u5b50\u4e5f\u90fd\u4f4f\u5728\u91cc\u9762\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u4e0e\u4e00\u540d\u4f24\u52bf\u8f83\u8f7b\u7684\u8bb0\u8005\u501f\u4e86\u5206\u793e\u7684\u7b14\u8bb0\u672c\u7535\u8111\u5199\u6750\u6599\uff0c\u5d14\u90c1\u66fc\u8ddf\u5979\u5988\u4ee5\u53ca\u5979\u5ac2\u5b50\u867d\u7136\u6709\u4e9b\u5fe7\u5fc3\uff0c\u5374\u8fd8\u8981\u5546\u91cf\u4f55\u65f6\u6b63\u5f0f\u5411\u5f20\u606a\u4ee5\u53ca\u5efa\u90ba\u6668\u62a5\u7684\u4e24\u540d\u8bb0\u8005\u9053\u8c22\u5408\u9002\uff1a\u201c\u6628\u5929\u591c\u91cc\uff0c\u90fd\u662f\u7238\u7238\u4eb2\u81ea\u8ddf\u6211\u8fd9\u4e2a\u5b66\u751f\u901a\u7535\u8bdd\u5e7f\u9614\u5e2e\u5fd9\u7684\u4e8b\u60c5\uff0c\u8981\u9053\u8c22\u7684\u8bdd\uff0c\u7238\u4ed6\u4eba\u662f\u4e0d\u662f\u4e5f\u8981\u62bd\u51fa\u65f6\u95f4\u6765\u9732\u4e00\u4e0b\u9762\uff1f\u201d</p><p>\u3000\u3000\u201c\u8bf4\u8d77\u4f60\u7684\u5b66\u751f\uff0c\u6709\u4e9b\u4e8b\u6211\u6b63\u597d\u8981\u95ee\u4f60\u3002\u201d\u5d14\u90c1\u6052\u51fa\u73b0\u5728\u95e8\u53e3\uff0c\u8f7b\u8f7b\u6572\u4e86\u6572\u5f00\u7740\u7684\u623f\u95e8\uff0c\u793a\u610f\u4ed6\u59b9\u59b9\u51fa\u6765\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5979\u5988\u5728\u4e00\u65c1\u4e0d\u9ad8\u5174\u7684\u62b1\u6028\u7740\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\u8981\u907f\u5f00\u6211\u8ddf\u5c0f\u7434\uff0c\u5c0f\u66fc\u5f53\u5e74\u5728\u6d77\u5dde\u51fa\u4e86\u90a3\u4e48\u5927\u7684\u4e8b\uff0c\u4f60\u4eec\u4e24\u5144\u59b9\u7adf\u7136\u7792\u5230\u4e8b\u60c5\u8fc7\u53bb\u5c0f\u534a\u5e74\u624d\u544a\u8bc9\u5bb6\u91cc\uff0c\u771f\u4e0d\u6e05\u695a\u4f60\u4eec\u4e24\u5144\u59b9\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\u3002\u4f60\u4eec\u4e0d\u4f1a\u8fd8\u6709\u4ec0\u4e48\u4e8b\u60c5\u60f3\u8981\u7792\u7740\u6211\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u8fd8\u4e0d\u662f\u6015\u4f60\u8ddf\u7238\u7238\u62c5\u5fc3\uff1f\u201d\u5d14\u90c1\u66fc\u60f3\u8d77\u5f53\u5e74\u4e8b\u5c31\u5fc3\u6709\u4f59\u60b8\uff0c\u4e0d\u8fc7\u8ddf\u5bb6\u91cc\u8bf4\u8d77\u6765\u53c8\u8f7b\u63cf\u6de1\u5199\u4e86\u4e00\u756a\uff0c\u6bd5\u7adf\u5f53\u521d\u662f\u5979\u81ea\u5df1\u575a\u6301\u8981\u56de\u6d77\u5dde\u5f53\u4e2d\u5b66\u6559\u5e08\u7684\uff0c\u5979\u4e5f\u6015\u5bb6\u91cc\u4ee5\u6b64\u4e3a\u501f\u53e3\u4ece\u6b64\u7ea6\u675f\u5979\u7684\u81ea\u7531\u3002</p><p>\u3000\u3000\u201c\u8fd8\u63d0\u5f53\u5e74\u7684\u4e8b\u60c5\u505a\u4ec0\u4e48\uff1f\u201d\u5d14\u90c1\u6052\u5934\u75bc\u7684\u76b1\u7740\u7709\u5934\uff0c\u5f53\u65f6\u4ed6\u4e00\u8d77\u8ddf\u5c0f\u66fc\u7792\u7740\u5bb6\u91cc\uff0c\u4e8b\u60c5\u66b4\u9732\u540e\u7ed9\u9a82\u5f97\u72d7\u8840\u55b7\u5934\u3002\u4ed6\u9f3b\u6881\u90a3\u6bb5\u8138\u88f9\u7740\u4e00\u5708\u7eb1\u5e03\uff0c\u65e0\u5948\u7684\u8868\u60c5\u770b\u4e0d\u51fa\u6765\uff0c\u53ea\u5f97\u7ad9\u5728\u623f\u95e8\u53e3\u95ee\u5c0f\u66fc\uff0c\u201c\u90a3\u4e2a\u53eb\u5f20\u606a\u7684\u8fd8\u662f\u5927\u4e00\u7684\u5b66\u751f\u5427\uff0c\u4ed6\u600e\u4e48\u4f1a\u77e5\u9053\u5efa\u90ba\u6668\u62a5\u6d3e\u8bb0\u8005\u5230\u7ae0\u5dde\u91c7\u8bbf\u7684\uff1f\u6211\u53d1\u73b0\u4ed6\u4eec\u5728\u91c7\u8bbf\u524d\u505a\u4e86\u5927\u91cf\u7684\u51c6\u5907\u5de5\u4f5c\uff0c\u4e5f\u5e94\u8be5\u5145\u5206\u8003\u8651\u5230\u5230\u7ae0\u5dde\u6697\u8bbf\u7684\u5371\u9669\u6027\uff0c\u91c7\u8bbf\u7684\u6d88\u606f\u4e0d\u5e94\u8be5\u968f\u968f\u4fbf\u4fbf\u5c31\u5916\u6cc4\u7684\u3002\u201d</p><p>\u3000\u3000\u4ed4\u7ec6\u63a8\u6572\u4e0b\u6765\uff0c\u7591\u70b9\u5f88\u591a\uff0c\u6700\u5927\u7684\u7591\u70b9\u5c31\u662f\u8521\u4e91\u98de\u4ee5\u53ca\u6c5f\u5357\u7701\u5730\u65b9\u5f53\u5c40\u7adf\u7136\u5bf9\u8fd9\u4e9b\u7591\u70b9\u89c6\u800c\u4e0d\u89c1\uff0c\u5d14\u90c1\u6052\u5c31\u60f3\u6765\u627e\u59b9\u59b9\u591a\u4e86\u89e3\u4e00\u4e9b\u60c5\u51b5\uff0c\u4ed6\u53ef\u4e0d\u4e60\u60ef\u5c06\u7591\u95ee\u85cf\u5728\u809a\u5b50\u91cc\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u8bf4\u4e0d\u597d\uff0c\u611f\u89c9\u4e0a\u5f88\u4e71\uff0c\u201d\u5d14\u90c1\u66fc\u60f3\u8981\u4ece\u63a5\u89e6\u4e0d\u591a\u7684\u5370\u8c61\u5c06\u5f20\u606a\u7ed9\u5979\u7684\u611f\u89c9\u5b8c\u6574\u7684\u63cf\u8ff0\u51fa\u6765\u5f88\u96be\uff0c\u201c\u4f60\u4e5f\u6e05\u695a\u4e00\u4e2d\u7684\u90a3\u56db\u540d\u5b66\u751f\uff0c\u675c\u98de\u8ddf\u5f20\u606a\u662f\u5f88\u7279\u6b8a\u7684\u4e24\u4e2a\u4eba\uff0c\u675c\u98de\u521a\u8fdb\u5927\u5b66\u5c31\u521b\u529e\u516c\u53f8\u4e86\uff0c\u5f20\u606a\u8fd9\u4e2a\u5b66\u751f\u7ed9\u4eba\u7684\u611f\u89c9\u66f4\u7279\u6b8a\u4e00\u4e9b\u3002\u4f60\u90fd\u4e0d\u77e5\u9053\u6211\u4eec\u591c\u91cc\u662f\u5750\u4ec0\u4e48\u8f66\u8fc7\u6765\u7684\uff0c\u5c31\u662f\u4e0a\u6b21\u5728\u5317\u4eac\u8f66\u5c55\u4e2d\u4f60\u6307\u7ed9\u6211\u770b\u7684\u90a3\u6b3e\u6700\u6602\u8d35\u7684\u5954\u9a70\u8f66\uff0c\u8fd8\u6709\u4ed6\u8054\u7cfb\u5efa\u90ba\u6668\u62a5\u6d3e\u5f80\u7ae0\u5dde\u7684\u8bb0\u8005\u53ef\u6ca1\u6709\u901a\u8fc7\u5176\u4ed6\u4eba\u8f6c\u8fbe\uff0c\u800c\u662f\u76f4\u63a5\u901a\u7684\u7535\u8bdd\uff0c\u51cc\u6668\u4f60\u4eec\u7ed9\u6551\u51fa\u6765\u65f6\uff0c\u4ed6\u597d\u50cf\u662f\u8ddf\u6881\u7701\u957f\u901a\u8fc7\u7535\u8bdd\u2026\u2026\u6211\u611f\u89c9\u7238\u4ed6\u4f3c\u4e4e\u77e5\u9053\u4e9b\u4ec0\u4e48\uff0c\u4ed6\u603b\u662f\u62ff\u56fd\u5bb6\u673a\u5bc6\u5f53\u501f\u53e3\uff0c\u4ec0\u4e48\u90fd\u4e0d\u8ddf\u6211\u8bf4\uff0c\u8fd9\u91cc\u9762\u53c8\u80fd\u6709\u4ec0\u4e48\u56fd\u5bb6\u673a\u5bc6\uff1f\u4f60\u6709\u4ec0\u4e48\u7591\u95ee\uff0c\u8fd8\u4e0d\u5982\u76f4\u63a5\u7ed9\u7238\u6253\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5d14\u90c1\u6052\u60f3\u60f3\u4e5f\u662f\uff0c\u56de\u4e34\u65f6\u5de5\u4f5c\u5ba4\u7ed9\u4ed6\u7238\u5d14\u6587\u6bc5\u901a\u7535\u8bdd\u4e86\u3002</p><p>\u3000\u3000\u8001\u4f34\u8dd1\u5230\u91d1\u5c71\u770b\u90c1\u6052\u53bb\u4e86\uff0c\u5d14\u6587\u6bc5\u65e0\u6cd5\u968f\u4fbf\u5c31\u79bb\u5f00\u5317\u4eac\uff0c\u6df1\u591c\u8fd8\u5750\u5728\u4e66\u623f\u91cc\uff0c\u529e\u516c\u684c\u653e\u7740\u4e00\u53e0\u8d44\u6599\uff0c\u63a5\u5230\u90c1\u6052\u7684\u7535\u8bdd\uff0c\u5fcd\u4e0d\u4f4f\u8981\u57cb\u6028\uff1a\u201c\u5de5\u4f5c\u7684\u4e8b\u60c5\u53ef\u4ee5\u7f13\u4e00\u7f13\uff0c\u4f60\u8fd9\u4e0d\u662f\u7ed9\u4f60\u5988\u5fc3\u91cc\u6dfb\u5835\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5988\u80fd\u7406\u89e3\u6211\uff0c\u518d\u8bf4\u8fd9\u65f6\u5019\u5c06\u6750\u6599\u6293\u7d27\u6574\u7406\u51fa\u6765\uff0c\u4e5f\u662f\u8981\u4e3a\u4e86\u81ea\u5df1\u8ba8\u56de\u4e2a\u516c\u9053\u554a\uff0c\u201d\u5d14\u90c1\u6052\u5728\u7535\u8bdd\u91cc\u7b11\u7740\u8bf4\uff0c\u53ea\u662f\u5507\u89d2\u88c2\u4e86\uff0c\u62b9\u7740\u836f\u6c34\uff0c\u7b11\u7684\u65f6\u5019\u4f1a\u6709\u523a\u75db\uff0c\u201c\u5c0f\u66fc\u8bf4\u4f60\u624b\u91cc\u53ef\u80fd\u5df2\u7ecf\u6709\u5979\u90a3\u4e2a\u5b66\u751f\u7684\u8d44\u6599\uff0c\u6211\u5f88\u597d\u5947\uff0c\u80fd\u4e0d\u80fd\u8ddf\u6211\u900f\u4e2a\u5e95\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5d14\u6587\u6bc5\u7684\u76ee\u5149\u843d\u5728\u773c\u524d\u7684\u90a3\u4efd\u6863\u6848\u6750\u6599\u4e0a\uff0c\u5c06\u7535\u8bdd\u7b52\u5939\u5728\u80a9\u9888\u95f4\uff0c\u5c06\u6750\u6599\u4ece\u6863\u6848\u888b\u91cc\u62bd\u51fa\u6765\uff0c\u6750\u6599\u6700\u4e0a\u9762\u7684\u4e00\u9875\u8d6b\u7136\u8d34\u7740\u5f20\u606a\u7684\u7167\u7247\uff0c\u4ed6\u7a0d\u6c89\u541f\u4e00\u4f1a\u513f\uff0c\u624d\u8bf4\u9053\uff0c\u201c\u5929\u8d4b\u8fd9\u4e1c\u897f\uff0c\u8fd8\u771f\u662f\u5f88\u96be\u4f30\u91cf\uff0c\u5c0f\u66fc\u7684\u8fd9\u4e2a\u5b66\u751f\u53ef\u4ee5\u8bf4\u662f\u4e00\u4e2a\u5f02\u6570\uff0c\u4f60\u4e4b\u524d\u4e0d\u662f\u8ddf\u6211\u8fa9\u8bba\u8bf4\u4e2d\u56fd\u7684\u571f\u58e4\u6210\u957f\u4e0d\u51fa\u6bd4\u5c14\u76d6\u5179\u3001\u6210\u957f\u4e0d\u51fa\u5b59\u6b63\u4e49\u2026\u2026\u201d</p><p>\u3000\u3000\u9526\u6e56\u5728\u5efa\u90ba\u542f\u52a8\u6a61\u6811\u56ed\u8ba1\u5212\uff0c\u5229\u7528\u91d1\u66b4\u98ce\u66b4\u6467\u6b8b\u4e1c\u4e9a\u7ecf\u6d4e\u4e4b\u9645\uff0c\u5927\u89c4\u6a21\u7684\u5f15\u8fdb\u534e\u88d4\u79d1\u5b66\u5bb6\u53ca\u9ad8\u7ea7\u6280\u672f\u4eba\u5458\u8fd4\u56de\u56fd\u5185\u52a0\u5165\u5230\u6a61\u6811\u56ed\u8ba1\u5212\u4e2d\u6765\uff0c\u5d14\u6587\u6bc5\u4ece\u90a3\u91cc\u5c31\u5f00\u59cb\u5173\u6ce8\u8fd9\u5bb6\u8fd1\u5e74\u6765\u5d1b\u8d77\u4e8e\u6d77\u5dde\u7684\u6c11\u8425\u4f01\u4e1a\u3002\u968f\u540e\u9526\u6e56\u901a\u8fc7\u8d8a\u79c0\u63a7\u80a1\u8054\u5408\u9a6c\u6765\u897f\u4e9a\u7684\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u3001\u65b0\u52a0\u5761\u56fd\u5bb6\u6295\u8d44\u7ba1\u7406\u5c40\u4ece\u53f0\u79ef\u7535\u8fc2\u56de\u8bf7\u5230\u4ee5\u67f3\u5fd7\u6210\u4e3a\u9996\u7684\u5927\u6279\u4e2d\u9ad8\u7ea7\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u5230\u5efa\u90ba\u542f\u52a8\u4e2d\u6676\u5fae\u82af\u7684\u9879\u76ee\uff0c\u5d14\u6587\u6bc5\u751a\u81f3\u8fd8\u4eb2\u81ea\u8fc7\u95ee\u8fc7\u4e2d\u6676\u5fae\u82af\u9879\u76ee\u8bc4\u4f30\u7684\u60c5\u51b5\uff0c\u5e76\u5728\u6700\u7ec8\u51b3\u7b56\u65f6\u7ed9\u4e88\u4e86\u80af\u5b9a\u7684\u610f\u89c1\u3002\u6628\u5929\u591c\u91cc\u901a\u7535\u8bdd\u65f6\uff0c\u5d14\u6587\u6bc5\u5c31\u6e05\u695a\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u624d\u5982\u6b64\u4fe1\u4efb\u7684\u542c\u4ece\u4ed6\u7684\u610f\u89c1\u5bf9\u90c1\u6052\u4ee5\u53ca\u5176\u4ed6\u4e24\u540d\u65b0\u534e\u793e\u8bb0\u8005\u5b9e\u65bd\u62a2\u6551\u65b9\u6848\u3002</p><p>\u3000\u3000\u201c\u5443\u3001\u5443\u3001\u5443\u2026\u2026\u201d\u5d14\u90c1\u6052\u5728\u7535\u8bdd\u91cc\u6700\u591a\u53d1\u51fa\u7684\u5c31\u662f\u8fd9\u4e2a\u611f\u53f9\u97f3\uff0c\u5f88\u96be\u5c06\u4e0a\u5348\u7ad9\u5728\u75c5\u5e8a\u524d\u7684\u9752\u5e74\u8ddf\u9526\u6e56\u7684\u7f14\u9020\u8005\u7b49\u540c\u8d77\u6765\uff0c\u8fd9\u6837\u7684\u4e8b\u5b9e\u4e5f\u96be\u4ee4\u4eba\u6d88\u5316\u4e86\uff0c\u5012\u662f\u7b26\u5408\u5c0f\u66fc\u5bf9\u67d0\u4e9b\u7ec6\u8282\u7684\u56f0\u60d1\u3002\u6881\u4f1f\u6cd5\u4e0e\u6c5f\u5357\u7701\u5730\u65b9\u5f53\u5c40\u7684\u6001\u5ea6\u81ea\u7136\u4e5f\u4e0d\u96be\u7406\u89e3\u4e86\uff0c\u5d14\u90c1\u6052\u4eca\u5929\u4e5f\u4ece\u65b0\u534e\u793e\u6c5f\u5357\u5206\u793e\u7684\u540c\u4e8b\u90a3\u91cc\u5f97\u77e5\u9526\u6e56\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u8ba1\u5212\uff0c\u90a3\u5c31\u610f\u5473\u7740\u9526\u6e56\u4e0e\u6881\u4f1f\u6cd5\u4e00\u76f4\u6709\u8f83\u4e3a\u5bc6\u5207\u7684\u8054\u7cfb\u3002\u4f46\u662f\u9526\u6e56\u90a3\u4e9b\u70ed\u5207\u7684\u4ecb\u5165\u7ae0\u5dde\u6709\u4ec0\u4e48\u610f\u56fe\uff1f\u6240\u8c13\u5efa\u90ba\u6668\u62a5\u793e\u7684\u8bb0\u8005\u591a\u534a\u4e5f\u662f\u53d7\u9526\u6e56\u7684\u6307\u6d3e\u624d\u5230\u7ae0\u5dde\u8fdb\u884c\u8c03\u67e5\uff0c\u800c\u4e14\u8c03\u67e5\u7684\u89c4\u6a21\u7edd\u5bf9\u4e0d\u81f3\u4e24\u540d\u8bb0\u8005\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u4ed6\u4eec\u53ea\u63a5\u89e6\u5230\u9a6c\u6d77\u9f99\u4e0e\u7b80\u5fd7\u5eb7\uff0c\u665a\u4e0a\u8fd8\u53ea\u662f\u7b80\u5fd7\u5eb7\u4e00\u4eba\u524d\u6765\uff1b\u4e0d\u8fc7\u5d14\u6587\u6bc5\u901a\u8fc7\u6b66\u6628\u591c\u4e00\u8d77\u884c\u52a8\u7684\u6b66\u8b66\u5f88\u6e05\u695a\u7684\u77e5\u9053\u9526\u6e56\u5f80\u7ae0\u5dde\u6d3e\u51fa\u5927\u6279\u7684\u4eba\u624b\u6697\u4e2d\u8fdb\u884c\u8c03\u67e5\u3002</p><p>\u3000\u3000\u5d14\u6587\u6bc5\u4e0d\u96be\u60f3\u8c61\u90c1\u6052\u77e5\u9053\u8be6\u60c5\u4e4b\u540e\u4f1a\u6709\u600e\u6837\u7684\u60ca\u8be7\u8868\u60c5\uff0c\u53ea\u662f\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\uff1a\u201c\u8521\u4e91\u98de\u521a\u521a\u8ddf\u6211\u901a\u8fc7\u7535\u8bdd\uff0c\u544a\u8bc9\u6211\u9526\u6e56\u501f\u5efa\u90ba\u6668\u62a5\u793e\u7684\u540d\u4e49\u975e\u5e38\u8be6\u7ec6\u7684\u8c03\u67e5\u4e86\u7ae0\u5dde\u7a00\u571f\u77ff\u8d44\u6e90\u7684\u73b0\u72b6\uff0c\u4f60\u4ece\u4f60\u7684\u7acb\u573a\u5e2e\u6211\u5206\u6790\u5206\u6790\u770b\uff0c\u4ed6\u4eec\u662f\u51fa\u4e8e\u600e\u6837\u7684\u76ee\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u624b\u91cc\u53c8\u6ca1\u6709\u8be6\u7ec6\u7684\u6750\u6599\u53ef\u4f9b\u5206\u6790\uff0c\u54ea\u91cc\u77e5\u9053\u4ed6\u4eec\u6709\u4ec0\u4e48\u610f\u56fe\uff1f\u201d\u5d14\u90c1\u6052\u4e00\u65f6\u8fd8\u771f\u641e\u4e0d\u61c2\u9526\u6e56\u6d3e\u4eba\u5230\u7ae0\u5dde\u6709\u4ec0\u4e48\u76ee\u7684\uff0c\u5bf9\u4fc4\u5c14\u53c8\u60f3\u5230\u4e00\u4e2a\u5173\u952e\u6027\u95ee\u9898\uff0c\u201c\u6309\u7167\u4f60\u8fd9\u4e48\u8bf4\uff0c\u4e09\u5e74\u524d\u5c0f\u66fc\u521a\u5230\u6d77\u5dde\u65f6\uff0c\u9526\u6e56\u5df2\u7ecf\u6709\u4e9b\u89c4\u6a21\u4e86\u5427\uff1f\u5f20\u606a\u90a3\u65f6\u5019\u53ef\u4e0d\u5355\u7eaf\u662f\u5b66\u6821\u91cc\u4e00\u540d\u7ea8\u7ed4\u5b66\u751f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ed6\u8fd9\u65f6\u5019\u8fd8\u4e0d\u662f\u5728\u62ff\u5b66\u751f\u8eab\u4efd\u505a\u63a9\u62a4\uff1f\u201d\u5d14\u6587\u6bc5\u5fae\u5fae\u4e00\u53f9\uff0c\u201c\u4e4b\u524d\u4e5f\u6709\u4eba\u6015\u4ed6\u592a\u9ad8\u8c03\uff0c\u4e5f\u6709\u4eba\u4e0d\u559c\u6b22\u8ddf\u4ed6\u6b63\u9762\u63a5\u89e6\uff0c\u6bd5\u7adf\u4ed6\u7684\u7ecf\u5386\u4f3c\u4e4e\u5df2\u7ecf\u4e0d\u80fd\u4ee5\u5b66\u751f\u521b\u4e1a\u5947\u8ff9\u7684\u5671\u5934\u6765\u5f62\u5bb9\uff0c\u4e5f\u4e0d\u9002\u5408\u8ba9\u516c\u4f17\u77e5\u9053\u9526\u6e56\u5e55\u540e\u7684\u6545\u4e8b\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u5927\u516c\u65e0\u79c1\u7684\u5d07\u9ad8\u610f\u56fe\u4ece\u6765\u90fd\u662f\u4ee4\u4eba\u8d39\u89e3\u7684\uff0c\u201d\u5f20\u606a\u5e76\u6ca1\u6709\u5728\u665a\u996d\u540e\u5e76\u6ca1\u6709\u5306\u5fd9\u79bb\u5f00\u91d1\u5c71\u56de\u5efa\u90ba\u53bb\uff0c\u51b3\u5b9a\u5728\u6c81\u56ed\u7559\u5bbf\u4e00\u591c\uff0c\u8fd9\u4e00\u591c\u4e5f\u662f\u4e0e\u8bb8\u9e3f\u4f2f\u3001\u53f6\u5efa\u658c\u79c9\u70db\u591c\u8c08\uff0c\u201c\u7a00\u571f\u4ee5\u53ca\u5176\u4ed6\u56fd\u5185\u5de5\u4e1a\u50a8\u91cf\u5360\u5784\u65ad\u5730\u4f4d\u7684\u7a00\u6709\u91d1\u5c5e\u8d44\u6e90\u7684\u5730\u4f4d\uff0c\u5e94\u8be5\u5f97\u5230\u66f4\u8ba4\u771f\u3001\u66f4\u4e25\u683c\u7684\u91cd\u89c6\uff0c\u5e94\u8be5\u505a\u4e3a\u5927\u56fd\u6218\u7565\u4e2d\u7684\u91cd\u8981\u4e00\u73af\uff0c\u4f5c\u4e3a\u4e0e\u6d77\u5916\u8d44\u6e90\u63a7\u5236\u8005\u5236\u8861\u7684\u7ecf\u6d4e\u5229\u5668\u6765\u5bf9\u5f85\u3002\u4f46\u662f\u8bdd\u53c8\u8bf4\u56de\u6765\uff0c\u5373\u4f7f\u4e2d\u592e\u91cd\u65b0\u4ee5\u66f4\u8c28\u614e\u7684\u6001\u5ea6\u5ba1\u89c6\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u8c03\u6574\uff0c\u4e5f\u53ea\u80fd\u901a\u8fc7\u591a\u5bb6\u516c\u53f8\u6765\u5b9e\u73b0\u80cc\u540e\u7684\u6218\u7565\u610f\u56fe\u3002\u6216\u8bb8\u4e2d\u592e\u66f4\u591a\u7684\u4f1a\u5e0c\u671b\u7531\u56fd\u6709\u4f01\u4e1a\u80fd\u63a7\u5236\u8fd9\u4e00\u6218\u7565\u8d44\u6e90\uff0c\u4f46\u662f\u4e5f\u4e0d\u4f1a\u5b8c\u5168\u5bf9\u6c11\u8425\u8d44\u672c\u5173\u4e0a\u5927\u95e8\uff0c\u8fd9\u4e5f\u662f\u6c11\u8425\u8d44\u672c\u80fd\u53c2\u4e0e\u56fd\u5bb6\u6218\u7565\u7684\u4e0d\u591a\u9014\u5f84\u4e4b\u4e00\u3002\u5982\u4f55\u53bb\u53c2\u4e0e\uff0c\u8fd8\u8981\u770b\u4e2d\u592e\u8fd9\u6b21\u5bf9\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u7684\u8c03\u6574\uff0c\u4e0d\u8fc7\u7fdf\u4e39\u9752\u6839\u636e\u5df2\u6709\u7684\u4ea7\u4e1a\u653f\u7b56\u521d\u6b65\u5217\u4e86\u4e00\u4e2a\u65b9\u6848\uff0c\u5305\u62ec\u5982\u4f55\u5728\u56fd\u5185\u5916\u6ce8\u518c\u4e00\u7cfb\u5217\u7684\u8d38\u6613\u516c\u53f8\uff0c\u5f88\u6709\u53c2\u8003\u4ef7\u503c\u7684\uff0c\u4f60\u4eec\u53ef\u4ee5\u5148\u770b\u770b\u3002\u201d</p></article>"}}}]);
