(window.webpackJsonp=window.webpackJsonp||[]).push([[102],{124:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u56db\u8282 \u51f6\u624b",content:"<article><h2>\u7b2c\u5341\u56db\u8282 \u51f6\u624b</h2><p>\u4eba\u603b\u662f\u4f1a\u6b7b\u7684\uff0c\u9f99\u4e5f\u540c\u6837\u3002</p><p>\u4f20\u8bf4\u9f99\u7684\u5bff\u547d\u80fd\u8fbe\u4e0a\u5343\u5e74\uff0c\u662f\u4eba\u7c7b\u7684\u5341\u500d\u4e0d\u6b62\u3002\u4f46\u518d\u957f\u7684\u5bff\u547d\uff0c\u7ec8\u7a76\u4e5f\u6709\u5c3d\u5934\uff0c\u4f55\u51b5\u8fd9\u4e16\u754c\u4e0a\u8fd8\u6709\u4e2a\u8bcd\u53eb\u201c\u610f\u5916\u201d\u3002\u9f99\u56fa\u7136\u751f\u800c\u5f3a\u5927\uff0c\u5929\u8d4b\u8d85\u7fa4\uff0c\u4e5f\u4e0d\u662f\u8bf4\u5c31\u4e0d\u4f1a\u88ab\u6740\u6b7b\u7684\u3002\u8fd9\u79cd\u4f53\u578b\u5e9e\u5927\u7684\u751f\u7269\uff0c\u56e0\u4e3a\u559c\u6b22\u6536\u96c6\u91d1\u94f6\u73e0\u5b9d\uff0c\u628a\u81ea\u5df1\u7684\u6d1e\u7a74\u5e03\u7f6e\u5f97\u5bcc\u4e3d\u5802\u7687\uff0c\u4ece\u6765\u90fd\u662f\u88ab\u5192\u9669\u8005\u4eec\u89c6\u4e3a\u5feb\u901f\u626c\u540d\u53d1\u8d22\u7684\u57ab\u811a\u77f3\u3002\u5982\u679c\u80fd\u6740\u6b7b\u4e00\u53ea\u9f99\uff0c\u4e0d\u4f46\u7acb\u523b\u6210\u4e3a\u5927\u82f1\u96c4\uff0c\u540d\u626c\u5929\u4e0b\uff0c\u5355\u8bba\u4ece\u9f99\u7a9f\u91cc\u6478\u51fa\u6765\u7684\u90a3\u4e9b\u8d22\u5b9d\uff0c\u5c31\u8db3\u591f\u4e00\u8f88\u5b50\u4eab\u7528\u4e0d\u5c3d\u4e86\u3002\u800c\u4e14\u9f99\u662f\u5929\u8d4b\u65bd\u6cd5\u7684\u751f\u7269\uff0c\u5957\u7528\u4e00\u53e5\u8bdd\uff0c\u53eb\u505a\u201c\u5168\u8eab\u4e0a\u4e0b\u90fd\u662f\u5b9d\u201d\uff0c\u65e0\u8bba\u9f99\u9cde\u3001\u9f99\u76ae\u3001\u9f99\u7259\u3001\u9f99\u9aa8\u3001\u9f99\u8840\u7b49\u7b49\uff0c\u4e00\u6982\u90fd\u662f\u73cd\u8d35\u7684\u9b54\u6cd5\u6750\u6599\uff0c\u8fd9\u4e5f\u8ba9\u9f99\u62db\u81f4\u4e86\u5f88\u591a\u5deb\u5e08\u7684\u89ca\u89ce\u3002</p><p>\u800c\u4e14\u9f99\u5f80\u5f80\u90fd\u6bd4\u8f83\u81ea\u8d1f\u9a84\u50b2\uff0c\u79bb\u7fa4\u7d22\u5c45\uff0c\u8001\u6b7b\u4e0d\u76f8\u5f80\u6765\u2014\u2014\u7b80\u5355\u5730\u8bf4\u5c31\u662f\u90fd\u559c\u6b22\u5f53\u5b85\uff0c\u6210\u5929\u95f7\u5728\u5bb6\u91cc\u7761\u5927\u89c9\u3002\u5f53\u5c60\u9f99\u8005\u4eec\u6740\u4e0a\u95e8\u7684\u65f6\u5019\uff0c\u9f99\u5f80\u5f80\u8fde\u4e2a\u5e2e\u624b\u90fd\u6ca1\u6709\uff0c\u53ea\u80fd\u63aa\u624b\u4e0d\u53ca\uff0c\u5b64\u519b\u594b\u6218\u3002</p><p>\u743c\u6069\u4e0d\u77e5\u9053\u773c\u524d\u8fd9\u53ea\u94f6\u9f99\u662f\u5230\u4e86\u751f\u6b7b\u5927\u9650\uff0c\u81ea\u5df1\u8001\u6b7b\u4e86\uff0c\u8fd8\u662f\u88ab\u70db\u5821\u7684\u4eba\u6740\u4e86\u3002\u53cd\u6b63\uff0c\u5b83\u662f\u5df2\u7ecf\u6b7b\u4e86\uff0c\u800c\u4e14\u53d8\u6210\u4e86\u4e2a\u5e7d\u7075\u3002</p><p>\u5e7d\u7075\u662f\u4e00\u79cd\u975e\u5e38\u7f55\u89c1\u7684\u4ea1\u7075\uff0c\u662f\u7531\u6b7b\u8005\u7684\u7075\u9b42\u53d8\u6210\u7684\u3002\u6309\u9053\u7406\u8bf4\uff0c\u4e00\u5207\u6709\u7075\u9b42\u7684\u751f\u7269\u6b7b\u4e86\uff0c\u6709\u4fe1\u4ef0\u7684\u81ea\u7136\u524d\u5f80\u795e\u7947\u7684\u56fd\u5ea6\uff0c\u6ca1\u4fe1\u4ef0\u7684\u53bb\u6b7b\u4ea1\u4e4b\u795e\u90a3\u91cc\u62a5\u5230\uff0c\u4e0d\u5e94\u8be5\u8fd8\u7559\u5728\u7269\u8d28\u754c\u3002\u4f46\u5728\u67d0\u4e9b\u7279\u6b8a\u60c5\u51b5\u4e0b\uff0c\u7075\u9b42\u88ab\u67d0\u79cd\u5f3a\u5927\u7684\u529b\u91cf\uff08\u6216\u8bb8\u662f\u9b54\u6cd5\uff0c\u6216\u8bb8\u662f\u5176\u4ed6\uff09\u675f\u7f1a\uff0c\u65e0\u6cd5\u6446\u8131\uff0c\u7ed3\u679c\u5c31\u6210\u4e86\u4ea1\u7075\u3002</p><p>\u56e0\u4e3a\u5e7d\u7075\u90fd\u662f\u7075\u9b42\u53d7\u5230\u5f3a\u5927\u529b\u91cf\u7684\u675f\u7f1a\u800c\u6f14\u5316\u800c\u6210\uff0c\u5f80\u5f80\u56f0\u5728\u67d0\u4e00\u5730\u57df\u5185\uff0c\u6216\u8005\u67d0\u4ef6\u7269\u54c1\u4e2d\uff0c\u65e0\u6cd5\u81ea\u7531\u6d3b\u52a8\u3002\u53ea\u8981\u9003\u51fa\u8fd9\u4e00\u533a\u57df\u6216\u8005\u4e0d\u63a5\u89e6\u7269\u54c1\uff0c\u5373\u4fbf\u5b89\u5168\uff0c\u4e0d\u4f1a\u88ab\u5e7d\u7075\u6240\u4f24\uff1b\u4f46\u53cd\u8fc7\u6765\u8bf4\uff0c\u8fd9\u6837\u88ab\u6c38\u8fdc\u56da\u7981\u675f\u7f1a\u7740\uff0c\u5c31\u7b97\u662f\u4eba\u90fd\u8981\u6028\u6c14\u51b2\u5929\uff0c\u4f55\u51b5\u5e7d\u7075\uff0c\u5f80\u5f80\u5c31\u53d8\u5f97\u51f6\u623e\u65e0\u6bd4\u3002</p><p>\u73b0\u5b9e\u4e2d\uff0c\u51fa\u73b0\u8fd9\u79cd\u5e7d\u7075\u7684\uff0c\u5f80\u5f80\u90fd\u662f\u67d0\u4e2a\u5deb\u5e08\u6740\u6b7b\u4e86\u4eba\uff0c\u7528\u9b54\u6cd5\u5c06\u6b7b\u8005\u7684\u7075\u9b42\u56da\u7981\u4f4f\uff0c\u53d8\u6210\u5e7d\u7075\u3002\u5728\u8fd9\u79cd\u60c5\u51b5\u4e0b\uff0c\u675f\u7f1a\u8005\u540c\u65f6\u53c8\u662f\u51f6\u624b\uff0c\u5e7d\u7075\u7684\u6028\u6124\u51f6\u623e\u53ef\u60f3\u800c\u77e5\uff0c\u5f80\u5f80\u5b8c\u5168\u4e27\u5931\u751f\u524d\u7684\u5fc3\u667a\uff0c\u89c1\u4eba\u5c31\u6740\uff0c\u4e0d\u53ef\u7406\u55bb\u3002</p><p>\u96be\u602a\u70db\u5821\u628a\u8fd9\u91cc\u5212\u4e3a\u7981\u5730\u2026\u2026\u6709\u8fd9\u4e48\u4e00\u53ea\u94f6\u9f99\u5e7d\u7075\u5728\uff0c\u4e0d\u662f\u7981\u5730\u662f\u4ec0\u4e48\u3002</p><p>\u8fd9\u4e0b\u5b50\u6709\u5927\u9ebb\u70e6\u4e86\u3002</p><p>\u94f6\u9f99\u5e7d\u7075\u7f13\u7f13\u6d6e\u73b0\uff0c\u4f4e\u5934\u770b\u7740\u743c\u6069\u548c\u6885\u83f2\u65af\uff0c\u51fa\u4e4e\u610f\u6599\u7684\u662f\uff0c\u5b83\u5e76\u6ca1\u6709\u76f4\u63a5\u653b\u51fb\uff0c\u800c\u662f\u5f00\u53e3\u8bf4\u8bdd\u3002</p><p>\u201c\u4f60\u4eec\u662f\u8c01\uff1f\u201d\u94f6\u9f99\u95ee\u3002</p><p>\u5b83\u7528\u7684\u662f\u6807\u51c6\u7684\u4eba\u7c7b\u901a\u7528\u8bed\uff0c\u58f0\u97f3\u4f18\u7f8e\u52a8\u542c\uff0c\u6bcf\u4e2a\u97f5\u5f8b\u8282\u594f\u90fd\u6070\u5230\u597d\u5904\uff0c\u7b80\u76f4\u4fbf\u662f\u6d51\u7136\u5929\u6210\u7684\u4e50\u7ae0\u3002\u800c\u4e14\u743c\u6069\u7acb\u523b\u53d1\u73b0\u5b83\u5176\u5b9e\u662f\u201c\u5979\u201d\uff0c\u4e00\u53ea\u5973\u6027\u94f6\u9f99\uff0c\u6216\u8005\u8bf4\u751f\u524d\u662f\u4e00\u53ea\u5973\u6027\u94f6\u9f99\u3002</p><p>\u4e0d\u7ba1\u662f\u7537\u662f\u5973\uff0c\u65e2\u7136\u5979\u6ca1\u6709\u4e3b\u52a8\u653b\u51fb\u800c\u662f\u9009\u62e9\u8be2\u95ee\uff0c\u90a3\u81ea\u7136\u662f\u6c42\u4e4b\u4e0d\u5f97\u3002\u743c\u6069\u6b63\u8981\u56de\u7b54\uff0c\u6885\u83f2\u65af\u5374\u5df2\u7ecf\u4e0a\u524d\u4e00\u6b65\uff0c\u9762\u5bf9\u7740\u94f6\u9f99\u3002</p><p>\u5979\u6ca1\u6709\u8bf4\u8bdd\uff0c\u8eab\u4e0a\u7684\u94f6\u7532\u5374\u6cdb\u8d77\u5149\u8292\uff0c\u8d8a\u6765\u8d8a\u4eae\uff0c\u6700\u540e\u7b80\u76f4\u50cf\u8981\u628a\u5979\u53d8\u6210\u4e00\u4e2a\u5c0f\u592a\u9633\u4f3c\u7684\u3002\u94f6\u9f99\u5e7d\u7075\u4e00\u5f00\u59cb\u4e5f\u6709\u4e9b\u83ab\u540d\u5176\u5999\uff0c\u4f46\u968f\u5373\u50cf\u662f\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u795e\u8272\u9661\u7136\u53d8\u6210\u4e86\u53c8\u60ca\u53c8\u559c\uff0c\u53c8\u662f\u4e0d\u6562\u7f6e\u4fe1\u3002</p><p>\u201c\u8428\u83f2\u8389\u5a05\u65af\uff1f\u201d\u5979\u53eb\u51fa\u4e00\u4e2a\u540d\u5b57\u3002</p><p>\u6885\u83f2\u65af\u7684\u94f6\u7532\u4e0a\u5149\u8292\u66f4\u76db\uff0c\u5c06\u5979\u6574\u4e2a\u4eba\u90fd\u5305\u88f9\u5176\u4e2d\uff0c\u770b\u4e0d\u5206\u660e\u3002\u6700\u7ec8\u6240\u6709\u7684\u5149\u8f89\u805a\u96c6\u6210\u4e00\u9053\u94f6\u5149\u77eb\u592d\u817e\u8d77\uff0c\u8dc3\u5728\u534a\u7a7a\uff0c\u5e7b\u6210\u4e00\u53ea\u94f6\u9f99\u865a\u50cf\u6765\u3002</p><p>\u8be5\u2026\u2026\u8be5\u6b7b\uff0c\u4eca\u5929\u5230\u5e95\u662f\u649e\u4e0a\u4ec0\u4e48\u9b3c\u4e86\uff0c\u743c\u6069\u53ea\u611f\u89c9\u81ea\u5df1\u7684\u810a\u80cc\u4e00\u9635\u9635\u53d1\u9ebb\uff0c\u4e00\u53ea\u94f6\u9f99\u5e7d\u7075\u5df2\u7ecf\u591f\u9ebb\u70e6\u4e86\uff0c\u5c45\u7136\u53c8\u5192\u51fa\u4e00\u53ea\u6765\u3002</p><p>\u6bd4\u8d77\u6d1e\u7a9f\u91cc\u8fd9\u53ea\u53eb\u7c73\u8389\u4e9a\u59c6\u7684\u94f6\u9f99\u5e7d\u7075\uff0c\u6885\u83f2\u65af\u8eab\u4e0a\u5e7b\u51fa\u7684\u94f6\u9f99\u5e7d\u7075\u4f53\u578b\u4f3c\u4e4e\u8981\u5c0f\u4e00\u4e9b\uff0c\u800c\u4e14\u66f4\u52a0\u900f\u660e\u865a\u5e7b\uff0c\u4eff\u4f5b\u4e00\u9635\u98ce\u8fc7\u6765\u90fd\u80fd\u5439\u6563\u4f3c\u7684\u3002\u5f53\u7136\u8fd9\u5ea7\u6d1e\u7a9f\u91cc\u4f3c\u4e4e\u6ca1\u6709\u98ce\uff0c\u4e8e\u662f\u4e24\u53ea\u94f6\u9f99\u5e7d\u7075\uff0c\u4e00\u5927\u4e00\u5c0f\u5728\u7a7a\u4e2d\u5bf9\u89c6\u7740\u3002</p><p>\u201c\u8428\u5a1c\u8fe6\u6cd5\u65af\uff0c\u201d\u6bd4\u8f83\u5c0f\u4e00\u4e9b\u7684\u94f6\u9f99\u5e7d\u7075\u5f00\u53e3\u8bf4\uff0c\u7528\u7684\u662f\u9f99\u8bed\uff0c\u743c\u6069\u81ea\u7136\u4e5f\u80fd\u542c\u61c2\u3002\u8fd9\u53ea\u94f6\u9f99\u4e5f\u662f\u4e2a\u5973\u6027\uff0c\u58f0\u97f3\u540c\u6837\u4f18\u96c5\u52a8\u542c\uff0c\u53ea\u662f\u6709\u79cd\u7f25\u7f25\u7f08\u7f08\u82e5\u6709\u82e5\u65e0\u7684\u611f\u89c9\uff0c\u201c\u5f88\u4e45\u4e0d\u89c1\u4e86\u3002\u8fd8\u6709\uff0c\u201d\u5979\u62b1\u6028\u7740\uff0c\u201c\u4f60\u600e\u4e48\u6539\u4e2a\u8fd9\u4e48\u5947\u602a\u7684\u540d\u5b57\uff0c\u7c73\u8389\u4e9a\u59c6\uff1f\u542c\u542c\uff0c\u591a\u50bb\uff0c\u7b80\u76f4\u5c31\u50cf\u662f\u4e2a\u4eba\u7c7b\u7684\u540d\u5b57\u3002\u8981\u4e0d\u662f\u4eca\u5929\u5076\u7136\u9760\u8fd1\u90a3\u4e2a\u6d1e\uff0c\u611f\u89c9\u5230\u662f\u4f60\u7684\u6c14\u5473\uff0c\u6211\u90fd\u60f3\u4e0d\u5230\u4f1a\u662f\u4f60\u3002\u201d</p><p>\u7c73\u8389\u4e9a\u59c6\uff0c\u6216\u8005\u8bf4\u8428\u5a1c\u8fe6\u6cd5\u65af\uff0c\u53cd\u6b63\u5c31\u662f\u8fd9\u53ea\u70db\u5821\u4e0b\u7684\u94f6\u9f99\u5e7d\u7075\u4e0d\u6ee1\u5730\u6447\u6643\u7740\u8111\u888b\uff0c\u201c\u5e9f\u8bdd\uff0c\u96be\u9053\u4f60\u4ee5\u4e3a\u6211\u5f88\u559c\u6b22\u8fd9\u4e2a\u50bb\u540d\u5b57\u5417\uff1f\u8981\u4e0d\u662f\u2026\u2026\u201d\u5979\u987f\u4f4f\u4e86\uff0c\u540e\u9762\u7684\u8bdd\u6ca1\u6709\u7ee7\u7eed\uff0c\u201c\u7b97\u4e86\uff0c\u5f88\u4e45\u4e0d\u89c1\u2026\u2026\u4f60\u600e\u4e48\u5f04\u6210\u8fd9\u5e45\u6837\u5b50\u554a\uff1f\u201d</p><p>\u201c\u5514\uff0c\u6211\u4eec\u4f3c\u4e4e\u5883\u51b5\u5dee\u4e0d\u591a\u554a\uff0c\u201d\u8428\u83f2\u8389\u5a05\u65af\u7684\u58f0\u97f3\u91cc\u6709\u4e00\u4e9b\u9690\u9690\u7684\u7b11\u610f\uff0c\u201c\u81f3\u5c11\u770b\u8d77\u6765\u5dee\u4e0d\u591a\u5462\u3002\u201d</p><p>\u7c73\u8389\u4e9a\u59c6\u770b\u7740\u8428\u83f2\u8389\u4e9a\u65af\uff0c\u518d\u770b\u770b\u5979\u4e0b\u9762\u7684\u6885\u83f2\u65af\uff0c\u773c\u5149\u843d\u5728\u5c11\u5973\u7a7f\u7684\u94f6\u7532\u4e0a\u3002\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5979\u4f4e\u6c89\u5730\u8f7b\u543c\u7740\uff0c\u201c\u4f60\u88ab\u4eba\u6740\u4e86\uff1f\u201d</p><p>\u201c\u770b\u4e5f\u770b\u5f97\u51fa\u6765\u5427\uff0c\u201d\u8428\u83f2\u8389\u4e9a\u65af\u8f7b\u63cf\u6de1\u5199\u5730\u56de\u7b54\uff0c\u8bed\u6c14\u6f20\u4e0d\u5728\u4e4e\uff0c\u4eff\u4f5b\u6240\u8c08\u8bba\u7684\u88ab\u6740\u5bf9\u8c61\u4e0d\u662f\u5979\u81ea\u5df1\u4f3c\u7684\uff0c\u201c\u672c\u6765\u611f\u89c9\u5230\u4f60\u5728\u8fd9\u91cc\uff0c\u8fd8\u60f3\u8bf7\u4f60\u5e2e\u5fd9\u7684\u3002\u73b0\u5728\u770b\u6765\uff0c\u5012\u662f\u4e0d\u5fc5\u6307\u671b\u4e86\u3002\u201d</p><p>\u5979\u9887\u6709\u4e9b\u5931\u671b\u5730\u6447\u4e86\u6447\u5934\uff0c\u201c\u7b97\u4e86\uff0c\u6211\u8fd8\u662f\u627e\u522b\u4eba\u5427\uff0c\u518d\u89c1\u3002\u201d</p><p>\u201c\u7b49\u7b49\uff0c\u201d\u7c73\u8389\u4e9a\u59c6\u4f4e\u543c\u7740\uff0c\u201c\u544a\u8bc9\u6211\u662f\u8c01\u6740\u4e86\u4f60\uff1f\u201d</p><p>\u4f46\u8428\u83f2\u8389\u4e9a\u65af\u4f3c\u4e4e\u4e0d\u613f\u518d\u591a\u8bf4\uff0c\u5979\u61d2\u6d0b\u6d0b\u5730\u53f9\u606f\u7740\uff0c\u865a\u50cf\u6563\u5f00\uff0c\u5316\u4f5c\u4e00\u56e2\u94f6\u5149\u8fdb\u5165\u6885\u83f2\u65af\u8eab\u4e0a\u7a7f\u7684\u94f6\u7532\u91cc\u3002</p><p>\u203b\u203b\u203b</p><p>\u83ab\u540d\u5176\u5999\u8d76\u4e0a\u8fd9\u4e48\u4e00\u51fa\u620f\uff0c\u743c\u6069\u53ef\u8c13\u662f\u5b8c\u5168\u6478\u4e0d\u7740\u5934\u8111\uff0c\u4f46\u4ed6\u5230\u5e95\u4e0d\u662f\u7b28\u86cb\uff0c\u8111\u4e2d\u7a0d\u7a0d\u8f6c\u51e0\u4e2a\u5ff5\u5934\uff0c\u4ed6\u4e5f\u5df2\u7ecf\u5927\u81f4\u731c\u51fa\u4e86\u57fa\u672c\u60c5\u51b5\u3002</p><p>\u6709\u4e24\u53ea\u94f6\u9f99\uff0c\u4e00\u53ea\u53eb\u8428\u83f2\u8389\u4e9a\u65af\uff0c\u4e00\u53ea\u53eb\u8428\u5a1c\u8fe6\u6cd5\u65af\u2026\u2026\u597d\u5427\uff0c\u9f99\u90fd\u559c\u6b22\u628a\u81ea\u5df1\u7684\u540d\u5b57\u53d6\u5f97\u7279\u522b\u7279\u522b\u957f\uff0c\u4eff\u4f5b\u8fd9\u6837\u542c\u8d77\u6765\u6709\u6c14\u6d3e\u3002\u5176\u4e2d\u8428\u5a1c\u8fe6\u6cd5\u65af\u4e0d\u77e5\u600e\u4e48\u88ab\u5c01\u5728\u70db\u5821\u4e0b\u9762\uff0c\u6210\u4e86\u5e7d\u7075\uff0c\u8fd8\u6539\u4e86\u4e2a\u540d\u5b57\u53eb\u7c73\u8389\u4e9a\u59c6\uff1b\u53e6\u4e00\u53ea\u94f6\u9f99\u8428\u83f2\u8389\u4e9a\u65af\u4f3c\u4e4e\u8fd0\u6c14\u540c\u6837\u7cdf\u7cd5\uff0c\u5979\u88ab\u4ec0\u4e48\u4eba\u6740\u6b7b\u4e86\uff0c\u800c\u4e14\u88ab\u505a\u6210\u4e86\u76d4\u7532\u2014\u2014\u5982\u524d\u6240\u8ff0\uff0c\u9f99\u5168\u8eab\u90fd\u662f\u4e0a\u7b49\u4f18\u826f\u7684\u6750\u6599\uff0c\u9f99\u76ae\u505a\u76d4\u7532\u65e2\u8f7b\u8584\u53c8\u7262\u56fa\u5f80\u5f80\u8fd8\u9644\u5e26\u5404\u79cd\u9b54\u6cd5\u3002\u800c\u8428\u83f2\u8389\u4e9a\u65af\u81ea\u5df1\u4e5f\u88ab\u5c01\u5728\u76d4\u7532\u91cc\uff0c\u4e5f\u6210\u4e86\u4e00\u53ea\u5e7d\u7075\u9f99\u3002</p><p>\u5982\u4eca\u8428\u83f2\u8389\u4e9a\u65af\u5076\u7136\u53d1\u73b0\u6d1e\u53e3\uff0c\u611f\u89c9\u5230\u91cc\u9762\u6709\u719f\u4eba\u7684\u6c14\u606f\uff0c\u53d1\u73b0\u8fd9\u4e2a\u7c73\u8389\u4e9a\u59c6\u539f\u6765\u5c31\u662f\u8428\u5a1c\u8fe6\u6cd5\u65af\uff0c\u4e8e\u662f\u51c6\u5907\u524d\u6765\u6c42\u52a9\uff0c\u4ee5\u6c42\u6446\u8131\u8fd9\u79cd\u5e7d\u7075\u72b6\u6001\u3002\u6885\u83f2\u65af\u4e0d\u77e5\u9053\u662f\u88ab\u63a7\u5236\u8fd8\u662f\u88ab\u8bef\u5bfc\uff0c\u6216\u8005\u5176\u4ed6\u7f18\u6545\uff0c\u603b\u4e4b\u662f\u6309\u94f6\u9f99\u7684\u610f\u613f\u4e0b\u6765\u4e86\u2026\u2026\u987a\u4fbf\u628a\u743c\u6069\u4e5f\u5e26\u4e0b\u6765\u4e86\u3002\u7ed3\u679c\u6ca1\u60f3\u5230\u70db\u5821\u5730\u4e0b\u8fd9\u53ea\u94f6\u9f99\u4e5f\u5df2\u7ecf\u6210\u4e86\u5e7d\u7075\u9f99\uff0c\u81ea\u5df1\u90fd\u6446\u8131\u4e0d\u4e86\uff0c\u54ea\u91cc\u8fd8\u80fd\u5e2e\u5f97\u4e0a\u5fd9\u3002</p><p>\u603b\u4e4b\uff0c\u8fd9\u7eaf\u5c5e\u63d2\u66f2\u548c\u610f\u5916\u2026\u2026\u4f46\u95ee\u9898\u662f\uff0c\u73b0\u5728\u4f3c\u4e4e\u6709\u9ebb\u70e6\u627e\u4e0a\u95e8\u4e86\u3002</p><p>\u8428\u83f2\u8389\u4e9a\u65af\u53d1\u73b0\u6c42\u52a9\u65e0\u671b\uff0c\u5f88\u6f47\u6d12\u5730\u76f4\u63a5\u5c31\u56de\u94f6\u7532\u91cc\u7761\u89c9\u53bb\u4e86\uff0c\u5374\u7559\u4e0b\u743c\u6069\u548c\u6885\u83f2\u65af\u9762\u5bf9\u7740\u4e00\u53ea\u53d1\u6012\u7684\u94f6\u9f99\u5e7d\u7075\u2014\u2014\u7c73\u8389\u4e9a\u59c6\u786e\u5b9e\u662f\u53d1\u6012\u4e86\uff0c\u5979\u7684\u53e3\u4e2d\u5df2\u7ecf\u55b7\u51fa\u7f15\u7f15\u5bd2\u51b0\u6c14\u606f\uff0c\u6c34\u94f6\u7403\u822c\u7684\u773c\u775b\u6e10\u6e10\u8f6c\u4e3a\u9c9c\u7ea2\uff0c\u77aa\u7740\u9762\u524d\u7684\u4e24\u4e2a\u4eba\u7c7b\u3002</p><p>\u201c\u662f\u8c01\u6740\u4e86\u8428\u83f2\u8389\u4e9a\u65af\uff1f\u201d\u5979\u95ee\u3002</p><p>\u743c\u6069\u5728\u5fc3\u4e2d\u5492\u9a82\u7740\uff0c\u4ed6\u600e\u4e48\u77e5\u9053\u8c01\u6740\u4e86\u8428\u83f2\u8389\u4e9a\u65af\u2026\u2026\u83ab\u975e\u662f\u6885\u83f2\u65af\uff1f\u4f46\u5979\u662f\u4e2a\u5723\u6b66\u58eb\uff0c\u4e0d\u53ef\u80fd\u505a\u8fd9\u79cd\u4e8b\u60c5\u5427\u3002\u6b63\u60f3\u7740\uff0c\u6885\u83f2\u65af\u62ac\u5934\u770b\u7740\u94f6\u9f99\uff0c\u201c\u662f\u6211\u6bcd\u4eb2\u6740\u7684\u3002\u201d\u5979\u5e73\u9759\u5730\u8bf4\u3002</p></article>"}}}]);
