(window.webpackJsonp=window.webpackJsonp||[]).push([[1021],{1044:function(p){p.exports={chapterTitle:null,title:"\u7b2c827\u7ae0 \u6700\u7cdf\u7cd5\u7684\u5c40\u9762",content:"<article><h2>\u7b2c827\u7ae0 \u6700\u7cdf\u7cd5\u7684\u5c40\u9762</h2><p>\u3000\u3000\u5468\u747e\u73ba\u653e\u4e0b\u7535\u8bdd\uff0c\u53c8\u6709\u8840\u6db2\u5728\u5f80\u5934\u9885\u51b2\u7684\u611f\u89c9\uff0c\u624b\u6276\u7740\u684c\u5b50\u8ba9\u81ea\u5df1\u7684\u60c5\u7eea\u5e73\u9759\u4e0b\u6765\uff0c\u6162\u6162\u7684\u6e05\u7406\u601d\u8def\u3002\u4ed6\u63a5\u5230\u6c60\u4f50\u82f1\u5bff\u7684\u7535\u8bdd\u540e\uff0c\u53c8\u8fde\u7eed\u6253\u4e86\u51e0\u4e2a\u7535\u8bdd\uff0c\u4e86\u89e3\u5230\u65b0\u534e\u793e\u8bb0\u8005\u5728\u7ae0\u5dde\u6697\u8bb0\u88ab\u62d8\u7684\u4e00\u4e9b\u7ec6\u8282\u3002</p><p>\u3000\u3000\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u56e0\u5728\u5efa\u90ba\u5927\u9152\u5e97\u5ad6\u5a3c\u88ab\u9ad8\u65b0\u533a\u5206\u5c40\u8de8\u533a\u62d8\u6355\uff0c\u6b66\u8b66\u5b98\u5175\u5728\u5efa\u90ba\u6668\u62a5\u8bb0\u8005\u7684\u5f15\u9886\u4e0b\u53ca\u65f6\u89e3\u6551\u88ab\u6263\u62bc\u7684\u65b0\u534e\u793e\u8bb0\u8005\uff0c\u6c5f\u5357\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u4ee3\u7701\u957f\u6881\u4f1f\u6cd5\u4eb2\u81ea\u7763\u4fc3\u7701\u516c\u5b89\u5385\u8c03\u67e5\u6b64\u6848\u2026\u2026</p><p>\u3000\u3000\u5bf9\u5468\u747e\u73ba\u6765\u8bf4\uff0c\u8fd9\u4e9b\u7ec6\u8282\u8db3\u4ee5\u5439\u6563\u8ff7\u96fe\u9732\u51fa\u80cc\u540e\u9690\u85cf\u7684\u90a3\u5f20\u98de\u626c\u8dcb\u6248\u7684\u9762\u5b54\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u53ea\u89c9\u5f97\u624b\u8db3\u6709\u4e9b\u53d1\u51c9\uff0c\u8fd9\u4f3c\u4e4e\u8868\u660e\u534a\u4e2a\u6708\u524d\u5728\u8bb2\u53f0\u4e0a\u516c\u7136\u53d7\u5230\u7684\u4fae\u8fb1\u5e76\u975e\u5076\u7136\u3002\u51fa\u73b0\u8fd9\u79cd\u60c5\u51b5\u53ea\u6709\u4e24\u79cd\u53ef\u80fd\uff0c\u8981\u4e48\u662f\u9526\u6e56\u4e00\u76f4\u5728\u5173\u6ce8\u56fd\u5185\u7684\u7a00\u571f\u4ea7\u4e1a\uff0c\u8981\u4e48\u662f\u8fd9\u5c0f\u5b50\u8ddf\u59d3\u5f90\u7684\u4e00\u76f4\u5728\u76ef\u7740\u4ed6\u4eec\u5468\u5bb6\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u544a\u8bc9\u81ea\u5df1\u8981\u51b7\u9759\uff0c\u4e0d\u80fd\u81ea\u4e71\u9635\u811a\uff0c\u60c5\u51b5\u4e5f\u672a\u5fc5\u60f3\u8c61\u4e2d\u90a3\u4e48\u7cdf\u7cd5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5468\u747e\u745c\u9001\u8d70\u845b\u5efa\u5fb7\u7b49\u4eba\uff0c\u5c06\u8fd9\u4e00\u5929\u6765\u7684\u51cc\u4e71\u601d\u8def\u7a0d\u7a0d\u6574\u7406\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u8c22\u6c49\u660e\u5012\u4e86\u4e24\u676f\u7ea2\u9152\u7aef\u8fc7\u6765\uff0c\u5468\u747e\u745c\u559c\u6b22\u5728\u5165\u7761\u524d\u559d\u4e00\u676f\u7ea2\u9152\uff0c\u5973\u4eba\u5230\u5979\u8fd9\u5e74\u7eaa\u8fd8\u7ea0\u7f20\u5728\u6743\u529b\u6597\u4e89\u7684\u65cb\u6da1\u4e4b\u4e2d\uff0c\u66f4\u9700\u8981\u77e5\u9053\u5982\u4f55\u4fdd\u517b\u3002</p><p>\u3000\u3000\u5ba4\u5185\u53ea\u6253\u5f00\u7740\u4e00\u76cf\u58c1\u706f\uff0c\u5e7d\u6697\u7684\u5149\u7ebf\u7a7f\u8fc7\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u676f\uff0c\u9eef\u6de1\u7684\u9152\u6db2\u6709\u7740\u5c06\u51dd\u56fa\u8840\u6db2\u4e00\u6837\u7684\u989c\u8272\uff0c\u7a97\u5916\u5c31\u662f\u4e34\u6daf\u6e56\uff0c\u6e56\u5bf9\u5cb8\u7684\u5c71\u5cb8\u53ea\u6709\u9ed1\u9ee2\u9ee2\u7684\u6697\u5f71\uff0c\u591c\u91cc\u6709\u4e9b\u98ce\uff0c\u6e56\u6c34\u7684\u58f0\u97f3\u5374\u5f88\u5c0f\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u4ece\u4e08\u592b\u8c22\u6c49\u660e\u624b\u91cc\u63a5\u8fc7\u9152\u676f\uff0c\u6253\u7b97\u559d\u5b8c\u4e0a\u5e8a\u7761\u89c9\uff0c\u5367\u5ba4\u91cc\u7684\u7535\u8bdd\u94c3\u58f0\u9661\u7136\u54cd\u8d77\u3002\u5468\u747e\u745c\u5413\u4e86\u4e00\u8df3\uff0c\u9152\u6db2\u6cfc\u5230\u5730\u677f\u4e0a\u3002\u6df1\u591c\u7684\u7535\u8bdd\u603b\u8ba9\u4eba\u6709\u4e0d\u7965\u7684\u9884\u611f\uff0c\u5468\u747e\u745c\u6027\u606a\u4e2d\u6709\u575a\u6bc5\u7684\u4e00\u9762\uff0c\u4f46\u662f\u5982\u6b64\u654f\u611f\u65f6\u523b\uff0c\u5979\u4e5f\u5bb3\u6015\u518d\u542c\u5230\u4ec0\u4e48\u4e0d\u5229\u7684\u6d88\u606f\u4f20\u6765\u3002</p><p>\u3000\u3000\u8c22\u6c49\u660e\u8d70\u8fc7\u53bb\u62ff\u8d77\u6765\u8bdd\u7b52\uff0c\u8fc7\u4e86\u7247\u523b\uff0c\u53c8\u8f6c\u8fc7\u8eab\u6765\u544a\u8bc9\u59bb\u5b50\uff1a\u201c\u5927\u54e5\u7684\u7535\u8bdd\uff1f\u4ed6\u6709\u4e8b\u627e\u4f60\u3002\u201d</p><p>\u3000\u3000\u201c\u6668\u66e6\u7eb8\u4e1a\u7684\u4e8b\u60c5\u6ca1\u6709\u4eba\u544a\u8bc9\u4ed6\u5427\uff0c\u4ed6\u8fd9\u4e48\u665a\u6253\u7535\u8bdd\u6765\u505a\u4ec0\u4e48\uff1f\u201d\u5468\u747e\u745c\u5fc3\u60f3\u5979\u54e5\u54e5\u90a3\u91cc\u603b\u4e0d\u4f1a\u51fa\u4ec0\u4e48\u7eb0\u6f0f\uff0c\u5fc3\u7a0d\u5b89\u4e9b\uff0c\u8d70\u8fc7\u6765\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u8c22\u6c49\u660e\u6ca1\u6709\u523b\u610f\u7684\u53bb\u542c\u8bdd\u7b52\u91cc\u4f20\u6765\u7ec6\u5fae\u7684\u58f0\u97f3\uff0c\u4f46\u662f\u4ece\u59bb\u5b50\u5468\u747e\u745c\u8138\u4e0a\u7ec6\u5fae\u7684\u795e\u8272\u53d8\u5316\u77e5\u9053\u4e8b\u60c5\u4e0d\u5927\u5999\u2014\u2014\u679c\u7136\u53c8\u662f\u4e00\u4e2a\u574f\u6d88\u606f\u3002</p><p>\u3000\u3000\u201c\u65b0\u534e\u793e\u8bb0\u8005\u88ab\u6263\u62bc\u8fd9\u4e8b\uff0c\u53ef\u80fd\u8fd8\u662f\u90a3\u4e2a\u5c0f\u755c\u7272\u5728\u80cc\u540e\u6363\u9b3c\uff01\u201d\u5468\u747e\u745c\u653e\u4e0b\u7535\u8bdd\uff0c\u8138\u8272\u5df2\u7ecf\u9634\u6c89\u5230\u80fd\u62e7\u4e0b\u6c34\u6765\uff0c\u5c06\u53d1\u751f\u7684\u4e8b\u60c5\u544a\u8bc9\u4e08\u592b\uff0c\u201c\u901a\u77e5\u5e76\u9886\u7740\u6b66\u8b66\u5230\u7ae0\u5dde\u6551\u4eba\u7684\u662f\u5efa\u90ba\u6668\u62a5\u793e\u7684\u8bb0\u8005\u2026\u2026\u201d</p><p>\u3000\u3000\u6ca1\u6709\u5229\u5bb3\u5173\u7cfb\uff0c\u5730\u65b9\u62a5\u793e\u7edd\u4e0d\u53ef\u80fd\u8dd1\u5230\u53e6\u4e00\u4e2a\u5730\u65b9\u53bb\u63ed\u4ec0\u4e48\u5185\u5e55\uff0c\u5efa\u90ba\u6668\u62a5\u793e\u4e0e\u9526\u6e56\u7684\u5173\u7cfb\u5bc6\u5207\uff0c\u9526\u6e56\u5c61\u5c61\u6709\u91cd\u5927\u6d88\u606f\u516c\u5e03\uff0c\u5747\u5148\u901a\u8fc7\u5efa\u90ba\u6668\u62a5\u793e\u65d7\u4e0b\u7684\u62a5\u7eb8\u3001\u671f\u520a\uff0c\u8fd9\u91cc\u7684\u9053\u9053\uff0c\u8c22\u6c49\u660e\u5f88\u6e05\u695a\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4e48\u8bf4\u6765\uff0c\u4f60\u54e5\u534a\u4e2a\u6708\u524d\u7ed9\u90a3\u5c0f\u5b50\u5728\u8bb2\u5802\u4e0a\u516c\u7136\u4fae\u8fb1\u4e5f\u4e0d\u662f\u5076\u7136\uff1f\u201d\u8c22\u6c49\u660e\u5f88\u81ea\u7136\u5c06\u8fd9\u4e9b\u5929\u53d1\u751f\u7684\u4e8b\u60c5\u90fd\u8054\u7cfb\u5230\u4e00\u8d77\u53bb\uff0c\u7a81\u7136\u6709\u4e00\u79cd\u8179\u80cc\u53d7\u654c\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u201c\u6211\u54e5\u8fd9\u4eba\u4e5f\u771f\u662f\u7684\uff0c\u4ed6\u975e\u8981\u7275\u6d89\u5230\u8fd9\u79cd\u4e8b\u60c5\u4e2d\u53bb\u5e72\u4ec0\u4e48\uff1f\u201d\u5468\u747e\u745c\u9501\u7740\u7709\u5934\uff0c\u521a\u521a\u559d\u4e86\u7ea2\u9152\u8d77\u4e86\u4e9b\u7761\u610f\uff0c\u8fd9\u65f6\u5019\u5374\u89c9\u5f97\u8111\u5b50\u53d1\u80c0\u3002</p><p>\u3000\u3000\u201c\u4e09\u4e95\u7684\u6c60\u4f50\u5148\u751f\u8bf7\u6258\uff0c\u4f60\u54e5\u4e5f\u662f\u4e0d\u4fbf\u62d2\u7edd\uff0c\u201d\u8c22\u6c49\u660e\u8bf4\u9053\uff0c\u201c\u4e09\u4e95\u8d22\u9600\u5728\u5e55\u540e\u63a7\u5236\u7740\u65e5\u672c\u6700\u5927\u7684\u51e0\u5927\u7535\u5b50\u5382\u5546\uff0c\u8c22\u5bb6\u8981\u5728\u7535\u5b50\u4ea7\u4e1a\u4e0a\u6709\u6240\u5efa\u6811\uff0c\u8981\u8ddf\u7d22\u5c3c\u3001\u677e\u4e0b\u8fd9\u4e9b\u4f01\u4e1a\u5408\u4f5c\uff0c\u4e0d\u80fd\u5c11\u4e86\u7a7f\u9488\u5f15\u7ebf\u7684\u4eba\u2026\u2026\u4e0d\u8fc7\u773c\u4e0b\u4e5f\u770b\u4e0d\u51fa\u4f1a\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u4e0d\u7528\u592a\u62c5\u5fc3\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u80fd\u53eb\u4eba\u4e0d\u62c5\u5fc3\uff1f\u662f\u90a3\u4e2a\u5c0f\u755c\u7272\u85cf\u5728\u5e55\u540e\u7b56\u5212\u8fd9\u4e00\u5207\uff01\u8c01\u80fd\u731c\u5230\u4ed6\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u8c01\u80fd\u731c\u5230\u4ed6\u4e0b\u4e00\u6b65\u4f1a\u6709\u4ec0\u4e48\u52a8\u4f5c\uff1f\u201d\u5468\u747e\u745c\u538b\u7740\u55d3\u5b50\u6076\u72e0\u72e0\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u8c22\u6c49\u660e\u9ed8\u7136\u65e0\u8bed\u3002</p><p>\u3000\u3000\u6b64\u65f6\u5df2\u6709\u660e\u663e\u7684\u8ff9\u8c61\u8868\u660e\u8d8a\u79c0\u63a7\u80a1\u52a8\u7528\u5927\u91cf\u7684\u8d44\u91d1\u901a\u8fc7\u516c\u5f00\u5e02\u573a\u6536\u8d2d\u5609\u4fe1\u7535\u5b50\u7684\u80a1\u7968\uff0c\u867d\u7136\u5609\u4fe1\u96c6\u56e2\u79ef\u6781\u9632\u5fa1\uff0c\u5609\u4fe1\u7535\u5b50\u8ba9\u5bf9\u624b\u593a\u53bb\u63a7\u5236\u6743\u7684\u53ef\u80fd\u6027\u5f88\u5c0f\uff0c\u4f46\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u53ef\u80fd\uff1b\u8fd9\u65f6\u5019\u9526\u6e56\u53c8\u52a8\u8d77\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u5ff5\u5934\uff0c\u60f3\u501f\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u673a\u4f1a\u5c06\u89e6\u624b\u4f38\u5230\u91d1\u5c71\u6765\uff0c\u6668\u66e6\u7eb8\u4e1a\u7ed9\u9526\u6e56\u6210\u529f\u6536\u8d2d\u7684\u53ef\u80fd\u6027\u4f9d\u65e7\u5f88\u5c0f\uff0c\u4f46\u4e5f\u4e0d\u662f\u5b8c\u5168\u6ca1\u6709\u53ef\u80fd\uff1b\u8c01\u4e5f\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u7adf\u7136\u5728\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u7684\u95ee\u9898\u63ea\u8fd9\u8fb9\u7684\u5c0f\u8fab\u5b50\u3002</p><p>\u3000\u3000\u8116\u5b50\u4e0a\u5957\u7740\u4e09\u6839\u7ef3\u7d22\uff0c\u8fd9\u4e09\u6839\u7ef3\u7d22\u8c8c\u4f3c\u90fd\u5f88\u677e\uff0c\u4f46\u662f\u8c01\u8981\u7ed9\u8fd9\u4e48\u5957\u7740\uff0c\u5fc3\u60c5\u81ea\u7136\u4e0d\u4f1a\u8f7b\u677e\u3002</p><p>\u3000\u3000\u201c\u4ed6\u4eec\u8fd9\u4e48\u505a\u7684\u4e00\u5207\u90fd\u662f\u5728\u9488\u5bf9\u6211\u4eec\u5468\u5bb6\uff1f\u201d\u5468\u747e\u745c\u795e\u7ecf\u8d28\u7684\u53eb\u8d77\u6765\uff0c\u201c\u90a3\u53ea\u8001\u72d0\u72f8\u59cb\u7ec8\u864e\u89c6\u7708\u7708\u7684\u9690\u85cf\u5728\u80cc\u540e\uff0c\u4ed6\u662f\u7b49\u4e0d\u53ca\u8981\u4e0b\u624b\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u660e\u4e0d\u6562\u80af\u5b9a\u8fd9\u4e00\u5207\u90fd\u662f\u5f90\u6c34\u5e73\u7b56\u5212\u7684\uff0c\u4f46\u662f\u9526\u6e56\u5e55\u540e\u6700\u575a\u5b9a\u7684\u575a\u6301\u8005\u65e0\u7591\u5c31\u662f\u5f90\u5b66\u5e73\u3002\u5728\u5ba6\u6d77\u6c89\u6c89\u6d6e\u6d6e\u591a\u5e74\u7684\u5f90\u5b66\u5e73\u7adf\u7136\u59cb\u7ec8\u6ca1\u6709\u5f7b\u5e95\u7684\u6c89\u4e0b\u53bb\uff0c\u4e0d\u5f97\u4e0d\u8bf4\u662f\u4e00\u4e2a\u5947\u8ff9\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u65e9\u6668\u63a5\u5230\u6881\u4f1f\u6cd5\u7684\u7535\u8bdd\uff0c\u8bf4\u6628\u5929\u7701\u516c\u5b89\u5385\u8fde\u591c\u5ba1\u8baf\u6628\u5929\u51cc\u6668\u4ece\u6b66\u8b66\u624b\u4e2d\u79fb\u4ea4\u8fc7\u6765\u7684\u4e09\u540d\u5acc\u7591\u4eba\u53d6\u5f97\u7a81\u7834\uff0c\u5176\u4e2d\u4e00\u4eba\u9876\u4e0d\u4f4f\u5ba1\u8baf\u7684\u538b\u529b\uff0c\u627f\u8ba4\u5510\u82f1\u80b2\u5728\u5e55\u540e\u64cd\u7eb5\u4e86\u975e\u6cd5\u62d8\u7981\u6848\uff0c\u7701\u5385\u5df2\u7ecf\u4e0e\u5efa\u90ba\u8b66\u65b9\u53d6\u5f97\u8054\u7cfb\uff0c\u8ba1\u5212\u4ee5\u5acc\u6d89\u975e\u6cd5\u62d8\u7981\u7684\u540d\u4e49\u5c06\u5510\u82f1\u80b2\u62bc\u56de\u91d1\u5c71\u8fdb\u884c\u5ba1\u8baf\u3002</p><p>\u3000\u3000\u5efa\u90ba\u8b66\u65b9\u76f8\u5f53\u914d\u5408\uff0c\u4e3a\u4e86\u8282\u7ea6\u8fd9\u8fb9\u7684\u65f6\u95f4\uff0c\u8fd8\u7279\u5730\u6d3e\u8b66\u8f66\u5c06\u5510\u82f1\u80b2\u62bc\u5230\u91d1\u5c71\u6765\u79fb\u4ea4\u7ed9\u7701\u516c\u5b89\u5385\u3002</p><p>\u3000\u3000\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u4e24\u5144\u5f1f\u6697\u4e2d\u64cd\u7eb5\u7ae0\u5dde\u5730\u65b9\u4e0a\u7684\u6076\u52bf\u529b\u9738\u5360\u79c1\u77ff\u3001\u75af\u72c2\u76d7\u91c7\u7a00\u571f\u3001\u8eab\u4e0a\u80cc\u8d1f\u5341\u591a\u6761\u547d\u6848\uff0c\u4e0d\u53ef\u80fd\u4e0d\u9732\u4e00\u70b9\u75d5\u8ff9\uff0c\u4e5f\u66fe\u591a\u6b21\u88ab\u53d7\u5bb3\u8005\u4e3e\u62a5\u5230\u7701\u516c\u5b89\u5385\u7684\uff0c\u4f46\u662f\u4ee5\u5f80\u7684\u5904\u7406\u65b9\u5f0f\uff0c\u8981\u4e48\u5c06\u6848\u4ef6\u53d1\u56de\u7ae0\u5dde\u8b66\u65b9\u4fa6\u67e5\uff0c\u8981\u4e48\u5c06\u5377\u5b97\u5c18\u5c01\u8d77\u6765\u7f6e\u4e4b\u4e0d\u7406\u3002</p><p>\u3000\u3000\u591a\u65b9\u9762\u65bd\u52a0\u7684\u538b\u529b\u8fd8\u662f\u6709\u6548\u7684\uff0c\u591a\u7c73\u8bfa\u9aa8\u724c\u7684\u5173\u952e\u662f\u53ca\u65f6\u7684\u63a8\u5012\u7b2c\u4e00\u5f20\u9aa8\u724c\uff0c\u63a5\u4e0b\u6765\u7b2c\u4e8c\u5f20\u9aa8\u724c\u5012\u4e0b\u5c31\u6210\u4e86\u987a\u7406\u6210\u7ae0\u7684\u4e8b\u60c5\u3002\u6881\u4f1f\u6cd5\u5f53\u5373\u5c31\u5c06\u51b3\u5b9a\u4ece\u7701\u516c\u5b89\u5385\u3001\u7701\u7eaa\u59d4\u3001\u7701\u77ff\u76d1\u5c40\u7b49\u5404\u90e8\u95e8\u62bd\u8c03\u4eba\u624b\u7ec4\u6210\u8054\u5408\u8c03\u67e5\u7ec4\u524d\u5f80\u7ae0\u5dde\u6478\u5e95\u6e05\u67e5\u534e\u7a00\u77ff\u4e1a\u975e\u6cd5\u5784\u65ad\u7ae0\u5dde\u79c1\u76d7\u7a00\u571f\u77ff\u8d44\u6e90\u7684\u7f6a\u884c\uff0c\u4ee5\u524d\u7684\u9648\u6848\u3001\u65e7\u6848\u4e5f\u8981\u4e00\u8d77\u7ffb\u51fa\u6765\u5f7b\u67e5\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u5176\u4ed6\u5b98\u5458\u5728\u8fd9\u4ef6\u4e8b\u60c5\u4e0a\u96c6\u4f53\u9ed8\u8ba4\u4e86\u5f53\u524d\u6240\u53d1\u751f\u7684\u4e8b\u5b9e\uff0c\u8c01\u90fd\u77e5\u9053\u7ae0\u5dde\u7a00\u571f\u77ff\u7ed9\u5730\u65b9\u6076\u52bf\u529b\u75af\u72c2\u76d7\u91c7\u7684\u73b0\u72b6\u5373\u5c06\u7531\u65b0\u534e\u793e\u4ee5\u5185\u53c2\u7684\u5f62\u5f0f\u4e0a\u5448\u5230\u4e2d\u592e\u9886\u5bfc\u7684\u9762\u524d\uff0c\u800c\u4e14\u65b0\u534e\u793e\u8bb0\u8005\u5728\u7ae0\u5dde\u906d\u9047\u5230\u8fd9\u6837\u7684\u5f85\u9047\uff0c\u60f3\u906e\u63a9\u662f\u906e\u63a9\u4e0d\u4f4f\u4e86\u3002\u6c5f\u5357\u7701\u91cc\u8fd9\u65f6\u5019\u679c\u65ad\u884c\u52a8\u4e5f\u7b97\u662f\u4ea1\u7f8a\u8865\u7262\u3001\u6234\u7f6a\u7acb\u529f\uff0c\u6709\u4e9b\u4eba\u5fc3\u91cc\u8fd8\u5728\u731c\u6d4b\u8fd9\u6b21\u534e\u7a00\u77ff\u4e1a\u8981\u7ed9\u62ff\u51fa\u6765\u5f53\u66ff\u7f6a\u7f8a\u4e86\uff0c\u8fd8\u6ca1\u6709\u610f\u8bc6\u5230\u8fd9\u67e5\u4e0b\u53bb\u4f1a\u7275\u6d89\u6709\u591a\u6df1\u3002</p><p>\u3000\u3000\u7701\u91cc\u7684\u52a8\u4f5c\u6781\u5feb\uff0c\u4e5f\u5c31\u4e8c\u5341\u56db\u5c0f\u65f6\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u51e0\u4e4e\u6ca1\u6709\u7ed9\u7ae0\u5dde\u5730\u65b9\u653f\u5e9c\u6709\u53cd\u5e94\u7684\u65f6\u95f4\u51b3\u5b9a\u5c31\u505a\u4e86\u51fa\u6765\u3002\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u4e5f\u652f\u6301\u8fd9\u6b21\u8054\u5408\u8c03\u67e5\u884c\u52a8\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u5728\u533b\u9662\u91cc\u5f97\u77e5\u64cd\u7eb5\u62d8\u7981\u6848\u7684\u5e55\u540e\u4e3b\u4f7f\u7ed9\u4f9b\u4e86\u51fa\u6765\uff0c\u5fc3\u91cc\u53ef\u6ca1\u6709\u592a\u591a\u7684\u60ca\u559c\uff0c\u4ed6\u5fc3\u91cc\u53ea\u76d8\u65cb\u7740\u4e00\u4e2a\u5ff5\u5934\uff1a\u7f14\u9020\u51fa\u9526\u6e56\u7ecf\u6d4e\u5e1d\u56fd\u7684\u90a3\u4e2a\u9752\u5e74\u4ecb\u5165\u6b64\u4e8b\u7a76\u7adf\u662f\u4e3a\u4e86\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u5d14\u90c1\u6052\u6628\u5929\u591c\u91cc\u6574\u7406\u6750\u6599\u5230\u534a\u591c\uff0c\u53c8\u4e0e\u7236\u4eb2\u901a\u7535\u8bdd\u5230\u51cc\u6668\uff0c\u5929\u8499\u8499\u4eae\u624d\u5165\u7761\uff0c\u5bf9\u4ed6\u8fd9\u4e2a\u8fd8\u7b97\u662f\u91cd\u4f24\u53f7\u7684\u75c5\u4eba\u6765\u8bf4\uff0c\u8d1f\u8377\u8fd8\u6709\u4e9b\u5927\uff0c\u4e0a\u5348\u63a5\u5230\u5206\u793e\u9886\u5bfc\u7684\u7535\u8bdd\uff0c\u5934\u8fd8\u662f\u660f\u6c89\u6c89\u7684\uff0c\u6c5f\u5357\u5206\u793e\u4e5f\u5c06\u6d3e\u4eba\u53c2\u52a0\u7701\u91cc\u7ec4\u7ec7\u7684\u8fd9\u4e2a\u8054\u5408\u8c03\u67e5\u5c0f\u7ec4\u5230\u7ae0\u5dde\u53bb\u3002</p><p>\u3000\u3000\u201c\u54e5\uff0c\u7b80\u8bb0\u8005\u5e26\u7740\u4f60\u7684\u670b\u53cb\u6765\u62dc\u8bbf\u4f60\uff1f\u201d\u5d14\u90c1\u6052\u770b\u7740\u5c0f\u59b9\u6572\u95e8\u8fdb\u6765\uff0c\u7b80\u5fd7\u5eb7\u8ddf\u4e00\u4e2a\u957f\u76f8\u964c\u751f\u7684\u7537\u4eba\u968f\u540e\u8d70\u4e86\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u8bb0\u4e0d\u8d77\u8fd9\u4e2a\u7537\u4eba\u4ec0\u4e48\u65f6\u5019\u662f\u81ea\u5df1\u7684\u670b\u53cb\uff1f</p><p>\u3000\u3000\u201c\u600e\u4e48\uff0c\u771f\u4e0d\u8ba4\u8bc6\u6211\u4e86\uff1f\u201d\u53f6\u5efa\u658c\u4f38\u624b\u5728\u5d14\u90c1\u6052\u9762\u524d\u6447\u4e86\u6447\uff0c\u201c\u5f53\u65f6\u5728\u5341\u4e8c\u4e2d\uff0c\u6211\u6bd4\u4f60\u5c0f\u4e00\u5c4a\uff0c\u4f60\u5988\u8fd8\u7ed9\u6211\u4eec\u73ed\u4ee3\u8fc7\u4e24\u5929\u7684\u6570\u5b66\u8bfe\uff0c\u6211\u5bb6\u5728\u5317\u4eac\u65f6\u4f4f\u4e1c\u56db\u80e1\u540c\uff0c\u8ddf\u4f60\u5bb6\u5c31\u9694\u4e24\u6761\u5df7\u5b50\u2026\u2026\u4f60\u771f\u7684\u5bf9\u6211\u6ca1\u6709\u5370\u8c61\uff1f\u201d</p><p>\u3000\u3000\u5341\u4e8c\u4e2d\uff0c\u5d14\u90c1\u6052\u53ea\u8bb0\u5f97\u521d\u4e2d\u662f\u5728\u5341\u4e8c\u4e2d\u8bfb\u7684\uff0c\u90a3\u8fd8\u662f\u4e03\u516b\u5e74\u7684\u4e8b\u60c5\uff0c\u53c8\u4e0d\u662f\u540c\u73ed\uff0c\u53c8\u4e0d\u662f\u540c\u5c4a\uff0c\u5230\u5e95\u7b97\u54ea\u95e8\u5b50\u670b\u53cb\uff1f</p><p>\u3000\u3000\u5d14\u90c1\u6052\u5fc3\u91cc\u5373\u4f7f\u6709\u7591\u60d1\uff0c\u4f46\u662f\u4eba\u662f\u7b80\u5fd7\u5eb7\u9886\u8fc7\u6765\u7684\uff0c\u7b80\u5fd7\u5eb7\u6bd5\u7adf\u5bf9\u4ed6\u6709\u6551\u547d\u4e4b\u6069\uff0c\u81ea\u7136\u4e0d\u4f1a\u51b7\u6de1\uff0c\u8d77\u8eab\u8981\u7ed9\u4ed6\u4eec\u62c9\u6905\u5b50\uff0c\u8fd8\u5429\u5490\u4e00\u65c1\u7684\u59bb\u5b50\u53bb\u5012\u6c34\u3002</p><p>\u3000\u3000\u7b80\u5fd7\u5eb7\u5728\u4e00\u65c1\u70ed\u5207\u7684\u8bf4\u9053\uff1a\u201c\u6628\u5929\u8fd8\u4e0d\u77e5\u9053\u5d14\u8bb0\u8005\u7684\u670b\u53cb\u7adf\u7136\u662f\u76db\u946b\u96c6\u56e2\u7684\u53f6\u603b\uff0c\u4e0d\u7136\u6628\u5929\u5c31\u62c9\u53f6\u603b\u4e00\u8d77\u8fc7\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6f14\u53cc\u7c27\u4f3c\u7684\u8bf4\u9053\uff1a\u201c\u6211\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u90a3\u5c0f\u5b50\u524d\u5929\u591c\u91cc\u6253\u7535\u8bdd\u63ea\u6211\u8d77\u6765\uff0c\u539f\u6765\u8981\u6551\u7684\u4eba\u662f\u4f60\uff0c\u4e0d\u7136\u6628\u5929\u5c31\u6765\u9080\u529f\u4e86\u3002\u5f53\u5e74\u5341\u4e8c\u4e2d\u7684\u540c\u5b66\uff0c\u4f60\u8fd8\u6709\u51e0\u4e2a\u4eba\u5728\u8054\u7edc\uff1f\u201d\u70ed\u5207\u7684\u5750\u5230\u5d14\u90c1\u6052\u7684\u75c5\u69bb\u8fb9\uff0c\u56de\u5934\u770b\u7740\u5d14\u90c1\u66fc\u53c8\u8bf4\u9053\uff0c\u201c\u8fd9\u662f\u5c0f\u66fc\u5427\uff0c\u5f53\u5e74\u7ecf\u5e38\u5728\u80e1\u540c\u53e3\u770b\u5230\u4e00\u4e2a\u5c0f\u9f3b\u6d95\u866b\u8ddf\u5728\u90c1\u6052\u80cc\u540e\u2026\u2026\u201d</p><p>\u3000\u3000\u77ed\u77ed\u4e24\u4e09\u53e5\u8bdd\uff0c\u5c31\u5c06\u8981\u544a\u8bc9\u7684\u6d88\u606f\u90fd\u544a\u8bc9\u5d14\u90c1\u6052\u4e86\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u90fd\u89c9\u5f97\u6709\u4e9b\u610f\u5916\uff0c\u773c\u524d\u8fd9\u4e2a\u9ad8\u9ad8\u5927\u5927\u7684\u9752\u5e74\u5c31\u662f\u534e\u4e1c\u6700\u5927\u5546\u4e1a\u96c6\u56e2\u7684\u8001\u603b\u53f6\u5efa\u658c\uff1f\u8fd8\u6709\uff0c\u5f20\u606a\u662f\u901a\u8fc7\u4ed6\u8054\u7cfb\u5728\u7ae0\u5dde\u7684\u5efa\u90ba\u6668\u62a5\u8bb0\u8005\u6551\u51fa\u54e5\u54e5\u7684\uff1f\u4ed6\u8fd8\u6070\u5de7\u8ddf\u54e5\u54e5\u662f\u5341\u4e8c\u4e2d\u7684\u540c\u5b66\uff0c\u8fd9\u4e48\u7b97\u8d77\u6765\uff0c\u8fd8\u662f\u81ea\u5df1\u7684\u6821\u53cb\u55bd\uff1f</p><p>\u3000\u3000\u5d14\u90c1\u6052\u4e0d\u53ca\u53f6\u5efa\u658c\u4ed6\u4eec\u6709\u5907\u800c\u6765\uff0c\u8111\u7b4b\u6709\u4e9b\u77ed\u8def\uff0c\u4ed6\u6e05\u695a\u5e55\u540e\u662f\u9526\u6e56\u5728\u8c03\u67e5\u7ae0\u5dde\u7684\u7a00\u571f\u77ff\u8d44\u6e90\u73b0\u72b6\uff0c\u6ca1\u60f3\u5230\u76db\u946b\u96c6\u56e2\u53c8\u9661\u7136\u63d2\u4e86\u8fdb\u6765\u3002\u4ed6\u521a\u521a\u56de\u56fd\u5185\uff0c\u4fe1\u606f\u91cf\u8fd8\u6709\u9650\uff0c\u6709\u4e9b\u4e0d\u660e\u767d\u53f6\u5efa\u658c\u8dd1\u51fa\u6765\u7684\u610f\u56fe\u3002</p><p>\u3000\u3000\u201c\u6211\u77e5\u9053\u662f\u4f60\u540e\uff0c\u8fd8\u8ddf\u6211\u4e09\u53d4\u901a\u8fc7\u7535\u8bdd\uff0c\u6211\u4e09\u53d4\u8fd8\u7279\u610f\u5429\u5490\u6211\u5230\u533b\u9662\u540e\u7ed9\u4ed6\u6253\u7535\u8bdd\uff0c\u597d\u8ba9\u4ed6\u95ee\u5019\u4f60\u7684\u4f24\u60c5\u2026\u2026\u201d\u53f6\u5efa\u658c\u7ee7\u7eed\u70ed\u5207\u7684\u62c9\u5173\u7cfb\uff0c\u89c1\u5d14\u90c1\u6052\u7684\u8868\u60c5\u6709\u4e9b\u50f5\u6ede\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u4e09\u53d4\uff0c\u5175\u603b\u7684\u53f6\u795d\u6c11\uff0c\u90c1\u6052\u4f60\u8be5\u4e0d\u4f1a\u4e0d\u8ba4\u8bc6\u5427\uff1f\u5c0f\u6850\u4f60\u8be5\u8ba4\u8bc6\u5427\uff1f\u6211\u8ddf\u5c0f\u6850\u901a\u7535\u8bdd\u65f6\uff0c\u5c0f\u6850\u8fd8\u56de\u5fc6\u8fc7\u5728\u5317\u4eac\u8ddf\u4f60\u76f8\u4eb2\u7684\u4e8b\u60c5\uff0c\u8f6c\u773c\u95f4\u56db\u4e94\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u4f60\u4eec\u4e24\u4eba\u90fd\u5404\u81ea\u7ed3\u5a5a\u4e86\uff0c\u73b0\u5728\u5c0f\u6850\u5728\u9999\u96ea\u6d77\u7535\u5668\u5f53\u526f\u603b\u3002\u201d</p><p>\u3000\u3000\u8fd9\u4e48\u4e00\u8bf4\uff0c\u5d14\u90c1\u6052\u7684\u8bb0\u5fc6\u5c31\u9c9c\u6d3b\u8d77\u6765\u4e86\u3002</p><p>\u3000\u3000\u5175\u5668\u5de5\u4e1a\u603b\u516c\u53f8\u7684\u6e0a\u6e90\u8981\u8ffd\u6eaf\u5230\u6700\u65e9\u7684\u5175\u5668\u5de5\u4e1a\u90e8\uff0c\u540e\u6765\u53c8\u96b6\u5c5e\u4e8e\u56fd\u9632\u79d1\u5de5\u59d4\u3002\u4eca\u5e74\u521d\uff0c\u56fd\u9632\u79d1\u5de5\u59d4\u7684\u539f\u73ed\u4eba\u9a6c\u90fd\u8f6c\u8fc7\u53bb\u7ec4\u5efa\u603b\u88c5\u5907\u90e8\u4e86\uff0c\u5175\u5668\u5de5\u4e1a\u603b\u516c\u53f8\u53c8\u6539\u5c5e\u65b0\u6210\u7acb\u7684\u56fd\u9632\u79d1\u5de5\u59d4\u65d7\u4e0b\uff0c\u4f46\u662f\u5728\u4eca\u5e74\u4e4b\u524d\uff0c\u53f6\u795d\u6c11\u4e0e\u5d14\u6587\u6bc5\u8981\u7b97\u662f\u4e00\u4e2a\u7cfb\u7edf\u5185\u7684\u4e0a\u4e0b\u7ea7\u5173\u7cfb\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u7684\u786e\u4e0e\u53f6\u795d\u6c11\u6709\u8fc7\u63a5\u89e6\uff0c\u4f46\u5bf9\u4ece\u5c0f\u5728\u5efa\u90ba\u957f\u5927\u7684\u53f6\u5c0f\u6850\u4e0d\u719f\u6089\uff0c\u81f3\u4e8e\u90a3\u6b21\u76f8\u4eb2\uff0c\u4e5f\u4e0d\u8fc7\u662f\u56db\u5e74\u524d\u7684\u6625\u5929\u7ed9\u53cc\u65b9\u5bb6\u957f\u9a97\u8fc7\u53bb\u5230\u5496\u5561\u5385\u5750\u4e86\u5c0f\u534a\u5929\u800c\u5df2\uff0c\u5dee\u70b9\u7ed9\u5f3a\u8feb\u6210\u4e86\u653f\u6cbb\u5a5a\u59fb\u7684\u727a\u7272\u54c1\u3002</p><p>\u3000\u3000\u4e2d\u56fd\u7701\u90e8\u7ea7\u5b98\u5458\u3001\u9ad8\u7ea7\u5c06\u9886\u624d\u591a\u5927\u7684\u5708\u5b50\uff0c\u53f6\u5efa\u658c\u786c\u8981\u626f\u5173\u7cfb\u8fd8\u4e0d\u662f\u8f7b\u800c\u6613\u4e3e\u7684\u4e8b\u60c5\uff1f</p><p>\u3000\u3000\u201c\u53f6\u53d4\u53d4\u7684\u5973\u513f\u5728\u9999\u96ea\u6d77\u7535\u5668\u5f53\u526f\u603b\uff1f\u201d\u5d14\u90c1\u6052\u542c\u5230\u4e00\u4e2a\u5173\u952e\u7684\u5730\u65b9\uff0c\u91cd\u590d\u7684\u95ee\u4e86\u4e00\u904d\u3002\u6628\u5929\u591c\u91cc\u624d\u5728\u7535\u8bdd\u542c\u5230\u6709\u5173\u9526\u6e56\u7684\u4e00\u4e9b\u60c5\u51b5\uff0c\u4ed6\u4e5f\u4e0d\u53ef\u80fd\u4e00\u901a\u7535\u8bdd\u5c31\u5bf9\u9526\u6e56\u4e00\u5207\u90fd\u4e86\u7136\u4e8e\u5fc3\uff0c\u5e76\u4e0d\u77e5\u9053\u9526\u6e56\u80cc\u540e\u7684\u653f\u6cbb\u52bf\u529b\u5305\u62ec\u53f6\u5bb6\u3001\u9526\u6e56\u4e0e\u76db\u946b\u96c6\u56e2\u5728\u591a\u9886\u57df\u6709\u7740\u5bc6\u5207\u7684\u5408\u4f5c\u3001\u751a\u81f3\u53ef\u4ee5\u8bf4\u662f\u5df2\u7ecf\u5f52\u62e2\u5230\u540c\u4e00\u4e2a\u4f53\u7cfb\u4e4b\u4e0b\uff0c\u4f46\u662f\u4eca\u5e74\u7684\u592e\u89c6\u6807\u738b\u4f01\u4e1a\u9999\u96ea\u6d77\u7535\u5668\u8ddf\u9526\u6e56\u7684\u5173\u7cfb\u4ed6\u8fd8\u662f\u6e05\u695a\u7684\u3002</p><p>\u3000\u3000\u770b\u5427\uff0c\u8fd8\u662f\u626f\u4e0a\u5173\u7cfb\u4e86\uff0c\u5d14\u90c1\u6052\u5fc3\u91cc\u60f3\u7740\uff1a\u9526\u6e56\u4ecb\u5165\u6b64\u4e8b\u5230\u5e95\u6709\u4ec0\u4e48\u610f\u56fe\uff1f</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u6211\u4eec\u76db\u946b\u90fd\u7559\u4e0d\u4f4f\u5979\u4eba\uff0c\u201d\u53f6\u5efa\u658c\u5fae\u5fae\u4e00\u7b11\uff0c\u5d14\u90c1\u6052\u8fd8\u8eba\u5728\u91d1\u5c71\u7684\u533b\u9662\u91cc\uff0c\u6216\u8bb8\u5bf9\u4e00\u4e9b\u5185\u60c5\u8fd8\u65e0\u6cd5\u53ca\u65f6\u4e86\u89e3\uff0c\u53f6\u5efa\u658c\u5e76\u4e0d\u8ba4\u4e3a\u4ed6\u4f5c\u4e3a\u65b0\u534e\u793e\u7684\u8d44\u6df1\u8bb0\u8005\u3001\u5373\u5c06\u4e0a\u4efb\u7684\u65b0\u534e\u793e\u56fd\u5185\u65b0\u95fb\u90e8\u526f\u90e8\u957f\u3001\u53c8\u662f\u5d14\u6587\u6bc5\u7684\u513f\u5b50\uff0c\u60f3\u77e5\u9053\u9526\u6e56\u7684\u5185\u60c5\u4f1a\u6709\u591a\u56f0\u96be\uff0c\u6709\u4e9b\u4e8b\u60c5\u4e0d\u4f46\u4e0d\u4f1a\u7792\u4ed6\uff0c\u8fd8\u60f3\u7740\u501f\u52a9\u4ed6\u6765\u8fbe\u5230\u76ee\u7684\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u9999\u96ea\u6d77\u4e5f\u7b97\u8ddf\u76db\u946b\u6709\u4e9b\u5173\u7cfb\uff0c\u76db\u946b\u4e0e\u9526\u6e56\u90fd\u662f\u9999\u96ea\u6d77\u7684\u9999\u6e2f\u63a7\u80a1\u516c\u53f8\u8d8a\u79c0\u96c6\u56e2\u7684\u80a1\u4e1c\uff0c\u8fd9\u4e48\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u6709\u4e9b\u7ed5\u53e3\uff1f\u54c8\u54c8\u2026\u2026\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u4ed6\u4eca\u5929\u8fc7\u6765\u7684\u610f\u56fe\u975e\u5e38\u660e\u663e\uff0c\u4e00\u662f\u901a\u8fc7\u5d14\u90c1\u6052\u5728\u65b0\u534e\u793e\u5185\u90e8\u7684\u5730\u4f4d\uff0c\u5e0c\u671b\u65b0\u534e\u793e\u5bf9\u56fd\u5185\u7684\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u95ee\u9898\u53d1\u6325\u4f5c\u7528\uff0c\u53e6\u4e00\u4e2a\u610f\u56fe\u5c31\u662f\u8981\u76f4\u63a5\u8ffd\u5230\u5317\u4eac\u53bb\u8ddf\u5d14\u6587\u6bc5\u89c1\u9762\uff0c\u7a00\u571f\u8d44\u6e90\u7684\u519b\u4e8b\u6218\u7565\u610f\u4e49\uff0c\u5fc5\u987b\u8ba9\u519b\u65b9\u53d1\u51fa\u58f0\u97f3\uff0c\u800c\u603b\u88c5\u5907\u90e8\u5c31\u662f\u519b\u65b9\u80fd\u76f4\u63a5\u53d1\u51fa\u58f0\u97f3\u7684\u90e8\u95e8\u3002</p><p>\u3000\u3000\u201c\u5c0f\u66fc\u7684\u90a3\u4e2a\u5b66\u751f\u5462\uff1f\u8fd8\u771f\u4e8f\u4e86\u4ed6\u7ed9\u4f60\u6253\u90a3\u901a\u7535\u8bdd\uff0c\u4e0d\u7136\u6211\u4eec\u8fd8\u7ed9\u5173\u5728\u897f\u5858\u5e9f\u77ff\u573a\u7684\u9ed1\u5c4b\u5b50\u91cc\u3002\u201d\u5d14\u90c1\u6052\u5012\u662f\u77e5\u9053\u9526\u6e56\u5e55\u540e\u7684\u8d44\u6599\u4e0d\u53ef\u4ee5\u968f\u4fbf\u900f\u9732\u51fa\u53bb\uff0c\u4ed6\u65e2\u7136\u77e5\u9053\u5f20\u606a\u7684\u8eab\u4efd\uff0c\u4e5f\u731c\u6d4b\u9526\u6e56\u4ecb\u5165\u7ae0\u5dde\u80af\u5b9a\u53e6\u6709\u76ee\u7684\uff0c\u5fc3\u60f3\u5f20\u606a\u4ed6\u4eba\u5e94\u8be5\u8fd8\u6ca1\u6709\u79bb\u5f00\u91d1\u5c71\uff0c\u89c1\u5f20\u606a\u6ca1\u6709\u968f\u53f6\u5efa\u658c\u4e00\u540c\u51fa\u73b0\uff0c\u5d14\u90c1\u6052\u89c9\u5f97\u6709\u4e9b\u5947\u602a\u3002</p><p>\u3000\u3000\u201c\u90a3\u5c0f\u5b50\u554a\uff0c\u8bf4\u4e0d\u5b9a\u4e2d\u5348\u4e4b\u524d\u5c31\u8981\u56de\u5efa\u90ba\u53bb\uff0c\u5012\u662f\u59d4\u6258\u6211\u8fd9\u6b21\u53bb\u5317\u4eac\u8981\u4e13\u7a0b\u770b\u671b\u4e00\u4e0b\u5d14\u53d4\u53d4\u4ed6\u4eba\uff0c\u6211\u81ea\u5df1\u4e5f\u662f\u8fd9\u610f\u601d\uff0c\u201d\u53f6\u5efa\u658c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u521a\u624d\u5728\u4f4f\u9662\u90e8\u542c\u8bf4\u4f60\u4eec\u6253\u7b97\u4eca\u5929\u5c31\u56de\u5317\u4eac\uff1f\u201d</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u5728\u65c1\u8fb9\u89c9\u5f97\u5947\u602a\uff0c\u5f20\u606a\u90a3\u5bb6\u4f19\u8981\u4e13\u7a0b\u6258\u4eba\u53bb\u770b\u671b\u7238\u7238\u505a\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u201c\u6253\u7b97\u662f\u4eca\u5929\u56de\u53bb\uff0c\u53c8\u4e0d\u662f\u4ec0\u4e48\u8981\u547d\u7684\u4f24\uff0c\u5c31\u7b97\u8981\u517b\u4f24\uff0c\u8fd8\u662f\u56de\u5317\u4eac\u517b\u4f24\u597d\u3002\u201d\u5d14\u90c1\u6052\u8bf4\u9053\uff0c\u4ed6\u5bf9\u53f6\u5efa\u658c\u603b\u6709\u4e9b\u8b66\u60d5\uff0c\u7279\u522b\u662f\u53f6\u5efa\u658c\u6446\u660e\u4e86\u610f\u56fe\u8981\u53bb\u5317\u4eac\u89c1\u7236\u4eb2\u3002\u4ed6\u8fd9\u65f6\u5019\u80fd\u591f\u5c06\u53f6\u5bb6\u4e0e\u76db\u946b\u96c6\u56e2\u8054\u7cfb\u8d77\u6765\u4e86\uff0c\u53f6\u5efa\u658c\u8981\u89c1\u7236\u4eb2\uff0c\u5012\u4e0d\u662f\u5f00\u53e3\u62d2\u7edd\u5c31\u80fd\u591f\u963b\u6b62\u7684\u3002\u53f6\u5bb6\u51fa\u4e86\u56db\u4e2a\u7701\u90e8\u7ea7\u5b98\u5458\uff0c\u7279\u522b\u662f\u53f6\u81fb\u6c11\u4ece\u53bb\u5e74\u4ee5\u6765\u5728\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u4e2d\u6709\u51fa\u8272\u7684\u8868\u73b0\uff0c\u98ce\u5934\u6700\u5065\uff0c\u5728\u5916\u7ecf\u8d38\u90e8\u7684\u6392\u540d\u76f4\u7ebf\u4e0a\u5347\uff0c\u8981\u4e0d\u662f\u8d44\u5386\u5c1a\u6d45\uff0c\u5dee\u70b9\u5c31\u5728\u8fd9\u6b21\u7684\u56fd\u52a1\u9662\u673a\u6784\u6539\u9769\u4e2d\u51fa\u638c\u5916\u7ecf\u8d38\u90e8\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u53c8\u95ee\u5750\u5728\u4e00\u65c1\u7684\u5d14\u90c1\u66fc\uff1a\u201c\u5c0f\u66fc\u662f\u4e00\u8d77\u56de\u5317\u4eac\u8fd8\u662f\u56de\u5efa\u90ba\uff1f\u8981\u662f\u56de\u5efa\u90ba\u7684\u8bdd\uff0c\u5f20\u606a\u90a3\u5c0f\u5b50\u53ef\u80fd\u8fd9\u65f6\u5019\u8fd8\u6ca1\u6709\u52a8\u8eab\u5462\u3002\u201d\u62ff\u51fa\u624b\u673a\u51c6\u5907\u6253\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5bf9\u5d14\u90c1\u66fc\u6765\u8bf4\uff0c\u4e8b\u60c5\u5230\u8fd9\u4e00\u6b65\u5c31\u7b97\u4e86\u7ed3\u4e86\uff0c\u5979\u5f53\u7136\u662f\u56de\u5efa\u90ba\u53bb\uff0c\u4f46\u662f\u5979\u5fc3\u91cc\u5145\u6ee1\u7740\u5de8\u5927\u7684\u7591\u95ee\uff1a\u6628\u5929\u591c\u91cc\u54e5\u54e5\u660e\u663e\u5bf9\u5f20\u606a\u90a3\u5bb6\u4f19\u7684\u80cc\u666f\u6709\u6000\u7591\uff0c\u8ddf\u7238\u7238\u901a\u8fc7\u7535\u8bdd\u4e4b\u540e\u5374\u53c8\u4e00\u58f0\u4e0d\u542d\u4e86\uff1b\u8fd9\u65f6\u5019\u534e\u4e1c\u5730\u533a\u6700\u5927\u7684\u5546\u4e1a\u96c6\u56e2\u76db\u946b\u73af\u7403\u7535\u5668\u7684\u8001\u603b\u53f6\u5efa\u658c\u53c8\u7a81\u7136\u5192\u51fa\u6765\uff0c\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\u561b\uff1f</p><p>\u3000\u3000\u5d14\u90c1\u6052\u5fc3\u91cc\u5bf9\u5f20\u606a\u8fd9\u4e2a\u5c11\u5e74\u65f6\u5c31\u4e00\u624b\u7f14\u9020\u9526\u6e56\u7ecf\u6d4e\u5e1d\u56fd\u7684\u4f20\u5947\u4eba\u7269\u5145\u6ee1\u7740\u65e0\u6bd4\u7684\u597d\u5947\u5fc3\uff0c\u6628\u5929\u4e0a\u5348\u89c1\u8fc7\uff0c\u7adf\u7136\u8fd8\u4ee5\u4e3a\u662f\u4e2a\u5e73\u5e38\u7684\u5b98\u5ba6\u5b50\u5f1f\uff01\u5d14\u90c1\u6052\u8bf4\u9053\uff1a\u201c\u65e2\u7136\u8fd8\u6ca1\u6709\u8d70\uff0c\u5c31\u4e00\u8d77\u5403\u987f\u4e2d\u996d\u5427\u2014\u2014\u5c0f\u66fc\u6253\u7b97\u4e0b\u5348\u56de\u5efa\u90ba\u7684\uff0c\u6b63\u597d\u642d\u4ed6\u4eec\u7684\u8f66\u56de\u53bb\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0a\u5348\uff0c\u5468\u6e38\u8d76\u5230\u91d1\u5c71\uff0c\u4ee3\u8868\u65b0\u5149\u7eb8\u4e1a\u6b63\u5f0f\u4e0e\u6668\u66e6\u7eb8\u4e1a\u63a5\u89e6\u6d3d\u8c08\u5168\u8d44\u6536\u8d2d\u4e8b\u5b9c\u3002</p><p>\u3000\u3000\u5f20\u606a\u63a5\u5230\u53f6\u5efa\u658c\u7684\u7535\u8bdd\u65f6\uff0c\u4ed6\u4e0e\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u6b63\u5728\u6c5f\u5357\u7701\u653f\u5e9c\u529e\u516c\u5927\u697c\u6881\u4f1f\u6cd5\u7684\u529e\u516c\u5ba4\u91cc\u5bc6\u8c08\uff1a\u201c\u2026\u2026\u534e\u7a00\u77ff\u4e1a\u4e4b\u6240\u4ee5\u80fd\u5728\u7ae0\u5357\u5730\u533a\u75af\u72c2\u7684\u76d7\u91c7\u7a00\u571f\u8d44\u6e90\uff0c\u751a\u81f3\u7ae0\u5dde\u7684\u51e0\u5bb6\u56fd\u6709\u77ff\u4f01\u90fd\u53d7\u534e\u7a00\u77ff\u4e1a\u63a7\u5236\uff0c\u8ddf\u7ae0\u5dde\u5730\u65b9\u653f\u5e9c\u7684\u7eb5\u5bb9\u6709\u8131\u4e0d\u5f00\u7684\u5173\u7cfb\u3002\u7ae0\u5dde\u6301\u6709\u73cd\u5b9d\u800c\u4e0d\u80fd\u81ea\u91cd\uff0c\u771f\u662f\u4ee4\u4eba\u627c\u8155\u53f9\u606f\u2026\u2026\u8fd9\u6b21\u82e5\u67e5\u4e0d\u51fa\u7ae0\u5dde\u5730\u65b9\u653f\u5e9c\u7684\u95ee\u9898\u4e5f\u5c31\u7f62\u4e86\uff0c\u82e5\u662f\u67e5\u51fa\u6709\u6bd4\u8f83\u5927\u7684\u95ee\u9898\uff0c\u6211\u5efa\u8bae\u6c5f\u5357\u7701\u653f\u5e9c\u53ef\u4ee5\u501f\u673a\u5c06\u4e0b\u653e\u5230\u7ae0\u5dde\u5730\u65b9\u7684\u7a00\u571f\u91c7\u77ff\u6743\u90fd\u96c6\u4e2d\u8d77\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u82e5\u5728\u5e73\u65f6\uff0c\u6d89\u53ca\u5230\u5730\u65b9\u4e0e\u7701\u91cc\u7684\u5229\u76ca\u5206\u914d\uff0c\u4e0d\u8bba\u662f\u901a\u8fc7\u5e76\u8d2d\u8fd8\u662f\u884c\u653f\u547d\u4ee4\u5f3a\u5236\u6027\u7684\u91cd\u7ec4\uff0c\u7701\u91cc\u8981\u60f3\u5c06\u7ae0\u5dde\u5e02\u5c5e\u56fd\u6709\u77ff\u4f01\u7684\u91c7\u77ff\u6743\u96c6\u4e2d\u8d77\u6765\u7684\u53ef\u80fd\u6027\u6781\u5fae\u3002\u4f46\u662f\u4e8b\u65e0\u7edd\u5bf9\uff0c\u8fd9\u6b21\u80fd\u5c06\u7ae0\u5dde\u5e02\u4ece\u5e02\u59d4\u4e66\u8bb0\u3001\u5e02\u957f\u3001\u7eaa\u59d4\u4e66\u8bb0\u7b49\u5e02\u9886\u5bfc\u4e00\u7a9d\u94f2\u9664\uff0c\u66b4\u51fa\u80fd\u9707\u60ca\u5168\u56fd\u7684\u7a9d\u6848\uff0c\u7701\u91cc\u8981\u6536\u6743\uff0c\u7ae0\u5dde\u5730\u65b9\u662f\u4e0d\u4f1a\u518d\u6709\u4ec0\u4e48\u53cd\u5bf9\u58f0\u97f3\u4e86\u3002</p><p>\u3000\u3000\u73b0\u5728\u8fd9\u662f\u8981\u4ece\u534e\u7a00\u77ff\u4e1a\u8fd9\u6761\u7ebf\u6316\u4e0b\u53bb\uff0c\u81f3\u5c11\u8ba9\u5510\u82f1\u80b2\u3001\u5510\u82f1\u57f9\u5144\u5f1f\u6ca1\u6709\u4fa5\u5e78\u7684\u4f59\u5730\uff0c\u4ed6\u4eec\u4e3a\u4e86\u81ea\u4fdd\uff0c\u5c31\u4f1a\u54ac\u51fa\u5176\u4ed6\u4eba\u4e86\uff0c\u4e00\u5207\u8fd8\u8981\u770b\u7701\u91cc\u7684\u51b3\u5fc3\u4ee5\u53ca\u4e2d\u592e\u9ad8\u5c42\u65bd\u52a0\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u7701\u5c5e\u77ff\u4f01\u4e0e\u5730\u65b9\u5173\u8fde\u758f\u8fdc\u4e00\u4e9b\uff0c\u7ecf\u6d4e\u5229\u76ca\u4e5f\u4e0d\u4e00\u81f4\uff0c\u52fe\u7ed3\u8d77\u6765\u7eb5\u5bb9\u79c1\u77ff\u75af\u72c2\u76d7\u91c7\u7684\u73b0\u8c61\u624d\u53ef\u80fd\u5f97\u5230\u904f\u5236\u3002</p><p>\u3000\u3000\u5e02\u573a\u5316\u81ea\u7531\u7ade\u4e89\u7edd\u975e\u4e00\u5242\u80fd\u5305\u6cbb\u767e\u75c5\u7684\u4e07\u7075\u4e39\u836f\uff0c\u5f53\u7136\uff0c\u4e3a\u4e86\u56fd\u5bb6\u5229\u76ca\u8fdb\u884c\u5784\u65ad\u6027\u7ecf\u8425\u4e5f\u4e0d\u5e94\u8be5\u6210\u4e3a\u4ece\u666e\u901a\u6c11\u4f17\u5934\u4e0a\u641c\u522e\u6cb9\u6c34\u7684\u501f\u53e3\uff0c\u5173\u952e\u8fd8\u8981\u770b\u6267\u653f\u8005\u5ba1\u65f6\u5ea6\u52bf\u7684\u667a\u6167\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u4e0d\u5962\u671b\u9526\u6e56\u80fd\u9a6c\u4e0a\u5c31\u8fdb\u5165\u7a00\u571f\u4ea7\u4e1a\u94fe\u7684\u6700\u4e0a\u7aef\uff0c\u6c5f\u5357\u7701\u80fd\u4e0b\u51b3\u5b9a\u5c06\u5206\u6563\u7684\u7a00\u571f\u5f00\u91c7\u77ff\u4f01\u96c6\u4e2d\u8d77\u6765\uff0c\u80fd\u964d\u4f4e\u9526\u6e56\u8fdb\u5165\u7ae0\u5dde\u7a00\u571f\u4ea7\u4e1a\u94fe\u7684\u96be\u5ea6\uff0c\u5f53\u7136\u4e5f\u66f4\u65b9\u4fbf\u4e2d\u592e\u653f\u5e9c\u63a7\u5236\u7a00\u571f\u4ea7\u4e1a\u7684\u53d1\u5c55\u65b9\u5411\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u603b\u662f\u5fd9\u788c\u7684\uff0c\u4ed6\u4e2d\u5348\u53e6\u6709\u5bb4\u5e2d\u8981\u8d74\uff0c\u5f20\u606a\u63a5\u5230\u53f6\u5efa\u658c\u7684\u7535\u8bdd\u5c31\u544a\u8f9e\u79bb\u5f00\uff0c\u4e58\u8f66\u8d76\u5f80\u533b\u9662\u3002</p><p>\u3000\u3000\u5468\u6e38\u4e0e\u8bb8\u9e3f\u4f2f\u4e2d\u9014\u53bb\u4e86\u6c81\u56ed\uff0c\u5f20\u606a\u8d76\u5230\u533b\u9662\u5b89\u6392\u5d14\u90c1\u6052\u7b49\u4e09\u540d\u65b0\u534e\u793e\u8bb0\u8005\u4f11\u517b\u7684\u7279\u62a4\u697c\uff0c\u770b\u5230\u697c\u524d\u7684\u505c\u8f66\u8349\u576a\u4e0a\u505c\u7740\u91d1\u5c71\u5e02\u59d4\u7684\u4e00\u53f7\u8f66\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u4e5f\u8fc7\u6765\u51d1\u70ed\u95f9\u4e86\uff1f\u5f20\u606a\u4e0b\u8f66\u65f6\u6276\u7740\u8f66\u95e8\uff0c\u7591\u60d1\u7684\u770b\u7740\u9ed1\u8272\u768400001\u8f66\u724c\uff0c\u770b\u6765\u5468\u747e\u73ba\u8ddf\u7ae0\u5dde\u7684\u8054\u7cfb\u8fd8\u662f\u76f8\u5f53\u7684\u5bc6\u5207\u554a\uff01\u4ed6\u5012\u662f\u6ca1\u6709\u60f3\u5230\u5468\u747e\u73ba\u8fd8\u662f\u901a\u8fc7\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u7684\u6c60\u4f50\u82f1\u5bff\u77e5\u9053\u7ae0\u5dde\u53d1\u751f\u4e86\u53d8\u6545\u3002</p><p>\u3000\u3000\u60f3\u5fc5\u5468\u747e\u745c\u4e5f\u521a\u8d76\u5230\u8fd9\u91cc\uff0c\u4ee5\u81f4\u53f6\u5efa\u658c\u3001\u7b80\u5fd7\u5eb7\u90fd\u6ca1\u6709\u6765\u5f97\u53ca\u6253\u7535\u8bdd\u901a\u77e5\u81ea\u5df1\uff0c\u5f20\u606a\u6478\u4e86\u6478\u9f3b\u5934\uff0c\u5507\u89d2\u6d6e\u51fa\u82e5\u6709\u82e5\u65e0\u7684\u6d45\u7b11\uff0c\u8d70\u8fdb\u7279\u62a4\u697c\u3002</p><p>\u3000\u3000\u201c\u5ac2\u5b50\uff0c\u4f60\u5230\u91d1\u5c71\u6765\uff0c\u600e\u4e48\u90fd\u4e0d\u8ddf\u747e\u745c\u8a00\u8bed\u4e00\u58f0\uff1f\u8981\u4e0d\u662f\u6211\u8d76\u5de7\u5230\u533b\u9662\u6765\u89c6\u5bdf\u5de5\u4f5c\uff0c\u90fd\u4e0d\u77e5\u9053\u90c1\u6052\u5728\u6c5f\u5357\u7684\u5730\u9762\u4e0a\u51fa\u4e86\u4e8b\u60c5\u3002\u90c1\u6052\u8f6c\u773c\u4e5f\u8981\u8d70\u4e0a\u9886\u5bfc\u5c97\u4f4d\u4e86\uff0c\u8981\u662f\u5728\u6c5f\u5357\u51fa\u4e86\u4e8b\u60c5\uff0c\u4f60\u8bf4\u6211\u542c\u4e86\u5fc3\u91cc\u4f1a\u6709\u4ec0\u4e48\u6ecb\u5473\uff1f\u7ae0\u5dde\u4e0b\u9762\u4e61\u9547\u7684\u6076\u52bf\u529b\u4e5f\u592a\u65e0\u6cd5\u65e0\u5929\u4e86\uff0c\u8fd9\u6b21\u4e00\u5b9a\u8981\u72e0\u72e0\u7684\u6574\u6cbb\u4e00\u4e0b\u7ed9\u90c1\u6052\u8ba8\u56de\u516c\u9053\u4e0d\u53ef\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u63a8\u95e8\u8fdb\u53bb\u4e4b\u524d\u5c31\u542c\u5230\u5468\u747e\u745c\u90a3\u5373\u4f7f\u70ed\u5207\u62e2\u7edc\u4eba\u90fd\u7565\u6709\u4e9b\u5c16\u9510\u7684\u58f0\u97f3\uff0c\u63a8\u5f00\u95e8\u770b\u5230\u5468\u747e\u745c\u63e1\u7740\u5d14\u90c1\u66fc\u6bcd\u4eb2\u7684\u624b\u5750\u5728\u7a97\u524d\u6905\u5b50\u4e0a\uff0c\u770b\u5d14\u90c1\u66fc\u6bcd\u4eb2\u8138\u4e0a\u6709\u4e9b\u50f5\u6ede\u7684\u795e\u60c5\uff0c\u770b\u5f97\u51fa\u5d14\u5bb6\u8ddf\u5468\u5bb6\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u5468\u747e\u745c\u8fd9\u662f\u786c\u7740\u5934\u76ae\u627e\u4e0a\u95e8\u6765\u7684\u3002</p><p>\u3000\u3000\u201c\u5982\u679c\u4ec5\u4ec5\u662f\u8ba1\u8f83\u4e2a\u4eba\u6069\u6028\u7684\u8bdd\uff0c\u5d14\u8bb0\u8005\u7684\u601d\u60f3\u89c9\u609f\u5c31\u592a\u4f4e\u7ea7\u4e86\uff01\u201d\u5f20\u606a\u7ad9\u5728\u95e8\u53e3\u6307\u6851\u9a82\u69d0\u7684\u8bf4\u9053\uff0c\u51b7\u51b7\u7684\u523a\u4e86\u5468\u747e\u745c\u4e00\u53e5\u3002</p><p>\u3000\u3000\u521a\u624d\u8fd8\u5750\u5728\u4e00\u65c1\u5f53\u966a\u886c\u7684\u53f6\u5efa\u658c\u542c\u4e86\u5f20\u606a\u521a\u8fdb\u95e8\u5c31\u6ca1\u6709\u5410\u4ec0\u4e48\u597d\u8c61\u7259\uff0c\u9732\u9f7f\u7b11\u4e86\u8d77\u6765\uff0c\u54ea\u91cc\u4f1a\u7ba1\u5468\u747e\u745c\u9634\u6c89\u7684\u8138\u8272\uff1f</p><p>\u3000\u3000\u5468\u747e\u745c\u6028\u6bd2\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u5f20\u606a\u7684\u51fa\u73b0\u65e0\u7591\u8bc1\u5b9e\u5230\u5979\u4eec\u7684\u62c5\u5fe7\uff1a\u7ae0\u5dde\u4e8b\u4ef6\u679c\u7136\u4ece\u5934\u5230\u5c3e\u90fd\u662f\u8fd9\u5c0f\u5b50\u5728\u80cc\u540e\u64cd\u7eb5\uff0c\u53ea\u662f\u4e0d\u6e05\u695a\u4ed6\u8981\u6405\u548c\u5230\u4ec0\u4e48\u7a0b\u5ea6\u624d\u4f1a\u6536\u624b\u3002</p><p>\u3000\u3000\u5d14\u90c1\u66fc\u7684\u6bcd\u4eb2\u5012\u662f\u677e\u4e86\u4e00\u53e3\u6c14\u7684\u6837\u5b50\uff0c\u5d14\u90c1\u6052\u867d\u7136\u5e38\u7ed9\u4eba\u6279\u8bc4\u8eab\u4e0a\u7684\u6b63\u4e49\u7ec6\u8106\u8fc7\u5ea6\uff0c\u4f46\u7edd\u4e0d\u662f\u6ca1\u6709\u653f\u6cbb\u5934\u8111\u7684\u4eba\uff0c\u4ed6\u5f53\u521d\u5c31\u662f\u56e0\u4e3a\u5468\u747e\u73ba\u4e0e\u5f20\u606a\u5728\u8bb2\u5802\u4e0a\u7684\u90a3\u573a\u8fa9\u8bba\u624d\u5174\u8d77\u53bb\u7ae0\u5dde\u6697\u8bbf\u7684\u5fc3\u601d\uff0c\u5bf9\u5468\u747e\u73ba\u7684\u59b9\u59b9\u3001\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u5468\u747e\u745c\u7684\u51fa\u73b0\uff0c\u81ea\u7136\u6709\u7740\u8b66\u60d5\u3002\u4e0d\u8fc7\u4f5c\u4e3a\u6b63\u5e38\u7684\u5f85\u5ba2\u4e4b\u9053\uff0c\u4ed6\u8fd8\u8981\u633d\u7559\u5468\u747e\u745c\u4e2d\u5348\u7559\u4e0b\u6765\u4e00\u8d77\u7528\u9910\u7684\u3002</p><p>\u3000\u3000\u5468\u747e\u745c\u600e\u4e48\u4f1a\u7559\u4e0b\u6765\u7ed9\u5f20\u606a\u3001\u53f6\u5efa\u658c\u8054\u5408\u8d77\u6765\u5f53\u9776\u5b50\u653b\u51fb\uff1f\u63a8\u8bf4\u5e02\u59d4\u4e2d\u5348\u8fd8\u8981\u4e8b\u60c5\uff0c\u5c31\u5306\u5fd9\u79bb\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5d14\u90c1\u6052\u8138\u4e0a\u9f3b\u6881\u6bb5\u8499\u7740\u4e00\u5708\u7eb1\u5e03\u8fd8\u6ca1\u6709\u62c6\u6389\uff0c\u867d\u7136\u5df2\u7ecf\u4e0d\u5927\u788d\u4e8b\uff0c\u4f46\u662f\u5374\u4e0d\u5408\u9002\u8d70\u51fa\u533b\u9662\u53bb\u9152\u5e97\u7528\u9910\uff0c\u6240\u5e78\u7279\u62a4\u697c\u80fd\u63d0\u4f9b\u4e0d\u9519\u7684\u5348\u9910\uff0c\u5927\u5bb6\u4fbf\u7559\u5728\u7279\u62a4\u697c\u91cc\u5403\u996d\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u662f\u5f88\u70ed\u5207\u7684\u4eba\uff0c\u8fd9\u6b21\u7684\u610f\u56fe\u53c8\u5f88\u660e\u663e\uff0c\u5d14\u90c1\u6052\u5fc3\u91cc\u867d\u7136\u6709\u8b66\u60d5\uff0c\u4f46\u662f\u53f6\u5efa\u658c\u53ef\u5206\u62c5\u4e86\u6551\u4ed6\u4e8e\u96be\u7684\u540d\u4e49\uff0c\u4e8e\u60c5\u4e8e\u7406\u90fd\u4e0d\u80fd\u62d2\u4ed6\u4e8e\u5343\u91cc\u4e4b\u5916\uff0c\u9080\u8bf7\u4ed6\u4e0e\u7b80\u5fd7\u5eb7\u53bb\u5317\u4eac\u505a\u5ba2\u2014\u2014\u5c0f\u534a\u5929\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u53f6\u5efa\u658c\u4e0e\u5d14\u90c1\u6052\u4e00\u5bb6\u7684\u5173\u7cfb\u5012\u662f\u9887\u4e3a\u70ed\u7edc\u3002</p><p>\u3000\u3000\u4e0b\u5348\uff0c\u53f6\u5efa\u658c\u4e0e\u5d14\u90c1\u6052\u7b49\u4eba\u4e00\u8d77\u53bb\u5317\u4eac\uff0c\u540c\u4e58\u4e00\u73ed\u98de\u673a\u56de\u5317\u4eac\u7684\u8fd8\u6709\u6606\u817e\u5728\u7ebf\u65b0\u95fb\u90e8\u95e8\u7684\u8bb0\u8005\u2014\u2014\u7b80\u5fd7\u5eb7\u6682\u65f6\u8981\u5728\u7ae0\u5357\u7a00\u571f\u73b0\u72b6\u4e0a\u7ed9\u53f6\u5efa\u658c\u63d0\u4f9b\u66f4\u8be6\u7ec6\u7684\u60c5\u62a5\uff0c\u8fd9\u6b21\u4e5f\u5148\u8ddf\u7740\u53f6\u5efa\u658c\u53bb\u5317\u4eac\u4e00\u8d77\u5230\u5d14\u90c1\u6052\u5bb6\u505a\u5ba2\uff0c\u5f20\u606a\u79bb\u5f00\u91d1\u5c71\u4e4b\u524d\uff0c\u5148\u9001\u4ed6\u4eec\u53bb\u673a\u573a\u3002</p><p>\u3000\u3000\u5728\u53bb\u673a\u573a\u7684\u8def\u4e0a\uff0c\u5f20\u606a\u7279\u610f\u5c06\u5d14\u90c1\u6052\u4e0e\u4ed6\u8ddf\u53f6\u5efa\u658c\u5b89\u6392\u5728\u4ed6\u7684\u8f66\u4e0a\uff0c\u4ece\u4e2d\u5348\u7528\u9910\u65f6\u5d14\u90c1\u6052\u7684\u795e\u6001\u5c31\u80fd\u5224\u65ad\u51fa\u4ed6\u5df2\u7ecf\u77e5\u9053\u81ea\u5df1\u7684\u8eab\u4efd\u3002</p><p>\u3000\u3000\u5728\u5bbd\u655e\u7684\u5954\u9a70\u8f66\u540e\u5ea7\uff0c\u5d14\u90c1\u6052\u6b32\u8a00\u53c8\u6b62\uff0c\u4e0d\u77e5\u9053\u600e\u6837\u6311\u8d77\u8bdd\u5934\u3002</p><p>\u3000\u3000\u201c\u542c\u8bf4\u5d14\u5927\u54e5\u9a6c\u4e0a\u5c31\u8981\u62c5\u4efb\u65b0\u534e\u793e\u56fd\u5185\u65b0\u95fb\u90e8\u7684\u526f\u603b\u957f\uff0c\u9526\u6e56\u5728\u56fd\u5185\u6709\u4ec0\u4e48\u52a8\u9759\uff0c\u90fd\u5c06\u7792\u4e0d\u8fc7\u5d14\u5927\u54e5\u7684\u773c\u775b\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u5d14\u5927\u54e5\u4f3c\u4e4e\u5f88\u597d\u5947\u4e3a\u4ec0\u4e48\u6070\u597d\u5efa\u90ba\u6668\u62a5\u4e5f\u6709\u8bb0\u8005\u5728\u7ae0\u5dde\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5d14\u90c1\u6052\u70b9\u70b9\u5934\u3002</p><p>\u3000\u3000\u201c\u4e0d\u4ec5\u5efa\u90ba\u6668\u62a5\uff0c\u6606\u817e\u5728\u7ebf\u65b0\u95fb\u90e8\u95e8\u4e5f\u6709\u8bb0\u8005\u5728\u7ae0\u5dde\uff0c\u4ed6\u4eec\u4e0b\u5348\u4e5f\u8ddf\u4f60\u4eec\u4e58\u540c\u4e00\u73ed\u98de\u56de\u5317\u4eac\u53bb\uff0c\u53e6\u5916\u9526\u6e56\u5e02\u573a\u8c03\u67e5\u90e8\u95e8\u7684\u5458\u5de5\u4eca\u5929\u4e0a\u5348\u521a\u521a\u79bb\u5f00\u91d1\u5c71\u56de\u6d77\u5dde\u53bb\u3002\u6211\u5bf9\u56fd\u5185\u7684\u7a00\u571f\u4ea7\u4e1a\u4e4b\u524d\u6709\u4e9b\u4e00\u4e9b\u5173\u6ce8\uff0c\u4e0d\u8fc7\u6b63\u5f0f\u4ecb\u5165\u8fd8\u662f\u7f18\u4e8e\u534a\u4e2a\u6708\u524d\u8ddf\u5468\u747e\u73ba\u5728\u4e1c\u5927\u8bb2\u5802\u4e0a\u7684\u4e00\u6b21\u8fa9\u8bba\uff0c\u60f3\u5fc5\u5d14\u5927\u54e5\u4e5f\u77e5\u9053\u90a3\u6b21\u8fa9\u8bba\u7684\u60c5\u51b5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5c31\u51ed\u4e00\u5f20\u5229\u53e3\u9a73\u65a5\u5f97\u5468\u747e\u73ba\u5410\u8840\u6655\u5012\uff0c\u8fd9\u4efd\u53e3\u820c\u4e4b\u5229\uff0c\u8fd8\u771f\u662f\u4f50\u8bc1\u4ed6\u7684\u8fc7\u4eba\u80fd\u529b\u3002\u5d14\u90c1\u6052\u7b11\u7740\u8bf4\uff1a\u201c\u5c31\u662f\u542c\u5c0f\u66fc\u8bf4\u8d77\uff0c\u6211\u624d\u53bb\u7ae0\u5dde\u91c7\u8bbf\uff0c\u53bb\u4e4b\u524d\u7ecf\u8fc7\u5efa\u90ba\u60f3\u8981\u8054\u7cfb\u4f60\uff0c\u9519\u80a9\u800c\u8fc7\uff0c\u624d\u591a\u60f9\u51fa\u8fd9\u6bb5\u4e8b\u6765\u3002\u201d</p><p>\u3000\u3000\u8981\u4e0d\u53d1\u751f\u8fd9\u6bb5\u53d8\u6545\uff0c\u4e8b\u60c5\u54ea\u53ef\u80fd\u8fd9\u4e48\u987a\u5229\uff1f\u5f20\u606a\u5fc3\u91cc\u5077\u7b11\u7740\uff0c\u8138\u8272\u5374\u6ca1\u6709\u9732\u51fa\u58f0\u8272\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\u5468\u5bb6\u5728\u5317\u4eac\u6709\u5f88\u6df1\u539a\u7684\u4eba\u8109\uff0c\u6211\u4e5f\u770b\u5230\u4e86\u5468\u747e\u73ba\u5411\u56fd\u52a1\u9662\u7a00\u571f\u529e\u63d0\u4ea4\u7684\u7a00\u571f\u4ea7\u4e1a\u653f\u7b56\u8c03\u6574\u5efa\u8bae\u4e66\u7684\u5185\u5bb9\uff0c\u89c2\u70b9\u4e0e\u4ed6\u5728\u4e1c\u5927\u8bb2\u5802\u4e0a\u516c\u5171\u5ba3\u626c\u7684\u4e00\u81f4\u2026\u2026\u8fd9\u4e00\u70b9\uff0c\u5d14\u5927\u54e5\u4f60\u5230\u5317\u4eac\u540e\u53ef\u4ee5\u4e86\u89e3\u4e00\u4e0b\u3002\u4f60\u80fd\u4e0d\u80fd\u60f3\u8c61\u5468\u747e\u73ba\u5728\u6b64\u4e4b\u524d\u5bf9\u7ae0\u5dde\u7a00\u571f\u77ff\u8d44\u6e90\u7ed9\u79c1\u77ff\u75af\u72c2\u76d7\u91c7\u901a\u8fc7\u591a\u79cd\u6e20\u9053\u8fd0\u5f80\u56fd\u5916\u629b\u552e\u7684\u60c5\u51b5\u5df2\u7ecf\u76f8\u5f53\u7684\u8ba4\u8bc6\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u6ca1\u6709\u8bc1\u636e\uff1f\u201d\u5d14\u90c1\u6052\u7709\u5934\u5fae\u5fae\u76b1\u8d77\uff0c\u4ed6\u80fd\u610f\u8bc6\u5230\u95ee\u9898\u7684\u4e25\u91cd\u6027\u3002</p><p>\u3000\u3000\u201c\u5468\u747e\u73ba\u5728\u8bb2\u5802\u5927\u8086\u5ba3\u626c\u4ed6\u7684\u7a00\u571f\u4ea7\u4e1a\u53d1\u5c55\u89c2\u70b9\u65f6\uff0c\u534e\u7a00\u77ff\u4e1a\u4e0e\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u5728\u534e\u673a\u6784\u7684\u9ad8\u5c42\u4e5f\u5728\u73b0\u573a\uff0c\u5c0f\u5d14\u8001\u5e08\u5f53\u65f6\u5728\u573a\u4e5f\u6709\u76ee\u7779\uff0c\u53e6\u5916\u4e5f\u6709\u66f4\u786e\u51ff\u7684\u8bc1\u636e\u8bc1\u660e\u4ed6\u4eec\u4e4b\u95f4\u7684\u8054\u7cfb\u786e\u5b9e\u975e\u4e00\u822c\u7684\u5bc6\u5207\uff0c\u800c\u7ae0\u5357\u5730\u533a\u6bd4\u5408\u6cd5\u5f00\u91c7\u6570\u591a\u51fa\u6570\u500d\u7684\u76d7\u91c7\u77ff\u77f3\u5c31\u662f\u901a\u8fc7\u534e\u7a00\u4e0e\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u5728\u534e\u673a\u6784\u7684\u8054\u5408\u642d\u8bbe\u7684\u4e00\u4e9b\u7279\u6b8a\u6e20\u9053\u6d41\u51fa\u56fd\u95e8\u7684\u3002\u53ef\u4ee5\u6beb\u4e0d\u7559\u60c5\u7684\u8bf4\uff0c\u4ed6\u8fd9\u662f\u5728\u80cc\u53db\u56fd\u5bb6\u5728\u7a00\u571f\u77ff\u4ea7\u8d44\u6e90\u7684\u6218\u7565\u5229\u76ca\uff01\u201d\u5f20\u606a\u8bf4\u8fd9\u53e5\u8bdd\u65f6\u76ee\u5149\u51b7\u51bd\u800c\u65e0\u60c5\uff0c\u5c31\u662f\u8981\u4e00\u68cd\u5b50\u5c06\u5468\u747e\u73ba\u6253\u6b7b\uff0c\u6709\u4e9b\u95ee\u9898\u82e5\u7531\u5d14\u90c1\u6052\u4ee5\u65b0\u534e\u793e\u56fd\u5185\u65b0\u95fb\u90e8\u8d1f\u8d23\u4eba\u7684\u8eab\u4efd\u53bb\u5411\u6709\u5173\u90e8\u95e8\u53cd\u5e94\u66f4\u6709\u6548\u679c\uff0c\u5f20\u606a\u6ca1\u6709\u523b\u610f\u7684\u53bb\u89c2\u5bdf\u5d14\u90c1\u6052\u7684\u53cd\u5e94\uff0c\u4ed6\u53c8\u8bf4\u9053\uff0c\u201c\u7a00\u571f\u8d44\u6e90\u7684\u6218\u7565\u4ef7\u503c\uff0c\u6211\u60f3\u5d14\u6587\u6bc5\u5c06\u519b\u4f1a\u6bd4\u666e\u901a\u4eba\u6709\u66f4\u6df1\u523b\u7684\u8ba4\u8bc6\u3002\u6211\u4e0d\u6e05\u695a\u5d14\u6587\u6bc5\u5c06\u519b\u5728\u77e5\u9053\u7ae0\u5357\u5730\u533a\u7684\u7a00\u571f\u8d44\u6e90\u7ed9\u75af\u72c2\u76d7\u91c7\u7684\u5b9e\u9645\u60c5\u51b5\u4e4b\u540e\uff0c\u4f1a\u6709\u600e\u6837\u7684\u5fc3\u60c5\uff1f\u5982\u679c\u8fd9\u4e2a\u5c40\u9762\u4e0d\u7acb\u5373\u5c31\u63a7\u5236\u4f4f\uff0c\u53cd\u800c\u8981\u53d8\u672c\u52a0\u5389\u7684\u7eb5\u5bb9\u4e0b\u53bb\uff0c\u518d\u8fc7\u4e8c\u5341\u5e74\u3001\u4e09\u5341\u5e74\uff0c\u53c8\u5c06\u662f\u600e\u6837\u7684\u4e00\u756a\u5c40\u9762\uff1f\u9526\u6e56\u4ecb\u5165\u6b64\u4e8b\uff0c\u662f\u6709\u81ea\u5df1\u7684\u5229\u76ca\u8bc9\u6c42\uff0c\u4f46\u662f\u6211\u76f8\u4fe1\u9526\u6e56\u7684\u5229\u76ca\u8bc9\u6c42\u4e0e\u56fd\u5bb6\u5d1b\u8d77\u7684\u6218\u7565\u662f\u4e00\u81f4\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5468\u747e\u745c\u6ca1\u60f3\u5230\u4e8b\u60c5\u8981\u6bd4\u76f8\u8c61\u4e2d\u4e25\u91cd\uff0c\u5979\u751a\u81f3\u6ca1\u6709\u6562\u5c06\u6d88\u606f\u7acb\u5373\u6253\u7535\u8bdd\u544a\u8bc9\u5979\u54e5\u3002\u5979\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u7684\u773c\u775b\u5df2\u7ecf\u76ef\u7740\u4e09\u4e95\u7279\u79cd\u91d1\u5c5e\u7684\u90a3\u6761\u7ebf\u4e0a\u4e86\uff0c\u5bf9\u5979\u6765\u8bf4\uff0c\u8ba9\u5f20\u606a\u901a\u8fc7\u5d14\u90c1\u6052\u8ddf\u603b\u88c5\u5907\u90e8\u7684\u5d14\u6587\u6bc5\u642d\u4e0a\u5173\u7cfb\uff0c\u611f\u89c9\u5c31\u5df2\u7ecf\u591f\u7cdf\u7cd5\u4e86\u3002</p><p>\u3000\u3000\u5728\u56fd\u5185\uff0c\u519b\u65b9\u901a\u5e38\u4e0d\u4f1a\u53d1\u51fa\u4ec0\u4e48\u58f0\u97f3\uff0c\u8981\u662f\u5c31\u6b64\u4ee5\u4e3a\u519b\u65b9\u6ca1\u6709\u58f0\u97f3\u6216\u8005\u8bf4\u519b\u65b9\u53d1\u4e0d\u51fa\u4ec0\u4e48\u58f0\u97f3\u5c31\u5927\u9519\u7279\u9519\u4e86\u3002</p><p>\u3000\u3000\u9526\u6e56\u81f3\u5c11\u5728\u79c1\u4eba\u5173\u7cfb\u5c42\u6b21\u7684\u4eba\u8109\u7f51\u7edc\u662f\u8d8a\u6765\u8d8a\u5f3a\u5927\u4e86\u3002</p><p>\u3000\u3000\u867d\u7136\u662f\u4e00\u4e2a\u7cdf\u7cd5\u7684\u6d88\u606f\uff0c\u5468\u747e\u745c\u8fd8\u662f\u8981\u6253\u7535\u8bdd\u544a\u8bc9\u5979\u54e5\uff0c\u603b\u8981\u9884\u9632\u6700\u7cdf\u7cd5\u7684\u5c40\u9762\u51fa\u73b0\u3002</p><p>\u3000\u3000\u6700\u7cdf\u7cd5\u7684\u5c40\u9762\u662f\u4ec0\u4e48\uff1f\u5468\u747e\u745c\u751a\u81f3\u8fd8\u6ca1\u6709\u60f3\u5230\u80cc\u53db\u56fd\u5bb6\u5229\u76ca\u8fd9\u4e00\u5c42\u4e0a\u53bb\uff0c\u5468\u747e\u73ba\u751a\u81f3\u5e73\u65f6\u90fd\u6ca1\u6709\u56de\u907f\u4e0e\u4e09\u4e95\u3001\u534e\u7a00\u7684\u9ad8\u5c42\u4eba\u5458\u5728\u516c\u5f00\u573a\u5408\u9732\u9762\u3002\u53e6\u5916\uff0c\u4e09\u4e95\u6e17\u900f\u5230\u4e2d\u56fd\u6709\u4e8c\u5341\u5e74\u7684\u5386\u53f2\uff0c\u5728\u56fd\u5185\u6276\u690d\u7684\u52bf\u529b\u4e5f\u662f\u6839\u6df1\u8482\u56fa\uff0c\u4e5f\u7edd\u4e0d\u5e0c\u671b\u770b\u5230\u6709\u6700\u7cdf\u7cd5\u7684\u5c40\u9762\u51fa\u73b0\u3002</p></article>"}}}]);
