(window.webpackJsonp=window.webpackJsonp||[]).push([[1022],{1045:function(p){p.exports={chapterTitle:null,title:"\u7b2c1019\u7ae0 \u64cd\u788e\u4e86\u5fc3",content:"<article><h2>\u7b2c1019\u7ae0 \u64cd\u788e\u4e86\u5fc3</h2><p>\u3000\u3000\u5229\u76ca\u8bc9\u6c42\u9ad8\u5ea6\u4e00\u81f4\u65f6\uff0c\u5c31\u80fd\u4e00\u62cd\u5373\u5408\u3002</p><p>\u3000\u3000\u4e94\u6708\u4e94\u65e5\u4e0a\u5348\uff0c\u5218\u6210\u536b\u5728\u7535\u8bdd\u91cc\u5411\u4e2d\u56fd\u6d77\u6d0b\u77f3\u6cb9\u603b\u516c\u53f8\u515a\u7ec4\u4e66\u8bb0\u53ca\u5176\u4ed6\u515a\u7ec4\u6210\u5458\u901a\u62a5\u4e86\u5510\u5b66\u8c26\u3001\u5f20\u606a\u3001\u53f6\u5efa\u658c\u7b49\u4eba\u6df1\u591c\u5bc6\u8bbf\u7684\u60c5\u51b5\uff0c\u6d77\u6d0b\u77f3\u6cb9\u603b\u516c\u53f8\u515a\u7ec4\u901a\u8fc7\u7b80\u77ed\u8ba8\u8bba\u5f88\u5feb\u5c31\u80fd\u4e00\u81f4\u540c\u610f\u4e0e\u9526\u6e56\u4e0e\u6c5f\u5357\u7701\u59d4\u7701\u653f\u5e9c\u5408\u4f5c\u3002</p><p>\u3000\u3000\u9999\u6e2f\u4e0a\u5e02\u878d\u8d44\u8ba1\u5212\u5bf9\u4e2d\u6d77\u6cb9\u7684\u4e0b\u4e00\u6b65\u53d1\u5c55\u5e03\u5c40\u6781\u4e3a\u91cd\u8981\uff0c\u4e0d\u5bb9\u6709\u5931\uff0c\u5728\u524d\u666f\u9eef\u6de1\u4e4b\u65f6\uff0c\u9526\u6e56\u603b\u989d\u9ad8\u8fbe\u56db\u4ebf\u7f8e\u5143\u7684\u8d22\u52a1\u6295\u8d44\u8ba1\u5212\u6709\u5982\u96ea\u4e2d\u9001\u78b3\uff0c\u4e2d\u6d77\u77f3\u6cb9\u603b\u516c\u53f8\u515a\u7ec4\u526f\u4e66\u8bb0\u5218\u6210\u536b\u7b80\u76f4\u6709\u7ed9\u6551\u4e86\u4e00\u547d\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u4e2d\u6d77\u6cb9\u7ad9\u5728\u81ea\u5df1\u7684\u653f\u6cbb\u7acb\u573a\u8fd8\u662f\u5229\u76ca\u7acb\u573a\uff0c\u4e5f\u8981\u62b5\u5236\u4e09\u4e95\u7269\u4ea7\u671d\u4e1c\u6d77\u5206\u754c\u6cb9\u6c14\u7530\u4f38\u624b\uff0c\u4e4b\u524d\u8003\u8651\u8981\u4e0a\u5e02\u878d\u8d44\u53ef\u80fd\u4f1a\u5931\u8d25\uff0c\u5e95\u6c14\u4e0d\u8db3\uff0c\u59ff\u6001\u5c31\u8f6f\uff0c\u6ca1\u6709\u542d\u58f0\uff1b\u773c\u524d\u4e0a\u5e02\u878d\u8d44\u6210\u529f\u7684\u628a\u63e1\u5927\u5e45\u63d0\u9ad8\uff0c\u5c31\u66f4\u6709\u5e95\u6c14\u62b5\u5236\u3002</p><p>\u3000\u3000\u4e2d\u6d77\u6cb9\u8981\u8fdb\u5165\u6210\u54c1\u6cb9\u5e02\u573a\uff0c\u5c31\u662f\u8981\u4ece\u4e2d\u77f3\u5316\u3001\u4e2d\u77f3\u6cb9\u7897\u91cc\u62a2\u98df\u5403\uff0c\u65e2\u7136\u6c5f\u5357\u7701\u653f\u5e9c\u613f\u610f\u914d\u5408\uff0c\u4e2d\u6d77\u6cb9\u4e00\u767e\u4e8c\u5341\u4e2a\u7684\u613f\u610f\u53bb\u62e6\u8def\u62a2\u52ab\u6536\u8d2d\u91d1\u5c71\u77f3\u6cb9\uff0c\u63a5\u53d7\u91d1\u5c71\u77f3\u6cb9\u7684\u52a0\u6cb9\u7ad9\u8d44\u4ea7\u4e0e\u70bc\u6cb9\u5382\u8d44\u4ea7\uff0c\u4e2d\u6d77\u6cb9\u5728\u6210\u54c1\u6cb9\u5e02\u573a\u5c31\u7b97\u6709\u4e86\u4e2a\u5e95\u5b50\uff0c\u867d\u7136\u5f88\u8584\u5f31\uff0c\u4f46\u662f\u8fd8\u53ef\u4ee5\u5728\u4e1c\u5c71\u6e2f\u53e3\u5de5\u4e1a\u533a\u5185\u6295\u8d44\u5efa\u9020\u66f4\u5927\u89c4\u6a21\u7684\u77f3\u6cb9\u70bc\u5316\u57fa\u5730\uff0c\u5728\u6c5f\u5357\u7701\u5883\u5185\u6295\u8d44\u5efa\u9020\u66f4\u591a\u7684\u52a0\u6cb9\u7ad9\uff0c\u751a\u81f3\u53ef\u4ee5\u5148\u652f\u6301\u6c11\u8425\u8d44\u672c\u6295\u8d44\u5efa\u52a0\u6cb9\u7ad9\uff0c\u53ea\u8981\u80fd\u5148\u6253\u5f00\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u7684\u5784\u65ad\u5e03\u5c40\u5c31\u884c\u3002</p><p>\u3000\u3000\u5728\u5218\u6210\u536b\u4e0e\u53f6\u5efa\u658c\u98de\u5f80\u9999\u6e2f\u5177\u4f53\u5546\u8c08\u8d22\u52a1\u6295\u8d44\u534f\u8bae\u4e4b\u65f6\uff0c\u6d77\u6cb9\u603b\u516c\u53f8\u4e00\u65b9\u9762\u6307\u4ee4\u57fa\u5730\u516c\u53f8\u4e0b\u5c5e\u7684\u534e\u4e1c\u5206\u516c\u53f8\u4e0e\u65b0\u4ead\u5730\u65b9\u8fdb\u884c\u5b9e\u8d28\u6027\u7684\u5173\u4e8e\u751f\u4ea7\u652f\u6301\u57fa\u5730\u9879\u76ee\u6295\u8d44\u534f\u8bae\u7684\u8c08\u5224\uff0c\u4e00\u65b9\u9762\uff0c\u6d77\u6cb9\u603b\u516c\u53f8\u526f\u603b\u7406\u5f20\u6210\u88d5\u98de\u5230\u91d1\u5c71\uff0c\u4e0e\u6c5f\u5357\u7701\u653f\u5e9c\u6d3d\u8c08\u5728\u4e1c\u5c71\u6e2f\u6295\u8d44\u5efa\u8bbe\u5927\u578b\u70bc\u5316\u4ea7\u4e1a\u57fa\u5730\u53ca\u5e76\u8d2d\u6c5f\u5357\u7701\u5730\u65b9\u6240\u5c5e\u70bc\u5316\u3001\u6210\u54c1\u6cb9\u9500\u552e\u4e1a\u52a1\u8d44\u4ea7\u7684\u4e8b\u5b9c\uff0c\u540c\u65f6\uff0c\u6d77\u6cb9\u603b\u516c\u53f8\u5728\u4e2d\u592e\u9ad8\u5c42\u5c55\u5f00\u516c\u5173\uff0c\u4fc3\u8fdb\u8ba1\u5212\u53d1\u5c55\u59d4\u8c03\u6574\u5bf9\u6c5f\u5357\u7701\u77f3\u6cb9\u4ea7\u4e1a\u7684\u6574\u5408\u89c4\u5212\uff0c\u5728\u4e09\u4e95\u7269\u4ea7\u7684\u63d0\u6848\u8fd8\u5728\u90e8\u59d4\u5c42\u6b21\u4ee5\u4e0b\u8ba8\u8bba\u65f6\uff0c\u6d77\u6cb9\u603b\u516c\u53f8\u4e5f\u575a\u51b3\u7684\u53d1\u51fa\u62b5\u5236\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e2d\u6d77\u6cb9\u7684\u59ff\u6001\u9661\u7136\u5f3a\u786c\u8d77\u6765\uff0c\u4ee4\u6c60\u4f50\u79c0\u85cf\u6781\u4e3a\u610f\u5916\uff0c\u53ea\u4e0d\u8fc7\u4e00\u5207\u90fd\u6682\u65f6\u9690\u85cf\u5728\u6c34\u9762\u4e0b\u8fdb\u884c\uff0c\u6c60\u4f50\u79c0\u85cf\u627e\u4e0d\u5230\u95ee\u9898\u51fa\u5728\u54ea\u91cc\u3002</p><p>\u3000\u3000\u201c\u4f60\u80fd\u77e5\u9053\u95ee\u9898\u51fa\u5728\u54ea\u91cc\uff1f\u201d\u6c60\u4f50\u79c0\u85cf\u76d8\u817f\u5750\u5728\u69bb\u69bb\u7c73\u4e0a\uff0c\u770b\u7740\u4ed6\u5728\u4e2d\u56fd\u7684\u597d\u53cb\u3001\u65e5\u672c\u9a7b\u534e\u5927\u4f7f\u9986\u4e66\u8bb0\u5b98\u5bab\u672c\u96c4\u4e09\uff0c\u95ee\u4ed6\u3002</p><p>\u3000\u3000\u201c\u4e09\u4e95\u60c5\u62a5\u90e8\u95e8\u6ca1\u6709\u641c\u96c6\u5230\u6709\u7528\u7684\u4fe1\u606f\uff1f\u201d\u5bab\u672c\u96c4\u4e09\u524d\u989d\u5fae\u79c3\uff0c\u4ed6\u4eca\u5e74\u521a\u521a\u4ece\u4f7f\u9986\u4e00\u79d8\u63d0\u5347\u5230\u4e66\u8bb0\u5b98\u7684\u4f4d\u5b50\u4e0a\uff0c\u9887\u4e3a\u4eab\u53d7\u5728\u4e2d\u56fd\u3001\u5728\u5317\u4eac\u7684\u751f\u6d3b\uff0c\u4e5f\u4e60\u60ef\u4e0e\u6c60\u4f50\u79c0\u85cf\u7528\u6c49\u8bed\u4ea4\u8c08\uff0c\u4ed6\u77e5\u9053\u4e09\u4e95\u7ecf\u6d4e\u7814\u7a76\u6240\u5229\u7528\u4e09\u4e95\u7269\u4ea7\u65d7\u4e0b\u4f17\u591a\u7684\u5206\u652f\u673a\u6784\u5bf9\u4e9a\u6d32\u7ecf\u6d4e\u4ea7\u4e1a\u7684\u60c5\u62a5\u641c\u96c6\u5de5\u4f5c\u5f02\u5e38\u5f3a\u5927\u3002</p><p>\u3000\u3000\u201c\u4e2d\u6d77\u6cb9\u5728\u4e2d\u56fd\u5357\u6d77\u897f\u6c5f\u4ee5\u53ca\u6e24\u6d77\u6e7e\u65b0\u6c82\u7b49\u6d77\u57df\u7684\u6cb9\u6c14\u52d8\u63a2\u5f00\u91c7\u4e1a\u52a1\u90fd\u9700\u8981\u6295\u5165\u5927\u7b14\u7684\u8d44\u91d1\uff0c\u6839\u636e\u7814\u7a76\u6240\u63d0\u4ea4\u7684\u60c5\u62a5\uff0c\u4e2d\u6d77\u6cb9\u7684\u6d77\u5916\u4e0a\u5e02\u878d\u8d44\u8ba1\u5212\u8fdb\u884c\u5f97\u5e76\u4e0d\u987a\u5229\uff0c\u201d\u6c60\u4f50\u79c0\u85cf\u5b9e\u5728\u60f3\u4e0d\u660e\u767d\u4e60\u60ef\u660e\u54f2\u4fdd\u8eab\u7684\u4e2d\u56fd\u592e\u4f01\u5b98\u5458\u4eec\u600e\u4e48\u53ef\u80fd\u5982\u6b64\u5f3a\u786c\u7684\u8868\u6001\uff0c\u4ed6\u7ed9\u597d\u53cb\u52d8\u8336\uff0c\u95ee\u9053\uff0c\u201c\u4ed6\u4eec\u8fd9\u65f6\u5019\u660e\u786e\u7684\u8868\u6001\u62d2\u7edd\u6211\u4eec\u4e09\u4e95\u7684\u6295\u8d44\uff0c\u96be\u9053\u4e0d\u6015\u5728\u6d77\u5916\u4e0a\u5e02\u8ba1\u5212\u53d7\u632b\u4e4b\u4e2d\u88ab\u653f\u654c\u501f\u8fd9\u7f18\u7531\u653b\u51fb\u5417\uff1f\u6ca1\u6709\u80fd\u529b\u6d77\u5916\u4e0a\u5e02\u878d\u8d44\uff0c\u5374\u8fd8\u6709\u8d44\u683c\u5bf9\u4e3b\u52a8\u627e\u4e0a\u95e8\u6765\u7684\u5408\u4f5c\u8d44\u91d1\u6311\u4e09\u6361\u56db\u2026\u2026\u201d\u6c60\u4f50\u79c0\u85cf\u6700\u540e\u4e00\u53e5\u5b50\u8bdd\u662f\u6a21\u4eff\u60f3\u8c61\u4e2d\u7684\u4e2d\u56fd\u5b98\u5458\u8d28\u95ee\u653f\u654c\u7684\u8bed\u6c14\u3002</p><p>\u3000\u3000\u201c\u636e\u6211\u6240\u77e5\uff0c\u4e2d\u6d77\u6cb9\u4e0e\u957f\u671f\u7684\u5408\u4f5c\u4f19\u4f34\u83f2\u5229\u6d66\u65af\u77f3\u6cb9\u516c\u53f8\u5728\u65b0\u6c82\u6cb9\u7530\u52d8\u63a2\u9879\u76ee\u4e5f\u5728\u95f9\u4e0d\u6109\u5feb\uff0c\u8981\u662f\u5931\u53bb\u83f2\u5229\u6d66\u65af\u77f3\u6cb9\u516c\u53f8\u7684\u652f\u6301\uff0c\u4e2d\u6d77\u6cb9\u7684\u60c5\u51b5\u4f1a\u66f4\u7a98\u8feb\u2014\u2014\u4ed6\u4eec\u7684\u6001\u5ea6\u662f\u6709\u4e9b\u5947\u602a\u3002\u201d\u5bab\u672c\u96c4\u4e09\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4f55\u6b62\u662f\u5947\u602a\uff0c\u7b80\u76f4\u5c31\u662f\u53cd\u5e38\uff0c\u4e2d\u56fd\u7684\u5b98\u5458\u6709\u591a\u5c11\u662f\u6709\u786c\u9aa8\u6c14\u7684\uff0c\u504f\u504f\u90fd\u805a\u5230\u4e2d\u6d77\u6cb9\u53bb\u4e86\uff1f\u201d\u6c60\u4f50\u79c0\u85cf\u7684\u8bed\u6c14\u91cc\u591a\u5c11\u6709\u4e9b\u4e0d\u5c51\u4e0e\u8ba5\u8bbd\uff0c\u4ed6\u5728\u5317\u4eac\u4e0e\u4e2d\u56fd\u5b98\u5458\u63a5\u89e6\u591a\u4e86\uff0c\u77e5\u9053\u4e2d\u56fd\u8bb8\u591a\u5b98\u5458\u5373\u4f7f\u4e0d\u592a\u8d2a\uff0c\u4f46\u662f\u66f4\u591a\u7684\u4e5f\u4f1a\u91c7\u53d6\u660e\u54f2\u4fdd\u8eab\u7684\u6e29\u548c\u7acb\u573a\uff0c\u53c8\u4e0e\u5bab\u672c\u96c4\u4e09\u8bc9\u82e6\uff0c\u201c\u5c31\u4e09\u4e95\u7269\u4ea7\u81ea\u8eab\u6765\u8bf4\uff0c\u5bf9\u5206\u754c\u6cb9\u6c14\u7530\u5e76\u6ca1\u6709\u7279\u522b\u7684\u6e34\u671b\uff0c\u80fd\u53c2\u4e0e\u5f00\u53d1\u5219\u53c2\u4e0e\u5f00\u53d1\uff0c\u4e0d\u80fd\u53c2\u4e0e\u5f00\u53d1\uff0c\u4e5f\u8981\u6700\u5927\u9650\u5ea6\u7684\u62d6\u5ef6\u4e2d\u6d77\u6cb9\u5bf9\u5176\u8fdb\u884c\u5f00\u53d1\uff0c\u6211\u4e5f\u4e0e\u77f3\u6cb9\u52d8\u63a2\u5f00\u91c7\u516c\u53f8\u9a7b\u534e\u4e8b\u52a1\u6240\u7684\u8d1f\u8d23\u4eba\u901a\u8fc7\u7535\u8bdd\uff0c\u8ba9\u4e8b\u52a1\u6240\u5c3d\u5feb\u67e5\u660e\u5230\u5e95\u662f\u54ea\u91cc\u51fa\u4e86\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u4ee5\u4e3a\u963b\u6b62\u4e2d\u6d77\u6cb9\u6d77\u5916\u4e0a\u5e02\u878d\u8d44\u5b9e\u8d28\u4e0a\u80fd\u591f\u6709\u6548\u7684\u963b\u5ef6\u4e2d\u56fd\u77f3\u6cb9\u5de5\u4e1a\u5411\u6d77\u6d0b\u5ef6\u4f38\u4e0e\u53d1\u5c55\uff0c\u8fd8\u80fd\u4ea7\u751f\u4e00\u7cfb\u5217\u5bf9\u65e5\u672c\u6709\u5229\u7684\u8fde\u9501\u53cd\u5e94\uff0c\u201d\u5bab\u672c\u96c4\u4e09\u8bf4\u9053\uff0c\u201c\u5728\u4e00\u65f6\u8fd8\u67e5\u4e0d\u660e\u95ee\u9898\u51fa\u5728\u54ea\u91cc\u65f6\uff0c\u901a\u8fc7\u5404\u79cd\u624b\u6bb5\u963b\u6b62\u4e2d\u6d77\u6cb9\u7684\u6d77\u5916\u4e0a\u5e02\u8ba1\u5212\uff0c\u5b9e\u9645\u4e0a\u4e5f\u80fd\u8fbe\u5230\u6211\u4eec\u8981\u671f\u5f85\u7684\u6548\u679c\uff0c\u5373\u4f7f\u8fd9\u6761\u5de8\u6761\u6ce8\u5b9a\u8981\u5d1b\u8d77\uff0c\u6211\u4eec\u4e5f\u8981\u5c3d\u53ef\u80fd\u62d6\u5ef6\u5b83\u7684\u5d1b\u8d77\u3002\u201d</p><p>\u3000\u3000\u201c\u9999\u6e2f\u662f\u4e2d\u56fd\u7684\u7279\u522b\u884c\u653f\u533a\uff0c\u4e2d\u6d77\u6cb9\u8fd9\u6b21\u662f\u8981\u5728\u9999\u6e2f\u8054\u5408\u8bc1\u5238\u4ea4\u6613\u6240\u4e0a\u5e02\u878d\u8d44\uff0c\u6211\u4eec\u8be5\u5982\u4f55\u65bd\u52a0\u5f71\u54cd\uff0c\u201d\u6c60\u4f50\u79c0\u85cf\u4e0d\u60f3\u8ddf\u5bab\u672c\u96c4\u4e09\u8ba8\u8bba\u4ec0\u4e48\u5927\u9053\u7406\uff0c\u76f4\u63a5\u95ee\u4e00\u4e9b\u66f4\u52a1\u5b9e\u7684\u95ee\u9898\uff0c\u201c\u6253\u538b\u539f\u6cb9\u671f\u8d27\uff0c\u6216\u8005\u8ba9\u7ecf\u6d4e\u7814\u7a76\u6240\u53d1\u5e03\u5bf9\u4e2d\u6d77\u6cb9\u4e0d\u5229\u7684\u5206\u6790\u62a5\u544a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u662f\u6211\u4eec\u80fd\u505a\u7684\u4e00\u4e9b\u4e8b\u60c5\u2014\u2014\u201d\u5bab\u672c\u96c4\u4e09\u8bf4\u9053\uff0c\u201c\u9999\u6e2f\u4f5c\u4e3a\u4e9a\u6d32\u91d1\u878d\u4e2d\u5fc3\uff0c\u66f4\u51c6\u5907\u7684\u610f\u4e49\u4e0a\u6765\u8bf4\uff0c\u662f\u5168\u7403\u534e\u5546\u7279\u522b\u662f\u4e1c\u5357\u4e9a\u6570\u5343\u4e07\u534e\u5546\u7684\u91d1\u878d\u7ecf\u6d4e\u4e2d\u5fc3\uff0c\u534e\u5546\u8fd9\u4e00\u7fa4\u4f53\u5728\u4e1c\u5357\u4e9a\u56fd\u5bb6\u5206\u5e03\u6700\u5e7f\u3002\u4e00\u822c\u8bf4\u6765\uff0c\u4e1c\u5357\u4e9a\u56fd\u5bb6\u5bf9\u6d77\u5916\u8d44\u672c\u5e02\u573a\u7684\u5f71\u54cd\u529b\u5f88\u5c0f\uff0c\u4f46\u51fa\u4e8e\u4e0a\u9762\u6211\u6240\u8bf4\u7684\u539f\u56e0\uff0c\u4e1c\u5357\u4e9a\u56fd\u5bb6\u5bf9\u9999\u6e2f\u8d44\u672c\u5e02\u573a\u7684\u5f71\u54cd\u5374\u4e0d\u5c0f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u77e5\u9053\u4e86\uff0c\u771f\u662f\u4e0d\u9519\u7684\u4e3b\u610f\uff0c\u201d\u6c60\u4f50\u79c0\u85cf\u70b9\u70b9\u5934\uff0c\u53c8\u7b11\u7740\u8bf4\uff0c\u201c\u4e0d\u8fc7\u60f3\u963b\u6b62\u4e2d\u6d77\u6cb9\u6d77\u5916\u4e0a\u5e02\u7684\uff0c\u4e5f\u4e0d\u53ea\u8fd9\u51e0\u5bb6\u2026\u2026\u53ea\u662f\u6709\u4e9b\u4eba\u4e0d\u65b9\u9762\u8df3\u51fa\u6765\u6380\u98ce\u4f5c\u6d6a\u7f62\u4e86\uff0c\u6211\u4eec\u5012\u662f\u8981\u5e2e\u7740\u63a8\u4e00\u628a\u3002\u201d</p><p>\u3000\u3000\u4e2d\u56fd\u4e0d\u7981\u4e0e\u65e5\u672c\u5728\u4e1c\u6d77\u6d77\u57df\u5212\u754c\u4e0a\u5b58\u5728\u4e89\u7aef\uff0c\u4e0e\u4e1c\u5357\u4e9a\u7684\u83f2\u5f8b\u5bbe\u3001\u5370\u5c3c\u3001\u9a6c\u6765\u897f\u4e9a\u3001\u8d8a\u5357\u7b49\u56fd\u5728\u5357\u6d77\u6d77\u57df\u5212\u754c\u4e5f\u5b58\u5728\u6fc0\u70c8\u7684\u77db\u76fe\u3002\u6d77\u57df\u4e89\u7aef\uff0c\u8bf4\u5230\u5e95\u4e5f\u662f\u6d77\u6d0b\u7ecf\u6d4e\u8d44\u6e90\u5f52\u5c5e\u7684\u4e89\u7aef\uff0c\u83f2\u5f8b\u5bbe\u3001\u5370\u5c3c\u3001\u9a6c\u6765\u897f\u4e9a\u3001\u8d8a\u5357\u7b49\u56fd\u81ea\u7136\u4e5f\u4e0d\u4f1a\u770b\u5230\u4e2d\u56fd\u77f3\u6cb9\u5de5\u4e1a\u5feb\u901f\u7684\u6d77\u6d0b\u5ef6\u4f38\uff0c\u963b\u6b62\u4e2d\u6d77\u6cb9\u6d77\u5916\u4e0a\u5e02\u662f\u7b26\u5408\u5171\u540c\u5229\u76ca\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4f9d\u7167\u60ef\u4f8b\uff0c\u53c2\u4e0e\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u80a1\u7968\u53d1\u884c\u4e0e\u6295\u8d44\u7684\u56fd\u9645\u6295\u8d44\u94f6\u884c\u5bf9\u5373\u5c06\u5728\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u4e0a\u5e02\u878d\u8d44\u7684\u516c\u53f8\u53d1\u5e03\u5206\u6790\u62a5\u544a\u4f9b\u6295\u8d44\u8005\u53c2\u8003\uff0c\u8fd9\u5c06\u4f5c\u4e3a\u6295\u8d44\u8005\u662f\u5426\u53c2\u4e0e\u80a1\u7968\u53d1\u884c\u7684\u91cd\u8981\u53c2\u8003\u3002</p><p>\u3000\u30005\u6708\u4e0a\u4e2d\u65ec\uff0c\u9999\u6e2f\u8bc1\u5238\u5e02\u573a\u5c31\u9646\u7eed\u6709\u591a\u5bb6\u6295\u8d44\u53d1\u5e03\u5bf9\u4e2d\u6d77\u6cb9\u4e0d\u5229\u7684\u62a5\u544a\uff0c\u5728\u4e1c\u5357\u4e9a\u5404\u56fd\u751a\u81f3\u5305\u62ec\u65b0\u52a0\u5761\u3001\u9999\u6e2f\u7684\u4e00\u4e9b\u8d22\u7ecf\u5a92\u4f53\u90fd\u6709\u5bf9\u4e2d\u6d77\u6cb9\u8fdb\u884c\u8d1f\u9762\u62a5\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd8\u771f\u662f\u5934\u75bc\u554a\uff01\u201d\u5f20\u606a\u5750\u5728\u7fdf\u4e39\u9752\u7684\u529e\u516c\u5ba4\u91cc\uff0c\u53cc\u624b\u6795\u5728\u8111\u540e\uff0c\u8eab\u5b50\u4ef0\u9760\u5728\u6c99\u53d1\u4e0a\uff0c\u67da\u6728\u77ee\u51e0\u4e0a\u653e\u7740\u9999\u6e2f\u4e24\u5bb6\u5a92\u4f53\u5bf9\u4e2d\u6d77\u6cb9\u7684\u8d1f\u9762\u62a5\u9053\uff0c\u9999\u6e2f\u867d\u7136\u56de\u5f52\u5c06\u8fd1\u4e24\u5e74\u591a\u65f6\u95f4\uff0c\u9999\u6e2f\u5a92\u4f53\u8fd8\u662f\u559c\u6b22\u53d1\u5e03\u5927\u9646\u7684\u8d1f\u9762\u62a5\u9053\uff0c\u867d\u7136\u5982\u6b64\u7684\u8206\u8bba\u76d1\u7763\u529f\u7528\u4f1a\u66f4\u597d\uff0c\u4f46\u662f\u4e8b\u60c5\u4e34\u5230\u81ea\u5df1\u5934\u4e0a\u8fd8\u662f\u89c9\u5f97\u5934\u75bc\uff0c\u518d\u8bf4\u8fd9\u4e9b\u8d1f\u9762\u62a5\u9053\u5e76\u4e0d\u5355\u7eaf\uff0c\u80cc\u540e\u4e5f\u6709\u9ed1\u624b\u5728\u64cd\u7eb5\uff0c\u201c\u770b\u6765\u65b0\u829c\u662f\u53bb\u4e0d\u4e86\uff0c\u201d\u5f20\u606a\u5934\u5411\u540e\u4ef0\u8d77\uff0c\u770b\u7740\u7ad9\u5728\u6c99\u53d1\u540e\u7684\u7fdf\u4e39\u9752\uff0c\u62c9\u624b\u53bb\u63e1\u5979\u6e29\u6696\u6da6\u6ed1\u7684\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u65e9\u77e5\u9053\u4e8b\u60c5\u4e0d\u4f1a\u8fd9\u4e48\u7b80\u5355\u2026\u2026\u4e8b\u60c5\u8fd8\u771f\u5c31\u4e0d\u662f\u8fd9\u4e48\u7b80\u5355\u3002\u201d</p><p>\u3000\u3000\u6839\u636e\u9999\u6e2f\u7684\u6cd5\u6d25\uff0c\u4e2d\u6d77\u6cb9\u4e0e\u4e0a\u5e02\u627f\u9500\u5546\u5728\u4e0a\u5e02\u542c\u8bc1\u524d\u662f\u4e0d\u53ef\u4ee5\u901a\u8fc7\u5a92\u4f53\u516c\u5f00\u53d1\u5e03\u4e0a\u5e02\u4fe1\u606f\u7684\uff0c\u4e2d\u6d77\u6cb9\u7684\u9ad8\u5c42\u867d\u7136\u4e0d\u4f1a\u592a\u8001\u5b9e\uff0c\u53ea\u662f\u5bf9\u8fd9\u7c7b\u60c5\u51b5\u7f3a\u4e4f\u5904\u7f6e\u7ecf\u9a8c\uff0c\u6709\u4e9b\u7ed9\u4e3b\u627f\u9500\u5546\u7275\u7740\u9f3b\u5b50\u8d70\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u4f5c\u4e3a\u8d22\u52a1\u6295\u8d44\u8005\uff0c\u5bf9\u4e2d\u6d77\u6cb9\u9999\u6e2f\u4e0a\u5e02\u4e0b\u4e86\u4efd\u989d\u6700\u5927\u7684\u5f71\u5b50\u8ba2\u5355\uff0c\u800c\u4e14\u4e2d\u6d77\u6cb9\u80fd\u5426\u6210\u529f\u4e0a\u5e02\u4e5f\u5173\u7cfb\u5230\u6c5f\u5357\u7701\u77f3\u6cb9\u70bc\u5316\u4ea7\u4e1a\u7684\u53d1\u5c55\uff0c\u8fd9\u65f6\u5019\u81ea\u7136\u8981\u536f\u8db3\u529b\u6c14\u3002</p><p>\u3000\u3000\u8fc7\u4e24\u5929\uff0c\u56fd\u5bb6\u65c5\u6e38\u5c40\u5728\u65b0\u829c\u8981\u53ec\u5f00\u4e00\u4e2a\u5173\u4e8e\u603b\u7ed3\u63a8\u5e7f\u65b0\u829c\u65c5\u6e38\u4ea7\u4e1a\u89c4\u5212\u4e0e\u53d1\u5c55\u7684\u4f1a\u8bae\uff0c\u5168\u56fd\u6709\u591a\u5bb6\u65c5\u6e38\u57ce\u5e02\u6d3e\u51fa\u4ee3\u8868\u53c2\u52a0\uff0c\u8fd9\u662f\u5bf9\u65b0\u829c\u8fc7\u53bb\u4e24\u5e74\u65f6\u95f4\u91cc\u53d1\u5c55\u65c5\u6e38\u4ea7\u4e1a\u7684\u80af\u5b9a\uff0c\u5f20\u606a\u672c\u6765\u8ba1\u5212\u501f\u5468\u672b\u7684\u65f6\u95f4\u56de\u4e00\u4e0b\u65b0\u829c\uff0c\u540d\u4e49\u4e0a\u662f\u7ed9\u4ed6\u7238\u7238\u5e86\u529f\uff0c\u5176\u5b9e\u4e5f\u662f\u81ea\u5df1\u80fd\u653e\u677e\u4e00\u4e0b\u3002\u8fd9\u65f6\u5019\uff0c\u5373\u4f7f\u56de\u65b0\u829c\u4e5f\u653e\u677e\u4e0d\u4e86\uff0c\u8fd8\u4e0d\u5982\u5b88\u5728\u5efa\u90ba\uff0c\u8981\u6709\u4ec0\u4e48\u60c5\u51b5\uff0c\u8fd8\u8981\u4eb2\u81ea\u5f80\u9999\u6e2f\u98de\u4e00\u8d9f\u3002</p><p>\u3000\u3000\u529e\u516c\u5ba4\u91cc\u6ca1\u6709\u5176\u4ed6\u4eba\uff0c\u53c8\u770b\u7740\u623f\u95f4\u95e8\u7684\u65b9\u5411\uff0c\u7fdf\u4e39\u9752\u4e5f\u60f3\u4e0e\u5f20\u606a\u80fd\u4eb2\u70ed\u4e9b\uff0c\u7ad9\u5728\u6c99\u53d1\u540e\uff0c\u6367\u7740\u5f20\u606a\u80e1\u6e23\u5b50\u5fae\u523a\u7684\u4e0b\u5df4\uff0c\u8ba9\u4ed6\u7684\u540e\u8111\u52fa\u62b5\u7740\u81ea\u5df1\u67d4\u8f6f\u7684\u5c0f\u8179\uff0c\u4f4e\u5934\u770b\u7740\u5f20\u606a\u7684\u773c\u775b\uff0c\u773c\u4e4c\u5b50\u5f88\u4eae\uff0c\u773c\u795e\u6df1\u9083\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u5bf9\u4e2d\u6d77\u6cb9\u7684\u4fe1\u5fc3\u5f88\u5927\u554a\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u5bf9\u4e2d\u6d77\u6cb9\u6709\u4fe1\u5fc3\uff0c\u201d\u5f20\u606a\u6de1\u6de1\u4e00\u7b11\uff0c\u611f\u89c9\u5230\u5934\u62b5\u7740\u7684\u7fdf\u4e39\u9752\u7684\u5c0f\u8179\u67d4\u8f6f\u800c\u6e29\u6696\uff0c\u201c\u6211\u662f\u5bf9\u4e2d\u56fd\u7684\u77f3\u6cb9\u80fd\u6e90\u4ea7\u4e1a\u5784\u65ad\u653f\u7b56\u4ee5\u53ca\u4e2d\u56fd\u672a\u6765\u5341\u5e74\u7684\u7ecf\u6d4e\u589e\u957f\u6709\u4fe1\u5fc3\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u549a\u549a\u549a\u2026\u2026\u201d\u5d14\u56fd\u6052\u5728\u529e\u516c\u5ba4\u5916\u6572\u4e86\u6572\u95e8\uff0c\u95e8\u6ca1\u6709\u5173\u4e0a\uff0c\u4ed6\u542c\u5230\u5f20\u606a\u5728\u91cc\u9762\u7684\u8bf4\u8bdd\uff0c\u63a8\u5f00\u95e8\u8fdb\u6765\uff0c\u7b11\u7740\u95ee\u9053\uff0c\u201c\u6211\u8fd8\u60f3\u8bf7\u6559\u4e00\u4e0b\uff1a\u606a\u5c11\u4e3a\u4ec0\u4e48\u5bf9\u77f3\u6cb9\u80fd\u6e90\u4ea7\u4e1a\u5784\u65ad\u653f\u7b56\u6709\u5982\u6b64\u5f3a\u70c8\u7684\u4fe1\u5fc3\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u65c1\u82e5\u65e0\u4e8b\u7684\u671d\u65c1\u8fb9\u79fb\u4e86\u79fb\uff0c\u597d\u50cf\u5c31\u4e0e\u5f20\u606a\u4ee5\u5e73\u5e38\u7684\u59ff\u6001\u5728\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u63e1\u4e0d\u5230\u7fdf\u4e39\u9752\u6e29\u6696\u67d4\u8f6f\u7684\u5c0f\u624b\uff0c\u5f20\u606a\u53ea\u80fd\u5c06\u624b\u653e\u5728\u6c99\u53d1\u4e0a\u7684\u6276\u624b\uff0c\u8bf7\u5d14\u56fd\u6052\u5750\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4ece\u6cd5\u7406\u4e0a\uff0c\u56fd\u5bb6\u662f\u8981\u5c06\u4ea7\u4e1a\u7ecf\u6d4e\u547d\u8109\u76f4\u63a5\u638c\u63e1\u5728\u624b\u91cc\u7684\uff0c\u8fd9\u662f\u56fd\u6709\u4f01\u4e1a\u5728\u57fa\u7840\u4ea7\u4e1a\u9886\u57df\u8fdb\u884c\u5784\u65ad\u7ecf\u8425\u5929\u7136\u4f18\u52bf\u2014\u2014\u8fd9\u65b9\u9762\u6682\u4e14\u4e0d\u8bf4\uff0c\u8fd8\u6709\u597d\u4e9b\u4eba\u8ba4\u4e3a\u8fd9\u4e9b\u90fd\u662f\u9700\u8981\u88ab\u6539\u9769\u6389\u7684\u4e1c\u897f\u3002\u5355\u7eaf\u7684\u4ece\u56fd\u5bb6\u5229\u76ca\u8003\u8651\uff0c\u5784\u65ad\uff0c\u7279\u522b\u662f\u884c\u653f\u76f4\u63a5\u5e72\u9884\u7684\u5784\u65ad\u5728\u77f3\u6cb9\u80fd\u6e90\u5de5\u4e1a\u9886\u57df\u5176\u5b9e\u662f\u67c4\u53cc\u5203\u5251\uff0c\u6211\u4eec\u770b\u5230\u5784\u65ad\u4f01\u4e1a\u5bb9\u6613\u6ecb\u751f\u8d2a\u6c61\u8150\u5316\u7684\u5b98\u50da\u4f5c\u98ce\uff0c\u9020\u6210\u751f\u4ea7\u6548\u7387\u4f4e\u4e0b\uff0c\u5bf9\u5e02\u573a\u4e0d\u77e5\u5ec9\u803b\u7684\u63a0\u593a\uff0c\u4f46\u662f\u53e6\u4e00\u65b9\u9762\uff0c\u56fd\u5185\u5bf9\u6d77\u5916\u539f\u6cb9\u7684\u8fdb\u53e3\u4f9d\u8d56\u65e5\u76ca\u589e\u5f3a\uff0c\u800c\u56fd\u9645\u539f\u6cb9\u5e02\u573a\u662f\u77f3\u6cb9\u5de8\u5934\u7684\u5929\u4e0b\uff0c\u8fd9\u4e9b\u77f3\u6cb9\u5de8\u5934\u80cc\u540e\u65e0\u4e00\u4e0d\u4ee3\u8868\u7740\u56fd\u5bb6\u5229\u76ca\u3002\u4e2d\u592e\u9ad8\u5c42\u8981\u662f\u80fd\u4fdd\u6301\u6e05\u9192\uff0c\u4f1a\u77e5\u9053\u5728\u56fd\u9645\u539f\u6cb9\u5e02\u573a\u4e5f\u8981\u6709\u4ee3\u8868\u4e2d\u56fd\u56fd\u5bb6\u5229\u76ca\u7684\u77f3\u6cb9\u5de8\u5934\u53bb\u51b2\u950b\u9677\u9635\u3002\u6b64\u65f6\u7684\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u3001\u4e2d\u6d77\u6cb9\u6240\u8c13\u7684\u56fd\u5185\u4e09\u5de8\u5934\u90fd\u8fd8\u6ca1\u6709\u53bb\u56fd\u9645\u539f\u6cb9\u5e02\u573a\u51b2\u950b\u9677\u9635\u7684\u8fd9\u4e2a\u5b9e\u529b\uff0c\u65f6\u95f4\u53c8\u5f88\u7d27\u8feb\uff0c\u600e\u4e48\u529e\uff1f\u4e2d\u592e\u8d22\u653f\u4e5f\u7d27\u5f20\uff0c\u4e0d\u80fd\u65e0\u9650\u5236\u7684\u5bf9\u5176\u8fdb\u884c\u5de8\u989d\u8d22\u653f\u8865\u8d34\uff0c\u4e00\u65f6\u4e4b\u8ba1\u53ea\u80fd\u54ac\u7740\u7259\u575a\u6301\u77f3\u6cb9\u80fd\u6e90\u7684\u5784\u65ad\u4e13\u8425\uff0c\u8ba9\u8fd9\u4e09\u5bb6\u4f01\u4e1a\u53bb\u5784\u65ad\u5e02\u573a\u91cc\u53bb\u5438\u8840\u2026\u2026\u6bd4\u8f83\u8d77\u77f3\u6cb9\u4ea7\u4e1a\uff0c\u6211\u76f8\u4fe1\u56fd\u5185\u8d44\u6e90\u5145\u8db3\u7684\u7164\u70ad\u4ea7\u4e1a\u6709\u53ef\u80fd\u5bf9\u6c11\u8425\u8d44\u672c\u653e\u5f00\u3002\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u5fae\u5fae\u8f7b\u53f9\uff0c\u5fc3\u91cc\u60f3\u4e0d\u660e\u767d\u5f20\u606a\u6574\u5929\u7684\u8fd8\u5728\u4e1c\u5927\u6821\u91cc\u8017\u7740\u505a\u4ec0\u4e48\uff0c\u6216\u8bb8\u662f\u5728\u4eab\u53d7\u751f\u6d3b\u5427\u3002</p><p>\u3000\u3000\u201c\u867d\u7136\u5df2\u7ecf\u51b3\u5b9a\u5bf9\u4e2d\u6d77\u6cb9\u8fdb\u884c\u8d22\u52a1\u6295\u8d44\uff0c\u4e2d\u5fc3\u8fd9\u8fb9\u51fa\u4e8e\u5c3d\u804c\uff0c\u8fd8\u662f\u8981\u7ed9\u51fa\u4e00\u4efd\u62a5\u544a\u2014\u2014\u4e2d\u6d77\u6cb9\u7684\u89c4\u6a21\u867d\u7136\u6bd4\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u8981\u5c0f\u5f97\u591a\uff0c\u4f46\u662f\u4e2d\u56fd\u6d77\u6d0b\u77f3\u6cb9\u4e13\u8425\u6743\u4e0e\u4e2d\u56fd\u5e02\u573a\u7b2c\u4e09\u5927\u5784\u65ad\u77f3\u6cb9\u5546\u7684\u8eab\u4efd\u5e94\u8be5\u80fd\u4fdd\u8bc1\u8fd9\u6b21\u7684\u8d22\u52a1\u6295\u8d44\u4f1a\u5f97\u5230\u53ef\u9760\u7684\u6536\u76ca\u3002\u201d\u5d14\u56fd\u6052\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u5728\u56fd\u5bb6\u5784\u65ad\u4e3b\u4e49\u7684\u5e87\u62a4\u4e0b\uff0c\u5341\u4e09\u4ebf\u4eba\u53e3\u7684\u5e9e\u5927\u77f3\u6cb9\u6d88\u8d39\u5e02\u573a\u5c31\u7ed9\u4e09\u5bb6\u516c\u53f8\u5206\uff0c\u8981\u4e0d\u80fd\u8d5a\u94b1\u8fd8\u771f\u8981\u4e09\u5bb6\u516c\u53f8\u7684\u9ad8\u5c42\u611a\u8822\u5230\u76f8\u5f53\u7684\u7a0b\u5ea6\u624d\u884c\u2014\u2014\u53e6\u5916\uff0c\u8bb8\u591a\u56fd\u4f01\u5728\u6caa\u6df1\u4e24\u5e02\u559c\u6b22\u641e\u5e55\u540e\u4ea4\u6613\uff0c\u4f46\u5728\u5916\u56fd\u53cb\u4eba\u9762\u524d\u5374\u53c8\u8868\u73b0\u5341\u5206\u7684\u5e72\u51c0\u8ddf\u89c4\u77e9\uff0c\u56fd\u5bb6\u7ecf\u8d38\u59d4\u4e0e\u4e2d\u6d77\u77f3\u6cb9\u5c06\u5218\u6210\u536b\u63a8\u51fa\u6765\u8d1f\u8d23\u4e0a\u5e02\u516c\u53f8\uff0c\u4e5f\u662f\u8981\u6539\u5584\u6d77\u5916\u6295\u8d44\u4eba\u5bf9\u4e2d\u56fd\u77f3\u6cb9\u5de5\u4e1a\u7684\u770b\u6cd5\u3002\u201d</p><p>\u3000\u3000\u201c\u8bda\u7136\uff0c\u4f60\u5bf9\u56fd\u5185\u4ea7\u4e1a\u653f\u7b56\u7684\u7406\u89e3\u4e4b\u6df1\u523b\uff0c\u5c11\u6709\u4eba\u80fd\u53ca\uff0c\u4e2d\u6d77\u6cb9\u8fd9\u6b21\u9999\u6e2f\u4e0a\u5e02\u878d\u8d44\u7684\u6700\u5927\u5356\u70b9\u5c31\u662f\u5bf9\u6d77\u6d0b\u77f3\u6cb9\u80fd\u6e90\u7684\u5784\u65ad\u4e13\u8425\uff0c\u201d\u5d14\u56fd\u6052\u770b\u5230\u67da\u6728\u77ee\u51e0\u4e0a\u653e\u7740\u7684\u9999\u6e2f\u62a5\u520a\u300a\u660e\u62a5\u300b\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u8fc7\u773c\u4e0b\u5a92\u4f53\u4e0e\u4e00\u4e9b\u6295\u884c\u53d1\u5e03\u7684\u8d1f\u9762\u62a5\u9053\u4e0e\u62a5\u544a\u5374\u96c6\u4e2d\u706b\u529b\u653b\u51fb\u8fd9\u70b9\uff0c\u8fd9\u70b9\u6709\u4e9b\u5934\u75bc\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u627f\u8ba4\u4ed6\u5bf9\u6b64\u4e5f\u5f88\u5934\u75bc\u3002</p><p>\u3000\u3000\u5a92\u4f53\u7684\u8d1f\u9762\u62a5\u9053\u4e5f\u4e0d\u662f\u8981\u8bf4\u4e2d\u56fd\u653f\u5e9c\u5bf9\u77f3\u6cb9\u4ea7\u4e1a\u7684\u5784\u65ad\u4e13\u8425\u653f\u7b56\u7f6a\u5927\u6076\u6781\uff0c\u4e2d\u592e\u4e5d\u4e5d\u5e74\u521d\u6b63\u5f0f\u7684\u653e\u5f00\u6c11\u8425\u8d44\u672c\u3001\u5916\u8d44\u8fdb\u5165\u6210\u54c1\u6cb9\u9500\u552e\u7684\u9650\u5236\uff0c\u50cf\u5e7f\u5dde\u7b49\u6700\u65e9\u8bd5\u70b9\u7684\u5730\u65b9\uff0c\u6e2f\u8d44\u4e0e\u6c11\u8425\u7684\u52a0\u6cb9\u7ad9\u89c4\u6a21\u5df2\u7ecf\u8d85\u8fc7\u63a7\u5236\u5357\u65b9\u5e02\u573a\u7684\u4e2d\u77f3\u5316\uff0c\u8fd9\u4e9b\u5a92\u4f53\u4e0e\u6295\u884c\u90fd\u5927\u80c6\u9884\u6d4b\u4e2d\u56fd\u653f\u5e9c\u4f1a\u8fdb\u4e00\u6b65\u7684\u653e\u5f00\u5bf9\u539f\u6cb9\u5f00\u91c7\u4e0e\u70bc\u5236\u4ea7\u4e1a\u9886\u57df\u7684\u9650\u5236\u2014\u2014\u8fd9\u5c06\u5bf9\u4e2d\u6d77\u6cb9\u6700\u91cd\u8981\u7684\u4e00\u9879\u65e0\u5f62\u8d44\u4ea7\u5373\u5bf9\u6d77\u6d0b\u77f3\u6cb9\u7684\u5784\u65ad\u4e13\u8425\u6743\u9020\u6210\u7834\u574f\u6027\u7684\u51b2\u51fb\u3002</p><p>\u3000\u3000\u4e2d\u6d77\u6cb9\u8fd8\u662f\u7f3a\u4e4f\u7ecf\u9a8c\uff0c\u4e00\u65b9\u9762\uff0c\u4e2d\u6d77\u6cb9\u62fc\u547d\u8ddf\u6295\u8d44\u8005\u89e3\u91ca\u5bf9\u6d77\u6d0b\u77f3\u6cb9\u80fd\u6e90\u7684\u5784\u65ad\u4e13\u8425\u662f\u4e0a\u5e02\u516c\u53f8\u6700\u5927\u7684\u8d22\u5bcc\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u5a92\u4f53\u4e0e\u6295\u884c\u5374\u5224\u65ad\u8bf4\u4e2d\u6d77\u6cb9\u5bf9\u6d77\u6d0b\u77f3\u6cb9\u80fd\u6e90\u7684\u5784\u65ad\u4e13\u8425\u6743\u4f1a\u53d7\u5230\u51b2\u51fb\uff0c\u672a\u6765\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u7684\u77f3\u6cb9\u80fd\u6e90\u5784\u65ad\u4e13\u8425\u6743\u4e5f\u4f1a\u6536\u5230\u51b2\u51fb\u2014\u2014\u4e5d\u4e5d\u5e74\uff0c\u521a\u521a\u8fdb\u5165\u6210\u54c1\u6cb9\u9500\u552e\u9886\u57df\u7684\u6c11\u8425\u8d44\u672c\u90fd\u76f8\u4fe1\u56fd\u5bb6\u4f1a\u9646\u7eed\u653e\u5f00\u5bf9\u77f3\u6cb9\u4ea7\u4e1a\u7684\u9650\u5236\uff0c\u56fd\u5185\u5a92\u4f53\u4e5f\u5728\u717d\u98ce\u70b9\u706b\u7684\u8fdb\u884c\u8fd9\u65b9\u9762\u7684\u62a5\u9053\uff0c\u4e2d\u6d77\u6cb9\u5374\u4e00\u5473\u7684\u5f3a\u8c03\u5784\u65ad\u4e13\u8425\u6743\uff0c\u5728\u6295\u8d44\u4eba\u770b\u6765\u6709\u4e9b\u8272\u5389\u5185\u834f\u7684\u611f\u89c9\uff0c\u7591\u8651\u66f4\u6df1\u3002</p><p>\u3000\u3000\u53e6\u5916\uff0c\u5c31\u7b97\u539f\u6cb9\u5f00\u91c7\u4e0e\u70bc\u5316\u9886\u57df\uff0c\u4e5f\u4e0d\u662f\u5c31\u4e00\u70b9\u53e3\u5b50\u90fd\u6ca1\u6709\u6253\u5f00\u3002</p><p>\u3000\u3000\u524d\u5929\u7684\u9999\u6e2f\u660e\u62a5\u5c31\u4ee5\u6574\u7248\u7bc7\u5e45\u62a5\u9053\u4e86\u897f\u5b81\u5730\u533a\u6c11\u8425\u8d44\u672c\u8fdb\u5165\u539f\u6cb9\u5f00\u91c7\u3001\u70bc\u6cb9\u4ea7\u4e1a\u7684\u72b6\u51b5\u3002</p><p>\u3000\u3000\u65e9\u5728\u4e5d\u56db\u5e74\uff0c\u4e2d\u56fd\u77f3\u6cb9\u5929\u71c3\u6c14\u603b\u516c\u53f8\uff08\u4e2d\u77f3\u6cb9\uff09\u56e0\u4e3a\u897f\u5b81\u5730\u533a\u539f\u6cb9\u5f00\u91c7\u6548\u7387\u6781\u4f4e\uff08\u8d5a\u4e0d\u5230\u94b1\uff09\u7684\u539f\u56e0\uff0c\u4e0e\u897f\u5b81\u653f\u5e9c\u7b7e\u8ba2\u5730\u533a\u8d44\u6e90\u5f00\u53d1\u534f\u8bae\uff0c\u5c06\u4e00\u4e9b\u6cb9\u7530\u4ea4\u7ed9\u5e02\u53bf\u5730\u65b9\u653f\u5e9c\u7ec4\u7ec7\u5f00\u53d1\uff0c\u5730\u65b9\u653f\u5e9c\u7531\u4e8e\u7f3a\u4e4f\u8d44\u91d1\u4e0e\u6280\u672f\uff0c\u7eb7\u7eb7\u91c7\u7528\u62db\u5546\u5f15\u8d44\u3001\u51fa\u8ba9\u4e95\u4f4d\u7684\u65b9\u5f0f\uff0c\u5f15\u8fdb\u8054\u8425\u5355\u4f4d\u53c2\u4e0e\u77f3\u6cb9\u5f00\u53d1\uff0c\u5176\u4e2d\u4e5f\u5305\u62ec\u6c11\u8425\u8d44\u672c\uff0c\u5bfc\u81f4\u897f\u5b81\u5730\u533a\u524d\u5148\u6d8c\u5411\u51fa\u4e00\u5343\u591a\u5bb6\u6c11\u8425\u91c7\u6cb9\u53ca\u70bc\u6cb9\u4f01\u4e1a\uff08\u6c11\u8425\u91c7\u6cb9\u5012\u662f\u5f88\u8d5a\u94b1\u3001\u73af\u5883\u7834\u574f\u4e5f\u5927\uff09\uff0c\u6b64\u65f6\u5df2\u7ecf\u6210\u4e3a\u897f\u5b81\u5730\u533a\u7684\u652f\u67f1\u4ea7\u4e1a\u3002</p><p>\u3000\u3000\u867d\u7136\u5728\u56fd\u5185\u77f3\u6cb9\u5f00\u91c7\u9886\u57df\uff0c\u8fd9\u53ea\u662f\u5f88\u5fae\u5c0f\u7684\u4e00\u90e8\u5206\uff0c\u4e5f\u6ca1\u6709\u5f97\u5230\u56fd\u52a1\u9662\u7684\u6b63\u5f0f\u627f\u8ba4\uff0c\u4f46\u662f\u4e5f\u88ab\u8ba4\u4e3a\u5728\u539f\u6cb9\u5f00\u91c7\u4ee5\u53ca\u6210\u54c1\u6cb9\u70bc\u5316\u4e0a\u6253\u5f00\u4e86\u4e00\u5c0f\u9053\u53e3\u5b50\uff0c\u4e5d\u4e5d\u5e74\u5e74\u521d\u4e2d\u592e\u53c8\u6b63\u5f0f\u653e\u5f00\u5bf9\u6c11\u8425\u8d44\u672c\u8fdb\u5165\u6210\u54c1\u6cb9\u9500\u552e\u7684\u9650\u5236\uff0c\u8bb8\u591a\u4eba\u90fd\u7406\u6240\u5f53\u7136\u7684\u8ba4\u4e3a\u5728\u539f\u6cb9\u5f00\u91c7\u53ca\u6210\u54c1\u6cb9\u70bc\u5316\u4e0a\u7684\u7ba1\u5236\u4f1a\u8fdb\u4e00\u6b65\u7684\u653e\u5f00\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u653f\u7b56\u9884\u671f\u5bf9\u4e2d\u6d77\u6cb9\u7684\u6d77\u5916\u4e0a\u5e02\u662f\u6781\u4e3a\u4e0d\u5229\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u8bb0\u5fc6\u91cc\u5bf9\u4e2d\u6d77\u6cb9\u7684\u5370\u8c61\u662f\u6a21\u7cca\u7684\uff0c\u53ea\u8bb0\u5f97\u4e2d\u6d77\u6cb9\u9996\u6b21\u9999\u6e2f\u4e0a\u5e02\u878d\u8d44\u5c31\u906d\u5230\u91cd\u632b\uff0c\u5bf9\u7ec6\u8282\u4e0d\u662f\u5f88\u4e86\u89e3\uff0c\u4f46\u662f\u4ed6\u8fd9\u6bb5\u65f6\u95f4\u901a\u8fc7\u5bf9\u56fd\u5185\u77f3\u6cb9\u4ea7\u4e1a\u8fdb\u884c\u7ec6\u81f4\u800c\u6df1\u5165\u7684\u8c03\u67e5\u4e0e\u7814\u7a76\uff0c\u518d\u4e0e\u6a21\u7cca\u7684\u5370\u8c61\u60f3\u6bd4\u5bf9\uff0c\u524d\u5c18\u5f80\u4e16\u91cc\u53d1\u751f\u8fc7\u3001\u4f46\u4e0d\u5b58\u5728\u4ed6\u8bb0\u5fc6\u91cc\u7684\u5185\u5bb9\uff0c\u4e5f\u5c31\u4e0d\u96be\u63a8\u6f14\u51fa\u6765\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u662f\u80cc\u540e\u6709\u9ed1\u624b\u5728\u63a8\u52a8\u4e5f\u597d\uff0c\u8fd8\u662f\u771f\u5b9e\u7684\u5a92\u4f53\u8206\u8bba\u5c31\u662f\u5982\u6b64\uff0c\u5c31\u662f\u8fd9\u79cd\u653f\u7b56\u9884\u671f\u52a0\u4e0a\u5f53\u65f6\u9999\u6e2f\u8d44\u672c\u5e02\u573a\u5143\u6c14\u672a\u590d\u4e0e\u539f\u6cb9\u4ef7\u683c\u5927\u5e45\u4e0b\u964d\u7b49\u591a\u79cd\u56e0\u7d20\uff0c\u6700\u7ec8\u5bfc\u81f4\u4e2d\u6d77\u6cb9\u9996\u6b21\u6d77\u5916\u4e0a\u5e02\u8ba1\u5212\u592d\u6298\u3002\u6070\u6070\u662f\u4e2d\u6d77\u6cb9\u7684\u6d77\u5916\u4e0a\u5e02\u8ba1\u5212\u5931\u5229\u76f4\u63a5\u4fc3\u4f7f\u4e2d\u592e\u505a\u51fa\u575a\u51b3\u53d6\u7f14\u897f\u5b81\u5730\u533a\u7684\u6c11\u8425\u91c7\u6cb9\u4e1a\u7684\u51b3\u5b9a\uff0c\u5835\u4e0a\u6c11\u8d44\u8fdb\u5165\u77f3\u6cb9\u5f00\u91c7\u9886\u57df\u7684\u53e3\u5b50\uff0c\u9020\u6210\u5f53\u65f6\u8f70\u52a8\u4e00\u65f6\u7684\u897f\u5b81\u77f3\u6cb9\u7fa4\u4f53\u4e8b\u4ef6\u2014\u2014\u4e2d\u592e\u7684\u77f3\u6cb9\u5784\u65ad\u653f\u7b56\u51b3\u5fc3\u5e2e\u52a9\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u7684\u6d77\u5916\u4e0a\u5e02\u5316\u89e3\u6700\u5927\u7684\u963b\u529b\u3002\u53ea\u53ef\u60dc\uff0c\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u8fd9\u4e24\u5bb6\u516c\u53f8\u5f88\u6ca1\u6709\u9aa8\u6c14\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u662f\u7ed9\u4e2d\u6d77\u6cb9\u7684\u5931\u8d25\u6781\u5927\u7684\u523a\u6fc0\u5230\u4e86\uff0c\u4e2d\u6d77\u6cb9\u4e5d\u4e5d\u5e74\u9996\u5ea6\u6d77\u5916\u4e0a\u5e02\u5c06\u5e02\u76c8\u7387\u5b9a\u57289\u500d\u5de6\u53f3\uff0c\u4e2d\u77f3\u6cb9\u96f6\u96f6\u5e74\u4e3a\u4fdd\u8bc1\u6d77\u5916\u4e0a\u5e02\u6210\u529f\uff0c\u5e02\u76c8\u7387\u4e00\u4e0b\u5b50\u964d\u6389\u4e00\u534a\uff084.6\u500d\uff09\u3002\u4ee5\u4e2d\u77f3\u6cb9\u540e\u6765\u7684\u76c8\u5229\u80fd\u529b\uff0c\u96f6\u96f6\u5e74\u7684\u6d77\u5916\u4e0a\u5e02\u51e0\u4e4e\u5c31\u662f\u5728\u5f80\u6d77\u5916\u6295\u8d44\u8005\u5634\u91cc\u585e\u80a5\u8089\uff0c\u5305\u62ec\u4ee5\u540e\u7684\u56fd\u5185\u91d1\u878d\u8d44\u4ea7\u6d77\u5916\u4e0a\u5e02\uff0c\u90fd\u72af\u4e86\u8fd9\u4e2a\u4e25\u91cd\u7684\u8f6f\u9aa8\u75c5\u3002</p><p>\u3000\u3000\u4e2d\u6d77\u6cb9\u9996\u6b21\u4e0a\u5e02\u5931\u8d25\u7684\u5f71\u54cd\u5982\u6b64\u4e4b\u5927\uff0c\u5f20\u606a\u80fd\u770b\u5f97\u6e05\u695a\uff0c\u5374\u662f\u5176\u4ed6\u4eba\u65e0\u6cd5\u9884\u6d4b\u7684\uff0c\u81f3\u5c11\u5728\u7ecf\u7814\u4e2d\u5fc3\u63d0\u524d\u7684\u62a5\u544a\u91cc\u4e5f\u53ea\u5bf9\u4e2d\u56fd\u6d77\u6d0b\u77f3\u6cb9\u5de5\u4e1a\u9020\u6210\u7684\u8d1f\u9762\u5f71\u54cd\u8fdb\u884c\u4e86\u5206\u6790\u3002\u4e00\u65e6\u4e0a\u5e02\u5931\u8d25\uff0c\u5206\u754c\u6cb9\u6c14\u7530\u7684\u5f00\u53d1\u6ce8\u5b9a\u8981\u62d6\u5ef6\u4e0b\u53bb\uff0c\u4e2d\u56fd\u6d77\u6d0b\u77f3\u6cb9\u5de5\u4e1a\u7684\u53d1\u5c55\u81f3\u5c11\u88ab\u5ef6\u7f13\u4e24\u5230\u4e09\u5e74\uff0c\u5bf9\u6c5f\u5357\u7701\u7684\u7ecf\u6d4e\u53d1\u5c55\u4e5f\u6709\u4e0d\u5229\uff0c\u6700\u5927\u7684\u4e0d\u5229\u5c31\u662f\u4e4b\u540e\u6d77\u5916\u4e0a\u5e02\u7684\u56fd\u4f01\u4eec\u90fd\u7eb7\u7eb7\u7684\u4ece\u4e2d\u6d77\u6cb9\u9996\u6b21\u4e0a\u5e02\u5931\u8d25\u91cc\u5438\u53d6\u4e86\u9519\u8bef\u7684\u6559\u8bad\uff0c\u4e4b\u540e\u56fd\u4f01\u6d77\u5916\u4e0a\u5e02\u878d\u8d44\u4e3b\u8981\u4f9d\u636e\u51c0\u8d44\u4ea7\uff0c\u800c\u4e0d\u518d\u6839\u636e\u56fd\u9645\u60ef\u4f8b\u4f9d\u9760\u5e02\u76c8\u7387\u6765\u8ba1\u7b97\u80a1\u4ef7\uff0c\u8fd9\u91cc\u9762\u635f\u5931\u7684\u56fd\u5bb6\u5229\u76ca\u5df2\u7ecf\u5f88\u96be\u7528\u5177\u4f53\u591a\u5c11\u4ebf\u7684\u6570\u5b57\u6765\u8ba1\u7b97\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u4e86\u60f3\uff0c\u95ee\u7fdf\u4e39\u9752\uff1a\u201c\u6211\u4eec\u6709\u6ca1\u6709\u5fc5\u8981\u53bb\u4e00\u8d9f\u9999\u6e2f\uff0c\u6211\u60f3\u4e0e\u5218\u6210\u536b\u3001\u53f6\u54e5\u8fd8\u6709\u5b59\u5148\u751f\u8c08\u4e00\u6b21\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u6211\u966a\u4f60\u53bb\u4e00\u8d9f\u9999\u6e2f\u3002\u201d\u5979\u8fd8\u662f\u6709\u4e9b\u6015\u4e0e\u5510\u5a67\u89c1\u9762\uff0c\u603b\u662f\u505a\u8d3c\u5fc3\u865a\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ee\u5d14\u56fd\u6052\uff1a\u201c\u5d14\u9662\u957f\u6709\u6ca1\u6709\u65f6\u95f4\u8d70\u4e00\u8d9f\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u53bb\u9999\u6e2f\u7684\u4f5c\u7528\u4e0d\u5927\uff0c\u4e0d\u5982\u7559\u5728\u5efa\u90ba\u5199\u4e24\u7bc7\u6587\u7ae0\u3002\u201d\u5d14\u56fd\u6052\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u65f6\u5019\u4e3a\u56fd\u5bb6\u5784\u65ad\u8fa9\u62a4\uff0c\u53ef\u662f\u8981\u6328\u7816\u5934\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u5728\u60f3\u4e2d\u592e\u6b64\u65f6\u4e5f\u8bb8\u6709\u4e9b\u52a8\u6447\uff0c\u201d\u5d14\u56fd\u6052\u6c89\u541f\u7684\u8bf4\u9053\uff0c\u8f6c\u800c\u53c8\u81ea\u5632\u7684\u7b11\u9053\uff0c\u201c\u80fd\u4e0e\u4e2d\u77f3\u6cb9\u3001\u4e2d\u77f3\u5316\u3001\u4e2d\u6d77\u6cb9\u8fd9\u4e9b\u5de8\u5934\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\uff0c\u6328\u4e9b\u7816\u5934\u4e5f\u7b97\u4e0d\u4e86\u4ec0\u4e48\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u60f3\u60f3\u4e5f\u5bf9\uff0c\u9996\u5148\u8981\u5c06\u56fd\u5185\u8206\u8bba\u7ed9\u6273\u8fc7\u6765\uff0c\u4e0d\u80fd\u81ea\u5df1\u7ed9\u81ea\u5df1\u62c6\u53f0\uff0c\u4e5f\u9700\u8981\u4e2d\u592e\u5728\u77f3\u6cb9\u80fd\u6e90\u4ea7\u4e1a\u7684\u5784\u65ad\u653f\u7b56\u4e0a\u518d\u4e00\u6b21\u8868\u660e\u7acb\u573a\uff0c\u6700\u5927\u9650\u5ea6\u7684\u5316\u89e3\u6d77\u5916\u6295\u8d44\u8005\u7684\u7591\u60d1\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u79bb\u5f00\u529e\u516c\u5ba4\uff0c\u7fdf\u4e39\u9752\u8d70\u8fc7\u53bb\u5c06\u529e\u516c\u5ba4\u95e8\u5173\u4e0a\uff0c\u5750\u5230\u6c99\u53d1\u6276\u624b\u4e0a\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u662f\u4e2d\u6d77\u6cb9\u5728\u6d77\u5916\u4e0a\u5e02\uff0c\u4e3b\u627f\u9500\u5546\u4e5f\u662f\u522b\u4eba\uff0c\u7ed3\u679c\u5012\u662f\u6211\u4eec\u9526\u6e56\u5728\u80cc\u540e\u4f7f\u6700\u5927\u7684\u529b\u6c14\u3002\u201d</p><p>\u3000\u3000\u82e5\u662f\u53ea\u4e3a\u8ffd\u6c42\u5229\u76ca\u6700\u5927\u5316\uff0c\u9526\u6e56\u5e94\u8be5\u75db\u6253\u843d\u6c34\u72d7\uff0c\u4f7f\u4e2d\u6d77\u6cb9\u7684\u6d77\u5916\u4e0a\u5e02\u8ba1\u5212\u53d7\u5230\u66f4\u5927\u7684\u632b\u6298\uff0c\u5e03\u4e0b\u4e00\u4e2a\u5927\u5c40\uff0c\u7136\u800c\u53c2\u4e0e\u660e\u5e74\u4e2d\u77f3\u6cb9\u7684\u6d77\u5916\u8bc1\u5238\u5e02\u573a\u80a1\u7968\u53d1\u884c\u8ba1\u5212\uff0c\u6536\u76ca\u4f1a\u6709\u6570\u500d\u7684\u589e\u6da8\u2014\u2014\u53ea\u662f\u4e0d\u80fd\u770b\u5230\u4e2d\u56fd\u5f3a\u5927\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u8d44\u4ea7\u518d\u591a\uff0c\u53c8\u6709\u4f55\u76ca\uff1f\u4e5f\u7f3a\u4e4f\u8db3\u591f\u7684\u4fdd\u969c\u4e0e\u8fdb\u4e00\u6b65\u53d1\u5c55\u7684\u6f5c\u529b\u3002</p></article>"}}}]);
