(window.webpackJsonp=window.webpackJsonp||[]).push([[1026],{1049:function(p){p.exports={chapterTitle:null,title:"\u7b2c1023\u7ae0 \u8ba9\u6211\u7559\u5728\u73c0\u65af\u5427",content:"<article><h2>\u7b2c1023\u7ae0 \u8ba9\u6211\u7559\u5728\u73c0\u65af\u5427</h2><p>\u3000\u3000\u5bf9\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u53ef\u80fd\u5728\u5bf9\u65b0\u6c82\u6d77\u57df\u77f3\u6cb9\u8d44\u6e90\u5408\u4f5c\u52d8\u63a2\u4e2d\u6570\u636e\u4f5c\u5047\u7684\u731c\u7591\u4f1a\u76f4\u63a5\u7834\u574f\u4e24\u5bb6\u516c\u53f8\u5728\u6d77\u6d0b\u77f3\u6cb9\u52d8\u63a2\u5f00\u91c7\u9886\u57df\u7684\u5408\u4f5c\u57fa\u7840\uff0c\u4e5f\u5c06\u5bf9\u4e2d\u7f8e\u4e24\u56fd\u5728\u6d77\u6d0b\u77f3\u6cb9\u80fd\u6e90\u5f00\u53d1\u9886\u57df\u7684\u5408\u4f5c\u9020\u6210\u5f3a\u70c8\u7684\u51b2\u51fb\uff0c\u5982\u6b64\u91cd\u5927\u7684\u4e8b\u9879\u662f\u5218\u6210\u536b\u4e0e\u4e2d\u6d77\u77f3\u6cb9\u515a\u7ec4\u6210\u5458\u90fd\u65e0\u6cd5\u505a\u51fa\u7684\u91cd\u8981\u51b3\u7b56\u3002</p><p>\u3000\u3000\u5218\u6210\u536b\u4e0e\u5f20\u606a\u5728\u9999\u6e2f\u534a\u5c9b\u9152\u5e97\u91cc\u5206\u6790\u4e8b\u4ef6\u5e55\u540e\u7684\u7591\u70b9\uff0c\u5176\u4ed6\u4e2d\u6d77\u77f3\u6cb9\u515a\u7ec4\u6210\u5458\u4e5f\u5f7b\u5e95\u672a\u7720\uff0c\u65e2\u4e0d\u80fd\u60ca\u52a8\u4e2d\u6d77\u77f3\u6cb9\u65b9\u9762\u76f4\u63a5\u8d1f\u8d23\u65b0\u6c82\u6cb9\u7530\u5408\u4f5c\u9879\u76ee\u7684\u8d1f\u8d23\u4eba\uff0c\u5728\u638c\u63e1\u521d\u6b65\u7684\u8bc1\u636e\u4e4b\u524d\uff0c\u66f4\u4e0d\u80fd\u60ca\u52a8\u5230\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u65b9\uff0c\u8fd8\u8981\u5c3d\u53ef\u80fd\u7684\u641c\u96c6\u6750\u6599\uff0c\u8fd9\u4ef6\u4e8b\u60c5\u5fc5\u987b\u8981\u5411\u5206\u7ba1\u4e2d\u6d77\u77f3\u6cb9\u7684\u56fd\u5bb6\u7ecf\u8d38\u59d4\u4e3b\u4efb\u4ee5\u53ca\u5206\u7ba1\u4e2d\u7f8e\u80fd\u6e90\u5408\u4f5c\u7684\u526f\u603b\u7406\u9ec4\u708e\u6e05\u6c47\u62a5\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\u4e0a\u5348\uff0c\u526f\u603b\u7406\u9ec4\u708e\u6e05\u5c31\u5bf9\u6b64\u4e8b\u505a\u51fa\u6279\u793a\uff0c\u4e2d\u6d77\u77f3\u6cb9\u65e2\u5b9a\u7684\u9999\u6e2f\u4e0a\u5e02\u878d\u8d44\u8ba1\u5212\u4e0d\u53d8\uff0c\u6307\u793a\u56fd\u5bb6\u7ecf\u8d38\u59d4\u8054\u5408\u77f3\u6cb9\u90e8\u95e8\u660e\u786e\u9650\u5236\u975e\u516c\u6709\u8d44\u672c\u8fdb\u5165\u77f3\u6cb9\u5f00\u91c7\u9886\u57df\u7684\u653f\u7b56\uff0c\u5e76\u660e\u786e\u8981\u6c42\u5728\u56fd\u5185\u5177\u5907\u52d8\u63a2\u3001\u5f00\u91c7\u6280\u672f\u6761\u4ef6\u7684\u6d45\u6d77\u6cb9\u7530\u8981\u5c3d\u53ef\u80fd\u7684\u81ea\u4e3b\u52d8\u63a2\u5f00\u53d1\u3002</p><p>\u3000\u3000\u526f\u603b\u7406\u9ec4\u708e\u6e05\u7684\u6279\u793a\u4e5f\u662f\u8003\u8651\u5f53\u524d\u4e2d\u7f8e\u4e24\u56fd\u5728\u80fd\u6e90\u5f00\u53d1\u9886\u57df\u7684\u5408\u4f5c\u80cc\u666f\u4e0d\u80fd\u8f7b\u6613\u7684\u7834\u574f\uff0c\u6ca1\u6709\u63ed\u7a7f\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u7684\u9669\u6076\u7528\u5fc3\u5c06\u5176\u4e00\u811a\u8e22\u51fa\u5c40\u53bb\uff0c\u4f46\u662f\u4e5f\u5728\u539f\u5219\u4e0a\u8981\u6c42\u65b0\u6c82\u6d77\u57df\u77f3\u6cb9\u8d44\u6e90\u7684\u8fdb\u4e00\u6b65\u52d8\u63a2\u3001\u5f00\u91c7\u5de5\u4f5c\u7531\u4e2d\u6d77\u77f3\u6cb9\u72ec\u7acb\u8fdb\u884c\uff0c\u4e0e\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u7684\u5408\u4f5c\u5f00\u91c7\u5408\u540c\u4e5f\u4ec5\u9650\u4e8e\u76ee\u524d\u5df2\u63a2\u660e5\u4ebf\u6876\u50a8\u91cf\u7684\u533a\u57df\u5185\u3002\u5373\u4f7f\u5728\u8be5\u533a\u57df\u7684\u5408\u4f5c\uff0c\u4e5f\u8981\u4e25\u683c\u6309\u7167\u4e4b\u524d\u7684\u5408\u4f5c\u52d8\u63a2\u534f\u8bae\u6267\u884c\uff0c\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u6700\u591a\u4e5f\u53ea\u80fd\u536020\uff05\u7684\u5f00\u91c7\u6743\u76ca\uff0c\u5408\u4f5c\u671f\u9650\u4e0d\u8d85\u8fc7\u5341\u4e94\u5e74\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u4f1a\u8ddf\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u6495\u7834\u8138\u76ae\uff0c\u4f46\u662f\u8fd9\u4e00\u533a\u57df\u7684\u77f3\u6cb9\u8d44\u6e90\u52d8\u63a2\u662f\u4e2d\u6d77\u77f3\u6cb9\u4e0b\u5c5e\u52d8\u63a2\u516c\u53f8\u4e0e\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u5408\u4f5c\u52d8\u63a2\uff0c\u4e3a\u4f55\u8096\u5eb7\u6069\u77f3\u6cb9\u516c\u53f8\u5728\u52d8\u63a2\u6570\u636e\u4e0a\u9020\u5047\u800c\u4e2d\u6d77\u77f3\u6cb9\u4e0b\u5c5e\u52d8\u63a2\u516c\u53f8\u5374\u6ca1\u6709\u53d1\u89c9\uff1f\u8981\u60f3\u8fdb\u4e00\u6b65\u6478\u6e05\u65b0\u6c82\u6d77\u57df\u7684\u77f3\u6cb9\u8d44\u6e90\uff0c\u5bf9\u4e9b\u4e8b\u5c31\u4e0d\u80fd\u653e\u624b\u4e0d\u8c03\u67e5\u3002</p><p>\u3000\u3000\u5185\u5e55\u6d88\u606f\u662f\u6781\u5176\u53ef\u6015\u7684\uff0c\u5728\u526f\u603b\u7406\u9ec4\u708e\u6e05\u505a\u6279\u793a\u4e4b\u524d\uff0c\u4e2d\u6d77\u6cb9\u6536\u4e86\u7684\u5f71\u5b50\u8ba2\u5355\u4e0d\u8db3\u8ba1\u5212\u878d\u8d44\u503c\u7684\u516d\u6210\uff0c\u5728\u526f\u603b\u7406\u9ec4\u708e\u6e05\u505a\u51fa\u6279\u793a\u4e4b\u540e\uff0c\u5728\u4e24\u5929\u65f6\u95f4\u91cc\u5c31\u53c8\u6d8c\u51fa\u603b\u989d\u8d85\u8fc7\u516d\u4ebf\u7f8e\u5143\u7684\u5f71\u5b50\u8ba2\u5355\u6765\uff0c\u8ba4\u8d2d\u4ef7\u683c\u5c0f\u5e45\u7684\u4e0a\u5347\uff0c\u4f3c\u4e4e\u4e00\u591c\u4e4b\u95f4\u8c01\u90fd\u77e5\u9053\u4e2d\u6d77\u77f3\u6cb9\u62df\u4e0a\u5e02\u516c\u53f8\u540d\u4e0b\u5c06\u62e5\u6709\u8d85\u5927\u578b\u6574\u88c5\u6d45\u6d77\u6cb9\u7530\u3002</p><p>\u3000\u3000\u8fd9\u79cd\u60c5\u51b5\u5728\u56fd\u5185\u6b8a\u96be\u907f\u514d\uff0c\u9526\u6e56\u4e5f\u65e0\u80fd\u529b\u633d\u72c2\u6f9c\uff0c\u773c\u4e0b\u53ea\u6709\u8981\u5c3d\u53ef\u80fd\u7684\u4fdd\u8bc1\u81ea\u5df1\u7684\u5229\u76ca\u3002\u867d\u8bf4\u4f1a\u51fa\u73b0\u8d85\u989d\u8ba4\u8d2d\uff0c\u9526\u6e56\u53ea\u8981\u6309\u7167\u8d85\u989d\u7cfb\u6570\u63d0\u4f9b\u8db3\u591f\u591a\u7684\u8ba4\u8d2d\u8d44\u91d1\u5c31\u80fd\u83b7\u5f97\u9884\u671f\u7684\u6301\u80a1\u6bd4\u4f8b\uff0c\u8fd9\u65b9\u9762\u5012\u4e0d\u7528\u592a\u62c5\u5fc3\u3002</p><p>\u3000\u3000\u53ea\u8981\u65b0\u6c82\u6d77\u57df\u786e\u5b9a\u5b58\u5728\u4e30\u5bcc\u7684\u6cb9\u6c14\u8d44\u6e90\uff0c\u4e2d\u6d77\u77f3\u6cb9\u62df\u4e0a\u5e02\u7684\u4e1a\u52a1\u8d44\u4ea7\u4ef7\u503c\u5c31\u4f1a\u5f97\u5230\u6781\u9ad8\u7684\u63d0\u5347\uff0c\u8fd9\u90e8\u5206\u8d22\u52a1\u6295\u8d44\u5c31\u4f1a\u6536\u5230\u4e30\u539a\u7684\u56de\u62a5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5728\u9999\u6e2f\u505c\u7559\u4e86\u5dee\u4e0d\u591a\u6709\u4e00\u5468\u7684\u65f6\u95f4\uff0c\u79bb\u5f00\u9999\u6e2f\u540e\uff0c\u6ca1\u6709\u76f4\u63a5\u8fd4\u56de\u5185\u5730\uff0c\u800c\u662f\u53bb\u4e86\u6fb3\u5927\u5229\u4e9a\u3002</p><p>\u3000\u3000\u4ece\u9999\u6e2f\u4e58\u4e09\u5c0f\u65f6\u98de\u673a\u5230\u65b0\u52a0\u5761\uff0c\u518d\u4ece\u65b0\u52a0\u5761\u51fa\u53d1\uff0c\u8f6c\u673a\u4e94\u5c0f\u65f6\u540e\u5230\u6089\u5c3c\uff0c\u518d\u4ece\u6089\u5c3c\u5728\u5929\u7a7a\u98de\u884c\u56db\u5c0f\u65f6\u62b5\u8fbe\u6fb3\u5927\u5229\u4e9a\u897f\u6fb3\u6d32\u9996\u5e9c\u73c0\u65af\u3002</p><p>\u3000\u3000\u8981\u4e0d\u662f\u8fd9\u91cc\u7684\u94c1\u77ff\u77f3\u4ea7\u91cf\u672a\u6765\u5c06\u5360\u636e\u5168\u7403\u603b\u4ea7\u91cf\u7684\u4e09\u5206\u4e4b\u4e00\uff0c\u5f20\u606a\u90fd\u65e0\u6cd5\u60f3\u8c61\u81ea\u5df1\u4f1a\u6709\u673a\u4f1a\u8e0f\u6b65\u8fd9\u5ea7\u5c0f\u57ce\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5728\u6fb3\u5927\u5229\u4e9a\u4eba\u773c\u91cc\uff0c\u73c0\u65af\u4e5f\u662f\u4e00\u5ea7\u9065\u8fdc\u800c\u964c\u751f\u7684\u57ce\u5e02\uff0c\u79bb\u73c0\u65af\u6700\u8fd1\u7684\u4e00\u5ea7\u5927\u57ce\u5e02\u5dee\u4e0d\u591a\u4e5f2400\u516c\u91cc\u4e4b\u5916\uff0c\u82e5\u662f\u4e58\u5750\u592a\u5e73\u6d0b-\u5370\u5ea6\u6d0b\u5927\u94c1\u8def\u7a7f\u8d8a\u6fb3\u6d32\u5185\u9646\uff0c\u4ece\u6089\u5c3c\u5230\u73c0\u65af\u9700\u8981\u56db\u5929\u56db\u591c\u3002</p><p>\u3000\u3000\u5373\u4f7f\u4efb\u4f55\u4e00\u4e2a\u6fb3\u5927\u5229\u4e9a\u4eba\u6ca1\u6709\u4e00\u4e2a\u4f1a\u8ba4\u4e3a\u8eab\u4e3a\u4e9a\u6fb3\u5927\u5229\u4e9a\u5dde\u9996\u5e9c\u7684\u73c0\u65af\u662f\u5ea7\u201c\u5c0f\u57ce\u201d\uff0c\u4e8b\u5b9e\uff0c\u6574\u4e2a\u897f\u6fb3\u7684\u5927\u90e8\u5206\u4eba\u53e3\u90fd\u5b9a\u5c45\u5728\u73c0\u65af\uff0c\u800c\u6574\u4e2a\u897f\u6fb3\u53c8\u5360\u636e\u4e86\u6574\u4e2a\u6fb3\u5927\u5229\u4e9a\u4e09\u5206\u4e4b\u4e00\u7684\u571f\u5730\u3002\u7136\u800c\uff0c\u73c0\u65af\u7684\u786e\u662f\u4e00\u5ea7\u5341\u5206\u8896\u73cd\u7684\u9996\u5e9c\u57ce\u5e02\uff0c\u81f3\u5c11\u5728\u4e5d\u4e5d\u5e74\uff0c\u5728\u94c1\u77ff\u77f3\u8d38\u6613\u706b\u7206\u4e4b\u524d\uff0c\u57ce\u5e02\u7684\u5546\u4e1a\u533a\u4e0e\u91d1\u878d\u533a\u51e0\u4e4e\u90fd\u96c6\u4e2d\u5728\u5e02\u4e2d\u5fc3\u7684\u4e24\u6761\u4e3b\u8981\u8857\u9053\u9644\u8fd1\uff0c\u53ea\u9700\u8981\u5341\u51e0\u5206\u949f\u5c31\u53ef\u4ee5\u56f4\u7ed5\u8fd9\u5757\u5730\u65b9\u8d70\u4e0a\u4e00\u5708\uff0c\u5728\u65c5\u6e38\u624b\u518c\u4e2d\u6807\u6ce8\u7684\u4e3b\u8981\u666f\u70b9\u8ddd\u79bb\u5e02\u4e2d\u5fc3\u4e5f\u5927\u7ea6\u5728\u5341\u5206\u949f\u6b65\u884c\u8ddd\u79bb\u9644\u8fd1\uff0c\u8fdc\u6ca1\u6709\u51e0\u5e74\u4e4b\u540e\u7684\u7e41\u8363\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u5f20\u606a\u4e5f\u6ca1\u6709\u4eb2\u773c\u89c1\u8fc7\u73c0\u671f\u51e0\u5e74\u4e4b\u540e\u7684\u7e41\u8363\uff0c\u7a0d\u6709\u4e9b\u7ecf\u6d4e\u77e5\u8bc6\u5374\u662f\u4e0d\u96be\u63a8\u7b97\u7684\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\uff0c\u4e2d\u56fd\u56fd\u5185\u94a2\u94c1\u4ea7\u91cf\u624d\u7a81\u7834\u4e00\u4ebf\u5428\u6ca1\u8fc7\u591a\u4e45\uff0c\u5df2\u7ecf\u662f\u5168\u7403\u7b2c\u4e00\u5927\u94a2\u94c1\u4ea7\u56fd\uff0c\u4f46\u662f\u5bf9\u6d77\u5916\u94c1\u77ff\u77f3\u7684\u4f9d\u8d56\u6bd4\u4f8b\u8fd8\u5360\u4e0d\u5230\u5168\u7403\u94c1\u77ff\u77f3\u8d38\u6613\u768410\uff05\uff0c\u800c\u4e14\u8fd8\u4e3b\u8981\u4f9d\u8d56\u9644\u8fd1\u7684\u5370\u5ea6\u7b49\u5730\u533a\uff0c\u5bf9\u897f\u6fb3\u6d32\u7684\u94c1\u77ff\u77f3\u7ecf\u6d4e\u523a\u6fc0\u4e0d\u5927\u3002</p><p>\u3000\u3000\u897f\u6fb3\u6d32\u7684\u94c1\u77ff\u8d44\u6e90\u65e9\u5c31\u95fb\u540d\u5929\u4e0b\uff0c\u77ff\u4ea7\u5f00\u91c7\u4e0e\u51fa\u53e3\u662f\u897f\u6fb3\u6d32\u7684\u7edd\u5bf9\u652f\u67f1\u4ea7\u4e1a\uff0c\u4f46\u662f\u4e5d\u516b\u5e74\u897f\u6fb3\u6d32\u7684\u94c1\u77ff\u77f3\u51fa\u53e3\u91cf\u4e0d\u8db3\u516b\u5343\u4e07\u5428\u3002\u516b\u5e74\u4e4b\u540e\uff0c\u968f\u7740\u4e2d\u56fd\u94a2\u94c1\u4ea7\u80fd\u7684\u6fc0\u589e\uff0c\u897f\u6fb3\u6d32\u7684\u94c1\u77ff\u77f3\u51fa\u53e3\u91cf\u6fc0\u589e\u5230\u4e09\u4ebf\u5428\uff0c\u800c\u4e14\u6bcf\u5428\u94c1\u77ff\u77f3\u7684\u552e\u4ef7\u5c06\u6fc0\u589e\u4e94\u500d\uff0c\u4f5c\u4e3a\u5df2\u7ecf\u662f\u6fb3\u5927\u5229\u4e9a\u77ff\u4ea7\u4e2d\u5fc3\u7684\u897f\u6fb3\u6d32\u7ecf\u6d4e\u89c4\u6a21\u5728\u77ed\u77ed\u516b\u5e74\u4e4b\u5185\u53c8\u6fc0\u589e\u4e86\u5341\u516b\u4e5d\u500d\uff0c\u5bf9\u4e8e\u4eba\u53e3\u4e0d\u8db3\u4e8c\u767e\u4e07\u7684\u73c0\u65af\u800c\u8a00\uff0c\u5c31\u8db3\u4ee5\u60f3\u8c61\u516b\u5e74\u4e4b\u540e\u7684\u7e41\u8363\u4e86\u3002</p><p>\u3000\u3000\u4e94\u6708\u7684\u73c0\u65af\u6b63\u503c\u6df1\u79cb\u5b63\u8282\uff0c\u5178\u578b\u7684\u5730\u4e2d\u6d77\u6c14\u5019\uff0c\u6df1\u79cb\u5b63\u8282\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u5bd2\u610f\uff0c\u9633\u5149\u660e\u5a9a\uff0c\u5341\u5206\u7684\u5b89\u9759\uff0c\u4f0a\u5229\u838e\u5c71\u9876\u7684\u5982\u8335\u8349\u5730\u4e0a\u76db\u5f00\u7740\u8bf4\u4e0d\u51fa\u540d\u5b57\u6765\u7684\u91ce\u82b1\u3002</p><p>\u3000\u3000\u9664\u4e86\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4ee5\u53ca\u7fdf\u4e39\u9752\u4e4b\u5916\uff0c\u5b59\u9759\u9999\u5728\u65b0\u52a0\u5761\u4e0e\u4ed6\u4eec\u6c47\u5408\u4e00\u8d77\u6765\u5230\u73c0\u65af\uff0c\u501f\u52a9\u5546\u52a1\u8003\u5bdf\u7684\u540d\u4e49\uff0c\u5374\u662f\u6765\u73c0\u65af\u65c5\u6e38\u6e21\u5047\u6765\u4e86\u3002\u5230\u73c0\u65af\u7b2c\u4e00\u5929\uff0c\u5b59\u9759\u9999\u8981\u53bb\u770b\u8457\u540d\u7684\u5c16\u5cf0\u77f3\u9635\uff0c\u5c31\u4e0e\u53f6\u5efa\u658c\u5355\u72ec\u53bb\u4e86\u73c0\u65af\u5317\u9762\u7684\u5357\u90a6\u56fd\u5bb6\u516c\u56ed\uff0c\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u5728\u5e02\u5185\u6e38\u73a9\uff0c\u7ea6\u597d\u7b2c\u4e8c\u5929\u624d\u5f00\u59cb\u5546\u52a1\u4e0a\u7684\u8003\u5bdf\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u4f0a\u5229\u838e\u5c71\u9876\u7684\u82f1\u7687\u516c\u56ed\u8349\u5730\u4e0a\uff0c\u51dd\u671b\u7740\u5929\u9e45\u6cb3\u5bf9\u5cb8\u4e00\u5e62\u7070\u8272\u5c16\u9876\u7684\u5efa\u7b51\uff0c\u7fdf\u4e39\u9752\u5a34\u9759\u7684\u7ad9\u5728\u4ed6\u8eab\u8fb9\uff0c\u636e\u9526\u6e56\u5546\u4e8b\u9a7b\u73c0\u65af\u529e\u4e8b\u5904\u7684\u5de5\u4f5c\u4eba\u5458\u4ecb\u7ecd\uff0c\u90a3\u5e62\u7070\u8272\u5c16\u9876\u7684\u5efa\u7b51\u5c31\u662f\u4e09\u4e95\u7269\u4ea7\u5728\u73c0\u65af\u7684\u529e\u516c\u5927\u697c\uff0c\u4e09\u83f1\u5546\u4e8b\u4ee5\u53ca\u4f0a\u85e4\u5fe0\u5546\u4e8b\u5728\u73c0\u65af\u7684\u529e\u4e8b\u673a\u6784\u4e5f\u90fd\u5728\u90a3\u680b\u529e\u516c\u5927\u697c\u91cc\u3002</p><p>\u3000\u3000\u201c\u65e5\u672c\u5df2\u7ecf\u4e0e\u6fb3\u5927\u5229\u4e9a\u5b8c\u6210\u78e8\u5408\uff0c\u7559\u7ed9\u6211\u4eec\u7684\u7a7a\u9699\u5df2\u7ecf\u662f\u975e\u5e38\u72ed\u7a84\u4e86\u2026\u2026\u201d\u5f20\u606a\u8f7b\u8f7b\u5482\u4e86\u5482\u5634\uff0c\u8f6c\u8fc7\u8eab\u6765\u7275\u8fc7\u7fdf\u4e39\u9752\u7684\u624b\u3002</p><p>\u3000\u3000\u201c\u4e0d\u662f\u8bf4\u4e86\u660e\u5929\u518d\u53bb\u8003\u8651\u516c\u53f8\u7684\u4e8b\u60c5\uff1f\u201d\u7fdf\u4e39\u9752\u7f8e\u7738\u6a2a\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u6709\u8bb8\u591a\u9e1f\u843d\u5728\u8349\u5730\u4e0a\u4f11\u61a9\uff0c\u4e5f\u4e1d\u6beb\u4e0d\u6015\u8349\u5730\u4e0a\u7684\u6e38\u5ba2\uff0c\u5979\u9887\u6709\u5174\u8da3\u7684\u4f38\u624b\u7ed9\u4e00\u53ea\u7ea2\u5634\u9ec4\u8db3\u7684\u5927\u9e1f\u5544\u624b\uff0c\u53c8\u62c9\u5f20\u606a\u4f38\u624b\u53bb\u7ed9\u9e1f\u5544\u3002</p><p>\u3000\u3000\u5165\u591c\u524d\uff0c\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u6253\u7535\u8bdd\u8fc7\u6765\u8bf4\u8981\u7559\u5728\u5357\u90a6\u56fd\u5bb6\u516c\u56ed\u9644\u8fd1\u7684\u5c0f\u9547\u4e0a\u8fc7\u591c\uff0c\u5f20\u606a\u4fbf\u4e0e\u7fdf\u4e39\u9752\u4eab\u53d7\u8fd9\u5f02\u57df\u96be\u5f97\u7684\u591c\u665a\u3002</p><p>\u3000\u3000\u897f\u6fb3\u6d32\u76db\u4ea7\u94c1\u3001\u954d\u2026\u2026\u9ec4\u91d1\u3001\u94dc\u3001\u5929\u7136\u6c14\u7b49\u591a\u79cd\u81ea\u7136\u8d44\u6e90\uff0c\u4e16\u754c\u4e3b\u8981\u7684\u80fd\u6e90\u4e0e\u5de5\u7a0b\u516c\u53f8\u90fd\u5728\u73c0\u65af\u8bbe\u6709\u529e\u4e8b\u673a\u6784\uff0c\u5373\u4fbf\u662f\u201c\u5c0f\u57ce\u201d\u7684\u73c0\u65af\u4e5f\u662f\u76f8\u5f53\u7684\u5962\u534e\uff0c\u5f20\u606a\u4ed6\u4eec\u5165\u4f4f\u7684\u9152\u5e97\u4e1d\u6beb\u4e0d\u5f31\u4e8e\u5728\u9999\u6e2f\u5165\u4f4f\u7684\u534a\u5c9b\u9152\u5e97\u3002</p><p>\u3000\u3000\u5f20\u606a\u5165\u4f4f\u7684\u662f\u5e26\u5ba4\u5185\u6e38\u6cf3\u6c60\u7684\u8c6a\u534e\u623f\u95f4\uff0c\u5728\u5e02\u5185\u6e38\u73a9\u4e86\u4e00\u5929\uff0c\u56de\u5230\u623f\u95f4\u91cc\uff0c\u6ce1\u5728\u6e38\u6cf3\u6c60\u91cc\u5374\u662f\u8212\u670d\u6781\u4e86\u3002\u5728\u9152\u5e97\u5546\u573a\u4e70\u6cf3\u8863\u65f6\uff0c\u5f20\u606a\u63a8\u8350\u7fdf\u4e39\u9752\u4e70\u4e09\u70b9\u5f0f\u7684\uff0c\u7fdf\u4e39\u9752\u6ca1\u6709\u542c\u4ed6\u7684\uff0c\u4e0d\u8fc7\u8fd8\u662f\u4e70\u4e86\u4ef6\u76f8\u5f53\u6027\u611f\u7684\u6cf3\u8863\uff0c\u9ad8\u5f00\u7684\u8170\u90e8\u8ba9\u5979\u8fd1\u4e4e\u5b8c\u7f8e\u7684\u53cc\u817f\u683c\u5916\u7684\u4fee\u957f\u633a\u62e8\uff0c\u6cf3\u8863\u8d28\u5730\u6781\u4f73\uff0c\u7d27\u7ef7\u5728\u5979\u8eab\u4e0a\uff0c\u4ee4\u5979\u50b2\u4eba\u7684\u80f8\u90e8\u547c\u4e4b\u6b32\u51fa\u3002\u5373\u4f7f\u5ba4\u5185\u53ea\u6709\u5f20\u606a\uff0c\u4e5f\u6b63\u56e0\u4e3a\u5ba4\u5185\u53ea\u6709\u5f20\u606a\uff0c\u7fdf\u4e39\u9752\u6362\u4e86\u6cf3\u8863\u51fa\u6765\uff0c\u8fd8\u6709\u4e9b\u5bb3\u7f9e\u7684\u6367\u7740\u80f8\uff0c\u5750\u5230\u6cf3\u6c60\u8fb9\u4e5f\u4e0d\u4e0b\u6c34\uff0c\u5c31\u5b89\u9759\u7684\u770b\u7740\u5f20\u606a\u5728\u90a3\u91cc\u6e38\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u6276\u7740\u6c60\u58c1\u505c\u5728\u6c34\u91cc\uff0c\u6291\u5934\u770b\u7740\u7fdf\u4e39\u9752\uff1a\u201c\u4f60\u662f\u4e0d\u662f\u6709\u4ec0\u4e48\u5fc3\u601d\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u80fd\u6709\u4ec0\u4e48\u5fc3\u601d\uff1f\u201d\u7fdf\u4e39\u9752\u7b11\u4e86\u7b11\uff0c\u4fef\u4e0b\u8eab\u5b50\u6765\u4f38\u624b\u6367\u7740\u5f20\u606a\u7684\u8138\u988a\uff0c\u5374\u8ba9\u5f20\u606a\u770b\u5230\u6d51\u5706\u96ea\u767d\u7684\u53cc\u4e73\u8981\u4ece\u6cf3\u8863\u91cc\u6324\u51fa\u6765\u3002\u5f20\u606a\u770b\u4e86\u5fc3\u9b42\u4e00\u8361\uff0c\u624b\u6309\u7740\u5979\u6ed1\u817b\u5982\u7389\u53c8\u5f39\u6027\u60ca\u4eba\u7684\u5927\u817f\uff0c\u5fcd\u4e0d\u4f4f\u4f38\u624b\u6402\u7740\u5979\u6d51\u5706\u7684\u81c0\u90e8\uff0c\u5c31\u8fd9\u6837\u8ba9\u81ea\u5df1\u505c\u5728\u6c34\u91cc\uff0c\u6b23\u8d4f\u7740\u5750\u5728\u6c60\u8fb9\u7684\u7fdf\u4e39\u9752\u7684\u7edd\u7f8e\u5bb9\u989c\uff0c\u79c0\u7f8e\u7edd\u4f26\u7684\u8138\u86cb\uff0c\u8138\u988a\u6d01\u767d\u6ed1\u817b\u5982\u51dd\u8102\uff0c\u5ae3\u7ea2\u7684\u5634\u5507\u5fae\u542f\u9732\u51fa\u6d01\u767d\u5982\u7389\u7684\u8d1d\u9f7f\uff0c\u53cc\u7738\u91cc\u7684\u98ce\u60c5\u51b6\u8273\u800c\u8ff7\u79bb\uff0c\u8eab\u4f53\u5fae\u4fef\u7740\u8bf4\u8bdd\uff0c\u53cc\u4e73\u6c89\u7538\u7538\u7684\u6d51\u5706\u7855\u5927\uff0c\u6df1\u9083\u7684\u4e73\u6c9f\u96ea\u5ae9\u8bf1\u4eba\uff0c\u5c31\u8fd9\u4e48\u770b\u7740\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u6709\u80a1\u60c5\u6b32\u5728\u80f8\u8179\u95f4\u5347\u817e\uff0c\u5f20\u606a\u62c9\u7740\u7fdf\u4e39\u9752\u7684\u624b\u81c2\uff0c\u8bf4\u9053\uff1a\u201c\u4e0b\u6765\u6e38\u6c34\u6765\u3002\u201d\u5c06\u5979\u7ed9\u62c9\u4e86\u4e0b\u6765\uff0c\u5728\u6c34\u91cc\u62e5\u62b1\u7740\uff0c\u8ba9\u5979\u4e30\u8174\u7684\u8f6f\u8089\u8d34\u7740\u81ea\u5df1\u7684\u8eab\u4f53\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u611f\u89c9\u5230\u5f20\u606a\u81a8\u80c0\u7684\u4e0b\u4f53\uff0c\u55e4\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u771f\u662f\u597d\u8272\u9b3c\u3002\u201d\u8eab\u4f53\u5374\u70ed\u60c5\u4f3c\u706b\u7684\u6324\u5728\u4ed6\u7684\u6000\u91cc\u6ca1\u6709\u79bb\u5f00\uff0c\u5f20\u606a\u8ba9\u5979\u7684\u53cc\u817f\u6253\u5f00\u7f20\u5728\u4ed6\u7684\u8170\u4e0a\uff0c\u7fdf\u4e39\u9752\u4e5f\u987a\u4ece\u7684\u7167\u505a\uff0c\u8fd9\u6837\u4e00\u6765\uff0c\u53cc\u817f\u4e4b\u95f4\u7684\u79c1\u5904\u6b63\u597d\u62b5\u5728\u90a3\u81a8\u80c0\u575a\u5f3a\u7684\u4e0b\u4f53\u4e0a\uff0c\u611f\u89c9\u5230\u5f20\u606a\u7684\u624b\u4f38\u4e0b\u6765\u8981\u53bb\u62e8\u5f00\u4e24\u817f\u95f4\u6cf3\u8863\u8f7b\u8584\u7684\u88c6\u5e03\u8ba9\u79c1\u5904\u5f7b\u5e95\u9732\u5728\u6c34\u91cc\uff0c\u7fdf\u4e39\u9752\u53ea\u662f\u60c5\u52a8\u7684\u6402\u7740\u4ed6\u7684\u8116\u5b50\uff0c\u5634\u5507\u9644\u5230\u4ed6\u7684\u8033\u8fb9\u8f7b\u58f0\u5453\u8bed\uff1a\u201c\u8ba9\u6211\u7559\u5728\u73c0\u65af\u5427\u3002\u201d</p></article>"}}}]);
