(window.webpackJsonp=window.webpackJsonp||[]).push([[1028],{1051:function(p){p.exports={chapterTitle:null,title:"\u7b2c834\u7ae0 \u5fc3\u4e2d\u7684\u6050\u60e7",content:"<article><h2>\u7b2c834\u7ae0 \u5fc3\u4e2d\u7684\u6050\u60e7</h2><p>\u3000\u3000\u8c22\u6c49\u9756\u5728\u75c5\u623f\u5916\u7684\u5c0f\u5385\u91cc\u5b89\u9759\u7684\u7b49\u7740\uff0c\u8c22\u665a\u5c71\u3001\u8c22\u610f\u3001\u8c22\u5251\u5357\u4e5f\u90fd\u9ed8\u7136\u65e0\u8bed\u7684\u5750\u5728\u90a3\u91cc\uff0c\u53ea\u662f\u5404\u6000\u5fc3\u601d\u7f62\u4e86\uff0c\u80fd\u9690\u7ea6\u542c\u89c1\u9694\u7740\u4e00\u9053\u623f\u95e8\u7684\u4e89\u8bba\u58f0\uff0c\u6a21\u6a21\u7cca\u7cca\u4e5f\u542c\u4e0d\u6e05\u695a\uff0c\u4f46\u662f\u90fd\u80fd\u731c\u5230\u91cc\u9762\u4eba\u5728\u4e89\u8bba\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u7247\u54cd\uff0c\u5468\u747e\u745c\u3001\u8c22\u6c49\u660e\u8fd8\u6709\u5468\u747e\u73ba\u7684\u59bb\u5b50\u4ece\u91cc\u95f4\u8d70\u51fa\u6765\uff0c\u5468\u747e\u73ba\u7684\u59bb\u5b50\u62ff\u4e00\u79cd\u51b7\u6de1\u91cc\u5316\u5165\u60b2\u54c0\u7684\u8bed\u8c03\u8bf4\u9053\uff1a\u201c\u8001\u5468\u62c5\u4e0d\u8d77\u98ce\u9669\u4e86\uff0c\u8fd8\u662f\u4fdd\u7559\u6cbb\u7597\u7684\u597d\uff0c\u6062\u590d\u7684\u53ef\u80fd\u6027\u5927\u4e9b\uff0c\u81f3\u5c11\u4eba\u7559\u5728\u4e16\u95f4\u80fd\u6709\u4e2a\u5ff5\u60f3\u2026\u2026\u201d</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e0d\u9192\u8fc7\u6765\uff0c\u5927\u6982\u66f4\u7b26\u5408\u5468\u5bb6\u7684\u5229\u76ca\u5427\uff0c\u53ea\u6709\u4ed6\u8fd9\u6837\u624d\u4f1a\u6709\u4eba\u51fa\u4e8e\u601c\u60af\u4e4b\u5fc3\u5e2e\u5468\u5bb6\u8bf4\u4e00\u4e24\u53e5\u8bdd\u3002\u8c22\u6c49\u9756\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u8d70\u5230\u75c5\u5e8a\u524d\u770b\u7740\u8138\u5f62\u524a\u7626\u7684\u5468\u747e\u73ba\u6ca1\u6709\u610f\u8bc6\u7684\u8eb2\u5728\u75c5\u5e8a\u4e0a\uff0c\u53e3\u9f3b\u8fde\u7740\u547c\u5438\u673a\uff0c\u624b\u8155\u8fd8\u5728\u8f93\u6db2\uff0c\u867d\u8bf4\u6ca1\u6709\u4eba\u4e0b\u9762\u8c03\u67e5\u4ec0\u4e48\uff0c\u4f46\u4ece\u6628\u5929\u9ec4\u660f\u53d1\u75c5\u4ee5\u6765\uff0c\u9664\u4e86\u51e0\u4e2a\u6559\u5e08\u4e0e\u4ed6\u5728\u5b66\u6821\u5e26\u7684\u5b66\u751f\u4e4b\u5916\uff0c\u6821\u65b9\u5e76\u6ca1\u6709\u6b63\u5f0f\u7684\u4ee3\u8868\u8fc7\u6765\u63a2\u89c6\uff0c\u8bf4\u660e\u5468\u747e\u73ba\u6d89\u6848\u7684\u6d88\u606f\u5bf9\u8fd9\u4e9b\u4eba\u4e5f\u4e0d\u662f\u4ec0\u4e48\u79d8\u5bc6\u4e86\u3002</p><p>\u3000\u3000\u5927\u6982\u4ece\u6821\u65b9\u7684\u89d2\u5ea6\u8003\u8651\u4e5f\u4e0d\u4f1a\u5e0c\u671b\u5468\u747e\u73ba\u9192\u8fc7\u6765\uff0c\u8fd9\u6837\u80fd\u8ba9\u4e1c\u5927\u907f\u514d\u9677\u5165\u4e00\u6869\u5b66\u672f\u4e11\u95fb\u4e4b\u4e2d\uff0c\u53ea\u4e0d\u8fc7\u6821\u65b9\u7684\u610f\u89c1\u8fd9\u6b21\u4f1a\u76f8\u5bf9\u8584\u5f31\u4e00\u4e9b\uff0c\u4e5f\u53ea\u80fd\u9759\u89c2\u4e8b\u6001\u7684\u6f14\u53d8\u3002</p><p>\u3000\u3000\u8fd9\u4e5f\u7b97\u662f\u95e8\u5ead\u51b7\u843d\u4e86\u5427\uff1f\u8c22\u6c49\u9756\u53c8\u505c\u7559\u7247\u523b\uff0c\u5c31\u544a\u8f9e\u79bb\u53bb\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e0d\u4f1a\u5c31\u6b64\u6536\u624b\u5427\uff1f\u201d\u94bb\u8fdb\u8f66\u91cc\uff0c\u8c22\u610f\u9887\u6709\u7591\u8651\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u4e0d\u7ba1\u9526\u6e56\u4f1a\u4e0d\u4f1a\u5c31\u6b64\u6536\u624b\uff0c\u96be\u9053\u6709\u7ed1\u5728\u4e00\u8d77\u53bb\u6b7b\u7684\u5fc5\u8981\uff1f\u201d\u8c22\u6c49\u9756\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u7b49\u4e86\u7247\u523b\uff0c\u6ca1\u6709\u89c1\u8c22\u665a\u5c71\u8ddf\u7740\u4ece\u533b\u9662\u91cc\u51fa\u6765\uff0c\u5c31\u5429\u5490\u53f8\u673a\u5f00\u8f66\uff0c\u201c\u65e2\u7136\u8fc7\u6765\u4e86\uff0c\u5c31\u966a\u6211\u4e00\u8d77\u53bb\u770b\u770b\u5b50\u5609\u3002\u201d</p><p>\u3000\u3000\u8f66\u8fc7\u5b66\u5e9c\u5df7\u65f6\uff0c\u7adf\u770b\u5230\u5f20\u606a\u4e00\u4eba\u5728\u8857\u4e0a\u767e\u65e0\u804a\u8d56\u7684\u95f2\u901b\u7740\u3002</p><p>\u3000\u3000\u5f88\u96be\u60f3\u8c61\u5f20\u606a\u767e\u65e0\u804a\u8d56\u95f2\u901b\u7684\u6a21\u6837\uff0c\u8c22\u6c49\u9756\u624b\u90fd\u6293\u5230\u8f66\u628a\u4e0a\u4e86\uff0c\u7ec8\u662f\u6ca1\u6709\u542d\u58f0\u8ba9\u53f8\u673a\u505c\u8f66\u3002</p><p>\u3000\u300016\u65e5\uff0c\u4e5f\u5c31\u662f\u524d\u5929\uff0c\u9526\u6e56\u5728\u9999\u6e2f\u9686\u91cd\u7684\u4e3e\u884c\u7231\u8fbe\u65b0\u6b3e\u624b\u673ai198\u7684\u9996\u5356\u4f1a\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u9526\u6e56\u5728\u5185\u5730\u542f\u52a8\u5927\u89c4\u6a21\u7684\u65b0\u6b3e\u624b\u673a\u4e0a\u5e02\u5ba3\u4f20\u6d3b\u52a8\uff0c\u5e76\u901a\u8fc7\u76db\u946b\u7535\u5668\u5728\u5404\u5730\u7684\u95e8\u5e97\u5f00\u59cb\u63a5\u53d7\u5185\u5730\u7528\u6237\u7684\u9884\u8d2d\uff0c\u4e5f\u5c06\u5728\u534a\u4e2a\u6708\u4e4b\u540e\uff0c\u5728\u5185\u5730\u4e3b\u8981\u57ce\u5e02\u540c\u65f6\u5f00\u542f\u7231\u8fbei198\u624b\u673a\u7684\u9996\u5356\u4f1a\uff0c\u6b63\u5f0f\u5c06\u7231\u8fbei198\u624b\u673a\u63a8\u5411\u5e02\u573a\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u5185\u5730\u7684\u7528\u6237\u8981\u6ca1\u6709\u6e20\u9053\u4ece\u9999\u6e2f\u4e70\u8d27\uff0c\u53ea\u80fd\u7b49\u5230\u534a\u4e2a\u6708\u540e\u624d\u80fd\u770b\u5230\u9526\u6e56\u6b63\u5f0f\u5728\u5185\u5730\u4e0a\u5e02\u7684\u6700\u65b0\u6b3e\u624b\u673a\uff0c\u534a\u4e2a\u6708\u540e\u9526\u6e56\u8fd8\u5c06\u540c\u65f6\u63a8\u51fa\u53e6\u5916\u4e24\u6b3e\u65b0\u6b3e\u624b\u673a\uff0c\u4f7f\u5f97\u9526\u6e56\u5f53\u524d\u5728\u5e02\u573a\u4e0a\u7684\u624b\u673a\u54c1\u79cd\u8fbe\u5230\u4e94\u79cd\u3002</p><p>\u3000\u3000\u9526\u6e56\u4ec5\u7528\u4e86\u4e00\u5e74\u5c31\u6210\u4e3a\u56fd\u5185\u5e02\u573a\u7684\u7b2c\u56db\u5927\u624b\u673a\u9500\u552e\u5382\u5546\u5e76\u5728\u4e1c\u5357\u4e9a\u5e02\u573a\u5360\u636e\u4e00\u5b9a\u7684\u5e02\u573a\u4efd\u989d\uff0c\u867d\u7136\u9526\u6e56\u7684CKE\uff08\u4e2d\u6587\u6570\u5b57\u952e\u8f93\u5165\uff09\u6280\u672f\u624d\u6b63\u5f0f\u516c\u5e03\u624d\u4e24\u5929\u7684\u65f6\u95f4\uff0c\u52a8\u4f5c\u8fc5\u901f\u7684\u4e13\u4e1a\u5a92\u4f53\u5df2\u7ecf\u7ed9\u4e88\u6781\u9ad8\u7684\u8bc4\u4ef7\uff0c\u8ba4\u4e3a\u6b64\u6280\u672f\u5c06\u8fdb\u4e00\u6b65\u7684\u6269\u5927\u9526\u6e56\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u6240\u5360\u7684\u4efd\u989d\uff0c\u751a\u81f3\u6709\u53ef\u80fd\u5c06\u5a01\u80c1\u5230\u7231\u7acb\u4fe1\u3001\u6469\u6258\u7f57\u62c9\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u56fd\u5185\u624b\u673a\u5e02\u573a\u5c31\u50cf\u5f53\u521d\u7684\u789f\u673a\u5e02\u573a\u4e00\u6837\uff0c\u6b63\u5904\u4e8e\u84ec\u52c3\u53d1\u5c55\u671f\uff0c\u552f\u4e00\u4e0d\u540c\u7684\u5c31\u662f\u624b\u673a\u5e02\u573a\u7684\u8fdb\u5165\u662f\u4e25\u683c\u53d7\u5230\u9650\u5236\u7684\uff0c\u4e0d\u4ec5\u662f\u4fe1\u606f\u4ea7\u4e1a\u90e8\u5bf9\u56fd\u5185\u5382\u5546\u8fdb\u5165\u8be5\u5e02\u573a\u6709\u884c\u653f\u4e0a\u7684\u9650\u5236\uff0c\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u8fd9\u4e2a\u534a\u5b98\u65b9\u7ec4\u7ec7\u66f4\u662f\u6709\u6548\u7684\u7ef4\u6301\u4e86\u56fd\u5185\u624b\u673a\u4ea7\u4e1a\u683c\u5c40\u7684\u79e9\u5e8f\uff0c\u800c\u9526\u6e56\u5219\u662f\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u80cc\u540e\u7684\u5e7d\u7075\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u8fd8\u5f88\u96be\u63a8\u6d4b\u9526\u6e56\u7684\u624b\u673a\u4e1a\u52a1\u6700\u7ec8\u5c06\u505a\u5230\u591a\u5927\u89c4\u6a21\uff0c\u4f46\u7edd\u5bf9\u8981\u8fdc\u8fdc\u8d85\u8fc7\u9526\u6e56\u4e4b\u524d\u7684\u789f\u673a\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e5f\u4e8e\u6628\u5929\u7531\u4e01\u69d0\u5728\u5317\u4eac\u7684\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u8bba\u575b\u4e0a\u6b63\u5f0f\u5ba3\u5e03\u5c06\u5411\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u6210\u5458\u4f01\u4e1a\u4f18\u5148\u63d0\u4f9bCKE\u6280\u672f\u7684\u4f7f\u7528\u6388\u6743\uff0c\u751a\u81f3\u4f1a\u63d0\u53d64\uff05\u7684\u6388\u6743\u91d1\u8bbe\u7acb\u4e00\u4e2a\u53d1\u5c55\u57fa\u91d1\uff0c\u4ee5\u5e2e\u52a9\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u7684\u4e2d\u5c0f\u6210\u5458\u4f01\u4e1a\u53d1\u5c55\u4fe1\u606f\u6280\u672f\u2014\u2014\u9526\u6e56\u65e5\u540e\u6240\u6709\u5411\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6210\u5458\u4f01\u4e1a\u63d0\u4f9b\u7684\u6280\u672f\u6388\u6743\uff0c\u90fd\u5c06\u63d0\u53d62-4\uff05\u7684\u6388\u6743\u91d1\u6350\u732e\u7ed9\u8be5\u53d1\u5c55\u57fa\u91d1\uff0c\u8be5\u53d1\u5c55\u57fa\u91d1\u7531\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u8d1f\u8d23\u7b79\u5907\u3001\u7ba1\u7406\u3002</p><p>\u3000\u3000\u4e0d\u8981\u8bf4\u4fe1\u606f\u4ea7\u4e1a\u90e8\u90e8\u957f\u803f\u91cd\u9633\u4e86\uff0c\u534e\u590f\u7535\u5b50\u96c6\u56e2\u8463\u4e8b\u957f\u517c\u603b\u7ecf\u7406\u3001\u5c06\u9526\u6e56\u6068\u4e4b\u5165\u9aa8\u7684\u845b\u5efa\u5fb7\u6628\u5929\u5728\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u8bba\u575b\u4e0a\u4e5f\u4e0d\u5f97\u4e0d\u516c\u5f00\u8d5e\u626c\u4e86\u9526\u6e56\u7684\u6b64\u4e3e\uff0c\u8868\u793a\u4f1a\u4fc3\u4f7f\u5df2\u7ecf\u662f\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u6210\u5458\u7684\u534e\u590f\u7535\u5b50\u65d7\u4e0b\u4f01\u4e1a\u52a0\u5f3a\u4e0e\u9526\u6e56\u5728\u6280\u672f\u9886\u57df\u7684\u5408\u4f5c\uff0c\u4e5f\u5c06\u8ba9\u65d7\u4e0b\u4f01\u4e1a\u79ef\u6781\u7684\u53c2\u4e0e\u8be5\u53d1\u5c55\u57fa\u91d1\u7684\u7b79\u5907\u53ca\u7ba1\u7406\u5de5\u4f5c\u3002\u803f\u91cd\u9633\u4e4b\u524d\u5bf9\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e00\u76f4\u90fd\u91c7\u53d6\u4e0d\u8868\u6001\u7684\u59ff\u6001\uff0c\u4f46\u5728\u6628\u5929\u7684\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u8bba\u575b\u4e5f\u516c\u5f00\u8868\u793a\u5e0c\u671b\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u80fd\u5728\u56fd\u5185\u4fe1\u606f\u4ea7\u4e1a\u9886\u57df\u53d1\u6325\u66f4\u5927\u7684\u4f5c\u7528\u3002</p><p>\u3000\u3000\u82e5\u8981\u6bd4\u80f8\u4e2d\u7684\u683c\u5c40\uff0c\u8c01\u80fd\u53ca\u5f97\u4e0a\u8fd9\u4e2a\u9752\u5e74\uff1f\u8c22\u6c49\u9756\u4ece\u540e\u89c6\u955c\u91cc\u770b\u89c1\u5f20\u606a\u6b63\u65e0\u8da3\u7684\u8e22\u7740\u8857\u5934\u7684\u4e00\u53ea\u7a7a\u6613\u62c9\u7f50\u5728\u8d70\uff0c\u5fc3\u91cc\u611f\u6168\u83ab\u660e\uff0c\u5b9e\u5728\u627e\u4e0d\u5230\u80fd\u591f\u8ba9\u81ea\u5df1\u5728\u8fd9\u91cc\u4e0b\u8f66\u7684\u501f\u53e3\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u8fd8\u4f4f\u5728\u4e1c\u5927\u9752\u5e74\u6559\u5e08\u516c\u5bd3\u524d\u7684\u516c\u5bd3\u697c\u91cc\uff0c\u8fd9\u662f\u9526\u6e56\u63d0\u4f9b\u7ed9\u521b\u4e1a\u56ed\u533a\u4f01\u4e1a\u5458\u5de5\u7684\u5ec9\u4ef7\u8fc7\u6e21\u623f\u3002\u867d\u8bf4\u662f\u5ec9\u4ef7\u8fc7\u6e21\u623f\uff0c\u4f46\u662f\u4e5d\u516b\u5e74\u7684\u5efa\u90ba\u5e02\u4e5f\u5f88\u96be\u627e\u5230\u5982\u6b64\u9ad8\u7ea7\u7684\u4f4f\u5b85\u516c\u5bd3\u4e86\uff0c\u8fd9\u4e5f\u662f\u5e2e\u5fd9\u6682\u65f6\u6ca1\u6709\u4ec0\u4e48\u8d44\u91d1\u5b9e\u529b\u7684\u521b\u4e1a\u4f01\u4e1a\u80fd\u5728\u521b\u4e1a\u9636\u6bb5\u66f4\u633d\u7559\u4f4f\u66f4\u591a\u7684\u4f18\u79c0\u5458\u5de5\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u63c9\u7740\u60fa\u677e\u7684\u7761\u773c\uff0c\u770b\u7740\u5979\u7238\u8ddf\u5979\u54e5\u4fe9\u4eba\u7ad9\u5728\u95e8\u53e3\uff0c\u8be7\u5f02\u7684\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4fe9\u4eba\u6709\u95f2\u5de5\u592b\u60f3\u8d77\u6211\u6765\uff1f\u4e0d\u5f97\u4e86\uff0c\u8d76\u7d27\u7ed9\u6211\u627e\u4e9b\u5403\u7684\u4e1c\u897f\u8fc7\u6765\uff0c\u6628\u5929\u665a\u4e0a\u5fd8\u4e86\u5403\u996d\uff0c\u4e00\u8d77\u71ac\u5230\u4eca\u5929\u4e2d\u5348\u624d\u7761\u89c9\uff0c\u5f62\u8c61\u592a\u60e8\u51c9\uff0c\u4e5f\u5c31\u6ca1\u6709\u6562\u51fa\u95e8\u53bb\u4e70\u4e1c\u897f\u5403\uff0c\u8981\u4e0d\u662f\u4f60\u4eec\u8d76\u5de7\u8fc7\u6765\uff0c\u8bf4\u4e0d\u5b9a\u6211\u5c31\u8981\u997f\u6b7b\u5728\u8fd9\u91cc\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u610f\u89c1\u5c4b\u91cc\u6536\u62fe\u5f97\u8fd8\u7b97\u5e72\u51c0\uff0c\u95ee\u9053\uff1a\u201c\u9648\u9759\u6700\u8fd1\u6709\u8fc7\u6765\uff1f\u201d</p><p>\u3000\u3000\u201c\u524d\u5929\u7ecf\u8fc7\u5efa\u90ba\u3002\u524d\u5929\u521a\u8ddf\u9526\u6e56\u7b7e\u597d\u6280\u672f\u8f6c\u8ba9\u7684\u534f\u8bae\uff0c\u4f46\u662f\u8981\u5c06\u9526\u6e56\u7684\u6280\u672f\u79fb\u690d\u5230\u79d1\u738b\u7684\u624b\u673a\u91cc\uff0c\u8fd8\u8981\u4f9d\u8d56\u8fd9\u8fb9\u7684\u5de5\u4f5c\u5ba4\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u6280\u672f\uff0c\u662f\u6628\u5929\u9526\u6e56\u5728\u5317\u4eac\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u8bba\u575b\u4e0a\u5ba3\u5e03\u8981\u6388\u6743\u8f6c\u8ba9\u7684\u90a3\u9879\u6280\u672f\uff1f\u201d\u8c22\u6c49\u9756\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u6211\u4e0d\u77e5\u9053\u554a\uff0c\u9526\u6e56\u6628\u5929\u53c8\u6709\u8bf4\u8981\u8f6c\u8ba9\u4ec0\u4e48\u6280\u672f\u5417\uff1f\u9648\u9759\u6628\u5929\u4e5f\u5728\u5317\u4eac\uff0c\u6211\u6253\u7535\u8bdd\u95ee\u95ee\u5979\u5c31\u77e5\u9053\u4e86\u3002\u201d\u8c22\u5b50\u5609\u6478\u4e86\u6478\u8111\u888b\uff0c\u4e00\u8138\u7684\u8ff7\u832b\u3002</p><p>\u3000\u3000\u8c22\u6c49\u9756\u89c1\u5973\u513f\u5145\u8033\u4e0d\u95fb\u7a97\u5916\u4e8b\u7684\u8ff7\u7cca\u6a21\u6837\uff0c\u5fc3\u60f3\u5979\u53ef\u80fd\u8fd8\u4e0d\u77e5\u9053\u5468\u747e\u73ba\u6628\u5929\u51fa\u4e8b\u4e86\uff0c\u8ba9\u8c22\u610f\u4e0b\u697c\u8d76\u7d27\u53bb\u4e70\u4e9b\u5403\u98df\u8fc7\u6765\uff0c\u8d70\u8fdb\u5c4b\u91cc\u5e2e\u7740\u5c06\u5c4b\u5b50\u518d\u6536\u62fe\u4e00\u904d\uff0c\u8fd9\u624d\u77e5\u9053\u79d1\u738b\u9ad8\u79d1\u662f\u7b2c\u4e00\u5bb6\u5f97\u5230\u9526\u6e56\u6388\u6743\u79fb\u690d\u4e2d\u6587\u6570\u5b57\u952e\u8f93\u5165\u6280\u672f\u7684\u4f01\u4e1a\uff0c\u6bd4\u9526\u6e56\u6b63\u5f0f\u516c\u5e03\u8fd8\u8981\u65e9\u3002</p><p>\u3000\u3000\u76f8\u5bf9\u4e8e\u5f53\u524d\u624b\u673a\u52a8\u8f84\u4e09\u56db\u5343\u7684\u552e\u4ef7\uff0c\u6bcf\u652f\u624b\u673a\u79fb\u690d\u9526\u6e56\u4e2d\u6587\u6570\u5b57\u952e\u8f93\u5165\u6280\u672f\u7684\u6388\u6743\u8d39\u7528\u624d\u4e00\u7f8e\u5143\uff0c\u7684\u786e\u8981\u7b97\u5f97\u4e0a\u76f8\u5f53\u7684\u4f4e\u5ec9\u3002</p><p>\u3000\u3000\u9526\u6e56\u4e0d\u4f1a\u5728\u6388\u6743\u8d39\u7528\u4e0a\u6b67\u89c6\u5bf9\u5f85\u5176\u4ed6\u4f01\u4e1a\uff0c\u8c22\u6c49\u9756\u5f88\u7591\u60d1\u9526\u6e56\u8fd9\u6b21\u4e3a\u4f55\u8fd8\u8981\u5982\u6b64\u7684\u6177\u6168\uff0c\u5373\u4f7f\u4ed6\u5bf9\u624b\u673a\u4ea7\u4e1a\u8c08\u4e0d\u4e0a\u975e\u5e38\u7684\u4e86\u89e3\uff0c\u4f46\u4e5f\u77e5\u9053\u9526\u6e56\u7684\u8fd9\u9879\u6280\u672f\u5bf9\u56fd\u5185\u624b\u673a\u5e02\u573a\u800c\u8a00\u7b97\u662f\u975e\u5e38\u7684\u5173\u952e\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u5728\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e0a\u8981\u4f53\u73b0\u51fa\u4f18\u52bf\u6765\uff0c\u8fd8\u8981\u56fd\u5185\u624b\u673a\u5382\u5546\u8054\u5408\u8d77\u6765\u5171\u540c\u63a8\u52a8\u624b\u673a\u77ed\u4fe1\u4e1a\u52a1\u7684\u53d1\u5c55\uff0c\u4e0d\u7136\u6211\u4eec\u8981\u4e2d\u6587\u8f93\u5165\u505a\u4ec0\u4e48\uff0c\u624b\u673a\u53c8\u4e0d\u662f\u638c\u4e0a\u7535\u8111\uff1f\u9526\u6e56\u8981\u4e0d\u60f3\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e0e\u534e\u590f\u7535\u5b50\u8054\u5408\u8d77\u6765\u5728\u8fd9\u4e0a\u9762\u5361\u4ed6\u4eec\u7684\u8116\u5b50\uff0c\u4ed6\u4eec\u5f53\u7136\u8981\u8868\u73b0\u5f97\u5927\u65b9\u4e00\u4e9b\u2014\u2014\u7535\u4fe1\u516c\u53f8\u53ef\u662f\u4fe1\u606f\u4ea7\u4e1a\u90e8\u81ea\u5df1\u5bb6\u5f00\u7684\u2026\u2026\u518d\u8bf4\u4e86\uff0c\u9526\u6e56\u8fd9\u65f6\u5019\u518d\u8ddf\u56fd\u5185\u7684\u5382\u5546\u9488\u9488\u8ba1\u8f83\u8fd9\u4e9b\u8fd8\u6709\u4ec0\u4e48\u51fa\u606f\uff1f\u201d\u8c22\u5b50\u5609\u5012\u662f\u4e00\u9488\u89c1\u8840\u7684\u6307\u51fa\u9526\u6e56\u7684\u7528\u5fc3\uff0c\u201c\u7535\u4fe1\u516c\u53f8\u5bf9\u79fb\u52a8\u901a\u8baf\u7684\u6536\u8d39\u5c45\u9ad8\u4e0d\u4e0b\uff0c\u5bfb\u5e38\u4eba\u5373\u4f7f\u6512\u8db3\u4e86\u4e09\u56db\u5343\u5143\u94b1\u4e70\u4e00\u53ea\u624b\u673a\uff0c\u4f46\u662f\u6bcf\u6708\u4e0a\u5343\u5143\u7684\u901a\u8baf\u8d39\u7528\u4e5f\u4e0d\u662f\u591a\u5c11\u4eba\u80fd\u627f\u62c5\u7684\uff0c\u6211\u4eec\u5b66\u6821\u6709\u4e9b\u5b66\u751f\u6bcf\u4e2a\u6708\u7684\u751f\u6d3b\u8d39\u751a\u81f3\u90fd\u4e0d\u5230\u4e00\u767e\u5143\uff0c\u771f\u4e0d\u77e5\u9053\u4ed6\u4eec\u662f\u600e\u4e48\u6d3b\u4e0b\u6765\u7684\u2014\u2014\u60f3\u8981\u7535\u4fe1\u516c\u53f8\u8fd9\u4e24\u5e74\u4e3b\u52a8\u964d\u4f4e\u79fb\u52a8\u901a\u8baf\u7684\u6536\u8d39\u90a3\u7b80\u76f4\u5c31\u662f\u75f4\u4eba\u505a\u68a6\uff0c\u9526\u6e56\u65e9\u5c31\u770b\u5230\u5728\u56fd\u5916\u6ca1\u6709\u4ec0\u4e48\u524d\u9014\u7684\u77ed\u4fe1\u4e1a\u52a1\u5728\u56fd\u5185\u5e02\u573a\u5012\u662f\u5927\u6709\u53ef\u4e3a\uff0c\u53ea\u662f\u7535\u4fe1\u516c\u53f8\u7684\u5b98\u8001\u7237\u6bd4\u8f83\u8fdf\u949d\u800c\u5df2\uff0c\u6d77\u5916\u624b\u673a\u5382\u5546\u4e5f\u4e50\u610f\u770b\u5230\u7535\u4fe1\u516c\u53f8\u7684\u5b98\u8001\u7237\u53cd\u5e94\u8fdf\u949d\u2026\u2026\u9526\u6e56\u73b0\u5728\u5728\u4e2d\u6587\u8f93\u5165\u6280\u672f\u4e0a\u662f\u6709\u4e9b\u4f18\u52bf\uff0c\u4f46\u662f\u77ed\u4fe1\u4e1a\u52a1\u62d6\u4e0a\u4e24\u5e74\u3001\u4e09\u5e74\u518d\u53d1\u5c55\u8d77\u6765\uff0c\u5230\u90a3\u91cc\uff0c\u9526\u6e56\u7684\u6280\u672f\u76f8\u5bf9\u4e8e\u90a3\u4e9b\u6d77\u5916\u624b\u673a\u5de8\u5934\u8fd8\u80fd\u5269\u4e0b\u591a\u5c11\u4f18\u52bf\uff1f\u9526\u6e56\u62e8\u6253\u7b97\u76d8\u518d\u662f\u7cbe\u660e\u4e0d\u8fc7\u4e86\uff0c\u8fd9\u65f6\u5019\u5fc5\u987b\u7ed9\u4fe1\u606f\u4ea7\u4e1a\u90e8\u4e9b\u751c\u5934\uff0c\u518d\u5c06\u8054\u8baf\u3001\u8054\u4fe1\u3001\u4e1c\u5174\u4e09\u5bb6\u516c\u53f8\u8054\u5408\u8d77\u6765\u2014\u2014\u4e0d\u8981\u770b\u773c\u4e0b\u7684\u6536\u8d39\u662f\u5f88\u4f4e\u5ec9\uff0c\u4f46\u662f\u7b49\u8ba9\u4ed6\u4eec\u501f\u8fd9\u62db\u5784\u65ad\u4e86\u4e2d\u6587\u6570\u5b57\u952e\u8f93\u5165\u6280\u672f\u7684\u5e02\u573a\uff0c\u624b\u673a\u552e\u4ef7\u4f1a\u4f9d\u8d8b\u52bf\u6301\u7eed\u964d\u4f4e\uff0c\u4f46\u662f\u4ed6\u4eec\u6280\u672f\u6536\u8d39\u59cb\u7ec8\u4e0d\u964d\uff0c\u5176\u5b9e\u4e5f\u662f\u76f8\u5f53\u53ef\u89c2\u7684\u3002\u6211\u7b97\u662f\u660e\u767d\u8fc7\u6765\uff0c\u8981\u5e2e\u9526\u6e56\u8003\u8651\u4ec0\u4e48\u95ee\u9898\uff0c\u81f3\u5c11\u4e5f\u8981\u770b\u5230\u4e09\u4e94\u5e74\u540e\u7684\u53ef\u80fd\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u6c49\u9756\u59cb\u7ec8\u5bf9\u5b50\u5609\u5bc4\u4e88\u539a\u671b\uff0c\u53ea\u662f\u8fd9\u59ae\u5b50\u6027\u5b50\u6709\u4e9b\u4e56\u5f20\uff0c\u4e5f\u4e0d\u662f\u4ed6\u8fd9\u4e2a\u5f53\u7236\u4eb2\u80fd\u7ea6\u675f\u5f97\u4e86\u7684\uff0c\u4e5f\u53ea\u80fd\u968f\u5979\u7684\u610f\uff0c\u4f46\u4e5f\u5173\u5fc3\u5979\u65e5\u540e\u7684\u53d1\u5c55\uff1a\u201c\u4f60\u4eec\u79d1\u738b\u9ad8\u79d1\u6709\u4ec0\u4e48\u6253\u7b97\uff1f\u201d</p><p>\u3000\u3000\u201c\u6682\u65f6\u4e0d\u53ef\u80fd\u6709\u592a\u957f\u8fdc\u7684\u89c4\u5212\uff0c\u5c31\u7b97\u60f3\u51fa\u5947\u5236\u80dc\uff0c\u4e5f\u8981\u6709\u51fa\u5947\u5236\u80dc\u7684\u57fa\u7840\u624d\u884c\uff0c\u6211\u4eec\u624b\u91cc\u8fde\u5f20\u624b\u673a\u751f\u4ea7\u724c\u7167\u90fd\u6ca1\u6709\uff0c\u52a8\u4f5c\u592a\u5927\uff0c\u6307\u4e0d\u5b9a\u7ed9\u4eba\u5bb6\u505a\u4e86\u5ac1\u8863\uff0c\u8054\u4fe1\u4e5f\u4e0d\u662f\u6ca1\u6709\u5361\u6211\u4eec\u8116\u5b50\u7684\u624b\u6bb5\u3002\u773c\u4e0b\u53ea\u6709\u6162\u6162\u7684\u592f\u5b9e\u8fd9\u4e2a\u57fa\u7840\uff0c\u9526\u6e56\u603b\u4e4b\u4e0d\u4f1a\u592a\u5c0f\u5bb6\u5b50\u6c14\u7684\u5bf9\u56fd\u5185\u7684\u540c\u884c\u65a4\u65a4\u8ba1\u8f83\u2026\u2026\u201d\u5373\u4f7f\u4e00\u672c\u6b63\u7ecf\u7684\u8c08\u516c\u53f8\u7684\u4e8b\u60c5\uff0c\u8c22\u5b50\u5609\u4e5f\u662f\u76d8\u7740\u817f\u8737\u5750\u5728\u6c99\u53d1\uff0c\u9732\u51fa\u767d\u751f\u751f\u7684\u5149\u811a\u4e2b\u5b50\u3002</p><p>\u3000\u3000\u201c\u4f60\u5012\u662f\u5bf9\u90a3\u4e2a\u5bb6\u4f19\u5f88\u6709\u4fe1\u5fc3\u554a\u2026\u2026\u201d\u8c22\u610f\u7aef\u7740\u70ed\u817e\u817e\u7684\u9e2d\u8840\u7c89\u4e1d\u6c64\u63a8\u95e8\u8fdb\u6765\uff0c\u5c06\u5851\u6599\u888b\u89e3\u5f00\u653e\u5230\u9910\u684c\u4e0a\uff0c\u8fd8\u5e2e\u5fd9\u62ff\u6765\u7b77\u5b50\uff0c\u4f3a\u5019\u8fd9\u5c0f\u59d1\u5976\u5976\uff0c\u4ed6\u4e5f\u8981\u5c0f\u5fc3\u4e9b\uff0c\u751f\u6015\u4ed6\u4e0d\u5982\u610f\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4eec\u62c5\u5fc3\u4ec0\u4e48\uff1f\u201d\u8c22\u5b50\u5609\u6b6a\u7740\u8111\u888b\u7591\u60d1\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u8c22\u610f\u54b3\u55fd\u4e86\u4e24\u58f0\uff0c\u5fc3\u60f3\u8fd9\u5c0f\u59d1\u5976\u5976\u53ef\u80fd\u8fd8\u4e0d\u77e5\u9053\u5468\u747e\u73ba\u8fd9\u65f6\u5019\u8eba\u5728\u533b\u9662\u91cc\u4e0d\u9192\u4eba\u4e8b\u5462\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u7b97\u662f\u5012\u4e86\uff0c\u4f46\u662f\u8c22\u610f\u8fd8\u62c5\u5fc3\u5f20\u606a\u8fd9\u5bb6\u4f19\u63a5\u4e0b\u6765\u53ef\u80fd\u5bf9\u8c22\u5bb6\u91c7\u53d6\u7684\u52a8\u4f5c\uff0c\u5f7c\u6da8\u6b64\u6d88\u8fd9\u4e9b\u5e74\uff0c\u9526\u6e56\u7684\u5d1b\u8d77\u582a\u7528\u5947\u8ff9\u6765\u5f62\u5bb9\uff0c\u6b64\u65f6\u7684\u6b63\u6cf0\u96c6\u56e2\u53ef\u4ee5\u8bf4\u51e0\u4e4e\u6ca1\u6709\u591a\u5c11\u4f59\u529b\u6765\u62b5\u6297\u9526\u6e56\u6b63\u9762\u6253\u51fb\u3002</p><p>\u3000\u3000\u9526\u6e56\u5bf9\u5e02\u573a\u7684\u63a7\u5236\u80fd\u529b\u81ea\u4e0d\u7528\u53bb\u8bf4\uff0c\u80cc\u540e\u5b98\u573a\u4e0a\u7684\u89d2\u529b\uff0c\u6b63\u6cf0\u96c6\u56e2\u4e5f\u5168\u65e0\u62db\u67b6\u4e4b\u529b\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u6d89\u6848\uff0c\u5468\u5bb6\u5728\u4e2d\u592e\u9ad8\u5c42\u7684\u4eba\u8109\u7b97\u662f\u5f7b\u5e95\u65ad\u4e86\uff0c\u5468\u747e\u745c\u5c31\u7b97\u7ecf\u5f97\u8d77\u8003\u9a8c\uff0c\u4f46\u5979\u5728\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u4e0a\u5e72\u6ee1\u4e00\u4efb\u7684\u53ef\u80fd\u6027\u4e5f\u662f\u6781\u5fae\uff0c\u5f88\u53ef\u80fd\u7b49\u6c5f\u5357\u7701\u7684\u653f\u6cbb\u7ecf\u6d4e\u5c40\u9762\u7a33\u5b9a\u4e0b\u6765\u5c31\u4f1a\u7ed9\u95f2\u7f6e\uff0c\u8fd9\u8fd8\u8981\u5468\u747e\u745c\u81ea\u5df1\u4e0d\u51fa\u5dee\u9519\u3002\u6b63\u6cf0\u96c6\u56e2\u7684\u4e3b\u8981\u6839\u57fa\u8fd8\u662f\u5728\u4e1c\u6d77\u7701\uff0c\u5728\u4e1c\u6d77\u7701\u5730\u65b9\u4e0a\u7684\u5f71\u54cd\u529b\uff0c\u6b63\u6cf0\u96c6\u56e2\u73b0\u5728\u90fd\u6ca1\u6709\u8d44\u683c\u7ed9\u9526\u6e56\u63d0\u978b\u4e86\uff0c\u65e0\u8bba\u662f\u7701\u91cc\u8fd8\u662f\u5730\u65b9\uff0c\u4e3b\u8981\u515a\u653f\u9886\u5bfc\u90fd\u4e0d\u53ef\u80fd\u9009\u62e9\u7ad9\u5230\u9526\u6e56\u7684\u5bf9\u7acb\u9762\u53bb\u3002\u5f20\u606a\u90a3\u5bb6\u4f19\u82e5\u771f\u8981\u5bf9\u8c22\u5bb6\u505a\u4ec0\u4e48\u5c0f\u52a8\u4f5c\uff0c\u5730\u65b9\u7684\u52bf\u529b\u5927\u6982\u4f1a\u4e00\u9762\u5012\u7684\u6295\u5411\u9526\u6e56\u3001\u8054\u5408\u8d77\u6765\u7ed9\u6b63\u6cf0\u5c0f\u978b\u7a7f\u3002</p><p>\u3000\u3000\u8c22\u5bb6\u4ee5\u540e\u7684\u8def\u8981\u600e\u4e48\u8d70\uff1f</p><p>\u3000\u3000\u8fd9\u4e2a\u95ee\u9898\uff0c\u4e5f\u662f\u8c22\u6c49\u9756\u6b64\u65f6\u8003\u8651\u7684\u95ee\u9898\uff0c\u5468\u5bb6\u7b97\u662f\u5f7b\u5e95\u6ca1\u843d\u4e86\uff0c\u8c22\u5bb6\u56db\u5e74\u524d\u7ed9\u5468\u747e\u73ba\u62d6\u4e0b\u6c34\uff0c\u8c22\u6c49\u9756\u8fd9\u6b21\u5f53\u7136\u4e0d\u7518\u5fc3\u518d\u7ed9\u62d6\u53bb\u5f53\u966a\u846c\u3002</p><p>\u3000\u3000\u79d1\u738b\u9ad8\u79d1\u4e0e\u9526\u6e56\u7684\u5173\u7cfb\u5012\u662f\u6ca1\u6709\u4efb\u4f55\u6539\u53d8\u7684\u8ff9\u8c61\uff0c\u662f\u4e0d\u662f\u610f\u5473\u7740\u9526\u6e56\u4e5f\u53ef\u80fd\u5bf9\u8c22\u5bb6\u653e\u4e00\u9a6c\uff1f\u8c22\u6c49\u9756\u5012\u4e5f\u6ca1\u6709\u8ba9\u5b50\u5609\u53bb\u8bd5\u63a2\u9526\u6e56\u7684\u53e3\u98ce\uff0c\u8c22\u610f\u5374\u662f\u5728\u65c1\u8fb9\u6b32\u8a00\u53c8\u6b62\u3002\u8fd9\u4f1a\u513f\u6401\u5728\u684c\u4e0a\u7684\u624b\u673a\u9707\u52a8\u8d77\u6765\uff0c\u8c22\u5b50\u5609\u62ff\u8d77\u624b\u673a\u770b\u4e86\u4e00\u4e0b\u53f7\u7801\u663e\u793a\uff0c\u8bf4\u9053\uff1a\u201c\u9648\u9759\u6253\u8fc7\u6765\u7684\u7535\u8bdd\uff0c\u5979\u665a\u4e0a\u8981\u4ece\u5317\u4eac\u4e58\u98de\u673a\u56de\u5efa\u90ba\u7684\uff0c\u8fd9\u4f1a\u513f\u5e94\u8be5\u4e0b\u98de\u673a\u4e86\u2026\u2026\u201d\u63a5\u901a\u7535\u8bdd\u624d\u5f97\u77e5\u9648\u9759\u9a6c\u4e0a\u5c31\u8981\u5230\u5b66\u5e9c\u5df7\uff0c\u8ba9\u5979\u4e0b\u697c\u6765\u4e00\u8d77\u5230\u5b66\u5e9c\u5df7\u627e\u5bb6\u9910\u5385\u5403\u996d\uff0c\u56fd\u5185\u822a\u73ed\u4e0a\u63d0\u4f9b\u7684\u4f19\u98df\u7b80\u76f4\u5c31\u4e0d\u80fd\u5403\u3002</p><p>\u3000\u3000\u8c22\u5b50\u5609\u6302\u4e86\u7535\u8bdd\u5c31\u5f00\u59cb\u8d76\u4eba\u4e86\uff1a\u201c\u9648\u9759\u9a6c\u4e0a\u56de\u6765\uff0c\u6211\u8981\u8d76\u7740\u5148\u6d17\u4e2a\u6fa1\uff0c\u7b49\u4f1a\u513f\u4e0b\u697c\u53bb\u627e\u5979\u8981\u597d\u597d\u7684\u7292\u52b3\u6211\u4e00\u987f\u2026\u2026\u8fd9\u4e9b\u5929\u95fb\u5230\u9e2d\u8840\u5473\u5c31\u60f3\u5410\uff0c\u9ebb\u70e6\u4f60\u4eec\u8d76\u7d27\u6253\u5305\u5e26\u8d70\uff0c\u4e0d\u8981\u7ed9\u8fd9\u5c4b\u5b50\u518d\u6dfb\u5473\u9053\u4e86\u2026\u2026\u201d\u8bf4\u7740\u5c31\u8fdb\u6d74\u5ba4\u6d17\u6fa1\u53bb\u4e86\u3002</p><p>\u3000\u3000\u8c22\u610f\u9887\u4e3a\u65e0\u5948\u7684\u5c06\u9e2d\u8840\u7c89\u4e1d\u518d\u6253\u597d\u5305\u653e\u51b0\u7bb1\u91cc\u53bb\uff0c\u4e0e\u7236\u4eb2\u4e0b\u697c\u79bb\u5f00\uff0c\u5f00\u8f66\u7ecf\u8fc7\u5b66\u5e9c\u770b\u89c1\u5f20\u606a\u5750\u5728\u8857\u8fb9\u7684\u957f\u6905\u4e0a\u6b63\u8ddf\u4eba\u901a\u7535\u8bdd\uff0c\u8fd8\u770b\u5230\u5085\u4fca\u4e0e\u4e24\u540d\u7a7f\u7740\u4fbf\u670d\u7684\u9752\u5e74\u7ad9\u5728\u4e0d\u8fdc\u5904\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e0b\u5348\u6709\u7a7a\u95f2\u65f6\u95f4\uff0c\u5f20\u606a\u7279\u610f\u5728\u6e56\u7554\u5c0f\u5c4b\u4eb2\u81ea\u4e0b\u53a8\u51c6\u5907\u4e86\u51e0\u6837\u83dc\uff0c\u7528\u8fc7\u9910\u8fd8\u6bb7\u52e4\u5c06\u7897\u789f\u6536\u62fe\u5e72\u51c0\u3002\u6536\u62fe\u5e72\u51c0\u540e\uff0c\u8bb8\u601d\u4e0e\u665a\u6674\u8bf4\u8981\u4f11\u606f\u4e86\uff0c\u5c31\u5c06\u4ed6\u7ed9\u8d76\u4e86\u51fa\u6765\u3002</p><p>\u3000\u3000\u770b\u6765\u68a6\u60f3\u90fd\u662f\u597d\u7684\uff0c\u4f46\u662f\u8ddf\u73b0\u5b9e\u8fd8\u662f\u6709\u76f8\u5f53\u5dee\u8ddd\u7684\uff0c\u60f3\u4e0d\u660e\u767d\u8bb8\u601d\u80fd\u8ba9\u665a\u6674\u7559\u4e0b\u6765\uff0c\u5374\u504f\u504f\u8981\u5c06\u81ea\u5df1\u8d76\u51fa\u6765\uff0c\u8bf4\u4e0d\u5b9a\u9694\u4e24\u5929\u5c31\u90fd\u80fd\u7559\u5728\u6e56\u7554\u5c0f\u5c4b\u91cc\u3002\u5f20\u606a\u671f\u76fc\u7740\uff0c\u767e\u65e0\u804a\u8d56\u53ea\u6709\u5728\u5b66\u5e9c\u5df7\u7684\u8857\u5934\u95f2\u901b\uff0c\u8e22\u7740\u8857\u5934\u7684\u7a7a\u6613\u62c9\u7f50\uff0c\u76f4\u5230\u73af\u536b\u5de5\u4eba\u5c06\u6613\u62c9\u7f50\u6361\u8d70\u3002\u4ed6\u8fd8\u8bb0\u5f97\u5728\u4ed6\u53bb\u9999\u6e2f\u7684\u524d\u591c\u5728\u5df7\u5b50\u53e3\u4e00\u5bb6\u996e\u98df\u644a\u5403\u4e86\u4e00\u7c92\u68d5\u5b50\u3001\u559d\u4e86\u4e00\u676f\u5496\u5561\u6ca1\u6709\u4ed8\u94b1\u7ed9\u4eba\u5bb6\uff0c\u8fd9\u65f6\u95f4\u8fd8\u65e9\uff0c\u5df7\u5b50\u53e3\u7684\u591c\u644a\u8fd8\u6ca1\u6709\u6446\u51fa\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u7740\u5c06\u56db\u5143\u94b1\u8fd8\u7ed9\u4eba\u5bb6\u644a\u4e3b\uff0c\u5c31\u6ca1\u6709\u6025\u7740\u56de\u53bb\uff0c\u5750\u5728\u8857\u8fb9\u7684\u957f\u6905\u4e0a\u63a5\u9648\u4fe1\u751f\u4ece\u5317\u4eac\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4e5f\u6ca1\u6709\u6ce8\u610f\u5230\u8c22\u6c49\u9756\u5750\u8f66\u4e24\u6b21\u7ecf\u8fc7\u3002</p><p>\u3000\u3000\u5218\u660e\u8f89\u8ddf\u7740\u9648\u9759\u8eab\u540e\u4e0b\u4e86\u51fa\u79df\u8f66\uff0c\u770b\u7740\u5750\u5728\u8857\u8fb9\u957f\u6905\u4e0a\u770b\u4e0a\u53bb\u6709\u4e9b\u72af\u50bb\u7684\u5f20\u606a\uff0c\u5fc3\u91cc\u767e\u611f\u4ea4\u96c6\uff0c\u8c01\u80fd\u60f3\u5230\u9526\u6e56\u80fd\u6709\u6b64\u65f6\u7684\u8f89\u714c\uff1f</p><p>\u3000\u3000\u9648\u9759\u6b23\u559c\u7684\u8d70\u8fc7\u53bb\u8ddf\u5f20\u606a\u6253\u62db\u547c\uff0c\u5218\u660e\u8f89\u5219\u89c9\u5f97\u6b65\u5c65\u6c89\u91cd\uff0c\u4e5f\u6015\u4e0a\u524d\u62db\u547c\u4e0d\u7ed9\u642d\u7406\uff0c\u4fbf\u4e0e\u9648\u9759\u7684\u52a9\u7406\u7ad9\u5728\u8fdc\u5904\u7b49\u7740\u3002</p><p>\u3000\u3000\u6709\u4e9b\u6069\u6028\uff0c\u4f1a\u8ba9\u4eba\u94ed\u5fc3\u523b\u9aa8\uff0c\u6709\u4e9b\u6069\u6028\uff0c\u5f20\u606a\u5219\u770b\u5f97\u5f88\u6de1\u3002\u5f20\u606a\u671d\u5218\u660e\u8f89\u5fae\u5fae\u9894\u9996\u800c\u7b11\uff0c\u7b97\u662f\u6253\u8fc7\u62db\u547c\uff0c\u4ef0\u7740\u5934\u770b\u7740\u4fcf\u751f\u751f\u7ad9\u5728\u8ddf\u524d\u7684\u9648\u9759\uff1a\u201c\u4f60\u600e\u4e48\u4f1a\u5728\u5efa\u90ba\uff1f\u201d</p><p>\u3000\u3000\u201c\u4ece\u5317\u4eac\u98de\u56de\u6765\uff0c\u521a\u4e0b\u98de\u673a\uff0c\u7b49\u7740\u627e\u5b50\u5609\u4e00\u8d77\u5403\u665a\u996d\u5462\uff0c\u5012\u662f\u6ca1\u6709\u60f3\u5230\u4f60\u4f1a\u5b64\u96f6\u96f6\u7684\u4e00\u4e2a\u4eba\u5750\u5728\u8fd9\u91cc\u53d1\u6123\u2026\u2026\u201d\u9648\u9759\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u7b49\u7740\u8fd8\u4eba\u5bb6\u94b1\uff0c\u201d\u5f20\u606a\u53c8\u4e0d\u80fd\u8ddf\u9648\u9759\u8bc9\u82e6\u521a\u7ed9\u4e24\u4e2a\u5973\u4eba\u65e0\u60c5\u7684\u8d76\u51fa\u95e8\u6765\uff0c\u63c9\u4e86\u63c9\u8116\u5b50\uff0c\u671d\u65c1\u8fb9\u8ba9\u4e86\u8ba9\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u8fd8\u662f\u5750\u4e0b\u6765\u8bf4\u8bdd\u5427\uff0c\u8116\u5b50\u8fd9\u4e48\u62ac\u7740\u4f1a\u6bd4\u8f83\u9178\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u8eab\u9ad8\u63a5\u8fd1\u4e00\u7c73\u4e03\uff0c\u518d\u7a7f\u4e0a\u9ad8\u8ddf\u978b\uff0c\u89c6\u89c9\u4e0a\u66f4\u52a0\u7684\u633a\u62d4\uff0c\u7a7f\u7740\u4e73\u767d\u8272\u7684\u4e2d\u8896\u5706\u9886\u886c\u886b\uff0c\u4ece\u4e0b\u5f80\u4e0a\u4ef0\u89c6\u4f1a\u5c24\u5176\u89c9\u5f97\u53cc\u5cf0\u7684\u633a\u62d4\u79c0\u7acb\uff0c\u4f46\u5f85\u9648\u9759\u5750\u4e0b\uff0c\u5c31\u80fd\u77a5\u89c1\u5fae\u9732\u7684\u767d\u5ae9\u4e73\u6c9f\uff0c\u4e0b\u8eab\u662f\u84dd\u8272\u53ca\u819d\u5957\u88d9\u4e0e\u8089\u8272\u4e1d\u889c\u4e0e\u7ec6\u6839\u978b\uff0c\u4f7f\u5f97\u5979\u7684\u4fee\u957f\u5c0f\u817f\u770b\u4e0a\u53bb\u66f4\u5177\u9b45\u529b\u3002</p><p>\u3000\u3000\u201c\u4f60\u4f1a\u6b20\u4eba\u5bb6\u94b1\uff1f\u201d\u9648\u9759\u95ee\u9053\uff0c\u6df1\u9083\u900f\u7740\u795e\u79d8\u5149\u91c7\u7684\u773c\u7738\u5b50\u51dd\u671b\u7740\u5f20\u606a\u7684\u8138\u988a\uff0c\u5f85\u4ed6\u4fa7\u8fc7\u5934\u6765\uff0c\u53c8\u5047\u88c5\u65e0\u610f\u7684\u79fb\u5f00\uff0c\u6ce8\u610f\u5230\u5f20\u606a\u7684\u76ee\u5149\u4f3c\u4e4e\u843d\u5728\u81ea\u5df1\u7684\u9888\u8116\u4e0a\uff0c\u7adf\u60c5\u4e0d\u81ea\u7981\u7684\u89c9\u5f97\u90a3\u91cc\u6709\u4e9b\u53d1\u70ed\uff0c\u4f38\u624b\u6478\u4e86\u4e00\u4e0b\uff0c\u53c8\u4fa7\u8fc7\u5934\u6765\u95ee\u5f20\u606a\uff0c\u201c\u8fd9\u91cc\u6709\u4ec0\u4e48\uff1f\u201d</p><p>\u3000\u3000\u201c\u6709\u6839\u5934\u53d1\u4e1d\u2026\u2026\u201d\u4f17\u76ee\u777d\u777d\u4e4b\u4e0b\uff0c\u5f20\u606a\u53c8\u4e0d\u80fd\u4f38\u624b\u53bb\u5e2e\u9648\u9759\u5c06\u5979\u8116\u6897\u4e0a\u7684\u90a3\u6839\u9752\u4e1d\u62c8\u8d77\uff0c\u53c8\u4e0d\u80fd\u5634\u51d1\u5230\u90a3\u80a4\u5982\u51dd\u8102\u7684\u8116\u6897\u4e0a\u5c06\u90a3\u6839\u9752\u4e1d\u5439\u6389\uff0c\u5f20\u606a\u5fc3\u91cc\u867d\u7136\u53d7\u4e86\u8fd9\u6837\u7684\u8bf1\u60d1\uff0c\u8fd8\u662f\u53ea\u62ff\u8a00\u8bed\u6307\u6325\u9648\u9759\u81ea\u5df1\u5c06\u90a3\u6839\u65ad\u53d1\u6361\u6389\u3002</p></article>"}}}]);
