(window.webpackJsonp=window.webpackJsonp||[]).push([[103],{126:function(p){p.exports={chapterTitle:null,title:"\u7b2c103\u7ae0 \u7a81\u5982\u5176\u6765\u7684\u6e29\u67d4",content:"<article><h2>\u7b2c103\u7ae0 \u7a81\u5982\u5176\u6765\u7684\u6e29\u67d4</h2><p>\u3000\u3000\u4e5d\u56db\u5e74\uff0c\u56fd\u5185\u653f\u5e9c\u8fd8\u4e0d\u4e60\u60ef\u5f00\u8bda\u5e03\u516c\u7684\u9762\u5bf9\u5a92\u4f53\u3001\u516c\u4f17\u3002\u5176\u5b9e\u8fd9\u4e00\u70b9\uff0c\u5728\u4eca\u4e14\u5341\u51e0\u5e74\u7684\u65f6\u95f4\u91cc\uff0c\u90fd\u6ca1\u6709\u5f97\u5230\u6839\u672c\u6027\u7684\u6539\u5584\u3002</p><p>\u3000\u3000\u5e02\u59d4\u4e66\u8bb0\u8c04\u5bb3\u526f\u5e02\u957f\u7684\u60c5\u8282\u4e00\u7ecf\u516c\u5e03\uff0c\u65e0\u7591\u4f1a\u5f15\u8d77\u56fd\u5185\u5916\u5a92\u4f53\u6781\u5927\u7684\u5174\u8da3\uff0c\u65e0\u8bba\u54ea\u65b9\u9762\uff0c\u90fd\u4e0d\u5e0c\u671b\u5a92\u4f53\u7ea0\u7f20\u8fd9\u79cd\u4ee4\u4eba\u96be\u582a\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u51fa\u4e8e\u8fd9\u79cd\u8003\u8651\uff0c\u4e0d\u53ef\u80fd\u516c\u5f00\u5ba1\u7406\u6b64\u6848\uff0c\u4e00\u4e9b\u7ec6\u8282\u4e5f\u4e0d\u4f1a\u5411\u5a92\u4f53\u516c\u5e03\u3002</p><p>\u3000\u3000\u5373\u4f7f\u5386\u53f2\u4e0d\u53d1\u751f\u9006\u8f6c\uff0c\u4e01\u5411\u5c71\u62d6\u5ef6\u5230\u4e94\u5e74\u540e\u518d\u9677\u5165\u7262\u72f1\uff0c\u5173\u4e8e\u9677\u5bb3\u5510\u5b66\u8c26\u7684\u7ec6\u8282\u4e5f\u6ca1\u6709\u5411\u516c\u4f17\u4e0e\u5a92\u4f53\u516c\u5e03\uff0c\u5e02\u6c11\u53ea\u662f\u9053\u542c\u9014\u8bf4\u7684\u6d41\u4f20\u5404\u79cd\u7248\u672c\u7684\u8c23\u8a00\u3002</p><p>\u3000\u3000\u897f\u971e\u533a\u68c0\u5bdf\u9662\u4f1a\u53e6\u5916\u6d3e\u68c0\u5bdf\u5b98\u8d76\u5f80\u6d77\u5dde\u5411\u5510\u5b66\u8c26\u3001\u5f20\u77e5\u884c\u7b49\u4eba\u8046\u8baf\u76f8\u5173\u6848\u60c5\uff0c\u5f20\u606a\u4e5f\u53ef\u4ee5\u4e0d\u7528\u5230\u7701\u57ce\u6765\u3002\u8003\u8651\u5230\u8bb8\u601d\u7684\u60c5\u7eea\uff0c\u5f20\u606a\u501f\u53e3\u4ed6\u4e0d\u9700\u8981\u641e\u7279\u6b8a\u5316\uff0c\u5411\u5b66\u6821\u8bf7\u4e86\u4e09\u5929\u5047\uff0c\u642d\u5e02\u91cc\u5230\u7701\u57ce\u516c\u5e72\u7684\u516c\u8f66\uff0c15\u65e5\u4e0b\u5348\u62b5\u8fbe\u7701\u57ce\u3002</p><p>\u3000\u300016\u65e5\uff0c\u5f20\u606a\u662f\u7531\u7701\u68c0\u5bdf\u9662\u526f\u68c0\u5bdf\u957f\u91d1\u56fd\u6d77\u4eb2\u81ea\u9001\u5230\u897f\u971e\u533a\u68c0\u5bdf\u9662\u7684\u63a5\u53d7\u8046\u8baf\uff0c\u5bf9\u4e0e\u4ed6\u76f8\u5173\u7684\u6848\u60c5\u8fdb\u884c\u590d\u6838\u3002</p><p>\u3000\u3000\u8bb8\u601d\u4e5f\u662f\u8fd9\u4e00\u5929\u63a5\u53d7\u897f\u971e\u533a\u68c0\u5bdf\u9662\u7684\u8046\u8baf\uff0c\u4e0e\u5f20\u606a\u5b89\u6170\u5979\u7684\u8bdd\u4e00\u6837\uff0c\u6574\u4e2a\u8fdb\u7a0b\u975e\u5e38\u987a\u5229\uff0c\u6ca1\u6709\u4eba\u5e0c\u671b\u7ea0\u7f20\u8fd9\u4e2a\u7ec6\u8282\uff0c\u4e00\u5929\u5c31\u5b8c\u6210\u5ba1\u67e5\u3002\u5f53\u591c\uff0c\u5f20\u606a\u7167\u4f8b\u4f4f\u5728\u5f90\u5b66\u5e73\u5bb6\uff0c\u8bb8\u601d\u5728\u5bbe\u9986\u67af\u5750\u4e86\u4e00\u591c\uff0c\u5979\u6765\u7701\u57ce\u524d\uff0c\u6ca1\u6709\u8ddf\u5bb6\u91cc\u8bf4\u8d77\u7f18\u7531\uff0c\u53ea\u8bf4\u5230\u7701\u57ce\u51fa\u5dee\u3002\u8bb8\u601d\u4e5f\u6ca1\u6709\u8ba9\u8bb8\u7ef4\u77e5\u9053\u5979\u6765\u7701\u57ce\uff0c\u5979\u4e0d\u662f\u80fd\u63a9\u9970\u60c5\u7eea\u7684\u4eba\u3002</p><p>\u3000\u3000\u7b2c\u4e8c\u5929\uff0c\u897f\u971e\u533a\u68c0\u5bdf\u9662\u7ecf\u68c0\u5bdf\u59d4\u5458\u4f1a\u8ba8\u8bba\uff0c\u5c31\u505a\u51fa\u4e0d\u8d77\u8bc9\u51b3\u5b9a\uff0c\u62a5\u7ecf\u4e0a\u4e00\u7ea7\u68c0\u5bdf\u9662\u6279\u51c6\u4e4b\u540e\uff0c\u4f1a\u5c06\u4e0d\u8d77\u8bc9\u51b3\u5b9a\u4e66\u9001\u8fbe\u76f8\u5173\u5f53\u4e8b\u4eba\u53ca\u5355\u4f4d\u3002</p><p>\u3000\u3000\u8d70\u51fa\u897f\u971e\u533a\u68c0\u5bdf\u9662\uff0c\u8bb8\u601d\u5a07\u8273\u7684\u8138\u8ba9\u6e05\u51b7\u7684\u98ce\u5439\u5f97\u6108\u53d1\u7684\u767d\uff0c\u90a3\u79cd\u892a\u5c3d\u8840\u8272\u7684\u767d\uff0c\u4eff\u4f5b\u5165\u51ac\u540e\u7684\u521d\u96ea\uff0c\u773c\u775b\u6e05\u4eae\u800c\u660e\u5a9a\uff0c\u7a7f\u7740\u5ae9\u9ec4\u8272\u7684\u7f69\u8863\u3002\u770b\u89c1\u5f20\u606a\u7ad9\u5728\u8857\u5bf9\u9762\uff0c\u8d70\u8fc7\u6765\uff0c\u4f38\u624b\u6402\u4f4f\u4ed6\u7684\u80f3\u818a\uff0c\u8eab\u4f53\u6b62\u4e0d\u4f4f\u7684\u98a4\u6296\uff0c\u773c\u6cea\u4e0d\u505c\u7684\u6ef4\u843d\u3002</p><p>\u3000\u3000\u8857\u4e0a\u7684\u884c\u4eba\u5f88\u5c11\uff0c\u67cf\u6cb9\u8def\u51b0\u5f97\u53d1\u767d\uff0c\u6c14\u6e29\u4e00\u4e0b\u5b50\u5c31\u964d\u5230\u96f6\u5ea6\u4ee5\u4e0b\uff0c\u5357\u65b9\u7684\u6e7f\u5ea6\u53c8\u9ad8\uff0c\u523a\u6ed1\u7684\u6e7f\u51b7\u8ba9\u4eba\u96be\u4ee5\u5fcd\u53d7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5c06\u8bb8\u601d\u6402\u8fdb\u6000\u91cc\uff0c\u4e0b\u5df4\u538b\u7740\u5979\u5149\u6d01\u7684\u989d\u5934\uff0c\u8ba9\u5979\u5728\u81ea\u5df1\u7684\u6000\u91cc\u9759\u9759\u7684\u54ed\u6ce3\uff0c\u8eab\u4f53\u7ed9\u8bb8\u601d\u53cd\u624b\u62b1\u4f4f\uff0c\u8bb8\u601d\u5f88\u7528\u529b\uff0c\u7ed9\u5979\u52d2\u5f97\u6709\u4e9b\u75db\uff0c\u5f20\u606a\u4e5f\u611f\u5230\u83ab\u540d\u7684\u60b2\u4f24\u3002</p><p>\u3000\u3000\u201c\u6211\u4eec\u518d\u7559\u4e00\u5929\uff1f\u201d\u5f20\u606a\u4f4e\u5934\u95ee\u8bb8\u601d\u3002</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u8bb8\u601d\u7d27\u7d27\u62b1\u7740\u5f20\u606a\uff0c\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u79bb\u4e0d\u5f00\u8fd9\u4e2a\u5c11\u5e74\uff0c\u6ca1\u6709\u4ed6\uff0c\u81ea\u5df1\u5927\u6982\u5c31\u6ca1\u6709\u4e00\u70b9\u529b\u6c14\u80fd\u7ad9\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u6253\u7b97\u4eca\u5929\u5c31\u56de\u6d77\u5dde\u7684\uff0c\u79bb\u5f00\u5f90\u5b66\u5e73\u5bb6\uff0c\u8fd8\u90d1\u91cd\u5176\u4e8b\u7684\u9053\u4e86\u522b\u3002\u89c1\u8bb8\u601d\u7684\u60c5\u7eea\u4e0d\u7a33\u5b9a\uff0c\u5f20\u606a\u51b3\u5b9a\u5728\u7701\u57ce\u518d\u7559\u4e00\u5929\uff0c\u966a\u5979\u6563\u6563\u5fc3\u3002\u7ed9\u5bb6\u91cc\u6302\u4e86\u7535\u8bdd\uff0c\u672c\u6765\u7b54\u5e94\u4eca\u5929\u665a\u4e0a\u8981\u56de\u5bb6\u5403\u996d\u7684\uff0c\u4e4b\u540e\u53c8\u7ed9\u665a\u6674\u6302\u4e86\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5728\u8857\u89d2\u627e\u4e86\u4e00\u5bb6\u5496\u5561\u9986\u7b49\u5927\u534a\u4e2a\u5c0f\u65f6\uff0c\u8c22\u665a\u6674\u624d\u5f00\u8f66\u8fc7\u6765\u63a5\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u201c\u4e00\u63a5\u5230\u7535\u8bdd\uff0c\u6211\u5c31\u8d76\u8fc7\u6765\u4e86\uff0c\u987a\u8def\u628a\u8bb8\u7ef4\u9001\u56de\u5b66\u6821\uff0c\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u4e45\u4e86\u5427\uff1f\u201d</p><p>\u3000\u3000\u5750\u5230\u8c22\u665a\u6674\u8f66\u4e0a\uff0c\u624d\u77e5\u9053\u5979\u662f\u5728\u8def\u4e0a\u803d\u6401\u4e86\u8fd9\u4e48\u957f\u65f6\u95f4\uff0c\u5f20\u606a\u63a2\u5934\u770b\u4e86\u770b\uff0c\u8f66\u901f\u8fd8\u6ca1\u6709\u8fc7\u4e09\u5341\u8fc8\uff0c\u7ec6\u60f3\u8d77\u6765\uff0c\u4e4b\u524d\u8fd8\u6ca1\u6709\u770b\u5230\u8fc7\u5979\u4eb2\u81ea\u5f00\u8f66\u7684\u65f6\u5019\uff0c\u771f\u662f\u96be\u4e3a\u5979\u4e86\u3002</p><p>\u3000\u3000\u751f\u6d3b\u6062\u590d\u6b63\u5e38\u4e4b\u540e\uff0c\u8c22\u665a\u6674\u4e0e\u82b7\u5f64\u5355\u72ec\u4f4f\u5728\u79bb\u4e1c\u6d77\u5927\u5b66\u4e0d\u8fdc\u7684\u7535\u68af\u516c\u5bd3\u91cc\uff0c\u53ea\u6709\u5468\u672b\u65f6\u624d\u5c06\u82b7\u5f64\u9001\u5230\u65b0\u6885\u82d1\u966a\u5f90\u5b66\u5e73\u3001\u5468\u53d4\u60e0\u3002</p><p>\u3000\u3000\u4fdd\u59c6\u4eca\u5929\u8bf7\u5047\uff0c\u8fd8\u7279\u610f\u8ba9\u8bb8\u7ef4\u6765\u7167\u987e\u82b7\u5f64\uff0c\u8fd9\u4f1a\u513f\u53c8\u5c06\u8bb8\u7ef4\u9001\u56de\u53bb\u3002</p><p>\u3000\u3000\u201c\u662f\u4e0d\u662f\u5148\u627e\u5730\u65b9\u5403\u70b9\u4e1c\u897f\uff1f\u201d\u8c22\u665a\u6674\u5c06\u8f7f\u8f66\u9a76\u5165\u5730\u4e0b\u8f66\u5e93\uff0c\u5dee\u4e0d\u591a\u5feb\u5230\u4e2d\u5348\u7684\u65f6\u95f4\uff0c\u201c\u5bb6\u91cc\u53ea\u6709\u9762\u6761\u3001\u901f\u51bb\u6c34\u997a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u8bb8\u601d\u4e00\u773c\uff0c\u5979\u7edd\u7f8e\u65e0\u7455\u7684\u8138\u4e0a\u72b9\u8c6b\u7740\uff0c\u5927\u6982\u4e0d\u60f3\u51fa\u53bb\u89c1\u964c\u751f\u4eba\uff0c\u8bf4\u9053\uff1a\u201c\u8981\u4e0d\u4e70\u4e9b\u4ec0\u4e48\uff0c\u6211\u4eec\u5728\u5bb6\u91cc\u81ea\u5df1\u505a\u7740\u5403\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u5c34\u5c2c\u7684\u7b11\u4e86\u7b11\uff1a\u201c\u4eca\u5929\u4fdd\u59c6\u4e0d\u5728\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u5728\u5bb6\u662f\u8001\u5c0f\uff0c\u8c22\u5bb6\u6ca1\u6709\u53d1\u5bb6\u65f6\uff0c\u5979\u4e5f\u6ca1\u6709\u5403\u8fc7\u82e6\uff0c\u5a5a\u540e\u8ba9\u5f90\u5fd7\u660e\u5ba0\u7740\uff0c\u73b0\u5728\u52c9\u5f3a\u5b66\u4f1a\u4e0b\u9762\u6761\u3002\u8c22\u665a\u6674\u4e0d\u81ea\u7136\u7684\u6413\u4e86\u6413\u624b\uff0c\u773c\u795e\u95ea\u70c1\u7740\uff0c\u767d\u7699\u7684\u8138\u62b9\u4e86\u5c42\u7ea2\u6655\u3002</p><p>\u3000\u3000\u201c\u90a3\u6211\u4eec\u4e70\u672c\u83dc\u8c31\u73b0\u5b66\u73b0\u505a\uff0c\u201d\u5f20\u606a\u6beb\u65e0\u70ab\u8000\u7684\u610f\u601d\uff0c\u8fc7\u60ef\u8863\u6765\u4f38\u624b\u3001\u996d\u6765\u5f20\u53e3\u7684\u751f\u6d3b\uff0c\u5bf9\u7b80\u5355\u7684\u83dc\u80b4\u6709\u628a\u63e1\uff0c\u590d\u6742\u7684\u83dc\u7cfb\u6709\u53c2\u4e0e\u83dc\u8c31\u7684\u9700\u8981\uff0c\u634f\u4e86\u634f\u82b7\u5f64\u7684\u5c0f\u8138\uff0c\u201c\u7b49\u4f1a\u513f\u82b7\u5f64\u4e5f\u8981\u5e2e\u5e2e\u624b\u554a\u3002\u201d</p><p>\u3000\u3000\u8c22\u665a\u6674\u8fde\u83dc\u5e02\u573a\u90fd\u6ca1\u5927\u53bb\u8fc7\uff0c\u521d\u5230\u8fd9\u4e48\u70ed\u95f9\u7684\u5730\u65b9\uff0c\u82b7\u5f64\u62fd\u7740\u5f20\u606a\u7684\u624b\uff0c\u624b\u5fc3\u91cc\u90fd\u6fe1\u51fa\u6c57\u6765\u3002\u8c22\u665a\u6674\u98ce\u97f5\u8ff7\u4eba\uff0c\u8bb8\u601d\u5a07\u67d4\u8273\u7f8e\uff0c\u52a0\u4e0a\u6f02\u4eae\u5230\u6781\u70b9\u7684\u5c0f\u5973\u5b69\uff0c\u81ea\u7136\u60f9\u4eba\u6ce8\u76ee\uff0c\u4ece\u83dc\u5e02\u573a\u8f6c\u4e86\u4e00\u5708\uff0c\u5f20\u606a\u624b\u91cc\u6367\u7740\u4e00\u5806\u98df\u6750\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u80fd\u8ba9\u8fd9\u4e48\u591a\u4eba\u770b\u7740\u773c\u998b\uff0c\u591a\u6367\u4e00\u500d\u7684\u4e1c\u897f\u90fd\u5fc3\u7518\u60c5\u613f\u3002\u201d</p><p>\u3000\u3000\u83dc\u5e02\u573a\u51fa\u53e3\u7684\u62a5\u4ead\u5c31\u90a3\u6709\u5bb6\u5e38\u83dc\u8c31\u51fa\u552e\uff0c\u8c22\u665a\u6674\u5c06\u83dc\u8c31\u4e22\u5f20\u606a\u6000\u91cc\uff1a\u201c\u522b\u987e\u7740\u80e1\u8bf4\u516b\u9053\uff0c\u5c31\u770b\u4f60\u73b0\u5b66\u73b0\u5356\u7684\u6c34\u5e73\u4e86\u3002\u201d</p><p>\u3000\u3000\u5230\u4e86\u5bb6\uff0c\u8c22\u665a\u6674\u6ca1\u6709\u5bf9\u5f20\u606a\u62b1\u592a\u5927\u7684\u671f\u671b\uff0c\u62ff\u8d77\u83dc\u8c31\u4e00\u672c\u6b63\u7ecf\u7684\u5b66\u4e86\u8d77\u6765\uff0c\u624b\u91cc\u62ff\u7740\u6839\u80e1\u841d\u535c\u6bd4\u5212\u7740\uff0c\u8ddf\u8bb8\u601d\u8ba8\u8bba\u600e\u4e48\u5207\u4e1d\u624d\u597d\u770b\u3002\u8bb8\u601d\u5fcd\u5506\u4e0d\u4f4f\uff0c\u9732\u51fa\u4eca\u5929\u7684\u7b2c\u4e00\u4e2a\u5fae\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u665a\u6674\u59d0\uff0c\u4f60\u8ba9\u5f20\u606a\u800d\u4e86\uff0c\u4ed6\u624b\u827a\u53ef\u4f60\u4f60\u73b0\u5b66\u7684\u5f3a\u3002\u201d\u4e0d\u7ecf\u610f\u7684\u773c\u6ce2\u6d41\u8f6c\uff0c\u6563\u53d1\u51fa\u4ece\u9aa8\u5b50\u91cc\u900f\u51fa\u6765\u7684\u98ce\u60c5\u3002</p><p>\u3000\u3000\u201c\u5148\u8ba9\u665a\u6674\u59d0\u73b0\u4e00\u4e0b\u4e11\u518d\u8bf4\u554a\uff0c\u201d\u5f20\u606a\u8146\u8138\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u5988\u521a\u8ddf\u6211\u7238\u7ed3\u5a5a\u90a3\u9635\u5b50\uff0c\u7b2c\u4e00\u6b21\u5356\u5f04\u624b\u827a\uff0c\u5fd8\u8bb0\u5f80\u7c73\u9505\u91cc\u52a0\u6c34\u4e86\uff0c\u5e78\u597d\u53d1\u73b0\u53ca\u65f6\uff0c\u5927\u5bb6\u8fd8\u80fd\u5403\u4e0a\u7206\u7c73\u82b1\uff0c\u6ca1\u628a\u9505\u7ed9\u6bc1\u4e86\uff1b\u6211\u8fd8\u60f3\u770b\u665a\u6674\u59d0\u6709\u4ec0\u4e48\u60ca\u4eba\u7684\u6770\u4f5c\u5462\uff01\u201d</p><p>\u3000\u3000\u8bb8\u601d\u5fcd\u5506\u4e0d\u4f4f\uff0c\u63c9\u7740\u8170\u7b11\u8d77\u6765\uff0c\u82b7\u5f64\u5174\u594b\u62fd\u7740\u5979\u5988\u5988\u7684\u8863\u8896\u6447\uff0c\u8c22\u665a\u6674\u7a81\u7136\u8138\u6d8c\u7ea2\u6f6e\uff0c\u5c06\u624b\u91cc\u80e1\u841d\u535c\u4e22\u6c34\u6c60\u91cc\uff1a\u201c\u90a3\u90fd\u4e22\u7ed9\u4f60\u505a\u597d\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u7a81\u7136\u8138\u8fd9\u4e48\u7ea2\uff0c\u4e0d\u4f1a\u4e5f\u6709\u4ec0\u4e48\u60ca\u4eba\u7684\u58ee\u4e3e\uff1f\u201d\u5f20\u606a\u773c\u76ef\u7740\u8c22\u665a\u6674\u7c89\u8138\u6e17\u8840\u4e00\u6837\u6f6e\u7ea2\uff0c\u5f88\u5c11\u89c1\u5979\u8fd9\u4e48\u5a07\u5a9a\u7684\u65f6\u5019\uff0c\u8ffd\u95ee\u5979\uff0c\u201c\u8bf4\u6765\u542c\u542c\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u8fd8\u60f3\u770b\u6211\u597d\u620f\uff1f\u201d\u8c22\u665a\u6674\u6a2a\u4e86\u4ed6\u4e00\u773c\uff0c\u62c9\u7740\u8bb8\u601d\u8bf4\uff0c\u201c\u6211\u4eec\u5230\u5ba2\u5385\u770b\u7535\u5f71\u53bb\uff0c\u8ba9\u4ed6\u5728\u53a8\u623f\u6298\u817e\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\uff0c\u81ea\u5df1\u7cfb\u7740\u56f4\u88d9\uff0c\u770b\u89c1\u8c22\u665a\u6674\u62c9\u7740\u8bb8\u601d\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u51d1\u5230\u5979\u8033\u6735\u8bf4\u4e86\u51e0\u53e5\u8bdd\uff0c\u8bb8\u601d\u7a81\u7136\u5a07\u7b11\u8d77\u6765\uff0c\u7b11\u58f0\u817b\u817b\u7684\uff0c\u63c9\u7740\u8170\u5598\u4e0d\u6c14\u6765\uff0c\u8138\u90fd\u7b11\u7ea2\u4e86\uff0c\u8c22\u665a\u6674\u4e5f\u8ddf\u7740\u7b11\u8d77\u6765\uff0c\u62b1\u7740\u82b7\u5f64\uff0c\u4e09\u4e2a\u4eba\u7b11\u6eda\u5728\u6c99\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u201c\u771f\u8fd9\u4e48\u597d\u7b11\uff1f\u201d\u5f20\u606a\u5934\u63a2\u51fa\u53a8\u623f\uff0c\u201c\u8bb8\u601d\u59d0\u8bf4\u7ed9\u6211\u542c\u542c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5973\u4eba\u5bb6\u7684\u4e8b\uff0c\u4f60\u5173\u5fc3\u4ec0\u4e48\uff1f\u201d\u8c22\u665a\u6674\u671d\u5f20\u606a\u6325\u6325\u624b\uff0c\u8f6c\u5934\u77aa\u7740\u8bb8\u601d\uff0c\u201c\u53ef\u4e0d\u8981\u544a\u8bc9\u5f20\u606a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u77e5\u9053\uff0c\u4e0d\u8bf4\u5c31\u662f\u2026\u2026\u201d\u8bb8\u601d\u63c9\u7740\u8170\uff0c\u8ba9\u547c\u5438\u987a\u8fc7\u6765\uff0c\u5267\u70c8\u7684\u7b11\uff0c\u8ba9\u5979\u7c89\u8138\u9161\u7ea2\uff0c\u6108\u53d1\u7684\u5a07\u5a9a\uff0c\u4e0d\u77e5\u4e0d\u89c9\u95f4\uff0c\u5df2\u770b\u5230\u54c0\u4f24\u7684\u795e\u8272\u3002</p><p>\u3000\u3000\u8bb8\u601d\u6536\u62fe\u5fc3\u60c5\uff0c\u5230\u53a8\u623f\u7ed9\u5f20\u606a\u6253\u4e0b\u624b\uff0c\u8c22\u665a\u6674\u4e0e\u82b7\u5f64\u4e5f\u8ddf\u7740\u8fdb\u6765\u5e2e\u5fd9\uff0c\u5e78\u597d\u53a8\u623f\u591f\u5927\uff0c\u56db\u4e2a\u4eba\u80fd\u8f6c\u5f00\u8eab\u5b50\u3002\u8bb8\u601d\u624b\u811a\u9ebb\u5229\uff0c\u4f46\u8c22\u665a\u6674\u4e0e\u82b7\u5f64\u6324\u8fdb\u6765\u975e\u4f46\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u8fd8\u6dfb\u4e86\u4e0d\u5c11\u4e71\uff0c\u60f9\u5f97\u5f20\u606a\u6700\u540e\u5c06\u5979\u4eec\u6bcd\u5973\u8d76\u51fa\u53a8\u623f\u3002</p><p>\u3000\u3000\u5dee\u4e0d\u591a\u6298\u817e\u5230\u4e0b\u5348\u4e24\u70b9\u534a\uff0c\u82b7\u5f64\u63c9\u7740\u809a\u5b50\u53ef\u601c\u516e\u516e\u7684\u76ef\u7740\u5f20\u606a\u597d\u4e00\u4f1a\u513f\uff0c\u4e00\u684c\u4e30\u76db\u7684\u5348\u9910\u624d\u51c6\u5907\u597d\uff0c\u5927\u5bb6\u90fd\u997f\u762a\u4e86\u809a\u5b50\uff0c\u6ca1\u4eba\u6709\u5fc3\u601d\u7ec6\u56bc\u6162\u54bd\u7684\u54c1\u5c1d\u5f20\u606a\u82b1\u7684\u5fc3\u601d\uff0c\u76f4\u53eb\u597d\u5403\uff0c\u4e00\u98ce\u5377\u6b8b\u4e91\u7684\u5c06\u516d\u4e2a\u83dc\u6d88\u706d\u5e72\u51c0\u3002\u7ec8\u4e86\uff0c\u8c22\u665a\u6674\u81ea\u5938\u6d17\u4e00\u624b\u597d\u7897\uff0c\u7ad9\u8d77\u6765\u5374\u5440\u5440\u76f4\u53eb\u5403\u6491\u4e86\uff0c\u4e0d\u987e\u5f62\u8c61\u7684\u63c9\u4e86\u63c9\u809a\u5b50\uff0c\u8fd8\u662f\u7531\u5f20\u606a\u4e0e\u8bb8\u601d\u5c06\u9910\u684c\u6536\u62fe\u5e72\u51c0\u3002</p><p>\u3000\u3000\u4e0b\u5348\u8c01\u90fd\u5174\u4e0d\u8d77\u8981\u51fa\u53bb\u7684\u5ff5\u5934\uff0c\u8c22\u665a\u6674\u7ed9\u516c\u53f8\u6302\u4e86\u7535\u8bdd\uff0c\u5f20\u606a\u7ed9\u848b\u8587\u6302\u4e86\u7535\u8bdd\uff0c\u8be2\u95ee\u539f\u6599\u7eb8\u6d46\u7684\u60c5\u51b5\uff0c\u95ee\u4e86\u9020\u7eb8\u5382\u6b63\u5f0f\u6295\u4ea7\u524d\u7684\u51c6\u5907\u60c5\u51b5\uff0c\u4e4b\u540e\u56db\u4e2a\u4eba\u7a9d\u5728\u6c99\u53d1\u4e0a\u770b\u789f\u7247\u3002</p><p>\u3000\u3000\u4e0d\u662f\u5361\u5e26\u5f0f\u5f55\u50cf\u673a\uff0c\u800c\u4e5d\u56db\u5e74\u521a\u521a\u9762\u5e02\u7684VCD\u673a\u3002</p><p>\u3000\u3000\u63d0\u53ca\u4f17\u6240\u5468\u77e5\u7684\u5bb6\u7535\u884c\u4e1a\uff0c\u5f20\u606a\u5f88\u81ea\u7136\u60f3\u8d77\u5bb6\u7535\u884c\u4e1a\u7684\u4e13\u5229\u4e4b\u75db\uff0c\u5728\u5341\u5e74\u4e4b\u540e\uff0c\u7a0d\u6709\u7ecf\u6d4e\u5e38\u8bc6\u7684\u4eba\u90fd\u4f1a\u4e3a\u6b64\u611f\u5230\u75db\u60dc\u3002</p><p>\u3000\u3000\u4e16\u754c\u4e0a\u7b2c\u4e00\u53f0VCD\u673a\u662f\u7531\u5b89\u5fbd\u4e07\u71d5\u96c6\u56e2\u7814\u5236\uff0c\u8fd9\u53ef\u80fd\u662f\u6d88\u8d39\u7c7b\u7535\u5b50\u9886\u57df\uff0c\u4e2d\u56fd\u9886\u5148\u4e16\u754c\u7684\u60df\u4e00\u673a\u4f1a\uff0c\u4f46\u662f\u5f53\u65f6\u4e07\u71d5\u96c6\u56e2\u7684\u638c\u95e8\u4eba\u3001\u4e5f\u662fVCD\u673a\u7814\u5236\u8005\u59dc\u4e07\u52d0\u5148\u751f\u72af\u4e0b\u4ee4\u4ed6\u540e\u6094\u7ec8\u751f\u7684\u81f4\u547d\u9519\u8bef\uff1a\u4ed6\u7adf\u7136\u8ba4\u4e3a\u5728\u5f53\u65f6\u7684\u60c5\u51b5\uff0c\u7533\u4e0d\u7533\u8bf7\u4e13\u5229\u6ca1\u6709\u592a\u5927\u7684\u610f\u4e49\uff0c\u4ee5\u81f4\u4e07\u71d5\u63a8\u51fa\u7684\u7b2c\u4e00\u62791000\u53f0VCD\u673a\uff0c\u51e0\u4e4e\u90fd\u88ab\u56fd\u5185\u5916\u5bb6\u7535\u516c\u53f8\u4e70\u53bb\u505a\u4e86\u6837\u673a\uff0c\u6210\u4e3a\u89e3\u5256\u7684\u5bf9\u8c61\u3002</p><p>\u3000\u3000\u968f\u540e\uff0c\u7d22\u5c3c\u3001\u677e\u4e0b\u3001\u98de\u5229\u6d66\u7b49\u56fd\u5916\u5236\u9020\u4e1a\u5de8\u5927\u7eb7\u7eb7\u52a0\u5f3a\u5bf9VCD\u673a\u7684\u7814\u7a76\uff0c\u63a8\u51fa\u65b0\u7684\u4e13\u5229\u6280\u672f\uff0c\u5237\u65b0VCD\u7684\u6280\u672f\u6807\u51c6\uff0c\u4ee5\u81f4\u5305\u62ec\u4e07\u71d5\u96c6\u56e2\u5728\u5185\u7684\u56fd\u5185\u5bb6\u7535\u4f01\u4e1a\u751f\u4ea7VCD\u9700\u8981\u5411\u8fd9\u4e9b\u56fd\u5916\u5de8\u5934\u7f34\u7eb3\u5de8\u989d\u7684\u4e13\u5229\u8d39\u7528\uff0c\u5728\u4e4b\u540e\u7684\u4e03\u516b\u5e74\u65f6\u95f4\u91cc\uff0c\u8fd9\u4e9b\u4e13\u5229\u8d39\u7528\u5728\u56fd\u5185\u5e02\u573a\u5c31\u7d2f\u79ef\u4e0a\u767e\u4ebf\u4e4b\u591a\u3002</p></article>"}}}]);
