(window.webpackJsonp=window.webpackJsonp||[]).push([[103],{125:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e94\u8282 \u6551\u661f",content:"<article><h2>\u7b2c\u5341\u4e94\u8282 \u6551\u661f</h2><p>\u8fd9\u8bdd\u4e00\u51fa\u53e3\uff0c\u628a\u743c\u6069\u90fd\u5413\u4e86\u4e00\u8df3\uff0c\u5bf9\u9762\u7684\u94f6\u9f99\u5e7d\u7075\u4e5f\u6014\u4e86\u4e00\u4e0b\uff0c\u4eff\u4f5b\u6ca1\u6599\u5230\u5979\u5c45\u7136\u4f1a\u5982\u6b64\u56de\u7b54\u3002\u4f46\u968f\u5373\uff0c\u5979\u5c31\u6012\u543c\u8d77\u6765\uff0c\u5de8\u5927\u7684\u9f99\u541f\u58f0\u9707\u5f97\u6574\u4e2a\u6d1e\u7a74\u90fd\u5728\u7c0c\u7c0c\u98a4\u52a8\uff0c\u73af\u7ed5\u7740\u5899\u58c1\u7684\u51e0\u5341\u4e2a\u5b88\u62a4\u8005\u4e4b\u7403\u4eff\u4f5b\u6d77\u6d6a\u4e2d\u7684\u6d6e\u6807\uff0c\u88ab\u9707\u5f97\u6447\u6643\u4e0d\u5b9a\u3002</p><p>\u201c\u51f6\u624b\uff01\u201d\u94f6\u9f99\u5e7d\u7075\u6012\u543c\u7740\uff0c\u4f4e\u4e0b\u5de8\u5927\u7684\u5934\u9885\u671d\u6885\u83f2\u65af\u4fef\u51b2\u8fc7\u6765\uff0c\u5973\u5723\u6b66\u58eb\u5e76\u4e0d\u95ea\u907f\uff0c\u5979\u751a\u81f3\u90fd\u6ca1\u6709\u4e3e\u5251\uff0c\u53ea\u662f\u9759\u9759\u5730\u7ad9\u5728\u539f\u5730\uff0c\u6beb\u4e0d\u62b5\u6297\u3002</p><p>\u5e7d\u7075\u662f\u7075\u4f53\uff0c\u5b83\u5df2\u7ecf\u4e0d\u662f\u7269\u8d28\u5b58\u5728\uff0c\u5b8c\u5168\u7531\u8d1f\u80fd\u91cf\u652f\u6491\u3002\u5e7d\u7075\u6ca1\u6709\u5b9e\u4f53\uff0c\u6ca1\u6cd5\u50cf\u751f\u524d\u4e00\u6837\u76f4\u63a5\u653b\u51fb\uff0c\u5b83\u53ef\u4ee5\u7528\u9b54\u6cd5\uff0c\u4f46\u8fd9\u91cc\u6709\u51e0\u5341\u4e2a\u5b88\u62a4\u8005\u4e4b\u7403\uff0c\u7528\u9b54\u6cd5\u662f\u81ea\u6740\u884c\u4e3a\uff1b\u9664\u4e86\u9b54\u6cd5\u4e4b\u5916\uff0c\u5b83\u8fd8\u53ef\u4ee5\u7528\u201c\u7a7f\u900f\u201d\u6765\u6740\u4eba\u3002</p><p>\u751f\u547d\u662f\u4ee5\u6b63\u80fd\u91cf\u652f\u6491\uff0c\u4e0e\u4ea1\u7075\u6ee1\u8574\u7684\u8d1f\u80fd\u91cf\u5b8c\u5168\u76f8\u6096\u76f8\u514b\u3002\u51e1\u4eba\u5982\u679c\u88ab\u5e7d\u7075\u7a7f\u8fc7\uff0c\u8f7b\u5219\u865a\u5f31\u65e0\u529b\uff0c\u91cd\u5219\u5927\u75c5\u4e00\u573a\uff0c\u751a\u81f3\u4e00\u547d\u545c\u547c\u3002\u8d8a\u5f3a\u5927\u51f6\u623e\u7684\u5e7d\u7075\uff0c\u81ea\u7136\u643a\u5e26\u7684\u8d1f\u80fd\u91cf\u8d8a\u591a\uff0c\u9762\u524d\u8fd9\u53ea\u5e7d\u7075\u751f\u524d\u662f\u53ea\u94f6\u9f99\uff0c\u66f4\u4e0d\u7528\u63d0\u4e86\uff0c\u53ea\u8981\u88ab\u5b83\u7a7f\u900f\uff0c\u5c31\u7b97\u6885\u83f2\u65af\u662f\u5723\u6b66\u58eb\u4e5f\u627f\u53d7\u4e0d\u4f4f\u3002</p><p>\u5f53\u7136\uff0c\u5e7d\u7075\u5e76\u4e0d\u662f\u65e0\u6cd5\u62b5\u5fa1\u3002\u5b9e\u9645\u4e0a\uff0c\u6885\u83f2\u65af\u8eab\u4e3a\u6b63\u4e49\u4e4b\u795e\u63d0\u5c14\u7684\u5723\u6b66\u58eb\uff0c\u4e43\u662f\u53d7\u795e\u7947\u5e87\u4f51\u7737\u987e\u7684\u795e\u9009\u8005\uff0c\u5bf9\u4ed8\u4ea1\u7075\u5176\u5b9e\u662f\u6700\u6070\u5f53\u4e0d\u8fc7\u7684\u4eba\u9009\u3002\u5f53\u65e5\u743c\u6069\u5728\u6c99\u6f20\u521d\u89c1\u5979\u65f6\uff0c\u6885\u83f2\u65af\u4e00\u5251\u51fb\u6e83\u9aa8\u866b\uff0c\u82e5\u662f\u6362\u4e86\u5176\u4ed6\u4eba\uff0c\u5c31\u7b97\u662f\u540c\u6837\u5f3a\u608d\u7684\u6218\u58eb\uff0c\u7edd\u5bf9\u505a\u4e0d\u5230\u8fd9\u4e48\u5e72\u51c0\u5229\u843d\u3002</p><p>\u7267\u5e08\u548c\u5723\u6b66\u58eb\uff0c\u539f\u672c\u5c31\u662f\u4ea1\u7075\u7684\u514b\u661f\u3002</p><p>\u4f46\u6885\u83f2\u65af\u4e0d\u77e5\u9053\u641e\u4ec0\u4e48\u540d\u5802\uff0c\u5c45\u7136\u7ad9\u7740\u4e00\u52a8\u4e0d\u52a8\uff0c\u4eff\u4f5b\u4e0d\u60f3\u6d3b\u4e86\u4e00\u822c\u3002\u773c\u770b\u94f6\u9f99\u5e7d\u7075\u5c31\u8981\u51b2\u649e\u8fc7\u6765\uff0c\u743c\u6069\u6025\u901f\u5ff5\u8bf5\u5492\u8bed\uff0c\u4f38\u624b\u5728\u6885\u83f2\u65af\u80a9\u5934\u4e00\u6309\u3002</p><p>\u201c\u8d1f\u80fd\u91cf\uff01\u201d</p><p>\u4ed6\u6700\u540e\u7528\u9f99\u8bed\u4f4e\u559d\u7740\uff0c\u6307\u5b9a\u4e86\u8fd9\u4e2a\u6cd5\u672f\u7684\u9632\u5fa1\u5bf9\u8c61\u3002\u4e00\u9762\u865a\u5e7b\u7684\u900f\u660e\u5de8\u5927\u76fe\u724c\u5e94\u58f0\u800c\u8d77\uff0c\u6321\u5728\u6885\u83f2\u65af\u9762\u524d\uff0c\u76fe\u9762\u4e0a\u94f6\u8272\u706b\u7130\u707f\u70c2\u71c3\u70e7\uff0c\u6c79\u6d8c\u731b\u70c8\uff0c\u4eff\u4f5b\u4e00\u7247\u706b\u6d77\u3002</p><p>\u94f6\u9f99\u5e7d\u7075\u5df2\u7ecf\u4fef\u51b2\u8fc7\u6765\uff0c\u6b63\u649e\u4e0a\u6b63\u80fd\u91cf\u5851\u6210\u7684\u94f6\u706b\u5927\u76fe\uff0c\u8f7b\u5fae\u7684\u55e4\u55e4\u58f0\u4e0d\u7edd\u4e8e\u8033\uff0c\u4eff\u4f5b\u6cb8\u6c34\u6d47\u5728\u5bd2\u51b0\u4e0a\u5192\u51fa\u4e1d\u4e1d\u8f7b\u70df\u3002\u94f6\u706b\u76fe\u724c\u4e00\u51fb\u800c\u6e83\uff0c\u6563\u5f52\u65e0\u5f62\uff0c\u4f46\u5e7d\u7075\u4e5f\u6012\u543c\u7740\u9000\u4e86\u56de\u53bb\uff0c\u5979\u663e\u7136\u4e5f\u53d7\u4e86\u4f24\u3002</p><p>\u5979\u5e76\u4e0d\u662f\u552f\u4e00\u7684\u53d7\u4f24\u8005\u3002</p><p>\u743c\u6069\u7684\u9b54\u6cd5\u62b5\u6d88\u4e86\u94f6\u9f99\u5e7d\u7075\u643a\u5e26\u7684\u4e00\u90e8\u5206\u8d1f\u80fd\u91cf\uff0c\u4f46\u5e76\u4e0d\u662f\u5168\u90e8\u3002\u4f9d\u65e7\u6709\u5f88\u5927\u4e00\u90e8\u5206\u8d1f\u80fd\u91cf\u51b2\u8fdb\u6885\u83f2\u65af\u7684\u8eab\u4f53\uff0c\u4f24\u5bb3\u5230\u4e86\u5973\u5723\u6b66\u58eb\u3002</p><p>\u5c11\u5973\u8eab\u4f53\u6447\u6643\u7740\uff0c\u8e09\u8dc4\u540e\u9000\u4e24\u6b65\uff0c\u5979\u7684\u8138\u8272\u9aa4\u7136\u95f4\u53d8\u5f97\u82cd\u767d\uff0c\u6beb\u65e0\u8840\u8272\uff0c\u8fde\u63e1\u5251\u7684\u53f3\u624b\u90fd\u6709\u4e9b\u98a4\u6296\u8d77\u6765\u3002\u4f46\u540c\u65f6\u5979\u4eff\u4f5b\u4e5f\u4ece\u604d\u60da\u4e2d\u6e05\u9192\u8fc7\u6765\uff0c\u62ac\u8d77\u5934\uff0c\u53cc\u624b\u63e1\u5251\u9ad8\u9ad8\u4e3e\u8d77\uff0c\u76ef\u7740\u9ad8\u7a7a\u4e2d\u7684\u94f6\u9f99\u5e7d\u7075\u3002</p><p>\u94f6\u9f99\u5e7d\u7075\u6012\u543c\u7740\uff0c\u653e\u5f03\u6885\u83f2\u65af\uff0c\u671d\u743c\u6069\u731b\u6251\u8fc7\u6765\u3002\u5deb\u5e08\u5306\u5fd9\u540e\u9000\uff0c\u6b63\u8981\u63b7\u51fa\u9f99\u9cde\u76fe\u6765\u62b5\u6321\uff0c\u8eab\u8fb9\u5149\u8292\u4e00\u63a0\uff0c\u6885\u83f2\u65af\u7684\u94f6\u5251\u4e0a\u653e\u51fa\u8000\u773c\u5723\u5149\uff0c\u8f70\u7136\u7efd\u5f00\uff0c\u671d\u94f6\u9f99\u5288\u4e86\u8fc7\u53bb\u3002</p><p>\u8fd9\u79cd\u795e\u5723\u8f89\u5149\uff0c\u5bf9\u4ea1\u7075\u6700\u6709\u514b\u5236\u6740\u4f24\u3002\u94f6\u9f99\u5e7d\u7075\u4e0d\u6562\u786c\u78b0\uff0c\u98d5\u5730\u540e\u9000\u907f\u5f00\uff0c\u6885\u83f2\u65af\u4fa7\u8eab\u6321\u5728\u743c\u6069\u524d\u9762\uff0c\u201c\u4f60\u8d70\u3002\u201d\u5979\u8bf4\u3002</p><p>\u743c\u6069\u7f13\u7f13\u540e\u9000\uff0c\u4f46\u5e76\u4e0d\u6025\u4e8e\u62d4\u817f\u9003\u547d\uff0c\u4ed6\u5728\u89c2\u5bdf\u7740\u5f62\u52bf\u3002\u94f6\u9f99\u5e7d\u7075\u53d1\u51fa\u5c16\u9510\u7684\u5636\u543c\uff0c\u8fd9\u4e0d\u50cf\u662f\u9f99\u541f\uff0c\u53cd\u5012\u66f4\u50cf\u662f\u6765\u81ea\u4e0b\u5c42\u754c\u7684\u547c\u53f7\u3002\u5979\u5e9e\u5927\u7684\u865a\u5e7b\u8eab\u4f53\u6025\u5267\u7f29\u5c0f\uff0c\u6700\u7ec8\u51dd\u6210\u4e86\u4e00\u56e2\u7070\u8272\u7684\u70df\u96fe\u3002\u70df\u96fe\u6563\u53bb\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u770b\u89c1\u4e86\u4e00\u53ea\u5de8\u5927\u7684\u9f99\u7684\u9ab7\u9ac5\u5934\u9aa8\u3002</p><p>\u6216\u8005\u66f4\u51c6\u786e\u5730\u8bf4\uff0c\u662f\u4e00\u5bf9\u989a\u9aa8\u2014\u2014\u4e0a\u989a\u548c\u4e0b\u989a\uff0c\u865a\u865a\u5730\u5f20\u5f00\u7740\uff0c\u767d\u68ee\u68ee\u7684\u7259\u9f7f\u4e0a\u4f3c\u4e4e\u8fd8\u6cbe\u7740\u6697\u7ea2\u8272\u7684\u8840\u8ff9\u3002\u7a7a\u6d1e\u7684\u543c\u53eb\u58f0\u4ece\u91cc\u9762\u53d1\u51fa\u6765\uff0c\u5c16\u5229\u523a\u8033\u3002</p><p>\u8fd9\u6b21\u4e0d\u518d\u662f\u5e7d\u7075\uff0c\u800c\u662f\u5b9e\u4f53\uff0c\u8fd9\u5e94\u8be5\u662f\u5979\u7684\u5c38\u9aa8\u2026\u2026\u6b8b\u5b58\u7684\u90e8\u5206\u3002</p><p>\u743c\u6069\u7684\u9b54\u6cd5\u4f24\u5bb3\u5230\u4e86\u5e7d\u7075\uff0c\u5979\u4f3c\u4e4e\u521a\u521a\u624d\u610f\u8bc6\u5230\u65c1\u8fb9\u8fd8\u6709\u4e00\u4f4d\u5deb\u5e08\u3002\u666e\u901a\u4eba\u662f\u6ca1\u529e\u6cd5\u5bf9\u4ed8\u5904\u4e8e\u865a\u4f53\u72b6\u6001\u7684\u5e7d\u7075\u7684\uff0c\u4f46\u5deb\u5e08\u53ef\u4ee5\uff0c\u800c\u4e14\u672a\u5fc5\u4e00\u5b9a\u8981\u4f7f\u7528\u76f4\u63a5\u653b\u51fb\u7684\u6cd5\u672f\u3002</p><p>\u94f6\u9f99\u6539\u53d8\u4e86\u6218\u672f\u3002</p><p>\u5979\u53d1\u51fa\u5389\u58f0\u568e\u53eb\uff0c\u5f20\u5f00\u5de8\u989a\uff0c\u671d\u743c\u6069\u98de\u649e\u8fc7\u6765\uff0c\u770b\u67b6\u5f0f\u6253\u7b97\u628a\u4ed6\u7684\u5934\u4e00\u53e3\u54ac\u65ad\u3002\u743c\u6069\u8131\u53e3\u53eb\u51fa\u53e3\u4ee4\uff0c\u5c06\u9f99\u9cde\u76fe\u6d6e\u8d77\uff0c\u968f\u5373\u51c6\u5907\u518d\u65bd\u4e00\u4e2a\u6cd5\u672f\uff0c\u4f46\u6885\u83f2\u65af\u52a8\u4f5c\u6bd4\u4ed6\u5feb\u5f97\u591a\u3002\u5c11\u5973\u53cc\u624b\u63e1\u5251\uff0c\u7bad\u6b65\u6321\u5728\u743c\u6069\u8eab\u524d\uff0c\u94f6\u5251\u4e0a\u8f70\u7136\u6cdb\u51fa\u8000\u773c\u767d\u5149\uff0c\u671d\u94f6\u9f99\u5288\u4e86\u8fc7\u53bb\u3002</p><p>\u5de8\u5927\u989a\u9aa8\u4e0a\u5192\u51fa\u6d53\u6d53\u7070\u70df\uff0c\u4e00\u80a1\u65e0\u5f62\u7684\u529b\u91cf\u8ff8\u53d1\u51fa\u6765\uff0c\u6885\u83f2\u65af\u7684\u94f6\u5251\u8fd8\u6ca1\u5288\u4e2d\uff0c\u6574\u4e2a\u4eba\u90fd\u88ab\u53cd\u5f39\u4e86\u56de\u53bb\uff0c\u5dee\u70b9\u649e\u5728\u743c\u6069\u8eab\u4e0a\u3002\u5deb\u5e08\u6b63\u5728\u5ff5\u4e00\u4e2a\u5492\u8bed\uff0c\u88ab\u8fd9\u4e00\u649e\u5dee\u70b9\u6253\u65ad\uff0c\u4ed6\u5306\u5fd9\u7a33\u4f4f\u8eab\u5f62\uff0c\u5ff5\u51fa\u6700\u540e\u4e00\u4e2a\u97f3\u8282\uff0c\u5c06\u5df2\u7ecf\u78be\u788e\u7684\u7518\u8349\u6839\u7c89\u672b\u5f80\u7a7a\u4e2d\u4e00\u6d12\u3002</p><p>\u201c\u8d70\uff01\u201d</p><p>\u743c\u6069\u5b8c\u6210\u6cd5\u672f\uff0c\u8f6c\u8eab\u5c31\u8dd1\uff0c\u6885\u83f2\u65af\u8ddf\u968f\u5728\u540e\u3002\u4ed6\u4eec\u8981\u901a\u8fc7\u6765\u65f6\u7684\u7ef5\u957f\u96a7\u9053\u56de\u53bb\uff0c\u56e0\u4e3a\u4e5f\u53ea\u77e5\u9053\u8fd9\u4e00\u6761\u8def\uff0c\u4f46\u94f6\u9f99\u7c73\u8389\u4e9a\u59c6\u663e\u7136\u5e76\u4e0d\u6253\u7b97\u5c31\u8fd9\u6837\u653e\u4ed6\u4eec\u8d70\u3002</p><p>\u5c16\u5578\u58f0\u518d\u5ea6\u54cd\u8d77\uff0c\u96a7\u9053\u53e3\u7a81\u7136\u5347\u8d77\u4e00\u9053\u51b0\u5899\uff0c\u6321\u4f4f\u4e86\u53bb\u8def\u3002\u90a3\u53ea\u5de8\u5927\u7684\u989a\u9aa8\u518d\u5ea6\u7f13\u7f13\u98de\u8fd1\uff0c\u5229\u9f7f\u5f20\u5408\u7740\uff0c\u53d1\u51fa\u5494\u5494\u7684\u54cd\u52a8\uff0c\u4f3c\u4e4e\u6253\u7b97\u5c06\u4e24\u4eba\u5168\u90fd\u54ac\u788e\u3002</p><p>\u6885\u83f2\u65af\u7684\u94f6\u5251\u4e0a\u518d\u5ea6\u53d1\u51fa\u767d\u5149\uff0c\u780d\u5288\u7740\u8fd9\u5177\u60ac\u6d6e\u7684\u989a\u9aa8\u3002\u5979\u6b64\u524d\u5df2\u7ecf\u53d7\u4e86\u4f24\uff0c\u6c14\u529b\u4e0d\u52a0\uff0c\u800c\u9762\u5bf9\u7684\u4ea1\u7075\u53c8\u6781\u5176\u5f3a\u608d\uff0c\u6bd5\u7adf\u751f\u524d\u662f\u4e00\u53ea\u94f6\u9f99\u3002\u56e0\u4e3a\u521a\u624d\u743c\u6069\u7684\u6cd5\u672f\u52a0\u6301\uff0c\u6885\u83f2\u65af\u7684\u52a8\u4f5c\u53d8\u5f97\u6bd4\u5e73\u65f6\u66f4\u5feb\uff0c\u94f6\u5251\u5982\u540c\u6025\u98ce\u66b4\u96e8\u822c\u780d\u5288\u5728\u989a\u9aa8\u4e0a\uff0c\u4f46\u5e76\u4e0d\u80fd\u9020\u6210\u592a\u5927\u7684\u4f24\u5bb3\uff0c\u53cd\u800c\u4e00\u6b21\u6b21\u88ab\u53cd\u9707\u56de\u6765\uff0c\u743c\u6069\u6ce8\u610f\u5230\u5c11\u5973\u7684\u8138\u8272\u5df2\u7ecf\u8d8a\u6765\u8d8a\u82cd\u767d\uff0c\u5598\u606f\u58f0\u6e10\u6e10\u7c97\u91cd\u8d77\u6765\uff0c\u663e\u7136\u5df2\u7ecf\u6709\u4e9b\u627f\u53d7\u4e0d\u4f4f\u7684\u5148\u5146\u3002</p><p>\u56e0\u4e3a\u5b88\u62a4\u8005\u4e4b\u7403\u7684\u5b58\u5728\uff0c\u743c\u6069\u548c\u94f6\u9f99\u90fd\u4e0d\u6562\u4f7f\u7528\u653b\u51fb\u6027\u7684\u9b54\u6cd5\uff0c\u5bfc\u81f4\u8fd9\u573a\u6218\u6597\u53d8\u5f97\u683c\u5916\u7684\u6c89\u95f7\u65e0\u8da3\uff0c\u7b80\u76f4\u5982\u540c\u4e00\u573a\u8857\u5934\u6253\u95f9\u3002</p><p>\u4f46\u5fc5\u987b\u8bf4\uff0c\u94f6\u9f99\u7684\u8fd9\u79cd\u6218\u672f\u975e\u5e38\u6709\u6548\u3002</p><p>\u4ea1\u7075\u662f\u6ca1\u6709\u75b2\u60eb\u611f\u7684\uff0c\u5b83\u4eec\u6709\u7740\u65e0\u9650\u7684\u7cbe\u529b\uff0c\u800c\u6d3b\u4eba\u5219\u4e0d\u884c\u3002\u989a\u9aa8\u6f02\u6d6e\u7740\uff0c\u79fb\u52a8\u7740\uff0c\u7ea0\u7f20\u7740\uff0c\u806a\u660e\u5730\u6d88\u8017\u7740\u5723\u6b66\u58eb\u7684\u529b\u6c14\u3002\u5373\u4f7f\u88ab\u5288\u4e2d\uff0c\u88ab\u94f6\u5251\u4e0a\u7684\u5723\u5149\u4f24\u5bb3\uff0c\u5b83\u4e5f\u4f9d\u7136\u80fd\u7f13\u6162\u5730\u81ea\u884c\u6062\u590d\uff0c\u4e8b\u5b9e\u4e0a\uff0c\u4f5c\u4e3a\u88ab\u56da\u7981\u675f\u7f1a\u5728\u6b64\u5730\u7684\u5e7d\u7075\uff0c\u5982\u679c\u4e0d\u4f7f\u7528\u975e\u5e38\u5f3a\u529b\u7684\u9b54\u6cd5\uff0c\u5b83\u6839\u672c\u5c31\u662f\u65e0\u6cd5\u88ab\u5f7b\u5e95\u6467\u6bc1\u7684\u3002\u5c31\u7b97\u6682\u65f6\u80fd\u5c06\u5b83\u6d88\u706d\uff0c\u90a3\u4e5f\u4ec5\u4ec5\u662f\u6682\u65f6\uff0c\u5b83\u53ef\u4ee5\u8f7b\u800c\u6613\u4e3e\u5730\u590d\u751f\u3002</p><p>\u53ef\u4ee5\u8bf4\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u9762\u5bf9\u7684\uff0c\u662f\u4e00\u4e2a\u4ee5\u4ed6\u4eec\u7684\u80fd\u529b\u6839\u672c\u4e0d\u53ef\u80fd\u6467\u6bc1\u7684\u654c\u4eba\u3002\u56e0\u4e3a\u6b7b\u4ea1\uff0c\u56e0\u4e3a\u8089\u4f53\u5df2\u7ecf\u5931\u53bb\uff0c\u56e0\u4e3a\u653b\u51fb\u6cd5\u672f\u7684\u88ab\u7981\u6b62\uff0c\u94f6\u9f99\u7684\u8868\u73b0\u4f3c\u4e4e\u5e76\u4e0d\u591a\u4e48\u5f3a\u5927\uff0c\u4f46\u5374\u975e\u5e38\u96be\u7f20\u3002</p><p>\u5c01\u4f4f\u6d1e\u53e3\u7684\u51b0\u5899\u975e\u5e38\u539a\uff0c\u743c\u6069\u6839\u672c\u65e0\u6cd5\u6253\u5f00\u3002\u7531\u4e8e\u6709\u4e8b\u5148\u51c6\u5907\uff0c\u4ed6\u81ea\u5df1\u8981\u8131\u8eab\u5e76\u4e0d\u96be\uff0c\u4f46\u5374\u6ca1\u529e\u6cd5\u628a\u6885\u83f2\u65af\u4e5f\u5e26\u51fa\u53bb\u3002\u8fd9\u5c31\u975e\u5e38\u9ebb\u70e6\uff0c\u4ed6\u4e0d\u60f3\u653e\u5f03\u5973\u5723\u6b66\u58eb\u3002</p><p>\u968f\u7740\u65f6\u95f4\u7684\u63a8\u79fb\uff0c\u80dc\u5229\u7684\u5929\u5e73\u4f3c\u4e4e\u8d8a\u6765\u8d8a\u503e\u5411\u4e8e\u94f6\u9f99\u4e00\u65b9\u3002</p><p>\u6b63\u5f53\u743c\u6069\u5df2\u7ecf\u8981\u653e\u5f03\u5e0c\u671b\u7684\u65f6\u5019\uff0c\u4ed6\u611f\u89c9\u5934\u9876\u4f20\u6765\u4e00\u9635\u9b54\u6cd5\u6ce2\u52a8\u3002\u5deb\u5e08\u62ac\u5934\u671b\u53bb\uff0c\u89c1\u63a5\u8fd1\u7a79\u9876\u7684\u5730\u65b9\uff0c\u6084\u7136\u5212\u5f00\u4e00\u9053\u692d\u5706\u5f62\u94f6\u8272\u4f20\u9001\u95e8\uff0c\u4e00\u4e2a\u7a7f\u7740\u7070\u8272\u957f\u888d\u7684\u8eab\u5f71\u4ece\u91cc\u9762\u8d70\u51fa\u6765\uff0c\u7f13\u7f13\u843d\u4e0b\u3002</p><p>\u6765\u4eba\u5e94\u8be5\u662f\u5728\u8eab\u4e0a\u9644\u52a0\u4e86\u7fbd\u843d\u672f\uff0c\u888d\u8896\u98d8\u98d8\uff0c\u5982\u540c\u7fbd\u6bdb\u4e00\u822c\u98d8\u843d\u4e0b\u6765\uff0c\u5de6\u624b\u4e2d\u4f3c\u4e4e\u8fd8\u62ff\u7740\u4e00\u5f20\u5377\u8f74\u3002\u4ed6\u8be7\u5f02\u5730\u770b\u7740\u573a\u5185\uff0c\u8138\u4e0a\u795e\u60c5\u9887\u4e3a\u8ff7\u60d1\uff0c\u501f\u7740\u7a79\u9876\u7684\u95ea\u70c1\u5fae\u5149\uff0c\u743c\u6069\u7acb\u523b\u8ba4\u51fa\u4e86\u8fd9\u4e2a\u4eba\u662f\u8c01\u3002</p><p>\u5c31\u662f\u90a3\u4e2a\u88ab\u6885\u83f2\u65af\u903c\u95ee\u5e74\u9f84\u7684\u5c11\u5e74\u3002</p></article>"}}}]);
