(window.webpackJsonp=window.webpackJsonp||[]).push([[1032],{1055:function(p){p.exports={chapterTitle:null,title:"\u7b2c1029\u7ae0 \u8fd9\u4e16\u754c\u592a\u75af\u72c2",content:"<article><h2>\u7b2c1029\u7ae0 \u8fd9\u4e16\u754c\u592a\u75af\u72c2</h2><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u7b49\u4eba\u7ed9\u5d07\u62dc\u8005\u56f4\u5f97\u6c34\u6cc4\u4e0d\u901a\uff0c\u7531\u6b64\u53ef\u89c1\u8fd9\u6bb5\u65f6\u95f4\u8206\u8bba\u5a92\u4f53\u5bf9\u6606\u817e\u5728\u7ebf\u5728\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u6210\u529f\u4e0a\u5e02\u4e00\u4e8b\u7684\u9020\u52bf\u7a0b\u5ea6\uff0c\u5f20\u606a\u81ea\u7136\u662f\u63d2\u4e0d\u8fc7\u53bb\u8ddf\u4ed6\u4eec\u4ea4\u8c08\uff0c\u968f\u6881\u6587\u6c5f\u5230\u5efa\u90ba\u6765\u7684\u738b\u5f69\u73b2\u4e5f\u7ed9\u6324\u4e86\u4e00\u65c1\u3002\u5979\u5012\u662f\u843d\u5f97\u8f7b\u95f2\uff0c\u4e0e\u5f20\u606a\u4ed6\u4eec\u4e00\u8d77\u5750\u5728\u5427\u53f0\u8fb9\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u8d75\u9633\u5df2\u7ecf\u53bb\u6d59\u4e1c\u5c31\u804c\uff0c\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u5e38\u52a1\u526f\u7701\u957f\u4e00\u804c\uff0c\u800c\u738b\u5f69\u73b2\u7684\u54e5\u54e5\u738b\u5fd7\u5c31\u5728\u6587\u821f\u5e02\u7684\u653f\u5e9c\u90e8\u95e8\u5de5\u4f5c\uff0c\u6216\u8bb8\u8981\u4e13\u95e8\u4e3a\u6b64\u5f80\u6587\u821f\u8d70\u4e00\u8d9f\uff0c\u5c31\u8ddf\u738b\u5f69\u73b2\u968f\u53e3\u8bf4\u8d77\u8fd9\u4e8b\u3002</p><p>\u3000\u3000\u8bf4\u8d77\u6765\u4e5f\u602a\uff0c\u5f20\u606a\u4e4b\u524d\u8fd8\u63a8\u6d4b\u8fc7\u8d75\u9633\u53bb\u6d59\u4e1c\u53ef\u80fd\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\u517c\u6587\u821f\u5e02\u59d4\u4e66\u8bb0\u804c\uff0c\u6ca1\u60f3\u5230\u8d75\u9633\u53bb\u6d59\u4e1c\u5374\u517c\u5e38\u52a1\u526f\u7701\u957f\u3002\u521d\u770b\u8fd9\u91cc\u9762\u6ca1\u6709\u592a\u591a\u7684\u533a\u522b\uff0c\u5c31\u662f\u4e3a\u8d75\u9633\u63a5\u6b64\u65f6\u6d59\u4e1c\u7701\u7701\u957f\u9648\u5929\u4fdd\u7684\u73ed\u800c\u505a\u51c6\u5907\uff0c\u4f46\u662f\u62c5\u4efb\u7701\u59d4\u526f\u4e66\u8bb0\u517c\u5e38\u52a1\u526f\u7701\u957f\u672a\u514d\u6709\u6025\u7740\u5c06\u9648\u5929\u4fdd\u8d76\u4e0b\u53bb\u3001\u5484\u5484\u903c\u4eba\u7684\u610f\u5473\uff0c\u6362\u4f5c\u8c01\u7ad9\u5728\u9648\u5929\u4fdd\u7684\u7acb\u573a\u4e0a\uff0c\u5fc3\u91cc\u90fd\u4e0d\u4f1a\u8212\u670d\u7684\u3002</p><p>\u3000\u3000\u5373\u4f7f\u8d75\u9633\u60f3\u4e0e\u9648\u5929\u4fdd\u641e\u597d\u5173\u7cfb\uff0c\u4f46\u662f\u5fc3\u91cc\u7684\u8fd9\u79cd\u9694\u9602\u662f\u5f88\u96be\u6d88\u9664\u7684\u3002</p><p>\u3000\u3000\u9664\u975e\u7740\u624b\u53bb\u8c03\u67e5\uff0c\u4e0d\u7136\u662f\u5f88\u96be\u7406\u6e05\u695a\u5185\u4e2d\u7684\u8e4a\u8df7\uff0c\u4e25\u5bb6\u5bf9\u6d59\u4e1c\u515a\u653f\u6e17\u900f\u5f88\u5389\u5bb3\uff0c\u4e5f\u8bf4\u4e0d\u5b9a\u5c31\u662f\u4e25\u5bb6\u5728\u80cc\u540e\u64cd\u7eb5\u3002</p><p>\u3000\u3000\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u57281978\u66b4\u9732\u4e86\u884c\u8e2a\uff0c\u5f20\u606a\u5c31\u5728\u65b0\u5e02\u8857\u541b\u8c6a\u5927\u9152\u5e97\u9884\u8ba2\u4e86\u9152\u5bb4\uff0c\u5403\u8fc7\u665a\u9910\u4e4b\u540e\uff0c\u518d\u56de1978\u6765\uff0c\u8ba9\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4e0e\u5efa\u90ba\u65e0\u6570\u6000\u7740\u68a6\u60f3\u7684\u521b\u4e1a\u8005\u4eec\u4ee5\u53ca\u9ad8\u6821\u5927\u5b66\u751f\u4f1a\u4ea4\u6d41\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u660e\u5e74\u4e0a\u5348\u8fd8\u6709\u6b63\u5f0f\u7684\u6f14\u8bb2\u3001\u8bbf\u8c08\u6d3b\u52a8\uff0c\u4e5f\u6ca1\u6709\u62d6\u592a\u665a\uff0c\u5c31\u5404\u81ea\u6563\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e26\u7740\u4e1d\u5fae\u7684\u9152\u5e97\u8fd4\u56de\u7a7a\u8361\u8361\u7684\u516c\u5bd3\uff0c\u624d\u9661\u7136\u60f3\u8d77\u9759\u6aac\u56de\u9999\u6e2f\uff0c\u6ca1\u6709\u4eba\u4f1a\u5728\u81ea\u5df1\u5750\u5728\u6c99\u53d1\u770b\u4e66\u50cf\u53ea\u732b\u4e00\u6837\u7684\u8d34\u8fc7\u6765\u6216\u5149\u7740\u817f\u4e2b\u5b50\u3001\u9732\u51fa\u7ea4\u7ec6\u4fee\u957f\u7684\u5c0f\u817f\u5728\u773c\u524d\u8d70\u6765\u8d70\u524d\uff0c\u7fdf\u4e39\u9752\u53bb\u73c0\u65af\u4e4b\u540e\uff0c\u536b\u5170\u4e5f\u6781\u5c11\u4f4f\u56de\u9759\u5e74\u516c\u5bd3\u91cc\uff0c\u5979\u4ece\u5c0f\u5c31\u662f\u8d70\u8bfb\u751f\uff0c\u5176\u5b9e\u5185\u5fc3\u8fd8\u662f\u5f88\u671f\u5f85\u5bc4\u5bbf\u751f\u6d3b\uff0c\u8fd9\u51e0\u5929\uff0c\u5f20\u606a\u90fd\u6ca1\u6709\u600e\u4e48\u89c1\u5230\u5979\u7684\u9762\u3002</p><p>\u3000\u3000\u6d17\u8fc7\u6fa1\uff0c\u53a8\u623f\u91cc\u716e\u7740\u5496\u5561\uff0c\u5750\u7740\u5ba2\u5385\u91cc\u6c99\u53d1\u4e0a\uff0c\u770b\u7740\u7a97\u5916\u6f84\u6f88\u7684\u591c\u5e55\uff0c\u5373\u4f7f\u6709\u4e9b\u7a7a\u8361\uff0c\u611f\u89c9\u4e5f\u76f8\u5f53\u4e0d\u9519\u3002\u5f20\u606a\u53c8\u8d70\u4e0a\u697c\u5c06\u624b\u63d0\u7535\u8111\u62ff\u4e0b\u6765\uff0c\u63a5\u4e0a\u7f51\u7ebf\uff0c\u60f3\u770b\u770b\u5510\u5a67\u591c\u91cc\u6709\u6ca1\u6709\u5199\u90ae\u4ef6\u8fc7\u6765\uff0c\u60f3\u8212\u670d\u7684\u8eba\u5728\u6c99\u53d1\u4e0a\u770b\u90ae\u4ef6\u5374\u662f\u4e0d\u6210\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u624d\u89c9\u5f97\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u7684\u65b9\u4fbf\u3002</p><p>\u3000\u3000\u5f88\u53ef\u60dc\uff0c\u56fd\u5185\u7684\u4e5d\u4e5d\u5e74\u9ad8\u7aef\u5546\u52a1\u5e02\u573a\u592a\u5c0f\u4e86\uff0c\u65e0\u8bba\u662f\u7535\u4fe1\u8fd8\u662f\u8054\u901a\uff0c\u90fd\u4e0d\u53ef\u80fd\u5f00\u5c55\u8fd9\u9879\u4e1a\u52a1\uff0c\u5c31\u5f20\u606a\u5962\u4f88\u7684\u6295\u8d44\u4e00\u5957\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u7684\u670d\u52a1\u7f51\u7edc\uff0c\u4ed6\u4e5f\u62ff\u4e0d\u5230\u4fe1\u4ea7\u90e8\u7684\u8bb8\u53ef\u8bc1\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u5012\u6709\u4e9b\u72b9\u8c6b\u8d77\u6765\uff0c\u52a0\u62ff\u5927RIM\u516c\u53f8\u5df2\u7ecf\u4e3a\u5176\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u9ed1\u8393\u624b\u673a\u5728\u7f8e\u56fd\u6295\u8d44\u5efa\u670d\u52a1\u5668\u7f51\u7edc\u4e86\uff0c\u9526\u6e56\u662f\u7b49\u4ed6\u4eec\u5c06\u5e02\u573a\u57f9\u80b2\u8d77\u6765\uff0c\u8fd8\u662f\u73b0\u5728\u5c31\u7d27\u8ddf\u7740\u4e00\u8d77\u505a\u57f9\u80b2\u8fd9\u5757\u5e02\u573a\uff1f</p><p>\u3000\u3000\u867d\u7136\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7684\u5b98\u53f8\u8ba9\u7231\u8fbe\u5728\u5317\u7f8e\u5730\u533a\u58f0\u540d\u96c0\u8d77\uff0c\u6781\u5927\u7684\u4fc3\u8fdb\u4e86iplayer\u7684\u9500\u552e\uff0c\u4f46\u662f\u5317\u7f8e\u5e02\u573a\u7684\u6d88\u8d39\u89c2\u5ff5\u76f8\u5bf9\u7406\u667a\uff0c\u5317\u7f8e\u516c\u53f8\u4eca\u5e74\u867d\u7136\u5c06\u638c\u4e0a\u7535\u8111\u3001\u624b\u673a\u4ee5\u53caDVD\u5f71\u789f\u673a\u90fd\u63a8\u5411\u5317\u7f8e\u5e02\u573a\uff0c\u867d\u7136\u6709\u4e9b\u6210\u7ee9\uff0c\u4f46\u662f\u6210\u7ee9\u6bd4\u9884\u671f\u8fd8\u7a0d\u5dee\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u6b64\u65f6\u8fd8\u6ca1\u6709\u6210\u719f\u7684\u6280\u672f\u63a8\u51faPDA\u4e0e\u79fb\u52a8\u901a\u8baf\u76f8\u7ed3\u5408\u7684\u667a\u80fd\u6027\u624b\u673a\uff0c\u4f46\u662f\u6536\u8d2d\u53e4\u5fb7\u8d39\u52d2\u516c\u53f8\u4e4b\u540e\uff0c\u5c06\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u690d\u5165PDA\u5df2\u7ecf\u6210\u719f\uff0c\u4f46\u662f\u5317\u7f8e\u5730\u533a\u6ca1\u6709\u901a\u4fe1\u8fd0\u8425\u5546\u63d0\u4f9b\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u6280\u672f\u7684\u670d\u52a1\u7f51\u7edc\uff0c\u9526\u6e56\u8981\u8fdb\u5165\u8fd9\u4e00\u5757\u5e02\u573a\uff0c\u5c31\u5fc5\u987b\u5b66RIM\u516c\u53f8\u4e00\u6837\u81ea\u5df1\u6295\u5165\u5de8\u8d44\u5efa\u670d\u52a1\u7f51\u7edc\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u81ea\u5df1\u5012\u4e86\u4e00\u5927\u676f\u5496\u5561\uff0c\u4ed6\u4ece\u4e0d\u62c5\u5fc3\u559d\u591a\u5496\u5561\u4f1a\u5931\u7720\uff0c\u7ed9\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u3001\u8096\u664b\u6210\u7b49\u4eba\u5199\u90ae\u4ef6\u8be2\u95ee\u6709\u6b64\u65f6\u5728\u5317\u7f8e\u5730\u533a\u5efa\u79fb\u52a8\u7535\u5b50\u90ae\u4ef6\u670d\u52a1\u7f51\u7edc\u7684\u5fc5\u8981\uff0c\u53d1\u8fc7\u90ae\u4ef6\u4e4b\u540e\uff0c\u5df2\u7ecf\u5feb\u51cc\u6668\u4e24\u70b9\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u684c\u4e0a\u624b\u673a\u7a81\u7136\u9707\u52a8\u8d77\u6765\uff0c\u5f20\u606a\u8fd8\u4ee5\u4e3a\u8c01\u770b\u5230\u4ed6\u7684\u90ae\u4ef6\u8fd9\u65f6\u5019\u6253\u7535\u8bdd\u8fc7\u6765\u8ba8\u8bba\u4e8b\u60c5\uff0c\u5f20\u606a\u5c06\u624b\u673a\u62ff\u8fc7\u6765\uff0c\u5374\u662f\u9648\u5983\u84c9\u6253\u8fc7\u6765\uff0c\u5fc3\u91cc\u89c9\u5f97\u5947\u602a\uff0c\u60f3\u8d77\u5979\u4e0b\u5348\u8eab\u4f53\u6709\u4e9b\u4e0d\u8212\u670d\uff0c\u4f1a\u4e0d\u4f1a\u53d1\u751f\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u3000\u3000\u7535\u8bdd\u90a3\u5934\u662f\u4e2a\u4e0d\u5927\u719f\u6089\u7684\u5973\u5b69\u5b50\u7684\u7126\u6025\u58f0\u97f3\uff1a\u201c\u4f60\u662f\u5f20\u606a\u5427\uff1f\u9648\u5983\u84c9\u4e0b\u5348\u53bb\u6821\u533b\u9662\u770b\u75c5\uff0c\u5403\u4e86\u836f\u597d\u70b9\u4e86\uff0c\u8fd9\u65f6\u5019\u809a\u5b50\u53c8\u75db\u4e86\u8d77\u6765\uff0c\u8eab\u4e0a\u90fd\u662f\u865a\u6c57\uff0c\u6211\u4eec\u4e0d\u6562\u518d\u628a\u5979\u5f80\u6821\u533b\u9662\u9001\uff0c\u4e0d\u77e5\u9053\u8be5\u600e\u4e48\u529e\u597d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u62dc\u6258\u4f60\u4eec\u5c06\u5979\u6276\u4e0b\u5bbf\u820d\uff0c\u6211\u9a6c\u4e0a\u5c31\u5230\u4f60\u4eec\u5bbf\u820d\u697c\u4e0b\u6765\u63a5\u5979\u53bb\u533b\u9662\u2026\u2026\u201d\u5f20\u606a\u4e0d\u6653\u5f97\u9648\u5983\u84c9\u751f\u4e86\u4ec0\u4e48\u75c5\u5403\u8fc7\u836f\u534a\u591c\u75c5\u60c5\u5374\u4f1a\u7a81\u7136\u52a0\u5267\uff0c\u4ed6\u6293\u8d77\u624b\u673a\u3001\u8f66\u94a5\u5319\u5c31\u4e0b\u4e86\u697c\uff0c\u5f00\u8f66\u5230\u5973\u751f\u5bbf\u820d\u697c\u4e0b\uff0c\u89c1\u9648\u5983\u84c9\u5bbf\u820d\u7684\u597d\u51e0\u4e2a\u5973\u5b69\u5b50\u90fd\u5728\u697c\u4e0b\u7126\u6025\u7684\u7b49\u7740\u4ed6\u5f00\u8f66\u8fc7\u6765\u3002\u7ad9\u5728\u8def\u706f\u4e0b\u7684\uff0c\u9648\u5983\u84c9\u624b\u6309\u5728\u5c0f\u8179\u5904\uff0c\u8eab\u5b50\u75db\u5f97\u5fae\u5fae\u4f5d\u507b\u7740\uff0c\u8138\u8272\u82cd\u767d\u3001\u5634\u5507\u90fd\u6ca1\u6709\u8840\u8272\uff0c\u591c\u91cc\u5929\u6c14\u51c9\u4e86\u4e0b\u6765\uff0c\u5979\u7684\u989d\u5934\u5374\u6e17\u51fa\u9ec4\u8c46\u5927\u7684\u6c57\u73e0\uff0c\u5934\u53d1\u96f6\u4e71\uff0c\u7709\u5934\u7d27\u76b1\u5728\u4e00\u8d77\uff0c\u8ddf\u5e73\u65e5\u6e05\u4e3d\u660e\u8273\u7684\u5979\u5224\u82e5\u4e24\u4eba\uff0c\u90a3\u53cc\u773c\u775b\u8fd9\u65f6\u5019\u770b\u4e0a\u53bb\u5374\u50cf\u65b0\u751f\u7684\u5c0f\u72d7\u90a3\u4e48\u53ef\u601c\u2014\u2014\u770b\u5230\u8fd9\u6837\u7684\u773c\u795e\uff0c\u5f20\u606a\u5fc3\u91cc\u5374\u662f\u4e00\u62bd\uff0c\u5bf9\u9648\u5983\u84c9\u5bbf\u820d\u7684\u5973\u751f\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u660e\u5929\u4e0a\u5348\u6709\u8003\u8bd5\uff0c\u6211\u9001\u5979\u53bb\u533b\u9662\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d\u6253\u5f00\u53f3\u8fb9\u7684\u8f66\u95e8\u8ba9\u5979\u4eec\u5c06\u9648\u5983\u84c9\u6276\u4e0a\u8f66\u6765\uff0c\u62ff\u624b\u5e2e\u5979\u64e6\u4e86\u64e6\u989d\u5934\u7684\u6c57\uff0c\u611f\u89c9\u5230\u6709\u4e9b\u53d1\u70eb\uff0c\u770b\u5979\u65e0\u52a9\u7684\u8ddf\u4e2a\u5c0f\u5b69\u4f3c\u7684\uff0c\u5e2e\u5979\u6263\u4e0a\u5b89\u5168\u5e26\uff0c\u5fcd\u4e0d\u4f4f\u6ca1\u6709\u95ee\u5979\u5728\u6821\u533b\u9662\u91cc\u600e\u4e48\u770b\u75c5\u7684\uff0c\u4e5f\u6ca1\u6709\u65f6\u95f4\u770b\u5979\u5bbf\u820d\u5973\u5b69\u5b50\u653e\u5230\u4eea\u8868\u76d8\u4e0a\u7684\u6821\u533b\u9662\u75c5\u5386\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0a\u521d\u4e2d\u65f6\u5f97\u8fc7\u6025\u6027\u9611\u5c3e\u708e\uff0c\u611f\u89c9\u9648\u5983\u84c9\u7684\u75c7\u72b6\u6709\u4e9b\u50cf\uff0c\u5173\u952e\u5979\u4e0b\u5348\u8eab\u4f53\u5c31\u4e0d\u8212\u670d\uff0c\u5374\u4e00\u76f4\u62d6\u5230\u73b0\u5728\uff0c\u5f00\u8f66\u76f4\u63a5\u5230\u6700\u8fd1\u7684\u5e02\u7b2c\u4e09\u4eba\u6c11\u533b\u9662\uff0c\u62ff\u7740\u6821\u533b\u9662\u75c5\u5386\u51e0\u4e4e\u662f\u534a\u62b1\u7740\u5c06\u9648\u5983\u84c9\u9001\u8fdb\u6025\u8bca\u697c\u3002</p><p>\u3000\u3000\u8bca\u51b6\u7684\u5927\u592b\u662f\u4e2a\u5934\u53d1\u971c\u767d\u7684\u5973\u7684\uff0c\u8bca\u65ad\u662f\u6025\u6027\u9611\u5c3e\u708e\uff0c\u5979\u7ffb\u770b\u4e86\u4e00\u4e0b\u5f20\u606a\u5e26\u8fc7\u6765\u7684\u6821\u533b\u9662\u75c5\u5386\uff0c\u7709\u5934\u76b1\u7740\u4e22\u5230\u4e00\u8fb9\uff0c\u6ca1\u6709\u8bf4\u4ec0\u4e48\u3002\u5f20\u606a\u77a5\u4e86\u4e00\u773c\u75c5\u5386\uff0c\u6068\u4e0d\u5f97\u5c06\u6821\u533b\u9662\u7ed9\u9648\u5983\u84c9\u8bca\u6cbb\u7684\u533b\u751f\u6495\u6210\u788e\u7247\uff0c\u75c5\u5386\u4e0a\u7adf\u7136\u5f00\u51fa\u6b62\u75db\u7247\u6765\uff0c\u96be\u602a\u5973\u5927\u592b\u770b\u4e86\u4f1a\u7709\u5934\u5927\u76b1\u6ca1\u6709\u542d\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u6400\u7740\u9648\u5983\u84c9\u53bb\u89c2\u5bdf\u5ba4\uff0c\u536b\u751f\u95f4\u5728\u9633\u53f0\u4e0a\uff0c\u9648\u5983\u84c9\u62ff\u7740\u75c5\u53f7\u670d\u53bb\u9633\u53f0\u4e0a\u7684\u536b\u751f\u95f4\u91cc\u53bb\u6362\u597d\u51fa\u6765\u65f6\uff0c\u6342\u7740\u809a\u5b50\u5feb\u8981\u8737\u6210\u4e00\u56e2\u3002\u8fd9\u65f6\u5019\u8fc7\u6765\u505a\u672f\u524d\u51c6\u5907\u7684\u4e00\u4e2a\u9ad8\u800c\u767d\u80d6\u7684\u62a4\u58eb\u770b\u5230\u5f20\u53e3\u5c31\u8bad\u65a5\u5f20\u606a\uff1a\u201c\u75c5\u4eba\u8fd9\u65f6\u5019\u8981\u534a\u5750\u7740\uff0c\u4f60\u4e0d\u4f1a\u62c9\u8d77\u88ab\u5355\u906e\u7740\u5e2e\u5979\u6362\u8863\u670d\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u53ea\u201c\u55ef\u554a\u201d\u70b9\u5934\u8ba4\u9519\uff0c\u54ea\u6562\u56de\u5634\uff1f\u53c8\u534a\u62b1\u7740\u5c06\u9648\u5983\u84c9\u9001\u5230\u624b\u672f\u51c6\u5907\u5ba4\u4f5c\u672f\u524d\u51c6\u5907\uff0c\u4ed6\u6276\u7740\u9648\u5983\u84c9\u8eba\u5230\u5c0f\u5e8a\u4e0a\uff0c\u5e2e\u5979\u64e6\u989d\u5934\u7684\u6c57\uff0c\u8fd9\u65f6\u5019\u53c8\u6709\u4e2a\u5e74\u8f7b\u7684\u62a4\u58eb\u8fdb\u6765\u3002</p><p>\u3000\u3000\u90a3\u62a4\u58eb\u89c1\u5f20\u606a\u7ad9\u5728\u623f\u95f4\u91cc\u4e0d\u52a8\uff0c\u7709\u5934\u53c8\u626c\u4e86\u626c\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8981\u5e2e\u5979\u5907\u76ae\uff0c\u4f60\u4e2a\u5927\u7537\u4eba\u662f\u4e0d\u662f\u51fa\u53bb\u56de\u907f\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u4e4d\u7136\u60f3\u8d77\u521d\u4e2d\u9611\u5c3e\u708e\u624b\u672f\u7ed9\u4e2a\u5341\u516b\u4e5d\u5e74\u7684\u5c0f\u62a4\u58eb\u5907\u76ae\u5243\u6bdb\u7684\u4e11\u6001\uff0c\u90a3\u65f6\u5019\u624d\u9661\u7136\u6709\u4e86\u6027\u610f\u8bc6\u3002\u7ed9\u62a4\u58eb\u626c\u7709\u770b\u7740\uff0c\u5f20\u606a\u5c34\u5c2c\u7684\u966a\u7b11\u7740\uff0c\u5fd9\u63a8\u95e8\u8d70\u4e86\u51fa\u53bb\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u89c1\u5f20\u606a\u53d7\u7a98\u7684\u6837\u5b50\uff0c\u90fd\u8981\u7b11\u51fa\u6765\uff0c\u53ea\u662f\u5634\u4e00\u51bd\uff0c\u809a\u5b50\u66f4\u75db\u5f97\u5389\u5bb3\u3002</p><p>\u3000\u3000\u7ed9\u9648\u5983\u84c9\u52a8\u624b\u672f\u7684\u662f\u4e2a\u53c8\u77ee\u53c8\u80d6\u7684\u5973\u533b\u751f\uff0c\u5979\u62ff\u4e86\u624b\u672f\u901a\u77e5\u5355\u8ba9\u5f20\u606a\u7b7e\u5b57\uff0c\u56e0\u4e3a\u9648\u5983\u84c9\u7684\u75c5\u60c5\u62d6\u4e86\u5feb\u4e00\u5929\uff0c\u6709\u5411\u5f25\u6f2b\u6027\u9611\u5c3e\u708e\u8f6c\u53d8\u7684\u8ff9\u8c61\uff0c\u624b\u672f\u8fd8\u662f\u6709\u4e00\u5b9a\u7684\u5371\u9669\u6027\u3002\u5f20\u606a\u65e0\u6cd5\u53bb\u60f31\uff05\u6216\u80055\uff05\u7684\u53ef\u80fd\u6027\uff0c\u8fd9\u65f6\u5019\u9664\u4e86\u76f8\u4fe1\u533b\u751f\u8fd8\u80fd\u6709\u4ec0\u4e48\u529e\u6cd5\uff0c\u5728\u624b\u672f\u901a\u77e5\u5355\u4e0a\u7b7e\u4e86\u5b57\uff0c\u968f\u540e\u9001\u9648\u5983\u84c9\u8fdb\u624b\u672f\u5ba4\u53bb\u3002</p><p>\u3000\u30001\uff05\u7684\u72b6\u51b5\u5e76\u6ca1\u6709\u53d1\u751f\uff0c\u624b\u672f\u8fdb\u884c\u5f97\u5f88\u5feb\uff0c\u4e0d\u5230\u534a\u4e2a\u5c0f\u65f6\u5c31\u7ed3\u675f\uff0c\u9648\u5983\u84c9\u8138\u4e0a\u770b\u4e0a\u53bb\u6bd4\u8fdb\u624b\u672f\u5ba4\u524d\u597d\u4e00\u4e9b\u4e86\uff0c\u6ca1\u6709\u90a3\u4e48\u75db\u82e6\uff0c\u4e5f\u53ef\u80fd\u662f\u5c40\u90e8\u9ebb\u9189\u7684\u7f18\u6545\u3002</p><p>\u3000\u3000\u5728\u62a4\u58eb\u7684\u534f\u52a9\u4e0b\uff0c\u5c06\u9648\u5983\u84c9\u8f6c\u79fb\u5230\u75c5\u5e8a\u4e0a\uff0c\u63a5\u4e0b\u6765\u53c8\u542c\u62a4\u58eb\u8bf4\u4e86\u8bb8\u591a\u624b\u672f\u540e\u62a4\u7406\u7684\u6ce8\u610f\u4e8b\u9879\u3002\u867d\u7136\u521d\u4e2d\u4e5f\u60a3\u8fc7\u6025\u6027\u9611\u5c3e\u708e\uff0c\u4f46\u662f\u90a3\u65f6\u4ed6\u662f\u75c5\u4eba\uff0c\u6709\u4ed6\u5988\u8dd1\u524d\u8dd1\u540e\uff0c\u4ed6\u6709\u4e09\u5929\u6ca1\u6709\u8fdb\u98df\u8fdb\u6c34\uff0c\u9664\u4e86\u5907\u76ae\u7684\u4e8b\u60c5\uff0c\u5c31\u8fd9\u8bb0\u5fc6\u6700\u6df1\u523b\u4e86\u3002</p><p>\u3000\u3000\u770b\u7740\u9648\u5983\u84c9\u4e00\u8138\u7684\u6194\u60b4\uff0c\u5934\u53d1\u4e5f\u51cc\u4e71\uff0c\u5f20\u606a\u51fa\u53bb\u6253\u4e86\u4e9b\u6c34\uff0c\u540c\u75c5\u623f\u7684\u6709\u4e2a\u7167\u6599\u5973\u513f\u7684\u4e2d\u5e74\u5987\u5973\u501f\u4e86\u4e00\u6761\u5d2d\u65b0\u7684\u6bdb\u5dfe\u7ed9\u4ed6\u5e2e\u9648\u5983\u84c9\u64e6\u8138\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u8eab\u4f53\u865a\u5f31\u7684\u534a\u9760\u7740\u6bef\u5b50\uff0c\u770b\u7740\u5f20\u606a\u5728\u90a3\u91cc\u5fd9\u624b\u5fd9\u811a\u7684\uff0c\u4e5f\u6ca1\u6709\u529b\u6c14\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u9648\u5983\u84c9\u5e73\u65f6\u575a\u5b9a\u800c\u6709\u4e9b\u5014\u5f3a\u7684\u773c\u795e\u8fd9\u65f6\u5019\u624d\u6d41\u9732\u51fa\u695a\u695a\u53ef\u601c\u7684\u5473\u9053\uff0c\u7b11\u4e86\u7b11\uff0c\u6258\u7740\u5979\u7684\u8116\u8ddf\u80cc\u5c06\u6bef\u5b50\u62bd\u51fa\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u7761\u5427\uff0c\u9192\u8fc7\u6765\u80fd\u66f4\u597d\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u7761\u4e0d\u7740\u2026\u2026\u201d\u9648\u5983\u84c9\u53d1\u767d\u7684\u5634\u5507\u62bf\u4e86\u62bf\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8981\u4e0d\u4f60\u5e2e\u6211\u5c06\u5c0f\u5a67\u95f9\u8d77\u6765\uff0c\u8ba9\u5979\u8ddf\u6211\u8bf4\u4f1a\u513f\u8bdd\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u5510\u5a67\u6253\u7535\u8bdd\uff0c\u5c06\u5979\u4ece\u7761\u68a6\u91cc\u95f9\u9192\uff0c\u544a\u8bc9\u5979\u9648\u5983\u84c9\u521a\u52a8\u597d\u624b\u672f\uff0c\u5728\u7535\u8bdd\u91cc\u7ed9\u5510\u5a67\u597d\u4e00\u9635\u57cb\u6028\u4e0d\u53ca\u65f6\u901a\u77e5\u5979\uff0c\u5f20\u606a\u5fd9\u5c06\u8033\u673a\u63d2\u5230\u624b\u673a\u4e0a\u5e2e\u9648\u5983\u84c9\u6234\u4e0a\u8c03\u6574\u597d\u59ff\u52bf\uff0c\u4ed6\u5230\u9633\u53f0\u4e0a\u53bb\u62bd\u70df\u3002</p><p>\u3000\u3000\u5f20\u606a\u62bd\u4e86\u70df\u56de\u6765\uff0c\u770b\u89c1\u9648\u5983\u84c9\u773c\u76ae\u5b50\u53d1\u8f6f\uff0c\u5c31\u5c06\u624b\u673a\u62ff\u4e86\u8fc7\u6765\uff0c\u8ba9\u5979\u4f11\u606f\uff0c\u4ed6\u53bb\u9633\u53f0\u4e0a\u8ddf\u5510\u5a67\u8bf4\u8bdd\u3002\u52a8\u4e86\u624b\u672f\uff0c\u9648\u5983\u84c9\u60c5\u51b5\u5c31\u4e0d\u518d\u4e25\u91cd\uff0c\u5f20\u606a\u8ba9\u5510\u5a67\u4e0d\u7528\u8d76\u56de\u6765\uff0c\u8bf4\u4e86\u4e00\u4f1a\u513f\u8bdd\uff0c\u624d\u6302\u65ad\u7535\u8bdd\u56de\u9648\u5983\u84c9\u75c5\u5e8a\u8fb9\u5750\u7740\u3002</p><p>\u3000\u3000\u591c\u91cc\uff0c\u9648\u5983\u84c9\u9192\u8fc7\u6765\u4e00\u6b21\uff0c\u5230\u5929\u8499\u8499\u4eae\u65f6\uff0c\u5f20\u606a\u4e5f\u575a\u6301\u4e0d\u4f4f\u5c31\u8db4\u5728\u5e8a\u8fb9\u7761\u89c9\uff0c\u5373\u4f7f\u4eca\u5929\u5929\u6c14\u8fd9\u4e48\u70ed\u3001\u9648\u5983\u84c9\u4e00\u8eab\u90fd\u6ca1\u6709\u6d17\u6fa1\uff0c\u5979\u8eab\u4e0a\u8fd8\u662f\u6709\u7740\u6de1\u6de1\u597d\u5473\u7684\u6c14\u606f\uff0c\u505a\u4e86\u4e2a\u68a6\uff0c\u53cc\u624b\u7ed9\u5510\u5a67\u3001\u9648\u5983\u84c9\u7275\u7740\u5728\u4e00\u8fb9\u65e0\u8fb9\u65e0\u9645\u7684\u8349\u6d77\u91cc\u6f2b\u6b65\uff0c\u4e00\u76f4\u6ca1\u6709\u76ee\u7684\u5730\u7684\u5728\u6f2b\u6b65\u3002</p><p>\u3000\u3000\u62a4\u58eb\u6765\u5de1\u623f\u65f6\uff0c\u5f20\u606a\u9192\u8fc7\u6765\uff0c\u53d1\u73b0\u624b\u771f\u7ed9\u7761\u68a6\u4e2d\u7684\u9648\u5983\u84c9\u6293\u7d27\u7740\uff0c\u62a4\u58eb\u8fc7\u6765\u770b\u6302\u5728\u5e8a\u5c3e\u7684\u75c5\u5386\u5361\u5c06\u9648\u5983\u84c9\u4ece\u7761\u68a6\u4e2d\u60ca\u9192\uff0c\u5979\u624d\u677e\u5f00\u5f20\u606a\u7684\u624b\u3002</p><p>\u3000\u3000\u5f20\u606a\u6253\u6765\u6c34\u5e2e\u9648\u5983\u84c9\u6d17\u8fc7\u8138\uff0c\u53c8\u5e2e\u5979\u5c06\u5934\u53d1\u68b3\u6574\u9f50\uff0c\u8ddf\u5979\u8bf4\u9053\uff1a\u201c\u6211\u53bb\u95ee\u4e00\u4e0b\uff0c\u770b\u6709\u6ca1\u6709\u9ad8\u7ea7\u75c5\u5e8a\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u8fd9\u91cc\u86ee\u597d\u7684\uff0c\u4eba\u591a\u8fd8\u70ed\u95f9\u4e00\u4e9b\uff0c\u201d\u9648\u5983\u84c9\u6447\u4e86\u6447\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u4e0a\u5348\u8fd8\u6709\u4e8b\u60c5\uff0c\u5148\u56de\u53bb\u5427\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6211\u627e\u62a4\u58eb\u6216\u8005\u8bf7\u75c5\u623f\u91cc\u7684\u4eba\u5e2e\u5fd9\u2026\u2026\u201d</p><p>\u3000\u3000\u4e0a\u5348\u7684\u6d3b\u52a8\uff0c\u5f20\u606a\u6628\u5929\u8ddf\u675c\u98de\u3001\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u8bf4\u8fc7\u8981\u53c2\u52a0\u7684\uff0c\u4e5f\u4e0d\u80fd\u6307\u6d3e\u8c01\u8fc7\u6765\u7167\u987e\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u884c\uff0c\u6211\u5148\u56de\u53bb\u5e94\u4ed8\u4e00\u4e0b\uff0c\u4e0d\u4f1a\u803d\u6401\u591a\u4e45\uff0c\u4f60\u6709\u4ec0\u4e48\u4e8b\u60c5\u5c31\u7ed9\u6211\u6253\u7535\u8bdd\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u56de\u5230\u5b66\u6821\uff0c\u8ddf\u675c\u98de\u4ed6\u4eec\u8bf4\u8d77\u9648\u5983\u84c9\u591c\u91cc\u52a8\u624b\u672f\u7684\u4e8b\u60c5\uff0c\u9611\u5c3e\u708e\u624b\u672f\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\uff0c\u675c\u98de\u4ed6\u4eec\u8fd9\u65f6\u5019\u90fd\u8dd1\u4e0d\u5f00\u53bb\u63a2\u671b\uff0c\u5f20\u606a\u4e0e\u9a6c\u5411\u4e1c\u3001\u6881\u6587\u6c5f\u4ed6\u4eec\u5306\u5306\u89c1\u4e86\u4e00\u9762\uff0c\u5c31\u53c8\u5f80\u533b\u9662\u91cc\u8d76\uff0c\u534a\u9014\u505c\u8f66\u5e2e\u9648\u5983\u84c9\u4e70\u4e86\u4e9b\u751f\u6d3b\u7528\u54c1\uff0c\u5979\u90a3\u6837\u5b50\u8fd8\u8981\u5728\u533b\u9662\u91cc\u4f4f\u51e0\u5929\u3002</p><p>\u3000\u3000\u4e1c\u5c94\u897f\u5c94\uff0c\u5230\u533b\u9662\u5df2\u7ecf\u8fc7\u5341\u70b9\u4e86\uff0c\u533b\u9662\u91cc\u4eba\u5f88\u591a\uff0c\u5f20\u606a\u7a7f\u8fc7\u7efc\u5408\u5927\u697c\u7684\u95e8\u8bca\u5927\u5385\u7ad9\u5728\u7535\u68af\u524d\u7b49\u7535\u68af\uff0c\u7535\u68af\u95e8\u6253\u5f00\uff0c\u4f55\u5f26\u4ece\u91cc\u9762\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u90fd\u5413\u4e86\u4e00\u8df3\uff1a\u201c\u4f60\u600e\u4e48\u5728\u533b\u9662\u91cc\uff1f\u201d</p><p>\u3000\u3000\u201c\u6211\u8ddf\u82cf\u4e00\u5a77\u6765\u7167\u987e\u9648\u5983\u84c9\u5440\uff1f\u201d\u4f55\u5f26\u7b11\u7740\u8bf4\uff0c\u201c\u8be5\u4e0d\u4f1a\u5acc\u6211\u4eec\u788d\u4e8b\u5427\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u65e0\u8bed\u7684\u7b11\u4e86\u7b11\u3002\u9648\u5983\u84c9\u4e0e\u82cf\u4e00\u5a77\u662f\u9ad8\u4e2d\u540c\u684c\u4e86\u4e09\u5e74\u7684\u540c\u5b66\uff0c\u5173\u7cfb\u5f88\u4eb2\u5bc6\uff0c\u82cf\u4e00\u5a77\u53c8\u8ddf\u4f55\u5f26\u662f\u5927\u5b66\u540c\u5bbf\u820d\u4e09\u5e74\u7684\u540c\u5b66\uff0c\u5979\u4e0e\u9648\u5983\u84c9\u7684\u5173\u7cfb\u4e5f\u76f8\u5f53\u719f\u7edc\uff0c\u5979\u4e0e\u82cf\u4e00\u5a77\u4e0a\u5348\u6ca1\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fc7\u6765\u7167\u987e\u9648\u5983\u84c9\u4e00\u70b9\u90fd\u4e0d\u8ba9\u4eba\u610f\u5916\u3002</p><p>\u3000\u3000\u201c\u4e1c\u897f\u4f60\u90fd\u4e70\u597d\u4e86\uff1f\u201d\u4f55\u5f26\u8eab\u5b50\u5fae\u5fae\u503e\u8fc7\u6765\u4f38\u624b\u62e8\u5f00\u5f20\u606a\u624b\u91cc\u63d0\u7740\u7684\u5851\u6599\u888b\u770b\u4e86\u770b\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u7684\u4e1c\u897f\uff0c\u6211\u4eec\u4e5f\u90fd\u5e26\u8fc7\u6765\u4e86\uff0c\u6211\u4eec\u8981\u7684\u4e1c\u897f\uff0c\u4f60\u8fd9\u91cc\u4e5f\u6ca1\u6709\uff0c\u6211\u8fd8\u8981\u518d\u53bb\u8d85\u5e02\u8d70\u4e00\u8d9f\u2014\u2014\u82cf\u4e00\u5a77\u5728\u5e2e\u9648\u5983\u84c9\u64e6\u6fa1\uff0c\u4f60\u4e0a\u697c\u4e5f\u5e2e\u4e0d\u4e0a\u5fd9\uff0c\u4f60\u966a\u6211\u53bb\u8d85\u5e02\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u63d0\u7740\u5851\u6599\u888b\u8ddf\u7740\u4f55\u5f26\u518d\u5f80\u95e8\u8bca\u5927\u5385\u5916\u8d70\uff0c\u8fd9\u65f6\u5019\u4eba\u5f88\u591a\uff0c\u8fd9\u65f6\u5019\u5916\u9762\u6c14\u6e29\u5347\u4e86\u8d77\u6765\uff0c\u5c55\u793a\u51fa\u5efa\u90ba\u706b\u7089\u57ce\u5e02\u7684\u5a01\u529b\uff0c\u4e5f\u5e78\u4e8f\u8fd9\u7efc\u5408\u5927\u95e8\u662f\u65b0\u5efa\u7684\uff0c\u7a7a\u8c03\u6253\u5f97\u5f88\u8db3\uff0c\u4e0d\u7136\u5149\u8fd9\u4e48\u591a\u770b\u75c5\u7684\u4eba\uff0c\u70ed\u90fd\u8981\u70ed\u6655\u4e00\u4e24\u4e2a\u4eba\u3002</p><p>\u3000\u3000\u7a81\u7136\u6709\u4e2a\u5c0f\u7537\u5b69\u4ece\u540e\u9762\u7a9c\u51fa\u6765\uff0c\u5f20\u606a\u624b\u81c2\u7ed9\u649e\u4e86\u4e00\u4e0b\uff0c\u5851\u6599\u888b\u8131\u4e86\u534a\u5929\uff0c\u4e1c\u897f\u90fd\u6d12\u4e86\u51fa\u6765\uff0c\u5f20\u606a\u8fd8\u60f3\u5f00\u53e3\u63d0\u9192\u90a3\u5c0f\u7537\u5b69\u5c0f\u5fc3\u4e9b\u4e0d\u8981\u649e\u5230\u522b\u4eba\uff0c\u8fd8\u6ca1\u6709\u7b49\u4ed6\u5f00\u53e3\uff0c\u5c31\u5728\u4ed6\u4eec\u8eab\u524d\u6709\u4e2a\u5de6\u817f\u6253\u77f3\u818f\u3001\u62c4\u7740\u62d0\u6756\u7ad9\u5728\u90a3\u91cc\u5f80\u522b\u5904\u5f20\u671b\u7684\u8001\u592a\u592a\u7ed9\u5c0f\u7537\u5b69\u5b50\u8e6d\u4e86\u4e00\u4e0b\u5931\u53bb\u91cd\u5fc3\uff0c\u6454\u5230\u5728\u5730\u3002</p><p>\u3000\u3000\u4f55\u5f26\u5c31\u7ad9\u5728\u90a3\u5973\u4eba\u8eab\u8fb9\uff0c\u4e0b\u610f\u8bc6\u7684\u8981\u53bb\u6400\u5979\u5374\u6ca1\u6709\u6400\u4f4f\uff0c\u5f20\u606a\u4e5f\u987e\u4e0d\u4e0a\u6361\u4e1c\u897f\uff0c\u5148\u4e0e\u4f55\u5f26\u5c06\u8001\u592a\u592a\u6276\u8d77\u6765\uff0c\u95ee\u5979\u8981\u4e0d\u8981\u7d27\uff1f</p><p>\u3000\u3000\u8001\u592a\u592a\u7709\u5934\u5fae\u9501\u7740\uff0c\u9760\u7740\u5927\u5385\u7684\u67f1\u5b50\uff0c\u8270\u96be\u7684\u5c06\u6253\u4e0a\u77f3\u818f\u7684\u5de6\u817f\u70b9\u4e86\u70b9\u5934\uff0c\u8138\u4e0a\u9732\u51fa\u4e9b\u75db\u82e6\u7684\u795e\u8272\uff0c\u5f20\u606a\u5e2e\u8001\u592a\u592a\u5c06\u62d0\u6756\u6361\u56de\u7ed9\u5979\u6491\u7740\uff0c\u8ba9\u4f55\u5f26\u6276\u7740\u8001\u592a\u592a\uff0c\u4ed6\u53c8\u5c06\u6d12\u4e86\u4e00\u5730\u7684\u4e1c\u897f\u6361\u56de\u5851\u6599\u888b\uff0c\u56f4\u770b\u7684\u4eba\u4e5f\u5c31\u6563\u5f00\u4e86\u3002\u8fd9\u65f6\u5019\u6709\u4e2a\u7a7f\u7740\u725b\u4ed4\u77ed\u88e4\u3001\u7a7f\u9ad8\u8ddf\u978b\u7684\u5999\u9f84\u5973\u90ce\u8d70\u8fc7\u6765\uff0c\u6400\u4f4f\u8001\u592a\u592a\uff0c\u8bed\u6c14\u6028\u6bd2\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u600e\u4e48\u8fd9\u4e48\u4e0d\u5c0f\u5fc3\uff0c\u4f60\u817f\u518d\u6454\u65ad\u4e00\u4e0b\uff0c\u8c01\u5e2e\u4f60\u638f\u94b1\uff1f\u4f60\u77e5\u9053\u6211\u4eec\u5df2\u7ecf\u5e2e\u4f60\u82b1\u591a\u5c11\u94b1\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5999\u9f84\u5973\u90ce\u4eba\u957f\u5f97\u86ee\u6f02\u4eae\uff0c\u4f46\u662f\u5bf9\u8001\u592a\u592a\u7684\u53e3\u6c14\u5f88\u4e0d\u5584\uff0c\u4e0d\u8fc7\u8fd9\u662f\u522b\u4eba\u7684\u5bb6\u52a1\u4e8b\uff0c\u4ed6\u4e5f\u61d2\u5f97\u7ba1\uff0c\u8ddf\u4f55\u5f26\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u8d70\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4eec\u649e\u5012\u4eba\u600e\u4e48\u5c31\u60f3\u8d70\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u4f55\u5f26\u8d70\u51fa\u53bb\u6ca1\u6709\u51e0\u6b65\uff0c\u90a3\u4e2a\u5999\u9f84\u5973\u90ce\u7a81\u7136\u51b2\u8fc7\u6765\u4e00\u628a\u62fd\u4f4f\u4ed6\u8ddf\u4f55\u5f26\u7684\u8863\u670d\u4e0d\u8ba9\u4ed6\u4eec\u8d70\uff0c\u5f20\u606a\u8c14\u7136\u7684\u8f6c\u56de\u8eab\uff0c\u8bf4\u9053\uff1a\u201c\u662f\u521a\u624d\u90a3\u4e2a\u5c0f\u7537\u5b69\u5b50\u8e6d\u4e86\u5979\u4e00\u4e0b\uff0c\u8ddf\u6211\u4eec\u6ca1\u6709\u5173\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u91cc\u54ea\u6709\u4ec0\u4e48\u5c0f\u7537\u5b69\u5b50\uff0c\u6211\u770b\u5230\u4f60\u4eec\u649e\u4eba\u4e86\u3002\u201d\u90a3\u5999\u9f84\u5973\u90ce\u677e\u5f00\u5f20\u606a\u7684\u624b\uff0c\u4e00\u624b\u62fd\u4f4f\u4f55\u5f26\u7684\u8863\u89d2\uff0c\u53c8\u4e00\u624b\u6293\u4f4f\u5979\u7684\u80f3\u818a\uff0c\u4e00\u53e3\u54ac\u5b9a\u662f\u4f55\u5f26\u649e\u5012\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u5148\u653e\u5f00\u624b\u3002\u201d\u5f20\u606a\u6293\u4f4f\u5973\u90ce\u7684\u624b\u8155\uff0c\u8ba9\u5979\u677e\u5f00\u4f55\u5f26\uff0c\u6321\u5728\u4f55\u5f26\u9762\u524d\uff0c\u53c8\u4fa7\u8fc7\u5934\u95ee\u8001\u592a\u592a\uff0c\u201c\u4f60\u81ea\u5df1\u8bf4\u8c01\u521a\u624d\u78b0\u5012\u4e86\u4f60\u3002\u201d</p><p>\u3000\u3000\u770b\u5230\u90a3\u5973\u90ce\u671d\u8001\u592a\u592a\u77aa\u5927\u7684\u773c\u775b\uff0c\u5f20\u606a\u6068\u4e0d\u5f97\u4e00\u5df4\u638c\u5c06\u5979\u62bd\u6b7b\uff1b\u8001\u592a\u592a\u987f\u65f6\u5c31\u6539\u4e86\u53e3\uff0c\u6307\u7740\u4f55\u5f26\u4e00\u53e3\u54ac\u5b9a\u662f\u5979\uff1a\u201c\u4e0d\u662f\u4f60\uff0c\u662f\u5979\uff01\u662f\u5979\u649e\u5012\u7684\u6211\u3002\u201d\u53c8\u4e00\u5c41\u80a1\u5750\u5012\u5728\u5730\u4e0a\uff0c\u55ef\u55ef\u5440\u5440\u7684\u53eb\u75db\u8d77\u6765\uff0c\u671d\u56f4\u8fc7\u6765\u7684\u4eba\u53eb\u5c48\u3002</p><p>\u3000\u3000\u5f20\u606a\u538b\u7740\u5fc3\u5934\u7684\u6012\u706b\uff0c\u514d\u5f97\u4eba\u5bb6\u6492\u6cfc\u800c\u9677\u5165\u88ab\u52a8\uff0c\u677e\u5f00\u6293\u5999\u9f84\u5973\u6717\u7684\u624b\uff0c\u5c06\u624b\u91cc\u7684\u5851\u6599\u888b\u7ed9\u56f4\u89c2\u7684\u4eba\u770b\uff1a\u201c\u6211\u4e5f\u7ed9\u90a3\u5c0f\u7537\u5b69\u649e\u4e86\u4e00\u4e0b\uff0c\u5851\u6599\u888b\u90fd\u7834\u4e86\uff0c\u4e1c\u897f\u6d12\u4e86\u4e00\u5730\u8fd8\u521a\u521a\u6361\u8d77\u6765\u2014\u2014\u8001\u592a\u592a\u8dcc\u4e86\u4e00\u8de4\uff0c\u53ef\u80fd\u6ca1\u6709\u770b\u6e05\u695a\u4eba\uff0c\u4f46\u8fd9\u5973\u7684\u8fc7\u6765\uff0c\u8001\u592a\u592a\u5df2\u7ecf\u7ad9\u8d77\u6765\u4e86\u4e00\u4f1a\u513f\uff0c\u5979\u54ea\u53ea\u773c\u775b\u770b\u5230\u6211\u4eec\u649e\u4eba\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u6211\u8bb9\u8bc8\u4f60\uff0c\u4f60\u8bf4\u6211\u8bb9\u8bc8\u4f60\uff01\u201d\u90a3\u5999\u9f84\u5973\u90ce\u7a81\u7136\u5c31\u53d1\u8d77\u98da\u6765\uff0c\u5c06\u5979\u630e\u5728\u624b\u81c2\u4e0a\u7684\u624b\u888b\u62ff\u4e0b\u6765\uff0c\u201c\u4f60\u773c\u775b\u7741\u7741\u5927\uff0c\u8fd9\u662f\u4ec0\u4e48\u5305\uff0c\u8fd9\u662f\u53e4\u5947\uff0c\u610f\u5927\u5229\u53e4\u5947\uff0c\u4f60\u9a6c\u5b50\u51fa\u53bb\u5356\u90fd\u4e70\u4e0d\u8d77\uff01\u201d\u53c8\u795e\u60c5\u6fc0\u52a8\u7684\u6253\u5f00\u624b\u888b\u638f\u51fa\u624b\u6765\uff0c\u201c\u8fd9\u773c\u775b\u7741\u7741\u5927\uff0c\u8fd9\u662f\u4ec0\u4e48\u624b\u673a\uff0c\u8fd9\u662f\u7231\u8fbei380\uff0c\u4f60\u9a6c\u5b50\u51fa\u53bb\u5356\u90fd\u4e70\u4e0d\u8d77\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u600e\u4e48\u8fd9\u4e48\u8bf4\u8bdd\uff01\u201d\u4f55\u5f26\u6ee1\u8138\u6c14\u5f97\u901a\u7ea2\uff0c\u6323\u7740\u7ad9\u51fa\u6765\u8ddf\u5999\u9f84\u5973\u90ce\u6012\u76ee\u5bf9\u8d28\uff0c\u201c\u8001\u5a46\u5a46\u660e\u660e\u662f\u7ed9\u522b\u4eba\u649e\u5012\u4e86\u2026\u2026\u201d\u5979\u4f55\u66fe\u88ab\u4eba\u8fd9\u4e48\u51a4\u5c48\u8fc7\uff0c\u4e24\u53e5\u8bdd\u4e00\u8bf4\uff0c\u773c\u775b\u90fd\u7ea2\u4e86\u8d77\u6765\u3002</p><p>\u3000\u3000\u90a3\u5999\u9f84\u5973\u90ce\u53c8\u4f38\u624b\u62fd\u4f4f\u4f55\u5f26\u7684\u8863\u8896\uff0c\u53e6\u4e00\u53ea\u624b\u62ff\u7740\u624b\u673a\u5c31\u5f00\u59cb\u62e8\u53f7\uff1a\u201c\u4f55\u52c7\uff0c\u4f60\u6b7b\u54ea\u91cc\u53bb\u4e86\uff1f\u4f60\u5feb\u8fc7\u6765\uff0c\u6709\u4eba\u5c06\u4f60\u5988\u649e\u5012\u4e86\uff0c\u817f\u53ef\u80fd\u53c8\u65ad\u4e86\u3002\u649e\u4f60\u5988\u7684\u4e24\u4e2a\u4eba\u8fd8\u8981\u6e9c\u8d70\uff0c\u6b63\u7ed9\u6211\u62fd\u5728\u8fd9\u91cc\uff0c\u6709\u4e2a\u7537\u7684\u8fd8\u60f3\u5bf9\u6211\u52a8\u624b\u52a8\u811a\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8001\u592a\u592a\u4e00\u5c41\u80a1\u5750\u5012\u5728\u5730\u4e0a\uff0c\u4f55\u5f26\u5782\u6cea\u6b32\u6ef4\uff0c\u56f4\u89c2\u7684\u4eba\u4e5f\u4e0d\u77e5\u9053\u771f\u76f8\u7a76\u7adf\u662f\u5982\u4f55\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ac\u5934\u770b\u89c1\u5927\u5385\u6709\u53ea\u6444\u50cf\u673a\u5bf9\u7740\u8fd9\u91cc\uff0c\u6ca1\u60f3\u5230\u4e5d\u4e5d\u5e74\u65b0\u5efa\u7684\u533b\u9662\u7efc\u5408\u5927\u697c\u90fd\u88c5\u4e86\u76d1\u63a7\u7cfb\u7edf\uff0c\u4e0d\u60f3\u5c06\u8fd9\u65f6\u5019\u5c06\u77db\u76fe\u6fc0\u5316\uff0c\u63ea\u4f4f\u90a3\u5973\u4eba\u7684\u624b\uff0c\u6307\u7740\u90a3\u91cc\uff1a\u201c\u4f60\u773c\u775b\u7741\u5927\u4e00\u4e9b\uff0c\u90a3\u53ea\u6444\u50cf\u673a\u5c06\u8fd9\u91cc\u90fd\u62cd\u4e0b\u6765\u4e86\uff01\u6211\u4eec\u8981\u4e0d\u8981\u627e\u533b\u9662\u4fdd\u5b89\u770b\u4e00\u4e0b\u76d1\u63a7\u5f55\u50cf\uff1f\u201d\u731b\u7136\u5c06\u90a3\u5973\u7684\u624b\u7529\u5f00\uff0c\u90a3\u5973\u7684\u987f\u65f6\u6123\u5728\u90a3\u91cc\uff0c\u5979\u6ca1\u6709\u60f3\u5230\u8fd8\u6709\u6444\u50cf\u5934\u5bf9\u7740\u8fd9\u91cc\uff0c\u4e00\u65f6\u95f4\u6ca1\u6709\u53d1\u73b0\u3002</p><p>\u3000\u3000\u5999\u9f84\u5973\u90ce\u4e0e\u8001\u592a\u592a\u7684\u8868\u60c5\uff0c\u4f17\u4eba\u90fd\u770b\u5728\u773c\u91cc\uff0c\u987f\u65f6\u54c4\u7136\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u60f3\u4e5f\u6068\uff0c\u8981\u4e0d\u662f\u6709\u8fd9\u6444\u50cf\u5934\uff0c\u4eca\u5929\u8fd8\u771f\u89e3\u91ca\u4e0d\u6e05\u695a\u4e86\uff0c\u53c8\u4e0d\u80fd\u5728\u4f17\u4eba\u56f4\u89c2\u4e0b\u52a8\u624b\u6253\u5973\u4eba\uff0c\u8981\u6362\u4e2a\u5730\u65b9\uff0c\u624d\u4e0d\u7ba1\u662f\u4e0d\u662f\u5973\u7684\uff0c\u4e00\u5df4\u638c\u90fd\u62bd\u4e0a\u53bb\uff1a\u201c\u4f60\u8fd8\u4e0d\u662f\u8bb9\u8bc8\uff0c\u4f60\u8981\u8bb9\u8bc8\uff0c\u8fd9\u4e9b\u4e1c\u897f\u90fd\u7ed9\u4f60\uff01\u201d\u5c06\u5851\u6599\u888b\u8fde\u91cc\u9762\u4e1c\u897f\u90fd\u4e22\u5230\u90a3\u5973\u4eba\u8138\u4e0a\uff0c\u201c\u770b\u4f60\u7a7f\u7684\u4eba\u6a21\u72d7\u6837\u7684\uff0c\u8fd8\u6709\u51e0\u5206\u59ff\u8272\uff0c\u771f\u4ed6\u5988\u7684\u6076\u5fc3\uff01\u201d\u4e5f\u6ca1\u6709\u5fc3\u60c5\u518d\u51fa\u53bb\u4e70\u4e1c\u897f\uff0c\u90fd\u6025\u5f97\u8981\u53d1\u6296\uff0c\u62c9\u7740\u4f55\u5f26\u7684\u624b\u5c31\u5f80\u7535\u68af\u90a3\u8fb9\u8d70\u53bb\u3002</p><p>\u3000\u3000\u56de\u5230\u75c5\u623f\uff0c\u82cf\u4e00\u5a77\u521a\u5e2e\u9648\u5983\u84c9\u64e6\u6d17\u8fc7\u8eab\u5b50\uff0c\u770b\u7740\u5f20\u606a\u62c9\u7740\u4f55\u5f26\u7684\u624b\u8fdb\u6765\uff0c\u5634\u5df4\u5f20\u5f97\u8001\u5927\uff0c\u4e0d\u53ef\u601d\u5f02\u7684\u6307\u7740\u4ed6\u4eec\uff1a\u201c\u4f60\u4eec\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u53d1\u73b0\u6c14\u5f97\u53d1\u6655\uff0c\u62c9\u7740\u4f55\u5f26\u7684\u624b\u4e00\u76f4\u6ca1\u6709\u677e\u5f00\uff0c\u4f55\u5f26\u62a2\u7740\u5fff\u6068\u7684\u5c06\u521a\u624d\u5728\u5927\u5385\u4e0a\u9047\u5230\u90a3\u6869\u4e8b\u8bf4\u4e86\u51fa\u6765\uff0c\u5c31\u7b97\u5230\u4e86\u697c\u4e0a\uff0c\u8bf4\u8bdd\u90fd\u6c14\u5f97\u8eab\u5b50\u6709\u4e9b\u53d1\u6296\u3002</p><p>\u3000\u3000\u5f20\u606a\u638f\u51fa\u624b\u673a\uff0c\u95ee\u9648\u5983\u84c9\uff1a\u201c\u6211\u5e2e\u4f60\u4e70\u4e86\u4e9b\u4e1c\u897f\uff0c\u521a\u624d\u4e00\u751f\u6c14\uff0c\u90fd\u4e22\u90a3\u5a18\u4eec\u8138\u4e0a\u4e86\uff0c\u6211\u8ba9\u4eba\u4e70\u4e86\u518d\u9001\u8fc7\u6765\uff0c\u4f60\u8fd8\u9700\u8981\u4ec0\u4e48\u4e1c\u897f\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5148\u6b47\u6b47\u6c14\u5427\uff0c\u201d\u9648\u5983\u84c9\u6e29\u5a49\u800c\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4e0d\u503c\u5f97\u4e3a\u8fd9\u79cd\u4eba\u751f\u6c14\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6253\u7535\u8bdd\u901a\u77e5\u4eba\u518d\u53bb\u4e70\u4e9b\u751f\u6d3b\u7528\u54c1\u6765\uff0c\u56e0\u4e3a\u9648\u5983\u84c9\u8981\u51e0\u5929\u4e0d\u80fd\u8fdb\u98df\uff0c\u4e2d\u5348\u5403\u996d\uff0c\u4ed6\u8ddf\u4f55\u5f26\u8fd8\u6709\u82cf\u4e00\u5a77\u8fd8\u662f\u51fa\u53bb\u5403\u7684\u597d\uff0c\u5728\u75c5\u623f\u91cc\u5403\u80fd\u998b\u6b7b\u9648\u5983\u84c9\u3002</p><p>\u3000\u3000\u8fc7\u4e86\u5927\u7ea6\u6709\u534a\u5c0f\u65f6\uff0c\u542c\u89c1\u8fc7\u9053\u91cc\u5435\u5435\u56b7\u56b7\u7684\uff0c\u4e0d\u77e5\u9053\u8fd9\u5c42\u697c\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5f20\u606a\u4e0e\u4f55\u5f26\u8d70\u5230\u8fc7\u9053\u91cc\uff0c\u5c31\u770b\u89c1\u521a\u624d\u90a3\u5973\u7684\u8ddf\u4e00\u4e2a\u7a7f\u7740\u82b1\u886c\u886b\u7684\u7537\u4eba\u8fd8\u6709\u4e24\u4e2a\u7a7f\u4fdd\u5b89\u5236\u670d\u7684\u4eba\u4ece\u9694\u58c1\u75c5\u623f\u91cc\u51fa\u6765\uff0c\u5979\u770b\u5230\u5f20\u606a\u8ddf\u4f55\u5f26\u9732\u8138\uff0c\u6307\u7740\u5979\u4eec\u5c31\u558a\uff1a\u201c\u4f55\u52c7\uff0c\u5c31\u662f\u4ed6\u4eec\uff0c\u5c31\u662f\u4ed6\u4eec\u649e\u5012\u4f60\u5988\uff0c\u8fd8\u5c06\u4e1c\u897f\u7838\u6211\u8138\u4e0a\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u6012\u706b\u6cb8\u817e\uff0c\u4f46\u662f\u4e5f\u77e5\u9053\u8fd9\u4e00\u7537\u4e00\u5973\u81f3\u5c11\u8981\u786e\u8ba4\u76d1\u63a7\u5f55\u50cf\u8bc1\u636e\u7ed9\u6d88\u9664\u6389\u6216\u8005\u53bb\u786e\u8ba4\u76d1\u63a7\u5f55\u50cf\u6ca1\u6709\u7559\u4e0b\u8bc1\u636e\u624d\u6562\u518d\u627e\u4e0a\u95e8\u6765\uff0c\u4e0d\u7ba1\u54ea\u4e00\u79cd\uff0c\u90fd\u8bf4\u660e\u4ed6\u4eec\u8ddf\u8fd9\u533b\u9662\u6709\u52fe\u7ed3\u3002\u5f20\u606a\u60f3\u5fcd\u4e00\u53e3\u6c14\uff0c\u7b49\u901a\u77e5\u4eba\u8fc7\u6765\u518d\u6536\u62fe\u8fd9\u4e9b\u4eba\u6e23\uff0c\u4f46\u662f\u4f55\u5f26\u54ea\u91cc\u63a7\u5236\u5f97\u4f4f\uff0c\u7ad9\u51fa\u6765\u6307\u7740\u90a3\u5973\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u4eec\u600e\u4e48\u8fd9\u4e48\u4e0d\u8981\u8138\uff1f\u201d</p></article>"}}}]);
