(window.webpackJsonp=window.webpackJsonp||[]).push([[104],{126:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516d\u8282 \u57cb\u4f0f",content:"<article><h2>\u7b2c\u5341\u516d\u8282 \u57cb\u4f0f</h2><p>\u4ed6\u663e\u7136\u6ca1\u6709\u6599\u5230\u8fd9\u91cc\u6b63\u5728\u53d1\u751f\u4e00\u573a\u5e76\u4e0d\u534e\u4e3d\u7684\u6218\u6597\uff0c\u4ed6\u83ab\u540d\u5176\u5999\u5730\u98d8\u843d\u4e0b\u6765\uff0c\u770b\u7740\u573a\u5185\uff0c\u7136\u540e\u8ba4\u51fa\u4e86\u6885\u83f2\u65af\u3002\u81f3\u4e8e\u743c\u6069\uff0c\u4ed6\u5012\u6ca1\u4ec0\u4e48\u5370\u8c61\u3002</p><p>\u5c11\u5e74\u7565\u7565\u72b9\u8c6b\u4e86\u4e00\u4e0b\uff0c\u4e3e\u8d77\u624b\u4e2d\u7684\u5377\u8f74\uff0c\u201c\u8428\u5a1c\u8fe6\u6cd5\u65af\uff0c\u201d\u4ed6\u9ad8\u58f0\u53eb\u51fa\u94f6\u9f99\u5e7d\u7075\u7684\u539f\u540d\uff0c\u5377\u8f74\u4e0a\u653e\u5c04\u51fa\u91d1\u8272\u7684\u5149\u8292\uff0c\u7167\u8000\u5f97\u6574\u4e2a\u6d1e\u7a9f\u90fd\u4eae\u5802\u8d77\u6765\uff0c\u201c\u8428\u5a1c\u8fe6\u6cd5\u65af\uff0c\u201d\u4ed6\u9ad8\u58f0\u547c\u5524\u7740\uff0c\u201c\u4ee5\u5927\u672f\u58eb\u6258\u5c14\u65af\uff08Archsorcerer Torth\uff09\u4e4b\u540d\uff0c\u8bf7\u4f60\u542c\u4ece\u6211\u7684\u6307\u4ee4\u3002\u201d</p><p>\u6b63\u5728\u548c\u6885\u83f2\u65af\u7f20\u6597\u7684\u5de8\u5927\u989a\u9aa8\u9000\u51fa\u6218\u56e2\uff0c\u4e00\u9635\u6d53\u96fe\u4e4b\u540e\uff0c\u5b83\u6d88\u5931\u4e86\uff0c\u53d6\u800c\u4ee3\u4e4b\u7684\u662f\u5de8\u5927\u7684\u94f6\u9f99\u865a\u50cf\uff0c\u5979\u53c8\u73b0\u51fa\u4e86\u5e7d\u7075\u72b6\u6001\u3002\u201c\u6258\u5c14\u65af\uff0c\u201d\u5979\u4f4e\u541f\u7740\uff0c\u773c\u775b\u76ef\u7740\u5c11\u5e74\u624b\u4e2d\u62ff\u7740\u7684\u90a3\u5f20\u5377\u8f74\uff0c\u201c\u90a3\u4e2a\u4e0d\u5b88\u4fe1\u7528\u7684\u6df7\u86cb\u2026\u2026\u597d\u5427\uff0c\u827e\u683c\u5170\u7279\uff0c\u4f60\u6765\u4e86\u3002\u201d</p><p>\u5c11\u5e74\u70b9\u70b9\u5934\uff0c\u63e1\u7740\u5377\u8f74\u4e0a\u524d\u4e24\u6b65\u3002\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4ed6\u95ee\uff0c\u4e0d\u662f\u95ee\u94f6\u9f99\uff0c\u4e5f\u4e0d\u662f\u743c\u6069\uff0c\u800c\u662f\u770b\u7740\u6885\u83f2\u65af\uff0c\u4eff\u4f5b\u5979\u7684\u8bdd\u624d\u503c\u5f97\u4fe1\u8d56\u4f3c\u7684\u3002\u8fd9\u771f\u662f\u83ab\u540d\u5176\u5999\uff0c\u96be\u9053\u4ed6\u90fd\u5fd8\u4e86\u524d\u51e0\u5929\u662f\u8c01\u5728\u903c\u95ee\u4ed6\u7684\u5e74\u9f84\u4e48\uff1f</p><p>\u7ecf\u8fc7\u6fc0\u6218\uff0c\u6885\u83f2\u65af\u7684\u795e\u60c5\u5df2\u7ecf\u975e\u5e38\u75b2\u60eb\uff0c\u51ed\u501f\u9ad8\u8d85\u7684\u6b66\u6280\u548c\u7ecf\u9a8c\uff0c\u4ee5\u53ca\u94f6\u9f99\u76ae\u7532\u7684\u4fdd\u62a4\uff0c\u5979\u5e76\u6ca1\u6709\u771f\u6b63\u88ab\u90a3\u53ea\u5de8\u5927\u989a\u9aa8\u54ac\u4f24\uff0c\u4f46\u4f53\u529b\u6d88\u8017\u6781\u5927\u3002\u8bf4\u5230\u8fd9\u91cc\uff0c\u743c\u6069\u5012\u662f\u975e\u5e38\u5947\u602a\uff0c\u6885\u83f2\u65af\u7684\u94f6\u7532\u4e0a\uff0c\u663e\u7136\u4e5f\u675f\u7f1a\u7740\u4e00\u53ea\u94f6\u9f99\u5e7d\u7075\uff0c\u5979\u5230\u5e95\u548c\u6885\u83f2\u65af\u662f\u654c\u662f\u53cb\uff1f\u82e5\u8bf4\u662f\u654c\u4eba\uff0c\u4f3c\u4e4e\u53c8\u4e0d\u592a\u50cf\uff0c\u56e0\u4e3a\u6885\u83f2\u65af\u4e00\u76f4\u5728\u4f7f\u7528\u8fd9\u4ef6\u76ae\u7532\u800c\u6ca1\u6709\u88ab\u4f24\u5bb3\u5230\uff0c\u8fd9\u4e0d\u5408\u5e38\u7406\uff1b\u82e5\u8bf4\u662f\u670b\u53cb\uff0c\u600e\u4e48\u6298\u817e\u8fd9\u4e48\u6fc0\u70c8\uff0c\u5979\u8fd8\u4e0d\u51fa\u6765\u529d\u67b6\u3002</p><p>\u542c\u5230\u5c11\u5e74\u8be2\u95ee\uff0c\u6885\u83f2\u65af\u7b80\u8981\u5730\u89e3\u91ca\u4e86\u4e00\u4e0b\u7f18\u7531\u3002\u5c11\u5e74\u76ef\u7740\u5979\uff0c\u4eff\u4f5b\u5728\u5224\u65ad\u8fd9\u756a\u8bdd\u7684\u771f\u5047\uff0c\u4f46\u6700\u540e\u4ed6\u663e\u7136\u76f8\u4fe1\u4e86\u3002\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u5c11\u5e74\u8bf4\uff0c\u201c\u90a3\u4e48\u2026\u2026\u5443\uff0c\u8fd9\u662f\u4ef6\u5f88\u4ee4\u4eba\u9057\u61be\u7684\u4e8b\u60c5\uff0c\u7c73\u8389\u4e9a\u59c6\uff0c\u201d\u4ed6\u770b\u7740\u94f6\u9f99\u5e7d\u7075\uff0c\u201c\u4f60\u7684\u4eb2\u4eba\u906d\u9047\u4e86\u4e0d\u5e78\u3002\u4e0d\u8fc7\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u8fd9\u548c\u8fd9\u4f4d\u6885\u83f2\u65af\u5c0f\u59d0\u5e76\u6ca1\u6709\u5173\u7cfb\u2026\u2026\u201d</p><p>\u201c\u5979\u627f\u8ba4\u662f\u5979\u6bcd\u4eb2\u6740\u4e86\u8428\u83f2\u8389\u4e9a\u65af\uff01\u201d\u5e7d\u7075\u6012\u543c\u7740\u3002</p><p>\u201c\u5979\u6bcd\u4eb2\u548c\u5979\u662f\u4e24\u4e2a\u4eba\u3002\u201d\u5c11\u5e74\u8bf4\u3002</p><p>\u201c\u597d\u5427\uff0c\u597d\u5427\uff0c\u201d\u5e7d\u7075\u70e6\u8e81\u5730\u626d\u52a8\u7740\u5979\u90a3\u865a\u65e0\u7684\u5e9e\u5927\u8eab\u8eaf\uff0c\u201c\u6211\u77e5\u9053\u4e86\uff0c\u201d\u5979\u4f3c\u4e4e\u5176\u5b9e\u5bf9\u4e3a\u4eb2\u4eba\u590d\u4ec7\u4e5f\u4e0d\u591a\u4e48\u5728\u610f\uff0c\u201c\u603b\u4e4b\u5feb\u7ed9\u6211\u90a3\u4e1c\u897f\uff01\u201d</p><p>\u5c11\u5e74\u8d70\u4e0a\u524d\uff0c\u5c06\u624b\u4e2d\u6cdb\u7740\u91d1\u5149\u7684\u5377\u8f74\u653e\u5728\u6d1e\u7a9f\u4e2d\u592e\u7684\u68fa\u6750\u4e0a\uff0c\u62ff\u8d77\u4e0a\u9762\u5806\u653e\u7684\u51e0\u672c\u4e66\u3002\u94f6\u9f99\u53d1\u51fa\u9ad8\u5174\u7684\u58f0\u97f3\uff0c\u865a\u50cf\u6e10\u6e10\u6de1\u53bb\uff0c\u5730\u9762\u4e0a\u518d\u5ea6\u51fa\u73b0\u4e86\u90a3\u9ed1\u8272\u7684\u6f29\u6da1\uff0c\u5c06\u68fa\u6750\u8fde\u540c\u4e0a\u9762\u7684\u5377\u8f74\u4e5f\u541e\u566c\u8fdb\u53bb\uff0c\u6700\u7ec8\u4e00\u5207\u90fd\u6d88\u5931\u4e0d\u89c1\uff0c\u552f\u6709\u60ac\u6d6e\u5728\u56db\u58c1\u7684\u5b88\u62a4\u8005\u4e4b\u7403\uff0c\u4f9d\u65e7\u5728\u9759\u9759\u6cdb\u7740\u5e7d\u5149\u3002</p><p>\u201c\u8d70\u5427\u3002\u201d\u5c11\u5e74\u8bf4\uff0c\u6367\u7740\u624b\u4e2d\u7684\u4e66\uff0c\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u8bf4\u3002</p><p>\u203b\u203b\u203b</p><p>\u94f6\u9f99\u5e7d\u7075\u867d\u7136\u9000\u53bb\uff0c\u5c01\u4f4f\u6d1e\u53e3\u7684\u51b0\u5899\u5374\u4f9d\u65e7\u8fd8\u5728\uff0c\u743c\u6069\u6b63\u5728\u5934\u75bc\u600e\u4e48\u6253\u5f00\u5b83\uff0c\u4ed6\u73b0\u5728\u9690\u9690\u6709\u4e9b\u540e\u6094\uff0c\u89c9\u5f97\u4e0d\u8be5\u5b8c\u5168\u653e\u5f03\u6389\u5851\u80fd\u548c\u4ea1\u7075\u4e24\u5927\u5b66\u6d3e\u7684\u6cd5\u672f\u8bad\u7ec3\uff0c\u5426\u5219\u53ea\u8981\u4e00\u4e2a\u975e\u5e38\u7b80\u5355\u7684\u71c3\u70e7\u4e4b\u624b\uff0c\u5e94\u8be5\u5c31\u80fd\u878d\u5316\u51b0\u5899\uff0c\u4e0d\u81f3\u4e8e\u8fd9\u4e48\u9ebb\u70e6\u3002</p><p>\u5355\u5355\u5982\u6b64\u5012\u4e5f\u7f62\u4e86\uff0c\u4f46\u5851\u80fd\u3001\u4ea1\u7075\u662f\u653b\u51fb\u6027\u6700\u5f3a\u7684\u4e24\u5927\u5b66\u6d3e\uff0c\u743c\u6069\u4e00\u4e3e\u5168\u90e8\u653e\u5f03\uff0c\u5bfc\u81f4\u7684\u7ed3\u679c\u5c31\u662f\u73b0\u5728\u5e38\u5e38\u53ea\u80fd\u9760\u4e00\u53d1\u77f3\u5f39\u672f\u4fdd\u547d\uff0c\u8fd9\u5c31\u5f88\u72fc\u72c8\u3002\u5deb\u5e08\u8fd9\u7897\u996d\uff0c\u5982\u679c\u80fd\u4e00\u76f4\u5403\u4e0b\u53bb\uff0c\u7ec3\u5230\u9ad8\u6df1\u6c34\u51c6\uff0c\u81ea\u7136\u662f\u4e0a\u5929\u5165\u5730\u65e0\u6240\u4e0d\u80fd\uff0c\u5343\u519b\u4e07\u9a6c\u4e2d\u95f2\u5ead\u4fe1\u6b65\uff0c\u8fdc\u8fdc\u8d85\u8d8a\u4f53\u529b\u548c\u5e38\u8bc6\u7684\u754c\u9650\uff0c\u8fd1\u4e4e\u4e0d\u53ef\u601d\u8bae\u5883\u754c\u3002\u4f46\u95ee\u9898\u662f\uff0c\u5728\u8fd8\u6ca1\u7ec3\u5230\u9ad8\u9636\u6c34\u51c6\u7684\u65f6\u5019\uff0c\u5deb\u5e08\u5176\u5b9e\u662f\u975e\u5e38\u8106\u5f31\u7684\uff0c\u6bd4\u6218\u58eb\u8106\u5f31\u5f97\u591a\uff0c\u65e2\u7f3a\u4e4f\u76d4\u7532\u7684\u4fdd\u62a4\uff0c\u53c8\u7f3a\u4e4f\u6b66\u6280\u683c\u6597\u6280\u5de7\uff0c\u80fd\u4f7f\u7528\u7684\u6cd5\u672f\u4e5f\u5c11\u5f97\u53ef\u601c\u3002</p><p>\u5deb\u5e08\u8981\u60f3\u80fd\u98ce\u5149\uff0c\u81f3\u5c11\u8981\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u4e94\u5c42\uff0c\u5b66\u4f1a\u201c\u4f20\u9001\u672f\u201d\u8fd9\u79cd\u4fdd\u547d\u5927\u6cd5\u5b9d\uff0c\u5927\u6982\u624d\u6709\u5e0c\u671b\u3002\u4f46\u743c\u6069\u76ee\u524d\u662f\u8fde\u7b2c\u56db\u5c42\u90fd\u5c61\u8bd5\u4e0d\u6210\uff0c\u4f55\u8c08\u7b2c\u4e94\u5c42\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u5f53\u65f6\u4ed6\u5728\u5b66\u6821\u91cc\uff0c\u66fe\u7ecf\u5bf9\u8299\u857e\u72c4\u5938\u5938\u5176\u8c08\uff0c\u8bf4\u5851\u80fd\u3001\u4ea1\u7075\u662f\u52a3\u7b49\u5b66\u6d3e\u7684\u65f6\u5019\uff0c\u81ea\u5df1\u4e5f\u4e0d\u8fc7\u662f\u4e00\u4e2a\u672a\u6bd5\u4e1a\u7684\u5deb\u5e08\uff0c\u63a5\u89e6\u9b54\u6cd5\u4e0d\u5230\u4e94\u5e74\uff0c\u66f4\u4e0d\u5982\u90a3\u4e9b\u4e16\u5bb6\u5b50\u5f1f\u6709\u5bb6\u5b66\u6e0a\u6e90\u718f\u9676\uff0c\u54ea\u91cc\u771f\u8c08\u5f97\u4e0a\u5bf9\u9b54\u6cd5\u6709\u591a\u4e48\u6df1\u9083\u7684\u89c1\u8bc6\u3002\u90a3\u4e9b\u8bdd\u4e5f\u5c31\u4e0d\u514d\u7a7a\u758f\uff0c\u542c\u8d77\u6765\u50cf\u6a21\u50cf\u6837\uff0c\u5176\u5b9e\u4e0d\u80fd\u4ed4\u7ec6\u8003\u7a76\uff0c\u62ff\u53bb\u9a97\u9a97\u5c0f\u5973\u5b69\u81ea\u7136\u65e0\u59a8\uff0c\u771f\u8981\u81ea\u5df1\u53cd\u601d\u8d77\u6765\uff0c\u5374\u7740\u5b9e\u6709\u4e9b\u5b5f\u6d6a\u5192\u5931\u4e86\u3002</p><p>\u516b\u5927\u5b66\u6d3e\uff0c\u81ea\u4ece\u8010\u745f\u9668\u706d\u540e\u521b\u7acb\uff0c\u5e76\u5b58\u81f3\u4eca\u4e00\u5343\u4e03\u767e\u4f59\u5e74\uff0c\u603b\u662f\u5404\u6709\u6240\u957f\u7684\u5427\u3002\u5982\u679c\u771f\u50cf\u743c\u6069\u4ee5\u524d\u6240\u8ba4\u4e3a\u7684\uff0c\u8981\u5206\u4e2a\u4e09\u516d\u4e5d\u7b49\uff0c\u90a3\u5851\u80fd\u3001\u4ea1\u7075\u5b66\u6d3e\u5c82\u4e0d\u65e9\u65e9\u6d88\u4ea1\u4e86\u3002</p><p>\u4e0d\u8fc7\uff0c\u8fd9\u4e5f\u662f\u65e0\u53ef\u5948\u4f55\u7684\u4e8b\u60c5\u3002\u5982\u679c\u4e0d\u662f\u51fa\u6765\u8fd9\u4e00\u8d9f\uff0c\u4eb2\u8eab\u4f53\u4f1a\u5230\u8fd9\u4e2a\u9053\u7406\uff0c\u4ed6\u6050\u6015\u4f1a\u4e00\u76f4\u90fd\u575a\u6301\u4ee5\u524d\u7684\u89c2\u70b9\u3002\u5982\u4eca\u4e8b\u5df2\u81f3\u6b64\uff0c\u4e5f\u6ca1\u6cd5\u518d\u56de\u5934\u6539\u53d8\u4e86\u3002</p><p>\u679c\u7136\u552f\u6709\u5b9e\u8df5\u624d\u80fd\u83b7\u5f97\u771f\u77e5\u3002\u518d\u4f18\u826f\u7684\u6559\u80b2\uff0c\u518d\u9002\u5408\u7684\u73af\u5883\uff0c\u518d\u4f18\u79c0\u7684\u5e08\u8d44\u548c\u8bbe\u5907\uff0c\u7ec8\u7a76\u4e5f\u4e0d\u80fd\u57f9\u517b\u51fa\u771f\u6b63\u7684\u4eba\u624d\u6765\u3002\u5c31\u7b97\u662f\u6240\u8c13\u7684\u201c\u519b\u4e8b\u6f14\u4e60\u201d\uff0c\u4e5f\u6bd5\u7adf\u662f\u5728\u519b\u961f\u7684\u4fdd\u62a4\u4e0b\uff0c\u7ec8\u7a76\u4e0d\u662f\u771f\u6b63\u7684\u5b9e\u6218\u3002</p><p>\u53ea\u80fd\u4ee5\u540e\u66f4\u52a0\u8c28\u614e\u4e86\u3002</p><p>\u6b63\u5728\u60f3\u7740\u8981\u4e0d\u8981\u53ec\u5524\u4e2a\u571f\u5143\u7d20\u51fa\u6765\u628a\u51b0\u5899\u7838\u5f00\uff0c\u5c11\u5e74\u5374\u671d\u53e6\u5916\u7684\u65b9\u5411\u8d70\u53bb\u3002\u201c\u90a3\u6761\u8def\u4e0d\u901a\u7684\uff0c\u201d\u4ed6\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u53ea\u80fd\u8fdb\u6765\uff0c\u4e0d\u80fd\u51fa\u53bb\u3002\u201d</p><p>\u5c11\u5e74\u9886\u7740\u4e24\u4eba\uff0c\u7a7f\u8fc7\u6d1e\u7a9f\uff0c\u8d70\u5230\u5bf9\u9762\uff0c\u7136\u540e\u743c\u6069\u53d1\u73b0\u539f\u672c\u4e00\u76f4\u4ee5\u4e3a\u662f\u9ed1\u6c89\u6c89\u5899\u58c1\u7684\u5730\u65b9\u8fd8\u6709\u4e00\u4e2a\u9690\u853d\u7684\u6d1e\u53e3\u3002\u201c\u4ece\u8fd9\u91cc\u8d70\u3002\u201d\u5c11\u5e74\u8bf4\uff0c\u6367\u7740\u4e66\u5728\u524d\u9762\u5f15\u8def\u3002</p><p>\u8fd9\u662f\u4e00\u6761\u659c\u659c\u5411\u4e0a\u7684\u8def\uff0c\u5730\u4e0a\u94fa\u7740\u77f3\u677f\uff0c\u4e24\u8fb9\u90fd\u662f\u77f3\u58c1\uff0c\u548c\u6765\u65f6\u7684\u8def\u4f3c\u4e4e\u5dee\u4e0d\u591a\uff0c\u4f46\u6ca1\u6709\u4e86\u90a3\u4e9b\u5b88\u62a4\u8005\u4e4b\u7403\u770b\u7740\u788d\u773c\u3002</p><p>\u201c\u8fd9\u91cc\u662f\u7981\u5730\uff0c\u201d\u8d70\u4e86\u4e00\u6bb5\uff0c\u5c11\u5e74\u5934\u4e5f\u4e0d\u56de\u5730\u5f00\u53e3\u8bf4\uff0c\u201c\u4ee5\u540e\u4e0d\u8981\u5230\u8fd9\u91cc\u6765\u3002\u8fd9\u6b21\u662f\u6211\u6070\u597d\u6765\uff0c\u5426\u5219\u4f60\u4eec\u5c31\u6b7b\u5b9a\u4e86\u3002\u201d</p><p>\u201c\u8c22\u8c22\u3002\u201d\u6885\u83f2\u65af\u7b80\u6d01\u5730\u56de\u7b54\u3002</p><p>\u743c\u6069\u8d81\u673a\u6253\u63a2\u6d88\u606f\uff0c\u60f3\u77e5\u9053\u8fd9\u6761\u94f6\u9f99\u5230\u5e95\u662f\u600e\u4e48\u56de\u4e8b\u3002\u6240\u5e78\uff0c\u5c11\u5e74\u5012\u5f88\u968f\u548c\uff0c\u867d\u7136\u4f3c\u4e4e\u6709\u4e9b\u654f\u611f\uff0c\u4ed6\u7b80\u660e\u627c\u8981\u5730\u628a\u60c5\u51b5\u4ecb\u7ecd\u4e86\u4e00\u904d\uff0c\u987a\u4fbf\u518d\u6b21\u8b66\u544a\u4e24\u4eba\uff0c\u5343\u4e07\u4e0d\u8981\u8e0f\u5165\u6b64\u5904\u3002</p><p>\u6839\u636e\u5c11\u5e74\u7684\u4ecb\u7ecd\uff0c\u539f\u6765\u5728\u5f88\u591a\u5e74\u524d\uff0c\u8fd9\u53ea\u94f6\u9f99\u4e0d\u77e5\u4ece\u4f55\u5904\u6765\u5230\u70db\u5821\u9644\u8fd1\uff0c\u5230\u5904\u6363\u4e71\u60f9\u4e8b\uff0c\u7ed9\u70db\u5821\u9020\u6210\u7684\u5f88\u5927\u7684\u9ebb\u70e6\u3002\u6700\u540e\uff0c\u4e00\u4f4d\u5c45\u4f4f\u7684\u70db\u5821\u7684\u9ad8\u9636\u65bd\u6cd5\u8005\u201c\u5927\u672f\u58eb\u6258\u5c14\u65af\u201d\u51fa\u624b\uff0c\u7528\u5f3a\u5927\u7684\u6cd5\u672f\u5c06\u94f6\u9f99\u675f\u7f1a\u4f4f\uff0c\u547d\u4ee4\u5979\u5fc5\u987b\u4e3a\u70db\u5821\u670d\u52a1\uff0c\u4fdd\u62a4\u6b64\u5730\u7684\u6210\u5458\u3001\u5efa\u7b51\uff0c\u4ee5\u53ca\u66f4\u91cd\u8981\u7684\u4e66\u7c4d\uff0c\u7ea6\u5b9a\u671f\u9650\u662f\u4e8c\u5341\u5e74\u3002\u4e8c\u5341\u5e74\u4e4b\u540e\uff0c\u6258\u5c14\u65af\u5c06\u4f1a\u91ca\u653e\u5979\u3002</p><p>\u4e8c\u5341\u5e74\u7684\u65f6\u95f4\uff0c\u5bf9\u4e8e\u9f99\u6765\u8bf4\u4e0d\u7b97\u4ec0\u4e48\uff0c\u4f46\u5929\u6709\u4e0d\u6d4b\u98ce\u4e91\u7684\u662f\uff0c\u5728\u7b2c\u5341\u4e94\u5e74\u65f6\uff0c\u6258\u5c14\u65af\u8eab\u6545\u4e86\u2014\u2014\u800c\u4e14\u8fd9\u4f4d\u5927\u672f\u58eb\u5728\u53bb\u4e16\u524d\uff0c\u6ca1\u6765\u5f97\u53ca\u4e3a\u94f6\u9f99\u89e3\u5f00\u675f\u7f1a\u3002\u8fd9\u5bfc\u81f4\u7684\u7ed3\u679c\u5c31\u662f\uff0c\u94f6\u9f99\u88ab\u8fd9\u4e2a\u6cd5\u672f\u675f\u7f1a\u7740\uff0c\u4e00\u76f4\u4e3a\u70db\u5821\u6548\u529b\u3002</p><p>\u5927\u672f\u58eb\u6258\u5c14\u65af\u662f\u5386\u53f2\u4e0a\u540d\u58f0\u6700\u663e\u8d6b\u7684\u65bd\u6cd5\u8005\u4e4b\u4e00\uff0c\u4ed6\u5f53\u65f6\u65bd\u52a0\u7684\u9b54\u6cd5\u592a\u8fc7\u5f3a\u5927\u4e86\uff0c\u5bfc\u81f4\u540e\u6765\u70db\u5821\u91cc\u7684\u5deb\u5e08\u6839\u672c\u6ca1\u6709\u8fd9\u4e2a\u80fd\u529b\u89e3\u5f00\uff08\u5176\u5b9e\u743c\u6069\u6000\u7591\u662f\u4e0d\u60f3\u89e3\u5f00\uff0c\u56e0\u4e3a\u8fd9\u6837\u4e00\u6765\u5c31\u591a\u4e86\u4e00\u4e2a\u514d\u8d39\u6c38\u4e45\u7684\u94f6\u9f99\u536b\u5175\uff09\u3002\u53cd\u6b63\u7ed3\u679c\u5c31\u662f\uff0c\u8fd9\u4f4d\u88ab\u70db\u5821\u4eba\u79f0\u547c\u4e3a\u201c\u7c73\u8389\u4e9a\u59c6\u201d\u7684\u94f6\u9f99\uff0c\u4ece\u6682\u4f4f\u8005\u53d8\u6210\u4e86\u6c38\u4e45\u5c45\u6c11\u3002</p><p>\u94f6\u9f99\u6ca1\u6cd5\u6446\u8131\u6cd5\u672f\u7684\u675f\u7f1a\uff0c\u88ab\u8feb\u4e3a\u70db\u5821\u6548\u529b\uff0c\u5374\u4e0d\u7b49\u4e8e\u8bf4\u5fc3\u7518\u60c5\u613f\u3002\u5f53\u610f\u8bc6\u5230\u81ea\u7531\u65e0\u671b\u540e\uff0c\u5979\u53d8\u5f97\u8d8a\u6765\u8d8a\u813e\u6c14\u66b4\u8e81\uff0c\u6027\u683c\u6076\u52a3\uff0c\u5bf9\u4e8e\u90a3\u4e9b\u8bd5\u56fe\u8fdb\u5165\u70db\u5821\u5077\u4e66\u7684\u5bb6\u4f19\u8fdb\u884c\u51f6\u6b8b\u800c\u91ce\u86ee\u7684\u653b\u51fb\uff0c\u628a\u4ed6\u4eec\u7684\u5c38\u4f53\u54ac\u6210\u4e00\u5757\u4e00\u5757\u7684\u788e\u7247\uff0c\u6d12\u5f97\u70db\u5821\u5230\u5904\u90fd\u662f\u3002</p><p>\u7136\u540e\uff0c\u53d1\u751f\u4e86\u4e00\u4ef6\u610f\u5916\u3002</p><p>\u4e00\u7fa4\u5f3a\u5927\u7684\u5361\u6797\u73ca\u5deb\u5e08\uff0c\u8bd5\u56fe\u4ece\u70db\u5821\u4e2d\u76d7\u53d6\u4e00\u4efd\u73cd\u8d35\u8d44\u6599\uff0c\u4ed6\u4eec\u9996\u5148\u9762\u4e34\u7684\u969c\u788d\u5c31\u662f\u8fd9\u53ea\u94f6\u9f99\u5b88\u536b\u3002\u6700\u540e\uff0c\u5deb\u5e08\u4eec\u82b1\u4e86\u4e24\u5e74\u65f6\u95f4\uff0c\u7ecf\u8fc7\u5468\u5bc6\u51c6\u5907\uff0c\u7ec8\u4e8e\u91ca\u653e\u51fa\u4e00\u4e2a\u5f3a\u5927\u7684\u4f24\u5bb3\u9b54\u6cd5\uff0c\u5c06\u94f6\u9f99\u6740\u6b7b\u4e86\u3002\u9f99\u7684\u8eaf\u4f53\u5feb\u901f\u8150\u673d\u3001\u5d29\u6e83\uff0c\u8840\u8089\u9aa8\u9abc\u5168\u90fd\u5316\u4e3a\u5c18\u571f\uff0c\u9664\u4e86\u4e00\u5bf9\u989a\u9aa8\u3002</p><p>\u867d\u7136\u6b7b\u4ea1\uff0c\u4f46\u5927\u672f\u58eb\u6258\u5c14\u65af\u7684\u5f3a\u5927\u9b54\u6cd5\u4f9d\u65e7\u53d1\u6325\u7740\u4f5c\u7528\uff0c\u7262\u7262\u675f\u7f1a\u4f4f\u4e86\u94f6\u9f99\u7684\u5e7d\u7075\u3002\u8fd9\u5bfc\u81f4\u4e86\u66f4\u6076\u52a3\u7684\u7ed3\u679c\uff0c\u53d8\u6210\u5e7d\u7075\u540e\uff0c\u53d7\u5230\u8d1f\u80fd\u91cf\u7684\u4fb5\u67d3\uff0c\u7c73\u8389\u4e9a\u59c6\u6bd4\u751f\u524d\u66f4\u52a0\u7684\u51f6\u66b4\u3001\u6076\u6bd2\u548c\u6b8b\u5fcd\uff0c\u5e76\u4e14\u813e\u6c14\u53e4\u602a\uff0c\u65e0\u53ef\u7406\u55bb\u3002</p><p>\u7531\u4e8e\u62c5\u5fc3\u6765\u8bbf\u8005\u7684\u5b89\u5168\uff0c\u70db\u5821\u7684\u9886\u8896\u4eec\u6700\u7ec8\u505a\u4e86\u4e2a\u51b3\u7b56\uff0c\u4ed6\u4eec\u8058\u8bf7\u4e86\u4e00\u961f\u5deb\u5e08\u548c\u6b66\u50e7\uff0c\u4ee5\u81ea\u6211\u727a\u7272\u4e3a\u4ee3\u4ef7\uff0c\u5c06\u94f6\u9f99\u7684\u5e7d\u7075\u538b\u5236\u4f4f\uff0c\u5c01\u5728\u70db\u5821\u5730\u4e0b\u7684\u6d1e\u7a9f\u4e2d\uff0c\u548c\u6258\u5c14\u65af\u7684\u68fa\u6750\u5728\u4e00\u8d77\u3002</p><p>\u70db\u5821\u7684\u5730\u4e0b\u6709\u51e0\u6761\u901a\u9053\uff0c\u5076\u5c14\u4e5f\u4f1a\u6709\u7a83\u4e66\u8d3c\u8bd5\u56fe\u4ece\u8fd9\u91cc\u8fdb\u5165\u70db\u5821\u6216\u8005\u9003\u8131\uff0c\u4f46\u4ed6\u4eec\u65e0\u4e00\u4f8b\u5916\u5730\u90fd\u88ab\u94f6\u9f99\u5e7d\u7075\u6740\u6b7b\u4e86\u3002\u4ed6\u4eec\u76d7\u7a83\u5230\u624b\u7684\u4e66\uff0c\u5219\u88ab\u94f6\u9f99\u5c0f\u5fc3\u5730\u6536\u8d77\u6765\uff0c\u653e\u5728\u6258\u5c14\u65af\u7684\u68fa\u6750\u4e0a\uff0c\u7b49\u5f85\u7740\u70db\u5821\u7684\u4eba\u6765\u53d6\u8d70\u3002\u5f53\u7136\uff0c\u4ed6\u4eec\u4e5f\u4f1a\u63d0\u4f9b\u4e00\u4e9b\u62a5\u916c\uff0c\u4f5c\u4e3a\u5bf9\u94f6\u9f99\u5e7d\u7075\u7684\u5b89\u629a\u548c\u5956\u52b1\u3002</p><p>\u8fd9\u4e2a\u53eb\u827e\u683c\u5170\u7279\u7684\u5c11\u5e74\uff0c\u5c31\u662f\u6765\u53d6\u90a3\u4e9b\u4e66\u7684\uff0c\u6070\u597d\u6551\u4e86\u6885\u83f2\u65af\u548c\u743c\u6069\u3002</p><p>\u5927\u81f4\u7684\u60c5\u5f62\u5c31\u662f\u5982\u6b64\uff0c\u743c\u6069\u518d\u548c\u6b64\u524d\u6885\u83f2\u65af\u6240\u8bf4\u4e00\u5bf9\u7167\uff0c\u4fbf\u77e5\u9053\u95ee\u9898\u51fa\u5728\u54ea\u91cc\u3002\u6885\u83f2\u65af\u542c\u5230\u7684\u6d88\u606f\u663e\u7136\u4e0d\u5168\uff0c\u662f\u96f6\u788e\u62fc\u51d1\u8d77\u6765\u7684\uff0c\u4e2d\u95f4\u5c11\u4e86\u4e00\u5927\u622a\uff0c\u8fd8\u6709\u4e9b\u5730\u65b9\u524d\u540e\u98a0\u5012\u4e86\u3002</p><p>\u597d\u5427\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u603b\u7b97\u662f\u53c8\u9003\u8fc7\u4e00\u52ab\u4e86\u3002</p><p>\u5730\u4e0b\u6d1e\u7a9f\u6709\u4e24\u6761\u901a\u9053\uff0c\u4e00\u6761\u53ea\u80fd\u8fdb\u4e0d\u80fd\u51fa\uff0c\u5c31\u662f\u743c\u6069\u8fdb\u6765\u7684\u90a3\u6761\uff1b\u53e6\u5916\u4e00\u6761\u53ea\u80fd\u51fa\u4e0d\u80fd\u8fdb\uff0c\u5c31\u662f\u73b0\u5728\u6b63\u5728\u8d70\u7684\u8fd9\u6761\u3002\u6885\u83f2\u65af\u88ab\u94f6\u9f99\u5e7d\u7075\u643a\u5e26\u7684\u8d1f\u80fd\u91cf\u4f24\u5bb3\uff0c\u770b\u8d77\u6765\u53d7\u521b\u4e0d\u8f7b\uff0c\u5979\u7684\u6b65\u4f10\u6709\u4e9b\u6c89\u91cd\u6ede\u788d\uff0c\u8fd9\u5728\u4ee5\u524d\u662f\u4e0d\u53ef\u80fd\u51fa\u73b0\u7684\u60c5\u51b5\u3002\u743c\u6069\u60f3\u53bb\u6276\u5979\uff0c\u4f46\u88ab\u907f\u5f00\u4e86\uff0c\u5979\u7684\u5634\u5507\u7d27\u62bf\u7740\uff0c\u62d2\u7edd\u4ed6\u4eba\u7684\u5e2e\u52a9\u3002</p><p>\u524d\u65b9\u6e10\u6e10\u770b\u5230\u4eae\u5149\uff0c\u770b\u6765\u5df2\u7ecf\u5feb\u8981\u5230\u5730\u8868\u6d1e\u53e3\u4e86\u3002\u6298\u817e\u8fd9\u534a\u5929\uff0c\u5916\u9762\u5e94\u8be5\u65e9\u5df2\u7ecf\u5929\u4eae\u4e86\uff0c\u4e09\u4eba\u52a0\u5feb\u811a\u6b65\u3002</p><p>\u773c\u770b\u5c31\u8981\u8d70\u51fa\u6d1e\u53e3\uff0c\u9aa4\u7136\u95f4\uff0c\u6beb\u65e0\u534a\u70b9\u9884\u5146\u7684\uff0c\u4e00\u56e2\u6d53\u5bc6\u7684\u9ed1\u6697\u7b3c\u7f69\u4e86\u5468\u56f4\u3002</p><p>\u201c\u55e4\uff01\u201d</p><p>\u8f7b\u5fae\u7684\u5229\u5668\u7834\u7a7a\u58f0\u4e00\u63a0\u800c\u8fc7\uff0c\u7d27\u63a5\u7740\u5c31\u662f\u5c11\u5e74\u75db\u82e6\u7684\u60e8\u53eb\uff0c\u5939\u6742\u7740\u6c89\u91cd\u7684\u8089\u4f53\u649e\u4e0a\u5899\u58c1\u7684\u58f0\u97f3\u3002\u743c\u6069\u8fd8\u6ca1\u53cd\u5e94\u8fc7\u6765\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5c31\u89c1\u9ed1\u6697\u4e2d\u94f6\u5149\u7efd\u653e\u800c\u8d77\uff0c\u4eff\u4f5b\u9e23\u96f7\u95ea\u7535\u4e00\u822c\u5212\u8fc7\uff0c\u6885\u83f2\u65af\u53f1\u559d\u4e00\u58f0\uff0c\u671d\u7740\u7a7a\u6c14\u4e2d\u4e00\u5251\u5288\u51fa\u3002</p></article>"}}}]);
