(window.webpackJsonp=window.webpackJsonp||[]).push([[104],{127:function(p){p.exports={chapterTitle:null,title:"\u7b2c1088\u7ae0 \u5343\u79a7\u591c\u5373\u5c06\u6765\u4e34",content:"<article><h2>\u7b2c1088\u7ae0 \u5343\u79a7\u591c\u5373\u5c06\u6765\u4e34</h2><p>\u3000\u3000\u591c\u5e55\u5c06\u71d5\u5f52\u6e56\u7b3c\u7f69\u5176\u4e2d\uff0c\u73af\u6e56\u6563\u843d\u7740\u7a00\u758f\u7684\u5730\u576a\u706f\u6563\u53d1\u51fa\u5e7d\u6de1\u7684\u5149\uff0c\u6708\u5706\u5982\u7389\uff0c\u7ed9\u8f7b\u4e91\u63a9\u6620\uff0c\u60ac\u6302\u5728\u6df1\u9083\u800c\u795e\u79d8\u7684\u591c\u7a7a\uff0c\u770b\u7740\u6e56\u6c34\u4e4b\u4e0a\uff0c\u8fd8\u6709\u8f7b\u96fe\u6d41\u8f6c\u3002</p><p>\u3000\u3000\u5165\u51ac\u540e\u7684\u7b2c\u4e00\u6b21\u5bd2\u6d41\u5982\u671f\u800c\u81f3\uff0c\u65f6\u4e0d\u65f6\u6709\u5bd2\u98ce\u4ece\u697c\u9699\u91cc\u5439\u6765\uff0c\u591c\u5df2\u7ecf\u5f88\u6df1\uff0c\u6e56\u8fb9\u8fd8\u6709\u4e9b\u9752\u5e74\u7537\u5973\u604b\u4eba\u5f88\u8010\u5bd2\u7684\u7f20\u7ef5\u7740\uff0c\u9648\u9759\u62b1\u7740\u5c0f\u7eb8\u7bb1\u5b50\uff0c\u8ddf\u5728\u5f20\u606a\u7684\u540e\u9762\uff0c\u4ece\u8fd9\u4e9b\u9752\u5e74\u7537\u5973\u604b\u4eba\u4eec\u4e2d\u95f4\u7a7f\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u6709\u5bf9\u604b\u4eba\u5728\u524d\u9762\u8d70\uff0c\u7537\u5b69\u5b50\u60f3\u7275\u5973\u5b69\u5b50\u7684\u624b\uff0c\u5973\u5b69\u5b50\u624b\u7ef7\u76f4\u8d34\u7740\u88e4\u7f1d\uff0c\u7537\u5b69\u5b50\u624b\u5f80\u5916\u4f38\u4e86\u4e24\u4e0b\uff0c\u7b2c\u4e8c\u624b\u78b0\u5230\u5973\u5b69\u5b50\u7684\u624b\u80cc\uff0c\u5973\u5b69\u5b50\u5374\u53d7\u60ca\u7684\u624b\u4e00\u7f29\uff0c\u7537\u5b69\u5b50\u5c31\u5148\u6253\u9000\u5802\u9f13\uff0c\u8fd8\u63a9\u9970\u5c34\u5c2c\u7684\u6478\u4e86\u6478\u9f3b\u5b50\uff0c\u51e0\u4e4e\u4ee5\u4e3a\u5973\u5b69\u5b50\u662f\u5728\u62d2\u7edd\uff0c\u795e\u60c5\u6709\u4e9b\u6cae\u4e27\uff0c\u4e24\u4e2a\u4eba\u4f3c\u4e4e\u90fd\u77e5\u9053\u5bf9\u65b9\u5bf9\u81ea\u5df1\u6709\u597d\u611f\uff0c\u5374\u8fd8\u6ca1\u6709\u786e\u8ba4\u604b\u7231\u5173\u7cfb\uff0c\u5927\u5bb6\u90fd\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u3002</p><p>\u3000\u3000\u9648\u9759\u770b\u5728\u773c\u91cc\u76f4\u89c9\u5f97\u597d\u7b11\uff0c\u770b\u7740\u5f20\u606a\u4e00\u672c\u6b63\u7ecf\u677f\u7740\u8138\u7684\u4fa7\u8138\u988a\uff0c\u62ac\u811a\u8f7b\u8f7b\u8e22\u4e86\u4ed6\u4e00\u4e0b\uff0c\u8ba9\u4ed6\u653e\u6162\u811a\u6b65\uff0c\u8981\u662f\u8d70\u592a\u8fd1\uff0c\u4f1a\u8ba9\u8fd9\u5bf9\u604b\u4eba\u66f4\u52a0\u7684\u4e0d\u77e5\u6240\u63aa\u3002</p><p>\u3000\u3000\u201c\u4f60\u770b\u8fd9\u7537\u7684\u591a\u50bb\u554a\uff0c\u5927\u51b7\u5929\u7684\uff0c\u5973\u5b69\u5b50\u5c06\u624b\u9732\u5728\u5916\u9762\uff0c\u4e0d\u5c31\u662f\u671f\u5f85\u7ed9\u4ed6\u6478\u4e00\u628a\uff1f\u8fd9\u5973\u5b69\u5b50\u4e5f\u771f\u662f\uff0c\u7537\u5973\u5e73\u7b49\u90fd\u53eb\u4e86\u597d\u4e9b\u5e74\u4e86\uff0c\u4ed6\u80c6\u5b50\u5c0f\uff0c\u5c31\u4e0d\u4f1a\u4e3b\u52a8\u53bb\u6478\u4ed6\uff1f\u201d\u5f20\u606a\u4e00\u4ed8\u7687\u5e1d\u4e0d\u6025\u592a\u76d1\u6025\u7684\u6a21\u6837\u8d34\u7740\u9648\u9759\u7684\u8033\u6735\u5c0f\u58f0\u7684\u8bf4\u7b11\uff0c\u201c\u6211\u90fd\u66ff\u4ed6\u4eec\u6025\u5f97\u4e0d\u5f97\u4e86\uff0c\u7275\u4e0a\u624b\uff0c\u8be5\u5e72\u561b\u5e72\u561b\u53bb\uff0c\u627e\u4e2a\u6696\u548c\u7684\u89d2\u843d\u4e5f\u6bd4\u5728\u6e56\u8fb9\u5439\u98ce\u5f3a\u554a\uff0c\u73b0\u5728\u5f97\u8981\u6709\u96f6\u4e0b\u4e09\u56db\u5ea6\u4e86\u5427\uff01\u201d</p><p>\u3000\u3000\u9648\u9759\u62bf\u7740\u5507\u800c\u7b11\uff0c\u4e0e\u5f20\u606a\u80a9\u6328\u7740\u80a9\uff0c\u8bf4\u9053\uff1a\u201c\u8c01\u6709\u4f60\u7ecf\u9a8c\u4e30\u5bcc\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u538b\u7740\u811a\u6b65\uff0c\u8ba9\u524d\u9762\u90a3\u5bf9\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u604b\u4eba\u8d70\u5728\u524d\u9762\u3002\u9648\u9759\u62b1\u7740\u7eb8\u7bb1\u5b50\u6328\u7740\u5f20\u606a\u8eab\u8fb9\uff0c\u8fd8\u7279\u610f\u8ba9\u5f20\u606a\u7684\u8eab\u4f53\u5e2e\u81ea\u5df1\u6321\u7740\u98ce\uff0c\u770b\u7740\u8fd9\u5bf9\u5e74\u8f7b\u7684\u604b\u4eba\u4f1a\u600e\u4e48\u6837\u6345\u7834\u6700\u540e\u4e00\u5c42\u7a97\u6237\u7eb8\uff0c\u611f\u89c9\u597d\u7b11\uff0c\u53c8\u89c9\u5f97\u773c\u524d\u7684\u6b64\u666f\u8ba9\u4eba\u611f\u89c9\u4eb2\u5207\u3002</p><p>\u3000\u3000\u5979\u4e0e\u5f20\u606a\u7684\u5173\u7cfb\u5728\u7ecf\u5386\u90a3\u6b21\u8f66\u5185\u4e0d\u80fd\u7b97\u6210\u529f\u7684\u63a5\u543b\u4e4b\u540e\u5c31\u81ea\u7136\u800c\u7136\u7684\u4eb2\u5bc6\u8d77\u6765\uff0c\u80fd\u660e\u767d\u5f7c\u6b64\u7684\u5fc3\u601d\uff0c\u4e5f\u5c06\u81ea\u5df1\u7684\u5fc3\u601d\u6e05\u695a\u65e0\u8bef\u7684\u8868\u8fbe\u51fa\u6765\uff0c\u5b9e\u5728\u6ca1\u6709\u5fc5\u8981\u50cf\u5341\u4e03\u516b\u5c81\u7684\u604b\u4eba\u4e00\u8d77\u5c0f\u5fc3\u7ffc\u7ffc\u8bd5\u63a2\u7740\u63a5\u89e6\uff0c\u9648\u9759\u4e5f\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u81ea\u5df1\u4e0e\u5f20\u606a\u4f1a\u6709\u8db3\u591f\u7684\u65f6\u95f4\u73a9\u8fd9\u79cd\u5e74\u8f7b\u604b\u4eba\u7684\u6e38\u620f\uff0c\u6216\u8bb8\u7a0d\u4e00\u9519\u8fc7\uff0c\u5c31\u8981\u6c38\u8fdc\u9519\u8fc7\u4e86\u3002</p><p>\u3000\u3000\u5728\u5efa\u90ba\uff0c\u8981\u8eb2\u8fc7\u5b50\u5609\u7684\u89c6\u91ce\u5c31\u5f88\u4e0d\u5bb9\u6613\uff0c\u96be\u5f97\u6709\u8fd9\u4e48\u597d\u7684\u673a\u4f1a\u5c06\u5b50\u5609\u8e22\u5f00\uff0c\u5f20\u606a\u8bf4\u8981\u5e2e\u5fd9\u6536\u62fe\u6b8b\u5c40\uff0c\u9648\u9759\u54ea\u6709\u62d2\u7edd\u7684\u9053\u7406\uff1f\u8fd9\u4f1a\u513f\u53c8\u5c06\u52a9\u7406\u652f\u8d70\uff0c\u8ba9\u5979\u6307\u6325\u7269\u7ba1\u4eba\u5458\u5c06\u5927\u4ef6\u7684\u4e1c\u897f\u642c\u5230\u5176\u4ed6\u5730\u65b9\u6682\u65f6\u8d2e\u5b58\u4e0b\u6765\uff0c\u5979\u4e0e\u5f20\u606a\u5e26\u7740\u968f\u8eab\u6240\u9700\u7684\u7269\u54c1\u5230\u6e56\u7554\u6728\u5c4b\u53bb\u3002</p><p>\u3000\u3000\u8d70\u5230\u6e56\u7554\u6728\u5c4b\u8981\u62d0\u8fdb\u53bb\uff0c\u770b\u5230\u7537\u5b69\u5b50\u90fd\u6ca1\u6709\u518d\u6b21\u5c1d\u8bd5\u53bb\u5973\u5b69\u5b50\u7684\u624b\uff0c\u9648\u9759\u90fd\u6025\u5f97\u8981\u6376\u80f8\u987f\u8db3\u4e86\uff0c\u8d70\u5230\u6e56\u7554\u6728\u5c4b\u7684\u5927\u95e8\u524d\uff0c\u624d\u7b11\u51fa\u58f0\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u5c0f\u5b69\u5b50\u8c08\u604b\u7231\u539f\u6765\u662f\u8fd9\u822c\u6a21\u6837\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u5462\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\uff0c\u544a\u8bc9\u9648\u9759\u94a5\u5319\u5728\u81ea\u5df1\u7684\u88e4\u515c\u91cc\u3002</p><p>\u3000\u3000\u201c\u4e8f\u5927\u4e86\u3002\u201d\u9648\u9759\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e8f\u5927\u4e86\uff1f\u201d\u5f20\u606a\u770b\u7740\u9648\u9759\u5728\u591c\u8272\u4e0b\u4eae\u6676\u6676\u7684\u7738\u5b50\uff0c\u95ee\u9053\u3002</p><p>\u3000\u3000\u9648\u9759\u62bf\u5634\u7b11\u7740\uff0c\u4e0d\u8bf4\u8bdd\uff0c\u5c06\u5c0f\u7eb8\u7bb1\u5b50\u53e0\u653e\u5728\u5f20\u606a\u6367\u7740\u5927\u7eb8\u7bb1\u4e0a\u9762\uff0c\u906e\u4f4f\u4ed6\u7684\u8138\uff0c\u624b\u4f38\u8fdb\u4ed6\u7684\u88e4\u515c\u91cc\u638f\u94a5\u5319\uff0c\u624b\u6307\u9694\u7740\u8584\u8584\u7684\u5e03\u80fd\u89e6\u5230\u5f20\u606a\u7684\u5927\u817f\u8089\uff0c\u6e29\u70ed\u7684\uff0c\u8ddf\u4ed6\u8bf4\uff1a\u201c\u4f60\u88e4\u5b50\u7a7f\u5f97\u597d\u8584\u554a\uff01\u4e0d\u51b7\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u6478\u6478\u6211\u7684\u80cc\uff0c\u201d\u5f20\u606a\u5c06\u7eb8\u7bb1\u5b50\u62b5\u5728\u5899\u4e0a\u7701\u4e9b\u529b\u6c14\uff0c\u201c\u4f60\u8ba9\u6211\u6367\u8fd9\u4e48\u591a\u4e1c\u897f\uff0c\u80cc\u5fc3\u90fd\u5728\u6d41\u6c57\uff0c\u54ea\u91cc\u80fd\u611f\u89c9\u5230\u51b7\u554a\uff1f\u201d</p><p>\u3000\u3000\u9648\u9759\u5ae3\u7136\u4e00\u7b11\uff0c\u5c06\u4e0a\u9762\u7684\u5c0f\u7eb8\u7bb1\u5b50\u6367\u56de\u53bb\uff0c\u7ed9\u5f20\u606a\u51cf\u8f7b\u4e9b\u91cd\u91cf\u3002</p><p>\u3000\u3000\u8fdb\u4e86\u5927\u95e8\uff0c\u8fd8\u8981\u5f80\u4fdd\u5168\u7cfb\u7edf\u91cc\u8f93\u6307\u7eb9\uff0c\u5f20\u606a\u521a\u8981\u5c06\u5927\u7eb8\u7bb1\u5b50\u653e\u4e0b\uff0c\u5374\u53c8\u7ed9\u9648\u9759\u5c06\u5c0f\u7eb8\u7bb1\u5b50\u53e0\u5728\u4e0a\u9762\uff0c\u6ca1\u60f3\u5230\u8bb8\u601d\u5df2\u7ecf\u8ba9\u9648\u9759\u7684\u6307\u7eb9\u6570\u636e\u8f93\u5165\u4fdd\u5168\u7cfb\u7edf\u4e86\uff0c\u770b\u7740\u9648\u9759\u5c06\u4e2d\u6307\u8d34\u5728\u6307\u7eb9\u9501\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u7528\u4e2d\u6307\u5f00\u95e8\u7684\u59ff\u6001\u771f\u9177\uff0c\u6211\u4e5f\u8981\u7ed9\u6307\u7eb9\u6570\u636e\u5e93\u91cc\u90fd\u8f93\u5165\u4e2d\u6307\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u7b11\u7740\u53c8\u4f38\u7740\u4e2d\u6307\u671d\u5f20\u606a\u6bd4\u5212\u4e86\u4e00\u4e0b\uff0c\u770b\u7740\u5f20\u606a\u5f20\u5634\u8981\u54ac\u8fc7\u6765\uff0c\u5fd9\u5a07\u7b11\u7740\u8eb2\u5f00\uff0c\u5148\u8d70\u8fdb\u623f\u95f4\u91cc\uff0c\u5c06\u4e24\u53ea\u7eb8\u7bb1\u5b50\u90fd\u7559\u5728\u5f20\u606a\u5904\u7406\u3002</p><p>\u3000\u3000\u697c\u4e0a\u6709\u591a\u4f59\u7684\u5367\u5ba4\uff0c\u53ea\u662f\u88ab\u8925\u4ec0\u4e48\u7684\u8981\u65b0\u94fa\uff0c\u5f20\u606a\u5e2e\u9648\u9759\u5c06\u4e24\u53ea\u7eb8\u7bb1\u5b50\u6367\u4e0a\u53bb\uff0c\u7d2f\u5f97\u6ee1\u5934\u5927\u6c57\uff0c\u5750\u5728\u90a3\u91cc\u770b\u9648\u9759\u5728\u90a3\u91cc\u6536\u62fe\u623f\u95f4\u3002</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u201d\u9648\u9759\u60f3\u8d77\u4e00\u4ef6\u4e8b\u6765\uff0c\u5a07\u6da6\u7684\u5634\u5507\u5fae\u5fae\u5f20\u8d77\uff0c\u6320\u4e86\u6320\u540e\u8111\u52fa\uff0c\u8bf4\u9053\uff0c\u201c\u6709\u4ef6\u4e1c\u897f\u5fd8\u62ff\u4e86\uff0c\u6211\u8fd8\u8981\u53bb\u8d85\u5e02\u91cc\u8dd1\u4e00\u4e0b\uff0c\u8f66\u5e93\u6709\u6ca1\u6709\u8f66\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\uff0c\u201d\u5f20\u606a\u6447\u4e86\u6447\u5934\uff0c\u5934\u51d1\u8fc7\u6765\u770b\u4e86\u770b\u7eb8\u7bb1\u5b50\uff0c\u201c\u751f\u6d3b\u7528\u54c1\u4e0d\u90fd\u62ff\u5168\u4e86\u5417\uff1f\u5b9e\u5728\u4e0d\u884c\uff0c\u4f60\u7528\u8bb8\u601d\u7684\u4e5f\u53ef\u4ee5\uff0c\u5979\u90fd\u5c06\u8ba9\u623f\u5b50\u8ba9\u7ed9\u4f60\u4f4f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e0d\u77e5\u9053\u8bb8\u601d\u5728\u8fd9\u91cc\u6709\u6ca1\u6709\uff1f\u201d\u9648\u9759\u62ff\u8d77\u6765\u624b\u673a\u8d70\u51fa\u53bb\u6253\u7535\u8bdd\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u8d70\u8fdb\u6765\u8bf4\uff0c\u201c\u8bb8\u601d\u8fd9\u91cc\u6ca1\u6709\uff0c\u6211\u8fd8\u662f\u8981\u53bb\u8d85\u5e02\u4e00\u8d9f\uff0c\u4f60\u4e0d\u7528\u966a\u6211\u53bb\uff0c\u6211\u81ea\u5df1\u53bb\u5c31\u884c\u3002\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u6211\u77e5\u9053\u4f60\u4ec0\u4e48\u4e1c\u897f\u5fd8\u62ff\u4e86\uff0c\u201d\u5f20\u606a\u5c06\u9648\u9759\u62c9\u4f4f\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u7559\u4e0b\u6765\u6536\u62fe\u623f\u95f4\uff0c\u6211\u5e2e\u4f60\u53bb\u8d85\u5e02\u4e70\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u77e5\u9053\uff1f\u201d\u9648\u9759\u7591\u60d1\u7684\u76ef\u7740\u5f20\u606a\u7684\u773c\u775b\u770b\u3002</p><p>\u3000\u3000\u201c\u4e00\u5b9a\u8981\u6211\u8868\u73b0\u51fa\u5f88\u5931\u671b\u7684\u6837\u5b50\uff0c\u4f60\u624d\u76f8\u4fe1\u6211\u786e\u5b9e\u77e5\u9053\u4f60\u7f3a\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u634f\u4e86\u634f\u9648\u9759\u5ae9\u6ed1\u7684\u8138\u988a\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u90a3\u6211\u5c31\u8868\u73b0\u51fa\u5f88\u5931\u671b\u53c8\u5f88\u5931\u843d\u7684\u6837\u5b50\u2026\u2026\u201d</p><p>\u3000\u3000\u9648\u9759\u987f\u65f6\u6ee1\u8138\u7ea2\u6655\uff0c\u4e0d\u597d\u610f\u601d\u770b\u5f20\u606a\u7684\u773c\u775b\uff0c\u8f6c\u8fc7\u8eab\u53bb\u6536\u62fe\u5e8a\u94fa\u3002</p><p>\u3000\u3000\u201c\u82cf\u83f2\u7684\u884c\u4e0d\u884c\uff1f\u201d\u5f20\u606a\u8d70\u51fa\u95e8\u53c8\u9000\u56de\u6765\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u5feb\u6b7b\u5f00\u2026\u2026\u201d\u9648\u9759\u7f9e\u6da9\u96be\u582a\u7684\u62ff\u8d77\u4e00\u53ea\u6795\u5934\u8981\u671d\u95e8\u53e3\u4e22\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u5df7\u5357\u5df7\u5317\u5404\u6709\u4e00\u5bb6\u4e2d\u7b49\u89c4\u6a21\u7684\u8d85\u5e02\uff0c\u7ed9\u9644\u8fd1\u7684\u4f4f\u6237\u4e0e\u5b66\u751f\u63d0\u4f9b\u5f88\u5927\u7684\u4fbf\u5229\u3002</p><p>\u3000\u3000\u5f20\u606a\u63d0\u7740\u8d2d\u7269\u7bee\u5148\u6311\u9009\u4e9b\u96f6\u98df\u3001\u9152\u6c34\uff0c\u7136\u540e\u624d\u8d70\u5230\u5973\u6027\u7528\u54c1\u8d27\u67b6\u90a3\u8fb9\u5e2e\u9648\u9759\u6311\u5973\u6027\u536b\u751f\u7528\u54c1\uff0c\u8fd8\u6709\u4e9b\u5973\u5b66\u751f\u5728\u90a3\u91cc\u6311\u4e1c\u897f\uff0c\u770b\u7740\u5f20\u606a\u8d70\u8fc7\u53bb\uff0c\u50bb\u50bb\u7684\u770b\u4e86\u5f20\u606a\u51e0\u773c\uff0c\u5979\u4eec\u5012\u5148\u8eb2\u5f00\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5476\u7740\u5634\u800c\u7b11\uff0c\u5fc3\u60f3\u6362\u6210\u524d\u4e16\u7684\u81ea\u5df1\u6216\u8bb8\u4f1a\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u8fd9\u65f6\u5019\u771f\u6ca1\u6709\u4ec0\u4e48\u5fc3\u7406\u969c\u788d\uff0c\u770b\u89c1\u90a3\u5806\u5973\u5b66\u751f\u7b11\u7740\u53c8\u5e26\u4e9b\u7fa1\u6155\u795e\u8272\u7684\u8eb2\u5f00\uff0c\u4ed6\u8fd8\u5f97\u610f\u7684\u62ff\u7740\u4e24\u79cd\u54c1\u724c\u7ad9\u5728\u90a3\u91cc\u6bd4\u8f83\u3002\u8fd9\u65f6\u5019\u6b63\u5904\u4e8e\u591c\u81ea\u4fee\u521a\u8fc7\u5bbf\u95e8\u5374\u8fd8\u6ca1\u6709\u5173\u7684\u65f6\u95f4\uff0c\u4e1c\u5927\u4e0e\u5e08\u5927\u7684\u5b66\u751f\u90fd\u559c\u6b22\u6293\u7d27\u65f6\u95f4\u51fa\u6765\u905b\u4e00\u905b\uff0c\u8ddf\u653e\u98ce\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u5343\u79a7\u591c\u4f60\u4eec\u6709\u6ca1\u6709\u8ba1\u5212\u597d\u600e\u4e48\u8fc7\uff1f\u821e\u8e48\u793e\u7ec4\u7ec7\u4e00\u8d77\u722c\u71d5\u5f52\u5c71\uff0c\u4f60\u4eec\u4e5f\u8ddf\u7740\u4e00\u8d77\u53bb\u73a9\u5427\uff1f\u201d</p><p>\u3000\u3000\u8d27\u67b6\u5bf9\u9762\u4f20\u6765\u5973\u5b69\u5b50\u7684\u8c08\u8bdd\u58f0\uff0c\u5728\u8ba8\u8bba\u4e5d\u4e5d\u5e74\u5143\u65e6\u524d\u591c\u600e\u4e48\u6e21\u8fc7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u5343\u79a7\u591c\u4e00\u8fc7\u5c31\u662f\u65b0\u5343\u5e74\u4e86\uff0c\u8fd9\u6bb5\u65f6\u95f4\u5a92\u4f53\u9664\u4e86\u70ed\u7092\u201c\u5343\u5e74\u866b\u201d\u6982\u5ff5\u4e4b\u5916\uff0c\u5c31\u662f\u70ed\u7092\u201c\u5343\u79a7\u591c\u201d\uff0c\u7231\u8fbe\u8fd8\u4e13\u95e8\u63a8\u51fa\u201c\u5343\u79a7\u7248\u201d\u53cc\u5c4f\u6298\u53e0\u624b\u673a\uff0c\u7edd\u5927\u591a\u4eba\u90fd\u89c9\u5f97\u8fd9\u4e00\u591c\u610f\u4e49\u975e\u51e1\u3002\u7537\u5973\u60c5\u4eba\u6b64\u591c\u4e0d\u51fa\u6765\u5e7d\u4f1a\uff0c\u7b80\u76f4\u5c31\u6ca1\u6709\u5929\u7406\u4e86\uff0c\u8fd9\u4e00\u591c\u4e5f\u5c06\u51d1\u6210\u8bb8\u591a\u7537\u5973\u6210\u4e3a\u604b\u4eba\u3002\u8fd9\u4e48\u8bf4\u6765\uff0c\u5343\u79a7\u591c\u7684\u4ef7\u503c\u8fd8\u9887\u4e3a\u53ef\u89c2\u3002</p><p>\u3000\u3000\u201c\u8981\u662f\u8c01\u80fd\u5728\u71d5\u5f52\u6e56\u641e\u70df\u82b1\u665a\u4f1a\u5c31\u597d\u4e86\u2026\u2026\u201d\u5f20\u606a\u542c\u7740\u58f0\u97f3\u6709\u4e9b\u719f\u6089\uff0c\u5c06\u8d27\u67b6\u4e0a\u6ee1\u5f53\u5f53\u7684\u7269\u54c1\u62bd\u51fa\u6765\u4e00\u4e9b\uff0c\u770b\u5230\u9648\u5b81\u90a3\u5f20\u6e05\u65b0\u52a8\u4eba\u7684\u8138\u86cb\uff0c\u5979\u624b\u91cc\u6b63\u62ff\u7740\u4e24\u74f6\u6d17\u53d1\u6c34\u5728\u6bd4\u8f83\uff0c\u53c8\u968f\u610f\u7684\u8ddf\u4e00\u65c1\u5bbf\u820d\u540c\u5b66\u804a\u7740\u5929\uff0c\u201c\u4f60\u4eec\u60f3\u60f3\u770b\uff0c\u51cc\u6668\u5341\u4e8c\u70b9\u7684\u949f\u58f0\u521a\u54cd\uff0c\u7eda\u70c2\u7684\u70df\u82b1\u4ece\u6e56\u4e2d\u5fc3\u7efd\u653e\uff0c\u6211\u4eec\u5750\u5728\u71d5\u5f52\u5c71\u9876\u4e0a\uff0c\u624d\u4f1a\u8ba9\u4eba\u89c9\u5f97\u5343\u79a7\u591c\u771f\u7684\u610f\u4e49\u975e\u51e1\u2026\u2026\u4f60\u4eec\u8bf4\uff0c\u4f1a\u4e0d\u4f1a\u771f\u6709\u54ea\u4e2a\u50bb\u74dc\u60f3\u5230\u5343\u79a7\u591c\u5728\u71d5\u5f52\u6e56\u6c34\u4e0a\u653e\u70df\u82b1\u8ddf\u5973\u5b69\u5b50\u6c42\u7231\u554a\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u4e0d\u4f60\u4e3a\u5927\u5bb6\u727a\u7272\u4e00\u4e0b\uff1f\u6211\u4eec\u73b0\u5728\u5c31\u901a\u5904\u516c\u544a\uff1a\u8c01\u5343\u79a7\u591c\u5728\u71d5\u5f52\u6e56\u653e\u70df\u82b1\u5c31\u80fd\u8d62\u5f97\u7f8e\u4eba\u5f52\uff0c\u6211\u770b\u5230\u65f6\u5019\u8fd8\u662f\u6709\u5f88\u591a\u4eba\u9ad8\u5174\u505a\u8fd9\u4e2a\u50bb\u74dc\u7684\uff01\u6211\u770b\u4e8e\u7af9\u5c31\u633a\u50bb\u7684\uff01\u201d</p><p>\u3000\u3000\u201c\u5207\uff0c\u4ed6\u8981\u6709\u8fd9\u8111\u5b50\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u8fde\u4e00\u4e2a\u5973\u5b69\u5b50\u90fd\u9a97\u4e0d\u5230\uff01\u518d\u8bf4\u51ed\u4ec0\u4e48\u8981\u59d1\u5976\u5976\u6211\u727a\u7272\u6210\u5168\u4f60\u4eec\u5440\uff01\u201d\u9648\u5b81\u4e0e\u5979\u4eec\u5bbf\u820d\u7684\u5973\u5b69\u5b50\u7b11\u95f9\u7740\u62ff\u7740\u9009\u597d\u7684\u529b\u58eb\u6d17\u53d1\u6c34\u671d\u6536\u94f6\u53f0\u8d70\u53bb\uff0c\u5979\u679c\u7136\u8fd8\u662f\u5728\u7528\u529b\u58eb\u6d17\u53d1\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u8d77\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u4ed6\u4e0e\u675c\u98de\u5728\u5343\u79a7\u591c\u7ec4\u7ec7\u56f4\u68cb\u793e\u7684\u6210\u5458\u62d6\u5bb6\u643a\u53e3\u722c\u71d5\u5f52\u6e56\u770b\u65e5\u51fa\uff0c\u4e8e\u7af9\u4e0e\u9648\u5b81\u90fd\u5728\uff0c\u5343\u79a7\u591c\u5927\u5bb6\u5728\u71d5\u5f52\u5c71\u9876\u4e0a\u6253\u724c\u804a\u5929\u5750\u4e86\u4e00\u591c\uff0c\u770b\u5230\u65b0\u5343\u5e74\u7684\u7b2c\u4e00\u4e2a\u65e5\u51fa\uff0c\u4e0b\u5c71\u540e\u624d\u77e5\u9053\u5c71\u4e0b\u4e00\u76f4\u5728\u4e0b\u96e8\uff0c\u9664\u4e86\u591c\u91cc\u767b\u5c71\u7684\u4eba\uff0c\u6574\u5ea7\u57ce\u5e02\u90fd\u9519\u8fc7\u4e86\u65b0\u5343\u5e74\u7684\u7b2c\u4e00\u4e2a\u65e5\u51fa\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u8d27\u67b6\u524d\u8d70\u4e86\u4e00\u4f1a\u513f\u795e\uff0c\u8fb9\u4e0a\u6709\u5973\u5b69\u5b50\u54e7\u7136\u7b11\u4e86\u4e00\u58f0\uff0c\u5f20\u606a\u624d\u56de\u8fc7\u795e\u6765\uff0c\u53d1\u73b0\u81ea\u5df1\u62ff\u7740\u5973\u6027\u536b\u751f\u7528\u54c1\u7ad9\u5728\u8d27\u67b6\u524d\u53d1\u6123\u4f3c\u4e4e\u771f\u7684\u6709\u4e9b\u72af\u50bb\u3002</p><p>\u3000\u3000\u770b\u7740\u9648\u5b81\u4e0e\u5bbf\u820d\u5973\u5b69\u5b50\u7ed3\u5b8c\u8d26\u8d70\u51fa\u8d85\u5e02\u5927\u95e8\uff0c\u5f20\u606a\u63d0\u7740\u8d2d\u7269\u7bee\u5230\u6536\u94f6\u53f0\u7ed3\u8d26\uff0c\u4e70\u4e86\u4e00\u5806\u4e1c\u897f\uff0c\u7ed3\u5b8c\u5e10\uff0c\u63d0\u7740\u8d2d\u7269\u888b\u8ffd\u5230\u5df7\u5b50\u53e3\uff0c\u9648\u5b81\u4e0e\u5979\u5bbf\u820d\u7684\u5973\u5b69\u5b50\u5df2\u7ecf\u8d70\u8fdc\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u90a3\u91cc\u770b\u7740\u5979\u4eec\u6447\u6447\u6643\u6643\u7684\u62d0\u5411\u4e1c\u95e8\u624d\u8f6c\u8fc7\u8eab\u7a7f\u8fc7\u4e1c\u534e\u5927\u9053\u5f80\u6e56\u7554\u6728\u5c4b\u8d70\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8d70\u56de\u6e56\u7554\u6728\u5c4b\uff0c\u9648\u9759\u5df2\u7ecf\u5c06\u623f\u95f4\u6536\u62fe\u6574\u9f50\u3002\u5c4b\u91cc\u7a7a\u8c03\u6253\u4e86\u4e0a\u6765\uff0c\u5979\u5c06\u5916\u5957\u8131\u6389\uff0c\u53ea\u7a7f\u7740\u7d27\u8eab\u7684\u7f8a\u7ed2\u886b\uff0c\u5c06\u957f\u53d1\u968f\u610f\u7684\u633d\u7740\uff0c\u770b\u4e0a\u53bb\u6e29\u5a49\u6dd1\u7f8e\uff0c\u770b\u7740\u5f20\u606a\u63d0\u7740\u8d2d\u7269\u888b\u4ece\u697c\u68af\u8d70\u4e0a\u6765\uff0c\u5979\u9012\u624b\u8fc7\u6765\u5c06\u8d2d\u7269\u888b\u63a5\u8fc7\u6765\uff0c\u9752\u6625\u5a07\u5a9a\u7684\u8138\u4e0a\u6d0b\u6ea2\u7740\u8ff7\u4eba\u7684\u8ff7\u5bb9\u3002</p><p>\u3000\u3000\u770b\u7740\u9648\u9759\u4ead\u4ead\u7389\u7acb\u3001\u98ce\u59ff\u7ef0\u7ea6\u7684\u7ad9\u5728\u90a3\u91cc\u671f\u76fc\u81ea\u5df1\u4e0a\u697c\u6765\uff0c\u5f20\u606a\u5fc3\u91cc\u90a3\u79cd\u5939\u6742\u7740\u5931\u843d\u60c5\u7eea\u7684\u9519\u4e71\u624d\u505c\u6b62\u7ee7\u7eed\u9519\u4e71\u4e0b\u53bb\uff0c\u6709\u90a3\u79cd\u719f\u6089\u7684\u5fc3\u5b89\u611f\u3002</p><p>\u3000\u3000\u5c06\u8d2d\u7269\u888b\u4ea4\u7ed9\u9648\u9759\u63d0\u7740\uff0c\u5f20\u606a\u624b\u6284\u4f4f\u9648\u9759\u67d4\u8f6f\u7684\u8170\u80a2\uff0c\u5c06\u5979\u6402\u5728\u6000\u91cc\u7528\u529b\u62b1\u4e86\u62b1\uff0c\u5728\u5979\u8033\u8fb9\u8f7b\u58f0\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u7684\u591c\u665a\u6709\u4f60\u966a\u7740\uff0c\u771f\u597d\uff01\u201d</p><p>\u3000\u3000\u611f\u89c9\u5230\u5f20\u606a\u7684\u624b\u6709\u529b\u7684\u6258\u7740\u81ea\u5df1\u67d4\u8f6f\u7684\u8170\u80a2\uff0c\u9648\u9759\u4e0d\u5927\u6562\u770b\u5f20\u606a\u7684\u773c\u775b\uff0c\u5373\u4f7f\u60f3\u50cf\u6210\u719f\u7537\u5973\u90a3\u6837\u5766\u7136\u5927\u65b9\u7684\u5904\u7406\u4e24\u4eba\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u4f9d\u65e7\u6709\u7740\u5c11\u5973\u4f3c\u7684\u80c6\u602f\uff0c\u4e5f\u611f\u89c9\u8d34\u5728\u4ed6\u6000\u91cc\u7684\u611f\u89c9\u771f\u597d\u3002</p><p>\u3000\u3000\u7ad9\u5728\u697c\u68af\u53e3\u62b1\u4e86\u4e00\u4f1a\u513f\uff0c\u5f20\u606a\u5c06\u9648\u9759\u653e\u5f00\uff0c\u5c06\u8d2d\u7269\u888b\u91cc\u5979\u9700\u8981\u7684\u4e1c\u897f\u9012\u7ed9\u5979\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u5148\u53bb\u6d17\u4e0b\u6fa1\uff0c\u6211\u5148\u5c06\u7ea2\u9152\u62ff\u5230\u9601\u697c\u53bb\uff0c\u8fd9\u680b\u623f\u5b50\uff0c\u6211\u6700\u949f\u610f\u9601\u697c\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u4e5f\u662f\u2026\u2026\u201d\u9648\u9759\u7b11\u7740\u8bf4\uff0c\u4e09\u697c\u7684\u9601\u697c\u53ef\u4ee5\u4e00\u89c8\u65e0\u9057\u7684\u6b23\u8d4f\u591c\u8272\u4e0b\u7684\u71d5\u5f52\u6e56\uff0c\u5728\u5730\u677f\u4e0a\u94fa\u4e0a\u67d4\u8f6f\u987a\u6ed1\u7684\u7ed2\u6bdb\u6bef\uff0c\u5750\u5728\u4e0a\u6765\u4e0e\u60c5\u4eba\u559d\u7ea2\u9152\u3001\u804a\u5929\u7684\u611f\u89c9\u6700\u597d\u4e86\u3002</p><p>\u3000\u3000\u9648\u9759\u6d17\u8fc7\u6fa1\u51fa\u6765\uff0c\u6362\u4e86\u4e00\u6761\u6d45\u8272\u957f\u88e4\uff0c\u7d27\u8eab\u7684\uff0c\u5c06\u4fee\u957f\u7f8e\u817f\u5c55\u793a\u6dcb\u6f13\u5c3d\u81f4\uff0c\u4e0a\u8eab\u6362\u4e86\u4ef6\u70df\u7070\u8272\u7684\u5bbd\u8896\u7f8a\u7ed2\u886b\uff0c\u5c06\u4e0a\u7fd8\u7d27\u81f4\u7684\u81c0\u90e8\u906e\u8d77\u6765\uff0c\u8d64\u8db3\u7a7f\u7740\u7ed2\u6bdb\u62d6\u978b\uff0c\u88e4\u811a\u633d\u8d77\u6765\uff0c\u9732\u51fa\u4e00\u5c0f\u811a\u5ae9\u767d\u800c\u7ea4\u7ec6\u7684\u5c0f\u811a\uff0c\u770b\u7740\u5f20\u606a\u5750\u5728\u5730\u677f\u4e0a\u8ddf\u4eba\u8bf4\u7535\u8bdd\uff0c\u5979\u521a\u8981\u653e\u7f13\u811a\u6b65\u9000\u4e0b\u53bb\uff0c\u5f20\u606a\u6307\u4e86\u6307\u77ee\u684c\u4e0a\u6ce8\u5165\u7ea2\u9152\u7684\u9152\u676f\uff0c\u793a\u610f\u5979\u5750\u8fc7\u6765\u3002</p><p>\u3000\u3000\u9648\u9759\u5c06\u9152\u676f\u62ff\u5728\u624b\u91cc\uff0c\u5b89\u9759\u7684\u5750\u5728\u5f20\u606a\u7684\u8eab\u8fb9\uff0c\u542c\u4ed6\u8ddf\u4ed6\u5988\u5728\u7535\u8bdd\u91cc\u4e71\u6405\u86ee\u7f20\uff0c\u591c\u5f88\u9759\uff0c\u5979\u4e5f\u6e05\u6670\u7684\u542c\u5f97\u89c1\u5f20\u606a\u4ed6\u5988\u5988\u4ece\u624b\u673a\u91cc\u4f20\u51fa\u6765\u7684\u58f0\u97f3\uff0c\u4e24\u4eba\u4e3a\u5bd2\u6f6e\u88ad\u6765\u5728\u5efa\u90ba\u591c\u91cc\u8981\u591a\u5c11\u8863\u670d\u626f\u4e86\u534a\u5929\uff0c\u5012\u662f\u60f3\u8c61\u4e0d\u51fa\u4ed6\u8ddf\u4ed6\u5988\u539f\u6765\u8fd9\u4e48\u8bf4\u8bdd\u7684\u3002\u60f3\u60f3\u4e5f\u771f\u662f\u7684\uff0c\u5e73\u65f6\u5927\u5bb6\u90fd\u4e60\u60ef\u6027\u7684\u5fd8\u8bb0\u5f20\u606a\u8fd8\u53ea\u662f\u4e8c\u5341\u4e00\u5c81\u7684\u5c0f\u9752\u5e74\u800c\u5df2\u3002</p><p>\u3000\u3000\u554a\uff0c\u4ed6\u53ea\u6709\u4e8c\u5341\u4e00\u5c81\u554a\uff0c\u81ea\u5df1\u8db3\u8db3\u8981\u6bd4\u4ed6\u5927\u4e94\u516d\u5c81\u5462\uff0c\u9648\u9759\u60f3\u8d77\u6765\u8fd8\u771f\u662f\u5413\u4e00\u8df3\u5462\uff0c\u4f3c\u4e4e\u4ece\u6765\u90fd\u610f\u8bc6\u4ed6\u8981\u6bd4\u81ea\u5df1\u6210\u719f\uff0c\u5373\u4f7f\u5173\u7cfb\u672a\u6311\u660e\u65f6\uff0c\u60f3\u5230\u8981\u4f9d\u8d56\u8c01\uff0c\u4e5f\u77e5\u9053\u8fd9\u5bb6\u4f19\u53ef\u4ee5\u4f9d\u8d56\uff0c\u89c1\u5f20\u606a\u7591\u60d1\u7684\u671b\u8fc7\u6765\uff0c\u9648\u9759\u7f9e\u6da9\u7684\u62ff\u53cc\u624b\u6342\u4f4f\u8138\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u89c1\u9648\u9759\u53cc\u624b\u6342\u4f4f\u8138\u57cb\u5728\u819d\u76d6\u95f4\uff0c\u6342\u7740\u8bdd\u7b52\u4e0d\u8ba9\u4ed6\u5988\u542c\u5230\uff0c\u95ee\u9648\u9759\u3002</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u7a81\u7136\u610f\u8bc6\u5230\u4f60\u8fd8\u4e00\u68f5\u5c0f\u5ae9\u8349\uff01\u201d\u9648\u9759\u8138\u57cb\u5728\u819d\u76d6\u91cc\u7b11\u7740\u8bf4\uff0c\u8fd8\u662f\u4e0d\u597d\u610f\u601d\u62ac\u8d77\u5934\u6765\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u5f20\u606a\u53c8\u8ddf\u4ed6\u5988\u5728\u7535\u8bdd\u91cc\u626f\u4e86\u4e24\u53e5\u5c31\u6302\u6389\u7535\u8bdd\uff0c\u89c1\u9648\u9759\u624b\u91cc\u7684\u9152\u8981\u6cfc\u51fa\u6765\uff0c\u5e2e\u5979\u5c06\u9152\u676f\u6276\u6b63\uff0c\u7b11\u7740\u95ee\u5979\uff1a\u201c\u662f\u4e0d\u662f\u89c9\u5f97\u81ea\u5df1\u5360\u5230\u597d\u5927\u7684\u4fbf\u5b9c\uff1f\u201d</p><p>\u3000\u3000\u201c\u9b3c\u624d\u5360\u5f97\u5230\u4f60\u7684\u4fbf\u5b9c\u3002\u201d\u9648\u9759\u7b11\u7740\u8bf4\u9053\uff0c\u62ac\u8d77\u8138\u6765\u770b\u7740\u5f20\u606a\uff0c\u7f8e\u8138\u7eef\u7ea2\u8ff7\u4eba\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u522b\u5885\u4e09\u5929\u540e\u5c31\u6536\u62fe\u59a5\u5f53\uff0c\u9648\u9759\u4e5f\u6ca1\u6709\u501f\u53e3\u7ee7\u7eed\u5728\u6e56\u7554\u6728\u5c4b\u501f\u4f4f\u4e0b\u6765\uff0c\u518d\u8bf4\u8c22\u5b50\u5609\u8138\u76ae\u539a\uff0c\u5c06\u6b8b\u5c40\u6536\u62fe\u597d\u5c31\u5f53\u4e8b\u60c5\u6ca1\u6709\u53d1\u751f\u8fc7\uff0c\u53c8\u8981\u642c\u8fc7\u6765\u8ddf\u9648\u9759\u4e00\u8d77\u4f4f\uff0c\u9648\u9759\u4e5f\u53ea\u80fd\u642c\u56de\u71d5\u56ed\u53bb\u3002</p><p>\u3000\u3000\u5c31\u5728\u65b0\u5343\u5e74\u5143\u65e6\u524d\u4e24\u5929\uff0c\u4f3c\u4e4e\u662f\u4e3a\u65b0\u5343\u5e74\u732e\u793c\uff0c\u4e09\u5341\u65e5\uff0c\u5916\u7ecf\u8d38\u90e8\u90e8\u957f\u4e0e\u7f8e\u56fd\u8d38\u6613\u4ee3\u8868\u5728\u8c08\u5224\u7ec4\u8fdb\u884c\u6700\u540e\u4e00\u8f6e\u516d\u5929\u516d\u591c\u7684\u8c08\u5224\u4e4b\u540e\u5206\u522b\u4ee3\u8868\u4e24\u56fd\u653f\u5e9c\u5728\u5317\u4eac\u7b7e\u7f72\u4e2d\u7f8e\u53cc\u8fb9\u534f\u8bae\uff0c\u6807\u5fd7\u7740\u4e2d\u56fd\u52a0\u5165\u4e16\u8d38\u7ec4\u7ec7\u8e0f\u51fa\u6700\u5173\u952e\u7684\u4e00\u6b65\u3002</p><p>\u3000\u3000\u7531\u4e8e\u7b7e\u7f72\u53cc\u8fb9\u534f\u8bae\u7684\u6d88\u606f\u662f\u5728\u9ec4\u660f\u65f6\u516c\u5e03\uff0c\u6070\u597d\u662f\u5168\u7403\u4e3b\u8981\u8bc1\u5238\u5e02\u573a\u4ea4\u6613\u7684\u7a7a\u7a97\u65f6\u95f4\uff0c\u867d\u7136\u77e5\u9053\u8fd9\u4e00\u4e8b\u4ef6\u4ee4\u4eba\u632f\u594b\u800c\u4e14\u610f\u4e49\u91cd\u5927\uff0c\u53ea\u662f\u6682\u65f6\u8fd8\u65e0\u6cd5\u5f97\u5230\u8d44\u672c\u5e02\u573a\u7684\u5b9e\u65f6\u53cd\u9988\u3002</p><p>\u3000\u3000\u9648\u9759\u8fd4\u56de\u71d5\u56ed\u522b\u5885\uff0c\u7ed9\u8c22\u5b50\u5609\u7f20\u4f4f\u8131\u4e0d\u5f00\u8eab\uff0c\u5f20\u606a\u8981\u8bb8\u601d\u5343\u79a7\u591c\u5230\u5efa\u90ba\u6765\u966a\u81ea\u5df1\uff0c\u4ed6\u8fd9\u51e0\u5929\u90fd\u4f4f\u5728\u6e56\u7554\u6728\u5c4b\u3002\u5165\u591c\u540e\uff0c\u4ed6\u5c06\u624b\u5934\u7684\u4e8b\u60c5\u5904\u7406\u5b8c\uff0c\u4e0e\u5510\u5a67\u901a\u8fc7\u7535\u8bdd\u4e4b\u540e\uff0c\u5750\u5728\u9601\u697c\u91cc\u770b\u4e86\u4e00\u4f1a\u513f\u4e66\u3002\u9999\u6e2f\u5927\u5b66\u7684\u516c\u5047\u8981\u4ece\u5143\u65e6\u90a3\u4e00\u5929\u7b97\u8d77\uff0c\u5510\u5a67\u5343\u79a7\u591c\u90a3\u5929\u8fd8\u6709\u8bfe\u8981\u4e0a\uff0c\u4e0d\u80fd\u8d76\u56de\u5efa\u90ba\u6765\uff0c\u5f20\u606a\u5fc3\u60f3\u8fd9\u6837\u4e5f\u597d\uff0c\u4e0d\u7136\u771f\u5c31\u5206\u8eab\u4e4f\u672f\u4e86\u3002\u867d\u7136\u7231\u8fbe\u3001\u76db\u946b\u90fd\u5728\u8ddf\u7740\u7092\u4f5c\u8fd9\u79cd\u8282\u65e5\u6982\u5ff5\uff0c\u4f46\u662f\u5f20\u606a\u8fd8\u662f\u89c9\u5f97\u4e00\u5e74\u5f53\u4e2d\u641e\u51fa\u8fd9\u4e9b\u8c8c\u4f3c\u6709\u610f\u4e49\u7684\u8282\u65e5\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u5f88\u5934\u75bc\u3002</p><p>\u3000\u3000\u6709\u4e86\u4e9b\u56f0\u610f\u5c31\u5b64\u96f6\u96f6\u7684\u72ec\u81ea\u4e0b\u697c\u7761\u89c9\u7761\u89c9\uff0c\u7761\u5f97\u771f\u9999\uff0c\u5374\u7ed9\u624b\u673a\u94c3\u58f0\u95f9\u9192\u3002</p><p>\u3000\u3000\u5f20\u606a\u7741\u773c\u770b\u7740\u5728\u6f06\u9ed1\u623f\u95f4\u91cc\u624b\u673a\u7684\u5c4f\u5e55\u53d1\u51fa\u67d4\u548c\u7684\u83b9\u5149\uff0c\u7ed9\u4ece\u7761\u68a6\u4e2d\u95f9\u54cd\u662f\u4ee4\u4eba\u5f88\u90c1\u95f7\u7684\u4e00\u4ef6\u4e8b\uff0c\u4ed6\u5750\u5728\u5e8a\u6cbf\u4e0a\u60f3\u7f13\u4e00\u53e3\u6c14\u518d\u63a5\u7535\u8bdd\uff0c\u8fd9\u65f6\u5019\u4e22\u5728\u5916\u9762\u8d77\u5c45\u5ba4\u91cc\u7684\u53e6\u4e00\u53ea\u624b\u673a\u4e5f\u54cd\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u8fd8\u5728\u60f3\u7740\u4f1a\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5ea7\u673a\u53c8\u60f3\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u62cd\u4e86\u62cd\u8111\u888b\uff0c\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u706b\u70e7\u7709\u5934\u7684\u4e8b\u60c5\uff0c\u8fd9\u91cc\u7684\u5ea7\u673a\u8fd9\u65f6\u5019\u53ea\u6709\u6781\u5c11\u6570\u51e0\u4e2a\u4eba\u4f1a\u6253\u8fdb\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u5148\u5c06\u5e8a\u5934\u7684\u5ea7\u673a\u7535\u8bdd\u63a5\u8d77\u6765\uff0c\u5085\u4fca\u7684\u58f0\u97f3\u4ece\u91cc\u9762\u4f20\u51fa\u6765\uff1a\u201c\u6210\u603b\u8981\u6211\u7d27\u6025\u8054\u7cfb\u4f60\uff0c\u4f60\u7684\u624b\u673a\u4ed6\u6253\u4e0d\u8fdb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u770b\u624b\u673a\u5c4f\uff0c\u663e\u793a\u662f\u7f8e\u56fd\u533a\u53f7\uff0c\u8ddf\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u6211\u77e5\u9053\uff0c\u6211\u7b49\u4e00\u4f1a\u513f\u7ed9\u4ed6\u6253\u56de\u53bb\uff0c\u4e24\u53ea\u624b\u673a\u90fd\u6709\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u4ed6\u4eec\u771f\u662f\u4e00\u70b9\u90fd\u6c89\u4e0d\u4f4f\u6c14\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\u6309\u4e86\u901a\u8bdd\u6309\u94ae\uff0c\u6ca1\u7b49\u4ed6\u5c06\u624b\u673a\u8d34\u5230\u8033\u8fb9\uff0c\u5c31\u542c\u89c1\u8096\u664b\u6210\u5174\u594b\u9ad8\u4ea2\u7684\u8bed\u6c14\u4ece\u7535\u8bdd\u91cc\u4f20\u51fa\u6765\uff1a\u201c\u53d7\u4e2d\u7f8e\u8fbe\u6210\u53cc\u8fb9\u534f\u8bae\u6d88\u606f\u7684\u523a\u6fc0\uff0c\u6606\u817e\u80a1\u4ef7\u5f00\u76d8\u5c31\u51b2\u4e0a\u56db\u5341\u516d\u7f8e\u5143\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54a6\uff0c\u201d\u5f20\u606a\u5431\u4e86\u4e00\u58f0\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u5916\u9762\u8fd8\u6709\u4e00\u53ea\u624b\u673a\u5728\u54cd\uff0c\u6211\u53bb\u63a5\u4e00\u4e0b\uff0c\u7b49\u4f1a\u513f\u518d\u8ddf\u4f60\u804a\u8fd9\u4e8b\u2026\u2026\u201d\u4e00\u8fb9\u8ddf\u901a\u8bdd\uff0c\u4e00\u8fb9\u6253\u5f00\u623f\u95e8\uff0c\u5c06\u8d77\u5c45\u5ba4\u8336\u51e0\u4e0a\u7684\u624b\u673a\u62ff\u8d77\u6765\uff0c\u89c1\u662f\u53f6\u5efa\u658c\u4ece\u9999\u6e2f\u6253\u6765\u7684\u7535\u8bdd\uff0c\u63a5\u901a\u4ed6\u7684\u7535\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u9999\u6e2f\u90a3\u8fb9\u6709\u4ec0\u4e48\u70ed\u95f9\uff0c\u975e\u8981\u8fd9\u65f6\u5019\u5435\u9192\u6211\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6606\u817e\u80a1\u4ef7\u51b2\u4e0a\u56db\u5341\u516d\u7f8e\u5143\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5f97\uff0c\u521a\u505a\u68a6\u5462\u5c31\u7ed9\u4f60\u4eec\u7684\u7535\u8bdd\u5435\u9192\uff0c\u8001\u8096\u7ed9\u6211\u6253\u7535\u8bdd\u4e5f\u8bf4\u8fd9\u4e8b\uff0c\u6210\u6c49\u748b\u4e5f\u6025\u7740\u627e\u6211\uff0c\u4f30\u8ba1\u4e5f\u662f\u8bf4\u8fd9\u4e8b\uff0c\u90a3\u6211\u628a\u4e24\u53ea\u624b\u673a\u4e00\u53ea\u8d34\u5de6\u8033\u3001\u4e00\u53ea\u8d34\u53f3\u8033\uff0c\u4f60\u4eec\u4fe9\u540c\u65f6\u8bf4\u5427\uff01\u201d\u5f20\u606a\u5750\u56de\u5230\u88ab\u7a9d\u91cc\uff0c\u8ddf\u53f6\u5efa\u658c\u626f\u9053\u3002</p><p>\u3000\u3000\u201c\u5976\u5976\u7684\uff0c\u4f60\u542c\u4e86\u8fd9\u6d88\u606f\u5c31\u4e00\u70b9\u90fd\u4e0d\u5174\u594b\uff1f\u201d\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u90a3\u5934\u7b11\u9a82\u9053\uff0c\u201c\u767d\u5174\u51b2\u51b2\u7684\u7ed9\u4f60\u6253\u7535\u8bdd\u4e86\uff0c\u5f97\uff0c\u4f60\u7ee7\u7eed\u7761\u89c9\u5427\uff0c\u6211\u627e\u522b\u4eba\u503e\u8bc9\u53bb\u8fd8\u4e0d\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8981\u4e0d\u4f60\u8ddf\u8001\u8096\u5148\u804a\u7740\uff0c\u6211\u7ed9\u6210\u6c49\u748b\u5148\u56de\u4e2a\u7535\u8bdd\uff1f\u201d\u5f20\u606a\u7b11\u7740\u95ee\u3002</p><p>\u3000\u3000\u524d\u4e9b\u5929ESS\u63d0\u524d\u516c\u5e03\u5143\u65e6\u4e4b\u540e\u5c31\u5c06\u51cf\u6301\u6240\u6301\u6606\u817e\u5728\u7ebf1700\u4e07\u80a1\u80a1\u7968\u7684\u8ba1\u5212\uff0c\u4f7f\u5f97\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u906d\u53d7\u91cd\u521b\uff0c\u4ece\u516c\u544a\u524d\u6bcf\u80a124\u7f8e\u5143\u7684\u9ad8\u70b9\u4e24\u5929\u4e4b\u5185\u66b4\u964d\u523019\u7f8e\u5143\u4ee5\u4e0b\u3002\u7531\u4e8e\u6b27\u7f8e\u6295\u8d44\u5546\u76f8\u4fe1\u4e92\u8054\u7f51\u7ecf\u6d4e\u5c06\u6301\u7eed\u957f\u671f\u7e41\u8363\uff0c\u518d\u52a0\u4e0a\u7f8e\u56fd\u8d38\u6613\u4ee3\u8868\u56e2\u4e8c\u5341\u56db\u65e5\u62b5\u8fbe\u5317\u4eac\u8fdb\u884c\u53cc\u8fb9\u534f\u8bae\u65b0\u4e00\u8f6e\u8c08\u5224\uff0c\u4f7f\u5f97\u6295\u8d44\u5546\u66f4\u770b\u597d\u4e2d\u56fd\u4e92\u8054\u7f51\u4f01\u4e1a\uff0c\u6628\u5929\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u6062\u590d\u523020\u7f8e\u5143\u4ee5\u4e0a\uff0c\u6628\u5929\u91cd\u65b0\u56de\u523021\u7f8e\u5143\u3002</p><p>\u3000\u3000\u5728\u8fc7\u53bb\u534a\u5e74\u65f6\u95f4\u91cc\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u8ddd\u4e0a\u5e02\u5f00\u76d8\u4ef7\u589e\u6da8\u4e86\u7ea6143\uff05\uff0c\u8868\u73b0\u4e5f\u7b97\u51fa\u8272\uff0c\u4f46\u8fd8\u8c08\u4e0d\u4e0a\u663e\u773c\uff0c\u82e5\u662f\u4ee5\u6bcf\u80a120\u7f8e\u5143\u51cf\u6301\uff0c\u9526\u6e56\u5c06\u4ece\u6606\u817e\u5728\u7ebf\u4e0a\u5957\u53d6\u7ea6\u4e09\u4ebf\u56db\u5343\u4e07\u7f8e\u5143\u7684\u5de8\u8d44\u3002\u76f8\u6bd4\u8f83\u8fd9\u4e9b\u5e74\u6765\u5728\u6606\u817e\u5728\u7ebf\u4e0a\u7684\u6295\u5165\uff0c\u6536\u76ca\u7387\u4e4b\u9ad8\u5df2\u7ecf\u8ba9\u4eba\u77a0\u76ee\u7ed3\u820c\u4e86\uff08\u6a61\u6811\u56ed\u624b\u4e2d\u8fd8\u5c06\u957f\u671f\u6301\u6709\u6606\u817e\u5728\u7ebf700\u4e07\u80a1\u7684\u80a1\u7968\uff09\uff0c\u8c01\u80fd\u60f3\u5230\u4e2d\u7f8e\u7b7e\u7f6e\u53cc\u8fb9\u534f\u8bae\u7684\u6d88\u606f\u80fd\u8ba9\u6606\u817e\u5728\u7ebf\u5728\u6d88\u5316ESS\u51cf\u6301\u4e0d\u5229\u6d88\u606f\u7684\u540c\u65f6\u66b4\u6da8\u5230\u6bcf\u80a146\u7f8e\u5143\uff0c\u4e5f\u5c31\u610f\u5473\u7740ESS\u624b\u91cc\u6240\u63011700\u4e07\u80a1\u5728\u4e00\u591c\u4e4b\u95f4\u80fd\u591f\u591a\u5957\u53d6\u56db\u4ebf\u591a\u7f8e\u5143\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u56fd\u5185\u624b\u673a\u4e1a\u52a1\u76c8\u5229\u5728\u6263\u9664\u5e94\u7f34\u7a0e\u8d39\u4e4b\u540e\u4e5f\u8fd8\u4e0d\u5230\u4e94\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u51e0\u4e4e\u5728\u4e00\u591c\u4e4b\u95f4\u80fd\u767d\u5f97\u56db\u4ebf\u591a\u7f8e\u5143\uff0c\u5f20\u606a\u53cd\u5e94\u7adf\u7136\u5982\u6b64\u5e73\u6de1\uff0c\u53f6\u5efa\u658c\u771f\u90c1\u95f7\u5230\u6781\u70b9\uff0c\u4ed6\u8fd8\u662f\u63a5\u5230\u8096\u664b\u6210\u7684\u7535\u8bdd\u4e4b\u540e\u5174\u51b2\u51b2\u7684\u7ed9\u5f20\u606a\u62e8\u7535\u8bdd\uff0c\u82e6\u7b11\u7740\u8bf4\uff1a\u201c\u5f97\uff0c\u65e9\u77e5\u9053\u5c31\u4e0d\u642d\u7406\u4f60\u4e86\uff0c\u5fae\u8f6f\u90a3\u8fb9\u4f20\u6765\u7684\u597d\u6d88\u606f\uff0c\u4f60\u8fd8\u662f\u95ee\u6210\u6c49\u748b\u5427\uff0c\u6211\u8ddf\u8001\u8096\u7172\u7535\u8bdd\u7ca5\u53bb\u2026\u2026\u201d\u6ca1\u7b49\u5f20\u606a\u95ee\u5fae\u8f6f\u90a3\u8fb9\u4f20\u6765\u4ec0\u4e48\u6d88\u606f\uff0c\u53f6\u5efa\u658c\u5728\u7535\u8bdd\u90a3\u5934\u5c31\u5c06\u7535\u8bdd\u6302\u4e86\u3002</p><p>\u3000\u3000\u5e78\u597d\u4eba\u5728\u7f8e\u56fd\u7684\u8096\u664b\u6210\u6ca1\u6709\u6302\u7535\u8bdd\uff0c\u5f20\u606a\u95ee\u4ed6\uff1a\u201c\u5fae\u8f6f\u90a3\u8fb9\u4f20\u6765\u4ec0\u4e48\u597d\u6d88\u606f\uff0c\u662f\u65e0\u6761\u4ef6\u63a5\u53d7\u6211\u4eec\u7684\u62a5\u4ef7\uff1f\u201d</p><p>\u3000\u3000\u201c\u662f\u6709\u6761\u4ef6\u7684\u63a5\u53d7\u6211\u4eec\u4e5d\u4ebf\u7f8e\u5143\u7684\u62a5\u4ef7\uff0c\u201d\u8096\u664b\u6210\u5728\u7535\u8bdd\u91cc\u8bf4\u9053\uff0c\u201c\u4f60\u4e0d\u7528\u518d\u7ed9\u6c49\u748b\u56de\u7535\u8bdd\uff0c\u4ed6\u5c31\u5728\u6211\u8eab\u8fb9\uff0c\u6211\u4e0e\u6c49\u748b\u7814\u7a76\u8fc7\uff0c\u4ed6\u4eec\u989d\u5916\u63d0\u51fa\u7684\u6761\u4ef6\uff0c\u6211\u4eec\u80fd\u591f\u63a5\u53d7\uff0c\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u5c06\u4f20\u771f\u53d1\u7ed9\u4f60\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u4e86\uff0c\u660e\u5929\u518d\u8bf4\u5427\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5efa\u90ba\u8fd9\u8fb9\u5927\u534a\u591c\u7684\uff0c\u5ba4\u5185\u6e29\u5ea6\u90fd\u5728\u96f6\u4e0b\uff0c\u6211\u6ca1\u6709\u5174\u8da3\u7a7f\u4e0a\u8863\u670d\u5230\u697c\u4e0b\u53bb\u62ff\u4f20\u771f\u2026\u2026\u201d</p><p>\u3000\u3000\u8096\u664b\u6210\u4e5f\u53ea\u6709\u65e0\u5948\u7684\u5148\u6302\u6389\u7535\u8bdd\u3002</p><p>\u3000\u3000\u65e9\u5728\u4e2d\u7f8e\u6b63\u5f0f\u7b7e\u7f72\u53cc\u8fb9\u534f\u8bae\u4e4b\u65f6\uff0c\u5fae\u8f6f\u65b9\u9762\u5c31\u610f\u8bc6\u5230\u7eb3\u65af\u8fbe\u514b\u7684\u4e2d\u56fd\u6982\u5ff5\u80a1\u80a1\u4ef7\u4f1a\u6fc0\u589e\uff0c\u5728\u786e\u5b9e\u770b\u5230\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u5f00\u76d8\u5373\u7206\u6da8\u4e4b\u540e\uff0c\u5fae\u8f6f\u65b9\u9762\u5c31\u77e5\u9053\u518d\u6ca1\u6709\u538b\u4ef7\u7684\u4f59\u5730\uff0c\u8fd8\u4e0d\u5982\u63a5\u53d7\u9526\u6e56\u4e5d\u4ebf\u7f8e\u5143\u7684\u62a5\u4ef7\u518d\u6362\u53d6\u5176\u4ed6\u66f4\u6709\u5229\u7684\u6761\u4ef6\u3002</p></article>"}}}]);
