(window.webpackJsonp=window.webpackJsonp||[]).push([[1046],{1069:function(p){p.exports={chapterTitle:null,title:"\u7b2c850\u7ae0 \u5357\u6d0b\u6d77\u8fd0",content:"<article><h2>\u7b2c850\u7ae0 \u5357\u6d0b\u6d77\u8fd0</h2><p>\u3000\u3000\u5f20\u606a\u8bf4\u7684\u8f7b\u63cf\u6de1\u5199\uff0c\u5b59\u9759\u9999\u5374\u7a9d\u7740\u4e00\u809a\u5b50\u6c14\uff0c\u82f1\u738b\u56fd\u9645\u7684\u603b\u88c1\u6768\u6210\u5b88\u5f53\u7740\u5979\u7684\u9762\u6765\u6316\u89d2\uff0c\u53ef\u4e0d\u662f\u6253\u5fc3\u773c\u91cc\u5c31\u77a7\u4e0d\u8d77\u4e16\u7eaa\u534e\u97f3\uff1f\u8d81\u7740\u6ca1\u4eba\u6ce8\u610f\uff0c\u5728\u684c\u4e0b\u8e22\u4e86\u53f6\u5efa\u658c\u4e00\u811a\u3002</p><p>\u3000\u3000\u201c\u4e16\u7eaa\u534e\u97f3\u5728\u5708\u5185\u662f\u5c0f\u89d2\u8272\uff0c\u53d7\u4eba\u8f7b\u89c6\u4e5f\u662f\u5728\u6240\u96be\u514d\u7684\uff0c\u6211\u4eec\u8fd8\u4e0d\u662f\u4e00\u6837\u8981\u5230\u5904\u88c5\u5b59\u5b50\uff1f\u201d\u53f6\u5efa\u658c\u9876\u4e86\u9876\u5f20\u606a\u7684\u80a9\u8180\uff0c\u8981\u5f20\u606a\u8d5e\u540c\u4ed6\u7684\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u8c04\u7b11\u9053\uff1a\u201c\u53d7\u4e0d\u4e86\u6c14\uff0c\u90a3\u5c31\u4ee5\u7259\u8fd8\u7259\u3001\u4ee5\u773c\u8fd8\u773c\uff0c\u82f1\u738b\u65d7\u4e0b\u7684\u827a\u4eba\uff0c\u4e00\u7ebf\u7684\uff0c\u54b1\u6316\u4e0d\u8d77\uff0c\u54b1\u53ef\u4ee5\u6311\u90a3\u4e9b\u6709\u6f5c\u529b\u7684\u4e0b\u624b\u2026\u2026\u518d\u8bf4\u90a3\u4e9b\u5e74\u8f7b\u7684\u827a\u4eba\u5728\u82f1\u738b\u4e5f\u662f\u7ed9\u6b8b\u9177\u5265\u524a\u7684\u5bf9\u8c61\uff0c\u9664\u4e86\u5408\u540c\u91cc\u89c4\u5b9a\u7684\u5546\u4e1a\u6d3b\u52a8\uff0c\u8fd8\u8981\u51fa\u53bb\u966a\u9152\u3001\u966a\u5a31\u4e50\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8be5\u4e0d\u662f\u770b\u4e0a\u82f1\u738b\u65d7\u4e0b\u54ea\u4f4d\u5973\u827a\u4eba\u4e86\uff1f\u201d\u53f6\u5efa\u658c\u89c1\u5f20\u606a\u8bf4\u7684\u70ed\u60c5\uff0c\u5fcd\u4e0d\u4f4f\u8981\u6324\u5151\u4ed6\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u5f97\uff0c\u5f53\u6211\u6ca1\u8bf4\u8fd9\u8bdd\u3002\u201d\u5f20\u606a\u644a\u4e86\u644a\u624b\uff0c\u65e0\u5948\u7684\u82e6\u7b11\u3002</p><p>\u3000\u3000\u5510\u5a67\u3001\u6c5f\u9edb\u513f\u5374\u5a07\u7b11\u8981\u5f20\u606a\u5766\u767d\u5230\u5e95\u770b\u4e2d\u82f1\u738b\u65d7\u4e0b\u7684\u54ea\u4f4d\u827a\u4eba\uff0c\u8bf1\u4f9b\u3001\u903c\u4f9b\u7684\u624b\u6bb5\u90fd\u7528\u4e86\u51fa\u6765\uff0c\u5728\u5979\u4eec\u773c\u91cc\uff0c\u5f20\u606a\u5c31\u662f\u4e00\u4e2a\u602a\u80ce\uff0c\u4e8c\u5341\u5c81\u7684\u9752\u5e74\u7537\u5973\u591a\u5c11\u6709\u4e9b\u5076\u50cf\u60c5\u7ed3\uff0c\u5c31\u662f\u5510\u5a67\u4e5f\u4f1a\u7fa1\u6155\u6c5f\u9edb\u513f\u7ad9\u5728\u8367\u5149\u706f\u4e0b\u3001\u7ed9\u5a92\u4f53\u8ffd\u9010\u7684\u5f62\u8c61\uff0c\u53c8\u6709\u591a\u5c11\u4eba\u80fd\u50cf\u5f20\u606a\u8fd9\u822c\u51b7\u9759\u7684\u5c06\u89c6\u7ebf\u7a7f\u8fc7\u7eda\u4e3d\u8000\u773c\u7684\u8ff7\u96fe\u3002</p><p>\u3000\u3000\u82e5\u662f\u4ec5\u770b\u8868\u9762\uff0c\u770b\u5a31\u4e50\u5708\u90a3\u4e9b\u5c42\u51fa\u4e0d\u7a77\u7684\u4e11\u95fb\uff0c\u5f20\u606a\u5bf9\u5a31\u4e50\u4ea7\u4e1a\u662f\u6839\u672c\u63d0\u4e0d\u8d77\u5174\u8da3\u7684\uff0c\u4f46\u4e0d\u53ef\u5426\u8ba4\u5a31\u4e50\u4ea7\u4e1a\u662f\u6587\u5316\u6e17\u900f\u3001\u4ef7\u503c\u8f93\u9001\u6700\u7280\u5229\u4e5f\u662f\u6700\u4e3b\u8981\u7684\u5de5\u5177\u4e0e\u624b\u6bb5\u3002\u8003\u8651\u5230\u8fd9\u4e9b\uff0c\u5373\u4f7f\u4e0d\u4f1a\u82b1\u591a\u5c11\u7cbe\u529b\uff0c\u5f20\u606a\u5012\u662f\u4e0d\u4ecb\u610f\u505a\u4e9b\u63a8\u6ce2\u52a9\u6f9c\u7684\u4e8b\u60c5\uff0c\u4e0d\u8fc7\u773c\u4e0b\u4e5f\u4e0d\u662f\u8bf4\u8fd9\u4e2a\u65f6\u673a\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u65e5\u540e\u5c31\u8981\u7acb\u8db3\u6e2f\u5c9b\uff0c\u9664\u975e\u5185\u5730\u5728\u91d1\u878d\u3001\u8d38\u6613\u4e0a\u7684\u81ea\u7531\u5ea6\u80fd\u6bd4\u53ca\u9999\u6e2f\uff0c\u4e0d\u7136\u9526\u6e56\u5546\u4e8b\u7684\u6839\u672c\u4e0d\u4f1a\u8fc1\u56de\u5185\u5730\uff0c\u9526\u6e56\u5546\u4e8b\u5373\u5c06\u8981\u5728\u6e2f\u5c9b\u7efd\u653e\u8000\u773c\u7684\u5149\u8292\uff0c\u6210\u4e3a\u6e2f\u5c9b\u8d22\u5bcc\u6e38\u620f\u7684\u65b0\u5174\u529b\u91cf\uff0c\u81f3\u4e8e\u5b59\u9759\u9999\u53d7\u7684\u8fd9\u4e9b\u95f2\u6c14\uff0c\u81ea\u6709\u53f6\u5efa\u658c\u5e2e\u5979\u8ba8\u56de\u6765\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u591c\u91cc\uff0c\u96e8\u505c\u4e86\uff0c\u6d77\u9762\u4e4b\u4e00\u7684\u4e4c\u4e91\u8fd8\u5728\u7ffb\u6eda\uff0c\u96e8\u5374\u662f\u6682\u65f6\u505c\u4e86\uff0c\u900f\u8fc7\u900f\u660e\u7684\u73bb\u7483\u7a97\uff0c\u80fd\u770b\u89c1\u6d77\u5bf9\u5cb8\u4e0d\u7720\u7684\u7480\u74a8\u706f\u5149\u3002</p><p>\u3000\u3000\u5f20\u606a\u9192\u8fc7\u6765\u4f9d\u7740\u5e8a\u5934\u5750\u4e86\u4e00\u4f1a\u513f\uff0c\u5f88\u60f3\u5c06\u7761\u5728\u8eab\u8fb9\u7684\u5510\u5a67\u63fd\u5728\u6000\u91cc\uff0c\u5979\u7761\u5f97\u6df1\u6c89\uff0c\u6311\u8d77\u7684\u776b\u6bdb\u6709\u7740\u7ec6\u5fae\u7684\u98a4\u52a8\uff0c\u8138\u5f62\u7cbe\u81f4\u7684\u96ea\u5ae9\u8138\u86cb\u5728\u5e7d\u6697\u7684\u58c1\u706f\u5149\u8f89\u4e0b\u663e\u5f97\u7eaf\u51c0\u65e0\u7455\uff0c\u7c89\u6da6\u7684\u5634\u89d2\u5728\u7761\u68a6\u4e2d\u4e5f\u5fae\u6485\u8d77\uff0c\u5a07\u67d4\u800c\u60f9\u4eba\u601c\u7231\uff0c\u96ea\u767d\u7684\u53cc\u81c2\u6a2a\u6446\u5728\u84dd\u8272\u8584\u88ab\u4e0a\u5706\u6da6\u751f\u8f89\u3002\u5f20\u606a\u5c06\u624b\u4ece\u5979\u7684\u8116\u5b50\u4e0b\u4f38\u8fc7\u53bb\uff0c\u60f3\u8981\u5c06\u5979\u6402\u8fdb\u6000\u91cc\u3002\u5510\u5a67\u5728\u7761\u68a6\u4e2d\u611f\u89c9\u5230\u52a8\u9759\uff0c\u5148\u7ffb\u8fc7\u8eab\u6765\uff0c\u5149\u6ed1\u96ea\u767d\u7684\u624b\u81c2\u6401\u5728\u5f20\u606a\u7684\u5c0f\u8179\u4e0a\uff0c\u8138\u86cb\u8d34\u7740\u5f20\u606a\u7684\u80f8\u53e3\u8815\u52a8\u7740\u60f3\u627e\u4e86\u4e2a\u8212\u670d\u7684\u4f4d\u7f6e\uff0c\u53cc\u817f\u5c31\u50cf\u7f20\u6811\u6839\u4f3c\u7684\u7f20\u4f4f\u5f20\u606a\u7684\u5927\u817f\uff0c\u67d4\u8f6f\u7684\u9634\u6bdb\u8d34\u7740\u5f20\u606a\u5927\u817f\u7684\u5916\u4fa7\u3002\u5f20\u606a\u5fc3\u95f4\u6709\u80a1\u6e29\u70ed\u7684\u6696\u6d41\u6ea2\u51fa\uff0c\u8eab\u5b50\u5f80\u4e0b\u6ed1\uff0c\u65b9\u4fbf\u624b\u629a\u4e0a\u5979\u5bcc\u6709\u5f39\u6027\u4e30\u76c8\u7684\u81c0\u90e8\u3002</p><p>\u3000\u3000\u8bb8\u591a\u7537\u4eba\u559c\u6b22\u63e1\u7740\u8eab\u8fb9\u5973\u4eba\u7684\u4e73\u623f\u7761\u89c9\uff0c\u5f20\u606a\u5374\u559c\u6b22\u6478\u7740\u8eab\u8fb9\u5973\u4eba\u7684\u81c0\u90e8\u7761\u89c9\uff0c\u5510\u5a67\u5e38\u56e0\u6b64\u7b11\u4ed6\u9732\u597d\u8272\u7684\u672c\u6027\u6765\u3002</p><p>\u3000\u3000\u518d\u9192\u6765\uff0c\u5df2\u7ecf\u662f\u6e05\u6668\u4e86\uff0c\u9192\u6765\u90a3\u77ac\u95f4\uff0c\u611f\u89c9\u5510\u5a67\u6b63\u62ff\u53d1\u4e1d\u6320\u81ea\u5df1\u7684\u9f3b\u5b54\uff0c\u9ebb\u75d2\u96be\u5fcd\uff0c\u7741\u5f00\u6765\uff0c\u8fd9\u5c0f\u59ae\u5b50\u7684\u8138\u86cb\u8fd8\u8d34\u7740\u81ea\u5df1\u7684\u80f8\u53e3\u88c5\u719f\u7761\uff0c\u5f20\u606a\u4f38\u624b\u5728\u5979\u7684\u81c0\u8089\u4e0a\u6469\u64e6\u7740\uff0c\u5c06\u5979\u7684\u8eab\u5b50\u7ffb\u8f6c\u8fc7\u53bb\uff0c\u5634\u5507\u8d34\u7740\u5979\u96ea\u812f\u7684\u808c\u80a4\u53bb\u5bfb\u5ae3\u7136\u7684\u4e73\u5c16\u8214\u543b\uff0c\u9f3b\u5c16\u55c5\u7740\u5e7d\u5e7d\u7684\u4f53\u9999\uff0c\u76f4\u5230\u5510\u5a67\u81ea\u5df1\u5fcd\u53d7\u4e0d\u4e86\u56db\u811a\u7ea0\u7f20\u4e0a\u6765\u3002\u6df1\u6df1\u7684\u62e5\u543b\u95f4\uff0c\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u62ff\u8d77\u5e8a\u5934\u67dc\u4e0a\u7684\u624b\u673a\uff0c\u89c1\u662f\u5988\u5988\u7684\u7535\u8bdd\uff0c\u7b11\u7740\u8ddf\u5510\u5a67\uff1a\u201c\u8001\u592a\u5a46\u771f\u662f\u70e6\u4eba\uff0c\u5927\u6e05\u6668\u5c31\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u5979\u96be\u9053\u4e0d\u4f1a\u53bb\u9a9a\u6270\u8001\u5934\u5b50\u554a\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u62ff\u8d77\u5979\u7684\u624b\u673a\u7ed9\u5f20\u606a\u770b\u65f6\u95f4\uff0c\u5df2\u7ecf\u516b\u70b9\u591a\u4e86\uff1b\u5f20\u606a\u7b11\u4e86\u7b11\u63a5\u901a\u7535\u8bdd\uff0c\u7535\u8bdd\u91cc\u5988\u5988\u5520\u53e8\u7740\u95ee\u6700\u8fd1\u597d\u4e0d\u597d\uff0c\u65b0\u829c\u8fd9\u51e0\u5929\u51fa\u73b0\u5f3a\u964d\u96e8\u5929\u6c14\uff0c\u6709\u6210\u707e\u7684\u8ff9\u8c61\uff0c\u5e02\u91cc\u51b3\u5b9a\u5c06\u6c5f\u5fc3\u51e0\u5ea7\u5c0f\u5c9b\u4ee5\u53ca\u4f4e\u6d3c\u5730\u91cc\u7684\u5c45\u6c11\u90fd\u8fc1\u51fa\u6765\uff0c\u7236\u4eb2\u5f88\u65e9\u5c31\u4e0b\u53bb\u505a\u5de5\u4f5c\u53bb\u4e86\uff0c\u53c8\u95ee\u5510\u5a67\u597d\u4e0d\u597d\uff0c\u542c\u5f20\u606a\u7684\u8bed\u6c14\u4e0d\u5927\u8010\u70e6\u5c31\u8981\u5510\u5a67\u63a5\u7535\u8bdd\u8ddf\u5979\u518d\u804a\u4e00\u4f1a\u513f\u3002</p><p>\u3000\u3000\u5510\u5a67\u6b7b\u6d3b\u4e0d\u80af\u9a6c\u4e0a\u5c31\u63a5\u7535\u8bdd\uff0c\u5f20\u606a\u53ea\u6709\u88c5\u6a21\u4f5c\u6837\u7684\u53bb\u5f00\u95e8\uff0c\u8fd8\u558a\u4e86\u4e00\u55d3\u5b50\uff1a\u201c\u5510\u5a67\u8d77\u5e8a\u63a5\u7535\u8bdd\u4e86\uff01\u201d\u5510\u5a67\u624d\u538b\u7740\u7b11\u5c06\u624b\u673a\u63a5\u8fc7\u6765\u62c9\u8fc7\u88ab\u5b50\u906e\u4f4f\u96ea\u767d\u7684\u5ae9\u4e73\u8ddf\u672a\u6765\u7684\u5a46\u5a46\u5728\u7535\u8bdd\u91cc\u5520\u5bb6\u5e38\u3002</p><p>\u3000\u3000\u5c0f\u59ae\u5b50\u7761\u8db3\u540e\u610f\u6001\u6175\u61d2\uff0c\u4e0d\u7ecf\u610f\u95f4\u7684\u7f8e\u76ee\u6a2a\u76fc\u80fd\u593a\u4eba\u9b42\u9b44\uff0c\u808c\u80a4\u96ea\u5ae9\u5065\u5eb7\u6390\u4e00\u628a\u80fd\u6390\u51fa\u6c34\u6765\uff0c\u8fd9\u6837\u7f8e\u597d\u7684\u6e05\u6668\u7adf\u7136\u8ddf\u7740\u8001\u5973\u4eba\u5728\u90a3\u91cc\u8912\u7535\u8bdd\u7ca5\uff0c\u5f20\u606a\u504f\u504f\u8fd8\u4e0d\u6562\u53d1\u8868\u610f\u89c1\uff0c\u6402\u7740\u5510\u5a67\u5a07\u5ae9\u7684\u8eab\u5b50\u8eba\u4e86\u4e00\u4f1a\u513f\uff0c\u542c\u5988\u5988\u5728\u7535\u8bdd\u8ddf\u5510\u5a67\u8ba8\u8bba\u4e2d\u5e74\u5987\u5973\u7528\u4ec0\u4e48\u5316\u5986\u54c1\u5408\u9002\uff0c\u8fd9\u8bdd\u9898\u4e00\u65f6\u534a\u4f1a\u4e5f\u7ed3\u675f\u4e0d\u4e86\uff0c\u53ea\u5f97\u90c1\u95f7\u51b2\u51c9\u6c34\u6fa1\u53bb\u3002\u7b49\u5230\u4ed6\u51b2\u8fc7\u51c9\u6c34\u6fa1\u51fa\u6765\uff0c\u624d\u770b\u5230\u5510\u5a67\u8ddf\u4ed6\u5988\u5728\u7535\u8bdd\u9053\u62dc\u62dc\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5a46\u5ab3\u5173\u7cfb\u4e0d\u9519\u554a\uff0c\u4f60\u771f\u6253\u7b97\u7b49\u4f1a\u513f\u4e0a\u8857\u5e2e\u6211\u5988\u4e70\u5316\u5986\u54c1\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6881\u59e8\u53ef\u6bd4\u4f60\u75bc\u6211\uff0c\u6211\u5f53\u7136\u8981\u4e13\u7a0b\u53bb\u5e2e\u6881\u59e8\u53bb\u4e70\u5316\u5986\u54c1\u554a\u3002\u201d\u5510\u5a67\u5a07\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u5f20\u606a\u60f3\u60f3\u4e5f\u662f\u60ed\u6127\uff0c\u4ed6\u5988\u5c06\u5510\u5a67\u89c6\u4e3a\u552f\u4e00\u7684\u51c6\u513f\u5ab3\u5987\uff0c\u81ea\u5df1\u504f\u504f\u5728\u5916\u9762\u60f9\u4e0b\u90a3\u4e48\u591a\u60c5\u503a\uff0c\u4ed6\u634f\u4e86\u634f\u5510\u5a67\u7684\u8138\u86cb\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u7b49\u4f1a\u513f\u6211\u966a\u4f60\u4e00\u8d77\u4e0a\u8857\u53bb\uff0c\u505a\u513f\u5b50\u603b\u4e0d\u80fd\u6bd4\u4e0d\u4e0a\u513f\u5ab3\u5987\u2026\u2026\u201d</p><p>\u3000\u3000\u7537\u4eba\u603b\u662f\u53d7\u4e0d\u4e86\u8bf1\u60d1\u8d2a\u604b\u5973\u8272\uff0c\u5510\u5a67\u5728\u5367\u5ba4\u8fde\u7740\u7684\u6d74\u5ba4\u91cc\u6d17\u6fa1\uff0c\u5f20\u606a\u8ba9\u7fdf\u4e39\u9752\u5c06\u4eca\u5929\u8981\u5904\u7406\u7684\u6587\u4ef6\u62ff\u8fc7\u6765\uff0c\u4ed6\u53c8\u80fd\u770b\u5230\u7fdf\u4e39\u9752\u8eab\u4e0a\u6563\u53d1\u51fa\u6765\u7684\u4e0d\u540c\u4e8e\u5510\u5a67\u7684\u7279\u6709\u7684\u9b44\u529b\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u9999\u6e2f\u6ede\u7559\u4e86\u597d\u4e9b\u5929\uff0c\u63a5\u89e6\u4e86\u8bb8\u591a\u5370\u5c3c\u534e\u5546\uff0c\u4e5f\u5229\u7528\u51e0\u5929\u7684\u65f6\u95f4\u597d\u597d\u7684\u53c2\u89c2\u4e86\u5370\u5c3c\u9a6c\u5bb6\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u5728\u9999\u6e2f\u7684\u57fa\u5730\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u5546\u4e8b\u8981\u53d1\u5c55\u7efc\u5408\u5546\u793e\u6a21\u5f0f\uff0c\u89e6\u624b\u5fc5\u987b\u8981\u4f38\u5230\u7269\u6d41\u7cfb\u7edf\u4e2d\u53bb\uff0c\u800c\u56fd\u9645\u8d44\u6e90\u4e0e\u5927\u5b97\u5546\u54c1\u8d38\u6613\u7684\u4e3b\u8981\u7269\u6d41\u65b9\u5f0f\u8fd8\u662f\u6d77\u6d0b\u8fd0\u8f93\uff0c\u201d\u5f20\u606a\u5728\u5bf9\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u5728\u9999\u6e2f\u7684\u57fa\u5730\u8868\u73b0\u51fa\u6781\u5927\u5174\u8da3\u4e4b\u65f6\uff0c\u5c06\u4ed6\u7684\u4e00\u4e9b\u60f3\u6cd5\u90fd\u4e0e\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\u505a\u4e86\u4ea4\u6d41\uff0c\u201c\u8fd9\u5927\u6982\u5c31\u662f\u6240\u8c13\u7684\u778c\u7761\u6765\u4e86\u5c31\u6709\u6795\u5934\u7684\u597d\u8fd0\u6c14\u2026\u2026\u201d</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u9634\u4e91\u975e\u4f46\u6ca1\u6709\u6563\u53bb\uff0c\u8fd8\u5728\u8fdb\u4e00\u6b65\u805a\u96c6\u6709\u7740\u91ca\u653e\u51fa\u66f4\u731b\u70c8\u72c2\u98ce\u66b4\u96e8\u7684\u8ff9\u8c61\uff0c\u4e9a\u6d32\u7ecf\u6d4e\u53d7\u5230\u4e25\u91cd\u7684\u6467\u6b8b\uff0c\u6b64\u65f6\u8fd8\u770b\u4e0d\u5230\u6709\u8f6c\u597d\u7684\u8ff9\u8c61\uff0c\u4e2d\u56fd\u5bf9\u6d77\u5916\u8d44\u6e90\u7684\u4f9d\u8d56\u7a0b\u5ea6\u8fd8\u6ca1\u6709\u96f6\u4e09\u5e74\u4e4b\u540e\u6765\u5f97\u90a3\u4e48\u4e25\u91cd\u3002\u8fd9\u65f6\u5019\uff0c\u6d77\u6d0b\u8fd0\u8f93\u4e1a\u81ea\u7136\u4e5f\u65e0\u6cd5\u72ec\u5584\u5176\u8eab\uff0c\u8dcc\u81f3\u8fd1\u5e74\u6765\u7684\u8c37\u5e95\uff0c\u8fd8\u6709\u6301\u7eed\u4e0b\u8dcc\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5370\u5c3c\u9a6c\u5bb6\u7684\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u867d\u7136\u5728\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e2d\u6709\u60ca\u65e0\u9669\uff0c\u6ca1\u6709\u906d\u53d7\u592a\u591a\u7684\u635f\u5931\uff0c\u4f46\u662f\u5728\u4e9a\u6d32\u7ecf\u6d4e\u5371\u673a\u4e2d\u9971\u53d7\u6467\u6b8b\uff0c\u6b63\u901a\u8fc7\u7f29\u51cf\u4e1a\u52a1\u6765\u6e21\u8fc7\u5371\u673a\uff0c\u8ba2\u8d2d\u7684\u51e0\u8258\u5927\u578b\u6563\u88c5\u8d27\u8f6e\u4e5f\u53d6\u6d88\u4e86\u8ba2\u5355\uff0c\u907f\u514d\u635f\u5931\u7684\u52a0\u5267\u3002\u5373\u4f7f\u9a6c\u5bb6\u8fd9\u6b21\u4f1a\u6709\u5927\u91cf\u7684\u8d44\u4ea7\u4ece\u5370\u5c3c\u56fd\u5185\u64a4\u51fa\u6765\uff0c\u4e5f\u4e0d\u4f1a\u8d38\u7136\u6295\u5230\u5bb6\u65cf\u7684\u6d77\u8fd0\u4e1a\u52a1\u4e2d\u53bb\u3002</p><p>\u3000\u3000\u5728\u9648\u6587\u806a\u3001\u90ed\u677e\u5ef6\u7684\u7275\u5934\u4e0b\uff0c\u9a6c\u6587\u6e20\u8868\u793a\u613f\u610f\u8d2d\u4e70\u9526\u6e56\u5546\u4e8b\u4e00\u4ebf\u7f8e\u5143\u7684\u516c\u53f8\u503a\uff0c\u65e2\u662f\u770b\u5230\u9526\u6e56\u5546\u4e8b\u4ee5\u6574\u4e2a\u9526\u6e56\u4f53\u7cfb\u8d44\u4ea7\u4e3a\u62b5\u62bc\u7684\u672a\u6765\u53d1\u5c55\u524d\u666f\uff0c\u5bb6\u65cf\u8d44\u4ea7\u4e5f\u9700\u8981\u66f4\u4fdd\u9669\u7684\u6295\u8d44\u65b9\u5f0f\uff0c\u5f53\u7136\uff0c\u9526\u6e56\u5728\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u4e2d\u5bf9\u5370\u5c3c\u534e\u4eba\u7684\u4e3e\u63aa\u624d\u662f\u83b7\u5f97\u4ed6\u4eec\u4fe1\u4efb\u4e0e\u597d\u611f\u7684\u6839\u672c\u3002</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6d77\u8fd0\u4e1a\u7684\u672a\u6765\u6709\u66f4\u4e50\u89c2\u7684\u4f30\u8ba1\uff0c\u4eca\u540e\u5341\u5e74\uff0c\u56fd\u9645\u95f4\u8d38\u6613\u51e0\u4e4e\u5f62\u6210\u4e00\u9879\u94c1\u5219\uff1a\u53ea\u8981\u4e2d\u56fd\u9700\u8981\u4ec0\u4e48\u7269\u8d44\uff0c\u8be5\u7269\u8d44\u7684\u56fd\u9645\u5e02\u573a\u4ef7\u683c\u5c31\u4f1a\u72c2\u98da\u731b\u8fdb\uff0c\u800c\u4e0e\u6b64\u4f9d\u8d56\u7684\u7269\u6d41\u8fd0\u8f93\u2014\u2014\u7279\u522b\u662f\u8fdc\u6d0b\u8fd0\u8f93\u2014\u2014\u4ef7\u683c\u4e5f\u4f1a\u8ddf\u7740\u72c2\u98da\u731b\u8fdb\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\u6b63\u503c\u5168\u7403\u8fdc\u6d0b\u8fd0\u8f93\u4e1a\u7684\u8c37\u5e95\uff0c\u4e0d\u666f\u6c14\u7684\u60c5\u51b5\u53ef\u80fd\u8981\u6301\u7eed\u5230\u660e\u5e74\u5e74\u4e2d\u624d\u4f1a\u7f13\u89e3\uff0c\u4f46\u662f\u8c37\u5e95\u6c38\u8fdc\u90fd\u6269\u5f20\u7684\u6700\u4f73\u65f6\u673a\u3002</p><p>\u3000\u3000\u5728\u51e0\u5929\u7684\u53c2\u89c2\u4e4b\u540e\uff0c\u5728\u9a6c\u6587\u6e20\u4e0e\u5357\u6d0b\u6d77\u8fd0\u9ad8\u5c42\u770b\u5230\u5f20\u606a\u8868\u73b0\u5bf9\u5357\u6d0b\u6d77\u8fd0\u5982\u6b64\u5f3a\u70c8\u7684\u5174\u8da3\u4e4b\u540e\u7591\u60d1\u65f6\uff0c\u5f20\u606a\u7ad9\u5728\u5357\u6d0b\u6d77\u8fd0\u505c\u9760\u5728\u9999\u6e2f\u96c6\u7bb1\u7801\u5934\u7684\u8d85\u5927\u578b\u8d27\u8f6e\u4e0a\uff0c\u8fce\u7740\u62c2\u9762\u5439\u6765\u5e26\u7740\u8165\u5473\u7684\u6d77\u98ce\uff0c\u5c06\u4e00\u4e9b\u4e0d\u592a\u6210\u719f\u7684\u60f3\u6cd5\u900f\u9732\u7ed9\u9a6c\u6587\u6e20\uff1a\u201c\u6211\u8fd9\u51e0\u5929\u6709\u4e9b\u60f3\u6cd5\u5728\u8111\u6d77\u91cc\u7ffb\u817e\uff0c\u5728\u9a6c\u5148\u751f\u79bb\u5f00\u9999\u6e2f\u4e4b\u524d\uff0c\u5c31\u7b97\u60f3\u6cd5\u8fd8\u4e0d\u591f\u6210\u719f\uff0c\u4f46\u4e5f\u8981\u8bf4\u51fa\u6765\u5411\u9a6c\u5148\u751f\u4f60\u5f53\u9762\u8bf7\u6559\u2026\u2026\u201d</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u63d0\u524d\u8fd4\u56de\u9a6c\u6765\u897f\u4e9a\uff0c\u8fd9\u4e9b\u5929\uff0c\u9648\u6587\u806a\u4e00\u76f4\u7559\u5728\u9999\u6e2f\uff0c\u5f20\u606a\u53c2\u89c2\u5357\u6d0b\u6d77\u8fd0\u5728\u9999\u6e2f\u57fa\u5730\u7684\u8fd9\u51e0\u5929\uff0c\u4ed6\u90fd\u4f1a\u62bd\u65f6\u95f4\u6765\u966a\u540c\uff0c\u53ea\u6709\u901a\u8fc7\u6df1\u5165\u7684\u4ea4\u6d41\uff0c\u624d\u80fd\u52a0\u6df1\u5f7c\u6b64\u7684\u4e86\u89e3\u4e0e\u4fe1\u4efb\u3002</p><p>\u3000\u3000\u4e86\u89e3\u4e0e\u4fe1\u4efb\u662f\u8fdb\u884c\u5408\u4f5c\u7684\u57fa\u7840\uff0c\u4e5f\u53ef\u4ee5\u8bf4\u9648\u6587\u806a\u5bf9\u5f20\u606a\u672c\u4eba\u5145\u6ee1\u4e86\u597d\u5947\u5fc3\uff0c\u60f3\u4e86\u89e3\u9526\u6e56\u521b\u9020\u5e02\u573a\u5947\u8ff9\u80cc\u540e\u7684\u6839\u6e90\uff0c\u8fd9\u51e0\u5929\u7684\u76f8\u5904\u4e0e\u4e86\u89e3\u5012\u6ca1\u6709\u8ba9\u4ed6\u5931\u671b\uff0c\u4e5f\u52a0\u6df1\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u4fe1\u5fc3\u2014\u2014\u9a6c\u6587\u6e20\u4e5f\u662f\u5982\u6b64\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u7ad9\u5728\u8231\u5ba4\u524d\u7684\u7532\u677f\u4e0a\u5439\u6d77\u98ce\uff0c\u9a6c\u6587\u6e20\u7b11\u7740\u8bf4\uff1a\u201c\u613f\u95fb\u5176\u8be6\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u6b21\u627f\u8499\u9a6c\u5148\u751f\u7684\u4fe1\u4efb\uff0c\u5728\u9526\u6e56\u5546\u4e8b\u53d1\u5c55\u4e4b\u521d\uff0c\u751a\u81f3\u8fde\u6839\u57fa\u90fd\u6ca1\u6709\u7acb\u7a33\u4e4b\u9645\uff0c\u5c31\u613f\u610f\u8d2d\u4e70\u9526\u6e56\u5546\u4e8b\u7684\u516c\u53f8\u503a\u6765\u5e2e\u52a9\u9526\u6e56\u5546\u4e8b\u7684\u53d1\u5c55\u2014\u2014\u201d\u5f20\u606a\u8bed\u6c14\u7a0d\u8f6c\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5e0c\u671b\u80fd\u9526\u6e56\u5546\u4e8b\u80fd\u5c06\u4ece\u9a6c\u5148\u751f\u5bb6\u65cf\u8d2d\u4e70\u9526\u6e56\u516c\u53f8\u503a\u6240\u83b7\u5f97\u7684\u8d44\u91d1\u6089\u6570\u6ce8\u5165\u5357\u6d0b\u6d77\u8fd0\uff0c\u4e0d\u6653\u5f97\u9a6c\u5148\u751f\u80fd\u4e0d\u80fd\u8003\u8651\u4e00\u4e0b\u6211\u8fd9\u4e2a\u5efa\u8bae\u2026\u2026\u201d</p></article>"}}}]);
