(window.webpackJsonp=window.webpackJsonp||[]).push([[1047],{1070:function(p){p.exports={chapterTitle:null,title:"\u7b2c851\u7ae0 \u84dd\u8272\u6d77\u6d0b\u901a\u9053",content:"<article><h2>\u7b2c851\u7ae0 \u84dd\u8272\u6d77\u6d0b\u901a\u9053</h2><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u5357\u6d0b\u6d77\u8fd0\u505c\u9760\u5728\u9999\u6e2f\u8d27\u8fd0\u7801\u5934\u7684\u5927\u578b\u8d27\u8f6e\u7532\u677f\u4e0a\uff0c\u8bf4\u51fa\u4ed6\u7684\u60f3\u6cd5\uff0c\u8138\u6487\u5411\u4e1c\u9762\u7684\u5927\u6d77\uff0c\u6e5b\u84dd\u7684\u6d77\u6c34\u5c31\u5d4c\u5728\u5929\u5730\u95f4\u7684\u5de8\u5927\u84dd\u5b9d\u77f3\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9a6c\u6587\u6e20\u9887\u4e3a\u60ca\u8be7\uff0c\u9a6c\u5bb6\u4e0e\u5357\u6d0b\u6d77\u8fd0\u51e0\u4e4e\u662f\u7b49\u540c\u4e00\u4f53\u7684\uff0c\u9a6c\u5bb6\u62e5\u6709\u5357\u6d0b\u6d77\u8fd0\u7684\u7edd\u5bf9\u63a7\u80a1\u6743\uff0c\u9a6c\u5bb6\u5c06\u8d44\u91d1\u6295\u5165\u9526\u6e56\u5546\u4e8b\uff0c\u9526\u6e56\u5546\u4e8b\u518d\u5c06\u8d44\u91d1\u6295\u5165\u5357\u6d0b\u6d77\u8fd0\uff0c\u5bf9\u9a6c\u5bb6\u6765\uff0c\u4f3c\u4e4e\u6ca1\u6709\u592a\u591a\u7684\u533a\u522b\u3002</p><p>\u3000\u3000\u6240\u4e0d\u540c\u7684\u4e8b\uff0c\u9a6c\u5bb6\u5c06\u8d44\u91d1\u6295\u5165\u9526\u6e56\u5546\u4e8b\uff0c\u6362\u53d6\u7684\u662f\u4e00\u4ebf\u7f8e\u5143\u7684\u503a\u6743\uff0c\u9526\u6e56\u5546\u4e8b\u5c06\u8d44\u91d1\u6295\u5165\u5357\u6d0b\u6d77\u8fd0\u6362\u53d6\u7684\u662f\u80a1\u6743\u3002</p><p>\u3000\u3000\u5728\u4e9a\u6d32\u7ecf\u6d4e\u4e0d\u77e5\u9053\u4f55\u65f6\u4f1a\u56de\u6696\u4e4b\u9645\uff0c\u503a\u6743\u6295\u8d44\u627f\u62c5\u7684\u98ce\u9669\u8981\u5c0f\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u9648\u6587\u806a\u7b49\u4eba\u4e5f\u7ad9\u5728\u90a3\u91cc\u7b49\u5f85\u5f20\u606a\u7684\u89e3\u91ca\u3002</p><p>\u3000\u3000\u201c\u7ad9\u5728\u8fd9\u4e48\u5e9e\u5927\u7684\u8fdc\u6d0b\u8d27\u8f6e\u4e0a\uff0c\u6211\u4f3c\u4e4e\u80fd\u770b\u5230\u9526\u6e56\u5546\u4e8b\u7684\u5c06\u6765\u80fd\u591f\u4f9d\u8d56\u8fd9\u4e9b\u8d85\u5927\u578b\u8fdc\u6d0b\u8d27\u8f6e\u8d70\u5411\u4e16\u754c\uff0c\u4f3c\u4e4e\u80fd\u770b\u5230\u84dd\u8272\u6d77\u6d0b\u6d0b\u9762\u4e0a\u7684\u5766\u9014\u5927\u9053\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u4e0d\u63a9\u9970\u9526\u6e56\u5546\u4e8b\u6d89\u8db3\u6d77\u8fd0\u4e1a\u3001\u5c06\u89e6\u624b\u4f38\u8fdb\u5168\u7403\u7269\u6d41\u4f53\u7cfb\u7684\u91ce\u5fc3\uff0c\u201c\u9526\u6e56\u5546\u4e8b\u7684\u76ee\u6807\u662f\u6210\u4e3a\u4ea7\u4e1a\u8d44\u6e90\u7684\u534f\u8c03\u8005\u3001\u7ec4\u7ec7\u8005\uff0c\u800c\u975e\u63a7\u5236\u8005\uff0c\u5e0c\u671b\u66f4\u591a\u7684\u8d44\u6e90\u80fd\u51dd\u805a\u5230\u4e00\u8d77\uff0c\u51dd\u805a\u6210\u4e00\u80a1\u66f4\u5f3a\u5927\u7684\u529b\u91cf\u3002\u6211\u5bf9\u6d77\u8fd0\u4e1a\u7684\u672a\u6765\u4e5f\u5f88\u6709\u4fe1\u5fc3\uff0c\u9526\u6e56\u5546\u4e8b\u4e0d\u4f1a\u8ffd\u6c42\u5bf9\u5357\u6d0b\u6d77\u8fd0\u7684\u63a7\u80a1\u6743\uff0c\u8d44\u91d1\u6089\u6570\u6ce8\u5165\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\uff0c\u5e0c\u671b\u80fd\u7ef4\u6301\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u5728\u4f4e\u8c37\u671f\u7684\u53d1\u5c55\u89c4\u6a21\uff1b\u9526\u6e56\u5546\u4e8b\u540c\u65f6\u53ef\u4ee5\u5e2e\u52a9\u5357\u6d0b\u6d77\u8fd0\u5f00\u62d3\u5185\u5730\u7684\u8fdc\u6d0b\u8fd0\u8f93\u4e1a\u52a1\u6765\u6e21\u8fc7\u5f53\u524d\u7684\u4e0d\u666f\u6c14\u65f6\u671f\u2026\u2026\u6beb\u65e0\u7591\u95ee\uff0c\u5185\u5730\u901a\u5f80\u4e16\u754c\u7684\u84dd\u8272\u6d77\u6d0b\u901a\u9053\u5c06\u6210\u4e3a\u5168\u7403\u8fdc\u6d0b\u8fd0\u8f93\u6700\u91cd\u8981\u7684\u8d38\u6613\u901a\u9053\u4e4b\u4e00\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e1c\u6d77\uff0c\u4ec5\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u5de5\u7a0b\u6269\u5efa\u5b8c\u6210\u4e4b\u540e\uff0c\u65b0\u589e\u7684\u8fdc\u6d0b\u8fd0\u529b\u9700\u6c42\u5c31\u5c06\u8fbe\u5230\u6bcf\u5e74\u56db\u767e\u4e07\u5428\uff0c\u201d\u53f6\u5efa\u658c\u8bf4\u9053\uff0c\u201c\u5185\u5730\u7684\u6d77\u8fd0\u4e1a\uff0c\u8fdc\u6d0b\u8fd0\u8f93\u80fd\u529b\u6781\u4e3a\u8584\u5f31\uff0c\u65b0\u589e\u8fdc\u6d0b\u8fd0\u529b\u8fc7\u591a\u7684\u4f9d\u8d56\u4e8e\u65e5\u3001\u97e9\u6d77\u8fd0\u4f01\u4e1a\uff0c\u5e76\u975e\u6211\u4eec\u5e0c\u671b\u770b\u5230\u7684\u5c40\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u7684\u6269\u5efa\u5de5\u7a0b\u8fd8\u662f\u5f90\u5b66\u5e73\u5728\u4e1c\u6d77\u7701\u957f\u4efb\u4e0a\u4ece\u65e5\u672c\u62ff\u5230\u76f8\u5f53\u4e8e\u5341\u4ebf\u7f8e\u5143\u7684\u65e5\u5143\u8d37\u6b3e\u624d\u6210\u529f\u5b9e\u65bd\u7684\uff0c\u4e24\u5e74\u5de5\u7a0b\u5efa\u8bbe\uff0c\u4eca\u5e74\u4e0b\u534a\u5e74\u5c31\u80fd\u5efa\u6210\u6295\u4ea7\uff0c\u867d\u8bf4\u6b63\u8d76\u4e0a\u7ecf\u6d4e\u5371\u673a\uff0c\u4f46\u662f\u56fd\u5185\u7531\u4e8e\u5b9e\u65bd\u591a\u9879\u523a\u6fc0\u7ecf\u6d4e\u7684\u5b8f\u89c2\u8c03\u63a7\u653f\u7b56\uff0c\u56fd\u5185\u7684\u94a2\u94c1\u9700\u6c42\u8fd8\u7b97\u65fa\u76db\u3002</p><p>\u3000\u3000\u9526\u6e56\u5728\u4e1c\u6d77\u7701\u5185\u7684\u5f71\u54cd\u529b\u4e0d\u5bb9\u7f6e\u7591\uff0c\u5bf9\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u7684\u5f71\u54cd\u529b\u66f4\u4e0d\u5bb9\u7f6e\u7591\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u5962\u671b\u80fd\u6bd4\u65e5\u3001\u97e9\u6d77\u8fd0\u4f01\u4e1a\u63d0\u9ad8\u6d77\u8fd0\u4ef7\u683c\uff0c\u4f46\u662f\u80fd\u62ff\u4e0b\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u7684\u77ff\u77f3\u6d77\u8fd0\u8ba2\u5355\uff0c\u5357\u6d0b\u6d77\u8fd0\u5c31\u80fd\u7ef4\u6301\u5f53\u524d\u7684\u6d77\u8fd0\u4e1a\u52a1\u89c4\u6a21\uff0c\u751a\u81f3\u80fd\u52a0\u5927\u5728\u4f4e\u8c37\u671f\u7684\u53d1\u5c55\u89c4\u6a21\uff1b\u9526\u6e56\u5546\u4e8b\u540c\u6837\u80fd\u5c06\u5357\u6d0b\u6d77\u8fd0\u56e0\u53d1\u5c55\u6240\u65b0\u589e\u7684\u5927\u578b\u8d27\u8f6e\u8ba2\u5355\u5e26\u56de\u7ed9\u56fd\u5185\u7684\u9020\u8239\u4f01\u4e1a\uff0c\u5e76\u4fc3\u8fdb\u9020\u8239\u4f01\u4e1a\u5411\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u589e\u52a0\u94a2\u6750\u8ba2\u5355\u2026\u2026</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u5c31\u662f\u8981\u5728\u73b0\u6709\u4f53\u5236\u4e0b\u3001\u5229\u7528\u56fd\u5185\u6df1\u539a\u7684\u4eba\u8109\u7f51\u7edc\u4ee5\u53ca\u9526\u6e56\u7684\u73b0\u6709\u529b\u91cf\uff0c\u6210\u4e3a\u79ef\u6781\u5411\u4ea7\u4e1a\u6e17\u900f\u7684\u5927\u8d38\u6613\u5546\uff0c\u53d1\u6325\u8d44\u6e90\u534f\u8c03\u7684\u4f5c\u7528\u3002</p><p>\u3000\u3000\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u7b49\u4eba\u8fd9\u4e9b\u5929\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u5b8f\u5927\u613f\u666f\u6709\u6240\u8c03\u6559\uff0c\u5374\u662f\u8fd9\u77ed\u77ed\u51e0\u53e5\u8bdd\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u5b8f\u5927\u613f\u666f\u6709\u66f4\u6df1\u523b\u7684\u7406\u89e3\uff0c\u4e5f\u80fd\u770b\u5230\u4e0e\u9526\u6e56\u5546\u4e8b\u7684\u7d27\u5bc6\u5408\u4f5c\u80fd\u5e26\u7ed9\u4ed6\u4eec\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u4e16\u95f4\u6ca1\u6709\u4e00\u53a2\u60c5\u613f\u7684\u604b\u7231\uff0c\u4e5f\u6ca1\u6709\u4e00\u53a2\u60c5\u613f\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u5f20\u606a\u540c\u65f6\u5e94\u5141\uff0c\u4e3a\u4e86\u4fdd\u8bc1\u9a6c\u5bb6\u7684\u6743\u76ca\uff0c\u9a6c\u5bb6\u53ef\u4ee5\u9009\u62e9\u5728\u4e00\u5b9a\u5e74\u9650\u540e\u5c06\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u503a\u6743\u8f6c\u6362\u6210\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u6301\u80a1\uff0c\u4e5f\u53ef\u4ee5\u653e\u5f03\u8be5\u9879\u6743\u5229\u6536\u56de\u672c\u91d1\u4e0e\u5e74\u606f\u3002</p><p>\u3000\u3000\u5bf9\u9526\u6e56\u5546\u4e8b\u7684\u503a\u6743\u6709\u9526\u6e56\u5546\u4e8b\u5bf9\u5357\u6d0b\u6d77\u8fd0\u7684\u80a1\u6743\u4f5c\u62b5\u62bc\uff0c\u9a6c\u5bb6\u751a\u81f3\u4e0d\u7528\u53bb\u8003\u8651\u9526\u6e56\u5546\u4e8b\u672a\u6765\u7684\u507f\u8fd8\u80fd\u529b\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u65b9\u6848\u4fdd\u8bc1\u9a6c\u5bb6\u7684\u8d44\u672c\u91d1\u6089\u6570\u6d41\u5411\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\uff0c\u5e76\u4e14\u964d\u4f4e\u4e86\u9a6c\u5bb6\u8981\u627f\u62c5\u7684\u6d77\u8fd0\u4e1a\u524d\u666f\u4e0d\u660e\u6717\u7684\u98ce\u9669\uff0c\u6700\u73b0\u5b9e\u7684\u597d\u5904\u8fd8\u662f\u9526\u6e56\u5546\u4e8b\u6709\u80fd\u529b\u5e2e\u52a9\u5357\u6d0b\u6d77\u8fd0\u66f4\u987a\u5229\u7684\u6e21\u8fc7\u5f53\u524d\u7ecf\u6d4e\u4e0d\u666f\u6c14\u65f6\u671f\u2014\u2014\u9526\u6e56\u5546\u4e8b\u5c31\u501f\u673a\u4e00\u6b65\u5c06\u89e6\u624b\u4f38\u8fdb\u6d77\u8fd0\u4e1a\u3002</p><p>\u3000\u3000\u5f20\u606a\u51fa\u4e8e\u5927\u5c40\u7684\u8003\u8651\uff0c\u5c06\u9526\u6e56\u5c06\u963b\u6b62\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u8513\u5ef6\u7684\u529f\u52b3\u63a8\u7ed9\u4e2d\u592e\u653f\u5e9c\uff0c\u8fd9\u4e2a\u5584\u610f\u7684\u8c0e\u8a00\u4e0d\u4ec5\u80fd\u8fce\u5f97\u4e2d\u592e\u9ad8\u5c42\u7684\u597d\u611f\uff0c\u540c\u6837\u4f1a\u8ba9\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u7b49\u4eba\u76f8\u4fe1\u9526\u6e56\u5728\u56fd\u5185\u6709\u7740\u66f4\u6df1\u539a\u7684\u57fa\u7840\u3002</p><p>\u3000\u3000\u7ecf\u5386\u8fc7\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\uff0c\u9a6c\u6587\u6e20\u3001\u9648\u6587\u806a\u7b49\u4eba\u867d\u5750\u62e5\u5de8\u4ebf\u8d22\u5bcc\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u5bcc\u53ef\u654c\u56fd\uff0c\u5b85\u524d\u5b85\u540e\u3001\u51fa\u884c\u5165\u6237\u90fd\u6709\u4fdd\u9556\u56f4\u62a4\uff0c\u4f46\u662f\u56fd\u5bb6\u653f\u6cbb\u52bf\u529b\u4e4b\u524d\uff0c\u4f9d\u65e7\u6709\u5982\u98ce\u96e8\u4e2d\u7684\u98d8\u840d\u65e0\u4f9d\u65e0\u9760\uff0c\u5728\u4e0d\u4e27\u5931\u5bf9\u4f01\u4e1a\u63a7\u5236\u6743\u7684\u524d\u63d0\u4e0b\uff0c\u53bb\u62b1\u4e00\u62b1\u7c97\u5927\u817f\uff0c\u662f\u5f88\u96be\u62d2\u7edd\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e34\u65f6\u63d0\u51fa\u7684\u65b9\u6848\uff0c\u7684\u786e\u80fd\u8ba9\u9a6c\u6587\u6e20\u7830\u7136\u5fc3\u52a8\uff0c\u9a6c\u6587\u6e20\u5f53\u5373\u8868\u793a\u4f1a\u7acb\u5373\u53ec\u5f00\u5357\u6d0b\u6d77\u8fd0\u7684\u8463\u4e8b\u5c40\u4f1a\u8bae\u8003\u8651\u5f20\u606a\u7684\u63d0\u8bae\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4e58\u8f66\u4ece\u9999\u6e2f\u56fd\u9645\u8d27\u8fd0\u56db\u53f7\u7801\u5934\u79bb\u5f00\uff0c\u4ece\u540e\u89c6\u955c\u8fd8\u80fd\u770b\u89c1\u7801\u5934\u4e0a\u5806\u6808\u5982\u9ad8\u697c\u7684\u96c6\u88c5\u7bb1\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u4e3a\u4e86\u6446\u8131\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u5bf9\u56fd\u5185\u7ecf\u6d4e\u7684\u5f71\u54cd\uff0c\u62c9\u52a8\u5185\u9700\uff0c\u52a0\u5f3a\u57fa\u7840\u8bbe\u65bd\u5efa\u8bbe\uff0c\u8fd9\u4e2a\u653f\u7b56\u662f\u6b63\u786e\u7684\uff0c\u4e0d\u8fc7\u4e5f\u4f1a\u6709\u8d1f\u9762\u5f71\u54cd\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5185\u9700\u62c9\u52a8\u8d77\u6765\uff0c\u7279\u522b\u662f\u57fa\u7840\u8bbe\u65bd\u7684\u5927\u89c4\u6a21\u6295\u8d44\uff0c\u4f1a\u4f7f\u56fd\u5185\u94a2\u6750\u3001\u6c34\u6ce5\u7b49\u539f\u6750\u6599\u7684\u4ef7\u683c\u5c45\u9ad8\u4e0d\u4e0b\uff0c\u5730\u65b9\u653f\u5e9c\u4e0e\u6c11\u8425\u8d44\u672c\u90fd\u4f1a\u6709\u6295\u8d44\u94a2\u94c1\u4ea7\u4e1a\u7684\u51b2\u52a8\u4e0e\u6e34\u671b\u554a\u2026\u2026\u8fd1\u5e74\u6765\u8bb8\u591a\u65b0\u5efa\u7684\u94a2\u94c1\u9879\u76ee\uff0c\u591a\u662f\u5229\u7528\u65e5\u672c\u7684\u6280\u672f\uff0c\u5bf9\u94c1\u77ff\u77f3\u539f\u6599\u7684\u8981\u6c42\u5f88\u9ad8\uff0c\u56fd\u5185\u94c1\u77ff\u50a8\u91cf\u867d\u7136\u5f88\u9ad8\uff0c\u4f46\u662f\u94c1\u77ff\u54c1\u4f4d\u5f88\u4f4e\uff0c\u4e0d\u80fd\u6ee1\u8db3\u65b0\u589e\u94a2\u94c1\u9879\u76ee\u7684\u539f\u6599\u9700\u6c42\uff0c\u7ef4\u6301\u751f\u4ea7\u53ea\u80fd\u4f9d\u8d56\u5bf9\u6d77\u5916\u4f18\u8d28\u94c1\u77ff\u8d44\u6e90\u7684\u8fdb\u53e3\u3002\u6211\u4ee5\u4e3a\u4eca\u540e\u5341\u5e74\uff0c\u56fd\u5185\u94a2\u94c1\u884c\u4e1a\u4e5f\u5f88\u96be\u6446\u8131\u5bf9\u65e5\u672c\u6280\u672f\u7684\u5f71\u54cd\uff0c\u6301\u7eed\u4e0a\u9879\u76ee\uff0c\u6301\u7eed\u7684\u589e\u52a0\u5bf9\u6d77\u5916\u94c1\u77ff\u8d44\u6e90\u7684\u4f9d\u8d56\uff0c\u800c\u56fd\u5185\u6d77\u8fd0\u4f01\u4e1a\u7684\u8fdc\u6d0b\u8fd0\u8f93\u80fd\u529b\u5f88\u8584\u5f31\uff0c\u662f\u77ed\u817f\u2026\u2026\u201d</p><p>\u3000\u3000\u8bf4\u5230\u8fd9\u91cc\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u5634\u89d2\u6302\u7740\u65e0\u5948\u7684\u7b11\u5bb9\uff0c\u8bf4\u9053\uff1a\u201c\u4f3c\u4e4e\u53ef\u4ee5\u770b\u5230\u5904\u5904\u7ed9\u4eba\u5bb6\u5361\u8116\u5b50\u7684\u672a\u6765\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5373\u4f7f\u5904\u5904\u7ed9\u4eba\u5bb6\u5361\u7740\u8116\u4e86\u3001\u5904\u5904\u7ed9\u4eba\u5bb6\u5265\u524a\uff0c\u5bf9\u56fd\u5185\u4e00\u4e9b\u4eba\u6765\u8bf4\uff0c\u4eba\u529b\u6210\u672c\u53ea\u6709\u7f8e\u56fd\u56db\u5341\u5206\u4e4b\u4e00\u7684\u5341\u4e09\u4ebf\u4eba\u53e3\uff0c\u6210\u4e86\u53ef\u4ee5\u8086\u610f\u6316\u6398\u7684\u6700\u5927\u5929\u7136\u77ff\u4ea7\u4e86\u3002\u201d\u53f6\u5efa\u658c\u9887\u4e3a\u8bbd\u523a\u7684\u8bf4\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u6e29\u548c\u7684\u7b11\u4e86\u7b11\uff0c\u4ed6\u5728\u8003\u8651\u5f20\u606a\u63cf\u8ff0\u7684\u7ecf\u6d4e\u524d\u666f\u4f1a\u7ed9\u6d77\u8fd0\u4e1a\u5e26\u6765\u591a\u5927\u7684\u673a\u9047\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u6ca1\u6709\u7ecf\u5386\u8fc7\u4e00\u904d\uff0c\u5f88\u96be\u7406\u89e3\u4e2d\u56fd\u5728\u5341\u5e74\u4e4b\u540e\u5bf9\u6d77\u5916\u8d44\u6e90\u7684\u4f9d\u8d56\u7a0b\u5ea6\u4e4b\u4e25\u5cfb\u3002</p><p>\u3000\u3000\u4e00\u65b9\u9762\u662f\u56fd\u5185\u5e9e\u5927\u7684\u5e02\u573a\u81ea\u8eab\u5bf9\u94a2\u94c1\u6709\u5f88\u9ad8\u7684\u9700\u6c42\uff0c\u4e00\u65b9\u9762\u5168\u7403\u5236\u9020\u4ea7\u4e1a\u5411\u4e2d\u56fd\u5927\u89c4\u6a21\u7684\u8f6c\u79fb\uff0c\u589e\u52a0\u4e86\u5bf9\u94a2\u94c1\u7684\u9700\u6c42\u3002\u4e5d\u4e03\u5e74\uff0c\u56fd\u5185\u94a2\u94c1\u603b\u4ea7\u91cf\u9996\u6b21\u7a81\u7834\u4e00\u4ebf\u5428\uff0c\u5750\u4e0a\u4e16\u754c\u94a2\u4ea7\u91cf\u7b2c\u4e00\u7684\u4f4d\u7f6e\uff0c\u4f46\u662f\u4e5d\u516b\u5e74\u5230\u96f6\u4e09\u5e74\uff0c\u53c8\u8fce\u6765\u56fd\u5185\u94a2\u94c1\u4ea7\u4e1a\u7684\u9ad8\u901f\u5efa\u8bbe\u671f\uff0c\u6c11\u8425\u91cd\u578b\u5316\u662f\u8fd9\u6bb5\u65f6\u95f4\u7684\u4e00\u4e2a\u9c9c\u660e\u7279\u5f81\uff0c\u6c11\u8425\u8d44\u672c\u5927\u89c4\u6a21\u7684\u6d8c\u5165\u94a2\u94c1\u884c\u4e1a\u3002</p><p>\u3000\u3000\u96f6\u4e00\u5e74\u4e4b\u540e\uff0c\u56fd\u5185\u94a2\u94c1\u4ea7\u80fd\u5c31\u5f00\u59cb\u731b\u70c8\u7684\u91ca\u653e\u51fa\u6765\uff0c\u94a2\u94c1\u4ea7\u91cf\u4ee5\u9ad8\u6b4c\u731b\u8fdb\u7684\u59ff\u6001\u589e\u6da8\u7740\uff0c\u5230\u96f6\u516b\u5e74\uff0c\u4e2d\u56fd\u94a2\u94c1\u4ea7\u91cf\u8d85\u8fc7\u4e94\u4ebf\u5428\u3002\u94a2\u6750\u4ea7\u91cf\u4e0d\u4ec5\u7a33\u5c45\u5168\u7403\u7b2c\u4e00\uff0c\u8fd8\u8d85\u8fc7\u7b2c\u4e8c\u540d\u5230\u7b2c\u516b\u540d\u56fd\u5bb6\u94a2\u94c1\u4ea7\u91cf\u4e4b\u548c\u3002\u5c4a\u65f6\uff0c\u56fd\u5185\u4ec5\u94a2\u94c1\u4e1a\u5bf9\u8fdc\u6d0b\u8fd0\u529b\u7684\u9700\u6c42\u5c31\u63a5\u8fd1\u4e94\u4ebf\u5428\uff0c\u662f\u539f\u6cb9\u8fdc\u6d0b\u8fd0\u529b\u9700\u6c42\u7684\u4e09\u500d\uff0c\u9700\u8981\u6b64\u65f6\u7684\u5357\u6d0b\u6d77\u8fd0\u9700\u8981\u4e94\u5341\u5bb6\u624d\u80fd\u6ee1\u8db3\u56fd\u5185\u94a2\u94c1\u4ea7\u4e1a\u5bf9\u8fdc\u6d0b\u8fd0\u529b\u7684\u9700\u6c42\u3002</p><p>\u3000\u3000\u201c\u5357\u6d0b\u6d77\u8fd0\u867d\u8bf4\u5728\u9a6c\u516d\u7532\u533a\u57df\u8fdc\u6d0b\u8fd0\u8f93\u80fd\u529b\u4f4d\u5c45\u7b2c\u4e8c\uff0c\u4f46\u662f\u8ddf\u5168\u7403\u7684\u6d77\u8fd0\u5de8\u5934\uff0c\u7279\u522b\u65e5\u672c\u3001\u5e0c\u814a\u7684\u51e0\u5927\u8fd0\u4f01\u76f8\u6bd4\uff0c\u89c4\u6a21\u8fd8\u5f88\u5c0f\uff0c\u8fdc\u6d0b\u8fd0\u8f93\u80fd\u589e\u6da8\u4e2a\u5341\u500d\uff0c\u5dee\u4e0d\u591a\u624d\u80fd\u5bf9\u56fd\u5185\u5bf9\u6d77\u8fd0\u7684\u4f9d\u8d56\u4ea7\u751f\u660e\u663e\u7684\u5f71\u54cd\u2026\u2026\u201d\u5f20\u606a\u6c89\u541f\u7247\u523b\uff0c\u5c06\u4ed6\u8ba1\u7b97\u7684\u7ed3\u679c\u544a\u8bc9\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\uff0c\u201c\u6211\u4eec\u80fd\u5bf9\u5357\u6d0b\u6d77\u8fd0\u7684\u76f4\u63a5\u6ce8\u8d44\uff0c\u8ddf\u5bf9\u672a\u6765\u7684\u9700\u6c42\u76f8\u6bd4\uff0c\u65e0\u5f02\u4e8e\u6000\u6c34\u8f66\u85aa\uff0c\u8fd8\u8981\u5f15\u5bfc\u66f4\u591a\u7684\u8d44\u91d1\u6ce8\u5165\u5357\u6d0b\u6d77\u8fd0\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e0d\u6b63\u662f\u6211\u4eec\u8981\u5e72\u7684\u6d3b\u5417\uff1f\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6210\u7acb\u9526\u6e56\u5546\u4e8b\u4e4b\u524d\uff0c\u9526\u6e56\u66f4\u591a\u7684\u8fd8\u662f\u5728\u4f53\u7cfb\u5185\u90e8\u8fdb\u884c\u8d44\u6e90\u534f\u8c03\uff0c\u6210\u7acb\u9526\u6e56\u5546\u4e8b\u4e4b\u540e\uff0c\u5219\u53ef\u4ee5\u5728\u66f4\u5e7f\u9614\u7684\u9886\u57df\u53d1\u6325\u8d44\u6e90\u534f\u8c03\u7684\u4f5c\u7528\uff0c\u53ef\u4ee5\u6a2a\u8de8\u4ea7\u4e1a\uff0c\u5bf9\u4e4b\u524d\u5f71\u54cd\u4e0d\u5230\u7684\u4ea7\u4e1a\u65bd\u52a0\u76f4\u63a5\u5f71\u54cd\uff0c\u5c06\u66f4\u591a\u3001\u66f4\u5927\u578b\u7684\u4f01\u4e1a\u7d27\u5bc6\u7684\u8054\u7cfb\u5728\u4e00\u8d77\u2014\u2014\u8fd9\u4e5f\u610f\u5473\u7740\u66f4\u5927\u7684\u6743\u529b\u3002\u56fd\u5185\u7684\u73b0\u6709\u4f53\u5236\u8fd8\u65e0\u6cd5\u9650\u5236\u9526\u6e56\u5546\u4e8b\u4ee5\u8fd9\u79cd\u6a21\u5f0f\u5bf9\u56fd\u5185\u7684\u4ea7\u4e1a\u65bd\u52a0\u5f71\u54cd\u3001\u8fdb\u884c\u6e17\u900f\uff0c\u5f53\u7136\uff0c\u9526\u6e56\u5546\u4e8b\u7684\u91cd\u5fc3\u5728\u5916\u4e0d\u5728\u5185\u3002</p><p>\u3000\u3000\u5f20\u606a\u773c\u775b\u7785\u7740\u53f6\u5efa\u658c\u3001\u5b59\u5c1a\u4e49\uff0c\u4e0d\u77e5\u9053\u5b59\u5c1a\u4e49\u5bf9\u53f6\u5efa\u658c\u4e0e\u5b59\u9759\u9999\u4e4b\u95f4\u7684\u4e8b\u60c5\u662f\u4e0d\u662f\u6709\u6240\u5bdf\u89c9\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u5177\u4f53\u800c\u5fae\u7684\u4e1a\u52a1\u5de5\u4f5c\u7531\u8096\u664b\u6210\u3001\u848b\u8587\u3001\u5468\u4e00\u5e73\u7b49\u4eba\u5404\u81ea\u8d1f\u8d23\u8d77\u6765\uff0c\u4f46\u662f\u9526\u6e56\u5546\u4e8b\u6210\u7acb\u7684\u76ee\u7684\u5c31\u4e0d\u662f\u4ee5\u53d1\u5c55\u7a33\u5b9a\u7684\u4e1a\u52a1\u4e3a\u76ee\u7684\u3002\u5b59\u5c1a\u4e49\u5728\u4e1c\u5357\u4e9a\u534e\u5546\u7fa4\u4f53\u91cc\u6709\u5f71\u54cd\u529b\uff0c\u800c\u53f6\u5efa\u658c\u5bf9\u56fd\u5185\u7684\u4f53\u7cfb\u4ee5\u53ca\u9526\u6e56\u73b0\u6709\u7684\u4eba\u8109\u7f51\u7edc\u64cd\u4f5c\u8d77\u6765\u5982\u9c7c\u5f97\u6c34\uff0c\u8ba9\u4ed6\u4eec\u4e24\u4eba\u5171\u540c\u8d1f\u8d23\u9526\u6e56\u5546\u4e8b\u5e94\u8be5\u662f\u6700\u9002\u5f53\u7684\u914d\u5408\uff0c\u53ea\u662f\u4e24\u4eba\u4e2d\u95f4\u585e\u4e86\u4e2a\u5b59\u9759\u9999\uff0c\u5c31\u6709\u4e9b\u8be1\u5f02\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u53ea\u80fd\u63a9\u8033\u76d7\u94c3\u7684\u65e0\u89c6\u5b59\u9759\u9999\u7684\u5b58\u5728\uff0c\u518d\u8bf4\u5efa\u90ba\u7684\u9752\u5e74\u516c\u5bd3\u91cc\u8fd8\u8d56\u7740\u4e00\u53ea\u8ba9\u4eba\u5934\u75bc\u7684\u5c0f\u5996\u7cbe\uff0c\u4e0d\u8fc7\u4ed6\u4eba\u5728\u9999\u6e2f\uff0c\u8eab\u8fb9\u6709\u5510\u5a67\uff0c\u4ed6\u53ea\u80fd\u5c06\u6240\u6709\u7684\u5fc3\u601d\u90fd\u6295\u5728\u8fd9\u5c0f\u59ae\u5b50\u8eab\u4e0a\u3002</p></article>"}}}]);
