(window.webpackJsonp=window.webpackJsonp||[]).push([[105],{127:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u4e03\u8282 \u518d\u89c1\u8679\u5f69\u9f99",content:"<article><h2>\u7b2c\u5341\u4e03\u8282 \u518d\u89c1\u8679\u5f69\u9f99</h2><p>\u6d53\u5bc6\u7684\u9ed1\u6697\u6b7b\u6b7b\u7b3c\u7f69\u7740\uff0c\u743c\u6069\u538b\u6839\u770b\u4e0d\u89c1\u60c5\u5f62\uff0c\u4ed6\u53ea\u80fd\u542c\u89c1\u4e00\u8fde\u4e32\u7684\u5175\u5203\u649e\u51fb\u58f0\u548c\u53f1\u559d\uff0c\u6709\u4e24\u4e2a\u4eba\u5728\u4ea4\u624b\uff0c\u5176\u4e2d\u4e4b\u4e00\u662f\u6885\u83f2\u65af\uff0c\u5979\u7684\u5bf9\u624b\u5219\u8fd8\u4e0d\u77e5\u9053\u662f\u8c01\u3002</p><p>\u5deb\u5e08\u5feb\u901f\u7ed9\u81ea\u5df1\u65bd\u52a0\u4e86\u4e00\u9053\u9690\u5f62\u672f\uff0c\u8fd9\u662f\u4ed6\u4eca\u5929\u6240\u51c6\u5907\u7684\u6cd5\u672f\u4e2d\u6700\u540e\u4e00\u62db\u6709\u6548\u7684\u4fdd\u547d\u624b\u6bb5\u3002\u4ed6\u7684\u8eab\u5f62\u8fc5\u901f\u6e6e\u6ca1\uff0c\u9664\u975e\u7ecf\u8fc7\u9b54\u6cd5\u5f3a\u5316\u6216\u8005\u7279\u522b\u8bad\u7ec3\u7684\u773c\u775b\uff0c\u6216\u8005\u67d0\u4e9b\u5929\u751f\u5c31\u80fd\u770b\u7834\u9690\u5f62\u7684\u751f\u7269\uff0c\u5426\u5219\u4fbf\u96be\u4ee5\u5bdf\u89c9\u5230\u4ed6\u7684\u5b58\u5728\u3002</p><p>\u5f53\u7136\uff0c\u9690\u5f62\u6bd5\u7adf\u53ea\u662f\u906e\u63a9\u5f62\u4f53\uff0c\u4e0d\u662f\u5b8c\u5168\u62b9\u53bb\u4e00\u4e2a\u4eba\u7684\u5b58\u5728\u3002\u7ecf\u9a8c\u4e30\u5bcc\u7684\u8001\u624b\uff0c\u53ea\u8981\u7559\u5fc3\u89c2\u5bdf\uff0c\u4f9d\u65e7\u80fd\u5f88\u5bb9\u6613\u5730\u4ece\u811a\u6b65\u3001\u547c\u5438\u3001\u5fc3\u8df3\u751a\u81f3\u8863\u670d\u7684\u6469\u64e6\u58f0\u3001\u7a7a\u6c14\u4e2d\u6c14\u6d41\u7684\u79fb\u52a8\u4e2d\u5224\u65ad\u51fa\u9690\u5f62\u8005\u7684\u4f4d\u7f6e\u3002\u4f46\u8fd9\u9700\u8981\u65f6\u95f4\u548c\u573a\u5408\u53bb\u4ece\u5bb9\u5224\u65ad\uff0c\u800c\u76ee\u524d\u5e76\u4e0d\u5177\u5907\u3002</p><p>\u6240\u4ee5\u743c\u6069\u6682\u65f6\u5e94\u8be5\u662f\u5b89\u5168\u7684\u3002</p><p>\u5728\u9690\u5f62\u9b54\u6cd5\u751f\u6548\u7684\u540c\u65f6\uff0c\u4ed6\u6084\u6084\u5730\u79bb\u5f00\u4e86\u539f\u672c\u6240\u7ad9\u7acb\u7684\u4f4d\u7f6e\uff0c\u810a\u80cc\u8d34\u4e0a\u51b0\u51b7\u7684\u5899\u58c1\uff0c\u8fd9\u8ba9\u4ed6\u7a0d\u7a0d\u5b89\u5fc3\u4e00\u4e9b\u3002\u5175\u5203\u649e\u51fb\u58f0\u4f9d\u65e7\u5728\u7ee7\u7eed\uff0c\u800c\u4e14\u66f4\u52a0\u5267\u70c8\uff0c\u4f46\u743c\u6069\u770b\u4e0d\u89c1\u5177\u4f53\u7684\u6218\u51b5\uff0c\u4ed6\u53ea\u80fd\u51ed\u501f\u6885\u83f2\u65af\u94f6\u5251\u4e0a\u7684\u5149\u8292\uff0c\u52c9\u5f3a\u5224\u65ad\u51fa\u5973\u5723\u6b66\u58eb\u7684\u5f62\u8ff9\uff0c\u800c\u5979\u7684\u5bf9\u624b\u5219\u5b8c\u5168\u9690\u6ca1\u5728\u9ed1\u6697\u4e2d\uff0c\u81f3\u4eca\u8fd8\u6ca1\u6709\u9732\u9762\u3002</p><p>\u8fd9\u7247\u9ed1\u6697\u663e\u7136\u662f\u4eba\u4e3a\u521b\u9020\u7684\u3002</p><p>\u743c\u6069\u8fd8\u51c6\u5907\u4e86\u4e00\u4e2a\u5149\u4eae\u672f\uff0c\u6216\u8bb8\u53ef\u4ee5\u521b\u9020\u51fa\u4e00\u4e2a\u5149\u7403\u6765\uff0c\u9a71\u9000\u9ed1\u6697\uff0c\u770b\u89c1\u654c\u4eba\uff0c\u4f46\u4ed6\u53ea\u8981\u8fd9\u4e48\u505a\uff0c\u9690\u5f62\u672f\u5c31\u4f1a\u7acb\u523b\u81ea\u52a8\u6d88\u5931\uff0c\u81ea\u5df1\u80af\u5b9a\u5c31\u4f1a\u6210\u4e3a\u88ab\u653b\u51fb\u7684\u76ee\u6807\u3002\u4e5f\u6709\u6cd5\u672f\u80fd\u591f\u8ba9\u5deb\u5e08\u5728\u9ed1\u6697\u4e2d\u89c6\u7269\u5982\u767d\u663c\uff0c\u4f46\u743c\u6069\u6ca1\u5b66\u8fc7\uff0c\u5c31\u7b97\u5b66\u4e86\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u95f2\u7740\u6ca1\u4e8b\u51c6\u5907\u8fd9\u79cd\u6cd5\u672f\u3002\u53c8\u6ca1\u6253\u7b97\u5730\u4e0b\u63a2\u9669\uff0c\u8981\u770b\u7a7f\u9ed1\u6697\u505a\u4ec0\u4e48\u3002\u6240\u4ee5\u4ed6\u5982\u4eca\u53ea\u80fd\u5e72\u7ad9\u7740\uff0c\u5e2e\u4e0d\u4e0a\u5fd9\u3002</p><p>\u4ed6\u51c6\u5907\u7684\u6cd5\u672f\u5df2\u7ecf\u7528\u4e86\u5c0f\u534a\uff0c\u867d\u7136\u8fd8\u6709\u51e0\u4e2a\u80fd\u7528\u6765\u7275\u5236\u5f71\u54cd\u654c\u4eba\uff0c\u5e2e\u52a9\u6885\u83f2\u65af\u53d6\u80dc\uff1b\u6216\u8005\u4ed6\u8eab\u4e0a\u7684\u957f\u888d\uff0c\u53ea\u8981\u4e00\u4e2a\u53e3\u4ee4\u6fc0\u53d1\uff0c\u4e5f\u7acb\u523b\u53ef\u4ee5\u53d1\u5c04\u51fa\u5bd2\u51b0\u7bad\u6765\u2014\u2014\u4f46\u4e00\u5207\u95ee\u9898\u7684\u5173\u952e\u662f\u5982\u4eca\u8fde\u5bf9\u65b9\u7684\u5f71\u5b50\u90fd\u770b\u4e0d\u89c1\uff0c\u6cd5\u672f\u671d\u8c01\u91ca\u653e\uff1f\u96be\u9053\u65e0\u5dee\u522b\u4e71\u7838\u4e48\u3002</p><p>\u65ad\u65ad\u7eed\u7eed\u7684\u547b\u541f\u58f0\u5728\u8eab\u8fb9\u5730\u4e0a\u54cd\u8d77\uff0c\u662f\u90a3\u4e2a\u5c11\u5e74\u827e\u683c\u5170\u7279\uff0c\u4ed6\u8d70\u5728\u6700\u524d\u9762\uff0c\u7ed3\u679c\u7b2c\u4e00\u4e2a\u88ab\u88ad\u51fb\uff0c\u4e0d\u8fc7\u770b\u8d77\u6765\u8fd8\u6ca1\u6b7b\uff0c\u81f3\u5c11\u8fd8\u80fd\u8eba\u5728\u5730\u4e0a\u5598\u6c14\u3002\u743c\u6069\u770b\u4e0d\u6e05\u695a\u60c5\u5f62\uff0c\u4e5f\u4e0d\u6562\u53bb\u6276\u4ed6\uff0c\u4ed6\u8d34\u7740\u5899\u58c1\u5411\u5916\u7f13\u6162\u79fb\u52a8\u7740\uff0c\u60f3\u8981\u5148\u8d70\u51fa\u8fd9\u7247\u9ed1\u6697\u518d\u8bf4\u3002</p><p>\u8fd9\u662f\u4eba\u4e3a\u5236\u9020\u7684\u9b54\u6cd5\u9ed1\u6697\uff0c\u90a3\u4e48\u8303\u56f4\u5fc5\u5b9a\u4e0d\u4f1a\u592a\u5927\uff0c\u5982\u4eca\u8eab\u5904\u5176\u4e2d\u675f\u624b\u675f\u811a\uff0c\u53ea\u8981\u8d70\u51fa\u53bb\uff0c\u81ea\u7136\u5c31\u6709\u529e\u6cd5\u5e94\u4ed8\u3002</p><p>\u4f46\u4ed6\u8fd8\u6ca1\u8d70\u51fa\u9ed1\u6697\uff0c\u5175\u5203\u649e\u51fb\u58f0\u9661\u7136\u6d88\u5931\u4e86\u3002\u6885\u83f2\u65af\u7684\u94f6\u5251\u548c\u94f6\u7532\u4e0a\u6cdb\u7740\u5fae\u5149\uff0c\u8ba9\u743c\u6069\u80fd\u6e05\u695a\u5730\u770b\u5230\u5979\u7684\u4f4d\u7f6e\uff0c\u5973\u5723\u6b66\u58eb\u811a\u6b65\u79fb\u9519\uff0c\u7f13\u7f13\u8f6c\u52a8\u8eab\u4f53\uff0c\u5bdf\u770b\u56db\u5468\uff0c\u5979\u663e\u7136\u4e5f\u5931\u53bb\u4e86\u5bf9\u624b\u7684\u884c\u8e2a\u3002</p><p>\u56db\u5468\u5b89\u9759\u4e0b\u6765\u3002</p><p>\u743c\u6069\u505c\u4f4f\u811a\u6b65\uff0c\u4e0d\u6562\u518d\u79fb\u52a8\u3002\u521a\u624d\u654c\u4eba\u5728\u548c\u6885\u83f2\u65af\u6fc0\u6218\uff0c\u4e0d\u4f1a\u6ce8\u610f\u5230\u81ea\u5df1\u8fd9\u4e2a\u9690\u5f62\u4eba\uff1b\u5982\u4eca\u5b89\u9759\u4e0b\u6765\uff0c\u81ea\u5df1\u518d\u53d1\u51fa\u4ec0\u4e48\u54cd\u52a8\uff0c\u5c31\u5f88\u5bb9\u6613\u66b4\u9732\u5f62\u8ff9\u4e86\u3002</p><p>\u4ed6\u770b\u7740\u6885\u83f2\u65af\uff0c\u5c11\u5973\u6b63\u53cc\u624b\u63e1\u7740\u94f6\u5251\uff0c\u8b66\u60d5\u5730\u89c2\u5bdf\u7740\uff0c\u5979\u7684\u80f8\u53e3\u5267\u70c8\u8d77\u4f0f\uff0c\u5598\u606f\u58f0\u6e05\u695a\u53ef\u95fb\u3002\u521a\u624d\u5728\u548c\u94f6\u9f99\u5e7d\u7075\u4ea4\u624b\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u5df2\u7ecf\u53d7\u4f24\u4e0d\u8f7b\uff0c\u4f53\u529b\u4e5f\u6d88\u8017\u5f88\u5927\uff0c\u867d\u7136\u51ed\u7740\u4e00\u80a1\u575a\u6bc5\u5f3a\u6491\u7740\uff0c\u7ec8\u7a76\u4e5f\u5df2\u7ecf\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u518d\u88ab\u7a81\u7136\u88ad\u51fb\uff0c\u4e00\u8f6e\u5feb\u901f\u4ea4\u624b\u540e\uff0c\u4f53\u529b\u5df2\u7ecf\u5feb\u8981\u6fd2\u4e34\u6781\u9650\u4e86\u3002</p><p>\u743c\u6069\u51c6\u5907\u4e86\u4e00\u4e2a\u6cd5\u672f\uff0c\u53ef\u4ee5\u8ba9\u6885\u83f2\u65af\u6682\u65f6\u6062\u590d\u4e00\u4e9b\u529b\u6c14\uff0c\u4f46\u4ed6\u4e0d\u6562\u4f7f\u7528\u3002\u654c\u4eba\u6b63\u5728\u9ed1\u6697\u4e2d\u7aa5\u4f3a\uff0c\u7b49\u5f85\u7740\u7834\u7efd\uff0c\u53d1\u51fa\u81f4\u547d\u4e00\u51fb\uff0c\u81ea\u5df1\u53ea\u8981\u4e00\u65bd\u6cd5\uff0c\u7acb\u523b\u5c31\u4f1a\u6210\u4e3a\u88ab\u653b\u51fb\u7684\u76ee\u6807\u3002</p><p>\u6240\u4ee5\u4ed6\u4e0d\u6562\u52a8\u5f39\u3002</p><p>\u6885\u83f2\u65af\u540c\u6837\u4e5f\u4e0d\u6562\u8f7b\u4e3e\u5984\u52a8\uff0c\u90a3\u4f1a\u9732\u51fa\u7834\u7efd\uff0c\u88ab\u72e1\u733e\u7684\u5bf9\u624b\u6293\u4f4f\u673a\u4f1a\u51fb\u6740\u3002\u5979\u6162\u6162\u632a\u52a8\u811a\u6b65\uff0c\u4fdd\u6301\u7740\u5b88\u5fa1\u59ff\u52bf\uff0c\u7d27\u7d27\u76ef\u7740\u9762\u524d\u7684\u9ed1\u6697\u865a\u7a7a\uff0c\u60f3\u4ece\u4e2d\u627e\u51fa\u654c\u4eba\u6765\uff0c\u4f46\u6ca1\u6709\u4efb\u4f55\u8e2a\u8ff9\u3002</p><p>\u7d27\u5f20\u4f1a\u6d88\u8017\u6389\u4eba\u7684\u5168\u90e8\u7cbe\u529b\uff0c\u5bf9\u624b\u4f9d\u65e7\u5728\u9ed1\u6697\u4e2d\u7aa5\u4f3a\u7740\uff0c\u6ca1\u6709\u4efb\u4f55\u52a8\u4f5c\uff0c\u4f46\u8fd9\u5c31\u662f\u65e0\u5f62\u7684\u538b\u529b\uff0c\u903c\u8feb\u5f97\u743c\u6069\u548c\u6885\u83f2\u65af\u90fd\u4e0d\u6562\u52a8\u5f39\uff0c\u552f\u6709\u90a3\u4e2a\u5c11\u5e74\u827e\u683c\u5170\u7279\u8fd8\u5728\u65ad\u65ad\u7eed\u7eed\u5730\u53d1\u51fa\u75db\u82e6\u547b\u541f\u3002\u743c\u6069\u7684\u80cc\u4e0a\u5df2\u7ecf\u88ab\u51b7\u6c57\u6d78\u900f\uff0c\u4ed6\u611f\u89c9\u81ea\u5df1\u7684\u5fc3\u5728\u7830\u7830\u8df3\uff0c\u5feb\u8981\u4ece\u80f8\u53e3\u649e\u51fa\u6765\uff0c\u8fd9\u6837\u4e0b\u53bb\u5c31\u7b97\u4e0d\u88ab\u6740\u6b7b\uff0c\u80af\u5b9a\u4e5f\u4f1a\u56e0\u4e3a\u7d27\u5f20\u548c\u75b2\u60eb\u800c\u81ea\u5df1\u57ae\u6389\u3002</p><p>\u4ed6\u5f00\u59cb\u5c0f\u5fc3\u5730\u79fb\u52a8\u811a\u6b65\uff0c\u51c6\u5907\u5f80\u6d1e\u53e3\u65b9\u5411\u632a\uff0c\u4f46\u8fd8\u6ca1\u8d70\u51fa\u4e00\u6b65\uff0c\u9661\u7136\u95f4\u773c\u524d\u660e\u4eae\uff0c\u9ed1\u6697\u81ea\u884c\u53c8\u7a81\u5730\u6563\u53bb\u3002\u743c\u6069\u53d1\u73b0\u81ea\u5df1\u4e0d\u77e5\u4e0d\u89c9\u95f4\u5df2\u7ecf\u8ddd\u79bb\u6d1e\u53e3\u5f88\u8fd1\uff0c\u501f\u7740\u5916\u9762\u900f\u8fdb\u6765\u7684\u6668\u5149\uff0c\u4ed6\u73b0\u5728\u6e05\u695a\u5730\u770b\u89c1\u90a3\u4e2a\u53d7\u4f24\u7684\u5c11\u5e74\uff0c\u6b63\u5750\u5728\u5730\u4e0a\uff0c\u80cc\u9760\u7740\u5899\u58c1\uff0c\u80f3\u818a\u4e0a\u88ab\u5212\u5f00\u4e86\u4e00\u9053\u6df1\u6df1\u7684\u4f24\u53e3\uff0c\u8840\u4ece\u91cc\u9762\u6d41\u51fa\uff0c\u5374\u662f\u9ed1\u8272\u7684\uff0c\u4ed6\u75db\u82e6\u5730\u547b\u541f\u7740\uff0c\u5168\u8eab\u8f7b\u5fae\u62bd\u6410\uff0c\u51e0\u6b21\u60f3\u7528\u529b\u7ad9\u8d77\u6765\u5374\u90fd\u65e0\u6cd5\u529e\u5230\u3002\u6885\u83f2\u65af\u4f9d\u65e7\u4fdd\u6301\u7740\u6212\u5907\u59ff\u52bf\uff0c\u4f46\u89c6\u7ebf\u6240\u53ca\u8303\u56f4\u5185\uff0c\u5374\u6ca1\u6709\u770b\u89c1\u4efb\u4f55\u654c\u4eba\u3002</p><p>\u5947\u602a\uff0c\u96be\u9053\u5c31\u8fd9\u6837\u653e\u5f03\u9000\u8d70\u4e86\uff1f</p><p>\u4e8b\u51fa\u7a81\u7136\uff0c\u53c8\u5728\u9ed1\u6697\u4e2d\uff0c\u743c\u6069\u4e5f\u6ca1\u770b\u89c1\u5230\u5e95\u662f\u8c01\u5728\u8fd9\u91cc\u4f0f\u51fb\u81ea\u5df1\u4e09\u4eba\uff0c\u4f46\u7565\u7565\u8f6c\u5ff5\u4e5f\u5c31\u80fd\u731c\u6d4b\u51fa\u6765\u3002\u8fd9\u91cc\u662f\u70db\u5821\uff0c\u8fd8\u80fd\u6709\u8c01\uff1f\u53ea\u53ef\u80fd\u662f\u90a3\u4e2a\u5973\u6740\u624b\u838e\u73de\u514b\u3002</p><p>\u627e\u4e86\u5979\u534a\u5929\uff0c\u539f\u6765\u5012\u662f\u8eb2\u5728\u8fd9\u91cc\uff0c\u53ea\u662f\u5979\u5230\u5e95\u6765\u70db\u5821\u505a\u4ec0\u4e48\uff1f\u5979\u5bf9\u743c\u6069\u8bf4\u662f\u6765\u5077\u4e66\uff0c\u4f46\u73b0\u5728\u770b\u8d77\u6765\u53c8\u4e0d\u592a\u50cf\uff1b\u6885\u83f2\u65af\u8bf4\u5979\u8981\u6765\u6740\u4eba\uff0c\u4f46\u5230\u5e95\u662f\u8981\u6740\u8c01\uff1f\u5982\u679c\u8981\u6740\u743c\u6069\uff0c\u90a3\u5979\u5176\u5b9e\u65e9\u6709\u673a\u4f1a\u53ef\u4ee5\u4e0b\u624b\uff1b\u5982\u679c\u8981\u6740\u6885\u83f2\u65af\uff0c\u53c8\u6ca1\u770b\u5979\u6709\u534a\u70b9\u52a8\u4f5c\u2014\u2014\u800c\u4e14\u6b64\u65f6\u6b64\u523b\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u5176\u5b9e\u90fd\u8fd8\u6ca1\u4e8b\uff0c\u771f\u6b63\u53d7\u4f24\u6328\u4e86\u4e00\u5200\u7684\u53cd\u800c\u662f\u90a3\u4e2a\u5c11\u5e74\u827e\u683c\u5170\u7279\u3002</p><p>\u8fd9\u5230\u5e95\u662f\u5728\u641e\u4ec0\u4e48\uff1f</p><p>\u6b63\u5f53\u4ed6\u7591\u60d1\u7684\u65f6\u5019\uff0c\u773c\u89d2\u4f59\u5149\u7a81\u7136\u77a5\u89c1\u4e00\u4e1d\u52a8\u9759\uff0c\u4f4e\u5934\u770b\u53bb\uff0c\u5374\u662f\u6885\u83f2\u65af\u7684\u5f71\u5b50\u6b63\u5fae\u5fae\u98a4\u52a8\u3002\u8fd9\u91cc\u5df2\u7ecf\u9760\u8fd1\u6d1e\u53e3\uff0c\u5916\u9762\u5929\u8272\u5927\u4eae\uff0c\u6668\u5149\u900f\u8fdb\u6765\uff0c\u6885\u83f2\u65af\u7684\u5f71\u5b50\u6de1\u6de1\u5730\u6295\u5728\u5730\u4e0a\uff0c\u6709\u4e9b\u626d\u66f2\uff0c\u4f46\u5982\u4eca\u4f3c\u4e4e\u6b63\u5728\u5fae\u5fae\u98a4\u52a8\uff0c\u5c31\u50cf\u873f\u8712\u722c\u884c\u7684\u86c7\u4e00\u6837\u3002</p><p>\u4ed6\u4e00\u60ca\u4e4b\u4e0b\uff0c\u6b63\u8981\u63d0\u9192\u6885\u83f2\u65af\uff0c\u5c31\u89c1\u90a3\u9053\u5f71\u5b50\u9aa4\u7136\u66b4\u8d77\uff0c\u4ece\u4e2d\u5e7b\u51fa\u4e00\u4e2a\u4eba\u6765\uff0c\u8910\u53d1\u94fe\u7532\uff0c\u4e30\u4e73\u7fd8\u81c0\uff0c\u6b63\u662f\u5973\u6740\u624b\u838e\u73de\u514b\uff0c\u5979\u53cc\u624b\u6301\u77ed\u5251\uff0c\u75be\u5982\u95ea\u7535\u822c\u671d\u6885\u83f2\u65af\u80cc\u540e\u523a\u53bb\u3002</p><p>\u8fd9\u4e00\u523a\u731d\u51fa\u4e0d\u610f\uff0c\u5feb\u82e5\u95ea\u7535\uff0c\u53c8\u662f\u5728\u80cc\u540e\u53d1\u96be\uff0c\u6885\u83f2\u65af\u7406\u5f53\u6839\u672c\u6765\u4e0d\u53ca\u53cd\u5e94\uff0c\u4f46\u5979\u5374\u4eff\u4f5b\u9884\u5148\u77e5\u9053\u4e00\u822c\uff0c\u6beb\u4e0d\u72b9\u8c6b\u5730\u626d\u8170\u53cd\u624b\u5c31\u662f\u4e00\u5251\uff0c\u671d\u6740\u624b\u76f4\u76f4\u5288\u4e0b\uff0c\u53c8\u5feb\u53c8\u72e0\uff0c\u51cc\u5389\u65e0\u6bd4\uff0c\u4eff\u4f5b\u538b\u6839\u5c31\u662f\u84c4\u52bf\u5df2\u4e45\uff0c\u5c31\u4e3a\u7b49\u5f85\u8fd9\u4e00\u51fb\u3002</p><p>\u4f46\u540c\u65f6\u5979\u5374\u6ca1\u6709\u95ea\u907f\uff0c\u4e5f\u5c31\u662f\u8bf4\u5982\u6b64\u4e00\u6765\uff0c\u5373\u4f7f\u5979\u7684\u94f6\u5251\u5288\u4e2d\uff0c\u6740\u624b\u7684\u77ed\u5251\u4e5f\u4f1a\u523a\u4e2d\u5979\u7684\u8eab\u4f53\uff0c\u8fd9\u662f\u4e2a\u4e24\u8d25\u4ff1\u4f24\u7684\u5c40\u9762\u3002</p><p>\u6740\u624b\u7684\u5de5\u4f5c\u662f\u6740\u4eba\uff0c\u4e0d\u60f3\u81ea\u6740\uff0c\u4e24\u8d25\u4ff1\u4f24\u7684\u5c40\u9762\u663e\u7136\u65e0\u6cd5\u88ab\u63a5\u53d7\u3002\u838e\u73de\u514b\u5306\u5fd9\u4e2d\u53cc\u81c2\u56de\u7ed5\u4e00\u5708\uff0c\u4e24\u67c4\u77ed\u5251\u4ea4\u53c9\uff0c\u683c\u4f4f\u4e86\u6885\u83f2\u65af\u7684\u94f6\u5251\u3002</p><p>\u5de8\u5927\u7684\u529b\u91cf\u6d8c\u6765\uff0c\u6740\u624b\u88ab\u649e\u9000\u4e86\u4e24\u6b65\uff0c\u5979\u7684\u53cc\u81c2\u9690\u9690\u9178\u9ebb\uff0c\u864e\u53e3\u7efd\u88c2\u51fa\u8840\uff0c\u51e0\u4e4e\u8981\u63e1\u4e0d\u4f4f\u77ed\u5251\uff0c\u5fc3\u4e2d\u7740\u5b9e\u60ca\u9a87\u65e0\u6bd4\u3002\u4f46\u7d27\u63a5\u7740\uff0c\u5979\u53d1\u73b0\u5973\u5723\u6b66\u58eb\u4f9d\u65e7\u7ad9\u5728\u539f\u5730\uff0c\u5e76\u6ca1\u6709\u4e58\u673a\u8ffd\u51fb\u3002</p><p>\u6885\u83f2\u65af\u5df2\u7ecf\u7cbe\u75b2\u529b\u7aed\u3002</p><p>\u5b9e\u9645\u4e0a\uff0c\u5973\u5723\u6b66\u58eb\u539f\u672c\u5c31\u5df2\u662f\u52c9\u5f3a\u652f\u6491\uff0c\u5288\u51fa\u8fd9\u4e00\u5251\uff0c\u5df2\u7ecf\u662f\u5f3a\u5f29\u4e4b\u672b\uff0c\u518d\u65e0\u518d\u6218\u4e4b\u529b\u3002\u6740\u624b\u89c1\u72b6\uff0c\u5fc3\u5934\u4e00\u9635\u72c2\u559c\uff0c\u819d\u76d6\u5fae\u66f2\uff0c\u8eab\u4f53\u518d\u5ea6\u5982\u79bb\u5f26\u4e4b\u7bad\u4e00\u822c\u5f39\u51fa\uff0c\u523a\u5411\u6885\u83f2\u65af\u3002</p><p>\u201c\u73ca\u5609\uff01\u201d</p><p>\u5343\u94a7\u4e00\u53d1\u4e4b\u9645\uff0c\u743c\u6069\u5c06\u9f99\u9cde\u5c0f\u76fe\u63b7\u51fa\uff0c\u53eb\u51fa\u53e3\u4ee4\u3002\u9f99\u9cde\u76fe\u6d6e\u7a7a\u98de\u65cb\uff0c\u671d\u6740\u624b\u731b\u529b\u649e\u53bb\uff0c\u5b83\u5df2\u7ecf\u6765\u4e0d\u53ca\u6321\u4f4f\u77ed\u5251\uff0c\u53ea\u80fd\u6307\u671b\u903c\u8feb\u6740\u624b\u81ea\u5df1\u95ea\u907f\uff0c\u653e\u5f03\u653b\u51fb\u3002</p><p>\u4f46\u6740\u624b\u624b\u4e2d\u6709\u4e24\u67c4\u77ed\u5251\u3002</p><p>\u5979\u5de6\u624b\u6325\u51fa\uff0c\u77ed\u5251\u51fb\u51fa\uff0c\u5251\u5c16\u62b5\u5728\u76fe\u9762\u4e0a\uff0c\u53d1\u51fa\u4ee4\u4eba\u7259\u9178\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\uff0c\u5c06\u9f99\u9cde\u76fe\u91cd\u91cd\u63a8\u5f00\uff0c\u8eab\u5f62\u4e1d\u6beb\u4e0d\u505c\uff0c\u53f3\u81c2\u4f9d\u65e7\u76f4\u523a\uff0c\u77ed\u5251\u4e0a\u5bd2\u5149\u95ea\u70c1\uff0c\u950b\u5203\u4f3c\u4e4e\u8fd8\u5e26\u7740\u4e00\u4e1d\u6bb7\u6bb7\u7eff\u5149\u3002\u6885\u83f2\u65af\u65e0\u6cd5\u95ea\u907f\uff0c\u4e5f\u65e0\u529b\u4e3e\u5251\u683c\u6321\uff0c\u743c\u6069\u66f4\u52a0\u6765\u4e0d\u53ca\u6551\u63f4\uff0c\u773c\u770b\u8fd9\u4e00\u51fb\u5c31\u8981\u5f97\u624b\u3002\u6885\u83f2\u65af\u7684\u867d\u7136\u7a7f\u7740\u94f6\u7532\uff0c\u5374\u4e5f\u672a\u5fc5\u80fd\u6321\u5f97\u4f4f\u3002\u6b63\u5343\u94a7\u4e00\u53d1\u95f4\uff0c\u9661\u5730\u773c\u524d\u8679\u5f69\u95ea\u70c1\uff0c\u4e00\u9762\u5f27\u5f62\u5149\u58c1\u81ea\u865a\u7a7a\u4e2d\u751f\u6210\uff0c\u6321\u5728\u6885\u83f2\u65af\u8eab\u524d\u3002</p><p>\u77ed\u5251\u523a\u5728\u8679\u5f69\u5149\u5f27\u4e0a\uff0c\u53d1\u51fa\u6e05\u8106\u7684\u91d1\u5c5e\u649e\u51fb\u58f0\uff0c\u9677\u5165\u534a\u5206\uff0c\u5374\u518d\u4e5f\u523a\u4e0d\u8fdb\u53bb\u3002\u6740\u624b\u4e00\u51fb\u4e0d\u4e2d\uff0c\u6025\u901f\u6536\u624b\uff0c\u7eb5\u8eab\u540e\u9000\uff0c\u51e0\u4e2a\u8d77\u843d\u5c31\u8dc3\u5165\u9ed1\u6697\u4e4b\u4e2d\uff0c\u6d88\u5931\u4e0d\u89c1\u3002</p><p>\u743c\u6069\u60ca\u9b42\u521d\u5b9a\uff0c\u6b63\u4e0d\u77e5\u600e\u4e48\u90a3\u5f27\u5f62\u8679\u5149\u81ea\u4f55\u800c\u6765\uff0c\u5c31\u542c\u89c1\u6d1e\u53e3\u5916\u9762\u6709\u4eba\u5728\u8f7b\u8f7b\u9f13\u638c\uff0c\u201c\u65e9\u4e0a\u597d\uff0c\u4e09\u4f4d\uff0c\u201d\u4e00\u4e2a\u6e05\u6717\u4fca\u96c5\u7684\u58f0\u97f3\u8bf4\uff0c\u201c\u4e00\u5927\u65e9\uff0c\u5728\u6d1e\u91cc\u505a\u4ec0\u4e48\u5462\uff1f\u201d</p><p>\u8bf4\u8bdd\u58f0\u4e2d\uff0c\u4e00\u4e2a\u91d1\u53d1\u9752\u5e74\u8d70\u4e86\u8fdb\u6765\uff0c\u4ed6\u7684\u8138\u4e0a\u7b11\u5bb9\u6e29\u548c\uff0c\u4eff\u4f5b\u6625\u5929\u7684\u707f\u70c2\u9633\u5149\u4f3c\u7684\uff0c\u4e00\u8eab\u767d\u888d\u7ea4\u5c18\u4e0d\u67d3\uff0c\u6c38\u8fdc\u5149\u6d01\u5982\u65b0\u3002\u743c\u6069\u4e0d\u592a\u8bb0\u5f97\u4ed6\u7684\u76f8\u8c8c\uff0c\u4f46\u773c\u5149\u4e00\u89e6\u5230\u4ed6\u90a3\u8679\u5f69\u95ea\u70c1\u7684\u5947\u602a\u53cc\u773c\uff0c\u987f\u65f6\u60f3\u4e86\u8d77\u6765\u3002</p><p>\u5c31\u662f\u5728\u9634\u9b42\u57ce\u91cc\u66fe\u7ecf\u9047\u4e0a\u8fc7\u7684\u90a3\u53ea\u8679\u5f69\u9f99\u3002</p><p>\u203b\u203b\u203b</p><p>\u5f53\u65e5\u5728\u9634\u9b42\u57ce\u91cc\uff0c\u8fd9\u53ea\u8679\u5f69\u9f99\u4e5f\u662f\u53d8\u6210\u4eba\u5f62\uff0c\u9047\u4e0a\u743c\u6069\uff0c\u5411\u4ed6\u8be2\u95ee\u8def\u5f84\u3002\u540e\u6765\u624d\u77e5\u9053\u539f\u6765\u662f\u8dd1\u53bb\u5077\u9634\u9b42\u57ce\u4e3b\u7684\u6d6e\u7a7a\u8230\uff0c\u5f04\u5f97\u5168\u57ce\u6212\u5907\uff0c\u4e00\u573a\u5927\u6218\u4e4b\u540e\uff0c\u526f\u57ce\u4e3b\u970d\u6770\u54c8\u7eb3\u4e00\u51fb\u5927\u88c2\u89e3\u672f\u8f70\u5f00\u4e86\u8679\u5f69\u9f99\u7684\u8679\u5149\u62a4\u7f69\uff0c\u628a\u4ed6\u6253\u5f97\u843d\u8352\u800c\u9003\u3002\u5bf9\u4e86\uff0c\u4ed6\u5f53\u65f6\u8fd8\u6709\u4e2a\u5c0f\u80d6\u5b50\u8ddf\u73ed\uff0c\u636e\u8bf4\u7a7f\u7740\u539a\u539a\u7684\u4e09\u5c42\u76ae\u7532\uff0c\u771f\u662f\u6015\u6b7b\u5230\u5bb6\u4e86\uff0c\u7ed3\u679c\u8fd8\u88ab\u6253\u5f97\u6700\u60e8\u3002</p><p>\u867d\u7136\u4f3c\u4e4e\u5f88\u72fc\u72c8\uff0c\u4f46\u743c\u6069\u5728\u5fc3\u4e2d\u538b\u6839\u5c31\u4e0d\u6562\u4e1d\u6beb\u8f7b\u89c6\u8fd9\u53ea\u8679\u5f69\u9f99\u3002\u80fd\u8f7b\u677e\u6f5c\u5165\u9634\u9b42\u57ce\uff0c\u80fd\u5728\u5927\u8303\u56f4\u8ff7\u9501\u7684\u538b\u5236\u4e0b\u529b\u654c\u5168\u57ce\u5deb\u5e08\uff0c\u6700\u540e\u8fd8\u8131\u56f0\u800c\u51fa\uff0c\u65e0\u8bba\u518d\u600e\u4e48\u4e0d\u5229\uff0c\u90a3\u4e5f\u79f0\u5f97\u4e0a\u662f\u867d\u8d25\u72b9\u80dc\u4e86\u3002</p><p>\u9634\u9b42\u57ce\u867d\u7136\u4ec5\u4ec5\u662f\u4e00\u5ea7\u57ce\u5e02\uff0c\u4eba\u53e3\u4e0d\u8fc7\u4e24\u4e07\u4e94\u5343\u4eba\uff0c\u4f46\u5374\u662f\u53e4\u9b54\u6cd5\u5e1d\u56fd\u201c\u8010\u745f\u745e\u5c14\u201d\u8f89\u714c\u6587\u660e\u7684\u552f\u4e00\u5ef6\u7eed\u548c\u4f20\u627f\uff0c\u5728\u5e7d\u5f71\u754c\u79ef\u84c4\u529b\u91cf\u8fd1\u4e24\u5343\u5e74\uff0c\u62e5\u6709\u4e00\u5343\u540d\u4ee5\u4e0a\u7684\u6b63\u5f0f\u5deb\u5e08\uff0c\u5176\u4e2d\u6709\u4e24\u767e\u540d\u5de6\u53f3\u90fd\u79f0\u5f97\u4e0a\u662f\u9ad8\u9636\u7cbe\u82f1\uff0c\u81f3\u4e8e\u9876\u5c16\u7684\u201c\u5927\u5965\u672f\u5e08\u201d\u7684\u6570\u91cf\uff0c\u5219\u662f\u79d8\u5bc6\uff0c\u65e0\u4eba\u77e5\u6653\u3002\u800c\u4e14\u9634\u9b42\u57ce\u7684\u6574\u4f53\u6c34\u51c6\uff0c\u8fdc\u8fdc\u5728\u7269\u8d28\u754c\u4e4b\u4e0a\u3002\u5982\u743c\u6069\u8fd9\u79cd\u521a\u521a\u624d\u6bd5\u4e1a\u7684\u5deb\u5e08\uff0c\u5728\u9634\u9b42\u57ce\u7684\u5deb\u5e08\u9636\u5c42\u4e2d\u53ea\u80fd\u7b97\u662f\u5e95\u5c42\uff0c\u4f46\u653e\u5728\u7269\u8d28\u754c\uff0c\u5df2\u7ecf\u7b97\u662f\u5f88\u4e0d\u9519\u7684\u4eba\u624d\u4e86\u3002\u5f88\u591a\u5deb\u5e08\u53d7\u8d44\u8d28\u3001\u6761\u4ef6\u3001\u8d22\u529b\u6240\u9650\uff0c\u82e6\u4fee\u5230\u51e0\u5341\u5c81\u751a\u81f3\u8001\u6b7b\uff0c\u4e5f\u672a\u5fc5\u80fd\u89e6\u6478\u5230\u9b54\u7f51\u7684\u7b2c\u4e09\u5c42\u5462\u3002</p><p>\u8fd9\u8fd8\u4ec5\u4ec5\u662f\u6307\u5deb\u5e08\uff0c\u4e8b\u5b9e\u4e0a\u5728\u9634\u9b42\u57ce\uff0c\u7531\u4e8e\u838e\u5c14\u5973\u795e\u7684\u5a01\u52bf\u6240\u53ca\uff0c\u9996\u5e2d\u5927\u7267\u5e08\u745e\u74e6\u5170\u7684\u52aa\u529b\uff0c\u57ce\u4e2d\u7267\u5e08\u4e00\u7cfb\u5b8c\u5168\u8db3\u4ee5\u548c\u5deb\u5e08\u5206\u5ead\u6297\u793c\uff0c\u5b9e\u529b\u76f8\u5f53\u3002</p><p>\u4e5f\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u6240\u4ee5\u9634\u9b42\u57ce\u4e00\u56de\u5230\u7269\u8d28\u754c\uff0c\u7acb\u523b\u4fbf\u5f15\u8d77\u5404\u65b9\u5173\u6ce8\u3002\u5982\u4eca\u4f20\u51fa\u9634\u9b42\u57ce\u521b\u9020\u51fa\u906e\u5929\u853d\u65e5\u7684\u9b54\u6cd5\uff0c\u8981\u4e00\u4e3e\u878d\u5316\u5fd7\u9ad8\u51b0\u5ddd\u7684\u6d88\u606f\uff0c\u8fd9\u66f4\u8ba9\u6240\u6709\u4eba\u90fd\u60f4\u60f4\u5fc3\u60ca\u3002\u4e00\u65b9\u9762\u662f\u6124\u6012\u4e8e\u9634\u9b42\u57ce\u7684\u8fd9\u79cd\u884c\u4e3a\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u5374\u4e5f\u662f\u9707\u60ca\u4e8e\u9634\u9b42\u57ce\u7684\u5b9e\u529b\u3002\u4e07\u5e74\u79ef\u96ea\u7684\u5343\u91cc\u5927\u51b0\u5ddd\uff0c\u80fd\u88ab\u4e00\u4e3e\u878d\u5316\uff0c\u8fd9\u662f\u5bfb\u5e38\u4eba\u538b\u6839\u60f3\u90fd\u4e0d\u6562\u60f3\u7684\u4e8b\u60c5\uff0c\u9634\u9b42\u57ce\u5c45\u7136\u90fd\u80fd\u505a\u5f97\u51fa\u6765\uff0c\u8fd9\u5df2\u7ecf\u8fd1\u4e4e\u4f20\u5947\u548c\u795e\u8bdd\u4e86\u3002</p><p>\u5982\u6b64\u5f3a\u5927\u7684\u9634\u9b42\u57ce\uff0c\u4f9d\u65e7\u6ca1\u80fd\u628a\u8fd9\u53ea\u8679\u5f69\u9f99\u5f53\u573a\u51fb\u6bd9\uff0c\u4e5f\u786e\u5b9e\u7b97\u4e0d\u4e0a\u4ec0\u4e48\u5149\u5f69\u7684\u4e8b\u60c5\u3002\u5927\u7ea6\u6b63\u56e0\u4e3a\u5982\u6b64\uff0c\u8fd9\u4e48\u8f70\u52a8\u7684\u4e8b\u60c5\u53d1\u751f\u4e4b\u540e\uff0c\u5c45\u7136\u6ca1\u6709\u534a\u70b9\u4e0b\u6587\uff0c\u4eff\u4f5b\u5b98\u65b9\u6253\u7b97\u65e9\u65e9\u9057\u5fd8\uff0c\u5c31\u5f53\u6ca1\u53d1\u751f\u8fc7\u4f3c\u7684\u3002\u5f53\u7136\uff0c\u8fd9\u4ec5\u4ec5\u662f\u731c\u6d4b\u3002</p><p>\u8fd9\u53ea\u5012\u9709\u7684\u8679\u5f69\u9f99\u5f53\u65f6\u88ab\u51fb\u4f24\uff0c\u8840\u88ab\u62ff\u6765\u5236\u6210\u4e86\u8679\u5f69\u6212\u6307\uff0c\u4f5c\u4e3a\u4e94\u5e74\u7ea7\u6bd5\u4e1a\u7684\u5956\u54c1\uff0c\u53ef\u60dc\u743c\u6069\u6ca1\u80fd\u62ff\u5230\u624b\u3002\u5b83\u7684\u9cde\u7247\u88ab\u5236\u6210\u4e86\u6d3b\u5316\u76fe\uff0c\u5012\u662f\u7531\u5e03\u96f7\u7eb3\u65af\u738b\u5b50\u4ea4\u7ed9\u4e86\u743c\u6069\uff0c\u73b0\u5728\u5c31\u60ac\u6d6e\u5728\u7a7a\u4e2d\u2026\u2026</p><p>\u7b49\u7b49\u2026\u2026\u8fd9\u76fe\u724c\u662f\u7528\u4ed6\u7684\u9cde\u7247\u505a\u7684\u2026\u2026</p><p>\u743c\u6069\u7684\u8eab\u4f53\u4e00\u4e0b\u5b50\u50f5\u4f4f\u4e86\uff0c\u51e0\u4e4e\u8981\u8bf4\u4e0d\u51fa\u8bdd\u6765\u3002\u521a\u624d\u4e3a\u4e86\u6551\u6885\u83f2\u65af\uff0c\u4ed6\u63b7\u51fa\u4e86\u9f99\u9cde\u76fe\uff0c\u6b64\u65f6\u8fd8\u60ac\u6d6e\u5728\u7a7a\u4e2d\u672a\u66fe\u6536\u56de\uff0c\u8fd9\u4e2a\u53d8\u6210\u91d1\u53d1\u9752\u5e74\u7684\u8679\u5f69\u9f99\u80af\u5b9a\u5df2\u7ecf\u770b\u89c1\u4e86\u2026\u2026</p><p>\u4ed6\u662f\u4f1a\u706b\u5192\u4e09\u4e08\u76f4\u63a5\u53d1\u98d9\u8fd8\u662f\u628a\u743c\u6069\u540a\u8d77\u6765\u6253\u2026\u2026</p><p>\u743c\u6069\u4e1d\u6beb\u4e0d\u6307\u671b\u81ea\u5df1\u80fd\u4ece\u4e00\u53ea\u8679\u5f69\u9f99\u9762\u524d\u9003\u8131\uff0c\u751a\u81f3\u4ed6\u8111\u5b50\u91cc\u4ec0\u4e48\u5ff5\u5934\u90fd\u6ca1\u8f6c\u2014\u2014\u7edd\u5bf9\u60ac\u6b8a\u7684\u5b9e\u529b\u5dee\u8ddd\u5230\u8fd9\u79cd\u7a0b\u5ea6\uff0c\u6839\u672c\u5c31\u6ca1\u6709\u4efb\u4f55\u76d8\u7b97\u601d\u8003\u7684\u4f59\u5730\u3002\u4ed6\u53ea\u80fd\u6014\u6014\u5730\u7ad9\u7740\uff0c\u770b\u7740\u91d1\u53d1\u9752\u5e74\u8d70\u8fdb\u6765\uff0c\u4ece\u8eab\u8fb9\u8d70\u8fc7\uff0c\u4f38\u624b\u865a\u7a7a\u4e00\u6293\uff0c\u5c06\u90a3\u9762\u9f99\u9cde\u76fe\u53d6\u5728\u624b\u4e2d\u3002</p><p>\u201c\u5514\uff0c\u201d\u4ed6\u7aef\u8be6\u7740\uff0c\u201c\u8fd9\u662f\u4f60\u7684\u76fe\u724c\uff1f\u201d\u4ed6\u95ee\u743c\u6069\u3002</p><p>\u743c\u6069\u70b9\u70b9\u5934\u3002</p></article>"}}}]);
