(window.webpackJsonp=window.webpackJsonp||[]).push([[105],{128:function(p){p.exports={chapterTitle:null,title:"\u7b2c1089\u7ae0 \u4e00\u591c\u6a2a\u8d22",content:"<article><h2>\u7b2c1089\u7ae0 \u4e00\u591c\u6a2a\u8d22</h2><p>\u3000\u3000\u53f6\u5efa\u658c\u6628\u591c\u7684\u5174\u594b\u5728\u8fd9\u91cc\u53d7\u632b\u4e4b\u540e\uff0c\u5f20\u606a\u4e00\u89c9\u7761\u5230\u5929\u4eae\u5c31\u6ca1\u6709\u518d\u7ed9\u4eba\u6253\u6405\u8fc7\uff0c\u6e05\u6668\u9192\u6765\uff0c\u7ad9\u5230\u843d\u5730\u7a97\u524d\uff0c\u5c06\u7a97\u5e18\u62c9\u5f00\uff0c\u8ba9\u6668\u5149\u8086\u65e0\u5fcc\u60ee\u7684\u503e\u6cc4\u8fdb\u6765\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u65e5\u5bd2\u6d41\u88ad\u6765\uff0c\u9662\u5b50\u91cc\u79ef\u7740\u4e9b\u971c\u8ff9\uff0c\u6ca1\u60f3\u5230\u4e00\u591c\u521a\u8fc7\uff0c\u9662\u5b50\u89d2\u843d\u91cc\u7684\u864e\u8e44\u6885\u6709\u82b1\u9aa8\u6735\u513f\u7efd\u5f00\uff0c\u6628\u5929\u8ddf\u8bb8\u601d\u901a\u7535\u8bdd\u65f6\u5979\u8fd8\u60e6\u8bb0\u7740\u9662\u5b50\u91cc\u7684\u864e\u8e44\u6885\u8fd9\u51e0\u5929\u4f1a\u5f00\uff0c\u6885\u679d\u67af\u7626\uff0c\u6ca1\u6709\u53f6\u5b50\uff0c\u7f00\u7740\u82b1\u9aa8\u6735\u513f\uff0c\u5728\u6e05\u5bd2\u7684\u6668\u5149\u91cc\uff0c\u8ba9\u4eba\u770b\u4e86\u5c24\u5176\u89c9\u5f97\u7cbe\u795e\u3002</p><p>\u3000\u3000\u5f20\u606a\u5230\u6e05\u5bd2\u903c\u4eba\u7684\u9662\u5b50\u91cc\u770b\u4e86\u4e00\u4f1a\u513f\u864e\u8e44\u6885\uff0c\u6709\u4eba\u5c06\u65e9\u9910\u4e0e\u6668\u62a5\u9001\u8fc7\u6765\u3002\u8981\u770b\u5404\u754c\u5bf9\u4e2d\u7f8e\u53cc\u8fb9\u534f\u8bae\u4ee5\u53ca\u6606\u817e\u5728\u7ebf\u80a1\u7968\u66b4\u6da8\u7684\u53cd\u5e94\uff0c\u4e0a\u7f51\u6d4f\u89c8\u65b0\u95fb\u66f4\u5408\u9002\u4e00\u4e9b\uff0c\u53ea\u662f\u751f\u6d3b\u5076\u5c14\u66f4\u4f20\u7edf\u4e00\u4e9b\u672a\u5c1d\u4e0d\u597d\u3002</p><p>\u3000\u3000\u5728ESS\u5373\u5c06\u5927\u89c4\u6a21\u51cf\u6301\u7684\u538b\u529b\uff0c\u6606\u817e\u5728\u7ebf\u7684\u80a1\u4ef7\u5728\u5f00\u76d8\u5927\u6da8\u4e4b\u540e\u53c8\u632f\u8361\u4e0a\u626c\u6700\u7ec8\u7ad9\u7a33\u572848\u7f8e\u5143\u4e4b\u4e0a\uff0c\u5e02\u503c\u4e00\u591c\u7ffb\u500d\uff0c\u7ed9\u4e92\u8054\u7f51\u8d22\u5bcc\u795e\u8bdd\u6dfb\u5199\u4e86\u65b0\u7684\u7bc7\u7ae0\u3002</p><p>\u3000\u3000\u6cf8\u6df1\u4e24\u5e02\u6307\u6570\u7ecf\u8fc7\u957f\u8fbe\u4e00\u4e2a\u591a\u6708\u7684\u632f\u8361\u8c03\u6574\u4e4b\u540e\u5728\u65b0\u5343\u5e74\u5143\u65e6\u5047\u65e5\u4f11\u5e02\u524d\u6700\u540e\u4e00\u4e2a\u4ea4\u6613\u65e5\u5927\u6da8\u5df2\u6210\u5b9a\u5c40\uff0c\u5927\u5bb6\u66f4\u5173\u7cfb\u65b0\u79d1\u6280\u6982\u5ff5\u80a1\u4f1a\u4e0d\u4f1a\u51fa\u73b0\u96c6\u4f53\u6da8\u505c\u7684\u76db\u51b5\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fb9\u8bfb\u62a5\u8fb9\u7528\u65e9\u9910\uff0c\u521a\u8fc7\u516b\u70b9\u949f\uff0c\u5085\u4fca\u4e0e\u884c\u653f\u79d8\u4e66\u5c31\u62b1\u7740\u8d44\u6599\u8fc7\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u8fd9\u4e48\u65e9\uff1f\u201d\u5f20\u606a\u62ff\u8d77\u6401\u5728\u684c\u4e0a\u7684\u8155\u8868\u770b\u4e86\u770b\u3002</p><p>\u3000\u3000\u201c\u53f6\u603b\u90a3\u8fb9\u50ac\u5f97\u6025\uff0c\u662f\u4e0d\u662f\u73b0\u5728\u5c31\u53ec\u5f00\u7535\u8bdd\u4f1a\u8bae\uff1f\u201d\u5085\u4fca\u95ee\u9053\u3002</p><p>\u3000\u3000\u53f6\u5c0f\u6850\u4e34\u4ea7\uff0c\u53f6\u8001\u7237\u5b50\u4eb2\u81ea\u51fa\u9762\u5c06\u5979\u6263\u5728\u8001\u5b85\u91cc\u8ba9\u5979\u5b89\u5fc3\u751f\u5b69\u5b50\uff0c\u884c\u653f\u90e8\u95e8\u8fd9\u8fb9\u4e00\u65f6\u7fa4\u9f99\u65e0\u9996\uff0c\u4e0d\u8fc7\u7531\u5085\u4fca\u6682\u65f6\u7ba1\u8d77\u8fd9\u5757\uff0c\u4e5f\u4e0d\u81f3\u4e8e\u51fa\u73b0\u4ec0\u4e48\u6df7\u4e71\u3002</p><p>\u3000\u3000\u201c\u4f60\u5c06\u5fae\u8f6f\u63d0\u4ea4\u7ed9\u6211\u4eec\u7684\u6700\u7ec8\u65b9\u6848\u5148\u62ff\u8fc7\u6765\u7ed9\u6211\u770b\u770b\uff0c\u8ba9\u4ed6\u4eec\u5148\u5f00\u7535\u8bdd\u4f1a\u8bae\u8ba8\u8bba\u5c31\u662f\u3002\u201d\u5f20\u606a\u4ece\u5085\u4fca\u624b\u91cc\u63a5\u8fc7\u6628\u5929\u591c\u91cc\u4ece\u7f8e\u56fd\u4f20\u56de\u6765\u7684\u4f20\u771f\u4ef6\u3002</p><p>\u3000\u3000\u201c\u53f6\u603b\u4ed6\u4eec\u6628\u5929\u591c\u91cc\u5c31\u6ca1\u6709\u7761\u89c9\uff0c\u8fd8\u7b49\u7740\u53ec\u5f00\u5b8c\u7535\u8bdd\u4f1a\u8bae\u4f1a\u8865\u89c9\u5462\uff0c\u201d\u5085\u4fca\u8bf4\u9053\uff0c\u53c8\u62bd\u51fa\u4e00\u4efd\u6587\u4ef6\u9012\u7ed9\u5f20\u606a\uff0c\u201c\u53f6\u603b\u4ed6\u4eec\u8fde\u591c\u8ba8\u8bba\u51fa\u6765\u7684\u7efc\u5408\u610f\u89c1\uff0c\u4e5f\u5728\u8fd9\u91cc\u3002\u201d</p><p>\u3000\u3000\u5fae\u8f6f\u786e\u4fe1\u81ea\u5df1\u5728\u5c01\u95ed\u5e94\u7528\u73af\u5883\u65b9\u9762\u7684\u4ea7\u5c40\u5fc3\u601d\u7ed9\u9526\u6e56\u7aa5\u7834\u4e4b\u540e\uff0c\u7279\u522b\u5728\u6628\u5929\u6606\u817e\u5728\u7ebf\u5728\u7eb3\u65af\u8fbe\u514b\u7684\u80a1\u4ef7\u66b4\u6da8\u4e4b\u540e\uff0c\u5c31\u653e\u5f03\u538b\u4ef7\u7684\u52aa\u529b\u4ee5\u6362\u53d6\u5176\u4ed6\u66f4\u4f18\u60e0\u7684\u6761\u4ef6\u3002</p><p>\u3000\u3000\u5728\u5fae\u8f6f\u63d0\u4ea4\u7684\u6700\u7ec8\u534f\u8bae\u65b9\u6848\u91cc\uff0c\u660e\u786e\u63d0\u51fa\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0eDMP-iplayer\u786c\u4ef6\u76f8\u7ed3\u5408\u7684\u6a21\u5f0f\u5e73\u53f0\u8981\u4e3a\u5176\u63a8\u5e7f\u65b0\u4e00\u4ee3\u7684\u97f3\u9891\u683c\u5f0f\u6807\u51c6\u63d0\u4f9b\u4fbf\u5229\uff0c\u5e76\u8981\u6c42\u9526\u6e56\u6709\u660e\u786e\u8ba1\u5212\u7684\u653e\u5f03\u5e02\u573a\u66b4\u5229\u7b56\u7565\u4ee5\u6269\u5927iplayer\u5728\u4fbf\u643a\u5f0f\u97f3\u4e50\u64ad\u653e\u5668\u5e02\u573a\u6240\u5360\u7684\u4efd\u989d\u3002\u53e6\u5916\uff0c\u5fae\u8f6f\u5728\u83b7\u5f97iplayer\u4ea7\u54c1\u6280\u672f\u6388\u6743\u65b9\u9762\u9700\u4eab\u53d7\u4e0e\u65af\u9ad8\u67cf\u540c\u7b49\u7ea7\u522b\u7684\u5f85\u9047\u3002</p><p>\u3000\u3000\u5fae\u8f6f\u5c06\u4e3a\u4e0a\u8ff0\u9644\u52a0\u6761\u6b3e\u4e0e\u5168\u7403\u97f3\u4e50\u5728\u7ebf30\uff05\u7684\u80a1\u6743\u5411\u9526\u6e56\u652f\u4ed8\u4e5d\u4ebf\u7f8e\u5143\uff0c\u5f53\u7136\uff0c\u5bf9\u5916\u5ba3\u5e03\u65f6\u4e5d\u4ebf\u7f8e\u5143\u53ea\u662f\u80a1\u6743\u5e76\u8d2d\u6b3e\uff0c\u4e0d\u6d89\u53ca\u5176\u4ed6\u3002</p><p>\u3000\u3000\u9526\u6e56\u989d\u5916\u63d0\u51fa\u7684\u5728\u95ea\u5b58\u76d8\u6280\u672f\u63a8\u5e7f\u65b9\u9762\u7684\u5408\u4f5c\u6761\u4ef6\uff0c\u5fae\u8f6f\u8003\u8651\u5230\u81ea\u8eab\u4e5f\u5c06\u53d7\u76ca\uff0c\u90fd\u6089\u6570\u7b54\u5e94\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u8fc7\u5fae\u8f6f\u63d0\u4ea4\u7684\u6700\u7ec8\u65b9\u6848\uff0c\u53c8\u770b\u8fc7\u53f6\u5efa\u658c\u4ed6\u4eec\u8fde\u591c\u8ba8\u8bba\u51fa\u6765\u7684\u610f\u89c1\uff0c\u8ba9\u5085\u4fca\u53d6\u652f\u7b14\u7ed9\u4ed6\uff0c\u5728\u6587\u4ef6\u4e0a\u7b7e\u4e86\u5b57\uff0c\u4ea4\u7ed9\u5085\u4fca\u8bf4\u9053\uff1a\u201c\u5c31\u8fd9\u6837\u597d\u4e86\uff0c\u4ed6\u4eec\u90fd\u6709\u7edf\u4e00\u610f\u89c1\u4e86\uff0c\u90a3\u8fd8\u5f00\u4ec0\u4e48\u4f1a\u554a\uff1f\u4eca\u5929\u5343\u79a7\u591c\uff0c\u5927\u5bb6\u8be5\u5e72\u561b\u5e72\u561b\u53bb\uff0c\u4f60\u4e5f\u56de\u6d77\u5dde\u53bb\uff0c\u56db\u53f7\u4e4b\u524d\u4e0d\u8981\u56de\u6765\uff0c\u6211\u4e0a\u5348\u8ddf\u675c\u98de\u7ea6\u597d\u4e86\u8981\u53bb\u6253\u7403\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u53f6\u5efa\u658c\u8fd8\u5750\u5728\u529e\u516c\u684c\u524d\u60a0\u95f2\u81ea\u5f97\u7684\u559d\u7740\u5496\u5561\uff0c\u89c6\u9891\u4f1a\u8bae\u7684\u754c\u9762\u5df2\u7ecf\u6253\u5f00\uff0c\u4ed6\u5c31\u8ddf\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u4ed6\u4eec\u5728\u89c6\u9891\u754c\u9762\u4e0a\u4e71\u4f83\uff0c\u7b49\u5230\u5f20\u606a\u90a3\u8fb9\u7684\u7ec8\u7aef\u63a5\u901a\uff0c\u4ed6\u8fd8\u5174\u594b\u7684\u5c06\u5496\u5561\u676f\u653e\u4e0b\uff0c\u51c6\u5907\u902e\u4f4f\u5f20\u606a\u62b1\u6028\u4ed6\u4eec\u6628\u5929\u901a\u5bb5\u672a\u7761\u7684\u8f9b\u82e6\uff0c\u6ca1\u60f3\u5230\u662f\u5085\u4fca\u9732\u51fa\u8138\u6765\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u80af\u5b9a\u4e86\u5fae\u8f6f\u63d0\u4ea4\u7684\u6700\u7ec8\u65b9\u6848\uff0c\u6211\u7b49\u4f1a\u513f\u5c06\u7b7e\u5b57\u6587\u4ef6\u7535\u5b50\u6863\u53d1\u7ed9\u4f60\u4eec\uff0c\u5f20\u606a\u5e0c\u671b\u5927\u5bb6\u5343\u79a7\u591c\u5c31\u4e0d\u8981\u592a\u8f9b\u52b3\u5de5\u4f5c\u4e86\uff0c\u8eab\u4f53\u529b\u884c\uff0c\u89c6\u9891\u4f1a\u8bae\u4e5f\u5c31\u4e34\u65f6\u53d6\u6d88\u6389\uff0c\u4e5f\u4e0d\u4f1a\u4e13\u95e8\u7ed9\u5927\u5bb6\u9001\u65b0\u5e74\u795d\u798f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u53f6\u5efa\u658c\u6447\u5934\u82e6\u7b11\uff0c\u54ea\u91cc\u80fd\u50cf\u4ed6\u90a3\u6837\u8f7b\u677e\u7684\u4e22\u4e0b\u624b\u91cc\u7684\u5de5\u4f5c\u554a\uff1f\u65e2\u7136\u6ca1\u6709\u4ec0\u4e48\u5f02\u8bae\uff0c\u4e0e\u5fae\u8f6f\u4e4b\u95f4\u7684\u534f\u8bae\uff0c\u8096\u664b\u6210\u4e0e\u6210\u6c49\u748b\u5728\u7f8e\u56fd\u5c31\u4e0e\u5fae\u8f6f\u6b63\u5f0f\u7b7e\u7f72\u534f\u8bae\uff0c\u7b97\u662f\u65b0\u5343\u5e74\u524d\u591c\u7684\u732e\u793c\u3002</p><p>\u3000\u3000\u8fd8\u6709\u5c31\u662f\u4e0e\u534e\u767b\u7cfb\u98ce\u6295\u516c\u53f8\u4e0e\u9ad8\u76db\u7684\u8c08\u5224\u6761\u4ef6\u8981\u91cd\u65b0\u4fee\u6b63\u3002</p><p>\u3000\u3000\u7eb3\u65af\u8fbe\u514b\u7b49\u6b27\u7f8e\u4e3b\u8981\u65b0\u79d1\u6280\u6307\u6570\u957f\u671f\u4e0a\u626c\uff0c\u76f8\u6bd4\u8f83\u524d\u4e9b\u5e74\u7684\u4f4e\u70b9\uff0c\u5dee\u4e0d\u591a\u90fd\u6709\u4e24\u5230\u4e09\u500d\u7684\u589e\u5e45\u3002\u6b27\u7f8e\u6295\u8d44\u5546\u8fd8\u6c89\u6d78\u5728\u4e92\u8054\u7f51\u7ecf\u6d4e\u5c06\u957f\u671f\u7e41\u8363\u7684\u7f8e\u68a6\u4e2d\uff0c\u867d\u7136\u90fd\u77e5\u9053\u5b58\u5728\u6ce1\u6cab\uff0c\u4f46\u662f\u5168\u7403\u4e92\u8054\u7f51\u4ea7\u4e1a\u7684\u9ad8\u901f\u589e\u6da8\u8ba9\u4ed6\u4eec\u76f8\u4fe1\u6b64\u65f6\u7684\u6ce1\u6cab\u4f1a\u5f88\u5feb\u7ed9\u5b9e\u4f53\u4ea7\u4e1a\u81ea\u7136\u6d88\u5316\u6389\u3002</p><p>\u3000\u3000\u62ff\u4e2d\u56fd\u6765\u8bf4\uff0c\u4e92\u8054\u7f51\u7528\u6237\u603b\u6570\u4e5d\u4e5d\u5e74\u589e\u6da8\u8d85\u8fc7150\uff05\uff0c\u4ece\u4e5d\u516b\u5e74\u5e95\u7684\u516b\u767e\u4e07\u4eba\u5feb\u901f\u589e\u6da8\u5230\u4e5d\u4e5d\u5e74\u5e95\u7684\u4e24\u5343\u4e07\u4eba\uff0c\u800c\u4e14\u5e02\u573a\u53d1\u5c55\u6f5c\u529b\u5de8\u5927\uff0c\u5fae\u8f6f\u7b49\u65b0\u79d1\u6280\u9886\u519b\u4f01\u4e1a\u4e5d\u4e5d\u5e74\u7684\u9884\u8ba1\u4e1a\u7ee9\u4e5f\u660e\u663e\u4f18\u4e8e\u5f80\u5e74\uff0c\u7ed9\u6295\u8d44\u5546\u6781\u5f3a\u7684\u4fe1\u5fc3\u3002</p><p>\u3000\u3000\u4f5c\u4e3a\u4e2d\u6587\u7b2c\u4e00\u95e8\u6237\u7f51\uff0c\u53c8\u662f\u5f53\u524d\u552f\u4e00\u4e00\u5bb6\u5728\u7eb3\u65af\u8fbe\u514b\u4e0a\u5e02\u7684\u4e2d\u6587\u95e8\u6237\u7f51\u7ad9\uff0c\u65e9\u671f\u53c2\u4e0e\u6606\u817e\u5728\u7ebf\u98ce\u9669\u6295\u8d44\u7684\u534e\u767b\u7cfb\u57fa\u91d1\u516c\u53f8\u8fd8\u76f8\u4fe1\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u6709\u7ee7\u7eed\u4e0a\u6da8\u7684\u6f5c\u529b\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u5c06\u540c\u65f6\u5174\u5efa\u4e24\u5ea7\u6676\u5706\u5382\u7684\u6d88\u606f\uff0c\u4e1a\u5185\u5df2\u7ecf\u6cb8\u6cb8\u626c\u626c\uff0c\u4ed6\u4eec\u4e5f\u6e05\u695a\u9526\u6e56\u6709\u5957\u73b0\u7684\u8feb\u5207\u9700\u6c42\u3002\u65e9\u524d\u6606\u817e\u5728\u7ebf\u516c\u4f17\u80a1\u624d750\u4e07\u80a1\uff0c\u4e00\u65e6ESS\u6240\u63011700\u4e07\u80a1\u8fc7\u4e86\u7981\u552e\u671f\u5c06\u5728\u77ed\u671f\u5185\u5feb\u901f\u51cf\u6301\uff0c\u52bf\u5fc5\u4f1a\u5f7b\u5e95\u6253\u4e71\u4ed6\u4eec\u539f\u6709\u7684\u8ba1\u5212\uff0c\u4ed6\u4eec\u73b0\u5728\u8981\u60f3\u5316\u89e3ESS\u51cf\u6301\u8ba1\u5212\u5e26\u6765\u7684\u538b\u529b\uff0c\u5c31\u8981\u8054\u5408\u5176\u4ed6\u6295\u8d44\u673a\u6784\u5c06ESS\u6240\u6301\u80a1\u7968\u5148\u6089\u6570\u627f\u63a5\u4e0b\u6765\u3002</p><p>\u3000\u3000\u8fd9\u6837\u7684\u8c08\u5224\u65e9\u5c31\u542f\u52a8\u4e86\uff0c\u9526\u6e56\u5f53\u7136\u4e0d\u53ef\u80fd\u5962\u671b\u8fd9\u4e9b\u6295\u8d44\u516c\u53f8\u4f1a\u4ee5\u5e02\u4ef7\u63a5\u76d8\uff0c\u4ef7\u683c\u8c08\u5224\u624d\u662f\u6700\u5173\u952e\u7684\u56e0\u7d20\u3002\u6628\u591c\u4e4b\u524d\uff0c\u80a1\u4ef7\u8fd8\u53ea\u6709\u6bcf\u80a121\u7f8e\u5143\uff0c\u4e00\u591c\u521a\u8fc7\uff0c\u5c31\u66b4\u589e\u5230\u6bcf\u80a148\u7f8e\u5143\uff0c\u4e4b\u524d\u7684\u8c08\u5224\u6210\u679c\uff0c\u8fd9\u65f6\u5019\u5dee\u4e0d\u591a\u90fd\u8981\u4f5c\u5e9f\u6389\u3002\u73b0\u5728\u4eba\u5bb6\u8fd8\u4f1a\u4e0d\u4f1a\u7ee7\u7eed\u8c08\u5224\u90fd\u6210\u95ee\u9898\uff0c\u6bd5\u7adf\u4fee\u6b63\u4e4b\u540e\u7684\u4ef7\u683c\u4e5f\u5c06\u5927\u5927\u8d85\u8fc7\u8fd9\u4e9b\u6295\u8d44\u516c\u53f8\u7684\u4e4b\u524d\u9884\u6d4b\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u66f4\u591a\u7684\u98ce\u9669\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u65b0\u5343\u5e74\u5143\u65e6\u8fc7\u540e\uff0cESS\u6240\u6301\u6606\u817e\u5728\u7ebf\u7684\u80a1\u7968\u5c31\u8fc7\u4e86\u7981\u552e\u671f\uff0c\u6839\u636e\u4e4b\u524d\u7684\u8ba1\u5212\uff0c\u8981\u662f\u6ca1\u6709\u5927\u6295\u8d44\u5546\u63a5\u76d8\uff0c\u5c31\u8981\u5728\u516c\u5f00\u5e02\u573a\u5f00\u59cb\u51cf\u6301\u8ba1\u5212\u2014\u2014\u4e2d\u7f8e\u53cc\u8fb9\u534f\u8bae\u80fd\u5728\u5982\u6b64\u5173\u952e\u65f6\u671f\u8fbe\u6210\u5e76\u523a\u6fc0\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u66b4\u589e\uff0c\u65e0\u7591\u662f\u7ed9\u9526\u6e56\u767d\u9001\u4e86\u56db\u4ebf\u591a\u7f8e\u5143\u7684\u73b0\u91d1\uff0c\u8fd9\u8981\u7b97\u4e00\u7b14\u98de\u6765\u6a2a\u8d22\uff0c\u8fd8\u5f7b\u5e95\u7c89\u788e\u6389\u5fae\u8f6f\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u80a1\u6743\u538b\u4ef7\u7684\u7528\u529b\u3002</p><p>\u3000\u3000\u9526\u6e56\u9ad8\u5c42\u4e00\u76f4\u4e3a\u8d44\u91d1\u7684\u7f3a\u4e4f\u7ea0\u7ed3\uff0c\u8fd9\u6b21\u82e5\u662f\u80fd\u987a\u5229\u51cf\u6301\u6606\u817e\u5728\u7ebf\u80a1\u7968\uff0c\u518d\u52a0\u5fae\u8f6f\u652f\u4ed8\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u80a1\u6743\u5e76\u8d2d\u6b3e\uff0c\u9526\u6e56\u5dee\u4e0d\u591a\u624b\u91cc\u5c06\u4e00\u4e0b\u5b50\u589e\u52a0\u5341\u516d\u4e03\u4ebf\u7f8e\u5143\u7684\u73b0\u91d1\u2014\u2014\u5c31\u7b97\u4e2d\u6676\u5fae\u82af\u7247\u65b0\u5efa\u4e24\u5ea7\u6676\u5706\u5382\uff0c\u9526\u6e56\u4e5f\u80fd\u627f\u62c5\u8d77\u81ea\u5df1\u7684\u90a3\u4efd\u51fa\u8d44\u4e49\u52a1\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u3001\u9648\u4fe1\u751f\u3001\u82cf\u6d25\u4e1c\u7b49\u4eba\u5174\u594b\u96be\u7720\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\u7f18\u7531\u7684\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6606\u817e\u5728\u7ebf\u80a1\u4ef7\u4e00\u591c\u66b4\u6da8\uff0c\u4e25\u6587\u4ecb\u5fc3\u91cc\u662f\u53c8\u5fcc\u53c8\u7fa1\u3002\u8c01\u80fd\u60f3\u5230\u51e0\u4e2a\u6e05\u534e\u5b66\u751f\u521b\u529e\u7684\u7f51\u7ad9\u4eca\u65e5\u5e02\u503c\u8d85\u8fc7\u4e8c\u5341\u4e03\u4ebf\u7f8e\u5143\uff0c\u4e0d\u8981\u8bf4\u6606\u817e\u5728\u7ebf\u9690\u85cf\u5728\u80cc\u540e\u7684\u6295\u8d44\u4eba\uff0c\u90a3\u51e0\u4e2a\u521b\u59cb\u4eba\u4e5f\u8eab\u5bb6\u66b4\u589e\uff0c\u9a6c\u5411\u4e1c\u4ee5190\u4e07\u80a1\u3001\u8eab\u5bb6\u8d85\u4e5d\u5343\u4e07\u7f8e\u5143\u6210\u4e3a\u4eca\u65e5\u5a92\u4f53\u8ffd\u9010\u7684\u70ed\u70b9\u3002</p><p>\u3000\u3000\u4e00\u6b21\u6b21\u7684\u4e8b\u5b9e\uff0c\u90fd\u63d0\u9192\u4e25\u6587\u4ecb\u4e4b\u524d\u7684\u8ba1\u5212\u592a\u4fdd\u5b88\u4e86\u3002</p><p>\u3000\u3000\u5728ESS\u516c\u5e03\u51cf\u6301\u8ba1\u5212\u4e4b\u540e\uff0c\u6606\u817e\u5728\u7ebf\u7684\u5e02\u503c\u8fd8\u662f\u8d85\u8fc7\u4e8c\u5341\u4e03\u4ebf\u7f8e\u5143\uff0c\u4e9a\u9a6c\u900a\u5e02\u503c\u8d85\u8fc7\u4e24\u767e\u516d\u5341\u4ebf\u7f8e\u5143\uff0c\u6d77\u7c9f\u79d1\u6280\u540d\u4e0b\u62e5\u6709\u7535\u5b50\u5546\u52a1\u3001\u7f51\u5427\u4ea7\u4e1a\u3001\u8f6f\u4ef6\u4ea7\u4e1a\u3001\u6676\u5706\u5236\u9020\u7b49\u9898\u6750\uff0c\u5373\u4f7f\u5728\u56fd\u5185\u7684\u80a1\u5e02\uff0c\u4e5f\u6ca1\u6709\u7406\u7531\u5e02\u503c\u6bd4\u4e0d\u4e0a\u81f3\u4eca\u53ea\u4f1a\u70e7\u94b1\u7684\u6606\u817e\u5728\u7ebf\u3002</p><p>\u3000\u3000\u5c3d\u7ba1\u6d77\u7c9f\u79d1\u6280\u6b64\u65f6\u7684\u5e02\u503c\u5df2\u7ecf\u589e\u6da8\u523058\u4ebf\uff0c\u8fd9\u5dee\u4e0d\u591a\u662f\u4e25\u6587\u4ecb\u5236\u8ba2\u8ba1\u5212\u4e4b\u521d\u8ba2\u7acb\u7684\u5e02\u503c\u76ee\u6807\uff0c\u5982\u4eca\u770b\u6765\uff0c\u8fd8\u662f\u592a\u4fdd\u5b88\u4e86\uff0c\u5e02\u503c\u8d85\u8fc7\u4e24\u767e\u4ebf\u4e0d\u80fd\u7b97\u662f\u4ec0\u4e48\u4e0d\u5207\u5b9e\u9645\u7684\u5e7b\u60f3\uff0c\u6606\u817e\u5728\u7ebf\u5c31\u662f\u6d3b\u751f\u751f\u7684\u4f8b\u5b50\u3002\u4e00\u65e6\u5e02\u503c\u80fd\u8fbe\u5230\u4e24\u767e\u4ebf\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u5230\u660e\u5e74\u4e94\u6708\u4efd\u4e4b\u540e\uff0c\u4ed6\u4eec\u53ef\u4ee5\u901a\u8fc7\u516c\u5f00\u589e\u53d1\u65b0\u80a1\u7684\u5f62\u5f0f\u4e00\u6b21\u6027\u878d\u5f97\u8d85\u8fc7\u516d\u5341\u4ebf\u7684\u8d44\u91d1\u3002</p><p>\u3000\u3000\u80fd\u6709\u8fd9\u516d\u5341\u4ebf\u7684\u8d44\u91d1\uff0c\u5c31\u80fd\u6781\u5927\u7684\u7f13\u89e3\u5b8f\u4fe1\u7cfb\u6210\u5458\u4f01\u4e1a\u6b64\u65f6\u9762\u4e34\u7684\u8d44\u91d1\u9965\u6e34\u95ee\u9898\u3002</p><p>\u3000\u3000\u4e25\u6587\u4ecb\u4e0d\u60f3\u7ed9\u5176\u4ed6\u6563\u6237\u6216\u6295\u8d44\u673a\u6784\u4ecb\u5165\u6d77\u7c9f\u79d1\u6280\u7684\u673a\u4f1a\uff0c\u6caa\u6df1\u4e24\u5e02\u4e00\u5f00\u76d8\uff0c\u4fe1\u901a\u8bc1\u5238\u65d7\u4e0b\u7684\u8d44\u91d1\u5c31\u7acb\u5373\u5c06\u6d77\u7c9f\u79d1\u6280\u7684\u80a1\u4ef7\u6258\u9ad8\u5230\u6da8\u505c\u4f4d\uff1b\u53d7\u4e2d\u7f8e\u7b7e\u7f72\u53cc\u8fb9\u534f\u8bae\u5229\u597d\u6d88\u606f\u7684\u5f71\u54cd\uff0c\u6caa\u6df1\u4e24\u5e02\u4e00\u7247\u8840\u6620\u6620\u7684\u7ea2\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8bb8\u601d\u4e2d\u5348\u5750\u8f66\u5230\u5efa\u90ba\u627e\u7b97\u966a\u5f20\u606a\u4e00\u8d77\u6e21\u8fc7\u7edd\u5927\u591a\u6570\u4eba\u90fd\u4ee5\u4e3a\u610f\u4e49\u975e\u51e1\u7684\u5343\u79a7\u591c\u3002\u5f20\u606a\u5c31\u5f53\u662f\u81ea\u5df1\u7684\u5047\u671f\uff0c\u5728\u8bb8\u601d\u5230\u5efa\u90ba\uff0c\u4e0e\u5979\u5230\u65b0\u5e02\u8857\u5403\u996d\u3001\u8d2d\u7269\uff0c\u5927\u8857\u5c0f\u5df7\u5728\u5404\u5927\u5546\u5bb6\u7684\u52aa\u529b\u4e0b\u4e5f\u5145\u6ee1\u4e86\u8282\u65e5\u6c14\u6c1b\uff0c\u5728\u4e1c\u65b9\u56fd\u9645\u5e7f\u573a\u6311\u9009\u56f4\u5dfe\u65f6\uff0c\u5f20\u606a\u63a5\u5230\u5510\u5a67\u6253\u6765\u7684\u7535\u8bdd\uff1a</p><p>\u3000\u3000\u201c\u771f\u662f\u8981\u611f\u8c22\u4e0a\u5e1d\u4e86\uff0c\u6cd5\u8bed\u8bfe\u7684\u8bb2\u5e08\u6628\u5929\u591c\u91cc\u60a3\u4e86\u4e25\u91cd\u98ce\u5bd2\uff0c\u6211\u73b0\u5728\u5c31\u5f80\u673a\u573a\u8d76\uff0c\u518d\u8fc7\u4e24\u4e2a\u534a\u5c0f\u65f6\u5c31\u80fd\u770b\u5230\u4f60\u4e86\u2026\u2026\u201d</p></article>"}}}]);
