(window.webpackJsonp=window.webpackJsonp||[]).push([[1052],{1075:function(p){p.exports={chapterTitle:null,title:"\u7b2c1049\u7ae0 \u8d70\u5149",content:"<article><h2>\u7b2c1049\u7ae0 \u8d70\u5149</h2><p>\u3000\u3000\u5218\u9e23\u950b\u6ed4\u6ed4\u4e0d\u7edd\u7684\u8c08\u4e86\u6709\u4e00\u4e2a\u534a\u5c0f\u65f6\uff0c\u8fd9\u4e9b\u53ea\u662f\u4ed6\u5e73\u65e5\u91cc\u4e00\u4e9b\u51cc\u4e71\u7684\u601d\u8003\uff0c\u6df7\u4e71\u3001\u6ca1\u6709\u592a\u591a\u7684\u6761\u7406\uff0c\u4e0d\u6e05\u695a\u773c\u524d\u8fd9\u4e9b\u5e73\u65f6\u53ea\u80fd\u542c\u8bf4\u6216\u8005\u53ea\u80fd\u4ece\u7535\u89c6\u91cc\u770b\u5230\u4eba\u7269\u5bf9\u4ed6\u4f1a\u6709\u4ec0\u4e48\u8bc4\u4ef7\u3002</p><p>\u3000\u3000\u9648\u71d5\u5750\u5728\u90a3\u91cc\uff0c\u624b\u5fc3\u91cc\u90fd\u66ff\u5218\u9e23\u950b\u634f\u4e86\u4e00\u628a\u6c57\u3002</p><p>\u3000\u3000\u867d\u7136\u4e5d\u4e5d\u5e74\u56fd\u5185\u7535\u5b50\u767e\u5f3a\u699c\uff0c\u7231\u8fbe\u96c6\u56e2\u624d\u5217\u7b2c\u56db\u4f4d\uff0c\u4f46\u662f\u5728\u5979\u4ee5\u53ca\u5728\u5979\u5efa\u90ae\u540c\u5b66\u7684\u773c\u4e2d\uff0c\u7231\u8fbe\u7684\u5730\u4f4d\u8981\u8fdc\u9ad8\u8fc7\u7535\u5b50\u767e\u5f3a\u699c\u6392\u540d\u7b2c\u4e00\u7684\u534e\u590f\u7535\u5b50\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e4b\u524d\u56fd\u5185\u5916\u7535\u5b50\u4f01\u4e1a\u5728\u5efa\u90ba\u8bbe\u7acb\u7684\u5546\u4e1a\u6027\u7814\u7a76\u673a\u6784\u4e0e\u5de5\u5382\u4e4b\u5916\uff0c\u5c31\u5728\u524d\u4e24\u5929\uff0c\u4e09\u661f\u521a\u521a\u5ba3\u5e03\u4e86\u5c06\u6269\u5927\u5efa\u90ba\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u4e2d\u5fc3\u7684\u89c4\u6a21\u3002</p><p>\u3000\u3000\u9664\u4e86\u4ece\u97e9\u56fd\u56fd\u5185\u5927\u91cf\u5916\u8c03\u6d3e\u7814\u7a76\u5458\u652f\u6301\u4e4b\u5916\uff0c\u8fd8\u516c\u5e03\u4e8c\u767e\u4eba\u4ee5\u4e0a\u7684\u5f00\u53d1\u5de5\u7a0b\u5e08\u62db\u8058\u8ba1\u5212\u3002\u8fd9\u662f\u4e09\u661f\u4e5d\u4e03\u5e74\u5728\u5efa\u90ba\u6210\u7acb\u4ea7\u54c1\u5f00\u53d1\u4e0e\u8bbe\u8ba1\u4e2d\u5fc3\u4ee5\u6765\u3001\u4e09\u661f\u7814\u53d1\u90e8\u95e8\u5728\u4e2d\u56fd\u505a\u51fa\u7684\u6700\u5927\u52a8\u4f5c\u3002\u5728\u6b64\u4e4b\u524d\uff0c\u4e09\u661f\u7535\u5b50\u53ea\u662f\u8ba1\u5212\u5c06\u4e2d\u56fd\u4f5c\u4e3a\u5168\u7403\u5236\u9020\u4e2d\u5fc3\uff0c\u800c\u5c3d\u53ef\u80fd\u7684\u5c06\u4ea7\u54c1\u7814\u53d1\u4e0e\u5f00\u53d1\u90e8\u95e8\u4fdd\u7559\u5728\u97e9\u56fd\u672c\u571f\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e09\u661f\u4e4b\u5916\uff0c\u6469\u6258\u7f57\u62c9\u4e5f\u5728\u516b\u6708\u4e0a\u65ec\u51b3\u5b9a\u5728\u5efa\u90ba\u6210\u7acb\u624b\u673a\u5e94\u7528\u8f6f\u4ef6\u7814\u53d1\u4e2d\u5fc3\u53ca\u7528\u6237\u4f53\u9a8c\u8bbe\u8ba1\u4e2d\u5fc3\uff0c\u5e76\u8ba1\u5212\u5c06\u4e2a\u4eba\u901a\u8baf\u4e8b\u4e1a\u90e8\u7684\u7814\u53d1\u673a\u6784\u90fd\u8fc1\u5f80\u5efa\u90ba\uff0c\u6210\u4e3a\u6469\u6258\u7f57\u62c9\u4e2d\u56fd\u7814\u7a76\u9662\u6700\u91cd\u8981\u7684\u4e00\u4e2a\u5206\u652f\u673a\u6784\u3002</p><p>\u3000\u3000\u4e09\u661f\u4e3a\u4ec0\u4e48\u505a\u8fd9\u6837\u7684\u6218\u7565\u8c03\u6574\uff0c\u6469\u6258\u7f57\u62c9\u4e3a\u4ec0\u4e48\u91cd\u89c6\u8d77\u5efa\u90ba\u7684\u7814\u7a76\u8d44\u6e90\uff1f\u5efa\u90ae\u7684\u8001\u5e08\u3001\u5b66\u751f\u90fd\u4f20\u5f97\u795e\u4e4e\u5176\u795e\uff0c\u65e0\u975e\u662f\u8feb\u4e8e\u7231\u8fbe\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u5e02\u573a\u7ed9\u4ed6\u4eec\u7684\u5f3a\u5927\u538b\u529b\u2014\u2014\u5efa\u90ae\u5dee\u4e0d\u591a\u6709\u4e09\u5206\u4e4b\u4e00\u7684\u7814\u7a76\u751f\u90fd\u53c2\u4e0e\u53cc\u5bfc\u5e08\u5236\u8ba1\u5212\u76f4\u63a5\u8fdb\u5165\u6a61\u6811\u56ed\u5b9e\u4e60\uff0c\u4e0e\u9664\u6b64\u4e4b\u5916\uff0c\u4e0e\u6a61\u6811\u56ed\u8fd8\u6709\u5f88\u591a\u5408\u4f5c\u8bfe\u9898\uff0c\u8fd9\u79cd\u5c0f\u9053\u6d88\u606f\u6765\u6e90\u6e20\u9053\u4f17\u591a\uff0c\u5728\u5b66\u6821\u91cc\uff0c\u7ed9\u5979\u7709\u76ee\u4f20\u60c5\u7684\u5e08\u5144\u4e5f\u591a\uff0c\u9648\u71d5\u5012\u662f\u77e5\u9053\u5f88\u591a\u7231\u8fbe\u7684\u60c5\u51b5\uff0c\u5979\u7684\u540c\u5b66\u5f53\u4e2d\u4e5f\u591a\u6570\u4ee5\u8003\u7814\u6216\u4ee5\u8fdb\u5165\u6a61\u6811\u56ed\u5de5\u4f5c\u4e3a\u594b\u6597\u76ee\u6807\u3002</p><p>\u3000\u3000\u9648\u71d5\u5012\u6ca1\u6709\u60f3\u8fc7\u6bd5\u4e1a\u4e86\u8981\u8fdb\u6a61\u6811\u56ed\u5de5\u4f5c\uff0c\u591c\u91cc\u5728\u8def\u4e0a\u770b\u6a61\u6811\u56ed\uff0c\u751a\u81f3\u5230\u4e5d\u5341\u70b9\u949f\u6a61\u6811\u56ed\u7684\u5b9e\u9a8c\u697c\u90fd\u8fd8\u706f\u706b\u901a\u660e\uff0c\u8fd9\u79cd\u5de5\u4f5c\u538b\u529b\u4e5f\u4e0d\u662f\u6240\u6709\u4eba\u90fd\u80fd\u627f\u53d7\u6216\u8005\u8bf4\u90fd\u613f\u610f\u627f\u53d7\u7684\u2014\u2014\u9648\u71d5\u751a\u81f3\u89c9\u5f97\u6709\u4e9b\u4e0d\u4eba\u9053\uff0c\u8fd9\u5176\u4e2d\u4e5f\u542c\u4e86\u4e9b\u5938\u5927\u5176\u8f9e\u7684\u4f20\u8a00\u3002</p><p>\u3000\u3000\u5979\u7684\u8fd9\u79cd\u60f3\u6cd5\u7ed9\u5218\u9e23\u950b\u72e0\u72e0\u7684\u5632\u7b11\u8fc7\uff0c\u8bf4\u5979\u662f\u5b98\u5ba6\u5c0f\u59d0\u4e0d\u77e5\u5e73\u6c11\u767e\u59d3\u7684\u75be\u82e6\uff0c\u5de5\u5546\u3001\u7a0e\u52a1\u3001\u90ae\u7535\u3001\u70df\u8349\u6216\u8005\u76f4\u63a5\u8fdb\u5165\u653f\u5e9c\u90e8\u95e8\u8fd9\u4e9b\u4e0d\u52aa\u529b\u5c31\u80fd\u6d3b\u5f97\u8f7b\u677e\u6ecb\u6da6\u7684\u5de5\u4f5c\u4e0d\u662f\u5e73\u6c11\u767e\u59d3\u80fd\u591f\u5962\u671b\u7684\uff0c\u5bf9\u4e8e\u5e73\u6c11\u767e\u59d3\u6765\u8bf4\uff0c\u8981\u662f\u80fd\u901a\u8fc7\u52aa\u529b\u7684\u5de5\u4f5c\u5c31\u80fd\u8fc7\u4e0a\u4f53\u9762\u7684\u751f\u6d3b\u5df2\u7ecf\u975e\u5e38\u5927\u7684\u5e78\u798f\u4e86\u3002</p><p>\u3000\u3000\u5728\u6a61\u6811\u56ed\uff0c\u90a3\u4e9b\u6709\u624d\u534e\u80af\u594b\u6597\u7684\u9752\u5e74\u81f3\u5c11\u80fd\u83b7\u5f97\u4f53\u73b0\u4e2a\u4eba\u4ef7\u503c\u7684\u673a\u4f1a\uff0c\u5f53\u7136\u4e5f\u6709\u627f\u53d7\u4e0d\u4e86\u538b\u529b\u7684\u4eba\u6700\u7ec8\u9009\u62e9\u79bb\u5f00\uff0c\u4e5f\u6709\u4e9b\u4eba\u4e00\u5f00\u59cb\u9009\u62e9\u8fdb\u5165\u6a61\u6811\u56ed\u594b\u6597\u51e0\u5e74\u5c31\u662f\u6253\u7b97\u5728\u6a61\u6811\u56ed\u79ef\u7d2f\u4e00\u4e9b\u8d44\u5386\u518d\u6362\u4e00\u4efd\u8f7b\u677e\u7684\u5de5\u4f5c\u2014\u2014\u56db\u4e94\u6708\u65f6\uff0c\u6709\u4e00\u6279\u4eba\u8f9e\u804c\u79bb\u5f00\u6a61\u6811\u56ed\uff0c\u7ed9\u8054\u60f3\u3001\u6d77\u5c14\u3001\u6ce2\u5bfc\u8fd9\u4e9b\u5927\u4f01\u4e1a\u62db\u63fd\u8fc7\u53bb\u65f6\u6708\u85aa\u4f4e\u4e8e\u4e00\u4e07\u90fd\u4e0d\u597d\u610f\u601d\u8ddf\u522b\u4eba\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u597d\u4e9b\u4eba\u5fc3\u91cc\u90fd\u6e05\u695a\uff0c\u56db\u4e94\u6708\u9009\u62e9\u79bb\u5f00\u6a61\u6811\u56ed\u7684\u90a3\u4e00\u6279\u7814\u7a76\u5458\u5dee\u4e0d\u591a\u90fd\u662f\u4e8c\u4e09\u6d41\u6c34\u5e73\u3002\u5728\u6a61\u6811\u56ed\uff0c\u771f\u6b63\u4f18\u79c0\u7684\u3001\u5de5\u4f5c\u5f97\u5230\u8ba4\u53ef\u7684\u7814\u7a76\u5458\u51e0\u4e4e\u90fd\u6709\u671f\u6743\uff0c\u771f\u6b63\u613f\u610f\u653e\u5f03\u671f\u6743\u9009\u62e9\u79bb\u5f00\u6a61\u6811\u56ed\u7684\u7814\u7a76\u5458\u6781\u5c11\uff1b\u4e5f\u4e0d\u662f\u6ca1\u6709\uff0c\u6709\u4e9b\u7814\u7a76\u5458\u5c31\u653e\u5f03\u671f\u6743\u9009\u62e9\u8d70\u4e0a\u81ea\u4e3b\u521b\u4e1a\u8fd9\u6761\u8def\u3002</p><p>\u3000\u3000\u6a61\u6811\u56ed\u5185\u90e8\u5bf9\u5458\u5de5\u521b\u4e1a\u8fd9\u79cd\u73b0\u8c61\u4e5f\u4e0d\u53cd\u5bf9\uff0c\u751a\u81f3\u540c\u610f\u7ed9\u8fd9\u4e9b\u8d70\u51fa\u53bb\u521b\u4e1a\u7684\u7814\u7a76\u4eba\u5458\u6216\u7ba1\u7406\u4eba\u5458\u4fdd\u7559\u804c\u4f4d\u8ba9\u4ed6\u4eec\u5c11\u4e9b\u540e\u987e\u4e4b\u5fe7\u3002</p><p>\u3000\u3000\u9648\u71d5\u5f88\u6e05\u695a\u5218\u9e23\u950b\u8fd8\u662f\u5f88\u91cd\u89c6\u5230\u6a61\u6811\u56ed\u7684\u5de5\u4f5c\u673a\u4f1a\uff0c\u4eca\u665a\u5bf9\u4ed6\u6765\u8bf4\uff0c\u65e0\u7591\u662f\u5929\u5927\u7684\u673a\u9047\uff0c\u6709\u591a\u5c11\u6e34\u671b\u8fdb\u5165\u6a61\u6811\u56ed\u3001\u8fdb\u5165\u7231\u8fbe\u96c6\u56e2\u5de5\u4f5c\u7684\u5b66\u751f\u80fd\u6709\u673a\u4f1a\u5728\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u3001\u6a61\u6811\u56ed\u9996\u5e2d\u79d1\u5b66\u5bb6\u3001\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u603b\u88c1\u8fd9\u4e9b\u5927\u4eba\u7269\u9762\u524d\u6709\u534a\u5c0f\u65f6\u7545\u8ff0\u89c1\u89e3\u3001\u8868\u73b0\u5b66\u8bc6\u7684\u673a\u4f1a\uff1f</p><p>\u3000\u3000\u9648\u71d5\u4e0e\u5218\u9e23\u950b\u5750\u5728\u4f11\u606f\u533a\u4e2d\u592e\u7684\u6c99\u53d1\u4e0a\uff0c\u770b\u7740\u7231\u8fbe\u3001\u6a61\u6811\u56ed\u7684\u9ad8\u5c42\u5728\u4e00\u8fb9\u7a83\u7a83\u79c1\u8bed\uff0c\u5979\u4eec\u53c8\u542c\u4e0d\u771f\u5207\uff0c\u770b\u7740\u5927\u5b66\u751f\u8054\u5408\u521b\u4e1a\u534f\u4f1a\u7684\u4e3b\u5e2d\u3001\u51e0\u4e4e\u88ab\u516c\u8ba4\u4e3a\u662f\u5efa\u90ba\u5927\u5b66\u751f\u521b\u4e1a\u7684\u9886\u8896\u4eba\u7269\u7684\u675c\u98de\u4e5f\u6ee1\u8138\u563b\u7b11\u7684\u5934\u51d1\u5728\u90a3\u91cc\uff0c\u4e0d\u6e05\u695a\u675c\u98de\u8ddf\u7231\u8fbe\u96c6\u56e2\u7a76\u7adf\u662f\u4ec0\u4e48\u5173\u7cfb\uff0c\u8fd8\u6709\u90a3\u4e2a\u6f02\u4eae\u5f97\u8ba9\u4eba\u770b\u5f97\u7259\u75bc\u7684\u5973\u5b69\u5b50\u53c8\u662f\u8c01\uff1f\u90a3\u4e2a\u53eb\u5f20\u606a\u7684\u4eba\u53c8\u662f\u8c01\uff1f\u4ed6\u5750\u5728\u90a3\u91cc\uff0c\u7231\u8fbe\u96c6\u56e2\u603b\u88c1\u3001\u6a61\u6811\u56ed\u9996\u5e2d\u79d1\u5b66\u5bb6\u3001\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u603b\u88c1\u5374\u8981\u4fa7\u8fc7\u8eab\u5b50\u8ddf\u4ed6\u8bf4\u8bdd\uff0c\u9648\u71d5\u5979\u7238\u7238\u662f\u4e2a\u5c0f\u5b98\u50da\uff0c\u5979\u80fd\u770b\u51fa\u5f85\u4eba\u63a5\u7269\u7ec6\u8282\u91cc\u7684\u533a\u522b\u6765\u3002</p><p>\u3000\u3000\u201c\u54b3\u2026\u2026\u201d\u9648\u4fe1\u751f\u6e05\u4e86\u4e00\u4e0b\u55d3\u5b50\u3002</p><p>\u3000\u3000\u5218\u9e23\u950b\u773c\u76ae\u5b50\u4e00\u8df3\uff0c\u8c01\u7ad9\u51fa\u6765\u4ee3\u8868\u7231\u8fbe\u96c6\u56e2\u8ddf\u4ed6\u8c08\u8bdd\uff0c\u5dee\u4e0d\u591a\u4ee3\u8868\u4e86\u7231\u8fbe\u96c6\u56e2\u5bf9\u4ed6\u7684\u8ba4\u540c\u7a0b\u5ea6\u3002</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u9648\u4fe1\u751f\u5de6\u624b\u8fd8\u62ff\u7740\u6cb9\u7b14\u5728\u968f\u8eab\u5e26\u7684\u672c\u5b50\u4e0a\u8bb0\u7740\u4ec0\u4e48\uff0c\u4fc4\u800c\u653e\u4e0b\u7b14\uff0c\u7ad9\u8d77\u6765\u4f38\u51fa\u624b\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u5f88\u9ad8\u5174\u7531\u6211\u6765\u5411\u4f60\u53d1\u51fa\u52a0\u5165\u8fd9\u4e2a\u5927\u56e2\u961f\u7684\u9080\u8bf7\uff0c\u5e0c\u671b\u4f60\u4e0d\u8981\u62d2\u7edd\u3002\u201d</p><p>\u3000\u3000\u4eca\u5929\u665a\u4e0a\u7684\u9645\u9047\u8ba9\u5218\u9e23\u950b\u611f\u5230\u6709\u4e9b\u4e0d\u53ef\u601d\u8bae\uff0c\u521d\u9762\u7ed9\u5237\u4e0b\u6765\uff0c\u4ed6\u90fd\u6709\u4e9b\u5fc3\u7070\u610f\u51b7\u4e86\uff0c\u54ea\u91cc\u60f3\u5230\u4f1a\u8fd9\u79cd\u673a\u9047\u7b49\u7740\u4ed6\uff1f\u4ed6\u770b\u4e86\u770b\u9648\u71d5\uff0c\u770b\u7740\u5979\u660e\u6f88\u6e05\u4eae\u7684\u773c\u775b\uff0c\u624d\u8d70\u4e0a\u524d\u8ddf\u9648\u4fe1\u751f\u63e1\u4e86\u63e1\u624b\uff0c\u8fd8\u4e0d\u5fd8\u6253\u8da3\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u5f97\u9996\u5148\u786e\u8ba4\u8fd9\u4e0d\u662f\u5728\u505a\u68a6\u3002\u201d</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u7b11\u4e86\u8d77\u6765\uff0c\u9648\u4fe1\u751f\u8ba9\u5218\u9e23\u950b\u90fd\u5750\u4e0b\u6765\u8bf4\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u867d\u7136\u4f60\u662f\u5e94\u8058\u6a61\u6811\u56ed\u7b2c\u4e09\u6240\u7684\u7cfb\u7edf\u67b6\u6784\u5de5\u7a0b\u5e08\uff0c\u7231\u8fbe\u96c6\u56e2\u773c\u4e0b\u6709\u4e2a\u9879\u76ee\uff0c\u6211\u5e0c\u671b\u4f60\u80fd\u4ee5\u9879\u76ee\u5de5\u7a0b\u5e08\u7684\u8eab\u4efd\u53c2\u52a0\uff0c\u4f60\u4eca\u540e\u80fd\u5728\u7231\u8fbe\u96c6\u56e2\u91cc\u505a\u5230\u4ec0\u4e48\u7a0b\u5ea6\uff0c\u5168\u8981\u770b\u4f60\u4e2a\u4eba\u7684\u80fd\u529b\u4e0e\u52aa\u529b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u6709\u95ee\u9898\uff0c\u201d\u5218\u9e23\u950b\u7422\u78e8\u7740\u4f1a\u6709\u4ec0\u4e48\u9879\u76ee\u9700\u8981\u81ea\u5df1\u53c2\u52a0\uff0c\u4e0d\u8fc7\u4e5f\u6ca1\u6709\u72b9\u8c6b\u5c31\u7b54\u5e94\u4e0b\u6765\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u90fd\u5728\u4f20\u8a00\u6a61\u6811\u56ed\u7684\u697c\u5c42\u963f\u59e8\u90fd\u8fc7\u4e86\u8ba1\u7b97\u673a\u4e09\u7ea7\uff0c\u6211\u521d\u9762\u7ed9\u5237\u4e0b\u6765\uff0c\u5f53\u65f6\u5c31\u60f3\u54ea\u6015\u5230\u6a61\u6811\u56ed\u5f53\u4e2a\u697c\u5c42\u963f\u59e8\u4e5f\u884c\u554a\u3002\u201d</p><p>\u3000\u3000\u9648\u4fe1\u751f\u6447\u5934\u800c\u7b11\uff0c\u5fc3\u60f3\u5f20\u606a\u5728\u56ed\u533a\u9047\u4e0a\u8fd9\u5bb6\u4f19\uff0c\u8be5\u4e0d\u4f1a\u9996\u5148\u770b\u4e0a\u4ed6\u7684\u6cb9\u8154\u6ed1\u8c03\u5427\u3002</p><p>\u3000\u3000\u5f20\u606a\u653e\u4e0b\u8df7\u7740\u7684\u4e8c\u90ce\u817f\uff0c\u770b\u4e86\u770b\u624b\u8868\uff0c\u7ad9\u8d77\u6765\u8ddf\u5218\u9e23\u950b\u8bf4\u9053\uff1a\u201c\u65f6\u95f4\u4e5f\u4e0d\u65e9\u4e86\uff0c\u5efa\u90ae\u7684\u535a\u58eb\u751f\u5bbf\u820d\u591c\u91cc\u4e0d\u5173\u95e8\uff0c\u4e0d\u6653\u5f97\u672c\u79d1\u751f\u5bbf\u820d\u5173\u4e0d\u5173\u95e8\uff1f\u6211\u8bf7\u4f60\u4eec\u5230\u5b66\u5e9c\u5df7\u9152\u5427\u559d\u4e00\u676f\u3002\u201d</p><p>\u3000\u3000\u9648\u71d5\u770b\u4e86\u4e00\u4e0b\u8868\uff0c\u90fd\u5feb\u5341\u4e00\u70b9\u4e86\uff0c\u5bbf\u820d\u662f\u56de\u4e0d\u53bb\uff0c\u73b0\u5728\u53ea\u80fd\u71ac\u5230\u5929\u4eae\u518d\u56de\u5bbf\u820d\u8865\u89c9\u4e86\uff0c\u96be\u9053\u8fd8\u80fd\u8ddf\u5218\u9e23\u950b\u56de\u4ed6\u5bbf\u820d\u53bb\u7761\u89c9\u53bb\uff1f\u90a3\u8fd8\u771f\u4fbf\u5b9c\u6b7b\u4ed6\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u8d77\u6765\uff0c\u5927\u5bb6\u90fd\u8ddf\u7740\u7ad9\u8d77\u6765\uff0c\u51c6\u5907\u5404\u81ea\u79bb\u5f00\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u4ece\u7f8e\u56fd\u8d76\u8fc7\u6765\u7684\u539fcypress\u534a\u5bfc\u4f53\u516c\u53f8\u7684\u5de5\u7a0b\u5e08\u5f90\u660e\u707f\u7b49\u4eba\u8bf4\u9053\uff1a\u201c\u9648\u5148\u751f\u4f1a\u5b89\u6392\u4eba\u9001\u4f60\u53bb\u5bbe\u9986\uff0c\u6211\u5c31\u5148\u79bb\u5f00\u4e86\uff0c\u660e\u5929\u7b7e\u7ea6\u7684\u4e8b\u60c5\uff0c\u4e5f\u7531\u9648\u5148\u751f\u4e0e\u5f20\u5c0f\u59d0\u8d1f\u8d23\uff0c\u5efa\u90ba\u5e02\u653f\u5e9c\u8fd9\u8fb9\uff0c\u8be5\u6709\u7684\u4f18\u60e0\u653f\u7b56\uff0c\u6a61\u6811\u56ed\u521b\u6295\u90fd\u4f1a\u5e2e\u4f60\u4eec\u4e89\u53d6\uff0c\u4e5f\u4f1a\u5b89\u6392\u65f6\u95f4\u8ba9\u4f60\u4eec\u4e0e\u5efa\u90ba\u5e02\u653f\u5e9c\u3001\u9ad8\u65b0\u533a\u7ba1\u59d4\u4f1a\u7684\u5b98\u5458\u76f4\u63a5\u63a5\u89e6\uff0c\u5e0c\u671b\u770b\u5230\u4f60\u4eec\u5728\u89e6\u63a7\u611f\u6d4b\u9886\u57df\u505a\u51fa\u6770\u51fa\u7684\u6210\u7ee9\uff0c\u8fd9\u662f\u5bf9\u5927\u5bb6\u90fd\u5229\u7684\u4e8b\u60c5\u3002\u201d</p><p>\u3000\u3000\u8fd9\u8fb9\u4ea4\u5f85\u8fc7\uff0c\u5f20\u606a\u7ed9\u5510\u5a67\u6253\u4e86\u7535\u8bdd\uff0c\u786e\u8ba4\u5979\u4eec\u8fd8\u57281978\uff0c\u5c31\u8bf7\u5218\u9e23\u950b\u3001\u9648\u71d5\u4e00\u8d77\u53bb1978\u5750\u4e00\u5750\u3002\u4e0b\u4e86\u697c\uff0c\u8ddf\u4ed6\u4eec\u4ecb\u7ecd\u5f90\u660e\u707f\u7b49\u4eba\u7684\u60c5\u51b5\uff1a\u201c\u4ed6\u4eec\u90fd\u662f\u89e6\u63a7\u611f\u6d4b\u9886\u57df\u7684\u4e13\u5bb6\uff0c\u4e2a\u4eba\u540d\u4e0b\u90fd\u62e5\u6709\u4e00\u4e9b\u53d1\u660e\u4e13\u5229\uff0c\u8ba1\u5212\u56de\u56fd\u521b\u4e1a\u7ee7\u7eed\u53d1\u5c55\u89e6\u63a7\u611f\u6d4b\u6280\u672f\uff0c\u6211\u4eec\u8fd9\u8fb9\u521d\u6b65\u51b3\u5b9a\u5411\u4ed6\u4eec\u63d0\u4f9b\u4e00\u5343\u4e07\u7684\u53d1\u5c55\u8d44\u91d1\u2026\u2026\u201d\u53c8\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u4e5f\u8981\u91cd\u65b0\u8ba4\u8bc6\u4e00\u4e0b\uff0c\u6211\u53eb\u5f20\u606a\uff0c\u6682\u65f6\u4e5f\u7b97\u662f\u4e1c\u5927\u7684\u5b66\u751f\uff0c\u7231\u8fbe\u7684\u4e8b\u52a1\uff0c\u6211\u4e5f\u53c2\u4e0e\u51b3\u7b56\u7684\u6743\u529b\uff0c\u56e0\u4e3a\u8fd8\u8981\u7ee7\u7eed\u5728\u4e1c\u5927\u9b3c\u6df7\u7684\u7f18\u6545\uff0c\u8fd9\u4e9b\u4e8b\u60c5\u662f\u4e0d\u5411\u5916\u516c\u5f00\u7684\u2014\u2014\u4f60\u5c06\u53c2\u4e0e\u7684\u8fd9\u4e2a\u9879\u76ee\uff0c\u7531\u6211\u76f4\u63a5\u8ffd\u8e2a\uff0c\u60f3\u6765\u7531\u6211\u7ed9\u4f60\u4ecb\u7ecd\u8fd9\u4e2a\u9879\u76ee\u7684\u60c5\u51b5\u6700\u5408\u9002\u4e0d\u8fc7\u3002\u201d</p><p>\u3000\u3000\u5218\u9e23\u950b\u5fc3\u91cc\u5728\u7422\u78e8\u201c\u76f4\u63a5\u8ffd\u8e2a\u201d\u8fd9\u4e2a\u8bcd\u80cc\u540e\u7684\u542b\u4e49\uff0c\u4e5f\u5f88\u96be\u51c6\u786e\u7684\u53bb\u7406\u89e3\u5f20\u606a\u6240\u8bf4\u7684\u201c\u53c2\u4e0e\u51b3\u7b56\u7684\u6743\u529b\u201d\u8fd9\u53e5\u8bdd\u6240\u5305\u542b\u7684\u51c6\u786e\u542b\u4e49\uff0c\u4ed6\u73b0\u5728\u90fd\u4e0d\u6e05\u695a\u9879\u76ee\u7684\u5177\u4f53\u5185\u5bb9\uff0c\u4e5f\u4e0d\u4fbf\u591a\u95ee\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u8d70\u51fa\u6a61\u6811\u56ed\uff0c\u7a7f\u7740\u4e1c\u534e\u5927\u9053\u5c31\u662f\u5e08\u5927\u5357\u95e8\uff0c\u4ece\u591c\u6df1\u4eba\u9759\u7684\u5e08\u5927\u6821\u56ed\u7a7f\u8fc7\uff0c\u8d70\u51fa\u5e08\u5927\u897f\u5927\u95e8\u4eba\u5c31\u662f\u5728\u5b66\u5e9c\u5df7\u4e4b\u4e2d\u4e86\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u7684\u5185\u5df7\u591a\u4e3a\u9152\u5427\uff0c\u6b63\u503c\u706f\u7ea2\u9152\u7eff\u4e4b\u65f6\uff0c\u597d\u4e9b\u9752\u5e74\u7537\u5973\u62db\u6447\u800c\u8fc7\u3002</p><p>\u3000\u3000\u82f1\u5f0f\u9152\u5427\u98ce\u683c\u76841978\u66f4\u50cf\u719f\u4eba\u805a\u4f1a\u7684\u5c0f\u793e\u533a\uff0c\u6ca1\u6709\u523a\u6fc0\u4eba\u5fc3\u7684\u97f3\u4e50\u4e0e\u653e\u7eb5\u7684\u6ee5\u996e\u72c2\u9189\uff0c\u8fd9\u65f6\u5019\u5ba2\u4eba\u5df2\u7ecf\u4e0d\u591a\u4e86\uff0c\u5f20\u606a\u8d70\u8fdb\u6765\uff0c\u770b\u89c1\u5510\u5a67\u8db4\u5728\u5427\u53f0\u4e0a\u5dee\u4e0d\u591a\u5feb\u8981\u7761\u7740\u4e86\uff0c\u8d70\u8fc7\u6765\uff0c\u624b\u6478\u4e86\u6478\u5979\u67d4\u5ae9\u7684\u8138\u988a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u4eca\u5929\u7684\u4f1a\u8bae\u6bd4\u8f83\u987a\u5229\uff0c\u5c31\u803d\u6401\u5230\u73b0\u5728\uff0c\u4f60\u53ef\u4ee5\u5148\u56de\u53bb\u7761\u89c9\u7684\u2026\u2026\u201d\u4ecb\u7ecd\u5218\u9e23\u950b\u3001\u9648\u71d5\u7ed9\u5979\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u5510\u5a67\u5a07\u67d4\u4e00\u7b11\uff0c\u5979\u773c\u76ae\u5b50\u53d1\u8f6f\uff0c\u7565\u5fae\u6709\u4e9b\u6c34\u80bf\uff0c\u5374\u66f4\u50cf\u6843\u82b1\u5a9a\u773c\u4e86\uff0c\u4f9d\u7740\u5f20\u606a\u8eab\u8fb9\u542c\u4ed6\u4eec\u8bf4\u8bdd\u3002</p><p>\u3000\u3000\u201c\u56fd\u5185\u7684\u4ea7\u4e1a\u73af\u5883\uff0c\u4e0d\u8981\u8bf4\u4f01\u4e1a\u4e86\uff0c\u5c31\u8fde\u4fe1\u4ea7\u90e8\u7684\u5b98\u5458\u90fd\u5f88\u60b2\u89c2\u2014\u2014\u786c\u4ef6\u6280\u672f\uff0c\u5dee\u522b\u4eba\u4e0d\u662f\u4e00\u70b9\u534a\u70b9\uff0c\u8f6f\u4ef6\u6280\u672f\uff0c\u5dee\u522b\u4eba\u66f4\u662f\u5341\u4e07\u516b\u5343\u91cc\uff0c\u5185\u5bb9\u2026\u2026\u770b\u770b\u56fd\u5185\u7684\u77e5\u8bc6\u7248\u6743\u73af\u5883\u5c31\u80fd\u591f\u60f3\u8c61\u8981\u5728\u56fd\u5185\u5efa\u7acb\u80fd\u76c8\u5229\u7684\u4e92\u8054\u7f51\u5185\u5bb9\u5e73\u53f0\u4f1a\u662f\u591a\u4e48\u56f0\u96be\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u786c\u4ef6\u3001\u8f6f\u4ef6\u3001\u5185\u5bb9\u5f00\u53d1\uff0c\u6bcf\u4e2a\u73af\u8282\u56fd\u5185\u4f01\u4e1a\u90fd\u6709\u77ed\u671f\u5185\u96be\u4ee5\u514b\u670d\u7684\u81f4\u547d\u7f3a\u9677\uff0c\u884c\u4e1a\u7a0d\u6709\u53cd\u601d\u7684\u4eba\u90fd\u4f1a\u60f3\u56fd\u5185\u4f01\u4e1a\u600e\u4e48\u53ef\u80fd\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u5e02\u573a\u7ade\u4e89\u8fc7\u6d77\u5916\u6d88\u8d39\u7535\u5b50\u5de8\u5934\uff1f\u90a3\u5c31\u53ea\u5269\u4e0b\u6a21\u5f0f\u521b\u65b0\u8fd9\u6761\u8def\u597d\u8d70\u4e86\u3002\u5728\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u54c1\u66f4\u65b0\u6362\u4ee3\u5982\u6b64\u8fc5\u901f\u7684\u5e74\u4ee3\uff0c\u5927\u591a\u6570\u6d88\u8d39\u8005\u5bf9\u786c\u4ef6\u8d28\u91cf\u7684\u8981\u6c42\u4e0d\u518d\u82db\u523b\uff0c\u800c\u66f4\u6ce8\u91cd\u7528\u6237\u4f53\u9a8c\u2014\u2014\u6211\u4eec\u5728\u786c\u4ef6\u6280\u672f\u4e0a\u77ed\u671f\u5f88\u96be\u8d85\u8d8a\u6d77\u5916\u7535\u5b50\u5de8\u5934\u505a\u5f97\u66f4\u597d\uff0c\u90a3\u5c31\u8981\u521b\u65b0\u6a21\u5f0f\uff0c\u5c31\u8981\u5728\u7528\u6237\u4f53\u9a8c\u4e0a\u4e0b\u5de5\u592b\u3002\u9664\u4e86\u4f18\u826f\u7684\u5de5\u4e1a\u8bbe\u8ba1\u4e4b\u5916\uff0c\u4e30\u5bcc\u7684\u5185\u5bb9\u5f00\u53d1\u4e0e\u5b8c\u5584\u7684\u8f6f\u4ef6\u5e94\u7528\u73af\u5883\u90fd\u80fd\u7ed9\u7528\u6237\u66f4\u5b8c\u7f8e\u7684\u4f53\u9a8c\u3002\u4f60\u7684\u4e00\u4e9b\u601d\u8def\u5f88\u6b63\u786e\uff0c\u4f20\u7edf\u7684\u6a21\u62df\u6280\u672f\u7535\u5b50\u5382\u5546\uff0c\u4ed6\u4eec\u662f\u5206\u5272\u7684\u3001\u5b64\u7acb\u7684\u53bb\u8003\u5bdf\u786c\u4ef6\u3001\u8f6f\u4ef6\u3001\u5185\u5bb9\u5f00\u53d1\u4e09\u8005\u4e4b\u95f4\u7684\u5173\u7cfb\uff0c\u6211\u4eec\u5c06\u786c\u4ef6\u3001\u8f6f\u4ef6\u3001\u5185\u5bb9\u5f00\u53d1\u505a\u4e3a\u4e00\u4e2a\u6574\u4f53\u53bb\u8003\u8651\u65f6\uff0c\u5728\u7528\u6237\u4f53\u9a8c\u8fd9\u4e00\u5757\u5c31\u80fd\u505a\u5230\u66f4\u597d\u3002\u4e00\u65b9\u9762\uff0c\u7528\u8f6f\u4ef6\u3001\u5185\u5bb9\u5f00\u53d1\u7b49\u80fd\u63d0\u9ad8\u7528\u6237\u4f53\u9a8c\u7684\u624b\u6bb5\u6765\u5f25\u8865\u786c\u4ef6\u6280\u672f\u7684\u5f31\u52bf\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u56fd\u5185\u4ea7\u4e1a\u73af\u5883\u4f7f\u5f97\u8f6f\u4ef6\u5f00\u53d1\u3001\u5185\u5bb9\u5f00\u53d1\u9886\u57df\u7684\u5229\u6da6\u6781\u4f4e\uff0c\u751a\u81f3\u53ef\u4ee5\u8bf4\u5728\u4e92\u8054\u7f51\u4e0a\u505a\u5185\u5bb9\u5f00\u53d1\u5e73\u53f0\u77ed\u671f\u5185\u5c31\u6839\u672c\u6ca1\u6709\u4ec0\u4e48\u5229\u6da6\u53ef\u8a00\uff0c\u6211\u4eec\u53ef\u4ee5\u4ece\u786c\u4ef6\u5229\u6da6\u91cc\u5206\u51fa\u4e00\u90e8\u5206\u5bf9\u5176\u8fdb\u884c\u8865\u8d34\uff0c\u4f7f\u5176\u80fd\u5747\u8861\u7684\u53d1\u5c55\u4e0b\u53bb\u2026\u2026\u4f60\u80fd\u60f3\u8c61\u4f60\u5c06\u53c2\u4e0e\u7684\u8fd9\u4e2a\u9879\u76ee\u4e3b\u8981\u662f\u505a\u54ea\u65b9\u9762\u7684\u5de5\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e92\u8054\u7f51\u5185\u5bb9\u5e73\u53f0\u7684\u5f00\u53d1\uff1f\u201d\u5218\u9e23\u950b\u8fdf\u7591\u7684\u95ee\u9053\uff0c\u4ed6\u5728\u5efa\u90ba\u7684\u7855\u535a\u5b66\u4e60\uff0c\u90fd\u662f\u504f\u8f6f\uff0c\u786c\u4ef6\u5f00\u53d1\u8ddf\u4ed6\u6ca1\u6709\u4ec0\u4e48\u5173\u7cfb\uff0c\u505a\u7cfb\u7edf\u67b6\u6784\u5de5\u7a0b\u5e08\uff0c\u4ed6\u4e5f\u4e0d\u8ba4\u4e3a\u81ea\u5df1\u6bd4\u90a3\u4e9b\u6d77\u5f52\u66f4\u51fa\u8272\u4ee5\u81f4\u80fd\u5f97\u5230\u7231\u8fbe\u9ad8\u5c42\u7684\u522e\u76ee\u76f8\u770b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u5728\u5efa\u90ba\uff0c\u521b\u57df\u53c2\u4e0e\u6295\u8d44\u4e86\u5353\u57df\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\uff0c\u521b\u57df\u8fd8\u53c2\u4e0e\u4e86\u4e00\u9879\u6570\u5b57\u56fe\u4e66\u9986\u5f00\u53d1\u8ba1\u5212\u7684\u6295\u8d44\uff1b\u5728\u5317\u4eac\uff0c\u6a61\u6811\u56ed\u521b\u6295\u53c2\u4e0e\u6295\u8d44\u4e86\u6606\u817e\u5728\u7ebf\uff1b\u5728\u6d77\u5916\uff0c\u7231\u8fbe\u7684\u6d77\u5916\u516c\u53f8\u53c2\u4e0e\u6295\u8d44\u4e86\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u3002\u4f60\u5b8c\u6210\u5165\u804c\u624b\u7eed\u4e4b\u540e\uff0c\u6211\u4f1a\u7ed9\u4f60\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u4e0e\u6211\u4eec\u6240\u638c\u63e1\u7684\u8fd9\u4e9b\u4e92\u8054\u7f51\u8d44\u6e90\u8fdb\u884c\u5145\u5206\u7684\u63a5\u89e6\uff1b\u5982\u679c\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\u4e0d\u591f\uff0c\u8fd8\u53ef\u4ee5\u591a\u7ed9\u4f60\u4e00\u4e9b\u65f6\u95f4\uff0c\u8fd9\u6bb5\u65f6\u95f4\u91cc\uff0c\u4f60\u552f\u4e00\u8981\u505a\u7684\u5c31\u662f\u6bcf\u5468\u63d0\u4ea4\u4e00\u4efd\u62a5\u544a\uff0c\u62a5\u544a\u968f\u4f60\u5199\uff0c\u5199\u4ec0\u4e48\u90fd\u53ef\u4ee5\uff0c\u8fc7\u4e86\u8fd9\u6bb5\u65f6\u95f4\uff0c\u6211\u4eec\u518d\u8003\u8651\u7ed9\u4f60\u5b89\u6392\u6b63\u5f0f\u7684\u804c\u52a1\u3002\u201d\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff0c\u201c\u65f6\u95f4\u4e0d\u65e9\u4e86\uff0c\u4f60\u4eec\u56de\u5bbf\u820d\u4e5f\u4e0d\u65b9\u4fbf\uff0c\u6211\u8ba9\u4eba\u7ed9\u4f60\u4eec\u5b89\u6392\u4f4f\u5bbe\u9986\u5427\u3002\u201d</p><p>\u3000\u3000\u770b\u7740\u5218\u9e23\u950b\u4e0e\u9648\u71d5\u79bb\u5f00\uff0c\u675c\u98de\u563b\u76ae\u7b11\u8138\u7684\u8bf4\u9053\uff1a\u201c\u4f60\u8fd9\u4e0d\u662f\u7eb5\u5bb9\u4eba\u5bb6\u72af\u9519\u8bef\u5417\uff0c\u4f60\u8bf4\u4ed6\u4eec\u4fe9\u5fc3\u60c5\u6fc0\u8361\u80fd\u6709\u591a\u5927\u6982\u7387\u4e0d\u72af\u9519\u8bef\uff1f\u201d\u8fd8\u60f3\u518d\u8bf4\u4ec0\u4e48\uff0c\u5374\u7ed9\u76db\u590f\u8d70\u5230\u8eab\u540e\u72e0\u6390\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u675c\u98de\u5728\u71d5\u56ed\u6574\u4e86\u4e00\u5957\u522b\u5885\uff0c\u4ed6\u5f00\u8f66\u8f7d\u7740\u76db\u590f\u79bb\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5510\u5a67\u3001\u9648\u5983\u84c9\u8fd4\u56de\u9752\u5e74\u516c\u5bd3\u3002\u5728\u8def\u4e0a\uff0c\u5f20\u606a\u7ed9\u9648\u5983\u84c9\u3001\u5510\u5a67\u80e1\u4e71\u5439\u5618\uff1a\u201c\u4e00\u822c\u4eba\u90fd\u4f1a\u4ee5\u4e3a\u9526\u6e56\u65d7\u4e0b\u7684\u4e2d\u4f4e\u7aef\u624b\u673a\u5e94\u8be5\u66f4\u597d\u5356\uff0c\u6211\u4eec\u5728\u4e2d\u4f4e\u7aef\u673a\u578b\u7684\u786c\u4ef6\u6280\u672f\u4e0a\u5df2\u7ecf\u6ca1\u6709\u4ec0\u4e48\u7455\u75b5\u4e86\uff0c\u552e\u4ef7\u4e5f\u76f8\u5bf9\u4fbf\u5b9c\uff0c\u5f88\u5c11\u4eba\u4f1a\u60f3\u5230\u552e\u4ef7\u9ad8\u8fbe\u516b\u5343\u516b\u767e\u5143\u7684d188\u53cc\u5c4f\u624b\u673a\u5c31\u8fd9\u4e00\u6b3e\u624b\u673a\u4e0a\u5e02\u4e0d\u5230\u4e00\u4e2a\u6708\u5c31\u5360\u5230\u7231\u8fbe\u6240\u6709\u624b\u673a\u9500\u91cf\u7684\u4e09\u5206\u4e4b\u4e00\u3001\u603b\u9500\u552e\u989d\u7684\u4e8c\u5206\u4e4b\u4e00\u3002\u8fd9\u4e00\u6b3e\u624b\u673a\u516b\u6708\u4efd\u5dee\u4e0d\u591a\u80fd\u7ed9\u7231\u8fbe\u63d0\u4f9b\u56db\u4e2a\u4ebf\u7684\u7a0e\u524d\u5229\u6da6\u3002\u201d</p><p>\u3000\u3000\u5510\u5a67\u633d\u7740\u9648\u5983\u84c9\u7684\u80f3\u818a\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u8d22\u4e3b\u5c31\u77e5\u9053\u70ab\u8000\u4ed6\u6709\u94b1\uff01\u201d</p><p>\u3000\u3000\u4ece\u516c\u5bd3\u697c\u7684\u697c\u9699\u91cc\uff0c\u80fd\u770b\u5230\u7ed9\u706f\u5149\u6620\u663e\u51fa\u8f6e\u5ed3\u7684\u56fe\u4e66\u9986\u591c\u666f\u6765\uff0c\u9648\u5983\u84c9\u5f80\u90a3\u91cc\u5fae\u5476\u8d77\u5634\u770b\u4e86\u770b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8fd8\u4ee5\u4e3a\u4f60\u6709\u591a\u6177\u6168\uff0c\u539f\u6765\u90fd\u4e0d\u8981\u534a\u4e2a\u6708\u6323\u7684\u94b1\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u4e3a\u6323\u8fd9\u94b1\u5bb9\u6613\uff01\u201d\u5f20\u606a\u4f38\u624b\u505a\u52bf\u8981\u53bb\u6253\u8fd9\u4e24\u4e2a\u4e2b\u5934\uff0c\u201c\u867d\u8bf4\u9526\u6e56\u5728\u56fd\u5185\u624b\u673a\u5e02\u573a\u8fd8\u53ea\u80fd\u5c48\u5c45\u7b2c\u4e09\uff0c\u4f46\u662f\u51ed\u501fd188\uff0c\u6211\u4eec\u516b\u6708\u4efd\u7684\u7a0e\u524d\u5229\u6da6\u603b\u989d\u5c06\u8d85\u8fc7\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u2014\u2014\u8981\u662f\u8fd9\u90fd\u4e0d\u80fd\u62ff\u51fa\u6765\u70ab\u8000\uff0c\u5546\u4e1a\u4e0a\u8fd8\u6709\u591a\u5c11\u6210\u529f\u80fd\u62ff\u51fa\u6765\u70ab\u8000\uff1f\u201d</p><p>\u3000\u3000\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5374\u662f\u4e0d\u7406\u5f20\u606a\uff0c\u7b11\u7740\u5728\u524d\u9762\u8dd1\u5f00\u4e86\u3002</p><p>\u3000\u3000\u8fdb\u4e86\u516c\u5bd3\uff0c\u5510\u5a67\u62a2\u5148\u53bb\u6d74\u5ba4\u91cc\u6d17\u6fa1\uff0c\u5510\u5a67\u4e0d\u518d\uff0c\u9648\u5983\u84c9\u5355\u72ec\u9762\u5bf9\u5f20\u606a\uff0c\u4fcf\u76ae\u7684\u795e\u6001\u5c31\u8981\u6536\u655b\u4e00\u4e9b\uff0c\u6ca1\u6709\u521a\u624d\u7684\u6d3b\u6cfc\uff0c\u5750\u4e0b\u6765\u542c\u4ed6\u8bf4\u8bdd\uff0c\u6c14\u8d28\u5a34\u9759\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u80fd\u7ee7\u7eed\u521a\u624d\u7684\u8bdd\u9898\uff1a\u201c\u2026\u2026d188\u80fd\u5927\u83b7\u6210\u529f\uff0c\u662f\u53cc\u5c4f\u8bbe\u8ba1\u6293\u4f4f\u6d88\u8d39\u8005\u7684\u773c\u7403\u2014\u2014\u8fd9\u5c5e\u4e8e\u7528\u6237\u4f53\u9a8c\u8303\u7574\u4e00\u4e2a\u5f88\u7ec6\u5fae\u7684\u5185\u5bb9\u3002\u7528\u6237\u4f53\u9a8c\u867d\u8bf4\u662f\u4ee5\u786c\u4ef6\u6280\u672f\u4e3a\u652f\u6491\u7684\uff0c\u4f46\u662f\u66f4\u5b8c\u7f8e\u7684\u7528\u6237\u4f53\u9a8c\u5e76\u4e0d\u610f\u5473\u7740\u9700\u8981\u66f4\u9876\u7ea7\u7684\u786c\u4ef6\u6280\u672f\u3002d188\u5728\u6280\u672f\u4e0a\u5e76\u6ca1\u6709\u7279\u522b\u51fa\u8272\u7684\u5730\u65b9\uff0c\u98de\u5229\u6d66\u3001\u7231\u7acb\u4fe1\u3001\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u4e9a\u57fa\u51e0\u6b3e\u9ad8\u7aef\u5546\u52a1\u624b\u673a\u7684\u6280\u672f\u90fd\u8981\u5f3a\u8fc7d188\uff0c\u4f46\u662f\u5728\u9ad8\u7aef\u5546\u52a1\u673a\u5e02\u573a\uff0cd188\u7684\u4f18\u52bf\u5374\u975e\u5e38\u7684\u660e\u663e\u2014\u2014\u4ee5\u7528\u6237\u4f53\u9a8c\u4e3a\u6838\u5fc3\u7684\u6280\u672f\u53d1\u5c55\u4e0e\u8bbe\u8ba1\u601d\u8def\u5c31\u662f\u9526\u6e56\u6562\u8fd9\u65f6\u5019\u5c31\u53bb\u56fd\u9645\u5e02\u573a\u7ade\u4e89\u7684\u4fe1\u5fc3\u6240\u5728\u3002\u8981\u662f\u6ca1\u6709\u6a21\u5f0f\u4e0a\u7684\u521b\u65b0\uff0c\u4e5f\u6ca1\u6709\u6280\u672f\u4e0a\u7684\u4f18\u52bf\uff0c\u53c8\u4e0d\u60f3\u505a\u5ec9\u4ef7\u7684\u4f53\u529b\u52b3\u52a8\u8005\uff0c\u5728\u56fd\u9645\u5e02\u573a\u662f\u65e0\u6cd5\u7acb\u8db3\u7684\u3002\u4f46\u662f\u6211\u4eec\u5728d188\u53cc\u5c4f\u624b\u673a\u7684\u4f18\u52bf\u975e\u5e38\u7684\u5f31\uff0c\u5c31\u50cf\u98ce\u4e2d\u7684\u70db\u706b\uff0c\u770b\u4e0a\u53bb\u6709\u4e9b\u4eae\u5149\uff0c\u6307\u4e0d\u5b9a\u4ec0\u4e48\u65f6\u5019\u5c31\u7184\u4e86\u3002\u5de5\u4e1a\u8bbe\u8ba1\u5f88\u5bb9\u6613\u7ed9\u7ade\u4e89\u5bf9\u624b\u6a21\u4eff\uff0c\u6211\u4eec\u63a8\u51fa\u53cc\u5c4f\u8bbe\u8ba1\u624b\u673a\u4e4b\u540e\uff0c\u7ade\u4e89\u5bf9\u624b\u6709\u8ff9\u53ef\u5faa\uff0c\u80fd\u975e\u5e38\u8fc5\u901f\u7684\u4f7f\u53cc\u5c4f\u6280\u672f\u6210\u719f\u8d77\u6765\uff0c\u4e00\u65e6\u4ed6\u4eec\u63a8\u51fa\u53cc\u5c4f\u624b\u673a\uff0c\u8fd9\u4e2a\u7ec6\u5206\u5e02\u573a\u5c31\u4e0d\u518d\u5f52\u6211\u4eec\u6240\u72ec\u6709\u2026\u2026\u5efa\u7acb\u80fd\u4e0d\u65ad\u5b8c\u5584\u3001\u6539\u8fdb\u7684\u3001\u4e0d\u6613\u7ed9\u7ade\u4e89\u5bf9\u624b\u6a21\u4eff\u7684\u7528\u6237\u4f53\u9a8c\u73af\u5883\u662f\u4e00\u4e2a\u9700\u8981\u76f8\u5f53\u91cd\u89c6\u7684\u590d\u6742\u95ee\u9898\u3002\u201d</p><p>\u3000\u3000\u201c\u5c06\u97f3\u9891\u82af\u7247\u96c6\u6210\u5230\u624b\u673a\u91cc\uff0c\u5f00\u53d1\u51fa\u97f3\u4e50\u624b\u673a\uff0c\u7531\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7ed9\u97f3\u4e50\u624b\u673a\u7528\u6237\u63d0\u4f9b\u5728\u7ebf\u6b4c\u66f2\u7b49\u97f3\u4e50\u5185\u5bb9\u4e0b\u8f7d\u7684\u670d\u52a1\u652f\u6301\uff0c\u4e5f\u63d0\u4f9b\u975e\u5e38\u51fa\u8272\u3001\u4fbf\u6377\u7684\u64ad\u653e\u8f6f\u4ef6\u53ef\u4f9b\u7528\u6237\u4f7f\u7528\uff0c\u81f3\u5c11\u5728\u97f3\u4e50\u624b\u673a\u7ec6\u5206\u5e02\u573a\uff0c\u7ade\u4e89\u5bf9\u624b\u5c31\u5f88\u96be\u6a21\u4eff\u9526\u6e56\u8fd9\u79cd\u5168\u65b9\u9762\u7684\u7528\u6237\u4f53\u9a8c\u6a21\u5f0f\uff0c\u201d\u9648\u5983\u84c9\u95ee\u9053\uff0c\u201c\u662f\u4e0d\u662f\u8be5\u8fd9\u4e48\u7406\u89e3\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u5dee\u4e0d\u591a\u5c31\u8be5\u8fd9\u6837\u7406\u89e3\uff0c\u201d\u5f20\u606a\u9aa8\u5934\u9165\u8f6f\u4e86\u4e00\u4e0b\u57ae\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u56db\u811a\u516b\u53c9\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4f60\u518d\u8ddf\u6211\u4e24\u5e74\uff0c\u5dee\u4e0d\u591a\u5c31\u53ef\u4ee5\u51fa\u5e08\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u53bb\uff0c\u8c01\u60f3\u8981\u62dc\u4f60\u4e3a\u5e08\uff1f\u201d\u9648\u5983\u84c9\u5a07\u67d4\u7684\u4e00\u7b11\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5510\u5a67\u5728\u6d74\u5ba4\u91cc\u558a\u9648\u5983\u84c9\uff0c\u5979\u62ff\u8fdb\u53bb\u7684\u7761\u88d9\u843d\u6c34\u91cc\u6e7f\u4e86\uff0c\u8ba9\u9648\u5983\u84c9\u5e2e\u5979\u518d\u62ff\u4ef6\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u770b\u7740\u9648\u5983\u84c9\u8fdb\u53bb\u597d\u4e00\u4f1a\u513f\u90fd\u6ca1\u6709\u89c1\u51fa\u6765\uff0c\u542c\u5230\u4e24\u4e2a\u5973\u5b69\u5b50\u5728\u91cc\u9762\u7684\u620f\u6c34\u58f0\uff0c\u5f20\u606a\u5fc3\u75d2\u75d2\u7684\u60f3\u7740\u662f\u4e0d\u662f\u5e2e\u5510\u5a67\u518d\u62ff\u4ef6\u7761\u88d9\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u5510\u5a67\u4e0d\u662f\u80fd\u71ac\u591c\u7684\u4eba\uff0c\u8fc7\u4e86\u5341\u4e00\u70b9\u773c\u76ae\u53ea\u6253\u67b6\u3002\u770b\u89c1\u9648\u5983\u84c9\u633d\u7740\u6e7f\u6f09\u6f09\u7684\u957f\u53d1\u51fa\u6765\u5524\u4ed6\u53bb\u6d17\uff0c\u5f20\u606a\u8d70\u8fdb\u53bb\u5510\u5a67\u6574\u4e2a\u4eba\u5df2\u7ecf\u8db4\u5728\u623f\u95f4\u7684\u5e8a\u4e0a\u7761\u7740\u4e86\uff0c\u7761\u88d9\u6380\u8d77\u9732\u51fa\u96ea\u767d\u4e30\u8174\u7684\u5927\u817f\uff0c\u767d\u8272\u5185\u88e4\u6709\u4e9b\u5c0f\uff0c\u8fd8\u9732\u4e86\u4e30\u8174\u9971\u6ee1\u7684\u81c0\u90e8\u8fb9\u7f18\u3002</p><p>\u3000\u3000\u201c\u554a\uff0c\u8fd9\u6b7b\u4e2b\u5934\u3002\u201d\u9648\u5983\u84c9\u77e5\u9053\u5510\u5a67\u4e0e\u5f20\u606a\u7684\u5173\u7cfb\u65e9\u5c31\u8d85\u8fc7\u90a3\u4e00\u5c42\u4e86\uff0c\u770b\u5230\u5510\u5a67\u8d70\u5149\uff0c\u8fd8\u662f\u5fd9\u8d70\u8fc7\u53bb\u5c06\u5979\u7684\u7761\u88d9\u62b9\u5e73\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u671d\u9648\u5983\u84c9\u77aa\u4e86\u77aa\u773c\uff0c\u8fd9\u4e24\u5929\u5510\u5a67\u8ddf\u9648\u5983\u84c9\u7f20\u5728\u4e00\u8d77\uff0c\u4ed6\u4e00\u70b9\u8364\u5473\u90fd\u6ca1\u6709\u6cbe\u5230\uff0c\u8fd9\u4f1a\u513f\u770b\u4e24\u773c\u8fd8\u4e0d\u884c\uff1f</p><p>\u3000\u3000\u9648\u5983\u84c9\u5ae3\u7136\u4e00\u7b11\uff0c\u53c8\u4f38\u624b\u5c06\u5510\u5a67\u7761\u88d9\u4e00\u89d2\u6380\u5f00\uff0c\u6062\u590d\u539f\u72b6\u8ba9\u5f20\u606a\u8fc7\u8fc7\u773c\u763e\u3002</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u82e6\u7b11\uff0c\u5510\u5a67\u7a7f\u7740\u4e1d\u8d28\u7761\u88d9\uff0c\u5728\u706f\u5149\u4e0b\u6709\u4e9b\u900f\u660e\uff0c\u53ef\u4ee5\u6e05\u6670\u7684\u770b\u89c1\u7f8e\u5982\u74a7\u7389\u7684\u8bf1\u4eba\u8089\u8272\uff0c\u5149\u770b\u770b\u53c8\u62b5\u4ec0\u4e48\u7528\uff1f</p><p>\u3000\u3000\u9648\u5983\u84c9\u7a7f\u7740\u68c9\u8d28\u4e0d\u900f\u5149\u7684\u957fT\u6064\uff0c\u5c06\u5979\u7a88\u7a95\u7684\u8eab\u6750\u90fd\u906e\u4f4f\uff0c\u53ea\u9732\u51fa\u5149\u6ed1\u7389\u5ae9\u7684\u4fee\u957f\u5c0f\u817f\u8d64\u8db3\u8e29\u5728\u5730\u677f\uff0c\u6e7f\u6f09\u6f09\u7684\u957f\u53d1\u504f\u5934\u633d\u5728\u624b\u91cc\uff0c\u66f4\u663e\u5f97\u5979\u80a4\u5982\u767d\u96ea\u3001\u5507\u82e5\u6a31\u6843\uff0c\u8102\u7389\u822c\u5ae9\u817b\u7684\u8138\u988a\u6709\u7740\u521a\u51fa\u6d74\u7684\u7ea2\u6655\uff0c\u591a\u5e73\u65f6\u7684\u51b7\u8273\u591a\u4e86\u4e9b\u5a07\u5a9a\u2014\u2014\u4e5f\u53ea\u80fd\u770b\u770b\uff0c\u5f20\u606a\u8fd9\u4e48\u60f3\u7740\uff0c\u5c06\u88e4\u515c\u91cc\u7684\u624b\u673a\u638f\u51fa\u6765\u653e\u5230\u5e8a\u8fb9\uff0c\u6362\u6d17\u7684\u8863\u670d\u4e0d\u6653\u5f97\u662f\u5510\u5a67\u8fd8\u662f\u9648\u5983\u84c9\u5df2\u7ecf\u5e2e\u4ed6\u51c6\u5907\uff0c\u4ed6\u6b63\u6253\u7b97\u8fdb\u53bb\u6d74\u5ba4\u6ce1\u6fa1\uff0c\u5510\u5a67\u5728\u7761\u68a6\u4e2d\u7ffb\u8eab\uff0c\u79c0\u8db3\u8e22\u5230\u4ed6\u7684\u624b\u673a\u3002</p><p>\u3000\u3000\u773c\u89c1\u7740\u624b\u673a\u843d\u5411\u5730\u677f\uff0c\u9648\u5983\u84c9\u4e0b\u610f\u8bc6\u7684\u5f2f\u8170\u53bb\u62fe\uff0c\u5f20\u606a\u4ece\u5979\u8361\u5f00\u7684\u9886\u53e3\u770b\u89c1\u4e00\u7247\u83b9\u767d\u7c89\u5ae9\u7684\u8000\u773c\u7389\u4f53\uff0c\u7389\u5973\u4e73\u5cf0\u5706\u6da6\u96ea\u5ae9\u575a\u633a\u800c\u9971\u6ee1\u2014\u2014\u9648\u5983\u84c9T\u6064\u886b\u91cc\u7adf\u7136\u6ca1\u6709\u6234\u4e73\u7f69\uff0c\u5f20\u606a\u773c\u775b\u90fd\u770b\u50bb\u4e86\u3002</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u9648\u5983\u84c9\u5c06\u624b\u673a\u653e\u5230\u5e8a\u4e0a\uff0c\u56de\u5934\u770b\u89c1\u5f20\u606a\u79fb\u5f00\u7684\u773c\u795e\uff0c\u610f\u8bc6\u5230\u521a\u624d\u8e72\u4e0b\u6765\u65f6\u8d70\u4e86\u5149\uff0c\u5979\u7684\u4e73\u5c16\u7ec6\u7ec6\u7684\uff0c\u7a7f\u4e0d\u900f\u5149\u7684T\u6064\u4e5f\u4e0d\u4f1a\u9732\u4ec0\u4e48\u75d5\u8ff9\uff0c\u54ea\u91cc\u60f3\u5230\u8fd8\u662f\u7ed9\u5f20\u606a\u770b\u5230\u773c\u91cc\uff1f\u6709\u4e9b\u7f9e\u6da9\uff0c\u5374\u53c8\u4e0d\u7518\u7684\u5c06\u624b\u673a\u62ff\u8d77\u6765\u8981\u7838\u7ed9\u5f20\u606a\uff1a\u201c\u662f\u4e0d\u662f\u4f60\u6545\u610f\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5f53\u6211\u795e\u4ed9\u554a\uff0c\u201d\u5f20\u606a\u624b\u6321\u7740\u8138\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u600e\u4e48\u77e5\u9053\u5c0f\u5a67\u6b63\u597d\u8981\u7ffb\u8eab\u5c06\u624b\u673a\u8e22\u4e0b\u6765\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u5f53\u7ed9\u72d7\u770b\u4e86\u3002\u201d\u9648\u5983\u84c9\u5fae\u5658\u7740\u5634\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6211\u53c8\u4e0d\u6545\u610f\u7684\uff0c\u201d\u5f20\u606a\u88c5\u59d4\u5c48\u7684\u8bf4\u9053\uff0c\u201c\u4f60\u5c31\u613f\u610f\u7ed9\u72d7\u770b\u4e86\uff1f\u201d</p><p>\u3000\u3000\u518d\u6597\u5634\u4e0b\u53bb\u5c31\u6210\u8c03\u60c5\u4e86\uff0c\u9648\u5983\u84c9\u5fae\u62bf\u4e86\u4e00\u4e0b\u5634\u5507\uff0c\u89c1\u5510\u5a67\u7ffb\u8fc7\u8eab\u6765\uff0c\u7761\u88d9\u662f\u6574\u9f50\u7684\uff0c\u53ea\u662f\u6709\u4e9b\u900f\u5149\uff0c\u80f8\u524d\u900f\u51fa\u7684\u5ae9\u7389\u8089\u8272\u91cc\u9732\u51fa\u4e24\u70b9\u5ae3\u7ea2\uff0c\u7761\u68a6\u4e2d\u7684\u5bb9\u989c\u683c\u5916\u7684\u5a07\u7f8e\uff0c\u53c8\u4e0d\u80fd\u5e2e\u5510\u5a67\u62ff\u6bef\u5b50\u76d6\u4e0a\uff0c\u624d\u7b11\u7740\u8ddf\u5f20\u606a\u8bf4\uff1a\u201c\u5c0f\u5a67\u7761\u76f8\u592a\u5dee\uff0c\u6628\u5929\u5dee\u70b9\u7ed9\u5979\u6324\u4e0b\u5e8a\uff0c\u6211\u4eca\u5929\u7761\u5c0f\u623f\u95f4\u53bb\uff0c\u4e0d\u7406\u5979\u4e86\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u542c\u4e86\u5c31\u8feb\u4e0d\u53ca\u5f85\u7684\u62f1\u624b\u97a0\u8eac\u3002</p><p>\u3000\u3000\u9648\u5983\u84c9\u89c1\u4ed6\u4e0d\u52a0\u63a9\u9970\u7684\u6025\u8272\u6a21\u6837\uff0c\u5fcd\u4e0d\u4f4f\u60f3\u7b11\uff0c\u7f8e\u7738\u6a2a\u76fc\u7684\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u62ff\u8d77\u68b3\u5b50\u5c31\u79bb\u5f00\u623f\u95f4\u3002</p></article>"}}}]);
