(window.webpackJsonp=window.webpackJsonp||[]).push([[1053],{1076:function(p){p.exports={chapterTitle:null,title:"\u7b2c1050\u7ae0 \u8e29\u5230\u96e8\u8863\u6ed1\u4e00\u8de4",content:"<article><h2>\u7b2c1050\u7ae0 \u8e29\u5230\u96e8\u8863\u6ed1\u4e00\u8de4</h2><p>\u3000\u3000\u7a97\u6237\u655e\u5f00\u7740\uff0c\u6e05\u6668\u7684\u98ce\u5439\u62c2\u6de1\u7d2b\u8272\u7684\u7eb1\u7a97\u5e18\uff0c\u5510\u5a67\u7741\u5f00\u60fa\u677e\u7684\u7761\u773c\uff0c\u96ea\u767d\u7684\u80f3\u818a\u8fd8\u6175\u61d2\u7684\u6a2a\u5728\u5f20\u606a\u7684\u80f8\u819b\u4e0a\uff0c\u660e\u660e\u8bb0\u5f97\u6628\u5929\u591c\u91cc\u662f\u8ddf\u5983\u84c9\u7761\u7684\uff0c\u600e\u4e48\u8fd9\u5bb6\u4f19\u50cf\u53ea\u732a\u4e00\u6837\u7684\u62f1\u5728\u8eab\u8fb9\uff1f\u5510\u5a67\u7591\u60d1\u7684\u63c9\u4e86\u63c9\u773c\u775b\uff0c\u8fd8\u662f\u5f88\u8212\u670d\u7684\u62b5\u5230\u5f20\u606a\u7684\u80f8\u53e3\u60f3\u518d\u7761\u4e00\u4f1a\u513f\uff0c\u5fae\u5f20\u7684\u67d4\u5507\u5410\u6c14\u5982\u5170\uff0c\u53cc\u817f\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u611f\u89c9\u6709\u52c3\u8d77\u7684\u4e1c\u897f\u9876\u5728\u4e0b\u4f53\uff0c\u5f80\u4e0b\u6478\u4e86\u6478\uff0c\u8fd9\u5bb6\u4f19\u7adf\u7136\u6ca1\u6709\u7a7f\u5185\u88e4\uff01\u5510\u5a67\u60f3\u8981\u5c06\u817f\u632a\u5f00\uff0c\u53c8\u6015\u78b0\u9192\u5f20\u606a\uff0c\u4e0d\u52a8\u5f39\uff0c\u90a3\u91cc\u53c8\u7ed9\u9876\u5f97\u75d2\u75d2\u7684\u96be\u53d7\uff0c\u5fc3\u60f3\u600e\u4e48\u4f1a\u8fd9\u4e48\u5de7\u662f\u8fd9\u4e2a\u59ff\u52bf\uff0c\u5c0f\u624b\u88f9\u4f4f\u62b5\u5728\u81ea\u5df1\u79c1\u5904\u7684\u90a3\u9752\u7b4b\u6012\u66b4\u7684\u7537\u6839\u63e1\u4e86\u63e1\uff0c\u542c\u5f20\u606a\u7684\u547c\u5438\u4e0d\u50cf\u662f\u7761\u7740\u4e86\uff0c\u4f38\u624b\u53bb\u62e8\u4ed6\u7684\u773c\u76ae\u5b50\uff0c\u89c1\u4ed6\u7684\u773c\u73e0\u5b50\u5728\u91cc\u9762\u8f6c\u52a8\uff0c\u7ffb\u8eab\u9a91\u5750\u5230\u5f20\u606a\u7684\u8eab\u4e0a\uff0c\u5f20\u5634\u54ac\u4f4f\u4ed6\u7684\u9f3b\u5934\uff0c\u8f7b\u8f7b\u7684\u5543\u556e\u4e86\u4e24\u4e0b\uff0c\u5a07\u67d4\u7684\u95ee\u9053\uff1a\u201c\u600e\u4e48\u662f\u4f60\u8fd9\u5934\u732a\u7761\u8fc7\u6765\u4e86\uff0c\u5983\u84c9\u4eba\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ed9\u6211\u8d76\u8dd1\u4e86\uff0c\u90a3\u6b7b\u4e2b\u5934\uff0c\u6574\u5929\u5c31\u77e5\u9053\u7834\u574f\u6211\u7684\u597d\u4e8b\uff0c\u201d\u5f20\u606a\u6402\u7740\u5510\u5a67\u76c8\u76c8\u53ea\u582a\u4e00\u63e1\u7684\u7ec6\u8170\uff0c\u201c\u5f80\u4e0b\u5750\u4e00\u70b9\u3002\u201d</p><p>\u3000\u3000\u8eab\u5b50\u987a\u4ece\u5f80\u4e0b\u79fb\uff0c\u76f4\u5230\u7ed9\u90a3\u6839\u707c\u70ed\u575a\u786c\u7684\u90e8\u5206\u62b5\u7740\u79c1\u5904\uff0c\u5510\u5a67\u624d\u77e5\u9053\u8fd9\u5bb6\u4f19\u60f3\u505a\u4ec0\u4e48\uff0c\u81c0\u90e8\u60f3\u8981\u626d\u5f00\uff0c\u5c0f\u8170\u5374\u4ed6\u4e24\u624b\u5361\u4f4f\uff0c\u9694\u7740\u8584\u8584\u7684\u5185\u88e4\uff0c\u80fd\u611f\u89c9\u5230\u90a3\u706b\u70ed\u575a\u786c\u51e0\u4e4e\u8981\u76f4\u63a5\u9876\u8fdb\u6765\uff0c\u9876\u6324\u7740\u79c1\u5904\u654f\u611f\u5230\u6781\u70b9\u7684\u5ae9\u8089\uff0c\u7ed9\u5979\u65e0\u6cd5\u9003\u907f\u7684\u7f9e\u803b\uff0c\u9876\u6324\u4e86\u51e0\u4e0b\uff0c\u611f\u89c9\u5230\u6e17\u51fa\u6765\u7684\u871c\u6c41\u8981\u5c06\u5185\u88e4\u6d78\u900f\uff0c\u8fd9\u5bb6\u4f19\u8fd8\u606c\u4e0d\u77e5\u803b\u7684\u8981\u6367\u5979\u7684\u8138\u988a\u6765\u543b\uff0c\u5510\u5a67\u5a01\u80c1\u7684\u54ac\u4f4f\u4ed6\u7684\u5634\u5507\u3002</p><p>\u3000\u3000\u5f20\u606a\u5374\u662f\u4e0d\u7ba1\uff0c\u820c\u5c16\u52aa\u529b\u7684\u5243\u5f00\u5979\u7684\u94f6\u7259\uff0c\u542e\u5438\u5ae9\u6ed1\u7684\u9999\u820c\u3002\u4eb2\u543b\u4e86\u7247\u523b\uff0c\u5510\u5a67\u53ea\u89c9\u5f97\u8981\u7a92\u606f\uff0c\u522b\u8fc7\u8138\u53bb\uff0c\u4fa7\u988a\u8d34\u5728\u5f20\u606a\u7684\u8138\u4e0a\uff0c\u8033\u8fb9\u5374\u7ed9\u5475\u4e86\u4e00\u53e3\u6c14\uff0c\u6e29\u70ed\u7684\u6c14\u606f\u4ece\u8033\u9053\u76f4\u5439\u4e86\u8fdb\u53bb\uff0c\u611f\u89c9\u5230\u4ed6\u7684\u820c\u5c16\u50cf\u7c98\u6ed1\u7684\u5c0f\u86c7\u7f20\u4e0a\u6765\uff0c\u9165\u9165\u9ebb\u9ebb\u7684\u611f\u89c9\u4ece\u5fc3\u91cc\u76f4\u6cdb\u51fa\u6765\uff0c\u4e0b\u8eab\u6162\u6162\u5347\u817e\u8d77\u70ed\u6d8c\uff0c\u4e30\u7fd8\u7389\u6ed1\u7684\u8089\u81c0\u7ed9\u5f20\u606a\u7684\u7ea4\u957f\u624b\u6307\u63c9\u5f04\u7740\uff0c\u4e00\u9635\u9635\u7684\u5feb\u611f\u6d8c\u8fc7\u6765\u3002</p><p>\u3000\u3000\u89c1\u5f20\u606a\u7684\u624b\u8981\u6252\u81ea\u5df1\u7684\u5185\u88e4\uff0c\u5510\u5a67\u62c5\u5fe7\u7684\u770b\u4e86\u770b\u623f\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8131\uff0c\u5983\u84c9\u4f1a\u8fdb\u6765\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u611f\u53d7\u7740\u5510\u5a67\u4e30\u7fd8\u96ea\u767d\u7f8e\u81c0\u7684\u8089\u611f\u4e0e\u5f39\u6027\uff0c\u6ca1\u6709\u8131\u5979\u7684\u5185\u88e4\uff0c\u5374\u5c06\u5185\u88e4\u7684\u88c6\u90e8\u62e8\u5230\u4e00\u8fb9\uff0c\u53c8\u4e00\u624b\u5361\u7740\u5979\u7684\u7ea4\u8170\uff0c\u8ba9\u5979\u6162\u6162\u5750\u4e0b\u53bb\uff1a\u201c\u8fd9\u6837\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u6e05\u6668\u7f20\u7ef5\u6781\u81f4\uff0c\u9b13\u98de\u88d9\u7ffb\uff0c\u5510\u5a67\u8102\u7389\u9999\u6ed1\u7684\u5a07\u8eaf\u9189\u9152\u4f3c\u7684\u6f6e\u7ea2\uff0c\u65e0\u529b\u7684\u762b\u8f6f\u5728\u90a3\u91cc\uff0c\u5f20\u606a\u4e5f\u60f3\u7545\u5feb\u7684\u503e\u6cc4\u5982\u6ce8\uff0c\u624b\u673a\u5374\u4e0d\u77e5\u60c5\u8da3\u7684\u8fd9\u65f6\u5019\u54cd\u8d77\u6765\u3002</p><p>\u3000\u3000\u542c\u7740\u624b\u673a\u94c3\u58f0\uff0c\u5510\u5a67\u5c31\u50cf\u82cf\u9192\u8fc7\u6765\u4f3c\u7684\u8737\u8d77\u8eab\u6765\uff0c\u4ece\u5f20\u606a\u7684\u8eab\u4e0a\u6323\u624e\u8d77\u6765\uff0c\u5c06\u88ab\u5355\u88f9\u4f4f\u9ad8\u6f6e\u4e4b\u540e\u6781\u81f4\u7f8e\u611f\u7684\u5a07\u8eaf\uff0c\u4e0d\u518d\u7ba1\u5f20\u606a\u7684\u6b7b\u6d3b\uff0c\u7b11\u7740\u8ba9\u4ed6\u63a5\u7535\u8bdd\u3002</p><p>\u3000\u3000\u5f20\u606a\u90c1\u95f7\u7684\u8981\u5410\u8840\uff0c\u4e0b\u4f53\u9752\u7b4b\u6012\u6da8\uff0c\u8fd8\u521a\u521a\u5c06\u96e8\u8863\u7a7f\u4e0a\uff0c\u8fd9\u7535\u8bdd\u4e5f\u592a\u4e0d\u662f\u65f6\u673a\u4e86\uff0c\u8981\u5c06\u5510\u5a67\u6293\u8fc7\u6765\u4e00\u8fb9\u8e42\u8e8f\u4e00\u8fb9\u63a5\u7535\u8bdd\uff0c\u5510\u5a67\u5374\u88f9\u7740\u88ab\u5355\u5f00\u95e8\u6e9c\u4e86\u51fa\u53bb\u3002\u770b\u7740\u9648\u5983\u84c9\u7684\u8eab\u5f71\u521a\u4ece\u623f\u95f4\u91cc\u8d70\u5230\u8fc7\u9053\u4e0a\uff0c\u5f20\u606a\u4e5f\u4e0d\u80fd\u8d64\u6761\u6761\u7684\u8ffd\u51fa\u53bb\uff0c\u8fd8\u8981\u8d76\u7d27\u8fc7\u53bb\u5c06\u623f\u95e8\u63a9\u4e0a\uff0c\u770b\u522b\u4eba\u8d70\u5149\u662f\u79cd\u4e50\u8da3\uff0c\u7ed9\u522b\u4eba\u770b\u8d70\u5149\u2026\u2026\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\u2014\u2014\u5f20\u606a\u5148\u5c06\u7535\u8bdd\u63a5\u8d77\u6765\u3002</p><p>\u3000\u3000\u662f\u9648\u4fe1\u751f\u7684\u7535\u8bdd\uff0c\u4ed6\u521a\u521a\u63a5\u5230\u901a\u77e5\uff0c\u5c31\u5728\u51cc\u6668\uff0c\u4e5f\u662f\u7f8e\u56fd\u7684\u4e0b\u5348\u65f6\u5206\uff0c\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u540c\u610f\u64a4\u6d88\u5bf9\u9526\u6e56\u7684\u6307\u63a7\u7b7e\u7f72\u5ead\u5916\u548c\u89e3\u534f\u8bae\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\uff0c\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u5728\u8d39\u5409\u5c3c\u4e9a\u5dde\u4e1c\u533a\u7684\u8054\u90a6\u5730\u533a\u6cd5\u9662\u63a7\u544a\u9526\u6e56\u7684iplayer\u4e1a\u52a1\u56e0\u4e3a\u6545\u610f\u4e0d\u4e25\u8c28\u7684\u52a0\u5bc6\u6280\u672f\u4fc3\u4f7f\u5317\u7f8e\u5730\u533a\u7684iplayer\u7528\u6237\u5927\u91cf\u4e0b\u8f7d\u514d\u8d39\u97f3\u4e50\u4fb5\u72af\u5176\u77e5\u8bc6\u7248\u6743\uff0c\u8bf7\u6c42\u8054\u90a6\u5730\u533a\u6cd5\u9662\u7981\u6b62\u9526\u6e56\u5728\u7f8e\u56fd\u7684iplayer\u4e1a\u52a1\uff0c\u5e76\u63d0\u51fa\u9ad8\u8fbe5370\u4e07\u7f8e\u5143\u7684\u9ad8\u989d\u8865\u507f\u3002</p><p>\u3000\u3000\u8d39\u5409\u5c3c\u4e9a\u5dde\u4e1c\u533a\u8054\u90a6\u5730\u533a\u6cd5\u9662\u4e00\u5ba1\u5224\u9526\u6e56\u8d25\u8bc9\uff0c\u4f46\u662f\u8fd9\u79cd\u5b98\u53f8\u8ddf\u592a\u6781\u62f3\u4f3c\u7684\uff0c\u53ea\u6709\u8981\u8db3\u591f\u7684\u7cbe\u529b\u4e0e\u8d22\u529b\uff0c\u53ef\u4ee5\u6253\u597d\u51e0\u6765\u56de\uff0c\u4e00\u5ba1\u5224\u8d1f\u6839\u672c\u5c31\u4e0d\u7b97\u4ec0\u4e48\uff0c\u9526\u6e56\u7ee7\u7eed\u901a\u8fc7\u5317\u7f8e\u516c\u53f8\u8058\u8bf7\u5317\u7f8e\u5730\u533a\u77e5\u540d\u7684\u5f8b\u5e08\u4e8b\u52a1\u6240\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7ea0\u7f20\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u534a\u5e74\u591a\u65f6\u95f4\uff0c\u4ec5\u5f8b\u5e08\u8d39\u5c31\u7528\u6389\u4e24\u767e\u591a\u4e07\u7f8e\u5143\u2014\u2014\u4e0d\u8fc7\u8fd9\u4e8c\u767e\u4e07\u7f8e\u5143\u7684\u5f8b\u5e08\u8d39\u7528\u5f97\u4e5f\u662f\u76f8\u5f53\u7684\u503c\u5f97\u3002</p><p>\u3000\u3000\u5728\u4e2d\u56fd\u53c2\u52a0\u5173\u8d38\u603b\u534f\u5b9a\u524d\u671f\uff0c\u8d54\u507f\u989d\u5982\u6b64\u4e4b\u9ad8\u7684\u7248\u6743\u5b98\u53f8\uff0c\u5728\u7f8e\u56fd\u4ee5\u53ca\u56fd\u5185\u90fd\u5f15\u8d77\u5a92\u4f53\u6781\u5927\u7684\u5173\u6ce8\uff0c\u52a0\u4e0a\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u5bf9\u7231\u8fbeiplayer\u4ea7\u54c1\u6240\u8ffd\u7a76\u7684\u77e5\u8bc6\u7248\u6743\u7ea0\u7eb7\u6070\u6070\u662f\u4f20\u7edf\u97f3\u50cf\u5355\u54cd\u5382\u5546\u4e0e\u4e92\u8054\u7f51\u5185\u5bb9\u5546\u4e4b\u95f4\u7684\u77db\u76fe\u7126\u70b9\u2014\u2014\u8fd9\u8d77\u5b98\u53f8\u7ed9\u5a92\u4f53\u7092\u4f5c\u7684\u70ed\u5ea6\u8981\u8fdc\u8fdc\u8d85\u8fc7\u53bb\u5e38\u4eba\u7684\u60f3\u8c61\u3002</p><p>\u3000\u3000\u5728\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u8d77\u8bc9\u4e4b\u524d\uff0c\u9526\u6e56\u5728\u5317\u7f8e\u5730\u533a\u7684iplayer\u6708\u51fa\u8d27\u91cf\u624d\u56db\u4e07\u4ef6\uff0c\u534a\u5e74\u65f6\u95f4\uff0c\u6025\u5267\u589e\u6da8\u5230\u5341\u4e09\u4e07\u4ef6\uff0c\u5e76\u4f7fiplayer\u5728\u5317\u7f8e\u5730\u533a\u7684\u603b\u9500\u552e\u91cf\u8d85\u8fc7md\u3002</p><p>\u3000\u3000\u8bf4\u5b9e\u8bdd\uff0c\u5f20\u606a\u6068\u4e0d\u5f97\u62b1\u4f4f\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u4e3b\u5e2d\u90a3\u5149\u6e9c\u6e9c\u7684\u79c3\u8111\u95e8\u72e0\u72e0\u7684\u4eb2\u4e0a\u51e0\u53e3\uff0c\u8fd9\u573a\u82b1\u8d39\u4e24\u767e\u591a\u4e07\u5f8b\u5e08\u8d39\u7684\u5b98\u53f8\uff0c\u5176\u5546\u4e1a\u4ef7\u503c\u62b5\u5f97\u4e0a\u4e00\u4ebf\u7f8e\u5143\u7684\u5e7f\u544a\u6295\u5165\u4e86\u2014\u2014iplayer\u5728\u5317\u7f8e\u5730\u533a\u8d85\u8d8aMD\u4e4b\u540e\uff0c\u4e0b\u4e00\u4e2a\u76ee\u6807\u5c31\u662fwalkman\u4e86\u3002</p><p>\u3000\u3000\u666e\u901a\u7684\u5e7f\u544a\u5f88\u96be\u5728\u77ed\u65f6\u95f4\u91cc\u8bf4\u670d\u5317\u7f8e\u6d88\u8d39\u8005\u4fe1\u4efb\u6765\u81ea\u4e2d\u56fd\u7684\u9ad8\u7aef\u7535\u5b50\u4ea7\u54c1\u2014\u2014\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7d27\u54ac\u4e0d\u653e\uff0c\u800c\u4e14\u8fd9\u5b98\u53f8\u6253\u8d77\u6765\u5bf9\u5317\u7f8e\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u4e5f\u8d8a\u6765\u8d8a\u4e0d\u5229\uff0c\u5728\u5317\u7f8e\u5730\u533a\u7684\u6d88\u8d39\u8005\u5fc3\u76ee\u4e2d\u9010\u6e10\u5efa\u7acb\u8d77\u6765\u81ea\u4e2d\u56fd\u7684\u7535\u5b50\u4f01\u4e1a\u4e5f\u662f\u503c\u5f97\u4fe1\u4efb\u7684\u6df1\u523b\u5370\u8c61\u3002</p><p>\u3000\u3000\u9526\u6e56\u53ef\u4ee5\u8bf4\u662f\u5728\u7f8e\u56fd\u4e00\u6218\u6210\u540d\u3002</p><p>\u3000\u3000\u4ece\u516d\u6708\u4e4b\u540e\uff0c\u5317\u7f8e\u5730\u533a\u5411\u9526\u6e56\u5546\u4e8b\u6bcf\u4e2a\u6708\u90fd\u8981\u8f93\u9001\u4e00\u5343\u4e07\u7f8e\u5143\u4ee5\u4e0a\u7684\u51c0\u5229\uff0c\u52a0\u4e0a\u6d77\u5916\u5176\u4ed6\u5730\u533a\u7684\u76c8\u5229\uff0c\u7b97\u662f\u6682\u65f6\u7684\u5c06\u603b\u8d1f\u503a\u9ad8\u8fbe\u5341\u516b\u4ebf\u7f8e\u5143\u7684\u9526\u6e56\u5546\u4e8b\u8d22\u52a1\u7ed9\u652f\u6491\u4f4f\u4e86\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u5b98\u53f8\u4e00\u5929\u4e0d\u4e86\u7ed3\uff0ciplayer\u5728\u5317\u7f8e\u5e02\u573a\u7684\u5408\u6cd5\u5730\u4f4d\u5c31\u4e00\u65e5\u5f97\u4e0d\u5230\u6700\u540e\u7684\u7a33\u56fa\uff0c\u4e5f\u4f7f\u7ecf\u9500\u5546\u591a\u6709\u7591\u8651\uff0c\u4e0d\u613f\u8ddf\u9526\u6e56\u5f00\u5c55\u66f4\u5f7b\u5e95\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u5f20\u606a\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u542c\u9648\u4fe1\u751f\u4f20\u9012\u7684\u8fd9\u4e2a\u597d\u6d88\u606f\uff0c\u7ed9\u6253\u65ad\u7684\u6028\u6c14\u5c31\u5f31\u4e86\u4e00\u4e9b\uff0c\u7b11\u7740\u8bf4\uff0c\u201c\u4ed6\u4eec\u4e5f\u610f\u8bc6\u7ee7\u7eed\u8ddf\u6211\u4eec\u7ea0\u7f20\u4e0b\u53bb\u5f97\u4e0d\u5230\u4ec0\u4e48\u597d\u5904\uff0c\u5ead\u5916\u548c\u89e3\u5c31\u5ead\u5916\u548c\u89e3\u2026\u2026\u201d</p><p>\u3000\u3000\u4e00\u65e6\u7b7e\u7f72\u6700\u7ec8\u7684\u548c\u89e3\u534f\u4f1a\uff0ciplayer\u4e1a\u52a1\u5728\u5317\u7f8e\u5730\u533a\u7684\u53d1\u5c55\u5c31\u6ca1\u6709\u4ec0\u4e48\u540e\u987e\u4e4b\u5fe7\uff0c\u5f53\u7136\uff0c\u9526\u6e56\u4e5f\u8981\u5bf9\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7b49\u4f20\u7edf\u97f3\u50cf\u97f3\u54cd\u5382\u5546\u505a\u51fa\u4e00\u4e9b\u8ba9\u6b65\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6700\u7ec8\u8fbe\u6210\u5bf9\u53cc\u65b9\u90fd\u6709\u4f53\u9762\u53f0\u9636\u53ef\u4e0b\u7684\u548c\u89e3\u534f\u8bae\uff0c\u5c06\u8fdb\u4e00\u6b65\u6269\u5927\u9526\u6e56\u5728\u5317\u7f8e\u5730\u533a\u7684\u5f71\u54cd\uff0c\u4e5f\u5c06\u8fdb\u4e00\u6b65\u7a33\u56fa\u9526\u6e56\u5728\u5317\u7f8e\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u201c\u8fbe\u6210\u548c\u89e3\u534f\u8bae\u4e4b\u540e\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u5f00\u59cb\u5728\u56fd\u5185\u9500\u552eiplayer\u4e86\u2026\u2026\u201d\u9648\u4fe1\u751f\u5728\u7535\u8bdd\u90a3\u5934\u8bf4\u9053\u3002</p><p>\u3000\u3000\u773c\u4e0b\u6700\u4fbf\u5b9c\u7684\u4e00\u6b3eiplayer\u5728\u6d77\u5916\u5e02\u573a\u7684\u552e\u4ef7\u4e5f\u5728\u4e24\u767e\u7f8e\u5143\u4ee5\u4e0a\uff0c\u7d22\u5c3c\u5728\u4e2d\u56fd\u9500\u552e\u7684walkman\u5e02\u573a\u552e\u4ef7\u90fd\u5728\u4e00\u767e\u7f8e\u5143\u5de6\u53f3\uff0c\u6574\u4f53\u5e02\u573a\u76f8\u5bf9\u72ed\u5c0f\uff1b\u771f\u6b63\u5927\u91cf\u5145\u585e\u56fd\u5185\u5e02\u573a\u7684\uff0c\u591a\u4e3a\u56fd\u5185\u7535\u5b50\u5382\u6240\u751f\u4ea7\u7684\u968f\u8eab\u542c\uff0c\u8d28\u91cf\u597d\u4e00\u4e9b\u7684\uff0c\u591a\u5728\u4e00\u4e24\u767e\u4eba\u6c11\u5e01\uff0c\u5dee\u4e00\u70b9\u7684\uff0c\u53ea\u9700\u8981\u4e09\u56db\u5341\u4eba\u6c11\u5e01\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u4e2d\u56fd\u4e5f\u5b58\u5728\u7740\u4e0d\u5bb9\u5c0f\u89c6\u7684\u9ad8\u7aef\u5e02\u573a\u2014\u2014\u5c31\u50cf\u9526\u6e56\u552e\u4ef7\u9ad8\u8fbe\u516b\u5343\u591a\u5143\u7684d188\u53cc\u5c4f\u624b\u673a\u80fd\u6210\u4e3a\u6392\u540d\u7b2c\u4e09\u7684\u70ed\u9500\u673a\u578b\uff0ciplayer\u5373\u4f7f\u4e0d\u964d\u4ef7\u5728\u56fd\u5185\u4e5f\u6709\u76f8\u5f53\u7684\u5e02\u573a\u5bb9\u91cf\u2014\u2014\u6253\u52ab\u5bcc\u4eba\uff0c\u5411\u6765\u662f\u5f20\u606a\u4e50\u610f\u5e72\u7684\u4e8b\u60c5\uff0c\u638c\u63e1iplayer\u6838\u5fc3\u77e5\u8bc6\u4ea7\u6743\u7684\u9526\u6e56\u5b8c\u5168\u6ca1\u6709\u4e3b\u52a8\u964d\u4ef7\u7684\u5fc5\u8981\u3002</p><p>\u3000\u3000\u53ea\u662f\u56fd\u5185\u7684\u7248\u6743\u73af\u5883\uff0c\u76d7\u7248\u97f3\u4e50\u7f51\u7ad9\u5df2\u7ecf\u5f00\u59cb\u6cdb\u6ee5\uff0c\u9526\u6e56\u60f3\u8981\u8ba9\u56fd\u5185\u7684iplayer\u7528\u6237\u638f\u94b1\u4e0b\u8f7d\u6b63\u7248\u97f3\u4e50\u65e0\u7591\u662f\u81ea\u5bfb\u6b7b\u8def\u3002</p><p>\u3000\u3000\u5728\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u7684\u5b98\u53f8\u4e86\u7ed3\u4e4b\u524d\uff0c\u9526\u6e56\u5c31\u5728\u56fd\u5185\u9500\u552e\u7684iplayer\u4e0a\u653e\u5f00\u9632\u76d7\u7248\u7684\u52a0\u5bc6\u9650\u5236\uff0c\u52bf\u5fc5\u4f1a\u7ed9\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u6293\u4f4f\u8fd9\u70b9\u653b\u51fb\uff0c\u867d\u7136\u4e0d\u80fd\u7b97\u5b9e\u8d28\u6027\u7684\u8bc1\u636e\uff0c\u4fbf\u4e5f\u8db3\u4ee5\u8ba9\u7f8e\u56fd\u7684\u8206\u8bba\u4e0e\u6cd5\u5b98\u90fd\u503e\u5411\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\uff0c\u6240\u4ee5\u9526\u6e56\u624d\u4e00\u76f4\u62d6\u7740\u6ca1\u6709\u5728\u56fd\u5185\u9500\u552eiplayer\u3002</p><p>\u3000\u3000\u5982\u4eca\u80fd\u591f\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u8fbe\u6210\u5ead\u5916\u548c\u89e3\u534f\u8bae\uff0c\u800c\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u6700\u591a\u53ea\u80fd\u5728\u5404\u89e3\u534f\u8bae\u4e66\u4e0a\u8981\u6c42\u9526\u6e56\u5317\u7f8e\u5730\u533a\u9500\u552e\u7684iplayer\u4e0a\u589e\u5f3a\u9632\u76d7\u7248\u6743\u7684\u52a0\u5bc6\u6280\u672f\uff0c\u7f8e\u56fd\u7684\u5b98\u53f8\u4e86\u7ed3\u4e4b\u540e\uff0c\u9526\u6e56\u5c31\u4e0d\u6015\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u8dd1\u5230\u4e2d\u56fd\u6765\u6253\u7248\u6743\u5b98\u53f8\u4e86\u3002</p><p>\u3000\u3000\u5f53\u7136\u4e86\uff0c\u9526\u6e56\u63d0\u4f9b\u7ed9\u7ecf\u9500\u5546\u7684\u4e5f\u662f\u52a0\u5bc6\u9632\u76d7\u673a\u578b\uff0c\u89e3\u51b3\u7684\u5de5\u4f5c\u5b8c\u5168\u53ef\u4ee5\u7531\u7ecf\u9500\u5546\u6765\u505a\u3002</p><p>\u3000\u3000\u4e0e\u9648\u4fe1\u751f\u901a\u4e86\u534a\u5929\u7535\u8bdd\uff0c\u5f53\u4e2d\u542c\u89c1\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u51fa\u95e8\u53bb\u4e86\uff0c\u6253\u7535\u8bdd\u7ed9\u5510\u5a67\u60f3\u5c06\u5979\u9a97\u56de\u6765\u7ee7\u7eed\u597d\u4e8b\uff0c\u5510\u5a67\u53ea\u8bf4\u8fc7\u4e00\u4f1a\u513f\u8ddf\u9648\u5983\u84c9\u4e00\u8d77\u5c06\u65e9\u9910\u5e2e\u4ed6\u4e70\u56de\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u65e0\u5948\u7684\u5230\u6d74\u5ba4\u6ce1\u6fa1\uff0c\u8eba\u5230\u6d74\u7f38\u91cc\uff0c\u7ed9\u70ed\u6c34\u4e00\u6ce1\uff0c\u4e0b\u9762\u53c8\u76f4\u8038\u8038\u7684\u7acb\u8d77\u6765\u6297\u8bae\uff0c\u5f20\u606a\u53ea\u80fd\u6545\u4f5c\u65e0\u89c6\uff0c\u770b\u592e\u89c6\u7684\u65e9\u95f4\u65b0\u95fb\u4eca\u5929\u6709\u4ec0\u4e48\u4e0d\u90a3\u4e48\u4e4f\u5473\u7684\u5185\u5bb9\uff0c\u542c\u89c1\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u56de\u6765\uff0c\u6216\u8bb8\u662f\u4e24\u4eba\u5230\u5367\u5ba4\u5e2e\u7740\u6536\u62fe\u623f\u95f4\uff0c\u6ca1\u8fc7\u4e00\u4f1a\u513f\u5c31\u542c\u89c1\u9648\u5983\u84c9\u201c\u554a\u201d\u7684\u5c16\u53eb\u4e00\u8d77\uff0c\u63a5\u7740\u5c31\u662f\u4eba\u4f53\u649e\u5230\u5730\u677f\u4e0a\u7684\u949d\u58f0\u3002</p><p>\u3000\u3000\u5f20\u606a\u6293\u8d77\u6d74\u5dfe\u56f4\u4f4f\u4e0b\u8eab\u8dd1\u51fa\u53bb\u770b\uff0c\u53ea\u89c1\u4e24\u59ae\u5b50\u5750\u5728\u5730\u677f\u4e0a\u800c\u7b11\uff0c\u6c14\u6c1b\u53c8\u5c34\u5c2c\u53c8\u66a7\u6627\uff0c\u4ed6\u5947\u602a\u7684\u95ee\u9053\uff1a\u201c\u6454\u4e86\u4e00\u8de4\u6709\u4ec0\u4e48\u597d\u7b11\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e2a\u6b7b\u732a\u5934\uff0c\u5c31\u77e5\u9053\u4e71\u6254\u5783\u573e\uff01\u201d\u5510\u5a67\u626c\u624b\u4e00\u7529\uff0c\u5f20\u606a\u8eb2\u4e86\u4e00\u4e0b\uff0c\u5dee\u70b9\u7ed9\u5957\u5957\u6254\u5230\u8138\u4e0a\uff0c\u539f\u6765\u9648\u5983\u84c9\u8e29\u5230\u8fd9\u4e2a\u6ed1\u5012\u4e86\u3002</p></article>"}}}]);
