(window.webpackJsonp=window.webpackJsonp||[]).push([[1054],{1077:function(p){p.exports={chapterTitle:null,title:"\u7b2c857\u7ae0 \u4e1c\u5c71\u5c9b",content:"<article><h2>\u7b2c857\u7ae0 \u4e1c\u5c71\u5c9b</h2><p>\u3000\u30006\u670831\u65e5\uff0c\u9999\u6e2f\u4ee5\u4e1c\u6d0b\u9762\u6709\u53f0\u98ce\u8fc7\u5883\uff0c\u9999\u6e2f\u53ea\u662f\u7ed9\u53f0\u98ce\u7684\u4fa7\u7ffc\u626b\u8fc7\uff0c\u4f46\u4e5f\u662f\u5168\u5929\u66b4\u96e8\u5982\u6ce8\u3002</p><p>\u3000\u3000\u660e\u5929\u5c31\u662f\u9999\u6e2f\u56de\u5f52\u4e00\u5468\u5e74\uff0c\u66fe\u5982\u5723\u526f\u603b\u7406\u4eca\u5929\u62b5\u8fbe\u672a\u53d7\u8fc7\u5883\u53f0\u98ce\u5f71\u54cd\u7684\u6df1\u5733\uff0c\u7136\u540e\u4ece\u6df1\u5733\u4e58\u4e13\u8f66\u62b5\u8fbe\u9999\u6e2f\uff0c\u51c6\u5907\u53c2\u52a0\u660e\u5929\u7684\u56de\u5f52\u4e00\u5468\u5e74\u5e86\u5178\u6d3b\u52a8\u3002\u603b\u53c2\u8c0b\u957f\u5728\u5185\u7684\u591a\u540d\u4e2d\u592e\u9ad8\u5c42\u9886\u5bfc\u660e\u5929\u5c06\u968f\u540c\u8d75\u6d4e\u4e1c\u603b\u7406\u4e00\u540c\u62b5\u8fbe\u9999\u6e2f\uff0c\u636e\u8bf4\u8d75\u6d4e\u4e1c\u603b\u7406\u4e5f\u4ec5\u4ec5\u662f\u77ed\u6682\u7684\u9017\u7559\u5c31\u4f1a\u79bb\u5f00\u9999\u6e2f\u524d\u5f80\u6cf0\u56fd\u8fdb\u884c\u56fd\u4e8b\u8bbf\u95ee\uff1b\u5728\u66fe\u5982\u5723\u526f\u603b\u7406\u4e4b\u524d\u5c31\u5148\u671f\u62b5\u8fbe\u7684\u8fd8\u6709\u56fd\u52a1\u9662\u529e\u526f\u4e3b\u4efb\u7b49\u591a\u540d\u4e2d\u592e\u9ad8\u7ea7\u5b98\u5458\u3002</p><p>\u3000\u3000\u8fd9\u4e9b\u6d3b\u52a8\uff0c\u961a\u552f\u6d9b\u4f5c\u4e3a\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u6839\u672c\u6ca1\u6709\u8d44\u683c\u53bb\u53c2\u4e00\u817f\uff0c\u5012\u662f\u5b59\u5c1a\u4e49\u4f5c\u4e3a\u4e2d\u6676\u5fae\u82af\u7684\u8463\u4e8b\u957f\u8fd8\u6709\u90ed\u677e\u5ef6\u7b49\u4eba\u4f5c\u4e3a\u534e\u4eba\u534e\u4fa8\u4ee3\u8868\u5e94\u9080\u5217\u5e2d\u660e\u5929\u665a\u4e0a\u7684\u5e86\u795d\u665a\u4f1a\u3002</p><p>\u3000\u3000\u770b\u7740\u7a97\u5916\u767d\u832b\u832b\u7684\u96e8\u67f1\uff0c\u4f3c\u4e4e\u80fd\u542c\u5230\u4e0d\u8fdc\u7684\u6d77\u5824\u7ed9\u5927\u6d6a\u62cd\u6253\u7684\u6012\u543c\u58f0\uff0c\u961a\u552f\u6d9b\u5fe7\u5fc3\u5fe1\u5fe1\uff0c\u4ed6\u77e5\u9053\u5f20\u606a\u4e0a\u5348\u90fd\u6ca1\u6709\u79bb\u5f00\u534a\u5c9b\u9152\u5e97\uff0c\u4f46\u662f\u6709\u91cd\u8981\u8bbf\u5ba2\u5728\uff0c\u4ed6\u4e5f\u4e0d\u80fd\u786c\u8981\u6c42\u4eba\u5bb6\u89c1\u81ea\u5df1\u3002</p><p>\u3000\u3000\u961a\u552f\u6d9b\u6709\u4e9b\u4f1a\u60f3\uff1a\u66fe\u5982\u5723\u526f\u603b\u7406\u63d0\u524d\u4e00\u5929\u5230\u9999\u6e2f\u6765\u53c2\u52a0\u56de\u5f52\u7eaa\u5ff5\u5e86\u5178\uff0c\u4f1a\u4e0d\u4f1a\u4e0e\u5f20\u606a\u89c1\u9762\uff1f\u4e24\u53ef\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u6216\u8bb8\u901a\u8fc7\u65c1\u4eba\u7ef4\u6301\u8054\u7cfb\u662f\u4e2d\u592e\u9ad8\u5c42\u773c\u4e0b\u5bf9\u9526\u6e56\u7684\u7edf\u4e00\u6001\u5ea6\u4e5f\u8bf4\u4e0d\u5b9a\u3002\u8fd9\u4e24\u5e74\uff0c\u56fd\u5bb6\u9f13\u52b1\u6c11\u8425\u4f01\u4e1a\u53d1\u5c55\uff0c\u4e2d\u592e\u9886\u5bfc\u5230\u6c11\u8425\u4f01\u4e1a\u53c2\u89c2\u7684\u4f8b\u5b50\u4e5f\u662f\u9c9c\u89c1\uff0c\u53ea\u4e0d\u8fc7\u5374\u6ca1\u6709\u542c\u8bf4\u8fc7\u6709\u54ea\u6709\u6bd4\u8f83\u6709\u5206\u91cf\u7684\u4e2d\u592e\u9886\u5bfc\u53bb\u9526\u6e56\u53c2\u89c2\uff0c\u5c31\u662f\u6d77\u5dde\u8fd9\u51e0\u5e74\u7ecf\u6d4e\u5de5\u4f5c\u5982\u6b64\u7684\u51fa\u8272\uff0c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u9ad8\u7ea7\u522b\u7684\u4e2d\u592e\u9886\u5bfc\u5230\u6d77\u5dde\u89c6\u5bdf\u3002</p><p>\u3000\u3000\u201c\u5f20\u606a\u660e\u5929\u8981\u56de\u5efa\u90ba\uff0c\u4eca\u5929\u4e0b\u5348\u96e8\u80fd\u505c\uff0c\u4e0b\u5348\u53ef\u80fd\u5c31\u8d70\u2026\u2026\u201d\u9676\u4e66\u827a\u6025\u51b2\u51b2\u7684\u8d70\u8fdb\u6765\uff0c\u7126\u6025\u7684\u8ddf\u961a\u552f\u6d9b\u8bf4\u9053\uff0c\u4ed6\u4eec\u90fd\u6e05\u695a\uff0c\u552f\u6709\u5f20\u606a\u80fd\u51b3\u5b9a\u8ba9\u65b0\u4ead\u53c2\u4e0e\u8fd9\u6b21\u7279\u6b8a\u7684\u62db\u5546\u5f15\u8d44\uff0c\u6ca1\u6709\u6c9f\u901a\u7684\u673a\u4f1a\uff0c\u5c82\u4e0d\u662f\u8bf4\u4e00\u70b9\u620f\u90fd\u6ca1\u6709\u4e86\uff1f</p><p>\u3000\u3000\u201c\u4f60\u662f\u542c\u8c01\u8bf4\u7684\uff1f\u201d\u961a\u552f\u6d9b\u4e5f\u6709\u4e9b\u7126\u6025\uff0c\u4ed6\u76f8\u4fe1\u51fa\u4e8e\u793c\u8282\uff0c\u8981\u6ca1\u6709\u7d27\u6025\u4e8b\u52a1\uff0c\u5f20\u606a\u79bb\u5f00\u9999\u6e2f\u4e4b\u524d\u4f1a\u51fa\u9762\u8ddf\u4ed6\u4eec\u8a00\u8bed\u4e00\u58f0\uff0c\u4e0d\u53ef\u80fd\u8ba9\u522b\u4eba\u8f6c\u544a\uff0c\u5728\u4ed6\u773c\u91cc\u7684\u9526\u6e56\u4e0d\u80fd\u7b97\u662f\u50b2\u6162\u7684\u4f01\u4e1a\uff0c\u867d\u7136\u6709\u50b2\u6162\u7684\u8d44\u683c\u3002</p><p>\u3000\u3000\u201c\u542c\u89c1\u9526\u6e56\u7684\u9ad8\u5c42\u95f2\u804a\uff0c\u8bf4\u5f20\u606a\u8981\u56de\u5b66\u6821\u53c2\u52a0\u671f\u672b\u8003\u8bd5\uff0c\u8fd9\u4e9b\u5929\u8fd8\u62bd\u51fa\u65f6\u95f4\u590d\u4e60\u529f\u8bfe\u6765\u7740\uff0c\u201d\u9676\u4e66\u827a\u8bf4\u9053\uff0c\u201c\u6211\u6253\u7535\u8bdd\u56de\u53bb\u95ee\u4e86\u4e00\u4e0b\uff0c\u4e1c\u5927\u4e00\u5e74\u7ea7\u7684\u671f\u672b\u8003\u8bd5\u4ece\u660e\u5929\u4e0b\u5348\u5f00\u59cb\uff0c\u5c82\u4e0d\u662f\u8bf4\u4ed6\u6700\u8fdf\u660e\u5929\u4e0a\u5348\u5c31\u8981\u4e58\u98de\u673a\u79bb\u5f00\u9999\u6e2f\uff1f\u201d\u53c8\u65e0\u5948\u7684\u8bf4\u9053\uff0c\u201c\u4ed6\u56de\u53bb\u53c2\u52a0\u4ec0\u4e48\u8003\u8bd5\u554a\uff01\u201d</p><p>\u3000\u3000\u961a\u552f\u6d9b\u4e5f\u662f\u82e6\u7b11\uff0c\u4f46\u662f\u5f20\u606a\u504f\u504f\u5c31\u662f\u4e1c\u5927\u4e00\u5e74\u7ea7\u7684\u5b66\u751f\uff0c\u504f\u504f\u8fd8\u8981\u8d76\u56de\u53bb\u5c3d\u4e9b\u5b66\u751f\u7684\u4e49\u52a1\uff0c\u4ed6\u4eec\u53c8\u80fd\u8bf4\u4e09\u9053\u56db\u4e0d\u6210\uff1f</p><p>\u3000\u3000\u8fd9\u4e9b\u5929\uff0c\u6c5f\u5317\u3001\u6c5f\u5357\u4e24\u91cd\u5929\uff0c\u961a\u552f\u6d9b\u4e5f\u662f\u53bb\u5e74\u6c5f\u5357\u7701\u5b98\u573a\u5927\u5730\u9707\u540e\u8c03\u5230\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u4efb\u4e0a\u7684\u3002\u56db\u5e74\u524d\uff0c\u65b0\u4ead\u4e0e\u6d77\u5dde\u7684\u4eba\u53e3\u89c4\u6a21\u3001\u7ecf\u6d4e\u89c4\u6a21\u3001\u5de5\u519c\u4e1a\u751f\u4ea7\u6761\u4ef6\u3001\u57ce\u5e02\u5efa\u8bbe\u7b49\u5404\u65b9\u9762\u7684\u6761\u4ef6\u90fd\u5f88\u76f8\u5f53\uff0c\u624d\u56db\u5e74\u7684\u65f6\u95f4\u8fc7\u53bb\uff0c\u65b0\u4ead\u90fd\u51e0\u4e4e\u7ed9\u6d77\u5dde\u7529\u5f97\u770b\u4e0d\u5230\u5f71\u5b50\u4e86\u3002\u8981\u8bf4\u961a\u552f\u6d9b\u4e0d\u5fc3\u7126\uff0c\u90a3\u662f\u9a97\u4eba\u7684\uff0c\u5c31\u7b97\u4e3a\u81ea\u5df1\u65e5\u540e\u7684\u4ed5\u9014\u7740\u60f3\uff0c\u8be5\u62c9\u4e0b\u8138\u8fd8\u662f\u8981\u62c9\u4e0b\u8138\u7684\u3002\u518d\u8bf4\u5982\u6b64\u7684\u9526\u6e56\uff0c\u4ed6\u4e00\u4e2a\u5e02\u59d4\u4e66\u8bb0\uff0c\u80fd\u6709\u8d44\u683c\u8ddf\u4ed6\u6446\u67b6\u5b50\u5417\uff1f</p><p>\u3000\u3000\u201c\u4f60\u4eb2\u81ea\u53bb\u627e\u5b8b\u57f9\u660e\uff0c\u6700\u597d\u80fd\u5728\u5f20\u606a\u79bb\u5f00\u9999\u6e2f\u4e4b\u524d\u80fd\u6709\u673a\u4f1a\u6c9f\u901a\u4e00\u4e0b\u3002\u201d\u961a\u552f\u6d9b\u4e5f\u4e0d\u4fbf\u5bf9\u9676\u4e66\u827a\u4e0b\u6b7b\u547d\u4ee4\uff0c\u6bd5\u7adf\u4e3b\u52a8\u6743\u638c\u63e1\u5728\u9526\u6e56\u624b\u91cc\uff0c\u8fd8\u80fd\u5f3a\u8feb\u5f20\u606a\u89c1\u81ea\u5df1\u4e0d\u6210\uff1f\u4ed6\u62ff\u8d77\u7535\u8bdd\u62e8\u5f20\u6885\u623f\u95f4\u7684\u53f7\u7801\uff0c\u6ca1\u542c\u89c1\u6709\u4eba\u63a5\uff0c\u5927\u58f0\u5c06\u9694\u58c1\u7684\u79d8\u4e66\u558a\u8fc7\u6765\uff0c\u8ba9\u4ed6\u53bb\u627e\u5f20\u6885\uff1b\u56e0\u4e3a\u7126\u6025\uff0c\u8fd9\u65f6\u5019\u5374\u627e\u4e0d\u5230\u4eba\uff0c\u5fc3\u91cc\u591a\u5c11\u6709\u4e9b\u607c\u6012\uff0c\u62b1\u6028\u4e86\u4e00\u58f0\uff1a\u201c\u8fd9\u65f6\u5019\u4e0d\u7559\u5728\u623f\u95f4\uff0c\u8dd1\u53bb\u54ea\u91cc\u4e86\uff1f\u201d\u4ed6\u4e0d\u8bb0\u5f97\u5f20\u6885\u7684\u624b\u673a\u53f7\u7801\uff0c\u4e5f\u5fd8\u8bb0\u8981\u53eb\u9676\u4e66\u827a\u6216\u8005\u79d8\u4e66\u76f4\u63a5\u6253\u5f20\u6885\u7684\u624b\u673a\u5c31\u884c\u3002</p><p>\u3000\u3000\u201c\u961a\u4e66\u8bb0\uff0c\u201d\u961a\u552f\u6d9b\u7684\u79d8\u4e66\u8fd8\u6ca1\u6709\u8d70\u51fa\u53bb\uff0c\u521a\u62c9\u5f00\u95e8\uff0c\u7a7f\u7740\u6d45\u84dd\u8272\u6392\u6263\u8fde\u8863\u88d9\u5f20\u6885\u5c31\u8981\u51b2\u8fdb\u6765\uff0c\u5c06\u961a\u552f\u6d9b\u7684\u79d8\u4e66\u5413\u4e86\u4e00\u8df3\uff0c\u4e5f\u5dee\u70b9\u7ed9\u649e\u5012\uff0c\u5f20\u6885\u4e09\u5341\u5c81\u51fa\u5934\u4e00\u4e9b\uff0c\u8eab\u6bb5\u5300\u79f0\uff0c\u80f8\u812f\u5706\u9f13\u9f13\u7684\u5c06\u6d45\u84dd\u8272\u88d9\u5e03\u6491\u8d77\u6765\uff0c\u4e30\u59ff\u7ef0\u7ea6\uff0c\u5979\u8d70\u8fdb\u6765\uff0c\u6839\u672c\u6ca1\u6709\u610f\u8bc6\u5230\u961a\u552f\u6d9b\u5bf9\u521a\u521a\u6ca1\u6709\u62e8\u5979\u623f\u95f4\u7684\u7535\u8bdd\u800c\u751f\u51fa\u4e86\u4e00\u4e9b\u607c\u6012\uff0c\u5979\u8bf4\u9053\uff0c\u201c\u9ad8\u6674\u8fd9\u59ae\u5b50\u673a\u7075\u7740\uff0c\u5927\u6e05\u65e9\u5c31\u5728\u4eba\u5bb6\u51fa\u5165\u7684\u5fc5\u7ecf\u4e4b\u8def\u4e0a\u6e9c\u8fbe\uff0c\u7ec8\u662f\u8ba9\u5979\u902e\u4f4f\u4eba\uff0c\u5f20\u606a\u8981\u9080\u8bf7\u60a8\u8ddf\u9676\u5e02\u957f\u4e00\u8d77\u5403\u4e2d\u996d\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u561b\uff1f\u201d\u961a\u552f\u6d9b\u542c\u5230\u5f20\u6885\u544a\u8bc9\u4ed6\u8fd9\u559c\u8baf\uff0c\u521a\u624d\u5fc3\u91cc\u4e00\u4e1d\u4e0d\u5feb\u987f\u65f6\u70df\u6d88\u4e91\u6563\uff0c\u90fd\u6709\u4e9b\u559c\u4e0d\u60c5\u7981\u7684\u6fc0\u52a8\uff0c\u4ed6\u4e5f\u6653\u5f97\u673a\u4f1a\u96be\u5f97\uff0c\u6293\u4f4f\u8fd9\u6b21\u673a\u4f1a\uff0c\u6574\u5ea7\u57ce\u5e02\u5c31\u8981\u5c11\u8d70\u597d\u51e0\u5e74\u7684\u5f2f\u8def\uff0c\u8fd9\u8ba9\u4ed6\u5982\u4f55\u80fd\u8f7b\u677e\u5f97\u8d77\u6765\u3002</p><p>\u3000\u3000\u9676\u4e66\u827a\u770b\u4e86\u770b\u8155\u8868\uff0c\u8fd9\u4f1a\u513f\u5df2\u7ecf\u5feb\u5341\u4e00\u70b9\u949f\uff0c\u95ee\u5f20\u6885\uff1a\u201c\u6709\u8bf4\u4e2d\u9910\u4ec0\u4e48\u65f6\u5019\u5f00\u59cb\uff1f\u201d\u8fd9\u4e5f\u6d89\u53ca\u5230\u65f6\u95f4\u957f\u77ed\u7684\u95ee\u9898\uff0c\u6ca1\u6709\u5145\u5206\u7684\u65f6\u95f4\u8fdb\u884c\u6c9f\u901a\uff0c\u4f3c\u4e4e\u4e5f\u4e0d\u80fd\u7279\u522b\u5982\u610f\u554a\u3002</p><p>\u3000\u3000\u8fd9\u4f1a\u513f\u8fc7\u9053\u91cc\u53c8\u4f20\u6765\u6c99\u6c99\u7684\u811a\u6b65\u58f0\uff0c\u5f20\u6885\u8fd8\u7ad9\u5728\u95e8\u53e3\uff0c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u671d\u95e8\u5916\u8bf4\u9053\uff1a\u201c\u7fdf\u52a9\u7406\uff0c\u9ad8\u6674\u8fd9\u59ae\u5b50\u600e\u4e48\u5c06\u4f60\u7ed9\u8bf7\u8fc7\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u961a\u552f\u6d9b\u5f80\u95e8\u53e3\u8d70\u4e86\u4e24\u6b65\uff0c\u770b\u89c1\u5f20\u606a\u660e\u8273\u59a9\u5a9a\u7684\u52a9\u7406\u7fdf\u4e39\u9752\u4e0e\u9ad8\u6674\u51fa\u73b0\u5728\u95e8\u53e3\u3002\u7fdf\u4e39\u9752\u76ee\u5149\u7565\u626b\u8fc7\u961a\u552f\u6d9b\u4e0e\u9676\u4e66\u827a\uff0c\u8bf4\u9053\uff1a\u201c\u606a\u5c11\u5728\u62db\u5f85\u5ba2\u4eba\uff0c\u4e0d\u80fd\u8131\u8eab\u4eb2\u81ea\u6765\u8bf7\u961a\u4e66\u8bb0\u4e0e\u9676\u5e02\u957f\uff0c\u606a\u5c11\u8fd8\u8981\u6211\u95ee\u961a\u4e66\u8bb0\u4e00\u58f0\uff0c\u4e2d\u9910\u4e4b\u524d\u65b9\u4e0d\u65b9\u4fbf\u8fc7\u53bb\u804a\u4e00\u804a\uff1f\u201d</p><p>\u3000\u3000\u961a\u552f\u6d9b\u5c31\u62c5\u5fc3\u6ca1\u6709\u6df1\u5165\u6c9f\u901a\u7684\u673a\u4f1a\uff0c\u54ea\u91cc\u4f1a\u89c9\u5f97\u6709\u4e00\u4e01\u70b9\u7684\u4e0d\u65b9\u4fbf\uff0c\u9664\u4e86\u9676\u4e66\u827a\u3001\u5f20\u6885\uff0c\u8fd8\u8ba9\u9ad8\u6674\u4e00\u8d77\u8fc7\u53bb\uff0c\u4ed6\u4e0d\u6653\u5f97\u5f20\u606a\u672c\u6765\u5c31\u8981\u89c1\u4ed6\u4eec\uff0c\u8fd8\u771f\u4ee5\u4e3a\u9ad8\u775b\u8d77\u4e86\u4f5c\u7528\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u961a\u552f\u6d9b\u3001\u9676\u4e66\u827a\u7b49\u4eba\u968f\u7fdf\u4e39\u9752\u4e58\u5750\u534a\u5c9b\u9152\u5e97\u4e3a\u9876\u5c42\u8c6a\u534e\u5957\u623f\u6240\u4e13\u7528\u7684\u7535\u68af\u62b5\u8fbe\u9876\u5c42\uff0c\u53e6\u6709\u5de5\u4f5c\u4eba\u5458\u5728\u7535\u68af\u95e8\u53e3\u7b49\u5019\uff0c\u966a\u540c\u4e00\u8d77\u8fdb\u5165\u88c5\u9970\u5f97\u91d1\u78a7\u8f89\u714c\u7684\u4f1a\u5ba2\u5385\uff0c\u4f1a\u5ba2\u5385\u91cc\u7684\u6c99\u53d1\u4e0a\u5750\u7740\uff0c\u9664\u4e86\u5f20\u606a\u3001\u53f6\u5efa\u658c\u4e4b\u5916\uff0c\u8fd8\u6709\u4e24\u540d\u964c\u751f\u7684\u4e2d\u5e74\u7537\u5b50\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f4d\u662f\u5370\u5c3c\u4e07\u4e1a\u96c6\u56e2\u8463\u4e8b\u957f\u3001\u603b\u88c1\u9648\u6587\u806a\u5148\u751f\uff0c\u8fd9\u4f4d\u662f\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u8463\u4e8b\u5c40\u4e3b\u5e2d\u9a6c\u6587\u6e20\u5148\u751f\uff0c\u6211\u521a\u521a\u8ddf\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u4e24\u4f4d\u5148\u751f\u8bf4\u8d77\u961a\u4e66\u8bb0\u60a8\uff1f\u201d\u5f20\u606a\u7ad9\u8d77\u6765\u5e2e\u961a\u552f\u6d9b\u3001\u9676\u4e66\u827a\u4ed6\u4eec\u4ecb\u7ecd\u3002</p><p>\u3000\u3000\u9a6c\u6587\u6e20\u4e0a\u5348\u8fc7\u6765\u5c31\u662f\u6b63\u5f0f\u7b54\u590d\u9a6c\u5bb6\u53ca\u5357\u6d0b\u6d77\u8fd0\u63a5\u590d\u5f20\u606a\u4e4b\u524d\u63d0\u51fa\u7684\u65b9\u6848\uff0c\u7ea6\u8c08\u5230\u4e2d\u5348\uff0c\u9648\u6587\u806a\u4ece\u6e2f\u5c9b\u8d76\u6765\u76f8\u89c1\uff0c\u5f20\u606a\u8bf4\u8981\u4ecb\u7ecd\u5185\u5730\u7684\u5b98\u5458\u7ed9\u4ed6\u4eec\u8ba4\u8bc6\uff0c\u7559\u4e0b\u6765\u4e00\u8d77\u7528\u4e2d\u9910\u3002</p><p>\u3000\u300027\u65e5\uff0c\u5f97\u5230\u6d88\u606f\u8bf4\u6d77\u5dde\u7ec4\u56e2\u5230\u9999\u6e2f\u62db\u5546\u5f15\u8d44\uff0c\u5438\u5f15\u5370\u5c3c\u534e\u4eba\u8d44\u672c\u6d41\u5411\u56fd\u5185\uff0c\u961a\u552f\u6d9b\u7d27\u6025\u4e4b\u4f59\u4e5f\u8ba9\u4eba\u6536\u96c6\u4e86\u4e9b\u5370\u5c3c\u534e\u5546\u7684\u8d44\u6599\uff0c\u77e5\u9053\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u90fd\u662f\u5370\u5c3c\u7684\u5de8\u5bcc\uff0c\u4e5f\u662f\u8fd9\u6b21\u7279\u6b8a\u62db\u5546\u5f15\u8d44\u6d3d\u8c08\u4f1a\u7684\u5370\u5c3c\u534e\u5546\u7684\u7ec4\u7ec7\u4eba\u4e4b\u4e00\uff0c\u5f20\u606a\u7adf\u7136\u4ecb\u7ecd\u4ed6\u4e0e\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u89c1\u9762\uff0c\u961a\u552f\u6d9b\u5fc3\u60c5\u6fc0\u52a8\u90fd\u6709\u4e9b\u96be\u4ee5\u81ea\u6291\u4e86\uff0c\u4e4b\u524d\u79cd\u79cd\u731c\u6d4b\u4e0e\u6028\u6c14\uff0c\u90fd\u4e00\u6d88\u800c\u51c0\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf7\u961a\u552f\u6d9b\u3001\u9676\u4e66\u827a\u5750\u4e0b\uff0c\u77a5\u4e86\u8ddf\u968f\u800c\u6765\u7684\u5f20\u6885\u3001\u9ad8\u6674\u4e00\u773c\uff0c\u89c9\u5f97\u6709\u4e9b\u5947\u602a\uff0c\u6ca1\u6709\u8bf7\u5979\u4eec\u4e5f\u5750\u4e0b\uff0c\u4e5f\u4e0d\u542d\u58f0\uff0c\u5c31\u81ea\u987e\u81ea\u7684\u5750\u4e0b\u3002</p><p>\u3000\u3000\u5f20\u6885\u3001\u9ad8\u6674\u4e5f\u77e5\u60c5\u8bc6\u8da3\u7684\u8ddf\u7740\u7fdf\u4e39\u9752\u5230\u5ba4\u5916\u7684\u9633\u53f0\u82b1\u56ed\u53c2\u89c2\uff0c\u5979\u4eec\u5728\u65b0\u4ead\u4e5f\u7b97\u662f\u89c1\u8bc6\u4e16\u9762\u7684\u4eba\uff0c\u867d\u8bf4\u65b0\u4ead\u773c\u4e0b\u8fd8\u53ea\u6709\u4e24\u5bb6\u7ed9\u6b63\u5f0f\u8bc4\u5b9a\u4e3a\u4e09\u661f\u7ea7\u7684\u9152\u5e97\uff0c\u4f46\u662f\u56fd\u5185\u6240\u8c13\u7684\u603b\u7edf\u5957\u623f\uff0c\u5f20\u6885\u5012\u662f\u6709\u673a\u4f1a\u5165\u4f4f\u8fc7\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u9152\u5e97\u91cc\u80fd\u5bb9\u5f97\u4e0b\u6709\u5982\u6b64\u7684\u5962\u534e\u5957\u623f\uff0c\u8d70\u5230\u9876\u906e\u4e0d\u9508\u94a2\u4e0e\u900f\u660e\u73bb\u7483\u6240\u5236\u906e\u96e8\u68da\u4e0b\u7684\u9732\u5929\u82b1\u56ed\uff0c\u900f\u8fc7\u73bb\u7483\u5e55\u5899\uff0c\u8fd8\u80fd\u770b\u89c1\u5ba4\u5185\u6e38\u6cf3\u6c60\u7684\u78a7\u7eff\u6c34\u6ce2\uff0c\u90fd\u6709\u4e9b\u5934\u6655\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u53bb\u7ba1\u5f20\u6885\u3001\u9ad8\u6674\u4e24\u4e2a\u5973\u4eba\u4e3a\u5957\u623f\u7684\u5962\u534e\u800c\u60c5\u8ff7\u610f\u4e71\uff0c\u8fd9\u4e2a\u4e16\u754c\u613f\u610f\u8db4\u5728\u8d22\u5bcc\u4e0e\u6743\u52bf\u811a\u4e0b\u7684\u7edd\u8272\u5973\u4eba\u591a\u4e86\uff0c\u4e5f\u4e0d\u591a\u8fd9\u4e24\u4e2a\uff0c\u4ed6\u5750\u4e0b\u6765\u8ddf\u9a6c\u6587\u6e20\u8bf4\u9053\uff1a\u201c\u521a\u521a\u6211\u7ed9\u4f60\u4ecb\u7ecd\u7684\u4e1c\u5c71\u5c9b\u5c31\u662f\u961a\u4e66\u8bb0\u7684\u7ba1\u8f96\u8303\u56f4\uff0c\u8fd8\u662f\u8bf7\u961a\u4e66\u8bb0\u7ed9\u4f60\u4e0e\u9648\u5148\u751f\u4ecb\u7ecd\u4f1a\u66f4\u51c6\u786e\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u961a\u552f\u6d9b\u7565\u89c9\u610f\u5916\uff0c\u5f20\u606a\u4e0e\u8fd9\u4e24\u4e2a\u5370\u5c3c\u5de8\u8c6a\u6ca1\u4e8b\u8ba8\u8bba\u65b0\u4ead\u7684\u4e1c\u5c71\u5c9b\u505a\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u89c1\u961a\u552f\u6d9b\u53cd\u5e94\u7a0d\u8fdf\uff0c\u5f20\u606a\u8bf4\u9053\uff1a\u201c\u6211\u8ddf\u9a6c\u5148\u751f\u3001\u9648\u5148\u751f\u4ecb\u7ecd\u8bf4\uff0c\u6c5f\u5357\u3001\u4e1c\u6d77\u4e24\u7701\uff0c\u5c0f\u6c5f\u53e3\u5357\u5317\u4e24\u4fa7\u4e00\u5343\u4e94\u767e\u591a\u516c\u91cc\u7684\u6d77\u5cb8\u7ebf\uff0c\u81f3\u4eca\u90fd\u6ca1\u6709\u4e00\u5ea7\u4e07\u5428\u7ea7\u7684\u6df1\u6c34\u6d77\u6e2f\uff0c\u8fd9\u662f\u7531\u4e8e\u5c0f\u6c5f\u4e0b\u6e38\u5730\u533a\u90fd\u662f\u6c99\u6d32\u6c89\u79ef\u5e73\u539f\u3001\u8fd1\u6d77\u6d77\u57df\u7f3a\u4e4f\u6df1\u6c34\u6c34\u9053\u6240\u81f4\uff0c\u76f4\u5230\u516b\u5341\u5e74\u4ee3\u521d\uff0c\u5728\u65b0\u4ead\u6d77\u5cb8\u5916\u53d1\u73b0\u4e86\u6761\u76f4\u901a\u592a\u5e73\u6d0b\u7684\u6df1\u6c34\u901a\u9053\uff0c\u65b0\u4ead\u7684\u4e1c\u5c71\u5c9b\u5c31\u4f4d\u4e8e\u8fd9\u6761\u6df1\u6c34\u901a\u9053\u4e0a\uff0c\u5177\u5907\u5efa\u8bbe\u6df1\u6c34\u6d77\u6e2f\u7684\u57fa\u7840\u6761\u4ef6\u2026\u2026\u6211\u7684\u8fd9\u70b9\u77e5\u8bc6\u90fd\u662f\u4ece\u79d1\u8c31\u8bfb\u7269\u4e0a\u770b\u6765\u7684\uff0c\u80af\u5b9a\u6ca1\u6709\u961a\u4e66\u8bb0\u3001\u9676\u526f\u5e02\u957f\u6765\u5f97\u719f\u6089\uff0c\u5177\u4f53\u7684\u8fd8\u8981\u8bf7\u4f60\u4eec\u6765\u4ecb\u7ecd\u3002\u201d</p><p>\u3000\u3000\u542c\u5f20\u606a\u8fd9\u4e48\u4ecb\u7ecd\u4e1c\u5c71\u5c9b\uff0c\u961a\u552f\u6d9b\u8eab\u5b50\u60c5\u4e0d\u81ea\u7981\u7684\u524d\u503e\uff0c\u624b\u6491\u5728\u56db\u8fb9\u7f15\u94dc\u5d4c\u94f6\u7684\u73bb\u7483\u8336\u51e0\u4e0a\uff0c\u53ea\u62ff\u5c41\u80a1\u5c16\u6cbe\u7740\u6c99\u53d1\uff0c\u773c\u775b\u4e4c\u6e9c\u53d1\u5149\uff0c\u96be\u6291\u5174\u594b\u4e4b\u60c5\uff1b\u4e0e\u9676\u4e66\u827a\u6362\u4e86\u773c\u8272\uff0c\u89c1\u4ed6\u773c\u91cc\u4e5f\u662f\u5982\u6b64\u3002</p><p>\u3000\u3000\u65b0\u4ead\u4e0e\u6d77\u5dde\u5730\u57df\u76f8\u90bb\uff0c\u81ea\u7136\u73af\u5883\u3001\u5730\u7406\u8d44\u6e90\u6781\u4e3a\u7c7b\u4f3c\uff0c\u5982\u4eca\u6d77\u5dde\u7ecf\u6d4e\u53d1\u5c55\u5df2\u6210\u6c14\u5019\uff0c\u5728\u6d77\u5dde\u7ecf\u6d4e\u53d1\u5c55\u9971\u548c\u4e4b\u524d\uff0c\u5f88\u96be\u60f3\u8c61\u65b0\u4ead\u5728\u62db\u5546\u5f15\u8d44\u65b9\u9762\u80fd\u7ade\u4e89\u8fc7\u6d77\u5dde\uff0c\u4f46\u662f\u65b0\u4ead\u6709\u4e00\u4e2a\u6781\u5927\u7684\u81ea\u7136\u4f18\u52bf\u662f\u6d77\u5dde\u6240\u4e0d\u5177\u5907\u7684\uff0c\u90a3\u5c31\u662f\u4e1c\u5c71\u5c9b\u6df1\u6c34\u6d77\u9053\u3002</p></article>"}}}]);
