(window.webpackJsonp=window.webpackJsonp||[]).push([[1054],{1077:function(p){p.exports={chapterTitle:null,title:"\u7b2c1051\u7ae0 \u88ab\u7325\u73a9\u7684\u548c\u89e3\u534f\u8bae",content:"<article><h2>\u7b2c1051\u7ae0 \u88ab\u7325\u73a9\u7684\u548c\u89e3\u534f\u8bae</h2><p>\u3000\u3000\u5f20\u606a\u4e5f\u633a\u5c34\u5c2c\u7684\uff0c\u9648\u5983\u84c9\u8e29\u4ec0\u4e48\u4e0d\u597d\uff0c\u504f\u8e29\u5728\u201c\u96e8\u8863\u201d\u4e0a\u6ed1\u5012\u4e86\u2014\u2014\u4e71\u4e22\u5783\u573e\u771f\u4e0d\u662f\u4ec0\u4e48\u597d\u4e60\u60ef\uff0c\u4ed6\u7f29\u56de\u6d74\u5ba4\uff0c\u91cd\u65b0\u8eba\u56de\u6e29\u70ed\u7684\u6c34\u91cc\uff0c\u8003\u8651\u8981\u4e0d\u8981\u4eb2\u81ea\u518d\u5230\u7f8e\u56fd\u53bb\u4e00\u8d9f\uff0c\u4e0d\u4ec5\u4ec5\u662f\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u64a4\u6d88\u8bc9\u8bbc\u7684\u4e8b\u60c5\uff0c\u4ece\u5fb7\u4eea\u5f15\u8d770.25/0.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u6280\u672f\u7684\u4e8b\u60c5\uff0c\u4e5f\u9700\u8981\u9002\u5f53\u7684\u63a8\u52a8\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u5f20\u606a\u7a7f\u597d\u8863\u670d\u4e0b\u697c\u6765\uff0c\u5510\u5a67\u4e0e\u9648\u5983\u84c9\u5750\u5728\u9910\u5385\u91cc\u7528\u65e9\u9910\uff0c\u4ed6\u5750\u8fc7\u6765\uff0c\u6293\u8d77\u6765\u4e00\u6839\u6cb9\u6761\u91ae\u7740\u5510\u5a67\u676f\u91cc\u7684\u8c46\u6d46\u5403\uff0c\u4e00\u8fb9\u5927\u56bc\u6cb9\u6761\uff0c\u4e00\u8fb9\u8ddf\u5510\u5a67\u3001\u9648\u5983\u84c9\u8bf4\u9053\uff1a\u201c\u522b\u6574\u5929\u8c46\u6d46\u3001\u6cb9\u6761\u7684\uff0c\u4ee5\u524d\u542c\u522b\u4eba\u8bf4\u6709\u94b1\u4eba\u5bb6\u65e9\u6668\u8d77\u6765\u90fd\u62ff\u9c7c\u7fc5\u6f31\u53e3\uff0c\u4f60\u8bf4\u6211\u4eec\u4ec0\u4e48\u65f6\u5019\u751f\u6d3b\u54c1\u4f4d\u80fd\u63d0\u9ad8\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u6251\u2026\u2026\u201d\u9648\u5983\u84c9\u8e29\u5230\u5b89\u5168\u5957\u6ed1\u5012\uff0c\u8fd8\u6709\u4e9b\u4e0d\u597d\u610f\u601d\u770b\u5f20\u606a\uff0c\u770b\u7740\u5f20\u606a\u4e0b\u697c\u6765\uff0c\u95f7\u5934\u559d\u8c46\u6d46\uff0c\u542c\u4ed6\u63d2\u8be8\u6253\u79d1\uff0c\u6ca1\u80fd\u5fcd\u4f4f\u7b11\uff0c\u5634\u91cc\u7684\u8c46\u6d46\u90fd\u55b7\u9910\u684c\u4e86\uff0c\u8fd8\u6709\u4e00\u4e9b\u76f4\u63a5\u55b7\u73bb\u7483\u676f\u91cc\uff0c\u545b\u5f97\u72e0\u72e0\u54b3\u55fd\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u8d76\u7d27\u62bd\u51fa\u9910\u5dfe\u7eb8\u5c06\u684c\u4e0a\u7684\u767b\u6d46\u6e0d\u62b9\u5e72\u51c0\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u7684\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u521a\u624d\u90a3\u4e00\u8de4\u5c41\u80a1\u74e3\u8fd8\u9690\u9690\u7684\u75bc\uff0c\u73bb\u7483\u91cc\u7684\u8c46\u6d46\u4e5f\u4e0d\u80fd\u559d\u4e86\uff0c\u6362\u4e86\u4e00\u53ea\u73bb\u7483\u676f\uff0c\u5c06\u539f\u5148\u7559\u7ed9\u5f20\u606a\u7684\u8c46\u6d46\u5012\u676f\u5b50\u91cc\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u62ff\u4f60\u7684\u9c7c\u7fc5\u6f31\u53e3\u53bb\u5427\uff0c\u8fd9\u79cd\u5c0f\u767e\u59d3\u559d\u7684\u8c46\u6d46\u53cd\u6b63\u4f60\u4e5f\u770b\u4e0a\u53bb\u2026\u2026\u201d\u7f8e\u7f8e\u7684\u559d\u4e86\u4e00\u53e3\uff0c\u53c8\u62ff\u6cb9\u6761\u5728\u8c46\u6d46\u91cc\u6405\u7740\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u4e0d\u7ba1\uff0c\u4ed6\u53cd\u6b63\u559d\u5510\u5a67\u7684\u8c46\u6d46\uff0c\u5510\u5a67\u53c8\u53bb\u4ece\u9648\u5983\u84c9\u676f\u5b50\u91cc\u518d\u5206\u4e00\u4e9b\u8c46\u6d46\u8fc7\u6765\uff0c\u4e09\u4e2a\u4eba\u5c31\u80fd\u6df7\u7740\u5c06\u65e9\u9910\u5403\u5b8c\u3002</p><p>\u3000\u3000\u5f20\u606a\u8bf4\u8d77\u8ba1\u5212\u53bb\u7f8e\u56fd\u8d70\u4e00\u8d9f\u7684\u4e8b\u60c5\uff0c\u5510\u5a67\u4e3a\u96be\u7684\u8bf4\u9053\uff1a\u201c\u6211\u5f97\u53bb\u91d1\u5c71\u966a\u6211\u5988\u51e0\u5929\uff0c\u53cd\u6b63\u6211\u62c9\u5983\u84c9\u4e00\u8d77\u8fc7\u53bb\u73a9\uff0c\u4f60\u6709\u6ca1\u6709\u7a7a\uff0c\u6211\u90fd\u65e0\u6240\u8c13\u2014\u2014\u4f60\u968f\u4fbf\u627e\u4e2a\u4eba\u966a\u4f60\u53bb\u7f8e\u56fd\u5427\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u644a\u644a\u624b\uff0c\u771f\u662f\u65e0\u8bed\u4ee5\u5bf9\u554a\uff0c\u771f\u662f\u51c4\u51c9\u554a\uff0c\u53c8\u4e0d\u80fd\u8bf4\u8bb8\u601d\u6ca1\u6709\u7a7a\uff0c\u867d\u8bf4\u5510\u5a67\u73b0\u5728\u5df2\u7ecf\u6000\u7591\u5230\u7fdf\u4e39\u9752\u4e86\uff0c\u4f46\u662f\u7fdf\u4e39\u9752\u8fdc\u5728\u73c0\u65af\uff0c\u4ece\u7f8e\u56fd\u56de\u6765\u662f\u8981\u518d\u5230\u6fb3\u6d32\u8f6c\u4e00\u5708\u7684\uff0c\u5374\u4e0d\u80fd\u62c9\u7740\u5979\u966a\u81ea\u5df1\u5230\u7f8e\u56fd\u8f6c\u4e00\u5708\uff0c\u665a\u6674\u8fd8\u6ca1\u6709\u66b4\u9732\u3002</p><p>\u3000\u3000\u6ca1\u6709\u4eba\u76f8\u4f34\uff0c\u6b64\u6b21\u7684\u7f8e\u56fd\u4e4b\u884c\u6210\u4e86\u7eaf\u7cb9\u7684\u5546\u52a1\u4e4b\u65c5\uff0c\u65e0\u804a\u4e4f\u5473\u3002</p><p>\u3000\u3000\u5230\u65e7\u91d1\u5c71\u91cc\uff0c\u8499\u4e50\u4e0e\u5e2d\u82e5\u7433\u8fd8\u6070\u5f53\u53bb\u4e86\u52a0\u62ff\u5927\u4e0d\u5728\u7f8e\u56fd\uff0c\u5f20\u606a\u5728\u65e7\u91d1\u5c71\u6ede\u7559\u4e86\u4e24\u5929\uff0c\u53c2\u52a0\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\uff08RIAA\uff09\u548c\u89e3\u534f\u8bae\u7684\u7b7e\u7f72\u4eea\u5f0f\u3002</p><p>\u3000\u3000\u6839\u636e\u548c\u89e3\u534f\u8bae\uff0c\u9526\u6e56\u5c06\u5bf9\u5728\u5317\u7f8e\u5730\u533a\u9500\u552e\u7684iplayer\u5347\u7ea7\u9632\u76d7\u7248\u7684\u52a0\u5bc6\u8f6f\u4ef6\u4ee5\u8fbe\u5230RIAA\u7684\u8981\u6c42\uff0cRIAA\u5219\u653e\u5f03\u5728\u5317\u7f8e\u5730\u533a\u7ee7\u7eed\u8d77\u8bc9\u4e0e\u8d54\u507f\u7684\u8981\u6c42\u3002</p><p>\u3000\u3000\u5728\u4e0a\u4e2a\u6708\uff0cRIAA\u8d77\u8bc9\u5317\u7f8e\u5730\u533a\u6700\u5927\u7684\u4e00\u5bb6\u514d\u8d39mp3\u97f3\u4e50\u8d44\u6e90\u7f51\u7ad9\u83b7\u80dc\uff0c\u867d\u7136\u8fd9\u5bb6\u7f51\u7ad9\u5ba3\u79f0\u662f\u975e\u76c8\u5229\u6027\u7684\uff0c\u6cd5\u9662\u8fd8\u662f\u5224\u51b3\u8fd9\u5bb6\u7f51\u7ad9\u6700\u7ec8\u8d25\u8bc9\uff0c\u9664\u4e86\u5173\u95ed\u7f51\u7ad9\u4e4b\u5916\uff0c\u8fd8\u8981\u652f\u4ed8\u5de8\u989d\u7684\u60e9\u7f5a\u6027\u8d54\u6b3e\u3002</p><p>\u3000\u3000\u5728\u5317\u7f8e\uff0c\u4e0d\u8981\u8bf4\u76c8\u5229\u6027\u8d28\u7684\uff0c\u975e\u76c8\u5229\u6027\u8d28\u7684\u76d7\u7248\u97f3\u4e50\u7f51\u7ad9\u7684\u751f\u5b58\u7a7a\u95f4\u5df2\u7ecf\u6781\u7a84\uff0c\u4ece\u800c\u7ed9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u6b64\u7c7b\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\u63d0\u4f9b\u4e86\u66f4\u5927\u7684\u5546\u4e1a\u7a7a\u95f4\uff0c\u5e02\u573a\u6f5c\u529b\u6fc0\u589e\u3002</p><p>\u3000\u3000\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4f5c\u4e3a\u5f53\u524d\u7528\u6237\u7fa4\u89c4\u6a21\u6700\u5927\u7684\u7f51\u4e0a\u97f3\u4e50\u5546\u5e97\uff0c\u83b7\u5f97\u4e3b\u8981\u4f20\u7edf\u97f3\u50cf\u3001\u97f3\u54cd\u7248\u6743\u5546\u7684\u6388\u8bb8\u3002</p><p>\u3000\u3000\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\uff0c\u9526\u6e56\u6301\u670970\uff05\u7684\u80a1\u4efd\uff0c\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u6301\u6709\u5269\u4e0b\u768430\uff05\u7684\u80a1\u4efd\uff0c\u4e09\u5bb6\u4f01\u4e1a\u4e3a\u6b64\u5171\u6295\u8d44\u8fd1\u4e5d\u5343\u4e07\u7f8e\u5143\uff0c\u8fd9\u672c\u8eab\u4e5f\u662f\u9526\u6e56\u91cd\u89c6\u4f20\u7edf\u97f3\u50cf\u97f3\u54cd\u5382\u5546\u7248\u6743\u5229\u76ca\u7684\u91cd\u8981\u4f53\u73b0\u3002</p><p>\u3000\u3000RIAA\u6216\u8bb8\u65e9\u5c31\u610f\u8bc6\u5230\u8fd9\u573a\u5b98\u53f8\u7ea0\u7f20\u4e0b\u53bb\u53ea\u4f1a\u8ba9\u9526\u6e56\u83b7\u5f97\u66f4\u5927\u7684\u5546\u4e1a\u5229\u76ca\uff0c\u4ed6\u4eec\u4e00\u5f00\u59cb\u4e5f\u53ea\u662f\u62b1\u7740\u6050\u5413\u7684\u6001\u5ea6\uff0c\u6ca1\u60f3\u5230\u9526\u6e56\u4f5c\u4e3a\u4e00\u5bb6\u5728\u4e2d\u56fd\u77ed\u671f\u5d1b\u8d77\u7684\u65b0\u5174\u4f01\u4e1a\u4f1a\u5982\u6b64\u987d\u5f3a\u7684\u9002\u5e94\u4e86\u7f8e\u56fd\u7684\u6cd5\u5f8b\u73af\u5883\u8ddf\u4ed6\u4eec\u7ea0\u7f20\u4e0b\u6765\uff0c\u4e00\u7ea0\u7f20\u8fd8\u7ea0\u7f20\u4e86\u534a\u5e74\u4e4b\u4e45\u3002</p><p>\u3000\u3000\u7b7e\u7f72\u548c\u89e3\u534f\u8bae\uff0c\u9526\u6e56\u5230\u573a\u7684\u9ad8\u5c42\u90fd\u8f6e\u6d41\u4e0a\u524d\u7ed9RIAA\u7684\u4ee3\u8868\u4ee5\u70ed\u70c8\u7684\u62e5\u62b1\uff0cRIAA\u7684\u4ee3\u8868\u4e00\u4e2a\u4e2a\u8868\u60c5\u5c34\u5c2c\u4e4b\u6781\uff0c\u611f\u89c9\u8fd9\u534a\u5e74\u65f6\u95f4\u6765\uff0c\u7ed9\u5bf9\u624b\u72e0\u72e0\u7684\u7325\u73a9\u4e86\u628a\uff0c\u7f8e\u56fd\u8bb8\u591a\u5a92\u4f53\u4e5f\u5c06\u53cc\u65b9\u4ee3\u8868\u70ed\u60c5\u62e5\u62b1\u7684\u56fe\u7247\u520a\u767b\u5728\u62a5\u7eb8\u3001\u6742\u5fd7\u7684\u663e\u8981\u4f4d\u7f6e\u3002</p><p>\u3000\u3000\u548c\u89e3\u534f\u8bae\u5bf9\u9526\u6e56\u6765\u8bf4\u5e94\u8be5\u7b97\u662f\u6b64\u6b21\u8bc9\u8bbc\u4e8b\u4ef6\u7684\u5b8c\u7f8e\u7ed3\u5c40\uff0c\u7531\u4e8eRIAA\u5bf9\u4e92\u8054\u7f51\u76d7\u7248\u97f3\u4e50\u7f51\u7ad9\u7684\u7a76\u8ffd\u731b\u6253\uff0c\u4f7f\u5f97\u4e1a\u5185\u6781\u5ea6\u770b\u597d\u4e92\u8054\u7f51\u4ed8\u8d39\u97f3\u4e50\u5e02\u573a\u7684\u524d\u666f\uff0c\u5360\u636e\u5f53\u524d\u7f51\u4e0a\u4ed8\u8d39\u97f3\u4e50\u5e02\u573a\u4e5d\u6210\u4efd\u989d\u3001\u4e5d\u4e5d\u5e74\u4e0a\u534a\u5e74\u8425\u4e1a\u6536\u5165\u8d85\u8fc7\u4e00\u5343\u4e07\u7f8e\u5143\u7684\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u5728\u65b0\u79d1\u6280\u6d6a\u6f6e\u6c79\u6d8c\u7684\u4e5d\u4e5d\u5e74\u516b\u6708\uff0c\u5e02\u573a\u4ef7\u503c\u81ea\u7136\u662f\u6781\u5ea6\u9ad8\u6da8\u3002</p><p>\u3000\u3000\u6210\u529f\u5c06\u6606\u817e\u5728\u7ebf\u80a1\u7968\u63a8\u4e0a\u7f8e\u56fd\u7eb3\u65af\u8fbe\u514b\u5e02\u573a\u516c\u5f00\u4ea4\u6613\u7684\u7f8e\u56fd\u6295\u884c\u9ad8\u76db\u516c\u53f8\u81ea\u7136\u4e0d\u4f1a\u770b\u4e0d\u5230\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u4ef7\u503c\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u7b49\u5230\u62b5\u8fbe\u65e7\u91d1\u5c71\uff0c\u9ad8\u76db\u516c\u53f8\u4e9a\u6d32\u526f\u603b\u88c1\u66fe\u5b50\u8f89\u5c31\u4ece\u9999\u6e2f\u8ffd\u5230\u65e7\u91d1\u5c71\u6765\uff0c\u5e0c\u671b\u9526\u6e56\u80fd\u5c06\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u4e0a\u5e02\u64cd\u4f5c\u8fd8\u4ea4\u7ed9\u9ad8\u76db\u516c\u53f8\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u4e5f\u662f\u8d76\u5728\u9526\u6e56\u4e0eRIAA\u7b7e\u7f72\u548c\u89e3\u534f\u8bae\u3001\u5f7b\u5e95\u89e3\u51b3\u6cd5\u5f8b\u95ee\u9898\u4e4b\u540e\u624d\u9732\u9762\u7684\u2014\u2014\u9ad8\u76db\u516c\u53f8\u662f\u4e0d\u53ef\u80fd\u63d0\u524d\u7ad9\u51fa\u6765\u652f\u6301\u9526\u6e56\u7684\u3002</p><p>\u3000\u3000\u4f4d\u4e8e\u6e14\u592b\u7801\u5934\u4e2d\u5fc3\u7684\u963f\u6839\u8bfa\u7279\u9152\u5e97\u7684\u5bb4\u4f1a\u5385\u91cc\uff0c\u8165\u7ea2\u7684\u5730\u6bef\u3001\u8165\u7ea2\u7684\u9152\u6db2\u5728\u6676\u83b9\u5254\u900f\u7684\u73bb\u7483\u91cc\u6447\u8361\uff0c\u5f20\u606a\u5b9e\u5728\u60f3\u4e0d\u5230\u660e\u767d\uff0c\u5317\u7f8e\u516c\u53f8\u62db\u8058\u7684\u5546\u52a1\u79d8\u4e66\u600e\u4e48\u6ca1\u6709\u51e0\u4e2a\u8010\u770b\u7684\uff0c\u8fd8\u662f\u8bf4\u81ea\u5df1\u5bf9\u897f\u65b9\u5973\u4eba\u5ba1\u7f8e\u6807\u51c6\u90fd\u505c\u7559\u5728\u82cf\u83f2\u739b\u7d22\u3001\u6cfd\u5854\u743c\u65af\u4e4b\u4e0a\u3002</p><p>\u3000\u3000\u201c\u9664\u4e86\u4f20\u7edf\u7684\u4e92\u8054\u7f51\u7f51\u7ad9\u6536\u5165\u6a21\u5f0f\u4e4b\u5916\uff0c\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u6709\u7740\u975e\u5e38\u826f\u597d\u7684\u7ebf\u4e0b\u786c\u4ef6\u5e73\u53f0\uff0c\u968f\u7740iplayer\u786c\u4ef6\u63a8\u5e7f\uff0c\u7f51\u7ad9\u7684\u97f3\u4e50\u9500\u552e\u4e1a\u52a1\u6a21\u5f0f\u5c06\u8d8a\u6765\u8d8a\u6210\u719f\uff0c\u89c4\u6a21\u4e5f\u5c06\u8d8a\u6765\u8d8a\u53ef\u89c2\uff0c\u7f8e\u56fd\u5546\u4e1a\u673a\u6784\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u8bc4\u4ef7\u76f8\u5f53\u9ad8\uff0c\u6b64\u65f6\u7b79\u5907\u4e0a\u5e02\uff0c\u5c06\u4e3a\u9526\u6e56\u8d62\u6765\u6bd4\u6606\u817e\u5728\u7ebf\u66f4\u5927\u7684\u5229\u76ca\u2026\u2026\u201d\u66fe\u5b50\u8f89\u5728\u5f20\u606a\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u9762\u524d\u4e0d\u61c8\u4f59\u529b\u7684\u9f13\u52a8\u7740\uff0c\u6606\u817e\u5728\u7ebf\u4e0a\u7684\u80a1\u4ef7\u5df2\u7ecf\u4e0a\u5347\u5230\u6bcf\u80a1\u5341\u56db\u7f8e\u5143\uff0c\u76f8\u6bd4\u8f83\u53d1\u884c\u4ef7\uff0c\u589e\u6da8\u63a5\u8fd1\u4e94\u500d\uff0c\u6bd4\u8f83\u8d77\u9ad8\u76db\u516c\u53f8\u65d7\u4e0b\u7684\u98ce\u6295\u6301\u80a1\u4ef7\uff0c\u589e\u6da8\u5e45\u5ea6\u66f4\u52a0\u6050\u6016\u7684\uff0c\u5f53\u7136\u4e86\uff0c\u9ad8\u76db\u7684\u83b7\u5f97\u5e45\u5ea6\u90fd\u8fdc\u8fdc\u6bd4\u4e0d\u4e0a\u6606\u817e\u5728\u7ebf\u7684\u6700\u521d\u6295\u8d44\u8005\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u2014\u2014\u9526\u6e56\u662f\u6a61\u6811\u56ed\u521b\u6295\u57fa\u91d1\u80cc\u540e\u7684\u5de8\u4eba\uff0c\u66fe\u5b50\u8f89\u5df2\u7ecf\u6e05\u695a\u4e86\u3002</p><p>\u3000\u3000\u6b64\u65f6\u7b79\u5907\u4e0a\u5e02\uff0c\u80fd\u6210\u529f\u4e0a\u5e02\u5e94\u8be5\u662f\u660e\u5e74\u6625\u5b63\u4e4b\u540e\u7684\u4e8b\u60c5\uff0c\u518d\u52a0\u4e0a\u81f3\u5c11\u516d\u4e2a\u6708\u7684\u7981\u9650\u671f\uff0c\u90a3\u5c31\u5230\u4e86\u660e\u5e74\u79cb\u51ac\u5b63\u8282\u4e86\u3002\u5f20\u606a\u624d\u4e0d\u4fe1\u5168\u7403\u8d44\u672c\u5e02\u573a\u4e0a\u7684\u65b0\u79d1\u6280\u6ce1\u6cab\u80fd\u575a\u6301\u5230\u660e\u5929\u79cb\u5b63\u4e0d\u7834\u706d\u3002\u6ce1\u6cab\u7834\u706d\u4e86\uff0c\u81f3\u5c11\u9700\u8981\u4e09\u5230\u56db\u5e74\u7684\u6062\u590d\u671f\uff0c\u65e2\u7136\u65e0\u6cd5\u4ece\u8fd9\u4e00\u6ce2\u7684\u65b0\u79d1\u6280\u6d6a\u6f6e\u4e2d\u83b7\u5229\uff0c\u5f20\u606a\u5bf9\u4e0a\u5e02\u5c31\u5f88\u7f3a\u4e4f\u5174\u8da3\uff0c\u518d\u8bf4\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u5e74\u521d\u4ece\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u83b7\u5f97\u516d\u5343\u4e07\u7f8e\u5143\u7684\u6ce8\u8d44\uff0c\u4e5f\u4e0d\u8db3\u8d44\u91d1\u3002</p><p>\u3000\u3000\u7f3a\u8d44\u91d1\u7684\u662f\u9526\u6e56\u5546\u4e8b\uff0c\u867d\u7136\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u63a7\u80a1\uff0c\u5374\u4e5f\u4e0d\u80fd\u5c06\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u540d\u4e0b\u7684\u8d44\u91d1\u8f6c\u7ed9\u9526\u6e56\u5546\u4e8b\u4f7f\u7528\uff0c\u770b\u7740\u66fe\u5b50\u8f89\u4e00\u8138\u7684\u70ed\u60c5\uff0c\u5f20\u606a\u6320\u4e86\u6320\u8111\u95e8\uff0c\u8bf4\u9053\uff1a\u201c\u7f51\u7ad9\u53c8\u4e0d\u7f3a\u8d44\u91d1\uff0c\u6ce8\u8d44\u4e0a\u5e02\uff0c\u610f\u4e49\u4f3c\u4e4e\u4e0d\u5927\u2026\u2026\u5373\u4f7f\u6211\u4eec\u6240\u6301\u7684\u80a1\u7968\u80fd\u5728\u80a1\u5e02\u5957\u73b0\uff0c\u90a3\u4e5f\u8981\u7b49\u5230\u660e\u5e74\u79cb\u5929\u4e4b\u540e\uff0c\u6211\u8fd9\u4eba\u6bd4\u8f83\u6025\u529f\u5229\uff0c\u5bf9\u8fdc\u6c34\u6551\u4e0d\u4e86\u8fd1\u706b\u7684\u4e8b\u60c5\u6ca1\u6709\u592a\u5927\u7684\u5174\u8da3\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u53e5\u8bdd\u4e4d\u542c\u662f\u62d2\u7edd\u4ed6\u7684\u4e0a\u5e02\u6e38\u8bf4\uff0c\u66fe\u5b50\u8f89\u96be\u514d\u6709\u4e9b\u5931\u671b\uff0c\u8fd9\u4e9b\u5e74\u90fd\u6ca1\u6709\u542c\u8bf4\u8fc7\u54ea\u5bb6\u516c\u53f8\u4f1a\u62d2\u7edd\u9ad8\u76db\u516c\u53f8\u7684\u4e0a\u5e02\u9080\u8bf7\uff0c\u9664\u975e\u662f\u7ed9\u6469\u6839\u8fd9\u4e9b\u7ade\u4e89\u5bf9\u624b\u6316\u8d70\uff0c\u96be\u9053\u8bf4\u9526\u6e56\u5728\u9ad8\u76db\u4e4b\u5916\u627e\u5230\u66f4\u597d\u7684\u4e0a\u5e02\u5408\u4f5c\u8005\u4e86\uff1f</p><p>\u3000\u3000\u665a\u5bb4\u4e2d\u9014\uff0c\u66fe\u5b50\u8f89\u5c31\u544a\u8f9e\u79bb\u5f00\u963f\u6839\u8bfa\u7279\u9152\u5e97\uff0c\u51c6\u5907\u4e58\u665a\u95f4\u7684\u98de\u673a\u98de\u5f80\u9ad8\u76db\u516c\u53f8\u5728\u7ebd\u7ea6\u534e\u5c14\u8857\u7684\u603b\u90e8\uff0c\u5f53\u4ed6\u8e0f\u4e0a\u98de\u673a\u7684\u90a3\u4e00\u523b\uff0c\u7a81\u7136\u60f3\u5230\u5f20\u606a\u7684\u8bdd\u91cc\u4e5f\u6ca1\u6709\u5b8c\u5168\u62d2\u7edd\u7684\u610f\u601d\u2014\u2014</p><p>\u3000\u3000\u8fdc\u6c34\u6551\u4e0d\u4e86\u8fd1\u706b\uff0c\u4ed6\u8bdd\u91cc\u7684\u610f\u601d\u662f\u9526\u6e56\u73b0\u5728\u8fd8\u662f\u7f3a\u4e4f\u8d44\u91d1\u7684\uff1f\u82e5\u662f\u6709\u98ce\u6295\u57fa\u91d1\u613f\u610f\u76f4\u63a5\u4ece\u9526\u6e56\u624b\u91cc\u63a5\u624b\u4e00\u90e8\u5206\u80a1\u6743\uff0c\u800c\u4e0d\u662f\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u76f4\u63a5\u6ce8\u8d44\u6301\u80a1\uff0c\u8fd9\u4e2a\u65b9\u6848\u8bf4\u4e0d\u5b9a\u80fd\u884c\uff1f</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u6447\u5934\u82e6\u7b11\uff0c\u4ed6\u7684\u7ae5\u5e74\u5728\u9999\u6e2f\u6e21\u8fc7\uff0c\u7136\u540e\u5168\u5bb6\u79fb\u6c11\u7f8e\u56fd\uff0c\u65e9\u5c31\u9002\u5e94\u4e86\u6b27\u7f8e\u7684\u5546\u4e1a\u73af\u5883\u4e0e\u601d\u7ef4\uff0c\u6ca1\u60f3\u5230\u5f20\u606a\u8c8c\u4f3c\u62d2\u7edd\u7684\u8bdd\u91cc\u8fd8\u85cf\u7740\u4e00\u9053\u5f2f\uff0c\u867d\u7136\u4e5f\u4e0d\u80fd\u786e\u5b9a\u771f\u5b9e\u60c5\u51b5\u662f\u4e0d\u662f\u4e0e\u81ea\u5df1\u731c\u6d4b\u7684\u4e00\u6837\uff0c\u4f46\u662f\u8fd9\u4e48\u5927\u7684\u4e00\u7b14\u4e1a\u52a1\u90fd\u503c\u5f97\u4ed6\u5c06\u8fd9\u8d9f\u822a\u73ed\u9519\u8fc7\u53bb\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53ef\u60dc\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u53c8\u5306\u5306\u4e0b\u4e86\u98de\u673a\uff0c\u4ece\u673a\u573a\u518d\u6b21\u8fd4\u56de\u963f\u6839\u8bfa\u7279\u9152\u5e97\uff0c\u9152\u5e97\u91cc\u7684\u5bb4\u4f1a\u8fd8\u6ca1\u6709\u7ed3\u675f\uff0c\u89c1\u5f20\u606a\u3001\u9648\u4fe1\u751f\u7b49\u4eba\u5bf9\u81ea\u5df1\u7684\u53bb\u800c\u590d\u8fd4\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u60ca\u8bb6\uff0c\u66fe\u5b50\u8f89\u5c31\u77e5\u9053\u731c\u6d4b\u4e0d\u5047\uff0c\u53ea\u662f\u5fae\u5fae\u4e00\u7b11\uff0c\u5927\u5bb6\u53c8\u805a\u5230\u53e6\u5916\u7684\u623f\u95f4\u91cc\u5546\u8bae\u4e00\u4e9b\u53ef\u884c\u6027\u7684\u7ec6\u8282\u3002</p><p>\u3000\u3000\u9526\u6e56\u60f3\u8981\u957f\u4e45\u7684\u4fdd\u6301\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u7edd\u5bf9\u63a7\u80a1\u662f\u884c\u4e0d\u901a\u7684\uff0c\u9664\u4e86\u5916\u90e8\u7684\u538b\u529b\uff0c\u6301\u6709\u5168\u7403\u97f3\u4e50\u5728\u7ebf20\uff05\u80a1\u6743\u7684\u4e1c\u829d\u4e5f\u4e00\u76f4\u5e0c\u671b\u9526\u6e56\u80fd\u653e\u5f03\u7edd\u5bf9\u63a7\u80a1\u6743\uff0c\u4f7f\u5f97\u4e1c\u829d\u7684iplayer\u786c\u4ef6\u9500\u552e\u80fd\u83b7\u5f97\u66f4\u516c\u5e73\u7684\u673a\u4f1a\u3002</p><p>\u3000\u3000\u8003\u8651\u5230\u65af\u9ad8\u67cf\u4e0e\u9526\u6e56\u7684\u4eb2\u5bc6\u5173\u7cfb\uff0c\u9526\u6e56\u5b9e\u9645\u53ea\u8981\u4fdd\u630140\uff05\u7684\u80a1\u6743\uff0c\u5c31\u80fd\u591f\u6bd4\u8f83\u7a33\u5b9a\u7684\u5c06\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u638c\u63a7\u5728\u624b\u91cc\uff0c\u4e5f\u5c31\u662f\u8bf4\uff0c\u6709\u6700\u591a30\uff05\u7684\u80a1\u6743\u53ef\u4ee5\u8f6c\u8ba9\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5e74\u521d\u65f6\uff0c\u4e1c\u829d\u4e0e\u65af\u9ad8\u67cf\u4e3a\u4e86\u83b7\u5f97\u5168\u7403\u97f3\u4e50\u5728\u7ebf30\uff05\u7684\u80a1\u6743\u5171\u652f\u4ed8\u516d\u5343\u4e07\u7f8e\u5143\u7684\u73b0\u91d1\uff0c\u5f20\u606a\u8fd9\u65f6\u5019\u5c31\u6446\u51fa\u8fd9\u6837\u7684\u59ff\u6001\uff1a\u9526\u6e56\u6700\u591a\u53ef\u4ee5\u76f4\u63a5\u8f6c\u8ba930\uff05\u7684\u80a1\u6743\uff0c\u5173\u952e\u8981\u770b\u9ad8\u76db\u516c\u53f8\u540d\u4e0b\u6216\u9ad8\u76db\u516c\u53f8\u7275\u7ebf\u642d\u6865\u7684\u98ce\u6295\u613f\u610f\u5f00\u51fa\u600e\u6837\u7684\u4ef7\u94b1\u4e86\u3002</p><p>\u3000\u3000\u66fe\u5b50\u8f89\u8fd8\u662f\u5f53\u591c\u5c31\u98de\u5f80\u7ebd\u7ea6\uff0c\u534e\u5c14\u8857\u624d\u662f\u98ce\u6295\u624e\u5806\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u4e0e\u5176\u8bf4\u66fe\u5b50\u8f89\u7684\u6548\u7387\u5f88\u9ad8\uff0c\u8fd8\u4e0d\u5982\u8bf4\u9ad8\u76db\u516c\u53f8\u5728\u534e\u5c14\u8857\u3001\u5728\u7f8e\u56fd\u7684\u5f71\u54cd\u529b\u5927\u5f97\u60ca\u4eba\uff0c\u5f20\u606a\u5728\u65e7\u91d1\u5c71\u7684\u7b2c\u4e09\u5929\uff0c\u66fe\u5b50\u8f89\u5c31\u4f20\u56de\u6d88\u606f\u8bf4\u4e9a\u9a6c\u900a\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u80a1\u6743\u8f6c\u8ba9\u5f88\u611f\u5174\u8da3\uff0c\u751a\u81f3\u5e0c\u671b\u80fd\u5168\u76d8\u63a5\u53d7\u9526\u6e56\u624b\u91cc70\uff05\u7684\u80a1\u4efd\uff0c\u5e76\u5f00\u51fa\u4e24\u4ebf\u7f8e\u5143\u7684\u8bd5\u63a2\u6027\u62a5\u4ef7\u3002</p><p>\u3000\u3000\u65b0\u79d1\u6280\u6d6a\u6f6e\u4e2d\uff0c\u4e9a\u9a6c\u900a\u5927\u6982\u662f\u53d7\u76ca\u6700\u5927\u7684\u65b0\u79d1\u6280\u516c\u53f8\u4e4b\u4e00\u5427\uff0c\u6bd4\u8d77\u4e9a\u9a6c\u900a\u7684\u5546\u4e1a\u5947\u8ff9\uff0c\u6b64\u65f6\u7684\u9526\u6e56\u81f3\u5c11\u5728\u8868\u9762\u4e0a\u770b\u6765\u8fd8\u7a0d\u900a\u4e00\u7b79\u3002</p><p>\u3000\u3000\u4e5d\u4e94\u5e74\u4e03\u6708\u4e2d\u65ec\u8d1d\u7d22\u65af\u4ee5\u7236\u6bcd\u501f\u7ed9\u4ed6\u768430\u4e07\u7f8e\u5143\u4e3a\u672c\uff0c\u5728\u897f\u96c5\u56fe\u90ca\u533a\u79df\u4e0b\u7684\u4f4f\u5904\u5efa\u7acb\u540d\u4e3a\u4e9a\u9a6c\u900a\u7684\u7f51\u8def\u4e66\u5e97\uff0c\u4e5d\u4e03\u5e74\u4e0a\u5e02\uff0c\u56db\u5e74\u540e\u7684\u4eca\u5929\uff0c\u4e9a\u9a6c\u900a\u5df2\u7ecf\u6210\u957f\u4e3a\u7f8e\u56fd\u6700\u5927\u7684\u4e92\u8054\u7f51\u96f6\u552e\u5546\uff0c\u5728\u4e92\u8054\u7f51\u4e0a\u4e3a\u4e00\u5343\u4e00\u767e\u591a\u4e07\u7528\u6237\u63d0\u4f9b\u56fe\u4e66\u3001\u97f3\u50cf\u3001\u8f6f\u4ef6\u3001\u73a9\u5177\u793c\u54c1\u7b49\u5546\u54c1\uff0c\u5e02\u503c\u8d85\u8fc7\u4e00\u767e\u4ebf\u7f8e\u5143\u3002\u4e9a\u9a6c\u900a\u5728\u4e0a\u5e02\u4e00\u5e74\u4e4b\u540e\uff0c\u4e3a\u4e86\u4fdd\u6301\u5317\u7f8e\u7f51\u4e0a\u96f6\u552e\u5546\u7684\u5730\u4f4d\uff0c\u5bf9\u5176\u4ed6\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u5f00\u59cb\u5927\u89c4\u6a21\u7684\u5e76\u8d2d\u3002</p><p>\u3000\u3000\u7f51\u4e0a\u4ed8\u8d39\u97f3\u4e50\u4e0b\u8f7d\u9500\u552e\u4f5c\u4e3a\u7535\u5b50\u5546\u52a1\u7684\u91cd\u8981\u5206\u652f\u8d8a\u6765\u8d8a\u5f97\u5230\u5e02\u573a\u7684\u8ba4\u53ef\uff0c\u800c\u4e14\u9526\u6e56\u6240\u521b\u9020\u7684\u4e92\u8054\u7f51\u8d44\u6e90\u4e0e\u786c\u4ef6\u5e73\u53f0\u76f8\u7ed3\u5408\u7684\u5546\u4e1a\u6a21\u5f0f\u5e02\u573a\u6f5c\u529b\u4e0d\u5bb9\u5c0f\u89c6\uff0c\u6a21\u5f0f\u53c8\u5f88\u96be\u6a21\u4eff\uff0c\u4e9a\u9a6c\u900a\u60f3\u4fdd\u6301\u5317\u7f8e\u6700\u5927\u7f51\u4e0a\u96f6\u552e\u5546\u7684\u5730\u4f4d\uff0c\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u751f\u51fa\u89ca\u89ce\u4e4b\u5fc3\u90a3\u662f\u518d\u6b63\u5e38\u4e0d\u8fc7\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u3001\u9648\u4fe1\u751f\u4ed6\u4eec\u542c\u5230\u4e9a\u9a6c\u900a\u7531\u66fe\u5b50\u8f89\u8f6c\u8ff0\u51fa\u6765\u7684\u6536\u8d2d\u610f\u56fe\uff0c\u8003\u8651\u90fd\u6ca1\u6709\u8003\u8651\uff0c\u5c31\u76f4\u63a5\u62d2\u7edd\u6389\uff0c\u6ca1\u6709\u4e0e\u4e9a\u9a6c\u900a\u8fdb\u4e00\u6b65\u63a5\u89e6\u7684\u610f\u613f\u3002</p><p>\u3000\u3000\u867d\u7136\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u5728\u672a\u6765\u5341\u5e74\u5185\u81ea\u8eab\u6240\u80fd\u521b\u9020\u7684\u5229\u6da6\u6781\u4e3a\u6709\u9650\uff0c\u4f46\u662f\u5176\u5bf9iplayer\u786c\u4ef6\u5728\u7248\u6743\u4fdd\u62a4\u4e25\u8c28\u7684\u6b27\u7f8e\u65e5\u97e9\u5730\u533a\u9500\u552e\u7684\u5de8\u5927\u4fc3\u8fdb\u4f5c\u7528\u662f\u4e0d\u5bb9\u5ffd\u89c6\u7684\uff0c\u9526\u6e56\u7edd\u4e0d\u53ef\u80fd\u653e\u5f03\u5b9e\u8d28\u6027\u7684\u63a7\u5236\u6743\u2014\u2014\u8981\u662f\u9526\u6e56\u613f\u610f\u653e\u5f03\u63a7\u5236\u6743\uff0c\u4e1c\u829d\u7edd\u5bf9\u4e0d\u4f1a\u541d\u556c\u8fd9\u4e24\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u9664\u4e86\u4e9a\u9a6c\u900a\u4e4b\u5916\uff0c\u4e4b\u524d\u6700\u65e9\u8fdb\u5165\u6606\u817e\u5728\u7ebf\u3001\u6b64\u65f6\u8d26\u9762\u76c8\u5229\u5de8\u5927\u7684\u534e\u767b\u7cfb\u98ce\u6295\u57fa\u91d1\u5bf9\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u7684\u80a1\u6743\u4e5f\u53d1\u751f\u6d53\u539a\u7684\u5174\u8da3\uff0c\u8fd9\u4e9b\u98ce\u6295\u7eaf\u7cb9\u662f\u60f3\u6295\u673a\u83b7\u5229\uff0c\u4e0d\u4f1a\u53bb\u725f\u6c42\u63a7\u80a1\u6743\uff0c\u4e3a\u4e86\u63a7\u5236\u98ce\u9669\uff0c\u751a\u81f3\u4e0d\u4f1a\u6301\u6709\u592a\u591a\u7684\u80a1\u4efd\uff0c\u4e5f\u6ca1\u6709\u901a\u8fc7\u66fe\u5b50\u8f89\uff0c\u800c\u9ad8\u76db\u516c\u53f8\u4f20\u51fa\u98ce\u58f0\u4e4b\u540e\uff0c\u5c31\u76f4\u63a5\u6d3e\u51fa\u4ee3\u8868\u5230\u65e7\u91d1\u5c71\u6765\u4e0e\u8fd9\u8fb9\u63a5\u89e6\uff0c\u4ed6\u4eec\u7684\u52a8\u4f5c\u8fc5\u901f\uff0c\u4e5f\u5f00\u51fa\u56db\u5343\u4e07\u7f8e\u5143\u636210\uff05\u80a1\u6743\u7684\u6761\u4ef6\u3002\u52a8\u4f5c\u5982\u6b64\u4e4b\u8fc5\u901f\uff0c\u8bf4\u660e\u4ed6\u4eec\u4e00\u76f4\u90fd\u5728\u76ef\u7740\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u3002</p><p>\u3000\u3000\u6bd4\u8d77\u5e74\u524d\u4e1c\u829d\u3001\u65af\u9ad8\u67cf\u7684\u6ce8\u8d44\u6301\u80a1\u4ef7\u7801\uff0c\u534e\u767b\u516c\u53f8\u65d7\u4e0b\u7684\u98ce\u6295\u5f00\u4ef7\u8db3\u8db3\u63d0\u9ad8\u4e86\u4e00\u500d\uff1a\u4f46\u662f\u76f8\u6bd4\u8f83\u53bb\u5e74\uff0c\u7eb3\u65af\u514b\u8fbe\u6307\u6570\u5728\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u91cc\u4e5f\u8db3\u8db3\u63d0\u9ad8\u4e86\u4e00\u500d\uff0c\u50cf\u4e9a\u9a6c\u900a\u8fd9\u7c7b\u7684\u7535\u5b50\u5546\u52a1\u7f51\u7ad9\u7684\u80a1\u4ef7\u589e\u6da8\u5e45\u5ea6\u90fd\u8fdc\u8fdc\u8d85\u8fc7\u4e00\u500d\u2014\u2014\u6606\u817e\u5728\u7ebf\u4e0a\u5e02\u4ea4\u6613\u8fd8\u4e0d\u8d85\u8fc7\u4e09\u4e2a\u6708\u7684\u65f6\u95f4\uff0c\u5e02\u503c\u5c31\u8d85\u8fc710\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u9ad8\u76db\u516c\u53f8\u65d7\u4e0b\u7684\u98ce\u6295\u8fd9\u6b21\u53cd\u800c\u80fd\u6c89\u4f4f\u6c14\uff0c\u5373\u4f7f\u66fe\u5b50\u8f89\u5728\u80cc\u540e\u7275\u7ebf\u642d\u6865\uff0c\u9ad8\u76db\u65d7\u4e0b\u7684\u98ce\u6295\u90fd\u6ca1\u6709\u6025\u7740\u51fa\u9762\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u65b0\u79d1\u6280\u6d6a\u6f6e\u80fd\u6301\u7eed\u660e\u5929\u79cb\u540e\u4e0d\u7834\u706d\u6781\u6ca1\u6709\u4fe1\u5fc3\uff0c\u4f46\u662f\u6709\u4fe1\u5fc3\u80fd\u575a\u6301\u5230\u660e\u5e74\uff0c\u8fd8\u6709\u5f88\u5bbd\u88d5\u7684\u65f6\u95f4\u8fdb\u884c\u80a1\u6743\u8f6c\u8ba9\u7684\u8c08\u5224\uff0c\u5c06\u76f8\u5173\u4e8b\u52a1\u90fd\u59d4\u6258\u7ed9\u5317\u7f8e\u516c\u53f8\uff0c\u4ed6\u4e0e\u9648\u4fe1\u751f\u3001\u67f3\u5fd7\u6210\u7b49\u4eba\u98de\u5f80\u8fbe\u62c9\u65af\u4e0e\u5fb7\u4eea\u8fdb\u884c0.25/0.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u6280\u672f\u8f6c\u8ba9\u6700\u540e\u9636\u6bb5\u7684\u8c08\u5224\u3002</p><p>\u3000\u3000\u5fb7\u4eea\u8fd9\u8fb9\u662f\u8001\u670b\u53cb\u5fb7\u5c14\u6cd5\u897f\u51fa\u9762\u63a5\u5f85\uff0c\u5fb7\u4eea\u4e2d\u56fd\u533a\u603b\u88c1\u5468\u957f\u9752\u81ea\u7136\u4e5f\u8981\u4ece\u5317\u4eac\u98de\u56de\u8fbe\u62c9\u65af\u53c2\u52a0\u6700\u540e\u9636\u6bb5\u7684\u8c08\u5224\u3002</p><p>\u3000\u3000\u516b\u6708\u4e0b\u65ec\u7684\u8fbe\u62c9\u65af\u6bd4\u5efa\u90ba\u7684\u5929\u6c14\u8fd8\u8981\u6e29\u6da6\u5f97\u591a\uff0c\u53ea\u662f\u5546\u52a1\u4e4b\u65c5\u7a0d\u663e\u67af\u71e5\u4e4f\u5473\u4e86\u4e9b\u3002</p><p>\u3000\u3000\u5fb7\u5c14\u6cd5\u897f\u771f\u8981\u7b97\u9526\u6e56\u7684\u8001\u670b\u53cb\u4e86\uff0c\u9648\u4fe1\u751f\u5728\u5fb7\u4eea\u5de5\u4f5c\u65f6\uff0c\u957f\u671f\u90fd\u662f\u5fb7\u5c14\u6cd5\u897f\u7684\u624b\u4e0b\u5f97\u529b\u5e72\u5c06\uff0c\u79c1\u4eba\u60c5\u8c0a\u9887\u539a\uff0c\u53ef\u60dc\u518d\u6df1\u539a\u7684\u79c1\u4ea4\u90fd\u4e0d\u80fd\u8ba9\u5fb7\u5c14\u6cd5\u897f\u5c0f\u5c0f\u7684\u201c\u727a\u7272\u201d\u4e00\u4e0b\u5fb7\u4eea\u7684\u5229\u76ca\uff0c\u8fd8\u771f\u662f\u8ba9\u4eba\u9057\u61be\u3002</p><p>\u3000\u3000\u8981\u8bf4\u9526\u6e56\u4e4b\u5916\u8fd8\u6709\u8c01\u5728\u770b\u7740\u9526\u6e56\u5947\u8ff9\u822c\u7684\u5d1b\u8d77\uff0c\u5fb7\u5c14\u6cd5\u897f\u8981\u7b97\u4e00\u4e2a\uff0c\u9526\u6e56\u6700\u65e9\u4e0a\u5f71\u789f\u673a\u4e1a\u52a1\u65f6\uff0c\u5fb7\u4eea\u5c31\u662f\u6700\u91cd\u8981\u7684\u5408\u4f5c\u4f19\u4f34\uff0c\u8fd9\u4e9b\u5e74\u6765\uff0c\u9526\u6e56\u4e0e\u5fb7\u4eea\u5728\u8bb8\u591a\u9886\u57df\u90fd\u6709\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u5408\u8d44\u516c\u53f8\u3001\u8054\u5408\u7814\u7a76\u673a\u6784\uff0c\u751a\u81f3\u5fb7\u4eea\u8fd8\u5bf9\u4e2d\u6676\u5fae\u82af\u6301\u670910\uff05\u7684\u80a1\u4efd\u2014\u2014\u7279\u522b\u662f\u5728\u5f71\u789f\u673a\u89e3\u7801\u82af\u7247\u4e0eiplayer\u786c\u4ef6\u67b6\u6784\u4e0a\u7684\u5408\u4f5c\uff0c\u4f7f\u5f97\u4e24\u5bb6\u516c\u53f8\u90fd\u6536\u83b7\u5230\u4e30\u539a\u7684\u5229\u76ca\u3002</p><p>\u3000\u3000\u7531\u4e8e\u5168\u7403\u7535\u5b50\u5e02\u573a\u7684\u75b2\u8f6f\uff0c\u5fb7\u4eea\u4e5d\u516b\u5e74\u5168\u5e74\u5229\u6da6\u4e0b\u964d\u5230\u4e0d\u8db3\u516b\u4ebf\u7f8e\u5143\uff0c\u5176\u4e2d\u8d85\u8fc7\u4e24\u4ebf\u7f8e\u5143\u662f\u4ece\u9526\u6e56\u5173\u8054\u7684\u4e1a\u52a1\u4e2d\u4ea7\u751f\u7684\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u4e0a\u534a\u5e74\uff0c\u867d\u8bf4\u65b0\u79d1\u6280\u7ecf\u6d4e\u72c2\u70ed\u65e0\u6bd4\uff0c\u4f46\u90a3\u591a\u662f\u8d44\u672c\u5e02\u573a\u7684\u4e8b\u60c5\uff0c\u5168\u7403\u7535\u5b50\u5143\u5668\u4ef6\u5e02\u573a\u4f9d\u65e7\u75b2\u8f6f\u4e0d\u582a\uff0c\u4f46\u662f\u968f\u7740\u4e2d\u56fd\u6d88\u8d39\u7c7b\u7535\u5b50\u5e02\u573a\u7684\u8fc5\u901f\u58ee\u5927\u4ee5\u53caiplayer\u5728\u5168\u7403\u7684\u9500\u552e\u6e10\u65fa\uff0c\u5fb7\u4eea\u9884\u8ba1\u4e5d\u4e5d\u5e74\u5c06\u4ece\u4e0e\u9526\u6e56\u5173\u8fde\u4e1a\u52a1\u4ea7\u4e1a\u8d85\u8fc7\u4e09\u4ebf\u7f8e\u5143\u7684\u51c0\u5229\u3002</p><p>\u3000\u3000\u9526\u6e56\u8fd9\u4e2a\u6218\u7565\u5408\u4f5c\u4f19\u4f34\uff0c\u4e5f\u8d8a\u6765\u8d8a\u4e0d\u5bb9\u5fb7\u4eea\u9ad8\u5c42\u4eba\u58eb\u5ffd\u89c6\u4e86\u3002</p><p>\u3000\u3000\u7f8e\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u5bf9\u5185\u60c5\u5374\u591a\u6709\u4e0d\u4e86\u89e3\uff0c\u4eca\u5e74\u4e0a\u534a\u5e74\uff0c\u9526\u6e56\u4e0e\u7f8e\u56fd\u5531\u7247\u5de5\u4e1a\u534f\u4f1a\u8fd9\u573a\u8de8\u8d8a\u534a\u5e74\u65f6\u95f4\u8de8\u5ea6\u7684\u5b98\u53f8\uff0c\u4f7f\u5f97\u9526\u6e56\u5728\u5317\u7f8e\u5730\u533a\u7684\u540d\u58f0\u5927\u6da8\uff0c\u4e5f\u4f7f\u5f97\u4e00\u5c0f\u64ae\u7f8e\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u8d28\u7591\u9526\u6e56\u7535\u5b50\u6280\u672f\u5feb\u901f\u63d0\u5347\u7684\u80cc\u540e\u662f\u5fb7\u4eea\u5728\u8fdb\u884c\u6280\u672f\u8f93\u9001\uff0c\u6709\u4e9b\u5a92\u4f53\u751a\u81f3\u63d0\u8bae\u91cd\u65b0\u5ba1\u67e5\u5fb7\u4eea\u4e0e\u9526\u6e56\u7684\u4ea4\u6613\uff0c\u4f7f\u5f97\u5fb7\u4eea\u627f\u53d7\u4e86\u4e0d\u5c0f\u7684\u538b\u529b\u3002</p><p>\u3000\u3000\u538b\u529b\u8be5\u538b\u529b\uff0c\u5728\u5de8\u5927\u7684\u5546\u4e1a\u5229\u76ca\u9762\u524d\uff0c\u8fd9\u79cd\u538b\u529b\u5c31\u7b97\u4e0d\u4e86\u2014\u2014\u8fd9\u65f6\u5019\u8c01\u4e5f\u4e0d\u80fd\u5ffd\u89c6\u4e2d\u56fd\u6b63\u8fc5\u901f\u6210\u957f\u7684\u6d88\u8d39\u7535\u5b50\u5e02\u573a\uff0c\u8c01\u4e5f\u4e0d\u80fd\u5ffd\u89c6\u9526\u6e56\u5728\u4e2d\u56fd\u6d88\u8d39\u7535\u5b50\u5e02\u573a\u7684\u5730\u4f4d\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u9648\u4fe1\u751f\u3001\u67f3\u5fd7\u6210\u7b49\u4eba\u62b5\u8fbe\u8fbe\u62c9\u65af\u7684\u5f53\u591c\uff0c\u5fb7\u4eea\u9996\u5e2d\u6267\u884c\u5b98\u5b89\u5409\u4f2f\u8fd8\u662f\u5728\u4ed6\u7684\u79c1\u5b85\u91cc\u7279\u610f\u7ed9\u5f20\u606a\u4e3e\u529e\u7684\u79c1\u5bb4\u4ee5\u5c3d\u6b22\u8fce\u4e4b\u60c5\u3002</p><p>\u3000\u3000\u5bbe\u670b\u5c3d\u6b22\u4e4b\u4f59\uff0c\u5b89\u5409\u4f2f\u3001\u5fb7\u5c14\u6cd5\u897f\u4e5f\u8981\u611f\u6982\u9526\u6e56\u7684\u53d1\u5c55\u4e4b\u901f\u3002</p><p>\u3000\u3000\u867d\u7136\u4e2d\u6676\u5fae\u82af\u53bb\u5e74\u5e74\u4e2d\u65f6\u63a5\u53d7\u5fb7\u4eea\u7684\u6ce8\u8d44\uff0c\u4e8b\u5b9e\u4e0a\u5e76\u6ca1\u6709\u83b7\u5f97\u5fb7\u4eea\u5728\u5236\u7a0b\u6280\u672f\u4e0a\u7684\u652f\u6301\uff0c\u4e2d\u6676\u5fae\u82af\u5374\u4ece\u5efa\u6210\u6295\u4ea7\u4e4b\u521d\u4e0d\u8db330\uff05\u7684\u826f\u54c1\u7387\u63d0\u9ad8\u5230\u73b0\u5728\u768480\uff05\uff0c\u8868\u73b0\u751a\u81f3\u8981\u597d\u8fc7\u5176\u4ed6\u5236\u7a0b\u5de5\u827a\u6210\u719f\u7684\u6676\u5706\u5382\u3002\u4e2d\u6676\u5fae\u82af\u65b0\u6d66\u5de5\u5382\u7ecf\u8fc7\u8fd1\u4e00\u5e74\u65f6\u95f4\u7684\u78e8\u5408\uff0c\u5b9e\u9645\u4ea7\u80fd\u63d0\u9ad8\u5230\u6bcf\u6708\u516d\u4e07\u7247\u3002</p><p>\u3000\u3000\u4e5d\u4e5d\u5e74\u4e0a\u534a\u5e74\uff0cSVCD\u789f\u673a\u56fd\u5185\u4ea7\u91cf\u8fbe\u5230\u4e00\u5343\u4e07\u53f0\uff0c\u5168\u7403SVCD\u789f\u673a\u4ea7\u91cf\u7ea6\u5728\u4e00\u5343\u4e24\u767e\u4e07\u53f0\u5de6\u53f3\uff0cSVCD\u89e3\u7801\u82af\u724790\uff05\u3001\u63a7\u5236\u82af\u724750\uff05\u4ee5\u4e0a\u7684\u5e02\u573a\u4efd\u989d\u7531\u7231\u8fbe\u3001\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u4e09\u5bb6\u516c\u53f8\u7684\u5408\u8d44\u516c\u53f8\u63a7\u5236\uff0c\u5728\u65b0\u6d66\u5de5\u5382\u7684\u5de5\u827a\u6761\u4ef6\u6ee1\u8db3\u6761\u4ef6\u4e4b\u540e\uff0c\u5408\u8d44\u516c\u53f8\u5dee\u4e0d\u591a\u5c06\u8fd1\u4e00\u534a\u7684\u8ba2\u5355\u8f6c\u7ed9\u4e2d\u6676\u5fae\u82af\u3002</p><p>\u3000\u3000\u4f9d\u9760\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u800c\u6210\u957f\u8d77\u6765\u7684\u56fd\u5185\u6570\u5b57\u624b\u673a\u53ca\u5176\u4ed6\u7535\u5b50\u5143\u5668\u4ef6\u5382\u5546\uff0c\u5728\u8fd9\u4e9b\u7535\u5b50\u5382\u5546\u6240\u5f62\u6210\u7684\u82af\u7247\u4ee3\u5de5\u4e1a\u52a1\u5e02\u573a\u91cc\uff0c\u4e2d\u6676\u5fae\u82af\u5dee\u4e0d\u591a\u5360\u4e86\u4e00\u534a\u7684\u4efd\u989d\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u5c31\u662f\u9526\u6e56\u62e5\u6709\u72ec\u7acb\u77e5\u8bc6\u4ea7\u6743\u7684\u4e2d\u4f4e\u7aef\u82af\u7247\u8ba2\u5355\u5dee\u4e0d\u591a\u90fd\u4ea4\u7ed9\u65b0\u6d66\u5de5\u5382\u4ee3\u5de5\uff0c\u5305\u62ec\u5229\u6da6\u6700\u9ad8\u7684\u95ea\u5b58\u82af\u7247\u4e1a\u52a1\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u65b0\u6d66\u5de5\u5382\u5728\u5168\u7403\u7535\u5b50\u4ea7\u4e1a\u76f8\u5bf9\u4f4e\u9761\u4e4b\u9645\uff0c\u4f9d\u8d56\u8fd9\u4e9b\u4e1a\u52a1\uff0c\u5728\u5de5\u5382\u5efa\u6210\u540e\u4e0d\u5230\u4e00\u5e74\u7684\u65f6\u95f4\u5c31\u5b9e\u73b0\u4e86\u76c8\u5229\uff0c\u5df2\u7ecf\u7b97\u662f\u76f8\u5f53\u51fa\u8272\u7684\u6210\u7ee9\u4e86\u2014\u2014\u5f53\u7136\uff0c\u4e2d\u6676\u5fae\u82af\u65b0\u6d66\u5de5\u5382\u7684\u6210\u529f\uff0c\u4e3b\u8981\u8fd8\u662f\u4f9d\u8d56\u4e8e\u9526\u6e56\u7684\u5782\u76f4\u751f\u4ea7\u4f53\u7cfb\uff0c\u65b0\u6d66\u5de5\u5382\u6b64\u65f6\u6240\u83b7\u5f97\u7684\u8ba2\u5355\u6216\u76f4\u63a5\u6216\u95f4\u63a5\u7684\u90fd\u8ddf\u9526\u6e56\u6709\u6781\u5927\u7684\u5173\u7cfb\u3002</p><p>\u3000\u3000\u9526\u6e56\u7684\u751f\u4ea7\u89c4\u6a21\u5728\u4e0d\u65ad\u7684\u6269\u5927\uff0c\u7279\u522b\u662f\u4ee5\u6570\u5b57\u624b\u673a\u6280\u672f\u4fc3\u8fdb\u534f\u4f1a\u4e3a\u57fa\u7840\u6ecb\u751f\u51fa\u6765\u7684\u6570\u5b57\u624b\u673a\u4ea7\u4e1a\u96c6\u7fa4\u4e5f\u6e10\u6e10\u6709\u4e86\u6839\u57fa\uff0c\u52a0\u4e0a\u56fd\u5185\u76f8\u5bf9\u4f4e\u5ec9\u7684\u5236\u9020\u6210\u672c\uff0c\u4e2d\u6676\u5fae\u82af\u5728\u56fd\u9645\u82af\u7247\u4e1a\u52a1\u5e02\u573a\u4e5f\u5c06\u62e5\u6709\u4e00\u5e2d\u4e4b\u5730\uff0c\u65b0\u6d66\u5de5\u5382\u5efa\u6210\u8fd8\u4e0d\u8db3\u4e00\u5e74\uff0c\u5c31\u5fc5\u987b\u53bb\u8003\u8651\u4ea7\u80fd\u6269\u5f20\u4e0e\u6280\u672f\u5347\u7ea7\u4e86\u3002</p><p>\u3000\u3000\u5bf9\u5fb7\u4eea\u6765\u8bf4\uff0c\u4e2d\u6676\u5fae\u82af\u5b5c\u5b5c\u4ee5\u6c42\u76840.25\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u6280\u672f\u662f\u5176\u6b63\u5728\u6dd8\u6c70\u6389\u7684\u6280\u672f\uff0c\u5fb7\u4eea\u4e00\u90e8\u5206\u91c7\u75280.18\u5fae\u7c73\u5236\u7a0b\u6280\u672f\u751f\u4ea7\u7684\u82af\u7247\u4ea7\u54c1\u4e5f\u4ea4\u7531\u53f0\u79ef\u7535\u3001\u97e9\u56fd\u5b89\u5357\u7b49\u6676\u5706\u5382\u4ee3\u5de5\uff0c\u8f6c\u8ba90.25/0.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u6280\u672f\uff0c\u5bf9\u5fb7\u4eea\u81ea\u7136\u6676\u5706\u751f\u4ea7\u4e1a\u52a1\u7684\u5f71\u54cd\u6781\u5c0f\u3002</p><p>\u3000\u3000\u4e00\u65b9\u9762\uff0c\u8feb\u4e8e\u9ad8\u6602\u7684\u751f\u4ea7\u4e0e\u7ba1\u7406\u6210\u672c\uff0c\u5fb7\u4eea\u9700\u8981\u4ece\u4e2d\u4f4e\u7aef\u7684\u6676\u5706\u751f\u4ea7\u4e2d\u9000\u51fa\u53bb\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u7531\u4e8e\u8d8a\u6765\u8d8a\u591a\u7684\u7535\u5b50\u516c\u53f8\u9010\u6e10\u638c\u63e1\u4e2d\u4f4e\u7aef\u82af\u7247\u8bbe\u8ba1\u6280\u672f\uff0c\u4e00\u65e6\u5fb7\u4eea\u4ece\u4e2d\u4f4e\u7aef\u6676\u5706\u751f\u4ea7\u4e2d\u9000\u51fa\u6765\uff0c\u5c06\u5f7b\u5e95\u65e0\u6cd5\u53bb\u5206\u4eab\u4e2d\u4f4e\u7aef\u82af\u7247\u5e02\u573a\u7684\u5229\u6da6\u3002</p><p>\u3000\u3000\u867d\u7136\u4e2d\u4f4e\u7aef\u82af\u7247\u5e02\u573a\u9662\u5229\u6da6\u7387\u8f83\u4f4e\uff0c\u4f46\u662f\u80dc\u5728\u5e02\u573a\u5bb9\u91cf\u5de8\u5927\uff0c\u603b\u5229\u6da6\u89c4\u6a21\u8fd8\u662f\u76f8\u5f53\u7684\u53ef\u89c2\u3002</p><p>\u3000\u3000\u5bf9\u4e2d\u4f4e\u7aef\u6676\u5706\u5382\u8fdb\u884c\u6301\u80a1\uff0c\u5206\u4eab\u4e2d\u4f4e\u7aef\u82af\u7247\u5e02\u573a\u7684\u5229\u6da6\uff0c\u65e0\u7591\u6210\u4e3a\u5fb7\u4eea\u6700\u4f73\u7684\u9009\u62e9\uff0c\u8fd9\u4e5f\u662f\u5fb7\u4eea\u5728\u4e00\u5e74\u524d\u505a\u51fa\u5411\u4e2d\u6676\u5fae\u82af\u6ce8\u8d44\u6301\u80a1\u6218\u7565\u51b3\u7b56\u91cd\u8981\u4f9d\u636e\u2014\u2014\u6b64\u65f6\u5c06\u6b63\u5728\u6dd8\u6c70\u76840.25\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u8f6c\u8ba9\u7ed9\u4e2d\u6676\u5fae\u82af\uff0c\u5fb7\u4eea\u5185\u90e8\u7684\u8ba4\u8bc6\u662f\u4e00\u81f4\u7684\uff0c\u4f5c\u4e3a\u8f6c\u8ba9\u6280\u672f\u7684\u9644\u52a0\u6761\u4ef6\uff0c\u5fb7\u4eea\u4e5f\u5c06\u7ee7\u7eed\u8ffd\u52a0\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u6295\u8d44\uff1b\u4f46\u662f0.18\u5fae\u7c73\u6676\u5706\u5236\u7a0b\u5de5\u827a\u662f\u5f53\u524d\u7684\u4e3b\u6d41\u6280\u672f\uff0c\u867d\u7136\u80fd\u64e6\u7740\u56fd\u5bb6\u76d1\u7ba1\u7684\u8fb9\u7f18\u8f6c\u8ba9\u7ed9\u4e2d\u6676\u5fae\u82af\uff0c\u5fb7\u4eea\u5374\u8981\u4e3a\u6b64\u627f\u53d7\u7f8e\u56fd\u5a92\u4f53\u4e0e\u516c\u4f17\u7684\u5de8\u5927\u8206\u8bba\u538b\u529b\uff0c\u4e0d\u7531\u5f97\u8ba9\u4eba\u8fdf\u7591\u4e0d\u51b3\u3002</p></article>"}}}]);
