(window.webpackJsonp=window.webpackJsonp||[]).push([[1059],{1082:function(p){p.exports={chapterTitle:null,title:"\u7b2c1056\u7ae0 \u4e4d\u6cc4\u7684\u6625\u5149",content:"<article><h2>\u7b2c1056\u7ae0 \u4e4d\u6cc4\u7684\u6625\u5149</h2><p>\u3000\u3000\u201c\u2026\u2026\u201d\u536b\u5170\u521a\u4e0b\u697c\u68af\u62d0\u89d2\uff0c\u697c\u4e0b\u5ba2\u5385\u91cc\u706f\u5149\u901a\u660e\uff0c\u770b\u7740\u5f20\u606a\u7ad9\u5728\u53cc\u624b\u6276\u7740\u7fdf\u4e39\u9752\u7684\u81c0\uff0c\u4e24\u4eba\u8d64\u8eab\u88f8\u8eab\uff0c\u4e0a\u56de\u770b\u5230\u5f20\u606a\u5c06\u7fdf\u4e39\u9752\u62b5\u5728\u95e8\u53e3\u543b\u5979\u7684\u4e73\u623f\uff0c\u773c\u4e0b\u8fd9\u573a\u666f\u66f4\u662f\u523a\u6fc0\u5f97\u8981\u547d\uff0c\u8fd8\u6709\u534a\u53e5\u8bdd\u5361\u5728\u5589\u5499\u773c\u91cc\u600e\u4e48\u4e5f\u5410\u4e0d\u51fa\u6765\uff0c\u4e5f\u5fd8\u4e86\u8981\u6536\u4f4f\u811a\uff0c\u8e0f\u7a7a\u4e86\u4e24\u4e2a\u697c\u9636\uff0c\u4e0b\u610f\u8bc6\u7684\u6293\u4f4f\u6276\u624b\uff0c\u5dee\u70b9\u4ece\u697c\u68af\u4e0a\u6eda\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7ffb\u8eab\u5750\u5230\u6c99\u53d1\u4e0a\uff0c\u62ff\u8d77\u8863\u670d\u906e\u5728\u8eab\u4e0a\uff0c\u89c1\u5f20\u606a\u8fd8\u5149\u7740\u4e0b\u8eab\u3001\u536b\u5170\u8fd8\u50bb\u6123\u6123\u7684\u76ef\u7740\u90a3\u91cc\u770b\uff0c\u201c\u4e11\u6b7b\u4e86\uff01\u201d\u5a07\u7b11\u7740\u5fd9\u62ff\u4e86\u4ef6\u886c\u886b\u5c06\u90a3\u91cc\u906e\u4e0a\uff0c\u5374\u817e\u4e0d\u51fa\u624b\u906e\u81ea\u5df1\u7684\u80f8\u53e3\uff0c\u5c31\u4fcf\u76ae\u7684\u5c06\u886c\u886b\u9886\u5b50\u6302\u5728\u4e0a\u9762\uff0c\u817e\u51fa\u624b\u6765\u62ff\u8863\u670d\u906e\u5728\u80f8\u53e3\u3002</p><p>\u3000\u3000\u201c\u53c8\u957f\u9488\u773c\u4e86\uff0c\u201d\u536b\u5170\u7ad9\u5728\u697c\u68af\u4e0a\uff0c\u8fd9\u624d\u6ee1\u8138\u98de\u7ea2\u7684\u62ff\u624b\u8499\u7740\u773c\u775b\uff0c\u53c8\u597d\u6c14\u53c8\u597d\u7b11\u7684\u8bf4\u9053\uff1a\u201c\u8fd9\u4e2a\u5927\u7684\u623f\u5b50\uff0c\u504f\u8981\u6211\u8eb2\u7740\u4f60\u4eec\u4e24\u4e2a\u6df7\u86cb\uff0c\u8fd8\u771f\u662f\u6ca1\u6709\u5929\u7406\u4e86\u2026\u2026\u201d\u9003\u4e5f\u4f3c\u7684\u4e0a\u4e86\u697c\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e5f\u89c9\u5f97\u597d\u7b11\uff0c\u521a\u624d\u771f\u662f\u592a\u6027\u6025\u4e86\uff0c\u81f3\u5c11\u4e5f\u8981\u7b49\u536b\u5170\u7761\u4e0b\u518d\u8bf4\u3002\u5979\u677e\u5f00\u624b\uff0c\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\uff1a\u201c\u5c0f\u5fc3\u4f1a\u4e0d\u884c\u54e6\uff01\u201d\u62ff\u8d77\u8863\u670d\u8981\u7a7f\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u73b0\u5728\u5979\u4e0d\u8be5\u518d\u95ef\u51fa\u6765\u4e86\uff0c\u6211\u4eec\u7ee7\u7eed\u5427\u2026\u2026\u201d\u5f20\u606a\u5750\u8fc7\u53bb\uff0c\u62b1\u7740\u7fdf\u4e39\u9752\u7684\u8eab\u5b50\uff0c\u62c9\u5230\u6000\u91cc\uff0c\u624b\u4ece\u5979\u5149\u6ed1\u7ea4\u76c8\u7684\u8170\u6478\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7ed9\u6253\u65ad\u4e86\u4e00\u4e0b\uff0c\u5174\u81f4\u6709\u4e9b\u51cf\uff0c\u7ed9\u4ed6\u7684\u624b\u8fd9\u4e48\u6478\u7740\uff0c\u8eab\u5b50\u53c8\u4e0b\u610f\u8bc6\u7684\u7ef7\u7d27\u4e86\uff0c\u8eab\u4f53\u6696\u6d0b\u6d0b\u7684\uff0c\u5174\u81f4\u53c8\u5174\u4e86\u8d77\u6765\uff0c\u987a\u4ece\u7684\u8de8\u5750\u5230\u5f20\u606a\u7684\u5927\u817f\u4e0a\uff0c\u4ece\u5934\u9876\u5230\u5c3e\u690e\u3001\u6d51\u8eab\u4e0a\u4e0b\u90fd\u7ed9\u4e00\u79cd\u6781\u81f4\u7d27\u80c0\u7684\u654f\u611f\u7d27\u7d27\u7684\u62fd\u4f4f\u3002</p><p>\u3000\u3000\u201c\u5443\u2026\u2026\u201d\u62b5\u5230\u6700\u6df1\u5904\uff0c\u7fdf\u4e39\u9752\u90fd\u5fcd\u4e0d\u4f4f\u8f7b\u53eb\u4e86\u4e00\u58f0\uff0c\u624b\u6491\u4f4f\u5f20\u606a\u7684\u5927\u817f\uff0c\u9002\u5e94\u4e86\u4e00\u4f1a\u513f\uff0c\u624d\u7f13\u7f13\u7684\u5750\u5b9e\u4e0b\u53bb\uff0c\u79c1\u5904\u6536\u7f29\u7740\uff0c\u5c31\u8fd9\u4e48\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u6765\u52a8\u2026\u2026\u201d\u5f20\u606a\u53cd\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u80cc\u90e8\u6ca1\u6709\u652f\u6491\uff0c\u624b\u62b1\u7740\u7fdf\u4e39\u9752\u8089\u7d27\u5f39\u5b9e\u7684\u7f8e\u81c0\uff0c\u65e0\u6cd5\u592a\u7528\u529b\u7684\u8fd0\u52a8\u3002\u7fdf\u4e39\u9752\u6402\u7740\u5f20\u606a\u7684\u8170\uff0c\u5979\u7684\u5927\u817f\u96ea\u767d\u4e30\u8174\uff0c\u5c06\u53cc\u811a\u6000\u4f4f\u5f20\u606a\u7684\u8170\uff0c\u5979\u5e73\u65e5\u5996\u8273\u51b7\u8273\u591a\u53d8\uff0c\u5185\u4e2d\u6027\u5b50\u575a\u5f3a\uff0c\u8fd9\u65f6\u5019\u7684\u5a07\u5598\u547b\u541f\u5374\u662f\u5a07\u6ef4\u6ef4\u7684\u67d4\u5ae9\uff0c\u4e0d\u4e00\u4f1a\u513f\u5c31\u6402\u7d27\u5f20\u606a\u7684\u8170\u4e0d\u518d\u52a8\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u7740\u7fdf\u4e39\u9752\u8138\u4e0a\u8ff7\u832b\u800c\u590d\u6742\u7684\u795e\u60c5\uff0c\u8138\u988a\u4e39\u7ea2\u5982\u971e\uff0c\u79c0\u76f4\u7684\u9f3b\u5c16\u6c81\u7740\u7ec6\u5bc6\u7684\u6c57\u73e0\uff0c\u9b13\u659c\u795e\u8ff7\uff0c\u5fae\u7741\u4e0d\u7741\u7684\u7738\u5b50\u91cc\u6563\u900f\u51fa\u6765\u8ff7\u79bb\u7684\u5149\u8bf1\u4eba\u5230\u6781\u70b9\uff0c\u5f20\u606a\u8f7b\u8f7b\u7684\u543b\u7740\u5979\u7684\u871c\u5507\u9999\u820c\uff0c\u5f85\u5979\u4e0b\u4f53\u7684\u62bd\u6410\u7a0d\u5e73\u7f13\u4e00\u4e9b\uff0c\u624d\u5c06\u5979\u653e\u5012\u5728\u6c99\u53d1\u4e0a\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u53c8\u62b1\u7d27\u5f20\u606a\u7684\u8170\u4e0d\u8ba9\u4ed6\u52a8\u5f39\uff0c\u5f20\u606a\u624b\u6402\u7740\u5979\u7684\u8170\u2014\u2014\u7a81\u7136\u7fdf\u4e39\u9752\u624b\u6491\u7740\u5f20\u606a\u7684\u80f8\u53e3\u8981\u8d77\u6765\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u624b\u63a9\u7740\u53cc\u817f\u4e4b\u95f4\u6025\u6025\u7684\u5f80\u536b\u751f\u95f4\u8d70\u53bb\uff0c\u5f20\u606a\u8ddf\u8fc7\u53bb\uff0c\u770b\u7740\u7fdf\u4e39\u9752\u5149\u7740\u8ff7\u4eba\u7684\u8eab\u4f53\u5750\u5728\u9a6c\u6876\u4e0a\uff0c\u8eab\u4e0b\u4f20\u6765\u6025\u4fc3\u7684\u6c34\u67f1\u6fc0\u6d41\u7684\u58f0\u97f3\u3002</p><p>\u3000\u3000\u201c\u51fa\u53bb\u554a\u2026\u2026\u201d\u4efb\u7fdf\u4e39\u9752\u5e73\u65f6\u518d\u6cfc\u8fa3\uff0c\u8fd9\u65f6\u5019\u4e5f\u53ea\u80fd\u4e0d\u582a\u7f9e\u7684\u5c06\u5934\u57cb\u5728\u5927\u817f\u4e4b\u95f4\uff0c\u4e4c\u9ed1\u7684\u79c0\u53d1\u6563\u4e0b\u6765\uff0c\u906e\u4f4f\u534a\u7247\u96ea\u767d\u7684\u8eab\u4f53\uff0c\u58f0\u97f3\u5a07\u67d4\u7684\u8ba9\u5f20\u606a\u51fa\u53bb\u3002</p><p>\u3000\u3000\u5bf9\u4e8e\u63a7\u5236\u6b32\u7a0d\u5f3a\u4e9b\u7684\u7537\u4eba\uff0c\u6700\u6781\u81f4\u7684\u4f53\u9a8c\u5927\u6982\u5c31\u662f\u4e00\u6b21\u6b21\u7684\u5c06\u60f9\u4eba\u601c\u7231\u7684\u5973\u4eba\u63a8\u4e0a\u4e91\u7aef\u5427\uff1f\u5f20\u606a\u54ea\u91cc\u4f1a\u8fd9\u4e48\u50bb\u542c\u5979\u7684\u8bdd\u5c31\u8d70\u51fa\u53bb\uff0c\u53cd\u800c\u5f97\u5bf8\u8fdb\u5c3a\u7684\u8d70\u8fdb\u6765\uff0c\u5206\u5f00\u5979\u7684\u79c0\u53d1\uff0c\u62c9\u7740\u5979\u7684\u624b\u63e1\u7740\u90a3\u91cc\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5c31\u8fd9\u4e48\u63e1\u7740\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\u624d\u6709\u62ac\u8d77\u5934\uff0c\u6062\u590d\u4e86\u4e9b\u5f80\u65e5\u7684\u86ee\u6c14\uff0c\u55d4\u9a82\u9053\uff1a\u201c\u5979\u4eec\u600e\u4e48\u6ca1\u7ed9\u4f60\u5f04\u6b7b\uff1f\u6211\u7684\u9b42\u90fd\u7ed9\u4f60\u5f04\u6563\u4e86\u3002\u201d\u4f38\u624b\u6253\u5f00\u6d74\u7f38\u7684\u6c34\u9f99\u5934\u653e\u6e29\u6c34\uff0c\u51c6\u5907\u6d17\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u697c\u4e0b\u7684\u6d74\u5ba4\u91cc\u6ca1\u6709\u7761\u8863\uff0c\u7fdf\u4e39\u9752\u4e0e\u5f20\u606a\u6d17\u8fc7\u6fa1\uff0c\u5149\u6e9c\u6e9c\u7684\u8d70\u51fa\u6765\uff0c\u770b\u7740\u6c99\u53d1\u6b8b\u75d5\u72fc\u7c4d\uff0c\u7279\u522b\u662f\u5e03\u6c99\u53d1\u4e0a\u6e7f\u4e86\u4e00\u6ee9\uff0c\u8fd9\u65f6\u5019\u4e5f\u6ca1\u6709\u6c14\u529b\u6536\u62fe\u3002\u7fdf\u4e39\u9752\u8f7b\u8f7b\u7684\u62e5\u7740\u5f20\u606a\u7684\u80cc\uff0c\u6e29\u67d4\u7684\u8bf4\u9053\uff1a\u201c\u7ed9\u4f60\u51c6\u5907\u4e86\u8863\u670d\uff0c\u5230\u697c\u4e0a\u53bb\u6362\u5427\u2026\u2026\u201d\u4fbf\u7559\u4e0b\u4e00\u5730\u7684\u72fc\u7c4d\u4e0a\u697c\u53bb\u6362\u8863\u670d\u3002</p><p>\u3000\u3000\u4e24\u4eba\u76f8\u62e5\u800c\u7761\uff0c\u7fdf\u4e39\u9752\u60e6\u8bb0\u7740\u5ba2\u5385\u91cc\u7684\u72fc\u7c4d\uff0c\u5929\u8499\u8499\u4eae\u5c31\u9192\u4e86\u8fc7\u6765\uff0c\u62ab\u7740\u7761\u8863\u4e0b\u4e86\u697c\uff0c\u770b\u5230\u697c\u4e0b\u5ba2\u5385\u91cc\u5df2\u7ecf\u6536\u62fe\u5f97\u6574\u6d01\u5982\u65b0\uff0c\u8863\u670d\u6574\u9f50\u7684\u53e0\u5728\u6c99\u53d1\u4e0a\uff0c\u6e7f\u4e86\u4e00\u6ee9\u7684\u5730\u65b9\u75d5\u8ff9\u4e5f\u7ed9\u5904\u7406\u5f97\u5f88\u6de1\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8f6c\u8eab\u4e0a\u4e86\u697c\uff0c\u63a8\u5f00\u536b\u5170\u5367\u5ba4\u7684\u95e8\u3002\u5ba4\u5185\u900f\u8fdb\u6765\u6e05\u6668\u9752\u5e7d\u5e7d\u7684\u5149\uff0c\u8fd9\u59ae\u5b50\u80cc\u5bf9\u7740\u95e8\u7761\u7740\uff0c\u4e00\u52a8\u4e0d\u52a8\uff0c\u4f3c\u4e4e\u6ca1\u6709\u89c9\u5bdf\u5230\u95e8\u7ed9\u6253\u5f00\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u4ec0\u4e48\u65f6\u5019\u4e0b\u697c\u5e2e\u5979\u4eec\u6536\u62fe\u7684\uff0c\u7fdf\u4e39\u9752\u8d70\u8fc7\u53bb\u6380\u8d77\u88ab\u5b50\uff0c\u4e5f\u4e00\u540c\u94bb\u8fdb\u88ab\u7a9d\u91cc\uff0c\u4ece\u540e\u9762\u4f38\u624b\u5c06\u7a7f\u7740\u4e1d\u8d28\u7761\u88d9\u7684\u536b\u5170\u8f7b\u6402\u7740\uff0c\u89c1\u5979\u8fd8\u6ca1\u6709\u52a8\u9759\uff0c\u5c31\u4f38\u624b\u5230\u5979\u5a07\u5ae9\u633a\u7acb\u7684\u4e73\u623f\u4e0a\u6478\u4e86\u4e00\u4e0b\uff0c\u53c8\u63e1\u4f4f\u634f\u4e86\u634f\uff0c\u536b\u5170\u8fd9\u624d\u4e0d\u88c5\u7761\u7684\u5c06\u5979\u7684\u624b\u62e8\u5f00\u3002</p><p>\u3000\u3000\u536b\u5170\u8f6c\u8fc7\u8eab\u6765\uff0c\u4e0e\u7fdf\u4e39\u9752\u76f8\u62e5\u800c\u7761\uff0c\u770b\u7740\u6e05\u6668\u5e7d\u5149\u91cc\u7684\u7fdf\u4e39\u9752\u683c\u5916\u7684\u7f8e\u4e3d\uff0c\u5e2e\u5979\u5c06\u8033\u9b13\u7684\u53d1\u4e1d\u64a9\u5230\u8033\u540e\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u771f\u6f02\u4eae\uff0c\u6574\u4e2a\u4eba\u90fd\u5bb9\u5149\u7115\u53d1\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u4ee5\u540e\u4f1a\u77e5\u9053\u7684\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7b11\u4e86\u7b11\uff0c\u4e60\u60ef\u7684\u5c06\u624b\u7a9d\u5230\u536b\u5170\u7684\u80f8\u53e3\uff0c\u201c\u6709\u4ec0\u4e48\u4e8b\u60f3\u8d77\u6765\u8981\u8ddf\u6211\u4eec\u8bf4\u3002\u201d</p><p>\u3000\u3000\u201c\u90fd\u7ed9\u5413\u5fd8\u6389\u4e86\uff0c\u600e\u4e48\u60f3\u90fd\u60f3\u4e0d\u8d77\u6765\uff0c\u201d\u536b\u5170\u7b11\u4e86\u8d77\u6765\uff0c\u201c\u4f60\u4eec\u4fe9\u4e2a\u6df7\u86cb\uff0c\u5f53\u6211\u4e0d\u5b58\u5728\u4e86\uff0c\u5728\u697c\u4e0b\u65f6\u5ba2\u5385\u91cc\u706f\u90fd\u4e0d\u5173\u3002\u201d</p><p>\u3000\u3000\u201c\u53c8\u4e0d\u7528\u62c5\u5fc3\u7ed9\u5176\u4ed6\u4eba\u770b\u89c1\uff0c\u8c01\u60f3\u5230\u4f60\u53c8\u7a81\u7136\u4e0b\u697c\uff1f\u201d\u7fdf\u4e39\u9752\u4e5f\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u7684\u786e\u592a\u6027\u6025\u4e86\u4e9b\uff0c\u201c\u4fbf\u5b9c\u4f60\u8fd9\u5bf9\u8d3c\u773c\u4e86\uff0c\u201d\u624b\u63e1\u7740\u536b\u5170\u4e73\u9e3d\u4f3c\u7684\u53cc\u4e73\uff0c\u63c9\u4e86\u63c9\uff0c\u201c\u4f1a\u60f3\u7537\u4eba\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8c01\u8ddf\u4f60\u4f3c\u7684\uff01\u201d\u536b\u5170\u8eab\u5b50\u5f80\u540e\u7f29\u4e86\u7f29\uff0c\u7fdf\u4e39\u9752\u7684\u624b\u5fc3\u6e29\u70ed\uff0c\u7ed9\u5979\u6478\u7740\u4e73\u623f\uff0c\u5fc3\u91cc\u75d2\u75d2\u7684\uff0c\u53c8\u597d\u5947\u7684\u95ee\u9053\uff0c\u201c\u4f60\u4eec\u600e\u4e48\u8ddf\u72d7\u4f3c\u7684\uff0c\u8fd9\u6837\u4e5f\u884c\uff1f\u201d</p><p>\u3000\u3000\u201c\u600e\u4e48\u4e0d\u884c\uff1f\u4f60\u60f3\u7537\u4eba\u4e86\u2026\u2026\u201d\u7fdf\u4e39\u9752\u7b11\u4e86\u8d77\u6765\uff0c\u624b\u4ece\u540e\u9762\u4f38\u5230\u536b\u5170\u7761\u88d9\u91cc\uff0c\u624b\u6307\u6240\u89e6\u3001\u808c\u80a4\u5ae9\u6ed1\u5982\u8102\u7389\uff0c\u201c\u4f60\u81ea\u5df1\u4f1a\u5f04\u90a3\u91cc\u4e86\uff1f\u201d\u7fdf\u4e39\u9752\u624b\u6307\u6478\u5230\u536b\u5170\u7684\u817f\u5fc3\u5904\u7684\u5185\u88e4\u5f88\u6e7f\uff0c\u8ddf\u7ed9\u6cb9\u6d78\u8fc7\u4f3c\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u624d\u4e0d\u4f1a\u2026\u2026\u201d\u536b\u5170\u5624\u549b\u7740\u5c06\u5934\u57cb\u5728\u7fdf\u4e39\u9752\u7684\u6000\u91cc\uff0c\u5939\u7d27\u7740\u53cc\u817f\u4e0d\u518d\u8ba9\u7fdf\u4e39\u9752\u624b\u4e71\u52a8\uff0c\u8bf4\u9053\uff0c\u201c\u96be\u53d7\u5f97\u5f88\uff0c\u7761\u4e0d\u7740\u624d\u4e0b\u53bb\u5e2e\u4f60\u4eec\u4e24\u4e2a\u6df7\u86cb\u6536\u62fe\u6b8b\u5c40\u2026\u2026\u201d\u53c8\u95ee\u9053\uff1a\u201c\u4f60\u5fc3\u91cc\u7231\u4ed6\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5728\u4f60\u7684\u773c\u91cc\uff0c\u7231\u60c5\u662f\u90a3\u6f84\u5f7b\u900f\u51c0\u7684\u6c34\uff0c\u4f3c\u4e4e\u6709\u4e86\u4e00\u70b9\u6742\u8d28\u5c31\u4e0d\u518d\u662f\u539f\u6765\u7684\u7231\u60c5\u4e86\uff1b\u5728\u6211\u7684\u773c\u91cc\uff0c\u7231\u60c5\u4e0d\u662f\u4ec0\u4e48\u7eaf\u51c0\u6c34\uff0c\u5fc3\u5728\u54ea\u91cc\uff0c\u7231\u60c5\u5c31\u5728\u54ea\u91cc\u2014\u2014\u9664\u4e86\u4ed6\uff0c\u6211\u7231\u4e0d\u4e86\u522b\u4eba\uff0c\u201d\u7fdf\u4e39\u9752\u8fd9\u4f1a\u513f\uff0c\u4e00\u4f1a\u513f\u6545\u610f\u505a\u51fa\u51f6\u6076\u7684\u795e\u60c5\uff0c\u8bf4\u9053\uff0c\u201c\u5076\u5c14\u4f1a\u60f3\u5230\u8001\u5a18\u4e5f\u7b97\u7edd\u8272\u7f8e\u4eba\u513f\u5c31\u8fd9\u4e48\u4fbf\u5b9c\u4e86\u4ed6\uff0c\u771f\u662f\u5e94\u8be5\u6068\u4ed6\u6068\u5f97\u7259\u75d2\u75d2\u7684\uff0c\u7b49\u54ea\u5929\u8001\u5a18\u5c06\u4ed6\u73a9\u817b\u5473\u4e86\u2026\u2026\u54fc\u54fc\uff01\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u536b\u5170\u5a07\u7b11\u4e86\u4e00\u58f0\uff0c\u53c8\u95ee\u9053\uff0c\u201c\u4e39\u9752\u59d0\uff0c\u4f60\u8bf4\u6211\u80fd\u7231\u4e0a\u522b\u4eba\u5417\uff1f\u201d</p><p>\u3000\u3000\u201c\u5230\u73c0\u65af\u6765\u966a\u6211\u5427\uff0c\u79bb\u90a3\u4e2a\u6df7\u86cb\u8fdc\u4e00\u4e9b\u5c31\u80fd\u3002\u201d\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff0c\u201c\u6765\u73c0\u65af\u4e5f\u65b9\u4fbf\u3002\u201d</p><p>\u3000\u3000\u201c\u6211\u53ef\u4e0d\u60f3\u518d\u957f\u9488\u773c\uff0c\u201d\u536b\u5170\u7b11\u4e86\u8d77\u6765\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8ddf\u4e24\u53ea\u72d7\u4f3c\u7684\uff0c\u4f60\u4eec\u771f\u4e0d\u8981\u8138\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u9192\u6765\u65f6\uff0c\u7fdf\u4e39\u9752\u4e0d\u5728\u8eab\u8fb9\uff0c\u4e0d\u6653\u5f97\u4ec0\u4e48\u65f6\u5019\u8d77\u5e8a\u4e86\u3002</p><p>\u3000\u3000\u5929\u5149\u5df2\u7ecf\u5927\u4eae\uff0c\u8eab\u4f53\u8212\u6cf0\uff0c\u5f20\u606a\u7ad9\u5728\u7a97\u524d\uff0c\u6781\u76ee\u8fdc\u773a\u8fdc\u5904\u6e5b\u84dd\u7684\u6d77\u6c34\uff0c\u6709\u51e0\u5ea7\u7fe1\u7fe0\u4e00\u6837\u7684\u8986\u76d6\u7740\u5bc6\u6797\u7684\u5c0f\u5c9b\u5206\u5e03\u5728\u8fd1\u6d77\u5904\u3002</p><p>\u3000\u3000\u5f20\u606a\u62ab\u7740\u7761\u8863\uff0c\u8d70\u51fa\u5367\u5ba4\uff0c\u6574\u4e2a\u623f\u5b50\u91cc\u4e0d\u50cf\u6709\u4eba\u7684\u6a21\u6837\uff0c\u4e0b\u5230\u697c\u68af\u62d0\u89d2\u5904\uff0c\u770b\u5230\u4ed6\u4e0e\u4e39\u9752\u7684\u8863\u670d\u6574\u6574\u9f50\u9f50\u7684\u53e0\u653e\u5728\u6c99\u53d1\u4e0a\uff0c\u5fc3\u60f3\u7740\u4e39\u9752\u4e0e\u536b\u5170\u6ca1\u6709\u51fa\u53bb\uff0c\u53c8\u9000\u56de\u5230\u697c\u4e0a\uff0c\u770b\u7740\u536b\u5170\u5367\u5ba4\u95e8\u5f00\u7740\u9053\u7f1d\uff0c\u63a8\u5f00\u6765\u770b\u89c1\u4e24\u4e2a\u7f8e\u4eba\u513f\u76f8\u62e5\u8eba\u5728\u4e00\u8d77\uff0c\u773c\u775b\u90fd\u7741\u7740\u3002</p><p>\u3000\u3000\u201c\u4f60\u8d77\u6765\u4e86\uff1f\u201d\u7fdf\u4e39\u9752\u6175\u61d2\u7684\u4f38\u4e86\u61d2\u8170\uff0c\u5750\u8d77\u6765\uff0c\u201c\u6e05\u6668\u8d77\u6765\u5c06\u697c\u4e0b\u6536\u62fe\u4e86\u4e00\u4e0b\uff0c\u53c8\u60f3\u7761\u56de\u7b3c\u89c9\uff0c\u6015\u95f9\u9192\u4f60\uff0c\u5c31\u7761\u8fd9\u91cc\u6765\u4e86\u3002\u201d</p><p>\u3000\u3000\u5979\u4f38\u7740\u61d2\u8170\uff0c\u7761\u88d9\u7684\u540a\u5e26\u6ed1\u4e0b\u80a9\u8180\uff0c\u9732\u51fa\u534a\u7247\u96ea\u767d\u4e30\u808c\uff0c\u536b\u5170\u7b11\u7740\u5e2e\u5979\u5c06\u540a\u5e26\u62c9\u597d\uff0c\u5979\u6ca1\u597d\u610f\u601d\u5750\u8d77\u6765\uff0c\u7761\u88d9\u5f88\u8f7b\u8584\uff0c\u6ca1\u6709\u6234\u4e73\u7f69\uff0c\u5173\u952e\u662f\u6e05\u6668\u611f\u89c9\u5185\u88e4\u6e7f\u900f\u4e86\u7a7f\u5728\u8eab\u4e0a\u96be\u53d7\uff0c\u5c31\u8131\u4e86\u6254\u5728\u5730\u677f\u4e0a\uff0c\u8981\u662f\u8d70\u5149\u8ba9\u8fd9\u5bb6\u4f19\u770b\u5230\uff0c\u53ef\u4e0d\u662f\u4e8f\u5927\u4e86\uff1f</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5e73\u65f6\u770b\u5230\u6d77\u8fb9\u7684\u5c0f\u5c9b\u6709\u4ec0\u4e48\u611f\u89c9\uff1f\u201d\u5f20\u606a\u8d70\u8fc7\u53bb\u5c06\u7eb1\u8d28\u7a97\u5e18\u62c9\u5f00\uff0c\u6307\u7740\u8fdc\u5904\u8ddf\u7fe1\u7fe0\u4f3c\u7684\u5c0f\u5c9b\u7ed9\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u770b\uff0c\u201c\u6211\u8bb0\u5f97\u897f\u6fb3\u6d32\u597d\u50cf\u5141\u8bb8\u79c1\u4eba\u8d2d\u4e70\u65e0\u4eba\u6d77\u5c9b\u2026\u2026\u201d\u89c1\u536b\u5170\u8138\u4e0a\u7684\u8868\u60c5\u5f88\u602a\u5f02\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u600e\u4e48\u4e86\uff1f\u201d</p><p>\u3000\u3000\u536b\u5170\u7684\u8868\u60c5\u5f53\u7136\u602a\u5f02\u4e86\uff0c\u5979\u54ea\u91cc\u60f3\u5230\u5f20\u606a\u4f1a\u8d70\u8fdb\u5979\u7684\u5367\u5ba4\uff0c\u8fd8\u76f4\u63a5\u51b2\u5230\u7a97\u5b50\u8fb9\u6765\u8ddf\u5979\u4eec\u8c08\u4ec0\u4e48\u65e0\u4eba\u6d77\u5c9b\uff0c\u5979\u65e9\u6668\u8131\u4e0b\u6765\u7684\u90a3\u6761\u5185\u88e4\u5c31\u6254\u5728\u5e8a\u8fd9\u8fb9\u7684\u5730\u677f\u4e0a\uff0c\u8fd8\u662f\u88c6\u5e95\u5e03\u671d\u4e0a\uff0c\u767d\u8272\u7684\u68c9\u5185\u88e4\uff0c\u90a3\u91cc\u7ed9\u871c\u6db2\u6d78\u6e7f\u4e86\u7684\u5730\u65b9\u8fd9\u65f6\u5019\u5df2\u7ecf\u5e72\u4e86\uff0c\u7559\u4e86\u5fae\u9ec4\u8272\u7684\u5370\u8ff9\u5341\u5206\u7684\u663e\u773c\u3002</p><p>\u3000\u3000\u770b\u7740\u5f20\u606a\u7684\u773c\u795e\u5f80\u4e0b\u79fb\uff0c\u536b\u5170\u7f9e\u4e0d\u751a\u7f9e\u3001\u731b\u7684\u63a2\u8fc7\u8eab\u5b50\u60f3\u5c06\u5185\u88e4\u62a2\u8fc7\u6765\uff0c\u592a\u7528\u529b\uff0c\u6574\u4e2a\u4eba\u4f7f\u53bb\u5e73\u8861\uff0c\u76f4\u4ece\u5e8a\u4e0a\u6ed1\u8dcc\u4e0b\u53bb\uff0c\u8111\u888b\u4e5f\u731b\u7684\u649e\u5230\u5f20\u606a\u7684\u5927\u817f\u6839\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u75db\u5f97\u591f\u545b\uff0c\u536b\u5170\u7ed9\u7f13\u51b2\u4e86\u4e00\u4e0b\uff0c\u8dcc\u5f97\u4e0d\u7b97\u592a\u91cd\uff0c\u53ea\u662f\u77ed\u77ed\u7684\u4e1d\u888d\uff0c\u4e1d\u888d\u4e0b\u4e0d\u7740\u7247\u7f15\uff0c\u6625\u5149\u4e00\u6cc4\u5343\u91cc\uff0c\u96ea\u767d\u633a\u7fd8\u7684\u53cc\u4e73\uff0c\u96ea\u767d\u7684\u5927\u817f\u90fd\u5f7b\u5f7b\u5e95\u5e95\u7684\u66b4\u9732\u5728\u6668\u5149\u4e4b\u4e0b\uff0c\u536b\u5170\u4e5f\u610f\u8bc6\u5230\u8d70\u5149\uff0c\u614c\u4e71\u7684\u906e\u4f4f\u53cc\u4e73\u722c\u4e0a\u5e8a\u53bb\uff0c\u5374\u4e0d\u6599\u5979\u6ca1\u6709\u7a7f\u5185\u88e4\uff0c\u5ae3\u7ea2\u5e72\u51c0\u7684\u4e0b\u4f53\u4e0e\u767d\u81c0\u90fd\u7ea4\u6beb\u6bd5\u5448\u7684\u4e5f\u66b4\u9732\u5728\u6668\u5149\u4e2d\uff0c\u8fd9\u4e00\u523b\uff0c\u5f20\u606a\u9f3b\u8840\u5dee\u70b9\u55b7\u51fa\u6765\u3002</p><p>\u3000\u3000\u536b\u5170\u62c9\u7740\u88ab\u5b50\u5c06\u5c41\u80a1\u3001\u8eab\u5b50\u3001\u5934\u90fd\u76d6\u5230\u91cc\u9762\uff0c\u6ca1\u6709\u52c7\u6c14\u518d\u770b\u5f20\u606a\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7b11\u5f97\u82b1\u679d\u4e71\u98a4\uff0c\u5f20\u606a\u5c06\u767d\u8272\u7684\u5185\u88e4\u4ece\u5730\u677f\u4e0a\u6361\u8d77\u6765\uff0c\u770b\u7740\u4e0a\u9762\u5fae\u9ec4\u7684\u75d5\u8ff9\uff0c\u7591\u60d1\u7684\u671d\u7fdf\u4e39\u9752\u4eae\u4e86\u4eae\uff0c\u95ee\u9053\uff1a\u201c\u4f60\u4eec\u4e24\u4e2a\u5973\u4eba\u5927\u6e05\u65e9\u5728\u505a\u4ec0\u4e48\u597d\u4e8b\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\u662f\u4f60\u60f3\u7684\u90a3\u6837\uff0c\u8fd8\u7ed9\u6211\u2026\u2026\u201d\u536b\u5170\u53c8\u7a81\u7136\u6380\u5f00\u88ab\u5b50\u5c06\u5f20\u606a\u624b\u91cc\u7684\u5185\u88e4\u62a2\u8fc7\u53bb\uff0c\u53c8\u5c06\u81ea\u5df1\u8499\u5728\u88ab\u5b50\u91cc\uff0c\u95f7\u58f0\u5728\u88ab\u5b50\u91cc\u5927\u53eb\uff0c\u201c\u7fdf\u4e39\u9752\uff0c\u4f60\u5feb\u5c06\u8fd9\u6d51\u7403\u4ece\u6211\u623f\u95f4\u91cc\u62c9\u51fa\u53bb\uff0c\u4f60\u4e5f\u662f\u4e2a\u6d51\u7403\uff01\u201d</p><p>\u3000\u3000\u201c\u6211\u60f3\u4ec0\u4e48\u60f3\u4e86\uff1f\u201d\u5f20\u606a\u7ed9\u7fdf\u4e39\u9752\u63a8\u7740\u51fa\u4e86\u95e8\uff0c\u4f38\u624b\u5230\u5979\u7761\u88d9\u4e0b\u6478\u4e86\u6478\uff0c\u4e5f\u6ca1\u6709\u7a7f\u5185\u88e4\uff0c\u66a7\u6627\u7684\u7b11\u4e86\u7b11\uff0c\u201c\u4f60\u4eec\u4fe9\u4e2a\u8fd9\u6837\u5b50\u5f88\u4e0d\u4eba\u9053\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u4e71\u60f3\u4ec0\u4e48\u554a\uff1f\u201d\u7fdf\u4e39\u9752\u6c14\u7b11\u7740\uff0c\u5c06\u5f20\u606a\u4e71\u634f\u5979\u81c0\u8089\u7684\u602a\u624b\u6253\u4e0b\u6765\uff0c\u95ee\u9053\uff0c\u201c\u4f60\u65e9\u4e0a\u60f3\u5403\u4ec0\u4e48\uff0c\u6211\u7ed9\u4f60\u51c6\u5907\u2026\u2026\u201d</p><p>\u3000\u3000\u716e\u4e86\u5496\u5561\uff0c\u71ac\u4e86\u8377\u5305\u86cb\uff0c\u5c31\u7740\u6cd5\u5f0f\u9762\u5305\uff0c\u7b80\u5355\u7684\u7528\u8fc7\u65e9\u9910\uff0c\u536b\u5170\u53cd\u6b63\u662f\u6ca1\u6709\u8138\u4e0b\u697c\u6765\u3002\u5403\u9971\u4e86\uff0c\u559d\u8db3\u4e86\uff0c\u5f20\u606a\u767e\u65e0\u804a\u8d56\u7684\u65e0\u6240\u4e8b\u4e8b\uff0c\u8ddf\u7fdf\u4e39\u9752\u5927\u58f0\u8bf4\u9053\uff1a\u201c\u6211\u5230\u6d77\u8fb9\u8d70\u4e00\u8d70\u53bb\uff01\u201d\u63a8\u5f00\u95e8\uff0c\u53c8\u6084\u6084\u7684\u5173\u4e86\u95e8\u9000\u56de\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd8\u60f3\u620f\u5f04\u536b\u5170\uff0c\u671d\u4ed6\u77aa\u4e86\u77aa\u773c\u775b\uff0c\u4e5f\u6ca1\u6709\u8bf4\u7834\uff0c\u8fc7\u4e86\u4e00\u4f1a\u513f\uff0c\u5c31\u542c\u89c1\u536b\u5170\u4ece\u697c\u4e0a\u8d70\u4e0b\u6765\u3002</p><p>\u3000\u3000\u201c\u90a3\u4e2a\u6df7\u86cb\u771f\u662f\u8ba9\u4eba\u6068\u554a\uff0c\u5728\u90a3\u4e2a\u6df7\u86cb\u56de\u6765\u4e4b\u524d\u6211\u8981\u8d76\u7d27\u6e9c\u51fa\u53bb\uff0c\u6ca1\u8138\u89c1\u4ed6\u4e86\u2026\u2026\u90a3\u6761\u5185\u88e4\u6ca1\u6cd5\u7a7f\uff0c\u6211\u5728\u697c\u4e0b\u8fd8\u6709\u6ca1\u6709\u5185\u88e4\u4e86\uff1f\u201d\u536b\u5170\u624b\u91cc\u62ff\u7740\u90a3\u6761\u5185\u88e4\u8d70\u4e0b\u697c\u68af\uff0c\u84e6\u7136\u770b\u5230\u5f20\u606a\u4e00\u8138\u574f\u7b11\u7684\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u53c8\u7f9e\u53c8\u607c\u7684\u5c06\u968f\u624b\u5c06\u624b\u91cc\u7684\u4e1c\u897f\u5c31\u5c06\u5f20\u606a\u90a3\u5f20\u574f\u7b11\u7740\u7684\u8138\u4e22\u8fc7\u53bb\uff0c\u7b49\u4e22\u51fa\u53bb\uff0c\u624d\u60f3\u8d77\u90a3\u662f\u8981\u62ff\u4e0b\u6765\u6362\u6d17\u7684\u810f\u5185\u88e4\uff0c\u53c8\u8df3\u8fc7\u6765\u5c06\u5185\u88e4\u4ece\u5f20\u606a\u624b\u91cc\u62a2\u8fc7\u53bb\uff0c\u81ea\u5df1\u90fd\u4e0d\u597d\u610f\u601d\u7684\u7b11\u4e86\u8d77\u6765\uff0c\u8138\u988a\u98de\u7ea2\uff0c\u62ff\u7740\u5185\u88e4\u5f80\u536b\u751f\u95f4\u91cc\u8eb2\uff0c\u8fd8\u4e0d\u5fd8\u624b\u906e\u7740\u7761\u88d9\u540e\u6446\uff0c\u8eab\u5b50\u5f80\u540e\u6485\u7740\uff0c\u514d\u5f97\u518d\u7ed9\u5f20\u606a\u770b\u5230\u5979\u7684\u5149\u81c0\u3002</p><p>\u3000\u3000\u770b\u5230\u536b\u5170\u7684\u72fc\u72c8\u6837\uff0c\u7fdf\u4e39\u9752\u6ca1\u826f\u5fc3\u7684\u7b11\u5f97\u76f4\u5598\u4e0d\u8fc7\u6c14\u6765\uff0c\u8fc7\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u536b\u5170\u624d\u4ece\u536b\u751f\u95f4\u91cc\u6362\u597d\u5185\u88e4\u51fa\u6765\uff0c\u8138\u988a\u98de\u7ea2\uff0c\u8ddf\u6e17\u8840\u4f3c\u7684\uff0c\u65e0\u6bd4\u7684\u5a07\u5a9a\uff0c\u5979\u770b\u7740\u7fdf\u4e39\u9752\u8fd8\u5728\u90a3\u91cc\u6ca1\u826f\u5fc3\u7684\u7b11\uff0c\u8dd1\u8fc7\u53bb\u72e0\u72e0\u7684\u6390\u7740\u5979\u7684\u81c0\u8089\uff1a\u201c\u767d\u5bf9\u4f60\u6709\u4fe1\u5fc3\u4e86\uff0c\u8ddf\u90a3\u6df7\u86cb\u5408\u4f19\u9a97\u6211\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u7b11\u7740\u5e2e\u536b\u5170\u5c06\u65e9\u9910\u51c6\u5907\u597d\uff0c\u5f20\u606a\u4e5f\u5750\u5230\u9910\u684c\u8fd9\u8fb9\u6765\uff0c\u536b\u5170\u8138\u522b\u8fc7\u53bb\uff0c\u5f88\u662f\u4e0d\u597d\u610f\u601d\uff0c\u81ea\u5df1\u60f3\u60f3\u4e5f\u8981\u7b11\uff0c\u4e00\u987f\u65e9\u9910\u90fd\u545b\u4e86\u597d\u51e0\u56de\u3002</p><p>\u3000\u3000\u201c\u4f60\u65e9\u4e0a\u8bf4\u65e0\u4eba\u6d77\u5c9b\u505a\u4ec0\u4e48\uff1f\u201d\u7fdf\u4e39\u9752\u60f3\u7740\u5f20\u606a\u521a\u624d\u8bf4\u7684\u4e8b\u3002</p><p>\u3000\u3000\u201c\u6211\u633a\u611f\u5174\u8da3\u7684\u2014\u2014\u51ac\u5929\u60f3\u5230\u53bb\u6d77\u8fb9\u6e38\u6cf3\uff0c\u5728\u8fd9\u91cc\u6709\u4e00\u5904\u79c1\u4eba\u6d77\u5c9b\u4f1a\u4e0d\u4f1a\u5f88\u8212\u9002\uff1f\u201d\u5f20\u606a\u95ee\u7fdf\u4e39\u9752\u3002</p><p>\u3000\u3000\u201c\u5443\uff01\u201d\u7fdf\u4e39\u9752\u5f20\u4e86\u5f20\u5634\uff0c\u8fc7\u4e86\u534a\u664c\uff0c\u624d\u95ee\u9053\uff0c\u201c\u4e70\u5ea7\u6d77\u5c9b\u7eaf\u7cb9\u662f\u4e3a\u4e86\u4f60\u4e2a\u4eba\u4eab\u53d7\uff1f\u201d</p><p>\u3000\u3000\u201c\u90a3\u5f53\u7136\uff0c\u4e0d\u7136\u6211\u8fd9\u4e48\u62fc\u547d\u7684\u8d5a\u94b1\u5e72\u561b\uff1f\u201d\u5f20\u606a\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u54ea\u91cc\u6709\u62fc\u547d\u4e86\uff1f\u201d\u536b\u5170\u6574\u4e2a\u65e9\u4e0a\u8fd9\u65f6\u5019\u624d\u53cd\u9a73\u5f20\u606a\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u5fc3\u60f3\u6628\u5929\u665a\u4e0a\u5c31\u5f88\u62fc\u547d\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u95ee\u9053\uff1a\u201c\u8981\u6e21\u5047\u7684\u8bdd\uff0c\u4e3a\u4ec0\u4e48\u4e0d\u5728\u9a6c\u5c14\u4ee3\u592b\u6295\u8d44\u65e0\u4eba\u6d77\u5c9b\uff0c\u6fb3\u5927\u5229\u4e9a\u8fd9\u8fb9\u662f\u4ec0\u4e48\u653f\u7b56\uff0c\u6211\u8fd8\u6ca1\u6709\u4e86\u89e3\u8fc7\uff0c\u6761\u4ef6\u5e94\u8be5\u4f1a\u6bd4\u9a6c\u5c14\u4ee3\u592b\u8981\u4e25\u683c\u5f97\u591a\u3002\u201d</p><p>\u3000\u3000\u8981\u6068\u8c01\uff0c\u800c\u4e14\u8fd8\u6068\u5f97\u5165\u9aa8\uff0c\u5c31\u9f13\u52a8\u4ed6\u53bb\u9a6c\u5c14\u4ee3\u592b\u2014\u2014\u6700\u597d\u662f\u5370\u5c3c\u2014\u2014\u8d2d\u4e70\u65e0\u4eba\u6d77\u5c9b\uff0c\u7136\u800c\u96f6\u56db\u5e74\u51ac\u5929\u8ba9\u4ed6\u4eec\u53bb\u90a3\u91cc\u6e21\u5047\u3002</p><p>\u3000\u3000\u5f20\u606a\u5f53\u7136\u4e0d\u4f1a\u53bb\u9a6c\u5c14\u4ee3\u592b\u6295\u8d44\u65e0\u4eba\u6d77\u5c9b\uff0c\u8ddf\u7fdf\u4e39\u9752\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u4e0d\u559c\u6b22\u9a6c\u5c14\u4ee3\u592b\uff0c\u6fb3\u5927\u5229\u4e9a\u8d2d\u4e70\u65e0\u4eba\u6d77\u5c9b\u4f1a\u6709\u4ec0\u4e48\u6761\u4ef6\uff1f\u8981\u6c42\u79fb\u6c11\u5417\uff1f\u90a3\u4f60\u5c31\u79fb\u6c11\u8fc7\u6765\u597d\u4e86\uff0c\u8fd9\u8fb9\u7684\u516c\u53f8\u8981\u529e\u4ec0\u4e48\u4e8b\u60c5\uff0c\u6216\u8005\u662f\u6cd5\u4eba\u4ee3\u8868\u4ec0\u4e48\u7684\uff0c\u6709\u4e2a\u6fb3\u5927\u5229\u4e9a\u7c4d\u4f1a\u66f4\u65b9\u4fbf\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4f60\u8ba9\u6211\u79fb\u6c11\uff1f\u201d\u7fdf\u4e39\u9752\u6123\u4e86\u6123\uff0c\u7f8e\u7738\u5fae\u7741\u770b\u7740\u5f20\u606a\uff0c\u62d2\u7edd\u7684\u610f\u601d\u5f88\u662f\u660e\u663e\u3002</p><p>\u3000\u3000\u201c\u4f60\u60f3\u54ea\u91cc\u53bb\u4e86\uff0c\u6ca1\u4e8b\u6211\u9001\u4f60\u5ea7\u8352\u5c9b\u5e72\u4ec0\u4e48\uff1f\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u56fd\u5185\u53c8\u4f20\u6765\u6d88\u606f\uff0c\u6709\u4e9b\u4eba\u5728\u90a3\u91cc\u8e66\u8fbe\u4e86\uff0c\u60f3\u67e5\u9526\u6e56\u7684\u8d26\u2014\u2014\u867d\u7136\u6211\u8fd9\u4eba\u4ece\u4e0a\u5230\u4e0b\u5e72\u5e72\u51c0\u51c0\u7684\u8ddf\u5904\u7537\u4e00\u6837\uff0c\u6211\u5c31\u662f\u4e0d\u9ad8\u5174\u522b\u4eba\u6765\u67e5\u6211\u7684\u8d26\uff0c\u4e3a\u4e86\u8ddf\u4ed6\u4eec\u7ee7\u7eed\u6349\u8ff7\u85cf\uff0c\u6fb3\u5927\u5229\u4e9a\u8fd9\u8fb9\u7684\u8d26\u76ee\uff0c\u6211\u4eec\u4e5f\u8981\u641e\u5f97\u590d\u6742\u4e00\u4e9b\u3002\u201d</p><p>\u3000\u3000\u201c\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u7684\u79c1\u4e0b\u534f\u8bae\uff0c\u4ee5\u53ca\u4ece\u4e1c\u5357\u4e9a\u91d1\u878d\u98ce\u66b4\u91cc\u5377\u7684\u94b1\uff0c\u603b\u662f\u4e2a\u95ee\u9898\uff0c\u201d\u7fdf\u4e39\u9752\u76b1\u7740\u7709\u5934\u8bf4\u9053\uff0c\u201c\u8fd9\u4e9b\u4e8b\u60c5\u867d\u7136\u5df2\u7ecf\u662f\u5404\u65b9\u90fd\u9ed8\u8ba4\u7684\u4e8b\u60c5\uff0c\u8981\u662f\u8ba9\u4e00\u4e9b\u4eba\u5c06\u4e8b\u60c5\u6345\u5230\u56fd\u5916\uff0c\u8fd8\u662f\u6709\u4e9b\u9ebb\u70e6\u7684\uff1f\u201d</p><p>\u3000\u3000\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u65f6\u72d9\u51fb\u4e1c\u5357\u4e9a\u5404\u56fd\u8d27\u5e01\u4f53\u7cfb\uff0c\u867d\u7136\u6ca1\u6709\u4ec0\u4e48\u6cd5\u5f8b\u4e0a\u7684\u9057\u60a3\uff0c\u4f46\u662f\u66b4\u9732\u51fa\u6765\u8fd8\u662f\u4f1a\u4f24\u5bb3\u4e1c\u5357\u4e9a\u6c11\u4f17\u7684\u611f\u60c5\uff0c\u4e94\u4ebf\u4eba\u53e3\u7684\u5e02\u573a\uff0c\u662f\u9526\u6e56\u65e0\u6cd5\u8f7b\u6613\u5272\u5f03\u7684\u3002</p><p>\u3000\u3000\u53e6\u5916\u4e0e\u5fb7\u4eea\u3001\u65af\u9ad8\u67cf\u6700\u521d\u5728\u789f\u673a\u89e3\u7801\u6280\u672f\u4e0a\u79c1\u7acb\u7684\u534f\u8bae\u89e6\u72af\u4e86\u56fd\u9645\u53cd\u5784\u65ad\u516c\u7ea6\uff0c\u867d\u7136\u9526\u6e56\u53ef\u4ee5\u8bf4\u56fd\u5185\u6ca1\u6709\u4ec0\u4e48\u53cd\u5784\u65ad\u6cd5\uff0c\u8fd9\u79cd\u884c\u4e3a\u4e5f\u5b8c\u5168\u79f0\u4e0d\u4e0a\u72af\u6cd5\uff0c\u4f46\u662f\u5374\u89e6\u52a8\u6b27\u7f8e\u7684\u53cd\u5784\u65ad\u6cd5\u5f8b\u4f53\u7cfb\uff0c\u6345\u51fa\u53bb\uff0c\u5bf9\u9526\u6e56\u5728\u6b27\u7f8e\u5e02\u573a\u4e0a\u7684\u4e1a\u52a1\u4e5f\u4f1a\u6709\u8d1f\u9762\u5f71\u54cd\u3002</p><p>\u3000\u3000\u8d27\u5e01\u6295\u673a\u7684\u4e8b\u60c5\uff0c\u4e0d\u7528\u62c5\u5fc3\u4f1a\u7ed9\u4eba\u6293\u5230\u628a\u67c4\uff0c\u9664\u975e\u5f7b\u67e5\u9526\u6e56\u7684\u8d26\u76ee\uff0c\u624d\u80fd\u4ece\u8d44\u91d1\u7684\u8d70\u5411\u4e0a\u770b\u51fa\u86db\u4e1d\u9a6c\u8ff9\uff0c\u5012\u662f\u789f\u673a\u89e3\u7801\u82af\u7247\u4e0a\u4f1a\u6709\u4e00\u4e9b\u9ebb\u70e6\u3002\u65af\u9ad8\u67cf\u4e0e\u5fb7\u4eea\u90fd\u662f\u6d77\u5916\u4e0a\u5e02\u516c\u53f8\uff0c\u8981\u662f\u4ed6\u4eec\u627f\u53d7\u4e0d\u4f4f\u6b27\u7f8e\u53f8\u6cd5\u4f53\u7cfb\u65bd\u52a0\u7684\u538b\u529b\u3001\u4e0d\u6253\u81ea\u62db\uff0c\u9526\u6e56\u4e5f\u53ea\u80fd\u8ddf\u7740\u66b4\u9732\u3002</p><p>\u3000\u3000\u4e0d\u8fc7\u8bdd\u53c8\u8bf4\u4e86\u56de\u6765\uff0c\u8fd9\u79cd\u79c1\u4e0b\u8ba2\u7acb\u6280\u672f\u6216\u4ea7\u54c1\u540c\u76df\u5784\u65ad\u5e02\u573a\u7684\u884c\u4e3a\uff0c\u6b27\u7f8e\u4f01\u4e1a\u4e5f\u6ca1\u6709\u5c11\u5e72\u3002\u9762\u4e34\u8d85\u989d\u5229\u6da6\u7684\u8bf1\u60d1\uff0c\u8fd8\u4e00\u672c\u6b63\u7ecf\u7684\u5728\u90a3\u91cc\u88c5\u5723\u4eba\u7684\u5f88\u5c11\uff0c\u4e0d\u4f1a\u9020\u6210\u7279\u522b\u4e25\u91cd\u7684\u5f71\u54cd\uff0c\u5f88\u53ef\u80fd\u4f1a\u9762\u4e34\u6b27\u7f8e\u53f8\u6cd5\u4f53\u7cfb\u7684\u5904\u7f5a\u3002</p><p>\u3000\u3000\u6fb3\u5927\u5229\u4e9a\u662f\u4e2a\u79fb\u6c11\u56fd\u5bb6\uff0c\u6574\u4e2a\u56fd\u5bb6\u5bf9\u4e9a\u6d32\u79fb\u6c11\u3001\u5bf9\u534e\u4eba\u90fd\u6ca1\u6709\u6b67\u89c6\u6027\u7684\u6392\u65a5\u3002\u5c06\u6765\uff0c\u6fb3\u5927\u5229\u4e9a\uff0c\u7279\u522b\u662f\u897f\u6fb3\u6d32\uff0c\u5c06\u662f\u9526\u6e56\u7ecf\u6d4e\u6784\u5c40\u4e2d\u5728\u6d77\u5916\u6700\u91cd\u8981\u7684\u73af\u8282\u4e4b\u4e00\uff0c\u6295\u8d44\u4e4b\u91cd\u662f\u5176\u4ed6\u6d77\u5916\u5730\u533a\u65e0\u6cd5\u76f8\u63d0\u5e76\u8bba\uff0c\u7fdf\u4e39\u9752\u79fb\u6c11\u5165\u6fb3\u5927\u5229\u4e9a\u7c4d\uff0c\u6709\u4e9b\u4e8b\u60c5\u7684\u786e\u4f1a\u65b9\u4fbf\u8bb8\u591a\u3002</p><p>\u3000\u3000\u201c\u4e5f\u6ca1\u6709\u4ec0\u4e48\u7279\u522b\u597d\u62c5\u5fc3\u7684\uff0c\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u4f60\u5148\u529e\u79fb\u6c11\u8fc7\u6765\uff0c\u6210\u7acb\u4e00\u5bb6\u79c1\u4eba\u516c\u53f8\uff0c\u5229\u7528\u79c1\u4eba\u516c\u53f8\u7684\u540d\u4e49\u5e2e\u6211\u5728\u8fd9\u91cc\u4e70\u4e00\u5ea7\u79c1\u4eba\u6d77\u5c9b\u73a9\u73a9\u3002\u201d</p><p>\u3000\u3000\u201c\u8ba9\u536b\u5170\u79fb\u6c11\u5427\uff0c\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\uff0c\u201c\u5728\u56fd\u5185\u65f6\uff0c\u6709\u4e9b\u7834\u4e8b\u786e\u5b9e\u8ba9\u4eba\u5fc3\u75b2\u529b\u5c3d\u3001\u7070\u5fc3\u4e27\u6c14\uff0c\u60f3\u7740\u80fd\u9003\u79bb\u90a3\u4e2a\u56fd\u5bb6\u5c31\u597d\u4e86\uff0c\u60f3\u60f3\u4e5f\u7b97\u4e86\uff0c\u5c31\u4ee5\u536b\u5170\u7684\u540d\u4e49\u5728\u6fb3\u5927\u5229\u4e9a\u6210\u7acb\u79c1\u4eba\u516c\u53f8\uff0c\u4ee5\u5979\u7684\u540d\u4e49\u4e70\u5ea7\u65e0\u4eba\u6d77\u5c9b\uff0c\u4f60\u7231\u600e\u4e48\u5efa\u5c31\u600e\u4e48\u5efa\u2026\u2026\u8981\u4e0d\u8981\u5e2e\u4f60\u5728\u6fb3\u6d32\u4e70\u51e0\u4e2a\u767d\u599e\uff1f\u201d</p><p>\u3000\u3000\u201c\u53c8\u8ddf\u6211\u626f\u4e0a\u4ec0\u4e48\u5173\u7cfb\uff1f\u201d\u536b\u5170\u6297\u8bae\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u65e9\u6668\u4e5f\u4e0d\u80fd\u767d\u7ed9\u4ed6\u770b\u4e86\u3002\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8fd9\u4e48\u4e00\u8bf4\uff0c\u536b\u5170\u7c89\u8138\u901a\u7ea2\u7684\u4e0d\u6562\u6297\u8bae\u4e86\uff0c\u7fdf\u4e39\u9752\u662f\u60f3\u7740\u536b\u5170\u5230\u73c0\u65af\u6765\u8bfb\u4e66\uff0c\u5c06\u79fb\u6c11\u4e0e\u8f6c\u5b66\u52a0\u6295\u8d44\u4e00\u8d77\u529e\u4e86\uff0c\u7279\u522b\u662f\u536b\u5170\u540d\u4e0b\u7684\u8d44\u4ea7\u8db3\u4ee5\u6295\u8d44\u4e00\u5ea7\u65e0\u4eba\u6d77\u5c9b\uff0c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u4f1a\u975e\u5e38\u7684\u65b9\u4fbf\u3002\u4e0d\u7136\u7684\uff0c\u4e0d\u79fb\u6c11\u5149\u8f6c\u5b66\uff0c\u624b\u7eed\u4f1a\u5341\u5206\u7684\u70e6\u7410\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u751a\u662f\u65e0\u8bed\uff0c\u536b\u5170\u7684\u8eab\u5b50\uff0c\u65e9\u5728\u4e09\u5e74\u524d\u90fd\u770b\u5149\u4e86\uff0c\u518d\u770b\u4e00\u56de\uff0c\u53c8\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\u3002\u770b\u7fdf\u4e39\u9752\u4e0e\u536b\u5170\u8fd9\u6837\u5b50\uff0c\u770b\u6765\u536b\u5170\u662f\u6253\u5b9a\u4e3b\u610f\u5230\u73c0\u65af\u6765\u966a\u7fdf\u4e39\u9752\u4e86\uff0c\u53ea\u662f\u8ba9\u536b\u5170\u79fb\u6c11\u7684\u8bdd\uff0c\u5979\u5230\u73c0\u65af\u7684\u8fc7\u7a0b\u4f1a\u66f4\u5feb\uff0c\u4e5f\u5c31\u4e0d\u8bf4\u4ec0\u4e48\u3002\u4ed6\u6295\u8d44\u65e0\u4eba\u6d77\u5c9b\uff0c\u9664\u4e86\u81ea\u5df1\u80fd\u4e00\u7247\u5e7f\u9614\u7684\u79c1\u4eba\u9886\u5730\u56fe\u4eab\u53d7\u4e4b\u5916\uff0c\u8fd8\u60f3\u7740\u7b49\u73c0\u65af\u94c1\u77ff\u7ecf\u6d4e\u5230\u96f6\u4e09\u5e74\u7a81\u98de\u731b\u8fdb\u3001\u9aa4\u7136\u95f4\u6269\u5927\u5341\u500d\u4e4b\u540e\uff0c\u8fd9\u7247\u6d77\u5bf9\u9762\u7684\u6d77\u5c9b\u5730\u4ea7\u4e0e\u623f\u4ea7\u4ef7\u503c\u4e5f\u4f1a\u6025\u5267\u4e0a\u5347\u2014\u2014\u73b0\u5728\uff0c\u897f\u6fb3\u6d32\u4e3a\u4e86\u5438\u5f15\u6295\u8d44\uff0c\u5730\u4ef7\u751a\u81f3\u6bd4\u5efa\u90ba\u8fd8\u8981\u4fbf\u5b9c\uff0c\u65e0\u4eba\u6d77\u5c9b\uff0c\u53ea\u8981\u6709\u4eba\u613f\u610f\u6295\u8d44\u5f00\u53d1\uff0c\u51e0\u4e4e\u662f\u767d\u9001\uff0c\u597d\u4e9b\u518d\u8fc7\u4e24\u5e74\uff0c\u6fb3\u5927\u5229\u4e9a\u4e3a\u4e86\u4fdd\u62a4\u6d77\u5c9b\u81ea\u7136\u73af\u5883\uff0c\u5c31\u7acb\u6cd5\u7981\u6b62\u79c1\u4eba\u8d2d\u4e70\u6d77\u5c9b\u4e86\u3002</p></article>"}}}]);
