(window.webpackJsonp=window.webpackJsonp||[]).push([[106],{128:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u5341\u516b\u8282 \u6211\u60f3\u8ba9\u4f60\u6b7b\u6389",content:"<article><h2>\u7b2c\u5341\u516b\u8282 \u6211\u60f3\u8ba9\u4f60\u6b7b\u6389</h2><p>\u91d1\u53d1\u9752\u5e74\u7b11\u4e86\u8d77\u6765\u3002\u201c\u96be\u602a\u6211\u8fdc\u8fdc\u5730\u5c31\u89c9\u5f97\u6709\u79cd\u7279\u522b\u719f\u6089\u7684\u611f\u89c9\uff0c\u201d\u4ed6\u8bf4\uff0c\u624b\u6307\u629a\u6469\u7740\u76fe\u724c\u7684\u8868\u9762\uff0c\u201c\u505a\u5de5\u4e0d\u9519\uff0c\u201d\u4ed6\u5938\u5956\u7740\uff0c\u4eff\u4f5b\u5728\u8bc4\u4ef7\u7684\u6839\u672c\u5c31\u4e0d\u662f\u81ea\u5df1\u7684\u9cde\u7247\u4f3c\u7684\uff0c\u201c\u9644\u9b54\u2026\u2026\u6d3b\u5316\u2026\u2026\u5514\uff0c\u201d\u4ed6\u5fae\u5fae\u95ed\u4e0a\u773c\u775b\uff0c\u611f\u53d7\u7740\uff0c\u201c\u8fd8\u6709\u8679\u5f69\u56fe\u7eb9\u2026\u2026\u54a6\uff1f\u201d</p><p>\u4ed6\u60ca\u8bb6\u4e86\u4e00\u58f0\uff0c\u4e0d\u77e5\u53d1\u73b0\u4e86\u4ec0\u4e48\uff0c\u8f6c\u5934\u671d\u743c\u6069\u770b\u4e86\u4e00\u773c\uff0c\u201c\u8fd9\u76fe\u724c\u662f\u8c01\u7ed9\u4f60\u7684\u2026\u2026\u554a\uff0c\u662f\u4f60\u554a\u3002\u201d</p><p>\u5f53\u65e5\u5728\u9634\u9b42\u57ce\uff0c\u4e0d\u8fc7\u5306\u5306\u4e00\u9762\uff0c\u743c\u6069\u540e\u6765\u77e5\u9053\u5bf9\u65b9\u662f\u53ea\u8679\u5f69\u9f99\uff0c\u81ea\u7136\u5370\u8c61\u6df1\u523b\uff1b\u4f46\u8fd9\u4f4d\u8679\u5f69\u9f99\u5374\u4e0d\u8fc7\u5f53\u743c\u6069\u662f\u4e2a\u5f88\u666e\u901a\u7684\u8def\u4eba\u7532\uff0c\u54ea\u91cc\u653e\u5728\u5fc3\u4e0a\uff0c\u603b\u7b97\u4ed6\u8bb0\u5fc6\u529b\u60ca\u4eba\uff0c\u8fc7\u76ee\u4e0d\u5fd8\uff0c\u5c45\u7136\u53c8\u60f3\u4e86\u8d77\u6765\u3002</p><p>\u65e2\u7136\u60f3\u8d77\u743c\u6069\u662f\u8c01\uff0c\u81ea\u7136\u4e5f\u5c31\u5927\u4f53\u77e5\u9053\u4ed6\u7684\u8eab\u4efd\u6765\u5386\uff0c\u8fd9\u9762\u9f99\u9cde\u76fe\u662f\u8c01\u7ed9\u4ed6\u7684\uff0c\u7b54\u6848\u4e5f\u5c31\u4e0d\u8a00\u800c\u55bb\u4e86\u3002\u91d1\u53d1\u9752\u5e74\u5e76\u6ca1\u6709\u8ffd\u95ee\uff0c\u4e5f\u6ca1\u6709\u5c06\u76fe\u724c\u8fd8\u7ed9\u743c\u6069\uff0c\u5374\u4e5f\u6ca1\u6709\u5982\u743c\u6069\u9884\u6599\u4e2d\u90a3\u6837\u53d1\u6012\uff0c\u201c\u81ea\u6211\u4ecb\u7ecd\u4e00\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u6211\u662f\u745e\u6069\u65af\u5766\uff0c\u201d\u8679\u5f69\u9f99\u5411\u6d1e\u4e2d\u7684\u4e09\u4e2a\u4eba\u70b9\u4e86\u70b9\u5934\uff0c\u7b97\u662f\u6253\u62db\u547c\uff0c\u201c\u5bf9\u4e86\uff0c\u8fd9\u9762\u76fe\u724c\u80fd\u501f\u6211\u770b\u770b\u5417\uff1f\u201d\u6700\u540e\u8fd9\u53e5\u8bdd\u662f\u5bf9\u743c\u6069\u8bf4\u7684\u3002</p><p>\u743c\u6069\u96be\u9053\u8fd8\u80fd\u62d2\u7edd\u4e48\u3002</p><p>\u201c\u51fa\u53bb\u518d\u8bf4\u5427\uff0c\u201d\u81ea\u79f0\u745e\u6069\u65af\u5766\u7684\u8679\u5f69\u9f99\u8bf4\uff0c\u4f9d\u65e7\u5fae\u7b11\u7740\uff0c\u4ed6\u7684\u773c\u5149\u4ece\u4e09\u4e2a\u4eba\u7684\u8138\u4e0a\u4e00\u63a0\u800c\u8fc7\uff0c\u5728\u6885\u83f2\u65af\u8eab\u4e0a\u7a0d\u7a0d\u505c\u7559\u4e86\u4e00\u4e0b\uff0c\u4f46\u4e5f\u968f\u5373\u82e5\u65e0\u5176\u4e8b\u5730\u907f\u8fc7\uff0c\u201c\u6d1e\u91cc\u7a7a\u6c14\u5f88\u7cdf\u7cd5\u7684\u3002\u201d\u4ed6\u5c06\u76fe\u724c\u653e\u5165\u81ea\u5df1\u53e3\u888b\u91cc\uff0c\u6bb7\u52e4\u5730\u4f38\u624b\u53bb\u6276\u6885\u83f2\u65af\uff0c\u5374\u88ab\u62d2\u7edd\u4e86\u3002</p><p>\u201c\u591a\u8c22\u3002\u201d\u6885\u83f2\u65af\u4f4e\u58f0\u8bf4\uff0c\u5979\u521a\u624d\u7ad9\u4e86\u8fd9\u534a\u5929\uff0c\u5df2\u7ecf\u7565\u7565\u6062\u590d\u70b9\u529b\u6c14\uff0c\u5f3a\u81ea\u652f\u6491\u7740\uff0c\u4e0d\u80af\u8981\u4ed6\u4eba\u5e2e\u5fd9\u3002\u745e\u6069\u65af\u5766\u89c1\u72b6\uff0c\u793c\u8c8c\u5730\u7f29\u56de\u624b\uff0c\u8eac\u8eab\u9000\u5f00\uff0c\u4ed6\u7684\u8eab\u4f53\u5468\u56f4\u5374\u7a81\u7136\u6cdb\u8d77\u4e00\u5c42\u73cd\u73e0\u4f3c\u7684\u5149\u8292\uff0c\u968f\u5373\u4ece\u4e2d\u5c04\u51fa\u4e24\u675f\u5c04\u7ebf\uff0c\u6253\u5728\u6885\u83f2\u65af\u8eab\u4e0a\u3002</p><p>\u6885\u83f2\u65af\u88ab\u8fd9\u4e24\u9053\u73cd\u73e0\u8272\u5c04\u7ebf\u51fb\u4e2d\uff0c\u539f\u672c\u82cd\u767d\u7684\u8138\u4e0a\u767b\u65f6\u4e00\u9635\u7ea2\u6da6\uff0c\u7cbe\u795e\u660e\u663e\u4e00\u632f\u3002\u201c\u8c22\u8c22\u3002\u201d\u5973\u5723\u6b66\u58eb\u518d\u6b21\u8bf4\uff0c\u5fae\u5fae\u4e00\u8eac\uff0c\u884c\u4e86\u4e2a\u9a91\u58eb\u793c\u3002</p><p>\u743c\u6069\u770b\u4e0d\u51fa\u745e\u6069\u65af\u5766\u7528\u7684\u662f\u4ec0\u4e48\u6cd5\u672f\u3002\u6309\u9053\u7406\u8bf4\uff0c\u5965\u672f\u9075\u5faa\u7684\u57fa\u672c\u539f\u5219\u662f\u7b49\u4ef7\u4ea4\u6362\uff0c\u56e0\u4e3a\u6ca1\u6709\u4e00\u4e2a\u795e\u7947\u505a\u5f3a\u5927\u540e\u76fe\uff0c\u5965\u6cd5\u8005\u65e0\u4ece\u501f\u529b\uff0c\u57fa\u672c\u4e5f\u5c31\u6ca1\u6709\u80fd\u4e3a\u4ed6\u4eba\u6cbb\u7597\u75ca\u6108\u7c7b\u578b\u7684\u9b54\u6cd5\uff0c\u81f3\u591a\u4e5f\u5c31\u662f\u5438\u8840\u9b3c\u4e4b\u89e6\u8fd9\u79cd\u8f6c\u79fb\u751f\u547d\u529b\u7684\uff0c\u6cbb\u7597\u4e86\u4e00\u4e2a\uff0c\u5c31\u8981\u4f24\u5bb3\u53e6\u5916\u4e00\u4e2a\u3002\u745e\u6069\u65af\u5766\u521a\u624d\u4f7f\u7528\u7684\uff0c\u5012\u50cf\u662f\u79cd\u795e\u672f\uff0c\u4f46\u8679\u5f69\u9f99\u662f\u5929\u751f\u7684\u5965\u6cd5\u8005\uff0c\u53ef\u80fd\u662f\u5deb\u5e08\u4e5f\u53ef\u80fd\u662f\u672f\u58eb\uff0c\u5374\u5e94\u8be5\u4e0d\u4f1a\u53bb\u7814\u7a76\u795e\u672f\u5f53\u7267\u5e08\uff0c\u5426\u5219\u5c82\u4e0d\u7b49\u82e5\u662f\u6d6a\u8d39\u5929\u8d4b\u3002</p><p>\u4ed6\u89c1\u8bc6\u4e0d\u8db3\uff0c\u4e5f\u770b\u4e0d\u51fa\u6765\uff0c\u745e\u6069\u65af\u5766\u81ea\u7136\u4e5f\u4e0d\u89e3\u91ca\u3002\u201c\u4e3a\u60a8\u8fd9\u6837\u7f8e\u4e3d\u7684\u5973\u58eb\u6548\u52b3\uff0c\u662f\u6211\u7684\u8363\u5e78\u3002\u201d\u4ed6\u5f6c\u5f6c\u6709\u793c\u5730\u8bf4\uff0c\u62ac\u624b\u53c8\u662f\u865a\u865a\u5f53\u7a7a\u4e00\u62b9\uff0c\u743c\u6069\u5b8c\u5168\u6ca1\u6709\u542c\u5230\u4ed6\u5ff5\u8bf5\u5492\u8bed\uff0c\u751a\u81f3\u5634\u5507\u90fd\u6ca1\u6709\u52a8\uff0c\u7a7a\u4e2d\u5df2\u7ecf\u51fa\u73b0\u4e86\u4e00\u53ea\u900f\u660e\u7684\u94f6\u8272\u5de8\u638c\uff0c\u5f80\u5750\u5728\u5730\u4e0a\u7684\u5c11\u5e74\u827e\u683c\u5170\u7279\u8eab\u4e0a\u4e00\u62b9\u3002</p><p>\u51e0\u7f15\u9ed1\u6c14\u4ece\u5c11\u5e74\u80f3\u818a\u4e0a\u7684\u4f24\u53e3\u5904\u84b8\u817e\u8d77\u6765\uff0c\u88ab\u94f6\u8272\u5de8\u638c\u4e00\u628a\u634f\u4f4f\uff0c\u540c\u65f6\u6563\u5f52\u65e0\u5f62\u3002\u5c11\u5e74\u5982\u91ca\u91cd\u8d1f\u4e00\u822c\uff0c\u6323\u624e\u7740\u7ad9\u4e86\u8d77\u6765\uff0c\u5411\u745e\u6069\u65af\u5766\u9053\u8c22\u3002</p><p>\u8679\u5f69\u9f99\u5fae\u7b11\u7740\uff0c\u8f6c\u8eab\u8d70\u51fa\u6d1e\u53e3\u3002\u743c\u6069\u7b49\u4eba\u8ddf\u7740\u8d70\u4e86\u51fa\u6765\uff0c\u4ed6\u53d1\u73b0\u81ea\u5df1\u51fa\u73b0\u5728\u70db\u5821\u4e2d\u5fc3\u9ad8\u5854\u524d\u7684\u82b1\u56ed\uff0c\u4e00\u4e2a\u767d\u80e1\u987b\u8001\u4eba\u6b63\u5728\u8349\u5730\u4e0a\uff0c\u770b\u7740\u4ed6\u4eec\u3002\u4ed6\u7a7f\u7740\u7070\u8272\u7684\u957f\u888d\uff0c\u6000\u4e2d\u62b1\u7740\u4e00\u672c\u8d85\u7ea7\u539a\u7684\u4e66\uff0c\u6234\u7740\u84dd\u8272\u7684\u6c34\u6676\u773c\u955c\uff0c\u795e\u60c5\u770b\u8d77\u6765\u9887\u4e3a\u75b2\u60eb\uff0c\u4eff\u4f5b\u4e00\u591c\u6ca1\u7761\u4f3c\u7684\u3002</p><p>\u201c\u5377\u518c\u5b88\u62a4\u8005\uff0c\u201d\u745e\u6069\u65af\u5766\u671d\u767d\u80e1\u987b\u8001\u4eba\u6253\u62db\u547c\uff0c\u201c\u5192\u6627\u6765\u8bbf\uff0c\u6253\u6270\u4e86\u3002\u201d</p><p>\u8001\u4eba\u5fae\u5fae\u70b9\u5934\u7b97\u662f\u8fd8\u793c\u3002\u5c11\u5e74\u827e\u683c\u5170\u7279\u4ece\u4eba\u7fa4\u80cc\u540e\u8f6c\u51fa\u6765\uff0c\u4e0a\u524d\u8eac\u8eab\u884c\u793c\uff0c\u5c06\u624b\u91cc\u7684\u4e66\u9012\u7ed9\u8001\u4eba\u3002\u201c\u4e4c\u5c14\u5170\u7279\u9601\u4e0b\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u90fd\u5728\u8fd9\u91cc\u4e86\u3002\u201d</p><p>\u8001\u4eba\u63a5\u8fc7\u4e66\uff0c\u5e76\u4e0d\u7ec6\u770b\uff0c\u6325\u6325\u624b\uff0c\u5c11\u5e74\u97a0\u4e86\u8eac\uff0c\u6084\u6084\u9000\u4e0b\uff0c\u7a7f\u8fc7\u82b1\u56ed\u8d70\u5230\u4e00\u5ea7\u9ad8\u5854\u91cc\u53bb\u4e86\u3002\u201c\u6b22\u8fce\uff0c\u745e\u6069\u65af\u5766\u5148\u751f\uff0c\u201d\u8001\u4eba\u5bf9\u8679\u5f69\u9f99\u8bf4\uff0c\u201c\u60a8\u5f88\u4e45\u6ca1\u6709\u6765\u70db\u5821\u4e86\uff0c\u8fd9\u6b21\u60f3\u5fc5\u53c8\u7ed9\u6211\u5e26\u6765\u4ec0\u4e48\u65b0\u5947\u6709\u8da3\u7684\u6d88\u606f\u5427\u3002\u201d</p><p>\u91d1\u53d1\u9752\u5e74\u5fae\u7b11\u7740\uff0c\u201c\u81ea\u7136\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u81ea\u7136\uff0c\u4e00\u4e9b\u4f60\u80af\u5b9a\u611f\u5174\u8da3\u7684\u6d88\u606f\u2014\u2014\u5173\u4e8e\u9634\u9b42\u57ce\u7684\uff0c\u5982\u4f55\uff1f\u201d</p><p>\u743c\u6069\u5fc3\u4e2d\u5fae\u5fae\u4e00\u60ca\u3002</p><p>\u201c\u9634\u9b42\u57ce\uff1f\u597d\u6781\u4e86\uff01\u201d\u8001\u4eba\u987f\u65f6\u4e24\u773c\u653e\u5149\uff0c\u201c\u665a\u4e0a\u6211\u4eec\u5171\u8fdb\u665a\u9910\u5982\u4f55\uff0c\u201d\u4ed6\u6bb7\u52e4\u5730\u9080\u8bf7\u7740\uff0c\u201c\u8fb9\u5403\u8fb9\u8c08\u3002\u201d</p><p>\u201c\u80fd\u4e0e\u70db\u5821\u7684\u5377\u518c\u5b88\u62a4\u8005\u5171\u8fdb\u665a\u9910\uff0c\u8fd9\u662f\u6240\u6709\u5411\u5f80\u77e5\u8bc6\u7684\u4eba\u68a6\u5bd0\u4ee5\u6c42\u7684\u8363\u8000\uff0c\u201d\u745e\u6069\u65af\u5766\u5fae\u5fae\u8eac\u8eab\uff0c\u56de\u7b54\u8bf4\uff0c\u201c\u4e0d\u8fc7\uff0c\u6211\u6709\u4e2a\u5c0f\u5c0f\u7684\u5efa\u8bae\u3002\u201d</p><p>\u201c\u8bf7\u8bf4\u3002\u201d</p><p>\u201c\u6211\u8fd9\u4f4d\u670b\u53cb\u2026\u2026\u5443\uff0c\u7a0d\u7b49\uff0c\u201d\u8679\u5f69\u9f99\u8f6c\u8fc7\u5934\u6765\uff0c\u95ee\u743c\u6069\uff0c\u201c\u4e0d\u597d\u610f\u601d\uff0c\u5fd8\u4e86\u8bf7\u6559\u4f60\u7684\u540d\u5b57\u3002\u201d</p><p>\u201c\u743c\u6069\xb7\u5170\u5c3c\u65af\u7279\u3002\u201d\u743c\u6069\u5c0f\u5fc3\u5730\u8bf4\uff0c\u4ed6\u611f\u89c9\u5728\u8fd9\u79cd\u573a\u5408\u5b8c\u5168\u63d2\u4e0d\u4e0a\u8bdd\u3002</p><p>\u201c\u90a3\u6211\u53eb\u4f60\u743c\u6069\uff0c\u53ef\u4ee5\u5427\uff0c\u201d\u8679\u5f69\u9f99\u8bf4\uff0c\u4e0d\u7b49\u743c\u6069\u7b54\u5e94\uff0c\u518d\u5ea6\u8f6c\u8fc7\u5934\u770b\u7740\u8001\u4eba\uff0c\u201c\u6211\u60f3\u9080\u8bf7\u6211\u7684\u670b\u53cb\u743c\u6069\u4e00\u8d77\u53c2\u52a0\u665a\u5bb4\uff0c\u60a8\u89c9\u5f97\u6211\u8fd9\u4e2a\u63d0\u8bae\u5982\u4f55\u3002\u201d</p><p>\u8001\u4eba\u7591\u60d1\u5730\u770b\u7740\u743c\u6069\uff0c\u56e0\u4e3a\u8679\u5f69\u9f99\u58f0\u79f0\u4ed6\u4eec\u662f\u670b\u53cb\uff0c\u4f46\u5b9e\u9645\u4e0a\uff0c\u8679\u5f69\u9f99\u5728\u524d\u4e00\u79d2\u949f\u8fd8\u4e0d\u77e5\u9053\u743c\u6069\u7684\u540d\u5b57\uff0c\u8fd9\u201c\u670b\u53cb\u201d\u5173\u7cfb\u4e5f\u672a\u514d\u592a\u4e0d\u7262\u9760\u4e86\u70b9\u3002\u4f46\u8001\u4eba\u6700\u540e\u6ca1\u6709\u5728\u610f\u8fd9\u4e9b\uff0c\u201c\u5f53\u7136\u6ca1\u95ee\u9898\uff0c\u201d\u4ed6\u8bf4\uff0c\u201c\u8bf7\uff0c\u8bf7\u8fd9\u8fb9\u8d70\uff0c\u5148\u5230\u6211\u7684\u529e\u516c\u5ba4\u5427\u3002\u201d</p><p>\u4ed6\u968f\u610f\u5730\u6307\u4e86\u6307\u5730\u9762\uff0c\u743c\u6069\u968f\u5373\u8eab\u540e\u9ed1\u6c89\u6c89\u7684\u6d1e\u53e3\u81ea\u884c\u5173\u95ed\u4e86\u3002</p><p>\u203b\u203b\u203b</p><p>\u8679\u5f69\u9f99\u745e\u6069\u65af\u5766\u8ddf\u7740\u70db\u5821\u7684\u6700\u9ad8\u9886\u8896\uff0c\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u8fdb\u5165\u4e2d\u592e\u9ad8\u5854\uff0c\u4e34\u884c\u524d\u544a\u8bc9\u743c\u6069\uff0c\u5230\u65f6\u5019\u4f1a\u6709\u4eba\u6765\u9080\u8bf7\u4ed6\u53c2\u52a0\u665a\u5bb4\u3002</p><p>\u81f3\u4e8e\u90a3\u4e2a\u5973\u6740\u624b\u838e\u73de\u514b\u600e\u4e48\u529e\uff0c\u4e4c\u5c14\u5170\u7279\u5012\u4f3c\u4e4e\u5e76\u4e0d\u5173\u5fc3\u3002\u201c\u6211\u4f1a\u5904\u7406\u7684\u3002\u201d\u5f53\u743c\u6069\u5bf9\u4ed6\u63d0\u8d77\u6b64\u4e8b\u7684\u65f6\u5019\uff0c\u4e4c\u5c14\u5170\u7279\u5148\u751f\u4f3c\u4e4e\u5f88\u4e0d\u8010\u70e6\u5730\u5982\u6b64\u56de\u7b54\u3002</p><p>\u597d\u5427\uff0c\u65e2\u7136\u4f60\u8fd9\u4e2a\u70db\u5821\u7684\u9886\u8896\u90fd\u4e0d\u5728\u610f\u81ea\u5df1\u5bb6\u91cc\u6f5c\u4f0f\u7740\u4e00\u4e2a\u5371\u9669\u7684\u6740\u624b\uff0c\u90a3\u743c\u6069\u8fd8\u80fd\u8bf4\u4ec0\u4e48\u5462\u2026\u2026\u53ea\u80fd\u81ea\u5df1\u63d0\u9ad8\u8b66\u60d5\u4e86\u3002</p><p>\u4e0d\u8fc7\u5c11\u5e74\u66fe\u7ecf\u8bf4\u8fc7\uff0c\u5730\u4e0b\u7684\u9f99\u7a9f\u53ea\u6709\u4e24\u6761\u901a\u9053\uff0c\u4e00\u6761\u53ea\u80fd\u8fdb\u4e0d\u80fd\u51fa\uff0c\u4e00\u6761\u53ea\u80fd\u51fa\u4e0d\u80fd\u8fdb\uff0c\u800c\u73b0\u5728\u4ed6\u4eec\u51fa\u6765\u7684\u8fd9\u6761\u901a\u9053\u521a\u624d\u5df2\u7ecf\u88ab\u5c01\u95ed\u4e86\uff0c\u90a3\u4e48\u5973\u6740\u624b\u5e94\u8be5\u4e5f\u88ab\u5c01\u6b7b\u5728\u91cc\u9762\u548c\u94f6\u9f99\u5e7d\u7075\u4f5c\u4f34\u4e86\u5427\u3002</p><p>\u521a\u624d\u5728\u751f\u6b7b\u5173\u5934\uff0c\u5267\u53d8\u8fed\u8d77\uff0c\u4e00\u65f6\u601d\u7eea\u6709\u4e9b\u6df7\u4e71\uff0c\u5982\u4eca\u51b7\u9759\u4e0b\u6765\uff0c\u60f3\u6765\u60f3\u53bb\uff0c\u8fd8\u662f\u89c9\u5f97\u8fd9\u4e8b\u60c5\u5b9e\u5728\u83ab\u540d\u5176\u5999\u7684\u5f88\u3002\u8fd9\u4e2a\u6740\u624b\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\u5462\uff1f\u5979\u81ea\u4ece\u6765\u5230\u70db\u5821\uff0c\u4f3c\u4e4e\u9664\u4e86\u52fe\u5f15\u743c\u6069\u4e4b\u5916\uff0c\u518d\u6ca1\u505a\u8fc7\u5176\u4ed6\u4e8b\u60c5\u3002\u5979\u60f3\u6740\u743c\u6069\uff1f\u65e9\u6709\u673a\u4f1a\u53ef\u4ee5\u4e0b\u624b\uff1b\u5979\u60f3\u6740\u6885\u83f2\u65af\uff1f\u6ca1\u89c1\u6709\u4efb\u4f55\u52a8\u4f5c\u2014\u2014\u4f46\u5982\u679c\u5979\u7684\u76ee\u6807\u4e0d\u662f\u9488\u5bf9\u743c\u6069\u548c\u6885\u83f2\u65af\u7684\u8bdd\uff0c\u521a\u624d\u5728\u6d1e\u53e3\uff0c\u4e3a\u4ec0\u4e48\u53c8\u8981\u4f0f\u51fb\u4ed6\u4eec\uff1f</p><p>\u6740\u624b\u867d\u7136\u4ee5\u6740\u4eba\u4e3a\u5de5\u4f5c\uff0c\u4f46\u5374\u5e94\u8be5\u4e0d\u4f1a\u65e0\u7f18\u65e0\u6545\u5730\u6740\u4eba\uff0c\u4ed6\u4eec\u4e0d\u4f1a\u6d6a\u8d39\u529b\u6c14\u505a\u6ca1\u5fc5\u8981\u7684\u4e8b\u60c5\u3002</p><p>\u8fd8\u6709\u4e00\u70b9\u8ba9\u743c\u6069\u4e0d\u660e\u767d\u7684\u662f\uff0c\u6740\u624b\u7b2c\u4e00\u4e2a\u5bf9\u90a3\u4e2a\u53eb\u827e\u683c\u5170\u7279\u7684\u5c11\u5e74\u51fa\u624b\uff0c\u8fd9\u6709\u53ef\u80fd\u662f\u56e0\u4e3a\u4ed6\u6b63\u597d\u8d70\u5728\u524d\u9762\u7684\u539f\u56e0\u3002\u4f46\u6740\u624b\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u628a\u4ed6\u6740\u4e86\uff1f\u5c11\u5e74\u7684\u4f24\u5728\u80f3\u818a\u4e0a\uff0c\u4f3c\u4e4e\u5f88\u91cd\uff0c\u6216\u8005\u8bf4\u6740\u624b\u5728\u77ed\u5251\u4e0a\u5e94\u8be5\u6dec\u4e86\u6bd2\uff0c\u8ba9\u5c11\u5e74\u5f53\u65f6\u5b8c\u5168\u4e27\u5931\u4e86\u884c\u52a8\u80fd\u529b\u2014\u2014\u4f46\u5979\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u628a\u5c11\u5e74\u6740\u6b7b\uff1f</p><p>\u8fd9\u5e94\u8be5\u5e76\u4e0d\u96be\uff0c\u800c\u4e14\u53ef\u4ee5\u5e72\u8106\u5229\u843d\u5730\u89e3\u51b3\u6389\u4e00\u4e2a\u5bf9\u624b\uff0c\u6362\u4e86\u743c\u6069\u662f\u6740\u624b\uff0c\u65e2\u7136\u8bbe\u4e0b\u4f0f\u51fb\uff0c\u81ea\u7136\u8981\u4e0a\u6765\u5c31\u4ee5\u96f7\u9706\u4e00\u51fb\u4e4b\u52bf\u7fe6\u706d\u6389\u654c\u4eba\u7684\u6709\u751f\u529b\u91cf\uff0c\u5426\u5219\u7559\u4e86\u5bf9\u624b\u4e00\u53e3\u6c14\u5728\uff0c\u8fd8\u4e0d\u77e5\u9053\u4f1a\u6709\u4ec0\u4e48\u53d8\u6545\u53d1\u751f\u5462\u3002</p><p>\u5230\u5e95\u600e\u4e48\u56de\u4e8b\u5462\uff1f</p><p>\u770b\u770b\u65f6\u95f4\uff0c\u5df2\u7ecf\u5c06\u8981\u5230\u516b\u70b9\u949f\u4e86\uff0c\u5e73\u5e38\u6b64\u65f6\u743c\u6069\u548c\u6885\u83f2\u65af\u5e94\u8be5\u90fd\u8fdb\u9ad8\u5854\u53bb\u5f00\u59cb\u4e00\u5929\u7684\u9605\u8bfb\u4e86\uff0c\u4f46\u7ecf\u8fc7\u6628\u665a\u534a\u591c\u5230\u4eca\u65e9\u8fd9\u4e48\u4e00\u901a\u6298\u817e\uff0c\u5148\u662f\u743c\u6069\u5dee\u70b9\u88ab\u838e\u73de\u514b\u523a\u6740\uff0c\u63a5\u7740\u662f\u5168\u70db\u5821\u5927\u641c\u6355\uff0c\u7136\u540e\u53c8\u6df1\u5165\u9f99\u7a74\u548c\u4e00\u53ea\u94f6\u9f99\u5e7d\u7075\u7ea0\u7f20\u534a\u65e5\uff0c\u6700\u540e\u597d\u4e0d\u5bb9\u6613\u9003\u51fa\u6765\uff0c\u5374\u53c8\u649e\u4e0a\u6740\u624b\u4f0f\u51fb\u3002\u6885\u83f2\u65af\u4f5c\u4e3a\u4e3b\u8981\u6218\u529b\uff0c\u6bcf\u6b21\u90fd\u662f\u9996\u5f53\u5176\u51b2\uff0c\u867d\u7136\u7ecf\u8fc7\u8679\u5f69\u9f99\u7684\u6cbb\u7597\uff0c\u4f9d\u65e7\u975e\u5e38\u75b2\u60eb\uff1b\u743c\u6069\u867d\u7136\u4f5c\u4e3a\u8eb2\u5728\u540e\u9762\u7684\u5deb\u5e08\uff0c\u5176\u5b9e\u6ca1\u6709\u53d7\u4f24\uff0c\u4f46\u8fd9\u4e48\u4e00\u8def\u7cbe\u795e\u6781\u5ea6\u7d27\u5f20\u8fc7\u6765\uff0c\u6b64\u65f6\u7ec8\u4e8e\u53ef\u4ee5\u677e\u53e3\u6c14\uff0c\u770b\u89c1\u9633\u5149\u660e\u5a9a\u8349\u5730\u78a7\u7eff\uff0c\u4e5f\u662f\u4e0d\u7531\u5f97\u5927\u5927\u677e\u4e86\u53e3\u6c14\uff0c\u53ea\u60f3\u7740\u8eba\u5728\u8349\u5730\u4e0a\u4f11\u606f\uff0c\u4e0d\u60f3\u518d\u53bb\u770b\u4e66\u4e86\u3002</p><p>\u70db\u5821\u91cc\u603b\u662f\u9759\u6084\u6084\u7684\uff0c\u8fd9\u91cc\u5c45\u4f4f\u7684\u65e0\u975e\u662f\u5deb\u5e08\u3001\u7267\u5e08\u6216\u8005\u7eaf\u7cb9\u7684\u5b66\u8005\uff0c\u665a\u4e0a\u5927\u5bb6\u81ea\u7136\u4f11\u606f\uff0c\u767d\u5929\u5219\u5927\u591a\u90fd\u5728\u9ad8\u5854\u91cc\u7ffb\u9605\u4e66\u7c4d\uff0c\u5bdf\u770b\u8d44\u6599\uff0c\u6216\u8005\u5404\u81ea\u5728\u795e\u6bbf\u91cc\u5fd9\u6d3b\u3002\u56e0\u4e3a\u6536\u8d39\u6602\u8d35\uff0c\u5730\u65b9\u53c8\u6709\u4e9b\u504f\u50fb\uff0c\u5ba2\u4eba\u4e5f\u5f88\u5c11\uff0c\u4e00\u5e74\u5230\u5934\u6ca1\u51e0\u4e2a\u3002</p><p>\u6885\u83f2\u65af\u653e\u4e0b\u94f6\u5251\uff0c\u5750\u5728\u8349\u5730\u4e0a\uff0c\u53cc\u624b\u62b1\u819d\uff0c\u5c06\u8138\u57cb\u5728\u819d\u76d6\u4e2d\u95f4\uff0c\u6c89\u601d\u7740\u3002\u743c\u6069\u4e0d\u77e5\u9053\u5979\u5728\u60f3\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u6562\u591a\u95ee\uff0c\u5728\u5979\u8eab\u8fb9\u5750\u4e0b\uff0c\u770b\u7740\u56db\u5468\u3002</p><p>\u5230\u5904\u90fd\u662f\u5b89\u5b89\u9759\u9759\u7684\uff0c\u521d\u5347\u7684\u671d\u9633\u5c06\u70b9\u70b9\u788e\u91d1\u6d12\u4e86\u4e0b\u6765\uff0c\u98ce\u4ece\u679d\u5934\u8f7b\u8f7b\u63a0\u8fc7\u3002\u70db\u5821\u5efa\u5728\u6d77\u8fb9\uff0c\u95f4\u6216\u8fd8\u6709\u767d\u8179\u9ed1\u7ffc\u7684\u6d77\u9e1f\u4ece\u5934\u4e0a\u98de\u8fc7\uff0c\u743c\u6069\u7684\u89c6\u7ebf\u6162\u6162\u5ef6\u4f38\u5230\u8fdc\u5904\uff0c\u6e10\u6e10\u4e0a\u79fb\uff0c\u8d8a\u8fc7\u57ce\u5899\uff0c\u770b\u7740\u5916\u9762\u851a\u84dd\u7684\u5929\u7a7a\uff0c\u6735\u6735\u767d\u4e91\u60a0\u7136\u81ea\u5f97\u5730\u6563\u6b65\u7740\u3002\u4ed6\u7a81\u7136\u53d1\u73b0\u81ea\u5df1\u4f3c\u4e4e\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u8fd9\u6837\u60a0\u95f2\u5730\u5728\u8349\u5730\u4e0a\u5750\u7740\u770b\u98ce\u666f\u4e86\uff0c\u4e0a\u4e00\u6b21\u662f\u4ec0\u4e48\u65f6\u5019\uff1f\u4f3c\u4e4e\u8fd8\u662f\u5728\u9634\u9b42\u57ce\u5b66\u6821\u7684\u65f6\u5019\uff0c\u6bd5\u4e1a\u8003\u8bd5\u4e4b\u524d\uff0c\u548c\u8299\u857e\u72c4\u4e00\u8d77\u2026\u2026</p><p>\u4e00\u60f3\u8d77\u8299\u857e\u72c4\uff0c\u5fc3\u5934\u5fae\u5fae\u63a0\u8fc7\u4e00\u4e1d\u4e0d\u5feb\u3002\u6765\u8fd9\u4e2a\u4e16\u754c\uff0c\u8299\u857e\u72c4\u7b97\u662f\u4ed6\u7b2c\u4e00\u4e2a\u5973\u4eba\uff0c\u4e24\u4eba\u5728\u4e00\u8d77\u5ea6\u8fc7\u4e86\u5927\u534a\u5e74\u7684\u5feb\u4e50\u65f6\u5149\uff0c\u6ca1\u60f3\u5230\u6700\u540e\u5374\u662f\u5982\u6b64\u7ed3\u5c40\u3002\u4e8b\u5b9e\u4e0a\uff0c\u743c\u6069\u81f3\u4eca\u8fd8\u4e0d\u80fd\u660e\u767d\uff0c\u8299\u857e\u72c4\u4e3a\u4ec0\u4e48\u8981\u90a3\u6837\u505a\u2014\u2014\u4f46\u4ed6\u4e0d\u613f\u610f\u4ed4\u7ec6\u53bb\u60f3\uff0c\u6f5c\u610f\u8bc6\u91cc\u5728\u9003\u907f\u8fd9\u4e2a\u95ee\u9898\u3002</p><p>\u53cd\u6b63\u4e00\u5207\u90fd\u8fc7\u53bb\u4e86\uff0c\u90a3\u5c31\u90fd\u8fc7\u53bb\u5427\u3002</p><p>\u6b63\u5982\u4ed6\u4e00\u76f4\u6240\u8bf4\uff0c\u5200\u67aa\u65e0\u773c\uff0c\u613f\u8d4c\u670d\u8f93\u3002\u4e16\u754c\u4e0a\u7684\u4e8b\u60c5\uff0c\u672c\u6765\u5c31\u4e0d\u7528\u6307\u671b\u4e8b\u4e8b\u5c3d\u5982\u4eba\u610f\uff0c\u88ab\u4eba\u800d\u4e86\uff0c\u4e0a\u5f53\u4e86\uff0c\u90a3\u4e5f\u53ea\u597d\u8ba4\u683d\uff0c\u8c01\u8ba9\u81ea\u5df1\u4e0d\u591f\u806a\u660e\u3002\u4eba\u751f\u5728\u4e16\uff0c\u7b2c\u4e00\u7b49\u8981\u7d27\u7684\uff0c\u662f\u5343\u4e07\u4e0d\u53ef\u63a8\u8131\u8d23\u4efb\uff0c\u505a\u4e8b\u60c5\u5931\u8d25\u4e86\uff0c\u522b\u8bf4\u4ec0\u4e48\u5bf9\u624b\u5f3a\u5927\u3001\u72e1\u733e\u3001\u90aa\u6076\u3001\u9634\u9669\u2014\u2014\u8fd9\u4e9b\u90fd\u662f\u5e9f\u8bdd\uff0c\u771f\u6b63\u8981\u660e\u767d\u7684\u9053\u7406\u53ea\u6709\u4e00\u70b9\uff1a\u4e8f\u4e86\u5c31\u8981\u8ba4\uff0c\u8f93\u4e86\u5c31\u8981\u4ed8\uff0c\u81ea\u5df1\u7b28\u88ab\u4eba\u800d\u4e86\uff0c\u90a3\u5c31\u8001\u8001\u5b9e\u5b9e\u8ba4\u683d\uff0c\u6ca1\u4ec0\u4e48\u597d\u8bf4\u7684\u3002</p><p>\u8fd9\u4e16\u754c\u4e0a\uff0c\u8c01\u4e5f\u6ca1\u6709\u4e49\u52a1\u8981\u5bf9\u522b\u4eba\u597d\uff0c\u8c01\u4e5f\u6ca1\u6709\u4e49\u52a1\u5e2e\u52a9\u522b\u4eba\uff0c\u5584\u5f85\u522b\u4eba\u2014\u2014\u6216\u8bb8\uff0c\u6ca1\u6709\u6069\u60c5\uff0c\u53ea\u6709\u4ea4\u6613\u3002</p><p>\u5927\u7ea6\u4e5f\u662f\u5728\u8fd9\u70b9\u4e0a\uff0c\u4ed6\u548c\u6885\u83f2\u65af\uff0c\u80fd\u5728\u67d0\u79cd\u7a0b\u5ea6\u8fbe\u6210\u4e00\u81f4\u5427\u3002\u5584\u5f85\u4ed6\u4eba\uff0c\u8fd9\u662f\u4f60\u81ea\u5df1\u613f\u610f\uff1b\u4f46\u5982\u679c\u88ab\u4ed6\u4eba\u4f24\u5bb3\u3001\u6b3a\u9a97\u3001\u80cc\u53db\uff0c\u90a3\u4e48\u4e5f\u4e0d\u5fc5\u56e0\u6b64\u800c\u607c\u6012\uff0c\u56e0\u4e3a\u522b\u4eba\u5e76\u6ca1\u6709\u8981\u5bf9\u4f60\u597d\u7684\u4e49\u52a1\u3002</p><p>\u53ea\u662f\u8bf4\u662f\u53ef\u4ee5\u8bf4\u5f97\u8fd9\u4e48\u6d12\u8131\uff0c\u771f\u6b63\u81ea\u5df1\u9762\u5bf9\uff0c\u8c01\u6709\u771f\u80fd\u5c31\u6b64\u5766\u8361\u653e\u5f00\uff0c\u5fc3\u65e0\u82a5\u8482\u3002\u82e5\u662f\u771f\u80fd\u5b8c\u5168\u770b\u5f97\u5f00\uff0c\u65e0\u6240\u8c13\uff0c\u4e5f\u5c31\u4e0d\u4f1a\u5728\u60f3\u8d77\u8fd9\u4ef6\u4e8b\u7684\u65f6\u5019\uff0c\u4f9d\u65e7\u89c9\u5f97\u6709\u4e9b\u9eef\u7136\u5427\u3002</p><p>\u4e0d\u53bb\u60f3\u4e86\u3002</p><p>\u4ed6\u5c06\u76ee\u5149\u8f6c\u5411\u65c1\u8fb9\u7684\u6885\u83f2\u65af\uff0c\u5c11\u5973\u4e0d\u77e5\u4f55\u65f6\u5df2\u7ecf\u62ac\u8d77\u5934\u6765\uff0c\u4e5f\u60a0\u60a0\u5730\u770b\u7740\u8fdc\u5904\u7684\u5929\u7a7a\uff0c\u5979\u53f3\u624b\u6258\u7740\u8138\uff0c\u91d1\u53d1\u6563\u6563\u5782\u4e0b\uff0c\u6709\u51e0\u7f15\u62c2\u5728\u8138\u4e0a\uff0c\u795e\u60c5\u60a0\u7136\u5e73\u6de1\uff0c\u65e2\u4e0d\u662f\u5e73\u65f6\u7684\u6de1\u6f20\uff0c\u4e5f\u4e0d\u662f\u5076\u5c14\u8868\u73b0\u51fa\u6765\u7684\u4e25\u8083\u6216\u8005\u8f6f\u5f31\uff0c\u743c\u6069\u51e0\u4e4e\u6ca1\u6709\u529e\u6cd5\u7528\u8bed\u8a00\u6765\u52c9\u5f3a\u5f62\u5bb9\uff0c\u53ea\u662f\u6014\u6014\u5730\u770b\u7740\uff0c\u4eff\u4f5b\u7b2c\u4e00\u6b21\u770b\u6e05\u695a\u8fd9\u4e2a\u5c11\u5973\u4f3c\u7684\u3002</p><p>\u4ed6\u4ece\u6ca1\u60f3\u8fc7\u6885\u83f2\u65af\u4e5f\u6709\u8fd9\u6837\u5e73\u548c\u95f2\u9002\u7684\u6a21\u6837\u5427\u3002\u5728\u5927\u90e8\u5206\u65f6\u5019\uff0c\u5373\u4f7f\u5c11\u5973\u5b89\u9759\u5730\u7ad9\u7740\u4e0d\u52a8\uff0c\u90fd\u603b\u7ed9\u4eba\u4e00\u79cd\u538b\u8feb\u611f\uff0c\u4eff\u4f5b\u51fa\u9798\u7684\u5229\u5203\uff0c\u5c31\u7b97\u4e0d\u6325\u52a8\uff0c\u4f9d\u65e7\u4e5f\u662f\u90a3\u6837\u7684\u5bd2\u5149\u51db\u51db\uff0c\u950b\u9510\u903c\u4eba\u3002\u751a\u81f3\u65e0\u8bba\u5979\u6e05\u9192\u65f6\uff0c\u5979\u7761\u7740\u65f6\uff0c\u5979\u5b89\u9759\u4e0d\u52a8\u65f6\uff0c\u5979\u6325\u5251\u6740\u654c\u65f6\uff0c\u751a\u81f3\u5979\u4e3a\u505a\u65bd\u6cd5\u4e13\u6ce8\u8bad\u7ec3\u65f6\uff0c\u743c\u6069\u90fd\u6e05\u6e05\u695a\u695a\u5730\u77e5\u9053\uff0c\u5979\u662f\u4e00\u4e2a\u5723\u6b66\u58eb\u3002</p><p>\u800c\u6b64\u523b\u7684\u5c11\u5973\uff0c\u5374\u6084\u7136\u4ee4\u4eba\u5fd8\u8bb0\u4e86\u8fd9\u79cd\u611f\u89c9\u3002\u6b64\u523b\u7684\u6885\u83f2\u65af\uff0c\u66f4\u50cf\u662f\u4e00\u4e2a\u6d89\u4e16\u4e0d\u6df1\u7684\u7eaf\u51c0\u5c11\u5973\uff0c\u60a0\u95f2\u5730\u5750\u5728\u8349\u5730\u4e0a\uff0c\u770b\u7740\u84dd\u5929\u767d\u4e91\uff0c\u52fe\u52d2\u7740\u81ea\u5df1\u672a\u6765\u7684\u68a6\u60f3\u3002</p><p>\u6b64\u65f6\u6b64\u523b\uff0c\u743c\u6069\u624d\u771f\u6b63\u771f\u5207\u5730\u610f\u8bc6\u5230\uff0c\u8fd9\u4e2a\u966a\u4f34\u81ea\u5df1\u4e00\u8def\u8d70\u6765\u7684\u5723\u6b66\u58eb\uff0c\u5176\u5b9e\u4e5f\u8fd8\u662f\u4e2a\u548c\u81ea\u5df1\u5e74\u9f84\u76f8\u4eff\u7684\u5c11\u5973\u3002</p><p>\u88ab\u81ea\u5df1\u6b3a\u8d1f\u7684\u5c11\u5973\u3002</p><p>\u8bba\u53ca\u4e24\u4eba\u4e4b\u95f4\u7684\u4e8b\u60c5\uff0c\u743c\u6069\u81ea\u8ba4\u8c08\u4e0d\u4e0a\u90aa\u6076\u3002\u4eba\u9762\u72ee\u62a2\u8d70\u4e86\u5723\u7269\uff0c\u6885\u83f2\u65af\u8981\u53bb\u53d6\u56de\uff0c\u743c\u6069\u5e76\u6ca1\u6709\u4e49\u52a1\u8981\u53bb\u5e2e\u52a9\u5979\u3002\u65e2\u7136\u81ea\u5df1\u5192\u7740\u4e22\u6389\u6027\u547d\u7684\u98ce\u9669\uff0c\u5e2e\u5979\u53d6\u56de\u4e86\u5723\u7269\uff0c\u90a3\u4e48\u6309\u7167\u4e8b\u5148\u7684\u534f\u8bae\u6536\u53d6\u62a5\u916c\uff0c\u4e5f\u662f\u7406\u6240\u5f53\u7136\u7684\u4e8b\u60c5\u3002\u6885\u83f2\u65af\u51fa\u4eba\uff0c\u743c\u6069\u51fa\u529b\uff0c\u516c\u5e73\u4ea4\u6613\uff0c\u5f7c\u6b64\u9075\u5b88\uff0c\u6ca1\u4ec0\u4e48\u53ef\u8bf4\u7684\uff0c\u53cc\u65b9\u90fd\u5bf9\u6b64\u8868\u793a\u8ba4\u53ef\u3002</p><p>\u53ea\u662f\uff0c\u8fd9\u7ec8\u7a76\u4e5f\u4e0d\u7b97\u4ec0\u4e48\u5584\u826f\u4e4b\u4e3e\u5427\uff0c\u9762\u5bf9\u7684\uff0c\u6bd5\u7adf\u53ea\u662f\u4e00\u4e2a\u5c11\u5973\u3002</p><p>\u6b63\u5f53\u4ed6\u5fc3\u4e2d\u5fae\u5fae\u6d8c\u52a8\uff0c\u7565\u7565\u89c9\u5f97\u4f3c\u4e4e\u6709\u4e9b\u6005\u7136\u7684\u65f6\u5019\uff0c\u5c11\u5973\u7a81\u7136\u504f\u8fc7\u8138\u6765\uff0c\u770b\u7740\u4ed6\u3002</p><p>\u201c\u743c\u6069\u3002\u201d\u5c11\u5973\u53eb\u4ed6\u3002</p><p>\u201c\u5514\uff1f\u201d</p><p>\u201c\u743c\u6069\uff0c\u4f60\u77e5\u9053\u4e48\uff0c\u201d\u5c11\u5973\u5e73\u9759\u5730\u8bf4\uff0c\u201c\u5176\u5b9e\uff0c\u4eca\u5929\u6211\u662f\u60f3\u8fc7\u8ba9\u4f60\u6b7b\u6389\u7684\u3002\u201d</p></article>"}}}]);
