(window.webpackJsonp=window.webpackJsonp||[]).push([[1060],{1083:function(p){p.exports={chapterTitle:null,title:"\u7b2c863\u7ae0 \u5e76\u8d2d\u98ce\u6ce2",content:"<article><h2>\u7b2c863\u7ae0 \u5e76\u8d2d\u98ce\u6ce2</h2><p>\u3000\u3000\u65b0\u5149\u7eb8\u4e1a\u5e76\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u7b2c\u4e00\u6b21\u8c08\u5224\u7531\u4e8e\u53cc\u65b9\u7684\u5dee\u8ddd\u592a\u5927\u800c\u65e0\u6cd5\u8fbe\u6210\u4e00\u81f4\u800c\u5931\u8d25\u3002</p><p>\u3000\u3000\u7ec8\u6b62\u7684\u8c08\u5224\u4e00\u76f4\u50f5\u6301\u5230\u516d\u6708\u4e2d\u65ec\uff0c\u90fd\u6ca1\u6709\u91cd\u65b0\u542f\u52a8\u3002</p><p>\u3000\u3000\u516d\u6708\u4e2d\u65ec\uff0c\u4ee5\u6574\u5408\u56fd\u5185\u7a00\u571f\u77ff\u8d44\u6e90\u4e3a\u76ee\u7684\u534e\u7a00\u77ff\u4e1a\u4ee5\u5e38\u4eba\u96be\u4ee5\u60f3\u8c61\u7684\u901f\u5ea6\u7ed9\u63a8\u52a8\u7ec4\u5efa\u8d77\u6765\uff0c\u56fd\u52a1\u9662\u4e5f\u9996\u6b21\u7279\u4f8b\u5141\u8bb8\u9526\u6e56\u4ee5\u6c11\u8425\u673a\u6784\u7684\u8eab\u4efd\u5bf9\u534e\u7a00\u77ff\u4e1a\u8fd9\u5bb6\u5177\u6709\u8d44\u6e90\u5784\u65ad\u6027\u8d28\u7684\u4f01\u4e1a\u6301\u80a1\uff0c\u4e0e\u6b64\u540c\u65f6\uff0c\u9526\u6e56\u95f4\u63a5\u63a7\u5236\u7684\u4e2d\u6676\u5fae\u82af\u5f15\u5165\u5fb7\u4eea\u7684\u6ce8\u8d44\uff0c\u9526\u6e56\u65d7\u4e0b\u7684\u7231\u8fbe\u7535\u5b50\u53c8\u4e0e\u4e1c\u829d\u8fbe\u6210\u6280\u672f\u5408\u4f5c\u7684\u534f\u8bae\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u59d4\u7684\u6001\u5ea6\u6b64\u65f6\u5c31\u6084\u7136\u53d1\u751f\u8f6c\u53d8\uff0c\u5f20\u606a\u5728\u9999\u6e2f\u7684\u90a3\u6bb5\u65f6\u95f4\u91cc\uff0c\u65b0\u5149\u7eb8\u4e1a\u5e76\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u7b2c\u4e8c\u6b21\u8c08\u5224\u5f97\u4ee5\u987a\u5229\u7684\u542f\u52a8\u3002</p><p>\u3000\u3000\u5f20\u606a\u539f\u4ee5\u4e3a\u7b2c\u4e8c\u6b21\u8c08\u5224\u5373\u4f7f\u8fd8\u4e0d\u80fd\u6210\u529f\uff0c\u4e5f\u4f1a\u7f29\u77ed\u53cc\u65b9\u8ba4\u77e5\u7684\u5dee\u8ddd\uff0c\u6ca1\u6709\u60f3\u5230\u4eca\u5929\u7adf\u7136\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u201c\u62db\u5546\u5f15\u8d44\u7684\u4e8b\u60c5\uff0c\u6089\u7531\u9526\u6e56\u5546\u4e8b\u90a3\u8fb9\u8d1f\u8d23\uff0c\u6211\u4f1a\u8ba9\u53f6\u5efa\u658c\u4e3b\u52a8\u8054\u7cfb\u738b\u7ef4\u5747\u4e66\u8bb0\uff0c\u201d\u5f20\u606a\u518d\u6ca1\u6709\u5fc3\u601d\u8ddf\u7f57\u541b\u5728\u6c5f\u5824\u4e0a\u5520\u5bb6\u5e38\uff0c\u201c\u6211\u8fd9\u5c31\u8981\u8d76\u53bb\u91d1\u5c71\u4e00\u8d9f\uff01\u201d</p><p>\u3000\u3000\u7f57\u541b\u4e5f\u4e0d\u633d\u7559\u5f20\u606a\uff0c\u4e5f\u65e0\u6cd5\u5f00\u53e3\u633d\u7559\uff0c\u60f3\u4e0d\u5230\u9526\u6e56\u58f0\u52bf\u5927\u6da8\u4e4b\u9645\uff0c\u91d1\u5c71\u4f1a\u5728\u5e76\u8d2d\u6848\u4e0a\u6345\u51fa\u8fd9\u79cd\u7bd3\u5b50\u6765\uff0c\u4e00\u822c\u8bf4\u6765\uff0c\u5e76\u8d2d\u6848\u8fd8\u5728\u8c08\u5224\u9636\u6bb5\uff0c\u5373\u4f7f\u8c08\u5224\u7684\u6761\u4ef6\u518d\u82db\u523b\uff0c\u666e\u901a\u804c\u5de5\u90fd\u4e0d\u53ef\u80fd\u77e5\u9053\uff0c\u90a3\u53c8\u600e\u4e48\u4f1a\u805a\u96c6\u8d77\u6765\u95f9\u4e8b\u5462\uff1f\u4ee5\u7f57\u541b\u5bf9\u9526\u6e56\u7684\u4e86\u89e3\uff0c\u9526\u6e56\u4e5f\u4e0d\u53ef\u80fd\u5728\u5e76\u8d2d\u6848\u4e0a\u53bb\u4fb5\u5360\u4ec0\u4e48\u56fd\u6709\u8d44\u4ea7\uff0c\u95f9\u51fa\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7528\u811a\u8dbe\u5934\u53bb\u8003\u8651\u95ee\u9898\u4e5f\u5c31\u80cc\u540e\u4e00\u5b9a\u6709\u4eba\u5728\u6363\u9b3c\u3002</p><p>\u3000\u3000\u4e00\u65e6\u5e76\u8d2d\u6848\u56e0\u4e3a\u8fd9\u79cd\u539f\u56e0\u88ab\u8feb\u7ec8\u6b62\uff0c\u90a3\u9526\u6e56\u4e0e\u6c5f\u5357\u7701\u3001\u91d1\u5c71\u5e02\u7684\u5173\u7cfb\u5c31\u8981\u5927\u5e45\u5ea6\u9000\u6b65\u4e86\uff0c\u5373\u4f7f\u9526\u6e56\u5546\u4e8b\u6709\u80fd\u529b\u7ed9\u6c5f\u5357\u7701\u3001\u91d1\u5c71\u5e02\u5e26\u53bb\u5927\u91cf\u7684\u5916\u6c47\u6295\u8d44\uff0c\u4f46\u662f\u603b\u4e5f\u6709\u4eba\u4e0d\u5e0c\u671b\u770b\u5230\u9526\u6e56\u5728\u6c5f\u5357\u7701\u3001\u91d1\u5c71\u5e02\u58f0\u52bf\u5927\u6da8\uff0c\u6bd5\u7adf\u6668\u66e6\u7eb8\u4e1a\u7ed9\u9526\u6e56\u6536\u8d2d\uff0c\u4e5f\u4f1a\u4f24\u5bb3\u5230\u4e00\u90e8\u5206\u4eba\u7684\u5229\u76ca\uff0c\u8fd9\u4e9b\u4eba\u5728\u80cc\u540e\u6363\u9b3c\u518d\u6b63\u5e38\u4e0d\u8fc7\uff0c\u5173\u952e\u662f\u6c5f\u5357\u7701\u59d4\u3001\u7701\u653f\u5e9c\u7684\u6001\u5ea6\u6447\u6446\u52a9\u6da8\u4e86\u8fd9\u4e9b\u4eba\u7684\u6c14\u7130\u3002</p><p>\u3000\u3000\u4e0d\u7ba1\u600e\u4e48\u6765\u8bf4\uff0c\u8fd9\u5bf9\u5efa\u90ba\u3001\u5bf9\u4e1c\u6d77\u7701\u6765\u8bf4\u5374\u662f\u4e00\u4ef6\u597d\u4e8b\uff0c\u7f57\u541b\u5012\u4e5f\u4e0d\u4f1a\u5c06\u5e78\u707e\u4e50\u7978\u7684\u795e\u8272\u6446\u5230\u8138\u4e0a\uff0c\u5173\u5fc3\u7684\u8bf4\u9053\uff1a\u201c\u884c\uff0c\u4f60\u8d76\u53bb\u91d1\u5c71\uff0c\u6709\u4ec0\u4e48\u4e8b\u60c5\u4f60\u53ef\u4ee5\u76f4\u63a5\u8054\u7cfb\u6211\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u7fdf\u4e39\u9752\u4e0b\u4e86\u6c5f\u5824\uff0c\u8ba9\u5085\u4fca\u5728\u6c5f\u5824\u76f4\u63a5\u6389\u8f6c\u8f66\u5934\u5f80\u5357\u5f00\u53bb\uff0c\u76f4\u63a5\u5c31\u5f80\u91d1\u5c71\u8d76\u53bb\u3002</p><p>\u3000\u3000\u5750\u4e0a\u8f66\u540e\uff0c\u6c5f\u5357\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u6881\u4f1f\u6cd5\u7684\u7535\u8bdd\u5f88\u5feb\u5c31\u6253\u5230\u4ed6\u7684\u624b\u673a\u4e0a\u3002</p><p>\u3000\u3000\u201c\u53d1\u751f\u8fd9\u6837\u7684\u4e8b\u60c5\uff0c\u7701\u91cc\u611f\u5230\u5f88\u9057\u61be\uff0c\u6211\u5728\u533b\u9662\u91cc\uff0c\u8ddf\u533b\u62a4\u4eba\u5458\u8ba8\u8bba\u8fc7\uff0c\u4e00\u5b9a\u4f1a\u786e\u4fdd\u8c08\u5224\u4eba\u5458\u4e0d\u4f1a\u6709\u4ec0\u4e48\u751f\u547d\u5371\u9669\uff0c\u5c1a\u5b66\u4e49\u4e66\u8bb0\u4e5f\u6b63\u5728\u8d76\u5230\u533b\u9662\u6765\u7684\u8def\u4e0a\u2026\u2026\u201d\u6881\u4f1f\u6cd5\u5728\u7535\u8bdd\u5c06\u60c5\u51b5\u53c8\u5927\u81f4\u8ddf\u5f20\u606a\u4ecb\u7ecd\u4e86\u4e00\u904d\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u8bdd\u91cc\u6ca1\u6709\u8bf4\u5e55\u540e\u53ef\u80fd\u5b58\u5728\u6307\u4f7f\u4eba\uff0c\u4e5f\u6ca1\u6709\u8bf4\u8981\u8ffd\u67e5\u4e0b\u53bb\u7684\u610f\u601d\u3002</p><p>\u3000\u3000\u60c5\u51b5\u6709\u4e9b\u590d\u6742\uff0c\u6bd5\u7adf\u4e5d\u516b\u5e74\u5c06\u4e00\u5ea7\u5927\u578b\u8fd8\u5728\u8d62\u5229\u7684\u56fd\u6709\u4f01\u4e1a\u51fa\u552e\u7ed9\u79c1\u4f01\u8981\u627f\u62c5\u4e00\u5b9a\u7684\u653f\u6cbb\u98ce\u9669\uff0c\u8fd9\u6b21\u53c8\u662f\u4e0a\u5343\u804c\u5de5\u805a\u96c6\u8d77\u6765\u95f9\u4e8b\uff0c\u8ffd\u67e5\u4e0b\u53bb\uff0c\u7701\u91cc\u5927\u6982\u4f1a\u62c5\u5fc3\u95f9\u51fa\u66f4\u5927\u7684\u98ce\u6ce2\uff0c\u4f7f\u5f97\u6881\u4f1f\u6cd5\u4e0e\u5c1a\u5b66\u4e49\u9677\u5165\u88ab\u52a8\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5f20\u606a\u542c\u6881\u4f1f\u6cd5\u5728\u7535\u8bdd\u8bf4\u4e86\u4e00\u901a\u5b89\u629a\u7684\u8bdd\uff0c\u4e34\u672b\u624d\u8bf4\u9053\uff1a\u201c\u591a\u8c22\u7701\u91cc\u5173\u5fc3\uff0c\u65e2\u7136\u6ca1\u6709\u4ec0\u4e48\u5927\u4e8b\uff0c\u6211\u5728\u5efa\u90ba\u4e5f\u5b89\u5fc3\u4e86\uff0c\u6211\u770b\u80fd\u4e0d\u80fd\u62bd\u51fa\u65f6\u95f4\u8d76\u53bb\u91d1\u5c71\u4e00\u8d9f\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u6ca1\u6709\u8ddf\u6881\u4f1f\u6cd5\u5c3d\u8bf4\u5b9e\u8bdd\uff0c\u8981\u4e0d\u8981\u8ffd\u67e5\u4e0b\u53bb\uff0c\u5f20\u606a\u8fd8\u8981\u8ddf\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u89c1\u8fc7\u9762\u7814\u7a76\u8fc7\u4e4b\u540e\u518d\u51b3\u5b9a\uff0c\u5728\u4e0e\u5468\u6e38\u3001\u8bb8\u9e3f\u4f2f\u89c1\u9762\u4e4b\u524d\uff0c\u5f20\u606a\u5c3d\u91cf\u8981\u907f\u514d\u4e0e\u6881\u4f1f\u6cd5\u5148\u89c1\u9762\uff0c\u66f4\u4e0d\u53ef\u80fd\u8ddf\u6c5f\u5357\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u89c1\u9762\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u5728\u7701\u4eba\u6c11\u533b\u9662\u91cc\u6302\u4e86\u7535\u8bdd\uff0c\u4ed6\u7422\u78e8\u4e0d\u900f\u5f20\u606a\u6700\u540e\u4e00\u53e5\u5230\u5e95\u662f\u8d76\u8fc7\u6765\u8fd8\u662f\u4e0d\u8d76\u8fc7\u6765\uff0c\u4f46\u662f\u4e5f\u6e05\u695a\u5f20\u606a\u6ca1\u6709\u8981\u8ddf\u4ed6\u89c1\u9762\u7684\u610f\u601d\u3002\u7709\u5934\u5fae\u76b1\u4e86\u8d77\u6765\uff0c\u89c9\u5f97\u4e8b\u60c5\u5f88\u68d8\u624b\uff0c\u79d8\u4e66\u8dd1\u8fc7\u6765\u544a\u8bc9\u4ed6\uff0c\u5c1a\u5b66\u4e49\u7684\u8f66\u9a6c\u4e0a\u5c31\u8fdb\u533b\u9662\u3002\u6881\u4f1f\u6cd5\u53ea\u5f97\u5148\u653e\u4e0b\u8111\u5b50\u91cc\u7684\u5c42\u5c42\u987e\u8651\uff0c\u5148\u51fa\u53bb\u63a5\u5c1a\u5b66\u4e49\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u4ece\u6765\u90fd\u6ca1\u6709\u8fd9\u4e48\u4f9d\u91cd\u8fc7\u4e00\u5bb6\u4f01\u4e1a\u673a\u6784\uff0c\u8fd9\u6b21\u4e8b\u4ef6\uff0c\u9526\u6e56\u53c8\u662f\u53d7\u5bb3\u8005\uff0c\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u4eb2\u81ea\u51fa\u52a8\u770b\u671b\u6170\u95ee\u4f24\u5458\u4e5f\u662f\u60c5\u7406\u4e4b\u4e2d\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u4e8b\u4ef6\u5904\u7406\u4e0d\u597d\uff0c\u9526\u6e56\u4e0e\u6c5f\u5357\u7701\u7684\u5173\u7cfb\u5c31\u8981\u5927\u5e45\u5ea6\u540e\u9000\uff0c\u9526\u6e56\u5168\u9762\u7f29\u51cf\u5728\u6c5f\u5357\u7684\u6295\u8d44\u4e0d\u8bf4\uff0c\u9526\u6e56\u4e00\u76f4\u6697\u4e2d\u63a8\u4e2d\u7684\u5c0f\u6c5f\u533a\u57df\u7ecf\u6d4e\u5408\u4f5c\u8fdb\u5c55\u5c31\u53ef\u80fd\u4ece\u6b64\u505c\u6ede\u4e0d\u524d\uff0c\u6700\u8ba9\u4eba\u5fc3\u7126\u7684\u5c31\u662f\u6446\u5728\u7709\u776b\u524d\u5927\u89c4\u6a21\u4ece\u5370\u5c3c\u64a4\u79bb\u7684\u534e\u4eba\u8d44\u672c\u8fd9\u5757\u9c9c\u7f8e\u7684\u86cb\u7cd5\u5c31\u6ca1\u6709\u6c5f\u5357\u7701\u7684\u4efd\u4e86\u3002</p><p>\u3000\u3000\u4f46\u662f\uff0c\u4eca\u5929\u7684\u4e8b\u60c5\u4e5f\u5f88\u96be\u8ffd\u67e5\uff0c\u5c31\u7b97\u6653\u5f97\u5e55\u540e\u6709\u4eba\u5728\u5506\u4f7f\uff0c\u4e5f\u5f88\u96be\u8ffd\u67e5\u4e0b\u53bb\uff0c\u6bd5\u7adf\u805a\u4f17\u95f9\u4e8b\u5e76\u6253\u7838\u4f24\u4eba\u7684\u90fd\u662f\u6668\u66e6\u7eb8\u4e1a\u7684\u666e\u901a\u804c\u5de5\uff0c\u641e\u5927\u98ce\u6ce2\u5bf9\u4ed6\u3001\u5bf9\u5c1a\u5b66\u4e49\u90fd\u5f88\u4e0d\u5229\u3002</p><p>\u3000\u3000\u518d\u8bf4\u7701\u91cc\u53cd\u5bf9\u5e76\u8d2d\u6848\u7684\u5b98\u5458\u4f9d\u65e7\u662f\u4e3b\u6d41\uff0c\u5374\u662f\u5c1a\u5b66\u4e49\u8003\u8651\u5230\u9526\u6e56\u80cc\u540e\u7684\u52bf\u529b\u8d8a\u6765\u8d8a\u6df1\u539a\uff0c\u751a\u81f3\u4e0e\u519b\u65b9\u626f\u4e0a\u8054\u7cfb\uff0c\u518d\u52c9\u5f3a\u540c\u610f\u5f00\u542f\u7b2c\u4e8c\u6b21\u8c08\u5224\uff0c\u7167\u987e\u9526\u6e56\u7684\u9762\u5b50\uff0c\u5fc3\u91cc\u5374\u672a\u5fc5\u652f\u6301\u8c08\u5224\u6210\u529f\uff0c\u800c\u9526\u6e56\u5546\u4e8b\u7ec4\u5efa\u5e76\u5728\u9999\u6e2f\u4e3a\u6d77\u5dde\u8fdb\u884c\u7279\u6b8a\u62db\u5546\u5f15\u8d44\u4e4b\u4e8b\u662f\u6700\u8fd1\u624d\u53d1\u751f\u7684\u2026\u2026</p><p>\u3000\u3000\u5c1a\u5b66\u4e49\u6ee1\u5934\u9ed1\u53d1\u6cb9\u4eae\uff0c\u5fc3\u5bbd\u4f53\u80d6\uff0c\u4e00\u70b9\u90fd\u4e0d\u50cf\u5e74\u8fc7\u516d\u65ec\u7684\u8001\u4eba\uff0c\u4ed6\u770b\u5230\u6881\u4f1f\u6cd5\u8dd1\u5230\u6025\u8bca\u697c\u5927\u5385\u6765\u63a5\u4ed6\uff0c\u8d70\u8fc7\u6765\uff0c\u4fa7\u5934\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u4f24\u5458\u60c5\u51b5\u600e\u4e48\u6837\uff1f\u201d</p><p>\u3000\u3000\u201c\u8f7b\u5fae\u4f24\u4e0d\u7b97\uff0c\u6709\u4e24\u4eba\u9aa8\u6298\u90fd\u9001\u8fdb\u4e86\u6025\u6551\u5ba4\uff0c\u9526\u6e56\u65b9\u9762\u7684\u8c08\u5224\u8d1f\u8d23\u4eba\u5468\u6e38\u88ab\u7816\u5757\u7838\u4e2d\u5934\u90e8\uff0c\u9001\u8fdb\u533b\u9662\u6765\u6709\u8fc7\u4e00\u9635\u660f\u8ff7\uff0c\u73b0\u5728\u6062\u590d\u4e9b\u610f\u8bc6\uff0c\u4f46\u4e0d\u662f\u5f88\u6e05\u9192\uff0c\u8111\u9707\u8361\u53ef\u80fd\u907f\u514d\u4e0d\u4e86\uff0c\u6b63\u7ec4\u7ec7\u533b\u751f\u7ed9\u4ed6\u505a\u68c0\u67e5\u2026\u2026\u201d\u6881\u4f1f\u6cd5\u8ddf\u5c1a\u5b66\u4e49\u4ecb\u7ecd\u60c5\u51b5\u3002</p><p>\u3000\u3000\u5c1a\u5b66\u4e49\u7709\u5934\u5fae\u8e59\u7740\uff0c\u4f4e\u58f0\u95ee\u9053\uff1a\u201c\u8fd9\u4e8b\u60c5\u8981\u600e\u4e48\u5904\u7406\uff1f\u804c\u5de5\u95f9\u60c5\u7eea\uff0c\u6211\u4eec\u4e5f\u8981\u7406\u89e3\u3002\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u77e5\u9053\u5c1a\u5b66\u4e49\u7684\u610f\u601d\uff0c\u4e0d\u662f\u8981\u4e0d\u8981\u8ffd\u67e5\u7684\u95ee\u9898\uff0c\u800c\u662f\u8981\u4e0d\u8981\u7ec8\u6b62\u8c08\u5224\u7f13\u89e3\u5de5\u4eba\u60c5\u7eea\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u6709\u4e00\u4e2a\u60c5\u51b5\uff0c\u8fd8\u662f\u5f20\u606a\u6682\u65f6\u4e0d\u77e5\u9053\u7684\uff0c\u5728\u5343\u540d\u804c\u5de5\u56f4\u653b\u8c08\u5224\u7ec4\u6210\u5458\u4e4b\u9645\uff0c\u4e5f\u6709\u6570\u767e\u540d\u804c\u5de5\u805a\u96c6\u5230\u7701\u59d4\u7701\u653f\u5e9c\u5927\u697c\u524d\u56f4\u95e8\u8bf7\u613f\u2014\u2014\u8fd9\u8bf4\u660e\u80cc\u540e\u662f\u6709\u4eba\u7b79\u5212\u3001\u7ec4\u7ec7\u7684\uff0c\u4f46\u662f\u7b79\u5212\u7ec4\u7ec7\u8005\u4e5f\u672a\u5fc5\u662f\u771f\u6b63\u7684\u5e55\u540e\u6307\u4f7f\uff0c\u6709\u4e9b\u65f6\u5019\uff0c\u771f\u6b63\u7684\u5e55\u540e\u4eba\u7269\u53ea\u9700\u8981\u52a8\u52a8\u5634\uff0c\u5c31\u80fd\u5c06\u804c\u5de5\u7684\u60c5\u7eea\u6fc0\u5316\u8d77\u6765\uff0c\u8fd9\u4e5f\u662f\u8fd9\u79cd\u4e8b\u4ef6\u96be\u5904\u7406\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u53e6\u4e00\u65b9\u9762\uff0c\u6668\u66e6\u7eb8\u4e1a\u5e76\u8d2d\u6848\uff0c\u5f97\u4e0d\u5230\u5927\u591a\u6570\u5b98\u5458\u7684\u652f\u6301\uff0c\u804c\u5de5\u53c8\u8d77\u6765\u95f9\u98ce\u6ce2\uff0c\u5bf9\u5c1a\u5b66\u4e49\u6765\u8bf4\uff0c\u4fdd\u8bc1\u653f\u6cbb\u3001\u793e\u4f1a\u7a33\u5b9a\u66f4\u91cd\u8981\u4e9b\uff0c\u751a\u81f3\u653e\u5f03\u64a4\u51fa\u5370\u5c3c\u7684\u534e\u4eba\u8d44\u672c\u8fd9\u5757\u5927\u86cb\u7cd5\u4e5f\u5728\u6240\u4e0d\u60dc\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u662f\u7ef4\u62a4\u5927\u90e8\u5206\u804c\u5de5\u5229\u76ca\uff0c\u6761\u4ef6\u4e5f\u5bbd\u677e\uff0c\u4f46\u662f\u6668\u66e6\u7eb8\u4e1a\u539f\u6709\u7684\u4e00\u5957\uff0c\u8981\u6539\u6389\uff0c\u8981\u9769\u65b0\uff0c\u80af\u5b9a\u4e5f\u6709\u4e00\u90e8\u5206\u804c\u5de5\u4e0d\u80fd\u9002\u5e94\uff0c\u201d\u6881\u4f1f\u6cd5\u7a0d\u8fa9\u89e3\u4e86\u4e00\u53e5\uff0c\u89c1\u5c1a\u5b66\u4e49\u7709\u5934\u5fae\u8e59\uff0c\u4ed6\u505c\u6b62\u8ddf\u5c1a\u5b66\u4e49\u8fd9\u65f6\u5019\u4e89\u8bba\u8fd9\u4e2a\u95ee\u9898\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8fd9\u4e2a\u4e3b\u52a8\u6743\u662f\u4e0d\u662f\u7ed9\u9526\u6e56\u90a3\u8fb9\uff1f\u9526\u6e56\u4eba\u5458\u5728\u8fd9\u6b21\u4e8b\u4ef6\u4e2d\u53d7\u4e86\u4f24\uff0c\u7701\u91cc\u518d\u65ad\u7136\u4e2d\u65ad\u8c08\u5224\uff0c\u5bf9\u53cc\u65b9\u7684\u5173\u7cfb\u635f\u5bb3\u592a\u5927\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u8ddf\u90a3\u4eba\u8054\u7cfb\u8fc7\u6ca1\u6709\uff1f\u201d\u5c1a\u5b66\u4e49\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u521a\u8054\u7cfb\u8fc7\uff0c\u5012\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53cd\u5e94\u2026\u2026\u201d\u6709\u4e9b\u95ee\u9898\uff0c\u6881\u4f1f\u6cd5\u80af\u5b9a\u4e0d\u4f1a\u8ddf\u5c1a\u5b66\u4e49\u8bf4\u7684\uff0c\u4ed6\u8ba4\u4e3a\u5c1a\u5b66\u4e49\u8fc7\u4e8e\u4fdd\u5b88\uff0c\u5bb9\u6613\u7ed9\u6c5f\u5357\u7701\u65e2\u5f97\u5229\u76ca\u7fa4\u4f53\u5f71\u54cd\uff0c\u6c5f\u5357\u7701\u8fd9\u4e9b\u5e74\u6765\u5728\u7ecf\u6d4e\u4e0a\u96be\u6709\u4f5c\u4e3a\uff0c\u5c1a\u5b66\u4e49\u662f\u8981\u627f\u62c5\u4e00\u4e9b\u8d23\u4efb\u3002\u5c1a\u5b66\u4e49\u524d\u4e9b\u5929\u770b\u5230\u5370\u5c3c\u64a4\u51fa\u534e\u4eba\u8d44\u672c\u4e4b\u9c9c\u7f8e\u800c\u6b23\u7136\u82e5\u559c\uff0c\u8bf4\u8981\u63a8\u52a8\u6668\u66e6\u7eb8\u4e1a\u5e76\u8d2d\u6848\u7684\u8c08\u5224\u8fdb\u7a0b\uff0c\u8fd9\u65f6\u5019\u5374\u53c8\u8981\u9000\u7f29\u3002\u6ee1\u8179\u7684\u610f\u89c1\uff0c\u6881\u4f1f\u6cd5\u4e5f\u662f\u6df1\u57cb\u5728\u5fc3\u91cc\uff0c\u4e0d\u5766\u9732\u5230\u8138\u4e0a\uff0c\u966a\u540c\u5c1a\u5b66\u4e49\u8fdb\u53bb\u9526\u6e56\u8fd9\u6b21\u53d7\u4f24\u7684\u5458\u5de5\u2014\u2014\u8fd9\u4e9b\u90fd\u4e0d\u662f\u9526\u6e56\u666e\u901a\u7684\u5458\u5de5\uff0c\u90fd\u662f\u9ad8\u7ea7\u7ba1\u7406\u4eba\u5458\uff0c\u5374\u76f4\u63a5\u5f71\u54cd\u9526\u6e56\u9ad8\u5c42\u5bf9\u6c5f\u5357\u7701\u7684\u611f\u89c2\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4ece\u5efa\u90ba\u5f80\u91d1\u5c71\u7684\u56fd\u9053\u6709\u4e00\u6bb5\u8def\u7ed9\u524d\u4e9b\u5929\u7684\u6301\u7eed\u66b4\u96e8\u51b2\u6bc1\uff0c\u8f66\u8f86\u4ece\u7701\u9053\u3001\u5e02\u9053\u7ed5\u884c\uff0c\u6240\u5e78\u591c\u91cc\u6ca1\u6709\u518d\u4e0b\u66b4\u96e8\uff0c\u8d76\u5728\u51cc\u6668\u4e4b\u524d\u62b5\u8fbe\u91d1\u5c71\u3002</p><p>\u3000\u3000\u88ab\u7838\u4f24\u6570\u4eba\uff0c\u4e00\u4eba\u5728\u56f4\u5835\u4e2d\u62c9\u8fdb\u4eba\u7fa4\u56f4\u6bb4\u81f4\u808b\u9aa8\u9aa8\u6298\uff0c\u4e00\u4eba\u662f\u5728\u64a4\u8d70\u5d34\u811a\u5bfc\u81f4\u811a\u8e1d\u9aa8\u6298\uff0c\u5f20\u606a\u4ed6\u4eec\u8d76\u5230\u533b\u9662\uff0c\u5468\u6e38\u867d\u6709\u4e9b\u540e\u9057\u75c7\u72b6\uff0c\u4eba\u662f\u6062\u590d\u4e86\u610f\u8bc6\uff0c\u4e5f\u6ca1\u6709\u5927\u788d\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u4e8b\u4ef6\uff0c\u9526\u6e56\u9ad8\u5c42\u5185\u90e8\u5df2\u7ecf\u4f20\u5f00\u4e86\uff0c\u5f20\u606a\u5728\u8def\u4e0a\u5c31\u63a5\u5230\u65e0\u6570\u7535\u8bdd\uff0c\u70e6\u4e0d\u80dc\u70e6\uff0c\u6700\u540e\u5c06\u7535\u8bdd\u4ea4\u7ed9\u7fdf\u4e39\u9752\u6321\u67b6\uff0c\u4ed6\u5728\u8f66\u4e0a\u7761\u4e86\u4e00\u89c9\u3002</p><p>\u3000\u3000\u8fd9\u51e0\u5929\u4e3a\u4e86\u8003\u8bd5\u7684\u4e8b\u60c5\uff0c\u7761\u89c9\u90fd\u6781\u5c11\uff0c\u592a\u778c\u7761\u5c31\u62ff\u53c2\u7247\u6491\u7740\uff0c\u8fd9\u65f6\u5019\u542c\u8bf4\u4eba\u6ca1\u6709\u5927\u788d\uff0c\u5c31\u6ca1\u6709\u4ec0\u4e48\u597d\u62c5\u5fc3\u7684\uff0c\u63a5\u4e0b\u6765\u8be5\u600e\u4e48\u4ea4\u950b\uff0c\u8be5\u600e\u4e48\u8ba8\u56de\u516c\u9053\uff0c\u662f\u63a5\u4e0b\u6765\u7684\u4e8b\u60c5\u3002\u56fd\u6709\u4f53\u5236\u6539\u9769\u6d6a\u6f6e\u4e2d\uff0c\u8fd9\u6837\u7684\u4e8b\u60c5\u53d1\u751f\u592a\u591a\u4e86\u3002</p><p>\u3000\u3000\u56fd\u4f01\u804c\u5de5\u4e0e\u8d44\u65b9\u7684\u77db\u76fe\uff0c\u4e0e\u653f\u5e9c\u7684\u77db\u76fe\uff0c\u5728\u4eca\u540e\u51e0\u5e74\u65f6\u95f4\u91cc\u90fd\u4f1a\u6bd4\u8f83\u7a81\u7834\u3002</p><p>\u3000\u3000\u7684\u786e\uff0c\u4ece\u4e5d\u516b\u5e74\u521d\u5c31\u6380\u8d77\u7684\u56fd\u4f01\u4f53\u5236\u6539\u9769\u6d6a\u6f6e\u4e2d\uff0c\u56fd\u4f01\u804c\u5de5\u53ef\u4ee5\u8bf4\u5904\u4e8e\u7edd\u5bf9\u7684\u5f31\u52bf\uff0c\u65e0\u6570\u5b98\u5546\u4eec\u6b22\u6b23\u9f13\u821e\u4e3e\u8d77\u5200\u53c9\u6765\u5206\u4eab\u9955\u992e\u76db\u5bb4\uff0c\u4f17\u591a\u56fd\u4f01\u804c\u5de5\u88ab\u8feb\u79bb\u5f00\u4ed6\u4eec\u670d\u52a1\u4e86\u534a\u751f\u7684\u4f01\u4e1a\u5206\u6d41\u4e0b\u5c97\uff0c\u53ea\u83b7\u5f97\u6781\u53ef\u601c\u7684\u8865\u507f\uff0c\u8fde\u57fa\u672c\u7684\u751f\u6d3b\u90fd\u65e0\u6cd5\u7ef4\u6301\uff0c\u60f3\u8ba9\u4ed6\u4eec\u4e0d\u4e49\u6124\u586b\u81ba\u4e5f\u4e0d\u53ef\u80fd\uff0c\u5f20\u606a\u751a\u81f3\u6000\u7591\u56fd\u4eba\u592a\u4e0d\u4e49\u6124\u586b\u81ba\u3002\u4f46\u4e5f\u65e0\u6cd5\u5426\u8ba4\u56fd\u4f01\u4f53\u5236\u6539\u9769\u6709\u5176\u63a8\u52a8\u793e\u4f1a\u3001\u7ecf\u6d4e\u5f80\u524d\u53d1\u5c55\u7684\u6b63\u9762\u56e0\u7d20\uff0c\u5bfc\u81f4\u77db\u76fe\u4e00\u6b21\u6b21\u6fc0\u5316\u7684\u5173\u952e\u662f\u5730\u65b9\u653f\u5e9c\u7684\u5931\u804c\uff0c\u751a\u81f3\u6709\u65e0\u6570\u5b98\u5458\u53c2\u4e0e\u8fdb\u6765\u5206\u4eab\u8fd9\u6b21\u7684\u9955\u992e\u76db\u5bb4\u3002</p><p>\u3000\u3000\u53ea\u4e0d\u8fc7\u8fd9\u6b21\u9526\u6e56\u662f\u7ed9\u8bef\u4f24\u7684\uff0c\u8bef\u4f24\u80cc\u540e\u4e5f\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\u3002</p></article>"}}}]);
