(window.webpackJsonp=window.webpackJsonp||[]).push([[1067],{1090:function(p){p.exports={chapterTitle:null,title:"\u7b2c1064\u7ae0 \u5bf9\u6572\u8bc8\u7684\u6b22\u8fce",content:"<article><h2>\u7b2c1064\u7ae0 \u5bf9\u6572\u8bc8\u7684\u6b22\u8fce</h2><p>\u3000\u3000\u63a5\u5230\u5085\u4fca\u7684\u7535\u8bdd\uff0c\u7530\u529b\u5c71\u5e26\u7740\u5f20\u6587\u4e3d\u8fc7\u6765\u627e\u5f20\u606a\u3002</p><p>\u3000\u3000\u4e16\u7eaa\u9526\u6e56\u5728\u5b66\u5e9c\u5df7\u5efa\u9752\u5e74\u793e\u533a\uff0c\u5171\u5efa\u4e86\u5341\u4e8c\u680b\u516c\u5bd3\u697c\uff0c\u56db\u680b\u697c\u5f62\u6210\u4e00\u4e2a\u5c01\u95ed\u7684\u5355\u5143\uff0c\u540e\u6765\u7ecf\u8fc7\u6539\u5efa\uff0c\u5c06\u4e2d\u95f4\u56db\u680b\u697c\u518d\u6b21\u8fdb\u884c\u5206\u5272\uff0c\u5c06\u4e34\u8fd1\u4e1c\u6d77\u5927\u5b66\u4e1c\u5927\u95e8\u7684\u90a3\u680b\u697c\u5c01\u95ed\u6210\u4e00\u4e2a\u72ec\u7acb\u7684\u5355\u5143\uff0c\u53e6\u4e09\u680b\u697c\u90fd\u5212\u7ed9\u5317\u5355\u5143\uff0c\u7ed9\u521b\u4e1a\u56ed\u7684\u521b\u4e1a\u4f01\u4e1a\u5458\u5de5\u63d0\u4f9b\u9ad8\u8d28\u4f4e\u4ef7\u7684\u4f4f\u623f\u3002</p><p>\u3000\u3000\u90a3\u680b\u72ec\u6210\u4e00\u4f53\u7684\u516c\u5bd3\u697c\u5728\u4e00\u4e9b\u4eba\u7684\u773c\u91cc\u81ea\u7136\u5c31\u6709\u4e9b\u795e\u79d8\u8272\u5f69\uff0c\u4e5f\u6709\u4e00\u4e9b\u4eba\u4f4f\uff0c\u4e0d\u8fc7\u90fd\u4f4f\u5728\u9ad8\u5c42\uff0c\u4f4e\u4e0b\u51e0\u5c42\u90fd\u5e38\u5e74\u7a7a\u7f6e\u7740\u3002</p><p>\u3000\u3000\u7530\u529b\u5c71\u4e5f\u662f\u9996\u6b21\u8d70\u8fdb\u8fd9\u680b\u72ec\u6210\u4e00\u4f53\u7684\u9752\u5e74\u516c\u5bd3\uff0c\u4e5f\u6709\u4e00\u4e9b\u5b66\u751f\u6a21\u6837\u7684\u9752\u5e74\u7537\u5973\u8fdb\u51fa\uff0c\u7530\u529b\u5c71\u4e0e\u5f20\u6587\u4e3d\u8ddf\u8b66\u536b\u5ba4\u7684\u4fdd\u5168\u4eba\u5458\u8bf4\u660e\u6765\u610f\uff0c\u4fdd\u5168\u4eba\u5458\u6253\u4e00\u4e2a\u7535\u8bdd\u4e4b\u540e\uff0c\u5085\u4fca\u4eb2\u81ea\u51fa\u6765\u63a5\u4ed6\u4eec\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u8fdb\u8d70\u7535\u68af\uff0c\u7530\u529b\u5c71\u770b\u5230\u7535\u68af\u4e0a\u53ea\u663e\u793a\u5341\u4e00\u5c42\uff0c\u7591\u60d1\u7684\u95ee\u5085\u4fca\uff1a\u201c\u7ad9\u5728\u5916\u9762\u770b\uff0c\u8fd9\u697c\u6709\u5341\u4e8c\u5c42\uff0c\u8fd8\u662f\u8bf4\u6211\u773c\u775b\u770b\u82b1\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u5341\u4e00\u3001\u5341\u4e8c\u5c42\u662f\u8fde\u4f53\u7684\u590d\u5f0f\u7ed3\u6784\uff0c\u201d\u5085\u4fca\u8bf4\u9053\uff0c\u5c06\u624b\u6307\u6309\u5728\u7535\u68af\u6309\u952e\u7684\u6307\u7eb9\u8bc6\u522b\u5668\u4e0a\uff0c\u8ddf\u7530\u529b\u5c71\u89e3\u51b3\u9053\uff0c\u201c\u53bb\u5e74\u53d1\u751f\u4e86\u4e9b\u4e8b\u60c5\uff0c\u6709\u4e9b\u5b89\u9632\u8bbe\u65bd\u624d\u4e25\u683c\u8d77\u6765\uff0c\u7535\u68af\u8981\u6307\u7eb9\u8bc6\u522b\u624d\u80fd\u4e0a\u5341\u4e00\u697c\uff0c\u6d88\u9632\u697c\u68af\u5230\u4e86\u5341\u697c\u5f80\u4e0a\u4e5f\u662f\u5355\u5411\u5f80\u4e0b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u4e5f\u602a\u6ca1\u8da3\u7684\uff0c\u8ddf\u9501\u5728\u7b3c\u5b50\u91cc\u6709\u4ec0\u4e48\u533a\u522b\uff1f\u201d</p><p>\u3000\u3000\u5085\u4fca\u4ece\u4e0d\u8ba4\u4e3a\u5f20\u606a\u7684\u751f\u6d3b\u4f1a\u6ca1\u8da3\uff0c\u534a\u591c\u91cc\u4e5f\u8fd8\u8981\u966a\u7740\u4ed6\u5077\u5077\u6478\u6478\u7684\u5c06\u4e00\u8f86\u4fdd\u65f6\u6377\u505c\u5728\u4eba\u5bb6\u522b\u5885\u524d\u5c06\u8f66\u94a5\u5319\u88c5\u4fe1\u5c01\u91cc\u585e\u5230\u95e8\u91cc\u53bb\uff0c\u4ed6\u5f53\u7136\u4e5f\u4e0d\u80fd\u5c06\u6709\u8da3\u7684\u90e8\u5206\u968f\u53e3\u4e71\u8bf4\uff0c\u8ddf\u7530\u529b\u5c71\u4ecb\u7ecd\u8d77\u6628\u5929\u591c\u91cc\u53d1\u751f\u7684\u4e00\u4e9b\u4e8b\u60c5\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u5bf9\u592b\u5987\u4ee5\u540e\u7684\u518d\u5c31\u4e1a\u95ee\u9898\uff0c\u606a\u5c11\u8fd8\u662f\u5e0c\u671b\u7530\u5382\u957f\u4f60\u80fd\u51fa\u9762\u5e2e\u4ed6\u4eec\u89e3\u51b3\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u7530\u529b\u5c71\u6c89\u9ed8\u7247\u664c\uff0c\u624d\u6df1\u6df1\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u5efa\u90ba\u6709\u4e8c\u5341\u4e8c\u4e07\u4e0b\u5c97\u804c\u5de5\uff0c\u5176\u4e2d\u6709\u591a\u5c11\u53cc\u4e0b\u5c97\u804c\u5de5\u5bb6\u5ead\uff0c\u8fd8\u6ca1\u6709\u786e\u5207\u7684\u7edf\u8ba1\u6570\u636e\uff0c\u5e94\u8be5\u4e0d\u4f1a\u5c11\uff0c\u6211\u4eec\u670d\u52a1\u6240\u63a5\u89e6\u7684\u5c31\u5f88\u591a\uff0c\u4eba\u5747\u4e00\u767e\u4e8c\u5341\u5143\u7684\u751f\u6d3b\u8d39\u5728\u5efa\u90ba\u57ce\u5e02\u91cc\u751f\u5b58\u8270\u96be\uff0c\u8fd9\u4e9b\u53cc\u4e0b\u5c97\u804c\u5de5\u5bb6\u5ead\u7684\u751f\u6d3b\u5c24\u5176\u7684\u56f0\u96be\uff0c\u5e02\u653f\u5e9c\u5e94\u8be5\u8981\u4f18\u5148\u89e3\u51b3\u53cc\u4e0b\u5c97\u804c\u5de5\u7684\u518d\u5c31\u4e1a\u95ee\u9898\uff0c\u5e0c\u671b\u8fd9\u6b21\u80fd\u6539\u5584\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6211\u51fa\u6765\u63a5\u4f60\u4eec\u65f6\uff0c\u606a\u5c11\u521a\u63a5\u5230\u7f57\u4e66\u8bb0\u7684\u7535\u8bdd\uff0c\u5e94\u8be5\u6709\u7ed3\u8bba\u4e86\u3002\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u5085\u4fca\u76f4\u63a5\u5c06\u7530\u529b\u5c71\u4e0e\u5f20\u6587\u4e3d\u9001\u5230\u697c\u9876\u7684\u7a7a\u4e2d\u5ead\u56ed\uff0c\u4e5d\u6708\u521d\u7684\u5efa\u90ba\uff0c\u592a\u9633\u5347\u8d77\u6765\u4e4b\u540e\uff0c\u8fd8\u662f\u6709\u4e9b\u708e\u70ed\uff0c\u4e0d\u8fc7\u697c\u9876\u7684\u7a7a\u4e2d\u5ead\u56ed\u6e05\u98ce\u4e60\u4e60\uff0c\u5341\u5206\u7684\u51c9\u723d\uff0c\u5f20\u606a\u5750\u5728\u8336\u5ba4\u91cc\u770b\u6587\u4ef6\uff0c\u770b\u5230\u5085\u4fca\u5e26\u7740\u7530\u529b\u5c71\u4e0e\u5f20\u6587\u4e3d\u4e0a\u6765\uff0c\u7ad9\u8d77\u6765\uff0c\u8ddf\u7530\u529b\u5c71\u8bf4\u9053\uff1a\u201c\u6211\u8fd8\u4ee5\u4e3a\u4e0b\u5c97\u804c\u5de5\u7684\u751f\u6d3b\u4fdd\u969c\u8d39\u63d0\u9ad8\u5230\u4e8c\u767e\u56db\u5341\u5143\u624d\u7b97\u6709\u4e2a\u57fa\u672c\u7684\u751f\u6d3b\u4fdd\u969c\uff0c\u5e02\u91cc\u8fde\u591c\u5f00\u4f1a\u8ba8\u8bba\u7684\u7ed3\u679c\uff0c\u5728\u539f\u5148\u7684\u57fa\u7840\u4e0a\u589e\u52a0\u516d\u5341\u5143\uff0c\u63d0\u9ad8\u5230\u4e00\u767e\u516b\u5341\u5143\u2026\u2026\u6709\u603b\u6bd4\u6ca1\u6709\u5f3a\u4e00\u4e9b\uff0c\u4e0b\u4e2a\u6708\u5c31\u6309\u8fd9\u4e2a\u6807\u51c6\u53d1\u653e\uff1b\u7701\u91cc\u4e5f\u4f1a\u8981\u6c42\u5404\u5730\u5e02\u6c11\u653f\u5e9c\u90e8\u95e8\u5bf9\u4e0b\u5c97\u804c\u5de5\u7684\u751f\u6d3b\u4fdd\u969c\u8865\u52a9\u5728\u539f\u6709\u7684\u57fa\u7840\u4e0a\u63d0\u9ad8\u4e00\u6b21\u6027\u63d0\u9ad830-50\uff05\u2026\u2026\u5728\u5b50\u5973\u4e0a\u5b66\u3001\u533b\u7597\u6551\u52a9\u65b9\u9762\uff0c\u4e5f\u4f1a\u7ed9\u4e0b\u5c97\u804c\u5de5\u63d0\u4f9b\u76f8\u5e94\u7684\u8865\u52a9\uff0c\u5177\u4f53\u7684\u65b9\u6cd5\u62d6\u540e\u4e00\u4e9b\u624d\u4f1a\u51fa\u53f0\uff0c\u5404\u4e2a\u5730\u65b9\u7684\u6807\u51c6\u4e5f\u4e0d\u5c3d\u4e00\u81f4\uff0c\u8981\u6839\u636e\u5730\u65b9\u8d22\u653f\u6536\u5165\u60c5\u51b5\u3002\u6211\u627e\u4f60\u8fc7\u6765\uff0c\u8fd8\u662f\u89c9\u5f97\u670d\u52a1\u6240\u5728\u89e3\u51b3\u4e0b\u5c97\u804c\u5de5\u7684\u518d\u5c31\u4e1a\u95ee\u9898\u8fd8\u80fd\u53d1\u6325\u66f4\u5927\u7684\u4f5c\u7528\uff0c\u521a\u624d\u6211\u8ddf\u7f57\u4e66\u8bb0\u901a\u7535\u8bdd\u65f6\u63d0\u5230\u4f60\uff0c\u7f57\u4e66\u8bb0\u5bf9\u4f60\u7684\u5370\u8c61\u5f88\u6df1\u523b\uff0c\u6b22\u8fce\u4f60\u53bb\u627e\u4ed6\u7684\u2018\u9ebb\u70e6\u2019\u3002\u770b\u6765\uff0c\u7530\u5382\u957f\u4f60\u8981\u5148\u53bb\u5411\u7f57\u4e66\u8bb0\u6c47\u62a5\u5de5\u4f5c\uff0c\u7136\u540e\u518d\u6765\u627e\u4f01\u4e1a\u62c9\u2018\u8d5e\u52a9\u2019\uff0c\u8fd9\u4e00\u6b21\u8d5e\u52a9\uff0c\u6211\u4eec\u4e5f\u53ef\u4ee5\u5149\u660e\u6b63\u5927\u7684\u7ed9\u2026\u2026\u201d</p><p>\u3000\u3000\u7530\u529b\u5c71\u5fc3\u60f3\u7f57\u541b\u5bf9\u81ea\u5df1\u6700\u6df1\u523b\u7684\u5370\u8c61\u5927\u6982\u5c31\u662f\u5c06\u4e3d\u4e30\u5370\u67d3\u5382\u5356\u7ed9\u9526\u6e56\u4ed6\u9886\u5bfc\u804c\u5de5\u5835\u5927\u95e8\u5427\uff0c\u53bb\u627e\u7f57\u541b\u201c\u9ebb\u70e6\u201d\uff0c\u4e5f\u5c31\u610f\u5473\u7740\u4ed6\u8fd9\u4e24\u5e74\u6765\u4ee5\u79c1\u4eba\u540d\u4e49\u641e\u7684\u4e0b\u5c97\u804c\u5de5\u518d\u5c31\u4e1a\u670d\u52a1\u6240\u80fd\u5f97\u5230\u5b98\u65b9\u7684\u627f\u8ba4\uff0c\u4e0d\u7528\u62c5\u5fc3\u518d\u7ed9\u6709\u4e9b\u90e8\u95e8\u4e0a\u56de\u627e\u9ebb\u70e6\uff0c\u4f46\u662f\u670d\u52a1\u6240\u505a\u51fa\u7684\u6210\u7ee9\u4e5f\u8981\u5f52\u5230\u653f\u5e9c\u5927\u529b\u652f\u6301\u7684\u540d\u4e49\u4e0b\u3002</p><p>\u3000\u3000\u7530\u529b\u5c71\u5bf9\u201c\u62db\u5b89\u201d\u4e5f\u6ca1\u6709\u4ec0\u4e48\u53cd\u611f\uff0c\u80fd\u4e3a\u4e0b\u5c97\u804c\u5de5\u505a\u66f4\u591a\u7684\u4e8b\u60c5\u624d\u662f\u6700\u4e3b\u8981\u7684\uff0c\u4ed6\u4ecb\u7ecd\u5f20\u6587\u4e3d\u7ed9\u5f20\u606a\u8ba4\u8bc6\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5f20\u6587\u4e3d\u63e1\u4e86\u63e1\u624b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u8d75\u9e4f\u6ca1\u6709\u8ddf\u6211\u63d0\u8d77\u8fc7\u4f60\u2014\u2014\u5bf9\u4e86\uff0c\u4f60\u4eec\u51b3\u5b9a\u51e0\u65f6\u7ed3\u5a5a\uff0c\u5230\u65f6\u5019\u8bb0\u5f97\u7ed9\u6211\u53d1\u4e00\u5f20\u8bf7\u67ec\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u6587\u4e3d\u4e0d\u77e5\u9053\u5f20\u606a\u77e5\u9053\u5979\uff0c\u7ed9\u8bf4\u7834\u8fd8\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u5979\u4e24\u5e74\u524d\u7ed9\u7530\u529b\u5c71\u63a8\u8350\u53bb\u6a61\u6811\u56ed\u5f53\u884c\u653f\u79d8\u4e66\uff0c\u6ca1\u6709\u9003\u51fa\u5f53\u65f6\u6a61\u6811\u56ed\u884c\u653f\u90e8\u7ecf\u7406\u8d75\u9e4f\u7684\u624b\u638c\u5fc3\uff0c\u4e0e\u8d75\u9e4f\u8c08\u604b\u7231\u4e4b\u540e\uff0c\u4e24\u4eba\u90fd\u5728\u6a61\u6811\u56ed\u5185\u90e8\u5de5\u4f5c\u4e5f\u6709\u4e9b\u4e0d\u5408\u9002\uff0c\u5f20\u6587\u4e3d\u7d22\u6027\u5230\u7530\u529b\u5c71\u529e\u7684\u4e0b\u5c97\u804c\u5de5\u518d\u5c31\u4e1a\u670d\u52a1\u6240\u91cc\u5de5\u4f5c\u2026\u2026\u4e24\u4eba\u7684\u5a5a\u671f\u4e5f\u8fd1\u4e86\uff0c\u53ea\u4e0d\u8fc7\u8d75\u9e4f\u4f5c\u4e3a\u9526\u6e56\u5546\u4e8b\u9a7b\u65b0\u4ead\u8054\u7edc\u529e\u516c\u5ba4\u7684\u603b\u8d1f\u8d23\u4eba\u7ed9\u6d3e\u5230\u65b0\u4ead\uff0c\u5efa\u90ba\u8fd9\u8fb9\u5a5a\u4e8b\u7684\u51c6\u5907\u4e5f\u662f\u5979\u5728\u7b80\u5355\u7684\u51c6\u5907\u3002</p><p>\u3000\u3000\u5f20\u6587\u4e3d\u8fd8\u4e00\u76f4\u6ca1\u6709\u89c1\u8fc7\u8d75\u9e4f\u773c\u4e2d\u7f69\u7740\u795e\u79d8\u5149\u73af\u7684\u5927\u8001\u677f\uff0c\u542c\u7530\u529b\u5c71\u63cf\u8ff0\uff0c\u4e5f\u5c31\u5bfb\u5e38\u7684\u5b66\u751f\u6a21\u6837\uff0c\u73b0\u5728\u770b\u770b\uff0c\u4e5f\u6ca1\u6709\u7279\u522b\u7684\u2014\u2014\u53ea\u662f\u4ed6\u53ef\u4ee5\u8bf4\u662f\u56fd\u5185\u6700\u6709\u6743\u52bf\u7684\u4eba\u4e4b\u4e00\u5427\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u5728\u65b0\u4ead\u7684\u6295\u8d44\u5982\u6b64\u4e4b\u5de8\uff0c\u8d75\u9e4f\u4f5c\u4e3a\u9526\u6e56\u5546\u4e8b\u9a7b\u65b0\u4ead\u8054\u7edc\u529e\u516c\u5ba4\u7684\u603b\u8d1f\u8d23\u4eba\uff0c\u5373\u4f7f\u8fd8\u6ca1\u6709\u8d44\u683c\u8fdb\u5165\u4e8c\u5341\u4e5d\u4eba\u4f1a\u8bae\uff0c\u4e5f\u79bb\u6838\u5fc3\u5c42\u4e0d\u8fdc\u4e86\uff0c\u4e0e\u9526\u6e56\u5546\u4e8b\u4e2d\u56fd\u5317\u65b9\u516c\u53f8\u603b\u7ecf\u7406\u59da\u575a\u5c42\u6b21\u76f8\u5f53\uff0c\u53ea\u4e0d\u8fc7\u9526\u6e56\u5546\u4e8b\u4e2d\u56fd\u603b\u90e8\u5c31\u5728\u5efa\u90ba\uff0c\u7531\u5468\u4e00\u5e73\u4e3b\u6301\uff0c\u6ca1\u6709\u5fc5\u8981\u518d\u6210\u7acb\u5357\u65b9\u516c\u53f8\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u8d75\u9e4f\u662f\u5357\u65b9\u516c\u53f8\u603b\u7ecf\u7406\u7684\u5f53\u7136\u4eba\u9009\u3002\u90fd\u8ddf\u8d75\u9e4f\u8c08\u5a5a\u8bba\u5ac1\uff0c\u5f20\u6587\u4e3d\u5f53\u7136\u6e05\u695a\u6b64\u65f6\u7684\u9526\u6e56\u5728\u56fd\u5185\u662f\u4f55\u7b49\u7684\u5f3a\u52bf\u3002</p><p>\u3000\u3000\u7530\u529b\u5c71\u8ddf\u8d75\u9e4f\u63a5\u89e6\u4e5f\u591a\uff0c\u6700\u521d\u662f\u4e3a\u4e86\u201c\u6572\u8bc8\u201d\u9526\u6e56\uff0c\u8d75\u9e4f\u662f\u9526\u6e56\u5f53\u65f6\u5728\u5efa\u90ba\u7684\u8054\u7edc\u4eba\uff0c\u540e\u6765\u5f20\u6587\u4e3d\u4e0e\u8d75\u9e4f\u5173\u7cfb\u516c\u5f00\uff0c\u5173\u7cfb\u5c31\u66f4\u719f\u7edc\u4e86\uff0c\u89c1\u5f20\u606a\u4e5f\u5173\u5fc3\u7740\u8d75\u9e4f\u4e0e\u5f20\u6587\u4e3d\u7684\u5a5a\u4e8b\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u90a3\u4e5f\u8981\u9526\u6e56\u786e\u4fdd\u7ed9\u8d75\u9e4f\u6709\u56de\u5efa\u90ba\u7ed3\u5a5a\u7684\u65f6\u95f4\uff0c\u6bcf\u6b21\u90fd\u662f\u6587\u4e3d\u8f9b\u82e6\u7684\u5f80\u65b0\u4ead\u8d76\u7740\u9e4a\u6865\u76f8\u4f1a\u3002\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\uff0c\u7530\u5382\u957f\u8ba9\u6587\u4e3d\u53bb\u65b0\u4ead\u6e21\u4e00\u4e2a\u6708\u7684\u871c\u6708\u4e5f\u6210\u554a\uff01\u201d\u5f20\u606a\u5f00\u73a9\u7b11\u8bf4\u9053\uff0c\u201c\u6b64\u65f6\u65b0\u4ead\u7684\u98ce\u666f\u8fd8\u662f\u76f8\u5f53\u4e0d\u9519\u7684\uff0c\u7b49\u4e34\u6e2f\u5de5\u4e1a\u53d1\u5c55\u8d77\u6765\uff0c\u5c31\u7b97\u518d\u6ce8\u91cd\u73af\u5883\uff0c\u8fd8\u662f\u8981\u4ed8\u51fa\u4e00\u5b9a\u7684\u4ee3\u4ef7\u2026\u2026\u201d</p><p>\u3000\u3000\u684c\u4e0a\u624b\u673a\u54cd\u4e86\u8d77\u6765\uff0c\u5f20\u606a\u62ff\u8d77\u624b\u673a\uff0c\u89c1\u662f\u9648\u9759\u6253\u6765\u7684\u7535\u8bdd\uff0c\u63a5\u901a\u7535\u8bdd\u7b11\u7740\u95ee\u5979\uff1a\u201c\u6709\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd8\u4ee5\u4e3a\u4f60\u5728\u5f00\u4f1a\u5462\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u8fd8\u5728\u4f1a\u8bae\u5ba4\u91cc\uff0c\u6211\u8bb0\u5f97\u4f60\u4e0a\u5348\u8981\u8ddf\u7530\u529b\u5c71\u89c1\u9762\uff0c\u6211\u4e0e\u5b50\u5609\u8fd8\u6709\u90c1\u840d\u7b80\u5355\u7684\u8ba8\u8bba\u4e86\u4e00\u4e0b\uff0c\u6211\u4eec\u79d1\u738b\u9ad8\u79d1\u4e5f\u5e94\u8be5\u4e3a\u4e0b\u5c97\u804c\u5de5\u51fa\u4e00\u4efd\u529b\u7684\uff0c\u4e0d\u8fc7\u6211\u53ef\u6ca1\u6709\u4f60\u6cb9\u6c34\u8db3\u2026\u2026\u201d\u9648\u9759\u5728\u7535\u8bdd\u90a3\u5934\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u868a\u5b50\u817f\u4e0a\u8089\u4e5f\u662f\u8089\uff0c\u7530\u5382\u957f\u4ed6\u4eba\u6b63\u5728\u6211\u8eab\u8fb9\u5462\uff0c\u8981\u4e0d\u4f60\u76f4\u63a5\u8ddf\u4ed6\u901a\u7535\u8bdd\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\uff0c\u77e5\u9053\u9648\u9759\u8ddf\u79d1\u738b\u9ad8\u5c42\u5728\u5efa\u90ba\u7684\u4eba\u5458\u5728\u4e00\u8d77\uff0c\u8fd8\u662f\u95ee\u4e86\u4e00\u53e5\uff0c\u201c\u65e9\u4e0a\u901a\u7535\u8bdd\u65f6\uff0c\u4f60\u65c1\u8fb9\u4e5f\u662f\u6709\u4eba\u5728\uff1f\u201d</p><p>\u3000\u3000\u201c\u55ef\uff01\u201d\u9648\u9759\u7ea0\u7ed3\u4e86\u534a\u5929\uff0c\u597d\u4e0d\u5bb9\u6613\u60f3\u5230\u4e00\u4e2a\u501f\u53e3\u7ed9\u5f20\u606a\u518d\u6b21\u6253\u8fd9\u4e2a\u7535\u8bdd\uff0c\u5c31\u662f\u4e3a\u4e86\u201c\u55ef\u201d\u4e00\u58f0\uff0c\u201c\u5177\u4f53\u7684\u4e8b\u60c5\uff0c\u6211\u8ba9\u5218\u660e\u8f89\u8ddf\u4ed6\u8054\u7cfb\u5427\uff0c\u6211\u8fd8\u8981\u63a5\u7740\u5f00\u4f1a\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6302\u4e86\u7535\u8bdd\uff0c\u8ddf\u7530\u529b\u5c71\u8bf4\u9053\uff1a\u201c\u6211\u53c8\u7ed9\u4f60\u4eec\u627e\u4e86\u4e2a\u53ef\u4ee5\u62c9\u8d5e\u52a9\u7684\u5bf9\u8c61\uff0c\u79d1\u738b\u9ad8\u79d1\u7684\u5e02\u573a\u603b\u76d1\u5218\u660e\u8f89\u4f1a\u627e\u65f6\u95f4\u8ddf\u4f60\u4eec\u8054\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u6587\u4e3d\u8ddf\u8d75\u9e4f\u8c08\u604b\u7231\u8fd1\u4e24\u5e74\u65f6\u95f4\uff0c\u4e0e\u5218\u660e\u8f89\u4e5f\u6709\u8fc7\u89c1\u9762\u7684\u673a\u4f1a\u3002\u5979\u77e5\u9053\u5218\u660e\u8f89\u7684\u7ecf\u5386\u4e5f\u7b97\u662f\u9887\u6709\u4f20\u5947\u8272\u5f69\uff0c\u4f5c\u4e3a\u8ddf\u968f\u5f20\u606a\u6700\u65e9\u7684\u56db\u4eba\u4f17\uff0c\u4e5f\u662f\u9526\u6e56\u6700\u521d\u7684\u4e3b\u8981\u9aa8\u5e72\uff0c\u53ea\u53ef\u60dc\u540e\u6765\u8df3\u69fd\u53bb\u4e86\u79d1\u738b\u3002\u9526\u6e56\u4e0e\u79d1\u738b\u7684\u6069\u6028\u4e5f\u662f\u590d\u6742\u83ab\u660e\u7684\uff0c\u81f3\u5c11\u5916\u4eba\u5f88\u96be\u660e\u767d\uff0c\u5728\u5916\u4eba\u773c\u91cc\uff0c\u79d1\u738b\u9ad8\u79d1\u4e0e\u79d1\u738b\u6709\u7740\u5f88\u6df1\u7684\u6e0a\u6e90\uff0c\u5e94\u8be5\u5173\u7cfb\u8981\u66f4\u4eb2\u8fd1\u4e00\u4e9b\uff0c\u4f46\u662f\u9526\u6e56\u5bf9\u5f85\u79d1\u738b\u9ad8\u79d1\u9f0e\u529b\u652f\u6301\uff0c\u5bf9\u79d1\u738b\u53c8\u662f\u82e5\u6709\u82e5\u65e0\u7684\u538b\u5236\u7740\uff0c\u800c\u5f53\u521d\u8df3\u51fa\u9526\u6e56\u7684\u5218\u660e\u8f89\u5374\u53c8\u5728\u79d1\u738b\u9ad8\u79d1\u91cc\u62c5\u4efb\u5e02\u573a\u603b\u76d1\u2014\u2014\u8fd9\u5176\u4e2d\u7684\u590d\u6742\u5173\u7cfb\uff0c\u8fd8\u771f\u8ba9\u4eba\u8d39\u89e3\uff0c\u9664\u975e\u5979\u77e5\u9053\u5f20\u606a\u6628\u5929\u6df1\u66f4\u534a\u591c\u5077\u5077\u6478\u6478\u7684\u5c06\u4fdd\u65f6\u6377\u505c\u5728\u4eba\u5bb6\u522b\u5885\u697c\u524d\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5750\u8f66\u7ecf\u8fc7\u4e1c\u6d77\u5927\u5b66\u4e1c\u5927\u95e8\uff0c\u987e\u6653\u6885\u7279\u610f\u770b\u4e86\u770b\u8f66\u7a97\u5916\u8fd9\u680b\u72ec\u6210\u4e00\u4f53\u7684\u516c\u5bd3\u697c\uff0c\u8ddf\u738b\u7434\u8bf4\u9053\uff1a\u201c\u5f20\u5e02\u957f\u7684\u513f\u5b50\u5c31\u4f4f\u8fd9\u680b\u697c\u9876\u697c\u2026\u2026\u201d\u5979\u4e0e\u738b\u7434\u5148\u6765\u5b66\u6821\u5e2e\u9648\u5b81\u642c\u5bbf\u820d\uff0c\u9648\u80b2\u4e0e\u4e8e\u7af9\u7559\u5728\u533b\u9662\u966a\u9648\u5b81\u505a\u68c0\u67e5\uff0c\u60c5\u51b5\u521d\u5224\u8fd8\u6bd4\u8f83\u4e50\u89c2\uff0c\u5979\u4eec\u5fc3\u5934\u4e5f\u6ca1\u6709\u591a\u5c11\u538b\u529b\u3002</p><p>\u3000\u3000\u201c\u770b\u4e0d\u5230\u697c\u9876\u6709\u534a\u4ea9\u5730\u5927\u7684\u56ed\u5b50\u554a\u3002\u201d\u738b\u7434\u4e5f\u8d34\u7740\u8f66\u7a97\u62ac\u5934\u5f80\u4e0a\u770b\uff0c\u53ea\u770b\u89c1\u697c\u9876\u6728\u6805\u680f\u91cc\u8fd8\u6709\u679d\u53f6\u6ea2\u51fa\u6765\uff0c\u786e\u5b9e\u50cf\u4e00\u5ea7\u7ed9\u6728\u6805\u680f\u56f4\u8d77\u6765\u7684\u56ed\u5b50\u3002</p><p>\u3000\u3000\u624b\u673a\u54cd\u8d77\u6765\uff0c\u987e\u6653\u6885\u4ece\u5305\u91cc\u62ff\u51fa\u624b\u673a\uff0c\u89c1\u662f\u5e02\u653f\u5e9c\u6253\u6765\u7684\u7535\u8bdd\uff0c\u4e0d\u6562\u6020\u6162\uff0c\u7acb\u5373\u63a5\u4e86\u8d77\u6765\u3002\u738b\u7434\u542c\u987e\u6653\u6885\u5728\u90a3\u91cc\u8bb2\u7535\u8bdd\uff0c\u63d0\u5230\u4e0b\u5c97\u4e0e\u53cc\u4e0b\u5c97\u804c\u5de5\u7684\u4e8b\u60c5\uff0c\u7b49\u5979\u6302\u4e0a\u7535\u8bdd\uff0c\u95ee\u9053\uff1a\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d</p><p>\u3000\u3000\u201c\u529e\u516c\u5ba4\u6253\u6765\u7684\u7535\u8bdd\uff0c\u7701\u653f\u5e9c\u4e00\u5927\u65e9\u5c31\u7d27\u6025\u901a\u77e5\u5404\u5730\u63d0\u9ad8\u4e0b\u5c97\u804c\u5de5\u751f\u6d3b\u4fdd\u969c\u8865\u52a9\u6807\u51c6\uff0c\u5e02\u653f\u5e9c\u51b3\u5b9a\u6309\u7167\u7701\u653f\u5e9c\u89c4\u5b9a\u7684\u6700\u9ad8\u6807\u51c6\u6267\u884c\uff0c\u6211\u4e0b\u5348\u5c31\u8981\u56de\u65b0\u829c\u53bb\u3002\u201d</p><p>\u3000\u3000\u201c\u554a\u2026\u2026\u4f1a\u4e0d\u4f1a\u8ddf\u6628\u5929\u90a3\u4e2a\u81ea\u6740\u7684\u4e0b\u5c97\u5973\u804c\u5de5\u6709\u5173\uff1f\u653f\u5e9c\u90e8\u95e8\u505a\u51b3\u7b56\uff0c\u4ec0\u4e48\u65f6\u5019\u8fd9\u4e48\u8fc5\u901f\u8fc7\uff1f\u201d\u738b\u7434\u8be7\u5f02\u7684\u95ee\u3002</p></article>"}}}]);
