(window.webpackJsonp=window.webpackJsonp||[]).push([[107],{130:function(p){p.exports={chapterTitle:null,title:"\u7b2c107\u7ae0 \u65e0\u975e\u5c31\u662f\u59a5\u534f",content:"<article><h2>\u7b2c107\u7ae0 \u65e0\u975e\u5c31\u662f\u59a5\u534f</h2><p>\u3000\u3000\u8ddf\u803f\u5efa\u5fe0\u6ca1\u6709\u63d0\u8d77\u674e\u829d\u82b3\u4e0e\u738b\u708e\u658c\u5077\u60c5\u7684\u4e8b\uff0c\u4e5f\u6ca1\u6709\u9a97\u4ed6\u8bf4\u675c\u98de\u5148\u6328\u6253\uff0c\u53ef\u4e0e\u4e0d\u53ef\u4e4b\u95f4\uff0c\u7531\u803f\u5efa\u5fe0\u81ea\u5df1\u731c\u6d4b\u3002\u867d\u7136\u5927\u5bb6\u90fd\u662f\u6b63\u5904\u7ea7\uff0c\u5f20\u77e5\u884c\u5374\u662f\u5e02\u957f\u3001\u5e02\u59d4\u4e66\u8bb0\u9762\u524d\u7684\u7ea2\u4eba\uff0c\u675c\u5c0f\u5c71\u66f4\u662f\u5927\u6743\u5728\u63e1\u7684\u533a\u59d4\u4e66\u8bb0\uff0c\u90fd\u6bd4\u6559\u80b2\u5c40\u957f\u91cd\u8981\u5f97\u591a\uff0c\u53ea\u8981\u4e0d\u51fa\u4eba\u547d\uff0c\u803f\u5efa\u5fe0\u4e5f\u77e5\u9053\u591a\u4e00\u4e8b\u4e0d\u5982\u5c11\u4e00\u4e8b\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u7684\u79d8\u4e66\u53ca\u65f6\u8d76\u5230\u533b\u9662\uff0c\u6d77\u5dde\u533b\u9662\u4e24\u540d\u9885\u5916\u79d1\u533b\u751f\u90fd\u7ed9\u53eb\u5230\u533b\u9662\uff0c\u5bf9\u66f9\u5149\u660e\u8fdb\u884c\u7d27\u6025\u624b\u672f\uff0c\u624d\u6d88\u9664\u5f15\u8d77\u5934\u90e8\u9aa8\u6298\u8ff8\u53d1\u75c7\u7684\u53ef\u80fd\uff0c\u8fd9\u65f6\u5019\u9ebb\u9189\u8fd8\u6ca1\u6709\u8fc7\u53bb\uff0c\u81ea\u7136\u65e0\u6cd5\u8ddf\u66f9\u5149\u660e\u4ea4\u6d41\uff0c\u5f20\u77e5\u884c\u4e0e\u803f\u5efa\u5fe0\u8d76\u8fc7\u6765\uff0c\u5c31\u662f\u8981\u786e\u5b9a\u66f9\u5149\u660e\u53d7\u7684\u4f24\u80fd\u591f\u6062\u590d\u8fc7\u6765\uff0c\u66f4\u4e0d\u80fd\u6709\u6027\u547d\u4e4b\u5fe7\uff0c\u5426\u5219\u6ca1\u6709\u4eba\u4f1a\u63d2\u624b\u8fd9\u4e8b\uff0c\u5f20\u77e5\u884c\u8fd8\u8981\u5c06\u5f20\u606a\u62c9\u56de\u5bb6\u3002</p><p>\u3000\u3000\u770b\u7740\u66f9\u5149\u660e\u8111\u888b\u7ed9\u5305\u88f9\u7684\u4e25\u4e25\u5b9e\u5b9e\u7684\uff0c\u5f20\u606a\u60f3\u8d77\u675c\u98de\u62f3\u62f3\u89c1\u8840\u7684\u6076\u72e0\u52b2\uff0c\u80cc\u810a\u6c57\u6bdb\u8038\u7acb\u3002\u4eba\u751f\u8bfb\u6863\u4e4b\u524d\uff0c\u4e5f\u6ca1\u6709\u4eb2\u773c\u770b\u5230\u675c\u98de\u62ff\u9ebb\u888b\u6b7b\u63cd\u66f9\u5149\u660e\u7684\u60c5\u5f62\uff0c\u53ea\u662f\u9053\u542c\u9014\u8bf4\uff0c\u5f20\u606a\u5728\u90a3\u4e4b\u540e\u624d\u4e0e\u675c\u98de\u5173\u7cfb\u597d\u8d77\u6765\uff0c\u90a3\u65f6\u675c\u98de\u6bd4\u73b0\u5728\u8981\u6210\u719f\u7a33\u91cd\u591a\u4e86\uff0c\u5927\u6982\u5c31\u662f\u8fd9\u6b21\u4e8b\u60c5\u7ed9\u4ed6\u592a\u591a\u7684\u8f6c\u6298\u5427\u3002\u65e0\u8bba\u5982\u4f55\uff0c\u66f9\u5149\u660e\u90fd\u6ca1\u6709\u9003\u8fc7\u4e00\u987f\u9971\u63cd\uff0c\u675c\u98de\u7684\u5185\u5fc3\u4e5f\u4e0d\u53ef\u907f\u514d\u7684\u53d7\u5230\u8fd9\u6b21\u4e8b\u4ef6\u7684\u51b2\u51fb\uff0c\u674e\u829d\u82b3\u4e0e\u738b\u708e\u658c\u7684\u547d\u8fd0\u5374\u662f\u9006\u8f6c\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u7ad9\u5728\u75c5\u5e8a\u524d\u550f\u5618\u4e0d\u5df2\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u3001\u803f\u5efa\u5fe0\u3001\u5f20\u77e5\u884c\u7ad9\u5728\u4e00\u65c1\u7a83\u7a83\u79c1\u8bed\uff0c\u4ed6\u4eec\u6b63\u8ba8\u8bba\u8fd9\u4e8b\u60c5\u5982\u4f55\u5584\u540e\u3002</p><p>\u3000\u3000\u5c31\u7b97\u77e5\u60c5\u4eba\u4e32\u901a\u4e00\u6c14\u54ac\u5b9a\u5f20\u606a\u5f53\u4f17\u6492\u4e0b\u7684\u8c0e\u8a00\u5c31\u662f\u4e8b\u5b9e\u7684\u771f\u76f8\uff0c\u5c31\u7b97\u8fd9\u80fd\u7792\u5929\u8fc7\u6d77\uff0c\u675c\u98de\u4e0b\u624b\u4e4b\u72e0\uff0c\u4e5f\u6709\u6545\u610f\u4f24\u5bb3\u4e4b\u5acc\uff0c\u4e0d\u80fd\u8ba9\u66f9\u5149\u660e\u53ca\u5bb6\u4eba\u81ea\u52a8\u95ed\u4e0a\u5634\uff0c\u5bf9\u675c\u5c0f\u5c71\u3001\u675c\u98de\u800c\u8a00\uff0c\u59cb\u7ec8\u662f\u79cd\u9ebb\u70e6\uff0c\u4f55\u51b5\u6d89\u53ca\u7537\u5973\u8273\u95fb\uff1f</p><p>\u3000\u3000\u201c\u803f\u5c40\u957f\u90fd\u4eb2\u81ea\u8fc7\u6765\u4e86\uff0c\u662f\u4e0d\u662f\u901a\u77e5\u4e00\u4e0b\u66f9\u8001\u5e08\u7684\u5bb6\u4eba\uff1f\u201d\u675c\u5c0f\u5c71\u95ee\u803f\u5efa\u5fe0\u3002</p><p>\u3000\u3000\u675c\u98de\u770b\u7740\u75c5\u5e8a\u4e0a\u7684\u66f9\u5149\u660e\uff0c\u773c\u775b\u4f9d\u65e7\u6ca1\u6cd5\u67d4\u548c\u8d77\u6765\uff0c\u5f20\u606a\u6487\u5634\u7b11\u4e86\u7b11\uff0c\u5fc3\u60f3\uff1a\u5927\u5bb6\u8fd9\u65f6\u5019\u624d\u60f3\u8d77\u901a\u77e5\u66f9\u5149\u660e\u5bb6\u4eba\u4e86\uff1f</p><p>\u3000\u3000\u201c\u738b\u708e\u658c\u4e0d\u51fa\u9762\u6ca1\u4e8b\uff1f\u201d\u803f\u5efa\u5fe0\u6709\u4e9b\u7591\u8651\uff0c\u5c31\u6015\u66f9\u5149\u660e\u7684\u5bb6\u4eba\u5230\u533b\u9662\u4e4b\u540e\u5435\u95f9\uff0c\u5fc3\u91cc\u60f3\u7b49\u66f9\u5149\u660e\u9192\u4e86\u4e4b\u540e\u624d\u901a\u77e5\u4ed6\u5bb6\u4eba\uff0c\u7b49\u9ebb\u9189\u8fc7\u4e86\uff0c\u53ea\u6015\u8981\u5230\u534a\u591c\uff0c\u90a3\u672a\u514d\u592a\u4e0d\u8fd1\u4eba\u60c5\u4e86\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u62bf\u7740\u5634\u4e0d\u53d1\u8868\u610f\u89c1\uff0c\u4e5f\u6ca1\u6709\u5fc5\u8981\u53d1\u8868\u610f\u89c1\uff0c\u53ea\u662f\u8868\u660e\u5510\u5b66\u8c26\u7684\u6001\u5ea6\uff0c\u5177\u4f53\u600e\u4e48\u505a\uff0c\u81ea\u7136\u7531\u675c\u5c0f\u5c71\u8ddf\u803f\u5efa\u5fe0\u8c08\uff0c\u4e5f\u662f\u675c\u5c0f\u5c71\u6b20\u803f\u5efa\u5fe0\u7684\u4eba\u60c5\u3002</p><p>\u3000\u3000\u201c\u738b\u708e\u658c\u4f5c\u4e3a\u4e00\u4e2d\u6821\u957f\u662f\u5931\u804c\u7684\u2026\u2026\u201d\u5f20\u606a\u5728\u65c1\u8fb9\u4e0d\u54b8\u4e0d\u6de1\u7684\u8bf4\u4e86\u4e00\u53e5\uff0c\u89c1\u4ed6\u4eec\u770b\u8fc7\u6765\uff0c\u95ed\u4e0a\u5634\u770b\u5411\u522b\u5904\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u89c1\u5f20\u606a\u8fd9\u4e48\u8bf4\uff0c\u5fc3\u60f3\u4e5f\u662f\uff0c\u738b\u708e\u658c\u4e5f\u8981\u53d7\u70b9\u6559\u8bad\uff0c\u5173\u952e\u65f6\u523b\u53ea\u60f3\u7740\u81ea\u5df1\u5f00\u6e9c\uff0c\u96be\u9053\u8fd9\u6837\u5c31\u4ee5\u4e3a\u80fd\u4fdd\u5168\u81ea\u5df1\uff1f\u4ed6\u8981\u80fd\u53ca\u65f6\u5236\u6b62\u675c\u98de\uff0c\u54ea\u91cc\u4f1a\u60f9\u51fa\u8fd9\u6869\u4e8b\uff1f\u675c\u5c0f\u5c71\u8ba9\u5f20\u606a\u4e00\u63d0\u9192\uff0c\u4e5f\u6068\u6068\u7684\u8bf4\uff1a\u201c\u738b\u708e\u658c\u4e0d\u9002\u5408\u5f53\u4e00\u4e2d\u6821\u957f\u2026\u2026\u201d\u8fd9\u8bdd\u672c\u4e0d\u8be5\u4ed6\u8bf4\uff0c\u4e0d\u8bf4\u5374\u4e0d\u89e3\u6068\u3002</p><p>\u3000\u3000\u201c\u738b\u708e\u658c\u7684\u7231\u4eba\u662f\u6211\u4eec\u5c40\u7684\u8096\u5a1c\u526f\u5c40\u957f\uff0c\u53ea\u6015\u4e0d\u5927\u597d\uff1f\u201d\u803f\u5efa\u5fe0\u6709\u4e9b\u72b9\u8c6b\u3002</p><p>\u3000\u3000\u675c\u5c0f\u5c71\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u803f\u5c40\u957f\u4e0d\u5982\u95ee\u95ee\u738b\u708e\u658c\u672c\u4eba\u7684\u610f\u89c1\uff0c\u76f4\u63a5\u8ddf\u4ed6\u8bf4\uff0c\u7f3a\u4e2a\u4f4d\u5b50\u5b89\u629a\u66f9\u8001\u5e08\uff0c\u95ee\u4ed6\u80fd\u4e0d\u80fd\u8ba9\u4e00\u4e0b\uff1f\u201d\u6bd4\u8d77\u95f9\u51fa\u8273\u95fb\uff0c\u5fc3\u60f3\u738b\u708e\u658c\u6216\u8bb8\u66f4\u613f\u610f\u8ba9\u51fa\u4f4d\u7f6e\uff0c\u201c\u4e0d\u8fc7\u66f9\u5149\u660e\u4e5f\u4e0d\u9002\u5408\u5f53\u4e00\u4e2d\u7684\u6821\u957f\u3002\u201d</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u89c1\u675c\u5c0f\u5c71\u8bf4\u7684\u8fd9\u4e48\u6709\u628a\u63e1\uff0c\u987a\u7740\u4ed6\u7684\u8bdd\u8bf4\uff1a\u201c\u8981\u662f\u738b\u708e\u658c\u4e3b\u52a8\u627f\u8ba4\u5de5\u4f5c\u4e2d\u6709\u4e0d\u8db3\u7684\u5730\u65b9\uff0c\u628a\u4e8c\u4e2d\u7684\u9f9a\u81ea\u9ad8\u8c03\u5230\u4e00\u4e2d\uff0c\u8ba9\u66f9\u5149\u660e\u53bb\u4e8c\u4e2d\u5f53\u6821\u957f\uff0c\u90a3\u4e5f\u662f\u53ef\u4ee5\u7684\u3002\u201d</p><p>\u3000\u3000\u9664\u4e86\u803f\u5efa\u5fe0\uff0c\u8c01\u4e5f\u4e0d\u8ba4\u8bc6\u9f9a\u81ea\u9ad8\uff0c\u5927\u51e1\u6d77\u5dde\u4eba\u90fd\u77e5\u9053\u6392\u5728\u4e00\u4e2d\u4e4b\u540e\u7684\u662f\u7b2c\u5341\u4e8c\u4e2d\u5b66\uff0c\u7b2c\u4e8c\u4e2d\u5b66\u8fd8\u8981\u5dee\u4e00\u4e9b\uff0c\u803f\u5efa\u5fe0\u8fd9\u4e48\u5b89\u6392\uff0c\u81ea\u7136\u6709\u4ed6\u7684\u79c1\u5fc3\uff0c\u4f46\u8fd9\u662f\u6559\u80b2\u5c40\u5185\u90e8\u7684\u4e8b\u60c5\uff0c\u675c\u5c0f\u5c71\u3001\u5f20\u77e5\u884c\u90fd\u61d2\u5f97\u8fc7\u95ee\u3002</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u770b\u4e86\u5f20\u77e5\u884c\u4e00\u773c\uff0c\u4ed6\u8fd9\u79cd\u5b89\u6392\u5728\u6559\u80b2\u5c40\u5185\u90e8\u6709\u963b\u529b\uff0c\u4f46\u662f\u5982\u6b64\u8fd9\u79cd\u5b89\u6392\u662f\u5510\u5e02\u957f\u8ba4\u53ef\u7684\uff0c\u90a3\u5c31\u6ca1\u6709\u963b\u529b\u4e86\u3002</p><p>\u3000\u3000\u5f20\u77e5\u884c\u5fae\u5fae\u9894\u9996\uff0c\u8868\u793a\u77e5\u9053\u803f\u5efa\u5fe0\u7684\u610f\u601d\uff0c\u5374\u4e0d\u660e\u786e\u8868\u6001\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u8eba\u5728\u75c5\u5e8a\u7684\u66f9\u5149\u660e\uff0c\u5fc3\u60f3\u4ed6\u4e5f\u771f\u662f\u53ef\u601c\uff0c\u6210\u4e3a\u522b\u4eba\u5229\u76ca\u4ea4\u6362\u781d\u7801\uff0c\u4ed6\u867d\u7136\u80fd\u6682\u65f6\u5f53\u4e0a\u4e8c\u4e2d\u7684\u6821\u957f\uff0c\u4e5f\u4e0d\u4f1a\u957f\u4e45\uff0c\u53ea\u8981\u4ed6\u5634\u5c01\u4f4f\u4e00\u5e74\u534a\u8f7d\uff0c\u5230\u65f6\u518d\u5c06\u4ed6\u634b\u4e0b\u6765\uff0c\u4ed6\u518d\u8bf4\u4ec0\u4e48\u90fd\u6ca1\u6709\u7528\uff0c\u8fd8\u53ef\u80fd\u7ed9\u81ea\u5df1\u62db\u906d\u66f4\u5927\u7684\u7978\u4e8b\u3002</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u89c1\u5927\u5bb6\u90fd\u8ba4\u53ef\u4ed6\u7684\u65b9\u6848\uff0c\u7ed9\u738b\u708e\u658c\u5bb6\u6302\u4e86\u7535\u8bdd\uff0c\u662f\u738b\u708e\u658c\u7231\u4eba\u8096\u5a1c\u63a5\u7684\u7535\u8bdd\uff0c\u803f\u5efa\u5fe0\u8bf7\u738b\u708e\u658c\u63a5\u7535\u8bdd\uff0c\u5148\u8d23\u95ee\u4ed6\u4e00\u4e2d\u53d1\u751f\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\u4ed6\u4eba\u4e3a\u4ec0\u4e48\u8eb2\u5728\u5bb6\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u62bf\u5634\u7b11\u7740\uff0c\u738b\u708e\u658c\u8981\u8fc7\u6765\u7684\u8bdd\uff0c\u4ed6\u8001\u5a46\u8096\u5a1c\u81ea\u7136\u4e0d\u53ef\u80fd\u4e0d\u8fc7\u6765\uff0c\u90a3\u738b\u708e\u658c\u6562\u8fc7\u6765\u9762\u5bf9\u66f9\u5149\u660e\u5417\uff1f</p><p>\u3000\u3000\u738b\u708e\u658c\u5728\u7535\u8bdd\u8bf4\u4ed6\u5728\u96ea\u5730\u91cc\u8dcc\u4e86\u4e00\u8de4\u5d34\u4e86\u811a\uff0c\u660e\u5929\u518d\u5230\u533b\u9662\u770b\u671b\u66f9\u5149\u660e\uff0c\u803f\u5efa\u5fe0\u95ee\u4e86\u66f9\u5149\u660e\u5bb6\u7684\u8054\u7cfb\u65b9\u5f0f\uff0c\u6302\u4e0a\u7535\u8bdd\u540e\u51b7\u51b7\u4e00\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u4e48\u5927\u7684\u4e8b\u60c5\uff0c\u4ed6\u8bf4\u5d34\u811a\u4e0d\u8fc7\u6765\uff0c\u770b\u6765\u4ed6\u662f\u771f\u4e0d\u60f3\u505a\u4e00\u4e2d\u6821\u957f\u4e86\u3002\u201d</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u6309\u738b\u708e\u658c\u7ed9\u7684\u8054\u7cfb\u65b9\u5f0f\u7ed9\u66f9\u5149\u660e\u5bb6\u6302\u4e86\u7535\u8bdd\uff0c\u7535\u8bdd\u91cc\u6ca1\u6709\u7ec6\u8bf4\uff0c\u53ea\u8bf4\u66f9\u5149\u660e\u5728\u5b66\u6821\u53d7\u4e86\u4f24\uff0c\u4eba\u73b0\u5728\u533b\u9662\uff0c\u5374\u4e5f\u5947\u602a\uff0c\u7adf\u7136\u6ca1\u6709\u54ea\u4e2a\u8001\u5e08\u4e3b\u52a8\u8054\u7cfb\u4ed6\u7684\u5bb6\u4eba\uff0c\u53ef\u89c1\u4ed6\u5e73\u65e5\u5728\u5b66\u6821\u5f88\u4e0d\u5f97\u4eba\u5fc3\u3002</p><p>\u3000\u3000\u66f9\u5149\u660e\u59bb\u5b50\u4e0e\u59bb\u5f1f\u8d76\u8fc7\u6765\u65f6\uff0c\u5feb\u5230\u5341\u4e8c\u70b9\u4e86\uff0c\u4ed6\u59bb\u5b50\u6709\u4e9b\u5f6a\u58ee\uff0c\u5dee\u4e0d\u591a\u6709\u5f20\u606a\u90a3\u4e48\u9ad8\uff0c\u8170\u7c97\u8180\u5706\uff0c\u8138\u4e0a\u8fd8\u6709\u6a2a\u8089\uff0c\u5979\u8d70\u8fdb\u6765\uff0c\u6251\u5730\u5c31\u54ed\u95f9\u3002\u75c5\u623f\u91cc\u7684\u4eba\u90fd\u7981\u4e0d\u4f4f\u76b1\u4e86\u76b1\u7709\u5934\u3002\u675c\u5c0f\u5c71\u5f00\u59cb\u5e86\u5e78\u6ca1\u6709\u542c\u738b\u708e\u658c\u7684\u4e3b\u610f\u5c06\u8fd9\u4e8b\u751f\u538b\u4e0b\u53bb\uff0c\u8fd9\u66f9\u5149\u660e\u7684\u8001\u5a46\u4e00\u770b\u5c31\u4e0d\u662f\u5584\u832c\uff0c\u8fd8\u662f\u5148\u884c\u5b89\u629a\u7684\u51b3\u7b56\u597d\u3002</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u76b1\u7740\u7709\u5934\u5bf9\u66f9\u5149\u660e\u7684\u8001\u5a46\u8bf4\uff1a\u201c\u66f9\u8001\u5e08\u5728\u5b66\u6821\u91cc\u6293\u5230\u5b66\u751f\u62bd\u70df\uff0c\u4ed6\u6559\u80b2\u5b66\u751f\u7684\u65b9\u6cd5\u8fc7\u4e8e\u6fc0\u70c8\u4e86\u4e00\u70b9\uff0c\u201d\u675c\u98de\u8138\u4e0a\u7684\u624b\u5370\u8fd8\u662f\u90a3\u4e48\u7684\u6e05\u6670\uff0c\u803f\u5efa\u5fe0\u5c06\u675c\u98de\u62c9\u8fc7\u6765\u7ed9\u5979\u770b\uff0c\u53c8\u4ecb\u7ecd\u675c\u5c0f\u5c71\u7684\u8eab\u4efd\uff0c\u201c\u66f9\u8001\u5e08\u592a\u6fc0\u52a8\u4e86\u4e9b\uff0c\u6ca1\u7ad9\u7a33\u59a5\uff0c\u4ece\u697c\u68af\u4e0a\u6454\u4e86\u4e0b\u6765\uff0c\u4e0d\u8fc7\u7ecf\u8fc7\u6551\u6cbb\uff0c\u5f88\u5feb\u5c31\u80fd\u6062\u590d\u2026\u2026\u201d</p><p>\u3000\u3000\u66f9\u5149\u660e\u59bb\u5b50\u8f6c\u5934\u770b\u89c1\u4e00\u5708\u4eba\u90fd\u9759\u9759\u7684\u770b\u7740\u5979\uff0c\u5fc3\u91cc\u53d1\u865a\uff0c\u4e5f\u5c31\u6536\u4f4f\u54ed\u58f0\uff0c\u4e0d\u6562\u518d\u95f9\uff0c\u53ea\u95ee\u803f\u5efa\u5fe0\uff0c\u4e3a\u4ec0\u4e48\u4e00\u4e2d\u7684\u6821\u957f\u738b\u708e\u658c\u4e0d\u8fc7\u770b\u671b\u66f9\u5149\u660e\u3002</p><p>\u3000\u3000\u201c\u738b\u6821\u957f\u5d34\u4e86\u811a\uff0c\u201d\u803f\u5efa\u5fe0\u7709\u5934\u76b1\u4e86\u76b1\uff0c\u53c8\u8bf4\uff0c\u201c\u5c31\u7b97\u5d34\u4e86\u811a\uff0c\u4e5f\u5e94\u8be5\u6765\u770b\uff0c\u771f\u662f\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u66f9\u5149\u660e\u59bb\u5b50\u4e00\u7ecf\u803f\u5efa\u5fe0\u8bf1\u5bfc\uff0c\u7acb\u5373\u5c06\u6028\u6c14\u6492\u5230\u738b\u708e\u658c\u8eab\u4e0a\uff0c\u53ea\u662f\u4e0d\u6562\u518d\u80e1\u95f9\uff0c\u4e00\u76f4\u4e0d\u505c\u7684\u7d6e\u7d6e\u53e8\u53e8\uff0c\u8fd8\u4ee3\u8868\u5979\u5bb6\u66f9\u5149\u660e\u5411\u675c\u98de\u9053\u6b49\uff0c\u5e0c\u671b\u675c\u5c0f\u5c71\u5927\u4eba\u5927\u8c05\uff0c\u4e0d\u8981\u751f\u6c14\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u53d7\u4e0d\u5230\u66f9\u5149\u660e\u7684\u5a46\u5a18\uff0c\u89c1\u9ebb\u9189\u65f6\u95f4\u5dee\u4e0d\u591a\u5c31\u8981\u5230\uff0c\u675c\u5c0f\u5c71\u3001\u5f20\u77e5\u884c\u3001\u5f20\u606a\u3001\u675c\u98de\u4ed6\u4eec\u90fd\u8d70\u5230\u5916\u9762\u7684\u8fc7\u9053\uff0c\u53ea\u7559\u803f\u5efa\u5fe0\u966a\u66f9\u5149\u660e\u59bb\u5b50\u3001\u59bb\u5f1f\u5728\u75c5\u5e8a\u91cc\uff0c\u6709\u6559\u80b2\u5c40\u957f\u803f\u5efa\u5fe0\u5728\uff0c\u5c31\u8db3\u4ee5\u9707\u4f4f\u66f9\u5149\u660e\u4e86\u3002</p><p>\u3000\u3000\u4e24\u540d\u7559\u4e0b\u6765\u7684\u4e3b\u6cbb\u533b\u751f\u5148\u8fdb\u53bb\u67e5\u770b\u66f9\u5149\u660e\u7684\u60c5\u51b5\uff0c\u8d70\u8fc7\u9053\uff0c\u624d\u5c0f\u58f0\u7684\u8bf4\u66f9\u5149\u660e\u7684\u5de6\u8138\u988a\u795e\u7ecf\u53d7\u635f\u4e25\u91cd\uff0c\u53ef\u80fd\u4f1a\u5bfc\u81f4\u9762\u762b\u3002\u5f20\u606a\u5fc3\u60f3\uff1a\u66f9\u5149\u660e\u5e73\u65f6\u5c31\u6ca1\u6709\u4ec0\u4e48\u8868\u60c5\uff0c\u9762\u762b\u5012\u4e0d\u4f1a\u6709\u592a\u5927\u7684\u5f71\u54cd\uff0c\u542c\u7740\u75c5\u623f\u91cc\u8d77\u4e86\u52a8\u9759\uff0c\u5927\u6982\u662f\u9192\u8fc7\u6765\u4e86\u3002\u5148\u662f\u803f\u5efa\u5fe0\u5728\u8ddf\u66f9\u5149\u660e\u8bf4\u4ed6\u9001\u5230\u533b\u9662\u540e\u7684\u6551\u6cbb\u60c5\u51b5\uff0c\u53c8\u6279\u8bc4\u4ed6\u6559\u80b2\u5b66\u751f\u7684\u65b9\u5f0f\u6709\u4e9b\u504f\u6fc0\uff0c\u5bfc\u81f4\u5e02\u91cc\u9886\u5bfc\u5bf9\u4e00\u4e2d\u7684\u6559\u80b2\u65b9\u5f0f\u5f88\u6709\u610f\u89c1\uff0c\u8bdd\u950b\u8f6c\u4e86\u8f6c\uff0c\u5c06\u6559\u80b2\u4e0d\u5f53\u7684\u4e3b\u8981\u8d23\u4efb\u63a8\u5230\u738b\u708e\u658c\u5934\u4e0a\uff0c\u4f46\u662f\u675c\u4e66\u8bb0\u5bf9\u4ed6\u4e25\u683c\u8981\u6c42\u675c\u98de\u7684\u6001\u5ea6\u5f88\u8ba4\u540c\uff0c\u4e5f\u5f88\u611f\u6fc0\uff0c\u7279\u610f\u9886\u7740\u675c\u98de\u4e00\u76f4\u5b88\u62a4\u5728\u75c5\u623f\u5916\uff0c\u8fde\u5f20\u79d8\u4e66\u957f\u4e5f\u60ca\u52a8\u4e86\uff0c\u8ddf\u5f20\u606a\u4e5f\u5728\u533b\u9662\u91cc\u2026\u2026\u5dee\u4e0d\u591a\u5c06\u6240\u6709\u7684\u610f\u601d\u90fd\u544a\u8bc9\u66f9\u5149\u660e\u4e4b\u540e\uff0c\u8fc7\u4e86\u4e00\u9635\u5b50\uff0c\u5f20\u606a\u4ed6\u4eec\u624d\u518d\u6b21\u8d70\u8fdb\u75c5\u623f\u3002</p><p>\u3000\u3000\u66f9\u5149\u660e\u773c\u775b\u7684\u76ee\u5149\u5f88\u590d\u6742\uff0c\u534a\u5f20\u8138\u7ed9\u7eb1\u5e03\u5305\u7740\uff0c\u5634\u5df4\u4e5f\u6ca1\u6709\u9732\u51fa\u6765\uff0c\u53ea\u770b\u89c1\u5634\u5507\u7684\u5730\u65b9\u8815\u52a8\u7740\uff0c\u53d1\u51fa\u5fae\u5f31\u7684\u58f0\u97f3\uff0c\u8c01\u4e5f\u542c\u4e0d\u6e05\u695a\u3002</p><p>\u3000\u3000\u542c\u4e0d\u6e05\u695a\u624d\u597d\uff0c\u8fd9\u6837\u624d\u53ef\u4ee5\u7ed9\u66f9\u5149\u660e\u8db3\u591f\u7684\u65f6\u95f4\u8003\u8651\u5f97\u5931\u3002\u66f9\u5149\u660e\u5927\u6982\u8fd9\u4e00\u8f88\u5b50\u8fd8\u6ca1\u6709\u7ed9\u8fd9\u4e48\u591a\u5b98\u5458\u56f4\u7740\uff0c\u590d\u6742\u7684\u773c\u795e\u66b4\u9732\u51fa\u4ed6\u7684\u5185\u5fc3\u6b63\u8fdb\u884c\u6fc0\u70c8\u7684\u6323\u624e\u7740\u3002\u7ed9\u5b66\u751f\u5f80\u6b7b\u91cc\u9971\u63cd\u4e86\u4e00\u987f\uff0c\u66f9\u5149\u660e\u5fc3\u91cc\u81ea\u7136\u6012\u4e0d\u53ef\u904f\uff0c\u4f46\u662f\u542c\u5230\u803f\u5efa\u5fe0\u660e\u786e\u8bf4\u5230\u738b\u708e\u658c\u4e0d\u9002\u5408\u7ee7\u7eed\u7559\u5728\u9886\u5bfc\u5c97\u4f4d\u4e0a\uff0c\u4ed6\u5185\u5fc3\u672a\u5c1d\u4e0d\u52a8\u6447\uff0c\u5351\u5fae\u6323\u624e\u8fd9\u4e48\u591a\u5e74\uff0c\u4e0d\u5c31\u662f\u4e3a\u4e86\u8fd9\u4e00\u6b65\u5417\uff1f</p><p>\u3000\u3000\u5f20\u606a\u5077\u773c\u770b\u7740\u8155\u4e0a\u7684\u624b\u8868\uff0c\u75c5\u623f\u91cc\u7684\u6c14\u6c1b\u9677\u5165\u6c89\u9ed8\u8db3\u8db3\u6709\u5341\u4e94\u5206\u949f\uff0c\u624d\u770b\u89c1\u66f9\u5149\u660e\u7ed9\u8499\u5728\u7eb1\u5e03\u4e0b\u7684\u5634\u518d\u6b21\u8815\u52a8\uff0c\u4ed6\u5a46\u5a18\u4fef\u8eab\u628a\u8033\u6735\u8d34\u8fc7\u53bb\uff0c\u590d\u8ff0\u4ed6\u542b\u7cca\u4e0d\u6e05\u7684\u8bdd\uff1a\u201c\u6211\u5bf9\u675c\u98de\u540c\u5b66\u7684\u6559\u80b2\u65b9\u5f0f\u6709\u6b20\u59a5\u5f53\uff0c\u8981\u5bf9\u675c\u98de\u540c\u5b66\u9053\u6b49\uff01\u201d</p><p>\u3000\u3000\u66f9\u5149\u660e\u8bf4\u8fd9\u53e5\u8bdd\u65f6\uff0c\u7eb1\u5e03\u4e0b\u7684\u5634\u5df4\u5728\u6296\u52a8\uff0c\u773c\u795e\u590d\u6742\uff0c\u672a\u5c1d\u6ca1\u6709\u54ac\u7259\u5207\u9f7f\u7684\u5473\u9053\uff0c\u4f46\u662f\u5df2\u7ecf\u4e0d\u91cd\u8981\u4e86\uff0c\u7b49\u5230\u73b0\u5728\uff0c\u4e0d\u5c31\u4e3a\u4e86\u8fd9\u53e5\u8bdd\u5417\uff1f</p><p>\u3000\u3000\u803f\u5efa\u5fe0\u5b89\u629a\u4ed6\u4e0d\u8981\u62c5\u5fc3\u533b\u836f\u8d39\u7684\u95ee\u9898\uff0c\u4e0d\u4ec5\u533b\u836f\u8d39\uff0c\u8fd8\u6709\u8425\u517b\u8d39\u90fd\u7531\u5e02\u6559\u80b2\u5c40\u5b89\u6392\uff0c\u8fd8\u8ba9\u4ed6\u4e0d\u8981\u62c5\u5fc3\u5de5\u4f5c\u4e0a\u7684\u4e8b\u60c5\uff0c\u5c31\u7b49\u4ed6\u75c5\u6108\u540e\u4f1a\u5b89\u6392\u5230\u66f4\u91cd\u8981\u7684\u5c97\u4f4d\uff0c\u73b0\u5728\u5c31\u662f\u4e13\u5fc3\u517b\u4f24\u3002</p></article>"}}}]);
