(window.webpackJsonp=window.webpackJsonp||[]).push([[1072],{1095:function(p){p.exports={chapterTitle:null,title:"\u7b2c874\u7ae0 \u8083\u67e5",content:"<article><h2>\u7b2c874\u7ae0 \u8083\u67e5</h2><p>\u3000\u3000\u4e94\u5929\u540e\uff0c7\u670821\u65e5\uff0c\u7ecf\u8fc7\u9a7b\u519b\u4e0e\u9632\u6c5b\u6c11\u4f17\u8270\u82e6\u5353\u7edd\u7684\u52aa\u529b\uff0c\u5bf9\u91d1\u5c71\u6e56\u5317\u5cb8\u8fd1\u4e24\u767e\u7c73\u5bbd\u7684\u6e83\u53e3\u5c01\u5835\u6210\u529f\uff0c\u6b64\u65f6\u519b\u533a\u8c03\u96c6\u4e00\u4e2a\u5e08\u7684\u90e8\u961f\u4e0e\u6570\u4e07\u6c11\u5de5\u9a7b\u5b88\u5728\u91d1\u5c71\u6e56\u3001\u996e\u9a6c\u6cb3\u5cb8\u5824\u4e0a\u6297\u6d2a\u62a2\u9669\u3001\u4fdd\u969c\u6e56\u5824\u3001\u6c5f\u5824\u5b89\u5168\u3002</p><p>\u3000\u3000\u8fd9\u6bb5\u65f6\u95f4\uff0c\u5f20\u606a\u8eab\u4f53\u4e25\u91cd\u900f\u652f\uff0c19\u65e5\u6709\u4e9b\u4f4e\u70e7\uff0c\u5403\u4e86\u4e9b\u836f\uff0c\u4e5f\u6ca1\u6709\u653e\u5728\u5fc3\u4e0a\uff0c\u6e83\u53e3\u5c01\u5835\u6210\u529f\u90a3\u5929\u53c8\u8dd1\u5230\u5824\u4e0a\u53bb\u770b\u60c5\u51b5\uff0c\u7fdf\u4e39\u9752\u4e0d\u5c0f\u5fc3\u7ed9\u4eba\u649e\u5230\u6ed1\u4e86\u4e00\u8de4\uff0c\u8dcc\u5230\u6e56\u91cc\u53bb\u4e86\uff0c\u5f20\u606a\u4e0e\u5085\u4fca\u62a2\u7740\u8df3\u4e0b\u53bb\u5c06\u7fdf\u4e39\u9752\u6551\u4e0a\u6765\uff0c\u7fdf\u4e39\u9752\u5012\u662f\u6ca1\u4e8b\uff0c\u5f20\u606a\u5f53\u65f6\u672a\u5c06\u6e7f\u8863\u7acb\u5373\u6362\u6389\uff0c\u591c\u91cc\u5c31\u53d1\u8d77\u9ad8\u70e7\u6765\uff0c\u4e00\u5ea6\u9677\u5165\u660f\u8ff7\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u5085\u4fca\u3001\u7fdf\u4e39\u9752\u90fd\u4e0d\u6562\u518d\u542c\u5f20\u606a\u7684\u8bdd\u4efb\u4ed6\u7559\u5728\u91d1\u5c71\uff0c\u5c06\u4e8b\u60c5\u544a\u8bc9\u4e00\u76f4\u7559\u5728\u65b0\u829c\u7684\u6881\u683c\u73cd\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u5728\u65b0\u829c\u6025\u5f97\u76f4\u8df3\u811a\uff0c\u4ece\u5efa\u90ba\u5230\u91d1\u5c71\u7684\u516c\u8def\u3001\u94c1\u8def\u6089\u6570\u906d\u6d2a\u6c34\u6df9\u6ca1\uff0c\u53c8\u4e0d\u80fd\u5728\u6b64\u7d27\u8feb\u65f6\u523b\u4e3a\u81ea\u5df1\u8d76\u53bb\u91d1\u5c71\u8c03\u7528\u519b\u7528\u76f4\u5347\u98de\u673a\uff0c\u518d\u8bf4\u4e08\u592b\u5f20\u77e5\u884c\u5728\u65b0\u829c\u6297\u6d2a\u62a2\u9669\u4e5f\u4e0d\u8981\u547d\uff0c\u4ece\u4e00\u4e2a\u591a\u6708\u524d\u4e0a\u4e86\u6c5f\u5824\u5c31\u56de\u8fc7\u4e24\u6b21\u5bb6\uff0c\u6881\u683c\u73cd\u8fd8\u8981\u53bb\u6c5f\u5824\u6216\u8005\u5e02\u653f\u5e9c\u6216\u8005\u5e02\u9632\u6c5b\u6307\u6325\u90e8\u624d\u80fd\u770b\u5230\u4ed6\u4eba\uff0c\u5979\u8fd8\u8981\u7559\u5728\u65b0\u829c\u76ef\u7740\u4e08\u592b\u4e0d\u51fa\u4ec0\u4e48\u95ee\u9898\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u8ba9\u5085\u4fca\u4e0e\u7fdf\u4e39\u9752\u5c06\u5f20\u606a\u8fde\u591c\u9001\u56de\u6d77\u5dde\u53bb\uff0c\u4e0d\u8981\u7559\u5728\u91d1\u5c71\uff1b\u91d1\u5c71\u5f80\u65b0\u4ead\u518d\u5f80\u6d77\u5dde\u7684\u516c\u8def\u4e0e\u8f6e\u6e21\u662f\u7545\u901a\u7684\u3002</p><p>\u3000\u30007\u670821\u65e5\u518d\u6b21\u62b5\u8fbe\u91d1\u5c71\u89c6\u5bdf\u6297\u6d2a\u62a2\u9669\u5de5\u4f5c\u7684\u66fe\u5982\u5723\u526f\u603b\u7406\u5f97\u77e5\u5f20\u606a\u7684\u75c5\u60c5\u4e4b\u540e\uff0c\u4e0e\u6c5f\u5357\u7701\u91d1\u5c71\u5e02\u7684\u5b98\u5458\u8d76\u5230\u533b\u9662\u770b\u671b\u4e86\u610f\u8bc6\u6a21\u7cca\u3001\u9677\u5165\u534a\u660f\u8ff7\u72b6\u6001\u7684\u5f20\u606a\uff0c\u5f85\u533b\u9662\u7ed9\u5f20\u606a\u8fdb\u884c\u7d27\u6025\u6cbb\u7597\u4e4b\u540e\uff0c\u5c31\u6307\u6d3e\u4ed6\u7684\u4e13\u673a\u5c06\u5f20\u606a\u4e00\u884c\u4eba\u8fde\u591c\u9001\u5f80\u6d77\u5dde\uff0c\u5f20\u606a\u5012\u662f\u9519\u8fc7\u4e0e\u66fe\u5982\u5723\u4f1a\u9762\u7684\u826f\u673a\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5230\u6d77\u5dde\u6b21\u65e5\u6e05\u6668\u624d\u9000\u70e7\uff0c\u5728\u6d77\u5dde\u5e02\u7b2c\u4e00\u4eba\u6c11\u533b\u9662\u4f4f\u9662\u89c2\u5bdf\u4e86\u4e24\u5929\uff0c\u624d\u51fa\u9662\u4f4f\u56de\u5bb6\u91cc\uff0c\u53ea\u4e0d\u8fc7\u5927\u5bb6\u90fd\u62ff\u6881\u683c\u73cd\u7684\u8bdd\u963b\u6321\u4ed6\u79bb\u5f00\u6d77\u5dde\uff0c\u5f20\u606a\u53ea\u5f97\u7559\u5728\u6d77\u5dde\u4f11\u517b\u3002</p><p>\u3000\u3000\u6881\u683c\u73cd\u7ec8\u7a76\u653e\u5fc3\u4e0d\u4e0b\uff0c\u5f53\u4e2d\u56de\u4e86\u4e00\u8d9f\u6d77\u5dde\uff0c\u770b\u7740\u5f20\u606a\u8138\u988a\u90fd\u7626\u9677\u4e0b\u6765\uff0c\u5634\u91cc\u62b1\u6028\u7740\uff1a\u201c\u54ea\u91cc\u9700\u8981\u4f60\u4eb2\u81ea\u4e0a\u53bb\u51b2\u950b\u9677\u9635\uff1f\u4f60\u8dd1\u8fc7\u53bb\u6307\u624b\u753b\u811a\u53ea\u662f\u7ed9\u4eba\u5bb6\u6dfb\u4e71\uff0c\u4f60\u8bf4\u8c01\u9700\u8981\u4f60\u7ad9\u51fa\u6765\u6307\u624b\u753b\u811a\uff1f\u9526\u6e56\u94b1\u4e5f\u6350\u4e86\uff0c\u7269\u4e5f\u6350\uff0c\u4e5f\u5728\u9999\u6e2f\u7ec4\u7ec7\u534e\u4eba\u534e\u4fa8\u52df\u6350\u8d48\u707e\uff0c\u4f60\u8fd8\u60f3\u600e\u4e48\u6837\uff1f\u4e0d\u8981\u5b66\u4f60\u7238\u8111\u5b50\u53ea\u6709\u4e00\u6839\u7b4b\uff01\u201d\u6068\u6068\u7684\u8dfa\u7740\u811a\uff0c\u201c\u771f\u662f\u9020\u4e86\u5b7d\uff0c\u6211\u591c\u91cc\u8fd8\u56de\u65b0\u829c\u53bb\uff0c\u4f60\u8bf4\u4f60\u4eec\u7237\u4fe9\u90fd\u80fd\u4e56\u4e56\u7684\u542c\u8bdd\uff0c\u6211\u9700\u8981\u5403\u8fd9\u4e2a\u82e6\u5934\uff1f\u536b\u5170\u7684\u9ad8\u8003\u6210\u7ee9\u4e5f\u51fa\u6765\u4e86\uff0c\u5c31\u7b49\u901a\u77e5\u4e66\uff0c\u5728\u65b0\u829c\u662f\u7b49\uff0c\u5728\u6d77\u5dde\u4e5f\u662f\u7b49\uff0c\u6211\u5c31\u8ba9\u5979\u7559\u5728\u6d77\u5dde\u770b\u7740\u4f60\u3001\u7167\u987e\u4f60\u2026\u2026\u201d</p><p>\u3000\u3000\u73b0\u5728\u5510\u5a67\u6574\u5929\u966a\u7740\u4ed6\uff0c\u7fdf\u4e39\u9752\u4e5f\u4e0d\u79bb\u8eab\uff0c\u665a\u6674\u4e5f\u56de\u6d77\u5dde\u4e86\uff0c\u52a0\u4e0a\u9648\u5983\u84c9\u7ecf\u5e38\u7ed9\u5510\u5a67\u7ea6\u6765\uff0c\u73b0\u5728\u53c8\u6dfb\u4e86\u536b\u5170\uff0c\u5982\u82b1\u4f3c\u7389\u7684\u4e00\u5806\u7389\u4eba\u513f\u5728\u8eab\u8fb9\u98d8\u6765\u8361\u53bb\uff0c\u4ed6\u4e00\u4e2a\u90fd\u6cbe\u4e0d\u7740\uff1b\u8fd9\u8fd8\u4e0d\u7b97\uff0c\u52a0\u4e0a\u60dc\u5bb9\u3001\u60dc\u7fbd\u3001\u82b7\u5f64\u4e09\u4e2a\u4e2b\u5934\u6574\u5929\u5728\u773c\u524d\u4e0a\u7a9c\u4e0b\u8df3\uff0c\u5f20\u606a\u60f3\u5230\u8fd9\u91cc\uff0c\u53ea\u80fd\u54a7\u5634\u82e6\u7b11\uff1a\u201c\u90fd\u662f\u8c01\u7167\u987e\u8c01\u554a\uff1f\u8fd9\u4e24\u5929\u90fd\u662f\u6211\u53bb\u83dc\u5e02\u573a\u4e70\u83dc\u4e70\u56de\u5bb6\u505a\u8fd9\u4e9b\u7c73\u866b\u5403\u2026\u2026\u5f97\uff0c\u591a\u536b\u5170\u59d0\u4e00\u4e2a\u4e5f\u4e0d\u591a\u3002\u201d</p><p>\u3000\u3000\u201c\u4f60\u751f\u75c5\u4e3b\u8981\u662f\u4f60\u7528\u8111\u8fc7\u5ea6\u3001\u4f53\u529b\u901a\u900f\u8fc7\u591a\uff0c\u9002\u5ea6\u7684\u52b3\u9038\u7ed3\u5408\u3001\u8fd0\u52a8\uff0c\u6709\u52a9\u4f60\u8eab\u4f53\u65e9\u65e5\u5eb7\u590d\u2026\u2026\u201d\u5510\u5a67\u5fae\u6485\u7740\u5634\uff0c\u8146\u7740\u8138\u8bf4\uff0c\u201c\u8ba9\u4f60\u505a\u4e9b\u4e8b\uff0c\u662f\u7701\u5f97\u4f60\u6574\u5929\u8111\u5b50\u7a7a\u4e0d\u4e0b\u6765\u60f3\u6765\u60f3\u53bb\u7684\u3002\u201d</p><p>\u3000\u3000\u201c\u5c31\u77e5\u9053\u4f60\u662f\u4f53\u8d34\u6211\u3001\u5173\u5fc3\u6211\u7684\u8eab\u4f53\uff0c\u5176\u5b9e\u6211\u7684\u8eab\u4f53\u5df2\u7ecf\u6062\u590d\u50cf\u5934\u725b\u4e86\u2026\u2026\u201d\u5f20\u606a\u4f38\u624b\u53bb\u634f\u5510\u5a67\u7684\u8138\u86cb\uff0c\u9971\u6696\u601d\u6deb\u6b32\uff0c\u4f46\u662f\u8fd9\u59ae\u5b50\u501f\u53e3\u4ed6\u8eab\u4f53\u6ca1\u6709\u5eb7\u590d\uff0c\u4e0d\u8ba9\u4ed6\u6cbe\u5979\u7684\u8eab\u5b50\uff0c\u6ca1\u6709\u6bd4\u8fd9\u66f4\u90c1\u95f7\u7684\u4e8b\u60c5\u4e86\u3002</p><p>\u3000\u3000\u5510\u5a67\u77e5\u9053\u5f20\u606a\u662f\u8bf4\u4ec0\u4e48\uff0c\u53c8\u4e0d\u4fbf\u5728\u6881\u683c\u73cd\u9762\u524d\u9732\u51fa\u9a6c\u811a\uff0c\u4fcf\u8138\u7eef\u7ea2\uff0c\u6446\u7740\u8138\u4e0d\u8ba9\u5f20\u606a\u63c9\u634f\u81ea\u5df1\u7684\u8138\u86cb\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u8d70\u8fdb\u6765\uff0c\u5f20\u606a\u770b\u5230\u5979\u773c\u89d2\u6709\u4e9b\u5fae\u7ea2\uff0c\u95ee\u9053\uff1a\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\uff0c\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\uff0c\u201c\u91d1\u5c71\u5e02\u91cc\u5bf9\u91d1\u5c71\u6e56\u6e83\u5824\u8fdb\u884c\u4e86\u521d\u6b65\u7684\u8c03\u67e5\uff0c\u6211\u521a\u521a\u63a5\u5230\u7535\u8bdd\uff0c\u542c\u8bf4\u4e86\u4e00\u4e9b\u4e8b\u60c5\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u5927\u5bb6\u90fd\u8f6c\u8fc7\u8138\u770b\u7740\u7fdf\u4e39\u9752\u3002</p><p>\u3000\u3000\u201c\u6e83\u5824\u4e4b\u524d\u5c31\u6709\u4eba\u53d1\u73b0\u4e86\u9669\u60c5\uff0c\u5f53\u65f6\u5b88\u5824\u519b\u6c11\u7684\u6ce8\u610f\u529b\u653e\u5728\u5e72\u5824\u4e0a\uff0c\u4e0b\u5824\u8f6e\u4f11\u7684\u6c11\u5de5\u53d1\u73b0\u5824\u575d\u80cc\u9762\u6e17\u6c34\u5c31\u901a\u77e5\u4e86\u5824\u6bb5\u7684\u62a2\u9669\u90e8\u961f\uff0c\u4f46\u5728\u62a2\u9669\u90e8\u961f\u8d76\u5230\u4e4b\u65f6\uff0c\u5e72\u5824\u5728\u77ac\u65f6\u5c31\u53d1\u751f\u5927\u574d\u584c\uff0c\u5f53\u65f6\u5c31\u6709\u516d\u540d\u6218\u58eb\u624b\u633d\u624b\u5e76\u80a9\u8df3\u5165\u51b3\u53e3\u4e4b\u4e2d\uff0c\u60f3\u4ee5\u8840\u8089\u4e4b\u8eaf\u6321\u4f4f\u6e83\u5824\u7684\u6d2a\u6c34\u4e3a\u62a2\u9669\u4e89\u53d6\u4e00\u7ebf\u673a\u4f1a\u3002\u8fd9\u516d\u540d\u6218\u58eb\u7684\u9057\u4f53\u4eca\u5929\u4e0a\u5348\u521a\u521a\u627e\u5230\uff0c\u6c81\u56ed\u5317\u9762\u7684\u6d2a\u6c34\u9000\u53bb\u4e4b\u540e\uff0c\u5728\u6ccc\u56ed\u5317\u9762\u7684\u4e00\u5904\u4f4e\u6d3c\u6c34\u6f6d\u91cc\u53d1\u73b0\u7684\uff0c\u516d\u540d\u6218\u58eb\u7684\u624b\u81c2\u8fd8\u7d27\u7d27\u633d\u6263\u5728\u4e00\u8d77\uff0c\u627e\u5230\u9057\u4f53\u7684\u4eba\u60f3\u7528\u529b\u5206\u5f00\u4ed6\u4eec\u4e00\u4e00\u5b89\u7f6e\uff0c\u5374\u662f\u600e\u4e48\u63b0\u90fd\u63b0\u4e0d\u5f00\u4ed6\u4eec\u5230\u6b7b\u90fd\u7d27\u6263\u5728\u4e00\u8d77\u7684\u624b\u81c2\u2026\u2026\u201d\u8bf4\u7740\u8bdd\uff0c\u7fdf\u4e39\u9752\u7684\u58f0\u97f3\u5c31\u54fd\u54bd\u8d77\u6765\uff0c\u623f\u95f4\u91cc\u7684\u5973\u5b69\u5b50\u773c\u5708\u90fd\u89c1\u7ea2\u4e86\uff0c\u5f20\u606a\u62bf\u7740\u5634\u4e0d\u542d\u58f0\uff0c\u6881\u683c\u73cd\u8f7b\u8f7b\u4e00\u53f9\uff0c\u5fc3\u80a0\u5230\u5e95\u662f\u8f6f\uff0c\u4e0d\u518d\u62b1\u6028\u5f20\u606a\u4e8b\u4e8b\u51b2\u5728\u524d\u9762\uff0c\u770b\u4e86\u770b\u624b\u8868\uff0c\u8bf4\u9053\uff0c\u201c\u6211\u8fd9\u5c31\u56de\u65b0\u829c\u53bb\uff0c\u4f60\u7238\u90a3\u6837\u7684\u4eba\uff0c\u6211\u600e\u4e48\u4e5f\u653e\u5fc3\u4e0d\u4e0b\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u4e0b\u697c\u9001\u5979\u5988\u4e0a\u4e86\u8f66\uff0c\u56de\u5230\u5ba2\u5385\uff0c\u518d\u5c06\u7fdf\u4e39\u9752\u53eb\u6765\u8be2\u95ee\u91d1\u5c71\u7684\u60c5\u51b5\u3002</p><p>\u3000\u3000\u201c\u9664\u4e86\u90a3\u516d\u540d\u90e8\u961f\u6218\u58eb\u4e4b\u5916\uff0c\u5f53\u65f6\u5e72\u5824\u4e0a\u8fd8\u6709\u4e8c\u5341\u591a\u4eba\u5b88\u5824\u7684\u519b\u6c11\u7ed9\u6d2a\u6c34\u5377\u5165\u6e83\u53e3\u4e4b\u4e2d\u6765\u4e0d\u53ca\u9003\u751f\uff0c\u91d1\u5c71\u65b0\u57ce\u53ca\u5317\u9762\u4e24\u4e2a\u53d7\u707e\u6700\u4e25\u91cd\u7684\u4e61\u9547\u786e\u8ba4\u4e00\u5171\u6709\u56db\u767e\u4f59\u4eba\u4e27\u751f\u6d2a\u6c34\u4e4b\u4e2d\uff0c\u53e6\u6709\u4e03\u767e\u4f59\u4eba\u5931\u8e2a\uff0c\u751f\u8fd8\u673a\u4f1a\u6e3a\u832b\u3002\u91d1\u5c71\u65b0\u57ce\u6b63\u5f53\u6e83\u5824\u6d2a\u6c34\uff0c\u6089\u6570\u88ab\u6df9\uff0c\u65e0\u6570\u65b0\u5efa\u4e0e\u5728\u5efa\u4ee5\u53ca\u672a\u5b8c\u6210\u62c6\u8fc1\u7684\u5efa\u7b51\u88ab\u6d2a\u6c34\u51b2\u6bc1\uff0c\u4ece\u91d1\u5c71\u65b0\u57ce\u5f80\u5317\uff0c\u4e00\u76f4\u5230\u5317\u9762\u7684\u8fde\u5c71\u53bf\u5883\u5185\uff0c\u90fd\u88ab\u6d2a\u6c34\u6df9\u53ca\uff0c\u5efa\u90ba\u4e0e\u91d1\u5c71\u7684\u516c\u8def\u3001\u94c1\u8def\u591a\u5904\u88ab\u6df9\u6ca1\uff0c\u4ea4\u901a\u4e2d\u65ad\uff0c\u6551\u63f4\u7269\u8d44\u90fd\u7528\u6c7d\u8f6e\u6cbf\u996e\u9a6c\u6cb3\u6eaf\u6c34\u800c\u4e0a\u8fd0\u5230\u91d1\u5c71\u2014\u2014\u91d1\u5c71\u6e56\u6e83\u5824\u9020\u6210\u7684\u76f4\u63a5\u635f\u5931\u53ef\u80fd\u5c31\u63a5\u8fd1\u767e\u4ebf\uff0c\u4ee4\u91d1\u5c71\u7ecf\u6d4e\u5143\u6c14\u5927\u4f24\u2026\u2026\u201d</p><p>\u3000\u3000\u91d1\u5c71\u7684\u7ecf\u6d4e\u635f\u5931\uff0c\u5f20\u606a\u80fd\u4f30\u8ba1\u4e00\u4e8c\uff0c\u6700\u4e3a\u5173\u952e\u7684\u8fd8\u662f\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u542f\u52a8\u4e24\u5e74\u7684\u65b0\u829c\u5efa\u8bbe\u4e0e\u4fe1\u606f\u4ea7\u4e1a\u53d1\u5c55\u8ba1\u5212\u90fd\u8981\u88ab\u8feb\u4e2d\u65ad\uff0c\u4f55\u65f6\u518d\u542f\u52a8\uff0c\u90fd\u8fd8\u662f\u672a\u77e5\u6570\u3002\u4ed6\u7a0d\u4f5c\u6c89\u541f\uff0c\u793a\u610f\u7fdf\u4e39\u7ee7\u7eed\u8bf4\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u9020\u6210\u5982\u6b64\u60e8\u91cd\u7684\u4eba\u5458\u4f24\u4ea1\u4e0e\u635f\u5931\uff0c\u7701\u5e02\u518d\u4e5f\u65e0\u6cd5\u548c\u7a00\u6ce5\u63a8\u8bff\u8d23\u4efb\uff0c\u5bfc\u81f4\u6e83\u5824\u76f4\u63a5\u7684\u539f\u56e0\u4e5f\u521d\u6b65\u67e5\u660e\uff0c\u5e02\u6c34\u5229\u5c40\u4e0b\u5c5e\u4e00\u5bb6\u516c\u53f8\u66fe\u60f3\u5728\u6e83\u5824\u5904\u5c31\u7d27\u8d34\u7740\u5824\u575d\u5efa\u4e00\u5ea7\u52a0\u6cb9\u7ad9\uff0c\u5728\u575d\u57fa\u6df1\u6316\u6570\u7c73\u6df1\u3001\u6570\u767e\u5e73\u65b9\u7c73\u7684\u5730\u57fa\u5751\uff0c\u5730\u57fa\u5751\u6df1\u5d4c\u5165\u575d\u57fa\u6df1\u5165\u3002\u4eca\u5e74\u9632\u6c5b\u5de5\u4f5c\u5f00\u59cb\u540e\uff0c\u52a0\u6cb9\u7ad9\u9879\u76ee\u7ed9\u6574\u6539\u6389\u4e86\uff0c\u4e5f\u8d23\u4ee4\u8be5\u516c\u53f8\u56de\u586b\u5730\u57fa\u5751\u3001\u6062\u590d\u575d\u57fa\u539f\u8c8c\uff0c\u53ea\u662f\u8fd9\u5bb6\u516c\u53f8\u53d6\u4e86\u677e\u571f\u56de\u586b\u6577\u884d\u4e86\u4e8b\uff0c\u5374\u6ca1\u6709\u60f3\u5230\u5c31\u662f\u8fd9\u4e00\u5904\u758f\u5ffd\u57cb\u4e0b\u7978\u6839\uff0c\u201d\u7fdf\u4e39\u9752\u8bf4\u9053\uff0c\u201c\u91d1\u5c71\u6e56\u65b0\u6e56\u5824\u4e5d\u4e94\u5e74\u4fee\u7b51\u65f6\uff0c\u603b\u627f\u63fd\u5546\u5c31\u662f\u6c34\u5229\u5c40\u4e0b\u5c5e\u7684\u516c\u53f8\uff0c\u4f46\u662f\u5206\u5305\u65bd\u5de5\u65b9\u7adf\u6709\u516d\u5341\u4e8c\u5bb6\u4e4b\u591a\uff0c\u7ba1\u7406\u5f02\u5e38\u7684\u6742\u4e71\uff0c\u4ee4\u4eba\u75db\u5fc3\u75be\u9996\u7684\u662f\uff0c\u8fd9\u4e9b\u65bd\u5de5\u65b9\u7ea6\u6709\u516d\u4e03\u6210\u90fd\u6ca1\u6709\u65bd\u5de5\u8d44\u8d28\u2026\u2026\u76ee\u524d\u6240\u6709\u53ef\u80fd\u7275\u6d89\u7684\u5b98\u5458\uff0c\u90fd\u76f4\u63a5\u62d8\u62bc\u8d77\u6765\u3002\u201d</p><p>\u3000\u3000\u5f20\u606a\u5934\u6795\u7740\u6c99\u53d1\u9760\u80cc\uff0c\u4e0d\u60f3\u542d\u58f0\uff0c\u65e0\u8bba\u662f\u516d\u540d\u82f1\u52c7\u727a\u7272\u7684\u6218\u58eb\u4e5f\u597d\uff0c\u65e0\u8bba\u662f\u81ea\u5df1\u6316\u81ea\u5df1\u5899\u811a\u57cb\u4e0b\u7978\u6839\u7684\u91d1\u5c71\u5e02\u6c34\u5229\u5c40\u4e5f\u597d\uff0c\u90fd\u4e0d\u4f1a\u51fa\u73b0\u5728\u5a92\u4f53\u4e0a\u3002\u5728\u6c5b\u671f\u524d\uff0c\u5404\u4e3b\u6d41\u5a92\u4f53\u5c31\u63a5\u5230\u901a\u77e5\uff0c\u62a5\u9053\u6297\u6d2a\u62a2\u9669\u7684\u65b0\u95fb\uff0c\u8981\u79ef\u6781\u62a5\u9053\u6b63\u9762\u7684\u6d88\u606f\uff0c\u5728\u5824\u575d\u4e0a\u548c\u7740\u6ce5\u6d46\u800c\u7761\u7684\u6218\u58eb\u4f1a\u88ab\u6444\u5f71\u673a\u7559\u4e0b\u5f71\u50cf\u5728\u7535\u89c6\u91cc\u64ad\u653e\uff0c\u8fd9\u4e9b\u58ee\u70c8\u727a\u7272\u7684\u6218\u58eb\u53cd\u800c\u4e3a\u4e86\u63a9\u9970\u6e83\u5824\u7684\u4e25\u91cd\u6027\u4e0e\u6b8b\u9177\u6027\u800c\u65e0\u6cd5\u6210\u4e3a\u5ba3\u4f20\u7684\u5178\u578b\u3002</p><p>\u3000\u3000\u665a\u4e0a\u5403\u996d\u65f6\uff0c\u5510\u5b66\u8c26\u4e0e\u987e\u5efa\u840d\u4e5f\u8d76\u8fc7\u6765\uff0c\u6bd4\u8d77\u91d1\u5c71\u53d7\u707e\u7684\u635f\u5931\uff0c\u5510\u5b66\u8c26\u66f4\u5173\u5fc3\u6c5f\u5357\u7701\u91d1\u5c71\u5e02\u653f\u5c40\u7531\u4e8e\u53d7\u707e\u800c\u53ef\u80fd\u5bfc\u81f4\u7684\u52a8\u8361\u3002</p><p>\u3000\u3000\u201c\u5468\u747e\u745c\u62c5\u4efb\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u4e4b\u540e\uff0c\u51b3\u5b9a\u5c06\u65b0\u6e56\u5824\u5317\u9762\u7684\u65e7\u5824\u62c6\u9664\u7ed9\u65b0\u57ce\u533a\u53d1\u5c55\u4fe1\u606f\u6280\u672f\u4ea7\u4e1a\u632a\u51fa\u7a7a\u95f4\uff0c\u6b63\u56e0\u4e3a\u65e7\u5824\u62c6\u8fc1\uff0c\u4f7f\u5f97\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u4e00\u76f4\u5230\u4e5d\u6c5f\u6cb3\u4e4b\u95f4\u6ca1\u6709\u4e00\u5904\u53ef\u4ee5\u7a0d\u963b\u6321\u6d2a\u6c34\u7684\u6709\u5229\u5730\u5f62\uff0c\u518d\u8bf4\u6297\u6d2a\u62a2\u9669\u4e2d\uff0c\u5468\u747e\u745c\u7684\u8868\u73b0\u4e5f\u4ee4\u6c5f\u5357\u7701\u59d4\u7701\u653f\u5e9c\u5931\u671b\uff0c\u4e00\u76f4\u7559\u5728\u540e\u65b9\u7ec4\u7ec7\u6297\u6d2a\u62a2\u9669\u6551\u707e\u7269\u8d44\u7684\u534f\u8c03\u4e8b\u52a1\uff0c\u201d\u5f20\u606a\u4ecb\u7ecd\u4ed6\u89c2\u5bdf\u5230\u7684\u60c5\u51b5\uff0c\u8bf4\u9053\uff0c\u201c\u5468\u747e\u745c\u867d\u7136\u8fd8\u7ee7\u7eed\u7559\u5728\u5e02\u59d4\u4e66\u8bb0\u7684\u4f4d\u5b50\u4e0a\uff0c\u4f46\u8fd9\u4e5f\u53ea\u662f\u66f4\u591a\u7684\u8003\u8651\u5230\u5728\u707e\u60c5\u5f02\u5e38\u4e25\u91cd\u3001\u6b8b\u9177\u65f6\u6709\u5fc5\u8981\u4fdd\u6301\u6c11\u5fc3\u7a33\u5b9a\uff0c\u5f53\u524d\u91d1\u5c71\u5e02\u7684\u9632\u6c5b\u5de5\u4f5c\u4e3b\u8981\u8fd8\u662f\u7531\u5e02\u957f\u8d75\u6709\u4f26\u8d1f\u8d23\uff0c\u526f\u603b\u7406\u66fe\u5982\u5723\u4e24\u6b21\u6765\u91d1\u5c71\u3001\u56fd\u9632\u90e8\u957f\u3001\u519b\u59d4\u526f\u4e3b\u5e2d\u6881\u4f1f\u70c8\u4e00\u6b21\u6765\u91d1\u5c71\u89c6\u5bdf\u4e5f\u90fd\u662f\u7531\u8d75\u6709\u4f26\u5411\u4ed6\u4eec\u6c47\u62a5\u707e\u60c5\u2026\u2026\u6e83\u5824\u539f\u56e0\u8c03\u67e5\u5c0f\u7ec4\u4e5f\u662f\u7531\u8d75\u6709\u8bba\u9886\u5bfc\u5f7b\u67e5\u95ee\u9898\u7684\u6839\u6e90\u2026\u2026\u6c5f\u5357\u7701\u59d4\u53ec\u5f00\u7d27\u6025\u4f1a\u8bae\uff0c\u63d0\u62d4\u8d75\u6709\u4f26\u4e3a\u7701\u59d4\u5e38\u59d4\u6210\u5458\u3002\u201d</p><p>\u3000\u3000\u8d75\u6709\u4f26\u53d6\u66ff\u5468\u747e\u745c\u5df2\u7ecf\u662f\u94c1\u677f\u9489\u9489\u7684\u4e8b\u60c5\u4e86\u3002\u81f3\u4e8e\u5c1a\u5b66\u4e49\u7684\u53bb\u5411\uff0c\u8fd9\u662f\u7531\u4e2d\u592e\u51b3\u5b9a\u7684\uff0c\u5f20\u606a\u89c9\u5f97\u5c1a\u5b66\u4e49\u4e5f\u53ef\u80fd\u4f1a\u5f88\u5feb\u9000\u4e0b\uff0c\u4f46\u6881\u4f1f\u6cd5\u7684\u8d44\u683c\u5c1a\u6d45\uff0c\u53ea\u6015\u65e0\u6cd5\u76f4\u63a5\u4e0a\u4f4d\uff0c\u6709\u53ef\u80fd\u4ece\u5916\u7701\u6216\u4e2d\u592e\u76f4\u63a5\u59d4\u6258\u4e00\u540d\u7701\u59d4\u4e66\u8bb0\u8fc7\u6765\u3002</p><p>\u3000\u3000\u4e34\u5230\u665a\u996d\u7ec8\u4e86\uff0c\u5510\u5b66\u8c26\u624d\u8bf4\u9053\uff1a\u201c\u6211\u521a\u63a5\u5230\u901a\u77e5\uff0c\u7b49\u6c5b\u671f\u8fc7\u4e86\u4e4b\u540e\uff0c\u6211\u5c31\u5148\u53bb\u4e2d\u592e\u515a\u6821\u5b66\u4e60\u4e09\u4e2a\u6708\u2026\u2026\u201d</p></article>"}}}]);
