(window.webpackJsonp=window.webpackJsonp||[]).push([[1077],{1100:function(p){p.exports={chapterTitle:null,title:"\u7b2c878\u7ae0 \u4e00\u4e2a\u4ebf",content:"<article><h2>\u7b2c878\u7ae0 \u4e00\u4e2a\u4ebf</h2><p>\u3000\u3000\u201c\u8001\u5211\u5382\u957f\u89c9\u5f97\u6211\u4eec\u4f1a\u662f\u6ca1\u6709\u8bda\u4fe1\u3001\u53ea\u77e5\u9053\u538b\u69a8\u5de5\u4eba\u3001\u6ca1\u6709\u4e1d\u6beb\u8d23\u4efb\u611f\u7684\u4f01\u4e1a\u5417\uff1f\u201d\u5f20\u606a\u5e73\u9759\u7684\u770b\u7740\u5211\u7231\u56fd\uff0c\u8f7b\u58f0\u7684\u95ee\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5211\u7231\u56fd\u5b8c\u5168\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u8fd9\u4e48\u4e2a\u9752\u5e74\u4f1a\u4ee3\u8868\u65b0\u5149\u7eb8\u4e1a\u8bf4\u8bdd\uff0c\u4f3c\u4e4e\u8eab\u4efd\u8fd8\u5728\u65b0\u5149\u7eb8\u4e1a\u8c08\u5224\u7ec4\u5468\u6e38\u4e4b\u4e0a\uff0c\u4ed6\u6123\u4e86\u534a\u664c\u90fd\u6ca1\u6709\u56de\u8fc7\u795e\u6765\uff0c\u90fd\u4e0d\u6653\u5f97\u8981\u600e\u4e48\u56de\u7b54\uff0c\u4e0b\u610f\u8bc6\u7684\u95ee\u4e86\u4e00\u53e5\uff1a\u201c\u5f20\u5955\u4e5f\u662f\u65b0\u5149\u7eb8\u4e1a\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u7b97\u662f\u5427\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u201c\u6c81\u56ed\u6709\u6211\u5c0f\u53d4\u7684\u6295\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u5211\u7231\u56fd\u8bb7\u8bb7\u7684\uff0c\u4e0d\u77e5\u9053\u63a5\u4e0b\u6765\u8fd8\u8981\u8bf4\u4ec0\u4e48\u597d\uff0c\u5728\u5f20\u606a\u9762\u524d\u5374\u4e0d\u7531\u7684\u5c0f\u5fc3\u8d77\u6765\uff0c\u4e5f\u4e0d\u4fbf\u7ec6\u95ee\u3002</p><p>\u3000\u3000\u6c81\u56ed\u79bb\u6e83\u5824\u5904\u8f83\u8fdc\uff0c\u6d2a\u6c34\u8fc7\u540e\u57fa\u672c\u4e0a\u6ca1\u6709\u4ec0\u4e48\u635f\u5931\uff0c\u4f46\u662f\u6c81\u56ed\u5e76\u6ca1\u6709\u6025\u7740\u6574\u7406\u6536\u62fe\u91cd\u65b0\u8425\u4e1a\uff0c\u800c\u662f\u7ee7\u7eed\u4f5c\u4e3a\u4e00\u4e2a\u4e34\u65f6\u5b89\u7f6e\u70b9\u5728\u4f7f\u7528\uff0c\u5f20\u5955\u4e0e\u6c81\u56ed\u6240\u6709\u7684\u5458\u5de5\u90fd\u8fd8\u5168\u8eab\u5fc3\u7684\u6295\u5165\u5230\u6551\u707e\u4e4b\u4e2d\u3002\u91d1\u5c71\u6e56\u4e1c\u897f\u4e24\u5cb8\u6e83\u5824\u540e\uff0c\u7eb8\u5382\u68da\u6237\u533a\u7684\u7b80\u964b\u5efa\u7b51\u7ed9\u6d2a\u6c34\u51b2\u584c\u4e86\u5927\u534a\uff0c\u8fd8\u662f\u5168\u4f9d\u8d56\u8fd9\u8fb9\u4e8b\u524d\u7ec4\u7ec7\u4e0a\u767e\u8f86\u5361\u8f66\u8fdb\u884c\u758f\u6563\uff0c\u624d\u6ca1\u6709\u9020\u6210\u4eba\u5458\u4f24\u4ea1\uff0c\u8d22\u4ea7\u635f\u5931\u4e5f\u51cf\u5c11\u4e86\u8bb8\u591a\u3002\u707e\u60c5\u6700\u4e25\u5cfb\u65f6\uff0c\u5f20\u606a\u3001\u8bb8\u9e3f\u4f2f\u3001\u8c2d\u5929\u7b49\u4eba\u90fd\u4eb2\u81ea\u5954\u6ce2\uff0c\u6587\u4e3d\u8fd8\u4e00\u76f4\u90fd\u7559\u5728\u6c81\u56ed\u5e2e\u52a9\u505a\u6551\u707e\u5de5\u4f5c\uff0c\u6240\u4ee5\u4ed6\u77e5\u9053\u707e\u60c5\u53d1\u751f\u540e\u6c81\u56ed\u5c31\u6e90\u6e90\u4e0d\u65ad\u5f80\u91d1\u5c71\u6350\u9001\u6551\u707e\u7269\u8d44\uff0c\u867d\u7136\u4e0d\u6e05\u695a\u5177\u4f53\u7684\u6570\u636e\uff0c\u4f46\u662f\u4ed6\u77e5\u9053\u8fd9\u4e9b\u7269\u8d44\u7d2f\u52a0\u8d77\u6765\u4e00\u5b9a\u662f\u4e2a\u8ba9\u4eba\u77aa\u76ee\u7ed3\u820c\u7684\u5929\u6587\u6570\u5b57\uff0c\u4ed6\u624b\u91cc\u7684\u9632\u6c34\u624b\u673a\u5c31\u662f\u6c81\u56ed\u6350\u51fa\u6765\u7684\u9632\u6c5b\u7269\u8d44\uff0c\u8fd9\u6837\u7684\u9632\u6c34\u624b\u673a\uff0c\u6c81\u56ed\u5411\u7701\u91cc\u4e00\u6350\u5c31\u662f\u4e00\u5343\u53ea\uff0c\u4ed6\u4e4b\u524d\u8fd8\u5728\u5947\u602a\uff1a\u4e00\u5bb6\u89c4\u6a21\u4e0d\u7b97\u592a\u5927\u7684\u90ca\u533a\u9152\u5e97\uff0c\u5e55\u540e\u8001\u677f\u7adf\u6709\u5982\u6b64\u5927\u7684\u8d22\u529b\uff1f</p><p>\u3000\u3000\u539f\u6765\u90fd\u662f\u8ba1\u5212\u51fa\u8d4422\u4ebf\u8d44\u91d1\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u7684\u4f01\u4e1a\u7ad9\u5728\u80cc\u540e\u652f\u6301\u6c81\u56ed\u7684\u8d48\u707e\u6551\u6d4e\u884c\u4e3a\u3002</p><p>\u3000\u3000\u5211\u7231\u56fd\u5fc3\u91cc\u95ee\u81ea\u5df1\uff1a\u80fd\u6307\u8d23\u8fd9\u6837\u7684\u4f01\u4e1a\u662f\u6ca1\u6709\u8bda\u4fe1\u3001\u53ea\u77e5\u9053\u538b\u69a8\u804c\u5de5\u3001\u6ca1\u6709\u4e1d\u6beb\u8d23\u4efb\u611f\u7684\u4f01\u4e1a\uff1f\u5c31\u5728\u707e\u524d\uff0c\u4eba\u5bb6\u8fd8\u6709\u4e00\u540d\u603b\u88c1\u4e0e\u591a\u540d\u9ad8\u5c42\u5728\u91d1\u5c71\u7ed9\u56f4\u5835\u81f4\u4f24\uff01</p><p>\u3000\u3000\u5218\u5b50\u6dc7\u89c1\u5211\u7231\u56fd\u8fd9\u822c\u6a21\u6837\uff0c\u5fc3\u60f3\u5211\u7231\u56fd\u539f\u6765\u662f\u77e5\u9053\u6c81\u56ed\u5728\u62a2\u9669\u6551\u707e\u4e2d\u53d1\u6325\u51fa\u4f5c\u7528\u7684\uff0c\u53ea\u662f\u4e4b\u524d\u4e0d\u6653\u5f97\u5728\u80cc\u540e\u652f\u6491\u6c81\u56ed\u62a2\u9669\u6551\u707e\u7684\u662f\u9526\u6e56\uff0c\u5fc3\u91cc\u604d\u7136\u60f3\u660e\u767d\u8fc7\u6765\uff1a\u771f\u662f\u6655\u4e86\u5934\u4e86\uff0c\u53ea\u8981\u5c06\u9526\u6e56\u6350\u8d44\u6c42\u707e\u7684\u4e8b\u60c5\u5ba3\u4f20\u4e00\u4e0b\uff0c\u666e\u901a\u804c\u5de5\u7684\u62b5\u89e6\u5fc3\u7406\u81ea\u7136\u5c31\u51b0\u5c01\u74e6\u89e3\u3002</p><p>\u3000\u3000\u5f20\u606a\u8ddf\u5218\u5b50\u6dc7\u8bf4\u9053\uff1a\u201c\u6211\u4f1a\u901a\u77e5\u5468\u6e38\u5c3d\u5feb\u8ddf\u5218\u7701\u957f\u4f60\u8054\u7cfb\u2026\u2026\u201d\u53c8\u4fa7\u5934\u8ddf\u5211\u7231\u56fd\u8bf4\u9053\uff0c\u201c\u8001\u5211\u5382\u957f\uff0c\u6211\u6b63\u597d\u627e\u4f60\u8bf4\u4ef6\u4e8b\uff0c\u7eb8\u5382\u7684\u68da\u6237\u533a\u7ed9\u51b2\u6bc1\u4e86\u5927\u534a\uff0c\u5373\u4f7f\u6709\u6ca1\u88ab\u51b2\u6bc1\u7684\uff0c\u4e5f\u90fd\u6210\u4e86\u5371\u623f\u2014\u2014\u5f90\u4f2f\u4f2f\u521a\u521a\u8ddf\u6211\u8c08\u707e\u540e\u91cd\u5efa\u7684\u4e8b\u60c5\uff0c\u707e\u540e\u91cd\u5efa\uff0c\u6211\u4eec\u4e0d\u80fd\u8896\u624b\u65c1\u89c2\uff0c\u6211\u4eec\u521a\u624d\u7ad9\u5728\u6cb3\u5824\u4e0a\u5546\u91cf\u4e86\u4e00\u4e0b\uff0c\u68da\u6237\u533a\u4f4f\u5b85\u8981\u5efa\u8bbe\u6210\u6807\u51c6\u7684\u751f\u6d3b\u5c0f\u533a\uff0c\u5927\u7ea6\u9700\u8981\u4e00\u4e2a\u4ebf\u7684\u8d44\u91d1\uff0c\u8fd9\u7b14\u94b1\uff0c\u6211\u4eec\u51fa\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u679c\u771f\u662f\u8d22\u5927\u6c14\u7c97\uff0c\u8fd9\u6b21\u6c5f\u5357\u53d7\u707e\uff0c\u91d1\u5c71\u6700\u4e3a\u4e25\u91cd\uff0c\u91d1\u5c71\u6e56\u6e83\u5824\u5c31\u5bfc\u81f4\u6709\u4e24\u4e07\u591a\u5957\u6c11\u623f\u76f4\u63a5\u88ab\u6d2a\u6c34\u51b2\u6bc1\uff0c\u5f20\u606a\u5f20\u53e3\u5c31\u627f\u63fd\u4e0b\u5341\u5206\u4e4b\u4e00\u7684\u91cd\u5efa\u4efb\u52a1\uff0c\u5218\u5b50\u6dc7\u8f9e\u522b\u4e0b\u4e86\u6cb3\u5824\uff0c\u5750\u56de\u8f66\u91cc\uff0c\u5fc3\u91cc\u60f3\uff1a\u4e2d\u592e\u51b3\u5b9a\u542f\u7528\u5f90\u5b66\u5e73\u679c\u771f\u662f\u6709\u70b9\u610f\u601d\u3002</p><p>\u3000\u3000\u5218\u5b50\u6dc7\u8d76\u56de\u5e02\u533a\uff0c\u8fd8\u8981\u56de\u7701\u59d4\u7701\u653f\u5e9c\u5927\u697c\u5411\u5c1a\u5b66\u4e49\u3001\u6881\u4f1f\u6cd5\u6c47\u62a5\u4ed6\u5230\u6668\u66e6\u7eb8\u4e1a\u5ba3\u5e03\u7701\u59d4\u51b3\u5b9a\u7684\u60c5\u51b5\u3002\u5982\u6b64\u770b\u6765\uff0c\u6668\u66e6\u7eb8\u4e1a\u90a3\u8fb9\u7684\u963b\u529b\u662f\u53ef\u4ee5\u4e0d\u7528\u8003\u8651\u4e86\uff0c\u4ed6\u5fc3\u91cc\u7422\u78e8\u7740\u5c1a\u5b66\u4e49\u5728\u9000\u4e0b\u4e4b\u524d\u51b3\u5fc3\u4fc3\u6210\u56fd\u5185\u6700\u5927\u4e00\u6869\u79c1\u6709\u8d44\u672c\u6536\u8d2d\u5927\u578b\u56fd\u4f01\u6848\uff0c\u6307\u4e0d\u5b9a\u662f\u5f97\u5230\u8d75\u6d4e\u4e1c\u603b\u7406\u7684\u6697\u793a\u2014\u2014\u8d75\u6d4e\u4e1c\u4e00\u76f4\u79ef\u6781\u63a8\u52a8\u591a\u79cd\u5f62\u5f0f\u7684\u56fd\u4f01\u6539\u5236\u3002</p><p>\u3000\u3000\u201c\u4f1a\u4e0d\u4f1a\u6807\u51c6\u6709\u4e9b\u9ad8\uff1f\u201d\u542c\u5218\u5b50\u6dc7\u6c47\u62a5\u5f20\u606a\u5728\u6cb3\u5824\u627f\u8bfa\u6350\u8d44\u91cd\u5efa\u7eb8\u5382\u68da\u6237\u533a\u7684\u4e8b\u60c5\uff0c\u6881\u4f1f\u6cd5\u4e0b\u610f\u8bc6\u95ee\u4e86\u4e00\u53e5\uff0c\u4e2d\u592e\u8d22\u653f\u5bf9\u6c5f\u5357\u7701\u5168\u7701\u7684\u7d27\u6025\u6551\u707e\u62e8\u6b3e\u4ee5\u53ca\u5bf9\u91d1\u5c71\u6e56\u3001\u996e\u9a6c\u6cb3\u7684\u707e\u540e\u5824\u9632\u6574\u56fa\u3001\u6cb3\u9053\u6e05\u6de4\u3001\u9000\u7530\u8fd8\u6e56\u53ca\u6cb3\u969c\u6e05\u9664\u7b49\u8bf8\u591a\u4e13\u9879\u62e8\u6b3e\u603b\u5171\u624d\u4e24\u4ebf\u591a\u70b9\uff0c\u9526\u6e56\u82e5\u662f\u5b9e\u6253\u5b9e\u7684\u638f\u51fa\u4e00\u4e2a\u4ebf\u4e3a\u7eb8\u5382\u68da\u6237\u533a\u641e\u707e\u540e\u91cd\u5efa\uff0c\u4e0d\u662f\u6807\u51c6\u6709\u4e9b\u9ad8\u7684\u95ee\u9898\uff0c\u7b80\u76f4\u5c31\u662f\u5962\u4f88\u554a\uff01</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u8f6c\u5ff5\u5c31\u660e\u767d\u8fc7\u6765\u4e86\uff0c\u89c9\u5f97\u8fd9\u53e5\u8bdd\u8bf4\u7684\u6709\u4e9b\u591a\u4f59\uff0c\u89c1\u5c1a\u5b66\u4e49\u5750\u5728\u4f1a\u8bae\u684c\u5bf9\u9762\u6ca1\u6709\u542d\u58f0\uff0c\u60f3\u5fc5\u4ed6\u4e5f\u77e5\u9053\uff1a\u8fd9\u7b14\u6350\u6b3e\u540d\u4e49\u8981\u7b97\u5f90\u5b66\u5e73\u7ed9\u62c9\u6765\u7684\uff0c\u68da\u6237\u533a\u5efa\u8bbe\u5f97\u6f02\u4eae\u4e00\u4e9b\uff0c\u603b\u662f\u5728\u7ed9\u5f90\u5b66\u5e73\u8138\u4e0a\u8d34\u91d1\uff0c\u5f20\u606a\u8fd9\u4eba\u6709\u8d64\u5b50\u4e4b\u5fc3\u4e0d\u5047\uff0c\u4f46\u7edd\u5bf9\u4e0d\u50bb\u3002</p><p>\u3000\u3000\u5c1a\u5b66\u4e49\u8003\u8651\u4e86\u4e00\u4f1a\u513f\uff0c\u8bf4\u9053\uff1a\u201c\u8fd9\u7b14\u94b1\u8981\u662f\u8ba9\u9526\u6e56\u4eb2\u81ea\u6765\u82b1\uff0c\u6216\u8bb8\u80fd\u82b1\u5f97\u66f4\u5b9e\u5728\u4e00\u4e9b\u2026\u2026\u6881\u7701\u957f\uff0c\u4f60\u662f\u4e0d\u662f\u4eb2\u81ea\u627e\u5f90\u4e3b\u4efb\u4e00\u8d77\u4e0e\u8ddf\u9526\u6e56\u65b9\u9762\u6c9f\u901a\u4e00\u4e0b\uff1f\u201d</p><p>\u3000\u3000\u56fd\u5185\u8fd8\u6ca1\u6709\u6350\u8d60\u4f01\u4e1a\u6216\u4e2a\u4eba\u76f4\u63a5\u53c2\u4e0e\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u7684\u5148\u4f8b\uff0c\u6881\u4f1f\u6cd5\u89c1\u5c1a\u5b66\u4e49\u662f\u4e0b\u5b9a\u51b3\u5fc3\u6253\u7834\u9648\u89c4\u65e7\u4fd7\u4e86\uff0c\u4e5f\u4e0d\u6015\u6c11\u653f\u90e8\u95e8\u4f1a\u6709\u62b5\u89e6\u60c5\u7eea\uff0c\u70b9\u5934\u7b54\u5e94\u4e0b\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u5c3d\u5feb\u627e\u5f20\u606a\u8c08\u4e00\u4e0b\u3002\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5211\u7231\u56fd\u4e0d\u6e05\u695a\u6c81\u56ed\u90a3\u91cc\u8fd9\u4e9b\u5929\u6e90\u6e90\u4e0d\u65ad\u6350\u51fa\u6765\u7684\u7269\u8d44\u52a0\u8d77\u6765\u503c\u591a\u5c11\u94b1\uff0c\u5f53\u542c\u5230\u5f20\u606a\u5f20\u53e3\u5141\u8bfa\u6350\u8d60\u4e00\u4e2a\u4ebf\u91cd\u5efa\u7eb8\u5382\u68da\u6237\u533a\uff0c\u5fc3\u91cc\u7830\u7136\u4e00\u8df3\uff0c\u6123\u4e86\u534a\u664c\u5c31\u7b97\u5218\u5b50\u6dc7\u79bb\u5f00\uff0c\u4ed6\u90fd\u6ca1\u6709\u542d\u58f0\u3002</p><p>\u3000\u3000\u5211\u7231\u56fd\u662f\u53d7\u8d75\u6d4e\u4e1c\u3001\u66fe\u5982\u5723\u4e24\u4f4d\u603b\u7406\u63a5\u89c1\u7684\u6297\u6d2a\u82f1\u96c4\uff0c\u53c8\u662f\u7701\u4eba\u5927\u4ee3\u8868\uff0c\u6240\u4ee5\u8d75\u6709\u4f26\u4e5f\u8bf7\u4ed6\u53c2\u52a0\u91d1\u5c71\u5e02\u91cc\u7684\u707e\u540e\u6c42\u6cbb\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u4eca\u5e74\u5927\u534a\u4e2a\u4e2d\u56fd\u53d7\u707e\uff0c\u4e2d\u592e\u8d22\u653f\u4e5f\u6349\u895f\u89c1\u8098\uff0c\u62e8\u7ed9\u6c5f\u5357\u7701\u7684\u4e24\u4ebf\u591a\u5df2\u7ecf\u7b97\u662f\u5927\u624b\u7b14\uff0c\u7701\u3001\u5e02\u4e24\u7ea7\u8d22\u653f\u7f3a\u94b1\uff0c\u6cb3\u9053\u5824\u9632\u6574\u6cbb\u3001\u6cb3\u969c\u6e05\u9664\u3001\u9000\u7530\u8fd8\u6e56\u53c8\u662f\u707e\u540e\u8d22\u653f\u5de5\u4f5c\u5fc5\u987b\u8981\u4fdd\u8bc1\u7684\u91cd\u70b9\uff0c\u5bf9\u53d7\u707e\u7fa4\u4f17\u7684\u76f4\u63a5\u6551\u707e\u6b3e\u5c31\u6781\u4e3a\u6709\u9650\uff0c\u6839\u636e\u73b0\u5728\u80fd\u6324\u51fa\u6765\u7684\u8d22\u653f\u53e3\u5b50\uff0c\u5012\u5c4b\u7684\u53d7\u707e\u7fa4\u4f17\uff0c\u6bcf\u6237\u4e5f\u624d\u53ea\u80fd\u9886\u4e00\u5343\u4e03\u767e\u5143\u7684\u6551\u707e\u6b3e\u3002</p><p>\u3000\u3000\u4e5d\u516b\u5e74\uff0c\u4e00\u5343\u4e03\u767e\u5143\u80fd\u505a\u4ec0\u4e48\uff1f\u80fd\u642d\u95f4\u5168\u5bb6\u6324\u5728\u4e00\u8d77\u906e\u98ce\u6321\u96e8\u7684\u68da\u5b50\u3002</p><p>\u3000\u3000\u5211\u7231\u56fd\u8fd9\u4e9b\u5929\u770b\u591f\u6101\u7709\u82e6\u8138\u7684\u9762\u5bb9\uff0c\u8bb8\u591a\u4eba\u5bb6\u4e4b\u524d\u751f\u6d3b\u867d\u7136\u6e05\u82e6\uff0c\u4f46\u8fd8\u80fd\u7ef4\u6301\u4e0b\u53bb\uff0c\u5982\u6b64\u4e00\u4e0b\u5b50\u6ca6\u4e3a\u8d64\u8d2b\uff0c\u8c01\u5fc3\u5934\u4f1a\u597d\u53d7\uff1f</p><p>\u3000\u3000\u5211\u7231\u56fd\u8003\u8651\u4e0d\u4e86\u592a\u8fdc\uff0c\u4ed6\u5f88\u76f4\u89c2\u5c31\u60f3\uff0c\u6d2a\u6c34\u8fd8\u6ca1\u6709\u5b8c\u5168\u9000\u53bb\uff0c\u7701\u91cc\u53c8\u6025\u7740\u5c06\u6668\u66e6\u7eb8\u4e1a\u5356\u6389\uff0c\u90a3\u4e5f\u662f\u7ed9\u7f3a\u94b1\u6025\u7684\u5427\uff1f</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5211\u7231\u56fd\u53d1\u6123\u7684\u6837\u5b50\uff0c\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u90a3\u5c31\u62dc\u6258\u8001\u5211\u5382\u957f\u53bb\u8ba9\u68da\u6237\u533a\u53d7\u707e\u7684\u7fa4\u4f17\u5b89\u4e2a\u5fc3\uff0c\u6211\u5f53\u7740\u5f90\u4f2f\u4f2f\u7684\u9762\uff0c\u662f\u4e0d\u6562\u98df\u8a00\u7684\uff0c\u4e0d\u8fc7\u5462\uff0c\u4e5f\u4e0d\u8981\u8bf4\u8ddf\u65b0\u5149\u7eb8\u4e1a\u6709\u5173\u7cfb\uff0c\u5e76\u8d2d\u8c08\u5224\u5f52\u5e76\u8d2d\u8c08\u5224\uff0c\u91cd\u5efa\u6350\u6b3e\u5f52\u91cd\u5efa\u6350\u6b3e\uff0c\u6211\u4eec\u4e5f\u4e0d\u662f\u60f3\u7740\u8981\u62ff\u8fd9\u4e00\u4e2a\u4ebf\u6765\u6536\u4e70\u4eba\u5fc3\u2026\u2026\u201d</p><p>\u3000\u3000\u5211\u7231\u56fd\u5012\u662f\u6709\u4e9b\u6000\u7591\u5f20\u606a\u8fd9\u4ee5\u5e74\u8f7b\uff0c\u4ed6\u771f\u80fd\u51b3\u5b9a\u6350\u4e00\u4e2a\u4ebf\u51fa\u6765\uff1f\u597d\u50cf\u552f\u6050\u8fd9\u4e00\u4e2a\u4ebf\u98de\u4e86\uff0c\u552f\u6050\u5f20\u606a\u6536\u56de\u4ed6\u8bf4\u7684\u8bdd\uff0c\u5fd9\u4e0d\u8fed\u7684\u4e0b\u4e86\u5824\uff0c\u4ed6\u5fc3\u91cc\u6253\u8d77\u4e00\u4e2a\u4e3b\u610f\uff1a\u8fd9\u5c0f\u5b50\u8981\u662f\u98df\u8a00\uff0c\u65b0\u5149\u7eb8\u4e1a\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u5c31\u4e0d\u8981\u60f3\u6210\u3002\u8f6c\u5ff5\u53c8\u60f3\u6253\u8fd9\u6837\u7684\u4e3b\u610f\u672a\u514d\u592a\u5bf9\u4e0d\u8d77\u4eba\u5bb6\uff0c\u4ed6\u4eec\u5df2\u7ecf\u505a\u5f97\u591f\u591a\u4e86\u3002</p><p>\u3000\u3000\u201c\u4e00\u4e2a\u4ebf\u554a\uff0c\u201d\u5f90\u5b66\u5e73\u8f7b\u8f7b\u4e00\u53f9\uff0c\u8bf4\u9053\uff0c\u201c\u4e00\u4e2a\u4ebf\u80fd\u591f\u505a\u66f4\u591a\u7684\u4e8b\u60c5\u554a\u3002\u201d</p><p>\u3000\u3000\u201c\u5f90\u4f2f\u4f2f\u786e\u5b9a\u51e0\u65f6\u80fd\u6b63\u5f0f\u4e3b\u6301\u6c5f\u5357\u7684\u5927\u5c40\uff1f\u201d\u5f20\u606a\u95ee\u4e86\u4e00\u53e5\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8fd9\u94b1\u4ea4\u7ed9\u6c11\u653f\u90e8\u95e8\uff0c\u51e0\u7ecf\u5468\u8f6c\uff0c\u4e0d\u6653\u5f97\u4f1a\u6709\u591a\u5c11\u94b1\u80fd\u76f4\u63a5\u7528\u5728\u707e\u6c11\u5934\u4e0a\uff0c\u6211\u8fd9\u624d\u54ac\u53e3\u8bf4\u5b9a\u7ed9\u68da\u6237\u533a\u91cd\u5efa\u6240\u7528\u3002\u4e00\u65b9\u9762\uff0c\u4e3a\u6536\u8d2d\u6668\u66e6\u7eb8\u4e1a\u51cf\u8f7b\u4e9b\u963b\u529b\uff0c\u4e00\u65b9\u9762\u4e5f\u80fd\u76ef\u7740\u6c11\u653f\u90e8\u95e8\u5c06\u94b1\u6b3e\u4e13\u9879\u7528\u5728\u68da\u6237\u533a\u7684\u91cd\u5efa\u5de5\u4f5c\u4e0a\u2026\u2026\u201d\u4ed6\u5012\u662f\u6ca1\u6709\u76f4\u63a5\u8bf4\u6551\u707e\u4e5f\u662f\u9700\u8981\u5178\u578b\u7684\uff0c\u6bd5\u7adf\u73b0\u5728\u8fd8\u4e0d\u662f\u5f90\u5b66\u5e73\u4e3b\u6301\u6c5f\u5357\u7701\u59d4\u7684\u5de5\u4f5c\u3002</p><p>\u3000\u3000\u591c\u8272\u6e10\u6df1\uff0c\u770b\u4e0d\u6e05\u5824\u4e0b\u7684\u6cb3\u6c34\uff0c\u5f20\u606a\u966a\u5f90\u5b66\u5e73\u4e0b\u4e86\u6cb3\u5824\u3002\u6881\u4f1f\u6cd5\u90a3\u8fb9\u6253\u7535\u8bdd\u8fc7\u6765\uff0c\u8bf4\u4e0e\u91d1\u5c71\u5e02\u91cc\u665a\u4e0a\u8981\u5c06\u7701\u91cc\u4e00\u4e9b\u6551\u707e\u5de5\u4f5c\u7684\u5b89\u6392\u8ddf\u5f90\u5b66\u5e73\u6c47\u62a5\uff0c\u9080\u8bf7\u5f20\u606a\u4e00\u8d77\u53c2\u52a0\u3002</p><p>\u3000\u3000\u5f90\u5b66\u5e73\u7b49\u4e0d\u53ca\u665a\u4e0a\uff0c\u5c31\u7ea6\u597d\u5728\u7701\u653f\u5e9c\u98df\u5802\u91cc\u4e00\u8d77\u7528\u9910\uff0c\u4ed6\u5c31\u4e0e\u5f20\u606a\u4e58\u8f66\u8d76\u56de\u5e02\u5185\u3002</p><p>\u3000\u3000\u8d76\u5230\u7701\u653f\u5e9c\u5927\u697c\u897f\u9644\u697c\u7684\u673a\u5802\u98df\u5802\uff0c\u591c\u91cc\u9664\u4e86\u4e9b\u9752\u5e74\u5b98\u5458\u5916\uff0c\u51e0\u4e4e\u6ca1\u6709\u4ec0\u4e48\u4eba\u4f1a\u7559\u5728\u673a\u5173\u98df\u5802\u7528\u9910\uff0c\u5f20\u606a\u4e0e\u5f90\u5b66\u5e73\u5230\u4e8c\u697c\uff0c\u770b\u5230\u8d75\u6709\u4f26\u6bd4\u4ed6\u4eec\u5148\u8d76\u5230\u4e86\uff0c\u6881\u4f1f\u6cd5\u8981\u4e34\u65f6\u53c2\u52a0\u4e00\u4e2a\u4f1a\u8bae\uff0c\u8981\u7a0d\u7b49\u4e00\u4f1a\u513f\u624d\u80fd\u62bd\u51fa\u8eab\u6765\u3002</p><p>\u3000\u3000\u201c\u6881\u7701\u957f\u8ddf\u6211\u901a\u8fc7\u6c14\u4e86\uff0c\u5c31\u9526\u6e56\u51b3\u5b9a\u6350\u6b3e\u7684\u8fd9\u4e00\u4e2a\u4ebf\uff0c\u5e02\u6c11\u653f\u90e8\u95e8\u4e0d\u63d2\u624b\uff0c\u7531\u9526\u6e56\u81ea\u884c\u51b3\u5b9a\u6b3e\u9879\u7684\u5b89\u6392\uff0c\u5e02\u91cc\u7ec4\u7ec7\u4e00\u4e2a\u534f\u8c03\u5c0f\u7ec4\u914d\u5408\u4e00\u5207\u89e3\u51b3\u6cd5\u5b9a\u7a0b\u5e8f\u4e0a\u7684\u95ee\u9898\u3002\u201d\u8d75\u6709\u4f26\u8bf4\u9053\uff0c\u201c\u8ba9\u6211\u5148\u8ddf\u4f60\u901a\u4e2a\u6c14\uff0c\u7701\u59d4\u4f1a\u5c3d\u5feb\u62ff\u51fa\u6b63\u5f0f\u7684\u51b3\u8bae\u51fa\u6765\u3002\u201d</p><p>\u3000\u3000\u7701\u59d4\u5df2\u7ecf\u64a4\u6d88\u5468\u747e\u745c\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u3001\u5e38\u59d4\u3001\u59d4\u5458\u7684\u804c\u52a1\uff0c\u7531\u8d75\u6709\u4f26\u62c5\u4efb\u5e02\u59d4\u4e66\u8bb0\u517c\u5e02\u957f\uff0c\u5468\u747e\u745c\u6682\u65f6\u4fdd\u7559\u7701\u59d4\u5e38\u59d4\u3001\u59d4\u5458\u7684\u804c\u52a1\uff0c\u4e0b\u4e00\u6b65\u600e\u4e48\u8c03\u6574\uff0c\u8fd8\u8981\u7701\u91cc\u53ec\u5f00\u5168\u59d4\u4f1a\u8bae\u624d\u80fd\u51b3\u5b9a\u3002</p><p>\u3000\u3000\u542c\u8d75\u6709\u4f26\u8fd9\u4e48\u8bf4\uff0c\u5f20\u606a\u5012\u89c9\u5f97\u76f8\u5f53\u610f\u5916\uff0c\u5fc3\u60f3\u5c1a\u5b66\u4e49\u8981\u65e9\u6709\u5982\u6b64\u7684\u51b3\u65ad\uff0c\u6c5f\u5357\u4f55\u81f3\u4e8e\u6b64\uff1f\u53ea\u662f\u5c1a\u5b66\u4e49\u8981\u65e9\u6709\u5982\u6b64\u7684\u51b3\u65ad\uff0c\u4e5f\u4f1a\u89c9\u5f97\u4ed5\u9014\u6b65\u5c65\u552f\u8270\u5427\uff1f\u8fd9\u9648\u89c4\u964b\u4e60\u8981\u6495\u7834\u5b83\uff0c\u5728\u4e5d\u5341\u5e74\u4ee3\uff0c\u603b\u4e0d\u662f\u7b80\u5355\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u4e86\u5f90\u5b66\u5e73\u4e00\u773c\uff0c\u6b63\u8272\u7684\u56de\u590d\u8d75\u6709\u4f26\uff0c\u8bf4\u9053\uff1a\u201c\u7701\u91cc\u80fd\u51fa\u6b63\u5f0f\u7684\u51b3\u8bae\uff0c\u9526\u6e56\u5012\u662f\u80fd\u505a\u66f4\u591a\u7684\u4e8b\u60c5\u2026\u2026\u201d</p></article>"}}}]);
