(window.webpackJsonp=window.webpackJsonp||[]).push([[1078],{1101:function(p){p.exports={chapterTitle:null,title:"\u7b2c1075\u7ae0 \u8001\u5a18\u53eb\u4f60\u6252\u4ffa\u5973\u513f\u8863\u670d\uff01",content:"<article><h2>\u7b2c1075\u7ae0 \u8001\u5a18\u53eb\u4f60\u6252\u4ffa\u5973\u513f\u8863\u670d\uff01</h2><p>\u3000\u3000\u674e\u6708\u8339\u4e0a\u524d\u62c9\u5f00\u5e03\u5e54\uff0c\u53ea\u770b\u5230\u5973\u513f\u53cc\u624b\u62a4\u5728\u80f8\u524d\u5728\u6323\u624e\uff0c\u5f20\u606a\u5c06\u5973\u513f\u6402\u5728\u6000\u91cc\uff0c\u8fd8\u624b\u5fd9\u811a\u4e71\u7684\u8981\u5c06\u5973\u513f\u62a4\u80f8\u7684\u624b\u63b0\u5f00\uff0c\u5f20\u606a\u88f8\u7740\u4e0a\u8eab\uff0c\u5973\u513f\u8eab\u4e0a\u5c31\u62ab\u7740\u4ef6\u886c\u886b\uff0c\u524d\u895f\u655e\u5f00\u7740\uff0c\u6bd4\u534a\u88f8\u597d\u4e0d\u4e86\u591a\u5c11\uff0c\u4e73\u7f69\u63a8\u6324\u7740\u6b6a\u6389\u4e00\u534a\uff0c\u96ea\u5ae9\u633a\u7acb\u7684\u5de6\u4e73\u9732\u51fa\u5927\u534a\u7247\u6765\uff0c\u5ae3\u7ea2\u8272\u7684\u4e73\u6655\u90fd\u9732\u51fa\u4e00\u661f\u70b9\u6765\u2026\u2026</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u5916\u9762\u7684\u6b4c\u8ff7\u6b63\u4e07\u4f17\u9f50\u547c\uff1a\u201c\u6c5f\u9edb\u513f\u3001\u6c5f\u9edb\u513f\u2026\u2026\u201d</p><p>\u3000\u3000\u674e\u6708\u8339\u89c1\u5973\u513f\u7ed9\u4eba\u8fd9\u4e48\u6b3a\u8d1f\uff0c\u4e00\u628a\u6012\u706b\u201c\u8e6d\u201d\u7684\u70e7\u4e0a\u5fc3\u5934\uff0c\u53cc\u624b\u5c31\u671d\u5f20\u606a\u7684\u8138\u4e0a\u6293\u53bb\uff1a\u201c\u4f60\u8fd9\u4e2a\u755c\u7272\uff0c\u4f60\u4ec0\u4e48\u5973\u4eba\u73a9\u5f04\u4e0d\u5230\uff0c\u5c31\u77e5\u9053\u6765\u6b3a\u8d1f\u6211\u4eec\u5bb6\u9edb\u513f\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6b63\u5e2e\u6c5f\u9edb\u513f\u5c06\u4ed6\u7684\u886c\u886b\u7a7f\u4e0a\u53bb\uff0c\u54ea\u91cc\u60f3\u5230\u6c5f\u9edb\u513f\u5979\u5988\u7a81\u7136\u62c9\u5f00\u5e03\u5e54\u53cc\u624b\u76f4\u63a5\u4ed6\u8138\u4e0a\u6293\u6765\uff0c\u4ed6\u7684\u624b\u8fd8\u6293\u7740\u9edb\u513f\u7684\u624b\u5f80\u886c\u886b\u8863\u8896\u91cc\u4f38\uff0c\u6765\u4e0d\u53ca\u62a4\u4f4f\u81ea\u5df1\u7684\u8138\uff0c\u53ea\u89c9\u5f97\u8138\u7ed9\u6293\u5f97\u706b\u8fa3\u8fa3\u7684\u3002</p><p>\u3000\u3000\u770b\u7740\u6c5f\u9edb\u513f\u5979\u5988\u8ddf\u6bcd\u8001\u864e\u4f3c\u7684\u6251\u8fc7\u6765\uff0c\u5f20\u606a\u5413\u5f97\u9003\u8352\u800c\u9003\uff0c\u9003\u8fdb\u5316\u5986\u5ba4\u524d\u8fd8\u4e0d\u5fd8\u56de\u5934\u671d\u6c5f\u9edb\u513f\u558a\uff1a\u201c\u7a7f\u597d\u8863\u670d\u5feb\u4e0a\u573a\uff01\u201d\u53cd\u624b\u5c06\u5316\u5986\u5ba4\u7684\u95e8\u5173\u4e0a\uff0c\u4ed6\u8fd8\u771f\u6015\u674e\u6708\u8339\u50cf\u53ea\u6bcd\u8001\u864e\u4f3c\u7684\u6251\u4e0a\u6765\u3002</p><p>\u3000\u3000\u5b59\u9759\u9999\u77e5\u9053\u4e8b\u60c5\u4e0d\u4f1a\u50cf\u8868\u9762\u4e0a\u770b\u5230\u7684\u90a3\u4e48\u7b80\u5355\uff0c\u5979\u4eec\u521a\u624d\u770b\u5230\u7684\u52a8\u4f5c\uff0c\u65e2\u53ef\u4ee5\u7406\u89e3\u6210\u5f20\u606a\u5728\u5bf9\u6c5f\u9edb\u513f\u975e\u793c\uff0c\u53c8\u53ef\u4ee5\u7406\u89e3\u6210\u5f20\u606a\u8981\u5c06\u4ed6\u7684\u886c\u886b\u7ed9\u6c5f\u9edb\u513f\u7a7f\u4e0a\uff0c\u5fc3\u60f3\u5f20\u606a\u518d\u6df7\u86cb\uff0c\u4e5f\u4e0d\u4f1a\u5728\u8fd9\u4e2a\u65f6\u673a\u5bf9\u6c5f\u9edb\u513f\u975e\u793c\u554a\uff0c\u5fd9\u4e0e\u8bb8\u7ef4\u5c06\u9677\u5165\u6293\u72c2\u72b6\u6001\u7684\u674e\u6708\u8339\u62c9\u4f4f\uff0c\u8ba9\u6c5f\u9edb\u513f\u8d76\u7d27\u7a7f\u597d\u886c\u886b\u51fa\u573a\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u73b0\u5728\u90fd\u4e0d\u77e5\u9053\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5f20\u606a\u6252\u5979\u7684\u8863\u670d\uff0c\u5979\u53ea\u662f\u4e0b\u610f\u8bc6\u7684\u53cd\u6297\uff0c\u4e4b\u6240\u4ee5\u53cd\u6297\uff0c\u66f4\u591a\u7684\u662f\u89c9\u5f97\u65f6\u673a\u4e0e\u573a\u6240\u4e0d\u5bf9\u2014\u2014\u770b\u7740\u5f20\u606a\u72fc\u72c8\u4e0d\u582a\u7684\u9003\u8fdb\u5316\u5986\u5ba4\uff0c\u90fd\u5fcd\u4e0d\u4f4f\u8981\u7b11\u8d77\u6765\uff0c\u5fc3\u60f3\u4ed6\u6216\u8bb8\u89c9\u5f97\u81ea\u5df1\u7a7f\u4e0a\u4ed6\u7684\u886c\u886b\u4e0a\u53f0\u6f14\u5531\u821e\u53f0\u6548\u679c\u66f4\u597d\u5427\uff1f\u90a3\u4ef6T\u6064\u886b\u4e22\u5728\u811a\u4e0b\u4e5f\u4e0d\u7406\uff0c\u8fd9\u4f1a\u513f\u624b\u5fd9\u811a\u4e71\u7684\u5c06\u886c\u886b\u6263\u5b50\u6263\u8d77\u6765\uff0c\u51c6\u5907\u51fa\u573a\u3002</p><p>\u3000\u3000\u886c\u886b\u4e0b\u6446\u906e\u4f4f\u819d\u76d6\u4e0a\u5341\u4e94\u516c\u5206\u7684\u5730\u65b9\uff0c\u521a\u597d\u5c06\u5b89\u5168\u5e95\u88e4\u906e\u4f4f\uff0c\u9732\u51fa\u96ea\u767d\u7ea4\u957f\u7684\u5927\u817f\uff0c\u5341\u5206\u7684\u6027\u611f\u3002</p><p>\u3000\u3000\u6c5f\u9edb\u513f\u8fd8\u6ca1\u6709\u60f3\u8fc7\u8981\u7a7f\u8fd9\u4e48\u6027\u611f\u7684\u670d\u88c5\u4e0a\u53f0\u8868\u6f14\uff0c\u6709\u4e9b\u4e0d\u597d\u610f\u601d\uff0c\u60f3\u7740\u8fd9\u662f\u5f20\u606a\u8981\u5979\u7a7f\u7684\uff0c\u6df1\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u4e5f\u4e0d\u7ba1\u5979\u5988\u5634\u91cc\u5bf9\u5f20\u606a\u7834\u53e3\u5927\u9a82\uff0c\u6253\u5f00\u95e8\u671d\u821e\u53f0\u8d70\u53bb\u2026\u2026\u5168\u573a\u987f\u65f6\u54cd\u8d77\u96f7\u9e23\u822c\u7684\u5c16\u53eb\u4e0e\u6b22\u547c\u3002</p><p>\u3000\u3000\u76db\u590f\u5728\u53f0\u4e0b\u770b\u7740\u6c5f\u9edb\u513f\u6027\u611f\u65e0\u6bd4\u7684\u53ea\u7a7f\u7740\u4e00\u4ef6\u957f\u6446\u886c\u886b\u51fa\u573a\uff0c\u53ea\u89c9\u5f97\u90a3\u4ef6\u886c\u886b\u773c\u719f\uff0c\u8fd9\u4e0d\u662f\u5f20\u606a\u767d\u5929\u7a7f\u7684\u886c\u886b\u5417\uff1f\u5979\u77e5\u9053\u5f20\u606a\u6df7\u86cb\uff0c\u77e5\u9053\u5f20\u606a\u82b1\u5fc3\uff0c\u8bb8\u601d\u7684\u5b58\u5728\uff0c\u5728\u8fd9\u4e2a\u5708\u5b50\u51e0\u4e4e\u662f\u516c\u5f00\u7684\u79d8\u5bc6\u4e86\uff0c\u53bb\u5e74\u5e95\u7fdf\u4e39\u9752\u5192\u6b7b\u66ff\u5f20\u606a\u6321\u4e00\u5200\uff0c\u4f3c\u4e4e\u4e5f\u8d85\u8fc7\u4e86\u4e0b\u5c5e\u5bf9\u8001\u677f\u7684\u5fe0\u8bda\uff0c\u5979\u54ac\u7259\u5207\u9f7f\u7684\u60f3\uff1a\u8fd9\u4e2a\u6d51\u7403\u8ddf\u6c5f\u9edb\u513f\u8c03\u60c5\u65f6\uff0c\u5c31\u6ca1\u6709\u60f3\u5230\u4ed6\u7684\u6b63\u724c\u5973\u670b\u53cb\u6b63\u5728\u53f0\u4e0b\u770b\u8868\u6f14\uff1f</p><p>\u3000\u3000\u76db\u590f\u62c5\u5fe7\u7684\u770b\u4e86\u5510\u5a67\u4e00\u773c\uff0c\u5510\u5a67\u671d\u76db\u590f\u7b11\u4e86\u7b11\uff0c\u7ec8\u7a76\u662f\u6ca1\u6709\u5fc3\u60c5\u518d\u5750\u5728\u53f0\u4e0b\u770b\u6f14\u5531\u4f1a\uff0c\u8ddf\u76db\u590f\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u53bb\u540e\u53f0\u770b\u770b\u5427\uff0c\u53ef\u80fd\u53d1\u751f\u4e8b\u60c5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u540e\u53f0\u90fd\u70b8\u9505\u4e86\u3002</p><p>\u3000\u3000\u4ee4\u5c0f\u71d5\u3001\u6797\u51b0\u63d0\u524d\u5230\u540e\u53f0\u51c6\u5907\u7ed9\u6c5f\u9edb\u513f\u5e86\u795d\uff0c\u5979\u4eec\u521a\u5230\u5316\u5986\u5ba4\uff0c\u5c31\u770b\u89c1\u5f20\u606a\u5149\u7740\u4e0a\u8eab\u4ece\u53e6\u4e00\u8fb9\u7684\u95e8\u72fc\u72c8\u7684\u9003\u8fdb\u6765\uff0c\u8138\u4e0a\u8fd8\u7ed9\u6293\u51fa\u597d\u51e0\u9053\u8840\u75d5\uff0c\u5316\u5986\u5ba4\u5916\u9690\u9690\u7ea6\u7ea6\u4f20\u6765\u6c5f\u9edb\u513f\u6bcd\u4eb2\u7684\u7834\u53e3\u5927\u9a82\u58f0\u3002</p><p>\u3000\u3000\u201c\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u4ee4\u5c0f\u71d5\u7ed9\u773c\u524d\u7684\u60c5\u51b5\u641e\u5f97\u83ab\u660e\u5176\u5999\u3002</p><p>\u3000\u3000\u6c5f\u6d9b\u5bf9\u542c\u6f14\u5531\u4f1a\u6ca1\u6709\u4ec0\u4e48\u5174\u8da3\uff0c\u6b63\u56e0\u4e3a\u662f\u5973\u513f\u7684\u6f14\u5531\u4f1a\uff0c\u66f4\u63d0\u4e0d\u8d77\u5174\u8da3\u6765\uff0c\u5750\u5728\u5916\u9762\u7684\u4f11\u606f\u5ba4\u4e0e\u5085\u4fca\u62bd\u70df\u8bf4\u8bdd\uff0c\u542c\u7740\u5316\u5986\u5ba4\u8fd9\u8fb9\u7684\u52a8\u9759\uff0c\u4e0d\u6653\u5f97\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u5fd9\u8d70\u8fc7\u6765\u770b\u7a76\u7adf\uff0c\u770b\u5230\u5f20\u606a\u8fd9\u6837\u5b50\uff0c\u542c\u89c1\u59bb\u5b50\u53c8\u5728\u95e8\u540e\u9762\u7834\u53e3\u5927\u9a82\uff0c\u8138\u8272\u9634\u6c89\u4e0b\u6765\uff0c\u6253\u5f00\u5316\u5986\u5ba4\u7684\u95e8\uff0c\u60f3\u95ee\u6e05\u695a\u7a76\u7adf\u3002</p><p>\u3000\u3000\u201c\u6c5f\u6d9b\uff0c\u8fd9\u4e2a\u755c\u7272\u6b3a\u8d1f\u4f60\u5973\u513f\uff01\u201d\u674e\u6708\u8339\u9694\u7740\u95e8\u51f6\u72e0\u7684\u6307\u7740\u5f20\u606a\u7834\u53e3\u5927\u9a82\uff0c\u7ed9\u8bb8\u7ef4\u62e6\u8981\u62b1\u4f4f\uff0c\u8fd8\u8981\u6323\u624e\u7740\u51b2\u8fdb\u6765\uff0c\u201c\u8fd9\u4e2a\u755c\u7272\u5728\u6252\u4f60\u5973\u513f\u7684\u8863\u670d\uff01\u201d</p><p>\u3000\u3000\u6c5f\u6d9b\u52c3\u7136\u8138\u53d8\uff0c\u51b2\u8fc7\u6765\u8981\u63ea\u7740\u5f20\u606a\u6253\u3002</p><p>\u3000\u3000\u5085\u4fca\u89c1\u67b6\u5f0f\u4e0d\u5bf9\uff0c\u5fd9\u6321\u5230\u5f20\u606a\u9762\u524d\uff0c\u5c06\u6c5f\u6d9b\u6325\u8fc7\u6765\u7684\u62f3\u5934\u6293\u4f4f\uff0c\u8bf4\u9053\uff1a\u201c\u53ef\u80fd\u662f\u8bef\u4f1a\u4e86\uff01\u201d</p><p>\u3000\u3000\u201c\u4f60\u54ea\u53ea\u773c\u775b\u770b\u89c1\u662f\u8bef\u4f1a\u4e86\uff0c\u6211\u4eb2\u773c\u770b\u5230\u8fd9\u4e2a\u755c\u7272\u5728\u6252\u6211\u5973\u513f\u7684\u8863\u670d\uff0c\u6211\u4f1a\u8bef\u4f1a\u4ed6\uff1f\u4ed6\u5c31\u662f\u4e2a\u79bd\u517d\uff01\u201d\u674e\u6708\u8339\u9694\u5f97\u8001\u8fdc\uff0c\u4e00\u53e3\u553e\u6db2\u76f4\u5410\u5230\u5085\u4fca\u7684\u8138\u4e0a\uff0c\u8981\u4e0d\u662f\u7ed9\u8bb8\u7ef4\u3001\u4ee4\u5c0f\u71d5\u4e24\u4eba\u5408\u529b\u62b1\u4f4f\uff0c\u5979\u4e24\u624b\u80fd\u6293\u5230\u5085\u4fca\u8138\u4e0a\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60c5\u90c1\u95f7\u65e0\u6bd4\uff0c\u674e\u6708\u8339\u4e5f\u662f\u62a4\u5973\u5fc3\u5207\uff0c\u81ea\u5df1\u88ab\u6293\u7b97\u662f\u767d\u6293\u4e86\uff0c\u8fd8\u80fd\u8ba8\u56de\u516c\u9053\u6765\uff1f\u89c1\u968f\u884c\u7684\u4fdd\u9556\u90fd\u51b2\u8fdb\u5316\u5986\u5ba4\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u4e0d\u8981\u8ba9\u4efb\u4f55\u4eba\u79bb\u5f00\uff01\u201d\u4ed6\u53ea\u89c9\u5f97\u8138\u706b\u8fa3\u8fa3\u7684\u75db\uff0c\u4e0d\u77e5\u9053\u7ed9\u6293\u4e86\u591a\u6df1\uff0c\u4e5f\u6ca1\u6709\u8138\u5728\u5316\u5986\u5ba4\u91cc\u7167\u955c\u5b50\uff0c\u8f6c\u8eab\u51fa\u4e86\u5316\u5986\u5ba4\uff0c\u5230\u7537\u5395\u6240\u7167\u955c\u5b50\u5904\u7406\u4f24\u53e3\u53bb\u3002</p><p>\u3000\u3000\u201c\u8001\u5a18\u6293\u7684\u4f60\u600e\u4e48\u4e86\uff0c\u6293\u7834\u4f60\u7684\u8138\u600e\u4e48\u4e86\uff1f\u8001\u5a18\u8fd8\u8981\u544a\u4f60\u800d\u6d41\u6c13\uff01\u201d\u674e\u6708\u8339\u89c1\u5f20\u606a\u86ee\u6a2a\u7684\u547d\u4ee4\u4fdd\u9556\u4ee5\u4e3a\u8981\u5c06\u5979\u4e00\u5bb6\u4eba\u90fd\u6263\u4e0b\u6765\uff0c\u5fc3\u5934\u7684\u6012\u706b\u66f4\u76db\uff0c\u9a82\u5f97\u66f4\u51f6\u3002</p><p>\u3000\u3000\u201c\u4efb\u4f55\u4eba\u90fd\u4e0d\u8bb8\u79bb\u5f00\uff01\u201d\u5b59\u9759\u9999\u8138\u8272\u94c1\u9752\u8d70\u8fdb\u6765\uff0c\u5f84\u76f4\u8d70\u8fdb\u6765\uff0c\u8d70\u5230\u5973\u670d\u88c5\u5e08\u7684\u9762\u524d\uff0c\u72e0\u72e0\u7684\u4e00\u5df4\u638c\u62bd\u8fc7\u53bb\uff0c\u5c06\u56e2\u5728\u624b\u91cc\u7684T\u6064\u731b\u7684\u7838\u5973\u670d\u88c5\u5e08\u7684\u8138\u4e0a\uff0c\u5389\u58f0\u95ee\u9053\uff0c\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u6700\u540e\u4e00\u5957\u670d\u88c5\u4e3a\u4ec0\u4e48\u6362\u6210\u8fd9\u4e2a\uff1f\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u7684\u4e3e\u52a8\u5f7b\u5e95\u7684\u5c06\u5316\u5986\u5ba4\u91cc\u7684\u4f17\u4eba\u90fd\u641e\u8499\u4e86\uff0c\u674e\u6708\u8339\u3001\u6c5f\u6d9b\u90fd\u8499\u4e86\uff0c\u53ea\u89c1\u90a3\u56e2T\u6064\u4ece\u5973\u670d\u88c5\u5e08\u7684\u8eab\u4e0a\u6eda\u843d\u4e0b\u6765\u5728\u5730\u9762\u4e0a\u6563\u5f00\u3002</p><p>\u3000\u3000\u201c\u8fd9\u662f\u600e\u4e48\u56de\u4e8b\uff1f\u201d\u5927\u5bb6\u5fc3\u91cc\u90fd\u6709\u8fd9\u6837\u7684\u7591\u95ee\uff0cT\u6064\u886b\u770b\u4e0d\u51fa\u6709\u4ec0\u4e48\u95ee\u9898\u554a\uff0cT\u6064\u7b80\u5355\u7684\u5206\u4e3a\u7ea2\u767d\u4e24\u8272\uff0c\u80f8\u524d\u6709\u4e00\u53ea\u9e70\u7684\u56fe\u6848\u2026\u2026</p><p>\u3000\u3000\u201c\u539f\u5148\u5b9a\u597d\u7684\u6700\u540e\u4e00\u5957\u670d\u88c5\u7ed9\u6cfc\u4e86\u53ef\u4e50\uff0c\u4e34\u65f6\u51b3\u5b9a\u6362\u8fd9\u4ef6\u2026\u2026\u201d\u8bb8\u7ef4\u4e5f\u6ca1\u6709\u770b\u51fa\u6765T\u6064\u6709\u4ec0\u4e48\u95ee\u9898\uff0c\u89c1\u5973\u670d\u88c5\u5e08\u7ed9\u5b59\u9759\u9999\u4e00\u5df4\u638c\u6253\u8499\u4e86\uff0c\u7ad9\u5728\u90a3\u91cc\u4e0d\u6562\u8bf4\u8bdd\u3002\u8bb8\u7ef4\u4e5f\u6ca1\u6709\u89c1\u8fc7\u5b59\u9759\u9999\u53d1\u8fd9\u4e48\u5927\u7684\u706b\uff0c\u5fc3\u60f3\u5c31\u7b97\u7a7f\u6362\u4e0a\u8fd9\u5957\u670d\u88c5\u6709\u4e9b\u4ed3\u4fc3\u4e0d\u5408\u9002\u3001\u5f20\u606a\u4e34\u65f6\u51b3\u5b9a\u8ba9\u6c5f\u9edb\u513f\u6362\u4e0a\u4ed6\u7684\u886c\u886b\u5f15\u8d77\u8fd9\u4e48\u5927\u7684\u8bef\u4f1a\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u5fc5\u8981\u8fd9\u4e48\u8d23\u602a\u670d\u88c5\u5e08\uff0c\u5979\u8d70\u8fc7\u6765\u5e2e\u670d\u88c5\u5e08\u89e3\u91ca\u3002</p><p>\u3000\u3000\u201c\u6211\u89c9\u5f97\u8fd9\u8863\u886b\u4e5f\u4e0d\u9519\u2026\u2026\u201d\u674e\u6708\u8339\u89c1\u5b59\u9759\u9999\u5c06\u5f20\u606a\u6252\u9edb\u513f\u8863\u670d\u7684\u8d23\u4efb\u8fc1\u6012\u5230\u670d\u88c5\u5e08\u7684\u5934\u4e0a\uff0c\u5979\u90fd\u89c9\u5f97\u8981\u66ff\u670d\u88c5\u5e08\u6253\u62b1\u4e0d\u5e73\uff0c\u4e5f\u60f3\u5230\u5f20\u606a\u521a\u624d\u7684\u4e3e\u52a8\u6216\u8bb8\u662f\u60f3\u66ff\u9edb\u513f\u6362\u8863\u670d\uff0c\u8981\u662f\u9edb\u513f\u4e0d\u613f\u610f\uff0c\u8fd9\u4e2a\u5bb6\u4f19\u96be\u9053\u8fd8\u771f\u6562\u5728\u821e\u53f0\u80cc\u540e\u7528\u5f3a\uff0c\u81ea\u5df1\u53cd\u5e94\u662f\u4e0d\u662f\u592a\u6fc0\u70c8\u4e86\uff1f\u4e0d\u5bf9\uff0c\u81ea\u5df1\u660e\u660e\u770b\u5230\u9edb\u513f\u662f\u5728\u6323\u624e\uff01</p><p>\u3000\u3000\u201c\u4e8b\u60c5\u6bd4\u6211\u4eec\u60f3\u8c61\u7684\u8981\u590d\u6742\uff0c\u201d\u5b59\u9759\u9999\u5438\u4e86\u4e00\u53e3\u6c14\uff0c\u7a0d\u6291\u5236\u5fc3\u91cc\u7684\u6012\u706b\uff0c\u8ddf\u674e\u6708\u8339\u5fc3\u5e73\u6c14\u548c\u7684\u8bf4\u8bdd\uff0c\u8f6c\u8138\u770b\u5411\u5973\u670d\u88c5\u5e08\u65f6\uff0c\u53c8\u4e25\u5389\u8d77\u6765\uff0c\u201c\u8c01\u6307\u4f7f\u4f60\u505a\u7684\uff1f\u4f60\u62ff\u4e86\u591a\u5c11\u94b1\uff1f\u4f60\u5634\u786c\u4e0d\u627f\u8ba4\uff0c\u6211\u9a6c\u4e0a\u901a\u77e5\u8b66\u65b9\u4ecb\u5165\u8c03\u67e5\u3002\u201d</p><p>\u3000\u3000\u5b59\u9759\u9999\u8fd9\u4e48\u8bf4\uff0c\u5927\u5bb6\u90fd\u771f\u7684\u610f\u8bc6\u5230\u8fd9\u8863\u670d\u6709\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u8fd9\u8863\u670d\u50cf\u4e0d\u50cf\u5370\u5c3c\u56fd\u65d7\u8ddf\u56fd\u5fbd\uff1f\u201d\u6797\u51b0\u5f31\u5f31\u7684\u95ee\u4e86\u4ee4\u5c0f\u71d5\u4e00\u53e5\uff0c\u201c\u8001\u9e70\u56fe\u6848\u4e0a\u597d\u50cf\u8fd8\u6709\u76fe\u5fbd\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u901a\u77e5\u8b66\u65b9\u4ecb\u5165\u8c03\u67e5\u5427\uff01\u201d\u5f20\u606a\u6342\u7740\u8138\u5149\u7740\u4e0a\u8eab\u7ad9\u5728\u95e8\u53e3\uff0c\u51b7\u9759\u7684\u8bf4\u9053\uff0c\u53ea\u662f\u4ed6\u8fd9\u6837\u5b50\u8fdb\u5316\u5986\u5ba4\u91cc\u592a\u96be\u770b\uff0c\u8ddf\u6c5f\u9edb\u513f\u7236\u6bcd\u9762\u5bf9\u9762\u4e5f\u6709\u4e9b\u96be\u582a\uff0c\u5c31\u8bf4\u4e86\u4e00\u53e5\u8bdd\uff0c\u4e8b\u60c5\u7559\u7ed9\u5b59\u9759\u9999\u5904\u7406\u5c31\u53ef\u4ee5\u4e86\uff0c\u8ba9\u5085\u4fca\u4e0e\u968f\u884c\u7684\u4fdd\u9556\u966a\u4ed6\u5148\u56de\u8f66\u4e0a\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5927\u5bb6\u8fd9\u65f6\u5019\u624d\u77e5\u9053\u7a76\u7adf\u53d1\u751f\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff1a\u5f20\u606a\u770b\u51fa\u6c5f\u9edb\u513f\u6700\u540e\u4e00\u5957\u670d\u88c5\u4e0a\u6709\u95ee\u9898\uff0c\u4f46\u662f\u65f6\u95f4\u6025\u8feb\uff0c\u5916\u9762\u6b4c\u8ff7\u90fd\u5728\u547c\u5524\u5979\u51fa\u573a\uff0c\u6765\u4e0d\u53ca\u518d\u56de\u5316\u5986\u5ba4\u6362\u670d\u88c5\uff0c\u4e34\u65f6\u4e0e\u6c5f\u9edb\u513f\u5728\u8fc7\u9053\u6362\u8863\u670d\uff0c\u8ba9\u6c5f\u9edb\u513f\u5979\u5988\u770b\u5230\u8bef\u4f1a\u4e86\u2026\u2026</p><p>\u3000\u3000\u53bb\u5e74\u4e94\u6708\u5370\u5c3c\u53d1\u751f\u6392\u534e\u9a9a\u4e71\u4ee5\u6765\uff0c\u5370\u5c3c\u5c31\u6210\u4e86\u5168\u4e16\u754c\u534e\u4eba\u6700\u538c\u6076\u4e0e\u618e\u6068\u7684\u56fd\u5bb6\u3002\u867d\u8bf4\u5982\u6b64\uff0c\u4f46\u662f\u53c8\u6709\u591a\u5c11\u4eba\u77e5\u9053\u5370\u5c3c\u7684\u56fd\u65d7\u662f\u7ea2\u767d\u4e24\u8272\u65d7\u3001\u56fd\u5fbd\u662f\u9e70\u76fe\u5fbd\u56fe\u6848\uff1f</p><p>\u3000\u3000\u6f14\u5531\u4f1a\u8fdb\u884c\u5230\u6700\u9ad8\u6f6e\uff0c\u5927\u5bb6\u90fd\u624b\u5fd9\u811a\u4e71\u7684\uff0c\u53c8\u6709\u8c01\u4f1a\u610f\u8bc6\u5230\u7adf\u7136\u6709\u4eba\u6545\u610f\u5728\u670d\u88c5\u4e0a\u505a\u624b\u811a\uff1f\u5c31\u7b97\u89c9\u5f97\u8863\u670d\u4e0a\u7684\u56fe\u6848\u6709\u4e9b\u602a\u5f02\uff0c\u5c31\u7b97\u4e0e\u5370\u5c3c\u56fd\u65d7\u3001\u56fd\u5fbd\u8054\u60f3\u5230\u4e00\u8d77\uff0c\u4ed3\u4fc3\u4e4b\u4e0b\uff0c\u4e5f\u5f88\u96be\u60f3\u5230\u4f1a\u6709\u591a\u4e48\u4e25\u91cd\u7684\u540e\u679c\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u51b7\u9759\u4e0b\u6765\uff0c\u624d\u77e5\u9053\u6839\u672c\u5c31\u662f\u4e00\u8d77\u8981\u5f7b\u5e95\u6bc1\u6389\u6c5f\u9edb\u513f\u6f14\u827a\u751f\u6daf\u7684\u9634\u8c0b\uff01</p><p>\u3000\u3000\u867d\u7136\u7edd\u5927\u90e8\u5206\u6b4c\u8ff7\u5728\u73b0\u573a\u4e0d\u4f1a\u770b\u51fa\u670d\u88c5\u7684\u95ee\u9898\uff0c\u4f46\u662f\u7ecf\u5a92\u4f53\u5927\u8086\u7092\u4f5c\uff0c\u95ee\u9898\u5c31\u4f1a\u5f02\u5e38\u7684\u4e25\u91cd\uff0c\u7279\u522b\u662f\u6c5f\u9edb\u513f\u5728\u6700\u540e\u538b\u8f74\u51fa\u573a\u65f6\u7684\u4e00\u5957\u670d\u88c5\u4f1a\u7ed9\u516b\u5366\u5a92\u4f53\u65e0\u9650\u5236\u7684\u8d4b\u4e88\u8c61\u5f81\u610f\u4e49\uff0c\u5230\u65f6\u5019\uff0c\u6c5f\u9edb\u513f\u4e0e\u4e16\u7eaa\u534e\u97f3\u5c31\u662f\u6709\u767e\u53e3\u4e5f\u96be\u8fa9\u3002</p><p>\u3000\u3000\u674e\u6708\u8339\u6123\u5728\u90a3\u91cc\uff0c\u53e3\u5403\u7684\u6307\u7740\u5730\u4e0a\u7684T\u6064\u886b\u95ee\u5979\u4e08\u592b\uff1a\u201c\u8fd9\u2026\u2026\u8fd9\u2026\u2026\u4e0a\u9762\u5370\u7684\u662f\u5370\u5c3c\u7684\u56fd\u65d7\u8ddf\u56fd\u5fbd\uff1f\u201d\u53bb\u5e74\u5370\u5c3c\u6392\u534e\u4e8b\u4ef6\u53d1\u751f\u540e\uff0c\u516c\u53f8\u679c\u65ad\u7684\u64a4\u6d88\u4e86\u65d7\u4e0b\u827a\u4eba\u5728\u5370\u5c3c\u7684\u5531\u7247\u9500\u552e\uff0c\u5979\u8fd8\u662f\u77e5\u9053\u9634\u8c0b\u53ef\u80fd\u5bfc\u81f4\u7684\u4e25\u91cd\u540e\u679c\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u6c5f\u6d9b\u8fd9\u624d\u60f3\u5230\u8981\u8ddf\u5f20\u606a\u9053\u6b49\uff0c\u8ffd\u5230\u5316\u5986\u5ba4\u95e8\u53e3\uff0c\u5916\u9762\u7684\u7535\u68af\u95e8\u5df2\u7ecf\u5408\u4e0a\uff0c\u6570\u5b57\u663e\u793a\u7535\u68af\u5728\u4e0b\u884c\u3002</p><p>\u3000\u3000\u53e6\u4e00\u4e2a\u7535\u68af\u95e8\u6253\u5f00\uff0c\u5510\u5a67\u4e0e\u76db\u590f\u8d70\u51fa\u6765\uff0c\u770b\u89c1\u6c5f\u9edb\u513f\u5979\u7238\u5728\u95e8\u53e3\uff0c\u95ee\u9053\uff1a\u201c\u6c5f\u53d4\u53d4\uff0c\u5f20\u606a\u4ed6\u4eba\u8fc7\u6765\u4e86\uff1f\u201d</p></article>"}}}]);
