(window.webpackJsonp=window.webpackJsonp||[]).push([[1080],{1103:function(p){p.exports={chapterTitle:null,title:"\u7b2c881\u7ae0 \u6551\u707e\u8d37\u6b3e",content:"<article><h2>\u7b2c881\u7ae0 \u6551\u707e\u8d37\u6b3e</h2><p>\u3000\u3000\u665a\u6674\u5077\u5077\u6478\u6478\u7684\u79bb\u5f00\u623f\u95f4\uff0c\u5f20\u606a\u4e00\u65f6\u6ca1\u6709\u7761\u610f\uff0c\u6d17\u8fc7\u6fa1\u5750\u5728\u6c99\u53d1\u4e0a\u770b\u8d44\u6599\u3002</p><p>\u3000\u3000\u73bb\u7483\u95e8\u655e\u5f00\u7740\uff0c\u591c\u98ce\u5439\u5f97\u7eb1\u5e18\u62c2\u52a8\uff0c\u5916\u9762\u662f\u91d1\u5c5e\u96d5\u827a\u7684\u9633\u53f0\uff0c\u6708\u5149\u5982\u6c34\u5c06\u9633\u53f0\u6d78\u5728\u5176\u4e2d\u3002</p><p>\u3000\u3000\u4ece\u51b0\u7bb1\u91cc\u62ff\u4e86\u4e00\u74f6\u77ff\u6cc9\u6c34\uff0c\u5f20\u606a\u8d70\u5230\u9633\u53f0\u4e0a\uff0c\u770b\u89c1\u9694\u58c1\u7684\u623f\u95f4\u7184\u7740\u706f\uff0c\u770b\u89c1\u7fdf\u4e39\u9752\u5a77\u5a77\u7389\u7acb\u7684\u7ad9\u5728\u9694\u58c1\u7684\u9633\u53f0\u4e0a\u770b\u591c\u666f\uff0c\u5979\u7f8e\u8273\u82e5\u5239\u90a3\u82b3\u534e\u7684\u9762\u5bb9\u4e0e\u6027\u611f\u5a07\u8eaf\u85cf\u5728\u5e7d\u6697\u7684\u5f71\u5b50\u91cc\u3002</p><p>\u3000\u3000\u201c\u4f60\u6ca1\u6709\u7761\u6b63\u597d\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5230\u6211\u8fd9\u91cc\u6765\u4e00\u4e0b\uff0c\u6211\u6709\u4e8b\u8981\u8bf7\u4f60\u5e2e\u7740\u505a\u2026\u2026\u201d\u8bf4\u7f62\u8f6c\u8eab\u79bb\u5f00\u9633\u53f0\uff0c\u5c06\u623f\u95e8\u6253\u5f00\uff0c\u7b49\u7fdf\u4e39\u9752\u4ece\u9694\u58c1\u8fc7\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5728\u623f\u95f4\u7a7f\u7740\u7761\u88d9\uff0c\u6709\u4e9b\u900f\u5149\uff0c\u5173\u4e86\u706f\u624d\u6562\u7ad9\u5230\u9633\u53f0\u4e0a\u6b23\u8d4f\u591c\u666f\uff0c\u4e5f\u542c\u5230\u8c22\u665a\u6674\u7a7f\u7740\u9ad8\u8ddf\u978b\u4ece\u5f20\u606a\u623f\u95f4\u79bb\u5f00\u7684\u58f0\u97f3\u2014\u2014\u4e0d\u77e5\u9053\u5f20\u606a\u8fd9\u4e48\u665a\u6709\u4ec0\u4e48\u4e8b\u60c5\u5429\u5490\uff0c\u7fdf\u4e39\u9752\u5306\u5fd9\u6362\u4e86\u8eab\u8863\u670d\u5c31\u5230\u9694\u58c1\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u7fdf\u4e39\u9752\u7a7f\u7740\u4e00\u6761\u767d\u8272\u7684\u88e4\u5b50\uff0c\u5f88\u8584\uff0c\u81c0\u90e8\u88f9\u5f97\u7d27\u7d27\u7684\uff0c\u5728\u706f\u5149\u4e0b\uff0c\u900f\u51fa\u91cc\u9762\u7a7f\u7740\u7684\u73ab\u7470\u7ea2\u857e\u4e1d\u5185\u88e4\u6765\uff0c\u4e0a\u8eab\u7a7f\u7740\u4e00\u4ef6\u9732\u8170\u7684\u65e0\u8896\u9488\u7ec7\u886b\uff0c\u9732\u51fa\u767d\u5ae9\u7684\u809a\u8110\uff0c\u5f20\u606a\u89c1\u60ef\u7fdf\u4e39\u9752\u8fc7\u4e8e\u6b63\u7edf\u7684\u804c\u4e1a\u88c5\u675f\uff0c\u500d\u89c9\u5f97\u6b64\u65f6\u7684\u7fdf\u4e39\u9752\u989d\u5916\u7684\u6210\u719f\u6027\u611f\uff0c\u4e5f\u6ca1\u6709\u60f3\u5230\u5979\u4f1a\u7a7f\u90a3\u79cd\u80cc\u9762\u6bd4\u8f83\u7a84\u3001\u80fd\u886c\u51fa\u81c0\u5f62\u7684\u90a3\u79cd\u5185\u88e4\uff0c\u9732\u9f7f\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u201c\u8fd9\u4f1a\u513f\u6709\u4ec0\u4e48\u4e8b\u60c5\uff1f\u201d\u7fdf\u4e39\u9752\u8fd9\u624d\u89c9\u6362\u8863\u670d\u592a\u5306\u5fd9\uff0c\u88e4\u5b50\u592a\u900f\u4e86\u3001\u9488\u7ec7\u886b\u592a\u77ed\uff0c\u5f3a\u4f5c\u9547\u5b9a\u7684\u95ee\u5f20\u606a\u6709\u4ec0\u4e48\u4e8b\u60c5\u627e\u5979\u3002</p><p>\u3000\u3000\u201c\u6211\u8ddf\u665a\u6674\u59d0\u521a\u521a\u5546\u91cf\u8fc7\uff0c\u6253\u7b97\u51cf\u6301\u7231\u8fbe\u4e0e\u8d8a\u79c0\u54046\uff05\u7684\u80a1\u4efd\uff0c\u5957\u53d6\u4e9b\u8d44\u91d1\u5b58\u5165\u4e9a\u6d32\u7684\u67d0\u5bb6\u94f6\u884c\uff0c\u6362\u53d6\u8be5\u94f6\u884c\u5411\u6c5f\u5357\u7701\u5b9a\u5411\u63d0\u4f9b\u4e00\u5b9a\u91cf\u7684\u4f4e\u606f\u6551\u707e\u8d37\u6b3e\uff0c\u8fd9\u6b21\u4e8b\u60c5\uff0c\u4f60\u66ff\u6211\u5b89\u6392\u4e00\u4e0b\u2026\u2026\u201d\u5f20\u606a\u60f3\u7740\u5c3d\u53ef\u80fd\u4e3a\u5f90\u5b66\u5e73\u5c31\u4efb\u6c5f\u5357\u7701\u59d4\u4e66\u8bb0\u5236\u9020\u6210\u58f0\u52bf\uff0c\u518d\u8bf4\u6c5b\u60c5\u5df2\u7ecf\u8fc7\u53bb\uff0c\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u4e0d\u80fd\u518d\u62d6\u5ef6\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u201c\u542c\u89c1\u5927\u547c\u5c0f\u53eb\u7684\uff0c\u8fd8\u4ee5\u4e3a\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u5462\uff0c\u539f\u6765\u662f\u5728\u5546\u91cf\u8fd9\u4ef6\u4e8b\u60c5\u554a\uff01\u201d\u7fdf\u4e39\u9752\u81ea\u8a00\u81ea\u8bed\u7684\u8bf4\u4e86\u4e00\u58f0\uff0c\u89c1\u5f20\u606a\u62ac\u5934\u770b\u5979\uff0c\u5979\u53ea\u662f\u4e13\u5fc3\u7684\u62ff\u51fa\u8bb0\u4e8b\u672c\u5c06\u5f20\u606a\u7684\u8bdd\u8bb0\u4e0b\u6765\uff0c\u53c8\u786e\u8ba4\u4e86\u51e0\u4ef6\u4e8b\u60c5\uff0c\u597d\u50cf\u521a\u624d\u7684\u81ea\u8a00\u81ea\u8bed\u53ea\u662f\u5979\u5fc3\u91cc\u5728\u60f3\u8fd9\u53e5\u8bdd\u800c\u6ca1\u6709\u53d1\u51fa\u58f0\u97f3\u6765\u4f3c\u7684\u3002</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u60f3\u53ef\u80fd\u662f\u665a\u6674\u5403\u75bc\u558a\u51fa\u7684\u90a3\u4e00\u58f0\u8ba9\u7fdf\u4e39\u9752\u7ad9\u5728\u9633\u53f0\u4e0a\u542c\u89c1\u4e86\uff0c\u53ea\u6709\u88c5\u7cca\u6d82\uff0c\u5429\u5490\u4e86\u51e0\u4ef6\u4e8b\u60c5\u5c31\u8ba9\u7fdf\u4e39\u9752\u56de\u5230\u5979\u81ea\u5df1\u7684\u623f\u95f4\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u521a\u521a\u7ec4\u5efa\uff0c\u672a\u6765\u7684\u524d\u666f\u8fd8\u4e0d\u662f\u5341\u5206\u7684\u660e\u663e\uff0c\u4f46\u662f\u4f5c\u4e3a\u9526\u6e56\u65d7\u4e0b\u7684\u6838\u5fc3\u4ea7\u4e1a\u7231\u8fbe\u96c6\u56e2\u6b64\u65f6\u5728\u56fd\u5185\u6d88\u8d39\u7c7b\u7535\u5b50\u4ea7\u4e1a\u91cc\u7684\u5730\u4f4d\u662f\u65e0\u5eb8\u7f6e\u7591\u7684\uff0c\u5c31\u7b97\u4e0d\u8003\u8651\u6b64\u65f6\u7684\u884c\u4e1a\u5730\u4f4d\u4e0e\u672a\u6765\u7684\u53d1\u5c55\u524d\u666f\uff0c\u5f20\u606a\u4e0e\u665a\u6674\u8054\u5408\u51fa\u552e\u7684\u4e24\u767e\u4e07\u80a1\u624d\u4f5c\u4ef7\u4e00\u4ebf\u4e24\u5343\u4e07\u7f8e\u5143\uff0c\u4ee5\u7231\u8fbe\u96c6\u56e2\u4e5d\u516b\u5e74\u7684\u9884\u8ba1\u5e74\u51c0\u5229\u4e09\u4ebf\u7f8e\u5143\u7684\u89c4\u6a21\u6765\u8ba1\u7b97\uff0c\u5e02\u76c8\u7387\u8fd8\u4e0d\u5230\u4e03\u500d\u3002</p><p>\u3000\u3000\u8d8a\u79c0\u63a7\u80a1\u65d7\u4e0b\u6ca1\u6709\u5b9e\u4f53\u8d44\u4ea7\uff0c\u591a\u4e3a\u80a1\u6743\u6295\u8d44\uff0c\u5bf9\u7231\u8fbe\u7535\u5b50\u6301\u80a130\uff05\uff0c\u5bf9\u9999\u96ea\u6d77\u7535\u5668\u63a7\u80a151\uff05\uff0c\u5bf9\u76db\u946b\u73af\u7403\u7535\u5668\u63a7\u80a120\uff05\uff0c\u5bf9\u4e2d\u6676\u5fae\u82af\u6301\u80a151\uff05\uff0c\u8fd9\u4e9b\u80a1\u6743\u8d44\u4ea7\u5373\u4f7f\u4ee5\u6700\u4f4e\u7684\u6807\u51c6\u8ba1\u7b97\uff0c\u4ef7\u503c\u4e5f\u5728\u767e\u4ebf\u6e2f\u5143\u4ee5\u4e0a\uff0c\u665a\u6674\u8ba1\u5212\u51fa\u552e\u8d8a\u79c06\uff05\u7684\u80a1\u6743\u624d\u4f5c\u4ef7\u516b\u5343\u4e07\u7f8e\u5143\u7b97\u662f\u6781\u5176\u7684\u516c\u9053\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u6b21\u65e5\u901a\u8fc7\u4f20\u771f\u4e0e\u90ed\u677e\u5ef6\u3001\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u3001\u4f55\u7167\u594e\u7b49\u4eba\u8054\u7cfb\u8fc7\u80a1\u6743\u8f6c\u8ba9\u7684\u4e8b\u5b9c\uff0c\u5f88\u5feb\u5c31\u5f97\u5230\u56de\u5e94\uff0c\u5bf9\u9526\u6e56\u6838\u5fc3\u4ea7\u4e1a\u65e9\u5c31\u5782\u6d8e\u6b32\u6ef4\u7684\u90ed\u677e\u5ef6\u51e0\u4e4e\u5728\u4f20\u771f\u53d1\u51fa\u6765\u6ca1\u8fc7\u51e0\u5206\u949f\u5c31\u76f4\u63a5\u6253\u901a\u5f20\u606a\u7684\u7535\u8bdd\uff1a\u201c\u624d6\uff05\uff0c\u662f\u4e0d\u662f\u592a\u5c11\u4e86\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u4ece\u542f\u52a8\u4e2d\u6676\u5fae\u82af\u9879\u76ee\u8d77\u6765\uff0c\u90ed\u6c0f\u4e91\u6e90\u96c6\u56e2\u5c31\u4e0e\u9526\u6e56\u6709\u7740\u5bc6\u5207\u7684\u5408\u4f5c\uff0c\u90ed\u677e\u5ef6\u5bf9\u9526\u6e56\u7684\u60c5\u51b5\u76f8\u5f53\u4e86\u89e3\uff0c\u4e0d\u8981\u8bf4\u8fd96\uff05\u662f\u62ff\u51fa\u6765\u7ed9\u4ed6\u4eec\u5927\u5bb6\u5206\u7684\uff0c\u5c31\u662f\u7ed9\u90ed\u6c0f\u4e00\u5bb6\uff0c\u90ed\u677e\u5ef6\u4e5f\u89c9\u5f97\u592a\u5c11\u4e86\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u9526\u6e56\u662f\u5e0c\u671b\u4e0e\u4e91\u6e90\u3001\u4e0e\u4e07\u4e1a\u3001\u4e0e\u5357\u6d0b\u7b49\u4f01\u4e1a\u53d1\u5c55\u66f4\u5bc6\u5207\u7684\u5173\u7cfb\uff0c\u4f1a\u8ba9\u5408\u4f5c\u4f19\u4f34\u4e5f\u6709\u673a\u4f1a\u5206\u4eab\u9526\u6e56\u7684\u6838\u5fc3\u4e1a\u52a1\u5229\u6da6\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8ddf\u90ed\u677e\u5ef6\u5728\u7535\u8bdd\u91cc\u89e3\u91ca\uff1a\u201c\u8fd9\u6b21\u80a1\u6743\u8f6c\u8ba9\uff0c\u83b7\u5f97\u7684\u8d44\u91d1\uff0c\u6070\u597d\u4e5f\u80fd\u5f25\u8865\u4e00\u7b14\u9884\u7b97\u5916\u8d44\u91d1\u5b89\u6392\u53ef\u80fd\u5bf9\u9526\u6e56\u9020\u6210\u7684\u8d44\u91d1\u7f3a\u53e3\u2026\u2026\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u542c\u7740\u5f20\u606a\u5728\u90a3\u91cc\u8ddf\u90ed\u677e\u5ef6\u5728\u7535\u8bdd\u91cc\u80e1\u626f\uff0c\u6123\u662f\u80fd\u5c06\u56e0\u679c\u98a0\u5012\u8fc7\u6765\u8bf4\u4e86\u5706\u6ed1\u81ea\u5982\uff0c\u6447\u7740\u5934\u5fae\u5fae\u7b11\u7740\uff0c\u5fc3\u91cc\u60f3\uff1a\u4ee5\u540e\u8981\u662f\u76f8\u4fe1\u8fd9\u5c0f\u5b50\u7684\u8bdd\u771f\u662f\u8ba9\u9b3c\u8ff7\u4e86\u5fc3\u7a8d\uff0c\u53c8\u89c1\u5f20\u606a\u8df7\u8d77\u4e8c\u90ce\u817f\u3001\u7a7f\u7740\u62d6\u978b\u7684\u811a\u677f\u5b50\u8fd8\u5728\u90a3\u91cc\u4e00\u98a4\u4e00\u98a4\u7684\u8ddf\u90ed\u677e\u5ef6\u5728\u7535\u8bdd\u4e71\u626f\uff1a\u201c\u8fd9\u4e00\u6b21\u7231\u8fbe\u3001\u8d8a\u79c0\u8ba1\u5212\u5404\u62ff6\uff05\u51fa\u6765\uff0c\u4ee5\u540e\u8fd8\u4f1a\u6709\u8fd9\u6837\u7684\u5b89\u6392\uff0c\u5173\u952e\u8fd8\u662f\u8981\u5927\u5bb6\u5bf9\u9526\u6e56\u6709\u4fe1\u5fc3\u624d\u884c\u2014\u2014\u8981\u662f\u4e07\u4e1a\u3001\u5357\u6d0b\u4ed6\u4eec\u8fd9\u6b21\u90fd\u9009\u62e9\u653e\u5f03\uff0c\u4e91\u6e90\u53c8\u613f\u610f\u72ec\u7acb\u63a5\u53d76\uff05\u6bd4\u4f8b\u7684\u80a1\u6743\u8f6c\u8ba9\uff0c\u6211\u4e5f\u662f\u5341\u5206\u611f\u8c22\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u4e0e\u90ed\u677e\u5ef6\u7ed3\u675f\u901a\u8bdd\u4e0d\u4e45\uff0c\u9648\u6587\u806a\u3001\u9a6c\u6587\u6e20\u3001\u4f55\u7167\u594e\u90fd\u5f88\u5feb\u6253\u6765\u7535\u8bdd\uff0c\u8868\u793a\u4f1a\u4ee5\u6700\u5feb\u7684\u901f\u5ea6\u5c06\u94b1\u6b3e\u6253\u9526\u6e56\u5728\u9999\u6e2f\u7684\u8d26\u6237\u4e0a\u3002</p><p>\u3000\u3000\u5370\u5c3c\u4e07\u4e1a\u96c6\u56e2\u7684\u9648\u6587\u806a\u9648\u6c0f\u6700\u9f0e\u76db\u65f6\u5bb6\u65cf\u8d44\u4ea7\u8d85\u8fc7\u4e09\u5341\u4e94\u4ebf\u7f8e\u5143\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u6765\u88ad\uff0c\u5bb6\u65cf\u8d44\u4ea7\u7f29\u6c34\u4e25\u91cd\uff0c\u53c8\u7ecf\u8fc7\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\uff0c\u4f46\u662f\u80fd\u8f6c\u79fb\u51fa\u6765\u7684\u5bb6\u65cf\u8d44\u4ea7\u89c4\u6a21\u4ecd\u5728\u5341\u4ebf\u7f8e\u5143\u4ee5\u4e0a\u3002</p><p>\u3000\u3000\u9a6c\u6587\u6e20\u9a6c\u6c0f\u5bb6\u65cf\u62e5\u6709\u8d44\u4ea7\u4e3b\u8981\u90fd\u96c6\u4e2d\u5728\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u4e4b\u4e2d\uff0c\u867d\u8bf4\u53d7\u5230\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u7684\u6253\u51fb\uff0c\u6bd5\u7adf\u53d7\u5370\u5c3c\u6392\u534e\u9a9a\u4e71\u7684\u5f71\u54cd\u4e0d\u5927\uff0c\u9700\u8981\u4ece\u5370\u5c3c\u8f6c\u79fb\u51fa\u6765\u7684\u5bb6\u65cf\u8d44\u4ea7\u89c4\u6a21\u751a\u81f3\u4e0d\u8db3\u4e09\u4ebf\u7f8e\u5143\u2014\u2014\u5357\u6d0b\u6d77\u8fd0\u96c6\u56e2\u6839\u57fa\u672a\u635f\uff0c\u9a6c\u6c0f\u751a\u81f3\u5c31\u53ef\u4ee5\u51ed\u501f\u5357\u6d0b\u6d77\u8fd0\u7684\u5b9e\u4f53\u4ee5\u53ca\u9a6c\u5bb6\u5728\u5370\u5c3c\u534e\u5546\u4e2d\u7684\u58f0\u8a89\u76f4\u63a5\u4ece\u5370\u5c3c\u5438\u7eb3\u534e\u4eba\u8d44\u672c\u3002</p><p>\u3000\u3000\u4f55\u7167\u594e\u4f55\u6c0f\u5bb6\u65cf\u8d44\u4ea7\u89c4\u6a21\u6bd4\u9648\u5bb6\u3001\u9a6c\u5bb6\u7565\u5c11\uff0c\u4f46\u662f\u80fd\u8f6c\u79fb\u51fa\u6765\u7684\u8d44\u4ea7\u4e5f\u6709\u4e94\u516d\u4ebf\u7f8e\u5143\u3002</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u90ed\u5bb6\u662f\u9a6c\u6765\u897f\u4e9a\u9996\u5bcc\uff0c\u4e9a\u6d32\u91d1\u878d\u98ce\u66b4\u5bf9\u9a6c\u4e9a\u897f\u4e9a\u7ecf\u6d4e\u6467\u6b8b\u6700\u4e25\u91cd\u65f6\uff0c\u90ed\u6c0f\u5bb6\u65cf\u8d44\u4ea7\u4e5f\u6ca1\u6709\u4f4e\u4e8e30\u4ebf\u7f8e\u5143\uff0c\u4e0d\u8fc7\u9a6c\u6765\u897f\u4e9a\u653f\u6cbb\u7a33\u5b9a\uff0c\u534e\u4eba\u7684\u653f\u6cbb\u5730\u4f4d\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u662f\u751f\u547d\u8d22\u4ea7\u5012\u6ca1\u6709\u5904\u4e8e\u5c8c\u5c8c\u53ef\u5371\u7684\u9669\u5730\uff0c\u90ed\u5bb6\u7684\u8d44\u4ea7\u4e0d\u9700\u8981\u6025\u4e8e\u9003\u79bb\u9a6c\u6765\u897f\u4e9a\uff0c\u53ea\u4e0d\u8fc7\u90ed\u677e\u5ef6\u4ed6\u672c\u4eba\u65e5\u76ca\u91cd\u89c6\u4e2d\u56fd\u5e02\u573a\uff0c\u8fd1\u5e74\u6765\u90fd\u5c3d\u53ef\u80fd\u7684\u52a0\u5927\u5728\u56fd\u5185\u7684\u6295\u8d44\u6bd4\u91cd\uff0c\u4e5f\u6709\u4f5c\u4e3a\u534e\u4eba\u53f6\u843d\u5f52\u6839\u7684\u5fc3\u601d\u5728\u5176\u4e2d\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u6b21\u8f6c\u8ba9\u51fa\u6765\u7684\u80a1\u4efd\uff0c\u5bf9\u4e8e\u9648\u3001\u9a6c\u3001\u4f55\u3001\u90ed\u56db\u5bb6\u6765\u8bf4\uff0c\u771f\u662f\u592a\u5c11\u4e86\u3002</p><p>\u3000\u3000\u81f3\u4e8e\u4f4e\u606f\u6551\u707e\u8d37\u6b3e\u7684\u95ee\u9898\uff0c\u90ed\u677e\u5ef6\u5f97\u77e5\u540e\uff0c\u7acb\u5373\u63a8\u8350\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u3002</p><p>\u3000\u3000\u867d\u8bf4\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u8fd8\u672a\u66fe\u6709\u8fc7\u5bf9\u5185\u5730\u7684\u653e\u8d37\u4e1a\u52a1\uff0c\u4f46\u6709\u5f20\u606a\u4e0e\u8c22\u665a\u6674\u8054\u5408\u5b58\u5165\u76f8\u5e94\u7684\u5de8\u989d\u6b3e\u9879\u4e0d\u81f3\u4e8e\u6253\u4e71\u65b0\u52a0\u5761\u534e\u5546\u94f6\u884c\u7684\u653e\u8d37\u8ba1\u5212\uff0c\u800c\u5185\u5730\u7701\u7ea7\u653f\u5e9c\u7684\u62c5\u4fdd\u4fe1\u8a89\u53c8\u7d20\u6765\u5728\u4e1c\u5357\u4e9a\u91d1\u878d\u501f\u8d37\u5e02\u573a\u5f88\u662f\u5f97\u5230\u4fe1\u4efb\uff0c\u90ed\u677e\u5ef6\u5728\u4e86\u89e3\u5f20\u606a\u6b64\u6b21\u5957\u73b0\u7684\u771f\u5b9e\u610f\u56fe\u4e4b\u540e\uff0c\u5c31\u4e3b\u52a8\u63d0\u51fa\u4ed6\u4e13\u7a0b\u524d\u5f80\u65b0\u52a0\u5761\u6765\u4fc3\u6210\u6b64\u4e8b\u3002</p><p>\u3000\u3000\u5f53\u665a\uff0c\u90ed\u677e\u5ef6\u5c31\u5728\u65b0\u52a0\u5761\u6253\u6765\u8d8a\u6d0b\u7535\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4e0e\u534e\u5546\u94f6\u884c\u8463\u4e8b\u5c40\u4e3b\u5e2d\u90b1\u8000\u7956\u5148\u89c1\u8fc7\u9762\uff0c\u4ed6\u5f88\u4e50\u610f\u4fc3\u6210\u6b64\u4e8b\uff0c\u53e6\u5916\u534e\u5546\u94f6\u884c\u5c06\u5411\u6c5f\u5357\u707e\u533a\u6350\u8d6020\u4e07\u65b0\u5143\u804a\u8868\u6170\u95ee\u4e4b\u60c5\uff1b\u8003\u8651\u5230\u5185\u5730\u707e\u540e\u91cd\u5efa\u5de5\u4f5c\u7e41\u91cd\uff0c\u4ed6\u4f1a\u6d3e\u4e13\u5458\u5230\u91d1\u5c71\u6765\u4e0e\u6709\u5173\u90e8\u95e8\u7684\u5b98\u5458\u8054\u7cfb\u2026\u2026\u201d</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u5728\u9a6c\u6765\u897f\u4e9a\u4e0e\u65b0\u52a0\u5761\u7684\u4eba\u671b\u6781\u9ad8\uff0c\u4e0d\u8fc7\u534e\u5546\u94f6\u884c\u5982\u6b64\u70ed\u6795\uff0c\u4e5f\u51fa\u4e4e\u5f20\u606a\u7684\u610f\u6599\uff0c\u5012\u662f\u7fdf\u4e39\u9752\u4e00\u9488\u89c1\u8840\uff0c\u8bf4\u9053\uff1b\u201c\u8be5\u4e0d\u4f1a\u534e\u5546\u94f6\u884c\u5728\u627f\u63fd\u9526\u6e56\u5546\u4e8b\u5728\u4e1c\u5357\u4e9a\u7684\u4e1a\u52a1\uff1f\u201d</p><p>\u3000\u3000\u5728\u65b0\u52a0\u5761\u5f39\u4e38\u4e4b\u5730\uff0c\u534e\u5546\u94f6\u884c\u53ea\u662f\u6392\u540d\u7b2c\u56db\u7684\u5546\u4e1a\u94f6\u884c\uff0c\u9526\u6e56\u5546\u4e8b\u5728\u4e1c\u5357\u4e9a\u7684\u91d1\u878d\u4e1a\u52a1\uff0c\u7684\u786e\u4e0d\u4f1a\u60f3\u7740\u8981\u4e0e\u534e\u5546\u94f6\u884c\u5408\u4f5c\uff0c\u5f20\u606a\u5fc3\u91cc\u5374\u60f3\u7740\uff0c\u4e0d\u7ba1\u534e\u5546\u94f6\u884c\u6253\u7740\u4ec0\u4e48\u4e3b\u610f\uff0c\u603b\u4e4b\u5148\u5c06\u773c\u524d\u7684\u4e8b\u60c5\u4fc3\u6210\u5c31\u884c\u4e86\uff1b\u4ee5\u540e\u8981\u4e0d\u8981\u5408\u4f5c\uff0c\u90a3\u662f\u4ee5\u540e\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7701\u7ea7\u8d22\u653f\u4e3a\u7701\u56fd\u6295\u6848\u80cc\u4e86\u8fd1\u767e\u4ebf\u7684\u7a9f\u7abf\uff0c\u8fd9\u6b21\u4e0d\u4ec5\u8981\u8d22\u653f\u4e0a\u62d4\u51fa\u5de8\u6b3e\u6765\u8fdb\u884c\u6551\u707e\uff0c\u8fd8\u56e0\u4e3a\u53d7\u707e\uff0c\u7a0e\u6e90\u8fd8\u53d7\u5230\u4e25\u91cd\u7684\u5f71\u54cd\uff0c\u8fd9\u79cd\u79cd\u60c5\u51b5\u52a0\u5728\u4e00\u8d77\uff0c\u8ba9\u672c\u6765\u5c31\u6349\u895f\u89c1\u8098\u7684\u7701\u8d22\u653f\u66f4\u52a0\u7684\u96ea\u4e0a\u52a0\u971c\u3002</p><p>\u3000\u3000\u201c\u4e24\u4ebf\u7f8e\u5143\u7684\u6551\u707e\u8d37\u6b3e\u6216\u8bb8\u8fd8\u89e3\u51b3\u4e0d\u4e86\u6839\u672c\u7684\u95ee\u9898\uff0c\u4f46\u4e5f\u80fd\u8ba9\u7701\u91cc\u5148\u7f13\u4e00\u53e3\u6c14\u2026\u2026\u201d</p><p>\u3000\u300028\u65e5\uff0c\u5f20\u606a\u51b3\u5b9a\u5728\u79bb\u5f00\u91d1\u5c71\u7684\u524d\u4e00\u591c\uff0c\u7167\u4f8b\u5230\u5f90\u5b66\u5e73\u5bb6\u91cc\u966a\u665a\u6674\u3001\u82b7\u5f64\u8fd8\u6709\u5468\u6dd1\u60e0\u4e00\u8d77\u7528\u9910\uff0c\u4e0d\u8fc7\u7b49\u5f90\u5b66\u5e73\u56de\u5230\u5bb6\u91cc\u5374\u662f\u5728\u51cc\u6668\u4e00\u70b9\u949f\uff0c\u4ed6\u5c06\u4e24\u4ebf\u7f8e\u5143\u6551\u707e\u8d37\u6b3e\u7684\u4e8b\u60c5\u8bf4\u7ed9\u5f90\u5b66\u5e73\u8bf4\u3002</p><p>\u3000\u3000\u201c\u662f\u5417\uff1f\u201d\u5f90\u5b66\u5e73\u96be\u5f97\u7684\u7709\u5934\u8212\u5c55\u5f00\u6765\uff0c\u201c\u4f60\u5c0f\u5b50\u53ef\u662f\u4e8b\u524d\u4e00\u70b9\u90fd\u6ca1\u6709\u900f\u9732\u98ce\u58f0\u554a\uff01\u201d</p><p>\u3000\u3000\u201c\u4e5f\u5c31\u8fd9\u4e24\u5929\u7684\u4e8b\u60c5\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u524d\u5929\u665a\u6674\u59d0\u627e\u6211\u5546\u91cf\u8bf4\u8981\u4f1a\u62ff\u51fa\u5728\u9526\u6e56\u7684\u4e00\u90e8\u5206\u80a1\u6743\u5957\u73b0\uff0c\u6211\u8fd9\u4e24\u5929\u624d\u5c06\u4e8b\u60c5\u5927\u81f4\u7684\u8c08\u4e86\u4e0b\u6765\u2014\u2014\u5957\u51fa\u7684\u73b0\u91d1\u8f6c\u5b58\u8fdb\u65b0\u52a0\u5761\u7684\u534e\u5546\u94f6\u884c\uff0c\u7531\u534e\u5546\u94f6\u884c\u5411\u7701\u91cc\u63d0\u4f9b\u4e24\u4ebf\u7f8e\u5143\u7684\u8d37\u6b3e\u2014\u2014\u53ea\u8981\u6211\u4eec\u8fd9\u8fb9\u4e0d\u53d8\u5366\uff0c\u8d37\u6b3e\u7684\u4e8b\u60c5\u5c31\u80fd\u843d\u5b9e\u4e0b\u6765\uff0c\u7701\u91cc\u53ef\u4ee5\u5c06\u8fd9\u7b14\u94b1\u8003\u8651\u5230\u6c42\u707e\u8ba1\u5212\u4e2d\u53bb\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5f90\u5b66\u5e73\u8f7b\u5410\u4e86\u4e00\u53e3\u6c14\uff0c\u77e5\u9053\u5f20\u606a\u4e3a\u4ec0\u4e48\u8981\u5c06\u8fd9\u4ef6\u4e8b\u641e\u8fd9\u4e48\u590d\u6742\uff0c\u53c8\u7b11\u4e86\u7b11\uff0c\u201c\u8fd8\u662f\u4f60\u6709\u529e\u6cd5\uff0c\u8fd9\u4e48\u590d\u6742\u7684\u4e8b\u60c5\uff0c\u4e24\u5929\u7684\u65f6\u95f4\u5c31\u7406\u987a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u4e5f\u4e0d\u662f\u6211\u6709\u529e\u6cd5\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u5185\u5730\u7684\u53d7\u707e\u7275\u52a8\u4e1c\u5357\u4e9a\u7684\u534e\u4eba\u534e\u4fa8\u7684\u5fc3\uff0c\u4e3a\u4e86\u8fd9\u4e8b\uff0c\u90ed\u677e\u5ef6\u5148\u751f\u5c06\u4ed6\u5728\u9a6c\u6765\u897f\u4e9a\u7684\u4e8b\u52a1\u90fd\u6682\u65f6\u4e22\u5230\u4e00\u8fb9\uff0c\u4e13\u7a0b\u8d76\u5230\u65b0\u52a0\u5761\u53bb\u8c08\u8fd9\u4e8b\u2014\u2014\u534e\u5546\u94f6\u884c\u8fd9\u6b21\u4e5f\u662f\u7279\u4e8b\u7279\u529e\u3002\u201d</p></article>"}}}]);
