(window.webpackJsonp=window.webpackJsonp||[]).push([[1082],{1105:function(p){p.exports={chapterTitle:null,title:"\u7b2c883\u7ae0 \u5b59\u9759\u6aac\u8981\u5212\u6e05\u754c\u9650",content:"<article><h2>\u7b2c883\u7ae0 \u5b59\u9759\u6aac\u8981\u5212\u6e05\u754c\u9650</h2><p>\u3000\u3000\u4e1c\u5927\u4e5d\u6708\u4e00\u65e5\u5f00\u5b66\uff0c\u5f20\u606a\u62d6\u5230\u4e09\u5341\u4e00\u65e5\u624d\u8fd4\u56de\u5efa\u90ba\uff0c\u4e0d\u8fc7\u5728\u4ed6\u56de\u5efa\u90ba\u7684\u8f66\u4e0a\u63a5\u5230\u4e00\u4e2a\u964c\u751f\u7684\u7535\u8bdd\uff0c\u63a5\u901a\u7535\u8bdd\uff0c\u8c22\u5b50\u5609\u7684\u6e05\u8106\u67d4\u5ae9\u58f0\u97f3\u4ece\u91cc\u9762\u4f20\u51fa\u6765\uff1a\u201c\u6211\u662f\u8c22\u5b50\u5609\uff0c\u4f60\u6709\u6ca1\u6709\u56de\u5b66\u6821\u5462\uff1f\u201d\u542c\u5979\u7684\u58f0\u97f3\u6c38\u8fdc\u90fd\u4e0d\u4f1a\u4e0e\u5979\u4e56\u623e\u7684\u6027\u5b50\u8054\u7cfb\u8d77\u6765\uff0c\u591a\u534a\u4f1a\u60f3\u62e5\u6709\u8fd9\u4e48\u67d4\u5ae9\u6e05\u8106\u7684\u58f0\u97f3\uff0c\u4e00\u5b9a\u662f\u4e2a\u96ea\u5ae9\u660e\u4e3d\u7684\u5a07\u61a8\u5c11\u5973\uff0c\u5f53\u7136\uff0c\u8c22\u5b50\u5609\u7684\u5916\u8868\u4e5f\u662f\u660e\u4e3d\u6e05\u79c0\uff0c\u53ea\u4e0d\u8fc7\u7a0d\u4e0e\u5979\u63a5\u89e6\u6df1\u4e00\u4e9b\uff0c\u5c31\u77e5\u9053\u5979\u7684\u58f0\u97f3\u4e0e\u5916\u5f62\u7edd\u5bf9\u662f\u8ff7\u60d1\u4eba\u7684\u9677\u9631\u3002</p><p>\u3000\u3000\u201c\u6211\u5728\u56de\u5efa\u90ba\u7684\u8def\u4e0a\uff0c\u4f60\u600e\u4e48\u4f1a\u6253\u6211\u7684\u7535\u8bdd\uff1f\u201d\u5f20\u606a\u5947\u602a\u7684\u95ee\u3002</p><p>\u3000\u3000\u201c\u4f60\u5927\u6982\u662f\u5947\u602a\u6211\u600e\u4e48\u4f1a\u77e5\u9053\u4f60\u7684\u7535\u8bdd\u5427\uff1f\u968f\u4fbf\u7ffb\u5f00\u9648\u9759\u7684\u624b\u673a\u5c31\u662f\u4e86\uff0c\u5373\u4f7f\u9648\u9759\u4e3a\u4ec0\u4e48\u4f1a\u6709\u4f60\u7684\u7535\u8bdd\uff0c\u6211\u5c31\u4e0d\u6e05\u695a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u542c\u89c1\u7535\u8bdd\u90a3\u5934\u6709\u5b09\u7b11\u58f0\uff0c\u5f20\u606a\u5bf9\u5973\u4eba\u7684\u58f0\u97f3\u5f88\u654f\u611f\uff0c\u542c\u51fa\u662f\u9648\u9759\u7684\u58f0\u97f3\uff0c\u60f3\u7740\u9ad8\u901f\u516c\u8def\u4e0a\u521d\u89c1\u7684\u90a3\u4e2a\u5f20\u626c\u6027\u611f\u7684\u5973\u4eba\u6765\uff0c\u4e4b\u540e\u9648\u9759\u7ed9\u4ed6\u7684\u5370\u8c61\u5374\u90fd\u662f\u5a34\u9759\u5178\u96c5\u7684\u77e5\u6027\u7f8e\u66f4\u591a\u4e00\u4e9b\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u606a\u5c31\u542c\u7740\u7535\u8bdd\u90a3\u5934\u7684\u5b09\u7b11\u58f0\uff0c\u7fdf\u4e39\u9752\u7591\u60d1\u7684\u770b\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u89c1\u4ed6\u5634\u89d2\u542b\u7740\u7b11\uff0c\u4e5f\u6ca1\u6709\u591a\u95ee\u3002</p><p>\u3000\u3000\u201c\u6211\u54e5\u60f3\u4e0e\u4f60\u89c1\u4e00\u9762\uff0c\u4e0d\u77e5\u9053\u4f60\u80fd\u5426\u62d4\u5197\uff1f\u201d\u8c22\u5b50\u5609\u7684\u58f0\u97f3\u53c8\u4e00\u672c\u6b63\u7ecf\u7684\u4f20\u8fc7\u6765\u3002</p><p>\u3000\u3000\u5f20\u606a\u8111\u5b50\u505c\u987f\u4e86\u4e24\u79d2\u949f\uff0c\u624d\u60f3\u8d77\u6765\u662f\u8c22\u610f\u8981\u89c1\u4ed6\uff0c\u8981\u4e0d\u662f\u9526\u6e56\u6a2a\u63d2\u4e00\u811a\u5728\u4e00\u5b9a\u7a0b\u5ea6\u4e0a\u626d\u9006\u4e86\u5386\u53f2\u7684\u8f68\u8ff9\uff0c\u8c22\u5bb6\u4eba\u6240\u5949\u884c\u7684\u5904\u4e16\u51c6\u5219\u4f1a\u8ba9\u4ed6\u4eec\u5982\u9c7c\u5f97\u6c34\uff0c\u5728\u4e5d\u5341\u5e74\u4ee3\u4e2d\u540e\u671f\u4ee5\u53ca\u4e8c\u5341\u4e00\u4e16\u7eaa\u6700\u521d\u7684\u51e0\u5e74\u65f6\u95f4\u91cc\uff0c\u6b63\u6cf0\u96c6\u56e2\u4f1a\u53d1\u5c55\u6210\u4e3a\u4e1c\u6d77\u6700\u5177\u5b9e\u529b\u7684\u5bb6\u65cf\u96c6\u56e2\uff0c\u800c\u8c22\u610f\u3001\u8c22\u5251\u5357\u3001\u8c22\u5b50\u5609\u5c06\u6210\u4e3a\u4e1c\u6d77\u5546\u754c\u6700\u8000\u773c\u7684\u660e\u661f\uff0c\u800c\u90a3\u65f6\u7684\u81ea\u5df1\u4e0d\u8fc7\u662f\u4ef0\u89c6\u4ed6\u4eec\u7684\u82b8\u82b8\u4f17\u751f\u4e2d\u4e00\u5458\u7f62\u4e86\u3002\u80fd\u8ba9\u8c22\u610f\u4f4e\u4e0b\u5934\u6765\u89c1\u81ea\u5df1\uff0c\u5f20\u606a\u5fc3\u91cc\u5012\u662f\u6709\u4e00\u79cd\u96be\u4ee5\u8ddf\u4eba\u8a00\u660e\u7684\u6ee1\u8db3\u611f\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u4ece\u91d1\u5c71\u56de\u5efa\u90ba\u7684\u8def\u51b5\u5f88\u5dee\uff0c\u88ab\u6d2a\u6c34\u6df9\u6ca1\u8fc7\uff0c\u6709\u4e9b\u7684\u8def\u9762\u574d\u584c\u4e86\u8bb8\u591a\u8fd8\u6ca1\u6709\u6765\u5f97\u53ca\u4fee\u8865\uff0c\u53ea\u5269\u4e0b\u72ed\u7a84\u7684\u8f66\u9053\u4f9b\u6765\u5f80\u7684\u8f66\u8f86\u901a\u884c\uff0c\u5f80\u5e38\u53ea\u9700\u8981\u56db\u4e2a\u5c0f\u65f6\u7684\u8f66\u7a0b\uff0c\u8db3\u591f\u82b1\u4e86\u516d\u4e2a\u5c0f\u65f6\uff0c\u76f4\u5230\u5929\u591c\u4e4b\u540e\u624d\u56de\u5230\u5b66\u5e9c\u5df7\u3002</p><p>\u3000\u3000\u5f20\u606a\u5728\u5b66\u5e9c\u5df7\u66f4\u611f\u53d7\u5230\u66f4\u6df1\u7684\u5bb6\u7684\u611f\u89c9\uff0c\u8981\u662f\u53ef\u4ee5\uff0c\u4ed6\u5b81\u53ef\u6574\u5929\u90fd\u8d56\u5728\u5b66\u5e9c\u5df7\u91cc\u3002</p><p>\u3000\u3000\u5b66\u5e9c\u5df7\u7684\u534e\u706f\u5df2\u4e0a\uff0c\u5b59\u9759\u6aac\u7a7f\u7740\u7c89\u7ea2\u8272\u7684\u8fde\u8863\u88d9\uff0c\u4ece\u9886\u53e3\u5230\u88d9\u5e26\u6709\u4e00\u6392\u94dc\u6263\uff0c\u624b\u91cc\u62ff\u7740\u7cbe\u81f4\u7684\u624b\u888b\uff0c\u7cbe\u81f4\u7f8e\u4e3d\u7684\u8138\u4e0a\u6ca1\u6709\u4efb\u4f55\u5986\uff0c\u660e\u5a9a\u53d1\u4eae\u7684\u9ed1\u773c\u7738\u5728\u8def\u706f\u5149\u4e0b\u683c\u5916\u7684\u7f8e\u4e3d\u3002\u6574\u4e2a\u5047\u671f\u90fd\u672a\u66fe\u89c1\u7684\u5b59\u9759\u6aac\u5c31\u50cf\u53ea\u5996\u7cbe\u4f3c\u7684\u7ad9\u5728\u8def\u706f\u67f1\u4e0b\u3002</p><p>\u3000\u3000\u201c\u8def\u4e0a\u987a\u5229\u5417\uff1f\u201d\u5b59\u9759\u6aac\u6de1\u6de1\u7684\u95ee\u4e86\u4e00\u53e5\uff0c\u4f3c\u4e4e\u5fd8\u8bb0\u5728\u5047\u671f\u4e4b\u95f4\u7684\u96f7\u96e8\u591c\uff0c\u5979\u8d64\u8eab\u88f8\u4f53\u7684\u7ed9\u5f20\u606a\u770b\u5149\u8fc7\u3002</p><p>\u3000\u3000\u201c\u8fd8\u597d\uff0c\u6709\u4e9b\u5835\u8f66\uff0c\u65e0\u975e\u662f\u5728\u8f66\u91cc\u7761\u89c9\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u627e\u5730\u65b9\u4e00\u8d77\u5403\u996d\u5427\uff0c\u665a\u4e0a\u8fd8\u7ea6\u4e86\u4eba\u57281978\u89c1\u9762\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u59ae\u5b50\u543b\u4e86\u80fd\u591f\u5f53\u4f5c\u6ca1\u543b\uff0c\u6478\u4e86\u53ef\u4ee5\u5f53\u4f5c\u6ca1\u6478\u8fc7\uff0c\u5f20\u606a\u53ef\u731c\u4e0d\u900f\u5979\u5fc3\u91cc\u5728\u60f3\u4ec0\u4e48\uff0c\u4e5f\u4e0d\u6562\u8ddf\u5979\u592a\u70ed\u7edc\uff0c\u53ea\u662f\u8d70\u8def\u6328\u8fd1\u4e86\u4e9b\uff0c\u95fb\u7740\u5979\u7684\u53d1\u68a2\u6709\u7740\u6de1\u6de1\u7684\u8584\u8377\u9999\u5473\u3002</p><p>\u3000\u3000\u5728\u5916\u8857\u627e\u4e86\u4e00\u5bb6\u88c5\u6f62\u7cbe\u81f4\u7684\u9910\u5385\uff0c\u73bb\u7483\u7a97\u6237\u7ed9\u68a7\u6850\u679d\u53f6\u906e\u4f4f\uff0c\u679d\u53f6\u7adf\u7136\u6709\u53ea\u9e1f\u5de2\uff0c\u8fd8\u6709\u4e24\u53ea\u5ae9\u9ec4\u5634\u4e2b\u7684\u5e7c\u9e1f\u5728\u5de2\u91cc\uff0c\u5f20\u606a\u6307\u9759\u6aac\u770b\u3002\u9759\u6aac\u5174\u594b\u7684\u63a2\u8fc7\u8eab\u5b50\u8db4\u5728\u7a97\u6237\u770b\uff0c\u80f8\u53e3\u78b0\u5230\u5f20\u606a\u7684\u624b\u81c2\uff0c\u8fd8\u4fa7\u8fc7\u5934\u77aa\u4e86\u4ed6\u4e00\u773c\uff0c\u597d\u50cf\u662f\u5f20\u606a\u6545\u610f\u5360\u5979\u4fbf\u5b9c\u4f3c\u7684\uff1a\u201c\u4f60\u5750\u5916\u9762\u6765\u2014\u2014\u6211\u4ece\u6765\u90fd\u6ca1\u6709\u770b\u5230\u8fc7\u9e1f\u7a9d\uff0c\u8fd9\u662f\u4ec0\u4e48\u9e1f\uff1f\u201d\u5374\u662f\u5728\u6362\u5ea7\u4f4d\u7684\u65f6\u5019\uff0c\u5728\u684c\u4e0b\u4f38\u624b\u6293\u4f4f\u5f20\u606a\u7684\u624b\u5fc3\u634f\u4e86\u4e00\u4e0b\u3002</p><p>\u3000\u3000\u4e0e\u8c22\u5b50\u5609\u5979\u4eec\u7ea6\u597d\u57281978\u89c1\u9762\uff0c\u7fdf\u4e39\u9752\u8eab\u4e3a\u52a9\u7406\uff0c\u8981\u66ff\u5f20\u606a\u5904\u7406\u7684\u7410\u788e\u4e8b\u52a1\u4e00\u5927\u5806\uff0c\u7528\u8fc7\u9910\u5979\u5c31\u5148\u56de\u516c\u5bd3\u4e86\uff0c\u5f20\u606a\u5c31\u4e0e\u9759\u6aac\u5f801978\u8d70\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u89c9\u5f97\u6211\u8fd8\u662f\u4e0d\u8981\u559c\u6b22\u4f60\u7684\u597d\uff0c\u201d\u5b59\u9759\u6aac\u770b\u7740\u8def\u706f\u4e0b\u7684\u9ed1\u5f71\uff0c\u201c\u6211\u53ef\u4e0d\u60f3\u6211\u7238\u7238\u7ed9\u6d3b\u6d3b\u6c14\u6b7b\uff0c\u6211\u59d0\u5df2\u7ecf\u662f\u90a3\u6837\u7684\uff0c\u6211\u518d\u90a3\u6837\u2026\u2026\u90a3\u6837\u7edd\u5bf9\u4e0d\u884c\uff0c\u4f60\u77e5\u4e0d\u77e5\u9053\uff1f\u201d</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u5f20\u606a\u6123\u4e86\u6123\uff0c\u4fa7\u8fc7\u5934\u770b\u7740\u8def\u706f\u5149\u4e0b\u5c0f\u5fc3\u7ffc\u7ffc\u8bf4\u8bdd\u7684\u5b59\u9759\u6aac\uff0c\u5ae3\u7ea2\u7684\u5634\u5507\u5fae\u5fae\u5658\u7740\uff0c\u201c\u6211\u5c31\u8fd9\u6837\u7ed9\u4f60\u7529\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u7529\u4f60\u4e2a\u9b3c\uff0c\u201d\u5b59\u9759\u6aac\u77aa\u4e86\u5f20\u606a\u4e00\u773c\uff0c\u795e\u60c5\u53c8\u6062\u590d\u4e86\u51e0\u5206\u56a3\u5f20\uff0c\u201c\u53ea\u662f\u6b63\u5f0f\u7684\u8b66\u544a\u4f60\u4e0d\u8981\u52fe\u5f15\u6211\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u8111\u5b50\u50f5\u5728\u90a3\u91cc\uff0c\u6709\u4e9b\u6ede\u969c\uff1a\u5230\u5e95\u662f\u8c01\u52fe\u5f15\u8c01\uff1f\u53c8\u89c9\u5f97\u8fd9\u4e2a\u95ee\u9898\u5b9e\u5728\u65e0\u8bba\u8ddf\u8fd9\u4e2b\u5934\u4e89\u8bba\u6e05\u695a\u3002\u4ed6\u8fd8\u6ca1\u6709\u53cd\u5e94\u8fc7\u6765\uff0c\u53c8\u542c\u89c1\u9759\u6aac\u6ca1\u6709\u5e95\u6c14\u7684\u8bf4\u9053\uff1a\u201c\u543b\u543b\u4e5f\u662f\u53ef\u4ee5\uff0c\u53cd\u6b63\u897f\u65b9\u4eb2\u543b\u662f\u6b63\u5e38\u7684\u793c\u8282\uff0c\u5076\u5c14\u62b1\u4e00\u62b1\u4e5f\u53ef\u4ee5\u2026\u2026\u5176\u4ed6\u7684\u4ec0\u4e48\uff0c\u7edd\u5bf9\u7684\u4e0d\u884c\u3002\u201d</p><p>\u3000\u3000\u5c31\u8fd9\u6837\u7ed9\u5212\u6e05\u4e86\u754c\u9650\uff0c\u5f20\u606a\u5fc3\u91cc\u771f\u662f\u90c1\u95f7\uff0c\u5b59\u9759\u6aac\u5012\u662f\u89e3\u51b3\u4e86\u56f0\u60d1\u5979\u8bb8\u4e45\u4ee5\u6765\u7684\u4e00\u4e2a\u5927\u96be\u9898\u4f3c\u7684\uff0c\u5fc3\u60c5\u987f\u65f6\u7545\u5feb\u8d77\u6765\uff0c\u4e00\u8def\u4e0a\u8fd8\u5439\u8d77\u54e8\u6765\uff0c\u8ddf\u5973\u6d41\u6c13\u4f3c\u7684\u3002</p><p>\u3000\u3000\u8d70\u52301978\uff0c1978\u6709\u7740\u82f1\u5f0f\u9152\u5427\u7684\u98ce\u683c\uff0c\u8c22\u5b50\u5609\u3001\u9648\u9759\u966a\u540c\u8c22\u610f\u5750\u5728\u957f\u957f\u7684\u6a61\u6728\u5427\u53f0\u524d\uff0c\u8981\u4e0d\u662f\u4ed6\u4eec\u77e5\u9053\u5f20\u606a\u7684\u5e95\u7ec6\uff0c\u8fd8\u4ee5\u4e3a\u662f\u4e00\u4e2a\u5bfb\u5e38\u7684\u5927\u5b66\u751f\u8d70\u5c06\u8fdb\u6765\uff0c\u6d45\u8272\u7ebf\u683c\u5b50\u77ed\u8896\u886c\u886b\u3001\u6d45\u5496\u5561\u8272\u4f11\u95f2\u957f\u88e4\u3001\u6de1\u9ec4\u8272\u5e06\u5e03\u978b\uff0c\u5e06\u5e03\u978b\u8fd8\u662f\u5321\u5a01\u7684\uff0c\u5c31\u8fd9\u4e48\u4e00\u4ed8\u6a21\u6837\uff0c\u8fd9\u9644\u8fd1\u9ad8\u6821\u91cc\u80fd\u6293\u51fa\u6210\u767e\u4e0a\u5343\u7684\u4eba\u51fa\u6765\uff0c\u4e0d\u53ef\u5426\u8ba4\uff0c\u5f20\u606a\u524a\u7626\u7684\u8138\u988a\u7ebf\u6761\u5206\u660e\u3001\u773c\u775b\u660e\u4eae\u795e\u91c7\u71a0\u71a0\uff0c\u6de1\u7136\u81ea\u82e5\u7684\u6c14\u8d28\uff0c\u5374\u7edd\u975e\u5bfb\u5e38\u5927\u5b66\u751f\u80fd\u591f\u76f8\u6bd4\u7684\u3002</p><p>\u3000\u3000\u5c31\u8fd9\u4e48\u4e00\u4e2a\u9752\u5e74\uff0c\u638c\u63e1\u7740\u4e00\u4e2a\u5e9e\u5927\u7684\u4f01\u4e1a\u5e1d\u56fd\uff0c\u8fd9\u5927\u6982\u662f\u8fd1\u5e74\u6765\u6700\u8ba9\u4eba\u4e0d\u53ef\u601d\u5f02\u7684\u4e8b\u4ef6\u4e4b\u4e00\u4e86\u3002\u8bb8\u591a\u521d\u542c\u5230\u6b64\u4e8b\u7684\u4eba\uff0c\u5b81\u53ef\u76f8\u4fe1\u53c8\u6709\u4e00\u67b6\u98de\u673a\u5728\u767e\u6155\u5927\u795e\u79d8\u5931\u8e2a\u4e86\u3002</p><p>\u3000\u3000\u201c\u4f60\u4eec\u5728\u8fd9\u91cc\u7b49\u4e45\u4e86\u5427\uff0c\u201d\u5f20\u606a\u632a\u5750\u5230\u5427\u53f0\u524d\u7684\u9ad8\u51f3\u4e0a\uff0c\u4e0e\u9648\u9759\u70b9\u70b9\u5934\uff0c\u201c\u4ece\u91d1\u5c71\u56de\u6765\u7684\u8def\u51b5\u5f88\u5dee\uff0c\u5728\u8def\u4e0a\u591a\u803d\u6401\u4e86\u4e24\u4e2a\u5c0f\u65f6\u2026\u2026\u201d\u63a5\u8fc7\u5b59\u9759\u6aac\u9012\u7ed9\u4ed6\u63ba\u77ff\u6cc9\u6c34\u7684\u829d\u534e\u58eb\u9152\uff0c\u95ee\u9648\u9759\uff0c\u201c\u79d1\u738b\u9ad8\u79d1\u7684\u5408\u8d44\u8c08\u59a5\u6ca1\u6709\uff1f\u6211\u5012\u662f\u5fd8\u4e86\u8fc7\u95ee\u4e00\u4e0b\uff0c\u53c8\u60f3\u4ee5\u4f60\u4eec\u7684\u5b9e\u529b\uff0c\u90a3\u7b14\u6295\u8d44\u5e94\u8be5\u5f88\u8f7b\u677e\u5c31\u80fd\u8c08\u4e0b\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8c08\u59a5\u4e86\uff0c\u5efa\u4e00\u5ea7\u751f\u4ea7\u624b\u673a\u7528\u5370\u5237\u7535\u8def\u677f\u4e0e\u673a\u58f3\u6ce8\u5851\u7684\u5de5\u5382\uff0c\u8bbe\u5907\u5df2\u7ecf\u4ece\u96c5\u52a0\u8fbe\u8d77\u8fd0\u4e86\uff0c\u5bf9\u65b9\u8fd8\u8ffd\u52a0\u4e86\u4e00\u5343\u4e07\u7f8e\u5143\u7684\u6295\u8d44\uff0c\u8fd8\u6709\u5341\u591a\u540d\u534e\u4eba\u5de5\u7a0b\u6280\u672f\u4eba\u5458\u4e00\u540c\u8fc7\u6765\u2026\u2026\u201d\u9648\u9759\u4fa7\u7740\u5934\uff0c\u67d4\u987a\u7684\u957f\u53d1\u4ece\u522b\u4e00\u4fa7\u6ed1\u4e0b\u6765\uff0c\u660e\u7738\u7693\u9f7f\u3001\u76f4\u9f3b\u7ea2\u5507\uff0c\u57281978\u7565\u6697\u8ff7\u79bb\u7684\u5149\u7ebf\u91cc\u5341\u5206\u7684\u7f8e\u4e3d\u3002</p><p>\u3000\u3000\u8c22\u610f\u8010\u7740\u6027\u5b50\u542c\u5f20\u606a\u4e0e\u9648\u9759\u8bf4\u8bdd\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u9526\u6e56\u867d\u7136\u4e5f\u6709\u5f97\u52bf\u4e0d\u9976\u4eba\u7684\u65f6\u5019\uff0c\u4f46\u662f\u4ed6\u8fd8\u6ca1\u6709\u53d1\u73b0\u9526\u6e56\u6709\u523b\u610f\u7684\u53bb\u4ed7\u52bf\u6b3a\u4eba\u6216\u5728\u80cc\u5730\u91cc\u7834\u574f\u5546\u4e1a\u89c4\u5219\uff0c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u9526\u6e56\u8981\u771f\u6709\u6210\u4e3a\u8d85\u5730\u6d41\u4f01\u4e1a\u7684\u6c14\u5ea6\uff0c\u4f1a\u5f02\u5e38\u91cd\u89c6\u5546\u8a89\u7684\uff0c\u8c22\u610f\u624d\u89c9\u5f97\u6709\u6b63\u6cf0\u4e0e\u9526\u6e56\u6709\u548c\u89e3\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5f20\u606a\u5012\u662f\u8003\u8651\u8fc7\u8c22\u5bb6\u6709\u5206\u88c2\u7684\u53ef\u80fd\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e0e\u5f90\u5b66\u5e73\u660e\u4e89\u6697\u6597\u4e86\u5341\u591a\u5e74\uff0c\u5468\u747e\u73ba\u8eba\u5728\u75c5\u5e8a\u4e0a\u6210\u4e86\u690d\u7269\u4eba\uff0c\u73b0\u5982\u4eca\uff0c\u5468\u747e\u745c\u4e5f\u5373\u5c06\u5f7b\u5e95\u4ece\u9000\u51fa\u4ed5\u9014\uff0c\u5f90\u5b66\u5e73\u6267\u638c\u6c5f\u5357\u4e00\u7701\u6743\u67c4\uff0c\u6210\u4e86\u5c01\u7586\u5927\u540f\uff0c\u4ed6\u4eec\u4e4b\u95f4\u7684\u6069\u6028\u7b97\u662f\u6709\u4e86\u4e2a\u4e86\u7ed3\u3002\u4f5c\u4e3a\u7ed9\u5377\u5165\u5176\u4e2d\u7684\u8c22\u5bb6\uff0c\u8c22\u77bb\u4e5f\u7ed9\u5f7b\u5e95\u6392\u6324\u51fa\u6b63\u6cf0\u4f53\u7cfb\uff0c\u81f3\u4e8e\u5176\u4ed6\u4eba\uff0c\u6216\u8bb8\u4e0e\u4ed6\u4eec\u8981\u76f8\u4e92\u95f4\u4e0d\u6b23\u8d4f\u751a\u81f3\u654c\u89c6\uff0c\u90fd\u6ca1\u6709\u4ec0\u4e48\u6df1\u4ec7\u5927\u6068\uff1a\u5c31\u62ff\u8c22\u665a\u6674\u4e0e\u8c22\u665a\u5c71\u6765\u8bf4\uff0c\u867d\u7136\u5341\u591a\u5e74\u89c1\u9762\u90fd\u4e0d\u4f1a\u62db\u547c\u4e00\u58f0\uff0c\u4f46\u6bd5\u7adf\u662f\u540c\u7236\u5f02\u6bcd\u7684\u5144\u59b9\uff0c\u800c\u665a\u6674\u5bf9\u5f85\u5b50\u5609\u7b49\u4eba\uff0c\u4ecd\u60c5\u4e49\u72b9\u5728\u3002\u5c31\u7b97\u8c22\u5bb6\u5484\u5484\u903c\u4eba\uff0c\u5c06\u81ea\u5df1\u6240\u521b\u7acb\u7684\u6d77\u6cf0\u593a\u4e86\u8fc7\u53bb\uff0c\u8c22\u5251\u5357\u53c8\u6316\u9526\u6e56\u7684\u5899\u89d2\uff0c\u4f46\u90fd\u6ca1\u6709\u7279\u522b\u7684\u8fc7\u5206\uff0c\u518d\u8bf4\u8fd9\u4e9b\u5e74\u6765\uff0c\u8c22\u5bb6\u53c8\u4f55\u66fe\u5728\u81ea\u5df1\u5934\u4e0a\u5360\u5230\u8fc7\u534a\u70b9\u4fbf\u5b9c\uff1f</p><p>\u3000\u3000\u8981\u8bf4\u6709\u6028\u6068\uff0c\u4e5f\u662f\u8c22\u5bb6\u5bf9\u81ea\u5df1\u6709\u6028\u6068\uff0c\u5f20\u606a\u5fc3\u60f3\u81ea\u5df1\u771f\u662f\u6ca1\u9053\u7406\u5bf9\u8c22\u5bb6\u6709\u6028\u6068\uff0c\u53ea\u4e0d\u8fc7\u845b\u5bb6\uff0c\u81ea\u5df1\u8fd8\u6b20\u9759\u6aac\u4e00\u4e2a\u627f\u8bfa\uff0c\u6797\u96ea\u53c8\u662f\u4e00\u4e2a\u6bd2\u874e\u5973\u4eba\uff0c\u8981\u662f\u8c22\u5bb6\u6267\u610f\u8981\u4e0e\u4ed6\u4eec\u7ea0\u7f20\u5728\u4e00\u8d77\uff0c\u5f20\u606a\u89c9\u5f97\u5f7c\u6b64\u95f4\u8fd8\u505a\u8def\u4eba\u7684\u597d\u3002</p><p>\u3000\u3000\u5f85\u8c22\u610f\u4e03\u7f20\u516b\u7ed5\u7684\u6697\u793a\u4e86\u6765\u610f\uff0c\u5f20\u606a\u53ea\u662f\u6de1\u6de1\u7684\u8ddf\u4ed6\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u505a\u4f01\u4e1a\u8981\u6709\u505a\u4f01\u4e1a\u7684\u6c14\u5ea6\uff0c\u6211\u5bf9\u6b63\u6cf0\u96c6\u56e2\u5728\u7535\u673a\u3001\u4eea\u8868\u5236\u9020\u4e0a\u8868\u73b0\u51fa\u6765\u7684\u4e13\u4e1a\u7cbe\u795e\u4e5f\u76f8\u5f53\u656c\u4f69\uff0c\u5bf9\u6d77\u6cf0\u7535\u5668\u5728\u5bb6\u7535\u8fde\u9501\u7ecf\u8425\u4e0a\u8868\u73b0\u51fa\u6765\u7684\u7ade\u4e89\u7cbe\u795e\u4e5f\u9887\u4e3a\u6b23\u8d4f\u2014\u2014\u6539\u9769\u5f00\u653e\u4e8c\u5341\u5e74\uff0c\u56fd\u5185\u5546\u4e1a\u51c6\u5219\u672a\u7acb\uff0c\u56fd\u4eba\u5c24\u5176\u4e0d\u91cd\u89c6\u5546\u8a89\uff0c\u8868\u73b0\u672a\u5fc5\u592a\u529f\u5229\u4e86\u4e9b\uff0c\u8fd9\u4e9b\u4f01\u4e1a\uff0c\u6211\u662f\u5f85\u89c1\u7684\uff0c\u82e5\u4e00\u5b9a\u8981\u6211\u6311\u4e24\u5bb6\u4f01\u4e1a\u4e3e\u4f8b\uff0c\u5609\u4fe1\u3001\u7cbe\u5178\uff0c\u6211\u5f88\u4e0d\u559c\u6b22\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u5c1a\u4e49\u53ef\u4ee5\u8bf4\u662f\u9526\u6e56\u4e00\u7cfb\u9664\u5f20\u606a\u4e4b\u5916\u6700\u91cd\u8981\u7684\u4eba\u7269\u4e4b\u4e00\uff0c\u845b\u836b\u5747\u5371\u9669\u9a7e\u9a76\u81f4\u4f7f\u5b59\u5c1a\u4e49\u5bb6\u4e00\u4f4d\u6545\u4eba\u901d\u4e16\uff0c\u800c\u4e8b\u540e\u845b\u836b\u5747\u53c8\u63a8\u8131\u7f6a\u8d23\uff0c\u52a0\u4e0a\u5b59\u5c1a\u4e49\u4e00\u5bb6\u4e0e\u845b\u660e\u4fe1\u7236\u5b50\u7684\u957f\u671f\u6069\u6028\uff0c\u8fd9\u4e2a\u7ed3\u4e0d\u4f1a\u8fd9\u4e48\u8f7b\u6613\u5c31\u89e3\u51b3\uff1b\u8c22\u610f\u4e0d\u660e\u767d\u5f20\u606a\u4e3a\u4f55\u5bf9\u7cbe\u5178\u5730\u4ea7\u6709\u5982\u6b64\u4e4b\u6df1\u7684\u6210\u89c1\uff0c\u4f46\u662f\u6797\u96ea\u8fd9\u4e2a\u5973\u4eba\u7684\u8d2a\u5a6a\u4e0e\u8086\u65e0\u5fcc\u60ee\u4e5f\u4ee4\u8c22\u610f\u611f\u5230\u540e\u6015\uff0c\u8981\u80fd\u8131\u5f00\u5173\u7cfb\u8fd8\u65e9\u8131\u5f00\u5173\u7cfb\u7684\u597d\u3002</p><p>\u3000\u3000\u8c22\u610f\u6e05\u695a\u5f20\u606a\u7684\u610f\u601d\u4e4b\u540e\uff0c\u7a0d\u4f5c\u5bd2\u6684\u4fbf\u544a\u8f9e\u79bb\u5f00\uff0c\u5750\u5230\u8f66\u91cc\uff0c\u5c06\u8033\u673a\u585e\u5230\u8033\u6735\u91cc\uff0c\u62e8\u901a\u7236\u4eb2\u7684\u7535\u8bdd\uff1a\u201c\u6211\u521a\u521a\u624d\u5f20\u606a\u89c1\u8fc7\u9762\uff0c\u4ed6\u7684\u5fc3\u601d\u4e5f\u660e\u767d\u65e0\u8bef\u7684\u8bf4\u4e86\u51fa\u6765\uff0c\u770b\u6765\uff0c\u6211\u4eec\u8981\u5c06\u79d1\u738b\u3001\u91d1\u5c71\u7535\u5b50\u56ed\u4ee5\u53ca\u5730\u4ea7\u4e1a\u52a1\u7684\u6295\u8d44\u90fd\u653e\u5f03\u6389\u2026\u2026\u4f60\u89c9\u5f97\u600e\u6837\uff1f\u201d</p></article>"}}}]);
