(window.webpackJsonp=window.webpackJsonp||[]).push([[1088],{1111:function(p){p.exports={chapterTitle:null,title:"\u7b2c1085\u7ae0 \u8eab\u8d25\u540d\u88c2",content:"<article><h2>\u7b2c1085\u7ae0 \u8eab\u8d25\u540d\u88c2</h2><p>\u3000\u3000\u7531\u4e8e\u4e2d\u6676\u5fae\u82af\u4e0e\u5fb7\u4eea\u7684\u6676\u5706\u5236\u7a0b\u6280\u672f\u8f6c\u8ba9\u534f\u8bae\u901a\u8fc7\u7f8e\u56fd\u5f53\u5c40\u7684\u5ba1\u67e5\uff0c\u5c06\u6781\u5927\u7684\u5f71\u54cd\u5230\u4e9a\u6d32\u6676\u5706\u4ea7\u4e1a\u7684\u683c\u5c40\uff0c\u4e9a\u6d32\u4e3b\u8981\u6676\u5706\u5382\u5546\u90fd\u9488\u950b\u76f8\u5bf9\u7684\u63d0\u51fa\u5e94\u5bf9\u7b56\u7565\uff0c\u610f\u5728\u8feb\u4f7f\u4e2d\u6676\u5fae\u82af\u7684\u65b0\u5382\u5efa\u8bbe\u8ba1\u5212\u6d41\u4ea7\u3002</p><p>\u3000\u3000\u6676\u5706\u5236\u9020\u79bb\u666e\u901a\u6c11\u4f17\u7684\u751f\u6d3b\u592a\u9065\u8fdc\u4e86\uff0c\u516c\u5f00\u5a92\u4f53\u5173\u6ce8\u4e0d\u591a\uff0c\u4e13\u4e1a\u5a92\u4f53\u53ca\u4e1a\u5185\u4eba\u58eb\u90fd\u62ed\u76ee\u4ee5\u5f85\uff0c\u5fc3\u91cc\u5f88\u6e05\u695a\u4e2d\u6676\u5fae\u82af\u7684\u5e94\u5bf9\u7b56\u7565\u5c06\u5f71\u54cd\u5230\u4e9a\u6d32\u7535\u5b50\u4ea7\u4e1a\u683c\u5c40\u7684\u53d1\u5c55\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e2d\u6676\u5fae\u82af\u4e0e\u5fb7\u4eea\u7684\u5408\u4f5c\u8feb\u4f7f\u4e9a\u6d32\u4e3b\u8981\u6676\u5706\u5382\u5546\u7206\u4ea7\u80fd\u6765\u5e94\u5bf9\u4e86\uff0c\u6b27\u7f8e\u6676\u5706\u5236\u9020\u8bbe\u5907\u4ef7\u683c\u5927\u6da8\uff0c\u5df2\u7ecf\u5927\u5e45\u5ea6\u7684\u6405\u4e71\u4e9a\u6d32\u7535\u5b50\u4ea7\u4e1a\u65e2\u6709\u7684\u683c\u5c40\uff0c\u53ea\u662f\u8fdb\u4e00\u6b65\u7684\u8d70\u52bf\u8fd8\u8981\u770b\u4e2d\u6676\u5fae\u82af\u7684\u5e94\u5bf9\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u6c89\u9ed8\u4e86\u7ea6\u534a\u4e2a\u6708\uff0c\u5341\u4e00\u6708\u4e0b\u65ec\u5728\u91d1\u5c71\u5ba3\u5e03\u4f9d\u6258\u91d1\u5c71\u6a61\u6811\u56ed\u6210\u7acb\u4e2d\u8bd5\u5b9e\u9a8c\u5ba4\uff0c\u9664\u6b64\u4e4b\u5916\uff0c\u7231\u8fbe\u96c6\u56e2\u65d7\u4e0b\u7231\u8fbe\u5fae\u7535\u5b50\u6709\u9650\u516c\u53f8\u5ba3\u5e03\u5c06\u5728\u91d1\u5c71\u6295\u8d44\u65b0\u5efa\u4e00\u5ea7\u82af\u7247\u5c01\u88c5\u6d4b\u8bd5\u5de5\u5382\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u5728\u5efa\u90ba\u5df2\u7ecf\u6210\u7acb\u4e86\u5f00\u53d1\u5236\u7a0b\u5de5\u827a\u7684\u5927\u578b\u5b9e\u9a8c\u5ba4\uff0c\u91d1\u5c71\u7684\u4e2d\u8bd5\u5b9e\u9a8c\u5ba4\u81ea\u7136\u662f\u4e3a\u6b63\u5f0f\u7684\u6676\u5706\u751f\u4ea7\u4f5c\u51c6\u5907\uff0c\u800c\u82af\u7247\u5c01\u88c5\u6d4b\u8bd5\u5de5\u5382\u4e3b\u8981\u627f\u62c5\u6676\u5706\u751f\u4ea7\u7684\u540e\u534a\u6bb5\u5de5\u5e8f\uff0c\u8fd9\u4e24\u5219\u6d88\u606f\u4e5f\u8fc5\u901f\u5f97\u5230\u6676\u5706\u8bbe\u5907\u5236\u9020\u5382\u5546\u7684\u786e\u8ba4\uff0c\u4e2d\u6676\u5fae\u82af\u4e0e\u7231\u8fbe\u5fae\u7535\u5b50\u7684\u786e\u91c7\u8d2d\u4e86\u5927\u91cf\u7684\u5c0f\u8bd5\u3001\u4e2d\u8bd5\u4ee5\u53ca\u5c01\u88c5\u6d4b\u8bd5\u8bbe\u5907\u3002</p><p>\u3000\u3000\u5728\u6574\u4e2a\u6676\u5706\u5236\u9020\u6295\u8d44\u4e2d\uff0c\u5c0f\u8bd5\u3001\u4e2d\u8bd5\u4ee5\u53ca\u5c01\u88c5\u3001\u6d4b\u8bd5\u7b49\u8bbe\u5907\u6240\u5360\u7684\u6bd4\u4f8b\u5f88\u5c11\uff0c\u5f53\u7136\u4e5f\u80fd\u786e\u51ff\u65e0\u7591\u7684\u8868\u660e\u4e2d\u6676\u5fae\u82af\u5373\u5c06\u5728\u91d1\u5c71\u5efa\u9020\u65b0\u7684\u6676\u5706\u5382\u3002</p><p>\u3000\u3000\u4e00\u77f3\u6fc0\u8d77\u5343\u5c42\u6d6a\u3002</p><p>\u3000\u3000\u4e1a\u5185\u4e4b\u524d\u90fd\u666e\u901a\u9884\u6d4b\uff1a\u5728\u5f53\u524d\u5f62\u52bf\u4e0b\uff0c\u7279\u522b\u662f\u6676\u5706\u5236\u9020\u8bbe\u5907\u4ef7\u683c\u5927\u589e\u4e4b\u540e\uff0c\u4e2d\u6676\u5fae\u82af\u4f1a\u653e\u5f03\u540c\u65f6\u5efa\u9020\u4e24\u5ea7\u65b0\u5382\u7684\u8ba1\u5212\u3002</p><p>\u3000\u3000\u82e5\u662f\u53ea\u5efa\u4e00\u5ea7\u65b0\u5382\uff0c\u6ca1\u6709\u53ef\u80fd\u9009\u62e9\u5728\u5efa\u90ba\u4ee5\u5916\u7684\u5730\u65b9\u3002</p><p>\u3000\u3000\u9996\u5148\u5efa\u90ba\u5e02\u653f\u5e9c\u7edd\u4e0d\u4f1a\u5e0c\u671b\u770b\u5230\u5341\u51e0\u4e8c\u5341\u4ebf\u7f8e\u5143\u7684\u5de8\u5927\u6295\u8d44\u843d\u5165\u522b\u4eba\u7684\u53e3\u888b\uff0c\u4f1a\u6781\u529b\u633d\u7559\u4e2d\u6676\u5fae\u82af\u5c06\u65b0\u5382\u5efa\u5728\u5efa\u90ba\uff0c\u76f8\u4fe1\u5efa\u90ba\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u653f\u7b56\u652f\u6301\u4e0d\u4f1a\u5f31\u3002</p><p>\u3000\u3000\u4e8b\u5b9e\u4e0a\uff0c\u4e2d\u6676\u5fae\u82af\u7684\u65b0\u6d66\u5de5\u5382\u5efa\u8bbe\u83b7\u5f97\u7684\u5730\u65b9\u652f\u6301\u4e0d\u6bd4\u534e\u590fNEC\u5dee\u3002</p><p>\u3000\u3000\u5176\u6b21\uff0c\u4e2d\u6676\u5fae\u82af\u5728\u5efa\u90ba\u5df2\u7ecf\u5efa\u6210\u65b0\u6d66\u5de5\u5382\u5e76\u83b7\u5f97\u8fd0\u8425\u4e0a\u7684\u6210\u529f\uff0c\u5728\u5efa\u90ba\u5efa\u65b0\u5382\u62e5\u6709\u8bf8\u591a\u7684\u4f18\u52bf\uff0c\u5230\u91d1\u5c71\u5efa\u65b0\u5382\uff0c\u5404\u9879\u80fd\u591f\u9884\u6d4b\u4ee5\u53ca\u4e0d\u53ef\u9884\u6d4b\u7684\u98ce\u9669\u6210\u672c\u5c06\u5927\u5e45\u63d0\u9ad8\u3002</p><p>\u3000\u3000\u73b0\u5728\u4e2d\u6676\u5fae\u82af\u51b3\u5b9a\u7acb\u5373\u7740\u624b\u5728\u91d1\u5c71\u5efa\u9020\u65b0\u7684\u6676\u5706\u5382\uff0c\u90a3\u5c31\u610f\u5473\u7740\u4e1a\u5185\u4e4b\u524d\u7684\u9884\u6d4b\u90fd\u662f\u9519\u8bef\u7684\uff0c\u4e2d\u6676\u5fae\u82af\u5c06\u540c\u65f6\u5728\u5efa\u90ba\u3001\u91d1\u5c71\u542f\u52a8\u65b0\u5382\u5efa\u9020\u8ba1\u5212\uff0c\u6839\u636e\u91d1\u5c71\u5c01\u88c5\u6d4b\u8bd5\u5de5\u5382\u7684\u6295\u8d44\u89c4\u6a21\u8ba1\u5212\uff0c\u91d1\u5c71\u5de5\u5382\u7684\u89c4\u6a21\u5c06\u5728\u516d\u6761\u7ebf\u5230\u516b\u6761\u7ebf\u4e4b\u95f4\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u201c\u516b\u683c\uff01\u516b\u683c\uff01\u201d\u6c60\u4f50\u79c0\u85cf\u6b63\u895f\u5371\u5750\u7684\u8dea\u5750\u5728\u684c\u524d\uff0c\u770b\u7740\u644a\u653e\u5728\u684c\u4e0a\u7684\u62a5\u544a\u770b\u4e86\u597d\u4e00\u4f1a\u513f\uff0c\u5fc3\u91cc\u6068\u6068\u7684\u53eb\u9a82\u7740\uff0c\u624b\u6491\u5728\u684c\u4e0a\uff0c\u62ac\u8d77\u5934\uff0c\u5fc3\u60c5\u90c1\u7ed3\u7684\u50cf\u538b\u7740\u58f0\u97f3\u5450\u558a\u4f3c\u7684\u95ee\u9053\uff0c\u201c\u4e24\u5ea7\u5341\u4e8c\u82f1\u5bf8\u65b0\u5382\u773c\u4e0b\u9884\u7b97\u5c31\u8981\u505a\u5230\u4e09\u5341\u4ebf\u7f8e\u5143\uff0c\u5b9e\u9645\u5efa\u6210\u66f4\u53ef\u80fd\u4f1a\u7a81\u7834\u56db\u5341\u4ebf\u7f8e\u5143\uff0c\u9526\u6e56\u600e\u4e48\u53ef\u80fd\u4f1a\u4ec0\u4e48\u591a\u8d44\u91d1\uff1f\u201d\u4ed6\u7684\u773c\u795e\u4ece\u5728\u5ea7\u8bf8\u4eba\u8138\u4e0a\u7f13\u7f13\u626b\u8fc7\uff0c\u505c\u5728\u8c22\u5251\u5357\u7684\u8138\u4e0a\uff0c\u95ee\u9053\uff1a\u201c\u8c22\u541b\u4ece\u91d1\u5c71\u8fc7\u6765\uff0c\u6709\u542c\u5230\u4ec0\u4e48\u4f20\u95fb\u6ca1\u6709\uff1f\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5174\u8da3\u7f3a\u7f3a\u7684\u5750\u5728\u90a3\u91cc\u72ec\u81ea\u996e\u9152\uff0c\u542c\u5230\u6c60\u4f50\u79c0\u85cf\u95ee\u4ed6\u8bdd\uff0c\u5c06\u9152\u676f\u653e\u4e0b\uff0c\u8bf4\u9053\uff1a\u201c\u6709\u4e9b\u5927\u4eba\u7269\u662f\u5728\u91d1\u5c71\uff0c\u53ea\u662f\u79d1\u738b\u5728\u91d1\u5c71\u7684\u4eba\u8109\u5b9e\u5728\u7b97\u4e0d\u4e0a\u597d\uff0c\u4e5f\u6253\u542c\u4e0d\u5230\u592a\u8be6\u7ec6\u7684\u6d88\u606f\u2026\u2026\u201d\u4ed6\u5df2\u7ecf\u5f00\u59cb\u5bf9\u8fd9\u4e9b\u8eb2\u5728\u80cc\u540e\u5bc6\u8c0b\u7684\u805a\u4f1a\u53cd\u611f\u4e86\uff0c\u5927\u6982\u662f\u6709\u8fc7\u591a\u6b21\u7b97\u8ba1\u9526\u6e56\u53c8\u90fd\u7ed9\u9526\u6e56\u53cd\u7b97\u8ba1\u7ecf\u5386\u7684\u7f18\u6545\uff0c\u8c22\u5251\u5357\u6b64\u65f6\u7684\u5fc3\u601d\u5c31\u662f\u5c06\u79d1\u738b\u7684\u63a7\u5236\u6743\u6293\u5728\u624b\u91cc\uff0c\u5c06\u79d1\u738b\u7ecf\u8425\u597d\uff0c\u9526\u6e56\u7684\u80cc\u5f71\u5728\u4ed6\u7684\u773c\u775b\u8d8a\u6765\u8d8a\u8d8a\u6a21\u7cca\uff0c\u5373\u4f7f\u8981\u7b97\u8ba1\u9526\u6e56\uff0c\u8fd8\u662f\u7559\u7ed9\u522b\u4eba\u53bb\u505a\u597d\u4e86\uff0c\u8fd9\u4e48\u4e00\u7fa4\u4eba\u5750\u5728\u8fd9\u91cc\uff0c\u8868\u9762\u4e0a\u540c\u4ec7\u654c\u5ffe\uff0c\u5b9e\u9645\u4e0a\u5374\u53c8\u5404\u6000\u9b3c\u80ce\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u6216\u8bb8\u5fc3\u91cc\u6a2a\u7740\u4e00\u6839\u672a\u5a5a\u59bb\u7ed9\u4eba\u6a2a\u5200\u593a\u7231\u7684\u523a\uff0c\u4f46\u6c60\u4f50\u79c0\u85cf\u80cc\u540e\u7684\u4e09\u4e95\u8d22\u9600\u6253\u7684\u662f\u4ec0\u4e48\u4e3b\u610f\uff0c\u8ddf\u79c3\u5b50\u5934\u4e0a\u7684\u8671\u5b50\u4e00\u6837\u663e\u773c\u3002\u89c1\u674e\u5728\u6d19\u8138\u65e0\u8868\u60c5\u7684\u5750\u5728\u90a3\u91cc\uff0c\u8c22\u5251\u5357\u5fc3\u60f3\u5728\u4ed6\u770b\u6765\u80fd\u6253\u51fb\u5230\u9526\u6e56\u81ea\u7136\u66f4\u597d\uff0c\u4f46\u662f\u505a\u51fa\u7b26\u5408\u6709\u5229\u4e09\u661f\u7684\u9009\u62e9\u624d\u662f\u6700\u91cd\u8981\u7684\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u91cc\u4e5f\u5f88\u5947\u602a\uff0c\u4e09\u661f\u7684\u7075\u9b42\u4eba\u7269\u3001\u7cbe\u795e\u9886\u8896\u674e\u5065\u7199\u600e\u4e48\u4f1a\u5bb9\u5fcd\u5973\u513f\u6ca6\u843d\u4e3a\u5f20\u606a\u7684\u5730\u4e0b\u60c5\u4eba\uff1f\u8c01\u89c1\u8fc7\u674e\u99a8\u4e88\u90fd\u8981\u60ca\u82e5\u5929\u4eba\u5427\uff0c\u5373\u4f7f\u674e\u5065\u7199\u8981\u5c06\u8fd9\u6837\u7684\u5973\u513f\u5f53\u6210\u7b79\u7801\uff0c\u90a3\u4e0e\u4e09\u4e95\u7684\u6c60\u4f50\u5bb6\u8054\u59fb\u4e0d\u662f\u5f88\u7b26\u5408\u4e09\u661f\u7684\u5229\u76ca\uff1f\u540c\u4e3a\u674e\u5065\u7199\u5bb6\u65cf\u7684\u6210\u5458\uff0c\u674e\u5728\u6d19\u5fc3\u91cc\u5bf9\u8fd9\u4e8b\u7684\u771f\u5b9e\u770b\u6cd5\u53c8\u662f\u4ec0\u4e48\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u89c9\u5f97\u597d\u4e9b\u4e1c\u897f\u90fd\u770b\u4e0d\u900f\uff0c\u5927\u5bb6\u90fd\u5fc3\u6000\u9b3c\u80ce\uff0c\u5c31\u50cf\u5bf9\u9762\u7684\u4e25\u6587\u4ecb\uff0c\u4ed6\u5750\u5728\u90a3\u91cc\u54ac\u7259\u5207\u9f7f\uff0c\u4e5f\u662f\u5047\u9762\u5177\u3002\u8bf4\u8d77\u6765\uff0c\u4e25\u6587\u4ecb\u8fd8\u8981\u597d\u597d\u611f\u8c22\u9526\u6e56\u3002\u6ca1\u6709\u9526\u6e56\u7684\u538b\u529b\uff0c\u6839\u672c\u5c31\u4e0d\u4f1a\u6709\u4e09\u661f\u5b8f\u82af\u7684\u5408\u8d44\u65b9\u6848\uff0c\u80cc\u540e\u7531\u6c60\u4f50\u79c0\u85cf\u4eb2\u81ea\u6349\u5200\u62df\u5b9a\u7684\u5408\u8d44\u65b9\u6848\u5bf9\u540e\u7ee7\u6709\u4e9b\u4e4f\u529b\u7684\u6d77\u7c9f\u79d1\u6280\u65e0\u7591\u5c31\u662f\u4e00\u7c92\u4ed9\u4e39\uff0c\u5408\u8d44\u65b9\u6848\u4e00\u7ecf\u516c\u5f00\uff0c\u8fde\u7eed\u534a\u4e2a\u6708\u7684\u6301\u7eed\u589e\u6da8\u4ee4\u6d77\u7c9f\u79d1\u6280\u7684\u5e02\u503c\u7206\u589e\u5230\u516d\u5341\u4ebf\uff0c\u4ed6\u5b9e\u5728\u6ca1\u6709\u7406\u7531\u5bf9\u9526\u6e56\u54ac\u7259\u5207\u9f7f\uff0c\u5e94\u8be5\u52aa\u529b\u4e0d\u8ba9\u5fc3\u91cc\u7684\u90a3\u4e00\u4e1d\u7b11\u610f\u6d6e\u4e0a\u6765\u624d\u662f\u3002</p><p>\u3000\u3000\u548c\u5ba4\u7684\u95e8\u7ed9\u63a8\u5f00\uff0c\u8c22\u5251\u5357\u770b\u89c1\u4ed6\u7684\u79d8\u4e66\u5728\u95e8\u5916\u505a\u51fa\u63a5\u7535\u8bdd\u7684\u624b\u52bf\uff0c\u5fc3\u91cc\u60f3\u7740\u53c8\u53d1\u751f\u7684\u4ec0\u4e48\u4e8b\u60c5\uff1f\u6ca1\u6709\u91cd\u8981\u4e8b\u60c5\uff0c\u79d8\u4e66\u662f\u4e0d\u4f1a\u8dd1\u8fc7\u6765\u6253\u6270\u4ed6\u4eec\u5bc6\u8c08\u7684\uff0c\u4ed6\u4e0e\u6c60\u4f50\u79c0\u85cf\u3001\u674e\u5728\u6d19\u7b49\u4eba\u9894\u9996\u81f4\u6b49\uff0c\u4ece\u5ea7\u4f4d\u4e0a\u722c\u8d77\u6765\uff0c\u90fd\u611f\u89c9\u5230\u811a\u8dea\u5750\u65f6\u95f4\u957f\u4e86\u90fd\u6709\u4e9b\u53d1\u9ebb\u3002</p><p>\u3000\u3000\u201c\u65b0\u6cf0\u6253\u6765\u7684\u7535\u8bdd\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u5fc3\u91cc\u4e00\u7d27\uff0c\u4e0d\u77e5\u9053\u8001\u5bb6\u8fd9\u65f6\u5019\u6253\u7535\u8bdd\u8fc7\u6765\u6709\u4ec0\u4e48\u8981\u7d27\u7684\u4e8b\u60c5\uff0c\u63a5\u8fc7\u624b\u673a\uff0c\u542c\u89c1\u662f\u4ed6\u5988\u7684\u58f0\u97f3\uff0c\u95ee\u9053\uff1a\u201c\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u201c\u4f60\u5927\u8205\u9192\u8fc7\u6765\u4e86\uff0c\u6211\u4eec\u6b63\u5750\u8f66\u53bb\u5efa\u90ba\uff0c\u4f60\u4eba\u5728\u5efa\u90ba\uff0c\u8981\u4e0d\u8981\u5148\u53bb\u533b\u9662\u770b\u4e00\u773c\u2026\u2026\u201d</p><p>\u3000\u3000\u5bf9\u8fd9\u4e2a\u8205\u8205\uff0c\u8c22\u5251\u5357\u5fc3\u91cc\u8fd8\u6709\u611f\u60c5\u7684\uff0c\u7a81\u7136\u60f3\u8d77\u6765\u5df2\u7ecf\u5f88\u4e45\u6ca1\u6709\u53bb\u533b\u9662\u770b\u671b\u4e86\uff0c\u6302\u4e86\u7535\u8bdd\uff0c\u5fd9\u8d70\u56de\u53bb\u8ddf\u6c60\u4f50\u79c0\u85cf\u4ed6\u4eec\u544a\u8f9e\uff0c\u5750\u8f66\u671d\u533b\u9662\u8d76\u53bb\u3002</p><p>\u3000\u3000\u8d76\u5230\u533b\u9662\uff0c\u5728\u505c\u8f66\u573a\u8f66\u521a\u505c\u7a33\uff0c\u770b\u5230\u8c22\u610f\u4ece\u524d\u9762\u4e00\u8f86\u8f66\u91cc\u8d70\u4e0b\u6765\uff0c\u8c22\u5251\u5357\u6014\u5728\u90a3\u91cc\uff0c\u60f3\u4e0d\u901a\u8c22\u610f\u4e3a\u4ec0\u4e48\u4f1a\u51fa\u73b0\u8fd9\u91cc\uff0c\u4e24\u8fb9\u5173\u7cfb\u7edd\u88c2\u4e4b\u540e\uff0c\u5df2\u7ecf\u6709\u4e00\u5e74\u591a\u6ca1\u6709\u8054\u7cfb\u4e86\u3002</p><p>\u3000\u3000\u770b\u89c1\u8c22\u610f\u7684\u773c\u775b\u770b\u5411\u8fd9\u8fb9\uff0c\u8c22\u5251\u5357\u63a8\u95e8\u4e0b\u4e86\u8f66\uff0c\u7ad9\u5728\u8f66\u95e8\u524d\uff0c\u4e5f\u6ca1\u6709\u671d\u8c22\u610f\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u6211\u4e5f\u662f\u521a\u521a\u63a5\u5230\u7535\u8bdd\uff0c\u6d88\u606f\u8ba9\u4eba\u5f88\u6cae\u4e27\uff0c\u4e0d\u8fc7\u6211\u60f3\u533b\u9662\u4e5f\u5c3d\u529b\u4e86\u2026\u2026\u201d\u8c22\u610f\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u8c22\u5251\u5357\u6123\u5728\u90a3\u91cc\uff0c\u4ed6\u521a\u624d\u63a5\u5230\u7535\u8bdd\u660e\u660e\u662f\u8bf4\u5927\u8205\u9192\u4e86\u8fc7\u6765\uff0c\u8c22\u610f\u7684\u53e3\u6c14\u5374\u662f\u5728\u8bf4\u5927\u8205\u5728\u533b\u9662\u91cc\u4e0d\u6cbb\u8eab\u6545\u4e86\u2026\u2026</p><p>\u3000\u3000\u8c22\u5251\u5357\u5f20\u4e86\u5f20\u5634\uff0c\u60f3\u95ee\u4ec0\u4e48\uff0c\u5374\u53d1\u73b0\u6ca1\u6709\u4ec0\u4e48\u53ef\u95ee\uff0c\u8981\u662f\u8205\u8205\u4ec5\u4ec5\u662f\u82cf\u9192\u8fc7\u6765\uff0c\u7684\u786e\u4e0d\u4f1a\u6709\u4eba\u4f1a\u4e13\u95e8\u901a\u77e5\u8c22\u610f\u7684\uff0c\u8c22\u610f\u5373\u4f7f\u63a5\u5230\u7535\u8bdd\u901a\u77e5\uff0c\u5927\u6982\u4e5f\u4e0d\u4f1a\u4e13\u7a0b\u8d76\u5230\u533b\u9662\u91cc\u6765\u5427\uff1f</p><p>\u3000\u3000\u8c22\u5251\u5357\u8ddf\u5728\u8c22\u610f\u7684\u80cc\u540e\u5f80\u75c5\u623f\u8d70\u53bb\uff0c\u5728\u8fc7\u9053\u91cc\u8fdc\u8fdc\u5c31\u542c\u89c1\u75c5\u623f\u91cc\u4f20\u6765\u7684\u54ed\u6ce3\u58f0\uff0c\u8d70\u8fc7\u53bb\uff0c\u63a8\u95e8\u534a\u63a9\u7740\u7684\u623f\u95e8\uff0c\u770b\u89c1\u8205\u5988\u5750\u5728\u75c5\u5e8a\u524d\u7684\u6905\u5b50\u4e0a\u54ed\u6ce3\uff0c\u9057\u4f53\u8fd8\u653e\u5728\u75c5\u5e8a\u4e0a\uff0c\u8c22\u5251\u5357\u8d70\u8fc7\u53bb\uff0c\u770b\u4e86\u4e00\u773c\uff0c\u7ef4\u751f\u88c5\u7f6e\u8fd8\u6ca1\u6709\u62c6\u9664\uff0c\u767d\u814a\u65e0\u5149\u7684\u8138\u5b54\u770b\u4e0d\u5230\u6709\u4e1d\u6beb\u7684\u751f\u547d\u8ff9\u8c61\u3002</p><p>\u3000\u3000\u201c\u8205\u8205\u9192\u8fc7\u6765\u6709\u6ca1\u6709\u8bf4\u4ec0\u4e48\uff1f\u201d\u8c22\u5251\u5357\u770b\u7740\u8205\u5988\u7684\u5934\u53d1\u82b1\u767d\uff0c\u8d70\u8fc7\u6765\u5750\u5230\u5979\u8eab\u8fb9\uff0c\u8f7b\u58f0\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4f60\u8205\u9192\u8fc7\u6765\uff0c\u8bf4\u8bdd\u6709\u4e9b\u56f0\u96be\uff0c\u6211\u8fd8\u662f\u80fd\u542c\u6e05\u695a\u4ed6\u662f\u60f3\u95ee\u4f60\u7684\u60c5\u51b5\uff0c\u6211\u8ddf\u4ed6\u8bf4\u4e86\uff0c\u53c8\u95ee\u4ed6\u8981\u4e0d\u8981\u544a\u8bc9\u5b66\u6821\uff0c\u4ed6\u8bf4\u7b97\u4e86\uff0c\u6211\u60f3\u4ed6\u6216\u8bb8\u4f1a\u60f3\u77e5\u9053\u9526\u6e56\u7684\u6d88\u606f\uff0c\u4ed6\u53c8\u8bf4\u7b97\u4e86\uff0c\u6211\u8d76\u7740\u51fa\u53bb\u7ed9\u4f60\u5988\u6253\u7535\u8bdd\uff0c\u56de\u6765\u65f6\u8fd8\u60f3\u8ddf\u4ed6\u518d\u8bf4\u4e24\u53e5\u8bdd\uff0c\u4f60\u8205\u5c31\u8fc7\u53bb\u4e86\uff0c\u8138\u4e0a\u8fd8\u6709\u6cea\u6c34\u90fd\u6ca1\u6709\u5e72\uff0c\u4ed6\u5fc3\u91cc\u662f\u6068\u554a\u2026\u2026\u201d</p><p>\u3000\u3000\u8c22\u5251\u5357\u770b\u7740\u75c5\u5e8a\u4e0a\u51b0\u51b7\u67af\u7626\u7684\u9057\u4f53\uff0c\u82cd\u767d\u6df1\u51f9\u7684\u8138\u4e0a\u8fd8\u6709\u4e9b\u6cea\u75d5\uff0c\u4ed6\u79bb\u5f00\u65f6\u5230\u5e95\u5728\u6068\u4ec0\u4e48\uff1f\u8fd9\u4e00\u5e74\u591a\u65f6\u95f4\u91cc\u53d1\u751f\u7684\u4e8b\u60c5\u592a\u591a\u4e86\uff0c\u7ae0\u5dde\u5927\u6848\u3001\u4e5d\u516b\u5e74\u6d2a\u707e\u3001\u91d1\u5c71\u6e56\u6e83\u5824\uff0c\u91d1\u5c71\u5de5\u4e1a\u56ed\u51e0\u4e4e\u7ed9\u5b8c\u5168\u51b2\u6e83\uff0c\u5609\u4fe1\u96c6\u56e2\u9677\u5165\u6fd2\u4e34\u7834\u4ea7\u7684\u7edd\u5883\uff0c\u8c22\u5bb6\u4e5f\u5206\u5d29\u79bb\u6790\u4e86\uff0c\u4ed6\u56de\u8fc7\u5934\u770b\u7740\u62bf\u7740\u5634\u4e00\u8a00\u4e0d\u53d1\u7684\u8c22\u610f\uff0c\u7a81\u7136\u60f3\u77e5\u9053\u662f\u8c01\u901a\u77e5\u6d88\u606f\u7ed9\u4ed6\u7684\u2026\u2026</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u80f3\u818a\u652f\u5728\u684c\u4e0a\u624b\u6258\u7740\u8111\u888b\u8ddf\u5f90\u5b66\u5e73\u4e0b\u68cb\uff0c\u5085\u4fca\u8d70\u8fdb\u6765\u544a\u8bc9\u4ed6\u5c31\u5728\u5165\u591c\u540e\u4e0d\u4e45\u5468\u747e\u73ba\u77ed\u6682\u82cf\u9192\u8fc7\u6765\u540e\u53bb\u4e16\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u89c1\u5f90\u5b66\u5e73\u82e5\u6709\u6240\u601d\u7684\u5c06\u68cb\u5b50\u4e22\u56de\u74f7\u76d2\u91cc\uff0c\u966a\u540c\u4ed6\u7ad9\u8d77\u6765\u8d70\u5230\u5916\u9762\u6708\u5f71\u6e05\u6d45\u7684\u5ead\u9662\u91cc\u3002</p><p>\u3000\u3000\u201c\u5728\u6211\u7684\u524d\u534a\u751f\u4e00\u76f4\u89c6\u4ed6\u4e3a\u5fd7\u540c\u9053\u5408\u7684\u631a\u53cb\uff0c\u4f60\u5a76\u5a76\u8fd8\u662f\u4ed6\u4ecb\u7ecd\u7ed9\u6211\u8ba4\u8bc6\u7684\uff0c\u201d\u5f90\u5b66\u5e73\u9887\u6709\u611f\u6168\u7684\u8f7b\u8f7b\u4e00\u53f9\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u8fd9\u540e\u534a\u751f\u7684\u6069\u6028\uff0c\u8fd9\u65f6\u4e5f\u7b97\u662f\u5f7b\u5e95\u7a7a\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u611f\u550f\u5618\uff0c\u4ed6\u662f\u4eba\u751f\u767e\u5e74\u672a\u53ca\u534a\u53c8\u5237\u65b0\u91cd\u6765\uff0c\u8fd9\u79cd\u7ea0\u7f20\u4e86\u4e00\u4e16\u7684\u6069\u6028\uff0c\u4e5f\u672a\u80fd\u4f53\u4f1a\u900f\u5f7b\u3002</p><p>\u3000\u3000\u5468\u6dd1\u60e0\u8d70\u8fdb\u5ead\u9662\u6765\uff0c\u8bf4\u9053\uff1a\u201c\u90a3\u8fb9\u6599\u7406\u540e\u4e8b\uff0c\u6211\u8981\u4e0d\u8981\u8fc7\u53bb\u4e00\u8d9f\uff1f\u201d</p><p>\u3000\u3000\u201c\u53bb\u4ec0\u4e48\u53bb\u554a\uff1f\u201d\u5f90\u5b66\u5e73\u8f7b\u58f0\u53f9\u9053\uff0c\u201c\u672a\u5fc5\u7ed9\u4eba\u5bb6\u642d\u7406\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u65f6\u95f4\u4e5f\u4e0d\u65e9\u4e86\uff0c\u6211\u5c31\u5148\u56de\u5bbe\u9986\u4f11\u606f\u4e86\u2026\u2026\u201d\u5f20\u606a\u544a\u8f9e\u9053\uff0c\u90a3\u76d8\u6b8b\u5c40\u7559\u5f85\u65e5\u540e\u518d\u7eed\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u8fdb\u8f66\u91cc\u79bb\u5f00\u7701\u59d4\u62db\u5f85\u5bbe\u9986\uff0c\u6708\u8272\u5012\u662f\u4e0d\u5dee\uff0c\u8bf4\u8d77\u6765\uff0c\u4e5f\u6ca1\u6709\u8ddf\u5468\u747e\u73ba\u5b9e\u9645\u63a5\u89e6\u8fc7\u51e0\u6b21\uff0c\u5012\u4e0d\u77e5\u9053\u4ed6\u77ed\u6682\u82cf\u9192\u8fc7\u6765\u65f6\u4f1a\u8bf4\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u201c\u4e1c\u5927\u6709\u4e9b\u6559\u5e08\u5b66\u751f\u542c\u5230\u6d88\u606f\u90fd\u8d76\u5230\u533b\u9662\u53bb\u2026\u2026\u201d\u5085\u4fca\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5475\uff0c\u201d\u5f20\u606a\u5634\u89d2\u5fae\u5fae\u4e0a\u7fd8\u9053\uff0c\u201c\u6211\u8fd8\u4ee5\u4e3a\u4ed6\u4eec\u4f1a\u6084\u65e0\u58f0\u606f\u7684\u5c06\u4e27\u4e8b\u7ed9\u529e\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u7684\u786e\u6709\u4e9b\u5947\u602a\uff0c\u201d\u5085\u4fca\u8bf4\u9053\uff0c\u201c\u5012\u662f\u6709\u4eba\u8bf4\u8981\u51c6\u5907\u529e\u8ffd\u60bc\u4f1a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u4eba\u6b7b\u4e07\u4e8b\u4f11\uff0c\u6ca1\u6709\u7ed9\u4ed6\u9616\u68fa\u5b9a\u8bba\u5df2\u7ecf\u7b97\u662f\u5bbd\u5bb9\u4e86\uff0c\u96be\u9053\u8fd8\u8981\u9ed1\u767d\u98a0\u5012\uff1f\u201d\u5f20\u606a\u5934\u75bc\u7684\u63c9\u4e86\u63c9\u592a\u9633\u7a74\uff0c\u53c8\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u5468\u747e\u73ba\u7ec8\u4e8e\u6b7b\u4e86\uff0c\u597d\u4e9b\u4eba\u5fc3\u91cc\u90fd\u677e\u4e86\u4e00\u53e3\u6c14\uff0c\u4f46\u662f\u5468\u747e\u73ba\u6b7b\u4e4b\u524d\u5230\u5e95\u8fd8\u662f\u9192\u8fc7\u6765\u7247\u523b\uff0c\u90a3\u4e9b\u4eba\u5589\u5499\u773c\u53ea\u6015\u53c8\u7ed9\u5835\u4e0a\u4e00\u5757\u77f3\u5934\uff0c\u90a3\u8fb9\u6ce8\u5b9a\u4f1a\u5f88\u70ed\u95f9\uff0c\u968f\u4ed6\u4eec\u6298\u817e\u53bb\u5427\u2026\u2026\u201d</p><p>\u3000\u3000\u5085\u4fca\u6ca1\u6709\u518d\u8bf4\u8bdd\uff0c\u5fc3\u60f3\u6216\u8bb8\u4e1c\u5927\u4f1a\u6709\u4e9b\u4e0d\u660e\u771f\u76f8\u7684\u5e08\u751f\u5c06\u5468\u747e\u73ba\u7684\u6b7b\u8ffd\u6eaf\u5230\u5f20\u606a\u5934\u4e0a\uff0c\u8fd9\u4e9b\u4eba\u5374\u4e0d\u77e5\u9053\u5468\u747e\u73ba\u8981\u4e0d\u662f\u6700\u540e\u8fd9\u822c\u6a21\u6837\uff0c\u4ed6\u7684\u5883\u9047\u4f1a\u66f4\u51c4\u60e8\uff0c\u4ed6\u90a3\u4e9b\u5bc4\u4ee5\u539a\u671b\u7684\u4eb2\u670b\u6545\u53cb\u90a3\u65f6\u5019\u8c01\u4f1a\u5e0c\u671b\u4ed6\u82cf\u9192\u8fc7\u6765\uff1f\u6216\u8bb8\u6709\u4e9b\u4eba\u522b\u6709\u610f\u56fe\u7684\u60f3\u7ed9\u5468\u747e\u73ba\u6765\u4e2a\u9ed1\u767d\u98a0\u5012\u7684\u9616\u68fa\u5b9a\u8bba\uff0c\u5c06\u90a3\u4e9b\u9ed1\u7684\u5f7b\u5e95\u7684\u6d17\u6210\u767d\u7684\uff0c\u8ba9\u7a00\u571f\u6848\u5f7b\u5e95\u7684\u6210\u4e3a\u4e0d\u7ed9\u5927\u4f17\u7ffb\u9605\u7684\u5386\u53f2\uff0c\u4ed6\u4eec\u4ee5\u540e\u5c31\u4e0d\u7528\u518d\u4e3a\u8fd9\u4ef6\u4e8b\u6709\u4ec0\u4e48\u62c5\u5fe7\u4e86\u3002\u53c8\u60f3\u8fd9\u53ea\u662f\u67d0\u4e9b\u4eba\u4e00\u53a2\u60c5\u613f\u7684\u60f3\u6cd5\u7f62\u4e86\uff0c\u4ed6\u66f4\u5e0c\u671b\u8fd9\u4e9b\u4eba\u52a8\u9759\u518d\u95f9\u5927\u4e00\u4e9b\uff0c\u8fd9\u6837\u5c31\u80fd\u8feb\u4f7f\u6709\u5173\u65b9\u9762\u89e3\u5bc6\u771f\u76f8\u3002</p><p>\u3000\u3000\u5085\u4fca\u4e5f\u4e0d\u662f\u90a3\u79cd\u4f1a\u5bf9\u6b7b\u8005\u5bbd\u5bb9\u7684\u4eba\uff0c\u53ea\u662f\u4e5f\u77e5\u9053\u89e3\u5bc6516\u6848\u7684\u771f\u76f8\u4e0d\u662f\u90a3\u4e48\u7b80\u5355\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5d14\u56fd\u6052\u5728\u5bb6\u4e00\u591c\u90fd\u6ca1\u6709\u7761\u8e0f\u5b9e\uff0c\u4ed6\u77e5\u9053\u591c\u91cc\u6709\u4e9b\u597d\u4e9b\u6559\u5e08\u3001\u5b66\u751f\u90fd\u8d76\u5230\u533b\u9662\u53bb\u3002</p><p>\u3000\u3000516\u6848\u6d89\u53ca\u8bb8\u591a\u4eba\u8fd8\u90fd\u53ea\u662f\u4f5c\u9000\u5c45\u4e8c\u7ebf\u5904\u7f6e\uff0c\u4e00\u4e9b\u7275\u6d89\u4e0d\u6df1\u7684\u4eba\u8fd8\u7ee7\u7eed\u6d3b\u8dc3\u5728\u5b66\u754c\u3001\u653f\u754c\uff0c\u73b0\u5728\u8fd8\u4e0d\u662f\u89e3\u5bc6516\u6848\u7684\u65f6\u673a\uff0c\u5373\u4f7f\u8981\u89e3\u5bc6\uff0c\u4e5f\u8f6e\u4e0d\u5230\u4e1c\u5927\u3002</p><p>\u3000\u3000\u629b\u5f00\u9690\u85cf\u5728\u80cc\u540e\u90a3\u4e9b\u9f8c\u9f8a\u7684\u4e1c\u897f\uff0c\u5468\u747e\u73ba\u5728\u5e08\u751f\u5fc3\u76ee\u4e2d\u4ee5\u53ca\u5728\u5b66\u754c\u6709\u7740\u76f8\u5f53\u4e0d\u9519\u7684\u58f0\u8a89\uff0c\u8fd9\u6070\u6070\u662f\u9ebb\u70e6\u7684\u6839\u6e90\u3002\u8fd9\u4e9b\u5e08\u751f\u80af\u5b9a\u4f1a\u8981\u6c42\u5b66\u6821\u51fa\u9762\u7ed9\u5468\u747e\u73ba\u4e3e\u529e\u8ffd\u60bc\u4f1a\uff0c\u5b66\u6821\u7c97\u66b4\u7684\u5236\u6b62\uff0c\u52bf\u5fc5\u4f1a\u5f15\u8d77\u5e08\u751f\u60c5\u7eea\u7684\u53cd\u5f39\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u6682\u65f6\u8fd8\u65e0\u6cd5\u5224\u65ad\u8fd9\u4ef6\u4e8b\u5bf9\u5f20\u606a\u4f1a\u6709\u4ec0\u4e48\u8d1f\u9762\u5f71\u54cd\uff0c\u53ea\u662f\u8ddf\u5085\u4fca\u8054\u7edc\u544a\u77e5\u6b64\u4e8b\uff0c\u672a\u89c1\u5f20\u606a\u5728\u91d1\u5c71\u6709\u4ec0\u4e48\u56de\u5e94\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u65e9\u65e9\u8d76\u5230\u9662\u529e\uff0c\u9662\u529e\u8bb8\u591a\u6559\u6388\u3001\u8bb2\u5e08\u90fd\u5728\u8bae\u8bba\u5468\u747e\u73ba\u6628\u591c\u53bb\u4e16\u4e4b\u4e8b\u3002</p><p>\u3000\u3000\u5468\u747e\u73ba\u4e24\u6b21\u660f\u53a5\u7ec8\u81f4\u4e0d\u9192\uff0c\u4ed6\u7b2c\u4e00\u6b21\u660f\u53a5\u8bf1\u53d1\u8111\u6ea2\u8840\u5c31\u662f\u5728\u6559\u5ba4\u91cc\u7ed9\u5f20\u606a\u75db\u8d23\u6240\u81f4\u3002\u867d\u7136\u5468\u747e\u73ba\u90a3\u65f6\u516c\u5f00\u5ba3\u626c\u7684\u89c2\u70b9\u5df2\u7ecf\u7ed9\u8bc1\u660e\u662f\u9519\u8bef\u7684\uff0c\u7531\u4e8e516\u6848\u5185\u60c5\u6ca1\u6709\u89e3\u5bc6\uff0c\u5468\u747e\u73ba\u7ed9\u4eba\u5370\u8c61\u53ea\u662f\u5b66\u672f\u89c2\u70b9\u6709\u504f\u5dee\u7684\u7f18\u6545\uff0c\u52a0\u4e0a\u5927\u5bb6\u90fd\u503e\u5411\u540c\u60c5\u6b7b\u53bb\u7684\u4eba\uff0c\u8bae\u8bba\u6b64\u4e8b\u65f6\uff0c\u5341\u4e2a\u5012\u6709\u4e5d\u4e2a\u5728\u6307\u8d23\u5f20\u606a\u4f5c\u4e3a\u5b66\u751f\u8fc7\u4e8e\u523b\u8584\u4e0e\u5f20\u72c2\u4e86\uff0c\u8bf4\u4e0d\u5b9a\u8fd9\u91cc\u9762\u4e5f\u6709\u5154\u6b7b\u72d0\u60b2\u3001\u540c\u4ec7\u654c\u5ffe\u4e4b\u611f\u3002\u5728\u4e2d\u56fd\uff0c\u6709\u591a\u5c11\u6559\u5e08\u80fd\u5766\u7136\u63a5\u53d7\u5b66\u751f\u7684\u53cd\u9a73\uff1f</p><p>\u3000\u3000\u867d\u7136\u5f20\u606a\u672a\u5fc5\u5728\u610f\u8fd9\u4e9b\u4e8b\u60c5\uff0c\u867d\u7136\u8fd9\u4e9b\u4e8b\u60c5\u867d\u7136\u4f1a\u5bf9\u5f20\u606a\u9020\u6210\u5b9e\u8d28\u6027\u7684\u5f71\u54cd\uff0c\u5d14\u56fd\u6052\u8fd8\u662f\u89c9\u5f97\u4e0d\u5e94\u8be5\u8ba9\u8fd9\u4e9b\u4e8b\u60c5\u7ee7\u7eed\u8513\u5ef6\u4e0b\u53bb\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u7ed9\u6821\u957f\u674e\u9e3f\u9e23\u6253\u4e86\u7535\u8bdd\uff0c\u8dd1\u5230\u6821\u529e\u53bb\u627e\u674e\u9e3f\u9e23\uff0c\u5c06\u4ed6\u7684\u62c5\u5fe7\u8ddf\u674e\u9e3f\u9e23\u8bf4\u4e86\u8bf4\u3002</p><p>\u3000\u3000\u674e\u9e3f\u9e23\u601d\u5fd6\u7247\u523b\uff0c\u8bf4\u9053\uff1a\u201c\u7167\u987e\u6b7b\u8005\u7684\u540d\u8a89\uff0c\u4e5f\u4e0d\u80fd\u53bb\u641e\u4ec0\u4e48\u662f\u975e\u4e0d\u5206\u7684\u9616\u68fa\u5b9a\u8bba\uff01\u6709\u8c01\u60c5\u7eea\u95f9\u5f97\u6700\u51f6\uff0c\u53ef\u4ee5\u9002\u5f53\u7684\u544a\u8bc9\u4ed6\u4eec\u4e00\u4e9b\u771f\u76f8\u2026\u2026\u201d</p><p>\u3000\u3000\u5d14\u56fd\u6052\u5fc3\u60f3\u8fd9\u4e48\u5904\u7406\u4e5f\u597d\uff0c\u53e3\u5934\u76f8\u4f20\uff0c\u4e5f\u7b97\u4e0d\u4e0a\u516c\u5f00\u6279\u9732\u771f\u76f8\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u8c22\u6c49\u9756\u5230\u5efa\u90ba\uff0c\u5374\u6ca1\u6709\u5f80\u533b\u9662\u53bb\uff0c\u4ed6\u5728\u5e9c\u9752\u8def\u6d77\u6cf0\u603b\u90e8\u5927\u53a6\u91cc\uff0c\u7ad9\u5728\u843d\u5730\u7a97\u524d\u51dd\u89c6\u7740\u7a97\u5916\u7684\u5efa\u7b51\u4e0e\u5efa\u7b51\u7f1d\u9699\u95f4\u7684\u84dd\u8272\u5929\u7a7a\u3002</p><p>\u3000\u3000\u8c22\u610f\u521a\u4ece\u533b\u9662\u8d76\u56de\u6765\u3002</p><p>\u3000\u3000\u201c\u6628\u5929\u591c\u91cc\uff0c\u5c31\u6709\u597d\u4e9b\u4e1c\u5927\u7684\u8001\u5e08\u4ee5\u53ca\u5b66\u751f\u90fd\u8d76\u5230\u533b\u9662\u53bb\uff0c\u201d\u8c22\u610f\u5c06\u4e00\u591c\u672a\u7761\u6709\u4e9b\u51cc\u4e71\u7684\u5934\u53d1\u7406\u4e86\u7406\uff0c\u201c\u90fd\u4e0d\u77e5\u9053\u662f\u8c01\u8fd9\u4e48\u53ca\u65f6\u5c06\u6d88\u606f\u4f20\u4e86\u51fa\u53bb\uff0c\u4f1a\u4e0d\u4f1a\u662f\u9526\u6e56\u7684\u4eba\uff1f\u201d</p><p>\u3000\u3000\u201c\u5f20\u606a\u8981\u60f3\u4ed6\u4e0d\u5f97\u5b89\u5b81\uff0c\u5c31\u4e0d\u4f1a\u5bb9\u4ed6\u5b89\u9759\u7684\u5728\u533b\u9662\u7761\u8fd9\u4e48\u4e45\u4e86\u3002\u201d\u8c22\u6c49\u9756\u8bf4\u9053\uff0c\u201c\u60f3\u641e\u4e8b\u7684\u4eba\u591a\u7684\u662f\uff01\u201d</p><p>\u3000\u3000\u201c\u55ef\uff0c\u201d\u8c22\u610f\u6b64\u65f6\u4e5f\u53ea\u662f\u7565\u77e5516\u6848\u7275\u6d89\u751a\u5e7f\uff0c\u771f\u6b63\u7ed9\u5904\u7406\u7684\u4eba\u5374\u4e0d\u591a\uff0c\u597d\u4e9b\u4eba\u73b0\u5728\u8fd8\u6d3b\u5f97\u5149\u9c9c\uff0c\u8fd9\u4e9b\u4eba\u91cc\u5f53\u7136\u4f1a\u66f4\u5e0c\u671b\u5468\u747e\u73ba\u6084\u7136\u65e0\u58f0\u7684\u6b7b\u53bb\uff0c\u4f46\u662f\u5468\u747e\u73ba\u5728\u54bd\u6c14\u524d\u9192\u8fc7\u6765\u7247\u523b\uff0c\u8fd9\u591a\u5c11\u8ba9\u4eba\u6709\u4e9b\u4e0d\u5b89\u5fc3\u4e86\uff0c\u91cd\u65b0\u4fa6\u529e516\u6848\u5df2\u7ecf\u4e0d\u5927\u53ef\u80fd\uff0c\u5c06\u4e8b\u60c5\u95f9\u5927\u5c31\u77e5\u9053\u5468\u747e\u73ba\u6b7b\u524d\u5230\u5e95\u6709\u6ca1\u6709\u8bf4\u8fc7\u4ec0\u4e48\u8bdd\uff0c\u81f3\u5c11\u80fd\u56fe\u4e2a\u5b89\u5fc3\uff0c\u4ed6\u6447\u5934\u800c\u7b11\uff0c\u201c\u4eca\u5929\u4e0a\u5348\u8dd1\u53bb\u533b\u9662\u7684\u5e08\u751f\u66f4\u591a\u4e86\uff0c\u8fd8\u6709\u4e9b\u4ed6\u66fe\u7ecf\u7684\u5b66\u751f\uff0c\u542c\u5230\u6d88\u606f\u90fd\u6253\u7535\u8bdd\u8fc7\u6765\u95ee\u5019\uff0c\u8fd8\u6709\u4eba\u6b63\u4ece\u5916\u5730\u8d76\u8fc7\u6765\u3002\u8fd9\u4e9b\u4eba\u5bf9\u4ed6\u660f\u8ff7\u4e0d\u9192\u4e4b\u540e\u6240\u53d7\u5230\u7684\u5f85\u9047\u9887\u611f\u4e0d\u516c\uff0c\u5230\u73b0\u5728\uff0c\u5b66\u6821\u4e0e\u56fd\u5546\u9662\u7684\u9886\u5bfc\u65e0\u4e00\u4eba\u4eb2\u81ea\u5230\u5b66\u6821\u534f\u8c03\u6cbb\u4e27\u7684\u4e8b\u60c5\uff0c\u66f4\u662f\u8ba9\u4ed6\u4eec\u611f\u5230\u6c14\u6124\uff0c\u7fa4\u60c5\u6fc0\u6124\uff0c\u81ea\u53d1\u7684\u6210\u7acb\u4e86\u6cbb\u4e27\u59d4\u5458\u4f1a\uff0c\u90a3\u8fb9\u867d\u7136\u52aa\u529b\u60f3\u963b\u6b62\u641e\u4ec0\u4e48\u8ffd\u60bc\u4f1a\uff0c\u53ea\u662f\u4ed6\u4eec\u60f3\u4f4e\u8c03\u7684\u6001\u5ea6\u66f4\u8ba9\u4eba\u6000\u7591\u662f\u53d7\u5230\u5b66\u6821\u65b9\u9762\u7684\u538b\u529b\u2026\u2026\u8fd9\u5927\u6982\u5c31\u662f\u6709\u82e6\u8bf4\u4e0d\u51fa\u5427\uff01\u201d</p><p>\u3000\u3000\u201c\u4ed6\u60f3\u8981\u4fdd\u6301\u6b7b\u540e\u7684\u58f0\u8a89\u53ea\u6015\u662f\u56f0\u96be\u4e86\uff0c\u201d\u8c22\u6c49\u9756\u6de1\u6de1\u7684\u8bf4\u9053\uff0c\u201c\u6709\u4e9b\u4eba\u662f\u4e0d\u4f1a\u5bb9\u5fcd\u5c06\u810f\u6c34\u6cfc\u5230\u5f20\u606a\u5934\u4e0a\u7684\uff0c\u4e1c\u5927\u53ea\u6015\u4f1a\u51fa\u9762\u516c\u5e03\u771f\u76f8\u2026\u2026\u201d\u5fc3\u60f3\u8eab\u8d25\u540d\u88c2\uff0c\u5927\u6982\u5c31\u662f\u8bf4\u8fd9\u4e2a\u7ed3\u5c40\u5427\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5e73\u65e5\u4e0e\u5468\u747e\u73ba\u4ea4\u5f80\u5bc6\u5207\u7684\u6559\u6388\u4e0e\u9752\u5e74\u6559\u5e08\u518d\u52a0\u4e0a\u5468\u747e\u73ba\u6559\u5b66\u8fd9\u4e9b\u5e74\u6765\u4e00\u4e9b\u9887\u6709\u6210\u5c31\u7684\u5f97\u610f\u5b66\u751f\u7ec4\u6210\u6cbb\u4e27\u59d4\u5458\u4f1a\u3002\u5c31\u5468\u747e\u73ba\u7684\u59bb\u5b50\u800c\u8a00\uff0c\u6001\u5ea6\u6447\u6446\u4e0d\u5b9a\uff0c\u5fc3\u77e5\u4e08\u592b\u6ca1\u6709\u5f00\u8ffd\u60bc\u4f1a\u7684\u8d44\u683c\uff0c\u5fc3\u91cc\u4e5f\u662f\u5962\u671b\u8fd9\u4e48\u95f9\u4e00\u95f9\uff0c\u6216\u8bb8\u80fd\u7ed9\u4e08\u592b\u6b7b\u540e\u6b63\u540d\uff0c\u54ea\u6015\u662f\u865a\u5047\u7684\uff0c\u8c01\u5e0c\u671b\u81ea\u5df1\u7684\u4e08\u592b\u4e0d\u6e05\u4e0d\u695a\u7684\u6b7b\u6389\uff1f\u5bf9\u5468\u747e\u73ba\u3001\u8c22\u6c49\u660e\u7b49\u4eba\u5fc3\u91cc\u4e5f\u6709\u6028\u6068\uff0c\u5f53\u521d\u653e\u5f03\u6cbb\u7597\u5c31\u662f\u4ed6\u4eec\u7684\u51b3\u5b9a\uff0c\u4e0d\u7136\u7684\u8bdd\uff0c\u81f3\u5c11\u4eba\u80fd\u7559\u4e0b\u6765\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u79ef\u6781\u6350\u94b1\u7b79\u529e\u4e27\u4e8b\uff0c\u8054\u7cfb\u6ba1\u4eea\u9986\u5148\u5c06\u9057\u4f53\u79fb\u8fc7\u53bb\uff0c\u5b89\u6392\u5916\u5730\u8d76\u6765\u8ffd\u60bc\u5b66\u751f\u4e0e\u4eb2\u670b\u7684\u98df\u5bbf\uff0c\u5546\u8ba8\u6cbb\u4e27\u4e8b\u5b9c\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u6cbb\u4e27\u59d4\u5458\u4f1a\u9996\u5148\u8981\u505a\u7684\u5c31\u662f\u627e\u5b66\u6821\u8ba8\u56de\u516c\u9053\uff0c\u6ca1\u6709\u5b66\u6821\u7684\u51fa\u9762\uff0c\u8ffd\u60bc\u4f1a\u603b\u662f\u4e0d\u6e05\u4e0d\u767d\u7684\u3002\u8fd1\u4e8c\u5341\u4eba\u4e58\u8f66\u6d69\u6d69\u8361\u8361\u7684\u5f80\u5b66\u6821\u8d76\u53bb\uff0c\u76f4\u63a5\u5c06\u5d14\u56fd\u6052\u5835\u5728\u529e\u516c\u5ba4\u91cc\u3002</p><p>\u3000\u3000\u5d14\u56fd\u6052\u5bf9\u6b64\u65e9\u6709\u51c6\u5907\uff0c\u53ea\u8ba9\u4ed6\u4eec\u63a8\u9009\u4e94\u540d\u4ee3\u8868\u8fdb\u4ed6\u7684\u529e\u516c\u5ba4\u8be6\u8c08\uff0c\u534a\u5c0f\u65f6\u540e\u4e94\u540d\u4ee3\u8868\u5509\u6c14\u53f9\u606f\u7684\u51fa\u6765\uff0c\u4e0d\u80af\u591a\u8bf4\u4ec0\u4e48\uff0c\u53ea\u8981\u6c42\u9000\u51fa\u51b6\u4e27\u59d4\u5458\u4f1a\u4e0d\u518d\u53c2\u4e0e\u4ec0\u4e48\u4e27\u4e8b\u3002</p><p>\u3000\u3000\u5927\u5bb6\u90fd\u4e0d\u662f\u50bb\u5b50\uff0c\u6240\u8c13\u7684\u6cbb\u4e27\u59d4\u5458\u4f1a\u5c31\u8fd9\u6837\u571f\u5d29\u74e6\u89e3\u3002\u5c31\u50cf\u8fd9\u4e16\u754c\u603b\u6709\u597d\u5947\u5fc3\u7279\u522b\u65fa\u76db\u7684\u4eba\uff0c\u4e5f\u603b\u6709\u600e\u4e48\u4e5f\u7ba1\u4e0d\u4f4f\u81ea\u5df1\u5634\u7684\u4eba\uff0c\u518d\u8bf4\u5468\u747e\u73ba\u4f4f\u9662\u91cc\uff0c\u4e5f\u4e0d\u662f\u6ca1\u6709\u8d28\u7591\u7684\u58f0\u97f3\uff0c\u4e00\u4e9b\u4e8b\u60c5\u7ecf\u8fc7\u4f17\u53e3\u76f8\u4f20\u7684\u827a\u672f\u52a0\u5de5\uff0c\u5468\u747e\u73ba\u7684\u6b7b\u5c31\u6210\u4e86\u5e94\u5f97\uff0c\u751a\u81f3\u5468\u747e\u73ba\u5f03\u5b98\u4ece\u5b66\u7684\u90a3\u4e9b\u65e7\u95fb\u52a3\u8ff9\u4e5f\u7ed9\u4eba\u4ece\u6545\u7eb8\u5806\u91cc\u7ffb\u4e86\u51fa\u6765\u5ba3\u626c\u3002</p><p>\u3000\u3000\u6cbb\u4e27\u6d3b\u52a8\u6210\u4e86\u4e00\u51fa\u95f9\u5267\uff0c\u5230\u9ec4\u660f\u65f6\uff0c\u7a00\u7a00\u62c9\u62c9\u53ea\u5269\u4e0b\u4e0d\u5230\u4e09\u4e94\u4eba\u8fd8\u575a\u6301\u7559\u5728\u6ba1\u4eea\u9986\u91cc\u6ca1\u6709\u8d70\uff0c\u90a3\u4e9b\u5b66\u751f\u4e0e\u6545\u53cb\u6350\u51d1\u7684\u6cbb\u4e27\u7ecf\u8d39\uff0c\u4e5f\u4e0d\u77e5\u9053\u7ed9\u8c01\u5377\u8d70\u4e86\u3002\u8c22\u5251\u5357\u770b\u7740\u51c4\u51c9\u7684\u573a\u9762\uff0c\u4e5f\u5f88\u65e0\u8bed\uff0c\u5e2e\u7740\u5c06\u8d39\u7528\u7ed3\u4e86\uff0c\u60f3\u7740\u6084\u65e0\u58f0\u606f\u7684\u706b\u5316\uff0c\u5c06\u9aa8\u7070\u5e26\u56de\u65b0\u6cf0\u5b89\u846c\u5c31\u597d\u4e86\uff0c\u5230\u5e95\u662f\u8c01\u5c06\u6d88\u606f\u6563\u5e03\u51fa\u53d1\u4e86\uff0c\u8fd8\u771f\u95f9\u51fa\u4e00\u573a\u95f9\u5267\uff1f</p><p>\u3000\u3000\u5b66\u6821\u90a3\u8fb9\u53ea\u5f53\u662f\u6495\u7834\u8138\u76ae\uff0c\u5c06\u7fa4\u60c5\u6fc0\u6124\u7684\u6cbb\u4e27\u59d4\u5458\u4f1a\u74e6\u89e3\u6389\u4e4b\u540e\uff0c\u5c31\u6b63\u5f0f\u6d3e\u4eba\u8fc7\u6765\u5904\u7406\u540e\u4e8b\uff0c\u63d0\u51fa\u8fd1\u671f\u5185\u8981\u5c06\u5468\u747e\u73ba\u5728\u6821\u5185\u7684\u4f4f\u623f\u6536\u56de\u3002</p><p>\u3000\u3000\u8c22\u5251\u5357\u4e5f\u6ca1\u6709\u529b\u6c14\u8ddf\u4e1c\u5927\u4e89\u8bba\uff0c\u8205\u5988\u8981\u662f\u8fd8\u60f3\u7559\u5728\u5efa\u90ba\uff0c\u4ed6\u638f\u94b1\u7ed9\u5979\u4e70\u5957\u623f\u5b50\u5c31\u662f\uff0c\u5b9e\u5728\u4e0d\u884c\u5c31\u56de\u65b0\u6cf0\u8001\u5bb6\u53bb\uff0c\u4ed6\u89c1\u4ed6\u5988\u4e5f\u8fdc\u6ca1\u6709\u62c5\u4efb\u91d1\u5c71\u5e02\u59d4\u4e66\u8bb0\u65f6\u7684\u7cbe\u6c14\u52b2\uff0c\u8fd9\u4e00\u5e74\u591a\u65f6\u95f4\u8870\u8001\u5f97\u5f88\u5feb\u3002</p><p>\u3000\u3000\u4e3a\u4e27\u4e8b\uff0c\u8c22\u5251\u5357\u5728\u5efa\u90ba\u803d\u6401\u4e86\u4e09\u5929\uff0c\u4ed6\u77e5\u9053\u8fd9\u4e09\u5929\u65f6\u95f4\u6c60\u4f50\u79c0\u85cf\u3001\u4e25\u6587\u4ecb\u3001\u674e\u5728\u6d19\u7b49\u4eba\u90fd\u7559\u5728\u5efa\u90ba\uff0c\u4e5f\u4e0d\u77e5\u9053\u4ed6\u4eec\u5bc6\u8c0b\u51fa\u9488\u5bf9\u9526\u6e56\u7684\u826f\u7b56\u6765\u6ca1\u6709\uff0c\u7ecf\u8fc7\u8fd9\u573a\u4e27\u4e8b\uff0c\u8c22\u5251\u5357\u66f4\u662f\u5bf9\u9488\u5bf9\u9526\u6e56\u7684\u5bc6\u8c0b\u63d0\u4e0d\u8d77\u5174\u8da3\u6765\u3002</p><p>\u3000\u3000\u5728\u4ed6\u79bb\u5f00\u5efa\u90ba\u4e4b\u524d\uff0c\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u5728\u5efa\u90ba\u6b63\u5f0f\u6700\u5148\u63a8\u51fa\u5404\u81ea\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u4f4e\u7aef\u624b\u673a\u4ea7\u54c1\uff0c\u4ed6\u7279\u610f\u5750\u8f66\u5230\u6d77\u6cf0\u8fde\u9501\u95e8\u5e97\u5c06\u4e24\u6b3e\u65b0\u673a\u90fd\u4e70\u4e86\u4e00\u53ea\u3002</p><p>\u3000\u3000\u867d\u7136\u4e0d\u77e5\u9053\u9526\u6e56\u4e3a\u4ec0\u4e48\u6ca1\u6709\u53ca\u65f6\u63a8\u51fa\u540c\u6837\u7684\u4f4e\u7aef\u624b\u673a\uff0c\u4f46\u662f\u4ece\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u7684\u4e24\u6b3e\u4f4e\u7aef\u624b\u673a\u4e0a\uff0c\u4f3c\u4e4e\u5df2\u7ecf\u80fd\u770b\u5230\u9526\u6e56\u8d70\u4e0a\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u738b\u5ea7\u7684\u672a\u6765\u3002</p><p>\u3000\u3000\u4e2d\u4f4e\u7aef\u5e02\u573a\uff0c\u5f53\u6280\u672f\u4e0d\u6210\u969c\u788d\uff0c\u5236\u9020\u6210\u672c\u5c06\u6210\u4e3a\u5173\u952e\u6027\u7684\u7ade\u4e89\u8981\u7d20\uff0c\u5728\u9ad8\u7aef\u5e02\u573a\uff0c\u5236\u9020\u6210\u672c\u867d\u7136\u4e0d\u662f\u7279\u522b\u7684\u5173\u952e\uff0c\u4f46\u4e5f\u662f\u6781\u5176\u91cd\u8981\u7684\u4e00\u4e2a\u56e0\u7d20\uff0c\u9526\u6e56\u7684\u6280\u672f\u3001\u5236\u9020\u4f53\u7cfb\u65e5\u76ca\u6210\u719f\uff0c\u5c06\u53d1\u6325\u96be\u4ee5\u60f3\u8c61\u7684\u5a01\u529b\u3002</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u4e5f\u8feb\u4e0d\u53ca\u5f85\u8981\u5c06\u4e00\u90e8\u5206\u6676\u5706\u4ea7\u80fd\u8fc1\u5230\u4e2d\u56fd\u6765\uff0c\u4f3c\u4e4e\u4e5f\u610f\u8bc6\u5230\u8fd9\u4e00\u70b9\u3002</p></article>"}}}]);
