(window.webpackJsonp=window.webpackJsonp||[]).push([[1089],{1112:function(p){p.exports={chapterTitle:null,title:"\u7b2c889\u7ae0 \u6349\u5978\u5728\u623f",content:"<article><h2>\u7b2c889\u7ae0 \u6349\u5978\u5728\u623f</h2><p>\u3000\u3000\u201c\u99a8\u4e88\u8981\u662f\u77e5\u9053\u79c0\u85cf\u541b\u4e3a\u5979\u5f00\u5b66\u4e13\u7a0b\u8d76\u5230\u5efa\u90ba\u6765\uff0c\u5979\u4e00\u5b9a\u4f1a\u5f02\u5e38\u6b23\u559c\u7684\uff0c\u4e00\u5b9a\u4f1a\u5f88\u611f\u52a8\u7684\u2026\u2026\u201d\u674e\u5728\u6d19\u8ddf\u6c60\u4f50\u79c0\u85cf\u8bf4\u8bdd\u65f6\u4e0d\u65e0\u5949\u627f\u7684\u610f\u5473\uff0c\u4ed6\u81ea\u5df1\u5012\u662f\u6d51\u7136\u4e0d\u4ecb\u610f\uff0c\u6307\u6325\u7740\u6c60\u4f50\u79c0\u85cf\u7684\u53f8\u673a\u5c06\u8f66\u5f00\u5230\u99a8\u4e88\u79df\u4f4f\u7684\u522b\u5885\u697c\u4e0b\uff0c\u89c1\u524d\u9762\u7684\u67f3\u6811\u4e0b\u505c\u7740\u4e24\u90e8\u5954\u9a70\u8f66\uff0c\u89c9\u5f97\u5f88\u662f\u5947\u602a\uff0c\u524d\u9762\u662f\u5c0f\u533a\u91cc\u7684\u516c\u5f00\u7eff\u5730\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u770b\u7740\u90a3\u4e24\u8f86\u5954\u9a70\u8f66\uff0c\u8fd8\u662f\u5954\u9a70\u8f66\u7cfb\u5217\u914d\u7f6e\u6700\u5962\u534e\u7684\u4e24\u6b3e\uff0c\u7b11\u7740\u8ddf\u674e\u5728\u6d19\u8bf4\u9053\uff1a\u201c\u652f\u90a3\u4eba\u8fd8\u662f\u5f88\u591a\u4eba\u5f88\u5bcc\u88d5\uff0c\u867d\u7136\u5927\u90e8\u5206\u7684\u652f\u90a3\u4eba\u5f88\u7a77\uff0c\u4f46\u662f\u5b98\u5458\u4e0e\u5546\u4eba\u4ee5\u53ca\u4e0e\u4ed6\u4eec\u76f8\u5173\u7684\u4eba\u603b\u662f\u5bcc\u88d5\u7684\uff0c\u8fd9\u90e8\u5206\u4eba\u6bd4\u4f8b\u4e0d\u5927\uff0c\u4f46\u662f\u76f8\u6bd4\u8f83\u8d77\u652f\u90a3\u5341\u4e09\u4ebf\u7684\u4eba\u53e3\uff0c\u6240\u5f62\u6210\u7684\u5e02\u573a\u5c31\u5f02\u5e38\u7684\u5e9e\u5927\uff0c\u6211\u542c\u8bf4\u4e09\u661f\u4e5f\u5f88\u91cd\u89c6\u652f\u90a3\u7684\u5e02\u573a\uff1f\u201d</p><p>\u3000\u3000\u6ca1\u6709\u4e2d\u56fd\u4eba\u5728\u573a\uff0c\u5076\u5c14\u8bf4\u4e00\u53e5\u8bdd\u6b67\u89c6\u4e2d\u56fd\u7684\u8bdd\uff0c\u5f53\u7136\u662f\u65e0\u4f24\u5927\u96c5\u7684\uff0c\u4e0d\u8fc7\u674e\u5728\u6d19\u60f3\u53bb\u5e74\u5728\u5b66\u5e9c\u5df7\u7684\u97e9\u56fd\u9910\u5385\u91cc\u62ff\u97e9\u8bed\u4e0e\u91d1\u5357\u52c7\u4ea4\u8c08\uff0c\u8a00\u8bed\u91cc\u4e5f\u6d41\u9732\u51fa\u5bf9\u8fd9\u4e2a\u56fd\u5bb6\u7684\u4e0d\u5c0a\u656c\uff0c\u90a3\u4e5f\u6ca1\u6709\u4ec0\u4e48\u5927\u4e0d\u4e86\u7684\u5730\u65b9\uff0c\u5374\u662f\u6ca1\u6709\u60f3\u5230\u4f1a\u6709\u4e2a\u542c\u5f97\u61c2\u97e9\u8bed\u7684\u5f20\u606a\u5750\u5728\u4ed6\u4eec\u8eab\u540e\uff0c\u8fd8\u501f\u673a\u5c06\u4ed6\u4eec\u6559\u8bad\u4e86\u4e00\u901a\uff0c\u60f3\u60f3\u5c31\u89c9\u5f97\u5fc3\u91cc\u523a\u75db\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u5f53\u7136\u4e0d\u4f1a\u7ea0\u6b63\u6c60\u4f50\u79c0\u85cf\u8bdd\u91cc\u5bf9\u4e2d\u56fd\u7684\u4e0d\u5c0a\u656c\uff0c\u4ed6\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4e09\u661f\u603b\u662f\u4ef0\u8d56\u4e09\u4e95\u7684\u2026\u2026\u201d\u4e9a\u6d32\u91d1\u878d\u5371\u673a\u6709\u5411\u5168\u7403\u8513\u5ef6\u7684\u8d8b\u52bf\uff0c\u4e09\u661f\u4e5f\u6df1\u9677\u5176\u4e2d\uff0c\u4e0d\u5f97\u4e0d\u51fa\u552e\u4e00\u4e9b\u91cd\u8981\u8d44\u4ea7\u4e0e\u4e1a\u52a1\u6765\u7ef4\u6301\u96c6\u56e2\u7684\u73b0\u91d1\u6d41\u4ee5\u6e21\u96be\u5173\uff0c\u6240\u4ee5\u674e\u5728\u6d19\u5728\u6c60\u4f50\u79c0\u85cf\u9762\u524d\u80fd\u5f88\u81ea\u7136\u7684\u653e\u4f4e\u59ff\u6001\uff0c\u4e5f\u5e0c\u671b\u6c60\u4f50\u79c0\u85cf\u4e0e\u99a8\u4e88\u7684\u604b\u60c5\u5bf9\u4e09\u661f\u6709\u6240\u5e2e\u52a9\uff0c\u4ed6\u4e0e\u6c60\u4f50\u5b63\u85cf\u8d70\u5230\u53f0\u9636\u4e0a\u6309\u7535\u95e8\u94c3\u3002</p><p>\u3000\u3000\u9a6c\u6d77\u9f99\u5750\u5728\u8f66\u91cc\uff0c\u4ef0\u5934\u9760\u7740\u8f66\u6905\u9760\u80cc\u7761\u7740\uff0c\u7ed9\u8fb9\u4e0a\u7684\u5de5\u4f5c\u4eba\u5458\u63a8\u9192\uff0c\u8be7\u7136\u770b\u89c1\u674e\u99a8\u4e88\u7684\u522b\u5885\u697c\u524d\u505c\u7740\u4e00\u8f86\u9ed1\u8272\u7684\u6797\u80af\uff0c\u770b\u5230\u674e\u5728\u6d19\u3001\u6c60\u4f50\u79c0\u85cf\u7ad9\u5728\u522b\u5885\u524d\u6309\u95e8\u94c3\uff0c\u5927\u547c\u4e0d\u5999\uff0c\u5fc3\u60f3\u5f20\u606a\u7ed9\u4eba\u5bb6\u5835\u5c4b\u91cc\u4e86\uff0c\u5fd9\u63a8\u8f66\u95e8\u4e0b\u4e86\u8f66\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u7ed9\u95e8\u94c3\u58f0\u60ca\u54cd\uff0c\u5fc3\u75db\u6b32\u88c2\uff0c\u6709\u90a3\u4e48\u4e00\u77ac\u95f4\u6709\u4e0d\u77e5\u8eab\u5904\u4f55\u5904\u7684\u832b\u7136\u611f\uff0c\u53ea\u8bb0\u5f97\u770b\u5230\u4e00\u4e2a\u719f\u6089\u7684\u80cc\u666f\u5c31\u4e0b\u610f\u8bc6\u7684\u51b2\u51fa\u9152\u5e97\u91cc\uff0c\u7ed9\u6cea\u6c34\u6a21\u7cca\u4e86\u53cc\u773c\uff0c\u5176\u5b9e\u4e5f\u662f\u7ad9\u8d77\u6765\u7684\u77ac\u95f4\uff0c\u9189\u610f\u5168\u6d8c\u4e86\u4e0a\u6765\uff0c\u8eab\u4f53\u4e5f\u65e0\u6cd5\u63a7\u5236\u4f4f\uff0c\u5012\u662f\u6ca1\u6709\u770b\u6e05\u695a\u4eba\u7684\u8138\uff0c\u5c31\u6709\u4e00\u79cd\u610f\u8bc6\u5d29\u6e83\u6389\u7684\u611f\u89c9\u3002</p><p>\u3000\u3000\u5f20\u606a\u611f\u89c9\u53cc\u817f\u7ed9\u62b1\u5728\u4e00\u4e2a\u6e29\u70ed\u67d4\u8f6f\u7684\u6000\u62b1\u91cc\uff0c\u6602\u7740\u5934\u770b\u8fc7\u53bb\uff0c\u5927\u817f\u7ed9\u4e2a\u5973\u4eba\u7d27\u7d27\u7684\u6402\u5728\u6000\u91cc\uff0c\u90a3\u5973\u4eba\u7684\u8138\u8d34\u7740\u81ea\u5df1\u7684\u5927\u817f\u6839\u90e8\uff0c\u8138\u7ed9\u4e4c\u9ed1\u67d4\u987a\u7684\u957f\u53d1\u906e\u4f4f\u3002\u5f20\u606a\u4e0d\u4e89\u6c14\u7684\u5fc3\u614c\u8d77\u6765\uff0c\u539f\u6765\u610f\u8bc6\u5d29\u6e83\u524d\u7684\u8bb0\u5fc6\u77ac\u95f4\u5e76\u4e0d\u662f\u5728\u505a\u68a6\u3002</p><p>\u3000\u3000\u5f20\u606a\u817f\u4e00\u52a8\uff0c\u674e\u99a8\u4e88\u5c31\u9192\u4e86\u8fc7\u6765\uff0c\u6709\u51e0\u79d2\u949f\u7684\u8ff7\u7cca\uff0c\u5076\u5c14\u624d\u610f\u8bc6\u5230\u81ea\u5df1\u7adf\u7136\u5c06\u5f20\u606a\u7684\u5927\u817f\u7d27\u7d27\u6402\u8d34\u7740\u81ea\u5df1\u7684\u80f8\u53e3\u7761\u89c9\uff0c\u614c\u4e71\u95f4\u653e\u5f00\uff0c\u6491\u8d77\u8eab\u5b50\u6765\u770b\u7740\u5f20\u606a\uff1a\u201c\u4f60\u9192\u6765\u4e86\uff1f\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u7684\u9886\u53e3\u8361\u4e0b\u6765\uff0c\u5979\u91cc\u9762\u6ca1\u6709\u6234\u4e73\u7f69\uff0c\u7855\u5927\u96ea\u5ae9\u7684\u4e73\u623f\u5806\u6324\u5728\u9886\u53e3\uff0c\u5f20\u606a\u4e4d\u7136\u53d1\u73b0\u662f\u674e\u99a8\u4e88\u8d34\u7740\u81ea\u5df1\u7684\u5927\u817f\u6839\u90e8\u800c\u7761\u89c9\uff0c\u4e0d\u662f\u671f\u5f85\u4e2d\u7684\u90a3\u4e2a\u5973\u5b69\u5b50\uff0c\u5fc3\u83ab\u540d\u7684\u523a\u75db\u4e86\u4e00\u4e0b\uff0c\u674e\u99a8\u4e88\u4e0e\u6b64\u65f6\u8fd8\u6ca1\u6709\u51fa\u9053\u7684\u97e9\u56fd\u4e00\u4e2a\u8457\u540d\u8f66\u6a21\u5f88\u76f8\u50cf\uff0c\u5f20\u606a\u7535\u8111\u91cc\u56fe\u7247\u6536\u85cf\u5939\u91cc\u4e00\u66fe\u90fd\u5b58\u653e\u7740\u8fd9\u4e2a\u8f66\u6a21\u7684\u56fe\u7247\uff0c\u800c\u4e14\u4ed6\u53c8\u559c\u6b22\u80cc\u59ff\uff0c\u96be\u602a\u9189\u9152\u65f6\u4f1a\u89c9\u5f97\u674e\u99a8\u4e88\u7684\u80cc\u5f71\u6709\u7740\u5f02\u6837\u7684\u719f\u6089\uff1f\u5f20\u606a\u6307\u4e86\u6307\u674e\u99a8\u4e88\u7684\u9886\u53e3\uff0c\u793a\u610f\u5979\u8d70\u5149\u4e86\u3002</p><p>\u3000\u3000\u201c\u554a\uff01\u201d\u674e\u99a8\u4e88\u8fd9\u624d\u610f\u8bc6\u5230\u81ea\u5df1\u91cc\u9762\u4ec0\u4e48\u90fd\u6ca1\u6709\u7a7f\uff0cT\u6064\u886c\u8fd8\u6709\u4e9b\u5fae\u900f\u660e\uff0c\u53cc\u624b\u6342\u7740\u80f8\u53e3\uff0c\u60ca\u7f9e\u7684\u4e0d\u6562\u770b\u5f20\u606a\uff0c\u4f4e\u7740\u5934\u89e3\u8bf4\u9053\uff0c\u201c\u6628\u5929\u770b\u89c1\u4f60\u9189\u5012\u5728\u8857\u5934\uff0c\u5c31\u5c06\u4f60\u7ed9\u6361\u4e86\u56de\u6765\u2014\u2014\u201d</p><p>\u3000\u3000\u201c\u6361\u56de\u6765\uff1f\u201d\u5f20\u606a\u82e6\u7b11\u4e86\u4e00\u4e0b\uff0c\u674e\u99a8\u4e88\u73b0\u5728\u7684\u6c49\u8bed\u7528\u8bcd\u8fd8\u771f\u662f\u6070\u5f53\u554a\u3002</p><p>\u3000\u3000\u674e\u99a8\u4e88\u8fd9\u624d\u6ce8\u610f\u5230\u697c\u4e0b\u95e8\u94c3\u58f0\u5927\u4f5c\uff0c\u65e0\u7455\u53bb\u7ec6\u60f3\u5f20\u606a\u8bdd\u91cc\u7684\u82e6\u6da9\u5473\u9053\uff0c\u518d\u8bf4\u4e24\u4eba\u5171\u5367\u4e00\u5e2d\uff0c\u8ba9\u5979\u5fc3\u91cc\u603b\u6709\u4e9b\u7f9e\u4e8e\u9762\u5bf9\u5f20\u606a\uff0c\u5fc3\u614c\u614c\u7684\u8d64\u8db3\u4e0b\u4e86\u5e8a\uff0c\u624b\u6342\u7740\u80f8\u53e3\u5230\u697c\u4e0b\u53bb\u5f00\u95e8\u53bb\u3002</p><p>\u3000\u3000\u201c\u4f60\u8fd8\u5728\u7761\u89c9\u5417\uff0c\u600e\u4e48\u8fd9\u4e48\u6162\u624d\u8fc7\u6765\u5f00\u95e8\uff1f\u201d\u674e\u5728\u6d19\u7a0d\u6709\u4e0d\u6ee1\u3001\u7591\u60d1\u7684\u770b\u7740\u9b13\u53d1\u51cc\u4e71\u7684\u99a8\u4e88\u3002\u6c60\u4f50\u79c0\u85cf\u4e5f\u89c9\u5f97\u6709\u4e9b\u7591\u60d1\uff0c\u89c1\u674e\u99a8\u4e88\u8138\u988a\u8fd8\u5e26\u7740\u4e9b\u7f9e\u6da9\u7684\u9161\u7ea2\uff0c\u89c1\u5979\u6342\u7740\u80f8\u53e3\uff0c\u5fc3\u60f3\u5979\u91cc\u9762\u6ca1\u6709\u7a7f\u5185\u8863\uff1f</p><p>\u3000\u3000\u201c\u4f60\u4eec\u600e\u4e48\u5c31\u8fc7\u6765\u4e86\uff0c\u90fd\u6ca1\u6709\u63d0\u524d\u6253\u5f00\u62db\u547c\uff1f\u201d\u674e\u99a8\u4e88\u614c\u4e71\u6253\u5f00\u95e8\uff0c\u8bf7\u674e\u5728\u6d19\u4e0e\u6c60\u4f50\u79c0\u85cf\u8bf7\u5c4b\u6765\uff0c\u4fdd\u9556\u4e0e\u53f8\u673a\u5b88\u5728\u95e8\u53e3\uff0c\u8ba9\u674e\u5728\u6d19\u3001\u6c60\u4f50\u79c0\u85cf\u8fdb\u5c4b\u540e\u3002</p><p>\u3000\u3000\u5f85\u4ed6\u4eec\u6362\u978b\u8fdb\u5c4b\u540e\uff0c\u674e\u99a8\u4e88\u53c8\u7a81\u7136\u89c9\u5f97\u4e0d\u5bf9\uff0c\u8ba9\u4ed6\u4eec\u770b\u5230\u5f20\u606a\u5728\u5c4b\u5c82\u4e0d\u662f\u95f9\u5f97\u5929\u7ffb\u5730\u8986\uff1f</p><p>\u3000\u3000\u4f50\u6c60\u79c0\u85cf\u770b\u5230\u978b\u67dc\u91cc\u7684\u7537\u5f0f\u76ae\u978b\uff0c\u5fc3\u5934\u7591\u8651\u5927\u589e\uff0c\u8138\u8272\u5df2\u7ecf\u4e25\u8083\u5f97\u94c1\u9752\u8d77\u6765\u3002\u6ca1\u7b49\u674e\u99a8\u4e88\u60f3\u597d\u501f\u53e3\u9a97\u674e\u5728\u6d19\u3001\u6c60\u4f50\u79c0\u85cf\u51fa\u53bb\uff0c\u5f20\u606a\u5e26\u7740\u5bbf\u9189\u3001\u8d64\u88f8\u7740\u4e0a\u8eab\u3001\u8d64\u7740\u811a\u3001\u8dcc\u8dcc\u649e\u649e\u7684\u6276\u7740\u697c\u68af\u4e0b\u697c\u6765\uff0c\u7531\u4e8e\u9152\u8fd8\u6ca1\u6709\u5b8c\u5168\u9192\u3001\u5934\u53c8\u75db\u5f97\u5389\u5bb3\u7684\u7f18\u6545\uff0c\u770b\u5230\u674e\u5728\u6d19\u3001\u6c60\u4f50\u79c0\u85cf\u7ad9\u5728\u697c\u4e0b\uff0c\u8fd8\u4e0b\u610f\u8bc6\u7684\u70b9\u4e86\u70b9\u5934\u7b97\u662f\u6253\u62db\u547c\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u4e4d\u770b\u5230\u5f20\u606a\u8d64\u818a\u3001\u8d64\u8db3\u4e0b\u697c\u6765\uff0c\u8111\u5b50\u6709\u51e0\u79d2\u949f\u505c\u6b62\u8fd0\u8f6c\uff0c\u5374\u662f\u5f20\u606a\u8fd9\u4e0b\u610f\u8bc6\u7684\u4e00\u70b9\u5934\uff0c\u5374\u5c06\u4ed6\u95f7\u5728\u8111\u53e3\u7684\u90a3\u56e2\u6012\u706b\u9aa4\u7136\u95f4\u70b9\u71c3\u3002</p><p>\u3000\u3000\u201c\u516b\u683c\uff01\u201d\u6c60\u4f50\u79c0\u85cf\u5927\u543c\u7740\u51b2\u4e0a\u53bb\u62fd\u7740\u5f20\u606a\u7684\u9886\u53e3\u5c31\u5c06\u4ed6\u62d6\u4e0b\u697c\u68af\u3002</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u65f6\u5019\u5012\u662f\u6e05\u9192\u4e86\u4e00\u4e0b\uff0c\u65e0\u804a\u65f6\u8ddf\u9a6c\u6d77\u9f99\u5b66\u8fc7\u51e0\u62db\u64d2\u62ff\uff0c\u624b\u6307\u53cd\u6263\u6c60\u4f50\u79c0\u85cf\u7684\u864e\u53e3\u5c06\u5176\u62f3\u624b\u6273\u5f00\uff0c\u5c06\u4ed6\u63a8\u5f00\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u53d1\u4ec0\u4e48\u795e\u7ecf\uff01\u201d</p><p>\u3000\u3000\u5bf9\u4e8e\u4e00\u4e2a\u81ea\u8d1f\u7684\u7537\u4eba\u6765\u8bf4\uff0c\u8fd8\u6709\u4ec0\u4e48\u80fd\u6bd4\u770b\u5230\u81ea\u5df1\u5c0f\u5fc3\u5475\u62a4\u4e86\u8fd1\u5341\u5e74\u3001\u7b49\u7740\u5979\u957f\u5927\u6210\u4eba\u3001\u81ea\u5df1\u90fd\u6ca1\u6709\u78b0\u5979\u4e00\u6839\u6beb\u53d1\u7684\u5973\u4eba\u7adf\u7136\u8ba9\u5176\u4ed6\u7537\u4eba\u62d4\u4e86\u5934\u7b79\u66f4\u8ba9\u4eba\u6124\u6012\u7684\uff1f</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u53ea\u89c9\u5f97\u80f8\u53e3\u5feb\u8981\u7ed9\u6012\u706b\u70e7\u5316\u4e86\uff0c\u5927\u543c\u7740\uff1a\u201c\u516b\u683c\uff0c\u6df7\u86cb\uff0c\u652f\u90a3\u79cd\uff0c\u8ba9\u4f60\u62a2\u5148\u73a9\u6211\u7684\u5973\u4eba\uff01\u201d\u4f38\u624b\u5c31\u8981\u53bb\u63ea\u5f20\u606a\u7684\u5934\u53d1\uff0c\u811a\u4e5f\u8ddf\u7740\u8e22\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u5f20\u606a\u95ea\u5f00\u7ed9\u6c60\u4f50\u79c0\u85cf\u7684\u8e22\u6253\uff0c\u811a\u4e0b\u65e0\u529b\u5374\u626d\u4e86\u4e00\u4e0b\uff0c\u5dee\u70b9\u8dcc\u5012\uff0c\u674e\u99a8\u4e88\u8fd8\u4ee5\u4e3a\u5f20\u606a\u7ed9\u8e22\u4e2d\uff0c\u5fd9\u51b2\u8fc7\u53bb\u62a4\u4f4f\u5f20\u606a\uff0c\u671d\u7740\u6c60\u4f50\u79c0\u85cf\u5927\u56b7\uff1a\u201c\u4e0d\u662f\u4f60\u60f3\u8c61\u7684\u90a3\u6837\uff0c\u6211\u4eec\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u505a\uff0c\u518d\u8bf4\u6211\u4e5f\u4e0d\u662f\u4f60\u7684\u5973\u4eba\uff01\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u624b\u8fd8\u4e0d\u5fd8\u6342\u7740\u80f8\u53e3\uff0c\u663e\u7136\u662f\u91cc\u9762\u6ca1\u6709\u7a7f\u5185\u8863\uff0c\u5f20\u606a\u53c8\u662f\u8fd9\u822c\u6a21\u6837\u3001\u8d64\u818a\u3001\u8d64\u8db3\u3001\u4e00\u4ed8\u7cbe\u75b2\u529b\u7aed\u7684\u6837\u5b50\uff0c\u5728\u4e00\u65c1\u770b\u597d\u620f\u7684\u674e\u5728\u6d19\u90fd\u4e0d\u76f8\u4fe1\u6628\u5929\u665a\u4e0a\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u53d1\u751f\uff0c\u4ed6\u73b0\u5728\u5c31\u5e0c\u671b\u770b\u5230\u6c60\u4f50\u79c0\u85cf\u7ed9\u5992\u706b\u70e7\u778e\u4e86\u773c\u775b\u2014\u2014\u5f53\u7136\uff0c\u674e\u5728\u6d19\u5fc3\u91cc\u4e5f\u662f\u5f02\u5e38\u7684\u6c14\u6124\uff0c\u88ab\u8a89\u5357\u97e9\u4e4b\u82b1\u7684\u4e09\u661f\u516c\u4e3b\u7adf\u7136\u7ed9\u652f\u90a3\u7537\u4eba\u73a9\u5f04\u6210\u6b8b\u82b1\u8d25\u67f3\uff0c\u7b80\u76f4\u5c31\u662f\u5bb6\u65cf\u7684\u803b\u8fb1\uff0c\u4f46\u662f\u4ed6\u8fd8\u80fd\u4fdd\u6301\u7406\u667a\uff0c\u5f88\u540e\u6094\u966a\u6c60\u4f50\u79c0\u85cf\u8fc7\u6765\uff0c\u8981\u662f\u6c60\u4f50\u79c0\u85cf\u5355\u72ec\u8fc7\u6765\uff0c\u4ed6\u6253\u6b7b\u5f20\u606a\uff0c\u6216\u88ab\u5f20\u606a\u6253\u6b7b\uff0c\u90fd\u662f\u975e\u5e38\u5b8c\u7f8e\u7684\u7ed3\u5c40\uff0c\u8c01\u4e5f\u4e0d\u4f1a\u5c06\u8d23\u4efb\u65e0\u9650\u5236\u7684\u63a8\u5230\u4e00\u4e2a\u5973\u4eba\u7684\u5934\u4e0a\u3002\u4ed6\u8111\u5b50\u91cc\u98de\u5feb\u7684\u8f6c\u7740\uff0c\u60f3\u7740\u8981\u600e\u6837\u624d\u80fd\u501f\u673a\u51fa\u5fc3\u5934\u7684\u4e00\u53e3\u6076\u6c14\u3002</p><p>\u3000\u3000\u201c\u516b\u683c\uff0c\u8361\u5987\uff01\u201d\u6c60\u4f50\u79c0\u85cf\u89c1\u674e\u99a8\u4e88\u7ad9\u51fa\u6765\u62a4\u4f4f\u5f20\u606a\uff0c\u53cd\u624b\u731b\u6247\u4e86\u5979\u4e00\u5df4\u638c\uff0c\u731b\u7136\u5c06\u5979\u63a8\u5012\u4e00\u8fb9\uff0c\u53c8\u7ee7\u7eed\u671d\u5f20\u606a\u8e22\u53bb\u3002\u5f20\u606a\u89c1\u674e\u99a8\u4e88\u88ab\u6253\uff0c\u5fc3\u5934\u7684\u6012\u706b\u4e5f\u7ed9\u6fc0\u4e86\u8d77\u6765\uff0c\u7ed9\u4ed6\u8e22\u5b9e\u5728\u80f8\u53e3\uff0c\u501f\u52bf\u53cd\u624b\u62b1\u4f4f\u4ed6\u7684\u817f\uff0c\u8d81\u52bf\u540e\u5750\uff0c\u672a\u5750\u5b9e\u5df2\u7ecf\u5c06\u6c60\u4f50\u79c0\u85cf\u62d6\u5012\u5728\u5730\uff0c\u53c8\u6beb\u4e0d\u72b9\u8c6b\u7684\u4e00\u811a\u671d\u4ed6\u7684\u8138\u8e22\u53bb\uff0c\u5374\u662f\u9189\u9152\u540e\u7684\u8eab\u4f53\u53d1\u865a\uff0c\u4e00\u811a\u8e22\u7a7a\uff0c\u6574\u4e2a\u4eba\u5750\u5230\u6c60\u4f50\u79c0\u85cf\u7684\u8138\u4e0a\uff0c\u4e24\u4e2a\u5927\u7537\u4eba\u626d\u6253\u5728\u4e00\u8d77\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u8868\u9762\u4e0a\u662f\u8c01\u4e5f\u4e0d\u5e2e\uff0c\u4f46\u662f\u5f20\u606a\u4e0e\u6c60\u4f50\u79c0\u85cf\u6eda\u5230\u4ed6\u811a\u8fb9\uff0c\u4ed6\u4e5f\u4f1a\u62bd\u51b7\u5b50\u671d\u5f20\u606a\u8eab\u4e0a\u8e22\u4e00\u811a\u3002</p><p>\u3000\u3000\u6c60\u4f50\u79c0\u85cf\u95e8\u5916\u7684\u4fdd\u9556\u3001\u53f8\u673a\u8981\u51b2\u8fdb\u6765\u5e2e\u5fd9\uff0c\u5374\u7ed9\u9a6c\u6d77\u9f99\u9886\u7740\u4eba\u4ece\u540e\u9762\u5236\u4f4f\uff0c\u9a6c\u6d77\u9f99\u4e00\u7bad\u6b65\u60a3\u8fdb\u5c4b\u5b50\uff0c\u4f38\u624b\u627c\u4f4f\u674e\u5728\u6d19\u7684\u8116\u5b50\u7528\u52b2\u4e00\u63a8\uff0c\u5c31\u5c06\u674e\u5728\u6d19\u63a8\u5750\u5728\u5730\uff0c\u53c8\u4e00\u624b\u9501\u4f4f\u6c60\u4f50\u79c0\u85cf\u7684\u80a9\u9aa8\uff0c\u5c06\u4ed6\u786c\u751f\u751f\u4e0e\u5f20\u606a\u5206\u5f00\u6765\uff0c\u89c1\u4ed6\u72b9\u6709\u4e0d\u7518\u7684\u8981\u671d\u5f20\u606a\u8e22\u8fc7\u53bb\uff0c\u559d\u9053\uff1a\u201c\u5728\u4e2d\u56fd\u4f60\u5c11\u653e\u8086\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u72fc\u72c8\u4e0d\u582a\u7684\u4ece\u5730\u4e0a\u722c\u8d77\u6765\uff0c\u5fc3\u91cc\u7a7a\u843d\u843d\u7684\uff0c\u4e5f\u6ca1\u6709\u5fc3\u60c5\u8ddf\u6c60\u4f50\u79c0\u85cf\u3001\u674e\u5728\u6d19\u8ba1\u8f83\u8fd9\u4e9b\u5c0f\u8282\uff0c\u53ea\u662f\u89c1\u674e\u99a8\u4e88\u7ed9\u6c60\u4f50\u79c0\u85cf\u6253\u4e86\u4e00\u5df4\u638c\u3001\u53c8\u7ed9\u63a8\u5012\u5728\u5730\u4e0a\u695a\u695a\u53ef\u601c\uff0c\u95ee\u5979\uff1a\u201c\u8981\u4e0d\u8981\u8ddf\u6211\u8d70\uff1f\u201d</p><p>\u3000\u3000\u674e\u99a8\u4e88\u89c1\u6c60\u4f50\u79c0\u85cf\u50cf\u91ce\u517d\u4e00\u6837\u7684\u53d1\u72c2\u53d1\u75af\uff0c\u54ea\u91cc\u6562\u8ddf\u4ed6\u5728\u4e00\u8d77\uff0c\u521a\u7ad9\u8d77\u6765\u8eb2\u5230\u5f20\u606a\u8eab\u540e\uff0c\u8fd8\u4e0d\u5fd8\u7ed9\u81ea\u5df1\u8fa9\u89e3\uff1a\u201c\u6211\u8ddf\u5f20\u606a\u541b\u4e0d\u662f\u4f60\u4eec\u60f3\u8c61\u7684\u90a3\u6837\uff0c\u4ed6\u559d\u9189\u4e86\u9152\u501f\u5bbf\u5728\u8fd9\u91cc\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u559d\u9189\u4e86\u9152\u501f\u5bbf\u5728\u8fd9\u91cc\uff0c\u800c\u4ed6\u7684\u53f8\u673a\u4fdd\u9556\u901a\u5bb5\u5b88\u5728\u522b\u5885\u5916\uff0c\u6c60\u4f50\u79c0\u85cf\u5403\u4e86\u72d7\u5c4e\u624d\u76f8\u4fe1\u674e\u99a8\u4e88\u4e0e\u5f20\u606a\u6628\u5929\u591c\u91cc\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u505a\uff0c\u53ea\u662f\u7ed9\u9a6c\u6d77\u9f99\u6293\u4f4f\u80a9\u9aa8\u6323\u624e\u4e0d\u5f97\uff0c\u53ea\u5f97\u6076\u72e0\u72e0\u7684\u76ef\u4f4f\u5f20\u606a\uff1a\u201c\u6211\u4f1a\u8ba9\u4f60\u77e5\u9053\u4ec0\u4e48\u53eb\u4e00\u65e0\u6240\u6709\u7684\u611f\u89c9\uff01\u201d</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u5fc3\u60c5\u8ddf\u6c60\u4f50\u79c0\u85cf\u4e89\u7740\u53e3\u820c\u4e4b\u4fbf\uff0c\u53ea\u6de1\u6de1\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u6211\u606d\u5019\u5c31\u662f\u4e86\u3002\u201d\u7275\u7740\u60f6\u60f6\u4e0d\u5b89\u7684\u674e\u99a8\u4e88\u7684\u5c0f\u624b\u8d70\u51fa\u522b\u5885\uff0c\u4ed6\u771f\u662f\u5bbf\u9189\u672a\u9192\uff0c\u4e0b\u53f0\u9636\u811a\u4e0b\u4e00\u8f6f\u4ece\u53f0\u9636\u4e0a\u6eda\u4e86\u4e0b\u6765\uff0c\u62c9\u7740\u674e\u99a8\u4e88\u4e5f\u8ddf\u4ed6\u8dcc\u4e0b\u53f0\u9636\uff0c\u8db4\u5230\u4ed6\u7684\u8eab\u5b50\uff0c\u4e24\u4eba\u614c\u624b\u614c\u811a\u7684\u722c\u8d77\u6765\uff0c\u72fc\u72c8\u4e0d\u582a\u7684\u94bb\u8fdb\u8f66\u91cc\u53bb\uff0c\u674e\u99a8\u4e88\u7f9e\u5f97\u906e\u4f4f\u8138\u4e0d\u6562\u770b\u4eba\u3002\u8981\u8bf4\u4ed6\u4eec\u4e0d\u662f\u4e00\u5bf9\u5978\u592b\u6deb\u5987\uff0c\u5c31\u8fde\u5f20\u606a\u4ed6\u81ea\u5df1\u7684\u90a3\u4e9b\u4fdd\u9556\u90fd\u6ca1\u6709\u3002</p></article>"}}}]);
