(window.webpackJsonp=window.webpackJsonp||[]).push([[1089],{1112:function(p){p.exports={chapterTitle:null,title:"\u7b2c1086\u7ae0 \u7206\u4ea7\u80fd",content:"<article><h2>\u7b2c1086\u7ae0 \u7206\u4ea7\u80fd</h2><p>\u3000\u3000\u674e\u5728\u6d19\u5c06\u4e09\u661f\u7535\u5b50\u4e2d\u56fd\u624b\u673a\u4e1a\u52a1\u90e8\u95e8\u7684\u8d1f\u8d23\u4eba\u90fd\u53ec\u96c6\u8fc7\u6765\u3002</p><p>\u3000\u3000\u6a1f\u6728\u8272\u7684\u684c\u5b50\u4e0a\uff0c\u6446\u653e\u7740\u4e24\u53ea\u624b\u673a\uff0c\u662f\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u65b0\u63a8\u51fa\u7684\u4e24\u6b3e\u624b\u673a\u3002</p><p>\u3000\u3000\u4e09\u661f\u5728\u5efa\u90ba\u7684\u624b\u673a\u5e94\u7528\u7814\u7a76\u4e2d\u5fc3\u8fde\u591c\u505a\u51fa\u8be6\u7ec6\u7684\u6d4b\u8bd5\u8bc4\u4f30\uff0c\u5404\u9879\u6027\u80fd\u90fd\u76f8\u5f53\u7684\u5e73\u5eb8\uff0c\u4e5f\u6ca1\u6709\u660e\u663e\u7684\u7f3a\u9677\uff0c\u5916\u578b\u5373\u4f7f\u80fd\u7b97\u5f97\u4e0a\u4f18\u70b9\u4e5f\u76f8\u5f53\u6709\u9650\uff0c\u674e\u5728\u6d19\u8ba9\u5927\u5bb6\u90fd\u5c06\u81ea\u5df1\u7684\u624b\u673a\u638f\u51fa\u6765\u6446\u5728\u4f1a\u8bae\u684c\u4e0a\uff0c\u8fd9\u4e24\u6b3e\u624b\u673a\u7684\u786e\u5f88\u4e0d\u8d77\u773c\uff0c\u4f46\u662f\u5728\u674e\u5728\u6d19\u4e0e\u5728\u5ea7\u7684\u4f17\u4eba\u773c\u91cc\uff0c\u8fd9\u4e24\u6b3e\u624b\u673a\u6709\u7740\u4e00\u9879\u6781\u4e3a\u663e\u8457\u7684\u3001\u4ee4\u4eba\u65e0\u6cd5\u5ffd\u89c6\u7684\u4f18\u70b9\uff0c\u90a3\u5c31\u662f\u5236\u9020\u6210\u672c\u591f\u4f4e\u3002</p><p>\u3000\u3000\u7efc\u5408\u5404\u65b9\u9762\u7684\u60c5\u62a5\uff0c\u8fd9\u4e24\u6b3e\u624b\u673a\u7684\u5236\u9020\u6210\u672c\u5728100\u7f8e\u5143\u5de6\u53f3\uff0c\u76f8\u5bf9\u4e8e160-170\u7f8e\u5143\u5de6\u53f3\u7684\u5e02\u573a\u552e\u4ef7\uff0c\u5236\u9020\u5546\u4e0e\u7ecf\u9500\u5546\u90fd\u6709\u7740\u76f8\u5f53\u5927\u7684\u5229\u6da6\u7a7a\u95f4\u3002</p><p>\u3000\u3000\u9526\u6e56\u63d0\u4f9b\u7684\u57fa\u9891\u82af\u7247\u7ec4\u57fa\u677f\u4e0a\u9664\u4e86\u57fa\u5e26\u82af\u7247\u4e4b\u5916\uff0c\u8fd8\u96c6\u6210\u4e86\u6570\u5b57\u624b\u673a\u6240\u9700\u7684\u5176\u4ed6\u4e3b\u8981\u6838\u5fc3\u5143\u4ef6\uff0c\u9526\u6e56\u8fd8\u989d\u5916\u4f9b\u5e94\u5ec9\u4ef7\u7684\u5c0f\u5c3a\u5bf8STN\u9ed1\u767d\u5c4f\uff0c\u8fd9\u662f\u8fd9\u4e24\u6b3e\u624b\u673a\u5236\u9020\u6210\u672c\u80fd\u63a7\u5236\u5728100\u7f8e\u5143\u5de6\u53f3\u7684\u5173\u952e\u56e0\u7d20\u3002</p><p>\u3000\u3000\u674e\u5728\u6d19\u770b\u7740\u62a5\u544a\u4e66\u91cc\u201c140\u7f8e\u5143\u201d\u8fd9\u4e2a\u523a\u773c\u7684\u6570\u5b57\u63c9\u7740\u592a\u9633\u7a74\uff0c\u8fd9\u662f\u4e09\u661f\u7535\u5b50\u6b64\u65f6\u751f\u4ea7\u540c\u89c4\u683c\u624b\u673a\u80fd\u63a7\u5236\u7684\u5236\u9020\u6210\u672c\u6781\u9650\uff0c\u9664\u4e86\u4e09\u661f\u7535\u5b50\u624b\u673a\u4e1a\u52a1\u7684\u5236\u9020\u90e8\u95e8\u4e3b\u8981\u8fd8\u7559\u5728\u97e9\u56fd\u672c\u571f\u3001\u4eba\u529b\u8d44\u6e90\u6210\u672c\u504f\u9ad8\u4e4b\u5916\uff0c\u57fa\u5e26\u82af\u7247\u7b49\u6838\u5fc3\u5143\u4ef6\u8fd8\u9700\u8981\u6b27\u7f8e\u5382\u5546\u63d0\u4f9b\uff0c\u8fd9\u4e9b\u90fd\u662f\u6210\u672c\u65e0\u6cd5\u964d\u4f4e\u7684\u5173\u952e\u539f\u56e0\u3002</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u73b0\u5728\u751f\u4ea7\u7684\u624b\u673a\uff0cCDMA\u57fa\u9891\u82af\u7247\u4e3b\u8981\u6765\u7531\u9ad8\u901a\u4f9b\u5e94\uff0cGSM\u57fa\u9891\u82af\u7247\u4e3b\u8981\u7531\u82f1\u98de\u51cc\u4e0e\u5fb7\u4eea\u4f9b\u5e94\u3002\u4e0d\u4ec5\u8fd9\u4e09\u5bb6\u82af\u7247\u5382\uff0c\u5168\u7403\u4e3b\u8981\u7684\u57fa\u5e26\u82af\u7247\u4f9b\u5e94\u5546\u8fd9\u65f6\u5019\u90fd\u5c06\u773c\u775b\u76ef\u5728\u5229\u6da6\u7a7a\u95f4\u66f4\u5927\u7684\u4e2d\u9ad8\u7aef\u82af\u7247\u7ec4\u4e0a\uff0c\u5373\u4f7f\u613f\u610f\u63d0\u4f9b\u5c11\u91cf\u7684\u4f4e\u7aef\u82af\u7247\u7ec4\uff0c\u4e5f\u4e0d\u4f1a\u538b\u7f29\u4ed6\u4eec\u7684\u5229\u6da6\u7a7a\u95f4\u4ee5\u4fbf\u4e0b\u6e38\u5382\u5546\u66f4\u6709\u7ade\u4e89\u529b\u3002</p><p>\u3000\u3000\u201c\u5c31\u7b97\u9526\u6e56\u6b64\u65f6\u5df2\u7ecf\u5f15\u8d77\u82f1\u98de\u51cc\u7b49\u4e3b\u8981\u624b\u673a\u82af\u7247\u5382\u5546\u7684\u8db3\u591f\u6ce8\u610f\uff0c\u4e0e\u9526\u6e56\u7a7f\u540c\u4e00\u6761\u88e4\u5b50\u7684\u5fb7\u4eea\u591a\u534a\u4e5f\u4f1a\u5c06\u82f1\u98de\u51cc\u7b49\u5382\u5546\u7684\u7cbe\u529b\u7275\u5236\u5728\u4e2d\u9ad8\u7aef\u82af\u7247\u7ec4\u7684\u7ade\u4e89\u4e0a\uff0c\u201d\u91d1\u5357\u52c7\u5206\u6790\u9053\uff0c\u201c\u6469\u6258\u7f57\u62c9\u3001\u8bfa\u57fa\u4e9a\u6240\u9762\u4e34\u7684\u60c5\u51b5\uff0c\u8ddf\u6211\u4eec\u6709\u4e9b\u7c7b\u4f3c\uff0c\u7231\u7acb\u4fe1\u3001\u98de\u5229\u6d66\u867d\u7136\u4e5f\u638c\u63e1\u57fa\u5e26\u82af\u7247\u6280\u672f\uff0c\u4f46\u662f\u5728\u5236\u9020\u6210\u672c\u63a7\u5236\u65b9\u9762\u751a\u81f3\u8fd8\u6bd4\u4e0d\u4e0a\u6211\u4eec\u2026\u2026\u5373\u4f7f\u6211\u4eec\u73b0\u5728\u8c03\u6574\u4f4e\u7aef\u5e02\u573a\u5e94\u5bf9\u7b56\u7565\uff0c\u4e24\u5e74\u65f6\u95f4\u91cc\u80fd\u8c03\u6574\u8fc7\u6765\u5df2\u7ecf\u662f\u8f83\u4e3a\u4e50\u89c2\u7684\u4f30\u8ba1\u4e86\uff0c\u800c\u5230\u65f6\u4e2d\u6676\u5fae\u82af\u4e24\u5ea7\u65b0\u5382\u5c06\u5efa\u6210\uff0c\u9526\u6e56\u5374\u53c8\u80fd\u7c4d\u6b64\u518d\u4e00\u6b21\u83b7\u5f97\u6210\u672c\u4e0a\u7684\u4f18\u52bf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5509\u2026\u2026\u201d\u674e\u5728\u6d19\u5fc3\u4e2d\u8f7b\u53f9\uff0c\u9526\u6e56\u81ea\u5df1\u6ca1\u6709\u5982\u9884\u671f\u6240\u6599\u7684\u8ddf\u79d1\u738b\u9ad8\u79d1\u3001\u8054\u4fe1\u540c\u671f\u63a8\u51fa\u4f4e\u7aef\u624b\u673a\uff0c\u4e00\u65b9\u9762\u53ef\u80fd\u662f\u9526\u6e56\u7684\u4ea7\u80fd\u8fd8\u6709\u9650\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u9526\u6e56\u5728\u4f4e\u7aef\u5e02\u573a\u5927\u95e8\u655e\u5f00\u4e4b\u65f6\u6682\u65f6\u653e\u5f03\u8fdb\u5165\u4f4e\u7aef\u5e02\u573a\uff0c\u4e5f\u5c31\u610f\u5473\u6709\u7740\u6bd4\u7ade\u4e89\u5bf9\u624b\u66f4\u7075\u6d3b\u7684\u7b56\u7565\uff0c\u4e09\u661f\u6b64\u65f6\u9aa4\u7136\u8f6c\u53d8\u624b\u673a\u4e1a\u52a1\u7684\u53d1\u5c55\u7b56\u7565\uff0c\u53ea\u4f1a\u66f4\u75b2\u4e8e\u5954\u547d\u3002</p><p>\u3000\u3000\u9664\u4e86\u6838\u5fc3\u6280\u672f\u4e0e\u65e5\u76ca\u5b8c\u5584\u7684\u5236\u9020\u4f53\u7cfb\u4e4b\u5916\uff0c\u674e\u5728\u6d19\u4e5f\u65e0\u6cd5\u95ed\u7740\u773c\u775b\u8bf4\u770b\u4e0d\u5230\u9526\u6e56\u5728\u672c\u571f\u5e02\u573a\u4e0a\u7684\u5929\u7136\u4f18\u52bf\u3002</p><p>\u3000\u3000\u5728\u4e2d\u56fd\u5176\u4ed6\u624b\u673a\u8fde\u9501\u4f01\u4e1a\u8fd8\u521a\u521a\u8d77\u6b65\u4e4b\u65f6\uff0c\u76db\u946b\u7535\u8baf\u5728\u4e5d\u4e03\u5e74\u5c31\u52a8\u7528\u4e94\u4ebf\u5de8\u8d44\u5b9e\u65bd\u201c\u767e\u57ce\u767e\u5e97\u201d\u65b9\u6848\uff0c\u4f9d\u6258\u76db\u946b\u7535\u5668\u5df2\u7ecf\u94fa\u5f00\u7684\u9aa8\u5e72\u7f51\u7edc\uff0c\u5c06\u624b\u673a\u76f4\u8425\u7f51\u7edc\u5168\u9762\u6e17\u900f\u5230\u4e8c\u4e09\u7ebf\u57ce\u5e02\uff1b\u4eca\u5e74\u4e5d\u6708\u4efd\uff0c\u76db\u946b\u7535\u8baf\u63d0\u51fa\u201c\u5343\u57ce\u5343\u5e97\u201d\u65b9\u6848\u5c06\u8425\u9500\u7f51\u7edc\u5411\u53bf\u57df\u57ce\u9547\u6e17\u900f\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u76db\u946b\u7535\u8baf\u63a5\u53d7\u53bf\u57df\u57ce\u9547\u624b\u673a\u7ecf\u9500\u5546\u7684\u52a0\u76df\uff0c\u201c\u5343\u57ce\u5343\u5e97\u201d\u65b9\u6848\u5b9e\u9645\u52a8\u7528\u7684\u8d44\u91d1\u76f8\u5f53\u6709\u9650\uff0c\u5374\u80fd\u8ba9\u76db\u946b\u7535\u8baf\u7684\u5e02\u573a\u8986\u76d6\u7a0b\u5ea6\u5927\u5e45\u4e0a\u5347\u3002</p><p>\u3000\u3000\u76db\u946b\u7535\u8baf\u4f5c\u4e3a\u9526\u6e56\u7cfb\u7684\u6210\u5458\u4f01\u4e1a\uff0c\u662f\u786e\u51ff\u65e0\u7591\u7684\uff0c\u9526\u6e56\u8fd9\u79cd\u9ad8\u6e17\u5165\u6027\u8d28\u7684\u5782\u76f4\u4f53\u7cfb\u4f7f\u5176\u5728\u672c\u571f\u5e02\u573a\u6709\u7740\u5f3a\u5927\u7684\u7ade\u4e89\u529b\u3002\u9526\u6e56\u751a\u81f3\u53ef\u4ee5\u538b\u7f29\u5404\u4e2a\u73af\u8282\u7684\u5229\u6da6\u7a7a\u95f4\u3001\u5927\u5e45\u964d\u4f4e\u4ea7\u54c1\u7684\u5b9e\u9645\u5e02\u573a\u4ef7\u683c\u6765\u5f7b\u5e95\u7684\u538b\u5236\u7ade\u4e89\u5bf9\u624b\uff0c\u867d\u7136\u9526\u6e56\u5728\u8fd9\u65b9\u9762\u8868\u73b0\u8fd8\u5f88\u514b\u5236\uff0c\u4f46\u662f\u7ade\u4e89\u6b8b\u9177\u5230\u4e00\u5b9a\u7a0b\u5ea6\uff0c\u8c01\u80fd\u4fdd\u8bc1\u9526\u6e56\u4e0d\u4f7f\u51fa\u8fd9\u4e2a\u6740\u624b\u950f\uff1f</p><p>\u3000\u3000\u4e09\u661f\u7535\u5b50\u5728\u97e9\u56fd\u4e5f\u662f\u5b9e\u65bd\u8fd9\u79cd\u9ad8\u6e17\u900f\u7684\u4ea7\u4e1a\u7b56\u7565\uff0c\u53ea\u662f\u97e9\u56fd\u672c\u571f\u5e02\u573a\u5bb9\u91cf\u6839\u672c\u65e0\u6cd5\u8ddf\u4e2d\u56fd\u76f8\u63d0\u5e76\u8bba\uff0c\u674e\u5728\u6d19\u5b9e\u5728\u60f3\u8c61\u4e0d\u51fa\u4e09\u661f\u5728\u4e2d\u56fd\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u8ddf\u9526\u6e56\u8089\u640f\u8fd8\u6709\u4ec0\u4e48\u4f18\u52bf\u53ef\u8a00\u3002</p><p>\u3000\u3000\u5728\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u4e0e\u9526\u6e56\u8089\u535a\u4e0d\u4ec5\u6ca1\u6709\u4efb\u4f55\u80dc\u7b97\uff0c\u8fd8\u4f1a\u6253\u4e71\u65e2\u7136\u7684\u5e02\u573a\u6218\u7565\uff0c\u4f46\u662f\u5c31\u773c\u7741\u7741\u7684\u770b\u7740\u9526\u6e56\u5c06\u6574\u4e2a\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u90fd\u9cb8\u541e\u8179\u4e2d\uff0c\u53c8\u600e\u4e48\u80fd\u591f\u7518\u5fc3\uff1f</p><p>\u3000\u3000\u867d\u7136\u6bcf\u53ea\u4f4e\u4ef7\u624b\u673a\u7684\u7edd\u5bf9\u5229\u6da6\u76f8\u5bf9\u6709\u9650\uff0c\u4f46\u662f\u5728\u4e2d\u56fd\u6570\u4ee5\u4ebf\u8ba1\u7684\u666e\u901a\u5e02\u6c11\u90fd\u6784\u6210\u4e2d\u4f4e\u7aef\u5e02\u573a\u7684\u6d88\u8d39\u6f5c\u529b\uff0c\u5e02\u573a\u6f5c\u529b\u5927\u5230\u8ba9\u4eba\u96be\u4ee5\u60f3\u8c61\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u7684\u529e\u516c\u684c\u4e0a\uff0c\u540c\u6837\u6446\u653e\u7740\u8054\u4fe1\u3001\u79d1\u738b\u9ad8\u79d1\u65b0\u63a8\u51fa\u7684\u4e24\u6b3e\u624b\u673a\uff0c\u4ed6\u773c\u775b\u7d27\u76ef\u7740\uff0c\u54ac\u7259\u5207\u9f7f\u7684\u6068\u5f97\u7259\u76f4\u75d2\u75d2\u3002</p><p>\u3000\u3000\u8096\u745e\u6c11\u7684\u516c\u7136\u5bf9\u6297\u662f\u845b\u5efa\u5fb7\u5fc3\u4e2d\u7684\u4e00\u6839\u6bd2\u523a\uff0c\u4f46\u662f\u5c31\u5728\u534e\u590f\u7535\u5b50\u6b63\u5f0f\u5212\u7ed9\u7ecf\u8d38\u59d4\u7ba1\u8f96\u4e4b\u9645\uff0c\u8054\u4fe1\u6210\u529f\u63a8\u51fa\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u6280\u672f\u7684\u65b0\u624b\u673a\uff0c\u65e0\u7591\u521d\u6b65\u8868\u660e\u8096\u745e\u6c11\u6700\u521d\u7684\u51b3\u5b9a\u5e76\u6ca1\u6709\u9519\u3002</p><p>\u3000\u3000\u56fd\u5185\u624b\u673a\u5e02\u573a\u4e00\u76f4\u7ef4\u6301\u9ad8\u901f\u589e\u6da8\uff0c\u4f46\u662f\u7531\u4e8e\u4e0a\u6e38\u5143\u5668\u4ef6\u4f9b\u5e94\u91cf\u4e0d\u8db3\uff0c\u9664\u4e86\u7231\u8fbe\u4e0e\u79d1\u738b\u9ad8\u79d1\u80fd\u83b7\u5f97\u8db3\u91cf\u7684\u82af\u7247\u4f9b\u5e94\u8ddf\u56fd\u5916\u624b\u673a\u5382\u5546\u76f4\u63a5\u7ade\u4e89\u5916\uff0c\u5176\u4ed6\u5341\u4e09\u5bb6\u56fd\u4ea7\u624b\u673a\u5382\u5546\u90fd\u9762\u4e34\u4ea7\u91cf\u4e25\u91cd\u4e0d\u8db3\u3001\u6709\u94b1\u8d5a\u4e0d\u5230\u7684\u5c40\u9762\u3002</p><p>\u3000\u3000\u9488\u5bf9\u5982\u6b64\u5c40\u9762\uff0c\u5176\u4ed6\u624b\u673a\u5382\u5546\u90fd\u91c7\u53d6\u63d0\u9ad8\u4e00\u6b21\u8ba2\u8d2d\u91cf\u3001\u589e\u52a0\u5e93\u5b58\u7684\u7b56\u7565\uff0c\u552f\u6709\u8054\u4fe1\u5728\u8096\u745e\u6c11\u7684\u7387\u9886\u4e0b\u4e0e\u9526\u6e56\u8fdb\u884c\u6df1\u5c42\u6b21\u7684\u5408\u4f5c\u3002</p><p>\u3000\u3000\u5728\u534e\u590f\u7535\u5b50\u4e0b\u5c5e\u7684\u4e09\u5bb6\u624b\u673a\u4f01\u4e1a\u91cc\uff0c\u8054\u4fe1\u7684\u4e1a\u7ee9\u4e00\u5411\u8981\u597d\u4e8e\u4e1c\u5174\u3001\u8054\u8baf\uff0c\u4f46\u662f\u4f18\u52bf\u4e0d\u7b97\u592a\u660e\u663e\u3002</p><p>\u3000\u3000\u590f\u5b63\u4ee5\u6765\uff0c\u8054\u4fe1\u9664\u4e86\u4e0e\u9526\u6e56\u5408\u4f5c\u5f00\u53d1\u57fa\u4e8eESS\u57fa\u5e26\u82af\u7247\u65b0\u624b\u673a\u7684\u5f00\u53d1\u4e4b\u5916\uff0c\u8fd8\u4ece\u9526\u6e56\u83b7\u5f97\u90e8\u5206\u7d27\u7f3a\u5143\u5668\u4ef6\u7684\u4f9b\u5e94\uff0c\u8054\u4fe1\u7b2c\u4e09\u5b63\u5ea6\u7684\u4e1a\u7ee9\u5df2\u7ecf\u8ddf\u4e1c\u5174\u3001\u8054\u8baf\u62c9\u5f00\u4e86\u8ddd\u79bb\u3002</p><p>\u3000\u3000\u65b0\u624b\u673a\u4e5f\u5f00\u53d1\u51fa\u6765\u4e86\uff0c\u6bd4\u8f83\u8d77\u8054\u8baf\u3001\u4e1c\u5174\u9700\u8981\u4ece\u6d77\u5916\u5408\u4f5c\u5382\u5546\u90a3\u91cc\u9ad8\u4ef7\u91c7\u8d2d\u624b\u673a\u82af\u7247\u7ec4\uff0c\u65b0\u624b\u673a\u7684\u552e\u4ef7\u867d\u7136\u4f4e\uff0c\u4f46\u662f\u5229\u6da6\u7a7a\u95f4\u5374\u8db3\u8db3\u63d0\u9ad8\u4e00\u500d\u3002</p><p>\u3000\u3000\u5c31\u7b97\u534e\u590f\u7535\u5b50\u8131\u79bb\u4fe1\u4ea7\u90e8\uff0c\u8096\u745e\u6c11\u65e0\u6cd5\u83b7\u5f97\u4fe1\u4ea7\u90e8\u7684\u76f4\u63a5\u652f\u6301\uff0c\u845b\u5efa\u5fb7\u8fd9\u65f6\u5019\u4e5f\u627e\u4e0d\u5230\u501f\u53e3\u5c06\u8096\u745e\u6c11\u8fd9\u6839\u6bd2\u523a\u4ece\u5fc3\u53e3\u62e8\u6389\uff0c\u751a\u81f3\u8fd8\u8981\u62c5\u5fc3\u8054\u4fe1\u4ece\u534e\u590f\u7535\u5b50\u72ec\u7acb\u51fa\u53bb\u3002</p><p>\u3000\u3000\u845b\u5efa\u5fb7\u95ed\u7740\u773c\u775b\uff0c\u53ea\u89c9\u5f97\u592a\u9633\u7a74\u9690\u9690\u53d1\u80c0\uff0c\u4e4b\u524d\u4e0e\u9526\u6e56\u4e89\u90fd\u65e0\u5173\u5927\u5c40\uff0c\u600e\u4e48\u4e5f\u6ca1\u6709\u60f3\u5230\u8054\u4fe1\u7adf\u7136\u6210\u4e86\u9526\u6e56\u624b\u91cc\u5f80\u81ea\u5df1\u5fc3\u53e3\u624e\u7684\u4e00\u6839\u9489\u5b50\uff0c\u591a\u5c11\u6709\u4e9b\u751f\u6b7b\u6538\u5173\u7684\u5371\u673a\u611f\uff0c\u5fc3\u91cc\u60f3\u7740\uff0c\u534e\u590fNEC\u65b0\u5382\u5efa\u8bbe\u54ac\u7740\u4e5f\u662f\u8981\u4e0a\u7684\uff0c\u4e0d\u80fd\u518d\u8ba9\u8d44\u6e90\u6d41\u5411\u9526\u6e56\u4e86\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5341\u4e00\u6708\u4e0b\u65ec\u5916\u754c\u7684\u76ee\u5149\u90fd\u76ef\u5728\u4e2d\u6676\u5fae\u82af\u7684\u4e24\u5ea7\u65b0\u5382\u5efa\u8bbe\u4e0a\uff0c\u5f20\u606a\u4ed6\u5374\u4ece\u91d1\u5c71\u8dd1\u5230\u65b0\u4ead\uff0c\u8fd8\u8981\u4e3a\u5176\u4ed6\u4e8b\u60c5\u5934\u75bc\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u5e76\u8d2d\u4e1c\u5c71\u94a2\u94c1\u6709\u9650\u516c\u53f8\u5171\u540c\u5b9e\u65bd\u4e1c\u5c71\u6e2f\u5343\u4e07\u5428\u7ea7\u94a2\u94c1\u5236\u9020\u57fa\u5730\u7684\u65b9\u6848\u8fdb\u884c\u957f\u8fbe\u4e00\u5e74\u7684\u8c08\u5224\u6700\u7ec8\u88ab\u8ba1\u5212\u53d1\u5c55\u59d4\u5426\u51b3\u6389\uff0c\u6c11\u8425\u8d44\u672c\u9996\u6b21\u5411\u8d85\u5927\u578b\u56fd\u6709\u4f01\u4e1a\u6e17\u900f\u7684\u52aa\u529b\u5c31\u6b64\u5ba3\u5e03\u5931\u8d25\u3002</p><p>\u3000\u3000\u8fd9\u4e2a\u7ed3\u679c\u4e5f\u6ca1\u6709\u7279\u522b\u8ba9\u4eba\u611f\u5230\u610f\u5916\uff0c\u4e4b\u524d\u4e5f\u9884\u5907\u4e86\u591a\u5957\u65b9\u6848\u3002</p><p>\u3000\u3000\u8c03\u6574\u540e\u6700\u7ec8\u83b7\u5ba1\u6279\u901a\u8fc7\u7684\u65b9\u6848\u662f\u7531\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u4e0e\u4e1c\u5c71\u94a2\u94c1\u8054\u5408\u51fa\u8d44\u6ce8\u518c\u65b0\u516c\u53f8\u8d1f\u8d23\u4e1c\u5c71\u6e2f\u5343\u4e07\u5428\u7ea7\u94a2\u94c1\u5236\u9020\u57fa\u5730\u7684\u5efa\u8bbe\u3001\u7ecf\u8425\u3002</p><p>\u3000\u3000\u4e1c\u6d77\u8054\u94c1\u94a2\u94c1\u4e0e\u4e1c\u5c71\u94a2\u94c1\u5728\u65b0\u516c\u53f8\u7684\u6301\u80a1\u6bd4\u4f8b\u4e3a7\uff1a3\uff0c\u4e5f\u5c06\u6309\u6b64\u6bd4\u4f8b\u5148\u671f\u5411\u65b0\u516c\u53f8\u63d0\u4f9b\u603b\u989d\u8fbe20\u4ebf\u7f8e\u5143\u7684\u5efa\u8bbe\u8d44\u91d1\uff0c\u94a2\u94c1\u57fa\u5730\u5efa\u8bbe\u9879\u76ee\u7b97\u662f\u6b63\u5f0f\u542f\u52a8\u4e86\u3002</p><p>\u3000\u3000\u9526\u6e56\u5546\u4e8b\u5bf9\u4e1c\u5c71\u94a2\u94c1\u6301\u80a175\uff05\uff0c\u6309\u6bd4\u4f8b\u5148\u671f\u5e94\u8be5\u627f\u62c54.5\u4ebf\u7f8e\u5143\u7684\u5efa\u8bbe\u8d44\u91d1\uff0c\u65b0\u4ead\u5730\u65b9\u5c06\u5148\u671f\u8981\u627f\u62c51.5\u4ebf\u7f8e\u5143\u7684\u5efa\u8bbe\u8d44\u91d1\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u6e2f\u5efa\u8bbe\u5de5\u7a0b\u542f\u52a8\u8d77\u6765\uff0c\u6c5f\u5357\u7701\u51e0\u4e4e\u6bcf\u4e00\u5206\u80fd\u6324\u51fa\u6765\u7684\u8d22\u529b\u90fd\u6295\u5230\u4e1c\u5c71\u6765\uff0c\u5b9e\u5728\u51d1\u4e0d\u8db31.5\u4ebf\u7f8e\u5143\u7684\u8d44\u91d1\u6765\uff0c\u8fd9\u90e8\u5206\u8d44\u91d1\u7684\u538b\u529b\u8fd8\u662f\u8981\u843d\u5728\u9526\u6e56\u7684\u5934\u4e0a\u2014\u2014\u9526\u6e56\u5148\u671f\u4e00\u5171\u8981\u62ff\u51fa\u516d\u4ebf\u7f8e\u5143\u51fa\u6765\u4ee5\u516c\u53f8\u503a\u7684\u540d\u4e49\u6ce8\u5165\u4e1c\u5c71\u94a2\u94c1\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u94a2\u94c1\u57fa\u5730\u7684\u5960\u57fa\u5178\u793c\u5f88\u4f4e\u8c03\uff0c\u4e5f\u6ca1\u6709\u7279\u610f\u7684\u53bb\u9080\u8bf7\u5a92\u4f53\u8bb0\u8005\uff0c\u9664\u4e86\u4e24\u7701\u5730\u65b9\u5a92\u4f53\u4e4b\u5916\uff0c\u8fd8\u6709\u4e9b\u7535\u5b50\u4e13\u4e1a\u5a92\u4f53\u8bb0\u8005\u4e0d\u8bf7\u800c\u81f3\u3002\u8fd9\u4e9b\u4e13\u4e1a\u5a92\u4f53\u66f4\u591a\u5173\u5fc3\u4e2d\u6676\u5fae\u82af\u5927\u80a1\u4e1c\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u52a1\u72b6\u51b5\uff0c\u5b9e\u5728\u4ee4\u4eba\u96be\u4ee5\u60f3\u8c61\u9526\u6e56\u5546\u4e8b\u7684\u8d22\u529b\u4e3a\u94a2\u94c1\u57fa\u5730\u5148\u671f\u6295\u5165\u516d\u4ebf\u7f8e\u5143\u4e4b\u540e\u8fd8\u80fd\u627f\u62c5\u4e2d\u6676\u5fae\u82af\u4e24\u5ea7\u65b0\u5382\u5efa\u8bbe\u8981\u5206\u62c5\u7684\u51fa\u8d44\u4e49\u52a1\u3002</p><p>\u3000\u3000\u521d\u51ac\u5b63\u8282\u7684\u65b0\u4ead\uff0c\u7ad9\u5728\u6d77\u8fb9\uff0c\u6d77\u98ce\u5439\u6765\u5df2\u7ecf\u9690\u7ea6\u6709\u98ce\u5272\u7684\u611f\u89c9\uff0c\u586b\u6d77\u5de5\u7a0b\u7684\u5e7f\u88a4\u5de5\u5730\u4e0a\uff0c\u5df2\u586b\u5e73\u7684\u6d77\u6ee9\u8fd8\u6ca1\u6709\u690d\u88ab\u8986\u76d6\uff0c\u79cb\u540e\u5e72\u71e5\u5c11\u96e8\uff0c\u6ce5\u571f\u6709\u4e9b\u6c99\u5c18\u5316\uff0c\u98ce\u5439\u8fc7\uff0c\u5439\u5f97\u53d1\u767d\u7684\u7ec6\u6c99\u5c18\u6d41\u52a8\uff0c\u4eba\u7ad9\u5728\u5176\u95f4\uff0c\u611f\u89c9\u5c31\u50cf\u7ad9\u5728\u5e7f\u88a4\u7684\u6c99\u6f20\u4e4b\u4e2d\uff0c\u8fdc\u5904\u662f\u6e5b\u84dd\u8272\u7684\u6e56\u6c34\u3002</p><p>\u3000\u3000\u5f20\u606a\u6ca1\u6709\u7279\u5730\u7684\u53bb\u53c2\u52a0\u94a2\u94c1\u57fa\u5730\u7684\u5960\u57fa\u5178\u793c\uff0c\u4ed6\u8ba9\u8f66\u505c\u5728\u8def\u8fb9\uff0c\u5728\u5de5\u5730\u4e0a\u8f6c\u4e86\u4e00\u5708\uff0c\u5403\u4e86\u534a\u5929\u7684\u98ce\u6c99\uff0c\u7b49\u5230\u63a5\u5230\u7535\u8bdd\u542c\u8bf4\u5960\u57fa\u5178\u793c\u7ed3\u675f\u3001\u8f66\u961f\u8fd4\u56de\u65b0\u4ead\u5e02\u91cc\uff0c\u4ed6\u624d\u5f80\u505c\u8f66\u7684\u5730\u65b9\u8d70\u3002</p><p>\u3000\u3000\u6574\u652f\u8f66\u961f\u90fd\u505c\u5728\u8def\u8fb9\u7b49\u4ed6\uff0c\u5f20\u606a\u770b\u89c1\u5510\u5b66\u8c26\u8f66\u7684\u8f66\u7a97\u6253\u5f00\u7740\uff0c\u4fbf\u5f84\u76f4\u671d\u5510\u5b66\u8c26\u7684\u8f66\u8d70\u8fc7\u53bb\u3002</p><p>\u3000\u3000\u201c\u6765\uff0c\u5750\u6211\u7684\u8f66\u3002\u201d\u5510\u5b66\u8c26\u5e2e\u5f20\u606a\u6253\u5f00\u8f66\u95e8\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u9646\u6587\u592b\u4e5f\u5728\u5510\u5b66\u8c26\u7684\u8f66\uff0c\u5510\u5b66\u8c26\u7684\u79d8\u4e66\u8bb8\u745e\u5e73\u4e0d\u5728\u8f66\u4e0a\uff0c\u5012\u662f\u8499\u5b66\u5e86\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\uff0c\u5927\u6982\u662f\u5728\u804a\u65b0\u4ead\u7684\u8bdd\u9898\uff0c\u5c06\u8499\u5b66\u5e86\u5524\u4e0a\u8f66\uff0c\u80fd\u66f4\u4e86\u89e3\u5177\u4f53\u7684\u60c5\u51b5\u3002\u5c31\u662f\u4e0d\u77e5\u9053\u8fd9\u4e9b\u843d\u5728\u522b\u4eba\u773c\u91cc\uff0c\u65b0\u4ead\u5e02\u91cc\u7684\u5176\u4ed6\u5b98\u5458\u5fc3\u91cc\u4f1a\u600e\u4e48\u60f3\u3002</p><p>\u3000\u3000\u8499\u5b66\u5e86\u5982\u4eca\u62c5\u4efb\u4e1c\u5c71\u6e2f\u533a\u526f\u533a\u957f\uff0c\u867d\u7136\u4ed6\u5bf9\u6e2f\u533a\u5efa\u8bbe\u66f4\u4e3a\u719f\u6089\uff0c\u65b0\u4ead\u5e02\u91cc\u8003\u8651\u5230\u4ed6\u7684\u7279\u6b8a\u6027\uff0c\u8ba9\u4ed6\u5206\u7ba1\u6e2f\u533a\u7684\u62db\u5546\u5f15\u8d44\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u89c1\u8499\u5b66\u5e86\u8981\u4e0b\u8f66\u7ed9\u81ea\u5df1\u8ba9\u4f4d\u5b50\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u6211\u5750\u540e\u9762\u8ddf\u5510\u4f2f\u4f2f\u3001\u9646\u7701\u957f\u6324\u4e00\u6324\u5c31\u662f\u2026\u2026\u6211\u4eec\u4e09\u4e2a\u90fd\u662f\u7626\u5b50\u3002\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u671d\u91cc\u8ba9\u4e86\u8ba9\uff0c\u8ba9\u5f20\u606a\u5750\u8fdb\u53bb\u3002</p><p>\u3000\u3000\u8f66\u884c\u9a76\u5728\u901a\u6e2f\u516c\u8def\u4e0a\uff0c\u4e24\u4fa7\u90fd\u662f\u5fd9\u788c\u4e0d\u4f11\u7684\u5de5\u5730\uff0c\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff1a\u201c\u4f3c\u4e4e\u80fd\u95fb\u5230\u5927\u5de5\u4e1a\u65f6\u4ee3\u7684\u6c14\u606f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u5fae\u7b11\u7740\u8bf4\uff1a\u201c\u4e2d\u592e\u8ba9\u5510\u7701\u957f\u8ddf\u5f90\u4e66\u8bb0\u5230\u6c5f\u5357\u6765\uff0c\u53ef\u4ee5\u6307\u671b\u4ed6\u4eec\u80fd\u80a9\u6311\u91cd\u4efb\u3001\u529b\u633d\u72c2\u6f9c\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u9646\u6587\u592b\u662f\u4e13\u7a0b\u4ee3\u8868\u4e1c\u6d77\u7701\u653f\u5e9c\u51fa\u5e2d\u5960\u57fa\u5178\u793c\u7684\uff0c\u4ed6\u7b11\u4e86\u7b11\uff0c\u5927\u5de5\u4e1a\u6c14\u606f\u4e5f\u662f\u5de8\u989d\u5efa\u8bbe\u8d44\u91d1\u7684\u6295\u5165\u6240\u81f4\uff0c\u5c31\u7b97\u5728\u5168\u7403\uff0c\u8fd9\u79cd\u5927\u8d44\u672c\u7684\u8fd0\u4f5c\u4e5f\u6781\u4e3a\u7f55\u89c1\u3002\u4e0d\u8981\u8bf4\u540e\u671f\u89c4\u6a21\u5de8\u5927\u7684\u4e34\u6e2f\u5de5\u4e1a\u5f00\u53d1\uff0c\u4ec5\u4ec5\u662f\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\u524d\u671f\u5de5\u7a0b\u5c31\u62c9\u52a8\u65b0\u4ead\u53ca\u5468\u8fb9\u5730\u533a\u7684\u7ecf\u6d4e\u89c4\u6a21\u589e\u6da8\u8d85\u8fc7\u4e00\u500d\uff0c\u53bb\u5e74\u6d2a\u707e\u4e2d\u906d\u53d7\u91cd\u521b\u7684\u6c5f\u5357\u7701\u7ecf\u6d4e\u4e5f\u56e0\u6b64\u6682\u65f6\u904f\u5236\u4f4f\u8870\u9000\u7684\u8d8b\u52bf\uff0c\u4eca\u5e74\u751a\u81f3\u5c31\u80fd\u6709\u8d85\u8fc7\u5168\u56fd\u5e73\u5747\u6c34\u5e73\u7684\u589e\u6da8\u3002</p><p>\u3000\u3000\u4e2d\u592e\u5728\u5173\u952e\u65f6\u523b\u679c\u65ad\u7684\u542f\u7528\u5f90\u5b66\u5e73\u3001\u5510\u5b66\u8c26\uff0c\u8fd9\u6b65\u68cb\u8fd8\u662f\u6781\u6709\u80c6\u7565\u4e0e\u89c1\u8bc6\u7684\uff0c\u4e0d\u7136\u771f\u7684\u5f88\u96be\u60f3\u8c61\u6c5f\u5357\u7701\u7ecf\u6d4e\u4f55\u65f6\u624d\u80fd\u8d70\u4e0a\u5f7b\u5e95\u590d\u82cf\u4e4b\u8def\u3002</p><p>\u3000\u3000\u8499\u5b66\u5e86\u5750\u5728\u524d\u6392\uff0c\u60f3\u7740\u9526\u6e56\u5546\u4e8b\u5728\u65b0\u4ead\u76f4\u63a5\u5df2\u7ecf\u6295\u5165\u4e0e\u5373\u5c06\u6295\u5165\u7684\u8d44\u91d1\u603b\u989d\u5c31\u63a5\u8fd1\u5341\u56db\u4ebf\u7f8e\u5143\uff0c\u8fd9\u662f\u65b0\u4ead\u4e5d\u4e03\u5e74\u5f15\u8fdb\u5916\u8d44\u603b\u989d\u7684\u4e03\u500d\u3002</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5510\u5b66\u8c26\u5750\u5728\u540e\u6392\uff0c\u60f3\u7b11\u5374\u6ca1\u6709\u7b11\uff0c\u800c\u662f\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u8bf4\u9053\uff0c\u201c\u7ecf\u6d4e\u6570\u636e\u6682\u65f6\u80fd\u6539\u89c2\uff0c\u53d7\u76ca\u533a\u57df\u8fd8\u662f\u6709\u9650\uff0c\u5168\u7701\u5de5\u4e1a\u7ed3\u6784\u7684\u8c03\u6574\u4e5f\u975e\u4e00\u671d\u4e00\u5915\u80fd\u5b8c\u6210\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u6320\u4e86\u6320\u8111\u888b\uff0c\u5fc3\u91cc\u4e5f\u6e05\u695a\u538b\u5728\u5f90\u5b66\u5e73\u3001\u5510\u5b66\u8c26\u7b49\u4eba\u5fc3\u5934\u7684\u662f\u4ec0\u4e48\u3002</p><p>\u3000\u3000\u660e\u5e74\u6c5f\u5357\u7701\u5168\u7701\u4e0b\u5c97\u804c\u5de5\u603b\u6570\u6709\u53ef\u80fd\u4f1a\u8d85\u8fc7\u4e00\u767e\u56db\u5341\u4e07\uff0c\u5b9e\u9645\u4e0a\u7b2c\u4e8c\u4ea7\u4e1a\u80fd\u63d0\u4f9b\u7684\u65b0\u5c97\u4f4d\u9884\u8ba1\u4e0d\u8db3\u5341\u4e07\u3002\u53e6\u5916\uff0c\u57ce\u9547\u5931\u4e1a\u4eba\u53e3\u7684\u5c31\u4e1a\u7075\u6d3b\u5ea6\u6bd4\u4e0d\u4e0a\u519c\u6751\u8d70\u51fa\u6765\u52a1\u5de5\u7684\u4eba\u5458\uff0c\u5c31\u50cf\u65b0\u4ead\u4e1c\u90e8\u7684\u5e9e\u5927\u5de5\u5730\u521b\u9020\u6570\u4ee5\u4e07\u8ba1\u7684\u5c97\u4f4d\uff0c\u6b64\u65f6\u5438\u7eb3\u7684\u52b3\u52a8\u529b\u4e3b\u529b\u8fd8\u662f\u519c\u6751\u8d70\u51fa\u6765\u7684\u52a1\u5de5\u4eba\u5458\u3002</p><p>\u3000\u3000\u4eca\u5929\u590f\u5929\uff0c\u4e1c\u6d77\u7701\u5168\u9762\u63d0\u9ad8\u4e0b\u5c97\u804c\u5de5\u7684\u751f\u6d3b\u6551\u6d4e\u6807\u51c6\uff0c\u7701\u8d22\u653f\u7edf\u7b79\u652f\u51fa\u56db\u4ebf\u591a\uff0c\u51e0\u4e4e\u6ca1\u6709\u538b\u529b\uff0c\u4f46\u662f\u6c5f\u5357\u7701\u8fde\u53bb\u5e74\u6d2a\u6c34\u53d7\u707e\u6c11\u4f17\u90fd\u6ca1\u6709\u5b89\u7f6e\u5b8c\u3002</p><p>\u3000\u3000\u9646\u6587\u592b\u7b11\u7740\u95ee\u5f20\u606a\uff1a\u201c\u4e2d\u6676\u5fae\u82af\u7684\u5047\u52a8\u4f5c\u592a\u591a\uff0c\u4e0d\u8981\u8bf4\u5916\u754c\u5bf9\u6b64\u611f\u5230\u6251\u6714\u8ff7\u79bb\uff0c\u7701\u91cc\u90fd\u6709\u4e9b\u7591\u60d1\u4e86\uff0c\u902e\u5230\u4f60\u7684\u4eba\u4e5f\u4e0d\u5bb9\u6613\uff0c\u5012\u662f\u8981\u95ee\u4e00\u95ee\u4f60\uff0c\u4f30\u8ba1\u91d1\u5c71\u5de5\u5382\u51e0\u65f6\u80fd\u4e0a\u9a6c\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd8\u8981\u770b\u65b0\u52a0\u5761\u90a3\u8fb9\u7684\u8c08\u5224\u8fdb\u5ea6\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u4e0d\u7ba1\u600e\u4e48\u8bf4\uff0c\u5e74\u5e95\u4e4b\u524d\u91d1\u5c71\u5de5\u5382\u5c31\u4f1a\u4e0a\u9a6c\uff0c\u5373\u4f7f\u65b0\u52a0\u5761\u65b9\u9762\u8c08\u4e0d\u4e0b\u6765\uff0c\u4e5f\u8981\u7b79\u96c6\u8d44\u91d1\u786c\u7740\u5934\u76ae\u4e0a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u9526\u6e56\u4e5f\u4e0d\u5e94\u8be5\u4ec5\u4ec5\u662f\u60f3\u5c06\u4e00\u5ea7\u6676\u5706\u5382\u5efa\u6210\u91d1\u5c71\u5427\uff1f\u201d\u9646\u6587\u592b\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u9646\u7701\u957f\u771f\u662f\u4e00\u53cc\u706b\u773c\uff0c\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u6211\u4eec\u4f30\u8ba1\u7740\uff0c\u518d\u8fc7\u4e24\u4e09\u5e74\uff0c\u4e2d\u4f4e\u7aef\u624b\u673a\u82af\u7247\u5dee\u4e0d\u591a\u4e5f\u5c06\u4ee50.25\u5fae\u7c73\u6676\u5706\u6280\u672f\u4e3a\u4e3b\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u9646\u6587\u592b\u4fa7\u8fc7\u5934\u6765\uff0c\u8bb6\u7136\u7684\u95ee\u9053\uff0c\u201c\u8fd9\u4e48\u9526\u6e56\u8981\u5728\u91d1\u5c71\u5efa\u5de5\u5382\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e00\u6bb5\u65f6\u671f\u4ee5\u6765\uff0c\u5373\u4f7f\u6709\u80fd\u529b\u63a7\u5236\u66f4\u591a\u4efd\u989d\u7684\u5e02\u573a\uff0c\u7231\u8fbe\u90fd\u4fdd\u6301\u7740\u4e00\u5b9a\u7a0b\u5ea6\u7684\u514b\u5236\uff0c\u201d\u5f20\u606a\u6478\u7740\u9f3b\u5b50\u8bf4\u9053\uff0c\u201c\u7ecf\u8425\u65b9\u9488\u603b\u4e0d\u662f\u56fa\u5b9a\u4e0d\u53d8\u7684\uff0c\u7279\u522b\u73b0\u9636\u6bb5\uff0c\u6211\u4eec\u5728\u4f4e\u7aef\u624b\u673a\u4e0a\u5df2\u7ecf\u5b8c\u6210\u4e86\u4e00\u4e9b\u51c6\u5907\u3002\u4f4e\u7aef\u624b\u673a\u6700\u7ec8\u65e0\u6cd5\u4f1a\u9003\u51fa\u8584\u5229\u7ade\u4e89\u7684\u5c40\u9762\uff0c\u5bf9\u4e8e\u65e0\u6cd5\u4eba\u4e3a\u9006\u8f6c\u7684\u4e8b\u60c5\uff0c\u6211\u4eec\u53ea\u80fd\u4e3b\u52a8\u53bb\u8fce\u5408\uff0c\u4e0b\u4e00\u6b65\u5c31\u662f\u6269\u5927\u751f\u4ea7\u89c4\u6a21\u8fdb\u4e00\u6b65\u644a\u8584\u6210\u672c\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u201d\u5510\u5b66\u8c26\u7b11\u7740\u95ee\u9053\uff0c\u201c\u6211\u5c31\u5728\u60f3\u4f60\u4eec\u4ec0\u4e48\u65f6\u5019\u4f1a\u7206\u4ea7\u80fd\u5462\uff0c\u7231\u8fbe\u624b\u673a\u773c\u4e0b\u7684\u624b\u673a\u7ec4\u88c5\u4ea7\u80fd\u624d\u4e24\u767e\u4e07\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u7ec4\u88c5\u4ea7\u80fd\u7a0d\u5f31\u4e00\u4e9b\uff0c\u4eca\u5e74\u521a\u80fd\u7a81\u7834\u4e24\u767e\u4e07\uff0c\u5236\u9020\u90e8\u95e8\u7684\u4e0b\u4e00\u6b65\u4e3b\u8981\u7cbe\u529b\u8fd8\u662f\u653e\u5728\u6838\u5fc3\u5143\u4ef6\u5668\u4ea7\u80fd\u6269\u5f20\u4e0a\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u73b0\u5728\u5c31\u5c06\u4e00\u4e9b\u7ec4\u88c5\u53ca\u4f4e\u7aef\u4ea7\u54c1\u4ea4\u7ed9\u65b0\u5143\u7535\u5668\u7b49\u4f01\u4e1a\u4ee3\u5de5\uff0c\u4e5f\u4f1a\u8fdb\u4e00\u6b65\u52a0\u5f3a\u5408\u4f5c\uff0c\u53ea\u662f\u4e0b\u4e00\u6b65\u7684\u4ea7\u80fd\u6269\u5f20\u5f88\u5927\uff0c\u5f88\u591a\u95ee\u9898\u8981\u534f\u8c03\u597d\uff0c\u8fdb\u5ea6\u5c31\u5feb\u4e0d\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6b65\u4f10\u8981\u8de8\u591a\u5927\uff1f\u201d\u5510\u5b66\u8c26\u773c\u775b\u53d1\u4eae\u7684\u95ee\u9053\u3002</p><p>\u3000\u3000\u201c\u4e24\u5e74\u80fd\u8981\u5efa\u62105000\u4e07\u624b\u673a\u7684\u624b\u673a\u7ec4\u88c5\u57fa\u5730\uff0c\u56db\u5e74\u5185\u4ea7\u80fd\u8981\u8fbe\u5230\u4e00\u4ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u5510\u5b66\u8c26\u3001\u9646\u6587\u592b\u540c\u65f6\u6df1\u5438\u4e00\u53e3\u6c14\uff0c\u9884\u8ba1\u5230\u96f6\u4e00\u5e74\u56fd\u5185\u79fb\u52a8\u7528\u6237\u603b\u6570\u90fd\u672a\u5fc5\u6709\u4e94\u5343\u4e07\uff0c\u96f6\u4e00\u5e74\u4ea7\u80fd\u7a81\u7834\u4e94\u5343\u4e07\u3001\u96f6\u4e09\u5e74\u4ea7\u80fd\u7a81\u7834\u4e00\u4ebf\u2026\u2026\u9526\u6e56\u7684\u76ee\u6807\u4f3c\u4e4e\u8fc7\u4e8e\u8fdc\u5927\u4e86\uff0c\u8981\u77e5\u9053\u4eca\u5e74\u79fb\u52a8\u901a\u8baf\u7528\u9014\u65b0\u589e\u7528\u6237\u521a\u521a\u8fc7\u4e00\u5343\u4e07\u3002</p><p>\u3000\u3000\u201c\u65b0\u7684\u5236\u9020\u57fa\u5730\u653e\u5728\u91d1\u5c71\uff1f\u201d\u5510\u5b66\u8c26\u95ee\u9053\uff0c\u5fc3\u91cc\u6e05\u695a\u7740\u5982\u6b64\u89c4\u6a21\u5236\u9020\u4f01\u4e1a\u5c06\u7ed9\u91d1\u5c71\u5e26\u6765\u4ec0\u4e48\u3002\u6676\u5706\u5382\u6240\u7528\u7684\u5efa\u8bbe\u8d44\u91d1\u591a\u6765\u7528\u4e8e\u9ad8\u7aef\u5236\u7a0b\u8bbe\u5907\u4e0e\u8d85\u51c0\u5ba4\u7684\u8fdb\u53e3\uff0c\u62db\u52df\u7684\u5458\u5de5\u4e5f\u90fd\u662f\u9ad8\u6c34\u51c6\uff0c\u5b9e\u9645\u4e0a\u5bf9\u7f13\u51b3\u91d1\u5c71\u5e02\u7684\u5c31\u4e1a\u538b\u529b\u6ca1\u6709\u592a\u5927\u7684\u5b9e\u9645\u6027\u5e2e\u52a9\uff0c\u4f46\u662f\u7ec4\u4ef6\u4ee5\u53ca\u7ec4\u88c5\u5de5\u5382\u7684\u60c5\u51b5\u5c31\u5927\u4e0d\u4e00\u6837\uff0c\u5de5\u5382\u5927\u591a\u6570\u5de5\u4f5c\u5c97\u4f4d\uff0c\u666e\u901a\u4eba\u7a0d\u52a0\u57f9\u8bad\u5c31\u80fd\u591f\u80dc\u4efb\uff0c\u800c\u4e14\u5de5\u4f5c\u5c97\u4f4d\u76f8\u5bf9\u5145\u5206\uff0c\u80fd\u66f4\u591a\u7684\u89e3\u51b3\u57ce\u5e02\u4eba\u53e3\u5931\u4e1a\u95ee\u9898\u3002</p><p>\u3000\u3000\u201c\u5dee\u4e0d\u591a\u662f\u8fd9\u6837\uff01\u201d\u5f20\u606a\u8bf4\u9053\u3002\u201c\u6211\u4eec\u662f\u5728\u8003\u8651\u5c06\u4f4e\u7aef\u624b\u673a\u7684\u751f\u4ea7\u5b8c\u5168\u653e\u5230\u91d1\u5c71\uff0c\u4e0d\u4ec5\u4ec5\u662f\u7ec4\u88c5\u7ebf\uff0c\u82af\u7247\u7ec4\u3001STN\u6676\u5c4f\u7b49\u7b49\u4f4e\u7aef\u7684\u751f\u4ea7\u7ebf\u90fd\u8981\u8fc1\u5230\u91d1\u5c71\u6765\uff0c\u6d77\u5dde\u3001\u5efa\u90ba\u5219\u4e13\u6ce8\u6838\u5fc3\u6280\u672f\u4e0e\u4e2d\u9ad8\u7aef\u4ea7\u4e1a\u7684\u5f00\u53d1\u4e0e\u751f\u4ea7\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5982\u6b64\u4e00\u6765\uff0c\u4f60\u4eec\u5c82\u4e0d\u662f\u8981\u5728\u4e2d\u4f4e\u7aef\u624b\u673a\u5e02\u573a\u79f0\u738b\uff1f\u201d\u9646\u6587\u592b\u95ee\u9053\u3002</p></article>"}}}]);
