(window.webpackJsonp=window.webpackJsonp||[]).push([[1091],{1114:function(p){p.exports={chapterTitle:null,title:"\u7b2c891\u7ae0 \u5c0f\u5ab3\u5987\u4e0e\u68cb\u5b50",content:"<article><h2>\u7b2c891\u7ae0 \u5c0f\u5ab3\u5987\u4e0e\u68cb\u5b50</h2><p>\u3000\u3000\u7fdf\u4e39\u9752\u4e0b\u53bb\u62ff\u4e86\u4e00\u5957\u5185\u8863\u8fc7\u6765\uff0c\u674e\u99a8\u4e88\u5230\u623f\u95f4\u8bd5\u7a7f\u4e86\u4e00\u4e0b\uff0c\u56de\u5230\u5ba2\u5385\u91cc\uff0c\u624b\u8fd8\u6367\u7740\u80f8\u53e3\uff0c\u624b\u91cc\u62ff\u7740\u7fdf\u4e39\u9752\u521a\u624d\u7ed9\u5979\u7684\u80f8\u7f69\uff0c\u4fcf\u8138\u6da8\u5f97\u7eef\u7ea2\uff0c\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u8ddf\u7fdf\u4e39\u9752\u8bf4\u9053\uff1a\u201c\u8c22\u8c22\u4f60\uff0c\u53ea\u662f\u2026\u2026\u6709\u4e9b\u52d2\uff01\u201d\u8bf4\u8fd9\u8bdd\u65f6\uff0c\u773c\u775b\u659c\u77a5\u4e86\u4e00\u773c\u5f20\u606a\uff0c\u7f9e\u4e0d\u80dc\u7f9e\u7684\u57cb\u4e0b\u4e86\u5934\u3002</p><p>\u3000\u3000\u536b\u5170\u4e0d\u53ef\u601d\u5f02\u7684\u5f20\u4e86\u5f20\u5634\uff0c\u5979\u53ef\u662f\u8ddf\u7fdf\u4e39\u9752\u6bd4\u8f83\u8fc7\u90a3\u91cc\u7684\u5927\u5c0f\uff1b\u5b59\u9759\u6aac\u9f3b\u8154\u91cc\u8f7b\u8f7b\u7684\u54fc\u4e86\u4e00\u58f0\uff0c\u8f6c\u5934\u770b\u5411\u4e00\u8fb9\uff0c\u5979\u53c8\u4e0d\u80fd\u8bf4\u674e\u99a8\u4e88\u5728\u5439\u725b\uff1b\u8bf4\u5b9e\u5728\u7684\uff0c\u7fdf\u4e39\u9752\u90a3\u4e00\u77ac\u95f4\u4e5f\u6709\u8981\u6253\u4eba\u7684\u51b2\u52a8\uff0c\u89c1\u5f20\u606a\u65e0\u52a8\u4e8e\u8877\u7684\u62ff\u7740\u8d44\u6599\u7ad9\u5728\u4e00\u65c1\u770b\u7740\uff0c\u597d\u50cf\u6ca1\u6709\u542c\u89c1\u674e\u99a8\u4e88\u8bf4\u7684\u8fd9\u53e5\u8bdd\uff0c\u5fc3\u60f3\u8fd9\u6df7\u86cb\u6628\u5929\u591c\u91cc\u5927\u6982\u5c06\u674e\u99a8\u4e88\u80f8\u524d\u7684\u90a3\u5bf9\u5c0f\u4e73\u732a\u73a9\u4e86\u4e2a\u75db\u5feb\uff0c\u8fd9\u65f6\u5019\u5fc3\u91cc\u6b63\u56de\u5473\u5f97\u75db\u5feb\u5427\uff1f\u4f46\u662f\u770b\u5230\u674e\u99a8\u4e88\u8138\u86cb\u6e05\u7eaf\u5a07\u8273\uff0c\u5a07\u7f9e\u3001\u5c0f\u7ffc\u7684\u6a21\u6837\uff0c\u597d\u50cf\u80f8\u7f69\u6709\u4e9b\u52d2\u5b8c\u5168\u662f\u5979\u7684\u9519\u4f3c\u7684\uff0c\u5979\u5fc3\u91cc\u53c8\u662f\u8f7b\u8f7b\u4e00\u53f9\uff1a\u8fd9\u6a21\u6837\u6211\u89c1\u72b9\u601c\uff0c\u4f55\u51b5\u65c1\u8fb9\u5750\u7740\u7684\u6d51\u7403\uff1f\u7fdf\u4e39\u9752\u7b11\u9765\u5982\u7b11\u7684\u5b89\u6170\u674e\u99a8\u4e88\u8bf4\u9053\uff1a\u201c\u6211\u5e2e\u4f60\u627e\u4e2a\u539a\u4e00\u70b9\u7684\u68c9\u886c\u886b\u5148\u7a7f\u7740\uff0c\u8fd9\u6837\u5c31\u4e0d\u6015\u8d70\u5149\u4e86\uff0c\u4f60\u90a3\u5c3a\u5bf8\uff0c\u53ea\u6015\u8fd8\u8981\u627e\u5185\u8863\u5e97\u8ba2\u505a\u54e9\uff01\u201d</p><p>\u3000\u3000\u201c\u8981\u771f\u7a7f\u4e0a\u539a\u68c9\u886c\u886b\uff0c\u6307\u4e0d\u5b9a\u67d0\u4eba\u4f1a\u4e0d\u4e50\u610f\u5462\u2026\u2026\u201d\u5b59\u9759\u6aac\u8737\u817f\u5750\u5728\u6c99\u53d1\u4e0a\uff0c\u8eab\u5b50\u6175\u61d2\u7684\u9760\u7740\u6c99\u53d1\u9760\u80cc\u5c55\u5f00\u7740\uff0c\u6ca1\u6709\u8981\u4e0a\u697c\u5e2e\u674e\u99a8\u4e88\u62ff\u8863\u670d\u7684\u610f\u601d\uff0c\u8fd8\u51b7\u4e0d\u4e01\u523a\u4e86\u5f20\u606a\u4e00\u53e5\u3002</p><p>\u3000\u3000\u201c\u7ed9\u4f60\u4eec\u6dfb\u9ebb\u70e6\u4e86\uff0c\u4e5f\u662f\u79c0\u85cf\u541b\u7684\u65e0\u793c\uff0c\u624d\u7ed9\u606a\u541b\u6dfb\u8fd9\u4e9b\u9ebb\u70e6\uff01\u201d\u674e\u99a8\u4e88\u53c8\u662f\u7f9e\u6da9\u53c8\u662f\u4e0d\u5b89\u7684\u8ddf\u7fdf\u4e39\u9752\u5979\u4eec\u4e00\u4e2a\u52b2\u7684\u9053\u6b49\uff0c\u4e00\u526f\u5c0f\u5ab3\u5987\u89c1\u5927\u5987\u7684\u6a21\u6837\uff0c\u201c\u63a5\u4e0b\u6765\u51e0\u5929\u8fd8\u8981\u9ebb\u70e6\u4f60\u4eec\u62db\u5f85\uff0c\u771f\u662f\u8fc7\u610f\u4e0d\u53bb\u2026\u2026\u201d</p><p>\u3000\u3000\u542c\u5230\u674e\u99a8\u4e88\u8fd9\u53e5\u8bdd\uff0c\u5b59\u9759\u6aac\u7709\u5934\u90fd\u8981\u7ad6\u4e86\u8d77\u6765\uff0c\u8d76\u60c5\u5979\u4eec\u4e09\u4e2a\u4eba\u79bb\u5f00\u4e00\u4f1a\u513f\uff0c\u5f20\u606a\u8fd9\u6d51\u7403\u5c31\u633d\u7559\u522b\u4eba\u4f4f\u4e0b\u6765\u4e86\uff1f\u6b6a\u7740\u5934\u770b\u4e86\u5f20\u606a\u51e0\u79d2\u949f\u2014\u2014\u89c1\u5f20\u606a\u65e0\u52a8\u4e8e\u8877\u7684\u5728\u770b\u624b\u91cc\u7684\u8d44\u6599\uff0c\u597d\u50cf\u8fd9\u4e8b\u8ddf\u4ed6\u5b8c\u5168\u6ca1\u5173\u7cfb\u4f3c\u7684\uff0c\u5979\u62c9\u4e86\u62c9\u536b\u5170\uff0c\u8bf4\u9053\uff1a\u201c\u6211\u4eec\u4e0a\u53bb\u5e2e\u674e\u5c0f\u59d0\u6311\u8863\u670d\u53bb\uff0c\u5c0f\u5a67\u590f\u5929\u8fd8\u6709\u8863\u670d\u7559\u5728\u8fd9\u91cc\uff0c\u6307\u4e0d\u5b9a\u674e\u5c0f\u59d0\u5408\u9002\u7a7f\u3002\u201d</p><p>\u3000\u3000\u201c\u4ec0\u4e48\u9ebb\u70e6\u4e0d\u9ebb\u70e6\u7684\uff0c\u8138\u788e\u4e86\u3001\u80bf\u4e86\u4e5f\u65e0\u6240\u8c13\uff0c\u5f20\u606a\u53cd\u6b63\u4e5f\u5e38\u8eb2\u8d77\u6765\u4e0d\u51fa\u53bb\u89c1\u4eba\uff0c\u201d\u7fdf\u4e39\u9752\u662f\u6b3a\u786c\u6015\u8f6f\u7684\u4e3b\uff0c\u89c1\u674e\u99a8\u4e88\u8fd9\u822c\u6a21\u6837\uff0c\u66f4\u4e0d\u5fcd\u5fc3\u6b3a\u8d1f\u5979\u4e86\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u4ed6\u8fc7\u4e86\u51e0\u5929\u8138\u4e0a\u7684\u4f24\u5c31\u597d\u4e86\uff0c\u5012\u662f\u4f60\u8138\u7684\u5370\u5b50\uff0c\u6211\u770b\u8981\u7b49\u5230\u4e0b\u5348\u624d\u80fd\u4f1a\u6d88\u6de1\u4e00\u4e9b\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u4e5f\u61d2\u5f97\u8ba1\u8f83\u7fdf\u4e39\u9752\u6697\u8bbd\u4ed6\u6ca1\u8138\u89c1\u4eba\uff0c\u53ea\u6015\u62c5\u5fc3\u674e\u99a8\u4e88\u4eca\u5929\u53bb\u5e08\u5927\u62a5\u540d\u5165\u5b66\u7684\u4e8b\u60c5\uff0c\u5979\u8fd9\u822c\u6a21\u6837\uff0c\u5a07\u8273\u8138\u4e0a\u5e26\u7740\u4e00\u9053\u9c9c\u7ea2\u7684\u5df4\u638c\u5370\uff0c\u66b4\u9732\u5728\u5916\u4eba\u7684\u9762\u524d\uff0c\u5c82\u4e0d\u662f\u8ba9\u5979\u5728\u5e08\u5927\u4ee5\u540e\u7684\u51e0\u5e74\u751f\u6daf\u90fd\u8981\u7559\u4e0b\u4e0d\u6109\u5feb\u7684\u75d5\u8ff9\uff1f</p><p>\u3000\u3000\u5f20\u606a\u62ac\u5934\u95ee\u7fdf\u4e39\u9752\uff0c\u5e08\u5927\u90a3\u8fb9\u7684\u62a5\u540d\u5165\u5b66\u80fd\u4e0d\u80fd\u627e\u4eba\u66ff\u674e\u99a8\u4e88\u4ee3\u529e\u4e00\u4e0b\uff0c\u5b59\u9759\u6aac\u7ad9\u5728\u6276\u68af\u90a3\u91cc\u542c\u5230\uff0c\u56de\u5934\u8bf4\u9053\uff1a\u201c\u4f60\u5728\u5e08\u5927\u4e0d\u662f\u4e5f\u6709\u51e0\u4e2a\u7ea2\u989c\u77e5\u5df1\u5417\uff1f\u627e\u5979\u4eec\u5e2e\u5fd9\u53bb\u95ee\u4e00\u4e0b\u4e0d\u5c31\u5f97\u4e86\u3002\u201d\u8bdd\u662f\u5e2e\u5fd9\u7684\u8bdd\uff0c\u53ea\u662f\u8bed\u6c14\u6709\u4e9b\u4e0d\u5584\u7f62\u4e86\uff0c\u5979\u60f3\u7740\u5f20\u606a\u53bb\u5e74\u79cb\u540e\u8fd8\u8ddf\u4eba\u5bb6\u5728\u5b66\u5e9c\u5df7\u7684\u9910\u5385\u91cc\u8ddf\u97e9\u56fd\u68d2\u5b50\u95f9\u8d77\u6765\u53bb\u6d3e\u51fa\u6240\uff0c\u600e\u4e48\u4e5f\u6ca1\u6709\u60f3\u5230\u5f20\u606a\u4e0d\u542d\u4e0d\u58f0\u7684\u5c31\u8ddf\u8fd9\u5996\u7cbe\u52fe\u642d\u4e0a\u4e86\uff1f</p><p>\u3000\u3000\u5f20\u606a\u8fd9\u624d\u610f\u8bc6\u5230\u5c06\u674e\u99a8\u4e88\u5e26\u56de\u5b66\u5e9c\u5df7\u4f3c\u4e4e\u4e0d\u662f\u90a3\u4e48\u7684\u5bf9\u52b2\uff0c\u610f\u8bc6\u5230\u4ed6\u7559\u5728\u90a3\u91cc\u5c31\u662f\u4e00\u4e2a\u5438\u5f15\u706b\u529b\u7684\u7126\u70b9\uff0c\u5429\u5490\u7fdf\u4e39\u9752\uff1a\u201c\u4f60\u7b49\u4f1a\u513f\u9886\u7740\u674e\u5c0f\u59d0\u5230\u697c\u4e0b\u9009\u5957\u5979\u559c\u6b22\u7684\u516c\u5bd3\uff0c\u9526\u6e56\u4e0d\u80fd\u6020\u6162\u4e86\u670b\u53cb\u2014\u2014\u5176\u4ed6\u4e8b\u60c5\uff0c\u4f60\u4e5f\u5e2e\u7740\u529e\u59a5\u4e86\uff0c\u201d\u5c06\u6587\u4ef6\u8d44\u6599\u5939\u5728\u814b\u4e0b\uff0c\u8ddf\u674e\u99a8\u4e88\u8bf4\u9053\uff0c\u201c\u6211\u8fd8\u6709\u4e9b\u4e8b\u60c5\u8981\u5904\u7406\uff0c\u8981\u6709\u4ec0\u4e48\u4e0d\u65b9\u4fbf\u7684\u5730\u65b9\uff0c\u4f60\u76f4\u63a5\u8fc7\u6765\u627e\u6211\u2026\u2026\u201d\u201c\u549a\u549a\u549a\u201d\u8d76\u7d27\u76f4\u63a5\u4e0a\u4e86\u697c\u8fdb\u4e86\u4ed6\u81ea\u5df1\u7684\u623f\u95f4\uff0c\u7684\u786e\u6709\u4e00\u5806\u7684\u90ae\u4ef6\u4e0e\u4e8b\u52a1\u7b49\u4ed6\u5904\u7406\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5f20\u606a\u800d\u6ed1\u5934\u6e9c\u8d70\uff0c\u5b59\u9759\u6aac\u706b\u6c14\u4e5f\u6ca1\u6709\u6492\u7684\u5730\u65b9\uff0c\u4e0d\u8fc7\u542c\u5f20\u606a\u7684\u610f\u601d\uff0c\u4e0d\u662f\u8981\u8ba9\u674e\u99a8\u4e88\u8ddf\u5979\u4eec\u4f4f\u5728\u4e00\u8d77\uff0c\u53e6\u5916\u7ed9\u674e\u99a8\u4e88\u5b89\u6392\u7684\u4f4f\u6240\u5c31\u5728\u773c\u76ae\u5b50\u5e95\u4e0b\uff0c\u4e5f\u6ca1\u6709\u53e6\u627e\u5730\u65b9\u91d1\u5c4b\u85cf\u5a07\u7684\u610f\u601d\uff0c\u5fc3\u60f3\u4f1a\u4e0d\u4f1a\u771f\u51a4\u6789\u4ed6\u4eec\u4e86\uff1f\u77a5\u4e86\u674e\u99a8\u4e88\u4e00\u773c\uff0c\u8f6c\u5ff5\u5c31\u5c06\u8fd9\u4e2a\u5ff5\u5934\u6253\u6d88\u6389\u4e86\uff1a\u7537\u4eba\u5728\u5979\u623f\u91cc\u501f\u5bbf\u4e00\u665a\uff0c\u8981\u80fd\u6309\u8010\u5f97\u4f4f\u624d\u53eb\u6709\u9b3c\u5462\uff0c\u5979\u5012\u662f\u5fd8\u4e86\u5979\u8ddf\u5f20\u606a\u540c\u5c45\u4e00\u5ba4\u8fd9\u4e48\u4e45\uff0c\u4e5f\u6ca1\u6709\u53d1\u751f\u903e\u8d8a\u6700\u540e\u4e00\u9053\u754c\u7ebf\u7684\u4e8b\u60c5\u3002\u53ea\u662f\u4e0d\u597d\u610f\u601d\u8ffd\u8fc7\u53bb\u8be6\u95ee\uff0c\u671d\u5f20\u606a\u7684\u80cc\u5f71\u77aa\u4e86\u51e0\u79d2\u949f\uff0c\u5c31\u8f6c\u8eab\u6765\u62ff\u51fa\u624b\u673a\u6253\u7535\u8bdd\u627e\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u95ee\u5e08\u5927\u7559\u5b66\u751f\u5165\u5b66\u62a5\u540d\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e5f\u4e0d\u662f\u80fd\u72e0\u4e0b\u5fc3\u6765\u6043\u5f3a\u51cc\u5f31\u7684\u4e3b\uff0c\u7279\u522b\u674e\u99a8\u4e88\u90a3\u526f\u5c0f\u5ab3\u5987\u7684\u6a21\u6837\uff0c\u4e5f\u8ba9\u5979\u4e0d\u5fcd\u5fc3\u6076\u8a00\u6076\u8bed\u76f8\u5411\uff0c\u5979\u5f53\u7136\u4e5f\u4e0d\u4f1a\u5c06\u8fd9\u4e8b\u60c5\u6345\u7ed9\u5510\u5a67\u77e5\u9053\uff0c\u5979\u81ea\u5df1\u8fd8\u5fc3\u865a\u7740\uff0c\u627e\u82cf\u4e00\u5a77\u3001\u4f55\u5f26\u8054\u7cfb\u674e\u99a8\u4e88\u5165\u5b66\u7684\u4e8b\u60c5\uff0c\u5c31\u662f\u5728\u60f3\uff1a\u8981\u662f\u82cf\u4e00\u5a77\u5c06\u8fd9\u4e8b\u544a\u8bc9\u5510\u5a67\u77e5\u9053\uff0c\u5c31\u8ddf\u5979\u6ca1\u6709\u5173\u7cfb\u4e86\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u5e2e\u674e\u99a8\u4e88\u5728\u8fd9\u680b\u697c\u91cc\u9009\u4e86\u4e00\u5957\u522b\u81f4\u7684\u516c\u5bd3\u8ba9\u5979\u5b89\u987f\u4e0b\u6765\uff0c\u5979\u8138\u4e0a\u7684\u5df4\u638c\u5370\u4e5f\u4e00\u65f6\u6d88\u4e0d\u4e86\uff0c\u7fdf\u4e39\u9752\u8ba9\u5979\u5728\u516c\u5bd3\u7a0d\u4f5c\u4f11\u606f\uff0c\u5979\u9700\u8981\u6dfb\u7f6e\u4ec0\u4e48\uff0c\u5979\u4eec\u5e2e\u5979\u4e70\u56de\u6765\u3002</p><p>\u3000\u3000\u201c\u4f60\u8bf4\u5f20\u606a\u662f\u4e0d\u662f\u8ddf\u6211\u4eec\u800d\u4ec0\u4e48\u82b1\u62db\uff1f\u201d\u8d70\u8fdb\u7535\u68af\u91cc\uff0c\u5b59\u9759\u6aac\u53c8\u8feb\u4e0d\u53ca\u5f85\u7684\u5c06\u5fc3\u91cc\u7684\u7591\u8651\u8bf4\u7ed9\u7fdf\u4e39\u9752\u3001\u536b\u5170\u542c\uff0c\u201c\u6545\u610f\u5c06\u97e9\u56fd\u5973\u4eba\u5b89\u6392\u5728\u6211\u4eec\u773c\u76ae\u5b50\u5e95\u4e0b\uff0c\u6446\u51fa\u4e00\u526f\u6e05\u8005\u81ea\u6e05\u7684\u6a21\u6837\uff0c\u9b3c\u624d\u76f8\u4fe1\u4ed6\u6628\u5929\u8ddf\u97e9\u56fd\u5973\u4eba\u5446\u4e00\u665a\u4e0a\u6ca1\u6709\u53d1\u751f\u4ec0\u4e48\uff1f\u201d\u89c1\u7fdf\u4e39\u9752\u3001\u536b\u5170\u90fd\u72d0\u7591\u7684\u770b\u5411\u5979\uff0c\u5fd9\u7ea2\u7740\u8138\u5fc3\u614c\u7684\u8fa9\u89e3\u9053\uff0c\u201c\u6211\u8ddf\u90a3\u6d51\u7403\u53ef\u6ca1\u6709\u53d1\u751f\u4ec0\u4e48\uff0c\u4f60\u4eec\u53ef\u4e0d\u8981\u778e\u60f3\uff01\u201d</p><p>\u3000\u3000\u201c\u90a3\u4f60\u4e00\u526f\u6068\u4e0d\u5f97\u8981\u5c06\u90a3\u5973\u4eba\u5403\u4e0b\u53bb\u7684\u6837\u5b50\u505a\u4ec0\u4e48\uff1f\u201d\u7fdf\u4e39\u9752\u9017\u5b59\u9759\u6aac\u7b11\u7740\u8bf4\uff0c\u201c\u54e6\uff0c\u5bf9\u4e86\uff0c\u6211\u660e\u767d\u4f60\u662f\u66ff\u5510\u5a67\u611f\u5230\u4e0d\u503c\u5462\u2026\u2026\u201d</p><p>\u3000\u3000\u5b59\u9759\u6aac\u8fd9\u4e0b\u5b50\u6c14\u7130\u5c31\u706d\u4e86\uff0c\u8fd8\u8981\u5c0f\u5fc3\u7ffc\u7ffc\u7684\u63a9\u9970\u7740\u4e0d\u8ba9\u7fdf\u4e39\u9752\u770b\u51fa\u4e9b\u4ec0\u4e48\u6765\u3002</p><p>\u3000\u3000\u536b\u5170\u8981\u53bb\u5b66\u6821\u53c2\u52a0\u9662\u91cc\u7684\u65b0\u751f\u5165\u5b66\u4eea\u5f0f\uff0c\u8fd8\u8981\u51c6\u5907\u5de5\u4f5c\u7684\u4e8b\u60c5\uff0c\u7fdf\u4e39\u9752\u5c31\u62d6\u7740\u5b59\u9759\u6aac\u966a\u5979\u4e00\u8d77\u5e2e\u674e\u99a8\u4e88\u6dfb\u7f6e\u4e1c\u897f\u53bb\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u91d1\u5357\u52c7\u5230\u4e0b\u5348\u624d\u77e5\u9053\u674e\u99a8\u4e88\u7ed9\u5f20\u606a\u62d0\u8dd1\u7684\u4e8b\u60c5\uff0c\u4ed6\u627e\u5230\u674e\u5728\u6d19\uff1a\u201c\u99a8\u4e88\u5c0f\u59d0\u7684\u4e8b\u60c5\uff0c\u662f\u4e0d\u662f\u8ddf\u674e\u4f1a\u957f\u8bf4\u4e00\u58f0\uff1f\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4ec0\u4e48\u4e8b\u60c5\uff0c\u99a8\u4e88\u5df2\u7ecf\u7ed9\u5efa\u90ba\u7684\u5206\u516c\u53f8\u6253\u8fc7\u7535\u8bdd\u4e86\uff0c\u8bf4\u5979\u8981\u6682\u65f6\u4f4f\u9752\u5e74\u516c\u5bd3\u90a3\u91cc\u2026\u2026\u201d\u674e\u5728\u6d19\u6ee1\u4e0d\u5728\u4e4e\u7684\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u8fd9\u600e\u4e48\u884c\uff1f\u201d\u91d1\u5357\u52c7\u7126\u6025\u7684\u8bf4\u9053\uff0c\u201c\u65e2\u7136\u99a8\u4e88\u5c0f\u59d0\u5230\u5efa\u90ba\u662f\u53e6\u6709\u76ee\u7684\uff0c\u5c31\u5e94\u8be5\u8981\u6c42\u5979\u79bb\u5f00\u4e2d\u56fd\u3002\u201d</p><p>\u3000\u3000\u201c\u99a8\u4e88\u662f\u6210\u5e74\u4eba\u4e86\uff0c\u201d\u674e\u5728\u6d19\u7709\u5934\u4e00\u626c\uff0c\u6709\u4e9b\u4e0d\u8010\u70e6\u7684\u8bf4\u9053\uff0c\u201c\u6211\u770b\u4f60\u4e5f\u4e0d\u8981\u5e72\u6d89\u5979\u7684\u51b3\u5b9a\u597d\u4e86\uff0c\u53ea\u8981\u5efa\u90ba\u5206\u516c\u53f8\u80fd\u968f\u65f6\u638c\u63e1\u99a8\u4e88\u7684\u884c\u8e2a\u5c31\u53ef\u4ee5\u4e86\uff0c\u5b66\u5e9c\u5df7\u9644\u8fd1\u7684\u6cbb\u5b89\u4e5f\u503c\u5f97\u4fe1\u4efb\uff0c\u5176\u4ed6\u4e8b\u60c5\u4e0d\u8981\u53bb\u7ba1\u5b83\u4e86\u3002\u201d</p><p>\u3000\u3000\u674e\u5728\u6d19\u5012\u4e0d\u662f\u8981\u5e2e\u99a8\u4e88\uff0c\u4ed6\u5fc3\u91cc\u4e00\u6837\u75db\u6068\u88ab\u8a89\u4e3a\u5357\u97e9\u4e4b\u82b1\u7684\u99a8\u4e88\u7adf\u7136\u7ed9\u652f\u90a3\u7537\u4eba\u73a9\u5f04\uff0c\u4f46\u662f\u4e8b\u60c5\u5df2\u7ecf\u53d1\u751f\u4e86\uff0c\u5c31\u8981\u5c06\u4f5c\u7528\u6216\u4ef7\u503c\u53d1\u6325\u5230\u6700\u5927\uff0c\u8fd9\u4e8b\u60c5\u8981\u662f\u8ddf\u53d4\u53d4\u674e\u5065\u7199\u6c47\u62a5\uff0c\u591a\u534a\u4f1a\u6d3e\u4eba\u5c06\u674e\u99a8\u4e88\u5f3a\u884c\u5e26\u56de\u97e9\u56fd\u53bb\uff0c\u4f46\u662f\u90a3\u6837\u505a\u53c8\u6709\u4ec0\u4e48\u989d\u5916\u7684\u597d\u5904\uff0c\u5229\u7528\u99a8\u4e88\u4e0e\u4e09\u4e95\u52a0\u5f3a\u8054\u7cfb\u7684\u76ee\u7684\u5df2\u7ecf\u7ed9\u7834\u574f\u4e86\uff0c\u90a3\u8fd9\u65f6\u5019\u5c31\u8981\u4fdd\u6301\u5e76\u52a0\u5f3a\u6c60\u4f50\u79c0\u85cf\u5bf9\u9526\u6e56\u7684\u618e\u6068\uff0c\u81f3\u5c11\u4e09\u661f\u770b\u8d77\u6765\u4e5f\u662f\u53d7\u5230\u4e86\u4f24\u5bb3\u2014\u2014\u5982\u6b64\u624d\u5bf9\u4e09\u661f\u5f00\u62d3\u4e2d\u56fd\u7684\u5e02\u573a\u6709\u6240\u5e2e\u52a9\uff1b\u674e\u5728\u6d19\u5df2\u7ecf\u5c06\u9526\u6e56\u89c6\u4e3a\u4e09\u661f\u8fdb\u5165\u4e2d\u56fd\u5e02\u573a\u7684\u6700\u5927\u963b\u529b\uff0c\u4f46\u662f\u4e09\u661f\u5bf9\u4e2d\u56fd\u5e02\u573a\u7684\u6e17\u900f\u8fd8\u4e0d\u8db3\u591f\u5f3a\uff0c\u4e00\u5b9a\u8981\u501f\u52a9\u4e09\u4e95\u7684\u52bf\u529b\u53bb\u538b\u5236\u9526\u6e56\u624d\u884c\u3002\u5bb6\u65cf\u7684\u5973\u4eba\uff0c\u603b\u8981\u4e3a\u5bb6\u65cf\u505a\u51fa\u4e9b\u8d21\u732e\u7684\uff0c\u4e5f\u8981\u8ba9\u5f20\u606a\u90a3\u4e2a\u6df7\u86cb\u660e\u767d\uff1a\u4e09\u661f\u7684\u5973\u4eba\u4e0d\u662f\u90a3\u4e48\u597d\u73a9\u5f04\u7684\uff01</p><p>\u3000\u3000\u91d1\u5357\u52c7\u8fd9\u65f6\u5019\u5df2\u7ecf\u60f3\u660e\u767d\u4e86\u674e\u5728\u6d19\u7684\u5fc3\u601d\uff0c\u5fc3\u91cc\u4e3a\u99a8\u4e88\u611f\u5230\u4e00\u9635\u60b2\u51c9\uff0c\u6ca1\u6709\u8ddf\u674e\u5728\u6d19\u4e89\u8bba\u4ec0\u4e48\uff0c\u4ed6\u4e5f\u4e0d\u4f1a\u5c06\u4e8b\u60c5\u5355\u72ec\u6345\u5230\u603b\u90e8\u53bb\uff0c\u90a3\u6837\u5bf9\u99a8\u4e88\u7684\u4f24\u5bb3\u66f4\u5927\uff0c\u4ed6\u521a\u8981\u79bb\u5f00\u674e\u5728\u6d19\u5728\u5efa\u90ba\u7684\u4e34\u65f6\u529e\u516c\u5ba4\uff0c\u674e\u5728\u6d19\u7684\u79d8\u4e66\u8d70\u4e86\u8fdb\u6765\uff0c\u544a\u8bc9\u674e\u5728\u6d19\uff1a\u201c\u5efa\u90ba\u7cbe\u5178\u5730\u4ea7\u7684\u603b\u88c1\u6253\u7535\u8bdd\u8bf4\u662f\u60f3\u7ea6\u89c1\u674e\u4e13\u52a1\uff1f\u201d</p><p>\u3000\u3000\u674e\u5728\u6d19\u5bf9\u7cbe\u5178\u6ca1\u6709\u7559\u4e0b\u4ec0\u4e48\u5370\u8c61\uff0c\u62b1\u6028\u7684\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u4ec0\u4e48\u4eba\u90fd\u8981\u89c1\u9762\uff0c\u4e5f\u5f97\u8981\u6211\u6709\u4e09\u5934\u516d\u81c2\u624d\u884c\uff0c\u201d\u6325\u4e86\u6325\u624b\uff0c\u5429\u5490\u79d8\u4e66\u8bf4\u9053\uff0c\u201c\u544a\u8bc9\u5bf9\u65b9\u8bf4\u6211\u65f6\u95f4\u4e0a\u6309\u6392\u4e0d\u8fc7\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u7b49\u7b49\uff0c\u201d\u91d1\u5357\u52c7\u558a\u4f4f\u674e\u5728\u6d19\u7684\u79d8\u4e66\uff0c\u4ed6\u867d\u7136\u5bf9\u674e\u5728\u6d19\u6ee1\u8179\u610f\u89c1\uff0c\u4f46\u8fd8\u80fd\u5fe0\u4e8e\u804c\u5b88\uff0c\u5bf9\u674e\u5728\u6d19\u8bf4\u9053\uff0c\u201c\u7cbe\u5178\u4e0e\u5efa\u90ba\u5e02\u5e38\u52a1\u526f\u5e02\u957f\u80e1\u5b97\u5e86\u7684\u5173\u7cfb\u751a\u5bc6\uff0c\u53e6\u5916\u4e0e\u534e\u590f\u7535\u5b50\u7684\u5173\u7cfb\u4e5f\u4e0d\u9519\uff0c\u8fd8\u8bb0\u5f97\u534e\u590f\u7535\u5b50\u8463\u4e8b\u957f\u845b\u660e\u5fb7\u66fe\u4ecb\u7ecd\u7cbe\u5178\u7684\u603b\u88c1\u7ed9\u4f60\u8ba4\u8bc6\uff1f\u201d</p><p>\u3000\u3000\u201c\u54e6\uff0c\u662f\u90a3\u4e2a\u5973\u4eba\uff1f\u201d\u674e\u5728\u6d19\u60f3\u8d77\u6765\u4e86\uff0c\u6df7\u8ff9\u5546\u754c\u7684\u5973\u5f3a\u4eba\u4e0d\u5c11\uff0c\u8fd9\u4e48\u6f02\u4eae\u7684\u53ef\u4e0d\u591a\u89c1\u3002</p><p>\u3000\u3000\u201c\u7cbe\u5178\u66fe\u7ecf\u8ddf\u4e16\u7eaa\u9526\u6e56\u4e89\u593a\u5b66\u5e9c\u5df7\u7684\u5f00\u53d1\u6743\u5931\u5229\uff0c\u800c\u4e14\u4ed6\u4eec\u5e74\u521d\u5728\u5efa\u90ba\u4e0a\u9a6c\u4e86\u534e\u4e1c\u6700\u5927\u7684\u8f6f\u4ef6\u4ea7\u4e1a\u56ed\u9879\u76ee\uff0c\u674e\u4e13\u52a1\u5728\u5efa\u90ba\u4e0d\u59a8\u62bd\u51fa\u65f6\u95f4\u8ddf\u4ed6\u4eec\u63a5\u89e6\u4e00\u4e0b\u2026\u2026\u201d\u91d1\u5357\u52c7\u5efa\u8bae\u9053\u3002</p><p>\u3000\u3000\u201c\u90a3\u4f60\u5e2e\u6211\u5b89\u6392\u4e00\u4e0b\u2026\u2026\u201d\u674e\u5728\u6d19\u5012\u662f\u4ece\u5584\u5982\u6d41\uff0c\u7b54\u5e94\u4e86\u6797\u96ea\u7684\u89c1\u9762\u8981\u6c42\u3002</p></article>"}}}]);
