(window.webpackJsonp=window.webpackJsonp||[]).push([[1096],{1119:function(p){p.exports={chapterTitle:null,title:"\u7b2c896\u7ae0 \u5229\u76ca\u5747\u6cbe",content:"<article><h2>\u7b2c896\u7ae0 \u5229\u76ca\u5747\u6cbe</h2><p>\u3000\u3000\u201c\u8fd9\u5c0f\u5b50\u6ca1\u6709\u51e0\u5929\u8e66\u5934\u4e86\u5427\uff01\u201d\u5f20\u77e5\u975e\u5750\u8fdb\u8f66\u91cc\uff0c\u8bf4\u4e86\u4e00\u53e5\u3002</p><p>\u3000\u3000\u5f20\u606a\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u900f\u8fc7\u8f66\u7a97\u770b\u89c1\u8c22\u5251\u5357\u8d70\u5411\u9152\u5e97\u524d\u7684\u505c\u8f66\u573a\u2014\u2014\u4ed6\u7684\u8f66\u505c\u5728\u90a3\u91cc\u3002</p><p>\u3000\u3000\u201c\u96be\u8bf4\u7684\u5f88\uff0c\u201d\u5f20\u606a\u5f88\u8212\u670d\u7684\u9760\u5728\u8f66\u5750\u6905\u5b50\u771f\u76ae\u9760\u80cc\u4e0a\uff0c\u7fdf\u4e39\u9752\u5750\u5728\u524d\u9762\uff0c\u4ed6\u5750\u5728\u8fd9\u91cc\u80fd\u770b\u89c1\u5979\u767d\u7699\u3001\u67d4\u548c\u7684\u8138\u988a\uff0c\u8033\u5eca\u7684\u808c\u80a4\u6676\u83b9\u5254\u900f\uff0c\u201c\u9526\u6e56\u4ece\u6765\u90fd\u4e0d\u4f1a\u7f3a\u4e4f\u5bf9\u624b\u7684\uff0c\u8c22\u5251\u5357\u53c8\u4e0d\u662f\u5168\u65e0\u80fd\u8010\u7684\u4eba\uff0c\u4f60\u770b\u4ed6\u7684\u8138\uff0c\u4e5f\u6ca1\u6709\u4e27\u5bb6\u4e4b\u72ac\u7684\u6a21\u6837\u2014\u2014\u7cbe\u5178\u7684\u90a3\u4e2a\u5973\u4eba\u4e5f\u4e0d\u662f\u7b49\u95f2\u4e4b\u8f88\uff0c\u6211\u6000\u7591\u8fd8\u662f\u8ddf\u5b8f\u4fe1\u5efa\u8bbe\u7684\u4e25\u6587\u4ecb\u6709\u5173\u7cfb\u2026\u2026\u201d\u5f20\u606a\u60f3\u8d77\u516b\u6708\u672b\u5728\u5b66\u5e9c\u5df7\u4e0e\u4e25\u6587\u4ecb\u76f8\u9047\u7684\u60c5\u5f62\uff0c\u4e25\u6587\u4ecb\u90a3\u65f6\u4f3c\u4e4e\u5df2\u7ecf\u786e\u5b9a\u4e0e\u9526\u6e56\u4f1a\u6709\u5229\u76ca\u51b2\u7a81\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u5750\u76f4\u8eab\u5b50\uff0c\u4f38\u624b\u63a8\u4e86\u63a8\u524d\u5ea7\u7fdf\u4e39\u9752\u67d4\u8f6f\u7684\u9999\u80a9\uff0c\u95ee\u5979\uff1a\u201c\u4f60\u8bf4\u9526\u6e56\u4e0e\u5b8f\u4fe1\u5efa\u8bbe\u53ef\u80fd\u4f1a\u5728\u54ea\u4e9b\u5730\u65b9\u6709\u5229\u76ca\u51b2\u7a81\uff1f\u201d</p><p>\u3000\u3000\u201c\u2026\u2026\u201d\u5f20\u77e5\u975e\u770b\u5230\u8c22\u5251\u5357\u9a7e\u8f66\u52a0\u901f\u8d85\u8fc7\u4ed6\u4eec\uff0c\u8111\u5b50\u91cc\u7a81\u7136\u95ea\u8fc7\u4e00\u4e2a\u5ff5\u5934\uff0c\u8bf4\u9053\uff0c\u201c\u8c22\u5bb6\u90fd\u5206\u5bb6\u4e86\uff0c\u79d1\u738b\u8981\u81ea\u6551\uff0c\u53ea\u80fd\u5c06\u9526\u57ce\u5730\u4ea7\u7684\u80a1\u4efd\u8131\u624b\u5957\u73b0\uff0c\u7cbe\u5178\u5730\u4ea7\u4e5f\u662f\u5982\u6b64\uff0c\u4ed6\u4eec\u4f1a\u4e0d\u4f1a\u4e3b\u52a8\u627e\u5b8f\u4fe1\u5efa\u8bbe\u5408\u4f5c\uff1f\u201d</p><p>\u3000\u3000\u201c\u5e94\u8be5\u8bf4\u5b8f\u4fe1\u5efa\u8bbe\u65e9\u5c31\u770b\u4e2d\u9526\u57ce\u5730\u4ea7\u4e86\u5427\uff1f\u201d\u5f20\u606a\u5fae\u5fae\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u4ed6\u867d\u7136\u5bf9\u9526\u57ce\u5730\u4ea7\u6beb\u65e0\u8d2a\u5ff5\uff0c\u4f46\u662f\u9526\u6e56\u8fd9\u4e9b\u5e74\u6765\u4e00\u76f4\u90fd\u7528\u5fc3\u601d\u7684\u538b\u5236\u9526\u57ce\u5730\u4ea7\uff0c\u65e0\u8bba\u9526\u57ce\u5730\u4ea7\u662f\u5728\u8d75\u9526\u8363\u624b\u91cc\uff0c\u8fd8\u662f\u5728\u8c22\u5bb6\u3001\u7cbe\u5178\u4ee5\u53ca\u5609\u4fe1\u624b\u91cc\u2014\u2014\u5728\u65c1\u4eba\u773c\u91cc\u53ea\u6015\u65e9\u5c31\u8ba4\u5b9a\u9526\u6e56\u8fd9\u4e48\u505a\u7684\u610f\u56fe\u6700\u7ec8\u662f\u4e3a\u541e\u5e76\u9526\u57ce\u5730\u4ea7\uff0c\u5b8f\u4fe1\u5efa\u8bbe\u81ea\u4ee5\u4e3a\u8981\u4ece\u9526\u6e56\u864e\u53e3\u593a\u98df\uff0c\u4e25\u6587\u4ecb\u5728\u5b66\u5e9c\u5df7\u7684\u5472\u7259\u54a7\u5634\u4e5f\u4e0d\u96be\u7406\u89e3\u4e86\u3002\u8fd8\u6709\uff0c\u8096\u660e\u5efa\u80cc\u540e\u7684\u652f\u6301\u8005\u5e94\u8be5\u662f\u6d59\u4e1c\u4e25\u5bb6\u5427\uff0c\u8fd9\u4e48\u770b\u6765\uff0c\u4e25\u6587\u4ecb\u5bf9\u5efa\u90ba\u7684\u623f\u5730\u4ea7\u4e5f\u5174\u8da3\u76ce\u7136\u554a\u3002</p><p>\u3000\u3000\u201c\u5b8f\u4fe1\u5efa\u8bbe\u53d1\u5c55\u5230\u4eca\u5929\u7684\u89c4\u6a21\uff0c\u6307\u4e0d\u5b9a\u4e5f\u662f\u91ce\u5fc3\u52c3\u52c3\u554a\uff01\u201d\u7fdf\u4e39\u9752\u8f7b\u8f7b\u611f\u6168\u4e86\u4e00\u53e5\uff0c\u201c\u8bf4\u4e0d\u5b9a\u4ed6\u4eec\u8fd8\u6709\u4ec0\u4e48\u8ba1\u5212\u65e0\u610f\u4e2d\u7ed9\u9526\u6e56\u7834\u574f\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u5012\u4e5f\u662f\u7684\uff0c\u9526\u6e56\u6321\u4eba\u8d22\u8def\u7684\u4e8b\u60c5\u505a\u5f97\u4e0d\u591a\uff0c\u4f46\u4e5f\u7edd\u4e0d\u80fd\u7b97\u5c11\u2026\u2026\u8fd9\u53c8\u662f\u6ca1\u6709\u529e\u6cd5\u907f\u514d\u7684\u3002\u201d\u5f20\u606a\u5fae\u5fae\u4e00\u7b11\uff0c\u5bf9\u5b8f\u4fe1\u5efa\u8bbe\u6709\u53ef\u80fd\u6210\u4e3a\u5bf9\u624b\u5e76\u6ca1\u6709\u611f\u5230\u7279\u522b\u7684\u62c5\u5fc3\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u7701\u59d4\u62db\u5f85\u5bbe\u9986\u6700\u91cc\u4fa7\u6709\u5ea7\u7cbe\u81f4\u7684\u9662\u5b50\uff0c\u4e3b\u4f53\u5efa\u7b51\u662f\u5ea7\u4e24\u5c42\u7684\u6d0b\u623f\uff0c\u8fd9\u8fd8\u662f\u89e3\u653e\u4e4b\u524d\u91d1\u5c71\u5b88\u5907\u53f8\u4ee4\u84c4\u517b\u5c0f\u59be\u7684\u540e\u9662\uff0c\u5efa\u56fd\u540e\uff0c\u524d\u540e\u9662\u90fd\u7528\u6765\u6539\u5efa\u4e3a\u62db\u5f85\u5bbe\u9986\uff0c\u91d1\u5c71\u91cc\u7684\u65e7\u5efa\u7b51\u4fdd\u5b58\u5b8c\u597d\u7684\u4e0d\u591a\uff0c\u8fd9\u91cc\u7684\u73af\u5883\u5e7d\u6df1\uff0c\u5f90\u5b66\u5e73\u4e0e\u59bb\u5b50\u5468\u6dd1\u60e0\u5c31\u4f4f\u5728\u6700\u91cc\u4fa7\u7684\u9662\u5b50\u91cc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u5c0f\u53d4\u5f20\u77e5\u975e\u8d76\u5230\uff0c\u5f90\u5b66\u5e73\u4e5f\u624d\u56de\u6765\uff0c\u7701\u59d4\u526f\u4e66\u8bb0\u3001\u7701\u957f\u6881\u4f1f\u6cd5\u4e5f\u5728\u8fd9\u91cc\u3002</p><p>\u3000\u3000\u591c\u8272\u6df1\u6c89\uff0c\u6881\u4f1f\u6cd5\u90fd\u4e0d\u8bb0\u5f97\u8fd9\u6bb5\u65f6\u95f4\u6709\u5728\u51cc\u6668\u4e4b\u524d\u5165\u7761\u8fc7\uff0c\u65e9\u5728\u4e03\u6708\u4e0a\u65ec\uff0c\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u5c31\u5411\u4ed6\u4e0e\u5f53\u65f6\u7684\u7701\u59d4\u4e66\u8bb0\u5c1a\u5b66\u4e49\u6c47\u62a5\u4e86\u4e1c\u5c71\u5c9b\u5efa\u6df1\u6c34\u6e2f\u7684\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f53\u7136\uff0c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\u95ee\u9898\uff0c\u65e9\u5728\u5341\u5e74\u4e4b\u524d\u5c31\u6709\u4eba\u63d0\u51fa\u6765\u4e86\uff0c\u6280\u672f\u4e0a\u7684\u95ee\u9898\uff0c\u65e9\u5c31\u5168\u90e8\u5f97\u5230\u89e3\u51b3\uff0c\u4e00\u76f4\u62d6\u5230\u73b0\u5728\u90fd\u6ca1\u6709\u843d\u5230\u5b9e\u5904\uff0c\u81ea\u6709\u5f88\u591a\u7684\u539f\u56e0\uff0c\u6700\u5173\u952e\u7684\u56e0\u7d20\u8fd8\u662f\u6c5f\u5357\u7701\u7ecf\u6d4e\u5bf9\u6df1\u6c34\u6d77\u6e2f\u4f9d\u8d56\u4e0d\u5f3a\u3001\u5730\u65b9\u8d22\u653f\u7f3a\u4e4f\u5efa\u6e2f\u8d44\u91d1\u3002</p><p>\u3000\u3000\u8fd9\u6b21\u4e0e\u4ee5\u5f80\u4e0d\u540c\u7684\u662f\uff0c\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u63d0\u51fa\u8981\u8054\u5408\u9526\u6e56\u5546\u4e8b\u5f15\u5165\u6d77\u5916\u8d44\u672c\u53c2\u4e0e\u5efa\u6e2f\u7684\u65b0\u6784\u60f3\u3002</p><p>\u3000\u3000\u5f53\u65f6\uff0c\u6c5f\u5357\u7684\u6c5b\u60c5\u5c31\u5341\u5206\u7684\u7d27\u6025\uff0c\u4e5f\u6ca1\u6709\u65f6\u95f4\u53ec\u5f00\u5e38\u59d4\u4f1a\u8bae\u4e13\u95e8\u8ba8\u8bba\u8fd9\u4e8b\uff0c\u5012\u662f\u66fe\u5982\u5723\u526f\u603b\u7406\u4e24\u6b21\u5230\u91d1\u5c71\u89c6\u5bdf\u707e\u60c5\uff0c\u968f\u53e3\u63d0\u5230\u4e1c\u5c71\u5c9b\u5efa\u6df1\u6c34\u6e2f\u7684\u4e8b\u60c5\u3002</p><p>\u3000\u3000\u91d1\u5c71\u6e56\u6e83\u5824\u4e4b\u540e\uff0c\u6c5f\u5357\u7701\u7ecf\u6d4e\u906d\u53d7\u91cd\u521b\uff0c\u7701\u5185\u7684\u4f01\u4e1a\u6d3b\u8dc3\u5ea6\u4e5f\u6781\u4e3a\u6709\u9650\uff0c\u77ed\u671f\u5185\u6c5f\u5357\u7701\u8981\u907f\u514d\u51fa\u73b0\u7ecf\u6d4e\u4e25\u91cd\u8870\u9000\uff0c\u552f\u4e00\u7684\u9014\u5f84\u5c31\u662f\u4ee5\u5927\u89c4\u6a21\u7684\u57fa\u5efa\u6295\u8d44\u62c9\u52a8\u7ecf\u6d4e\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u4e5d\u4e03\u5e74\u5168\u7701\u56fd\u6c11\u751f\u4ea7\u603b\u503c\u8fd8\u4e0d\u5230\u4e24\u5343\u516d\u767e\u4ebf\uff0c\u65b0\u589e\u6295\u8d44\u4e0d\u8db3\u5343\u4ebf\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6df1\u6c34\u6e2f\u4ec5\u57fa\u5efa\u6295\u8d44\u5c31\u9700\u8981\u4e24\u767e\u4ebf\uff0c\u4e24\u767e\u4ebf\u7684\u57fa\u5efa\u6295\u8d44\u52bf\u5fc5\u5c06\u5927\u5e45\u4fc3\u8fdb\u5e26\u52a8\u6c34\u6ce5\u3001\u94a2\u6750\u3001\u5316\u5de5\u7b49\u5de5\u4e1a\u539f\u6750\u6599\u7684\u6295\u8d44\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u4e00\u76f4\u90fd\u4e0d\u662f\u5f88\u6e05\u695a\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7684\u95ee\u9898\u662f\u4ece\u54ea\u4e2a\u6e20\u9053\u53cd\u5e94\u5230\u66fe\u526f\u603b\u7406\u7684\u8033\u91cc\uff0c\u8bf4\u4e0d\u5b9a\u662f\u65b0\u4ead\u5e02\u59d4\u4e66\u8bb0\u7aa6\u552f\u6d9b\u6709\u4e9b\u624b\u8155\u3002\u542c\u5230\u9662\u5b50\u91cc\u6709\u6c7d\u8f66\u5f15\u64ce\u505c\u7184\u7684\u58f0\u97f3\uff0c\u4e0e\u5f90\u5b66\u5e73\u540c\u65f6\u5750\u76f4\u4e86\u5f80\u7a97\u5916\u770b\u4e86\u770b\uff0c\u5f20\u606a\u4e0e\u5f20\u77e5\u975e\u6b63\u4e0b\u8f66\u6765\u3002</p><p>\u3000\u3000\u201c\u5f90\u4e66\u8bb0\u4ec0\u4e48\u65f6\u5019\u8ba4\u8bc6\u5f20\u606a\u7684\uff1f\u201d\u6881\u4f1f\u6cd5\u5750\u56de\u6c99\u53d1\u4e0e\u5f90\u5b66\u5e73\u95f2\u804a\u3002</p><p>\u3000\u3000\u201c\u56db\u5e74\u591a\u4e86\uff0c\u201d\u5f90\u5b66\u5e73\u56de\u5fc6\u9053\uff0c\u201c\u8fd9\u5c0f\u5b50\u4e0b\u4e00\u624b\u597d\u68cb\uff0c\u6211\u5012\u662f\u597d\u4e45\u6ca1\u6709\u4e0b\u68cb\u7684\u95f2\u5de5\u592b\u4e86\uff0c\u53ea\u6015\u66f4\u4e0b\u4ed6\u4e0d\u8fc7\u3002\u201d\u89c1\u5f20\u606a\u4e0e\u5f20\u77e5\u975e\u8fdb\u6765\uff0c\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u77e5\u9053\u4eca\u5929\u8fc7\u6765\u8981\u8c08\u4ec0\u4e48\u5427\uff1f\u6211\u8ddf\u6881\u7701\u957f\u90fd\u6709\u4e9b\u7d2f\u4e86\uff0c\u4f60\u4eec\u5750\u4e0b\u6765\u8c08\u8bdd\u5c31\u4e0d\u4f1a\u5148\u5356\u5173\u5b50\u4e86\u3002\u201d</p><p>\u3000\u3000\u201c\u90a3\u6211\u5c31\u7b80\u77ed\u8ddf\u5f90\u4f2f\u4f2f\u3001\u6881\u7701\u957f\u6c47\u62a5\u4e00\u4e0b\uff0c\u9526\u6e56\u7ecf\u6d4e\u7814\u7a76\u4e2d\u5fc3\u5173\u4e8e\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5bf9\u5c0f\u6c5f\u6d41\u57df\u533a\u57df\u7ecf\u6d4e\u4fc3\u8fdb\u5f71\u54cd\u7684\u62a5\u544a\u7ed3\u8bba\uff0c\u674e\u8fdc\u6e56\u7701\u957f\u3001\u7f57\u541b\u4e66\u8bb0\u3001\u8d75\u9633\u4e66\u8bb0\u662f\u57fa\u672c\u8ba4\u540c\u7684\uff0c\u201d\u5f20\u606a\u563f\u7136\u4e00\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u7b80\u77ed\u6c47\u62a5\u7ed3\u675f\uff0c\u6211\u8ddf\u5c0f\u53d4\u8fd9\u5c31\u56de\u5bbe\u9986\u4f11\u606f\u4e86\uff0c\u4e5f\u4e0d\u6253\u6270\u5f90\u4f2f\u4f2f\u8ddf\u6881\u7701\u957f\u4f11\u606f\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u6ca1\u4e2a\u6b63\u5f62\uff0c\u6881\u7701\u957f\u5728\u8fd9\u91cc\u4f60\u4e5f\u80e1\u4e71\u8bf4\u8bdd\uff1f\u201d\u5468\u6dd1\u60e0\u7aef\u8336\u5230\u5ba2\u5385\u6765\uff0c\u7b11\u7740\u9a82\u5f20\u606a\uff0c\u5f20\u77e5\u975e\u5fd9\u63a5\u8fc7\u8336\u5177\u3002</p><p>\u3000\u3000\u201c\u6211\u4eca\u5929\u4e0d\u5c06\u8bdd\u8bf4\u900f\u4e86\uff0c\u5f90\u4f2f\u4f2f\u54ea\u91cc\u80fd\u7761\u7740\u554a\uff1f\u201d\u5f20\u606a\u7b11\u7740\u63a5\u8fc7\u8336\u676f\uff0c\u4e0e\u5c0f\u53d4\u5750\u4e0b\u6765\u3002</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u7b11\u4e86\u7b11\uff0c\u6ca1\u6709\u8bf4\u8bdd\uff0c\u4ed6\u4e0e\u5f20\u606a\u63a5\u89e6\u4e5f\u591a\u4e86\uff0c\u4f46\u662f\u77e5\u9053\u4ed6\u4e0e\u5f20\u606a\u7684\u5173\u7cfb\u8fdc\u4e0d\u80fd\u8ddf\u5f90\u5b66\u5e73\u76f8\u6bd4\uff0c\u770b\u4ed6\u4eec\u5012\u662f\u6709\u79cd\u5bb6\u4eba\u822c\u7684\u4eb2\u5bc6\u65e0\u95f4\u3002</p><p>\u3000\u3000\u201c\u6cb9\u8154\u6ed1\u8c03\u7684\uff0c\u201d\u5f90\u5b66\u5e73\u62ff\u8d77\u77ee\u51e0\u4e0a\u7684\u8336\u676f\u559d\u4e86\u4e24\u53e3\u8336\uff0c\u201c\u90a3\u7bc7\u62a5\u544a\u7684\u7ed3\u8bba\u662f\u8c01\u90fd\u6ca1\u6cd5\u5426\u8ba4\u7684\uff0c\u8bf4\u4e9b\u6211\u4eec\u60f3\u77e5\u9053\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8bf4\u51fa\u6765\u4e5f\u5f88\u7b80\u5355\uff0c\u6982\u62ec\u8d77\u6765\u516b\u4e2a\u5b57\uff1a\u2018\u5171\u540c\u53c2\u4e0e\u3001\u5229\u76ca\u5747\u6cbe\u2019\uff0c\u8fd9\u5c31\u662f\u4e1c\u6d77\u7684\u57fa\u672c\u6001\u5ea6\u2026\u2026\u201d\u5f20\u606a\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u5229\u76ca\u5747\u6cbe\u90a3\u662f\u80af\u5b9a\u7684\uff0c\u201d\u6881\u4f1f\u6cd5\u7b11\u7740\u8bf4\uff0c\u201c\u6211\u4eec\u66f4\u671f\u5f85\u5171\u540c\u53c2\u4e0e\u2026\u2026\u201d</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u7edd\u975e\u7b79\u63aa\u8db3\u8d44\u91d1\u5c31\u80fd\u7acb\u5373\u4e0a\u9a6c\u7684\uff0c\u5c31\u7b97\u8d75\u6d4e\u4e1c\u3001\u66fe\u5982\u5723\u4e24\u4f4d\u603b\u7406\u652f\u6301\uff0c\u4e5f\u4e0d\u80fd\u6d88\u9664\u6240\u6709\u7684\u963b\u529b\u2014\u2014\u8d75\u6d4e\u4e1c\u6027\u683c\u5f3a\u52bf\uff0c\u4f46\u662f\u4ed6\u5728\u603b\u7406\u4f4d\u5b50\u4e0a\u505a\u4e0d\u6210\u7684\u4e8b\u60c5\u4e5f\u6709\u4e00\u5927\u5806\u3002\u9996\u5148\u5168\u90e8\u52a8\u7528\u6d77\u5916\u8d44\u672c\u5efa\u8bbe\u8d85\u5927\u578b\u6df1\u6c34\u6d77\u6e2f\uff0c\u56fd\u5185\u8fd8\u65e0\u5148\u4f8b\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e4b\u540e\uff0c\u4f1a\u5bf9\u5357\u8fb9\u6d59\u4e1c\u6cbf\u6d77\u8bf8\u6e2f\u5f62\u6210\u7ade\u4e89\uff0c\u5efa\u6e2f\u4e4b\u540e\uff0c\u53d1\u5c55\u6e2f\u53e3\u4ea7\u4e1a\u96c6\u7fa4\uff0c\u4f1a\u5bf9\u6d59\u6c5f\u7684\u6e2f\u53e3\u4ea7\u4e1a\u96c6\u7fa4\u5f62\u6210\u7ade\u4e89\u3002</p><p>\u3000\u3000\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u9996\u5148\u8981\u53d6\u5f97\u4e1c\u6d77\u7701\u7684\u652f\u6301\uff0c\u4e1c\u6d77\u7701\u6b64\u65f6\u5728\u56fd\u5185\u7684\u5730\u4f4d\u8981\u9ad8\u8fc7\u6c5f\u5357\u7701\uff0c\u8ba9\u4e1c\u6d77\u7701\u53c2\u4e0e\u8fdb\u6765\uff0c\u80fd\u6781\u5927\u7684\u5316\u89e3\u5efa\u6e2f\u7684\u963b\u529b\uff0c\u53e6\u4e00\u65b9\u9762\uff0c\u4e1c\u6d77\u7701\u8d22\u653f\u5bbd\u88d5\uff0c\u4e1c\u6d77\u7701\u80fd\u62ff\u51fa\u4e00\u90e8\u5206\u8d44\u91d1\u51fa\u6765\u6295\u5165\u5efa\u6e2f\uff0c\u4e5f\u5c31\u65e0\u6cd5\u5168\u90e8\u5f15\u8fdb\u6d77\u5916\u8d44\u672c\u2014\u2014\u4e1c\u6d77\u7701\u8d22\u653f\u5bbd\u88d5\u4e5f\u5f90\u5b66\u5e73\u5728\u4e1c\u6d77\u62c5\u4efb\u7701\u957f\u65f6\u6253\u4e0b\u7684\u597d\u5e95\u5b50\u3002</p><p>\u3000\u3000\u201c\u518d\u5177\u4f53\u8bf4\u4e00\u8bf4\uff0c\u201d\u5f90\u5b66\u5e73\u5bf9\u4e1c\u6d77\u7701\u8fd8\u662f\u6709\u611f\u60c5\u7684\uff0c\u5f53\u7136\u4e0d\u4f1a\u4ecb\u610f\u4e1c\u6d77\u7701\u6765\u5206\u6cbe\u5229\u76ca\uff0c\u201c\u5229\u76ca\u662f\u600e\u4e48\u6837\u4e00\u4e2a\u5206\u6cbe\u6cd5\uff1f\u201d\u4ed6\u6253\u7b97\u8ba9\u8fd9\u6b21\u8c08\u8bdd\u6881\u4f1f\u6cd5\u5531\u4e3b\u89d2\uff0c\u4e0d\u8fc7\u8981\u4ece\u5f20\u606a\u5634\u91cc\u638f\u8bdd\uff0c\u8fd8\u5f97\u4ed6\u4eb2\u81ea\u6765\u3002</p><p>\u3000\u3000\u201c\u5171\u540c\u6295\u8d44\u5efa\u6e2f\u8fd0\u8425\uff0c\u8fd9\u662f\u76f4\u63a5\u7684\u5171\u540c\u53c2\u4e0e\u3001\u5229\u76ca\u5747\u6cbe\uff1b\u4e1c\u5c71\u5c9b\u4e0e\u4e1c\u6d77\u94c1\u8def\u7ebf\u3001\u9ad8\u901f\u516c\u8def\u7ebf\u3001\u6c5f\u6d77\u822a\u7ebf\u7684\u8d2f\u901a\u4e5f\u662f\u5fc5\u9700\u4f18\u5148\u8003\u8651\u7684\uff0c\u6211\u60f3\u8fd9\u4e9b\uff0c\u8fd9\u8fb9\u4e5f\u662f\u6ca1\u6709\u7591\u95ee\u7684\uff0c\u201d\u5f20\u606a\u8bf4\u9053\uff0c\u201c\u8bf4\u5230\u91cd\u70b9\uff0c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u540e\u672a\u6765\u6700\u4e3b\u8981\u7684\u3001\u4e5f\u662f\u6700\u6838\u5fc3\u7684\u5229\u76ca\u8fd8\u662f\u4e34\u6e2f\u4ea7\u4e1a\u96c6\u7fa4\u7684\u53d1\u5c55\u95ee\u9898\uff0c\u6bd4\u5982\u8bf4\u6e2f\u53e3\u91cd\u5de5\u4e1a\u3001\u6bd4\u5982\u8bf4\u6e2f\u53e3\u9020\u4fee\u8239\u5de5\u4e1a\uff0c\u4e1c\u6d77\u7701\u5e0c\u671b\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u4ee5\u53ca\u6d0b\u6d66\u8239\u4e1a\u96c6\u56e2\u80fd\u6536\u8d2d\u65b0\u4ead\u5e02\u7684\u4e1c\u5c71\u94a2\u94c1\u516c\u53f8\u3001\u4e1c\u5c71\u9020\u8239\u5382\u2026\u2026\u6881\u7701\u957f\u5bf9\u4e1c\u5c71\u94a2\u94c1\u4e0e\u4e1c\u5c71\u9020\u8239\u5382\u4e86\u4e0d\u4e86\u89e3\uff1f\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u70b9\u70b9\u5934\uff0c\u8bf4\u9053\uff1a\u201c\u4e1c\u5c71\u94a2\u94c1\u4e0e\u4e1c\u5c71\u8239\u52a1\u90fd\u662f\u65b0\u4ead\u5e02\u5c5e\u7684\u56fd\u6709\u4f01\u4e1a\uff0c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u6536\u8d2d\u4e1c\u5c71\u94a2\u94c1\u3001\u6d0b\u6d66\u8239\u4e1a\u6536\u8d2d\u4e1c\u5c71\u9020\u8239\u5382\u4e4b\u540e\uff0c\u5c31\u80fd\u4fdd\u8bc1\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u4e0e\u6d0b\u6d66\u8239\u4e1a\u5728\u65b0\u4ead\u7684\u4f18\u5148\u53d1\u5c55\uff0c\u800c\u94a2\u94c1\u4e0e\u9020\u8239\u4e24\u4e2a\u884c\u4e1a\u5bf9\u6df1\u6c34\u6e2f\u8d44\u6e90\u7684\u4f9d\u8d56\u6027\u6781\u5f3a\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u662f\u554a\uff0c\u201d\u5f20\u606a\u70b9\u70b9\u5934\uff0c\u201c\u4e1c\u6d77\u8054\u5408\u94a2\u94c1\u96c6\u56e2\u672a\u6765\u7684\u53d1\u5c55\u5c06\u4e25\u91cd\u4f9d\u8d56\u6d77\u5916\u94c1\u77ff\u77f3\uff0c\u5382\u5740\u8fc1\u5230\u6df1\u6c34\u6d77\u6e2f\u9644\u8fd1\uff0c\u6bcf\u5428\u94a2\u7684\u8fd0\u8f93\u6210\u672c\u81f3\u5c11\u80fd\u4e0b\u964d\u4e24\u4e09\u767e\u5143\uff0c\u4e1c\u8054\u94a2\u94c1\u6269\u4ea7\u540e\u7684\u5e74\u4ea7\u80fd\u662f\u4e09\u767e\u4e07\u5428\uff0c\u4ec5\u8fd0\u8f93\u6210\u672c\u4e0b\u964d\u5c31\u6709\u4e03\u516b\u4ebf\uff0c\u800c\u4e1c\u8054\u94a2\u94c1\u4e5d\u4e03\u5e74\u5229\u6da6\u4e5f\u4e0d\u8fc7\u4e03\u516b\u4ebf\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u597d\u4e8b\uff0c\u201d\u6881\u4f1f\u6cd5\u8003\u8651\u4e86\u4e00\u4e0b\uff0c\u201c\u4e1c\u8054\u94a2\u94c1\u3001\u6d0b\u6d66\u8239\u4e1a\u5728\u6536\u8d2d\u4e1c\u5c71\u94a2\u94c1\u4e0e\u4e1c\u5c71\u9020\u8239\u5382\u4e4b\u540e\uff0c\u4f1a\u5c06\u53d1\u5c55\u7684\u91cd\u70b9\u653e\u5728\u65b0\u4ead\uff0c\u6211\u4eec\u53c8\u6709\u4ec0\u4e48\u597d\u62d2\u7edd\u7684\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e5f\u662f\uff0c\u201d\u5f20\u606a\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff0c\u201c\u8bf4\u8981\u5171\u540c\u53c2\u4e0e\uff0c\u5229\u76ca\u5747\u6cbe\uff0c\u9526\u6e56\u5546\u4e8b\u6253\u7b97\u5728\u4e1c\u8054\u94a2\u94c1\u6536\u8d2d\u4e1c\u5c71\u94a2\u94c1\u4e4b\u524d\u5bf9\u4e1c\u5c71\u94a2\u94c1\u8fdb\u884c\u6ce8\u8d44\uff0c\u5357\u6d0b\u6d77\u8fd0\u6253\u7b97\u5728\u6d0b\u6d66\u8239\u4e1a\u6536\u8d2d\u4e1c\u5c71\u9020\u8239\u5382\u4e4b\u524d\u5bf9\u4e1c\u5c71\u9020\u8239\u5382\u8fdb\u884c\u6ce8\u8d44\u2026\u2026\u201d</p><p>\u3000\u3000\u6881\u4f1f\u6cd5\u6709\u4e9b\u60ca\u8be7\uff0c\u5f90\u5b66\u5e73\u5728\u65c1\u8fb9\u6447\u5934\u7b11\u4e86\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u4f60\u8bf4\u4e86\u534a\u5929\u7684\u5171\u540c\u53c2\u4e0e\u3001\u5229\u76ca\u5747\u6cbe\uff0c\u8bf4\u5230\u5e95\u8fd8\u662f\u5728\u8003\u8651\u9526\u6e56\u600e\u6837\u53bb\u5206\u6cbe\u5229\u76ca\u5427\uff1f\u201d</p><p>\u3000\u3000\u201c\u8fd9\u4e2a\u662f\u5f53\u7136\uff0c\u603b\u4e0d\u80fd\u8d39\u5927\u534a\u5929\u7684\u529b\u6c14\uff0c\u4e00\u70b9\u597d\u5904\u90fd\u4eab\u53d7\u4e0d\u5230\uff0c\u9526\u6e56\u6bd5\u7adf\u662f\u4ee5\u76c8\u5229\u4e3a\u76ee\u7684\u7684\u3002\u201d\u5f20\u606a\u8146\u7740\u8138\u5bf9\u5f90\u5b66\u5e73\u7b11\u7740\u8bf4\u9053\u3002</p></article>"}}}]);
