(window.webpackJsonp=window.webpackJsonp||[]).push([[1099],{1122:function(p){p.exports={chapterTitle:null,title:"\u7b2c1096\u7ae0 \u6de1\u9a6c\u9521\u7684\u91ce\u5fc3",content:"<article><h2>\u7b2c1096\u7ae0 \u6de1\u9a6c\u9521\u7684\u91ce\u5fc3</h2><p>\u3000\u3000\u90ed\u677e\u5ef6\u6e05\u6668\u4ece\u5409\u9686\u5761\u4e58\u79c1\u4eba\u98de\u673a\u8d76\u5230\u65b0\u52a0\u5761\u966a\u540c\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u4e00\u8d77\u7528\u65e9\u9910\u3002</p><p>\u3000\u3000\u4ece\u5409\u9686\u5761\u5230\u65b0\u52a0\u5761\u5750\u8f66\u4e5f\u4e0d\u8fc7\u4e09\u56db\u4e2a\u5c0f\u65f6\u7684\u8f66\u7a0b\uff0c\u5bf9\u62e5\u6709\u79c1\u4eba\u98de\u673a\u7684\u90ed\u677e\u5ef6\u6765\u8bf4\uff0c\u8d76\u8fc7\u6765\u966a\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u4e00\u8d77\u7528\u65e9\u9910\uff0c\u5c31\u8ddf\u79bb\u5f00\u5bb6\u5230\u516c\u53f8\u529e\u516c\u4e00\u6837\u65b9\u4fbf\u3002</p><p>\u3000\u3000\u5b59\u9759\u6aac\u4e0d\u60f3\u53c2\u52a0\u4efb\u4f55\u7684\u5546\u52a1\u6d3b\u52a8\uff0c\u65e9\u65e9\u7684\u5c31\u79bb\u5f00\u9152\u5e97\u53bb\u5b66\u6821\u4e86\u3002</p><p>\u3000\u3000\u5750\u5728\u6d6e\u5c14\u987f\u9152\u5e97\u9876\u5c42\u7684\u8c6a\u534e\u9910\u5385\uff0c\u53ef\u4ee5\u770b\u89c1\u6d78\u67d3\u5728\u521d\u5347\u671d\u9633\u5149\u8f89\u4e4b\u4e2d\u7684\u65b0\u52a0\u5761\u6cb3\uff0c\u5f39\u4e38\u4e4b\u56fd\u7684\u7e41\u534e\u9648\u8c22\uff0c\u4f3c\u4e4e\u6c47\u805a\u5728\u8fd9\u95ea\u7740\u7cbc\u7cbc\u6ce2\u5149\u7684\u6cb3\u6d41\u4e4b\u4e2d\u3002</p><p>\u3000\u3000\u201c\u65b0\u52a0\u5761\u5168\u529b\u53d1\u5c55\u65c5\u6e38\u4e1a\uff0c\u5230\u5904\u90fd\u662f\u5404\u5f0f\u5404\u6837\u7684\u9152\u5e97\u548c\u65c5\u9986\uff0c\u4e0d\u8fc7\u8981\u8bf4\u5efa\u7b51\u4e4b\u7f8e\uff0c\u6574\u4e2a\u65b0\u52a0\u5761\uff0c\u5927\u6982\u53ea\u6709\u653f\u5e9c\u5927\u697c\u4e0e\u9ad8\u7b49\u6cd5\u9662\u624d\u80fd\u4e0e\u6d6e\u5c14\u987f\u9152\u5e97\u5ab2\u7f8e\u3002\u8fd9\u91cc\u66fe\u662f\u65b0\u52a0\u5761\u90ae\u653f\u603b\u5c40\uff0c\u5728\u4e16\u7eaa\u521d\u53f6\uff0c\u65b0\u52a0\u5761\u9c7c\u9f99\u6df7\u6742\u7684\u5404\u8272\u4eba\u7b49\u5c31\u5728\u8fd9\u5ea7\u5927\u697c\u91cc\u5bc4\u9001\u4e0e\u9886\u53d6\u5404\u81ea\u7684\u4e61\u6101\u4e0e\u613f\u671b\u2026\u2026\u201d\u90ed\u677e\u5ef6\u7ed9\u5f20\u606a\u4ecb\u7ecd\u8d77\u4ed6\u6240\u4e0b\u69bb\u7684\u8fd9\u5ea7\u5386\u53f2\u60a0\u4e45\u7684\u9152\u5e97\u5efa\u7b51\u3002</p><p>\u3000\u3000\u6628\u5929\u591c\u91cc\u5728\u9759\u6aac\u516c\u5bd3\u91cc\u53ae\u6df7\u4e86\u8bb8\u4e45\uff0c\u5230\u673a\u573a\u63a5\u5b59\u5c1a\u4e49\u56de\u9152\u5e97\u90fd\u5df2\u7ecf\u8fc7\u5341\u4e00\u70b9\u4e86\uff0c\u5f20\u606a\u8fd8\u6ca1\u6709\u65f6\u95f4\u597d\u597d\u9886\u7565\u6d6e\u5c14\u987f\u9152\u5e97\u7684\u53e4\u5178\u5efa\u7b51\u4e4b\u7f8e\uff0c\u6d6e\u5c14\u987f\u9152\u5e97\u662f\u65b0\u52a0\u5761\u7684\u6807\u5fd7\u6027\u9152\u5e97\uff0c\u65b0\u52a0\u5761\u65b9\u9762\u5c06\u4ed6\u4eec\u5b89\u6392\u4f4f\u8fdb\u8fd9\u91cc\uff0c\u81ea\u7136\u4e5f\u662f\u5341\u5206\u7528\u5fc3\u7684\u3002</p><p>\u3000\u3000\u201c\u6211\u521a\u4e0b\u98de\u673a\u65f6\uff0c\u63a5\u5230\u65b0\u52a0\u5761\u65b9\u9762\u7684\u7535\u8bdd\uff0c\u6de1\u9a6c\u9521\u9ad8\u7ea7\u8463\u4e8b\u674e\u663e\u6210\u5148\u751f\u4f1a\u62bd\u51fa\u65f6\u95f4\u6765\u4e13\u7a0b\u966a\u540c\u6211\u4eec\u53c2\u89c2\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u2026\u2026\u201d\u90ed\u677e\u5ef6\u8bf4\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u58f0\uff0c\u4e5f\u6ca1\u6709\u95ee\u65b0\u52a0\u5761\u65b9\u9762\u4e3a\u4ec0\u4e48\u7a81\u7136\u63d0\u9ad8\u63a5\u5f85\u89c4\u683c\uff0c\u5b59\u5c1a\u4e49\u770b\u8fc7\u6765\u7684\u773c\u795e\u5df2\u6709\u4e9b\u7591\u60d1\u4e86\u3002</p><p>\u3000\u3000\u4e4b\u524d\u7684\u5b89\u6392\u4e2d\u53ea\u662f\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u4e00\u540d\u9ad8\u7ea7\u526f\u603b\u88c1\u5168\u7a0b\u966a\u540c\uff0c\u674e\u663e\u6210\u662f\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u7684\u6838\u5fc3\u6210\u5458\uff0c\u662f\u65b0\u52a0\u5761\u8d22\u653f\u90e8\u9ad8\u7ea7\u5b98\u5458\uff0c\u662f\u6de1\u9a6c\u9521\u6295\u8d44\u96c6\u56e2\u7684\u51b3\u7b56\u8005\u4e4b\u4e00\uff0c\u867d\u7136\u8bf4\u9526\u6e56\u5728\u5185\u5730\u5df2\u7ecf\u624e\u4e0b\u76f8\u5f53\u6df1\u539a\u7684\u6839\u57fa\uff0c\u4f46\u662f\u5728\u4e9a\u6d32\u5927\u578b\u8d22\u56e2\u91cc\uff0c\u9526\u6e56\u8fd8\u53ea\u80fd\u7b97\u662f\u4e09\u6d41\u7684\u5c0f\u89d2\u8272\uff0c\u5b89\u6392\u674e\u663e\u6210\u5168\u7a0b\u966a\u540c\uff0c\u591a\u5c11\u6709\u4e9b\u8ba9\u4eba\u89c9\u5f97\u610f\u5916\u3002</p><p>\u3000\u3000\u5f20\u606a\u5e76\u6ca1\u6709\u56e0\u6b64\u89c9\u5f97\u7279\u522b\u7684\u8363\u8000\uff0c\u5fae\u8e59\u7740\u7709\u5934\uff0c\u4ed6\u5b81\u53ef\u5728\u65b0\u52a0\u5761\u63a5\u53d7\u6b63\u5e38\u89c4\u683c\u7684\u63a5\u5f85\uff0c\u4e0d\u7528\u5934\u75bc\u53bb\u8003\u8651\u674e\u663e\u6210\u51fa\u9762\u63a5\u5f85\u7684\u539f\u56e0\u3002</p><p>\u3000\u3000\u65e9\u9910\u9014\u4e2d\uff0c\u90ed\u677e\u5ef6\u63a5\u5230\u4e00\u4e2a\u7535\u8bdd\uff0c\u653e\u4e0b\u7535\u8bdd\uff0c\u8bf4\u9053\uff1a\u201c\u674e\u663e\u6210\u5148\u751f\u6bd4\u6211\u9884\u6599\u8fd8\u8981\u65e9\u7684\u8d76\u5230\u9152\u5e97\u6765\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u5509\u3002\u201d\u5f20\u606a\u8f7b\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u89c9\u5f97\u8fde\u5403\u987f\u65e9\u9910\u90fd\u4e0d\u5f97\u5b9a\u795e\uff0c\u8fd9\u6837\u7684\u9ad8\u89c4\u683c\u63a5\u5f85\u8fd8\u662f\u5c11\u6765\u4e3a\u5999\uff0c\u5fc3\u91cc\u8fd9\u4e48\u60f3\u7740\uff0c\u5374\u4e0d\u5f97\u4e0d\u4e0e\u5b59\u5c1a\u4e49\u7b49\u4eba\u4e22\u4e0b\u624b\u91cc\u7684\u9910\u5177\uff0c\u8ddf\u7740\u90ed\u677e\u5ef6\u8d70\u51fa\u9910\u5385\u76f8\u8fce\u4ee5\u793a\u5c0a\u91cd\u3002</p><p>\u3000\u3000\u674e\u663e\u6210\u56db\u5341\u5c81\u5de6\u53f3\uff0c\u4e2d\u7b49\u8eab\u6750\u3001\u8138\u524a\u7626\uff0c\u524d\u989d\u7684\u5934\u53d1\u7a00\u758f\uff0c\u4f5c\u4e3a\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u7684\u6838\u5fc3\u6210\u5458\uff0c\u4ed6\u7684\u76f8\u8c8c\u4e0e\u88ab\u65b0\u52a0\u5761\u6c11\u4f17\u89c6\u4e3a\u56fd\u7236\u4e4b\u4eba\u6709\u51e0\u5206\u76f8\u8096\uff0c\u5bf9\u65b0\u52a0\u5761\u8eab\u5c45\u9ad8\u4f4d\uff0c\u76f8\u5bf9\u4e5f\u5e74\u8f7b\u4e00\u4e9b\u3002\u5f20\u606a\u4e0e\u4ed6\u63e1\u4e86\u63e1\u624b\uff0c\u542c\u90ed\u677e\u5ef6\u5e2e\u4ed6\u4eec\u4ecb\u7ecd\u3002\u5f20\u606a\u5fc3\u91cc\u6b63\u7422\u78e8\u7740\u600e\u4e48\u5bd2\u6684\u5408\u9002\uff0c\u674e\u663e\u6210\u5374\u8bf4\u9053\uff1a\u201c\u4e00\u76f4\u90fd\u60f3\u4eb2\u773c\u770b\u4e00\u770b\u7f14\u9020\u9526\u6e56\u7684\u82f1\u96c4\uff0c\u6628\u5929\u591c\u91cc\u624d\u8d76\u56de\u65b0\u52a0\u5761\uff0c\u6ca1\u6709\u6562\u8d38\u7136\u6253\u7535\u8bdd\u6765\uff0c\u5f97\u77e5\u9053\u90ed\u5148\u751f\u4e5f\u8981\u5230\u65b0\u52a0\u5761\u6765\uff0c\u4e5f\u8bf7\u6258\u4ed6\u5e2e\u6211\u5f15\u8350\uff0c\u5e0c\u671b\u4e0d\u4f1a\u592a\u5510\u7a81\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54ea\u91cc\uff1f\u201d\u5f20\u606a\u7b11\u7740\u8bf4\u9053\uff0c\u201c\u8be5\u662f\u6211\u767b\u95e8\u62dc\u8bbf\u624d\u662f\u2026\u2026\u201d</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u4e0e\u674e\u6c0f\u5bb6\u65cf\u674e\u663e\u6210\u8fd9\u4ee3\u4eba\u7684\u5173\u7cfb\u6bd4\u8f83\u4eb2\u5bc6\uff0c\u5c31\u9080\u8bf7\u674e\u663e\u6210\u4e0e\u4ed6\u4eec\u4e00\u8d77\u7528\u65e9\u9910\u3002</p><p>\u3000\u3000\u867d\u7136\u534e\u5546\u5bcc\u8c6a\u699c\u51e0\u4e4e\u770b\u4e0d\u5230\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u6210\u5458\u7684\u540d\u5b57\uff0c\u4f46\u662f\u8fd8\u6ca1\u6709\u54ea\u4e2a\u534e\u5546\u5bb6\u65cf\u80fd\u7ad9\u51fa\u6765\u5ba3\u79f0\u6709\u7740\u6bd4\u674e\u6c0f\u5bb6\u65cf\u5728\u534e\u4eba\u5708\u6709\u7740\u66f4\u5927\u7684\u5f71\u54cd\u529b\u3001\u66f4\u5f3a\u7684\u7ecf\u6d4e\u5b9e\u529b\u3002</p><p>\u3000\u3000\u65b0\u52a0\u5761\u6b64\u65f6\u4e0e\u5185\u5730\u7684\u5173\u7cfb\u8c8c\u4f3c\u6709\u7740\u871c\u6708\u671f\u7684\u751c\u871c\uff0c\u7ecf\u6d4e\u4e0a\u5408\u4f5c\u4e5f\u5f02\u5e38\u7684\u5bc6\u5207\uff0c\u5b9e\u8d28\u4e0a\u5374\u662f\u8c8c\u5408\u795e\u79bb\u7684\uff0c\u8fd9\u4e0e\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u7684\u653f\u6cbb\u91ce\u5fc3\u6709\u5f88\u5927\u5173\u7cfb\u3002\u5185\u5730\u7684\u7ecf\u6d4e\u5d1b\u8d77\u4e4b\u540e\uff0c\u5927\u56fd\u4e4b\u52bf\u6e10\u6210\uff0c\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u5728\u534e\u4eba\u5708\u5185\u7684\u653f\u6cbb\u5730\u4f4d\u8d8a\u6765\u8d8a\u7ed9\u8fb9\u7f18\u5316\uff0c\u96be\u514d\u5c31\u6709\u5f88\u5f3a\u70c8\u7684\u653f\u6cbb\u4e0a\u7684\u5931\u843d\u611f\uff0c\u518d\u52a0\u4e0a\u65b0\u52a0\u5761\u5728\u5730\u7f18\u4e0a\u8fdc\u79bb\u5927\u9646\uff0c\u5f39\u4e38\u4e4b\u5730\u7ed9\u5939\u5728\u5357\u6d0b\u8bf8\u5c9b\u4e4b\u95f4\uff0c\u653f\u6cbb\u4e0a\u7684\u9009\u62e9\u4f59\u5730\u4e5f\u5f88\u5c11\uff0c\u7b49\u871c\u6708\u671f\u8fc7\u53bb\uff0c\u4e24\u8fb9\u7684\u5173\u7cfb\u5c31\u4f1a\u9010\u6e10\u51b7\u6de1\u4e0b\u6765\u3002</p><p>\u3000\u3000\u9526\u6e56\u6b64\u65f6\u4e0d\u80fd\u5931\u53bb\u65b0\u52a0\u5761\u65b9\u9762\u7684\u652f\u6301\uff0c\u65e0\u8bba\u662f\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u8fd8\u662f\u4e2d\u6676\u5fae\u82af\uff0c\u90fd\u9700\u8981\u65b0\u52a0\u5761\u65b9\u9762\u5728\u8d44\u91d1\u3001\u6280\u672f\u4e0a\u7684\u652f\u6301\uff0c\u4f46\u662f\u8003\u8651\u5230\u5c06\u6765\u4e2d\u65b0\u5173\u7cfb\u53ef\u80fd\u4f1a\u51b7\u6de1\u4e0b\u6765\uff0c\u8fd9\u65f6\u5019\u53c8\u8981\u907f\u514d\u9526\u6e56\u7ed9\u65b0\u52a0\u5761\u65b9\u9762\u6e17\u900f\u592a\u6df1\u3002\u51fa\u4e8e\u8fd9\u79cd\u79cd\u8003\u8651\uff0c\u9664\u4e86\u751f\u610f\u4e0a\u7684\u6b63\u5e38\u5f80\u6765\uff0c\u5f20\u606a\u5e76\u4e0d\u60f3\u4e0e\u65b0\u52a0\u5761\u674e\u6c0f\u5bb6\u65cf\u6210\u5458\u53d1\u5c55\u591a\u5c11\u5bc6\u5207\u7684\u201c\u79c1\u4eba\u5173\u7cfb\u201d\uff0c\u8fd9\u4e5f\u662f\u4ed6\u4f1a\u5076\u5c14\u7ecf\u8fc7\u65b0\u52a0\u5761\u5374\u5f88\u5c11\u6ede\u7559\u7684\u4e3b\u8981\u56e0\u7d20\u3002</p><p>\u3000\u3000\u5750\u5728\u6d6e\u5c14\u987f\u9152\u5e97\u9876\u697c\u7684\u8c6a\u534e\u9910\u5385\u91cc\uff0c\u4f3c\u4e4e\u80fd\u542c\u5230\u65b0\u52a0\u5761\u6cb3\u7684\u6f7a\u6f7a\u6d41\u6c34\u4e4b\u58f0\uff0c\u674e\u663e\u6210\u4f18\u96c5\u7684\u64cd\u6301\u7740\u5200\u53c9\uff0c\u7b11\u7740\u8bf4\uff1a\u201c\u5bf9\u4e8e\u9526\u6e56\u4e0e\u6211\u4eec\u7684\u8fd9\u6b21\u5408\u4f5c\uff0c\u6211\u4eec\u65e9\u524d\u8fd8\u51c6\u5907\u4e86\u4e00\u4e2a\u8865\u5145\u65b9\u6848\uff0c\u53ea\u662f\u8fd9\u65f6\u5019\u770b\u6765\u6709\u4e9b\u4e0d\u5408\u65f6\u5b9c\u4e86\uff0c\u6211\u662f\u5f88\u671f\u5f85\u4ea4\u7ed3\u4f60\u8fd9\u4e2a\u670b\u53cb\u7684\uff0c\u5fc3\u60f3\u7740\u79c1\u4e0b\u91cc\u4ea4\u6d41\u4e00\u4e0b\u4e5f\u8bb8\u65e0\u59a8\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5fc3\u91cc\u60f3\uff1a\u4f60\u65e2\u7136\u77e5\u9053\u4e0d\u5408\u65f6\u5b9c\u5c31\u4e0d\u8981\u8bf4\u51fa\u6765\u4e86\uff0c\u8138\u4e0a\u5374\u5fae\u5fae\u5728\u7b11\uff0c\u8bf4\u9053\uff1a\u201c\u80fd\u5728\u673a\u4f1a\u8ddf\u524d\u8f88\u5b66\u4e60\uff0c\u90a3\u662f\u6211\u7684\u8363\u5e78\u3002\u201d</p><p>\u3000\u3000\u201c\u4e2d\u6676\u5fae\u82af\u8981\u6536\u8d2d\u7279\u8bb8\u4e00\u5bb6\u6676\u5706\u5de5\u5382\uff0c\u8fd8\u8981\u518d\u65b0\u5efa\u4e00\u5bb6\u66f4\u9ad8\u7aef\u7684\u6676\u5706\u5de5\u5382\uff0c\u624d\u80fd\u5b8c\u5728\u4ece0.35\u5fae\u7c73\u52300.18\u5fae\u7c73\u7684\u4ea7\u4e1a\u5e03\u5c40\uff0c\u4f5c\u4e3a\u7b2c\u4e00\u5927\u80a1\u4e1c\uff0c\u9526\u6e56\u81f3\u5c11\u8fd8\u8981\u627f\u62c5\u5341\u56db\u4ebf\u7f8e\u5143\u7684\u51fa\u8d44\u4e49\u52a1\u2026\u2026\u201d\u674e\u663e\u6210\u8bf4\u8bdd\u65f6\uff0c\u4e60\u60ef\u6027\u6478\u4e86\u6478\u5934\u53d1\u7a00\u758f\u7684\u524d\u989d\uff0c\u201c\u6211\u4eec\u5f53\u65f6\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u80fd\u4ece\u6606\u817e\u5728\u7ebf\u4e0e\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0a\u83b7\u5f97\u5982\u6b64\u5de8\u91cf\u7684\u8d44\u91d1\uff0c\u5c31\u60f3\u7740\u9526\u6e56\u4f5c\u4e3a\u8fd1\u5e74\u6765\u6700\u4e3a\u51fa\u8272\u7684\u534e\u4eba\u4f01\u4e1a\uff0c\u4f5c\u4e3a\u534e\u5546\u754c\u7684\u8000\u773c\u65b0\u661f\uff0c\u6de1\u9a6c\u9521\u5b8c\u5168\u4e0d\u5e94\u8be5\u5bf9\u9526\u6e56\u9677\u5165\u4e00\u65f6\u7684\u56f0\u5883\u800c\u8896\u624b\u65c1\u89c2\uff0c\u6240\u4ee5\u6211\u4eec\u4e8b\u5148\u62df\u5b9a\u4e86\u4e00\u4e2a\u80fd\u591f\u7f13\u89e3\u9526\u6e56\u8d44\u91d1\u538b\u529b\u7684\u8865\u5145\u65b9\u6848\u3002\u73b0\u5728\u770b\u6765\uff0c\u8fd9\u662f\u5b8c\u5168\u4e0d\u5408\u65f6\u5b9c\u4e86\uff0c\u9526\u6e56\u5728\u6606\u817e\u5728\u7ebf\u4ee5\u53ca\u5168\u7403\u97f3\u4e50\u5728\u7ebf\u4e0a\u7684\u5546\u4e1a\u8fd0\u4f5c\u771f\u662f\u51fa\u4eba\u610f\u6599\u7684\u7cbe\u5f69\u2026\u2026\u201d</p><p>\u3000\u3000\u6240\u8c13\u7684\u8865\u5145\u65b9\u6848\u5927\u6982\u5c31\u662f\u6de1\u9a6c\u9521\u6295\u8d44\u96c6\u56e2\u5bf9\u9526\u6e56\u7684\u6838\u5fc3\u4f01\u4e1a\u8fdb\u884c\u6ce8\u8d44\u5427\u3001\u7136\u540e\u518d\u7531\u9526\u6e56\u7684\u6838\u5fc3\u4f01\u4e1a\u5b8c\u6210\u5bf9\u4e2d\u6676\u5fae\u82af\u7684\u51fa\u8d44\u4efb\u52a1\uff1f</p><p>\u3000\u3000\u4e0e\u65b0\u52a0\u5761\u653f\u5e9c\u6295\u8d44\u516c\u53f8\u4e00\u8d77\u4f5c\u4e3a\u65b0\u52a0\u5761\u6700\u795e\u79d8\u7684\u4e24\u5bb6\u6295\u8d44\u96c6\u56e2\uff0c\u6de1\u9a6c\u9521\u6781\u5c11\u5411\u5916\u754c\u516c\u5e03\u8d22\u52a1\u8d44\u6599\uff0c\u5f20\u606a\u5927\u6982\u80fd\u77e5\u9053\u6de1\u9a6c\u9521\u6295\u8d44\u96c6\u56e2\u8fd9\u51e0\u5e74\u5bf9\u5185\u5730\u6295\u8d44\u89c4\u6a21\u5927\u7ea6\u5728\u4e00\u5343\u4e94\u767e\u4ebf\u5230\u4e24\u5343\u4ebf\u6e2f\u5143\u4e4b\u95f4\u3002</p><p>\u3000\u3000\u8981\u662f\u80fd\u5bb9\u5fcd\u6de1\u9a6c\u9521\u7684\u6e17\u900f\uff0c\u9526\u6e56\u4ee5\u540e\u7684\u786e\u53ef\u4ee5\u4e0d\u7528\u592a\u62c5\u5fc3\u8d44\u91d1\u7684\u6765\u6e90\u95ee\u9898\u3002</p><p>\u3000\u3000\u5f88\u663e\u7136\uff0c\u90ed\u677e\u5ef6\u90ed\u5bb6\u3001\u9648\u6587\u806a\u9648\u5bb6\u3001\u9a6c\u6587\u6e20\u9a6c\u5bb6\u3001\u4f55\u7167\u594e\u4f55\u5bb6\u7b49\u5bb6\u65cf\u90fd\u662f\u6bd4\u8f83\u7eaf\u7cb9\u7684\u534e\u5546\u7fa4\u4f53\uff0c\u6ca1\u6709\u7279\u522b\u5f3a\u70c8\u7684\u653f\u6cbb\u91ce\u5fc3\uff0c\u4e0e\u4ed6\u4eec\u7ed3\u6210\u5bc6\u5207\u7684\u540c\u76df\u5173\u7cfb\uff0c\u6709\u52a9\u4e8e\u6269\u5927\u9526\u6e56\u5728\u534e\u5546\u5708\u5185\u7684\u5f71\u54cd\u529b\uff0c\u4e0d\u7528\u62c5\u5fc3\u653f\u6cbb\u4e0a\u7684\u98ce\u9669\u3002\u53e6\u5916\uff0c\u90ed\u3001\u9648\u3001\u9a6c\u3001\u4f55\u7b49\u534e\u5546\u5bb6\u65cf\u867d\u7136\u90fd\u662f\u4e00\u65b9\u8c6a\u5bcc\uff0c\u4f46\u662f\u5728\u7ecf\u6d4e\u5b9e\u529b\u4e0a\u4e0d\u62e5\u6709\u538b\u5236\u9526\u6e56\u7684\u4f18\u52bf\uff0c\u5728\u53cc\u5411\u6e17\u900f\u7684\u8fc7\u7a0b\u4e2d\uff0c\u9526\u6e56\u56e0\u6b64\u80fd\u591f\u5360\u636e\u4e3b\u5bfc\u5730\u4f4d\u3002</p><p>\u3000\u3000\u6de1\u9a6c\u9521\u6295\u8d44\u96c6\u56e2\u4ee5\u53ca\u80cc\u540e\u7684\u65b0\u52a0\u5761\u653f\u5e9c\uff0c\u610f\u56fe\u53ea\u6015\u672a\u5fc5\u7eaf\u7cb9\uff1b\u66f4\u4f55\u51b5\uff0c\u6de1\u9a6c\u9521\u6709\u7740\u5c06\u6574\u4e2a\u9526\u6e56\u9cb8\u541e\u8179\u4e2d\u7684\u5b9e\u529b\u3002\u5373\u4f7f\u80fd\u591f\u5c06\u6de1\u9a6c\u9521\u7684\u5f71\u54cd\u9650\u5236\u5728\u6709\u9650\u7684\u8303\u56f4\u4e4b\u5185\uff0c\u8fd9\u79cd\u5355\u5411\u7684\u6e17\u900f\uff0c\u4e5f\u4e0d\u662f\u5f20\u606a\u5e0c\u671b\u770b\u5230\u7684\u5c40\u9762\uff0c\u66f4\u597d\u7684\u9009\u62e9\u5c31\u662f\u5728\u5177\u4f53\u9879\u76ee\u4e0a\u8fdb\u884c\u5408\u4f5c\uff0c\u4e0e\u6de1\u9a6c\u9521\uff0c\u54ea\u6015\u662f\u66f4\u795e\u79d8\u7684\u65b0\u52a0\u5761\u653f\u5e9c\u6295\u8d44\u516c\u53f8\u8fdb\u884c\u5408\u4f5c\uff0c\u90fd\u4e0d\u7528\u6709\u592a\u5927\u7684\u987e\u5fcc\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0d\u52a8\u58f0\u8272\u7684\u671b\u4e86\u5b59\u5c1a\u4e49\u4e00\u773c\uff0c\u5b59\u5c1a\u4e49\u5fc3\u9886\u795e\u4f1a\u7684\u8ddf\u674e\u663e\u6210\u7b11\u7740\u8bf4\uff1a\u201c\u5fae\u8f6f\u7684\u90a3\u4e5d\u4ebf\u7f8e\u5143\uff0c\u65e9\u5c31\u6210\u6211\u4eec\u7684\u9884\u7b97\u4e4b\u4e2d\u4e86\uff0c\u4e0d\u8fc7\u8fd8\u662f\u975e\u5e38\u611f\u8c22\u6de1\u9a6c\u9521\u5982\u6b64\u5468\u5230\u7684\u66ff\u9526\u6e56\u8003\u8651\uff1b\u6709\u674e\u5148\u751f\u8fd9\u756a\u8bdd\uff0c\u9526\u6e56\u8981\u662f\u771f\u9047\u5230\u4ec0\u4e48\u96be\u5904\uff0c\u4e5f\u5c31\u6709\u52c7\u6c14\u5f00\u53e3\u8ddf\u6de1\u9a6c\u9521\u5546\u8bae\u4e86\u3002\u201d</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u9887\u4e3a\u60ca\u8be7\uff1a\u201c\u9526\u6e56\u65e9\u6709\u628a\u63e1\u5fae\u8f6f\u4f1a\u7b54\u5e94\u4f60\u4eec\u6240\u6709\u7684\u6761\u4ef6\uff1f\u201d</p><p>\u3000\u3000\u201c\u4e0d\uff0c\u4e0d\uff0c\u4e0d\uff0c\u201d\u5f20\u606a\u6447\u5934\u7b11\u7740\u8bf4\uff0c\u4ed6\u5f88\u9ad8\u5174\u80fd\u5c06\u8bdd\u9898\u5c94\u5f00\uff0c\u201c\u662f\u6211\u4eec\u4e3a\u4e86\u5f97\u5230\u5fae\u8f6f\u7684\u8fd9\u4e5d\u4ebf\u7f8e\u5143\uff0c\u7b54\u5e94\u4e86\u4ed6\u4eec\u5f88\u591a\u7684\u6761\u4ef6\uff0c\u53ea\u662f\u6709\u4e9b\u4e8b\u60c5\uff0c\u73b0\u5728\u8fd8\u4e0d\u80fd\u4e00\u4e00\u7ec6\u8bf4\uff0c\u771f\u662f\u62b1\u6b49\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u90ed\u677e\u5ef6\u8868\u793a\u7406\u89e3\u3002</p><p>\u3000\u3000\u674e\u663e\u6210\u89c1\u5f20\u606a\u3001\u5b59\u5c1a\u4e49\u8868\u73b0\u7684\u5174\u8da3\u5f88\u6de1\uff0c\u77e5\u9053\u6b64\u65f6\u4e5f\u4e0d\u662f\u63d0\u5411\u9526\u6e56\u6ce8\u8d44\u7684\u597d\u65f6\u673a\uff0c\u5373\u4f7f\u9526\u6e56\u52c9\u5f3a\u7b54\u5e94\u5411\u6de1\u9a6c\u9521\u53d1\u884c\u4e00\u4e9b\u80a1\u4efd\uff0c\u4e5f\u4e0d\u4f1a\u83b7\u5f97\u4ec0\u4e48\u597d\u4ef7\u7801\u3002\u5fc3\u60f3\u9526\u6e56\u7684\u52a8\u4f5c\u5982\u6b64\u4e4b\u5927\uff0c\u603b\u6709\u4f1a\u7ed9\u8d44\u91d1\u5361\u4f4f\u8116\u5b50\u900f\u4e0d\u8fc7\u6c14\u6765\u7684\u65f6\u5019\uff0c\u90a3\u65f6\u624d\u662f\u597d\u65f6\u673a\u3002</p><p>\u3000\u3000\u4e2d\u6676\u5fae\u82af\u65b0\u6d66\u5de5\u5382\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u662f\u4ee5\u661f\u5c55\u94f6\u884c\u7684\u540d\u4e49\u53c2\u4e0e\u6295\u8d44\u7684\uff1b\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u5de5\u7a0b\uff0c\u65b0\u52a0\u5761\u5f53\u5c40\u662f\u4ee5\u65b0\u52a0\u5761\u6e2f\u53e3\u96c6\u56e2\u53c2\u4e0e\u6295\u8d44\u7684\uff1b\u8fd9\u4e24\u5bb6\u4f01\u4e1a\u90fd\u662f\u6de1\u9a6c\u9521\u6295\u8d44\u96c6\u56e2\u65d7\u4e0b\u7684\u6838\u5fc3\u4f01\u4e1a\u3002\u901a\u8fc7\u8fd9\u4e24\u6b21\u7684\u6295\u8d44\u5408\u4f5c\uff0c\u674e\u663e\u6210\u4e5f\u5145\u5206\u8ba4\u8bc6\u5230\u9526\u6e56\u7684\u4f18\u79c0\u4e4b\u5904\u4e0e\u9ad8\u901f\u6210\u957f\u6027\uff1b\u9664\u4e86\u8fd9\u4e24\u70b9\u4e4b\u5916\uff0c\u9526\u6e56\u5728\u534e\u5546\u5708\u5185\u7684\u5f71\u54cd\u529b\u4e5f\u6781\u4e3a\u60f9\u4eba\u77a9\u76ee\u2014\u2014\u8981\u662f\u80fd\u5bf9\u9526\u6e56\u8fdb\u884c\u6e17\u900f\uff0c\u7edd\u5bf9\u5c06\u662f\u6de1\u9a6c\u9521\u8fd1\u5e74\u6765\u5c11\u6709\u7684\u660e\u667a\u6295\u8d44\u4e4b\u4e3e\uff0c\u539f\u4ee5\u4e3a\u9526\u6e56\u8981\u5f3a\u884c\u63a8\u52a8\u4e2d\u6676\u5fae\u82af\u5efa\u4e24\u5ea7\u6676\u5706\u65b0\u5382\u5c06\u662f\u4e00\u4e2a\u8fdb\u5165\u7684\u826f\u673a\uff0c\u6ca1\u6709\u60f3\u5230\u9526\u6e56\u80fd\u4ece\u4e92\u8054\u7f51\u4ea7\u4e1a\u94fe\u4e0a\u5957\u8fd9\u4e48\u591a\u7684\u73b0\u91d1\u51fa\u6765\u3002</p></article>"}}}]);
