(window.webpackJsonp=window.webpackJsonp||[]).push([[110],{132:function(p){p.exports={chapterTitle:null,title:"\u7b2c\u4e8c\u5341\u4e8c\u8282 \u8d70\u706b\u5165\u9b54\uff1f",content:"<article><h2>\u7b2c\u4e8c\u5341\u4e8c\u8282 \u8d70\u706b\u5165\u9b54\uff1f</h2><p>\u88ab\u8fd9\u4e48\u4e00\u6253\u6405\uff0c\u743c\u6069\u6682\u65f6\u5012\u5fd8\u4e86\u8ffd\u95ee\u5170\u68ee\u5fb7\u5c14\u7684\u5723\u6b66\u58eb\u5982\u4f55\u8fdb\u884c\u6700\u7ec8\u8bd5\u70bc\u7684\u95ee\u9898\u3002\u4e0d\u77e5\u600e\u4e48\u641e\u7684\uff0c\u8bdd\u9898\u8f6c\u5230\u4e86\u521a\u624d\u90a3\u4e2a\u5973\u6740\u624b\u8eab\u4e0a\uff0c\u5f53\u7136\uff0c\u53cd\u6b63\u4e5f\u53ea\u662f\u5728\u95f2\u804a\u3002</p><p>\u65e0\u8bba\u662f\u743c\u6069\uff0c\u8fd8\u662f\u6885\u83f2\u65af\uff0c\u90fd\u731c\u4e0d\u51fa\u5973\u6740\u624b\u7684\u6765\u5386\u5982\u4f55\uff0c\u4e5f\u4e0d\u77e5\u9053\u5979\u5230\u5e95\u60f3\u5e72\u4ec0\u4e48\uff0c\u4e0d\u8fc7\uff0c\u5f53\u743c\u6069\u63d0\u5230\u201c\u5979\u4e3a\u4ec0\u4e48\u4e0d\u76f4\u63a5\u628a\u90a3\u4e2a\u5c11\u5e74\u6740\u6b7b\u201d\u7684\u65f6\u5019\uff0c\u6885\u83f2\u65af\u7709\u6bdb\u5fae\u5fae\u4e00\u626c\uff0c\u82e5\u6709\u6240\u609f\u3002</p><p>\u201c\u96be\u9053\u8bf4\u2026\u2026\u201d\u5979\u4f4e\u58f0\u81ea\u8bed\uff0c\u4eff\u4f5b\u60f3\u5230\u4e86\u4ec0\u4e48\uff0c\u4f46\u968f\u5373\u53c8\u6447\u6447\u5934\uff0c\u201c\u4e0d\u5bf9\uff0c\u4e0d\u5bf9\uff0c\u5979\u6ca1\u90a3\u79cd\u611f\u89c9\u3002\u201d</p><p>\u201c\u4ec0\u4e48\u611f\u89c9\uff1f\u201d\u743c\u6069\u83ab\u540d\u5176\u5999\u3002</p><p>\u6885\u83f2\u65af\u6447\u5934\u4e0d\u7b54\uff0c\u743c\u6069\u4e5f\u4e0d\u597d\u8ffd\u95ee\uff0c\u4f55\u51b5\u4ed6\u539f\u672c\u4e5f\u4e0d\u591a\u4e48\u5173\u5fc3\u3002\u5973\u6740\u624b\u5df2\u7ecf\u88ab\u5c01\u5728\u5730\u4e0b\uff0c\u96be\u9053\u5979\u8fd8\u80fd\u94bb\u5730\u9003\u51fa\u6765\u4e0d\u6210\uff0c\u66f4\u522b\u63d0\u4e0b\u9762\u8fd8\u6709\u4e00\u53ea\u813e\u6c14\u6076\u52a3\u7684\u94f6\u9f99\u5e7d\u7075\u3002\u65e2\u7136\u90a3\u4e2a\u201c\u5377\u518c\u5b88\u62a4\u8005\u201d\u4e4c\u5c14\u5170\u7279\u90fd\u8fd9\u4e48\u80f8\u6709\u6210\u7af9\u7684\u6837\u5b50\uff0c\u743c\u6069\u81ea\u7136\u4e5f\u4e0d\u5fc5\u591a\u4e48\u62c5\u5fc3\u3002</p><p>\u53cd\u6b63\u5df2\u7ecf\u5dee\u4e0d\u591a\u662f\u4e2a\u6b7b\u4eba\u4e86\u3002</p><p>\u5c11\u5973\u72b9\u81ea\u5728\u6c89\u601d\u7740\uff0c\u743c\u6069\u5077\u5077\u4eb2\u543b\u7740\u5979\u7684\u8138\u988a\uff0c\u8f7b\u556e\u7740\u5706\u6da6\u5982\u73e0\u7684\u8033\u5782\uff0c\u6162\u6162\u4eb2\u5230\u96ea\u767d\u7684\u8116\u9888\uff0c\u95fb\u7740\u5979\u6de1\u6de1\u7684\u53d1\u9999\uff0c\u604d\u60da\u95f4\u53c8\u50cf\u662f\u56de\u5230\u4e86\u9065\u8fdc\u7684\u4ece\u524d\uff0c\u56de\u5230\u521d\u604b\u65f6\u4e00\u822c\u3002\u4ed6\u4e5f\u662f\u8fd9\u822c\u548c\u5973\u53cb\u5750\u5728\u6625\u5929\u7684\u8349\u5730\u4e0a\uff0c\u62b1\u5979\u5728\u6000\u91cc\uff0c\u770b\u7740\u78a7\u7a7a\u5982\u6d17\uff0c\u8349\u957f\u83ba\u98de\uff0c\u5fc3\u4e2d\u4ec0\u4e48\u90fd\u4e0d\u60f3\uff0c\u53ea\u613f\u65f6\u95f4\u6c38\u8fdc\u505c\u7559\u6b64\u523b\u3002</p><p>\u5f53\u7136\u8fd9\u662f\u4e0d\u53ef\u80fd\u7684\u3002</p><p>\u65f6\u503c\u6625\u590f\u5c06\u4ea4\uff0c\u78a7\u8349\u7e41\u8302\uff0c\u70db\u5821\u7684\u5de5\u4f5c\u4eba\u5458\u5f00\u59cb\u6765\u4fee\u526a\u8349\u576a\u3002\u4ed6\u4eec\u624b\u4e2d\u62ff\u7740\u4e00\u79cd\u5947\u602a\u7684\u673a\u68b0\uff0c\u743c\u6069\u8fdc\u8fdc\u77a5\u4e86\u4e00\u773c\uff0c\u611f\u89c9\u6709\u70b9\u50cf\u662f\u5730\u7403\u4e0a\u6240\u4f7f\u7528\u7684\u5272\u8349\u673a\uff0c\u4f46\u5374\u53c8\u4e0d\u592a\u76f8\u4f3c\uff0c\u81f3\u5c11\u566a\u97f3\u8981\u4f4e\u5f97\u591a\uff0c\u800c\u4e14\u5272\u8d77\u8349\u6765\u4e5f\u901f\u5ea6\u98de\u5feb\u3002</p><p>\u751c\u751c\u7684\u6e05\u9999\u4f20\u6765\uff0c\u4eff\u4f5b\u7518\u8517\u7684\u5473\u9053\u3002</p><p>\u65e2\u7136\u522b\u4eba\u5728\u5de5\u4f5c\uff0c\u743c\u6069\u548c\u6885\u83f2\u65af\u81ea\u7136\u4e0d\u597d\u8fd8\u5750\u5728\u8fd9\u91cc\uff0c\u4e24\u4eba\u8d77\u8eab\uff0c\u51c6\u5907\u5404\u81ea\u53bb\u9ad8\u5854\u5185\u770b\u4e66\uff0c\u65e2\u7136\u6765\u4e86\u70db\u5821\u8fd9\u79cd\u5927\u56fe\u4e66\u9986\uff0c\u4e0d\u5b9c\u6d6a\u8d39\u65f6\u95f4\u3002\u743c\u6069\u521a\u521a\u7ad9\u8d77\uff0c\u9661\u5730\u89c9\u5f97\u773c\u524d\u4e00\u9635\u53d1\u9ed1\uff0c\u7d27\u63a5\u7740\u80f8\u53e3\u5904\u611f\u89c9\u4e00\u9635\u5f7b\u9aa8\u9634\u5bd2\u900f\u51fa\u6765\uff0c\u9aa4\u7136\u95f4\u6563\u5e03\u5230\u56db\u80a2\u767e\u9ab8\u4e2d\uff0c\u5168\u8eab\u4eff\u4f5b\u88ab\u51bb\u50f5\u4e00\u822c\uff0c\u4e1d\u6beb\u4e0d\u542c\u4f7f\u5524\uff0c\u4e0d\u7531\u81ea\u4e3b\u5f80\u540e\u5c31\u5012\u3002</p><p>\u6885\u83f2\u65af\u4e00\u60ca\uff0c\u8fde\u5fd9\u4f38\u624b\u5c06\u4ed6\u6276\u4f4f\uff0c\u201c\u600e\u4e48\u4e86\uff1f\u201d\u5979\u95ee\uff0c\u5374\u6ca1\u6709\u542c\u5230\u56de\u7b54\u3002\u5b9a\u775b\u7ec6\u770b\uff0c\u89c1\u743c\u6069\u8138\u8272\u53d1\u9752\uff0c\u5634\u5507\u82cd\u767d\uff0c\u6beb\u65e0\u534a\u70b9\u8840\u8272\uff0c\u773c\u775b\u867d\u7136\u7741\u5f00\uff0c\u5374\u6beb\u65e0\u534a\u70b9\u5149\u91c7\uff0c\u53ea\u662f\u5446\u5446\u6014\u7740\uff0c\u773c\u795e\u6da3\u6563\u3002\u5979\u4e0d\u77e5\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\uff0c\u4e00\u65f6\u4e5f\u9690\u9690\u6709\u4e9b\u5fc3\u614c\uff0c\u5c06\u4ed6\u6276\u4f4f\uff0c\u534a\u62d6\u534a\u62b1\u7740\uff0c\u53c8\u5e73\u653e\u5728\u8349\u5730\u4e0a\uff0c\u6b63\u51c6\u5907\u53bb\u65c1\u8fb9\u795e\u6bbf\u91cc\u53bb\u8bf7\u4e2a\u7267\u5e08\u6765\u5bdf\u770b\uff0c\u7a81\u7136\u542c\u5230\u743c\u6069\u201c\u5443\u201d\u4e86\u4e00\u58f0\uff0c\u6df1\u6df1\u5410\u51fa\u4e00\u53e3\u6c14\u6765\u3002</p><p>\u6885\u83f2\u65af\u521a\u624d\u5bdf\u770b\u4ed6\u7684\u60c5\u51b5\uff0c\u8138\u8d34\u5f97\u8fd1\uff0c\u88ab\u8fd9\u4e00\u53e3\u6c14\u5410\u51fa\uff0c\u89e6\u9762\u751f\u5bd2\uff0c\u4e0d\u7531\u5f97\u4e5f\u6fc0\u6ce0\u6ce0\u6253\u4e86\u4e2a\u51b7\u98a4\uff0c\u6b63\u5728\u8be7\u5f02\uff0c\u5c31\u89c1\u743c\u6069\u539f\u672c\u6563\u4e71\u7684\u76ee\u5149\u91cd\u65b0\u805a\u7126\uff0c\u8138\u4e0a\u9752\u8272\u4e5f\u6e10\u6e10\u6d88\u9000\uff0c\u76b1\u7740\u7709\uff0c\u52c9\u5f3a\u5750\u8d77\u8eab\u6765\u3002</p><p>\u201c\u600e\u4e48\u4e86\uff1f\u201d\u6885\u83f2\u65af\u95ee\u3002</p><p>\u521a\u624d\u5728\u8349\u5730\u4e0a\u4e00\u5e2d\u8c08\u8bdd\uff0c\u867d\u7136\u96f6\u788e\u6563\u4e71\uff0c\u4e1c\u626f\u897f\u62c9\uff0c\u5374\u4e5f\u4f3c\u4e4e\u8ba9\u4e24\u4eba\u4e4b\u95f4\u7684\u8ddd\u79bb\u62c9\u8fd1\u4e86\u4e00\u4e9b\u3002\u4e00\u76f4\u4ee5\u6765\uff0c\u4e24\u4eba\u7684\u5173\u7cfb\uff0c\u60c5\u4eba\u4e0d\u50cf\u60c5\u4eba\uff0c\u4e3b\u4ec6\u4e0d\u4f3c\u4e3b\u4ec6\uff0c\u670b\u53cb\u4e0d\u50cf\u670b\u53cb\uff0c\u654c\u4eba\u4e5f\u4e0d\u7b97\u654c\u4eba\uff0c\u9887\u6709\u4e9b\u6a21\u6a21\u7cca\u7cca\uff0c\u82e5\u5373\u82e5\u79bb\u3002\u73b0\u5728\u4eff\u4f5b\u8d34\u8fd1\u4e86\u4e00\u4e9b\uff0c\u66f4\u50cf\u662f\u66fe\u7ecf\u5171\u7ecf\u60a3\u96be\u7684\u540c\u4f34\u4e86\u3002</p><p>\u8fd9\u4e5f\u7b97\u662f\u4e00\u4e2a\u8fdb\u6b65\u5427\u3002</p><p>\u743c\u6069\u7f13\u7f13\u547c\u5438\uff0c\u4e00\u65f6\u987e\u4e0d\u4e0a\u56de\u7b54\u3002\u4ed6\u521a\u624d\u4e5f\u4e0d\u77e5\u9053\u662f\u600e\u4e48\u641e\u4e86\uff0c\u96be\u9053\u662f\u5750\u5f97\u592a\u4e45\u7684\u7f18\u6545\uff1f\u4f46\u773c\u524d\u53d1\u9ed1\u5c31\u7b97\u4e86\uff0c\u90a3\u80a1\u4ece\u80f8\u53e3\u8513\u5ef6\u5230\u5168\u8eab\u7684\u9634\u5bd2\u662f\u600e\u4e48\u56de\u4e8b\uff0c\u76f4\u5230\u6b64\u65f6\u867d\u7136\u7a0d\u7a0d\u7f13\u89e3\uff0c\u4f46\u624b\u8db3\u56db\u80a2\u4f9d\u65e7\u8fd8\u662f\u51b0\u51c9\u50f5\u786c\uff0c\u81ea\u5df1\u611f\u89c9\u8fde\u547c\u51fa\u6765\u7684\u6c14\u90fd\u662f\u51b7\u7684\u3002</p><p>\u4e0d\u4f1a\u5f97\u4ec0\u4e48\u75c5\u4e86\u5427\uff1f</p><p>\u4ed6\u8fd0\u8d77\u90a3\u70b9\u7c97\u6d45\u7684\u5185\u529f\uff0c\u7f13\u7f13\u8c03\u606f\u7740\uff0c\u4e5f\u987e\u4e0d\u4e0a\u548c\u6885\u83f2\u65af\u89e3\u91ca\u3002\u597d\u5728\u5c11\u5973\u89c1\u4ed6\u8138\u8272\u6e10\u6e10\u7f13\u548c\uff0c\u5fc3\u77e5\u6ca1\u4e8b\uff0c\u4e5f\u4e0d\u591a\u95ee\uff0c\u53ea\u5728\u65c1\u8fb9\u966a\u7740\u3002\u4e0d\u77e5\u8fc7\u4e86\u591a\u4e45\uff0c\u743c\u6069\u603b\u7b97\u52c9\u5f3a\u8ba9\u81ea\u5df1\u90a3\u70b9\u5fae\u8584\u7684\u5185\u606f\u8fd0\u8f6c\u4e00\u5708\uff0c\u4f53\u5185\u9634\u5bd2\u4e4b\u611f\u5927\u51cf\uff0c\u611f\u89c9\u6062\u590d\u4e86\u51e0\u5206\u529b\u6c14\uff0c\u53ea\u662f\u5173\u8282\u90e8\u4f4d\u611f\u89c9\u4f9d\u65e7\u9690\u9690\u9178\u9ebb\uff0c\u6447\u6643\u7740\u60f3\u7ad9\u8d77\u8eab\u6765\uff0c\u7ed3\u679c\u90fd\u529e\u4e0d\u5230\uff0c\u6700\u540e\u8fd8\u662f\u88ab\u6885\u83f2\u65af\u4e00\u628a\u6276\u4f4f\u3002</p><p>\u201c\u6ca1\u4e8b\u4e86\uff0c\u201d\u743c\u6069\u5411\u6885\u83f2\u65af\u70b9\u70b9\u5934\uff0c\u201c\u4e0d\u77e5\u9053\u600e\u4e48\u641e\u7684\uff0c\u521a\u624d\u7a81\u7136\u5168\u8eab\u96be\u53d7\u3002\u201d\u4ed6\u7a81\u7136\u7b11\u4e86\u4e00\u7b11\uff0c\u4f4e\u58f0\u8bf4\uff0c\u201c\u8c22\u8c22\u3002\u201d</p><p>\u5c11\u5973\u70b9\u70b9\u5934\uff0c\u4e0d\u518d\u8bf4\u8bdd\u3002</p><p>\u4e24\u4eba\u539f\u672c\u662f\u51c6\u5907\u53bb\u9ad8\u5854\u770b\u4e66\uff0c\u5982\u4eca\u743c\u6069\u8eab\u4f53\u4e0d\u9002\uff0c\u81ea\u7136\u4e5f\u5c31\u4f5c\u7f62\u3002\u6885\u83f2\u65af\u6276\u7740\u4ed6\uff0c\u56de\u5230\u4f4f\u5904\uff0c\u743c\u6069\u7684\u623f\u95f4\uff0c\u95e8\u88ab\u6885\u83f2\u65af\u649e\u98de\u4e86\uff0c\u8fd8\u6ca1\u6765\u5f97\u53ca\u4fee\u7406\uff0c\u6b64\u65f6\u7a7a\u6d1e\u5927\u5f00\uff0c\u81ea\u7136\u4e0d\u751a\u65b9\u4fbf\uff0c\u4e8e\u662f\u4fbf\u79fb\u5c45\u5230\u6885\u83f2\u65af\u7684\u623f\u95f4\u3002</p><p>\u5c11\u5973\u7684\u623f\u95f4\u91cc\uff0c\u6709\u80a1\u6de1\u96c5\u5e7d\u5e7d\u7684\u9999\u6c14\uff0c\u4e0d\u77e5\u9053\u662f\u4ec0\u4e48\u3002\u636e\u743c\u6069\u7684\u89c2\u5bdf\uff0c\u6885\u83f2\u65af\u5e76\u4e0d\u7528\u4ec0\u4e48\u5316\u5986\u54c1\u4e4b\u7c7b\uff0c\u9999\u6c34\u81ea\u7136\u662f\u66f4\u6ca1\u6709\uff0c\u53cd\u6b63\u4ee5\u5979\u5929\u751f\u4e3d\u8d28\uff0c\u53c8\u6b63\u503c\u9752\u6625\u5999\u9f84\uff0c\u5012\u4e5f\u65e0\u9700\u80ed\u8102\u906e\u76d6\uff0c\u53cd\u800c\u6709\u635f\u989c\u8272\u3002\u518d\u8bf4\uff0c\u5979\u8eab\u4e3a\u5723\u6b66\u58eb\uff0c\u60f3\u5fc5\u5e73\u65f6\u4e3a\u6559\u4f1a\u4e1c\u5954\u897f\u8dd1\uff0c\u6267\u884c\u4efb\u52a1\u7684\u65f6\u5019\u5f88\u591a\uff0c\u5973\u5b69\u5b50\u7684\u5316\u5986\u54c1\u5f80\u5f80\u74f6\u74f6\u7f50\u7f50\u4e00\u5927\u5806\uff0c\u96be\u9053\u8fd8\u80fd\u90fd\u5e26\u5728\u8eab\u4e0a\u4e48\u3002</p><p>\u5979\u7684\u623f\u95f4\u548c\u743c\u6069\u4e0d\u540c\uff0c\u7a97\u6237\u8f83\u5927\uff0c\u539f\u672c\u662f\u53ef\u4ee5\u900f\u8fdb\u9633\u5149\uff0c\u5c06\u6574\u4e2a\u623f\u95f4\u7167\u5f97\u4eae\u5802\u5802\u7684\u3002\u4f46\u5b9e\u9645\u4e0a\u5374\u6709\u4e9b\u9634\u6697\uff0c\u56e0\u4e3a\u5979\u603b\u662f\u62c9\u7740\u539a\u539a\u7684\u7a97\u5e18\uff0c\u8fde\u770b\u4e66\u90fd\u5b81\u613f\u70b9\u706f\u3002\u743c\u6069\u4e0d\u592a\u660e\u767d\u8fd9\u662f\u4e3a\u4ec0\u4e48\uff0c\u66fe\u7ecf\u968f\u53e3\u95ee\u8fc7\u5979\uff0c\u6885\u83f2\u65af\u56de\u7b54\u8bf4\uff1a\u201c\u5b89\u5168\u3002\u201d</p><p>\u743c\u6069\u53ea\u80fd\u8038\u8038\u80a9\uff0c\u89c9\u5f97\u5979\u672a\u514d\u6709\u4e9b\u592a\u8fc7\u4e8e\u8c28\u614e\u4e86\uff0c\u4e0d\u8fc7\u8fd9\u4e5f\u65e0\u6240\u8c13\uff0c\u53cd\u6b63\u4ed6\u81ea\u5df1\u4e5f\u5e76\u4e0d\u591a\u4e48\u559c\u6b22\u9633\u5149\u3002\u5bf9\u4e8e\u4e00\u4e2a\u5728\u9634\u9b42\u57ce\u5446\u4e86\u5341\u4e94\u5e74\u7684\u4eba\u6765\u8bf4\uff0c\u8fd9\u662f\u5f88\u6b63\u5e38\u7684\u53cd\u5e94\u3002</p><p>\u6885\u83f2\u65af\u5c06\u4ed6\u6276\u5230\u5e8a\u4e0a\uff0c\u66ff\u4ed6\u8131\u53bb\u957f\u888d\u548c\u957f\u9774\uff0c\u76d6\u4e0a\u88ab\u5b50\uff0c\u5c0f\u5fc3\u5730\u6396\u597d\u88ab\u89d2\u3002\u5979\u505a\u8fd9\u4e9b\u4e8b\u60c5\u975e\u5e38\u81ea\u7136\uff0c\u4e1d\u6beb\u4e0d\u89c9\u5c40\u4fc3\uff0c\u4eff\u4f5b\u4e00\u4e2a\u670d\u4f8d\u4e08\u592b\u7684\u6e29\u5a49\u59bb\u5b50\u4f3c\u7684\uff0c\u743c\u6069\u770b\u5f97\u4e0d\u7531\u6709\u4e9b\u75f4\u4e86\u3002</p><p>\u201c\u827e\u5f25\u8587\u3002\u201d\u4ed6\u5fcd\u4e0d\u4f4f\u53eb\u5979\u3002</p><p>\u201c\u5514\uff1f\u201d\u5c11\u5973\u8f6c\u8fc7\u8138\u6765\u3002</p><p>\u201c\u4f60\u771f\u6f02\u4eae\u3002\u201d\u743c\u6069\u7531\u8877\u5730\u8bf4\u3002</p><p>\u6885\u83f2\u65af\u5fae\u5fae\u4e00\u7b11\uff0c\u4f5c\u4e3a\u4e00\u4e2a\u7f8e\u4e3d\u7684\u5973\u5b69\uff0c\u8fd9\u79cd\u8bdd\u60f3\u5fc5\u5df2\u7ecf\u542c\u5f97\u592a\u591a\u4e86\u5427\u3002\u201c\u4f60\u4f11\u606f\u5427\u3002\u201d\u5979\u8bf4\uff0c\u81ea\u5df1\u5728\u6905\u5b50\u4e0a\u5750\u4e86\u4e0b\u6765\uff0c\u94f6\u5251\u65e9\u5df2\u7f29\u5c0f\uff0c\u6536\u5165\u8170\u95f4\uff0c\u63a5\u7740\u4f38\u624b\u4e00\u6309\uff0c\u4e0d\u77e5\u89e6\u52a8\u4ec0\u4e48\u673a\u5173\uff0c\u5c06\u94f6\u7532\u5378\u4e86\u4e0b\u6765\u3002</p><p>\u743c\u6069\u770b\u89c1\u5979\u5378\u4e0b\u94f6\u7532\uff0c\u5012\u662f\u60f3\u4e86\u8d77\u6765\u3002\u6885\u83f2\u65af\u7684\u8fd9\u4ef6\u94f6\u7532\uff0c\u91cc\u9762\u4f3c\u4e4e\u4e5f\u675f\u7f1a\u7740\u4e00\u53ea\u94f6\u9f99\u7684\u5e7d\u7075\u5462\uff0c\u800c\u4e14\u8fd9\u53ea\u53eb\u8428\u83f2\u8389\u4e9a\u65af\u7684\u94f6\u9f99\uff0c\u4f3c\u4e4e\u5c31\u662f\u88ab\u6885\u83f2\u65af\u6bcd\u4eb2\u6240\u6740\u3002</p><p>\u8fd9\u4ef6\u4e8b\u8bf4\u8d77\u6765\u5012\u662f\u9887\u6709\u503c\u5f97\u73a9\u5473\u4e4b\u5904\uff0c\u743c\u6069\u5bf9\u9f99\u7c7b\u751f\u6001\u5b66\u7814\u7a76\u4e0d\u591a\uff0c\u4f46\u5927\u4f53\u4e5f\u80fd\u770b\u5f97\u51fa\u6765\uff0c\u201c\u8428\u83f2\u8389\u4e9a\u65af\u201d\u663e\u7136\u662f\u4e00\u53ea\u6210\u5e74\u94f6\u9f99\u2014\u2014\u751a\u81f3\u5df2\u7ecf\u6b65\u5165\u4e2d\u8001\u5e74\u4e5f\u53ef\u80fd\uff0c\u53ef\u4e0d\u662f\u90a3\u79cd\u521a\u5b75\u51fa\u6765\u7684\u9f99\u5b9d\u5b9d\u3002\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u5230\u5e95\u662f\u4ec0\u4e48\u4eba\uff0c\u5c45\u7136\u80fd\u6740\u6b7b\u4e00\u53ea\u6210\u5e74\u94f6\u9f99\uff0c\u800c\u4e14\u8fd8\u5265\u76ae\u5236\u7532\u3002</p><p>\u9f99\u53ef\u7b97\u662f\u8fd9\u4e16\u754c\u4e0a\u6700\u5f3a\u5927\u7684\u751f\u7269\u4e86\u3002\u4e00\u53ea\u6210\u5e74\u5de8\u9f99\u5018\u82e5\u53d1\u6012\uff0c\u6bc1\u57ce\u706d\u90a6\u90fd\u672a\u5fc5\u662f\u96be\u4e8b\uff0c\u81ea\u53e4\u4ee5\u6765\u82e5\u662f\u6709\u4eba\u7c7b\u80fd\u6740\u6b7b\u6210\u5e74\u5de8\u9f99\uff0c\u7acb\u523b\u4fbf\u4f1a\u88ab\u51a0\u4ee5\u201c\u5c60\u9f99\u8005\u201d\u3001\u201c\u5c60\u9f99\u82f1\u96c4\u201d\u4e4b\u7c7b\u7684\u79f0\u53f7\uff0c\u6628\u65e5\u72b9\u81ea\u9ed8\u65e0\u95fb\uff0c\u4e00\u671d\u6210\u540d\u5929\u4e0b\u77e5\u3002\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u80fd\u51fb\u6740\u94f6\u9f99\uff0c\u5e76\u4e14\u8fd8\u5c06\u5979\u7684\u7075\u9b42\u56da\u7981\u5728\u8fd9\u94f6\u7532\u5185\uff0c\u80fd\u6709\u8fd9\u4efd\u672c\u4e8b\uff0c\u60f3\u5fc5\u4e5f\u5b9a\u7136\u662f\u5927\u6709\u6765\u5934\u7684\u4eba\u7269\u5427\u3002</p><p>\u8fd9\u8fd8\u7f62\u4e86\uff0c\u66f4\u5947\u602a\u7684\u662f\uff0c\u65e2\u7136\u8fd9\u4ef6\u94f6\u7532\u4e2d\u56da\u7981\u7740\u8428\u83f2\u8389\u4e9a\u65af\u7684\u5e7d\u7075\uff0c\u800c\u8fd9\u53ea\u94f6\u9f99\u548c\u6885\u83f2\u65af\u7684\u6bcd\u4eb2\u53c8\u662f\u4ec7\u4eba\u2014\u2014\u90a3\u6885\u83f2\u65af\u600e\u4e48\u80fd\u4e00\u76f4\u7a7f\u7740\u8fd9\u4ef6\u94f6\u7532\u5374\u4e0d\u53d7\u4f24\u5bb3\uff0c\u96be\u9053\u8fd9\u53ea\u94f6\u9f99\u5e7d\u7075\u5c31\u4e0d\u4f1a\u8fc1\u6012\u4e8e\u6885\u83f2\u65af\u4e48\uff1f\u4ee5\u5e38\u7406\u6765\u8bf4\uff0c\u8fd9\u79cd\u56da\u7981\u5e7d\u7075\u7684\u7269\u54c1\uff0c\u90fd\u4f1a\u6210\u4e3a\u8bc5\u5492\u7269\u54c1\uff0c\u5177\u6709\u5f88\u5f3a\u7684\u653b\u51fb\u6027\uff0c\u4f1a\u7ed9\u643a\u5e26\u8005\u9020\u6210\u610f\u60f3\u4e0d\u5230\u7684\u4f24\u5bb3\u3002\u4f55\u51b5\u6885\u83f2\u65af\u662f\u6740\u6b7b\u94f6\u9f99\u7684\u51f6\u624b\u4e4b\u5973\u3002</p><p>\u4ed6\u8bd5\u63a2\u7740\u5411\u6885\u83f2\u65af\u63d0\u51fa\u8fd9\u4e2a\u95ee\u9898\uff0c\u5c11\u5973\u7684\u56de\u7b54\u51fa\u4e4e\u4ed6\u7684\u610f\u6599\u4e4b\u5916\u3002</p><p>\u201c\u8428\u83f2\u8389\u4e9a\u65af\u7684\u7075\u9b42\u6ca1\u6709\u53d8\u6210\u5e7d\u7075\uff0c\u201d\u6885\u83f2\u65af\u8bf4\uff0c\u201c\u5b9e\u9645\u4e0a\uff0c\u5728\u6b64\u4e4b\u524d\uff0c\u5979\u7684\u7075\u9b42\u5c31\u5df2\u7ecf\u88ab\u6467\u6bc1\u4e86\u2026\u2026\u51e0\u4e4e\u88ab\u6467\u6bc1\u4e86\u3002\u201d</p><p>\u6240\u8c13\u51e0\u4e4e\u88ab\u6467\u6bc1\u4e86\uff0c\u90a3\u610f\u601d\u81ea\u7136\u662f\u8bf4\u8fd8\u5b58\u7559\u4e86\u4e00\u4e1d\u6b8b\u9b42\uff0c\u4e5f\u5c31\u662f\u743c\u6069\u6240\u770b\u89c1\u7684\u3002\u8fd9\u4e00\u4e1d\u6b8b\u9b42\uff0c\u6ca1\u6709\u80fd\u529b\u5f62\u6210\u5e7d\u7075\uff0c\u5176\u5b9e\u5979\u751a\u81f3\u6ca1\u6709\u4efb\u4f55\u4f24\u5bb3\u80fd\u529b\uff0c\u53ea\u4e0d\u8fc7\u4f5c\u4e3a\u67d0\u4e2a\u7075\u4f53\uff0c\u9644\u7740\u5728\u94f6\u7532\u4e0a\u7f62\u4e86\u3002\u5076\u5c14\u80fd\u8bf4\u8bf4\u8bdd\uff0c\u548c\u6885\u83f2\u65af\u7528\u5fc3\u7075\u4ea4\u6d41\uff0c\u7edd\u5927\u90e8\u5206\u65f6\u95f4\u90fd\u662f\u5728\u6c89\u7761\u3002\u542c\u6885\u83f2\u65af\u7684\u53e3\u6c14\uff0c\u5979\u4f3c\u4e4e\u8fd8\u548c\u8fd9\u53ea\u94f6\u9f99\u5173\u7cfb\u76f8\u5904\u5f97\u4e0d\u9519\uff0c\u8fd9\u53ef\u771f\u4e86\u4e0d\u8d77\u3002\u743c\u6069\u81ea\u5ea6\u82e5\u662f\u81ea\u5df1\u8001\u5988\u6740\u4e86\u4e2a\u4eba\uff0c\u81ea\u5df1\u53ea\u6015\u600e\u4e48\u4e5f\u6ca1\u6cd5\u548c\u90a3\u4e2a\u6b7b\u4eba\u7684\u5e7d\u7075\u505a\u670b\u53cb\u3002</p><p>\u90a3\u4e48\uff0c\u65e2\u7136\u8428\u83f2\u8389\u4e9a\u65af\u6ca1\u6709\u53d8\u6210\u5e7d\u7075\uff0c\u53ea\u662f\u5bc4\u5bbf\u5728\u8fd9\u5e45\u94f6\u7532\u4e2d\uff0c\u56e0\u4e3a\u5979\u6b64\u65f6\u5df2\u7ecf\u53ea\u5269\u4e00\u7f15\u6b8b\u9b42\u2014\u2014\u90a3\u4e48\u5979\u5230\u5e95\u60f3\u600e\u4e48\u6837\u624d\u80fd\u6446\u8131\u76ee\u524d\u7684\u4e0d\u5229\u5c40\u9762\u5462\uff1f</p><p>\u5bf9\u6b64\u6885\u83f2\u65af\u4e5f\u4e0d\u77e5\u9053\uff0c\u94f6\u9f99\u6ca1\u6709\u544a\u8bc9\u5979\uff0c\u6240\u4ee5\u5979\u4e5f\u6ca1\u6709\u95ee\u3002</p><p>\u6240\u4ee5\u743c\u6069\u4e5f\u4e0d\u518d\u591a\u95ee\uff0c\u4ed6\u9690\u9690\u89c9\u5f97\u5934\u6709\u70b9\u75db\uff0c\u80f8\u53e3\u6e10\u6e10\u53c8\u6cdb\u8d77\u90a3\u80a1\u9634\u5bd2\u7684\u611f\u89c9\u6765\u3002\u987e\u4e0d\u4e0a\u518d\u8ddf\u6885\u83f2\u65af\u804a\u5929\uff0c\u4ed6\u5e73\u8eba\u5728\u5e8a\u4e0a\uff0c\u5f00\u59cb\u6697\u4e2d\u7f13\u7f13\u8fd0\u529f\u8c03\u606f\u3002\u5c11\u5973\u89c1\u4ed6\u4e0d\u518d\u8bf4\u8bdd\uff0c\u4ee5\u4e3a\u662f\u75b2\u5026\u4e86\uff0c\u4e5f\u5c31\u81ea\u987e\u81ea\u9760\u5728\u6905\u80cc\u4e0a\uff0c\u95ed\u76ee\u4f11\u606f\u3002</p><p>\u743c\u6069\u8fd0\u529f\u8c03\u606f\u4e86\u534a\u5929\uff0c\u9634\u5bd2\u7684\u611f\u89c9\u7a0d\u7a0d\u7f13\u89e3\uff0c\u4f46\u4e5f\u53ea\u662f\u7a0d\u7a0d\u7f13\u89e3\uff0c\u4f9d\u65e7\u9690\u9690\u5730\u5728\u80f8\u53e3\u6f5c\u4f0f\u7740\uff0c\u4eff\u4f5b\u968f\u65f6\u53ef\u80fd\u53d1\u4f5c\u3002\u4ed6\u4e5f\u77e5\u9053\u8fd9\u4e0d\u662f\u957f\u4e45\u4e4b\u8ba1\uff0c\u53ea\u8981\u81ea\u5df1\u677e\u61c8\uff0c\u8fd9\u80a1\u5bd2\u6c14\u5fc5\u5b9a\u53c8\u4f1a\u5377\u571f\u91cd\u6765\u3002\u201c\u8fd9\u5230\u5e95\u662f\u600e\u4e48\u641e\u4e86\uff1f\u201d\u4ed6\u5728\u5fc3\u4e2d\u5fd0\u5fd1\u7740\uff0c\u201c\u96be\u4e0d\u6210\u7ec3\u529f\u51fa\u5c94\u5b50\uff0c\u8d70\u706b\u5165\u9b54\u4e86\u4e0d\u6210\uff1f\u201d</p><p>\u8fd9\u4e2a\u5ff5\u5934\u968f\u5373\u88ab\u5426\u5b9a\u4e86\u3002\u8bf4\u5f97\u4e0d\u5ba2\u6c14\u70b9\uff0c\u8d70\u706b\u5165\u9b54\u4e5f\u8981\u6709\u8d70\u706b\u5165\u9b54\u7684\u8d44\u683c\uff0c\u7ffb\u7ffb\u6b66\u4fa0\u5c0f\u8bf4\uff0c\u4ece\u6765\u90fd\u662f\u80fd\u628a\u4ec0\u4e48\u4e7e\u5764\u5927\u632a\u79fb\u3001\u6613\u7b4b\u7ecf\u52a0\u5c0f\u65e0\u76f8\u529f\u6216\u8005\u5929\u4e0a\u5730\u4e0b\u552f\u6211\u72ec\u5c0a\u529f\u4e4b\u7c7b\u7684BT\u529f\u592b\u7ec3\u5230\u4ec0\u4e48\u4e03\u516b\u5c42\u3001\u516b\u4e5d\u91cd\u5883\u754c\u7684\u5f3a\u8005\uff0c\u624d\u6709\u8d44\u683c\u53bb\u8d70\u706b\u5165\u9b54\uff1b\u50cf\u743c\u6069\u8fd9\u79cd\u6b66\u5b66\u5e9f\u67f4\uff0c\u7ec3\u4e86\u516b\u5e74\u5185\u529f\u8fd8\u505c\u7559\u5728\u57fa\u7840\u9636\u6bb5\uff0c\u4e5f\u597d\u610f\u601d\u8bf4\u81ea\u5df1\u8d70\u706b\u5165\u9b54\uff1f\u4ed6\u54ea\u6709\u4ec0\u4e48\u706b\u53ef\u4ee5\u8d70\uff0c\u54ea\u6709\u4ec0\u4e48\u9b54\u53ef\u4ee5\u5165\u3002</p><p>\u5ff5\u5934\u8f6c\u6765\u8f6c\u53bb\uff0c\u53ea\u662f\u4e0d\u5f97\u5176\u89e3\u3002\u201c\u81ea\u5df1\u8fd9\u4e9b\u5929\u6765\u4e0d\u8fc7\u90fd\u5728\u70db\u5821\u5446\u7740\uff0c\u5403\u996d\u7761\u89c9\u52a0\u770b\u4e66\uff0c\u6ca1\u53d1\u751f\u4ec0\u4e48\u4e8b\u60c5\u554a\u2026\u2026\u7b49\u7b49\uff01\u201d</p><p>\u9661\u7136\u95f4\u8111\u4e2d\u7075\u5149\u4e00\u95ea\uff0c\u60f3\u4e86\u8d77\u6765\u3002\u6628\u665a\u548c\u90a3\u4e2a\u5973\u6740\u624b\u5728\u5e8a\u4e0a\u7ffb\u4e91\u8986\u96e8\uff0c\u6700\u540e\u4e3a\u4e86\u6446\u8131\uff0c\u5077\u5077\u7528\u4e86\u91c7\u7389\u8bc0\uff0c\u7ed3\u679c\u4e0d\u77e5\u51fa\u4e86\u4ec0\u4e48\u53d8\u6545\uff0c\u4e00\u80a1\u5947\u602a\u7684\u529b\u91cf\u4ece\u6740\u624b\u4f53\u5185\u6d8c\u8fdb\u81ea\u5df1\u8eab\u4f53\u2026\u2026\u8fd9\u611f\u89c9\u5b8c\u5168\u6ca1\u9519\uff0c\u5c31\u662f\u8fd9\u79cd\u9634\u5bd2\u3001\u51b0\u51b7\uff0c\u5145\u6ee1\u7740\u4e0d\u8a00\u800c\u55bb\u7684\u90aa\u6076\u548c\u6df7\u4e71\uff0c\u4eff\u4f5b\u5f7b\u5934\u5f7b\u5c3e\u7684\u54c0\u4f24\u548c\u7edd\u671b\uff0c\u4ee4\u4eba\u4e0d\u5bd2\u800c\u6817\u3002</p><p>\u5f53\u65f6\u743c\u6069\u5c31\u5413\u4e86\u4e00\u8df3\uff0c\u4e0d\u77e5\u9053\u51fa\u4e86\u4ec0\u4e48\u4e8b\u60c5\uff0c\u8fd8\u4ee5\u4e3a\u662f\u81ea\u5df1\u6ca1\u7528\u8fc7\u91c7\u7389\u8bc0\uff0c\u7b2c\u4e00\u6b21\u8fd0\u529f\u51fa\u9519\u3002\u540e\u6765\u4e00\u76f4\u5fd9\u4e71\uff0c\u628a\u8fd9\u4ef6\u4e8b\u60c5\u5b8c\u5168\u7ed9\u5fd8\u4e86\uff0c\u76f4\u81f3\u6b64\u65f6\u624d\u7a81\u7136\u53cd\u5e94\u8fc7\u6765\u3002</p><p>\u8fd9\u662f\u4ece\u90a3\u4e2a\u5973\u6740\u624b\u4f53\u5185\u5438\u6765\u7684\uff1f</p></article>"}}}]);
