(window.webpackJsonp=window.webpackJsonp||[]).push([[1103],{1126:function(p){p.exports={chapterTitle:null,title:"\u7b2c901\u7ae0 \u5317\u4eac\u7684\u79cb\u5929",content:"<article><h2>\u7b2c901\u7ae0 \u5317\u4eac\u7684\u79cb\u5929</h2><p>\u3000\u3000\u7fdf\u4e39\u9752\u8ddf\u6628\u591c\u4ec0\u4e48\u4e8b\u60c5\u90fd\u6ca1\u6709\u53d1\u751f\u8fc7\u4f3c\u7684\u8fc7\u6765\u901a\u77e5\u5f20\u606a\u4e0a\u5348\u98de\u673a\u8d77\u98de\u7684\u65f6\u95f4\uff0c\u8138\u4e0a\u4e5f\u4e0d\u9732\u4e00\u70b9\u7684\u75d5\u8ff9\uff0c\u5728\u5979\u63a8\u95e8\u8d70\u51fa\u53bb\u4e4b\u524d\uff0c\u5f20\u606a\u7a81\u7136\u8bf4\u4e86\u4e00\u53e5\uff1a\u201c\u83ab\u975e\u6211\u88ab\u4e00\u591c\u60c5\u4e86\uff1f\u201d</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u63e1\u4f4f\u95e8\u628a\u624b\u4e1d\u6beb\u65e0\u788d\u7684\u5c06\u8eab\u540e\u7684\u623f\u95e8\u5173\u4e0a\uff0c\u5173\u4e0a\u623f\u95e8\u7684\u77ac\u95f4\uff0c\u5c31\u54ac\u7259\u5207\u9f7f\u6068\u4e0d\u5f97\u5c06\u8fd9\u6d51\u7403\u751f\u541e\u6d3b\u5265\u4e86\uff0c\u6076\u72e0\u72e0\u7684\u8e29\u51fa\u4e00\u6b65\uff0c\u5de6\u811a\u978b\u8ddf\u628a\u5229\u5251\u4f3c\u7684\u7ec6\u9ad8\u8ddf\u5494\u7684\u4e00\u58f0\u65ad\u6210\u4e24\u622a\uff0c\u4eba\u7684\u91cd\u70b9\u9aa4\u7136\u95f4\u5931\u53bb\uff0c\u5fcd\u4e0d\u4f4f\u53d1\u51fa\u4e00\u58f0\u5a07\u547c\uff0c\u624b\u91cc\u4e00\u6367\u6587\u4ef6\u6d12\u4e86\u4e00\u5730\uff0c\u8eab\u5b50\u4e5f\u5c31\u52bf\u8dcc\u5012\u5728\u5730\u6bef\u4e0a\u3002</p><p>\u3000\u3000\u201c\u4f60\u6ca1\u4e8b\u5427\uff1f\u201d\u5f20\u606a\u4e00\u7bad\u6b65\u8df3\u4e0b\u5e8a\u62c9\u5f00\u623f\u95e8\uff0c\u5fd9\u4f38\u8fc7\u6765\u8981\u5c06\u7fdf\u4e39\u9752\u6400\u8d77\u6765\u3002</p><p>\u3000\u3000\u7fdf\u4e39\u9752\u624b\u6309\u4f4f\u7ed9\u5d34\u7740\u7684\u811a\u8e1d\uff0c\u89c1\u5f20\u606a\u8d64\u8eab\u88f8\u4f53\u53ea\u7a7f\u4e86\u4e00\u4ef6\u5e73\u89d2\u5185\u88e4\uff0c\u63a8\u4ed6\u8fdb\u623f\u95f4\uff1a\u201c\u7ed9\u522b\u4eba\u770b\u89c1\u6210\u4ec0\u4e48\u6837\u5b50\u4e86\uff1f\u6211\u6ca1\u6709\u4e8b\uff0c\u5c31\u6454\u4e86\u4e00\u8de4\u2014\u2014\u8fd9\u978b\u8ddf\u8d28\u91cf\u5dee\uff0c\u978b\u8ddf\u7a81\u7136\u5c31\u65ad\u4e86\uff0c\u56de\u5934\u6709\u95f2\u5de5\u592b\uff0c\u975e\u8981\u597d\u597d\u8ddf\u90a3\u5bb6\u978b\u5e97\u597d\u597d\u7684\u2018\u4ea4\u6d41\u4ea4\u6d41\u2019\u3002\u201d\u6700\u540e\u90a3\u53e5\u8bdd\u8bf4\u5f97\u54ac\u7259\u5207\u9f7f\u3002</p><p>\u3000\u3000\u89c1\u7fdf\u4e39\u9752\u8dea\u5728\u5730\u6bef\u4e0a\u6536\u62fe\u6563\u843d\u4e86\u4e00\u5730\u7684\u6587\u4ef6\uff0c\u95e8\u5916\u53c8\u6709\u811a\u6b65\u54cd\u8d77\uff0c\u60f3\u662f\u5916\u9762\u7684\u5de5\u4f5c\u4eba\u5458\u542c\u5230\u8fd9\u91cc\u6709\u52a8\u9759\u8d76\u8fc7\u6765\u770b\u7a76\u7adf\uff0c\u5f20\u606a\u4fbf\u5c06\u623f\u95e8\u63a9\u4e0a\uff0c\u60f3\u8d77\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u675c\u98de\u4e0e\u5468\u6653\u7490\u4e4b\u95f4\u7684\u90a3\u4e9b\u7834\u4e8b\uff0c\u65e0\u5948\u7684\u82e6\u7b11\u51e0\u4e0b\u3002</p><p>\u3000\u3000\u2026\u2026</p><p>\u3000\u3000\u5317\u4eac\u6b63\u503c\u79cb\u9ad8\u6c14\u723d\u7684\u65f6\u5019\uff0c\u7a7a\u6c14\u91cc\u7684\u6c99\u5c18\u4e5f\u4e0d\u591a\uff0c\u51fa\u673a\u573a\uff0c\u4e00\u62b9\u5915\u9633\u7167\u5728\u8f66\u7a97\u4e0a\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u5750\u5728\u8f66\u540e\u53a2\u91cc\uff0c\u4e3a\u4e86\u65b9\u4fbf\u5317\u4eac\u5206\u516c\u53f8\u7684\u59da\u575a\u5728\u8f66\u4e0a\u6c47\u62a5\u5de5\u4f5c\uff0c\u7fdf\u4e39\u9752\u4e0b\u98de\u673a\u5c31\u4e3b\u52a8\u5750\u53e6\u4e00\u8f86\u8f66\u4e0a\uff0c\u5f20\u606a\u4e5f\u4e0d\u6653\u5f97\u5979\u662f\u4e0d\u662f\u5728\u8eb2\u7740\u81ea\u5df1\u3002</p><p>\u3000\u3000\u5317\u4eac\u5206\u516c\u53f8\u5e76\u5165\u9526\u6e56\u5546\u4e8b\u4f53\u7cfb\u4e4b\u4e0b\uff0c\u6210\u4e3a\u9526\u6e56\u5546\u4e8b\u5728\u56fd\u5185\u534e\u4e1c\u3001\u4e1c\u5317\u3001\u897f\u5317\u5730\u533a\u603b\u5206\u652f\u673a\u6784\uff0c\u53d7\u5efa\u90ba\u516c\u53f8\u7684\u9886\u5bfc\uff0c\u4e4b\u524d\u5728\u5317\u4eac\u5206\u516c\u53f8\u62c5\u4efb\u603b\u7ecf\u7406\u7684\u5468\u4e00\u5e73\u8c03\u5230\u9526\u6e56\u5546\u4e8b\u8bbe\u5728\u5efa\u90ba\u7684\u56fd\u5185\u603b\u90e8\u62c5\u4efb\u603b\u88c1\uff0c\u8d1f\u8d23\u9886\u5bfc\u9526\u6e56\u5546\u4e8b\u5728\u56fd\u5185\u7684\u4e1a\u52a1\uff0c\u6210\u4e3a\u771f\u6b63\u72ec\u5f53\u4e00\u9762\u7684\u5927\u5c06\u3002</p><p>\u3000\u3000\u5468\u4e00\u5e73\u79bb\u5f00\u5317\u4eac\u4e4b\u540e\uff0c\u5c31\u63a8\u8350\u4e4b\u524d\u4ed6\u5728\u5317\u4eac\u65f6\u7684\u526f\u624b\u59da\u575a\u63a5\u66ff\u5317\u4eac\u5206\u516c\u53f8\u603b\u7ecf\u7406\u7684\u4f4d\u5b50\u3002</p><p>\u3000\u3000\u53f6\u5efa\u658c\u7b11\u7740\u5750\u5728\u526f\u9a7e\u9a76\u4f4d\u4e0a\u7684\u59da\u575a\u8bf4\u9053\uff1a\u201c\u8fd9\u4e24\u4e2a\u6708\u7684\u611f\u89c9\u5982\u4f55\uff0c\u662f\u4e0d\u662f\u6709\u4e9b\u8f7b\u98d8\u98d8\u7684\u611f\u89c9\uff1f\u5230\u673a\u573a\u6765\u63a5\u6211\u4eec\uff0c\u6709\u6ca1\u6709\u523b\u610f\u7167\u955c\u5b50\u7ec3\u4e60\u7740\u8ba9\u81ea\u5df1\u8138\u4e0a\u7684\u8868\u60c5\u66f4\u51dd\u91cd\u4e00\u4e9b\uff1f\u201d</p><p>\u3000\u3000\u201c\u90fd\u8bf4\u6c14\u52bf\u538b\u4eba\u4e86\uff0c\u201d\u59da\u575a\u5634\u91cc\u867d\u7136\u4e5f\u662f\u8bf4\u7740\u7b11\uff0c\u5374\u662f\u8f6c\u8fc7\u8eab\u6765\u8bf4\u8bdd\uff0c\u201c\u5728\u4e24\u4f4d\u8001\u677f\u7684\u9762\u524d\uff0c\u6211\u5982\u5c65\u8584\u51b0\uff0c\u54ea\u91cc\u9700\u8981\u523b\u610f\u7684\u53bb\u7ec3\u4e60\u554a\uff1f\u201d</p><p>\u3000\u3000\u5f20\u606a\u6447\u5934\u800c\u7b11\uff0c\u6325\u4e86\u6325\u624b\uff0c\u8ba9\u59da\u575a\u8eab\u5b50\u8f6c\u56de\u53bb\u968f\u610f\u7684\u8bf4\u8bdd\u5c31\u884c\u3002\u4ece\u4ed6\u8eab\u4e0a\uff0c\u5f20\u606a\u591a\u5c11\u80fd\u770b\u5230\u81ea\u5df1\u5728\u524d\u5c18\u5f80\u4e16\u91cc\u7684\u4e00\u70b9\u5f71\u5b50\uff0c\u4e09\u5341\u5c81\u4e0d\u5230\u5c31\u80fd\u591f\u72ec\u6321\u4e00\u9762\uff0c\u5bf9\u4e8e\u6ca1\u6709\u4ec0\u4e48\u5bb6\u5ead\u80cc\u666f\u7684\u4eba\uff0c\u5f53\u771f\u8981\u7b97\u5f97\u4e0a\u5c11\u5e74\u5f97\u5fd7\uff0c\u60f3\u5fc5\u79c1\u751f\u6d3b\u6ca1\u6709\u81ea\u5df1\u90a3\u4e48\u6df7\u4e71\u5427\uff1f</p><p>\u3000\u3000\u8986\u6c34\u96be\u53d7\uff0c\u6df7\u4e71\u8fc7\uff0c\u518d\u60f3\u6beb\u65e0\u6742\u8d28\u7684\u56de\u590d\u5230\u4e4b\u524d\u7684\u72b6\u6001\u5c31\u5df2\u7ecf\u4e0d\u53ef\u80fd\u4e86\uff0c\u518d\u4e16\u4e3a\u751f\uff0c\u7075\u9b42\u5374\u6ca1\u6709\u5f97\u5230\u5f7b\u5e95\u7684\u6dec\u70bc\u2014\u2014\u5f20\u606a\u8fd9\u65f6\u5019\u5012\u89c9\u5f97\u4ed6\u4e0e\u7fdf\u4e39\u9752\u4e4b\u95f4\u7684\u5173\u7cfb\u6709\u4e9b\u68d8\u624b\u4e86\uff0c\u81ea\u5df1\u4e5f\u7684\u786e\u591f\u6df7\u4e71\u7684\u3002</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u59da\u575a\u7684\u624b\u673a\u6709\u7535\u8bdd\u6253\u8fdb\u6765\uff0c\u59da\u575a\u8f6c\u8fc7\u8eab\u6765\u6b49\u610f\u7684\u7b11\u4e86\u7b11\uff0c\u53c8\u4e0d\u80fd\u8bf4\u4e0b\u8f66\u63a5\u7535\u8bdd\uff0c\u53c8\u4e0d\u80fd\u5728\u8f66\u91cc\u3001\u5728\u4e24\u4f4d\u5927\u8001\u677f\u7684\u9762\u524d\u5927\u8c08\u4e1a\u52a1\uff0c\u53ea\u662f\u7b80\u5355\u7684\u8bf4\u4e86\u4e24\u53e5\uff0c\u5c31\u5c06\u7535\u8bdd\u6302\u4e0a\u4e86\uff0c\u56de\u5934\u53c8\u8ddf\u5f20\u606a\u3001\u53f6\u5efa\u658c\u89e3\u91ca\u9053\uff1a\u201c\u6d77\u5dde\u7684\u4e00\u5bb6\u8d38\u6613\u516c\u53f8\uff0c\u60f3\u4e0e\u6211\u4eec\u5728\u534e\u5317\u5e02\u573a\u5408\u4f5c\uff0c\u8fd9\u5bb6\u516c\u53f8\u7684\u8001\u603b\u4eca\u5929\u521a\u5230\u5317\u4eac\u6765\u2026\u2026\u201d</p><p>\u3000\u3000\u201c\u54e6\u3002\u201d\u53f6\u5efa\u658c\u5e94\u4e86\u4e00\u58f0\uff0c\u9526\u6e56\u5546\u4e8b\u867d\u8bf4\u521a\u6210\u6ca1\u51e0\u4e2a\u6708\uff0c\u4f46\u662f\u6784\u67b6\u6062\u5f18\uff0c\u5317\u4eac\u5206\u516c\u53f8\u867d\u8bf4\u53ea\u80fd\u7b97\u9526\u6e56\u5546\u4e8b\u65d7\u4e0b\u7684\u4e8c\u7ea7\u5206\u516c\u53f8\uff0c\u4f46\u662f\u6240\u62e5\u6709\u7684\u8d44\u6e90\u4e5f\u7edd\u975e\u56fd\u5185\u4e00\u822c\u8d38\u6613\u516c\u53f8\u80fd\u76f8\u63d0\u5e76\u8bba\u7684\uff0c\u59da\u575a\u600e\u4e48\u8bf4\u4e5f\u662f\u4e00\u4e2a\u4eba\u7269\uff0c\u4ed6\u4eec\u80fd\u505a\u7684\u5c31\u662f\u8981\u8ba9\u8fd9\u4e9b\u4eba\u5c06\u80fd\u529b\u5145\u5206\u7684\u53d1\u6325\u51fa\u6765\uff0c\u5177\u4f53\u7684\u4e8b\u60c5\u8981\u5c11\u53d1\u8868\u610f\u89c1\uff0c\u514d\u5f97\u4e0b\u9762\u4eba\u65e0\u6240\u9002\u4ece\u3002</p><p>\u3000\u3000\u201c\u6d77\u5dde\u7684\u54ea\u5bb6\u516c\u53f8\uff0c\u662f\u4e0d\u662f\u9686\u88d5\u8d38\u6613\uff1f\u201d\u5f20\u606a\u5374\u5173\u5fc3\u7684\u95ee\u4e86\u4e00\u58f0\uff0c\u4ed6\u8bb0\u5f97\u9686\u88d5\u8d38\u6613\u5dee\u4e0d\u591a\u4e5f\u662f\u8fd9\u65f6\u5019\u5f00\u62d3\u534e\u5317\u5e02\u573a\u7684\u3002</p><p>\u3000\u3000\u201c\u606a\u5c11\u4e5f\u77e5\u9053\u8fd9\u5bb6\u516c\u53f8\uff1f\u521a\u624d\u90a3\u4e2a\u7535\u8bdd\u5c31\u662f\u9686\u88d5\u7684\u603b\u7ecf\u7406\u9b4f\u65f6\u88d5\u4eb2\u81ea\u6253\u8fc7\u6765\u7684\u2026\u2026\u201d\u59da\u575a\u56de\u7b54\u9053\u3002</p><p>\u3000\u3000\u201c\u54e6\u2026\u2026\u201d\u5f20\u606a\u5e94\u4e86\u4e00\u58f0\uff0c\u624b\u76d6\u7740\u8138\u601d\u91cf\u8d77\u6765\u3002\u524d\u5c18\u5f80\u4e16\u91cc\uff0c\u81ea\u5df1\u4e5f\u53d7\u9b4f\u65f6\u88d5\u8d4f\u8bc6\u624d\u80fd\u591f\u5728\u9686\u88d5\u96c6\u56e2\u5185\u90e8\u5feb\u901f\u5347\u8fc1\u5e76\u6700\u7ec8\u80fd\u8d1f\u8d23\u9686\u88d5\u96c6\u56e2\u65d7\u4e0b\u5e9e\u5927\u7684\u5404\u7c7b\u4e13\u4e1a\u4eea\u5668\u8d38\u6613\u4e1a\u52a1\uff0c\u5f53\u65f6\u7ed9\u522b\u4eba\u7684\u611f\u89c9\u5c31\u662f\u9b4f\u65f6\u88d5\u597d\u50cf\u6709\u4e2a\u5973\u513f\u6025\u7740\u8981\u5ac1\u4eba\u4f3c\u7684\u3002\u8bf4\u8d77\u6765\u9b4f\u65f6\u88d5\u5bf9\u81ea\u5df1\u6709\u9020\u5c31\u7684\u60c5\u8c0a\uff0c\u53ea\u4e0d\u8fc7\u9b4f\u65f6\u88d5\u5373\u4f7f\u5faa\u7740\u5386\u53f2\u7684\u8f68\u8ff9\u4e5f\u4f1a\u6709\u6781\u597d\u7684\u53d1\u5c55\uff0c\u5012\u662f\u6ca1\u6709\u53bb\u753b\u86c7\u6dfb\u8db3\u7684\u5fc5\u8981\uff0c\u5f20\u606a\u4e5f\u5c31\u5c06\u9b4f\u65f6\u88d5\u53ea\u5f53\u6210\u964c\u751f\u4eba\u6765\u5bf9\u5f85\u3002</p><p>\u3000\u3000\u542c\u5230\u59da\u575a\u8bf4\u9b4f\u65f6\u88d5\u4e3b\u52a8\u8981\u627e\u9526\u6e56\u5546\u4e8b\u5546\u8bae\u5408\u4f5c\u7684\u4e8b\u60c5\uff0c\u5f20\u606a\u8111\u5b50\u91cc\u5012\u662f\u6d8c\u51fa\u66f4\u591a\u7684\u5728\u8fd9\u4e2a\u4e16\u754c\u672a\u66fe\u53d1\u751f\u8fc7\u3001\u4e5f\u4e0d\u4f1a\u53d1\u751f\u7684\u56de\u5fc6\u6765\uff0c\u524d\u5c18\u5f80\u4e8b\u7684\u4eba\u751f\u867d\u8bf4\u560e\u7136\u800c\u6b62\u4e86\uff0c\u53ef\u662f\u81ea\u5df1\u8fd8\u662f\u90a3\u6837\u7684\u81ea\u5df1\uff0c\u4f3c\u4e4e\u4e5f\u6ca1\u6709\u4ec0\u4e48\u957f\u8fdb\u3002</p><p>\u3000\u3000\u6ca1\u6709\u957f\u8fdb\u554a\uff01\u5f20\u606a\u5fc3\u91cc\u8f7b\u8f7b\u7684\u4e00\u53f9\uff0c\u624b\u6307\u6572\u7740\u7709\u5934\uff0c\u8ddf\u59da\u575a\u8bf4\u9053\uff1a\u201c\u8fd9\u6837\u5427\uff0c\u4eba\u5bb6\u4e5f\u662f\u96be\u5f97\u6765\u4e00\u8d9f\u5317\u4eac\uff0c\u4f60\u5c06\u6211\u4eec\u9001\u5230\u4e2d\u592e\u515a\u6821\u5c31\u53ef\u4ee5\u4e86\u2026\u2026\u201d</p><p>\u3000\u3000\u5f20\u606a\u5bf9\u6d77\u5dde\u7684\u4f01\u4e1a\u4e00\u5411\u90fd\u5f88\u7167\u987e\uff0c\u4ed6\u8fd9\u4e48\u8bf4\uff0c\u53f6\u5efa\u658c\u4e0e\u59da\u575a\u90fd\u4e0d\u4f1a\u89c9\u5f97\u610f\u5916\uff0c\u59da\u575a\u70b9\u4e86\u70b9\u5934\uff0c\u5728\u8f66\u91cc\u5c31\u56de\u62e8\u4e86\u9b4f\u65f6\u88d5\u7684\u7535\u8bdd\u7ea6\u597d\u89c1\u9762\u7684\u65f6\u95f4\uff0c\u9001\u5f20\u606a\u3001\u53f6\u5efa\u658c\u5230\u6d77\u6dc0\u533a\u5927\u6709\u5e84\u5c31\u6298\u8fd4\u53bb\u89c1\u9b4f\u65f6\u88d5\u4e86\u3002</p><p>\u3000\u3000\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u5230\u5317\u4eac\u6765\u662f\u89c1\u5510\u5b66\u8c26\u7684\uff0c\u5230\u6d77\u6dc0\u533a\u897f\u82d1\u6865\u8fc7\u53bb\u4e00\u70b9\u8def\u5c31\u63d0\u524d\u4e0b\u4e86\u8f66\uff0c\u7ea6\u597d\u662f\u5728\u4e2d\u592e\u515a\u6821\u5185\u90e8\u7684\u9152\u5e97\u7528\u9910\uff0c\u5f20\u606a\u4e0e\u53f6\u5efa\u658c\u671d\u515a\u6821\u6b63\u95e8\u8d70\u53bb\uff0c\u9a6c\u6d77\u9f99\u7b49\u968f\u884c\u4eba\u5458\u90fd\u7559\u8f66\u91cc\uff0c\u7fdf\u4e39\u9752\u4e5f\u7559\u5728\u8f66\u91cc\u3002</p><p>\u3000\u3000\u867d\u8bf4\u5b98\u5546\u4ea4\u7ed3\u4e5f\u662f\u6b63\u5e38\uff0c\u4f46\u662f\u5730\u65b9\u4e0a\u7684\u5b98\u5458\u5728\u5317\u4eac\u8fd8\u662f\u6536\u655b\u4e00\u4e9b\u7684\u597d\uff0c\u4e58\u7740\u51e0\u8f86\u8c6a\u534e\u8f7f\u8f66\u8fdb\u4e2d\u592e\u515a\u6821\uff0c\u6307\u4e0d\u5b9a\u5c31\u4f1a\u7ed9\u5510\u5b66\u8c26\u5e26\u53bb\u4ec0\u4e48\u4e0d\u597d\u7684\u5f71\u54cd\u3002</p><p>\u3000\u3000\u201c\u6d77\u5dde\u5e02\u65b0\u7684\u5e02\u59d4\u4e66\u8bb0\u6307\u4e0d\u5b9a\u4e5f\u5728\u91cc\u9762\u5b66\u4e60\uff01\u201d\u53f6\u5efa\u658c\u62ac\u5934\u770b\u4e86\u770b\u515a\u6821\u5dcd\u5ce8\u7565\u6709\u4e9b\u9648\u65e7\u7684\u5927\u95e8\uff0c\u8f6c\u8fc7\u5934\u8ddf\u5f20\u606a\u7b11\u7740\u8bf4\u3002</p><p>\u3000\u3000\u201c\u6709\u53ef\u80fd\u5427\u2026\u2026\u201d\u5f20\u606a\u70b9\u4e86\u70b9\u5934\uff0c\u773c\u4e0b\u80fd\u786e\u5b9a\u7684\u4e8b\u60c5\u5c31\u662f\u4e2d\u592e\u5c06\u76f4\u63a5\u51b3\u5b9a\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u7684\u4eba\u9009\uff0c\u5e94\u8be5\u662f\u4e00\u4e2a\u53bb\u6d77\u5dde\u9540\u91d1\u7684\u89d2\u8272\uff0c\u8fd9\u79cd\u89d2\u8272\u8fd8\u6700\u662f\u96be\u7f20\uff0c\u65e2\u7136\u662f\u53bb\u6d77\u5dde\u9540\u91d1\u7684\uff0c\u804c\u52a1\u867d\u7136\u4e0d\u9ad8\uff0c\u4f46\u662f\u80cc\u666f\u5374\u662f\u4e0d\u7b80\u5355\u3002</p><p>\u3000\u3000\u201c\u53ef\u80fd\u4f1a\u6709\u4e9b\u4e0d\u7b80\u5355\u554a\u2026\u2026\u201d\u53f6\u5efa\u658c\u7b11\u4e86\u7b11\u3002</p><p>\u3000\u3000\u5510\u5b66\u8c26\u5230\u6c5f\u5357\u7701\u62c5\u4efb\u7701\u59d4\u5e38\u59d4\u3001\u5e38\u52a1\u526f\u7701\u957f\u7684\u4e8b\u60c5\u5df2\u6210\u5b9a\u5c40\u4e86\uff0c\u4e2d\u592e\u7ec4\u7ec7\u90e8\u7684\u7a0b\u5e8f\u4e5f\u8d70\u5f97\u5dee\u4e0d\u591a\u4e86\uff0c\u867d\u8bf4\u5510\u5b66\u8c26\u8fd8\u8981\u6709\u4e00\u4e2a\u591a\u6708\u624d\u80fd\u7ed3\u675f\u5728\u4e2d\u592e\u515a\u6821\u7684\u5b66\u4e60\uff0c\u4e0d\u8fc7\u4ed6\u5728\u6c5f\u5357\u7701\u7684\u804c\u52a1\u5728\u5341\u6708\u521d\u7684\u6c5f\u5357\u7701\u59d4\u5168\u59d4\u4f1a\u8bae\u4e0a\u5c31\u4f1a\u6b63\u5f0f\u7684\u516c\u5e03\u51fa\u6765\u3002</p><p>\u3000\u3000\u90fd\u8fd9\u4e2a\u65f6\u95f4\u4e86\uff0c\u6d77\u5dde\u65b0\u5e02\u59d4\u4e66\u8bb0\u7684\u4eba\u9009\uff0c\u5f20\u606a\u4ed6\u4eec\u90fd\u6ca1\u6709\u542c\u5230\u6709\u4ec0\u4e48\u98ce\u58f0\u5439\u6765\u3002\u867d\u7136\u8bf4\u6d77\u5dde\u5e02\u59d4\u4e66\u8bb0\u7684\u804c\u52a1\u60ac\u800c\u672a\u51b3\u5510\u5b66\u8c26\u4e5f\u662f\u53ef\u4ee5\u5148\u79bb\u5f00\u6d77\u5dde\u5230\u6c5f\u5357\u7701\u4efb\u804c\u7684\uff0c\u53ea\u662f\u591a\u5c11\u4f1a\u663e\u5f97\u6709\u4e9b\u4e0d\u7b80\u5355\u3002\u4f46\u662f\uff0c\u82cf\u9038\u7fa4\u5df2\u7ecf\u8c03\u79bb\u6d77\u5dde\uff0c\u5b8b\u57f9\u660e\u62c5\u4efb\u526f\u4e66\u8bb0\u3001\u4ee3\u5e02\u957f\uff0c\u675c\u98de\u62c5\u4efb\u526f\u4e66\u8bb0\u517c\u7ec4\u7ec7\u90e8\u957f\uff0c\u518d\u52a0\u4e0a\u9646\u6ea2\u5149\u62c5\u4efb\u526f\u4e66\u8bb0\u517c\u4eba\u5927\u4e3b\u4efb\uff0c\u5373\u4f7f\u91cc\u9762\u7684\u5185\u60c5\u4e0d\u7b80\u5355\uff0c\u65b0\u6765\u7684\u5e02\u59d4\u4e66\u8bb0\u4e0d\u591f\u542c\u8bf4\uff0c\u5b8c\u5168\u53ef\u4ee5\u7531\u5b8b\u57f9\u660e\u3001\u675c\u98de\u3001\u9646\u6ea2\u5149\u4e09\u4eba\u5c06\u4ed6\u67b6\u7a7a\uff0c\u4f46\u662f\u80fd\u548c\u5e73\u5171\u5904\u3001\u80fd\u5171\u540c\u4fc3\u8fdb\uff0c\u8fd8\u662f\u548c\u5e73\u5171\u5904\u3001\u5171\u540c\u4fc3\u8fdb\u7684\u597d\uff1b\u518d\u8bf4\u5230\u6d77\u5dde\u53bb\u9540\u91d1\u7684\u4eba\u7269\u53c8\u4e0d\u4f1a\u662f\u4ec0\u4e48\u5584\u832c\uff0c\u786c\u662f\u5f97\u7f6a\u4e86\uff0c\u5c06\u6765\u4e5f\u662f\u7ed9\u9526\u6e56\u7684\u53d1\u5c55\u518d\u6dfb\u5835\u4e0a\u4e00\u5ea7\u6321\u8def\u7684\u5c71\u3002</p><p>\u3000\u3000\u201c\u53c8\u80fd\u600e\u6837\uff1f\u201d\u5f20\u606a\u8f7b\u8f7b\u7684\u53f9\u4e86\u4e00\u53e3\u6c14\uff0c\u53c8\u8bf4\u9053\uff0c\u201c\u4eca\u5929\u53c8\u4e0d\u662f\u8fc7\u6765\u7814\u7a76\u8fd9\u4e8b\u60c5\u7684\u2026\u2026\u201d</p><p>\u3000\u3000\u8fd9\u65f6\u5019\u6709\u4e00\u8f86\u91d1\u8272\u7684\u51ef\u8fea\u62c9\u514b\u4ece\u515a\u6821\u6b63\u95e8\u9a76\u51fa\u6765\uff0c\u53f6\u5efa\u658c\u5476\u5634\u7b11\u7740\u8bf4\uff1a\u201c\u504f\u504f\u6211\u4eec\u8fd9\u4e48\u5c0f\u5fc3\u8c28\u614e\uff0c\u4eba\u5bb6\u5374\u662f\u4e0d\u7ba1\u2026\u2026\u201d</p><p>\u3000\u3000\u90a3\u8f86\u51ef\u8fea\u62c9\u514b\u7ecf\u8fc7\u8eab\u8fb9\u65f6\uff0c\u867d\u8bf4\u8f66\u7a97\u8d34\u7740\u819c\u770b\u4e0d\u6e05\u695a\u8f66\u91cc\u4eba\u7684\u76f8\u8c8c\uff0c\u5374\u6a21\u7cca\u7684\u770b\u5f97\u89c1\u8f66\u91cc\u4eba\u5728\u6253\u91cf\u4ed6\u4eec\uff0c\u5f20\u606a\u5fc3\u91cc\u89c9\u5f97\u5947\u602a\uff0c\u4e2d\u592e\u515a\u6821\u8fd8\u6709\u8c01\u8ba4\u8bc6\u4ed6\u4eec\uff1f\u51ef\u8fea\u62c9\u514b\u6302\u7740\u519b\u533a\u7684\u724c\u7167\uff0c\u5f00\u8f66\u7684\u53f8\u673a\u4e5f\u662f\u7a7f\u7740\u6b66\u8b66\u6253\u626e\u3002</p><p>\u3000\u3000\u201c\u8fd9\u519b\u724c\u4e0d\u662f\u968f\u4fbf\u80fd\u4e70\u5230\u7684\uff01\u201d\u53f6\u5efa\u658c\u56de\u5934\u770b\u4e86\u4e00\u773c\uff0c\u4ed6\u4e5f\u6ce8\u610f\u5230\u8f66\u7ecf\u8fc7\u65f6\uff0c\u8f66\u91cc\u4eba\u5728\u89c2\u5bdf\u4ed6\u4eec\u3002</p><p>\u3000\u3000\u5f20\u606a\u770b\u5230\u5510\u5b66\u8c26\u4ece\u6821\u5185\u5f80\u6821\u95e8\u53e3\u8d70\u6765\uff0c\u770b\u60ef\u7ed9\u4eba\u7fa4\u7c07\u62e5\u7740\u7684\u5510\u5b66\u8c26\uff0c\u89c1\u4ed6\u72ec\u81ea\u8896\u624b\u8d70\u6765\u8fd8\u771f\u6709\u4e9b\u4e0d\u4e60\u60ef\uff0c\u6709\u4e24\u4e2a\u6708\u6ca1\u6709\u89c1\uff0c\u5510\u5b66\u8c26\u6bd4\u5728\u6d77\u5dde\u65f6\u8981\u80d6\u4e86\u8bb8\u591a\u3002</p><p>\u3000\u3000\u79bb\u5f00\u6d77\u5dde\u5df2\u6210\u5b9a\u5c40\uff0c\u5510\u5b66\u8c26\u5230\u5317\u4eac\u6765\uff0c\u5c31\u771f\u7684\u5f00\u59cb\u5bf9\u6d77\u5dde\u653e\u624b\u4e86\uff0c\u867d\u7136\u8fd8\u8981\u7ee7\u7eed\u638c\u63e1\u5927\u5c40\u4e00\u6bb5\u65f6\u95f4\uff0c\u4f46\u662f\u5177\u4f53\u800c\u5fae\u7684\u4e8b\u60c5\uff0c\u90fd\u4e0d\u4f1a\u7279\u522b\u52b3\u5fc3\u7684\u53bb\u8fc7\u95ee\uff1b\u53e6\u5916\uff0c\u8fd8\u6ca1\u6709\u6b63\u5f0f\u5230\u6c5f\u5357\u7701\u5c65\u4efb\uff0c\u7279\u522b\u662f\u6d88\u606f\u8fd8\u6ca1\u6709\u5f97\u5230\u786e\u8ba4\u4e4b\u524d\uff0c\u4ed6\u53c8\u4e0d\u4fbf\u7acb\u5373\u53bb\u7814\u7a76\u6c5f\u5357\u7684\u60c5\u51b5\uff0c\u66f4\u6ca1\u6709\u6c5f\u5357\u7684\u4e8b\u52a1\u4f1a\u9ebb\u70e6\u5230\u4ed6\uff0c\u5fc3\u5bbd\u5219\u4f53\u80d6\u3002</p><p>\u3000\u3000\u6c5f\u5357\u7701\u5168\u59d4\u4f1a\u8bae\u8fd8\u6709\u4e00\u5468\u7684\u65f6\u95f4\u624d\u53ec\u5f00\uff0c\u5510\u5b66\u8c26\u7684\u4efb\u547d\u4f1a\u5728\u7701\u5168\u59d4\u4f1a\u8bae\u4e0a\u6b63\u5f0f\u7684\u786e\u5b9a\uff0c\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e00\u4e8b\uff0c\u5f20\u606a\u7edd\u6ca1\u6709\u7406\u7531\u8ba9\u5510\u5b66\u8c26\u5f53\u5c40\u5916\u4eba\uff0c\u4ed6\u4e0e\u53f6\u5efa\u658c\u5c31\u662f\u8d76\u8fc7\u6765\u89c1\u5510\u5b66\u8c26\u8c08\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u4e8b\u5b9c\u7684\u3002</p><p>\u3000\u3000\u76f8\u6bd4\u8f83\u800c\u8a00\uff0c\u5f90\u5b66\u5e73\u4e0e\u6881\u4f1f\u6cd5\u4e3b\u8981\u8d1f\u8d23\u5168\u7701\u7684\u7ecf\u6d4e\u5927\u5c40\uff0c\u50cf\u4e1c\u5c71\u5c9b\u5efa\u6e2f\u8fd9\u79cd\u5177\u4f53\u7684\u5927\u578b\u9879\u76ee\uff0c\u5510\u5b66\u8c26\u4ee3\u8868\u6c5f\u5357\u7701\u51fa\u9762\u534f\u8c03\u662f\u6700\u5408\u9002\u7684\uff0c\u7279\u522b\u662f\u4ed6\u8fd8\u8981\u5728\u5317\u4eac\u6ede\u7559\u4e00\u6bb5\u65f6\u95f4\u3002</p></article>"}}}]);
